(window.webpackJsonp=window.webpackJsonp||[]).push([[398],{421:function(p){p.exports={chapterTitle:null,title:"\u7b2c398\u7ae0 \u8981\u66f4\u5f3a",content:"<article><h2>\u7b2c398\u7ae0 \u8981\u66f4\u5f3a</h2><p>\u3000\u3000\u770b\u7740\u7fdf\u4e39\u9752\u773c\u775b\u7684\u7591\u60d1\u66f4\u6df1\uff0c\u5f20\u606a\u5f88\u65e0\u5948\u7684\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u770b\u6765\u81ea\u5df1\u53ea\u80fd\u505a\u4e2a\u9690\u5f62\u5bcc\u8c6a\u4e86\uff0c\u8981\u662f\u5c06\u4e8b\u60c5\u516c\u5e03\u4e8e\u4f17\uff0c\u5341\u4eba\u5012\u4f1a\u6709\u5341\u4e00\u4eba\u6301\u6709\u6df1\u5207\u6000\u7591\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5bf9\u62db\u5f85\u5bbe\u9986\u6539\u5236\u7684\u6295\u8d44\uff0c\u4e0e\u6211\u6ca1\u6709\u76f4\u63a5\u7684\u5173\u7cfb\u3002\u8981\u8bf4\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u4e5f\u4e0d\u6070\u5f53\uff0c\u76db\u9752\u501f\u7ed9\u4f60\u7684\u8d44\u91d1\uff0c\u7ed9\u4ed6\u516c\u53f8\u6240\u9020\u6210\u7684\u8d22\u52a1\u4e0a\u4e0d\u8db3\uff0c\u6211\u4f1a\u7ed9\u4ee5\u8865\u8db3\uff0c\u4f46\u662f\u4e5f\u4ec5\u9650\u4e8e\u95f4\u63a5\u501f\u8d37\u7684\u5173\u7cfb\u800c\u5df2\u3002\u5373\u4f7f\u53ef\u4ee5\u8bf4\u2018\u6e05\u8005\u81ea\u6e05\u2019\uff0c\u4f46\u662f\u6211\u7238\u7238\u5904\u5728\u8fd9\u6837\u7684\u4f4d\u5b50\u4e0a\uff0c\u4e5f\u8981\u8003\u8651\u5230\u2018\u4eba\u8a00\u53ef\u754f\u2019\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u624b\u6309\u7740\u989d\u524d\u5782\u4e0b\u7684\u4e4c\u9ed1\u4eae\u4e3d\u7684\u957f\u5934\uff0c\u795e\u60c5\u8ff7\u60d1\uff0c\u5012\u8ba9\u5979\u5a07\u8273\u7684\u5bb9\u989c\u591a\u4e86\u51e0\u8bb8\u7eaf\u771f\u7684\u5473\u9053\uff0c\u8fd9\u5927\u6982\u4f1a\u8ba9\u4e00\u4e2a\u6210\u719f\u7684\u5973\u4eba\u66f4\u52a0\u7684\u8ff7\u4eba\uff1b\u5f20\u606a\u4e5f\u4e0d\u4ecb\u610f\u51dd\u89c6\u7740\u5979\u591a\u770b\u4e9b\u65f6\u95f4\u3002</p><p>\u3000\u3000\u201c\u6709\u4e9b\u660e\u767d\uff0c\u53c8\u4e9b\u8ff7\u7cca\u2026\u2026\u201d\u7fdf\u4e39\u9752\u773c\u7751\u4e00\u62ac\uff0c\u770b\u7740\u5f20\u606a\u6b63\u51dd\u89c6\u7740\u5979\uff0c\u773c\u795e\u6df1\u9083\uff0c\u5373\u4f7f\u6709\u7740\u7537\u4eba\u6b23\u8d4f\u5973\u4eba\u7684\u610f\u5473\u5728\u91cc\u9762\uff0c\u4e5f\u6ca1\u6709\u6392\u65a5\u7684\u5fc3\u7406\uff0c\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u5979\u90fd\u80fd\u660e\u767d\uff0c\u786e\u51ff\u65e0\u7591\u7684\u662f\u8bf4\uff0c\u5f20\u77e5\u884c\u7236\u5b50\u624b\u5934\u63e1\u7740\u5e9e\u5927\u7684\u4ea7\u4e1a\u6295\u8d44\uff0c\u8db3\u4ee5\u8ba9\u8fd9\u4e2a\u5bb6\u5ead\u4eab\u7528\u4e0d\u5c3d\uff0c\u4e0d\u5c51\u5230\u65b0\u829c\u6765\u635e\u53d6\u4ec0\u4e48\u597d\u5904\uff0c\u751a\u81f3\u4e3a\u4e86\u907f\u5acc\uff0c\u60f3\u505a\u4e00\u4e9b\u5bf9\u65b0\u829c\u6709\u76ca\u7684\u4e8b\u60c5\uff0c\u4e5f\u8981\u591a\u8d39\u4e00\u756a\u624b\u811a\uff0c\u53ea\u662f\u4e3a\u4ec0\u4e48\u8fd9\u4e2a\u5bb6\u5ead\u4f1a\u62e5\u6709\u5e9e\u5927\u7684\u8d22\u5bcc\uff0c\u800c\u8fd9\u7b14\u8d22\u5bcc\u73b0\u5728\u5374\u4ea4\u7ed9\u4e00\u4e2a\u5341\u516b\u5c81\u7684\u9ad8\u4e2d\u751f\u7ba1\u7406\uff1f\u201c\u662f\u4ec0\u4e48\u6709\u540d\u516c\u53f8\u5417\uff1f\u201d</p><p>\u3000\u3000\u5982\u679c\u516c\u4f17\u90fd\u6709\u771f\u5b9e\u7684\u6295\u7968\u6743\uff0c\u4e00\u65e6\u81ea\u5df1\u5728\u77ed\u77ed\u4e24\u5e74\u65f6\u95f4\u521b\u5efa\u9526\u6e56\u53ca\u65d7\u4e0b\u4f01\u4e1a\u7684\u4e8b\u5b9e\u516c\u4f17\u4e8e\u4f17\uff0c\u8ba9\u516c\u4f17\u6295\u7968\u51b3\u5b9a\uff0c\u81ea\u5df1\u6709\u591a\u5927\u7684\u53ef\u80fd\u6027\u4f1a\u7ed9\u9001\u5230\u6709\u5173\u673a\u6784\u89e3\u5256\u7814\u7a76\uff1f\u5f20\u606a\u80fd\u660e\u767d\u7fdf\u4e39\u9752\u773c\u91cc\u7684\u7591\u60d1\uff0c\u53ea\u662f\u89e3\u91ca\u8d77\u6765\u66f4\u52a0\u8d39\u529b\uff0c\u8fd8\u662f\u8ba9\u5979\u6162\u6162\u53bb\u7422\u78e8\u5427\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8bf4\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7231\u8fbe\u7535\u5b50\u8fd8\u6709\u4e9b\u540d\u6c14\u7684\uff0c\u90a3\u5c31\u7b97\u662f\u5427\uff0c\u6211\u5bb6\u5728\u8fd9\u4e24\u5bb6\u516c\u53f8\u90fd\u6709\u6295\u8d44\u2026\u2026\u56e0\u4e3a\u8fd9\u4e24\u5bb6\u4f01\u4e1a\u7684\u603b\u90e8\u53ca\u6295\u8d44\u4e3b\u4f53\u90fd\u5728\u6d77\u5dde\uff0c\u6211\u7238\u7238\u8981\u5347\u4e0a\u526f\u5385\uff0c\u5c31\u9700\u8981\u79bb\u5f00\u6d77\u5dde\u4ee5\u89c4\u907f\u6709\u5173\u65b9\u9762\u7684\u6cd5\u89c4\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u7701\u5185\uff0c\u53ea\u8981\u4e0d\u662f\u592a\u5b64\u964b\u5be1\u95fb\u7684\u4eba\u90fd\u542c\u8bf4\u8fc7\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7231\u8fbe\u7535\u5b50\u4e24\u5bb6\u516c\u53f8\uff0c\u5f20\u77e5\u884c\u5bb6\u7adf\u662f\u5728\u8fd9\u4e24\u5bb6\u516c\u53f8\u91cc\u6709\u6295\u8d44\uff1f\u4e4b\u524d\u7fdf\u4e39\u9752\u89c9\u5f20\u77e5\u884c\u4e00\u5bb6\u4e5f\u672a\u514d\u592a\u4e0d\u77e5\u6536\u655b\uff0c\u6b64\u65f6\u770b\u6765\uff0c\u4e5f\u672a\u514d\u592a\u6536\u655b\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u9f99\u534e\u96c6\u56e2\u4e0e\u65b0\u5149\u7eb8\u4e1a\u3001\u7231\u8fbe\u7535\u5b50\u6bd4\u8d77\u6765\uff0c\u8fde\u63d0\u978b\u90fd\u4e0d\u914d\uff0c\u5f20\u800c\u7acb\u51fa\u884c\u5374\u4ee5\u5954\u9a70\u4ee3\u6b65\uff0c\u5efa\u5b85\u56f4\u6e56\uff0c\u6e56\u8fb9\u8fd8\u5806\u4e86\u5047\u5c71\u77f3\u3002</p><p>\u3000\u3000\u7f8e\u4e3d\u7684\u5973\u4eba\u8d70\u795e\u4e5f\u662f\u6781\u8010\u770b\u7684\uff0c\u5f20\u606a\u9887\u6709\u8010\u5fc3\u7684\u770b\u7740\u7fdf\u4e39\u9752\u8d70\u795e\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u8fd8\u9887\u4e3a\u723d\u5229\u7684\u3002</p><p>\u3000\u3000\u201c\u5982\u679c\u89c9\u5f97\u76f8\u5f53\u8d39\u89e3\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u6211\u4eec\u65e5\u540e\u518d\u8c08\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u4e0d\uff0c\u201d\u7fdf\u4e39\u9752\u56de\u8fc7\u795e\u6765\uff0c\u56de\u89c6\u7740\u5f20\u606a\u6df1\u9083\u7684\u773c\u795e\uff0c\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u8c08\u6ca1\u5173\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u8bf4\uff0c\u201d\u5f20\u606a\u7ad9\u8d77\u6765\u644a\u644a\u624b\uff0c\u201c\u53ea\u662f\u901a\u8fc7\u76db\u9752\u63d0\u4f9b\u4e00\u7b14\u8d44\u91d1\u7ed9\u4f60\uff0c\u4e5f\u662f\u770b\u91cd\u4f60\u5728\u7ba1\u7406\u4e0a\u7684\u80fd\u529b\uff0c\u81f3\u4e8e\u4f60\u7684\u51b3\u5b9a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5e72\u6d89\uff0c\u4e0d\u7136\u5c31\u8fdd\u80cc\u521d\u8877\u4e86\uff0c\u8fc7\u51e0\u5929\uff0c\u6d77\u5dde\u4f1a\u6709\u4e00\u4e9b\u4eba\u8fc7\u6765\uff0c\u9ebb\u70e6\u4f60\u5e2e\u5fd9\u63a5\u5f85\u4e00\u4e0b\u2026\u2026\u6211\u5c06\u4e00\u4e9b\u4e8b\u60c5\u544a\u8bc9\u4f60\uff0c\u4e5f\u662f\u4e0d\u5e0c\u671b\u4f60\u5fc3\u91cc\u6709\u5176\u4ed6\u4e0d\u5e94\u8be5\u6709\u7684\u62c5\u5fc3\uff0c\u8d44\u91d1\u6765\u6e90\u6ca1\u6709\u95ee\u9898\uff0c\u4e5f\u4e0d\u4f1a\u6709\u989d\u5916\u7684\u9644\u52a0\u6761\u4ef6\u2026\u2026\u8fd8\u6709\uff0c\u6211\u544a\u8bc9\u4f60\u7684\u4e8b\u60c5\u65e2\u7136\u5df2\u7ecf\u8ba9\u4f60\u611f\u5230\u7591\u60d1\u8d39\u89e3\uff0c\u5c31\u4e0d\u8981\u518d\u53bb\u6298\u78e8\u522b\u4eba\u7684\u795e\u7ecf\u4e86\u3002\u201d\u4f38\u624b\u9080\u5979\u4e00\u8d77\u4e0b\u697c\uff0c\u8d70\u5230\u697c\u68af\u62d0\u89d2\u5904\uff0c\u53c8\u7a81\u7136\u56de\u5934\u5bf9\u7fdf\u4e39\u9752\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u5f53\u7136\uff0c\u6211\u975e\u5e38\u559c\u6b22\u90a3\u5929\u5728\u7687\u540e\u591c\u603b\u4f1a\u7684\u7537\u5395\u91cc\uff0c\u4f60\u628a\u6211\u5f53\u6210\u666e\u901a\u7537\u5b69\u5b50\u65f6\u7684\u90a3\u79cd\u611f\u89c9\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u60f3\u8d77\u90a3\u5929\u81ea\u5df1\u7adf\u7136\u60f3\u4ee5\u7f8e\u8272\u8bd5\u63a2\u5f20\u606a\uff0c\u4fcf\u8138\u7c89\u7ea2\uff0c\u770b\u7740\u5f20\u606a\u4f3c\u7b11\u975e\u7b11\u7684\u51dd\u89c6\u7740\u81ea\u5df1\uff0c\u900f\u7740\u4e00\u80a1\u90aa\u9b45\u7684\u6c14\u606f\uff0c\u672c\u4e0d\u8be5\u5c11\u5e74\u6240\u6709\u3001\u8001\u6210\u800c\u777f\u667a\u7684\u773c\u795e\uff0c\u671b\u5f97\u8ba9\u4eba\u611f\u89c9\u5fc3\u91cc\u53d1\u865a\uff0c\u82b3\u5fc3\u5927\u4e71\uff1a\u81ea\u5df1\u7adf\u7136\u60f3\u53bb\u62ff\u7f8e\u8c8c\u8bd5\u63a2\uff0c\u771f\u662f\u4e0d\u8981\u547d\u4e86\uff01</p><p>\u3000\u3000\u51c6\u5907\u6881\u683c\u73cd\u3001\u5f20\u606a\u6bcd\u5b50\u4eca\u5929\u4ece\u9999\u6e2f\u56de\u6765\uff0c\u536b\u5170\u5979\u5988\u51c6\u5907\u597d\u4e9b\u5c0f\u83dc\uff0c\u7fdf\u4e39\u9752\u6b64\u65f6\u82b3\u5fc3\u5927\u4e71\uff0c\u6ca1\u6709\u4fe1\u5fc3\u4e0e\u5f20\u606a\u5446\u5728\u4e00\u8d77\uff0c\u8dd1\u8fc7\u53bb\u5e2e\u536b\u5170\u5979\u4eec\u51c6\u5907\u665a\u996d\u3002\u536b\u5170\u5c0f\u58f0\u95ee\u5979\uff1a\u201c\u4f60\u4eec\u5728\u4e0a\u9762\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4f60\u4e0b\u6765\u65f6\u8138\u90fd\u662f\u7ea2\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u63a9\u9970\u7684\u7b11\u4e86\u7b11\uff0c\u60f3\u5fc5\u536b\u5170\u65e5\u540e\u80fd\u6e10\u6e10\u660e\u767d\u5f20\u606a\u663e\u8d6b\u5f02\u5e38\u7684\u5bb6\u4e16\uff0c\u201c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u8fd8\u662f\u524d\u4e9b\u5929\u8ddf\u4f60\u8bf4\u7684\u4e8b\u60c5\u5dee\u4e0d\u591a\uff0c\u53ea\u662f\u518d\u786e\u8ba4\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u8fd9\u4e48\u8bf4\uff0c\u7fdf\u59d0\u4f1a\u5c06\u5bbe\u9986\u76d8\u4e0b\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u662f\u8ddf\u522b\u4eba\u4e00\u8d77\uff0c\u4f60\u4ee5\u540e\u7ee7\u7eed\u5e2e\u6211\u5c31\u662f\uff1b\u8fc7\u51e0\u5929\uff0c\u6d77\u5dde\u6709\u4e9b\u4eba\u8981\u8fc7\u6765\uff0c\u4f60\u5e2e\u6211\u4e00\u8d77\u63a5\u5f85\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4ece\u6d77\u5dde\u8fc7\u6765\u7684\u8fd9\u4e9b\u4eba\u662f\u76db\u9752\u9886\u8fc7\u6765\u7684\uff0c\u662f\u5f20\u77e5\u975e\u4e0e\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u7b49\u4eba\u3002</p><p>\u3000\u3000\u4ece\u6d77\u5dde\u5f00\u8f66\u8fc7\u6765\u8981\u516b\u4e2a\u5c0f\u65f6\uff0c\u4ed6\u4eec\u4e0a\u5348\u4ece\u6d77\u5dde\u51fa\u53d1\uff0c\u4e2d\u9014\u4f11\u606f\u5403\u996d\uff0c\u8d76\u5230\u65b0\u829c\u5df2\u7ecf\u662f\u9ec4\u660f\u4e86\u3002\u5f20\u606a\u76f4\u63a5\u4e0e\u7fdf\u4e39\u9752\u3001\u536b\u5170\u4e00\u8d77\u62c9\u7740\u5c0f\u53d4\u4ed6\u4eec\u8fdb\u5929\u4e91\u5c71\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u770b\u89c1\u7fdf\u4e39\u9752\u3001\u536b\u5170\u5c31\u53d6\u7b11\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u8dd1\u5230\u54ea\u91cc\uff0c\u9996\u5148\u5012\u662f\u8003\u8651\u5148\u4fdd\u62a4\u81ea\u5df1\u7684\u773c\u775b\u3002\u201d</p><p>\u3000\u3000\u536b\u5170\u5355\u7eaf\uff0c\u4e00\u65f6\u8fd8\u542c\u4e0d\u51fa\u90b5\u81f3\u521a\u8bdd\u91cc\u8c03\u4f83\u7684\u610f\u5473\uff0c\u7fdf\u4e39\u9752\u5012\u662f\u6062\u590d\u5e72\u7ec3\u6cfc\u8fa3\u7684\u6027\u5b50\uff0c\u5de7\u7b11\u5ae3\u7b11\u9053\uff1a\u201c\u6211\u5c31\u5f53\u90b5\u603b\u662f\u5728\u5938\u6211\u4eec\u2026\u2026\u4e0d\u8fc7\u6211\u4eec\u53ef\u4e0d\u8ba4\u8bc6\u5728\u6d77\u5dde\u7ed9\u606a\u5c11\u517b\u773c\u7684\u6f02\u4eae\u5973\u4eba\u662f\u591a\u4e48\u51fa\u8272\uff0c\u6539\u5929\u8fd8\u8981\u8bf7\u90b5\u603b\u4ecb\u7ecd\u5462\uff0c\u5510\u5e02\u957f\u7684\u5973\u513f\uff0c\u6211\u4eec\u5012\u662f\u89c1\u8fc7\u4e86\u2026\u2026\u201d\u4ee5\u5f20\u606a\u7684\u5730\u4f4d\uff0c\u90b5\u81f3\u521a\u60f3\u5fc5\u4e0d\u4f1a\u62ff\u5510\u5a67\u3001\u9648\u5983\u84c9\u8fd9\u4e9b\u5355\u7eaf\u3001\u5929\u771f\u7684\u5973\u5b69\u5b50\u5f00\u73a9\u7b11\uff0c\u5927\u6982\u5f20\u606a\u8eab\u8fb9\u8fd8\u6709\u5176\u4ed6\u989d\u5916\u51fa\u4f17\u7684\u5973\u4eba\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5567\u5567\u5634\uff0c\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u8fd9\u4e2a\u5973\u4eba\u8fd8\u771f\u662f\u5389\u5bb3\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u5728\u5f20\u606a\u9762\u524d\u62ff\u8bb8\u601d\u8ddf\u4ed6\u7684\u4e8b\u51fa\u6765\u8bf4\u53e8\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u77e5\u9053\u90b5\u81f3\u521a\u7b49\u4eba\u662f\u5f20\u606a\u627e\u6765\u53c2\u4e0e\u6295\u8d44\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u6539\u5236\u9879\u76ee\u7684\uff0c\u5f20\u77e5\u975e\u66f4\u662f\u5f20\u606a\u7684\u5802\u53d4\uff0c\u5f20\u606a\u8ba9\u5979\u5b89\u6392\u63a5\u5f85\uff0c\u5979\u81ea\u7136\u4e0d\u4f1a\u6020\u6162\u3002</p><p>\u3000\u3000\u5929\u4e91\u5c71\u4e0a\u7684\u98df\u5bbf\u867d\u7136\u4e0d\u5962\u534e\uff0c\u4f46\u662f\u8981\u7cbe\u5fc3\u5b89\u6392\uff0c\u4ec5\u5403\u98df\u4e00\u9879\u5c31\u5f88\u6709\u7279\u8272\uff0c\u5c71\u4e0a\u6709\u7af9\u5c4b\u53ef\u4ee5\u7559\u5bbf\uff0c\u5929\u5730\u9759\u8c27\uff0c\u4e5f\u975e\u7eb7\u6742\u7684\u57ce\u5e02\u4eba\u80fd\u591f\u4eab\u53d7\u7684\u591c\u665a\u751f\u6d3b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf7\u5927\u5bb6\u90fd\u5750\u5230\u4e00\u95f4\u7af9\u5c4b\u91cc\uff0c\u559d\u7740\u5c71\u91cc\u4eba\u5bb6\u917f\u9020\u6210\u7684\u751c\u679c\u9152\uff0c\u559d\u5f97\u5fae\u9163\uff0c\u542c\u7740\u7af9\u5c4b\u5916\u98ce\u63a0\u8fc7\u6797\u68a2\u7684\u58f0\u97f3\uff0c\u624d\u8ddf\u5c0f\u53d4\u4ed6\u4eec\u8bf4\uff1a\u201c\u76db\u9752\u4e4b\u524d\u5e94\u8be5\u6709\u8ddf\u4f60\u4eec\u63d0\u8d77\u8fc7\u65b0\u829c\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u6539\u5236\u7684\u4e8b\u60c5\uff0c\u4f60\u4eec\u4e00\u8fc7\u6765\uff0c\u6211\u6ca1\u6709\u5728\u90a3\u91cc\u62db\u5f85\u4f60\u4eec\uff0c\u800c\u662f\u76f4\u63a5\u5230\u5929\u4e91\u5c71\u6765\uff0c\u53ea\u662f\u60f3\u8bf4\uff0c\u62db\u5f85\u5bbe\u9986\u6539\u5236\u7684\u9879\u76ee\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u5b9e\u5728\u4e0d\u9700\u8981\u5927\u5f20\u65d7\u9f13\u8bf7\u4f60\u4eec\u8fc7\u6765\u8d70\u4e00\u8d9f\u3002\u65b0\u829c\u7684\u65c5\u6e38\u4e1a\u6709\u5f88\u5927\u7684\u53d1\u5c55\u6f5c\u529b\uff0c\u6211\u7238\u65e5\u540e\u60f3\u635e\u653f\u7ee9\uff0c\u4e5f\u4f1a\u4ece\u8fd9\u4e2a\u601d\u8def\u4e0b\u624b\uff0c\u6211\u60f3\u4f60\u4eec\u8fd9\u65f6\u5019\u4ecb\u5165\u65b0\u829c\u7684\u5bbe\u9986\u9910\u996e\u5a31\u4e50\u4e1a\uff0c\u7b97\u662f\u76f8\u5f53\u4e0d\u9519\u7684\u65f6\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u536b\u5170\u5728\u5916\u9762\u51c6\u5907\u4e9b\u5403\u98df\uff0c\u770b\u7740\u90b5\u81f3\u521a\u7b49\u4eba\u90fd\u5728\u4e13\u6ce8\u7684\u542c\u5f20\u606a\u4f83\u4f83\u800c\u8c08\uff0c\u7fdf\u4e39\u9752\u8fd9\u65f6\u5019\u7a0d\u80fd\u7aa5\u5f97\u4e86\u89e3\u5f20\u606a\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u4e2d\u95f4\u7684\u5f71\u54cd\u529b\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u65b9\u4fbf\u76f4\u63a5\u5230\u65b0\u829c\u6765\u6295\u8d44\uff0c\u6240\u4ee5\u5c06\u4f60\u4eec\u62c9\u8fc7\u6765\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u529b\u5206\u5219\u5f31\uff0c\u529b\u805a\u5219\u5f3a\uff0c\u65e0\u8bba\u5bbe\u9986\u3001\u9910\u996e\uff0c\u8fd8\u662f\u5a31\u4e50\u573a\uff0c\u90fd\u6709\u76f8\u5f53\u7684\u5173\u8054\u6027\uff0c\u4f60\u4eec\u5728\u76f8\u5173\u65b9\u9762\u90fd\u6709\u4e00\u4e9b\u7684\u4ea7\u4e1a\uff0c\u76f8\u5bf9\u6765\u8bf4\u8d44\u6e90\u4e0d\u96c6\u4e2d\uff0c\u4e5f\u5206\u6563\u4e86\u5927\u5bb6\u6709\u9650\u7684\u7cbe\u529b\uff0c\u8fd9\u4e9b\u8d44\u6e90\u90fd\u5806\u5230\u4e00\u5bb6\u516c\u53f8\u540d\u4e0b\uff0c\u4f1a\u4e0d\u4f1a\u66f4\u5408\u7406\uff0c\u4f1a\u4e0d\u4f1a\u6709\u66f4\u5927\u7684\u5f71\u54cd\u529b\u3001\u66f4\u5feb\u7684\u53d1\u5c55\u901f\u5ea6\uff1f\u6211\u4f1a\u5c06\u9526\u6e56\u5728\u4e16\u7eaa\u9910\u996e\u91cc40\uff05\u7684\u80a1\u4efd\u8f6c\u8ba9\u6211\u5c0f\u53d4\uff0c\u4e0d\u6653\u5f97\u4f60\u4eec\u8ba4\u4e3a\u4e16\u7eaa\u9910\u996e\u8fd9\u4e2a\u5e73\u53f0\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u624b\u91cc\u6ca1\u6709\u90a3\u4e48\u591a\u7684\u4f59\u94b1\u76d8\u4e0b\u9526\u6e56\u5728\u4e16\u7eaa\u9910\u996e\u91cc\u7684\u80a1\u4efd\u2026\u2026\u201d\u5f20\u77e5\u975e\u6765\u4e4b\u524d\u6ca1\u542c\u5f20\u606a\u63d0\u8d77\u8fc7\u8fd9\u832c\uff0c\u7a81\u7136\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u6709\u4e9b\u610f\u5916\u3002\u4e16\u7eaa\u9910\u996e\u662f\u5f53\u521d\u4e3a\u4e86\u8fd0\u8425\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u800c\u6210\u7acb\u7684\u516c\u53f8\uff0c\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u7684\u7ecf\u8425\u6743\u4ee5\u53ca\u76db\u4e16\u5e74\u534e\u5a31\u4e50\u573a\u7684\u4e00\u90e8\u5206\u7269\u4e1a\uff0c\u90fd\u662f\u4e16\u7eaa\u9910\u996e\u7684\u4f18\u8d28\u8d44\u4ea7\uff0c\u9526\u6e56\u5728\u4e16\u7eaa\u9910\u996e\u91cc40\uff05\u7684\u80a1\u4efd\uff0c\u5176\u4ed660\uff05\u90fd\u662f\u90b5\u81f3\u521a\u7684\u80a1\u4efd\u3002</p><p>\u3000\u3000\u201c\u4f60\u968f\u4fbf\u770b\u7740\u7ed9\u70b9\uff0c\u6211\u8fd8\u80fd\u8ddf\u5c0f\u53d4\u60a8\u8f83\u771f\u8ba1\u7b97\u90a3\u4e9b\u80a1\u4efd\u503c\u591a\u5c11\u94b1\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u8981\u638f\u4e00\u679a\u786c\u5e01\u7ed9\u6211\uff0c\u6211\u4e5f\u8ba4\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u8981\u4ee5\u4e3a\u6211\u62c9\u4e0d\u4e0b\u8fd9\u8138\uff0c\u201d\u5f20\u77e5\u975e\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u786c\u8981\u8f6c\u7ed9\u6211\uff0c\u90a3\u8f6c\u8ba9\u91d1\u5148\u6b20\u7740\uff0c\u4f60\u4e5f\u4e0d\u7f3a\u8fd9\u4e9b\u94b1\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\u5c31\u600e\u4e48\u529e\u3002\u201d\u5f20\u606a\u7b54\u5e94\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e0c\u671b\u5c06\u9526\u6e56\u5728\u4e16\u7eaa\u9910\u996e\u91cc40\uff05\u7684\u80a1\u4efd\u6089\u6570\u8f6c\u8ba9\u7ed9\u5c0f\u53d4\u5f20\u77e5\u975e\uff0c\u65b9\u4fbf\u4ed6\u4eec\u4ee5\u4e16\u7eaa\u9910\u996e\u6709\u9650\u516c\u53f8\u4e3a\u5e73\u53f0\u5c06\u76db\u9752\u3001\u5434\u5929\u5b9d\u90fd\u62c9\u8fdb\u6765\uff0c\u5c06\u76db\u9752\u7684\u76db\u4e16\u5e74\u534e\u5a31\u4e50\u573a\u4ee5\u53ca\u4ed6\u4e0e\u5218\u658c\u8c08\u7684\u90a3\u4e9b\u5728\u65b0\u829c\u5a31\u4e50\u4e1a\u7684\u6295\u8d44\u3001\u5434\u5929\u5b9d\u7684\u5efa\u90ba\u9152\u5e97\u53ca\u76f8\u5173\u9910\u996e\u4ea7\u4e1a\u3001\u90b5\u81f3\u521a\u7684\u82b1\u56ed\u9152\u5e97\u7b49\u8d44\u4ea7\u90fd\u7f6e\u5165\u4e16\u7eaa\u9910\u996e\uff0c\u5c31\u4f1a\u8ba9\u4e16\u7eaa\u9910\u996e\u5728\u5f88\u77ed\u7684\u65f6\u95f4\u5185\u5b9e\u529b\u5927\u589e\uff0c\u7531\u4e8e\u5404\u4eba\u540d\u4e0b\u7684\u8fd9\u4e9b\u516c\u53f8\u5e73\u65f6\u5c31\u6709\u6781\u9ad8\u7684\u5173\u8054\u6027\uff0c\u6574\u5408\u8d77\u6765\u4e5f\u76f8\u5bf9\u8f7b\u677e\uff0c\u800c\u4e14\u4ee5\u90b5\u81f3\u521a\u7684\u80fd\u529b\u7ecf\u8425\u8fd9\u4e9b\u8d44\u4ea7\u8d77\u6765\u4e5f\u6e38\u5203\u6709\u4f59\u3002\u4ee5\u8fd9\u4e2a\u5e73\u53f0\u5230\u65b0\u829c\u6765\u6295\u8d44\u5bbe\u9986\u3001\u9910\u996e\u3001\u5a31\u4e50\u7b49\u76f8\u5173\u4ea7\u4e1a\uff0c\u624d\u80fd\u6709\u8f83\u5927\u3001\u8f83\u5f3a\u7684\u4fc3\u8fdb\u4f5c\u7528\uff0c\u800c\u4e0d\u662f\u50cf\u6563\u5175\u6e38\u52c7\u4e00\u6837\u7684\u6ca1\u6709\u51b2\u51fb\u529b\u3002</p></article>"}}}]);
