(window.webpackJsonp=window.webpackJsonp||[]).push([[399],{422:function(p){p.exports={chapterTitle:null,title:"\u7b2c270\u7ae0 \u843d\u5b50\u5982\u7389",content:"<article><h2>\u7b2c270\u7ae0 \u843d\u5b50\u5982\u7389</h2><p>\u3000\u3000\u5f85\u5f20\u606a\u4ed6\u4eec\u5403\u8fc7\u665a\u996d\u79bb\u5f00\uff0c\u8c22\u665a\u6674\u5750\u5728\u9732\u53f0\u4e0a\uff0c\u5c06\u82b7\u5f64\u5c0f\u5c0f\u7684\u8eab\u4f53\u6402\u5728\u6000\u91cc\uff0c\u7ec6\u7ec6\u56de\u5473\u9ec4\u660f\u4e4b\u524d\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e2a\u5c0f\u7537\u4eba\u7684\u773c\u775b\u91cc\u85cf\u7740\u592a\u591a\u86ca\u60d1\u4eba\u5fc3\u7684\u4e1c\u897f\u4e86\uff0c\u5bf9\u5f85\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4ece\u5bb9\u4e0d\u8feb\uff0c\u5bf9\u5f85\u8fd9\u79cd\u4e8b\u4e5f\u4ece\u5bb9\u4e0d\u8feb\uff0c\u4ed6\u8eab\u4f53\u91cc\u7a76\u7adf\u600e\u6837\u7684\u529b\u91cf\uff0c\u624d\u8ba9\u4ed6\u4ece\u5bb9\u505a\u5230\u8fd9\u4e00\u70b9\uff1f</p><p>\u3000\u3000\u5085\u4fca\u76f4\u63a5\u5f00\u8f66\u9001\u8001\u5a46\u548c\u4e24\u4e2a\u5973\u513f\u56de\u5bb6\uff0c\u867d\u7136\u65f6\u95f4\u8fd8\u65e9\uff0c\u4f46\u662f\u5f20\u606a\u4e5f\u6ca1\u6709\u7559\u4e0b\u6765\uff0c\u4ed6\u8981\u53bb\u627e\u8bb8\u9e3f\u4f2f\u3002</p><p>\u3000\u3000\u6c99\u7530\u65e7\u57ce\u6539\u9020\u7684\u8fdb\u5c55\u6bd4\u60f3\u8c61\u4e2d\u8981\u5feb\uff0c\u6c99\u7530\u897f\u7247\u533a\u57df\u7684\u571f\u5730\u5dee\u4e0d\u591a\u74dc\u5206\u5b8c\u6bd5\uff0c\u6b63\u5f00\u59cb\u5927\u89c4\u6a21\u7684\u62c6\u8fc1\uff0c\u867d\u7136\u79bb\u771f\u6b63\u5efa\u8bbe\u5b8c\u6210\u8fd8\u6709\u5f88\u957f\u7684\u65f6\u95f4\uff0c\u4f46\u662f\u5df2\u7ecf\u6709\u4eba\u5bf9\u6c99\u7530\u4e1c\u7247\u5f00\u59cb\u52a8\u5fc3\u601d\u4e86\u3002</p><p>\u3000\u3000\u6c11\u95f4\u7684\u8d44\u672c\u529b\u91cf\u8fd8\u662f\u76f8\u5f53\u6709\u6f5c\u529b\u7684\uff0c\u7279\u522b\u662f\u9526\u57ce\u5730\u4ea7\u501f\u9274\u9999\u6e2f\u3001\u6df1\u5733\u7b49\u5730\u51fa\u552e\u201c\u697c\u82b1\u201d\u7684\u505a\u6cd5\u7b79\u96c6\u4e00\u90e8\u5206\u8d44\u91d1\uff0c\u8ba9\u4ed6\u4eec\u64cd\u4f5c\u8d77\u6765\u66f4\u52a0\u6e38\u5203\u6709\u4f59\u3002\u4e5d\u4e94\u5e74\uff0c\u56fd\u5185\u8fd8\u6ca1\u6709\u6d41\u884c\u6240\u8c13\u7684\u9884\u552e\u5236\u5ea6\uff0c\u4f46\u662f\u201c\u697c\u82b1\u201d\u7c7b\u4f3c\u4e8e\u8d2d\u697c\u4fdd\u8bc1\u91d1\u7684\u5f62\u5f0f\uff0c\u7ed9\u63d0\u524d\u7b79\u96c6\u90e8\u5206\u8d44\u91d1\u63d0\u4f9b\u4e00\u6761\u7b80\u6377\u7684\u4fbf\u5f84\uff0c\u5728\u8fc7\u53bb\u4e00\u4e2a\u6708\u91cc\uff0c\u9526\u57ce\u5730\u4ea7\u5728\u6c99\u7530\u8857\u5e02\u90a3\u680b\u4e09\u5341\u5c42\u7684\u5e9c\u5929\u5927\u53a6\uff0c\u51fa\u552e\u697c\u82b1\u7b79\u96c6\u8fd1\u4e24\u5343\u4e07\u7684\u8d44\u91d1\uff0c\u52a0\u4e0a\u9526\u57ce\u5730\u4ea7\u4ece\u65b0\u52a0\u5761\u83b7\u5f97\u7684\u653f\u5e9c\u8d37\u6b3e\uff0c\u8ba9\u4ed6\u4eec\u7684\u8d44\u91d1\u770b\u4e0a\u53bb\u66f4\u52a0\u7684\u5145\u88d5\u3002</p><p>\u3000\u3000\u6309\u8bf4\uff0c\u8fd9\u90e8\u5206\u8d44\u91d1\uff0c\u53ea\u80fd\u7528\u4e8e\u6c99\u7530\u8857\u5e02\u5e9c\u5929\u5927\u53a6\u7684\u5efa\u8bbe\uff0c\u4f46\u662f\u9526\u57ce\u5730\u4ea7\u5bf9\u672a\u6765\u4e24\u4e09\u5e74\u95f4\u7684\u5546\u4e1a\u5730\u4ea7\u5f62\u52bf\u5341\u5206\u770b\u597d\uff0c\u5229\u7528\u8fd9\u90e8\u5206\u8d44\u91d1\u5927\u8086\u7684\u5708\u5730\uff0c\u4ed6\u4eec\u5dee\u4e0d\u591a\u5708\u5360\u6c99\u7530\u897f\u7247\u8fd1\u4e09\u6210\u7684\u571f\u5730\u3002</p><p>\u3000\u3000\u4e8c\u5341\u4e07\u5e73\u65b9\u7c73\u7684\u5546\u4e1a\u5730\u4ea7\u50a8\u5907\u7528\u5730\uff0c\u5bf9\u5341\u51e0\u5e74\u540e\u7684\u5730\u4ea7\u516c\u53f8\u800c\u8a00\uff0c\u6839\u672c\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u4f46\u5bf9\u4e8e\u4e5d\u4e94\u5e74\u7684\u4e00\u5bb6\u5730\u533a\u6027\u7684\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\uff0c\u5df2\u7ecf\u662f\u76f8\u5f53\u60ca\u4eba\u4e86\u3002\u9526\u57ce\u5730\u4ea7\u540e\u671f\u7684\u62ff\u5730\u6210\u672c\u5e76\u4e0d\u5341\u5206\u7684\u4f4e\u5ec9\uff0c\u66f4\u591a\u7684\u4eba\u76ef\u4e0a\u8fd9\u7247\u571f\u5730\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u80c6\u91cf\u518d\u8086\u65e0\u5fcc\u60ee\u7684\u4f4e\u4ef7\u5708\u5730\uff0c\u8fd9\u4e48\u591a\u7684\u571f\u5730\uff0c\u5373\u4f7f\u76f8\u5f53\u4e00\u90e8\u5206\u571f\u5730\u6b3e\u62d6\u7740\u6ca1\u6709\u652f\u4ed8\uff0c\u4f46\u662f\u9526\u57ce\u5730\u4ea7\u4ecd\u8981\u62ff\u51fa\u5c06\u8fd1\u4e00\u4e2a\u4ebf\u7684\u771f\u91d1\u767d\u94f6\u3002</p><p>\u3000\u3000\u6574\u4e2a\u9526\u57ce\u96c6\u56e2\u4e00\u5e74\u7684\u8425\u4e1a\u6536\u5165\u90fd\u672a\u5230\u5341\u4e2a\u4ebf\uff0c\u4ec5\u4ec5\u9526\u57ce\u5730\u4ea7\u8981\u62ff\u51fa\u4e00\u4e2a\u4ebf\u8fd8\u662f\u76f8\u5f53\u7684\u5403\u529b\uff0c\u56fd\u5185\u7684\u4fe1\u8d37\u653f\u7b56\u8981\u7b49\u5230\u660e\u5e74\u4e0b\u534a\u5e74\u624d\u4f1a\u9010\u6e10\u7684\u653e\u677e\uff0c\u5f20\u606a\u8ba4\u4e3a\u9526\u57ce\u5730\u4ea7\u5f88\u53ef\u80fd\u632a\u7528\u4e86\u5e9c\u5929\u5927\u53a6\u7684\u5efa\u8bbe\u8d44\u91d1\uff0c\u624d\u80fd\u4e00\u4e0b\u5b50\u5c06\u644a\u5b50\u94fa\u8fd9\u4e48\u5927\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u9526\u57ce\u5730\u4ea7\u7684\u8fdb\u5ea6\u4e5f\u4e0d\u6162\uff0c\u770b\u6837\u5b50\uff0c\u5e9c\u5929\u5927\u53a6\uff0c\u660e\u5e74\u5e74\u4e2d\u5c31\u80fd\u5efa\u6210\u4f7f\u7528\uff0c\u9500\u552e\u987a\u5229\u7684\u8bdd\uff0c\u80fd\u591f\u7ed9\u9526\u57ce\u5730\u4ea7\u56de\u7b3c\u5927\u7b14\u7684\u8d44\u91d1\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u5e2e\u4ed6\u4eec\u6e21\u8fc7\u96be\u5173\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f88\u591a\u4eba\u90fd\u6709\u4e9b\u64cd\u4e4b\u8fc7\u6025\u4e86\uff0c\u8981\u662f\u80fd\u5c06\u5e02\u573a\u7684\u76ee\u5149\uff0c\u524d\u671f\u90fd\u96c6\u4e2d\u5728\u5e9c\u5929\u5927\u53a6\u4e0a\uff0c\u4e09\u5341\u5c42\u7684\u5e9c\u5929\u5927\u53a6\u8981\u53ca\u65f6\u5b8c\u6210\u9500\u552e\uff0c\u5e94\u8be5\u4f1a\u5f88\u8f7b\u677e\u3002\u4f46\u662f\u4e0d\u4ec5\u9526\u57ce\u5730\u4ea7\uff0c\u8fd8\u6709\u5176\u4ed6\u7684\u5730\u4ea7\u5546\u90fd\u8feb\u4e0d\u53ca\u5f85\u7684\u62ff\u90a3\u4e9b\u8fd8\u6ca1\u6709\u9732\u51fa\u5730\u9762\u7684\u5efa\u7b51\u51fa\u552e\u697c\u82b1\uff0c\u8fd9\u5c31\u5206\u6563\u4e86\u5e02\u573a\u7684\u5173\u6ce8\u5ea6\uff0c\u6d77\u5dde\u5e02\u8fd8\u4e0d\u80fd\u4e00\u4e0b\u5b50\u5bb9\u7eb3\u8fd9\u4e48\u591a\u7684\u5546\u4e1a\u5730\u4ea7\u3002</p><p>\u3000\u3000\u5e02\u573a\u7edd\u975e\u7406\u667a\u7684\uff0c\u6216\u8bb8\u6709\u4e9b\u4eba\u80fd\u770b\u5230\u5176\u4e2d\u7684\u5371\u673a\uff0c\u4f46\u662f\u4ed6\u4eec\u4ece\u6765\u90fd\u4e0d\u8ba4\u4e3a\u4f1a\u6709\u6beb\u65e0\u98ce\u9669\u7684\u6295\u8d44\u673a\u4f1a\uff0c\u5c31\u8fde\u90b5\u81f3\u521a\u540d\u4e0b\u7684\u5730\u4ea7\u516c\u53f8\u8fd8\u5728\u949f\u697c\u89d2\u4e0e\u4e94\u91cc\u6eaa\u4e1c\u6ee9\u4e4b\u95f4\u62ff\u4e86\u4e00\u5757\u5730\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u4e3a\u62ff\u5730\u7684\u4e00\u5343\u4e94\u516d\u767e\u4e07\u8d44\u91d1\u8981\u8150\u8680\u4e86\u591a\u5c11\u94f6\u884c\u5de5\u4f5c\u4eba\u5458\u3002</p><p>\u3000\u3000\u7531\u4e8e\u5e02\u91cc\u64a4\u6d88\u5728\u4e94\u91cc\u6eaa\u4e1c\u6ee9\u5efa\u522b\u5885\u7684\u8ba1\u5212\uff0c\u6253\u7b97\u5c06\u8fd9\u5757\u5730\u62ff\u51fa\u6765\u5efa\u4e00\u5ea7\u5927\u578b\u7684\u5e02\u6c11\u4f11\u95f2\u5e7f\u573a\uff0c\u6d88\u606f\u4e00\u4f20\u51fa\u53bb\uff0c\u4e94\u91cc\u6eaa\u4e1c\u6ee9\u9644\u8fd1\u7684\u5730\u5757\u5927\u5e45\u589e\u503c\uff0c\u6709\u4eba\u52a0\u4e94\u767e\u4e07\u6765\u63a5\u90b5\u81f3\u521a\u624b\u91cc\u7684\u5730\uff0c\u90b5\u81f3\u521a\u90fd\u6ca1\u6709\u7b54\u5e94\u3002</p><p>\u3000\u3000\u6c99\u7530\u65e7\u57ce\u6539\u9020\u4e4b\u524d\uff0c\u6d77\u5dde\u5e02\u56fd\u6709\u571f\u5730\u4f7f\u7528\u6743\u8f6c\u8ba9\uff0c\u7eaf\u7cb9\u91c7\u7528\u884c\u653f\u5212\u62e8\u7684\u65b9\u5f0f\uff0c\u5e02\u91cc\u843d\u4e0d\u4e0b\u4ec0\u4e48\u94b1\uff0c\u4ece\u9526\u57ce\u5730\u4ea7\u62ff\u5e9c\u5929\u5927\u53a6\u7684\u90a3\u5757\u5730\u5f00\u59cb\uff0c\u867d\u7136\u8fd8\u53ea\u662f\u5f62\u5f0f\u4e0a\u7684\u6bd4\u4ef7\uff0c\u4f46\u6bd5\u7adf\u5f15\u5165\u4e86\u7ade\u4ef7\u8f6c\u8ba9\u7684\u65b9\u5f0f\uff0c\u5e02\u91cc\u7684\u571f\u5730\u6536\u5165\u5927\u5e45\u589e\u52a0\u3002\u4ec5\u6c99\u7530\u897f\u7247\u7684\u571f\u5730\u8f6c\u8ba9\uff0c\u5e02\u91cc\u51c0\u5f97\u4e24\u4e2a\u4ebf\uff0c\u8ba9\u4e00\u5411\u6765\u6349\u895f\u89c1\u8098\u7684\u5e02\u8d22\u653f\u677e\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u6c99\u7530\u897f\u7247\u5927\u89c4\u6a21\u62c6\u8fc1\u5f15\u8d77\u8f83\u5c16\u9510\u7684\u793e\u4f1a\u77db\u76fe\uff0c\u5e02\u91cc\u53ea\u6015\u660e\u5e74\u6625\u5b63\u5c31\u4f1a\u8feb\u4e0d\u53ca\u5f85\u7684\u542f\u52a8\u6c99\u7530\u4e1c\u7247\u7684\u65e7\u57ce\u6539\u9020\u8ba1\u5212\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u8f66\u7ecf\u8fc7\u9526\u57ce\u5730\u4ea7\u5efa\u8bbe\u7684\u5e9c\u5929\u5927\u53a6\u5de5\u5730\uff0c\u770b\u89c1\u8bbe\u8ba1\u4e09\u5341\u5c42\u7684\u5e9c\u5929\u5927\u53a6\u5df2\u7ecf\u5efa\u5230\u4e00\u534a\u4e86\uff0c\u9526\u57ce\u7684\u901f\u5ea6\u5012\u662f\u4e0d\u6162\u3002</p><p>\u3000\u3000\u758f\u6e2f\u6cb3\u7684\u758f\u6d5a\u5de5\u7a0b\u3001\u6c99\u7530\u524d\u8857\u7684\u62d3\u5bbd\u5de5\u7a0b\u5728\u5165\u51ac\u524d\u5b8c\u6210\uff0c\u6c99\u7530\u524d\u8857\u7531\u539f\u6765\u72ed\u7a84\u7684\u4e24\u8f66\u9053\u62d3\u5bbd\u6210\u516d\u8f66\u9053\uff0c\u4e5f\u6b63\u662f\u56e0\u4e3a\u524d\u8857\u7684\u62d3\u5bbd\u5de5\u7a0b\uff0c\u624d\u8ba9\u5e9c\u5929\u5927\u53a6\u7684\u697c\u82b1\u5356\u5f97\u8fd9\u4e48\u597d\u3002\u4f46\u662f\u6c99\u7530\u5185\u90e8\u7684\u5df7\u8857\u8fd8\u662f\u90a3\u6837\u7684\u72ed\u7a84\uff0c\u5f20\u606a\u5f00\u8f66\u8fdb\u53bb\uff0c\u5c31\u6015\u524d\u9762\u4f1a\u6709\u8f66\u9006\u884c\uff0c\u4f1a\u8f66\u8981\u5341\u5206\u7684\u5c0f\u5fc3\u624d\u80fd\u907f\u514d\u64e6\u7740\u78b0\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u505c\u5728\u9053\u89c2\u5916\u7684\u8def\u7259\u4e0a\uff0c\u770b\u89c1\u524d\u9762\u9648\u5947\u5bb6\u7684\u9662\u5b50\u597d\u50cf\u7a0d\u7a0d\u4fee\u6574\u8fc7\uff0c\u676f\u76cf\u4ea4\u9519\u3001\u706f\u7ea2\u9152\u7eff\uff0c\u770b\u6765\u9648\u5983\u84c9\u5bb6\u7684\u9152\u9986\u5f88\u6709\u8d77\u8272\u3002\u5f20\u606a\u62ac\u811a\u8d70\u8fdb\u9053\u89c2\uff0c\u4eff\u4f5b\u5916\u9762\u7684\u706f\u7ea2\u9152\u7eff\u7684\u55a7\u6742\u8ba9\u9662\u4e2d\u7684\u4e24\u9897\u53e4\u674f\u6811\u5438\u5f97\u6ef4\u6c34\u4e0d\u6f0f\uff0c\u6084\u7136\u9759\u8c27\uff0c\u5165\u8033\u53ea\u6709\u9535\u7136\u68cb\u5b50\u843d\u76d8\u7684\u6e05\u54cd\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\uff0c\u56fd\u5185\u7684\u56f4\u68cb\u70ed\u8fd8\u6ca1\u6709\u6d88\u9000\uff0c\u9053\u89c2\u91cc\u7684\u68cb\u5ba4\u5e73\u65e5\u4e5f\u8981\u5230\u5341\u70b9\u4e4b\u540e\u624d\u4f1a\u7a7a\u5bc2\u4e0b\u6765\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u5c06\u68cb\u9662\u5f53\u6210\u4ed6\u7684\u534a\u4e2a\u5bb6\uff0c\u591c\u95f4\u516b\u4e5d\u70b9\u949f\u5230\u68cb\u9662\u6765\u627e\u8bb8\u9e3f\u4f2f\uff0c\u5341\u56de\u4e5d\u4e2d\uff0c\u90fd\u4e0d\u7528\u63d0\u524d\u6253\u7535\u8bdd\u544a\u8bc9\u4e00\u58f0\uff0c\u4e0d\u8fc7\u4eca\u5929\u662f\u63d0\u524d\u7ea6\u597d\u7684\u3002</p><p>\u3000\u3000\u68cb\u5ba4\u8bbe\u5728\u897f\u53a2\u623f\uff0c\u5f20\u606a\u5f80\u91cc\u8d70\uff0c\u7ecf\u8fc7\u504f\u6bbf\u770b\u89c1\u9648\u5983\u84c9\u5728\u91cc\u9762\u5199\u4f5c\u4e1a\uff0c\u8fd9\u91cc\u7684\u786e\u6bd4\u5979\u5bb6\u5b89\u9759\u591a\u4e86\uff0c\u53ea\u89c1\u5979\u7a7f\u7740\u5b9d\u77f3\u84dd\u7684\u7fbd\u7ed2\u886b\uff0c\u6b6a\u7740\u5934\uff0c\u94f6\u7259\u54ac\u7740\u7b14\u5934\uff0c\u6b63\u51dd\u7709\u601d\u8003\u7740\u4ec0\u4e48\uff0c\u5f20\u606a\u6ca1\u6709\u60ca\u6270\u5979\uff0c\u5f84\u76f4\u53bb\u627e\u8bb8\u9e3f\u4f2f\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u6b63\u4e0e\u4eba\u4e0b\u68cb\uff0c\u624b\u91cc\u634f\u7740\u68cb\u5b50\uff0c\u770b\u89c1\u5f20\u606a\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e45\u6ca1\u770b\u89c1\u4f60\u4e86\u2026\u2026\u201d\u5bf9\u5750\u5728\u4ed6\u5bf9\u9762\u7684\u90a3\u4e2a\u516d\u5341\u591a\u5c81\u7684\u6e05\u7626\u8001\u4eba\u8bf4\uff0c\u201c\u8fd9\u5c40\u68cb\uff0c\u4f60\u8f93\u4e86\u2026\u2026\u201d\u5c06\u624b\u91cc\u7684\u90a3\u7c92\u5b50\u4e22\u5165\u68cb\u76d2\u91cc\u3002</p><p>\u3000\u3000\u90a3\u4eba\u8bf4\uff1a\u201c\u4e00\u5e2d\u9152\u624d\u6362\u4e00\u5c40\u68cb\uff0c\u8fd9\u68cb\u624d\u5230\u4e2d\u76d8\uff0c\u867d\u7136\u6ca1\u5e0c\u671b\u8d62\uff0c\u4f46\u662f\u597d\u6b79\u8ba9\u6211\u8fd9\u987f\u9152\u8bf7\u5f97\u7269\u6709\u6240\u503c\u624d\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8bb8\u9e3f\u4f2f\u53c8\u8ddf\u4eba\u5bb6\u4e0b\u9a97\u9152\u68cb\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u4ec0\u4e48\u6025\u4e8b\uff0c\u5148\u770b\u4f60\u4eec\u4e0b\u68cb\u3002\u201d\u65c1\u8fb9\u89c2\u68cb\u7684\u4eba\u6709\u89c1\u8bc6\u8fc7\u5f20\u606a\u4e0e\u8bb8\u9e3f\u4f2f\u7684\u5bf9\u5f08\uff0c\u5e2e\u52a9\u7aef\u4e86\u5f20\u51f3\u5b50\u9012\u7ed9\u5f20\u606a\u5750\u65c1\u8fb9\u770b\u4ed6\u4eec\u4e0b\u68cb\uff0c\u5176\u4ed6\u89c2\u68cb\u7684\u4eba\u90fd\u7ad9\u7740\u3002</p><p>\u3000\u3000\u201c\u5c0f\u8001\u5f1f\u4e5f\u559c\u6b22\u4e0b\u68cb\uff1f\u201d\u90a3\u4eba\u8f6c\u8fc7\u5934\u95ee\u5f20\u606a\uff0c\u201c\u7b49\u4f1a\u513f\u966a\u4f60\u4e0b\u4e00\u5c40\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u90a3\u81ed\u68cb\uff0c\u4eba\u5bb6\u8ba9\u4f60\u4e94\u5b50\uff0c\u4f60\u90fd\u672a\u5fc5\u80fd\u8d62\uff0c\u201d\u8bb8\u9e3f\u4f2f\u53d6\u7b11\u90a3\u4eba\uff0c\u5e2e\u5f20\u606a\u4ecb\u7ecd\uff0c\u201c\u859b\u56fd\u534e\uff0c\u53e4\u5efa\u7b51\u4fdd\u62a4\u7814\u7a76\u6240\u7684\u6240\u957f\uff0c\u73b0\u5728\u53e4\u5efa\u6240\u80fd\u770b\u5f97\u5230\u4eba\u5f71\u7684\u4e5f\u5c31\u56db\u4e94\u53f7\u4eba\uff0c\u6574\u5929\u95f2\u7740\u6ca1\u4e8b\u505a\u94bb\u7814\u68cb\u8c31\uff0c\u4e5f\u5c31\u8fd9\u6c34\u5e73\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u90fd\u4e0d\u6e05\u695a\u6d77\u5dde\u5e02\u8fd8\u6709\u53e4\u5efa\u7b51\u4fdd\u62a4\u7814\u7a76\u6240\u4e4b\u7c7b\u7684\u5355\u4f4d\uff0c\u770b\u6765\u8bb8\u9e3f\u4f2f\u4eca\u5929\u627e\u81ea\u5df1\u662f\u8bf4\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u859b\u56fd\u534e\u563f\u7136\u4e00\u7b11\uff0c\u4ed6\u5fc3\u91cc\u5947\u602a\u8bb8\u9e3f\u4f2f\u600e\u4e48\u4f1a\u5c06\u81ea\u5df1\u7684\u8eab\u4efd\u5411\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u4ecb\u7ecd\u5f97\u8fd9\u4e48\u4ed4\u7ec6\uff0c\u65e2\u7136\u68cb\u4e0b\u8fd9\u4e48\u597d\uff0c\u5c11\u4e0d\u5f97\u4ee5\u540e\u4f1a\u7ea0\u7f20\u4eba\u5bb6\uff0c\u638f\u51fa\u70df\u6765\u5206\u7ed9\u5f20\u606a\uff1a\u201c\u90a3\u7b49\u4f1a\u513f\u966a\u8001\u54e5\u6211\u4e0b\u4e00\u76d8\u6307\u5bfc\u68cb\uff0c\u660e\u5929\u8bf7\u5c0f\u8001\u5f1f\u559d\u9152\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e00\u7801\u5f52\u4e00\u7801\uff0c\u4e00\u987f\u9152\u8981\u6362\u4e24\u5c40\u68cb\uff0c\u54ea\u6709\u8fd9\u4e48\u4fbf\u5b9c\u7684\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u8001\u8bb8\uff0c\u6d77\u5dde\u5e02\u91cc\u54ea\u91cc\u8fd8\u80fd\u627e\u5230\u6bd4\u6211\u4eec\u6240\u66f4\u6e05\u6c34\u7684\u8859\u95e8\uff0c\u8981\u6709\u6cb9\u6c34\u635e\uff0c\u54ea\u6015\u5929\u5929\u8bf7\u4f60\u559d\u9152\u90fd\u503c\u5f97\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u6325\u6325\u624b\uff0c\u5bf9\u89c2\u68cb\u7684\u4eba\u8bf4\uff1a\u201c\u859b\u56fd\u534e\u7684\u68cb\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u518d\u8bf4\u559d\u9152\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u634e\u4e0a\u4f60\u4eec\uff0c\u4f60\u4eec\u90fd\u81ea\u5df1\u4e0b\u68cb\u53bb\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u7684\u5a01\u671b\u5f88\u9ad8\uff0c\u58f0\u97f3\u843d\u5730\uff0c\u89c2\u68cb\u7684\u4eba\u5c31\u6563\u4e86\uff0c\u7559\u4e0b\u7ed9\u8bb8\u9e3f\u4f2f\u3001\u5f20\u606a\u3001\u859b\u56fd\u534e\u4e09\u4eba\u8bf4\u8bdd\u7684\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u5bf9\u859b\u56fd\u534e\u8bf4\uff1a\u201c\u4f60\u4ee5\u4e3a\u522b\u4eba\u4e0d\u77e5\u9053\u4f60\u4eec\u6240\u5077\u5077\u7684\u63a5\u5916\u5e02\u7684\u6d3b\uff1f\u8fd8\u6709\u4f60\u5973\u5a7f\u6574\u7684\u90a3\u4e2a\u4e13\u641e\u6587\u7269\u4fee\u7f2e\u7684\u5de5\u7a0b\u961f\uff0c\u91cc\u9762\u7684\u8001\u6280\u5de5\uff0c\u54ea\u4e2a\u4e0d\u662f\u4f60\u5939\u79c1\u786c\u585e\u8fdb\u53bb\u7684\uff1f\u53e4\u5efa\u6240\u4e0d\u662f\u4e00\u76f4\u90fd\u6709\u5341\u4e8c\u4eba\u7684\u7f16\u5236\uff0c\u4e3a\u4ec0\u4e48\u6574\u5929\u53ea\u770b\u5230\u4f60\u4eec\u56db\u4e94\u4e2a\u4eba\u5728\u91cc\u9762\u517b\u8001\uff0c\u5176\u4ed6\u4eba\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u53c8\u5c06\u859b\u56fd\u534e\u624b\u91cc\u7684\u70df\u593a\u8fc7\u6765\uff0c\u201c\u53e4\u5efa\u6240\u6bcf\u4e2a\u6708\u4e24\u767e\u5757\u7684\u5de5\u8d44\uff0c\u591f\u4f60\u62bd\u51e0\u5305\u91d1\u8299\u84c9\uff1f\u201d</p><p>\u3000\u3000\u201c\u73b0\u5728\u5e02\u91cc\u62e8\u7ed9\u7684\u7ecf\u8d39\u592a\u5c11\uff0c\u90fd\u4e0d\u591f\u517b\u5bb6\u6d3b\u53e3\uff0c\u8fd8\u4e0d\u5141\u8bb8\u6211\u4eec\u81ea\u8c0b\u751f\u8def\uff1f\u201d\u859b\u56fd\u534e\u7b11\u7740\u53cd\u9a73\u8bb8\u9e3f\u4f2f\uff0c\u201c\u518d\u8bf4\uff0c\u4f60\u8ba9\u6211\u505a\u7684\u90a3\u4e9b\u6d3b\uff0c\u6211\u53ef\u662f\u5206\u6587\u672a\u6536\uff0c\u6240\u91cc\u5176\u4ed6\u4eba\u5bf9\u6211\u7684\u610f\u89c1\u53ef\u5927\u4e86\uff0c\u627e\u4f60\u4e0b\u76d8\u68cb\u8fd8\u8981\u631f\u4e00\u987f\u9152\uff0c\u4ece\u6765\u5c31\u6ca1\u89c1\u8fc7\u4f60\u8fd9\u53f7\u4eba\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u7b11\u7740\u95ee\u4ed6\uff1a\u201c\u6c99\u7530\u6574\u4e2a\u7684\u90fd\u62c6\u6389\uff0c\u4f60\u4eec\u53e4\u5efa\u6240\u8fd8\u6709\u5b58\u5728\u7684\u610f\u4e49\uff1f\u201d\u8f6c\u5934\u5bf9\u5f20\u606a\u8bf4\uff0c\u201c\u6c99\u7530\u4e1c\u7247\u7684\u660e\u6e05\u5efa\u7b51\u6210\u5343\u4e0a\u4e07\uff0c\u8981\u4e00\u4e00\u6478\u5e95\uff0c\u5de5\u4f5c\u91cf\u5f88\u5927\uff0c\u8fd9\u624d\u5b8c\u6210\u4e09\u5206\u4e4b\u4e00\u4e0d\u5230\uff0c\u5de5\u4f5c\u90fd\u662f\u8001\u859b\u4ed6\u4eec\u5e2e\u52a9\u505a\u7684\uff0c\u6709\u4fee\u7f2e\u4ef7\u503c\u7ea6\u4e09\u5206\u4e4b\u4e00\uff0c\u5176\u4f59\u7684\u53ef\u62c6\u9664\u6309\u7167\u539f\u98ce\u683c\u4eff\u5efa\u3002\u201d</p></article>"}}}]);
