(window.webpackJsonp=window.webpackJsonp||[]).push([[4],{27:function(p){p.exports={chapterTitle:null,title:"\u7b2c4\u7ae0 \u518d\u89c1\u8bb8\u601d",content:"<article><h2>\u7b2c4\u7ae0 \u518d\u89c1\u8bb8\u601d</h2><p>\u3000\u3000\u7b2c\u4e8c\u5929\u5f20\u606a\u9192\u6765\u65f6\uff0c\u7238\u5988\u90fd\u5df2\u7ecf\u79bb\u5f00\u5bb6\u3002</p><p>\u3000\u3000\u4ece\u7238\u5988\u6628\u5929\u7684\u8c08\u8bdd\u91cc\uff0c\u73b0\u5728\u5916\u754c\u5df2\u7ecf\u5c06\u7126\u70b9\u653e\u5230\u8bb8\u601d\u8fd9\u4e2a\u5973\u4eba\u8eab\u4e0a\uff0c\u4f3c\u4e4e\u8bb8\u591a\u4eba\u90fd\u8ba4\u5b9a\u8bb8\u601d\u662f\u5510\u5b66\u8c26\u7684\u79d8\u5bc6\u60c5\u5987\u3002\u8fd9\u4e2a\u5973\u4eba\u7684\u8bc1\u8bcd\uff0c\u662f\u6cd5\u9662\u5224\u5b9a\u5510\u5b66\u8c26\u53d7\u8d3f\u7684\u5173\u952e\u56e0\u7d20\uff0c\u773c\u4e0b\u6216\u8bb8\u53ea\u80fd\u4ece\u8fd9\u4e2a\u5973\u4eba\u8eab\u5b50\u627e\u5230\u4e00\u4e9b\u89e3\u51b3\u95ee\u9898\u7684\u8109\u7edc\u3002</p><p>\u3000\u3000\u5e2e\u5510\u5b66\u8c26\u6d17\u8131\u7f6a\u540d\uff0c\u5f20\u606a\u5012\u4e0d\u5341\u5206\u70ed\u5fc3\uff0c\u5173\u952e\u8981\u8ba9\u7238\u7238\u77e5\u9053\u5982\u4f55\u53bb\u8d81\u5229\u907f\u5bb3\uff0c\u53ea\u8981\u7238\u7238\u8fd8\u5728\u7ee7\u7eed\u5f53\u4ed6\u7684\u5e02\u653f\u5e9c\u526f\u79d8\u4e66\u957f\uff0c\u81ea\u5df1\u597d\u6b79\u4e5f\u7b97\u5e72\u90e8\u5b50\u5f1f\uff0c\u5c31\u7b97\u5341\u516d\u5c81\u4e4b\u540e\u7684\u4eba\u751f\u91cd\u65b0\u6765\u4e00\u56de\uff0c\u4e5f\u662f\u5341\u5206\u5199\u610f\u7684\u4e00\u4ef6\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4e8b\u60c5\u4e0d\u4f1a\u8fd9\u4e48\u7b80\u5355\uff0c\u7238\u7238\u662f\u5510\u5b66\u8c26\u5e26\u53bb\u5e02\u653f\u5e9c\u5e76\u4e00\u624b\u63d0\u62d4\u8d77\u6765\u7684\u4eba\uff0c\u4e0d\u53ef\u80fd\u4e0d\u53d7\u5230\u5510\u5b66\u8c26\u6848\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u5f97\u3001\u5f97\uff0c\u5510\u5b66\u8c26\u6848\u8fd8\u662f\u89e3\u51b3\u95ee\u9898\u7684\u5173\u952e\uff0c\u5f20\u606a\u5fc3\u60f3\u53ea\u8981\u80fd\u4e01\u5411\u5c71\u634f\u9020\u7f6a\u8bc1\u8bec\u9677\u5510\u5b66\u8c26\u7684\u771f\u76f8\u63d0\u524d\u66b4\u9732\uff0c\u81ea\u5df1\u4f5c\u4e3a\u5e72\u90e8\u5b50\u5f1f\u7684\u5e78\u798f\u4eba\u751f\u6216\u8bb8\u4f1a\u4e00\u76f4\u5ef6\u7eed\u4e0b\u53bb\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u5403\u8fc7\u65e9\u996d\uff0c\u5f20\u606a\u5750\u8f66\u8d76\u5230\u8c61\u5c71\u98ce\u666f\u533a\uff0c\u592a\u9633\u624d\u5f00\u59cb\u70bd\u70ed\u8d77\u6765\u3002\u4e03\u6708\u7684\u5929\u6c14\u708e\u70ed\uff0c\u867d\u7136\u8c61\u5c71\u68ee\u6797\u533a\u662f\u6d77\u5dde\u6700\u4f73\u7684\u907f\u6691\u5730\u4e4b\u4e00\uff0c\u7531\u4e8e\u4e0d\u662f\u5468\u672b\u7684\u7f18\u6545\uff0c\u68ee\u6797\u516c\u56ed\u95e8\u53e3\u7684\u6e38\u4eba\u5f88\u5c11\u3002</p><p>\u3000\u3000\u5728\u8c61\u5c71\u68ee\u6797\u516c\u56ed\u5357\u95e8\u7684\u5e7f\u573a\u4e0a\uff0c\u6709\u8bb8\u591a\u6d17\u5370\u5e97\uff0c\u4e5f\u53ef\u4ee5\u5f88\u65b9\u4fbf\u7684\u79df\u5230\u7167\u76f8\u673a\u3002\u5f20\u606a\u5fc3\u60f3\u5c31\u7b97\u81ea\u5df1\u8bf4\u51fa\u5510\u5b66\u8c26\u6848\u7684\u771f\u76f8\uff0c\u7238\u7238\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u80fd\u6709\u8bf4\u670d\u529b\uff0c\u53ea\u6709\u62cd\u5230\u5f53\u4e8b\u4eba\u7684\u7167\u7247\u3002</p><p>\u3000\u3000\u5f20\u606a\u79df\u4e86\u4e00\u53ea\u6309\u52a8\u5feb\u95e8\u4e0d\u4f1a\u53d1\u751f\u5494\u5693\u54cd\u58f0\u7684\u7167\u76f8\u673a\uff0c\u4ece\u5357\u95e8\u5e7f\u573a\u5750\u4e13\u95e8\u7684\u6e38\u89c8\u8f66\u8d76\u5230\u5c71\u5317\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u4e5d\u4e5d\u5e74\u6d77\u5dde\u5e02\u4e2d\u7ea7\u6cd5\u9662\u5411\u516c\u4f17\u6709\u9650\u7684\u516c\u5e03\u4e01\u5411\u5c71\u6848\u7684\u7ec6\u8282\uff0c\u9664\u4e86\u4e01\u5411\u5c71\u7684\u5fc3\u8179\uff0c\u6d77\u5dde\u5e02\u51e0\u4e4e\u6ca1\u6709\u4eba\u77e5\u9053\u8c61\u5c71\u5317\u9e93\u91cc\u4e00\u5ea7\u4e0d\u5f88\u8d77\u773c\u7684\u7ea2\u7816\u522b\u5885\u662f\u4e01\u5411\u5c71\u5f53\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u4e4b\u540e\u5728\u6d77\u5dde\u5e02\u7684\u540e\u5bab\u3002</p><p>\u3000\u3000\u4e01\u5411\u5c71\u6848\u7ed3\u4e4b\u540e\u7684\u90a3\u4e2a\u590f\u5929\uff0c\u5f20\u606a\u7279\u5730\u8d76\u5230\u8fd9\u5ea7\u7ea2\u7816\u522b\u5885\u53c2\u89c2\u8fc7\u3002\u6bd4\u8f83\u6b64\u65f6\uff0c\u90a3\u5e94\u8be5\u662f\u6574\u6574\u4e94\u5e74\u4e4b\u540e\u7684\u4e8b\u60c5\u3002\u867d\u7136\u8bf4\u65f6\u95f4\u63d0\u524d\u4e86\u4e94\u5e74\uff0c\u4f46\u662f\u666f\u81f4\u51e0\u4e4e\u770b\u4e0d\u51fa\u53d8\u5316\u3002\u5927\u6982\u518d\u8fc7\u4e94\u5e74\uff0c\u8fd9\u6761\u7ed9\u8302\u5bc6\u7684\u679d\u53f6\u906e\u4f4f\u7684\u5e7d\u6df1\u7684\u6c34\u6ce5\u752c\u9053\uff0c\u8fd8\u4f1a\u5076\u5c14\u6709\u98ce\u5439\u843d\u7684\u53f6\u5b50\u3002</p><p>\u3000\u3000\u4e00\u9053\u767d\u57a9\u77f3\u5806\u780c\u7684\u9662\u5899\u6a2a\u5728\u6c34\u6ce5\u752c\u9053\u7684\u5c3d\u5934\uff0c\u53ea\u9732\u51fa\u522b\u5885\u9601\u697c\u7684\u4e00\u89d2\u3002\u6df1\u7ea2\u8272\u7684\u7816\u5899\uff0c\u9601\u697c\u4fa7\u9762\u7684\u5c0f\u7a97\u6b63\u5bf9\u7740\u6c34\u6ce5\u752c\u9053\uff0c\u7ad9\u5728\u8fd9\u91cc\u8fd8\u770b\u4e0d\u89c1\u6b63\u95e8\u3002</p><p>\u3000\u3000\u8fd9\u5ea7\u5c0f\u7ea2\u697c\u53ea\u6709\u4e01\u5411\u5c71\u7684\u4e00\u4e2a\u8fdc\u623f\u4eb2\u621a\u65e5\u5e38\u7167\u7ba1\uff0c\u5f20\u606a\u7a7f\u8fc7\u6797\u5b50\u7ed5\u5230\u522b\u5885\u7684\u6b63\u9762\uff0c\u94c1\u76ae\u95e8\u7d27\u7d27\u7684\u5173\u7740\uff0c\u770b\u4e0d\u89c1\u91cc\u9762\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u8981\u4e0d\u8981\u7ffb\u8fdb\u53bb\u770b\u770b\uff0c\u542c\u5230\u8fdc\u5904\u6709\u6c7d\u8f66\u9a76\u6765\uff0c\u5c06\u8eab\u5b50\u85cf\u5230\u8302\u5bc6\u7684\u679d\u53f6\u540e\uff0c\u770b\u89c1\u4e00\u8f86\u9ed1\u8272\u7684\u5c3c\u6851\u5728\u9662\u95e8\u524d\u505c\u4e86\u4e0b\u6765\uff0c\u4ece\u8f66\u724c\u4e0a\u770b\u4e0d\u51fa\u662f\u8c01\u7684\u8f66\u3002\u8f66\u5b50\u5c31\u5728\u9662\u95e8\u524d\u505c\u4e86\u4e00\u4f1a\u513f\uff0c\u9662\u95e8\u8ba9\u4eba\u4ece\u91cc\u9762\u7ed9\u6253\u5f00\uff0c\u5c3c\u6851\u8f66\u968f\u540e\u5c31\u5f00\u4e86\u8fdb\u53bb\uff0c\u9662\u5b50\u91cc\u8fd8\u505c\u7740\u4e00\u8f86\u7ea2\u8272\u7684\u82b1\u51a0\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u8bb8\u601d\u5982\u679c\u8fd9\u65f6\u5019\u5df2\u7ecf\u6210\u4e3a\u4e01\u5411\u5c71\u7684\u60c5\u5987\uff0c\u90a3\u8fd9\u8f86\u7ea2\u8272\u7684\u82b1\u51a0\u6781\u53ef\u80fd\u5c31\u662f\u8bb8\u601d\u7684\uff0c\u4e3e\u8d77\u7167\u76f8\u673a\u98de\u5feb\u6309\u4e0b\u5feb\u95e8\u3002</p><p>\u3000\u3000\u7167\u76f8\u673a\u6ca1\u6709\u8c03\u95ea\u5149\uff0c\u6797\u5b50\u91cc\u7684\u5149\u7ebf\u5f88\u6697\uff0c\u4eba\u79bb\u5f97\u8fd9\u4e48\u8fdc\uff0c\u4e0d\u6e05\u695a\u8fd9\u6b3e\u666e\u901a\u7684\u5c3c\u5eb7\u5149\u5b66\u76f8\u673a\u80fd\u4e0d\u80fd\u5c06\u8f66\u5b50\u62cd\u6e05\u695a\u3002</p><p>\u3000\u3000\u7b49\u5230\u4e2d\u5348\uff0c\u9662\u95e8\u518d\u6b21\u6253\u5f00\u7684\u65f6\u5019\uff0c\u5f20\u606a\u53ea\u62a2\u62cd\u5230\u4e01\u5411\u5c71\u5f2f\u8eab\u94bb\u8fdb\u5c3c\u6851\u8f66\u7684\u955c\u5934\u3002\u5c3c\u6851\u8f66\u5148\u9a76\u51fa\u9662\u5b50\uff0c\u5927\u6982\u8fc7\u4e86\u4e24\u4e09\u5206\u949f\uff0c\u7ea2\u8272\u82b1\u51a0\u624d\u7f13\u7f13\u542f\u52a8\uff0c\u4f46\u662f\u82b1\u51a0\u8f66\u7684\u4e3b\u4eba\u65e9\u5c31\u5750\u8fdb\u8f66\u91cc\uff0c\u5f20\u606a\u65e0\u6cd5\u80af\u5b9a\u91cc\u9762\u7684\u4eba\u662f\u4e0d\u662f\u8bb8\u601d\u3002</p><p>\u3000\u3000\u53ea\u8981\u80fd\u67e5\u5230\u7ea2\u8272\u82b1\u51a0\u7684\u4e3b\u4eba\u5c31\u662f\u8bb8\u601d\uff0c\u8fd9\u51e0\u5f20\u7167\u7247\u5c31\u80fd\u8bf4\u660e\u4e00\u4e9b\u95ee\u9898\u3002\u5f20\u606a\u6ca1\u6709\u7ffb\u8fdb\u53bb\u627e\u4e01\u5411\u5c71\u7684\u7f6a\u8bc1\uff0c\u90a3\u592a\u5192\u9669\u4e86\uff0c\u800c\u662f\u987a\u7740\u539f\u8def\u91cd\u65b0\u56de\u5230\u8c61\u5c71\u516c\u56ed\u7684\u5357\u95e8\uff0c\u60f3\u627e\u4e00\u5bb6\u6d17\u5370\u5e97\u51b2\u6d17\u80f6\u5377\uff0c\u5374\u770b\u89c1\u90a3\u8f86\u7ea2\u8272\u7684\u82b1\u51a0\u8f7f\u8f66\u5c31\u505c\u5728\u5357\u95e8\u5e7f\u573a\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u56db\u5904\u770b\u4e86\u770b\uff0c\u9664\u4e86\u906e\u9633\u4f1e\u4e0b\u6446\u644a\u7684\u4eba\uff0c\u5e7f\u573a\u4e0a\u53ea\u6709\u4e94\u516d\u4e2a\u6e38\u5ba2\uff0c\u770b\u4e0a\u53bb\u90fd\u4e0d\u50cf\u8fd9\u7ea2\u8272\u82b1\u51a0\u7684\u4e3b\u4eba\u3002</p><p>\u3000\u3000\u9644\u8fd1\u6ca1\u6709\u5c3c\u6851\u8f66\uff0c\u5f20\u606a\u60f3\u4e0d\u901a\u7ea2\u8272\u82b1\u51a0\u7684\u4e3b\u4eba\u6b64\u65f6\u8fd8\u6709\u5fc3\u60c5\u5728\u8fd9\u91cc\u6e38\u5c71\u73a9\u6c34\uff1f\u4fa7\u7740\u8eab\u5b50\u8d70\u8fdb\u4e00\u5bb6\u6d17\u5370\u5e97\uff0c\u773c\u775b\u8fd8\u770b\u7740\u5e7f\u573a\u4e0a\u7684\u8f7f\u8f66\uff0c\u6ca1\u6ce8\u610f\u6709\u4eba\u4ece\u91cc\u9762\u51fa\u6765\uff0c\u4e00\u5934\u649e\u4e0a\u53bb\uff0c\u534a\u7247\u80a9\u8180\u7ed9\u51b0\u51c9\u7684\u996e\u6599\u6d47\u5f97\u900f\u6e7f\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\uff0c\u5bf9\u4e0d\u8d77\u2026\u2026\u201d\u5bf9\u65b9\u4e00\u4e2a\u52b2\u7684\u9053\u6b49\uff0c\u614c\u624b\u614c\u811a\u7684\u6253\u5f00\u624b\u63d0\u888b\u7ffb\u627e\u4e1c\u897f\uff0c\u79c0\u53d1\u906e\u4f4f\u6574\u5f20\u8138\uff0c\u53ea\u9732\u51fa\u4e00\u622a\u767d\u817b\u7684\u9888\u8116\u5b50\uff0c\u7ed9\u4eba\u5f02\u5e38\u7684\u7ec6\u817b\u4e4b\u611f\uff0c\u4f3c\u4e4e\u773c\u775b\u770b\u7740\uff0c\u5c31\u80fd\u611f\u89c9\u5230\u90a3\u8ba9\u4eba\u5fc3\u60ca\u9b42\u8361\u7684\u8f6f\u5f39\u89e6\u611f\u3002</p><p>\u3000\u3000\u7edd\u5bf9\u662f\u4e00\u4e2a\u7f8e\u5973\uff0c\u6b64\u65f6\u7684\u5f20\u606a\u53ef\u4e0d\u662f\u9752\u6da9\u65e0\u77e5\u7684\u5341\u516d\u5c81\u5c11\u5e74\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u9000\u540e\u4e00\u6b65\uff0c\u60f3\u770b\u6e05\u79c0\u53d1\u906e\u63a9\u4e0b\u662f\u4f55\u7b49\u7cbe\u81f4\u65e0\u6687\u7684\u5bb9\u989c\u3002\u5bf9\u65b9\u5148\u62ac\u8d77\u5934\u6765\uff0c\u65e0\u8f9c\u53c8\u5185\u759a\u7684\u773c\u775b\u8ba9\u5f20\u606a\u77ac\u95f4\u5fc3\u731b\u7684\u8df3\u4e86\u4e00\u4e0b\uff0c\u5728\u90a3\u77ac\u95f4\uff0c\u5f20\u606a\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u62e5\u6709\u8fd9\u53cc\u7f8e\u4e3d\u773c\u775b\u7684\u5973\u4eba\u4f1a\u5411\u7701\u68c0\u67e5\u7ec4\u634f\u9020\u5510\u5b66\u8c26\u53d7\u8d3f\u7684\u614c\u8a00\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7559\u5728\u5f20\u606a\u8bb0\u5fc6\u91cc\u7684\u662f\u4e00\u5f20\u6194\u60b4\u4e0d\u582a\u7684\u8138\uff0c\u90a3\u65f6\u7684\u5f20\u606a\u521a\u8bfb\u5927\u4e00\uff0c\u6e05\u6da9\u800c\u7eaf\u771f\uff0c\u8fd8\u4e0d\u4f1a\u6b23\u8d4f\u6210\u719f\u5973\u4eba\u90a3\u79cd\u88ab\u98ce\u96e8\u6467\u6b8b\u540e\u6194\u60b4\u7684\u7f8e\uff0c\u4f46\u7ecf\u5386\u8bb8\u591a\u4e8b\u60c5\u4e4b\u540e\u7684\u7075\u9b42\u91cd\u65b0\u56de\u5230\u5341\u516d\u5c81\u7684\u5c11\u5e74\u8eaf\u4f53\u4e4b\u5185\uff0c\u5374\u7ed9\u8fd9\u5f20\u6210\u719f\u8273\u4e3d\u7684\u5bb9\u989c\u9707\u6151\u5f97\u5fc3\u65cc\u6447\u8361\u3002</p><p>\u3000\u3000\u8bb8\u601d\u8eab\u6750\u9ad8\u6311\uff0c\u7a7f\u7740\u5ae9\u9ec4\u8272\u7684\u8fde\u8863\u88d9\uff0c\u8170\u95f4\u624e\u7740\u624b\u638c\u5bbd\u7684\u725b\u76ae\u5e26\uff0c\u6108\u53d1\u886c\u6258\u8170\u80a2\u7684\u7ea4\u7ec6\uff0c\u6210\u719f\u8273\u4e3d\u7684\u9762\u5bb9\u65e2\u4e0d\u75b2\u60eb\uff0c\u4e5f\u4e0d\u6194\u60b4\uff0c\u85cf\u7740\u6de1\u6de1\u54c0\u6101\u7684\u7f8e\u7738\u593a\u4eba\u5fc3\u9b44\uff0c\u5927\u7ea6\u6709\u4e8c\u5341\u4e09\u56db\u5c81\uff0c\u6216\u8bb8\u8fd8\u8981\u5927\u4e00\u4e9b\uff0c\u6bd5\u7adf\u7f8e\u4e3d\u7684\u5973\u4eba\u4e0d\u5bb9\u6613\u770b\u51fa\u5979\u4eec\u771f\u5b9e\u7684\u5e74\u9f84\u3002\u5f20\u606a\u5b8c\u5168\u80fd\u7406\u89e3\u5988\u5988\u4e3a\u4ec0\u4e48\u7528\u5996\u7cbe\u8fd9\u4e2a\u5b57\u773c\u6765\u5f62\u5bb9\u5979\uff0c\u800c\u5728\u5988\u5988\u8bf4\u8bb8\u601d\u53ef\u80fd\u662f\u5510\u5b66\u8c26\u7684\u60c5\u4eba\u65f6\u7238\u7238\u4e3a\u4ec0\u4e48\u6ca1\u6709\u575a\u51b3\u7684\u53cd\u9a73\uff0c\u5f20\u606a\u5fc3\u60f3\u81ea\u5df1\u6709\u8db3\u591f\u7684\u6743\u52bf\uff0c\u4e5f\u4f1a\u5fcd\u4e0d\u4f4f\u5c06\u8fd9\u6837\u7684\u5973\u4eba\u636e\u4e3a\u5df1\u6709\u3002\u5976\u5976\u7684\uff0c\u5510\u5b66\u8c26\u4ed6\u662f\u526f\u5e02\u957f\uff0c\u662f\u4e00\u4e2a\u7537\u4eba\uff0c\u4f46\u4e0d\u662f\u4ec0\u4e48\u67f3\u4e0b\u60e0\uff0c\u5c31\u7b97\u4e4b\u524d\u5b88\u8eab\u5982\u7389\uff0c\u5927\u6982\u5728\u770b\u5230\u8bb8\u601d\u4e4b\u540e\uff0c\u4e5f\u4e0d\u4f1a\u518d\u60f3\u53bb\u505a\u4ec0\u4e48\u67f3\u4e0b\u60e0\u5427\u3002</p><p>\u3000\u3000\u4f46\u662f\u8fd9\u65f6\u5019\uff0c\u8bb8\u601d\u5e94\u8be5\u662f\u4e01\u5411\u5c71\u7684\u5973\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u8214\u4e86\u8214\u53d1\u5e72\u7684\u5634\u5507\uff0c\u55d3\u5b50\u773c\u4e5f\u6709\u70b9\u5e72\u6da9\u3002\u5728\u524d\u4e16\uff0c\u5f20\u606a\u4e5f\u7b97\u5e7f\u8bc6\u7f8e\u5973\uff0c\u5728\u4ed6\u6240\u8ba4\u8bc6\u7684\u5973\u4eba\u4e2d\uff0c\u4e5f\u53ea\u6709\u9648\u5b81\u4e0e\u5510\u5b66\u8c26\u7684\u5973\u513f\u5510\u5a67\u80fd\u4e0e\u773c\u4e0b\u7684\u8bb8\u601d\u76f8\u6bd4\uff0c\u867d\u7136\u90fd\u662f\u4e07\u91cc\u6311\u4e00\u7684\u7edd\u8272\uff0c\u4f46\u662f\u5979\u4eec\u7684\u6c14\u8d28\u5374\u8fe5\u7136\u4e0d\u540c\uff0c\u76f8\u5bf9\u9648\u5b81\u7684\u51b7\u8273\u6e05\u7eaf\u3001\u5510\u5a67\u7684\u751c\u7f8e\u5929\u771f\uff0c\u5f20\u606a\u76f8\u4fe1\u8bb8\u601d\u7684\u7f8e\u66f4\u80fd\u98a0\u5012\u4f17\u751f\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6ce8\u610f\u4f60\u8fdb\u6765\u2026\u2026\u201d\u8bb8\u601d\u5e76\u6ca1\u60f3\u5230\u773c\u524d\u5c11\u5e74\u6b64\u65f6\u7684\u795e\u60c5\u9664\u4e86\u60d1\u4e8e\u5979\u7684\u7f8e\u4e3d\u4e4b\u5916\u8fd8\u85cf\u7740\u5176\u4ed6\u590d\u6742\u7684\u60c5\u611f\uff0c\u638f\u51fa\u624b\u5e15\u8981\u53bb\u64e6\u5f20\u606a\u8eab\u4e0a\u7684\u53ef\u4e50\u3002</p><p>\u3000\u3000\u5f20\u606a\u95fb\u7740\u8bb8\u601d\u8eab\u4e0a\u98d8\u6765\u6e05\u5e7d\u7684\u4f53\u9999\uff0c\u4f38\u624b\u8981\u63a5\u624b\u5e15\uff0c\u89c6\u7ebf\u7981\u4e0d\u4f4f\u6ed1\u843d\u5230\u5979\u7834\u8863\u6b32\u51fa\u7684\u4e30\u6ee1\u80f8\u90e8\u4e0a\uff0c\u201c\u4f60\u8fd9\u91cc\u4e5f\u6e7f\u4e86\uff0c\u8981\u4e0d\u4f60\u5148\u64e6\u64e6\uff1b\u770b\u6211\u8fd9\u4e00\u8eab\u6e7f\u7684\uff0c\u4e5f\u64e6\u4e0d\u5e72\u51c0\u2026\u2026\u201d\u5fc3\u91cc\u5374\u53ef\u60dc\u8bb8\u601d\u7684\u80f8\u90e8\u4e0a\u53ea\u6cfc\u4e86\u51e0\u70b9\u996e\u6599\uff0c\u5370\u51fa\u4e00\u5c0f\u7247\u7ea2\u8272\u7684\u80f8\u8863\uff1b\u7ad9\u5728\u67dc\u53f0\u540e\u7684\u5e97\u4e3b\u4e5f\u5fcd\u4e0d\u4f4f\u63a2\u8fc7\u5934\u6765\u770b\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4fcf\u8138\u4e00\u7ea2\uff0c\u8eab\u5b50\u4fa7\u8fc7\u53bb\uff0c\u907f\u5f00\u5e97\u4e3b\u7684\u773c\u5149\uff0c\u5374\u6ca1\u60f3\u7740\u8981\u8eb2\u5f00\u773c\u524d\u5f20\u606a\u7684\u76ee\u5149\u3002\u62ff\u624b\u5e15\u5728\u80f8\u524d\u64e6\u4e86\u51e0\u4e0b\uff0c\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u64e6\u80f8\u90e8\u65f6\u5e26\u7740\u9886\u53e3\u5f80\u4e0b\u5760\u9732\u51fa\u66f4\u591a\u96ea\u767d\u7684\u4e73\u808c\u6b63\u9971\u4e86\u5f20\u606a\u7684\u773c\u798f\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\uff0c\u8981\u4e0d\u6211\u5e2e\u4f60\u4e70\u4ef6\u65b0\u7684\u6362\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u5173\u7cfb\uff0c\u5916\u9762\u592a\u9633\u5927\uff0c\u4e00\u4f1a\u513f\u5c31\u80fd\u5e72\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u6ca1\u5173\u7cfb\uff1f\u201d\u8bb8\u601d\u4e0d\u786e\u5b9a\u7684\u53c8\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5f20\u606a\u6545\u4f5c\u6f47\u6d12\u7684\u6325\u4e86\u6325\u624b\uff0c\u770b\u7740\u8bb8\u601d\u94bb\u8fdb\u7ea2\u8272\u82b1\u51a0\u3002</p><p>\u3000\u3000\u4e5d\u56db\u5e74\uff0c\u6d77\u5dde\u5e02\u8fd8\u6ca1\u6709\u7acb\u7b49\u53ef\u53d6\u7684\u5feb\u901f\u6d17\u5370\u5e97\uff0c\u5f20\u606a\u5c06\u80f6\u5377\u4ea4\u7ed9\u4e00\u5bb6\u5e97\u4e3b\u770b\u4e0a\u53bb\u4e0d\u662f\u592a\u597d\u5947\u7684\u6d17\u5370\u5e97\uff0c\u53c8\u5c06\u76f8\u673a\u9000\u4e86\u56de\u53bb\uff0c\u5403\u8fc7\u4e2d\u996d\uff0c\u518d\u56de\u5230\u5357\u95e8\u5e7f\u573a\u65f6\uff0c\u53d1\u73b0\u7ea2\u8272\u82b1\u51a0\u8fd8\u505c\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u201c\u5509\uff0c\u4f60\u8981\u4e0b\u5c71\u7684\u8bdd\uff0c\u6211\u634e\u4f60\u4e00\u7a0b\u2026\u2026\u201d\u5f20\u606a\u7ecf\u8fc7\u65f6\uff0c\u8bb8\u601d\u6258\u7740\u816e\u5e2e\u5bf9\u4ed6\u8bf4\uff0c\u96ea\u767d\u7684\u80f3\u81c2\u6401\u5728\u8f66\u7a97\u4e0a\uff0c\u538b\u51fa\u4e00\u9053\u8840\u75d5\u3002</p><p>\u3000\u3000\u201c\u7b49\u6211\uff1f\u201d\u5f20\u606a\u6307\u6307\u81ea\u5df1\uff0c\u4e0d\u660e\u767d\u8bb8\u601d\u4e3a\u4ec0\u4e48\u53c8\u56de\u6765\u4e86\uff0c\u4f46\u662f\u4e0a\u5929\u7ed9\u4e86\u8fd9\u4e48\u4e00\u4e2a\u8fd1\u6c34\u697c\u53f0\u7684\u673a\u4f1a\uff0c\u8981\u662f\u9519\u8fc7\u5e72\u8106\u53bb\u6b7b\u5f97\u4e86\uff0c\u867d\u8bf4\u5fc3\u91cc\u6709\u4e9b\u7591\u95ee\uff0c\u5f20\u606a\u8fd8\u662f\u8fc5\u901f\u7ed5\u5230\u53f3\u8fb9\uff0c\u6253\u5f00\u8f66\u95e8\uff0c\u534a\u4e2a\u8eab\u5b50\u63a2\u8fdb\u53bb\u65f6\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u8981\u53bb\u5077\u770b\u8bb8\u601d\u96ea\u767d\u7684\u4e73\u808c\u3002</p><p>\u3000\u3000\u8f66\u4ece\u8c61\u5c71\u4e0b\u6765\uff0c\u5f20\u606a\u7aa5\u7740\u8bb8\u601d\u4e30\u8174\u767d\u817b\u7684\u4fa7\u988a\uff0c\u82e5\u6709\u6240\u601d\u3002\u4ed6\u5728\u5fc3\u91cc\u53cd\u590d\u7684\u63a8\u6d4b\u8bb8\u601d\u5728\u5510\u5b66\u8c26\u6848\u4e2d\u6240\u626e\u6f14\u7684\u89d2\u8272\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u7684\u8bb0\u5fc6\u91cc\uff0c\u5510\u5b66\u8c26\u4e5d\u56db\u5e74\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u6539\u5236\u65f6\uff0c\u88ab\u4eba\u68c0\u4e3e\u53d7\u8d3f\uff0c\u7701\u91cc\u5c31\u9488\u5bf9\u8fd9\u4e8b\u6d3e\u51fa\u68c0\u67e5\u7ec4\uff0c\u5f88\u5feb\u5c31\u83b7\u5f97\u5510\u5b66\u8c26\u6536\u53d7\u65b0\u4e30\u96c6\u56e2\u59dc\u660e\u8bda\u8d3f\u8d42\u7684\u8bc1\u636e\u3002\u5728\u6cd5\u9662\u516c\u5f00\u7684\u5224\u8bcd\u4e2d\uff0c\u5510\u5b66\u8c26\u901a\u8fc7\u6253\u62db\u547c\u7684\u5f62\u5f0f\u5c06\u60c5\u5987\u8bb8\u601d\u5b89\u6392\u5230\u65b0\u4e30\u96c6\u56e2\u5de5\u4f5c\uff0c\u65b0\u4e30\u96c6\u56e2\u603b\u7ecf\u7406\u59dc\u660e\u8bda\u901a\u8fc7\u8bb8\u601d\u5411\u5510\u5b66\u8c26\u524d\u540e\u884c\u8d3f\u4e09\u6b21\u5171\u8ba127\u4e07\u5143\uff08\u53db\u5211\u65f6\u8fd8\u8981\u52a0\u4e0a\u8bb8\u601d\u5728\u65b0\u4e30\u96c6\u56e2\u534a\u5e74\u7684\u85aa\u8d44\u6240\u5f97\uff09\uff0c\u4ee5\u4fbf\u5510\u5b66\u8c26\u5728\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u6539\u5236\u5206\u914d\u5229\u76ca\u65f6\uff0c\u7ed9\u65b0\u4e30\u96c6\u56e2\u7ba1\u7406\u5c42\u66f4\u5927\u7684\u7167\u987e\u3002\u4e09\u6b21\u884c\u8d3f\u7684\u6b3e\u9879\u90fd\u6253\u5165\u8bb8\u601d\u7684\u79c1\u4eba\u8d26\u6237\uff0c\u52a0\u4e0a\u8bb8\u601d\u672c\u4eba\u7684\u8bc1\u8bcd\u4e0e\u5510\u5b66\u8c26\u592b\u5987\u8d26\u6237\u4e0a\u9ad8\u8fbe37\u4e07\u7684\u5b58\u6b3e\uff08\u5176\u4e2d16\u4e07\u65e0\u6cd5\u8bf4\u660e\u5408\u6cd5\u6765\u6e90\uff09\uff0c\u5f62\u6210\u5b8c\u6574\u7684\u8bc1\u636e\u94fe\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5927\u6982\u662f\u5728\u7238\u7238\u79bb\u5f00\u6d77\u5dde\u4e4b\u540e\u5411\u68c0\u67e5\u7ec4\u63d0\u4f9b\u90a3\u4efd\u9677\u5510\u5b66\u8c26\u4e8e\u4e07\u52ab\u4e0d\u590d\u7684\u8bc1\u636e\uff0c\u5f20\u606a\u770b\u7740\u8bb8\u601d\u4fa7\u8eab\u51f9\u51f8\u6709\u81f4\u7684\u66f2\u7ebf\uff0c\u5b9e\u5728\u60f3\u50cf\u4e0d\u51fa\u5728\u8fd9\u4e48\u7f8e\u4e3d\u7684\u8eab\u4f53\u91cc\u4f1a\u85cf\u7740\u4e00\u9897\u86c7\u874e\u5fc3\u80a0\u3002</p><p>\u3000\u3000\u4e0e\u5176\u5c06\u8bb8\u601d\u60f3\u50cf\u6210\u86c7\u874e\u7f8e\u5973\uff0c\u5f20\u606a\u5b81\u53ef\u8ba4\u4e3a\u5979\u6709\u7740\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\u3002\u5f20\u606a\u62cd\u62cd\u540e\u8111\u52fa\uff0c\u5fc3\u91cc\u7b11\u81ea\u5df1\u8fd8\u771f\u662f\u5e7c\u7a1a\uff0c\u90fd\u4e8c\u4e16\u4e3a\u4eba\u4e86\uff0c\u8fd8\u662f\u8981\u88ab\u4eba\u7684\u5916\u8868\u8ff7\u60d1\u3002</p></article>"}}}]);
