(window.webpackJsonp=window.webpackJsonp||[]).push([[40],{62:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e5d\u8282 \u4e16\u4e8b\u5982\u68cb",content:"<article><h2>\u7b2c\u4e09\u5341\u4e5d\u8282 \u4e16\u4e8b\u5982\u68cb</h2><p>\u62c9\u6c83\u514b\u662f\u8c01\uff0c\u743c\u6069\u5e76\u4e0d\u6e05\u695a\uff0c\u6682\u65f6\u4e5f\u61d2\u5f97\u5173\u5fc3\u3002\u7a81\u5982\u5176\u6765\u7684\uff0c\u4ed6\u5728\u6821\u56ed\u91cc\u649e\u4e0a\u4e00\u4f4d\u9634\u9b42\u738b\u5b50\uff0c\u800c\u4e14\u8fd8\u662f\u81ea\u5df1\u7684\u4e0a\u53f8\uff1b\u7a81\u5982\u5176\u6765\u7684\uff0c\u4ed6\u5c31\u5df2\u7ecf\u6210\u4e3a\u516c\u52a1\u5458\uff0c\u5e76\u4e14\u88ab\u5916\u6d3e\u51fa\u53bb\u8dd1\u817f\u9001\u4fe1\u2014\u2014\u8fd8\u5fc5\u987b\u7acb\u523b\u51fa\u53d1\u3002</p><p>\u201c\u6211\u8981\u548c\u6211\u59d0\u59d0\u9053\u522b\uff01\u201d\u4ed6\u6297\u8bae\u7740\u3002</p><p>\u201c\u4e0d\u9700\u8981\uff0c\u201d\u8d1f\u8d23\u9001\u4ed6\u51fa\u9634\u9b42\u57ce\u7684\u519b\u5b98\u9762\u65e0\u8868\u60c5\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u4f1a\u901a\u77e5\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u4eca\u5929\u662f\u7eff\u8349\u8282\uff0c\u6211\u6709\u4f11\u606f\u7684\u6743\u5229\uff01\u201d</p><p>\u201c\u786e\u5b9e\uff0c\u5728\u4f11\u606f\u65e5\u8fd8\u575a\u6301\u4e3a\u5e1d\u56fd\u6548\u529b\uff0c\u60a8\u7684\u52e4\u52b3\u548c\u656c\u4e1a\u5f88\u4ee4\u6211\u94a6\u4f69\u3002\u201d</p><p>\u201c\u6211\u8fd8\u6ca1\u7b7e\u52b3\u52a8\u5408\u540c\uff01\u6211\u8fd8\u4e0d\u77e5\u9053\u6211\u7684\u85aa\u6c34\u591a\u5c11\uff01\u201d</p><p>\u519b\u5b98\u50cf\u770b\u7740\u602a\u7269\u4e00\u6837\u770b\u7740\u743c\u6069\uff0c\u5f53\u7136\uff0c\u66f4\u53ef\u80fd\u662f\u6839\u672c\u5c31\u6ca1\u7406\u89e3\u4ed6\u7684\u8bdd\u3002</p><p>\u201c\u81f3\u5c11\u6211\u9700\u8981\u51c6\u5907\u6362\u6d17\u8863\u670d\u2026\u2026\u201d\u743c\u6069\u6709\u6c14\u65e0\u529b\u5730\u575a\u6301\u7740\u3002</p><p>\u201c\u5df2\u7ecf\u66ff\u60a8\u51c6\u5907\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u5c31\u5728\u90a3\u4e2a\u9ec4\u8272\u7684\u5305\u88f9\u91cc\uff0c\u201d\u519b\u5b98\u8bf4\uff0c\u201c\u597d\u4e86\uff0c\u8bf7\u7ad9\u7a33\uff0c\u8981\u964d\u843d\u4e86\u3002\u201d</p><p>\u9a86\u9a7c\u80cc\u4e0a\u9a6e\u7740\u51e0\u4e2a\u5305\u88f9\uff0c\u91cc\u9762\u88c5\u7740\u98df\u7269\u3001\u6c34\u548c\u8863\u670d\u7b49\uff0c\u88ab\u4e00\u8258\u6d6e\u7a7a\u8247\u8f7d\u4e0b\u6765\u3002\u519b\u5b98\u5c06\u9a86\u9a7c\u7684\u7f30\u7ef3\u9012\u7ed9\u743c\u6069\uff0c\u884c\u4e86\u4e2a\u519b\u793c\uff0c\u8d70\u56de\u6d6e\u7a7a\u8247\u4e0a\u3002</p><p>\u201c\u4e00\u5e06\u98ce\u987a\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u6d6e\u7a7a\u8247\u5347\u7a7a\uff0c\u98de\u56de\u6f02\u6d6e\u5728\u7a7a\u4e2d\u7684\u9634\u9b42\u57ce\uff0c\u53ea\u7559\u4e0b\u743c\u6069\u4e00\u4e2a\u4eba\u5446\u5446\u5730\u7ad9\u5728\u6c99\u6f20\u4e0a\u3002\u867d\u7136\u8fd8\u662f\u521d\u6625\uff0c\u4f46\u56e0\u4e3a\u662f\u6b63\u5348\uff0c\u6c99\u6f20\u4e2d\u5df2\u7ecf\u5f88\u70ed\u3002\u811a\u4e0a\u7a7f\u7740\u9774\u5b50\uff0c\u4e0d\u4f1a\u88ab\u70eb\u574f\uff0c\u4f46\u8138\u4e0a\u5df2\u7ecf\u5f00\u59cb\u5728\u4e0d\u505c\u5730\u5192\u6c57\u4e86\u3002</p><p>\u8fd9\u2026\u2026\u5c31\u8fd9\u6837\u4e86\uff1f</p><p>\u4eca\u5929\u65e9\u4e0a\uff0c\u81ea\u5df1\u8fd8\u5728\u548c\u59d0\u59d0\u7eaf\u6d01\u5730\u5171\u8fdb\u65e9\u9910\u2026\u2026\u597d\u5427\uff0c\u987a\u4fbf\u5077\u5077\u60f3\u50cf\u4e86\u4e00\u4e0b\u627e\u4e2a\u4ec0\u4e48\u5408\u9002\u7684\u673a\u4f1a\u628a\u73ca\u5609\u7ed9\u5077\u5403\u4e86\uff1b\u4e8c\u5341\u5206\u949f\u524d\uff0c\u81ea\u5df1\u8fd8\u5728\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\u91cc\u6563\u6b65\uff0c\u4e3a\u4ee5\u540e\u7684\u67af\u71e5\u751f\u6d3b\u611f\u5230\u8ff7\u832b\u800c\u5934\u75bc\uff1b\u5982\u4eca\u5374\u5df2\u7ecf\u5728\u708e\u70ed\u7684\u6c99\u6f20\u91cc\u53d1\u5446\uff0c\u770b\u7740\u5929\u4e0a\u90a3\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u4eff\u4f5b\u96c4\u4f1f\u7684\u7a7a\u4e2d\u5821\u5792\uff0c\u5728\u5730\u9762\u6295\u4e0b\u5de8\u5927\u7684\u9634\u5f71\u3002</p><p>\u867d\u8bf4\u4e00\u76f4\u5c31\u5f88\u5e0c\u671b\u80fd\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u5230\u5916\u9762\u7684\u4e16\u754c\u81ea\u7531\u95ef\u8361\u4e00\u756a\uff0c\u4f46\u8fd9\u4e5f\u672a\u514d\u592a\u7a81\u7136\u592a\u5feb\u4e86\u70b9\u5427\uff0c\u534a\u70b9\u5fc3\u7406\u51c6\u5907\u90fd\u6ca1\u6709\u3002</p><p>\u90a3\u4e48\uff0c\u4e0b\u4e00\u6b65\u600e\u4e48\u529e\uff1f</p><p>\u743c\u6069\u4f1a\u98de\u884c\u672f\uff0c\u4eca\u5929\u65e9\u4e0a\u4e5f\u51c6\u5907\u4e86\uff0c\u8eab\u4e0a\u7a7f\u7740\u957f\u888d\uff0c\u53e3\u888b\u91cc\u90fd\u88c5\u7740\u65bd\u6cd5\u6750\u6599\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u9634\u9b42\u57ce\u867d\u7136\u9ad8\u9ad8\u60ac\u6d6e\u7a7a\u4e2d\uff0c\u4f46\u53ea\u8981\u743c\u6069\u613f\u610f\uff0c\u98de\u4e0a\u53bb\u4f30\u8ba1\u4e5f\u4e0d\u96be\u3002</p><p>\u4f46\u98de\u4e0a\u53bb\u53c8\u80fd\u600e\u4e48\u6837\uff1f\u53ea\u6015\u5341\u6709\u516b\u4e5d\u8981\u88ab\u5f53\u4f5c\u654c\u4eba\u6253\u4e0b\u6765\u3002\u9634\u9b42\u738b\u5b50\u5e03\u96f7\u7eb3\u65af\xb7\u5766\u8212\u5c14\u4eb2\u81ea\u5429\u5490\u4e0b\u8fbe\u7684\u4efb\u52a1\uff0c\u81ea\u5df1\u6ca1\u5b8c\u6210\u5c45\u7136\u5c31\u6562\u56de\u6765\uff0c\u5c31\u7b97\u4e0d\u88ab\u57ce\u5899\u4e0a\u7684\u5de8\u5f29\u5c04\u6b7b\uff0c\u4e5f\u8981\u88ab\u5f53\u505a\u6e0e\u804c\u7f6a\u8bba\u5904\u5427\u3002</p><p>\u7b97\u4e86\uff0c\u65e2\u7136\u56de\u5934\u4e0d\u5f97\uff0c\u5c31\u53ea\u597d\u8d76\u7d27\u53bb\u628a\u8fd9\u4e00\u8d9f\u8dd1\u5b8c\uff0c\u65e9\u65e9\u56de\u6765\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u522b\u7684\u90fd\u6ca1\u4ec0\u4e48\uff0c\u4eba\u751f\u5728\u4e16\u561b\uff0c\u603b\u8981\u5e72\u6d3b\u5de5\u4f5c\u8d5a\u94b1\u5403\u996d\uff0c\u5f53\u516c\u52a1\u5458\u5c31\u5f53\u516c\u52a1\u5458\u5427\uff0c\u8dd1\u817f\u5c31\u8dd1\u817f\u5427\uff0c\u8fd9\u4e9b\u4ed6\u90fd\u6ca1\u610f\u89c1\u2014\u2014\u53ef\u662f\u8ba9\u73ca\u5609\u4e00\u4e2a\u4eba\u5446\u7740\u9634\u9b42\u57ce\u91cc\uff0c\u743c\u6069\u8fd8\u771f\u662f\u4e0d\u653e\u5fc3\u3002\u4ee5\u524d\u5e74\u7eaa\u5c0f\u4e5f\u5c31\u7f62\u4e86\uff0c\u5982\u4eca\u5341\u4e94\u5c81\u7684\u5973\u5b69\uff0c\u6b63\u662f\u60c5\u7aa6\u521d\u5f00\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u4e0d\u5728\u8eab\u8fb9\u4fdd\u62a4\uff0c\u4e07\u4e00\u88ab\u67d0\u4e2a\u4e0d\u6000\u597d\u610f\u7684\u8272\u72fc\u4e58\u865a\u800c\u5165\u2026\u2026\u53e3\u80e1\uff0c\u90a3\u5c31\u662f\u81ea\u5df1\u56de\u6765\u628a\u4ed6\u788e\u5c38\u4e07\u6bb5\u4e5f\u4e0d\u80fd\u89e3\u5fc3\u5934\u4e4b\u6068\u5440\u3002</p><p>\u65e9\u53bb\u65e9\u56de\uff0c\u65e9\u53bb\u65e9\u56de\u3002</p><p>\u6253\u5b9a\u4e86\u8fd9\u4e2a\u4e3b\u610f\uff0c\u743c\u6069\u8fde\u5fd9\u638f\u51fa\u521a\u624d\u5e03\u96f7\u7eb3\u65af\u9012\u7ed9\u81ea\u5df1\u7684\u5730\u56fe\uff0c\u4ed4\u7ec6\u7814\u7a76\u4e86\u4e00\u4e0b\u8def\u7ebf\uff0c\u5224\u65ad\u4e86\u4e00\u4e0b\u65b9\u5411\uff0c\u722c\u4e0a\u9a86\u9a7c\u5f00\u59cb\u6643\u6643\u60a0\u60a0\u5730\u5f80\u5317\u65b9\u524d\u8fdb\u3002</p><p>\u203b\u203b\u203b</p><p>\u9634\u9b42\u57ce\u738b\u5bab\u7684\u67d0\u4e2a\u623f\u95f4\u91cc\uff0c\u5e03\u96f7\u7eb3\u65af\u60a0\u95f2\u81ea\u5f97\u5730\u559d\u7740\u7edb\u7ea2\u8272\u7684\u8461\u8404\u9152\uff0c\u770b\u7740\u638c\u4e2d\u6258\u7740\u7684\u6c34\u6676\u7403\uff0c\u91cc\u9762\u743c\u6069\u7684\u8eab\u5f71\u6b63\u6e10\u6e10\u8fdc\u53bb\uff0c\u6e6e\u6ca1\u5728\u9ec4\u6c99\u4e2d\u3002</p><p>\u201c\u5e72\u5f97\u4e0d\u9519\uff0c\u68b5\u52a0\u591a\uff0c\u201d\u5e74\u8f7b\u7684\u9634\u9b42\u738b\u5b50\u5938\u5956\u7740\uff0c\u201c\u59d4\u5c48\u4f60\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4e86\u5e1d\u56fd\u3002\u201d\u7ad9\u5728\u738b\u5b50\u8eab\u540e\u7684\u4e00\u4e2a\u4e2d\u5e74\u4eba\u5fae\u5fae\u8eac\u8eab\uff0c\u606d\u656c\u5730\u56de\u7b54\uff0c\u4ed6\u7a7f\u7740\u9ed1\u8272\u7684\u5deb\u5e08\u957f\u888d\uff0c\u4f5c\u5de5\u7cbe\u81f4\uff0c\u6837\u5f0f\u975e\u5e38\u53e4\u6734\uff0c\u80f8\u53e3\u90e8\u4f4d\u7ee3\u7740\u4e00\u53ea\u957f\u5634\u7fe0\u9e1f\u7684\u5fbd\u8bb0\u3002\u4ed6\u7684\u76ae\u80a4\u540c\u6837\u767d\u7699\uff0c\u5374\u4e5f\u9690\u9690\u6cdb\u7740\u6697\u7070\u8272\u3002</p><p>\u4e3a\u4e86\u5e1d\u56fd\uff0c\u727a\u7272\u51e0\u4e2a\u4eba\uff0c\u7eb5\u7136\u662f\u81ea\u5df1\u7684\u8840\u8109\u5b50\u55e3\uff0c\u5fe0\u5fc3\u803f\u803f\u7684\u68b5\u52a0\u591a\u4e5f\u4e0d\u4f1a\u6709\u4e1d\u6beb\u5728\u610f\u3002</p><p>\u201c\u53ea\u662f\u2026\u2026\u201d\u4ed6\u72b9\u8c6b\u7740\uff0c\u4eff\u4f5b\u4e0d\u77e5\u9053\u662f\u5426\u8be5\u63d0\u51fa\u81ea\u5df1\u7684\u770b\u6cd5\uff0c\u201c\u8001\u5e08\uff0c\u5e93\u80af\u5bb6\u65cf\u5df2\u7ecf\u662f\u5168\u9762\u5012\u5411\u795e\u6bbf\u4e86\uff0c\u8fd9\u6b21\u662f\u4e0d\u662f\u2026\u2026\u201d</p><p>\u201c\u603b\u8981\u4ed8\u51fa\u70b9\u4ee3\u4ef7\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u4e0d\u4ee5\u4e3a\u610f\u5730\u7b11\u7740\uff0c\u201c\u4ee3\u4ef7\u8d8a\u5927\uff0c\u98ce\u9669\u8d8a\u5927\uff0c\u56de\u62a5\u4e5f\u5c31\u8d8a\u5927\u3002\u201d</p><p>\u8fd9\u4e2a\u9053\u7406\u68b5\u52a0\u591a\u5f53\u7136\u61c2\uff0c\u4ed6\u771f\u6b63\u60f3\u8bf4\u7684\u5176\u5b9e\u662f\u4e0b\u9762\u4e00\u53e5\u8bdd\u3002</p><p>\u201c\u53ef\u662f\uff0c\u8001\u5e08\uff0c\u60a8\u771f\u7684\u8ba4\u4e3a\u4ed6\u662f\u6700\u597d\u7684\u4eba\u9009\uff1f\u4f9d\u6211\u6240\u89c1\uff0c\u4ed6\u6050\u6015\u8fd8\u6bd4\u4e0d\u4e0a\u6590\u6d4e\uff0c\u66f4\u522b\u63d0\u5e93\u80af\u2026\u2026\u201d</p><p>\u201c\u4ed6\u5f53\u7136\u4e0d\u662f\u6700\u597d\u7684\u4eba\u9009\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bf4\uff0c\u201c\u4ed6\u662f\u6700\u4e0d\u574f\u7684\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d\u68b5\u52a0\u591a\u663e\u7136\u4e0d\u660e\u767d\u5e03\u96f7\u7eb3\u65af\u7684\u610f\u601d\u3002</p><p>\u201c\u8fd9\u4e16\u754c\u4e0a\u4ece\u6765\u5c31\u6ca1\u6709\u6700\u597d\u7684\uff0c\u4f46\u5374\u6709\u6700\u4e0d\u574f\u7684\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u5c48\u8d77\u624b\u6307\uff0c\u8f7b\u6572\u7740\u6c34\u6676\u7403\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u54cd\u58f0\uff0c\u201c\u4ece\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u6765\u8bf4\uff0c\u4ed6\u53ea\u662f\u4e2a\u5e73\u5e73\u65e0\u5947\u7684\u5e73\u5e38\u4eba\u2014\u2014\u5982\u679c\u8bf4\u8d44\u8d28\uff0c\u4ed6\u8fd9\u79cd\u8d44\u8d28\u7684\uff0c\u57ce\u91cc\u968f\u4fbf\u90fd\u80fd\u627e\u51fa\u767e\u516b\u5341\u4e2a\u3002\u4f46\u5982\u679c\u5c31\u67d0\u4e00\u70b9\u800c\u8bba\u2026\u2026\u68b5\u52a0\u591a\uff0c\u76f8\u4fe1\u6211\uff0c\u4ed6\u662f\u4e2a\u5929\u751f\u7684\u5927\u5965\u672f\u5e08\uff0c\u8fd9\u4e00\u70b9\uff0c\u975e\u5e38\u96be\u5f97\u3002\u201d</p><p>\u201c\u6211\u60f3\uff0c\u201d\u738b\u5b50\u770b\u7740\u6c34\u6676\u7403\uff0c\u8138\u4e0a\u9732\u51fa\u90a3\u5929\u771f\u65e0\u90aa\u7684\u7b11\u5bb9\uff0c\u201c\u6211\u80fd\u9884\u89c1\u5230\u4ed6\u7684\u547d\u8fd0\u3002\u201d</p><p>\u201c\u547d\u8fd0\uff1f\u201d\u68b5\u52a0\u591a\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u8001\u5e08\uff0c\u547d\u8fd0\u662f\u4e16\u95f4\u6700\u5965\u5999\u83ab\u6d4b\u4e4b\u7269\uff0c\u5c31\u8fde\u795e\u7947\u90fd\u4e0d\u53ef\u80fd\u9884\u89c1\u2026\u2026\u201d</p><p>\u201c\u77a7\uff0c\u77a7\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u5fae\u7b11\u7740\uff0c\u201c\u8fd9\u5c31\u662f\u4f60\u6bd4\u4ed6\u6240\u6b20\u7f3a\u7684\u6700\u5173\u952e\u4e00\u70b9\u3002\u201d</p><p>\u201c\u60a8\u7684\u610f\u601d\u662f\u6307\u2026\u2026\u201d\u68b5\u52a0\u591a\u5e76\u4e0d\u5f88\u660e\u767d\u5e03\u96f7\u7eb3\u65af\u7684\u610f\u601d\u3002</p><p>\u201c\u547d\u8fd0\u662f\u6700\u590d\u6742\u83ab\u6d4b\u4e4b\u7269\uff0c\u4f46\u5176\u5b9e\u4e5f\u6700\u7b80\u5355\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u5c06\u8eab\u4f53\u9760\u5728\u5bbd\u5927\u67d4\u8f6f\u7684\u6905\u80cc\u4e0a\uff0c\u4ef0\u8d77\u5934\uff0c\u770b\u7740\u9ed1\u6c89\u6c89\u7684\u5929\u82b1\u677f\uff0c\u201c\u8bf8\u795e\u4eec\u62e5\u6709\u592a\u8fc7\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u5bfc\u81f4\u4ed6\u4eec\u770b\u4e0d\u6e05\u6700\u7b80\u5355\u7684\u4e8b\u5b9e\uff0c\u552f\u6709\u51e1\u4eba\u624d\u771f\u6b63\u80fd\u7406\u89e3\u51e1\u4eba\uff0c\u68b5\u52a0\u591a\uff0c\u7262\u8bb0\u8fd9\u4e00\u70b9\u3002\u201d</p><p>\u201c\u662f\uff0c\u4f46\u662f\uff0c\u745e\u74e6\u5170\u9601\u4e0b\u5e94\u8be5\u4e5f\u5df2\u7ecf\u6ce8\u610f\u5230\u4ed6\uff0c\u6211\u62c5\u5fc3\u2026\u2026\u201d</p><p>\u201c\u745e\u74e6\u5170\u5927\u7267\u5e08\u4e48\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u8ba5\u8bbd\u5730\u7b11\u7b11\uff0c\u201c\u653e\u5fc3\uff0c\u4ed6\u592a\u2026\u2026\u5514\uff0c\u4ece\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u6765\u8bf4\uff0c\u4ed6\u592a\u5584\u826f\u4e86\u3002\u201d</p><p>\u68b5\u52a0\u591a\u7684\u8138\u4e0a\u9732\u51fa\u602a\u5f02\u7684\u8868\u60c5\u3002\u5341\u4e8c\u9634\u9b42\u738b\u5b50\u4e4b\u9996\uff0c\u745e\u74e6\u5170\xb7\u5766\u8212\u5c14\u5927\u7267\u5e08\uff0c\u6697\u591c\u5973\u795e\u838e\u5c14\u5728\u9634\u9b42\u57ce\u7684\u6700\u9ad8\u4ee3\u8a00\u4eba\uff0c\u5c45\u7136\u4f1a\u88ab\u8bc4\u4ef7\u4e3a\u201c\u592a\u5584\u826f\u4e86\u201d\uff0c\u8fd9\u7b80\u76f4\u662f\u4e16\u95f4\u6700\u5927\u7684\u7b11\u8bdd\u3002</p><p>\u4f46\u770b\u5e03\u96f7\u7eb3\u65af\u7684\u795e\u60c5\uff0c\u663e\u7136\u5e76\u975e\u5728\u8bf4\u7b11\u8bdd\uff0c\u53cd\u800c\u662f\u5f88\u8ba4\u771f\u5730\u9648\u8ff0\u4e8b\u5b9e\u3002\u68b5\u52a0\u591a\u4e0d\u6562\u518d\u95ee\uff0c\u4ed6\u77e5\u9053\u5e03\u96f7\u7eb3\u65af\u548c\u4ed6\u7684\u957f\u5144\u4e00\u76f4\u4e0d\u5408\uff0c\u800c\u4ed6\u5e76\u4e0d\u60f3\u5377\u5165\u738b\u5ba4\u5185\u90e8\u7684\u7eb7\u4e89\u3002</p><p>\u4ed6\u53ea\u4e3a\u5e1d\u56fd\u6548\u547d\u3002</p><p>\u201c\u53ea\u662f\uff0c\u8001\u5e08\uff0c\u5982\u679c\u4e0d\u80fd\u7792\u8fc7\u745e\u74e6\u5170\u9601\u4e0b\uff0c\u6211\u4eec\u8fd9\u4e48\u5927\u8d39\u5468\u7ae0\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\uff0c\u8fd9\u4e2a\u6211\u4e0d\u660e\u767d\u2026\u2026\u201d</p><p>\u201c\u7792\u8fc7\u745e\u74e6\u5170\uff1f\u201d\u5e74\u8f7b\u7684\u738b\u5b50\u53cd\u95ee\uff0c\u4ed6\u7684\u7709\u6bdb\u8f7b\u8f7b\u6311\u4e86\u8d77\u6765\uff0c\u201c\u68b5\u52a0\u591a\uff0c\u4f60\u662f\u8fd9\u4e48\u60f3\u7684\uff1f\u4f60\u4ee5\u4e3a\u8fd9\u53ef\u80fd\u4e48\uff1f\u201d</p><p>\u201c\u786e\u5b9e\u5f88\u96be\uff0c\u201d\u68b5\u52a0\u591a\u627f\u8ba4\uff0c\u201c\u4f46\u5982\u679c\u6211\u4eec\u80fd\u505a\u5f97\u518d\u5de7\u5999\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u6447\u5934\u3002</p><p>\u201c\u9519\u4e86\uff0c\u68b5\u52a0\u591a\uff0c\u201d\u4ed6\u8bf4\uff0c\u6307\u70b9\u7740\u4ed6\u7684\u5b66\u751f\uff0c\u201c\u544a\u8bc9\u6211\uff0c\u5982\u679c\u4f60\u6709\u4e00\u4e2a\u5e9e\u5927\u7684\u8ba1\u5212\uff0c\u4f60\u4e0d\u5e0c\u671b\u88ab\u522b\u4eba\u963b\u6320\u6253\u65ad\uff0c\u90a3\u4e48\u4f60\u89c9\u5f97\u6700\u5173\u952e\u7684\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4fdd\u5bc6\uff0c\u201d\u68b5\u52a0\u591a\u7acb\u523b\u56de\u7b54\uff0c\u201c\u5229\u7528\u4fe1\u606f\u4e0a\u7684\u4f18\u52bf\u6253\u51fb\u5bf9\u624b\uff0c\u5b8c\u6210\u8ba1\u5212\u3002\u201d</p><p>\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u8fde\u8fde\u6447\u5934\uff0c\u201c\u8fd9\u662f\u52a3\u7b49\u7684\u624b\u6cd5\uff0c\u68b5\u52a0\u591a\uff0c\u8df3\u51fa\u4f60\u7684\u804c\u4e1a\u9650\u5236\uff0c\u4e0d\u8981\u603b\u662f\u7528\u4e00\u4e2a\u9884\u8a00\u5e08\u7684\u773c\u5149\u53bb\u770b\u5f85\u4e8b\u7269\u3002\u6211\u4eec\u90fd\u662f\u9884\u8a00\u5e08\uff0c\u4f46\u6211\u4eec\u66f4\u662f\u5deb\u5e08\u2026\u2026\u597d\u5427\uff0c\u68b5\u52a0\u591a\uff0c\u6211\u6253\u4e2a\u6bd4\u65b9\uff0c\u4f60\u4e0d\u559c\u6b22\u4e0b\u68cb\uff0c\u4f46\u81f3\u5c11\u4e5f\u89c1\u8fc7\u6211\u4e0b\u68cb\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u7684\u3002\u201d\u68b5\u52a0\u591a\u56de\u7b54\uff0c\u4ed6\u81ea\u5df1\u4e0d\u7231\u4e0b\u68cb\uff0c\u4f46\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u975e\u5e38\u559c\u6b22\uff0c\u4f5c\u4e3a\u5b66\u751f\uff0c\u4ed6\u81ea\u7136\u4e5f\u662f\u7ecf\u5e38\u89c1\u5230\u7684\u3002</p><p>\u201c\u4e0b\u68cb\u7684\u65f6\u5019\uff0c\u4f60\u6240\u6709\u7684\u68cb\u5b50\u5bf9\u65b9\u90fd\u6e05\u6e05\u695a\u695a\u770b\u5728\u773c\u91cc\uff0c\u4f60\u6240\u6709\u7684\u6b65\u9aa4\u5bf9\u65b9\u90fd\u4e00\u6e05\u4e8c\u695a\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bf4\uff0c\u201c\u4f46\u9ad8\u660e\u7684\u68cb\u624b\uff0c\u5374\u603b\u662f\u80fd\u5c06\u5bf9\u624b\u6740\u5f97\u4e00\u8d25\u6d82\u5730\uff0c\u68b5\u52a0\u591a\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u8981\u5984\u60f3\u81ea\u5df1\u7684\u8ba1\u5212\u80fd\u5929\u8863\u65e0\u7f1d\uff0c\u65e0\u4eba\u77e5\u6653\uff0c\u201d\u738b\u5b50\u63a5\u7740\u8bf4\uff0c\u201c\u90a3\u662f\u75f4\u5fc3\u5984\u60f3\uff0c\u4e0d\u53ef\u80fd\u505a\u5230\u7684\u4e8b\u60c5\uff0c\u9664\u975e\u4f60\u7684\u5bf9\u624b\u662f\u4e2a\u7b28\u86cb\u2014\u2014\u4f46\u7b28\u86cb\u9700\u8981\u8d39\u5fc3\u601d\u5bf9\u4ed8\u4e48\uff1f\u201d\u4ed6\u770b\u7740\u68b5\u52a0\u591a\uff0c\u70b9\u70b9\u5934\uff0c\u201c\u6211\u4eec\u5c31\u662f\u68cb\u624b\uff0c\u4e0d\u5fc5\u9760\u7792\u7740\u9a97\u7740\uff0c\u6210\u5929\u7948\u6c42\u5bf9\u65b9\u4e0d\u770b\u51fa\u7834\u7efd\uff0c\u628a\u6210\u529f\u7684\u5e0c\u671b\u5bc4\u6258\u5728\u5bf9\u624b\u7684\u611a\u8822\u4e0a\u2014\u2014\u4e0d\uff0c\u8fd9\u662f\u7edd\u5bf9\u9519\u8bef\u7684\u60f3\u6cd5\uff0c\u68b5\u52a0\u591a\u3002\u6211\u4eec\u8981\u505a\u7684\uff0c\u5c31\u662f\u660e\u660e\u5bf9\u65b9\u77e5\u9053\u4e00\u5207\uff0c\u5230\u6700\u540e\u5374\u4f9d\u7136\u53ea\u80fd\u675f\u624b\u5c31\u64d2\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u73b0\u5b9e\u548c\u4e0b\u68cb\u7ec8\u7a76\u6709\u6240\u4e0d\u540c\uff0c\u201d\u738b\u5b50\u96be\u5f97\u6709\u5174\u81f4\u6307\u70b9\u5b66\u751f\uff0c\u68b5\u52a0\u591a\u4e5f\u4e0d\u6562\u63a5\u53e3\uff0c\u4efb\u4ed6\u957f\u7bc7\u5927\u8bba\u4e0b\u53bb\uff0c\u201c\u5728\u4e00\u4e9b\u7ec6\u8282\u4e0a\uff0c\u4e0d\u59a8\u73a9\u70b9\u624b\u8155\uff0c\u4e5f\u9700\u8981\u73a9\u70b9\u624b\u8155\u2014\u2014\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u80fd\u662f\u7ec6\u8282\u3002\u5982\u679c\u5984\u60f3\u7740\u5bf9\u65b9\u5b8c\u5168\u770b\u4e0d\u51fa\u4f60\u7684\u610f\u56fe\uff0c\u90a3\u5c31\u662f\u5728\u4fae\u8fb1\u5f7c\u6b64\u7684\u667a\u6167\u4e86\u3002\u201d</p><p>\u201c\u60a8\u5bf9\u60a8\u7684\u667a\u6167\u592a\u8fc7\u81ea\u8d1f\u4e86\u3002\u201d\u68b5\u52a0\u591a\u5f88\u60f3\u8fd9\u4e48\u8bf4\uff0c\u5f53\u7136\u4ed6\u53ea\u80fd\u5728\u5fc3\u91cc\u60f3\u60f3\u3002\u5bf9\u65b9\u662f\u9634\u9b42\u738b\u5b50\uff0c\u662f\u4ed6\u7684\u8001\u5e08\uff0c\u68b5\u52a0\u591a\u4e0d\u6562\u5192\u72af\u3002</p><p>\u4f46\u4ed6\u89c9\u5f97\u81ea\u5df1\u6709\u5fc5\u8981\u505a\u51fa\u63d0\u9192\u3002</p><p>\u201c\u53ef\u662f\u73b0\u5b9e\u548c\u4e0b\u68cb\u8fd8\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u201d\u68b5\u52a0\u591a\u8bf4\uff0c\u201c\u4e0b\u68cb\u7684\u65f6\u5019\uff0c\u53cc\u65b9\u7684\u5b9e\u529b\u662f\u5bf9\u7b49\u7684\uff0c\u73b0\u5b9e\u4e2d\u5374\u4e0d\u662f\uff1b\u800c\u4e14\u68cb\u76d8\u4e0a\u7684\u68cb\u5b50\u662f\u6b7b\u7684\uff0c\u5b8c\u5168\u670d\u4ece\u68cb\u624b\u7684\u6307\u4ee4\uff1b\u73b0\u5b9e\u4e2d\u7684\u68cb\u5b50\u5374\u662f\u6d3b\u7684\u2026\u2026\u4ed6\u4eec\u6bd5\u7adf\u4e0d\u662f\u771f\u6b63\u7684\u68cb\u5b50\uff0c\u4ed6\u4eec\uff0c\u5443\uff0c\u8001\u5e08\uff0c\u662f\u6709\u5371\u9669\u7684\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8f7b\u8f7b\u9f13\u638c\u3002</p><p>\u201c\u5bf9\u6781\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u68b5\u52a0\u591a\uff0c\u4f60\u8bf4\u5f97\u975e\u5e38\u5bf9\u3002\u4e16\u4e8b\u5982\u68cb\uff0c\u4f46\u53c8\u6709\u6240\u4e0d\u540c\u3002\u4e0b\u68cb\u65f6\u5b8c\u5168\u66b4\u9732\uff0c\u73b0\u5b9e\u4e2d\u53ef\u4ee5\u5728\u7ec6\u8282\u4e0a\u73a9\u5c0f\u52a8\u4f5c\u2014\u2014\u8fd9\u662f\u4e00\u4e2a\u4f18\u52bf\uff1b\u4e0b\u68cb\u662f\u5b9e\u529b\u5bf9\u7b49\uff0c\u73b0\u5b9e\u4e2d\u5e76\u975e\u5982\u6b64\u2014\u2014\u8fd9\u662f\u6211\u4eec\u7684\u52a3\u52bf\uff1b\u8fd9\u4e24\u8005\u53ef\u4ee5\u76f8\u4e92\u62b5\u6d88\uff0c\u90a3\u4e48\u2026\u2026\u201d</p><p>\u201c\u90a3\u4e48\uff1f\u201d\u68b5\u52a0\u591a\u7591\u60d1\u5730\u53cd\u95ee\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u8fd8\u6709\u7b2c\u4e09\u70b9\u4e0d\u540c\uff0c\u201d\u738b\u5b50\u5411\u4ed6\u7684\u5b66\u751f\u5fae\u5fae\u70b9\u5934\uff0c\u201c\u4fbf\u662f\u4f60\u521a\u624d\u6240\u8bf4\u7684\uff0c\u68cb\u76d8\u4e0a\u7684\u68cb\u5b50\u662f\u6b7b\u7684\uff0c\u73b0\u5b9e\u4e2d\u7684\u68cb\u5b50\u5374\u662f\u6d3b\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e5f\u662f\u6211\u4eec\u7684\u4f18\u52bf\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u8fd9\u4e0d\u662f\u4f18\u52bf\uff0c\u4e5f\u4e0d\u662f\u52a3\u52bf\uff0c\u8fd9\u662f\u53d8\u6570\uff0c\u201d\u738b\u5b50\u8bf4\uff0c\u201c\u6b63\u56e0\u4e3a\u8fd9\u4e2a\u53d8\u6570\uff0c\u6240\u4ee5\u8fd9\u4e00\u5207\u624d\u6709\u8da3\u554a\u3002\u201d</p><p>\u6709\u8da3\uff1f</p><p>\u68b5\u52a0\u591a\u82e6\u7b11\u7740\uff0c\u5f88\u591a\u65f6\u5019\u4ed6\u5b9e\u5728\u4e0d\u80fd\u7406\u89e3\u4ed6\u8fd9\u4f4d\u8001\u5e08\u7684\u60f3\u6cd5\uff0c\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u660e\u667a\u5730\u8f6c\u79fb\u4e86\u8bdd\u9898\uff0c\u201c\u90a3\u4e48\uff0c\u8001\u5e08\uff0c\u4e0b\u4e00\u6b65\u6211\u4eec\u8be5\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u4eec\uff1f\u201d\u6700\u5e74\u8f7b\u7684\u9634\u9b42\u738b\u5b50\u5c06\u773c\u5149\u6295\u5411\u5899\u58c1\u4e0a\u6302\u7740\u7684\u5730\u56fe\uff0c\u641c\u7d22\u7740\uff0c\u201c\u9ea6\u52d2\u521a\u7279\u8fd8\u5728\u827e\u5f17\u62c9\u65af\u5361\u5427\uff0c\u6253\u67b6\u7684\u4e8b\u60c5\u8ba9\u4ed6\u4eec\u5e72\u53bb\uff0c\u6211\u4eec\u4e0d\u63d2\u624b\u3002\u6211\u4eec\u5f53\u7136\u8fd8\u662f\u8001\u8001\u5b9e\u5b9e\u53bb\u8003\u53e4\u2026\u2026\u7acb\u77f3\u5e73\u539f\u90a3\u91cc\u90fd\u6316\u904d\u4e86\u5427\u3002\u201d</p><p>\u201c\u6316\u904d\u4e86\uff0c\u201d\u68b5\u52a0\u591a\u76b1\u7740\u7709\u5934\uff0c\u201c\u5f88\u5947\u602a\uff0c\u6211\u4eec\u51e0\u4e4e\u628a\u90a3\u5757\u6c99\u6f20\u90fd\u7ffb\u8fc7\u6765\u4e86\uff0c\u4ec0\u4e48\u90fd\u6ca1\u627e\u5230\uff0c\u767d\u8d39\u529b\u6c14\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u53f9\u606f\u7740\uff0c\u201c\u5947\u602a\u554a\uff0c\u660e\u660e\u4e00\u5207\u535c\u7b97\u7684\u7ed3\u679c\u90fd\u6307\u5411\u90a3\u91cc\u2026\u2026\u7b97\u4e86\uff0c\u518d\u6362\u4e2a\u5730\u65b9\u5427\uff0c\u8ba9\u6211\u6765\u770b\u770b\u5730\u56fe\u3002\u201d</p></article>"}}}]);
