(window.webpackJsonp=window.webpackJsonp||[]).push([[401],{424:function(p){p.exports={chapterTitle:null,title:"\u7b2c401\u7ae0 \u7fdf\u4e39\u9752\u6765\u6d77\u5dde",content:"<article><h2>\u7b2c401\u7ae0 \u7fdf\u4e39\u9752\u6765\u6d77\u5dde</h2><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u7ec4\u5efa\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u554a\u2026\u2026\u201d\u8c22\u665a\u6674\u4e30\u8174\u767d\u7699\u7684\u8138\u559d\u591a\u9152\u4f3c\u7684\u62b9\u7740\u7ea2\u6655\uff0c\u521a\u6d17\u8fc7\u70ed\u6c34\u6fa1\uff0c\u8eab\u4f53\u6709\u4e9b\u53d1\u70ed\uff0c\u542c\u5f20\u606a\u8bf4\u8d77\u5e02\u91cc\u6700\u65b0\u7684\u52a8\u4f5c\uff0c\u53ea\u662f\u8f7b\u58f0\u53f9\u606f\u4f3c\u7684\u611f\u6168\u4e86\u4e00\u53e5\uff0c\u8138\u671b\u7740\u7a97\u5916\u7684\u6e56\u6c34\uff0c\u201c\u5c31\u6015\u5e02\u91cc\u67d0\u4e9b\u4eba\u904f\u5236\u4e0d\u4f4f\u5c06\u57ce\u5546\u884c\u5f53\u6210\u63d0\u6b3e\u673a\u7684\u51b2\u52a8\u554a\uff0c\u5404\u5730\u7684\u57ce\u5e02\u4fe1\u7528\u793e\u641e\u4e86\u8fd9\u4e9b\u5e74\uff0c\u6700\u540e\u5dee\u4e0d\u591a\u90fd\u6210\u4e86\u70c2\u644a\u5b50\uff0c\u5c31\u662f\u524d\u8f66\u4e4b\u9274\u2026\u2026\u9650\u5236\u5e02\u91cc\u67d0\u4e9b\u4eba\u53ef\u80fd\u4f1a\u6709\u7684\u8fc7\u5ea6\u5e72\u6d89\uff0c\u5012\u5f88\u5173\u952e\uff0c\u4e5f\u4e0d\u8bf4\u4e0d\u4fe1\u4efb\u5510\u5b66\u8c26\uff0c\u5510\u5b66\u8c26\u79bb\u5f00\u6d77\u5dde\u5462\uff1f\u4e07\u4e00\u7ed9\u5468\u5bcc\u660e\u5360\u4e86\u4e0a\u98ce\u5462\uff1f\u4e00\u5bb6\u4f01\u4e1a\u6027\u8d28\u7684\u5546\u4e1a\u94f6\u884c\u603b\u4e0d\u80fd\u56e0\u4eba\u65bd\u653f\uff0c\u5373\u4f7f\u5e02\u91cc\u8981\u5bf9\u57ce\u5546\u884c\u63a7\u80a1\uff0c\u6211\u4eec\u8981\u53c2\u52a0\u7684\u8bdd\uff0c\u4e5f\u8981\u6709\u5bf9\u4e00\u4e9b\u6307\u4ee4\u6027\u7684\u8d37\u6b3e\u6709\u5426\u51b3\u7684\u6743\u529b\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u90a3\u7b80\u5355\uff0c\u5728\u67d0\u4e9b\u5b98\u5458\u7684\u773c\u91cc\uff0c\u6211\u4eec\u5c31\u5e94\u8be5\u662f\u987a\u4ece\u7684\u5c0f\u6c11\uff0c\u5373\u4f7f\u6709\u5426\u51b3\u6743\uff0c\u4f60\u8981\u52a8\u4e00\u52a8\uff0c\u90a3\u4e9b\u5b98\u50da\u601d\u60f3\u4e25\u91cd\u7684\u4eba\uff0c\u8fd8\u4e0d\u8981\u8df3\u51fa\u6765\u6307\u7740\u4f60\u7684\u9f3b\u5b50\u9a82\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u6d77\u5dde\u5e02\u7684\u4eba\u4e8b\u80fd\u7a33\u5b9a\u4e0b\u6765\uff0c\u90a3\u81ea\u7136\u662f\u518d\u597d\u4e0d\u8fc7\uff1b\u8981\u4e0d\u80fd\u786e\u5b9a\u4f1a\u7a33\u5b9a\uff0c\u624b\u6cd5\u8fd8\u8981\u59d4\u5a49\u4e00\u4e9b\uff0c\u4e0d\u8981\u8ba9\u5e02\u91cc\u7684\u90a3\u4e9b\u5927\u7237\u4ee5\u4e3a\u662f\u6211\u4eec\u8981\u8df3\u51fa\u6765\u62ff\u5426\u51b3\u6743\u2026\u2026\u8bf4\u4e0d\u5b9a\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u4e4b\u6d41\u4e00\u6765\u813e\u6c14\uff0c\u5c31\u5c06\u6211\u4eec\u6392\u65a5\u5728\u5916\uff0c\u7ec4\u5efa\u57ce\u5546\u884c\u53ea\u9700\u8981\u4e00\u4ebf\u7684\u6ce8\u518c\u8d44\u672c\u91d1\uff0c\u57ce\u5e02\u4fe1\u7528\u793e\u867d\u7136\u662f\u70c2\u644a\u5b50\u4e00\u4e2a\uff0c\u4f46\u662f\u6bd5\u7adf\u4e5f\u6709\u4e2a\u57fa\u7840\uff0c\u5e02\u91cc\u8981\u4ece\u5176\u4ed6\u4f01\u4e1a\u62c9\u8db3\u4e00\u4e2a\u4ebf\uff0c\u4e5f\u4e0d\u662f\u592a\u56f0\u96be\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5012\u662f\u6709\u53ef\u80fd\uff0c\u5468\u5bcc\u660e\u91cd\u65b0\u4e0e\u8d75\u9526\u8363\u8d70\u5230\u4e00\u8d77\uff0c\u6b63\u6cf0\u4e5f\u70ed\u60c5\u9ad8\u6da8\u7684\u5230\u6d77\u5dde\u6765\u5e03\u5c40\uff0c\u4f60\u6ca1\u770b\u89c1\u5927\u8857\u4e0a\uff0c\u79d1\u738b\u5f71\u789f\u673a\u7684\u5e7f\u544a\u90fd\u51e0\u4e4e\u90fd\u8981\u8d34\u6ee1\u6d77\u5dde\u5e02\u6240\u6709\u7684\u516c\u4ea4\u8f66\u8eab\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5173\u4e8e\u8981\u4e0d\u8981\u5bf9\u57ce\u5546\u884c\u63a7\u80a1\uff0c\u5e02\u91cc\u8fd8\u6ca1\u6709\u7edf\u4e00\u7684\u610f\u89c1\uff0c\u5510\u5a67\u5979\u7238\u7238\u4e5f\u6ca1\u6709\u660e\u8bf4\u5e02\u91cc\u6709\u54ea\u4e9b\u4eba\u53cd\u5bf9\u3002\u4e5f\u4e0d\u8bf4\u8fd9\u4e9b\u5b98\u5458\u5c31\u662f\u5728\u6363\u4e71\uff0c\u6bd5\u7adf\u6d89\u53ca\u5230\u654f\u611f\u7684\u94f6\u884c\u91d1\u878d\u4e1a\uff0c\u5728\u4f20\u7edf\u7684\u601d\u7ef4\u91cc\uff0c\u80fd\u8ba9\u79c1\u6709\u8d44\u672c\u53c2\u4e0e\u8fdb\u6765\u5df2\u7ecf\u662f\u4e0d\u9519\u7684\uff0c\u8ba9\u79c1\u6709\u8d44\u672c\u5360\u4e3b\u5bfc\u5730\u4f4d\uff0c\u5374\u4e0d\u662f\u4efb\u4f55\u4eba\u90fd\u80fd\u63a5\u53d7\u7684\u2026\u2026\u201d\u5f20\u606a\u64e6\u7740\u6e7f\u6f09\u6f09\u7684\u5934\u53d1\uff0c\u7528\u624b\u6307\u5c06\u5934\u53d1\u62e2\u5230\u540e\u9762\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u8fd9\u8fb9\u601d\u8def\u5192\u8fdb\u4e00\u4e9b\uff0c\u65e0\u7591\u4f1a\u628a\u4e00\u4e9b\u601d\u60f3\u50f5\u56fa\u7684\u5b98\u5458\u63a8\u5230\u5468\u5bcc\u660e\u90a3\u8fb9\u53bb\uff0c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u6211\u770b\u5468\u5bcc\u660e\u5df2\u7ecf\u6709\u91cd\u65b0\u6d3b\u8dc3\u7684\u8ff9\u8c61\uff0c\u6211\u4eec\u8981\u672a\u96e8\u7ef8\u7f2a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u81ea\u5df1\u62ff\u5426\u51b3\u6743\uff0c\u4f60\u8bf4\u53ef\u80fd\u4f1a\u6fc0\u6012\u90a3\u4e9b\u5b98\u8001\u7237\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u53f6\u5efa\u658c\u8bf4\u4e0d\u5b9a\u4e5f\u4f1a\u611f\u5174\u8da3\uff0c\u4ed6\u53c8\u4e0d\u6015\u5e02\u91cc\u7684\u5b98\u5458\u62ff\u4ed6\u4eec\u7684\u5c0f\u8fab\u5b50\uff0c\u505a\u6076\u4eba\u4e5f\u65e0\u6240\u8c13\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u62c9\u4ed6\u8d9f\u8fd9\u6d51\u6c34\uff0c\u90a3\u5012\u662f\u4e0d\u9519\uff0c\u4f60\u786e\u5b9a\u4ed6\u4f1a\u613f\u610f\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u6709\u4e9b\u4e25\u683c\u7684\u5730\u57df\u9650\u5236\uff0c\u53f6\u5bb6\u7684\u4ea7\u4e1a\u4e3b\u8981\u4e0d\u5728\u6d77\u5dde\uff0c\u5165\u80a1\u6d77\u5dde\u5e02\u7684\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\uff0c\u5bf9\u53f6\u5bb6\u540d\u4e0b\u7684\u4ea7\u4e1a\u6ca1\u6709\u592a\u591a\u76f4\u63a5\u7684\u597d\u5904\uff0c\u4ed6\u4eec\u624b\u91cc\u6709\u8d44\u91d1\uff0c\u6211\u60f3\u66f4\u613f\u610f\u53c2\u80a1\u5efa\u90ba\u5e02\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u7684\u7ec4\u5efa\u3002\u201d</p><p>\u3000\u3000\u201c\u7701\u57ce\u554a\uff0c\u4f60\u8bf4\u53f6\u5efa\u658c\u5c31\u6ca1\u6709\u8ddf\u6211\u4eec\u4e00\u6837\u7684\u70e6\u607c\uff1f\u4ed6\u5230\u8fd9\u91cc\u6765\u6076\u4eba\uff0c\u6211\u4eec\u53bb\u4ed6\u90a3\u91cc\u505a\u6076\u4eba\uff0c\u90a3\u4e0d\u5c31\u7686\u5927\u6b22\u559c\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u8981\u5165\u80a1\u5efa\u90ba\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u7b49\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u4e0e\u57ce\u5e02\u4fe1\u7528\u793e\u4e00\u6837\u518d\u4e00\u6b21\u8d70\u8fdb\u6b7b\u80e1\u540c\uff0c\u4e1c\u6d77\u7701\u5341\u4e09\u4e2a\u5730\u7ea7\u5e02\u7684\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u4e8e\u96f6\u56db\u5e74\u4ee5\u6548\u7ee9\u6700\u597d\u7684\u5efa\u90ba\u5e02\u57ce\u5546\u884c\u4e3a\u4e3b\u5bfc\u91cd\u7ec4\u89c4\u6a21\u66f4\u5927\u7684\u4e1c\u6d77\u5546\u4e1a\u94f6\u884c\u3002</p><p>\u3000\u3000\u53ea\u8981\u6709\u597d\u7684\u76d1\u7ba1\uff0c\u521d\u6b65\u5bf9\u79c1\u6709\u8d44\u672c\u653e\u5f00\u7684\u94f6\u884c\u4e1a\u672c\u8eab\u5c31\u662f\u4e00\u4e2a\u9ad8\u5229\u6da6\u3001\u534a\u5784\u65ad\u7684\u884c\u4e1a\uff0c\u4eca\u540e\u4f1a\u76f8\u5bf9\u5065\u5eb7\u53d1\u5c55\u3001\u76d1\u7ba1\u6bd4\u8f83\u5230\u4f4d\u7684\u5efa\u90ba\u5e02\u57ce\u5546\u884c\u5c31\u5f88\u503c\u5f97\u6295\u8d44\uff0c\u6bd4\u8f83\u5173\u952e\u7684\u4e00\u70b9\uff0c\u57ce\u5546\u884c\u7684\u6574\u4f53\u7cdf\u7cd5\u5c40\u9762\u4e0d\u662f\u4e2a\u4eba\u7684\u52aa\u529b\u5c31\u80fd\u626d\u8f6c\u7684\uff0c\u7b49\u516b\u5e74\u540e\uff08\u8fd9\u6b21\u6216\u8bb8\u4e0d\u8981\u516b\u5e74\u7684\u65f6\u95f4\uff09\uff0c\u7701\u5185\u5341\u4e09\u5bb6\u57ce\u5546\u884c\u91cd\u7ec4\u6210\u4e1c\u6d77\u5546\u4e1a\u94f6\u884c\u65f6\uff0c\u90a3\u624d\u662f\u771f\u6b63\u7684\u9999\u998d\u998d\uff0c\u7b49\u5230\u91cd\u7ec4\u65f6\u518d\u60f3\u8fdb\u53bb\uff0c\u5c31\u5f88\u96be\u62ff\u5230\u5165\u573a\u5238\u4e86\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u521a\u8bf4\u65f6\uff0c\u597d\u50cf\u5f88\u5fc3\u4e0d\u7518\u60c5\u4e0d\u613f\u7684\u6837\u5b50\uff1f\u201d\u8c22\u665a\u6674\u5947\u602a\u7684\u95ee\u4ed6\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6709\u529e\u6cd5\uff0c\u6211\u672c\u6765\u7684\u610f\u601d\u5012\u662f\u60f3\u5077\u5077\u7684\u732b\u5230\u4e00\u65c1\uff0c\u201d\u5f20\u606a\u5c06\u64e6\u6e7f\u5934\u53d1\u7684\u6bdb\u5dfe\u4e22\u5230\u4e00\u8fb9\uff0c\u8d70\u8fc7\u6765\u51dd\u89c6\u7740\u665a\u6674\u4fee\u76f4\u7684\u53cc\u817f\u3001\u8f7b\u8584\u900f\u660e\u7684\u540a\u5e26\u7761\u8863\u53ea\u76d6\u4f4f\u534a\u4e2a\u81c0\u90e8\uff0c\u9ad8\u7fd8\u6d51\u5706\u7684\u96ea\u767d\u4e30\u81c0\uff0c\u4e00\u637b\u7ea4\u8170\uff0c\u66f2\u7ebf\u6027\u611f\u8bf1\u4eba\uff0c\u80cc\u90e8\u7684\u4e30\u8174\u808c\u8089\u7ec6\u6ed1\uff0c\u5f20\u606a\u8d70\u8fc7\u6765\uff0c\u4ece\u540e\u9762\u62b1\u7740\u665a\u6674\uff0c\u7528\u5634\u5507\u5c06\u5979\u7684\u8116\u9888\u90a3\u91cc\u5fae\u66f2\u7684\u79c0\u53d1\u62f1\u5f00\uff0c\u9f3b\u5c16\u8f7b\u8f7b\u89e6\u7740\u4fee\u957f\u9888\u8116\u4e0a\u90a3\u4e00\u6761\u6761\u4eff\u4f5b\u7528\u7389\u77f3\u6d6e\u96d5\u4f3c\u7684\u808c\u68f1\uff0c\u201c\u6211\u53ef\u4e5f\u662f\u7ed9\u8d76\u9e2d\u5b50\u4e0a\u67b6\u2026\u2026\u8981\u505a\uff0c\u5f53\u7136\u8981\u505a\u597d\u55bd\u3002\u201d</p><p>\u3000\u3000\u201c\u8bf4\u8c0e\u5339\u8bfa\u66f9\u7684\u9f3b\u5b50\u4f1a\u957f\u957f\u7684\uff0c\u201d\u665a\u6674\u4fa7\u5934\u7b11\u4e86\u7b11\uff0c\u8033\u5782\u6328\u7740\u5f20\u606a\u7684\u5634\u5507\uff0c\u4fbf\u4e0d\u613f\u79bb\u5f00\uff0c\u201c\u4f60\u7684\u957f\u9f3b\u5b50\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u770b\u6211\u662f\u771f\u8bf4\u8c0e\u4e86\uff0c\u201d\u5f20\u606a\u4ece\u540e\u9762\u62b1\u4f4f\u665a\u6674\uff0c\u4e24\u4eba\u7684\u8eab\u4f53\u8d34\u5728\u4e00\u8d77\uff0c\u201c\u4e0d\u8fc7\u957f\u9f3b\u5b50\u597d\u50cf\u957f\u9519\u5730\u65b9\u4e86\u3002\u201d</p><p>\u3000\u3000\u665a\u6674\u52a8\u60c5\u7684\u7b11\u4e86\u7b11\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u4e2a\u7fdf\u4e39\u9752\u76f8\u5f53\u8bf1\u4eba\u5427\uff1f\u4f60\u4e5f\u771f\u4e0d\u5bb9\u6613\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u7ed9\u90a3\u4e2a\u72d0\u72f8\u7cbe\u52fe\u4e86\u6ca1\u9b42\u4e86\u5462\uff01\u6211\u5012\u559c\u6b22\u542c\u4f60\u8ddf\u6211\u8bf4\u8bf4\u5728\u65b0\u829c\u53d1\u751f\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54ea\u4e2a\u4eba\u5728\u80cc\u540e\u7f16\u6392\u6211\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u4e0b\u5df4\u8f7b\u538b\u7740\u665a\u6674\u7684\u80a9\u8180\uff0c\u771f\u4e0d\u5bb9\u6613\u96c6\u4e2d\u7cbe\u795e\u8bf4\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u524d\u4e9b\u5929\u4ece\u65b0\u829c\u56de\u6765\uff0c\u8d76\u5de7\u665a\u6674\u4e3a\u529e\u79c1\u7acb\u5b66\u6821\u62a5\u6587\u7684\u4e8b\u60c5\uff0c\u53bb\u4e86\u5317\u4eac\uff0c\u4eca\u5929\u624d\u56de\u6765\u3002\u5f20\u606a\u5df2\u7ecf\u8fde\u7eed\u4e24\u4e2a\u665a\u4e0a\u90fd\u5728\u5510\u5a67\u5bb6\u8e6d\u665a\u996d\u5403\uff0c\u5510\u5b66\u8c26\u4e5f\u5f88\u51c6\u65f6\u56de\u5bb6\u5403\u665a\u996d\uff0c\u5c31\u5728\u996d\u684c\u4e0a\u8ba8\u8bba\u7ec4\u5efa\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u7684\u4e8b\u60c5\uff0c\u665a\u4e0a\u90fd\u6ca1\u6709\u65f6\u95f4\u590d\u4e60\u3002\u665a\u4e0a\u56de\u6765\uff0c\u7b49\u665a\u6674\u7684\u79c1\u4eba\u52a9\u7406\u5f20\u5ead\u2014\u2014\u90a3\u4e2a\u6587\u79c0\u3001\u4eca\u5e74\u6625\u5929\u624d\u52a0\u5165\u6d77\u88d5\u516c\u53f8\u7684\u5973\u5b69\u5b50\u2014\u2014\u5904\u7406\u5b8c\u5de5\u4f5c\u4e0a\u7684\u4e8b\u60c5\u79bb\u5f00\uff0c\u7531\u4e8e\u6691\u671f\u8fd8\u5269\u4e0b\u6700\u540e\u51e0\u5929\uff0c\u82b7\u5f64\u8fd8\u5728\u5468\u6dd1\u60e0\u8eab\u8fb9\uff0c\u4fdd\u59c6\u8bf7\u5047\u56de\u53bb\u8fd8\u6ca1\u6709\u4e0a\u5de5\uff0c\u8fd9\u4e00\u89d2\u6e56\u7554\u7684\u4e09\u680b\u5c0f\u697c\u53ea\u6709\u4ed6\u4e0e\u8c22\u665a\u6674\u4e24\u4e2a\u4eba\u3002\u665a\u6674\u7684\u8eab\u4f53\u91cc\u8fd8\u6f2b\u6d41\u7740\u6781\u5ea6\u6b22\u5a31\u8fc7\u540e\u7684\u8212\u7545\uff0c\u8eab\u4f53\u8fd8\u5fae\u5fae\u9ebb\u8f6f\uff0c\u4e00\u65f6\u90fd\u65e0\u529b\u518d\u627f\u53d7\u5f20\u606a\u7684\u8fdb\u653b\uff0c\u4e24\u4eba\u7ad9\u5728\u540e\u7a97\u524d\u76f8\u62e5\u7740\u51dd\u89c6\u7a97\u5916\u7684\u6e56\u6c34\uff0c\u53ea\u662f\u62ff\u624b\u5e2e\u5f20\u606a\u7a0d\u7a0d\u7f13\u89e3\u4e00\u518d\u6fc0\u8361\u7684\u60c5\u6b32\u3002</p><p>\u3000\u3000\u201c\u7fdf\u4e39\u9752\u90a3\u4e2a\u5973\u4eba\u597d\u5389\u5bb3\u554a\u2026\u2026\u201d\u542c\u5f20\u606a\u8bf4\u5b8c\u65b0\u829c\u7684\u4e8b\u60c5\uff0c\u665a\u6674\u611f\u6168\u7684\u8bf4\uff0c\u4e30\u6ee1\u5706\u6da6\u7684\u5634\u5507\u5a07\u8273\u6b32\u6ef4\uff0c\u773c\u775b\u91cc\u7684\u77b3\u4ec1\u4eff\u4f5b\u4e24\u7c92\u6c34\u94f6\u4e38\u5b50\u4e00\u6837\u6df1\u9083\u6e05\u4eae\u3002</p><p>\u3000\u3000\u201c\u73af\u5883\u4f7f\u7136\uff0c\u90a3\u4e48\u6076\u52a3\u7684\u73af\u5883\uff0c\u4eba\u4e0d\u60f3\u7ed9\u90a3\u6837\u7684\u73af\u5883\u541e\u566c\u6389\uff0c\u90a3\u5c31\u8981\u51f6\u72e0\u8d77\u6765\uff0c\u522b\u4eba\u8fd8\u8bf4\u4f60\u5389\u5bb3\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6709\u5389\u5bb3\u5417\uff1f\u201d\u665a\u6674\u4e0d\u89e3\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u7ed9\u4eba\u4e0d\u5bb9\u6613\u63a5\u8fd1\u7684\u611f\u89c9\uff0c\u522b\u4eba\u5f53\u7136\u4e00\u89c6\u540c\u4ec1\u7684\u5f52\u5230\u5389\u5bb3\u7684\u4e00\u7c7b\u89d2\u8272\u91cc\uff0c\u201d\u5f20\u606a\u8f7b\u7b11\u7740\u8bf4\uff0c\u62c9\u7740\u665a\u6674\u5230\u5e8a\u8fb9\uff0c\u50cf\u5a74\u513f\u4e00\u6837\u7684\u5c06\u665a\u6674\u4e3e\u8d77\u6765\u62b1\u5728\u819d\u4e0a\uff0c\u201c\u4e0d\u8fc7\u5728\u6211\u7684\u773c\u91cc\uff0c\u665a\u6674\u4f60\u5c31\u8ddf\u5c0f\u5973\u5b69\u5b50\u4e00\u6837\u6ca1\u7528\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6709\u6ca1\u7528\u5417\uff1f\u201d\u665a\u6674\u5750\u5728\u5f20\u606a\u7684\u5927\u817f\u4e0a\uff0c\u624b\u6276\u7740\u4ed6\u7684\u80f8\u53e3\uff0c\u7761\u8863\u80a9\u5e26\u4ece\u80a9\u8180\u6ed1\u4e0b\u6765\uff0c\u5979\u4f38\u624b\u62c9\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u8bf4\u8bdd\uff0c\u4f60\u8db4\u6211\u80f8\u53e3\u6765\u3002\u201d</p><p>\u3000\u3000\u8fd9\u8bdd\u8bf4\u51fa\u6765\u4e5f\u53ea\u80fd\u62ff\u53bb\u9a97\u9a97\u5c0f\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u665a\u775b\u90fd\u4e0d\u6653\u5f97\u81ea\u5df1\u8eab\u4e0a\u6210\u719f\u7684\u97f5\u5473\u591a\u4e48\u7684\u8ba9\u4eba\u51b2\u52a8\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9500\u9b42\u4e86\u4e00\u591c\uff0c\u6e05\u6668\u8d77\u6765\u5012\u4e0d\u89c9\u4e1d\u6beb\u7684\u75b2\u60eb\uff0c\u5f20\u606a\u4e0d\u7531\u611f\u6168\u5e74\u8f7b\u7684\u8eab\u4f53\u771f\u662f\u597d\uff0c\u50cf\u81ea\u5df1\u4e09\u5341\u5c81\u7ed9\u9152\u8272\u6d78\u8680\u7684\u8eab\u4f53\uff0c\u5373\u4f7f\u80fd\u4e00\u591c\u72c2\u6b22\uff0c\u7b2c\u4e8c\u5929\u603b\u8981\u7761\u4e2a\u767d\u5929\u624d\u80fd\u6062\u590d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u665a\u6674\u4e94\u70b9\u949f\u4ece\u5f20\u606a\u7684\u6000\u91cc\u604b\u604b\u4e0d\u820d\u7684\u6323\u624e\u7740\u722c\u8d77\u6765\u56de\u897f\u8fb9\u7684\u5c0f\u697c\uff0c\u5f20\u606a\u6253\u7535\u8bdd\u8fc7\u53bb\u95ee\u5979\u8981\u5403\u4ec0\u4e48\u65e9\u9910\uff0c\u5979\u8eba\u5728\u5e8a\u4e0a\u62e5\u7740\u4e1d\u7ed2\u88ab\uff0c\u62ff\u5a07\u8f6f\u65e0\u529b\u7684\u8bed\u6c14\u8bf4\uff1a\u201c\u9aa8\u5b50\u90fd\u6563\u6389\u4e86\u2026\u2026\u201d\u5fc3\u5374\u5f02\u5e38\u7684\u8212\u7545\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f9d\u7740\u7070\u559c\u9e4a\u7684\u8c03\u5b50\u5439\u7740\u53e3\u54e8\uff0c\u716e\u6c34\u4e0b\u9762\u6761\u52a0\u8471\u82b1\u714e\u8377\u5305\u86cb\uff0c\u5f88\u5feb\u5c06\u65e9\u9910\u51c6\u5907\u597d\uff0c\u7aef\u5230\u665a\u6674\u7684\u623f\u95f4\u91cc\uff0c\u770b\u7740\u665a\u6674\u62e5\u88ab\u800c\u5750\uff0c\u80f3\u818a\u6401\u5728\u6c34\u7eff\u8272\u7684\u4e1d\u7ed2\u88ab\u4e0a\uff0c\u9732\u51fa\u4e30\u8174\u5706\u6da6\u3001\u96ea\u80a4\u7389\u808c\u822c\u7684\u80a9\u8180\uff0c\u5a07\u7f8e\u7684\u8138\u5e9e\u7ed9\u51cc\u4e71\u7684\u79c0\u4e3d\u534a\u63a9\uff0c\u7f8e\u7738\u8fd8\u5e26\u7740\u6628\u5929\u98a0\u72c2\u540e\u7684\u56f0\u987f\uff0c\u5374\u6625\u60c5\u878d\u878d\uff0c\u4ece\u9aa8\u5b50\u91cc\u6563\u53d1\u51fa\u4e00\u80a1\u6175\u61d2\u5a07\u67d4\u7684\u8ff7\u4eba\u98ce\u97f5\uff0c\u8273\u5149\u7115\u53d1\u6070\u5982\u65b0\u5ac1\u7684\u5c11\u5987\uff0c\u5f20\u606a\u770b\u4e86\u5fcd\u4e0d\u4f4f\u8981\u5c06\u53e3\u6c34\u6d41\u5230\u9762\u7897\u91cc\u53bb\u3002</p><p>\u3000\u3000\u201c\u665a\u6674\u59d0\uff0c\u65e9\u4e0a\u4f60\u5f88\u8bf1\u4eba\u5462\u3002\u201d\u5f20\u606a\u5c06\u65e9\u9910\u653e\u5e8a\u5934\u684c\u4e0a\uff0c\u62c9\u5230\u665a\u6674\u7684\u8eab\u524d\u3002</p><p>\u3000\u3000\u201c\u65e9\u4e0a\u8bf4\u4ec0\u4e48\u75af\u8bdd\uff0c\u8fd9\u4e9b\u8bdd\u4f60\u8ddf\u8bb8\u601d\u3001\u5510\u5a67\u8bf4\u53bb\u5427\uff0c\u524d\u4e9b\u5929\u770b\u5230\u773c\u89d2\u957f\u51fa\u9c7c\u5c3e\u7eb9\uff0c\u90fd\u5feb\u4f24\u5fc3\u6b7b\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\uff1f\u201d\u5f20\u606a\u4fef\u8fc7\u8eab\u53bb\u7ec6\u770b\u665a\u6674\u7684\u773c\u795e\uff0c\u6ca1\u6709\u770b\u5230\u4e00\u4e1d\u5c81\u6708\u7684\u75d5\u8ff9\uff0c\u201c\u6ca1\u6709\u554a\uff0c\u4f60\u5e73\u65e5\u90a3\u4e9b\u7684\u517b\u989c\u4e1c\u897f\u5012\u4e0d\u662f\u767d\u7528\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u517b\u989c\u7684\u4e1c\u897f\u518d\u597d\uff0c\u90a3\u4e5f\u53ea\u80fd\u5ef6\u7f13\u8870\u8001\uff0c\u53c8\u4e0d\u80fd\u963b\u6b62\u8870\u8001\uff0c\u201d\u665a\u6674\u7b11\u7740\u8bf4\uff0c\u201c\u4f24\u5fc3\u597d\u51e0\u5929\u4e86\uff0c\u90a3\u65f6\u4f60\u53c8\u4e0d\u5728\u6d77\u5dde\uff0c\u73b0\u5728\u8ba4\u547d\u4e86\uff0c\u4e5f\u4e0d\u7528\u4f60\u5b89\u6170\u6211\u4e86\uff0c\u8fc7\u51e0\u5929\u5c31\u6211\u90fd\u4e09\u5341\u4e00\u5c81\u4e86\uff0c\u4f60\u4e0d\u8981\u8bf4\u5e2e\u6211\u8fc7\u751f\u65e5\u4e4b\u7c7b\u7684\u8bdd\uff0c\u5973\u4eba\u8fc7\u4e86\u4e09\u5341\u5c81\uff0c\u518d\u8fc7\u4ec0\u4e48\u751f\u65e5\uff0c\u53ea\u662f\u63d0\u9192\u81ea\u5df1\u53c8\u8001\u4e86\u4e00\u5c81\uff0c\u591f\u4f24\u5fc3\u4e86\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u81ea\u5df1\u770b\u554a\uff0c\u201d\u5f20\u606a\u62ff\u8fc7\u4e00\u53ea\u5c0f\u5316\u5986\u955c\u9012\u5230\u665a\u6674\u773c\u524d\uff0c\u201c\u4f60\u8fd9\u6837\u5b50\uff0c\u53ef\u4e0d\u9700\u8981\u522b\u4eba\u6765\u5b89\u6170\u3002\u201d</p><p>\u3000\u3000\u201c\u54a6\u2026\u2026\u201d\u8c22\u665a\u6674\u770b\u7740\u5316\u5986\u955c\u91cc\u5bb9\u5149\u7115\u53d1\u7684\u81ea\u5df1\uff0c\u76b1\u9f3b\u6324\u773c\u770b\u4e86\u534a\u5929\uff0c\u773c\u89d2\u54ea\u91cc\u6709\u9c7c\u5c3e\u7eb9\uff0c\u201c\u5947\u602a\u4e86\uff0c\u6211\u524d\u5929\u9047\u5230\u8521\u88d5\u5a1f\u54ed\u8bc9\u6765\u7740\uff0c\u771f\u662f\u5947\u602a\u4e86\uff0c\u9047\u5230\u9b3c\u4e86\uff1f\u201d\u665a\u6674\u63a2\u7740\u8eab\u5b50\u53bb\u62ff\u4e1d\u7ed2\u88ab\u4e0a\u7684\u7761\u88d9\uff0c\u4e1d\u88ab\u5fae\u659c\uff0c\u9732\u51fa\u767d\u7699\u4f3c\u96ea\u7684\u4e73\u808c\uff0c\u7adf\u662f\u8d64\u8eab\u7761\u5728\u4e1d\u7ed2\u88ab\u91cc\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u9047\u5230\u9b3c\u4e86\uff0c\u4ec0\u4e48\u53eb\u9047\u5230\u9b3c\u4e86\uff0c\u201d\u5f20\u606a\u624b\u6307\u94b3\u7740\u665a\u6674\u4e30\u8174\u5ae9\u6ed1\u7684\u4e0b\u5df4\uff0c\u201c\u600e\u4e48\u4e0d\u60f3\u60f3\u8c01\u6628\u5929\u90a3\u4e48\u5356\u529b\uff0c\u600e\u4e48\u5c31\u53eb\u9047\u5230\u9b3c\u4e86\u3002\u201d</p><p>\u3000\u3000\u665a\u6674\u6251\u54e7\u4e00\u7b11\uff0c\u624b\u91cc\u7684\u4e1d\u7ed2\u88ab\u6ed1\u843d\uff0c\u9732\u51fa\u8d64\u88f8\u7684\u5b8c\u7f8e\u4e0a\u8eab\uff0c\u4e73\u623f\u96ea\u767d\u6d51\u5706\uff0c\u665a\u6674\u5012\u662f\u4e0d\u597d\u610f\u601d\u5728\u5f20\u606a\u9762\u524d\u8d64\u8eab\u7a7f\u8863\uff0c\u4fa7\u8fc7\u8eab\u5b50\u5c06\u7761\u88d9\u7a7f\u4e0a\uff0c\u6070\u597d\u7559\u7ed9\u5f20\u606a\u4e00\u4e2a\u5999\u66fc\u6027\u611f\u7684\u4fa7\u8eab\u4e0e\u534a\u7247\u6027\u611f\u7684\u4e30\u81c0\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u542c\u8bf4\u4eca\u5929\u90a3\u4e2a\u7fdf\u4e39\u9752\u4f1a\u5230\u6d77\u5dde\u6765\uff1f\u201d\u665a\u6674\u7a7f\u597d\u7761\u88d9\uff0c\u4e0b\u5e8a\u5c06\u5439\u7740\u51b7\u6c14\u7684\u7a7a\u8c03\u5173\u6389\uff0c\u8ddf\u5f20\u606a\u4e00\u4e2a\u6bdb\u75c5\uff0c\u590f\u5929\u559c\u6b22\u6253\u7a7a\u51b7\u6c14\u76d6\u7740\u88ab\u5b50\u7761\u89c9\uff0c\u8d64\u8db3\u8e29\u5728\u5730\u677f\u4e0a\uff0c\u88d9\u6446\u4e0b\u7684\u5c0f\u817f\u4fee\u76f4\u7ea4\u7ec6\uff0c\u56de\u8fc7\u5934\u5bf9\u5f20\u606a\u8bf4\uff0c\u201c\u6211\u60f3\u8bf7\u5979\u5403\u996d\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u90b5\u81f3\u521a\u3001\u76db\u9752\u3001\u5434\u5929\u5b9d\uff0c\u8fd8\u6709\u6211\u5c0f\u53d4\u662f\u6295\u8d44\u4eba\uff0c\u7fdf\u4e39\u9752\u662f\u8fc7\u6765\u6c47\u62a5\u65b0\u829c\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u6539\u5236\u8c08\u5224\u8fdb\u5ea6\u7684\uff0c\u5979\u4eec\u4e58\u6c5f\u8f6e\uff0c\u5e94\u8be5\u662f\u4e0a\u5348\u5230\u3002\u4f60\u8bf7\u5979\u5403\u996d\u554a\uff1f\u90a3\u665a\u4e0a\u4e00\u8d77\u5403\u996d\u5427\uff0c\u6211\u767d\u5929\u547d\u82e6\uff0c\u8fd8\u8981\u53bb\u8003\u8bd5\u2026\u2026\u201d\u5f20\u606a\u5fc3\u60f3\u665a\u6674\u8fd8\u662f\u5bf9\u7fdf\u4e39\u9752\u4e0e\u4f17\u4e0d\u540c\u7684\u4eba\u751f\u611f\u5174\u8da3\u5427\u3002\u771f\u641e\u4e0d\u660e\u767d\u4e24\u4e2a\u6210\u719f\u800c\u8273\u4e3d\u7684\u5973\u4eba\u6709\u4ec0\u4e48\u597d\u60fa\u60fa\u76f8\u60dc\u7684\u3002\u79bb\u90b5\u81f3\u521a\u4ed6\u4eec\u4e0a\u56de\u53bb\u65b0\u829c\u90fd\u5feb\u8fc7\u53bb\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5230\u6d77\u5dde\u6765\u5411\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u8fd9\u4e9b\u6295\u8d44\u4eba\u6c47\u62a5\u6539\u5236\u8c08\u5224\u7684\u8fdb\u5c55\uff0c\u4e3b\u8981\u7684\u8fd8\u662f\u60f3\u4eb2\u773c\u770b\u4e00\u770b\u9526\u6e56\u5728\u6d77\u5dde\u7684\u5f71\u54cd\u529b\uff0c\u90a3\u79cd\u9053\u542c\u9014\u8bf4\u3001\u4ece\u62a5\u7aef\u770b\u5230\u7684\u4e8b\u8ff9\uff0c\u603b\u65e0\u6cd5\u7ed9\u5979\u66f4\u5177\u4f53\u7684\u5370\u8c61\uff1a\u5f20\u606a\u8be5\u662f\u600e\u6837\u4e00\u4e2a\u62e5\u6709\u4e0d\u540c\u5e38\u4eba\u7684\u5f02\u8d28\u7684\u7537\u4eba\uff1f</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e5d\u4e09\u5e74\u7684\u65f6\u5019\u6765\u8fc7\u4e00\u6b21\u6d77\u5dde\uff0c\u662f\u65b0\u829c\u5e02\u59d4\u7ec4\u7ec7\u7684\u4e00\u6b21\u5b66\u4e60\uff0c\u6240\u8c13\u7684\u5b66\u4e60\u53ea\u6709\u534a\u5929\u7684\u65f6\u95f4\uff0c\u53c2\u89c2\u6e38\u73a9\u5012\u82b1\u4e86\u56db\u5929\u534a\uff0c\u5bf9\u6d77\u5dde\u6709\u4e9b\u5370\u8c61\uff0c\u5728\u8f6e\u8239\u4e0a\u8fd8\u8ddf\u63cf\u8ff0\u6d77\u5dde\u597d\u73a9\u7684\u5730\u65b9\u3002\u5929\u4e91\u5c71\u9ad8\u8038\u5165\u4e91\u6709\u5343\u7c73\uff0c\u6d77\u5dde\u7684\u8c61\u5c71\u5c31\u4e24\u767e\u7c73\u9ad8\uff0c\u5b9e\u5728\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u4ecb\u7ecd\u7684\u3002</p><p>\u3000\u3000\u7531\u4e8e\u65b0\u829c\u4e0e\u6d77\u5dde\u90fd\u4e0d\u662f\u4f20\u7edf\u4e0a\u7684\u91cd\u8981\u5de5\u4e1a\u57ce\u5e02\uff0c\u4e24\u5730\u4e4b\u95f4\u6ca1\u6709\u76f4\u8fbe\u5217\u8f66\uff0c\u8fc7\u8def\u8f66\u7684\u65f6\u95f4\u591a\u534a\u5728\u534a\u8def\uff0c\u867d\u7136\u4e58\u706b\u8f66\u7684\u65f6\u95f4\u6700\u77ed\uff0c\u4f46\u662f\u8fd8\u4e58\u6c7d\u8f66\u65b9\u4fbf\uff0c\u8981\u662f\u65f6\u95f4\u4e0d\u53ca\uff0c\u4ece\u65b0\u829c\u987a\u6c5f\u800c\u4e0b\uff0c\u4e5f\u53ea\u9700\u8981\u5341\u56db\u4e2a\u5c0f\u65f6\uff0c\u4e58\u9ec4\u660f\u65f6\u5206\u7684\u6c5f\u8f6e\uff0c\u7b2c\u4e8c\u5929\u65e9\u6668\u5c31\u80fd\u62b5\u8fbe\u6d77\u5dde\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4e16\u7eaa\u9910\u996e\u7684\u80a1\u4efd\u90fd\u8f6c\u8ba9\u7ed9\u5c0f\u53d4\uff0c\u65b0\u829c\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u6539\u5236\u7684\u4e8b\u60c5\uff0c\u4ed6\u53ea\u662f\u5076\u5c14\u4f1a\u95ee\u4e00\u4e9b\uff0c\u4e0d\u518d\u5173\u5fc3\u5f88\u8be6\u7ec6\u3002\u90b5\u81f3\u521a\u3001\u5218\u658c\u4ed6\u4eec\u90fd\u4e0d\u662f\u7701\u6cb9\u7684\u706f\uff0c\u4ed6\u6ca1\u6709\u5fc5\u8981\u4e8b\u4e8b\u5173\u5fc3\uff0c\u4e0d\u8fc7\u592e\u884c\u4eca\u5e74\u4ee5\u6765\u8fde\u7eed\u4e24\u6b21\u964d\u606f\uff0c\u653e\u677e\u94f6\u6839\u3001\u9f13\u52b1\u6295\u8d44\uff0c\u73b0\u5728\u4ece\u94f6\u884c\u8d37\u6b3e\u6bd4\u4e0a\u524d\u4e24\u5e74\u5bb9\u6613\u4e00\u4e9b\uff0c\u90b5\u81f3\u521a\u8ba1\u5212\u7740\u5c06\u4ed6\u53bb\u5e74\u5728\u6c99\u7530\u897f\u7247\u62ff\u4e0b\u7684\u90a3\u5757\u5730\u62ff\u51fa\u6765\u5efa\u697c\u3002</p><p>\u3000\u3000\u8239\u5230\u6d77\u5dde\u6e2f\uff0c\u5434\u5929\u5b9d\u62c9\u7740\u5085\u4fca\u5f00\u8f66\u6765\u63a5\u7fdf\u4e39\u9752\u3001\u536b\u5170\uff0c\u8bf4\u9053\uff1a\u201c\u5b66\u6821\u4eca\u5929\u6709\u8003\u8bd5\uff0c\u5230\u665a\u4e0a\u624d\u6709\u7a7a\uff0c\u6211\u662f\u5148\u9001\u4f60\u4eec\u53bb\u5bbe\u9986\uff0c\u8fd8\u662f\u5148\u53bb\u516c\u53f8\uff1f\u201d</p><p>\u3000\u3000\u201c\u5148\u53bb\u516c\u53f8\u5427\uff0c\u51e0\u5bb6\u516c\u53f8\u90fd\u5728\u4e00\u8d77\uff1f\u201d\u7fdf\u4e39\u9752\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u90fd\u4e0d\u5728\u4e00\u8d77\uff0c\u8981\u4e0d\u6211\u5148\u9886\u4f60\u4eec\u53bb\u90b5\u603b\u7684\u516c\u53f8\u53bb\uff0c\u90b5\u603b\u7684\u5730\u4ea7\u516c\u53f8\u4e0e\u4e16\u7eaa\u9910\u996e\u5728\u4e00\u680b\u697c\u91cc\u529e\u516c\uff0c\u5c31\u7d27\u6328\u7740\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\uff0c\u76db\u603b\u7684\u5a31\u4e50\u573a\u5c31\u5728\u9910\u996e\u5e7f\u573a\u8fb9\u4e0a\uff0c\u516c\u53f8\u5374\u5728\u522b\u5904\uff0c\u5f20\u603b\u7684\u516c\u53f8\u4e5f\u5728\u522b\u5904\u3002\u201d\u5085\u4fca\u8bf4\u9053\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5176\u5b9e\u662f\u60f3\u53bb\u9526\u6e56\u3001\u65b0\u5149\u7eb8\u4e1a\u3001\u7231\u8fbe\u7535\u5b50\u53bb\u770b\u4e00\u770b\uff0c\u53ea\u662f\u4e0d\u4fbf\u8bf4\u51fa\u53e3\uff0c\u4fbf\u5750\u8f66\u5148\u53bb\u4e16\u7eaa\u9910\u996e\u8c08\u6b63\u4e8b\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9910\u996e\u91cd\u7ec4\u4e2d\u66f4\u540d\u4e3a\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u9664\u4e86\u5404\u81ea\u540d\u4e0b\u7684\u76f8\u5173\u8d44\u4ea7\u90fd\u7f6e\u5165\u516c\u53f8\uff0c\u76db\u9752\u3001\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u884c\u5171\u62ff\u51fa\u4e24\u5343\u4e07\u7684\u8d44\u91d1\u7528\u4e8e\u516c\u53f8\u7684\u53d1\u5c55\uff0c\u9664\u4e86\u4ed6\u4eec\u4e4b\u5916\uff0c\u6301\u6709\u76db\u4e16\u5e74\u534e\u5a31\u4e50\u573a\u7269\u4e1a\u53e640\uff05\u80a1\u6743\u7684\u90ed\u5efa\u6210\uff08\u8be5\u7269\u4e1a\u6700\u521d\u7684\u62e5\u6709\u8005\uff09\u4e5f\u6210\u4e3a\u4e16\u7eaa\u9910\u996e\u7684\u80a1\u4e1c\uff0c\u4e16\u7eaa\u9910\u996e\u4ece\u4e0d\u5165\u6d41\u7684\u5c0f\u516c\u53f8\uff0c\u4e00\u4e0b\u6574\u5e76\u6210\u51c0\u8d44\u4ea7\u8fd1\u516d\u5343\u4e07\u7684\u5927\u578b\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u3002</p><p>\u3000\u3000\u6881\u519b\u8fd8\u4e0d\u80fd\u72ec\u6321\u4e00\u9762\uff0c\u7279\u522b\u76d7\u7248\u5236\u789f\u8981\u8ddf\u5404\u8272\u4eba\u7269\u6253\u4ea4\u901a\uff0c\u76db\u9752\u8fd8\u4e0d\u80fd\u8131\u5f00\u8eab\u3002\u5e02\u91cc\u5c06\u62c6\u8fc1\u5b89\u7f6e\u623f\u7684\u5927\u534a\u91cd\u4efb\u538b\u5728\u5b8f\u8fdc\u5b9e\u4e1a\u8eab\u4e0a\uff0c\u5f20\u77e5\u975e\u73b0\u9636\u6bb5\u53ea\u80fd\u5750\u5b88\u5b8f\u8fdc\u5b9e\u4e1a\uff0c\u5206\u8eab\u4e4f\u672f\u3002\u91cd\u5e76\u540e\u7684\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\uff0c\u7531\u90b5\u81f3\u521a\u51fa\u4efb\u8463\u4e8b\u957f\u3001\u603b\u7ecf\u7406\uff0c\u5434\u5929\u5b9d\u53ea\u5f53\u4efb\u5e38\u52a1\u526f\u603b\u7ecf\u7406\u7ed9\u90b5\u81f3\u521a\u6253\u4e0b\u624b\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u6bc5\u7136\u5c06\u4ed6\u7ecf\u8425\u591a\u5e74\u3001\u867d\u8bf4\u5229\u6da6\u4e00\u76f4\u4e0d\u9519\u4f46\u662f\u683c\u5c40\u504f\u5c0f\u7684\u82b1\u56ed\u9152\u5e97\u8f6c\u8ba9\u4ed6\u4eba\uff0c\u5957\u51fa\u8d44\u91d1\u7f6e\u5165\u4e16\u7eaa\u9910\u996e\uff0c\u65e5\u540e\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u5e94\u96c6\u4e2d\u7cbe\u529b\u53d1\u5c55\u8f83\u9ad8\u5c42\u6b21\u7684\u5bbe\u9986\u3001\u9910\u996e\u3001\u5a31\u4e50\u4ea7\u4e1a\uff0c\u800c\u4e0d\u7ed9\u8fc7\u5ea6\u5206\u6563\u7684\u4e2d\u5c0f\u578b\u95e8\u5e97\u5206\u6563\u7cbe\u529b\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u975e\u5e38\u94a6\u4f69\u5f20\u606a\u5728\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u9879\u76ee\u4e2d\u8868\u73b0\u51fa\u6765\u7684\u5929\u624d\u6784\u60f3\uff0c\u4e5f\u8ba4\u4e3a\u5c06\u8fd9\u79cd\u6a21\u5f0f\u53d1\u626c\u5149\u5927\uff0c\u624d\u662f\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u53d1\u5c55\u7684\u4e3b\u8981\u51fa\u8def\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u6b63\u4e8b\u7ea6\u5728\u4e0b\u5348\u8c08\uff0c\u5230\u516c\u53f8\u8f6c\u4e86\u4e00\u5708\uff0c\u5434\u5929\u5b9d\u4e0e\u5085\u4fca\u5c31\u9886\u7740\u7fdf\u4e39\u9752\u3001\u536b\u5170\u5979\u4eec\u53bb\u53c2\u89c2\u9910\u996e\u5e7f\u573a\u4e0e\u76db\u4e16\u5e74\u534e\u5a31\u4e50\u573a\u3002</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u76db\u4e16\u5e74\u534e\u5a31\u4e50\u573a\u5e76\u4e0d\u6bd4\u7687\u540e\u591c\u603b\u4f1a\u8c6a\u534e\uff0c\u4f46\u662f\u770b\u5230\u773c\u524d\u8fd9\u4e00\u5207\uff0c\u5728\u4e5d\u4e94\u5e74\u4e4b\u524d\u8fd8\u662f\u7ed9\u758f\u6e2f\u6cb3\u9020\u6210\u4e25\u91cd\u6c61\u67d3\u7684\u65b0\u5149\u9020\u7eb8\u5382\u65e7\u5382\u533a\uff0c\u9910\u996e\u5e7f\u573a\u5185\u5341\u51e0\u5bb6\u9910\u996e\u5e97\u5efa\u7b51\u5e03\u5c40\u6574\u996c\uff0c\u98ce\u683c\u96c5\u81f4\uff0c\u5404\u5bb6\u9910\u996e\u5e97\u62db\u724c\u4e5f\u90fd\u6781\u5177\u7279\u8272\uff0c\u5e7f\u573a\u5185\u6d53\u836b\u7e41\u82b1\uff0c\u9752\u7816\u5d4c\u9053\uff0c\u4e0e\u5916\u9762\u7684\u5e02\u6c11\u7eff\u5730\u7eff\u5730\u76f8\u878d\u6d3d\uff0c\u80cc\u540e\u7684\u758f\u6e2f\u6cb3\u77f3\u9636\u5806\u780c\uff0c\u5782\u6768\u67f3\u3001\u6728\u5236\u957f\u6905\u3001\u94f8\u94c1\u6276\u680f\u3001\u5e74\u8f7b\u7537\u5973\u90fd\u5c06\u6b64\u5904\u5f53\u6210\u5e7d\u4f1a\u7684\u573a\u6240\uff0c\u771f\u96be\u60f3\u8c61\u5434\u5929\u5b9d\u63cf\u8ff0\u4e24\u5e74\u524d\u8fd9\u91cc\u7684\u666f\u8272\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6e10\u6e10\u6709\u4e9b\u660e\u767d\u4e3a\u4ec0\u4e48\u5f20\u606a\u4f1a\u8ba9\u90b5\u81f3\u521a\u8fd9\u4e9b\u8001\u7ec3\u3001\u5706\u6ed1\u7684\u4eba\u7269\u4fef\u9996\u542c\u547d\uff0c\u5f20\u606a\u90a3\u65f6\u5019\u7b56\u5212\u8fd9\u4e9b\u4e8b\u7684\u65f6\u5019\u8fd8\u4e0d\u5230\u5341\u4e03\u5c81\u5427\u3002</p><p>\u3000\u3000\u4e2d\u5348\u5c31\u5728\u5efa\u90ba\u9152\u5e97\u91cc\u7528\u9910\uff0c\u5f20\u77e5\u975e\u9002\u5de7\u6709\u7a7a\uff0c\u539f\u6253\u7b97\u8981\u9001\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u5148\u53bb\u5bbe\u9986\u4f11\u606f\u4e00\u4e0b\uff0c\u5f20\u77e5\u975e\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u77e5\u9053\u8c22\u665a\u6674\u665a\u4e0a\u8981\u8bf7\u7fdf\u4e39\u9752\u5403\u996d\u7684\u4e8b\u60c5\uff0c\u4fbf\u8bf4\u4ed6\u5728\u65b0\u9526\u82d1\u7684\u623f\u5b50\u4e00\u76f4\u7a7a\u4e2d\uff0c\u94a5\u5319\u4e5f\u5728\u5f20\u606a\u90a3\u91cc\uff0c\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u5728\u6d77\u5dde\uff0c\u4e0d\u9700\u8981\u5bbe\u9986\uff0c\u53ef\u4ee5\u4f4f\u5230\u90a3\u91cc\u53bb\u3002</p><p>\u3000\u3000\u4f4f\u5bbe\u9986\u603b\u6709\u8eab\u5728\u5f02\u4e61\u7684\u611f\u89e6\uff0c\u7279\u522b\u662f\u4e24\u4e2a\u5973\u4eba\uff0c\u4e24\u4e2a\u6f02\u4eae\u7684\u5973\u4eba\u51fa\u5165\u5bbe\u9986\u6216\u8bb8\u4f1a\u5f15\u8d77\u522b\u4eba\u4e0d\u5408\u5b9c\u7684\u8054\u60f3\uff0c\u6709\u623f\u5b50\u4f4f\u81ea\u7136\u662f\u597d\u3002</p><p>\u3000\u3000\u4e0b\u5348\uff0c\u90b5\u81f3\u521a\u7a7a\u4e0b\u6765\uff0c\u4e0e\u5f20\u77e5\u975e\u3001\u5434\u5929\u5b9d\u4e00\u8d77\u8ba4\u771f\u542c\u7fdf\u4e39\u9752\u6c47\u62a5\u8fd9\u6bb5\u65f6\u95f4\u6765\u4ee3\u8868\u4ed6\u4eec\u4e0e\u65b0\u829c\u5e02\u6539\u5236\u5c0f\u7ec4\u7684\u8c08\u5224\u6210\u679c\u4ee5\u53ca\u5979\u5bf9\u65e5\u540e\u5bbe\u9986\u7ecf\u8425\u7684\u6574\u4f53\u89c4\u5212\uff0c\u624d\u5230\u4e09\u70b9\u949f\uff0c\u8fd8\u6ca1\u5f85\u5085\u4fca\u5f00\u8f66\u53bb\u63a5\uff0c\u5f20\u606a\u5c31\u81ea\u5df1\u8d70\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u8bf4\u4e00\u5929\u90fd\u6709\u8003\u8bd5\uff1f\u201d\u5f20\u77e5\u975e\u89c1\u5f20\u606a\u63a8\u5f00\u4f1a\u8bae\u5ba4\u7684\u95e8\u62ac\u51fa\u5934\u6765\u5f20\u671b\uff0c\u95ee\u4ed6\uff0c\u201c\u4f60\u600e\u4e48\u8fd9\u65f6\u5019\u5c31\u51fa\u73b0\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0a\u5348\u8003\u8bed\u6587\uff0c\u86ee\u987a\u5229\uff0c\u5199\u4e86\u4e00\u7bc7\u9887\u4e3a\u81ea\u5f97\u7684\u4f5c\u6587\uff0c\u7b49\u4f1a\u513f\u80cc\u8bf5\u51fa\u6765\u8ba9\u4f60\u4eec\u6da8\u6da8\u89c1\u8bc6\uff0c\u201d\u89c1\u5c0f\u53d4\u4ed6\u4eec\u90fd\u6487\u8fc7\u5934\u53bb\uff0c\u5f20\u606a\u624b\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u53c8\u7b11\u4e86\u7b11\u8bf4\uff0c\u201c\u4e0b\u5348\u8003\u6570\u5b66\uff0c\u6709\u4e00\u5927\u534a\u9898\u770b\u8d77\u6765\u719f\u6089\uff0c\u5374\u4e0d\u77e5\u9053\u600e\u4e48\u4e0b\u624b\uff0c\u5c31\u82b1\u4e86\u534a\u4e2a\u5c0f\u65f6\u5c06\u4f1a\u505a\u7684\u9898\u7b54\u4e86\u4e00\u904d\uff0c\u7b54\u5b8c\u5c31\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u536b\u5170\u5fcd\u4e0d\u4f4f\u6251\u54e7\u4e00\u7b11\uff0c\u5979\u6574\u5929\u4e0e\u7fdf\u4e39\u9752\u5728\u4e00\u8d77\uff0c\u7fdf\u4e39\u9752\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u5979\u4e5f\u5dee\u4e0d\u591a\u90fd\u77e5\u9053\u4e86\u3002\u77e5\u9053\u4e86\uff0c\u4e5f\u5c31\u7edd\u4e86\u5bf9\u5f20\u606a\u7684\u90a3\u4efd\u5ff5\u60f3\uff0c\u8fd9\u4e2a\u7537\u4eba\u79bb\u5979\u7684\u8ddd\u79bb\u6709\u5341\u4e07\u516b\u5343\u91cc\u4e4b\u9065\uff0c\u5f92\u6709\u7f8e\u4e3d\u7684\u5916\u8868\u662f\u5438\u5f15\u4e0d\u4e86\u8fd9\u6837\u7684\u7537\u4eba\u7684\uff0c\u56f4\u7ed5\u5728\u4ed6\u8eab\u8fb9\u7684\u7f8e\u5973\u8fd8\u4f1a\u5c11\u5417\uff1f\u53d1\u751f\u90a3\u6837\u7684\u4e8b\u60c5\uff0c\u81ea\u5df1\u4e0d\u5e94\u8be5\u662f\u5fc3\u5b58\u5e7b\u60f3\u7684\u5c0f\u5973\u5b69\u5b50\u4e86\uff0c\u53ea\u662f\u60f3\u4e0d\u5230\u5f20\u606a\u4f1a\u7ed9\u4e00\u6b21\u666e\u901a\u6570\u5b66\u8003\u8bd5\u96be\u4f4f\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u536b\u5170\u6342\u7740\u5634\u5728\u7b11\uff0c\u5df2\u7ecf\u770b\u4e0d\u53bb\u5bbe\u9986\u90a3\u4ef6\u4e8b\u7ed9\u5979\u7684\u5f71\u54cd\uff0c\u5f53\u7136\uff0c\u5185\u5fc3\u91cc\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u4e00\u773c\u5c31\u80fd\u770b\u900f\u7684\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5510\u5a67\u77e5\u9053\u4f60\u4eec\u8981\u8fc7\u6765\uff0c\u8d76\u5de7\u6211\u4eec\u4eca\u5929\u8003\u5b8c\u3001\u660e\u5929\u5f00\u59cb\u653e\u4e24\u5929\u5047\uff0c\u5979\u8981\u597d\u597d\u966a\u4f60\u4eec\u5728\u6d77\u5dde\u73a9\u4e24\u5929\u5462\uff0c\u597d\u50cf\u9648\u5983\u84c9\u8fc7\u4e24\u5929\u624d\u53bb\u4e1c\u5927\u62a5\u9053\uff0c\u4eca\u5929\u8bf7\u4f60\u4eec\u5403\u996d\u7684\u5730\u65b9\uff0c\u5c31\u662f\u9648\u5983\u84c9\u5bb6\u7684\u79c1\u623f\u83dc\u9986\u3002\u201d</p><p>\u3000\u3000\u4ed6\u4fbf\u5750\u4e0b\u6765\u4e00\u8d77\u542c\u7fdf\u4e39\u9752\u5bf9\u5bbe\u9986\u8fd0\u8425\u7684\u89c4\u5212\uff0c\u7fdf\u4e39\u9752\u5c06\u5979\u8fd9\u4e9b\u5929\u8003\u8651\u7684\u4e00\u4e9b\u60f3\u6cd5\u8868\u8fbe\u51fa\u6765\u4e4b\u540e\uff0c\u9996\u5148\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u606a\u5c11\uff0c\u6709\u4ec0\u4e48\u53ef\u4ee5\u8d50\u6559\u7684\uff1f\u201d\u90b5\u81f3\u521a\u90fd\u628a\u5934\u8f6c\u8fc7\u6765\u770b\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5e26\u4f60\u4eec\u8fd9\u4e48\u5077\u61d2\u7684\uff0c\u6211\u5c31\u662f\u8003\u5b8c\u8bd5\u6ca1\u5730\u65b9\u6253\u53d1\u65f6\u95f4\uff0c\u4f60\u4eec\u8981\u89c9\u5f97\u6211\u5728\u8fd9\u91cc\u788d\u773c\uff0c\u6211\u9a6c\u4e0a\u5c31\u51fa\u53bb\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u4ed6\u4eec\u53ea\u80fd\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u6b63\u56e0\u4e3a\u7fdf\u4e39\u9752\u662f\u5f20\u606a\u63a8\u8350\u7684\u4eba\uff0c\u4ed6\u4eec\u624d\u4f1a\u5bf9\u5979\u4fe1\u4efb\uff0c\u89c1\u5f20\u606a\u8fd9\u65f6\u5019\u53c8\u800d\u8d77\u6ed1\u5934\u6765\uff0c\u60dc\u5b57\u5982\u91d1\uff0c\u4e0d\u80af\u8d39\u8111\u7b4b\uff0c\u771f\u662f\u62ff\u4ed6\u6ca1\u6709\u529e\u6cd5\u3002\u5173\u4e8e\u4e16\u7eaa\u9910\u996e\u672a\u6765\u7684\u53d1\u5c55\uff0c\u90b5\u81f3\u521a\u4e5f\u6709\u8ba4\u771f\u8003\u8651\u8fc7\uff0c\u672c\u6253\u7b97\u501f\u8fd9\u673a\u4f1a\u4e00\u5e76\u8bf4\u51fa\u6765\u8ba8\u8bba\uff0c\u89c1\u5f20\u606a\u8c08\u5174\u4e0d\u6d53\uff0c\u504f\u6682\u65f6\u4f5c\u7f62\u3002</p><p>\u3000\u3000\u5728\u5bf9\u65b0\u829c\u5730\u65b9\u7ecf\u6d4e\u4ee5\u53ca\u5bbe\u9986\u4e1a\u81ea\u8eab\u8fd0\u8425\u7684\u7279\u70b9\u65b9\u9762\uff0c\u7fdf\u4e39\u9752\u81ea\u7136\u62e5\u6709\u6bd4\u5176\u4ed6\u4eba\u591a\u5f97\u591a\u7684\u7ecf\u9a8c\uff0c\u6d89\u53ca\u5230\u5177\u4f53\u7684\u7ecf\u8425\uff0c\u6307\u6559\u5f53\u7136\u8c08\u4e0d\u4e0a\uff0c\u968f\u4fbf\u5f00\u53e3\u8fd8\u4e0d\u5982\u4e0d\u5f00\u53e3\uff0c\u63a5\u4e0b\u6765\u53ea\u662f\u5c06\u6709\u7591\u95ee\u7684\u5730\u65b9\u63d0\u51fa\u6765\u8ba9\u7fdf\u4e39\u9752\u7ed9\u5927\u5bb6\u4e00\u4e00\u89e3\u7b54\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u8c61\u5c71\u5357\u5d16\u690d\u7269\u56ed\u89c4\u5212\u5730\u5357\u4fa7\u7684\u521b\u5efa\u79c1\u7acb\u5b66\u6821\u7684\u6279\u6587\u5df2\u7ecf\u62ff\u4e0b\uff0c\u5404\u9879\u624b\u7eed\u5df2\u7ecf\u9f50\u5907\uff0c\u4e4b\u524d\u5077\u5077\u6478\u7684\u8fdb\u884c\u5f81\u5730\u5de5\u4f5c\uff0c\u773c\u4e0b\u5c31\u53ef\u4ee5\u5927\u5f20\u65d7\u9f13\u7684\u5e72\u8d77\u6765\uff0c\u8c22\u665a\u6674\u8fd9\u65f6\u5019\u51e0\u4e4e\u5c06\u5979\u6240\u6709\u7684\u5fc3\u601d\u90fd\u503e\u6ce8\u5728\u8fd9\u6240\u5b66\u6821\u4e0a\uff0c\u5979\u8981\u60f3\u5728\u660e\u5e74\u5165\u590f\u4e4b\u524d\u5efa\u6210\u4e00\u5ea7\u5728\u5979\u5fc3\u76ee\u4e2d\u81f3\u5584\u81f3\u7f8e\u7684\u79c1\u4eba\u6821\u56ed\u6765\u3002</p><p>\u3000\u3000\u82b7\u5f64\u5979\u4eec\u4e5f\u5230\u4e86\u8be5\u5165\u5b66\u7684\u5e74\u9f84\u4e86\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u63d0\u524d\u4ece\u5728\u690d\u7269\u56ed\u91cc\u8bbe\u7acb\u7684\u4e34\u65f6\u7b79\u5efa\u529e\u516c\u5ba4\u56de\u6765\u3002\u4e3a\u4e86\u65b9\u4fbf\u7ba1\u7406\uff0c\u8c22\u665a\u6674\u5148\u5728\u690d\u7269\u56ed\u5357\u4fa7\uff0c\u4e34\u65f6\u5b66\u6821\u89c4\u5212\u5730\u5904\u5efa\u4e86\u4e00\u680b\u4e09\u5c42\u7684\u529e\u516c\u697c\u7528\u4e8e\u79c1\u7acb\u5b66\u6821\u7684\u7b79\u5907\uff0c\u7b49\u5b66\u6821\u5efa\u6210\u4e4b\u540e\uff0c\u529e\u516c\u697c\u8fd8\u53ef\u4ee5\u7ed9\u690d\u7269\u56ed\u7528\u3002</p><p>\u3000\u3000\u4e34\u8fd1\u9ec4\u660f\uff0c\u5148\u8ba9\u5085\u4fca\u5f00\u8f66\u53bb\u5b66\u6821\u63a5\u5510\u5a67\u8fc7\u6765\uff0c\u7136\u540e\u518d\u4e00\u8d77\u5750\u8f66\u5230\u4e39\u4e95\u5df7\uff0c\u4ece\u6c99\u7530\u524d\u8857\u62d0\u5165\u6d77\u5dde\u68cb\u827a\u9986\u4e0e\u5e9c\u5929\u5927\u53a6\u7684\u90a3\u6761\u5f04\u5802\uff0c\u53ef\u4ee5\u770b\u89c1\u6d77\u6cf0\u5bb6\u7535\u5356\u573a\u7684\u751f\u610f\u8fd8\u7b97\u7ea2\u706b\uff0c\u53f6\u5efa\u658c\u7684\u76db\u946b\u7535\u5668\u5728\u897f\u57ce\u7684\u5bb6\u7535\u5356\u573a\u867d\u7136\u62a2\u5360\u4e86\u5e02\u573a\u7684\u5148\u673a\uff0c\u4f46\u662f\u7531\u4e8e\u5356\u573a\u683c\u5c40\u3001\u5730\u7406\u6761\u4ef6\u7ec8\u8981\u5dee\u4e86\u4e00\u70b9\uff0c\u4e24\u8005\u5728\u6d77\u5dde\u7684\u4e1a\u7ee9\u53ea\u662f\u76f8\u5f53\u800c\u5df2\u3002</p><p>\u3000\u3000\u8f66\u5b50\u505c\u5728\u5e9c\u5929\u5927\u53a6\u80cc\u540e\u7684\u505c\u8f66\u573a\uff0c\u7fdf\u4e39\u9752\u611f\u6168\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u6c99\u7530\u5427\uff0c\u65b0\u829c\u5e02\u59d4\u4e09\u5e74\u524d\u7ec4\u7ec7\u5230\u6d77\u5dde\u53c2\u89c2\u5b66\u4e60\uff0c\u6211\u4e5f\u5230\u8fc7\u8fd9\u91cc\uff0c\u8ddf\u4e09\u5e74\u524d\u6bd4\uff0c\u53d8\u5316\u592a\u5927\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53d8\u5316\u4e5f\u6ca1\u6709\u591a\u5c11\u5927\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u7559\u4e24\u5929\uff0c\u597d\u597d\u770b\u4e00\u770b\u6d77\u5dde\u3002\u201d</p><p>\u3000\u3000\u4ece\u4e5d\u4e94\u5e74\u521d\uff0c\u5e02\u91cc\u901a\u8fc7\u57ce\u533a\u89c4\u5212\u603b\u7a3f\uff0c\u6574\u4e2a\u6c99\u7530\u90fd\u7ed9\u89c4\u5212\u4e3a\u5546\u4e1a\u5546\u52a1\u533a\uff0c\u8981\u5168\u90e8\u62c6\u9664\u91cd\u5efa\uff0c\u6700\u65b0\u542f\u52a8\u7684\u662f\u897f\u7247\u62c6\u8fc1\uff0c\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u6c99\u7530\u6570\u5343\u6237\u65e7\u5b85\u906d\u5230\u62c6\u9664\u3002\u4ed3\u4fc3\u4fee\u5efa\u57ce\u533a\u89c4\u5212\u7684\u96be\u5ea6\u5f88\u9ad8\uff0c\u4e3a\u4e86\u4fdd\u62a4\u4e1c\u7247\u7684\u5efa\u7b51\u7fa4\uff0c\u5f20\u606a\u4ee5\u9526\u6e56\u7684\u540d\u4e49\u9646\u7eed\u51fa\u624b\u8d2d\u4e0b\u4e39\u4e95\u5df7\u8fd1\u534a\u6761\u5df7\u5b50\u7684\u65e7\u5b85\uff0c\u6295\u5165\u5927\u91cf\u7684\u8d44\u91d1\u8fdb\u884c\u65e7\u5b85\u4fee\u7f2e\uff0c\u963b\u6b62\u5176\u4ed6\u5730\u4ea7\u5546\u5bf9\u6c99\u7530\u4e1c\u7247\u7684\u5efa\u7b51\u7fa4\u4e0b\u624b\u3002</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u4e0d\u62c6\uff0c\u4e39\u4e95\u5df7\u4e1c\u9762\u7684\u5730\u57df\u5c31\u4e0d\u80fd\u4e0e\u897f\u8fb9\u8fde\u6210\u4e00\u7247\uff0c\u5f00\u53d1\u7684\u4ef7\u503c\u5c31\u5dee\u4e86\u8bb8\u591a\uff0c\u8981\u62c6\u4e39\u4e95\u5df7\uff0c\u8981\u652f\u4ed8\u7684\u62c6\u8fc1\u6210\u672c\u5c31\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u4e86\u3002</p><p>\u3000\u3000\u91cd\u7ec4\u540e\uff0c\u9526\u6e56\u5df2\u7ecf\u5b8c\u5168\u662f\u5f20\u606a\u4e00\u4e2a\u4eba\u7684\u9526\u6e56\uff0c\u4f46\u5728\u4e00\u822c\u7684\u5370\u8c61\u91cc\uff0c\u8fd8\u5c06\u9526\u6e56\u4e0e\u6d77\u88d5\u516c\u53f8\u3001\u4e0e\u7701\u957f\u5f90\u5b66\u5e73\u8054\u7cfb\u5728\u4e00\u8d77\uff0c\u6ca1\u6709\u50bb\u5230\u8981\u53bb\u5f3a\u62c6\u9526\u6e56\u65d7\u4e0b\u7684\u7269\u4e1a\u3002\u90a3\u4e9b\u7a0d\u5fae\u77e5\u9053\u9526\u6e56\u771f\u6b63\u5e95\u7ec6\u7684\u4eba\uff0c\u5982\u540c\u9526\u57ce\u96c6\u56e2\u7684\u8d75\u9526\u8363\uff0c\u8fd9\u65f6\u5019\u66f4\u4e0d\u4f1a\u4e3b\u52a8\u53bb\u62db\u60f9\u9526\u6e56\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u5927\u534a\u5e74\u6765\u5bf9\u4e39\u4e95\u5df7\u7684\u65e7\u5b85\u6301\u7eed\u6295\u5165\u8d44\u91d1\u8fdb\u884c\u4fee\u7f2e\uff0c\u6574\u6761\u4e39\u4e95\u5df7\u4e0e\u5f80\u65e5\u76f8\u6bd4\uff0c\u6709\u7740\u6539\u5934\u6362\u9762\u7684\u53d8\u5316\uff0c\u7fdf\u4e39\u9752\u7ad9\u5728\u5e9c\u5929\u5927\u53a6\u80cc\u540e\u7684\u505c\u8f66\u573a\u4e0a\uff0c\u8eab\u540e\u4e09\u5341\u5c42\u9ad8\u3001\u65b0\u5efa\u7684\u73b0\u4ee3\u5546\u52a1\u529e\u516c\u7528\u697c\u5e9c\u5929\u5927\u697c\u3001\u5de6\u624b\u5b8c\u5168\u62c6\u9664\u540e\u91cd\u5efa\u7684\u5546\u4e1a\u533a\uff0c\u867d\u7136\u79bb\u5b8c\u5de5\u4e4b\u65e5\u5c1a\u65e9\uff0c\u4f46\u4e5f\u521d\u5177\u96cf\u5f62\uff0c\u53f3\u624b\u662f\u4fee\u7f2e\u5982\u65e7\u3001\u53e4\u8272\u53e4\u9999\u7684\u4e39\u4e95\u5df7\uff0c\u53ea\u770b\u8fd9\u5c40\u90e8\uff0c\u81ea\u7136\u4f1a\u89c9\u5f97\u6d77\u5dde\u8fd9\u4e24\u5e74\u7684\u53d8\u5316\u7ed9\u4eba\u8033\u76ee\u4e00\u65b0\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5176\u4e2d\u7684\u9053\u9053\u8ddf\u7fdf\u4e39\u9752\u7a0d\u7a0d\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u5f53\u7136\u4e0d\u4f1a\u63d0\u53ca\u4ed6\u6697\u4e2d\u62b5\u6297\u57ce\u533a\u89c4\u5212\u7684\u4e8b\u60c5\uff0c\u53ea\u5c06\u6c99\u7530\u9644\u8fd1\u7684\u5168\u8c8c\u8ddf\u7fdf\u4e39\u9752\u4ecb\u7ecd\u4e86\u4e00\u4e0b\uff0c\u514d\u5f97\u5979\u771f\u4ee5\u4e3a\u6d77\u5dde\u7684\u7ecf\u6d4e\u6709\u591a\u5c11\u53d1\u8fbe\uff0c\u6bd4\u60e0\u5c71\u8fd9\u6837\u7684\u57ce\u5e02\u6bd4\u8d77\u6765\uff0c\u8fd8\u5dee\u5f97\u5f88\u591a\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8fc7\u53d8\u5316\u8fd8\u662f\u975e\u5e38\u7684\u5927\uff0c\u201d\u7fdf\u4e39\u9752\u575a\u6301\u5979\u7684\u89c2\u70b9\uff0c\u201c\u6211\u4eec\u4ece\u6d77\u5dde\u6c5f\u6e2f\u7801\u5934\u4e00\u8def\u8fc7\u6765\uff0c\u8def\u4e24\u65c1\u7684\u5efa\u7b51\u90fd\u8fd8\u662f\u4e09\u5e74\u524d\u7684\u5efa\u7b51\uff0c\u4f46\u662f\u7ed9\u4eba\u7684\u611f\u89c9\u6574\u6d01\u591a\u4e86\uff0c\u6bd4\u8d77\u65b0\u829c\u5230\u5904\u90fd\u662f\u7834\u635f\u7684\u8def\u9762\uff0c\u6d77\u5dde\u5e02\u533a\u7684\u9053\u8def\u5c31\u8981\u597d\u5f88\u591a\uff0c\u9053\u8def\u7eff\u5316\u4e5f\u4e0d\u662f\u4e09\u5e74\u80fd\u6bd4\u7684\uff0c\u8fd8\u6709\u56db\u51e4\u6865\u9644\u8fd1\uff0c\u5434\u603b\u62ff\u4ee5\u524d\u7684\u7167\u7247\u7ed9\u6211\u770b\uff0c\u5b8c\u5168\u770b\u5230\u539f\u8c8c\u7684\u4e00\u70b9\u5f71\u5b50\u3002\u6211\u76f8\u4fe1\u8fd9\u91cc\u5c06\u662f\u672a\u6765\u6d77\u5dde\u5e02\u7684\u7f29\u5f71\uff0c\u6d77\u5dde\u518d\u8fd9\u4e48\u53d1\u5c55\u4e0b\u53bb\uff0c\u4e0d\u51fa\u56db\u4e94\u5e74\uff0c\u6211\u770b\u5c31\u8981\u6574\u4e2a\u7684\u7115\u7136\u4e00\u65b0\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u8bf4\u7684\u4e0d\u7b80\u5355\uff0c\u8fd9\u91cc\u5c11\u6709\u4e0d\u662f\u6d77\u5dde\u4eba\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8ba9\u5927\u90e8\u4eba\u5fc3\u91cc\u542c\u4e86\u90fd\u5f88\u8212\u670d\uff0c\u201d\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p></article>"}}}]);
