(window.webpackJsonp=window.webpackJsonp||[]).push([[402],{424:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e5d\u8282 \u6c89\u9ed8\u7684\u7206\u53d1",content:"<article><h2>\u7b2c\u4e94\u5341\u4e5d\u8282 \u6c89\u9ed8\u7684\u7206\u53d1</h2><p>\u8299\u857e\u72c4\u81ea\u7136\u6ca1\u6709\u5f02\u8bae\uff0c\u5b9e\u9645\u4e0a\u5979\u53ea\u8981\u80fd\u548c\u743c\u6069\u5728\u4e00\u8d77\uff0c\u53bb\u54ea\u91cc\u90fd\u884c\u3002\u963f\u65af\u5361\u7279\u62c9\u57ce\u786e\u5b9e\u65b0\u6765\u4e86\u4e00\u4e2a\u9a6c\u620f\u56e2\uff0c\u73b0\u5728\u5c31\u5728\u5e02\u4e2d\u5fc3\u5e7f\u573a\u4e0a\uff0c\u665a\u4e0a\u516b\u70b9\u5f00\u59cb\u8868\u6f14\uff0c\u73b0\u5728\u8fc7\u53bb\u8fd8\u6765\u5f97\u53ca\u3002</p><p>\u60c5\u4eba\u7ea6\u4f1a\uff0c\u6700\u901a\u5e38\u7684\u4f5c\u6cd5\u662f\u770b\u7535\u5f71\uff0c\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\u7535\u5f71\uff0c\u90a3\u5c31\u53ea\u597d\u7528\u9a6c\u620f\u6765\u4ee3\u66ff\u4e86\u3002\u743c\u6069\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u4e2d\uff0c\u9664\u4e86\u73ca\u5609\u4e4b\u5916\uff0c\u4ee5\u8299\u857e\u72c4\u76f8\u8bc6\u6700\u65e9\uff0c\u5728\u4e00\u8d77\u7684\u65f6\u95f4\u4e5f\u662f\u6bd4\u8f83\u591a\u7684\uff0c\u4f46\u8fd8\u771f\u4ece\u6765\u6ca1\u4e00\u8d77\u53bb\u770b\u8fc7\u9a6c\u620f\uff0c\u751a\u81f3\u901b\u8857\u7684\u6b21\u6570\u90fd\u5f88\u5c11\u3002\u6700\u521d\u5728\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u9662\u7684\u65f6\u5019\uff0c\u5c01\u95ed\u5f0f\u7ba1\u7406\uff0c\u4e00\u4e2a\u6708\u53ea\u6709\u4e00\u5929\u5047\uff0c\u743c\u6069\u8981\u56de\u5bb6\u966a\u59d0\u59d0\uff0c\u987e\u4e0d\u4e0a\u8299\u857e\u72c4\uff1b\u540e\u6765\u518d\u7eed\u524d\u7f18\uff0c\u5c31\u662f\u5230\u4e86\u5e7d\u6697\u5730\u57df\uff0c\u90a3\u79cd\u9b3c\u5730\u65b9\u81ea\u7136\u66f4\u6ca1\u4ec0\u4e48\u597d\u901b\u7684\u3002\u8fd9\u4e48\u7b97\u8d77\u6765\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\uff1f</p><p>\u5514\uff0c\u81ea\u5df1\u6574\u5929\u8dd1\u6765\u8dd1\u53bb\uff0c\u548c\u5404\u8272\u5404\u6837\u7684\u53d8\u6001\u5b58\u5728\u6253\u4ea4\u9053\uff0c\u5fd9\u5f97\u50cf\u6761\u4e27\u5bb6\u4e4b\u72ac\uff0c\u8eab\u8fb9\u5973\u4eba\u867d\u7136\u591a\uff0c\u4e5f\u5e38\u5e38\u4e0a\uff0c\u8273\u798f\u662f\u4e0d\u6d45\uff0c\u4f46\u771f\u6b63\u7275\u624b\u901b\u8857\uff0c\u8c08\u60c5\u8bf4\u7231\uff0c\u8fd9\u79cd\u6709\u6d6a\u6f2b\u60c5\u8c03\u7684\u4e8b\u5374\u505a\u5f97\u592a\u5c11\u4e86\uff0c\u5b9e\u5728\u662f\u7f8e\u4e2d\u4e0d\u8db3\uff0c\u4ee5\u540e\u8981\u591a\u52a0\u6ce8\u610f\u2026\u2026\u53ea\u53ef\u60dc\uff0c\u4eba\u5728\u6c5f\u6e56\uff0c\u8eab\u4e0d\u7531\u5df1\u554a\u3002</p><p>\u6709\u4e86\u8fd9\u4e2a\u89c9\u609f\uff0c\u743c\u6069\u4fbf\u8d8a\u52a0\u73cd\u60dc\u8fd9\u96be\u5f97\u7684\u673a\u4f1a\uff0c\u8f7b\u8f7b\u63e1\u7740\u8eab\u65c1\u5c11\u5973\u7684\u624b\uff0c\u4ece\u7199\u7199\u6518\u6518\u7684\u5927\u6865\u4e0a\u8d70\u8fc7\uff0c\u4e00\u8def\u524d\u5f80\u5e02\u4e2d\u5fc3\u5e7f\u573a\uff0c\u987a\u4fbf\u8fd8\u4e70\u4e86\u4e00\u888b\u7206\u7c73\u82b1\u3002</p><p>\u201c\u9a6c\u620f\u56e2\u91cc\u6709\u4ec0\u4e48\u554a\uff1f\u201d\u8299\u857e\u72c4\u95ee\u3002</p><p>\u201c\u8fd9\u4e2a\uff0c\u6211\u5176\u5b9e\u4e5f\u6ca1\u770b\u8fc7\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u65e0\u975e\u662f\u72d7\u718a\u9a91\u5355\u8f66\uff0c\u8001\u864e\u94bb\u706b\u5708\uff0c\u8fd8\u6709\u5c0f\u4e11\u4e4b\u7c7b\u7684\u5427\u3002\u201d</p><p>\u201c\u5c0f\u4e11\uff1f\u201d</p><p>\u201c\u5443\uff0c\u5c31\u662f\u6ed1\u7a3d\u6f14\u5458\u5566\uff0c\u5230\u65f6\u5019\u4f60\u4e00\u770b\u5c31\u77e5\u9053\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e00\u8fb9\u52aa\u529b\u89e3\u91ca\u7740\uff0c\u5e26\u7740\u8299\u857e\u72c4\u5230\u4e86\u4e2d\u5fc3\u5e7f\u573a\uff0c\u8fdc\u8fdc\u5df2\u7ecf\u770b\u89c1\u9a6c\u620f\u56e2\u7684\u5de8\u5927\u5e10\u7bf7\uff0c\u7136\u800c\u8d70\u5230\u8fd1\u524d\uff0c\u5374\u53d1\u73b0\u5468\u56f4\u7a7a\u51fa\u597d\u5927\u4e00\u5757\u7a7a\u5730\uff0c\u8eab\u7a7f\u94e0\u7532\u7684\u536b\u5175\u624b\u6301\u957f\u621f\u5e03\u6210\u4e86\u8b66\u6212\u7ebf\uff0c\u628a\u9a6c\u620f\u56e2\u7ed9\u56f4\u4e86\u8d77\u6765\uff0c\u7981\u6b62\u6240\u6709\u4eba\u9760\u8fd1\uff0c\u4e00\u526f\u5982\u4e34\u5927\u654c\u7684\u6a21\u6837\u3002</p><p>\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u96be\u9053\u9a6c\u620f\u56e2\u91cc\u53d1\u751f\u51f6\u6740\u6848\u4e86\uff1f\u201d</p><p>\u201c\u6bd4\u51f6\u6740\u6848\u8fd8\u6050\u6016\u3002\u201d\u65c1\u8fb9\u4e00\u4e2a\u4eba\u8bf4\u3002</p><p>\u7c97\u7565\u6253\u542c\u624d\u77e5\u9053\uff0c\u539f\u6765\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\uff0c\u9a6c\u620f\u56e2\u516b\u70b9\u949f\u6b63\u5f0f\u5f00\u6f14\uff0c\u6b64\u524d\u81ea\u7136\u5c31\u5df2\u7ecf\u6709\u4eba\u9646\u9646\u7eed\u7eed\u4e70\u7968\u8fdb\u573a\uff0c\u7136\u800c\u6240\u6709\u8fdb\u53bb\u7684\u4eba\uff0c\u5c31\u6ca1\u4e00\u4e2a\u51fa\u6765\u7684\u3002\u4e00\u5f00\u59cb\u5927\u5bb6\u8fd8\u6ca1\u6ce8\u610f\uff0c\u6bd5\u7adf\u9a6c\u620f\u56e2\u4e0d\u662f\u536b\u751f\u95f4\uff0c\u8fdb\u53bb\u4e4b\u540e\u5750\u4e0b\u770b\u8868\u6f14\uff0c\u4e00\u65f6\u534a\u4f1a\u4e0d\u51fa\u6765\u5f88\u6b63\u5e38\u3002\u7136\u800c\u65f6\u95f4\u957f\u4e86\uff0c\u4eba\u6570\u591a\u4e86\uff0c\u7ec8\u7a76\u8fd8\u662f\u6709\u4eba\u53d1\u73b0\u4e86\u8e4a\u8df7\u3002\u7591\u5fc3\u4e00\u8d77\uff0c\u8981\u9a8c\u8bc1\u5c31\u5f88\u5bb9\u6613\uff0c\u5f53\u7b2c\u4e09\u4e2a\u8fdb\u53bb\u5bdf\u770b\u7684\u4eba\u4e5f\u6ca1\u51fa\u6765\u4e4b\u540e\uff0c\u5927\u5bb6\u4fbf\u5f00\u59cb\u60ca\u60f6\uff0c\u57ce\u91cc\u7684\u536b\u5175\u4e5f\u63a5\u5230\u6d88\u606f\u8d76\u4e86\u8fc7\u6765\u3002</p><p>\u201c\u536b\u5175\u4e5f\u9677\u8fdb\u53bb\u4e86\uff1f\u201d\u743c\u6069\u731c\u6d4b\u3002</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u548c\u5e02\u6c11\u4e00\u6837\uff0c\u51e0\u4e2a\u536b\u5175\u4e5f\u662f\u6709\u53bb\u65e0\u56de\uff0c\u9a6c\u620f\u56e2\u7684\u5de8\u5927\u5e10\u7bf7\u4eff\u4f5b\u5c31\u50cf\u4e00\u4e2a\u72f0\u72de\u602a\u517d\uff0c\u541e\u566c\u7740\u6240\u6709\u6562\u4e8e\u8fdb\u6765\u7684\u4eba\u3002\u963f\u65af\u5361\u7279\u62c9\u7684\u536b\u5175\u4e0d\u80fd\u8bf4\u4e0d\u52c7\u6562\uff0c\u4f46\u9762\u5bf9\u8fd9\u79cd\u7075\u5f02\u4e8b\u4ef6\uff0c\u4ed6\u4eec\u4e5f\u671b\u800c\u5374\u6b65\uff0c\u961f\u957f\u4e0b\u4ee4\u5148\u628a\u5e10\u7bf7\u56f4\u4f4f\uff0c\u7b49\u5f85\u4e13\u4e1a\u4eba\u58eb\u524d\u6765\u3002</p><p>\u6240\u8c13\u7684\u4e13\u4e1a\u4eba\u58eb\uff0c\u81ea\u7136\u5c31\u662f\u6307\u515c\u5e3d\u5deb\u5e08\u3002</p><p>\u9047\u5230\u8fd9\u79cd\u4e8b\u60c5\uff0c\u660e\u663e\u5df2\u7ecf\u662f\u9b54\u6cd5\u7684\u8303\u7574\uff0c\u5fc5\u987b\u5f97\u5deb\u5e08\u51fa\u573a\u3002\u5b89\u59c6\u5e1d\u56fd\u6392\u65a5\u5965\u672f\uff0c\u963f\u65af\u5361\u7279\u62c9\u57ce\u91cc\u4e5f\u6ca1\u51e0\u4e2a\u201c\u81ea\u7531\u201d\u5deb\u5e08\uff0c\u57fa\u672c\u5168\u90fd\u662f\u96b6\u5c5e\u4e8e\u515c\u5e3d\u5deb\u5e08\u516c\u4f1a\u3002\u516c\u4f1a\u6709\u6743\u9881\u53d1\u65bd\u6cd5\u8bb8\u53ef\u8bc1\uff0c\u6709\u6743\u6293\u6355\u5904\u7f6e\u201c\u5965\u672f\u7f6a\u72af\u201d\uff0c\u7b97\u662f\u534a\u5b98\u65b9\u7ec4\u7ec7\uff0c\u50cf\u8fd9\u79cd\u7075\u5f02\u4e8b\u4ef6\uff0c\u4e5f\u5c5e\u4e8e\u4ed6\u4eec\u7684\u804c\u8d23\u8303\u56f4\u4e4b\u5185\u3002</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u515c\u5e3d\u5deb\u5e08\u65e9\u5c31\u5df2\u7ecf\u8d76\u6765\uff0c\u4eca\u5929\u5374\u4e0d\u77e5\u9053\u600e\u4e48\u641e\u7684\uff0c\u8fdf\u8fdf\u4e0d\u89c1\u8e2a\u5f71\uff0c\u8fd9\u8ba9\u5e02\u6c11\u4eec\u8bae\u8bba\u7eb7\u7eb7\uff0c\u5fc3\u751f\u7591\u8651\u3002\u743c\u6069\u5374\u662f\u77e5\u9053\u5185\u60c5\u7684\uff0c\u515c\u5e3d\u5deb\u5e08\u4eec\u88ab\u8001\u5deb\u5996\u62c9\u6c83\u514b\u6740\u5f97\u7247\u7532\u4e0d\u7559\uff0c\u8fd1\u4e4e\u5168\u519b\u8986\u706d\uff0c\u5982\u4eca\u6b63\u5904\u4e8e\u6700\u8870\u5f31\u7684\u65f6\u671f\uff0c\u54ea\u91cc\u8fd8\u613f\u610f\u591a\u60f9\u9ebb\u70e6\u3002\u5c31\u7b97\u60f3\u7ba1\uff0c\u4e00\u65f6\u95f4\u4e5f\u672a\u5fc5\u62bd\u8c03\u5f97\u51fa\u4eba\u624b\u3002</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u80af\u5b9a\u8f6c\u8eab\u8d70\u4eba\uff0c\u4f55\u5fc5\u591a\u60f9\u662f\u975e\uff0c\u4f46\u4eca\u5929\u662f\u5e26\u7740\u5973\u53cb\u5728\u8eab\u8fb9\uff0c\u8bf4\u597d\u8981\u5e26\u5979\u770b\u9a6c\u620f\u7684\uff0c\u4e00\u8def\u5174\u81f4\u52c3\u52c3\u5230\u4e86\u5730\u65b9\uff0c\u649e\u4e0a\u8fd9\u79cd\u4e8b\u60c5\uff0c\u603b\u662f\u9887\u4e3a\u4e0d\u723d\u3002\u4ed6\u53c8\u8be2\u95ee\u4e86\u4e00\u4e9b\u8ff9\u8c61\uff0c\u7565\u60f3\u4e86\u4e00\u60f3\uff0c\u201c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u8f7b\u58f0\u5bf9\u65c1\u8fb9\u7684\u5c11\u5973\u8bf4\uff0c\u201c\u5e2e\u6211\u5f3a\u5316\u4e00\u4e0b\u5965\u672f\u89c6\u89c9\u3002\u201d</p><p>\u201c\u53ef\u662f\u57ce\u91cc\u4e0d\u80fd\u65bd\u6cd5\u7684\u5440\u3002\u201d\u8299\u857e\u72c4\u6084\u58f0\u63d0\u9192\u3002</p><p>\u201c\u73b0\u5728\u6ca1\u4eba\u7ba1\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u542c\u6211\u7684\u5c31\u662f\u3002\u201d</p><p>\u8299\u857e\u72c4\u70b9\u70b9\u5934\uff0c\u6084\u6084\u81ea\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u8c61\u7259\u6cd5\u6756\uff0c\u7b3c\u5728\u8896\u4e2d\uff0c\u4f4e\u58f0\u5ff5\u8bf5\u4e86\u4e00\u53e5\u5492\u8bed\u3002\u4e00\u56e2\u5e38\u4eba\u770b\u4e0d\u89c1\u7684\u6de1\u7d2b\u8272\u96fe\u6c14\u81ea\u5979\u7684\u8896\u4e2d\u6d8c\u51fa\uff0c\u878d\u5165\u743c\u6069\u4f53\u5185\u3002\u540c\u4e00\u65f6\u95f4\uff0c\u743c\u6069\u81ea\u5df1\u7684\u53cc\u773c\u4e5f\u53d8\u6210\u6de1\u7d2b\uff0c\u9690\u9690\u661f\u8292\u95ea\u70c1\uff0c\u62ac\u5934\u671d\u7740\u9a6c\u620f\u56e2\u7684\u5e10\u7bf7\u671b\u8fc7\u53bb\u3002</p><p>\u7ecf\u8fc7\u9884\u8a00\u5e08\u5f3a\u5316\u7684\u5965\u672f\u89c6\u89c9\u6beb\u65e0\u969c\u788d\u5730\u7a7f\u900f\u4e86\u5e10\u7bf7\uff0c\u6e05\u695a\u770b\u89c1\u91cc\u9762\u7684\u60c5\u5f62\u3002\u743c\u6069\u7f13\u7f13\u79fb\u52a8\u7740\u76ee\u5149\uff0c\u4ed4\u7ec6\u89c2\u5bdf\u7740\uff0c\u8fc7\u4e86\u4e00\u4f1a\u7a81\u7136\u7b11\u4e86\u8d77\u6765\u3002\u201c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u8fdb\u53bb\u73a9\u73a9\u600e\u4e48\u6837\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u8299\u857e\u72c4\u6beb\u4e0d\u8fdf\u7591\u5730\u70b9\u5934\uff0c\u743c\u6069\u643a\u7740\u5979\u7684\u624b\uff0c\u4e0a\u524d\u548c\u536b\u5175\u4ea4\u6d89\uff0c\u8bf4\u81ea\u5df1\u662f\u6765\u81ea\u6cf0\u745f\u5c14\u7684\u5192\u9669\u8005\uff0c\u5e0c\u671b\u80fd\u591f\u8fdb\u53bb\u770b\u770b\u662f\u600e\u4e48\u56de\u4e8b\u3002\u65f6\u5df2\u6df1\u79cb\uff0c\u665a\u4e0a\u6c14\u6e29\u5f88\u4f4e\uff0c\u4ed6\u548c\u8299\u857e\u72c4\u90fd\u7a7f\u7740\u957f\u888d\uff0c\u5916\u7f69\u6597\u7bf7\uff0c\u5012\u662f\u6807\u51c6\u7684\u5192\u9669\u8005\u6253\u626e\uff0c\u536b\u5175\u961f\u957f\u4e5f\u7b97\u6709\u4e9b\u773c\u5149\uff0c\u867d\u7136\u89c9\u5f97\u8fd9\u4e24\u4eba\u4f3c\u4e4e\u592a\u8fc7\u5e74\u8f7b\uff0c\u4f46\u5e94\u8be5\u4e5f\u662f\u6709\u4e9b\u672c\u4e8b\uff0c\u65e2\u7136\u81ea\u613f\u8fdb\u53bb\uff0c\u90a3\u4e5f\u6b63\u597d\u987a\u6c34\u63a8\u821f\uff0c\u4e8e\u662f\u70b9\u5934\u5e94\u5141\u4e86\u3002</p><p>\u201c\u8fdb\u53bb\u4e4b\u540e\uff0c\u4f60\u53ea\u7ba1\u7275\u7740\u6211\u7684\u624b\uff0c\u522b\u653e\u5f00\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u5bf9\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u5e10\u7bf7\u91cc\u9762\u6709\u4e00\u4e2a\u5e7b\u672f\u5e08\uff0c\u672c\u9886\u4e0d\u9ad8\uff0c\u4ed6\u91ca\u653e\u4e86\u4e00\u4e2a\u2018\u5e7b\u666f\u2019\uff08\u6d77\u5e02\u8703\u697c\u7684\u4f4e\u7ea7\u7248\u672c\uff09\u3002\u4f60\u65e0\u8bba\u770b\u5230\u4ec0\u4e48\uff0c\u54ea\u6015\u662f\u5200\u5c71\u706b\u6d77\uff0c\u6d2a\u6c34\u731b\u517d\uff0c\u4e00\u6982\u4e0d\u8981\u7406\u776c\uff0c\u90a3\u4e9b\u5168\u90fd\u662f\u865a\u5047\u5e7b\u50cf\u3002\u4f60\u53ea\u8981\u8ddf\u7740\u6211\u8d70\u5c31\u884c\uff0c\u77e5\u9053\u5417\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4fbf\u5982\u743c\u6069\u6240\u8bf4\uff0c\u521a\u521a\u8d70\u8fdb\u5e10\u7bf7\uff0c\u773c\u524d\u666f\u8c61\u4fbf\u4e3a\u4e4b\u4e00\u53d8\uff0c\u4eff\u4f5b\u7f6e\u8eab\u4e8e\u4e00\u5ea7\u57ce\u5821\u524d\u3002\u743c\u6069\u548c\u8299\u857e\u72c4\u5f84\u76f4\u8d70\u8fdb\u57ce\u95e8\uff0c\u95ef\u5165\u5927\u5385\uff0c\u653e\u773c\u6240\u53ca\uff0c\u5230\u5904\u90fd\u662f\u98df\u4eba\u9b54\u3001\u8c7a\u72fc\u4eba\u3001\u718a\u5730\u7cbe\u3001\u5de8\u9b54\u7b49\u602a\u7269\uff0c\u547c\u543c\u5486\u54ee\uff0c\u9f87\u7259\u54a7\u5634\uff0c\u6216\u8005\u5728\u6293\u7740\u534a\u622a\u4eba\u7684\u5c38\u4f53\u5927\u5543\u5927\u56bc\uff0c\u5730\u9762\u4e0a\u6ee1\u662f\u8840\u8165\u548c\u6b8b\u7834\u80a2\u4f53\u3001\u5185\u810f\uff0c\u770b\u4e0a\u53bb\u4eff\u4f5b\u4e00\u811a\u8e0f\u8fdb\u4e86\u5996\u9b54\u7684\u5de2\u7a74\u3002</p><p>\u8299\u857e\u72c4\u7ec8\u7a76\u80c6\u5c0f\uff0c\u867d\u7136\u6709\u743c\u6069\u4e8b\u5148\u63d0\u9192\uff0c\u77e5\u9053\u5176\u5b9e\u90fd\u662f\u5e7b\u50cf\uff0c\u4f46\u4f9d\u65e7\u5bb3\u6015\u8d77\u6765\uff0c\u7d27\u7d27\u62b1\u7740\u743c\u6069\u7684\u80f3\u818a\uff0c\u5c06\u8eab\u4f53\u5c3d\u53ef\u80fd\u8d34\u8fd1\u3002\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u6ca1\u4e8b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u95fb\u95fb\u770b\uff0c\u7a7a\u6c14\u91cc\u534a\u70b9\u8840\u8165\u6c14\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u8299\u857e\u72c4\u88ab\u4ed6\u4e00\u63d0\u9192\uff0c\u95fb\u4e86\u4e00\u95fb\uff0c\u53d1\u73b0\u7a7a\u6c14\u91cc\u679c\u7136\u6ca1\u6709\u534a\u70b9\u8840\u8165\u6c14\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e48\u591a\u602a\u7269\u805a\u96c6\u5728\u4e00\u8d77\u7406\u5e94\u53d1\u51fa\u7684\u6076\u81ed\u3002\u4eba\u5c31\u662f\u8fd9\u6837\uff0c\u5fc3\u91cc\u77e5\u9053\u771f\u76f8\uff0c\u4f46\u8fd8\u662f\u5f88\u5bb9\u6613\u88ab\u611f\u5b98\u6240\u5f71\u54cd\uff0c\u5982\u4eca\u8299\u857e\u72c4\u7684\u89c6\u89c9\u548c\u55c5\u89c9\u76f8\u51b2\u7a81\uff0c\u5e7b\u672f\u7684\u6548\u529b\u7acb\u523b\u5c31\u5927\u6253\u6298\u6263\u4e86\u3002</p><p>\u743c\u6069\u63fd\u7740\u5c11\u5973\u7684\u7ea4\u8170\uff0c\u4e00\u8def\u4ece\u602a\u7269\u7fa4\u4e2d\u7a7f\u8fc7\u3002\u51e0\u4e2a\u718a\u5730\u7cbe\u6325\u821e\u72fc\u7259\u68d2\u51b2\u4e0a\u6765\u5f53\u5934\u5c31\u7838\uff0c\u8299\u857e\u72c4\u60ca\u53eb\u4e00\u58f0\uff0c\u773c\u770b\u8eb2\u95ea\u4e0d\u53ca\uff0c\u743c\u6069\u5374\u4e0d\u7406\u4e0d\u776c\uff0c\u4efb\u7531\u653b\u51fb\uff0c\u5f84\u76f4\u5f80\u524d\u8d70\u3002\u53ea\u542c\u5f97\u55e4\u55e4\u51e0\u58f0\uff0c\u72fc\u7259\u68d2\u548c\u743c\u6069\u7684\u8eab\u4f53\u4e00\u78b0\uff0c\u987f\u65f6\u6d88\u5931\uff0c\u8fde\u5e26\u90a3\u51e0\u4e2a\u718a\u5730\u7cbe\u90fd\u4e0d\u89c1\u8e2a\u5f71\u3002</p><p>\u201c\u865a\u5f71\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fde\u811a\u6b65\u58f0\u97f3\u548c\u98ce\u58f0\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u4ed6\u4e4b\u6240\u4ee5\u6562\u8fdb\u6765\uff0c\u4e5f\u6b63\u662f\u56e0\u4e3a\u4e8b\u5148\u5df2\u7ecf\u5224\u65ad\u51fa\u4e86\u5bf9\u624b\u7684\u5927\u81f4\u6c34\u51c6\u3002\u80fd\u591f\u521b\u9020\u201c\u5e7b\u666f\u201d\uff0c\u5728\u5e7b\u672f\u4e0a\u7684\u9020\u8be3\u4e5f\u4e0d\u7b97\u5dee\u4e86\u3002\u4f46\u5e7b\u672f\u4e4b\u6240\u4ee5\u80fd\u591f\u8ff7\u60d1\u4eba\uff0c\u9760\u7684\u662f\u5f71\u54cd\u4e94\u5b98\u4e94\u8bc6\uff0c\u5f71\u50cf\u3001\u58f0\u97f3\u3001\u6c14\u5473\u3001\u6e29\u5ea6\u3001\u89e6\u611f\uff0c\u8fd9\u4e9b\u90fd\u8981\u7cbe\u786e\u6a21\u62df\u51fa\u6765\uff0c\u76f8\u4e92\u914d\u5408\uff0c\u624d\u80fd\u7ed9\u4ee5\u5047\u4e71\u771f\uff0c\u7531\u5e7b\u5165\u5b9e\uff0c\u50cf\u8fd9\u91cc\u7684\u5e7b\u50cf\uff0c\u5f92\u6709\u5f71\u50cf\uff0c\u5176\u4ed6\u65b9\u9762\u7834\u7efd\u591a\u591a\uff0c\u6f0f\u6d1e\u767e\u51fa\uff0c\u4e5f\u5c31\u80fd\u6b3a\u7792\u666e\u901a\u4eba\uff0c\u771f\u9047\u4e0a\u9ad8\u624b\uff0c\u4e0d\u89c1\u5f97\u9700\u8981\u743c\u6069\u8fd9\u6837\u7684\u5deb\u5e08\uff0c\u54ea\u6015\u662f\u4e2a\u4e0d\u901a\u9b54\u6cd5\u7684\u6b66\u8005\uff0c\u53ea\u8981\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u6709\u70b9\u80c6\u91cf\uff0c\u4fdd\u6301\u9547\u5b9a\u7ec6\u5fc3\u89c2\u5bdf\uff0c\u80af\u5b9a\u5c31\u80fd\u770b\u51fa\u540d\u5802\u3002\u8fd9\u79cd\u4e09\u6d41\u7684\u5e7b\u672f\u6c34\u51c6\uff0c\u743c\u6069\u81ea\u5ea6\u90fd\u6bd4\u4ed6\u8981\u9ad8\u660e\u90a3\u4e48\u4e00\u7b79\u534a\u7b79\uff0c\u81ea\u7136\u6beb\u4e0d\u754f\u60e7\u3002</p><p>\u201c\u4e0d\u8fc7\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7684\u9a6c\u620f\u56e2\u91cc\uff0c\u80fd\u6709\u8fd9\u79cd\u9020\u8be3\u7684\u5e7b\u672f\u5e08\uff0c\u4e5f\u5df2\u7ecf\u662f\u5f88\u4e86\u4e0d\u8d77\u4e86\u3002\u201d</p><p>\u743c\u6069\u601d\u7d22\u7740\uff0c\u4e00\u76f4\u8d70\u5230\u5927\u5385\u7684\u4e2d\u95f4\uff0c\u90a3\u91cc\u5f88\u7a81\u5140\u5730\u6446\u7740\u4e00\u5f20\u534e\u4e3d\u7684\u738b\u5ea7\uff0c\u4e0a\u9762\u5750\u7740\u4e00\u4e2a\u6ee1\u8138\u987b\u9aef\u7684\u5de8\u4eba\uff0c\u7a7f\u7740\u767d\u91d1\u6253\u9020\u7684\u707f\u70c2\u94e0\u7532\uff0c\u8170\u4f69\u91cd\u5251\uff0c\u624b\u6267\u6743\u6756\uff0c\u795e\u60c5\u51db\u51db\u5a01\u4e25\uff0c\u6b63\u6012\u89c6\u7740\u95ef\u8fdb\u6765\u7684\u8fd9\u5bf9\u7537\u5973\u5c11\u5e74\u3002</p><p>\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u4ed6\u5389\u58f0\u6012\u559d\uff0c\u201c\u80c6\u6562\u64c5\u5165\u4f1f\u5927\u7684\u56fd\u738b\u2026\u2026\u201d</p><p>\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u5f84\u76f4\u4e0a\u524d\uff0c\u4e00\u628a\u6309\u4f4f\u4ed6\u7684\u80a9\u5934\uff0c\u5c06\u8fd9\u4e2a\u201c\u56fd\u738b\u201d\u63d0\u4e86\u8d77\u6765\uff0c\u5f80\u5730\u4e0a\u4e00\u6454\uff0c\u5de8\u4eba\u540e\u9762\u7684\u534a\u53e5\u8bdd\u81ea\u7136\u4e5f\u5c31\u6ca1\u80fd\u8bf4\u51fa\u53e3\u3002\u4ed6\u4e0d\u662f\u6b66\u8005\uff0c\u4f46\u4e5f\u662f\u4ece\u5c0f\u52b3\u4f5c\uff0c\u5e76\u975e\u5a07\u751f\u60ef\u517b\uff0c\u8fd9\u4e24\u5e74\u56db\u5904\u5954\u6ce2\uff0c\u4e0b\u5c42\u754c\u4e00\u884c\u4e4b\u540e\u81ea\u89c9\u8eab\u4f53\u66f4\u5f3a\u58ee\u51e0\u5206\u3002\u672c\u6765\u4ee5\u4e3a\u662f\u81ea\u5df1\u953b\u70bc\u7684\u7f18\u6545\uff0c\u6628\u5929\u624d\u4ece\u838e\u5c14\u53e3\u4e2d\u5f97\u77e5\uff0c\u662f\u838e\u73de\u514b\u5438\u6536\u7684\u90a3\u4e9b\u90aa\u9b54\u80fd\u91cf\uff0c\u901a\u8fc7\u771f\u540d\u5951\u7ea6\u4f20\u9012\u4e86\u4e00\u90e8\u5206\u5230\u4ed6\u8eab\u4e0a\uff0c\u6240\u4ee5\u6709\u5f3a\u5316\u4f5c\u7528\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u743c\u6069\u7684\u529b\u6c14\u4e5f\u4e0d\u8fc7\u5c31\u662f\u6bd4\u5e38\u4eba\u5927\u4e9b\uff0c\u5355\u624b\u63d0\u8d77\u4e00\u4e2a\u5de8\u4eba\uff0c\u8fd9\u79cd\u4e8b\u60c5\u6839\u672c\u5c31\u6ca1\u6709\u534a\u70b9\u5408\u7406\u6027\uff0c\u4ee5\u81f3\u4e8e\u8fde\u8299\u857e\u72c4\u5728\u65c1\u8fb9\u90fd\u770b\u5f97\u5446\u4e86\uff0c\u9669\u4e9b\u4ee5\u4e3a\u81ea\u5df1\u89c1\u5230\u7684\u53c8\u662f\u5e7b\u89c9\u3002</p><p>\u4f46\u5728\u4e0b\u4e00\u79d2\u949f\uff0c\u5979\u5c31\u5b8c\u5168\u660e\u767d\u8fc7\u6765\u3002</p><p>\u201c\u5de8\u4eba\u201d\u88ab\u743c\u6069\u968f\u624b\u4e00\u6454\uff0c\u5657\u901a\u5012\u5730\uff0c\u54fc\u54fc\u5527\u5527\u722c\u4e0d\u8d77\u6765\uff0c\u8eab\u4e0a\u7684\u5e7b\u672f\u4e5f\u76f4\u63a5\u6d88\u5931\uff0c\u663e\u51fa\u672c\u6765\u9762\u76ee\uff0c\u4f53\u6001\u77ed\u5c0f\uff0c\u8299\u857e\u72c4\u521d\u770b\u8fd8\u4ee5\u4e3a\u662f\u4e2a\u5b69\u7ae5\uff0c\u518d\u770b\u82cd\u8001\u9762\u5bb9\u548c\u7070\u767d\u957f\u987b\uff0c\u624d\u77e5\u9053\u662f\u4e2a\u4f8f\u5112\u3002\u4f8f\u5112\u65cf\u5927\u591a\u5728\u5e7b\u672f\u4e0a\u6781\u6709\u5929\u8d4b\uff0c\u7a0d\u52a0\u5b66\u4e60\u8bad\u7ec3\u4fbf\u662f\u4e0d\u9519\u7684\u5e7b\u672f\u5e08\uff0c\u4f46\u4ed6\u4eec\u751f\u6027\u4e0d\u7231\u4e89\u6597\uff0c\u559c\u6b22\u73a9\u95f9\uff0c\u6240\u4ee5\u5f88\u5c11\u53c2\u4e0e\u5192\u9669\uff0c\u53cd\u800c\u5728\u9a6c\u620f\u56e2\u8fd9\u79cd\u5730\u65b9\u89c1\u5f97\u591a\u4e9b\u3002\u4ee5\u743c\u6069\u7684\u529b\u6c14\uff0c\u63d0\u8d77\u5de8\u4eba\u662f\u4e0d\u53ef\u80fd\uff0c\u5c31\u7b97\u63d0\u8d77\u4e00\u4e2a\u6210\u5e74\u4eba\u7c7b\u4e5f\u662f\u5f88\u96be\uff0c\u4f46\u8981\u63d0\u8d77\u53ea\u5982\u4eba\u7c7b\u5b69\u7ae5\u7684\u4f8f\u5112\uff0c\u5374\u8fd8\u662f\u6ca1\u95ee\u9898\u7684\u3002</p><p>\u5f88\u663e\u7136\uff0c\u5728\u5e10\u7bf7\u5185\u5236\u9020\u201c\u5e7b\u666f\u201d\uff0c\u8ba9\u6240\u6709\u4eba\u6709\u8fdb\u65e0\u51fa\u7684\u7f6a\u9b41\u7978\u9996\uff0c\u4fbf\u662f\u8fd9\u4e2a\u4f8f\u5112\u4e86\u3002</p><p>\u201c\u6df7\u7403\uff01\u4f60\u6bc1\u4e86\u6211\u7684\u738b\u56fd\u2026\u2026\u201d\u4f8f\u5112\u9ad8\u58f0\u5927\u56b7\u3002</p><p>\u968f\u7740\u65bd\u6cd5\u8005\u672c\u4eba\u7684\u88ab\u64d2\uff0c\u5e7b\u50cf\u4e5f\u968f\u4e4b\u5feb\u901f\u6d88\u5931\uff0c\u6574\u4e2a\u5e10\u7bf7\u4e2d\u53c8\u6062\u590d\u4e86\u539f\u672c\u7684\u6837\u5b50\uff0c\u8fdb\u6765\u770b\u9a6c\u620f\u7684\u5e02\u6c11\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u4eff\u4f5b\u50cf\u662f\u505a\u4e86\u4e00\u573a\u5669\u68a6\u4f3c\u7684\uff0c\u604d\u60da\u95f4\u8fd8\u4e0d\u77e5\u9053\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u3002\u743c\u6069\u4e5f\u61d2\u5f97\u591a\u505a\u89e3\u91ca\uff0c\u5c06\u4f8f\u5112\u4ea4\u7ed9\u536b\u5175\uff0c\u6b8b\u5c40\u8ba9\u4ed6\u4eec\u53bb\u6536\u62fe\u3002</p><p>\u4e8b\u60c5\u672c\u6765\u5230\u8fd9\u91cc\u4fbf\u7b97\u7ed3\u675f\uff0c\u5012\u662f\u743c\u6069\u81ea\u5df1\u4e00\u65f6\u597d\u5947\uff0c\u95ee\u8d77\u4e8b\u60c5\u7f18\u7531\u3002\u9a6c\u620f\u56e2\u662f\u65b0\u6765\u963f\u65af\u5361\u7279\u62c9\u8868\u6f14\u7684\uff0c\u7406\u5f53\u548c\u6b64\u5730\u5e02\u6c11\u6ca1\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u8fd9\u4e2a\u4f8f\u5112\u4e3a\u4f55\u8981\u505a\u8fd9\u79cd\u4e8b\u60c5\uff1f\u5bf9\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u9a6c\u620f\u56e2\u7684\u8001\u677f\u51fa\u6765\u89e3\u91ca\uff0c\u8fd9\u662f\u4e2a\u5706\u6eda\u6eda\u7684\u80d6\u5b50\u3002\u636e\u4ed6\u6240\u8a00\uff0c\u8fd9\u4e2a\u4f8f\u5112\u5e7b\u672f\u5e08\u5df2\u7ecf\u5728\u9a6c\u620f\u56e2\u91cc\u5de5\u4f5c\u4e86\u51e0\u5341\u5e74\uff0c\u4e00\u76f4\u5f88\u4f4e\u8c03\uff0c\u813e\u6c14\u6e29\u548c\uff0c\u6027\u683c\u5fcd\u8ba9\uff0c\u4ece\u6765\u6ca1\u89c1\u4ed6\u6709\u8fc7\u4efb\u4f55\u66b4\u529b\u503e\u5411\uff0c\u6216\u8005\u548c\u522b\u4eba\u53d1\u751f\u8fc7\u4efb\u4f55\u4e89\u5435\uff0c\u7b80\u800c\u8a00\u4e4b\u5c31\u662f\u4e2a\u8d85\u7ea7\u8001\u5b9e\u672c\u5206\u7684\u4eba\uff0c\u5b9e\u5728\u6ca1\u60f3\u5230\u4ed6\u4f1a\u7a81\u7136\u505a\u51fa\u8fd9\u79cd\u4e8b\u60c5\u6765\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u53d1\u75af\u4e86\u4e00\u6837\uff0c\u771f\u662f\u65e0\u53ef\u7406\u55bb\u3002</p><p>\u201c\u662f\u5417\uff1f\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u4f60\u8bf4\u4ed6\u51e0\u5341\u5e74\u6765\u4e00\u76f4\u90fd\u662f\u4e2a\u8001\u597d\u4eba\uff0c\u5c31\u662f\u4eca\u5929\u7a81\u7136\u4e00\u4e0b\u5b50\u53d8\u4e86\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u603b\u6709\u4ec0\u4e48\u4e8b\u60c5\u523a\u6fc0\u4e86\u4ed6\uff0c\u6216\u8005\u8bf4\u89e6\u52a8\u4e86\u4ed6\u5427\uff0c\u96be\u4e0d\u6210\u5750\u5728\u90a3\u91cc\u597d\u597d\u7684\uff0c\u7a81\u7136\u5c31\u8df3\u8d77\u6765\u53d1\u75af\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u201d\u8001\u677f\u60f3\u4e86\u60f3\uff0c\u201c\u786e\u5b9e\u6ca1\u53d1\u751f\u4ec0\u4e48\u4e8b\u554a\uff0c\u9876\u591a\u5c31\u662f\u4ed6\u6d17\u76d8\u5b50\u592a\u78e8\u8e6d\u4e86\uff0c\u6211\u9a82\u4e86\u4ed6\u51e0\u53e5\u2026\u2026\u201d</p><p>\u201c\u6d17\u76d8\u5b50\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u4f60\u4eec\u662f\u9a6c\u620f\u56e2\uff0c\u53c8\u4e0d\u662f\u9910\u9986\uff0c\u6d17\u4ec0\u4e48\u76d8\u5b50\u3002\u201d</p><p>\u201c\u4f46\u6211\u4eec\u4e5f\u6709\u987e\u5ba2\u561b\uff0c\u987e\u5ba2\u6765\u770b\u9a6c\u620f\uff0c\u603b\u4e5f\u8981\u5403\u70b9\u4e1c\u897f\uff0c\u8981\u70b9\u86cb\u7cd5\u4ec0\u4e48\u7684\uff0c\u6211\u4eec\u987a\u4fbf\u5356\u70b9\uff0c\u81ea\u7136\u5c31\u8981\u6709\u4eba\u6d17\u76d8\u5b50\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u4eec\u8ba9\u4ed6\u6d17\u76d8\u5b50\uff1f\u201d\u743c\u6069\u66f4\u52a0\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u201c\u4ed6\u53ef\u662f\u4e2a\u5e7b\u672f\u5e08\u554a\u3002\u201d</p><p>\u867d\u7136\u5728\u743c\u6069\u773c\u91cc\uff0c\u8fd9\u4e2a\u4f8f\u5112\u7684\u5e7b\u672f\u80fd\u529b\u5e76\u4e0d\u7b97\u975e\u5e38\u9ad8\u660e\uff0c\u4f46\u90a3\u662f\u76f8\u5bf9\u4ed6\u800c\u8a00\uff0c\u4ed6\u6bd5\u7adf\u662f\u6b63\u89c4\u9b54\u6cd5\u5b66\u6821\u6bd5\u4e1a\uff0c\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u653e\u773c\u6574\u4e2a\u5927\u9646\u4e5f\u672a\u5fc5\u6709\u5343\u4eba\u3002\u5c31\u4ed6\u539f\u672c\u731c\u60f3\uff0c\u8fd9\u4e2a\u9a6c\u620f\u56e2\u660e\u663e\u4e5f\u4e0d\u662f\u4ec0\u4e48\u8d22\u5927\u6c14\u7c97\u540e\u53f0\u6df1\u539a\u7684\u7ec4\u7ec7\uff0c\u80fd\u591f\u5236\u9020\u51fa\u201c\u5e7b\u5883\u201d\uff08\u5c3d\u7ba1\u5f88\u4e0d\u5b8c\u7f8e\uff09\u7684\u5e7b\u672f\u5e08\uff0c\u600e\u4e48\u8bf4\u4e5f\u8be5\u662f\u53f0\u67f1\u5b50\uff0c\u6ca1\u60f3\u5230\u5c45\u7136\u4f1a\u88ab\u5206\u6d3e\u53bb\u505a\u6d17\u76d8\u5b50\u8fd9\u79cd\u6742\u52a1\u2026\u2026\u8fd9\u5230\u5e95\u662f\u81ea\u5df1\u542c\u9519\u4e86\uff0c\u8fd8\u662f\u9762\u524d\u7684\u8fd9\u4e2a\u80a5\u80d6\u8001\u677f\u8111\u5b50\u574f\u4e86\uff1f</p><p>\u201c\u662f\u554a\uff0c\u4ed6\u662f\u4f1a\u70b9\u5e7b\u672f\uff0c\u53ef\u662f\u5e7b\u672f\u5e08\u5c31\u4e0d\u80fd\u6d17\u76d8\u5b50\u5417\uff1f\u201d\u8001\u677f\u4e0d\u4ee5\u4e3a\u7136\u5730\u53cd\u95ee\uff0c\u201c\u4ed6\u672c\u6765\u7684\u5de5\u4f5c\u5c31\u662f\u5e72\u6742\u52a1\uff0c\u4e0a\u573a\u8868\u6f14\u90a3\u662f\u4e34\u65f6\u5ba2\u4e32\uff0c\u4e49\u52a1\u5e2e\u5fd9\uff0c\u4e0d\u7b97\u85aa\u6c34\u7684\u2026\u2026\u201d</p><p>\u201c\u4f60\u522b\u544a\u8bc9\u6211\u8bf4\uff0c\u4f60\u8ba9\u4e00\u4e2a\u5e7b\u672f\u5e08\u6d17\u76d8\u5b50\uff0c\u540c\u65f6\u8fd8\u8981\u4ed6\u4e0a\u573a\u6f14\u51fa\uff0c\u800c\u4e14\u53ea\u7ed9\u4ed6\u53d1\u6d17\u76d8\u5b50\u7684\u5de5\u8d44\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u8fd9\u51e0\u5341\u5e74\u6765\u4e00\u76f4\u90fd\u8fd9\u6837\uff1f\u201d</p><p>\u8001\u677f\u70b9\u5934\u3002</p><p>\u201c\u4ed6\u5c31\u6ca1\u4ec0\u4e48\u6028\u8a00\uff1f\u4ed6\u5c31\u4e0d\u8981\u6c42\u957f\u5de5\u8d44\uff1f\u4ed6\u5c31\u4e0d\u8981\u6c42\u63d0\u9ad8\u5f85\u9047\uff1f\u4e0d\u8981\u6c42\u4e09\u9669\u4e00\u91d1\u5b89\u6392\u4f4f\u623f\uff1f\u54e6\uff0c\u6700\u540e\u8fd9\u4e2a\u7b97\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u6709\u554a\uff0c\u6211\u8bf4\u4e86\uff0c\u4ed6\u813e\u6c14\u597d\u561b\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u53d1\u706b\u4e86\uff0c\u867d\u7136\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u8981\u53d1\u706b\uff0c\u201c\u813e\u6c14\u597d\u5c31\u5e94\u8be5\u88ab\u6b3a\u8d1f\u5417\uff1f\u201d</p><p>\u201c\u53ef\u662f\u5927\u5bb6\u90fd\u6b3a\u8d1f\u4ed6\u554a\uff0c\u90fd\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u743c\u6069\u51b7\u7b11\u3002\u201c\u662f\u554a\uff0c\u90fd\u4e60\u60ef\u4e86\uff0c\u90a3\u73b0\u5728\u5462\uff0c\u4e0d\u4e60\u60ef\u4e86\u5427\uff1f\u201d</p><p>\u8001\u677f\u6709\u4e9b\u5c34\u5c2c\uff0c\u743c\u6069\u61d2\u5f97\u518d\u591a\u8bf4\uff0c\u63fd\u7740\u8299\u857e\u72c4\u8f6c\u8eab\u79bb\u5f00\u3002\u201c\u743c\u6069\uff0c\u201d\u8d70\u51fa\u4e00\u6bb5\u8def\uff0c\u5c11\u5973\u602f\u602f\u5730\u95ee\uff0c\u201c\u90a3\u4e2a\u4f8f\u5112\uff0c\u4ed6\u5230\u5e95\u4e3a\u4ec0\u4e48\u53d1\u75af\u554a\uff1f\u201d</p><p>\u201c\u4ed6\u4e0d\u662f\u53d1\u75af\uff0c\u53ea\u662f\u7206\u53d1\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4eba\uff0c\u4e0d\u7ba1\u662f\u4ec0\u4e48\u51fa\u8eab\uff0c\u4ec0\u4e48\u5730\u4f4d\uff0c\u4ec0\u4e48\u5883\u9047\uff0c\u603b\u662f\u8fd8\u5e0c\u671b\u4e00\u4e2a\u2018\u516c\u5e73\u2019\u7684\u3002\u867d\u7136\u8fd9\u4e2a\u516c\u5e73\u592a\u865a\u5e7b\uff0c\u592a\u6a21\u7cca\uff0c\u6ca1\u6cd5\u786e\u5b9a\uff0c\u4f46\u5b83\u786e\u5b9e\u5b58\u5728\u3002\u4e00\u4e2a\u4eba\u727a\u7272\u5f97\u592a\u591a\uff0c\u5fcd\u8ba9\u5f97\u592a\u591a\uff0c\u4ed8\u51fa\u5f97\u592a\u591a\uff0c\u5982\u679c\u6ca1\u6709\u5f97\u5230\u76f8\u5e94\u56de\u62a5\u7684\u8bdd\uff0c\u4ed6\u7ec8\u7a76\u662f\u4f1a\u5fc3\u91cc\u4e0d\u5e73\u8861\u7684\u3002\u5c31\u7b97\u662f\u813e\u6c14\u518d\u597d\uff0c\u6db5\u517b\u518d\u597d\uff0c\u518d\u9006\u6765\u987a\u53d7\uff0c\u90a3\u4e5f\u53ea\u80fd\u662f\u6682\u65f6\u538b\u6291\uff0c\u53cd\u800c\u66f4\u52a0\u6076\u5316\u3002\u800c\u4e14\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u5f80\u5f80\u5c31\u662f\u8fd9\u6837\uff0c\u4f60\u8d8a\u9000\u8ba9\uff0c\u8d8a\u5bb9\u5fcd\uff0c\u522b\u4eba\u53cd\u800c\u8d8a\u903c\u8fd1\uff0c\u751a\u81f3\u4e60\u4ee5\u4e3a\u5e38\uff0c\u8ba4\u4e3a\u4e00\u5207\u90fd\u662f\u7406\u6240\u5f53\u7136\uff0c\u7136\u540e\u4f60\u5f97\u5230\u7684\u5c31\u8d8a\u5c11\uff0c\u4ed8\u51fa\u5f97\u5c31\u8d8a\u591a\uff0c\u5fc3\u91cc\u5c31\u66f4\u4e0d\u5e73\u8861\u3002\u603b\u6709\u4e00\u5929\uff0c\u53ef\u80fd\u53ea\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u89e6\u52a8\uff0c\u4e00\u70b9\u5bfc\u706b\u7d22\uff0c\u5c31\u4f1a\u5168\u90e8\u7206\u53d1\u51fa\u6765\u2014\u2014\u5c31\u50cf\u4ed6\u8fd9\u2026\u2026\u201d</p><p>\u4ed6\u7a81\u7136\u6014\u4f4f\u4e86\uff0c\u505c\u4f4f\u811a\u6b65\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u8299\u857e\u72c4\u5947\u602a\u3002</p><p>\u743c\u6069\u8f6c\u8138\u671d\u5979\u770b\u8fc7\u6765\uff0c\u4ed6\u4e00\u76f4\u770b\u7740\uff0c\u6ca1\u8bf4\u8bdd\uff0c\u5c0f\u5973\u5b69\u5fc3\u91cc\u4e0d\u7531\u5f97\u4e00\u9635\u9635\u53d1\u9ebb\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u743c\u6069\uff0c\u201d\u5979\u80c6\u602f\uff0c\u201c\u4f60\u522b\u5413\u6211\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u743c\u6069\u6447\u5934\u4e00\u7b11\uff0c\u8f7b\u8f7b\u6258\u7740\u5979\u7684\u8138\uff0c\u201c\u8299\u857e\u72c4\uff0c\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u5e72\u561b\u7a81\u7136\u8bf4\u5bf9\u4e0d\u8d77\u554a\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u743c\u6069\u4f38\u81c2\u62b1\u4f4f\u5979\uff0c\u5c11\u5973\u4e56\u4e56\u5730\u504e\u4f9d\u5728\u6000\u91cc\uff0c\u67d4\u67d4\u7684\u53d1\u4e1d\u5728\u591c\u98ce\u4e2d\u98d8\u62c2\uff0c\u6709\u51e0\u7f15\u8d34\u5728\u8138\u4e0a\uff0c\u663e\u5f97\u8d8a\u53d1\u6e29\u67d4\u3002</p><p>\u201c\u522b\u62c5\u5fc3\uff0c\u743c\u6069\uff0c\u201d\u5979\u4ee5\u4f4e\u5fae\u5f97\u51e0\u4e4e\u542c\u4e0d\u89c1\u7684\u58f0\u97f3\u8bf4\uff0c\u201c\u6211\u4e0d\u4f1a\u7684\u3002\u201d</p><p>\u53ea\u4e0d\u8fc7\uff0c\u672a\u6765\u5982\u4f55\uff0c\u771f\u7684\u662f\u53ef\u4ee5\u81ea\u5df1\u51b3\u5b9a\u7684\u4e48\uff1f</p></article>"}}}]);
