(window.webpackJsonp=window.webpackJsonp||[]).push([[402],{425:function(p){p.exports={chapterTitle:null,title:"\u7b2c402\u7ae0 \u660e\u5e74\u7684\u5fe7\u8651",content:"<article><h2>\u7b2c402\u7ae0 \u660e\u5e74\u7684\u5fe7\u8651</h2><p>\u3000\u3000\u5bbe\u9986\u4e0e\u9910\u996e\u662f\u4e0d\u5206\u5bb6\u7684\uff0c\u63a8\u95e8\u770b\u5230\u7167\u58c1\u4e0a\u7684\u9898\u5b57\uff0c\u7fdf\u4e39\u9752\u5c31\u80fd\u611f\u89c9\u5230\u8fd9\u5bb6\u79c1\u623f\u83dc\u9986\u7684\u96c5\u81f4\uff0c\u5012\u672a\u66fe\u5728\u65b0\u829c\u770b\u5230\u6709\u4e00\u5bb6\u9910\u9986\u80fd\u4e0e\u8fd9\u91cc\u76f8\u6bd4\u3002\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u91cc\u7684\u9910\u9986\u4e3b\u8981\u662f\u9762\u5411\u5927\u4f17\u5e02\u6c11\u7684\u8bdd\uff0c\u8fd9\u91cc\u5927\u6982\u53ea\u5411\u67d0\u4e00\u9636\u5c42\u7684\u5f00\u653e\uff0c\u5b85\u5b50\u5916\u90fd\u6ca1\u6709\u94ed\u724c\u3002</p><p>\u3000\u3000\u4e2d\u5348\u7684\u65f6\u5019\uff0c\u9648\u5983\u84c9\u63a5\u5230\u5510\u5a67\u7528\u5f20\u606a\u624b\u673a\u6253\u6765\u7684\u7535\u8bdd\uff0c\u592a\u9633\u5c06\u8fd1\u843d\u5c71\u5979\u5c31\u5728\u5e97\u91cc\u7b49\u7fdf\u4e39\u9752\u3001\u536b\u5170\u5979\u4eec\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u662f\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u3001\u5434\u5929\u5b9d\u3001\u5085\u4fca\u4ed6\u4eec\u4e00\u7fa4\u4eba\u90fd\u8ddf\u7740\u8fc7\u6765\uff0c\u7b49\u4f1a\u513f\u8c22\u665a\u6674\u8fd8\u8981\u4ece\u76ca\u9686\u9547\u8d76\u8fc7\u6765\uff0c\u8fd8\u4ee5\u4e3a\u9876\u591a\u6709\u5f20\u606a\u3001\u5510\u5a67\u966a\u7740\u5462\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u9648\u5983\u84c9\u6ca1\u60f3\u5230\u6709\u8fd9\u4e48\u591a\u4eba\uff0c\u9f3b\u7ffc\u90fd\u53d1\u6101\u7684\u76b1\u8d77\u6765\uff0c\u8ba9\u5979\u7a0d\u663e\u51b7\u6de1\u7684\u6e05\u4e3d\u591a\u4e86\u8bb8\u591a\u5929\u771f\u7684\u5473\u9053\uff0c\u201c\u6211\u6ca1\u6709\u7559\u5927\u684c\uff0c\u5c0f\u684c\u4e5f\u53ea\u7559\u4e86\u4e00\u684c\uff0c\u90fd\u9884\u8ba2\u51fa\u53bb\u4e86\uff0c\u600e\u4e48\u529e\u5440\uff1f\u201d</p><p>\u3000\u3000\u201c\u5927\u4e0d\u4e86\u5927\u5bb6\u90fd\u7aef\u7740\u996d\u7897\u5728\u5927\u5385\u7684\u8fc7\u9053\u91cc\u5403\u996d\uff0c\u201d\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u96be\u5f97\u770b\u89c1\u9648\u5983\u84c9\u4f1a\u9732\u51fa\u5c0f\u5973\u5b69\u7eaf\u771f\u7684\u795e\u60c5\uff0c\u53c8\u95ee\u5979\uff0c\u201c\u4f60\u4eec\u5927\u4e00\u65b0\u751f\u51e0\u65f6\u5f00\u5b66\uff0c\u4f60\u4eba\u600e\u4e48\u8fd8\u7559\u5728\u6d77\u5dde\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8981\u7b49\u51e0\u5929\uff0c\u8981\u516d\u53f7\u5462\uff0c\u201d\u9648\u5983\u84c9\u95ee\u7b54\u8bf4\uff0c\u7709\u5934\u8fd8\u76b1\u7740\uff0c\u771f\u662f\u53d1\u6101\u4e86\uff0c\u770b\u7740\u5979\u7238\u4ece\u91cc\u9762\u8fce\u51fa\u6765\uff0c\u4e00\u8138\u62b1\u6b49\u7684\u8bf4\uff0c\u201c\u6211\u53ea\u5f53\u6ca1\u6709\u51e0\u4e2a\u4eba\uff0c\u8ba9\u840d\u59d0\u53ea\u7559\u4e86\u4e00\u5f20\u5c0f\u684c\uff0c\u6709\u6ca1\u6709\u53d6\u6d88\u8ba2\u684c\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8bb8\u8001\u5e08\u8fd9\u51e0\u5929\u5728\u6d77\u5dde\u5427\uff1f\u8ba9\u8bb8\u8001\u5e08\u5728\u524d\u9762\u7ed9\u6211\u4eec\u632a\u4e00\u4e2a\u623f\u95f4\u51fa\u6765\uff0c\u542c\u7740\u843d\u5b50\u58f0\u559d\u9152\uff0c\u611f\u89c9\u4e0d\u6bd4\u8fd9\u91cc\u5dee\u3002\u201d</p><p>\u3000\u3000\u8fd9\u91cc\u4e0e\u524d\u9762\u7684\u68cb\u9986\u6709\u540e\u95e8\u76f8\u901a\uff0c\u9648\u5947\u5fc3\u77e5\u5e73\u65f6\u8981\u501f\u8bb8\u9e3f\u4f2f\u7684\u5730\u65b9\uff0c\u94c1\u5b9a\u8981\u7ed9\u8bb8\u9e3f\u4f2f\u9a82\u4ed6\u5fc3\u601d\u90fd\u94bb\u94b1\u773c\u91cc\u53bb\u7adf\u60f3\u5c06\u9152\u5e2d\u5f00\u5230\u4ed6\u7684\u5730\u76d8\u4e0a\uff01\u5f20\u606a\u51fa\u9762\u81ea\u7136\u4e0d\u6210\u95ee\u9898\uff0c\u5927\u4e0d\u4e86\u518d\u8ba9\u8bb8\u9e3f\u4f2f\u6df7\u5403\u4e00\u987f\uff0c\u8ba9\u9648\u5983\u84c9\u8d76\u7d27\u53bb\u627e\u8bb8\u9e3f\u4f2f\u8bf4\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u201c\u5927\u5bb6\u4e00\u8d77\u8fc7\u53bb\u5427\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u63a8\u95e8\u8d70\u8fdb\u68cb\u9986\uff0c\u68cb\u9986\u56db\u5c42\u7684\u6728\u7ed3\u6784\u65b9\u697c\uff0c\u6295\u5165\u5de8\u8d44\u4fee\u7f2e\u6574\u56fa\u4e4b\u540e\uff0c\u6b64\u65f6\u5df2\u7ecf\u5b8c\u5168\u6062\u590d\u6e05\u4e2d\u53f6\u65f6\u7684\u65e7\u89c2\uff0c\u4e00\u4e8c\u5c42\u4e3a\u68cb\u5ba4\uff0c\u4e09\u56db\u5c42\u4e3a\u5c55\u89c8\u5ba4\u4e0e\u56f4\u68cb\u6559\u5ba4\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u6b63\u7ad9\u5728\u4e00\u5f20\u68cb\u684c\u65c1\u770b\u522b\u4eba\u4e0b\u68cb\uff0c\u624b\u91cc\u6367\u7740\u4e00\u53ea\u5357\u74dc\u9020\u578b\u7684\u7d2b\u7802\u5c0f\u8336\u58f6\uff0c\u4e00\u8fb9\u770b\u68cb\uff0c\u4e00\u8fb9\u4e3e\u8d77\u8336\u58f6\u5c0f\u996e\u4e00\u53e3\u3002\u4eca\u5e74\u6625\u590f\uff0c\u4e3a\u8425\u6797\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e0e\u5468\u6e38\u3001\u5b8b\u5fd7\u4f51\u8ddf\u5730\u65b9\u653f\u5e9c\u5b98\u5458\u6253\u5f88\u591a\u4ea4\u9053\uff0c\u5c06\u624d\u8425\u6797\u7684\u4e8b\u60c5\u6709\u5e8f\u7684\u63a8\u8fdb\u4e0b\u53bb\uff0c\u524d\u540e\u5fd9\u788c\u4e86\u5c0f\u534a\u5e74\uff0c\u8fd9\u624d\u5f97\u7a7a\u56de\u6d77\u5dde\u4f11\u606f\u4e00\u6bb5\u65f6\u95f4\uff0c\u6bd4\u4ee5\u5f80\u66f4\u89c1\u6e05\u7626\uff0c\u7a7f\u7740\u9ed1\u7ef8\u706f\u7b3c\u88e4\u3001\u5bf9\u895f\u5510\u88c5\uff0c\u4e24\u9b13\u77ed\u53d1\u7070\u767d\uff0c\u4e00\u53cc\u773c\u775b\u5374\u662f\u5f02\u5e38\u6e05\u4eae\uff0c\u65c1\u4eba\u89c1\u4e86\u53ea\u5f53\u4ed6\u662f\u4fee\u8eab\u5f97\u6cd5\u7684\u8001\u8005\uff0c\u81f3\u5c11\u8bb8\u9e3f\u4f2f\u7684\u5e74\u7eaa\u5e76\u4e0d\u5927\uff0c\u4eca\u5e74\u624d\u4e94\u5341\u516d\uff0c\u53ea\u662f\u4ed6\u7684\u7070\u767d\u5934\u53d1\u7531\u6765\u5df2\u4e45\uff0c\u8fd8\u8981\u63a8\u6eaf\u5230\u4ed6\u7ed9\u524d\u5e02\u59d4\u4e66\u8bb0\u4e07\u5411\u524d\u5f53\u79d8\u4e66\u7684\u65f6\u671f\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u770b\u7740\u5f20\u606a\u4e0e\u90b5\u81f3\u521a\u4ed6\u4eec\u4ece\u540e\u5ead\u9662\u63a8\u95e8\u8fdb\u6765\uff0c\u7b11\u7740\u62db\u547c\u4ed6\uff1a\u201c\u4f60\u6ca1\u4e8b\u5230\u8fd9\u91cc\u6765\u95f2\u901b\uff0c\u6765\u627e\u6211\u4e0b\u68cb\uff1f\u201d</p><p>\u3000\u3000\u201c\u6765\u8bf7\u4f60\u559d\u9152\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u6b63\u597d\uff0c\u6211\u6709\u4e8b\u627e\u4f60\u8bf4\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u56de\u6765\u597d\u4e9b\u5929\uff0c\u90fd\u6ca1\u6709\u4e3b\u52a8\u8054\u7cfb\u81ea\u5df1\uff0c\u5f20\u606a\u53ea\u5f53\u65e0\u5173\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u4e0d\u65b9\u4fbf\u4e0e\u5468\u6e38\u3001\u5b8b\u5fd7\u4f51\u4ed6\u4eec\u63d0\u8d77\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u5929\u8fd8\u6ca1\u6709\u5b8c\u5168\u6697\u4e0b\u6765\uff0c\u5230\u68cb\u9986\u6765\u4e0b\u68cb\u7684\u4eba\u8fd8\u4e0d\u591a\uff0c\u632a\u51fa\u4e00\u95f4\u68cb\u5ba4\u7a0d\u4f5c\u5e03\u7f6e\uff0c\u672a\u7b49\u51b7\u9910\u76d8\u6446\u597d\uff0c\u8c22\u665a\u6674\u8ba9\u5979\u7684\u79c1\u4eba\u52a9\u7406\u5f20\u5ead\u5f00\u8f66\u9001\u5979\u5230\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u8fdb\u6765\u65f6\uff0c\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u3001\u5085\u4fca\uff0c\u751a\u81f3\u5f20\u77e5\u975e\u90fd\u6b20\u8d77\u8eab\u5b50\u8fce\u5979\uff0c\u8bb8\u9e3f\u4f2f\u70b9\u5934\u793a\u610f\uff0c\u5f20\u606a\u4f38\u624b\u63a5\u8fc7\u5979\u7684\u624b\u888b\uff0c\u5e2e\u5979\u62c9\u5f00\u4ed6\u4e0e\u7fdf\u4e39\u9752\u4e4b\u95f4\u7684\u6905\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u6674\u59d0\uff0c\u7279\u610f\u7ed9\u4f60\u7559\u7684\u5ea7\u4f4d\u2026\u2026\u8fd9\u5c31\u662f\u4ece\u65b0\u829c\u6765\u7684\u7fdf\u59d0\uff0c\u8001\u90b5\u4ed6\u4eec\u5f88\u6b23\u8d4f\u7684\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u624b\u6276\u7740\u6905\u80cc\uff0c\u5fae\u503e\u7740\u8eab\u5b50\u770b\u7740\u7fdf\u4e39\u9752\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u65e9\u542c\u8fc7\u4f60\u4e86\uff0c\u5f20\u606a\u8fd8\u662f\u7b49\u8138\u4e0a\u4f24\u75a4\u6d88\u4e86\u624d\u6562\u56de\u6d77\u5dde\uff0c\u6240\u4ee5\u6211\u575a\u6301\u665a\u4e0a\u8981\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u4f60\u4eec\u6ca1\u6709\u7b49\u5f88\u957f\u65f6\u95f4\u5427\uff1f\u201d\u624b\u8f7b\u642d\u5230\u5f20\u606a\u7684\u80a9\u8180\u4e0a\u5750\u4e0b\u6765\uff0c\u201c\u542c\u8bf4\u5f20\u606a\u5728\u65b0\u829c\u7ed9\u4f60\u60f9\u4e86\u5f88\u591a\u9ebb\u70e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\uff0c\u662f\u6211\u4eec\u7ed9\u606a\u5c11\u6dfb\u4e86\u5f88\u591a\u9ebb\u70e6\u624d\u662f\u3002\u201d\u7fdf\u4e39\u9752\u7ad9\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0b\u5df4\u6401\u5728\u5f20\u606a\u53e6\u4e00\u4fa7\u7684\u80a9\u8180\u4e0a\u95ee\u665a\u6674\uff1a\u201c\u665a\u6674\u59d0\uff0c\u82b7\u5f64\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff0c\u6211\u90fd\u6709\u4e9b\u60f3\u5979\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c31\u8fd9\u51e0\u5929\uff0c\u5e7c\u513f\u56ed\u90fd\u5f00\u5b66\u4e86\uff0c\u5979\u4e5f\u4e0d\u80fd\u603b\u8d56\u5728\u5979\u5976\u5976\u5bb6\uff0c\u770b\u8c01\u6709\u7a7a\u53bb\u7701\u57ce\u7684\u65f6\u95f4\uff0c\u5c06\u5979\u63a5\u8fc7\u6765\u3002\u201d\u8c22\u665a\u6674\u4f38\u624b\u5c06\u5510\u5a67\u989d\u524d\u7684\u4e00\u7f15\u6709\u4e9b\u51cc\u4e71\u7684\u5218\u6d77\u6311\u4e86\u4e00\u4e0b\uff0c\u7406\u987a\u3002\u624d\u56de\u8fc7\u5934\u6765\u5bf9\u7fdf\u4e39\u9752\u8bf4\uff1a\u201c\u4f60\u4eec\u6709\u6ca1\u6709\u5b89\u6392\u597d\u4f4f\u7684\u5730\u65b9\uff0c\u6211\u90a3\u91cc\u53ef\u4ee5\u4f4f\uff0c\u665a\u4e0a\u53ef\u4ee5\u4f4f\u6211\u90a3\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u8fd9\u4e48\u6253\u7b97\u7684\uff0c\u5c0f\u53d4\u7684\u623f\u5b50\u4e5f\u7a7a\u7740\uff0c\u6211\u770b\u5510\u5a67\u4eca\u5929\u665a\u4e0a\u591a\u534a\u4e5f\u4e0d\u4f1a\u56de\u5bb6\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u96be\u5f97\u6709\u4e9b\u673a\u4f1a\u4e0d\u7528\u665a\u4e0a\u6309\u65f6\u7761\u89c9\u7684\u3002\u201d\u5510\u5a67\u5f97\u610f\u7684\u8bf4\uff0c\u53c8\u62c9\u7740\u65c1\u8fb9\u7684\u9648\u5983\u84c9\u529d\u9053\uff0c\u201c\u665a\u4e0a\u4e00\u8d77\u8fc7\u53bb\u73a9\u5427\uff0c\u4eba\u591a\u624d\u70ed\u95f9\u3002\u201d</p><p>\u3000\u3000\u5510\u5a67\u6240\u8bf4\u7684\u4e0d\u6309\u65f6\u7761\u89c9\uff0c\u662f\u6307\u4e0d\u7528\u5728\u5979\u5988\u6307\u5b9a\u7684\u65f6\u95f4\u91cc\u7761\u89c9\uff0c\u4ee5\u5979\u8eab\u4f53\u91cc\u7684\u751f\u7269\u949f\u6765\u8bf4\uff0c\u80fd\u6491\u8fc7\u591c\u91cc\u5341\u4e00\u70b9\u4e0d\u6253\u778c\u7761\uff0c\u90a3\u5c31\u662f\u5947\u8ff9\u4e86\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5bf9\u8c22\u665a\u6674\u6240\u77e5\u751a\u5fae\uff0c\u4e4b\u524d\u6ca1\u6709\u8c01\u8ddf\u5979\u63d0\u773c\u524d\u8fd9\u4e2a\u655b\u5bb9\u8273\u7f8e\u7684\u5973\u4eba\u662f\u8c01\uff0c\u770b\u7740\u5979\u8fdb\u6765\u65f6\u90b5\u81f3\u521a\u4ed6\u4eec\u7684\u53cd\u5e94\uff0c\u7fdf\u4e39\u9752\u80fd\u660e\u767d\u773c\u524d\u8fd9\u4e2a\u5973\u4eba\u7684\u5730\u4f4d\u5f88\u9ad8\uff0c\u4ece\u7ec6\u5fae\u5904\uff0c\u80fd\u770b\u51fa\u4e5f\u8ddf\u5f20\u606a\u3001\u5510\u5a67\u4ed6\u4eec\u7684\u5173\u7cfb\u5f88\u4eb2\u5bc6\u3002\u5728\u6d77\u5dde\u7b26\u5408\u8fd9\u4e2a\u8eab\u4efd\u7684\u5973\u4eba\uff0c\u7fdf\u4e39\u9752\u5012\u662f\u9053\u542c\u9014\u7684\u542c\u8bf4\u8fc7\u4e00\u4e9b\u6d88\u606f\uff0c\u5979\u5c31\u662f\u5f90\u5b66\u5e73\u4e27\u8eab\u8f66\u7978\u7684\u72ec\u5b50\u7684\u59bb\u5b50\uff1f</p><p>\u3000\u3000\u7537\u4eba\u9047\u5230\u5973\u4eba\uff0c\u4f1a\u5f88\u5728\u610f\u5973\u4eba\u7684\u5bb9\u8c8c\uff0c\u6f02\u4eae\u7684\u5973\u4eba\u9047\u5230\u6f02\u4eae\u7684\u5973\u4eba\uff0c\u4f1a\u66f4\u5728\u610f\u5bf9\u65b9\u7684\u5bb9\u8c8c\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6bd4\u8c22\u665a\u6674\u66f4\u80fd\u7ed9\u4eba\u60ca\u8273\u7684\u611f\u89c9\uff0c\u5728\u7fdf\u4e39\u9752\u4ee5\u5f80\u7684\u4eba\u751f\u91cc\uff0c\u4e60\u60ef\u4ee5\u5979\u81ea\u8eab\u6240\u72ec\u6709\u7684\u8ff7\u4eba\u9b45\u529b\u5468\u8f6c\u4e8e\u7537\u4eba\u7684\u4e16\u754c\u4e4b\u5185\uff0c\u7709\u773c\u95f4\u4f1a\u60c5\u4e0d\u81ea\u7981\u7684\u6d41\u9732\u51fa\u4e07\u822c\u7684\u98ce\u60c5\u4e0e\u59a9\u5a9a\uff0c\u800c\u8c22\u665a\u6674\u7684\u51fa\u8eab\u53ca\u957f\u671f\u4ee5\u6765\u7684\u751f\u6d3b\uff0c\u6ce8\u5b9a\u5979\u7684\u7f8e\u662f\u6536\u655b\u542b\u84c4\u7684\uff0c\u6e29\u5a49\u7684\uff0c\u6b64\u65f6\u7684\u5979\uff0c\u6216\u8bb8\u53ea\u6709\u4e0e\u5f20\u606a\u72ec\u5904\uff0c\u624d\u4f1a\u5c06\u5979\u8eab\u4f53\u91cc\u6210\u719f\u7684\u51b6\u8273\u6beb\u65e0\u4fdd\u7559\u7684\u91ca\u653e\u51fa\u6765\uff0c\u624d\u4f1a\u6709\u65e0\u7aef\u59a9\u5a9a\u7684\u98ce\u60c5\u3002</p><p>\u3000\u3000\u665a\u996d\u65f6\uff0c\u8bb8\u9e3f\u4f2f\u4e4b\u524d\u8981\u8bf4\u7684\u4e8b\u60c5\u6ca1\u6709\u5728\u684c\u4e0a\u63d0\u53ca\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u5148\u9001\u665a\u6674\u5979\u4eec\u56de\u53bb\uff0c\u4ed6\u7559\u4e0b\u6765\u966a\u8bb8\u9e3f\u4f2f\u4e0b\u4e00\u5c40\u68cb\uff0c\u8ba9\u5c0f\u53d4\u5f20\u77e5\u975e\u7559\u4e0b\u6765\u7b49\u4f1a\u513f\u5f00\u8f66\u9001\u4ed6\u56de\u53bb\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\uff0c\u56f4\u68cb\u5728\u56fd\u5185\u5f88\u53d7\u6b22\u8fce\uff0c\u53bb\u5e74\u9a6c\u6653\u6625\u8fde\u593a\u4e1c\u6d0b\u8bc1\u5238\u676f\u548c\u5bcc\u58eb\u901a\u6000\uff0c\u56fd\u5185\u56f4\u68cb\u8fce\u6765\u4e2d\u64c2\u53f0\u5bd2\u540e\u7684\u53c8\u4e00\u4e2a\u9ad8\u5cf0\uff0c\u8d76\u4e0a\u4eca\u5929\u53c8\u662f\u5468\u672b\uff0c\u8fc7\u6765\u4e0b\u68cb\u7684\u4eba\u5f88\u591a\uff0c\u4e09\u697c\u7684\u56f4\u68cb\u6559\u5ba4\u91cc\u7684\u68cb\u684c\u90fd\u5df2\u7ecf\u6ca1\u7a7a\u4e0b\u7684\uff0c\u5403\u5b8c\u996d\uff0c\u5360\u7528\u68cb\u5ba4\u8981\u8d76\u7d27\u6e05\u51fa\u6765\u7ed9\u68cb\u53cb\u5bf9\u5f08\u3002</p><p>\u3000\u3000\u5927\u6982\u8c01\u4e5f\u4e0d\u66fe\u610f\u8bc6\u5230\uff0c\u4ece\u4eca\u5e74\uff08\u5373\u4e5d\u516d\u5e74\uff09\u8d77\uff0c\u97e9\u56fd\u674e\u660c\u9550\u5f00\u59cb\u4e86\u4ed6\u4e2a\u4eba\u7684\u4f20\u5947\u65f6\u4ee3\uff0c\u641e\u5f97\u56fd\u5185\u6c11\u4f17\u5bf9\u56f4\u68cb\u7684\u70ed\u60c5\u5927\u6d88\uff0c\u4ee5\u81f4\u5f20\u606a\u5230\u5927\u5b66\u540e\u7ec4\u7ec7\u56f4\u68cb\u793e\u5149\u666f\u4e5f\u4e0d\u662f\u90a3\u4e48\u597d\u770b\uff0c\u5012\u662f\u5927\u4e8c\u5f00\u59cb\u624d\u80fd\u9a97\u4e9b\u65b0\u751f\u7684\u5165\u793e\u8d39\u5403\u5403\u559d\u559d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u9e3f\u4f2f\u8fd8\u6709\u5c0f\u53d4\u5219\u5230\u56db\u697c\u7684\u529e\u516c\u5ba4\u91cc\u51d1\u548c\u7740\u4e0b\u68cb\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u62dc\u6258\u6765\u7ba1\u7406\u68cb\u9986\u7684\u4e24\u4eba\u90fd\u662f\u9752\u7f8a\u9053\u89c2\u7684\u5c45\u58eb\uff0c\u4e0e\u8bb8\u9e3f\u4f2f\u662f\u591a\u5e74\u7684\u4ea4\u60c5\uff0c\u559c\u6b22\u4e0b\u68cb\uff0c\u5e73\u65e5\u4e5f\u90fd\u4f4f\u5728\u9053\u89c2\u91cc\u3002</p><p>\u3000\u3000\u5c0f\u53d4\u5f20\u77e5\u975e\u5e2e\u7740\u62ff\u6765\u68cb\uff0c\u4ed6\u4eec\u5e2e\u7740\u6dfb\u8336\u5012\u6c34\u6536\u62fe\u505c\u5f53\u4e4b\u540e\uff0c\u624d\u51fa\u53bb\u89c2\u68cb\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u62ff\u8d77\u68cb\u5b50\uff0c\u5012\u5148\u8bf4\u8d77\u4e8b\u60c5\u6765\uff1a\u201c\u65b0\u829c\u7684\u4e8b\u60c5\uff0c\u6211\u542c\u745e\u5e73\u8be6\u7ec6\u8bf4\u8fc7\uff0c\u65b0\u829c\u7684\u6848\u5b50\u8fd9\u4e48\u5927\uff0c\u90a3\u4e2a\u7f57\u5f52\u6e90\u53ea\u662f\u5728\u5730\u65b9\u4e0a\u6709\u4e9b\u52bf\u529b\uff0c\u6ca1\u4eba\u4f1a\u5e2e\u4ed6\u4eec\u5c06\u8fd9\u6848\u5b50\u6309\u4e0b\u53bb\uff1b\u63a5\u4e0b\u6765\uff0c\u5e94\u8be5\u662f\u6574\u8083\u5168\u7701\u6c34\u5229\u7cfb\u7edf\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\u2026\u2026\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u201c\u6709\u4ec0\u4e48\u4e0d\u59a5\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ad9\u5728\u5f90\u5b66\u5e73\u5bf9\u7acb\u9762\u7684\u90a3\u4e9b\u4eba\uff0c\u6216\u8bb8\u66f4\u5e0c\u671b\u5f90\u5b66\u5e73\u8fd9\u6b21\u7684\u624b\u8155\u66f4\u5f3a\u786c\u4e00\u4e9b\u3002\u6211\u524d\u4e9b\u5929\u5728\u4e0a\u6e44\u542c\u5230\u4e00\u4e9b\u4e8b\uff0c\u5c0f\u6c5f\u6709\u4e9b\u6bb5\u7684\u5824\u9632\u5de5\u7a0b\uff0c\u80cc\u666f\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6709\u542c\u8bf4\u8fc7\uff0c\u201d\u6c34\u5229\u90e8\u4e0b\u8bbe\u5c0f\u6c5f\u6d41\u57df\u6c34\u5229\u6cbb\u7406\u529e\u516c\u5ba4\uff0c\u5c0f\u6c5f\u6d41\u57df\u7684\u6cbb\u7406\u4e0e\u6c5f\u9632\u5efa\u8bbe\u53d7\u7701\u91cc\u4e0e\u8fd9\u4e2a\u529e\u516c\u5ba4\u7684\u53cc\u91cd\u9886\u5bfc\uff0c\u4e5f\u7ed9\u4e86\u90a3\u4e9b\u559c\u6b22\u94bb\u7a7a\u5b50\u8d70\u95e8\u9053\u7684\u4eba\u66f4\u591a\u7684\u673a\u4f1a\uff0c\u8981\u6ca1\u6709\u65b0\u829c\u7684\u6848\u5b50\u505a\u7a81\u7834\u53e3\uff0c\u60f3\u4e0b\u624b\u67e5\u5c0f\u6c5f\u5824\u9632\uff0c\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u201c\u6708\u524d\u5317\u4eac\u7684\u9648\u5e0c\u540c\u6848\u4e5f\u5224\u4e86\uff0c\u65b0\u829c\u4e5f\u7684\u786e\u6345\u51fa\u8fd9\u4e48\u5927\u7684\u5a04\u5b50\uff0c\u8981\u5f7b\u67e5\uff0c\u4e5f\u53ea\u6709\u773c\u4e0b\u7684\u65f6\u673a\u6700\u9002\u5408\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u62c5\u5fc3\u6700\u8fd1\u51e0\u5e74\u5185\u5c31\u4f1a\u51fa\u5927\u5a04\u5b50\uff1f\u201d\u8bb8\u9e3f\u4f2f\u84e6\u7136\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\u6574\u8083\u8fdf\u65e9\u4f1a\u51fa\u5a04\u5b50\u4e86\uff0c\u65b0\u829c\u7684\u6c5f\u5824\uff0c\u4e0d\u8981\u8bf4\u4e8c\u5341\u5e74\u4e00\u9047\u7684\u6d2a\u6c34\uff0c\u6c34\u4f4d\u6da8\u8d77\u6765\uff0c\u5c31\u5f88\u5371\u9669\u3002\u201d\u5f20\u606a\u53ea\u80fd\u8fd9\u4e48\u8bf4\uff0c\u8bb8\u9e3f\u4f2f\u773c\u775b\u679c\u7136\u5f88\u6bd2\uff0c\u90fd\u8ba4\u8bc6\u5feb\u4e24\u5e74\u4e86\uff0c\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u4e00\u822c\u60c5\u51b5\u4e0b\u4e0d\u4f1a\u8fd9\u4e48\u6025\u7740\u4fc3\u4f7f\u5f90\u5b66\u5e73\u53bb\u505a\u4e00\u4ef6\u4f1a\u7ad6\u654c\u65e0\u6570\u7684\u4e8b\u60c5\uff0c\u4e5d\u516b\u5e74\uff0c\u5168\u56fd\u7684\u4e8b\u60c5\uff0c\u81ea\u5df1\u7ba1\u4e0d\u4e86\uff0c\u603b\u4e0d\u80fd\u773c\u7741\u7741\u7684\u770b\u7740\u4e1c\u6d77\u7701\u4e5f\u6c34\u6f2b\u904d\u91ce\u5427\u3002</p><p>\u3000\u3000\u201c\u6709\u65f6\u5019\uff0c\u4e5f\u662f\u51fa\u4e86\u5a04\u5b50\uff0c\u624d\u4f1a\u6709\u6539\u8fdb\u7684\uff0c\u7ecf\u6d4e\u6539\u9769\u5bb9\u6613\uff0c\u9093\u516c\u5927\u5200\u9614\u65a7\uff0c\u6c14\u9b44\u5f88\u5927\uff0c\u653f\u6cbb\u6539\u9769\u5f80\u5f80\u8270\u96be\uff0c\u60df\u6050\u5c0f\u5fc3\u7ffc\u7ffc\u8fd8\u4e0d\u591f\uff0c\u6211\u770b\u662f\u8981\u51fa\u4e00\u6b21\u5a04\u5b50\u624d\u53ef\u80fd\u5f80\u524d\u63a8\u4e00\u6b65\uff0c\u4e3a\u4ec0\u4e48\uff0c\u7ecf\u6d4e\u6539\u9769\uff0c\u7edd\u5927\u90e8\u5206\u4eba\u662f\u5f97\u5230\u5229\u76ca\u7684\uff0c\u601d\u60f3\u8f6c\u53d8\u4e5f\u5bb9\u6613\uff1b\u653f\u6cbb\u6539\u9769\uff0c\u5374\u8981\u9769\u6389\u4e00\u90e8\u5206\u4eba\u7684\u5229\u76ca\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8f7b\u8f7b\u4e00\u53f9\uff0c\u90fd\u90e8\u5c5e\u597d\u4e86\uff0c\u7701\u91cc\u7684\u52a8\u4f5c\u5c31\u505c\u4e0d\u4e0b\u6765\uff0c\u53c8\u95ee\u9053\uff0c\u201c\u7701\u7eaa\u59d4\u4f1a\u914d\u5408\u7701\u653f\u5e9c\u884c\u52a8\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u9676\u664b\u8fd9\u4e2a\u4eba\uff0c\u8fd8\u662f\u5f88\u8bb2\u7a76\u7b56\u7565\u7684\uff0c\u7684\u786e\u8981\u6c42\u7701\u7eaa\u59d4\u4e0b\u4e00\u9636\u6bb5\u91cd\u70b9\u4fa6\u67e5\u7701\u6c34\u5229\u7cfb\u7edf\u7684\u6848\u4ef6\uff0c\u8fd8\u8981\u628a\u65b0\u829c\u7684\u6848\u5b50\u529e\u6210\u5178\u578b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u987a\u52bf\u800c\u4e3a\uff0c\u5f97\u7f6a\u4eba\u7684\u4e8b\u60c5\u4e0d\u6311\u5934\u505a\uff0c\u8be5\u4e0b\u624b\u7684\u4e8b\u4e5f\u4e0d\u624b\u8f6f\uff0c\u9676\u664b\u7684\u786e\u5f88\u4f1a\u8bb2\u7a76\u7b56\u7565\uff0c\u5f90\u5b66\u5e73\u8981\u5b66\u4ed6\uff0c\u672a\u5fc5\u4e0d\u80fd\u66f4\u4e0a\u4e00\u5c42\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8f7b\u8f7b\u4e00\u53f9\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e8b\u8981\u529e\u6210\u4e0d\u96be\uff0c\u4f46\u662f\u529e\u6210\u4e8b\uff0c\u5f90\u5b66\u5e73\u8bf4\u4e0d\u5b9a\u4f1a\u62c5\u4e0a\u521a\u610e\u81ea\u7528\u7684\u6076\u540d\uff0c\u624b\u8155\u5f3a\u786c\u7684\u4eba\u603b\u8131\u4e0d\u4e86\u8fd9\u6837\u7684\u975e\u8bae\u3002\u56fd\u52a1\u9662\u91cc\uff0c\u524d\u4e24\u5e74\u7ecf\u6d4e\u5f62\u52bf\u4e0d\u5927\u597d\uff0c\u901a\u80c0\u98ce\u9669\u5927\uff0c\u6709\u4eba\u5c31\u957f\u671f\u6258\u75c5\u4f11\u517b\uff0c\u4e0d\u5927\u7406\u56fd\u4e8b\uff0c\u73b0\u5728\u7ecf\u6d4e\u4e0a\u7684\u901a\u80c0\u98ce\u9669\u63a7\u5236\u4e0b\u6765\uff0c\u6709\u4eba\u4e5f\u5c31\u8ddf\u7740\u517b\u597d\u4e86\u75c5\uff0c\u8fd9\u4e2a\u4eba\u5012\u662f\u4e0d\u559c\u6b22\u521a\u610e\u81ea\u7528\u7684\u5b98\u5458\uff0c\u4f1a\u89c9\u5f97\u8fd9\u6837\u7684\u5b98\u5458\u4e0d\u591f\u6210\u719f\uff0c\u6bcf\u6b21\u91cd\u5927\u4f1a\u8bae\u4e4b\u524d\uff0c\u7701\u90e8\u7ea7\u90fd\u4f1a\u6709\u4e9b\u8c03\u6574\uff0c\u660e\u5e74\u4e5d\u6708\u5c31\u662f\u5341\u4e94\u5927\uff0c\u5f90\u5b66\u5e73\u5c31\u672a\u5fc5\u6491\u5230\u4e5d\u6708\u554a\u2026\u2026\u201d\u8bb8\u9e3f\u4f2f\u6ca1\u6709\u5c06\u8bdd\u8bf4\u5b8c\uff0c\u4f46\u662f\u4ed6\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u5f20\u606a\u662f\u660e\u767d\u7684\u3002</p><p>\u3000\u3000\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u5927\u4e0d\u4e86\u4ee5\u540e\u4e0d\u518d\u62a2\u98ce\u5934\u4e86\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u60f3\u597d\u597d\u4f53\u9a8c\u4e00\u4e0b\u5927\u5b66\u7684\u751f\u6d3b\uff0c\u4e0d\u7528\u6574\u5929\u8fd9\u4e8b\u90a3\u4e8b\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u80fd\u8fd9\u4e48\u8003\u8651\u5c31\u597d\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8f7b\u58f0\u8bf4\uff0c\u201c\u603b\u4e4b\uff0c\u9526\u6e56\u8fd9\u6b21\u662f\u7ed9\u7701\u91cc\u505a\u51fa\u8d21\u732e\u7684\uff0c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u4ed6\u53c8\u4e0d\u662f\u7741\u773c\u778e\uff0c\u6211\u770b\u5728\u7701\u91cc\u5012\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5927\u95ee\u9898\uff0c\u53cd\u800c\u6211\u62c5\u5fc3\u5728\u6d77\u5dde\uff0c\u4e00\u65e6\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u505a\u5c0f\u52a8\u4f5c\u7684\u4eba\u5c31\u4f1a\u51fa\u8d77\u6765\u3002\u91d1\u56fd\u6d77\u5230\u6d77\u5dde\u6765\u4efb\u526f\u4e66\u8bb0\uff0c\u5230\u5e74\u5e95\u5c31\u6ee1\u4e24\u5e74\u4e86\uff0c\u4ed6\u56de\u7701\u91cc\uff0c\u5e02\u91cc\u5c31\u5c11\u4e86\u4e00\u4e2a\u80fd\u8bf4\u8bdd\u7684\u4eba\uff0c\u6211\u770b\u5728\u660e\u5e74\u7701\u90e8\u7ea7\u5e72\u90e8\u8c03\u52a8\u4e4b\u524d\uff0c\u5b8b\u57f9\u660e\u8981\u80fd\u8fdb\u5e02\u5e38\u59d4\u91cc\u53bb\uff0c\u624d\u66f4\u7a33\u59a5\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u518d\u50bb\uff0c\u4e5f\u80fd\u770b\u51fa\u675c\u5c0f\u5c71\u3001\u5b8b\u57f9\u660e\u662f\u652f\u6301\u5510\u5b66\u8c26\u7684\uff0c\u675c\u5c0f\u5c71\u5df2\u7ecf\u8fdb\u5e38\u59d4\u4e86\uff0c\u4ed6\u5f53\u7136\u8981\u538b\u7740\u5b8b\u57f9\u660e\u5192\u5934\u3002\u867d\u7136\u5e02\u5e38\u59d4\u662f\u7701\u7ba1\u5e72\u90e8\uff0c\u4f46\u662f\u4ece\u5e02\u91cc\u68af\u961f\u5e72\u90e8\u9009\u62e8\uff0c\u5e02\u91cc\u7684\u610f\u89c1\u5c31\u5f88\u91cd\u8981\u7684\uff0c\u4e00\u4e2a\u5e02\u91cc\u610f\u89c1\u5c31\u5206\u6b67\u4e25\u91cd\u7684\u5e72\u90e8\uff0c\u7701\u91cc\u4f1a\u600e\u4e48\u8003\u8651\uff1f</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u53ea\u662f\u7701\u957f\uff0c\u5efa\u8bae\u662f\u53ef\u4ee5\u7684\uff0c\u4f46\u662f\u4eba\u4e8b\u6743\u7ec8\u5f52\u8fd8\u63e1\u5728\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u624b\u91cc\u3002</p><p>\u3000\u3000\u8bb8\u591a\u4e8b\u60c5\uff0c\u8bb8\u9e3f\u4f2f\u53ea\u662f\u70b9\u5230\u4e3a\u6b62\uff0c\u8be5\u600e\u4e48\u51b3\u5b9a\uff0c\u8fd8\u5f97\u5f20\u606a\u8ddf\u5510\u5b66\u8c26\u4ed6\u4eec\u5546\u91cf\u7740\u62ff\u4e3b\u610f\uff0c\u5728\u68cb\u9986\u804a\u4e86\u5f88\u665a\uff0c\u7b49\u697c\u4e0b\u4e0b\u68cb\u7684\u4eba\u9646\u7eed\u8d70\u7a7a\uff0c\u4ed6\u4eec\u624d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u5bb6\u5728\u6c99\u7530\u7684\u5df7\u5b50\u6df1\u5904\uff0c\u8f66\u4e0d\u65b9\u4fbf\u5f00\u8fdb\u53bb\uff0c\u8bb8\u9e3f\u4f2f\u4e0d\u8ba9\u5f20\u77e5\u975e\u5f00\u8f66\u9001\uff0c\u4ed6\u4e58\u7740\u6e05\u98ce\u660e\u6708\u94bb\u5165\u4eff\u4f5b\u6d78\u5728\u6c34\u91cc\u7684\u9752\u77f3\u5df7\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u5341\u4e00\u70b9\uff0c\u9664\u4e86\u8def\u706f\uff0c\u8857\u8fb9\u7684\u5efa\u7b51\u5df2\u5f88\u5c11\u770b\u5230\u706f\u706b\u3002</p><p>\u3000\u3000\u201c\u8bb8\u9e3f\u4f2f\u662f\u8bf4\u5f90\u7701\u957f\u660e\u5e74\u4e0d\u7ed9\u95f2\u7f6e\uff0c\u4e5f\u4f1a\u7ed9\u8c03\u51fa\u4e1c\u6d77\u2026\u2026\u201d\u5f20\u77e5\u975e\u4e00\u8fb9\u5f00\u8f66\u4e00\u8fb9\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u53ef\u80fd\u6027\u5f88\u5927\uff0c\u201d\u5f20\u606a\u6487\u5634\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4eba\u60f3\u6b63\u76f4\u5f88\u96be\uff0c\u6ee1\u4e16\u754c\u7684\u4eba\u90fd\u5e0c\u671b\u4f60\u8ddf\u7740\u540c\u6d41\u5408\u6c61\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u5341\u5206\u62c5\u5fe7\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff1a\u201c\u5509\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u5c31\u6015\u522b\u4eba\u786c\u8981\u62ff\u7740\u51fa\u6765\u8bf4\u9879\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u6d77\u88d5\u516c\u53f8\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u53ea\u8981\u4e00\u4e2a\u2018\u5f71\u54cd\u4e0d\u597d\u2019\u7684\u501f\u53e3\uff0c\u6700\u4e0a\u5934\u7684\u90a3\u51e0\u5927\u4f6c\uff0c\u53c8\u6709\u8c01\u77e5\u9053\u5f71\u54cd\u662f\u4e0d\u662f\u771f\u7684\u4e0d\u597d\uff1f\u6700\u7ec8\u80fd\u4e0d\u80fd\u7ad9\u4f4f\u811a\u6839\uff0c\u4e5f\u53ea\u80fd\u662f\u770b\u6d3e\u7cfb\u3002\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u7528\u592a\u62c5\u5fc3\u4e86\uff0c\u53c8\u4e0d\u4f1a\u7ed9\u6253\u56de\u539f\u5f62\uff0c\u9876\u591a\u6536\u655b\u4e00\u4e9b\u8fc7\u65e5\u5b50\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5f90\u5b66\u5e73\u4eca\u5e74\u521d\u5728\u6797\u4e1a\u7cfb\u7edf\u8fd9\u4e48\u641e\u4e00\u4e0b\uff0c\u7701\u6797\u4e1a\u5385\u5385\u957f\u5218\u534e\u5c71\u5b89\u7136\u65e0\u4e8b\u8c03\u5230\u6c5f\u5357\u7701\uff0c\u5927\u6982\u5df2\u7ecf\u662f\u79cd\u8b66\u544a\u4e86\u5427\uff0c\u518d\u4e0b\u72e0\u5fc3\u6574\u8083\u7701\u6c34\u5229\u7cfb\u7edf\uff0c\u4f1a\u89e6\u52a8\u66f4\u591a\u4eba\u7684\u5229\u76ca\uff0c\u5f90\u5b66\u5e73\u660e\u5e74\u7ed9\u95f2\u7f6e\u7684\u53ef\u80fd\u6027\u76f8\u5f53\u5927\u3002\u4f46\u662f\u5f90\u5b66\u5e73\u94c1\u8155\u4e4b\u4e0b\u505a\u597d\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u5f20\u606a\u5185\u5fc3\u6df1\u5904\u4e0d\u5e0c\u671b\u770b\u5230\u4e5d\u516b\u5e74\u6d2a\u707e\u4f1a\u771f\u7684\u53d1\u751f\uff0c\u5374\u662f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u90a3\u662f\u5f90\u5b66\u5e73\u590d\u51fa\u7684\u60df\u4e00\u673a\u4f1a\u3002\u5bf9\u4e8e\u9ad8\u5c42\u4efb\u547d\u7684\u4e8b\u60c5\uff0c\u81ea\u5df1\u662f\u5b8c\u5168\u7684\u65e0\u80fd\u4e3a\u529b\uff0c\u53ea\u6709\u7b49\u5230\u4e8b\u60c5\u53d1\u751f\u3002\u4e0d\u8fc7\u5728\u90a3\u4e2a\u4e4b\u5916\uff0c\u8fd8\u662f\u6709\u5f88\u591a\u4e8b\u60c5\u53ef\u4ee5\u505a\u7684\uff0c\u8bb8\u9e3f\u4f2f\u8bf4\u7684\u6ca1\u4e8b\uff0c\u6d77\u5dde\u7684\u5c40\u52bf\u5e94\u8be5\u66f4\u7a33\u59a5\u4e00\u4e9b\u597d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5750\u8f66\u5230\u65b0\u9526\u82d1\u4e8c\u9053\u95e8\u5916\uff0c\u5f20\u606a\u4fbf\u8ba9\u5c0f\u53d4\u5f00\u8f66\u56de\u53bb\u3002</p><p>\u3000\u3000\u770b\u89c1\u665a\u6674\u697c\u4e0a\u53ea\u4eae\u7740\u4e00\u76cf\u5c0f\u58c1\u706f\uff0c\u4ece\u7eb1\u7a97\u5e18\u91cc\u900f\u51fa\u5149\u6765\uff0c\u6ca1\u770b\u89c1\u8d70\u52a8\uff0c\u5fc3\u60f3\uff0c\u5979\u4eec\u51e0\u4e2a\u5973\u7684\u5dee\u4e0d\u591a\u90fd\u7761\u4e86\u5427\uff0c\u65f6\u95f4\u90fd\u8fd9\u4e48\u665a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u95e8\u8fdb\u4e86\u5c4b\uff0c\u60f3\u5fc5\u665a\u6674\u5b89\u6392\u5979\u4eec\u90fd\u7761\u5979\u90a3\u697c\u91cc\uff0c\u6ca1\u611f\u5230\u8fd9\u91cc\u6709\u4eba\u4f4f\u8fdb\u6765\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5f90\u5b66\u5e73\u662f\u81ea\u5df1\u6700\u5927\u7684\u653f\u6cbb\u4e0a\u7684\u4f9d\u8d56\uff0c\u4eca\u665a\u4e0e\u8bb8\u9e3f\u4f2f\u8fdb\u884c\u8fd9\u6837\u7684\u8c08\u8bdd\uff0c\u5fc3\u60c5\u603b\u5f88\u96be\u5e73\u9759\u4e0b\u6765\u3002\u9910\u53f0\u4e0a\u8fd8\u6709\u65e9\u4e0a\u5269\u4e0b\u7684\u51b7\u5496\u5561\uff0c\u5f20\u606a\u5012\u4e86\u4e00\u676f\uff0c\u6253\u5f00\u540e\u95e8\uff0c\u60f3\u5230\u6e56\u8fb9\u5750\u4e00\u5750\uff0c\u770b\u89c1\u4e00\u4e2a\u7a88\u7a95\u7684\u8eab\u5f71\u60f3\u9000\u56de\u897f\u8fb9\u5c0f\u697c\u7684\u540e\u5ead\u9662\u91cc\u53bb\u3002</p><p>\u3000\u3000\u201c\u5594\uff0c\u8fd8\u6ca1\u6709\u7761\uff1f\u201d\u501f\u7740\u5929\u7a7a\u9ad8\u60ac\u7684\u90a3\u8f6e\u660e\u6708\uff0c\u5f20\u606a\u770b\u7740\u9648\u5983\u84c9\u660e\u4e3d\u6e05\u79c0\u7684\u8138\u4e0a\u6709\u7740\u8fdb\u9000\u4e24\u96be\u7684\u72b9\u8c6b\uff0c\u5927\u6982\u662f\u770b\u5230\u5c4b\u5b50\u91cc\u4eae\u4e86\u706f\uff0c\u624d\u8981\u8d70\u7684\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5c31\u5510\u5a67\u5148\u7761\u4e86\u3002\u201d\u9648\u5983\u84c9\u7ad9\u5728\u4e24\u5ea7\u56ed\u5b50\u7684\u94c1\u95e8\u91cc\uff0c\u4e5f\u4e0d\u9000\u56de\u53bb\uff0c\u4e5f\u4e0d\u8d70\u8fc7\u6765\uff0c\u201c\u665a\u6674\u59d0\u8ddf\u7fdf\u59d0\u5728\u90a3\u8fb9\u804a\u5929\u5462\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u624b\u6276\u7740\u94c1\u827a\u95e8\uff0c\u624b\u81c2\u5fae\u62ac\uff0c\u4f7f\u5f97\u5979\u67d4\u8f6f\u7684\u7ea4\u8170\u770b\u4e0a\u53ea\u76c8\u76c8\u4e00\u63e1\uff1b\u7ecf\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5f20\u606a\u80fd\u542c\u89c1\u6811\u84e0\u540e\u7684\u8bf4\u8bdd\u58f0\u3002</p><p>\u3000\u3000\u5206\u9694\u4e24\u680b\u697c\u540e\u5ead\u56ed\u7684\u8fd9\u6247\u94c1\u827a\u95e8\u4ece\u6765\u5c31\u6ca1\u6709\u843d\u8fc7\u9501\uff0c\u5f20\u606a\u7aef\u7740\u5496\u5561\u8d70\u8fc7\u53bb\uff0c\u7b49\u5f20\u606a\u8d70\u5230\u8ddf\u524d\uff0c\u9648\u5983\u84c9\u624d\u60ca\u8df3\u4e86\u4e00\u4e0b\u4f3c\u7684\u9000\u56de\u53bb\u3002</p><p>\u3000\u3000\u8d70\u8fc7\u95e8\uff0c\u5f20\u606a\u5c31\u770b\u89c1\u665a\u6674\u4e0e\u7fdf\u4e39\u9752\u5750\u5728\u6811\u7bf1\u80cc\u540e\u7684\u6728\u5236\u957f\u6905\u4e0a\uff0c\u536b\u5170\u4e0d\u5728\u540e\u9762\uff0c\u770b\u5230\u697c\u5385\u91cc\u6709\u7ea4\u5029\u7684\u4eba\u5f71\u6643\u52a8\uff0c\u9648\u5983\u84c9\u5927\u6982\u8ddf\u8fd9\u4e24\u4e2a\u719f\u5973\u6ca1\u6709\u592a\u591a\u7684\u5171\u540c\u8bed\u6c14\uff0c\u4e5f\u53ef\u80fd\u662f\u5f20\u606a\u8fc7\u6765\u7684\u7f18\u6545\uff0c\u9000\u5230\u8fd9\u91cc\uff0c\u4fbf\u8bf4\u4e86\u58f0\u201c\u56f0\u4e86\uff0c\u6211\u5148\u53bb\u7761\u89c9\u201d\uff0c\u4fbf\u8f6c\u8eab\u8fdb\u4e86\u5c4b\u3002</p><p>\u3000\u3000\u665a\u6674\u6709\u4e9b\u754f\u521d\u79cb\u6df1\u591c\u7684\u6e05\u5bd2\uff0c\u8f7b\u8584\u7684\u7761\u88d9\u5916\u8fd8\u7a7f\u7740\u957f\u8896\u886c\u886b\u3002\u7fdf\u4e39\u9752\u597d\u50cf\u7a7f\u7740\u662f\u665a\u6674\u7684\u8f7b\u8584\u4e1d\u8d28\u7761\u88d9\uff0c\u5979\u624b\u63a9\u7740\u80f8\u53e3\uff0c\u4ece\u4fa7\u9762\u770b\u4e0d\u51fa\u91cc\u9762\u7cfb\u7740\u4ec0\u4e48\uff0c\u5f20\u606a\u5c06\u5496\u5561\u676f\u5f80\u5979\u9762\u524d\u4e00\u9012\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u559d\u5496\u5561\u3002\u201d</p><p>\u3000\u3000\u201c\u559d\u4e86\u7761\u4e0d\u7740\u2026\u2026\u201d\u7fdf\u4e39\u9752\u4f38\u624b\u63a8\u51fa\u6765\uff0c\u62d2\u7edd\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u77a5\u773c\u4e00\u770b\uff0c\u5927\u534a\u53ea\u767d\u4e73\u9732\u5728\u5916\u9762\uff0c\u633a\u7acb\u7684\u4e73\u5c16\u5c06\u4e1d\u8d28\u77ed\u88d9\u9876\u8d77\uff0c\u6d77\u62e8\u60ca\u4eba\u3002</p><p>\u3000\u3000\u201c\u4e0d\u559d\u554a\uff0c\u90a3\u6211\u81ea\u5df1\u559d\u3002\u201d\u5f20\u606a\u60a0\u7136\u81ea\u5f97\u7684\u5728\u665a\u6674\u8eab\u8fb9\u5750\u4e0b\u6765\uff0c\u5374\u8ba9\u665a\u6674\u8f7b\u8f7b\u8e22\u4e86\u4e00\u4e0b\uff0c\u770b\u6765\u90a3\u4e00\u77a5\u6ca1\u6709\u9003\u8fc7\u5979\u7684\u773c\u775b\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u521a\u624d\u4f38\u624b\u4e00\u6321\u53ea\u662f\u63aa\u624b\u4e0d\u53ca\uff0c\u4f38\u51fa\u624b\u5c31\u610f\u8bc6\u5230\u5f20\u606a\u5728\u800d\u6ed1\u5934\uff0c\u5fd9\u53c8\u7f29\u56de\u624b\u63a9\u7740\u80f8\u53e3\u3002\u7fdf\u4e39\u9752\u4e60\u60ef\u665a\u4e0a\u4e0d\u7a7f\u5185\u8863\uff0c\u89c1\u665a\u6674\u7761\u88d9\u91cc\u4e5f\u6ca1\u6709\u6234\u675f\u7f1a\u90a3\u4e24\u5806\u5ae9\u8089\u56e2\u7684\u4e1c\u897f\uff0c\u53ea\u5f53\u5f20\u606a\u665a\u4e0a\u4e0d\u4f1a\u5230\u8fd9\u8fb9\uff0c\u4e00\u5c4b\u5b50\u5973\u4eba\uff0c\u6d17\u8fc7\u6fa1\u5979\u4e5f\u5c31\u6ca1\u6709\u7a7f\u5185\u8863\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u8fd9\u65f6\u5019\u8d70\u8fc7\u6765\uff0c\u53c8\u4e0d\u4fbf\u9a6c\u4e0a\u56de\u5c4b\u53bb\u7a7f\u5185\u8863\uff0c\u53ea\u80fd\u62ff\u624b\u63a9\u7740\u80f8\u53e3\u3002</p><p>\u3000\u3000\u60f3\u60f3\u4e4b\u524d\uff0c\u5468\u8f6c\u4e8e\u7537\u4eba\u4e16\u754c\u65f6\uff0c\u603b\u8981\u7ed9\u4e00\u4e9b\u7537\u4eba\u5360\u4e9b\u5c0f\u4fbf\u5b9c\uff0c\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u4f1a\u635f\u5931\u4ec0\u4e48\uff0c\u5012\u662f\u5728\u5f20\u606a\u9762\u524d\u60a3\u5f97\u60a3\u5931\u8d77\u6765\uff0c\u7fdf\u4e39\u9752\u8138\u6709\u4e9b\u53d1\u70eb\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5728\u804a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u8eab\u5b50\u4fef\u4e0b\u6765\uff0c\u624b\u8098\u538b\u5728\u819d\u76d6\u4e0a\u6162\u6761\u4e1d\u7406\u7684\u559d\u7740\u5496\u5561\u3002</p><p>\u3000\u3000\u201c\u80fd\u804a\u4ec0\u4e48\uff0c\u81ea\u7136\u662f\u804a\u6211\u4eec\u505a\u5973\u4eba\u7684\u82e6\u5904\uff0c\u4f60\u53c8\u4e0d\u4f1a\u5173\u5fc3\u3002\u201d\u665a\u6674\u6a2a\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u505a\u5973\u4eba\u6709\u82e6\u5904\uff0c\u542c\u4e86\u5c31\u77e5\u9053\u505a\u7537\u4eba\u6709\u54ea\u4e9b\u76f8\u5bf9\u7684\u597d\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u4f1a\u8bf4\u8fd9\u4e9b\u6ca1\u5fc3\u6ca1\u80ba\u7684\u8bdd\uff0c\u8fd9\u8bdd\u771f\u8be5\u8ba9\u4f60\u5988\u542c\u542c\uff0c\u8ba9\u4f60\u5988\u4fee\u7406\u4f60\u3002\u201d\u665a\u6674\u7b11\u7740\u5c06\u5f20\u606a\u624b\u91cc\u7684\u676f\u5b50\u91cc\u62a2\u8fc7\u6765\uff0c\u4fa7\u5934\u5bf9\u7fdf\u4e39\u9752\u8bf4\uff0c\u201c\u6211\u5012\u662f\u90a3\u79cd\u559d\u4e86\u5496\u5561\u4f1a\u72af\u56f0\u7684\u4eba\uff0c\u771f\u662f\u5947\u602a\uff0c\u5496\u5561\u56e0\u5bf9\u6211\u53ea\u8d77\u53cd\u4f5c\u7528\u2026\u2026\u201d\u5c0f\u53e3\u7684\u996e\u7740\u3002</p><p>\u3000\u3000\u665a\u6674\u4e0e\u5f20\u606a\u7684\u4eb2\u5bc6\uff0c\u5728\u5176\u4ed6\u4eba\u773c\u91cc\u90fd\u89c6\u4e3a\u6b63\u5e38\uff0c\u6bd5\u7adf\u8fd9\u4e24\u5e74\u6765\uff0c\u4e24\u4eba\u7684\u5173\u7cfb\u4e00\u76f4\u90fd\u5f88\u4eb2\u5bc6\uff0c\u524d\u540e\u6ca1\u6709\u592a\u5927\u7684\u53d8\u5316\u3002</p><p>\u3000\u3000\u5d14\u4e39\u9752\u5728\u5145\u6ee1\u7537\u6743\u6b32\u671b\u7684\u4e16\u754c\u6d41\u79bb\u4e86\u591a\u5e74\uff0c\u5df2\u7ecf\u6478\u900f\u666e\u901a\u7537\u4eba\u60c5\u6b32\u6a2a\u6d41\u7684\u7279\u8d28\uff0c\u4e5f\u5df2\u7ecf\u4e60\u60ef\u5c06\u6240\u6709\u7684\u7537\u4eba\u90fd\u770b\u6210\u662f\u90a3\u79cd\u52a8\u7269\uff0c\u6240\u4ee5\u4e00\u5f00\u59cb\u624d\u5bf9\u5f20\u606a\u6709\u8f83\u6df1\u7684\u6210\u89c1\u52a0\u4ee5\u6392\u65a5\u3002\u5373\u4f7f\u540e\u6765\u53d1\u751f\u90a3\u4e48\u591a\u4e8b\uff0c\u626d\u8f6c\u5bf9\u5f20\u606a\u7684\u6210\u89c1\uff0c\u56fa\u6709\u601d\u7ef4\u4f9d\u7136\u5f71\u54cd\u5979\u5bf9\u5f20\u606a\u7684\u770b\u6cd5\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4fa7\u5934\u770b\u4e86\u770b\u8c22\u665a\u6674\u3002\u8c22\u665a\u6674\u867d\u7136\u7f69\u7740\u4ef6\u957f\u8896\u886c\u886b\uff0c\u4f46\u662f\u6263\u5b50\u6ca1\u6263\uff0c\u524d\u895f\u655e\u7740\uff0c\u4e1d\u8d28\u7761\u88d9\u91cc\u8ddf\u81ea\u5df1\u4e00\u4e0b\uff0c\u90fd\u6ca1\u6709\u7a7f\u4ec0\u4e48\uff0c\u5f20\u606a\u521a\u624d\u8fd8\u501f\u673a\u5403\u81ea\u5df1\u8c46\u8150\uff0c\u5f80\u8c22\u665a\u6674\u7684\u9886\u53e3\u77a5\u4e24\u773c\u4e5f\u5f88\u6b63\u5e38\uff0c\u8c22\u665a\u6674\u4f3c\u4e4e\u4e00\u70b9\u90fd\u4e0d\u62c5\u5fc3\u8fd9\u4e2a\uff1f\u7fdf\u4e39\u9752\u4ece\u4eca\u665a\u7684\u63a5\u89e6\u5c31\u80fd\u770b\u51fa\u8c22\u665a\u6674\u662f\u4e00\u4e2a\u751f\u6d3b\u5f88\u96c5\u81f4\u7684\u4e00\u4e2a\u5973\u4eba\uff0c\u5979\u5c31\u4e00\u70b9\u90fd\u4e0d\u4ecb\u610f\u4e0e\u5f20\u606a\u5171\u7528\u4e00\u53ea\u676f\u5b50\u559d\u5496\u5561\uff0c\u901a\u5e38\u5973\u4eba\u5f88\u4ecb\u610f\u8ddf\u522b\u4eba\u5171\u7528\u4e00\u53ea\u6000\u5b50\uff0c\u90a3\u4e5f\u8be5\u4eb2\u5bc6\u5230\u76f8\u5f53\u7684\u7a0b\u5ea6\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u54ea\u91cc\u77e5\u9053\u7fdf\u4e39\u9752\u8111\u5b50\u8f6c\u51fa\u8fd9\u4e48\u591a\u9053\u9053\u6765\uff0c\u770b\u7740\u665a\u6674\u5c06\u4ed6\u7684\u5496\u5561\u559d\u5b8c\uff0c\u63a5\u8fc7\u7a7a\u676f\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u56de\u53bb\u4e86\uff0c\u4f60\u4eec\u4e5f\u53ef\u4ee5\u56de\u5c4b\u53bb\u7761\u89c9\u4e86\uff0c\u5bf9\u5973\u4eba\u6765\u8bf4\uff0c\u7761\u89c9\u662f\u6700\u91cd\u8981\u4e0d\u8fc7\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u3000\u3000\u201c\u62c9\u5012\u5427\uff0c\u88c5\u4f5c\u591a\u5c11\u61c2\u5973\u4eba\u7684\u6837\u5b50\uff0c\u4f60\u5148\u56de\u53bb\u7761\u5427\u3002\u201d\u665a\u6674\u7b11\u7740\u8f7b\u62cd\u5f20\u606a\u7684\u80cc\uff0c\u8ba9\u4ed6\u5148\u56de\u53bb\u7761\u89c9\u3002</p><p>\u3000\u3000\u6e29\u67d4\u7684\u770b\u7740\u5f20\u606a\u8f6c\u8eab\u8d70\u56de\u81ea\u5bb6\u7684\u540e\u56ed\uff0c\u665a\u6674\u624d\u56de\u5934\u7b11\u7740\u5bf9\u7fdf\u4e39\u9752\u8bf4\uff1a\u201c\u5f20\u606a\u8fd9\u5bb6\u4f19\uff0c\u773c\u775b\u603b\u4e0d\u4f1a\u5b89\u5206\u7684\uff0c\u4e0d\u8fc7\u4e5f\u602a\u4f60\u90a3\u91cc\u592a\u8bf1\u4eba\u4e86\uff0c\u4f5c\u4e3a\u5973\u4eba\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u60f3\u770b\u4e00\u770b\uff1a\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u53c8\u5927\u53c8\u633a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd9\u4e2a\u592a\u7d2f\u8d58\uff0c\u7ad9\u4e45\u4e86\u90fd\u4f1a\u8170\u80cc\u75bc\u2026\u2026\u201d\u7fdf\u4e39\u9752\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u8d70\u4e86\uff0c\u4e5f\u4e0d\u7528\u62ff\u624b\u63a9\u7740\u80f8\u53e3\uff0c\u624b\u64a4\u5230\u8170\u95f4\uff0c\u634f\u4e86\u4e24\u4e0b\u8170\u808c\uff0c\u597d\u50cf\u8170\u80cc\u8fd9\u65f6\u5019\u5c31\u6709\u4e9b\u9178\u75bc\u4f3c\u7684\uff0c\u201c\u683c\u5c11\u5728\u8c22\u603b\u9762\u524d\u5c31\u5f88\u4e56\u5de7\uff0c\u773c\u775b\u5012\u4e0d\u4f1a\u778e\u770b\uff1b\u5bb3\u6211\u624b\u4e3e\u5230\u73b0\u5728\u4e5f\u9178\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5594\uff0c\u201d\u665a\u6674\u8138\u4e0a\u6709\u4e9b\u5fae\u70eb\uff0c\u53ea\u662f\u6708\u5149\u4e0b\u4e0d\u660e\u663e\uff0c\u5fc3\u60f3\u5728\u7fdf\u4e39\u9752\u7684\u773c\u91cc\u5f20\u606a\u603b\u4e0d\u80fd\u7b97\u4e56\u5de7\u7684\u7537\u5b69\u5b50\uff0c\u5979\u8fd8\u504f\u504f\u8fd9\u4e48\u8bf4\uff0c\u770b\u6765\u521a\u624d\u4e0e\u5f20\u606a\u6709\u4e9b\u592a\u968f\u610f\u4e86\uff0c\u63a9\u9970\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u5fd8\u4e86\u544a\u8bc9\u4f60\uff0c\u6211\u4eec\u8fd9\u4e24\u5bb6\u662f\u968f\u610f\u8d70\u52a8\u7684\uff0c\u5e73\u65e5\u90a3\u8fb9\u697c\u7684\u536b\u751f\uff0c\u4e5f\u662f\u6211\u8fd9\u91cc\u7684\u5de5\u4eba\u7167\u5e94\u7740\uff0c\u65e9\u4e0a\u8fd8\u8981\u5e2e\u5f20\u606a\u505a\u65e9\u9910\u5462\uff0c\u4ed6\u7238\u5988\u4e0d\u5728\u6d77\u5dde\uff0c\u8981\u8ba9\u4ed6\u81ea\u5df1\u4e00\u4e2a\u4eba\u8fc7\uff0c\u90a3\u94c1\u5b9a\u662f\u4e00\u56e2\u7cdf\uff0c\u4f60\u4f1a\u4e0d\u5fcd\u5fc3\u770b\u7684\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u6291\u5934\u770b\u7740\u5f20\u606a\u5bb6\u4e8c\u697c\u671d\u5317\u7684\u90a3\u4e2a\u623f\u95f4\u706f\u4eae\u4e86\uff0c\u82e5\u662f\u8fd9\u6e56\u9762\u4e09\u680b\u7684\u6237\u578b\u7ed3\u6784\u76f8\u540c\uff0c\u90a3\u4e2a\u623f\u95f4\u504f\u5c0f\uff0c\u53ea\u9002\u5408\u62ff\u6765\u505a\u4e66\u623f\uff0c\u5bf9\u665a\u6674\u8bf4\uff1a\u201c\u606a\u5c11\u5e73\u65f6\u5230\u8fd9\u65f6\u5019\u4e5f\u4e0d\u4f11\u606f\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u5947\u602a\u4e3a\u4ec0\u4e48\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\u5374\u8981\u627f\u62c5\u8fd9\u4e48\u591a\u4e8b\uff0c\u201d\u665a\u6674\u8f6c\u8fc7\u8eab\uff0c\u80f3\u818a\u6401\u5728\u5fae\u51c9\u7684\u6905\u80cc\u4e0a\uff0c\u4ece\u7e41\u5bc6\u679d\u53f6\u7684\u7f1d\u9699\u91cc\u6ce8\u89c6\u7740\u90a3\u8fb9\u7a97\u53e3\u900f\u51fa\u6765\u7684\u5149\uff0c\u201c\u4ed6\u8fd9\u65f6\u5019\u5f88\u5c11\u4f1a\u7761\uff0c\u4e0d\u8fc7\u4e5f\u53ef\u80fd\u662f\u5728\u6253\u7535\u8111\u6e38\u620f\u2026\u2026\u4ed6\u8fd8\u4e13\u95e8\u8ba9\u516c\u53f8\u7684\u5de5\u7a0b\u5e08\u5c06\u56fd\u5916\u4e00\u4e9b\u597d\u7684\u7535\u8111\u6e38\u620f\u505a\u6210\u4e2d\u6587\u7248\u540e\u7ed9\u4ed6\u73a9\uff0c\u4f60\u8981\u73a9\u8fc7\u90a3\u79cd\u6e38\u620f\uff0c\u5c31\u77e5\u9053\u503c\u5f97\u727a\u7272\u8fd9\u65f6\u95f4\u7684\u7761\u7720\u3002\u201d\u8fd9\u4f1a\u513f\u770b\u89c1\u5f20\u606a\u8d70\u5230\u7a97\u53e3\uff0c\u597d\u50cf\u76b1\u7740\u770b\u7740\u7a97\u5916\u6e56\u6c34\u7684\u6837\u5b50\uff0c\u4e0d\u6653\u5f97\u4ed6\u665a\u4e0a\u5728\u68cb\u9986\u8ddf\u8bb8\u9e3f\u4f2f\u8ba8\u8bba\u4e86\u4ec0\u4e48\uff0c\u600e\u4e48\u4f1a\u4e00\u4ed8\u5fe7\u5fc3\u5fe1\u5fe1\u7684\u6837\u5b50\uff1f</p></article>"}}}]);
