(window.webpackJsonp=window.webpackJsonp||[]).push([[403],{425:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u8282 \u73ca\u5609\u7684\u5929\u8d4b",content:"<article><h2>\u7b2c\u516d\u5341\u8282 \u73ca\u5609\u7684\u5929\u8d4b</h2><p>\u4f8f\u5112\u91ca\u653e\u7684\u201c\u5e7b\u5883\u201d\uff0c\u5e76\u4e0d\u7b97\u662f\u5f88\u6709\u6740\u4f24\u529b\u7684\u9b54\u6cd5\uff0c\u5b83\u4e3b\u8981\u7684\u529f\u80fd\u662f\u8ba9\u4e00\u7247\u533a\u57df\u88ab\u5e7b\u50cf\u7b3c\u7f69\uff0c\u662f\u56f0\u4f4f\u4eba\u800c\u975e\u6740\u4eba\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u56e0\u4e3a\u5e7b\u50cf\u592a\u8fc7\u6050\u6016\uff0c\u5f88\u591a\u4eba\u614c\u4e71\u4e2d\u4e92\u76f8\u78b0\u649e\u3001\u62e5\u6324\u3001\u8df5\u8e0f\uff0c\u8fd8\u662f\u9887\u9020\u6210\u4e86\u4e00\u4e9b\u4f24\u4ea1\uff0c\u6709\u4e24\u4e2a\u4eba\u751a\u81f3\u88ab\u8e29\u65ad\u4e86\u808b\u9aa8\u3002\u800c\u4e14\u5c31\u7b97\u8eab\u4f53\u6ca1\u6709\u53d7\u4f24\uff0c\u76ee\u7779\u8fd9\u79cd\u8840\u8165\u573a\u9762\uff0c\u5fc3\u7406\u4e0a\u7684\u9634\u5f71\u4e5f\u662f\u5f88\u96be\u6d88\u53bb\u4e86\u3002</p><p>\u8fd9\u4e9b\u5584\u540e\u4e8b\u5b9c\uff0c\u81ea\u7136\u6709\u536b\u5175\u5904\u7406\uff0c\u4f46\u4eca\u665a\u7684\u9a6c\u620f\u4e5f\u662f\u80af\u5b9a\u770b\u4e0d\u6210\u4e86\u3002\u743c\u6069\u548c\u8299\u857e\u72c4\u79bb\u5f00\u4e2d\u5fc3\u5e7f\u573a\uff0c\u4fe1\u6b65\u5728\u8857\u9053\u4e0a\u95f2\u901b\u8d77\u6765\u3002\u963f\u65af\u5361\u7279\u62c9\u662f\u5b89\u59c6\u5e1d\u56fd\u9996\u90fd\uff0c\u8d39\u4f26\u7b2c\u4e03\u5927\u6e2f\u53e3\uff0c\u4ee5\u57ce\u5e02\u89c4\u6a21\u3001\u4eba\u53e3\u800c\u8bba\uff0c\u5728\u5168\u5927\u9646\u53ea\u6015\u6392\u4e0d\u8fdb\u524d\u4e8c\u5341\uff0c\u4f46\u4ee5\u7e41\u534e\u7a0b\u5ea6\u6765\u770b\uff0c\u7edd\u5bf9\u80fd\u8fdb\u5165\u524d\u4e94\u540d\u3002\u591c\u665a\u6bd4\u767d\u5929\u8fd8\u70ed\u95f9\uff0c\u9b54\u6cd5\u8def\u706f\u5c06\u5929\u7a7a\u7167\u5f97\u4eae\u5982\u767d\u663c\uff0c\u5404\u5bb6\u5546\u5e97\u524d\u7684\u62db\u724c\u95ea\u70c1\u7740\u4e94\u989c\u516d\u8272\u7684\u9713\u8679\uff0c\u55a7\u95f9\u53eb\u5356\u58f0\u6b64\u8d77\u5f7c\u4f0f\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u8857\u9053\u4e0a\u4eba\u6765\u4eba\u5916\uff0c\u5982\u6d77\u5982\u6f6e\u3002\u8299\u857e\u72c4\u4ece\u5c0f\u751f\u957f\u5728\u51b7\u6e05\u6e05\u7684\u9634\u9b42\u57ce\uff0c\u54ea\u91cc\u89c1\u8fc7\u8fd9\u6837\u70ed\u95f9\u7684\u5730\u65b9\uff0c\u65e2\u662f\u597d\u5947\uff0c\u4e5f\u6709\u4e9b\u80c6\u602f\uff0c\u7d27\u7d27\u6293\u7740\u743c\u6069\u7684\u624b\uff0c\u751f\u6015\u4e00\u4e0d\u5c0f\u5fc3\u5728\u4eba\u6f6e\u4e2d\u6324\u6563\u4e86\u3002</p><p>\u8868\u6f14\u867d\u7136\u770b\u4e0d\u6210\uff0c\u5c11\u5973\u5374\u4e5f\u4e1d\u6beb\u6ca1\u6709\u6cae\u4e27\u7684\u6837\u5b50\uff0c\u9ad8\u9ad8\u5174\u5174\u5730\u62c9\u7740\u743c\u6069\u8bf4\u8fd9\u8bf4\u90a3\u3002\u4e24\u4eba\u4e00\u8def\u95f2\u8d70\uff0c\u53c8\u8f6c\u5230\u4e86\u6c83\u91d1\u6b65\u9053\u6765\uff0c\u743c\u6069\u60f3\u8d77\u524d\u65e5\u6765\u6b64\uff0c\u6709\u4e00\u5bb6\u5546\u5e97\u91cc\u9762\u51fa\u552e\u5404\u79cd\u6bd4\u8f83\u201c\u7279\u522b\u201d\u7684\u9b54\u6cd5\u5236\u54c1\uff0c\u4f8b\u5982\u9970\u54c1\u3001\u4e50\u5668\u4e4b\u7c7b\u7684\uff0c\u5f53\u65f6\u5fd9\u7740\u627e\u9f99\u8840\uff0c\u4e0d\u66fe\u5728\u610f\uff0c\u53ea\u7565\u7565\u626b\u8fc7\u4e00\u773c\uff0c\u5370\u8c61\u4f3c\u4e4e\u8fd8\u4e0d\u9519\uff0c\u4e8e\u662f\u4fbf\u5e26\u7740\u8299\u857e\u72c4\u53bb\u4e86\u3002</p><p>\u90a3\u5bb6\u9b54\u6cd5\u5546\u5e97\u7684\u540d\u5b57\u5f88\u6709\u8da3\uff0c\u53eb\u505a\u201c\u5760\u661f\u6d77\u201d\uff0c\u8fd9\u662f\u5927\u9646\u4e0a\u4e00\u4e2a\u8457\u540d\u5185\u6d77\u7684\u540d\u5b57\u3002\u51db\u7684\u8001\u5e08\u6b23\u5e03\u5973\u738b\u6240\u7edf\u6cbb\u7684\u963f\u683c\u62c9\u9686\u5730\u533a\uff0c\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u5ef6\u4f38\u5165\u5760\u661f\u6d77\u7684\u72ed\u957f\u534a\u5c9b\u3002\u6885\u83f2\u65af\u548c\u51db\u8fd9\u6b21\u53bb\u627e\u6b23\u5e03\uff0c\u6700\u540e\u4e00\u6bb5\u8def\u4e5f\u5c31\u662f\u8981\u5750\u8239\u6a2a\u7a7f\u5760\u661f\u6d77\u3002\u5546\u5e97\u53d6\u8fd9\u4e2a\u540d\u5b57\uff0c\u5bd3\u610f\u5e94\u8be5\u662f\u201c\u7fa4\u661f\u835f\u8403\uff0c\u7433\u7405\u6ee1\u76ee\u201d\u7684\u610f\u601d\uff0c\u53ef\u60dc\u751f\u610f\u5374\u5f88\u51b7\u6e05\uff0c\u51e0\u4e4e\u662f\u95e8\u53ef\u7f57\u96c0\uff0c\u5927\u7ea6\u56e0\u4e3a\u5b9a\u4ef7\u5b9e\u5728\u592a\u6602\u8d35\u4e86\u3002</p><p>\u4e24\u4eba\u8d70\u8fdb\u5546\u5e97\uff0c\u5728\u9970\u54c1\u7684\u67dc\u53f0\u524d\u770b\u4e86\u534a\u65e5\uff0c\u743c\u6069\u6700\u540e\u9009\u4e2d\u4e00\u526f\u84dd\u5b9d\u77f3\u8033\u5760\uff0c\u4f5c\u5de5\u7cbe\u81f4\u5012\u7f62\u4e86\uff0c\u4e5f\u6ca1\u591a\u5f3a\u7684\u9b54\u6cd5\u6548\u679c\uff0c\u4e3b\u8981\u662f\u6709\u5f88\u6d53\u539a\u7684\u5f02\u56fd\u98ce\u60c5\uff0c\u548c\u5e73\u5e38\u6240\u89c1\u9887\u6709\u4e0d\u540c\u3002\u636e\u5e97\u4e3b\u8bf4\uff0c\u8fd9\u526f\u8033\u5760\u5e76\u975e\u8d39\u4f26\u672c\u571f\u6240\u4ea7\uff0c\u800c\u662f\u6e90\u81ea\u9065\u8fdc\u7684\u897f\u65b9\u5927\u9646\u9a6c\u5179\u7279\u514b\uff08Maztica\uff09\uff0c\u90a3\u662f\u4e00\u4e2a\u76db\u4ea7\u9ec4\u91d1\u3001\u94bb\u77f3\u3001\u8c61\u7259\u548c\u5404\u79cd\u73cd\u8d35\u9999\u6599\u7684\u5730\u65b9\uff0c\u751f\u6d3b\u7740\u51e0\u767e\u4e2a\u4e0d\u540c\u7684\u90e8\u843d\u6587\u660e\u3002\u5341\u5e74\u524d\uff0c\u5b89\u59c6\u7684\u5192\u9669\u8005\u4eec\u4e58\u8239\u5ea6\u8fc7\u6d69\u701a\u65e0\u9645\u7684\u65e0\u5c3d\u6d77\uff0c\u6cbf\u7740\u65e5\u843d\u7684\u65b9\u5411\u52c7\u6562\u524d\u8fdb\uff0c\u6700\u7ec8\u53d1\u73b0\u4e86\u8fd9\u5757\u65b0\u5927\u9646\uff0c\u5546\u4eba\u4eec\u968f\u540e\u800c\u81f3\uff0c\u8fd9\u6761\u65b0\u822a\u8def\u7684\u5f00\u8f9f\uff0c\u8ba9\u5b89\u59c6\u5e1d\u56fd\u7684\u56fd\u529b\u8fc5\u901f\u5f97\u5230\u63d0\u5347\uff0c\u7e41\u534e\u7a0b\u5ea6\u4e00\u65e5\u5343\u91cc\u3002</p><p>\u743c\u6069\u66ff\u8299\u857e\u72c4\u5c06\u84dd\u5b9d\u77f3\u8033\u5760\u6234\u4e0a\uff0c\u201c\u771f\u6f02\u4eae\u3002\u201d\u4ed6\u5938\u5956\u3002</p><p>\u5c0f\u5973\u5b69\u751c\u751c\u7b11\u7740\u3002</p><p>\u743c\u6069\u53c8\u770b\u4e86\u4e00\u4f1a\uff0c\u5374\u518d\u6ca1\u4ec0\u4e48\u5176\u4ed6\u6bd4\u8f83\u6ee1\u610f\u7684\uff0c\u4e0d\u7531\u5f97\u5fae\u5fae\u76b1\u7709\u3002\u8299\u857e\u72c4\u6709\u4e9b\u4e0d\u89e3\uff0c\u201c\u4f60\u8fd8\u8981\u4e70\u5417\uff1f\u201d\u5979\u6084\u58f0\u95ee\uff0c\u201c\u4e00\u526f\u5c31\u591f\u5566\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u6211\u59d0\u59d0\u7684\u751f\u65e5\u5feb\u5230\u4e86\uff0c\u60f3\u7ed9\u5979\u51c6\u5907\u4e00\u4ef6\u793c\u7269\u3002\u201d</p><p>\u518d\u8fc7\u4e24\u5929\uff0c\u5c31\u662f\u73ca\u5609\u7684\u751f\u65e5\u2014\u2014\u5f53\u7136\u4e5f\u662f\u743c\u6069\u81ea\u5df1\u7684\u751f\u65e5\uff0c\u4ed6\u4eec\u540c\u65e5\u6240\u751f\u3002\u743c\u6069\u73b0\u5728\u5728\u963f\u65af\u5361\u7279\u62c9\uff0c\u8981\u8d76\u56de\u9634\u9b42\u57ce\u662f\u7edd\u5bf9\u6765\u4e0d\u53ca\u4e86\uff0c\u4f46\u793c\u7269\u8fd8\u662f\u8981\u51c6\u5907\u7684\uff0c\u56de\u53bb\u4e4b\u540e\u8865\u9001\u3002</p><p>\u201c\u54e6\uff0c\u201d\u8299\u857e\u72c4\u70b9\u70b9\u5934\uff0c\u8868\u793a\u77e5\u9053\uff0c\u201c\u7ed9\u73ca\u5609\u59d0\u59d0\u7684\u793c\u7269\u554a\u2026\u2026\u743c\u6069\uff0c\u4f60\u5e72\u561b\u4e0d\u53bb\u90a3\u8fb9\u7684\u67dc\u53f0\u770b\u770b\u5462\uff1f\u201d</p><p>\u201c\u90a3\u8fb9\u7684\u67dc\u53f0\uff1f\u201d\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u90a3\u8fb9\u90fd\u662f\u4e50\u5668\u554a\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u554a\uff0c\u53ef\u4ee5\u9001\u73ca\u5609\u59d0\u59d0\u4e00\u4ef6\u4e50\u5668\uff0c\u6211\u89c9\u5f97\u4e0d\u9519\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5979\u53c8\u4e0d\u4f1a\uff0c\u5e72\u561b\u9001\u4e50\u5668\u3002\u201d</p><p>\u201c\u53ef\u662f\u73ca\u5609\u59d0\u59d0\u5728\u97f3\u4e50\u4e0a\u5f88\u6709\u5929\u8d4b\u554a\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u4e0d\u4f1a\u53ef\u4ee5\u5b66\u561b\uff0c\u9634\u9b42\u57ce\u91cc\u53cd\u6b63\u4e5f\u6709\u97f3\u4e50\u5b66\u9662\u3002\u201d</p><p>\u9634\u9b42\u57ce\u91cc\u786e\u5b9e\u6709\u4e00\u5ea7\u97f3\u4e50\u5b66\u9662\uff0c\u8fd8\u662f\u5f53\u5e74\u963f\u62c9\u838e\u738b\u540e\uff08\u4e5f\u5c31\u662f\u88ab\u8c0b\u6740\u7684\u90a3\u4f4d\uff09\u4e00\u624b\u521b\u5efa\u7684\uff0c\u5e76\u4e14\u81ea\u5df1\u51fa\u4efb\u9662\u957f\uff0c\u4f46\u5b66\u751f\u5f88\u5c11\uff0c\u4e3b\u8981\u4e5f\u5c31\u662f\u8ba9\u8d35\u65cf\u5b50\u5f1f\u4eec\u9676\u51b6\u70b9\u60c5\u64cd\uff0c\u589e\u52a0\u70b9\u98ce\u5ea6\u6240\u7528\u3002\u5170\u5c3c\u65af\u7279\u5bb6\u5982\u4eca\u4e5f\u5df2\u7ecf\u8dfb\u8eab\u8d35\u65cf\uff0c\u73ca\u5609\u81ea\u7136\u53ef\u4ee5\u8fdb\u53bb\u5b66\u4e60\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u5979\u6709\u8fd9\u65b9\u9762\u7684\u5174\u8da3\u548c\u5929\u8d4b\u4e48\uff1f</p><p>\u201c\u4f60\u8bf4\u73ca\u5609\u5728\u97f3\u4e50\u4e0a\u5f88\u6709\u5929\u8d4b\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u201c\u6211\u600e\u4e48\u4e0d\u77e5\u9053\uff0c\u4ece\u5c0f\u5230\u5927\uff0c\u4ece\u6ca1\u89c1\u5979\u6f14\u594f\u8fc7\u4e50\u5668\u2026\u2026\u5f53\u7136\u4e86\uff0c\u5c0f\u65f6\u5019\u5bb6\u91cc\u56f0\u96be\uff0c\u4e5f\u6ca1\u6761\u4ef6\u3002\u5531\u6b4c\u5012\u662f\u8fd8\u542c\u597d\u542c\u7684\uff0c\u4f46\u4e5f\u7b97\u4e0d\u4e0a\u591a\u4e48\u51fa\u7c7b\u62d4\u8403\u5427\u3002\u201d</p><p>\u201c\u90a3\u662f\u6ca1\u673a\u4f1a\u5c55\u73b0\uff0c\u4e0d\u662f\u6ca1\u6709\u5929\u8d4b\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u8299\u8389\u5a05\u548c\u6211\u8bf4\u8fc7\uff0c\u73ca\u5609\u59d0\u59d0\u5728\u97f3\u4e50\u4e0a\u7684\u5929\u8d4b\u6781\u9ad8\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u4e07\u4e2d\u65e0\u4e00\u3001\u767e\u4e16\u4e0d\u9047\u7684\u90a3\u79cd\u7a0b\u5ea6\u5462\u3002\u201d</p><p>\u743c\u6069\u5931\u7b11\uff0c\u201c\u6ca1\u8fd9\u4e48\u5938\u5f20\u5427\uff0c\u8299\u8389\u5a05\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u5979\u4eec\u53bb\u6559\u4f1a\u505a\u7948\u7977\u554a\u3002\u201d</p><p>1372DR\u7684\u7eff\u8349\u8282\uff0c\u743c\u6069\u81ea\u5deb\u5e08\u5b66\u6821\u6bd5\u4e1a\uff0c\u76f4\u63a5\u4ece\u6bd5\u4e1a\u5178\u793c\u4e0a\u5c31\u88ab\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u6d3e\u4e86\u9001\u4fe1\u7684\u4efb\u52a1\uff0c\u8d76\u51fa\u4e86\u9634\u9b42\u57ce\uff0c\u534a\u5e74\u540e\u624d\u56de\u6765\u3002\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6210\u529f\u6bd5\u4e1a\uff0c\u5170\u5c3c\u65af\u7279\u5bb6\u65cf\u664b\u5347\u4e3a\u8d35\u65cf\uff0c\u6709\u4e30\u539a\u7684\u85aa\u6c34\u4fdd\u969c\uff0c\u73ca\u5609\u81ea\u7136\u4e5f\u5c31\u4e0d\u7528\u518d\u5f00\u5546\u5e97\u8d5a\u94b1\u517b\u5bb6\uff0c\u4f46\u5979\u6bcf\u5929\u95f2\u5728\u5bb6\u4e2d\uff0c\u4e5f\u89c9\u5f97\u65e0\u6240\u4e8b\u4e8b\u3002\u6b63\u597d\u9694\u58c1\u5c31\u662f\u83ab\u5c3c\u5361\u5bb6\uff0c\u90bb\u5c45\u6765\u5f80\uff0c\u73ca\u5609\u8ba4\u8bc6\u4e86\u83ab\u5c3c\u5361\u59d0\u59b9\uff0c\u5173\u7cfb\u76f8\u5904\u5f97\u8fd8\u5f88\u4e0d\u9519\uff0c\u65e2\u5411\u8299\u857e\u72c4\u5b66\u4e60\u9b54\u6cd5\uff0c\u4e5f\u7ecf\u5e38\u548c\u8299\u8389\u5a05\u53bb\u591c\u5973\u58eb\u7684\u795e\u6bbf\u53bb\u505a\u7948\u7977\uff0c\u53c2\u52a0\u5404\u79cd\u6559\u4f1a\u4eea\u5f0f\u3002</p><p>\u73ca\u5609\u662f\u838e\u5c14\u4fe1\u5f92\uff0c\u53bb\u795e\u6bbf\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u6ca1\u4ec0\u4e48\u597d\u5947\u602a\u7684\u3002\u9634\u9b42\u57ce\u662f\u838e\u5c14\u6559\u4f1a\u7684\u5927\u672c\u8425\u4e4b\u4e00\uff0c\u4f20\u627f\u5343\u5e74\uff0c\u6559\u4f1a\u4e3e\u884c\u5e86\u5178\u3001\u796d\u7940\uff0c\u5404\u79cd\u4eea\u5f0f\u81ea\u7136\u4f1a\u6bd4\u8f83\u534e\u4e3d\uff0c\u5176\u4e2d\u201c\u97f3\u4e50\u201d\u662f\u5fc5\u4e0d\u53ef\u5c11\u7684\u8981\u7d20\uff0c\u56e0\u4e3a\u5973\u795e\u975e\u5e38\u7231\u597d\u6b64\u9053\u3002\u5728\u5076\u7136\u7684\u673a\u4f1a\u4e2d\uff0c\u8299\u8389\u5a05\u53d1\u73b0\u73ca\u5609\u5728\u97f3\u4e50\u65b9\u9762\u975e\u5e38\u6709\u5929\u8d4b\uff0c\u5c24\u5176\u662f\u5404\u79cd\u4e50\u5668\uff0c\u4e00\u5b66\u5c31\u4f1a\uff0c\u4e0a\u624b\u6781\u5feb\uff0c\u4ee4\u6559\u4f1a\u91cc\u7684\u7267\u5e08\u4eec\u60ca\u53f9\u79f0\u8d5e\u4e0d\u5df2\u3002\u751a\u81f3\u6709\u4e00\u4f4d\u201c\u591c\u89c6\u8005\u201d\uff08\u838e\u5c14\u6559\u4f1a\u516d\u5927\u4f4d\u9636\u4e2d\u7b2c\u4e94\u9636\uff0c\u5730\u4f4d\u4ec5\u6b21\u4e8e\u6700\u9ad8\u9636\u7684\u201c\u9ed1\u6697\u4e4b\u7130\u201d\uff09\u5938\u5956\u8bf4\uff0c\u4ee5\u73ca\u5609\u8fd9\u79cd\u826f\u6750\u7f8e\u8d28\uff0c\u5982\u679c\u52aa\u529b\u7684\u8bdd\uff0c\u5f88\u6709\u53ef\u80fd\u4f1a\u6210\u4e3a\u4e00\u540d\u4f18\u79c0\u7684\u541f\u6e38\u8bd7\u4eba\u3002</p><p>\u6240\u8c13\u541f\u6e38\u8bd7\u4eba\uff0c\u5176\u5b9e\u4e00\u822c\u6709\u4e24\u91cd\u542b\u4e49\u3002\u7b2c\u4e00\u91cd\u542b\u4e49\uff0c\u662f\u6307\u90a3\u4e9b\u884c\u8d70\u5927\u5730\uff0c\u968f\u98ce\u6d6a\u8ff9\u7684\u6c11\u95f4\u6b4c\u624b\u3001\u8bf4\u5531\u8005\uff0c\u6df1\u53d7\u5e73\u6c11\u767e\u59d3\u7684\u6b22\u8fce\uff0c\u4e5f\u80fd\u767b\u5927\u96c5\u4e4b\u5802\uff0c\u5411\u56fd\u738b\u3001\u57ce\u4e3b\u732e\u827a\uff0c\u5f80\u5f80\u8fd8\u80fd\u64b0\u5199\u5404\u79cd\u82f1\u96c4\u6545\u4e8b\u51fa\u7248\u53d1\u884c\uff0c\u5317\u5730\u6709\u4e00\u4f4d\u53eb\u505a\u8428\u5c14\u74e6\u591a\u7684\u541f\u6e38\u8bd7\u4eba\uff0c\u4fbf\u662f\u8457\u540d\u7684\u4f8b\u5b50\uff0c\u6700\u559c\u6b22\u5199\u4ee5\u5353\u5c14\u4e3a\u4e3b\u89d2\u7684\u5192\u9669\u5c0f\u8bf4\u3002\u8fd9\u79cd\u541f\u6e38\u8bd7\u4eba\uff0c\u66f4\u591a\u662f\u827a\u672f\u5bb6\u3001\u6b4c\u5531\u8005\uff0c\u4ed6\u4eec\u5f80\u5f80\u89c1\u95fb\u5e7f\u535a\uff0c\u9605\u5386\u4e30\u5bcc\uff0c\u4f46\u672a\u5fc5\u6709\u591a\u5f3a\u7684\u6218\u6597\u80fd\u529b\uff0c\u751a\u81f3\u53ef\u80fd\u5c31\u662f\u4e2a\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\u7684\u6587\u5f31\u4e4b\u8f88\uff0c\u5f53\u7136\u4e5f\u53ef\u80fd\u662f\u6df1\u85cf\u4e0d\u9732\u7684\u5f3a\u8005\uff0c\u6e38\u620f\u98ce\u5c18\u7684\u9ad8\u4eba\u3002\u8fd9\u662f\u5e7f\u4e49\u4e0a\u7684\u541f\u6e38\u8bd7\u4eba\uff0c\u610f\u5473\u7740\u4e00\u79cd\u751f\u6d3b\u65b9\u5f0f\u3002</p><p>\u541f\u6e38\u8bd7\u4eba\u7684\u7b2c\u4e8c\u91cd\u542b\u4e49\uff0c\u5219\u662f\u7279\u6307\u67d0\u4e9b\u7279\u522b\u7684\u3001\u6781\u5176\u7f55\u89c1\u7684\u5deb\u5e08\uff0c\u4ed6\u4eec\u548c\u666e\u901a\u7684\u5deb\u5e08\u4e00\u6837\uff0c\u90fd\u662f\u5965\u672f\u7684\u638c\u63a7\u8005\u3001\u8fd0\u7528\u8005\uff0c\u4f46\u65b9\u5f0f\u6709\u6240\u4e0d\u540c\u3002\u4fbf\u5982\u743c\u6069\uff0c\u4ed6\u5c5e\u4e8e\u5deb\u5e08\u4e2d\u7684\u5e38\u6001\uff0c\u7ecf\u8fc7\u523b\u82e6\u7684\u5b66\u4e60\uff0c\u638c\u63e1\u7406\u8bba\uff0c\u5206\u6790\u7814\u7a76\uff0c\u6a21\u62df\u6784\u9020\uff0c\u501f\u52a9\u9b54\u6cd5\u4e66\u3001\u5492\u6587\u3001\u65bd\u6cd5\u6750\u6599\u7b49\u8981\u7d20\u6765\u5b8c\u6210\u6cd5\u672f\uff0c\u4f46\u4e00\u4e2a\u541f\u6e38\u8bd7\u4eba\uff0c\u4ed6\u540c\u6837\u4e5f\u80fd\u91ca\u653e\u9b54\u6cd5\uff0c\u501f\u52a9\u7684\u624b\u6bb5\u5374\u662f\u201c\u97f3\u4e50\u201d\u3002\u8fd9\u662f\u72ed\u4e49\u4e0a\u7684\u541f\u6e38\u8bd7\u4eba\uff0c\u53c8\u79f0\u4e3a\u201c\u97f3\u5f8b\u58eb\u201d\uff0c\u610f\u5473\u7740\u4e00\u79cd\u8d85\u51e1\u80fd\u529b\u3002\u838e\u5c14\u6559\u4f1a\u90a3\u4f4d\u201c\u591c\u89c6\u8005\u201d\u6240\u6307\u7684\uff0c\u663e\u7136\u662f\u540e\u9762\u8fd9\u4e00\u79cd\u542b\u4e49\u3002</p><p>\u501f\u52a9\u97f3\u4e50\uff08\u5305\u62ec\u6b4c\u58f0\u3001\u4e50\u5668\u548c\u821e\u8e48\u7b49\u5f62\u5f0f\u5728\u5185\uff09\uff0c\u541f\u6e38\u8bd7\u4eba\u65e0\u9700\u9b54\u6cd5\u4e66\uff0c\u65e0\u9700\u8bf5\u5492\uff0c\u540c\u6837\u4e5f\u80fd\u5b8c\u7f8e\u5730\u6a21\u62df\u51fa\u5404\u79cd\u9b54\u6cd5\u6548\u679c\u3002\u8fd9\u4e0e\u5176\u8bf4\u662f\u4e00\u79cd\u201c\u80fd\u529b\u201d\uff0c\u4e0d\u5982\u8bf4\u662f\u4e00\u79cd\u201c\u5929\u8d4b\u201d\uff0c\u6ca1\u6709\u5929\u8d4b\u7684\u4eba\uff0c\u6c38\u8fdc\u4e5f\u4e0d\u53ef\u80fd\u505a\u5230\uff0c\u4f46\u6709\u5929\u8d4b\u7684\u4eba\uff0c\u5374\u5f80\u5f80\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u3002</p><p>\u5929\u8d4b\u8fd9\u79cd\u4e1c\u897f\uff0c\u6709\u65f6\u5019\u771f\u662f\u65e0\u8bdd\u53ef\u8bf4\uff0c\u80fd\u591f\u8ba9\u4eba\u5ac9\u5992\u7fa1\u6155\u6b7b\u3002\u5c31\u50cf\u51db\u4e00\u6837\uff0c\u5979\u662f\u9f99\u8109\u8005\uff0c\u662f\u672f\u58eb\uff0c\u5929\u8d4b\u4f18\u79c0\uff0c\u6240\u4ee5\u5979\u534a\u70b9\u4e0d\u52aa\u529b\uff0c\u8d85\u7ea7\u7231\u7761\u61d2\u89c9\uff0c\u8d2a\u73a9\u7231\u95f9\uff0c\u9b54\u6cd5\u9020\u8be3\u5374\u4f9d\u65e7\u5f88\u9ad8\uff0c\u53ea\u8981\u7a0d\u7a0d\u52aa\u529b\u7528\u529f\u70b9\uff0c\u8fdb\u6b65\u901f\u5ea6\u4fbf\u5feb\u5f97\u60ca\u4eba\uff0c\u5982\u4eca\u4e5f\u624d\u5341\u4e03\u5c81\uff0c\u4fbf\u5df2\u7ecf\u51dd\u6210\u771f\u540d\uff0c\u653e\u773c\u6574\u4e2a\u5927\u9646\uff0c\u867d\u7136\u4e0d\u8bf4\u7edd\u65e0\u4ec5\u6709\uff0c\u4e5f\u662f\u4e07\u4e2d\u65e0\u4e00\u7684\u5929\u624d\u4e86\u3002\u5c24\u5176\u662f\u6700\u8fd1\u5979\u5b8c\u6210\u201c\u9f99\u5316\u201d\uff0c\u6210\u4e3a\u771f\u6b63\u7684\u9f99\u5973\uff0c\u8fdb\u6b65\u901f\u5ea6\u80af\u5b9a\u66f4\u52a0\u4e00\u65e5\u5343\u91cc\u3002\u541f\u6e38\u8bd7\u4eba\u4e5f\u662f\u548c\u201c\u672f\u58eb\u201d\u7c7b\u4f3c\u7684\u5b58\u5728\uff0c\u6781\u5ea6\u8bb2\u7a76\u5929\u8d4b\uff0c\u5c31\u597d\u50cf\u5929\u751f\u4fbf\u662f\u4e00\u5757\u749e\u7389\uff0c\u53ea\u8981\u7ecf\u8fc7\u5408\u9002\u7684\u96d5\u7422\uff0c\u90a3\u5c31\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u73cd\u74a7\uff0c\u4efb\u4f60\u600e\u4e48\u52aa\u529b\u4e5f\u662f\u6bd4\u4e0d\u4e86\uff0c\u53ea\u80fd\u8eb2\u5728\u5899\u89d2\u53bb\u5212\u5708\u5708\u3002</p><p>\u743c\u6069\u662f\u5deb\u5e08\u4e2d\u7684\u5e38\u6001\uff0c\u53ef\u4ee5\u8bad\u7ec3\u57f9\u517b\uff0c\u51db\u8fd9\u79cd\u672f\u58eb\uff0c\u4ee5\u53ca\u8fd0\u7528\u201c\u97f3\u4e50\u201d\u7684\u529b\u91cf\u6a21\u62df\u9b54\u6cd5\u7684\u541f\u6e38\u8bd7\u4eba\uff0c\u6216\u8005\u8bf4\u97f3\u5f8b\u58eb\uff0c\u5219\u662f\u5deb\u5e08\u5f53\u4e2d\u7684\u7279\u4f8b\uff0c\u53ef\u9047\u4e0d\u53ef\u6c42\u3002</p><p>\u201c\u8fd9\u4ef6\u4e8b\u600e\u4e48\u73ca\u5609\u90fd\u6ca1\u548c\u6211\u8bf4\uff1f\u201d\u743c\u6069\u65e2\u662f\u60ca\u8bb6\u53c8\u662f\u6b23\u559c\uff0c\u201c\u4e0a\u6b21\u56de\u53bb\uff0c\u5979\u90fd\u6ca1\u548c\u6211\u63d0\u8fc7\uff1f\u201d</p><p>\u73ca\u5609\u5982\u679c\u4ec5\u4ec5\u662f\u6709\u97f3\u4e50\u5929\u8d4b\uff0c\u743c\u6069\u8fd8\u4e0d\u4f1a\u7279\u522b\u5728\u610f\uff0c\u81f3\u5c11\u4e0d\u4f1a\u8fd9\u4e48\u6fc0\u52a8\uff0c\u4f46\u5982\u679c\u771f\u5982\u90a3\u4f4d\u201c\u591c\u89c6\u8005\u201d\u6240\u8a00\uff0c\u73ca\u5609\u6709\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\u7684\u8d44\u8d28\uff0c\u90a3\u5c31\u662f\u4e00\u4e2a\u5929\u5927\u7684\u597d\u6d88\u606f\u4e86\u3002\u4ed6\u4e00\u76f4\u5934\u75bc\u7684\uff0c\u5c31\u662f\u81ea\u5df1\u7684\u751f\u6d3b\u603b\u662f\u5145\u6ee1\u8270\u96be\u5371\u9669\uff0c\u73ca\u5609\u662f\u4e2a\u67d4\u5f31\u5c11\u5973\uff0c\u81ea\u5df1\u4e0d\u6562\u628a\u5979\u5e26\u5728\u8eab\u8fb9\uff0c\u4f46\u603b\u662f\u8fd9\u6837\u5206\u5f00\u7740\u4e5f\u4e0d\u662f\u4e2a\u529e\u6cd5\u3002\u5018\u82e5\u73ca\u5609\u771f\u80fd\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\uff0c\u90a3\u8fd9\u4e2a\u96be\u9898\u81ea\u7136\u5c31\u8fce\u5203\u800c\u89e3\uff0c\u4e0d\u5728\u8bdd\u4e0b\u4e86\u3002\u4f46\u8fd9\u79cd\u597d\u4e8b\u60c5\uff0c\u73ca\u5609\u600e\u4e48\u90fd\u6ca1\u548c\u4ed6\u900f\u9732\u8fc7\u534a\u70b9\u98ce\u58f0\uff1f</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u81ea\u5df1\u5e94\u8be5\u90fd\u8fd8\u4e0d\u77e5\u9053\u5462\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u8fd9\u662f\u90a3\u4f4d\u591c\u89c6\u8005\u5927\u4eba\u79c1\u4e0b\u8bc4\u4ef7\u7684\uff0c\u53ea\u6709\u6559\u4f1a\u5185\u90e8\u7684\u4eba\u77e5\u6653\uff0c\u4e0d\u5141\u8bb8\u5916\u4f20\u3002\u6211\u662f\u542c\u8299\u8389\u5a05\u8bf4\u7684\uff0c\u4f46\u5979\u5631\u5490\u6211\u522b\u544a\u8bc9\u73ca\u5609\u59d0\u59d0\uff0c\u6211\u5c31\u6ca1\u8bf4\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u600e\u4e48\u53c8\u544a\u8bc9\u6211\u5462\uff1f\u201d\u743c\u6069\u5f00\u73a9\u7b11\u3002</p><p>\u201c\u4f60\u53c8\u4e0d\u662f\u5916\u4eba\u561b\u3002\u201d</p><p>\u8bf4\u662f\u6559\u4f1a\u5185\u90e8\u77e5\u6653\uff0c\u4e0d\u5141\u8bb8\u5916\u4f20\uff0c\u7ed3\u679c\u8299\u8389\u5a05\u8fd9\u4e2a\u7267\u5e08\u544a\u8bc9\u4e86\u81ea\u5df1\u7684\u59b9\u59b9\uff0c\u800c\u59b9\u59b9\u53c8\u6cc4\u9732\u7ed9\u4e86\u60c5\u4eba\u2026\u2026\u770b\u6765\u591c\u5973\u58eb\u5bf9\u4fe1\u5f92\u7684\u6559\u80b2\u5b9e\u5728\u6709\u5f85\u52a0\u5f3a\uff0c\u8fd9\u201c\u9690\u79d8\u201d\u7684\u5de5\u4f5c\u505a\u5f97\u5b9e\u5728\u4e0d\u5230\u4f4d\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u5b9e\u5728\u662f\u4e2a\u597d\u6d88\u606f\uff0c\u8ba9\u743c\u6069\u7684\u60c5\u7eea\u4e00\u4e0b\u5b50\u9ad8\u6da8\u8d77\u6765\uff0c\u6068\u4e0d\u5f97\u8d76\u5feb\u56de\u5230\u9634\u9b42\u57ce\uff0c\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u59d0\u59d0\u3002\u4e0d\u8fc7\u6446\u5728\u9762\u524d\u7684\u4e8b\u60c5\uff0c\u662f\u5148\u5f97\u7ed9\u73ca\u5609\u6311\u9009\u4e00\u4ef6\u793c\u7269\u3002</p><p>\u7c7b\u578b\u81ea\u7136\u5df2\u7ecf\u786e\u5b9a\u4e86\uff0c\u5c31\u662f\u4e50\u5668\uff0c\u6b63\u597d\u8fd9\u5ea7\u201c\u5760\u661f\u6d77\u201d\u91cc\u4e5f\u51fa\u552e\u5404\u79cd\u9b54\u6cd5\u4e50\u5668\uff0c\u5176\u4e2d\u8fd8\u9887\u6709\u4e00\u4e9b\u54c1\u8d28\u4f18\u826f\u7684\u8d27\u8272\u3002\u4f46\u4e50\u5668\u4e5f\u5206\u5f88\u591a\u79cd\uff0c\u73ca\u5609\u5230\u5e95\u4f1a\u559c\u6b22\u54ea\u4e00\u6837\uff0c\u8fd9\u53ef\u5c31\u4e0d\u6e05\u695a\u4e86\u3002</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u559c\u6b22\u54ea\u79cd\u4e50\u5668\u2026\u2026\u8fd9\u4e2a\u6211\u4e5f\u4e0d\u6e05\u695a\u5462\uff0c\u201d\u8299\u857e\u72c4\u53ef\u7231\u5730\u5410\u5410\u820c\u5934\uff0c\u201c\u8299\u8389\u5a05\u53ea\u662f\u8bf4\u5979\u5929\u8d4b\u6781\u9ad8\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u4e50\u5668\u4e0a\u624b\u5c31\u4f1a\uff0c\u4e00\u5b66\u5c31\u901a\uff0c\u5012\u6ca1\u8bf4\u7279\u522b\u64c5\u957f\u54ea\u4e00\u79cd\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u90a3\u5c31\u53ea\u80fd\u81ea\u5df1\u9009\u4e86\uff0c\u6700\u597d\u662f\u9009\u4e00\u79cd\u81ea\u5df1\u4f1a\u7684\uff0c\u8fd8\u80fd\u6559\u6559\u73ca\u5609\u3002\u53ef\u60dc\u4ed6\u5bf9\u6b64\u9053\u4e5f\u4e0d\u7cbe\u901a\uff0c\u552f\u4e00\u4f1a\u7684\u4e50\u5668\u662f\u4e8c\u80e1\uff0c\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\uff0c\u5176\u4ed6\u4e50\u5668\u57fa\u672c\u90fd\u4e0d\u4f1a\uff0c\u9009\u4ec0\u4e48\u597d\u5462\uff1f\u6216\u8005\u8ba9\u73ca\u5609\u5b66\u5439\u7bab\u2026\u2026\u8fd9\u60f3\u6cd5\u5b9e\u5728\u592a\u90aa\u6076\u4e86\u3002</p><p>\u5bf9\u4e86\uff0c\u81ea\u5df1\u597d\u50cf\u8fd8\u5b66\u8fc7\u51e0\u5929\u957f\u7b1b\uff0c\u80fd\u52c9\u5f3a\u5439\u5439\u2026\u2026\u5514\uff0c\u957f\u7b1b\uff1f</p><p>\u6b63\u5982\u6b64\u60f3\u7740\uff0c\u743c\u6069\u7684\u773c\u89d2\u4f59\u5149\u77a5\u89c1\u4e86\u4e00\u652f\u957f\u7b1b\uff0c\u901a\u4f53\u94f6\u767d\uff0c\u4fee\u957f\u7684\u7b1b\u8eab\u5149\u6d01\u5982\u955c\uff0c\u63e1\u5728\u624b\u4e2d\u5374\u4e0d\u89c9\u51b0\u51b7\uff0c\u53cd\u800c\u6709\u79cd\u9690\u9690\u7684\u6e29\u6da6\u611f\uff0c\u8bd5\u7740\u5439\u4e86\u5439\uff0c\u97f3\u8d28\u534e\u4e3d\uff0c\u6e05\u4eae\u60a0\u626c\uff0c\u786e\u5b9e\u662f\u96be\u5f97\u4e00\u89c1\u7684\u73cd\u54c1\u3002</p><p>\u636e\u5546\u5e97\u8001\u677f\u6240\u8a00\uff0c\u8fd9\u652f\u957f\u7b1b\u4e43\u662f\u5386\u53f2\u4e0a\u8457\u540d\u7684\u5236\u7b1b\u5bb6\u51ef\u5170\u5854\u83f2\u590f\u7684\u665a\u5e74\u6240\u9020\uff0c\u4e43\u662f\u96c6\u5176\u6280\u827a\u5927\u6210\u4e4b\u7269\uff0c\u540e\u6765\u53c8\u7ecf\u8fc7\u7cbe\u7075\u65cf\u541f\u6e38\u8bd7\u4eba\u6539\u9020\u9644\u9b54\uff0c\u80fd\u591f\u6d88\u9664\u75b2\u5026\uff0c\u6e05\u9192\u5934\u8111\uff0c\u8ba9\u5439\u594f\u8005\u59cb\u7ec8\u4fdd\u6301\u6700\u4f73\u7684\u4e50\u611f\u548c\u7cbe\u795e\u72b6\u6001\uff0c\u4ef7\u503c\u7740\u5b9e\u4e0d\u83f2\u3002\u51e0\u7ecf\u8f97\u8f6c\uff0c\u6d41\u4f20\u5230\u4ed6\u7956\u8f88\u624b\u4e2d\uff0c\u4e00\u76f4\u4f20\u4e86\u4e0b\u6765\uff0c\u662f\u4ed6\u8fd9\u5e97\u91cc\u6700\u73cd\u8d35\u7684\u5546\u54c1\u4e86\u3002</p><p>\u743c\u6069\u4e5f\u61d2\u5f97\u548c\u4ed6\u5e9f\u8bdd\uff0c\u76f4\u63a5\u95ee\u4ef7\uff0c\u8001\u677f\u5f00\u51fa\u4e86\u4e09\u5343\u91d1\u5e01\u7684\u9ad8\u4ef7\uff0c\u867d\u7136\u662f\u6bd4\u8f83\u9ed1\uff0c\u4f46\u8fd8\u7b97\u80fd\u63a5\u53d7\u3002\u743c\u6069\u53d6\u51fa\u5b9d\u77f3\uff0c\u6309\u5e02\u4ef7\u5151\u6362\u7ed9\u4ed6\uff0c\u53d6\u8d70\u4e86\u8fd9\u652f\u957f\u7b1b\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u5b83\u6709\u540d\u5b57\u5417\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u591c\u98ce\u4e4b\u7b1b\u3002\u201d</p><p>\u8fd9\u540d\u5b57\u8fd8\u4e0d\u9519\uff0c\u743c\u6069\u4e5f\u5c31\u61d2\u5f97\u6539\uff0c\u7ee7\u7eed\u6cbf\u7528\u3002</p><p>\u7ed9\u59d0\u59d0\u4e70\u5230\u4e86\u751f\u65e5\u793c\u7269\uff0c\u7b97\u662f\u4e86\u5374\u4e86\u4e00\u6869\u5fc3\u4e8b\uff0c\u81f3\u4e8e\u81ea\u5df1\u53cd\u5012\u4e0d\u7528\u8003\u8651\u4e86\u3002\u743c\u6069\u5bf9\u751f\u65e5\u770b\u5f97\u4e0d\u662f\u5f88\u91cd\uff0c\u4e5f\u4e0d\u6307\u671b\u80fd\u6536\u4ec0\u4e48\u751f\u65e5\u793c\u7269\u2026\u2026\u8bdd\u8bf4\u56de\u6765\uff0c\u838e\u5c14\u8fd8\u8bf4\u8981\u9001\u81ea\u5df1\u793c\u7269\uff0c\u5374\u4e0d\u8bf4\u662f\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u7ed9\u534a\u70b9\u6697\u793a\uff0c\u770b\u6765\u662f\u8981\u6765\u4e2a\u60ca\u559c\u3002\u5bf9\u6b64\u743c\u6069\u53ea\u5e0c\u671b\u771f\u7684\u662f\u201c\u60ca\u559c\u201d\u4e0d\u8981\u662f\u201c\u60ca\u6050\u201d\u5c31\u884c\uff0c\u5c24\u5176\u8981\u7d27\u7684\uff0c\u662f\u5343\u4e07\u522b\u5728\u81ea\u5df1\u548c\u8299\u857e\u72c4\u505a\u7231\u65f6\u53c8\u73a9\u5723\u8005\u964d\u4e34\uff0c\u90a3\u5c31\u771f\u662f\u8981\u65e0\u8bed\u4e86\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u8299\u857e\u72c4\uff0c\u201d\u743c\u6069\u968f\u53e3\u73a9\u7b11\uff0c\u201c\u4f60\u6709\u6ca1\u6709\u7ed9\u6211\u51c6\u5907\u751f\u65e5\u793c\u7269\u554a\uff1f\u201d</p><p>\u51fa\u4e4e\u610f\u6599\u5730\uff0c\u8299\u857e\u72c4\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u201c\u5f53\u7136\u6709\u5566\u3002\u201d\u5979\u6700\u540e\u8f7b\u58f0\u8bf4\u3002</p><p>\u201c\u662f\u5417\uff1f\u201d\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u662f\u4ec0\u4e48\uff0c\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u884c\uff0c\u4e0d\u80fd\u63d0\u524d\u8bf4\uff0c\u201d\u8299\u857e\u72c4\u8138\u4e0a\u7b11\u610f\u76c8\u76c8\uff0c\u5374\u9690\u7ea6\u6709\u51e0\u5206\u52c9\u5f3a\u7684\u6837\u5b50\uff0c\u201c\u5230\u65f6\u5019\u4f60\u5c31\u77e5\u9053\u5566\u3002\u201d</p><p>\u8fd9\u6837\u4e48\uff0c\u770b\u6765\u53c8\u662f\u4e00\u4e2a\u60ca\u559c\u554a\u3002\u4e0d\u8fc7\u6709\u53e5\u8bdd\u600e\u4e48\u8bf4\u6765\u7740\uff0c\u201c\u5f53\u4f60\u60f3\u7ed9\u4f60\u7684\u7537\u53cb\u4e00\u4e2a\u60ca\u559c\u65f6\uff0c\u4ed6\u603b\u662f\u80fd\u591f\u7ed9\u4f60\u4e00\u4e2a\u60ca\u559c\u201d\uff0c\u5e0c\u671b\u8fd9\u79cd\u60b2\u60e8\u7684\u4e8b\u60c5\u4e0d\u8981\u53d1\u751f\u5728\u81ea\u5df1\u8eab\u4e0a\u624d\u597d\u3002</p><p>\u5728\u63a5\u4e0b\u6765\u7684\u4e24\u5929\u91cc\uff0c\u5927\u5bb6\u90fd\u8fc7\u5f97\u5f88\u8f7b\u677e\uff0c\u8be5\u529e\u7684\u6b63\u4e8b\u5df2\u7ecf\u529e\u5b8c\uff0c\u5269\u4e0b\u7684\u5c31\u662f\u4f11\u606f\u3002\u82cf\u4f26\u7267\u5e08\u5931\u8e2a\u7684\u4e8b\u60c5\u5e76\u6ca1\u6709\u88ab\u516c\u5f00\u4f20\u626c\u51fa\u6765\uff0c\u636e\u838e\u73de\u514b\u6253\u63a2\u5230\u7684\u6d88\u606f\uff0c\u6708\u5973\u795e\u6559\u4f1a\u5df2\u7ecf\u76f4\u63a5\u9ed8\u8ba4\u4e86\u662f\u838e\u5c14\u6559\u4f1a\u4e0b\u7684\u624b\uff0c\u53ea\u662f\u6ca1\u6709\u8bc1\u636e\uff0c\u4e00\u65f6\u95f4\u4e5f\u65e0\u6cd5\u53ef\u65bd\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u7acb\u6b64\u5927\u529f\uff0c\u5728\u6559\u4f1a\u4e2d\u58f0\u671b\u66f4\u80dc\uff0c\u5728\u51e0\u4e2a\u7ade\u4e89\u8005\u4e2d\u9690\u7136\u6709\u4e86\u9886\u5148\u7684\u67b6\u52bf\u3002</p><p>\u90a3\u4e00\u65e5\u88ab\u838e\u5c14\u9644\u4f53\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u81ea\u5df1\u5931\u53bb\u610f\u8bc6\uff0c\u4e0d\u77e5\u9053\u5f53\u65f6\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u4f46\u4e8b\u540e\u56de\u60f3\uff0c\u4e5f\u77e5\u9053\u662f\u5973\u795e\u964d\u4e34\u4e86\u3002\u548c\u838e\u5c14\u7684\u8c08\u8bdd\u5185\u5bb9\uff0c\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u544a\u8bc9\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u4f46\u5353\u5c14\u5c11\u5973\u4e5f\u4e0d\u611a\u7b28\uff0c\u7ed3\u5408\u524d\u540e\u7ecf\u8fc7\u4e00\u60f3\uff0c\u4e5f\u5c31\u77e5\u9053\u838e\u5c14\u662f\u7279\u5730\u4e3a\u743c\u6069\u800c\u6765\uff0c\u53ef\u89c1\u8fd9\u5bb6\u4f19\u5728\u5973\u795e\u5fc3\u4e2d\u8fd8\u9887\u6709\u70b9\u5206\u91cf\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5f53\u7136\u4e0d\u4f1a\u56e0\u6b64\u800c\u60e7\u6015\u743c\u6069\uff0c\u751a\u81f3\u8c04\u5a9a\u8ba8\u597d\uff0c\u4f46\u5979\u7ec8\u7a76\u662f\u529f\u5229\u5fc3\u592a\u76db\uff0c\u5f80\u6614\u5728\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u5c31\u662f\u4e00\u5fc3\u60f3\u5f53\u4e3b\u6bcd\uff1b\u5982\u4eca\u5230\u4e86\u5730\u8868\uff0c\u53c8\u60f3\u80fd\u6267\u638c\u4e00\u65b9\uff0c\u201c\u5f80\u4e0a\u722c\u201d\uff0c\u8fd9\u51e0\u4e4e\u5df2\u7ecf\u662f\u6d78\u900f\u94ed\u523b\u5728\u5353\u5c14\u8840\u8109\u91cc\u7684\u4fe1\u5ff5\u548c\u6267\u7740\u4e86\u3002\u51fa\u4e8e\u8fd9\u79cd\u8003\u8651\uff0c\u5979\u4e5f\u96be\u514d\u4e0d\u81ea\u89c9\u95f4\u5bf9\u743c\u6069\u7684\u6001\u5ea6\u6709\u4e9b\u6539\u53d8\uff0c\u8eab\u6bb5\u7565\u7565\u67d4\u8f6f\u3002</p><p>\u6309\u7167\u8ba1\u5212\uff0c\u4f11\u606f\u4e24\u5929\uff0c\u743c\u6069\u6b63\u597d\u5728\u963f\u65af\u5361\u7279\u62c9\u5ea6\u8fc7\u751f\u65e5\u4e4b\u540e\uff0c\u5927\u5bb6\u5c31\u542f\u7a0b\u4e0a\u8def\uff0c\u56de\u9634\u9b42\u57ce\u3002\u4e0d\u8fc7\u5728\u6b64\u4e4b\u524d\uff0c\u4ed6\u8fd8\u8981\u9762\u5bf9\u5973\u795e\u59d0\u59d0\u548c\u8299\u857e\u72c4\u7684\u4e24\u4efd\u751f\u65e5\u793c\u7269\u3002</p></article>"}}}]);
