(window.webpackJsonp=window.webpackJsonp||[]).push([[404],{426:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u4e00\u8282 \u743c\u6069\u7684\u793c\u7269",content:"<article><h2>\u7b2c\u516d\u5341\u4e00\u8282 \u743c\u6069\u7684\u793c\u7269</h2><p>\u4e24\u4e16\u4e3a\u4eba\uff0c\u751f\u65e5\u4e5f\u8fc7\u4e86\u51e0\u5341\u4e2a\uff0c\u5927\u591a\u6570\u90fd\u662f\u7a00\u91cc\u7cca\u6d82\u5c31\u8fc7\u53bb\uff0c\u7ecf\u5e38\u662f\u5f53\u65f6\u7ed9\u5fd8\u4e86\uff0c\u4e8b\u540e\u624d\u60f3\u8d77\u6765\u8fd8\u6709\u8fd9\u832c\u3002\u8981\u8bf4\u751f\u65e5\u793c\u7269\uff0c\u5012\u4e5f\u6536\u8fc7\u4e00\u4e9b\uff0c\u771f\u6b63\u80fd\u7559\u4e0b\u5370\u8c61\u7684\u4e5f\u6ca1\u51e0\u4e2a\uff0c\u5f80\u5f80\u8f6c\u773c\u5c31\u5fd8\u4e86\u3002\u4e0d\u8fc7\u8fd9\u4e00\u6b21\uff0c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u592a\u4e00\u6837\u3002</p><p>\u8299\u857e\u72c4\u5012\u7f62\u4e86\uff0c\u743c\u6069\u4e5f\u6ca1\u671f\u5f85\u5979\u80fd\u7ed9\u81ea\u5df1\u4ec0\u4e48\u7279\u522b\u7684\u60ca\u559c\uff0c\u53cd\u6b63\u6574\u4e2a\u4eba\u90fd\u5df2\u7ecf\u5f04\u5230\u624b\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u66f4\u73cd\u8d35\u7684\u3002\u5012\u662f\u591c\u5973\u58eb\u838e\u5c14\uff0c\u83ab\u540d\u5176\u5999\u975e\u8981\u6765\u5f53\u81ea\u5df1\u7684\u59d0\u59d0\uff0c\u8fd8\u8bf4\u8981\u9001\u793c\u7269\uff0c\u5012\u662f\u8ba9\u4eba\u6709\u4e9b\u597d\u5947\u3002</p><p>\u4e0d\u8fc7\uff0c\u597d\u5947\u5f52\u597d\u5947\uff0c\u6b63\u4e8b\u8fd8\u5f97\u505a\u3002\u8fd9\u4e24\u5929\u867d\u8bf4\u662f\u4f11\u606f\u653e\u677e\uff0c\u4f46\u743c\u6069\u9664\u4e86\u966a\u8299\u857e\u72c4\u51fa\u95e8\u901b\u8fc7\u4e00\u6b21\u8857\uff0c\u6b64\u5916\u7684\u6240\u6709\u65f6\u95f4\u90fd\u95f7\u5728\u623f\u95f4\u91cc\u4e0d\u51fa\u6765\uff0c\u4e13\u5fc3\u81f4\u5fd7\u5730\u5236\u4f5c\u4e00\u4ef6\u9b54\u6cd5\u7269\u54c1\u3002</p><p>\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u7ed9\u4e00\u4e32\u9879\u94fe\u9644\u9b54\u3002</p><p>\u516b\u9897\u88ab\u4f8f\u5112\u5de5\u5320\u7cbe\u5fc3\u6253\u78e8\u6210\u5149\u6ed1\u7403\u9762\u7684\u540d\u8d35\u5b9d\u77f3\uff08\u94bb\u77f3\u3001\u739b\u7459\u3001\u7425\u73c0\u3001\u7d2b\u6c34\u6676\u3001\u661f\u7ea2\u77f3\u3001\u7fe1\u7fe0\u3001\u7d2b\u7389\u3001\u7ea2\u5b9d\u77f3\uff09\u548c\u56db\u9897\u7cbe\u5de7\u7684\u9ed1\u73cd\u73e0\uff0c\u88ab\u4e00\u675f\u94f6\u4e1d\u7f16\u6210\u7684\u7ec6\u5e26\u4e32\u8d77\uff0c\u6784\u9020\u7b80\u6d01\u5927\u65b9\uff0c\u4f46\u6750\u6599\u548c\u624b\u5de5\u90fd\u6602\u8d35\u5f97\u60ca\u4eba\uff0c\u800c\u4e14\u5176\u4e2d\u660e\u663e\u8fd8\u8574\u542b\u4e86\u9b54\u529b\u3002\u201c\u4f60\u8fd9\u662f\u5728\u51c6\u5907\u505a\u4ec0\u4e48\uff1f\u201d\u8299\u857e\u72c4\u6709\u4e9b\u5947\u602a\u5730\u95ee\u3002</p><p>\u201c\u505a\u4e00\u6761\u9b54\u6cd5\u9879\u94fe\u554a\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u7ed9\u59d0\u59d0\u7684\u751f\u65e5\u793c\u7269\u3002\u201d</p><p>\u201c\u751f\u65e5\u793c\u7269\uff1f\u4e0d\u662f\u5df2\u7ecf\u6709\u957f\u7b1b\u4e86\u5417\uff1f\u201d</p><p>\u201c\u53ef\u662f\u6211\u4eec\u662f\u53bb\u5e74\u4e5d\u6708\u5e95\u79bb\u5f00\u9634\u9b42\u57ce\u7684\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u59d0\u59d0\u5df2\u7ecf\u548c\u5373\u5c06\u5ea6\u8fc7\u4e24\u4e2a\u751f\u65e5\uff0c\u6240\u4ee5\u5f53\u7136\u9700\u8981\u4e24\u4efd\u751f\u65e5\u793c\u7269\u554a\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u957f\u7b1b\u662f\u4f5c\u4e3a\u4eca\u5e74\u7684\u793c\u7269\uff0c\u8fd9\u526f\u9879\u94fe\u662f\u8865\u9001\u53bb\u5e74\u7684\u3002\u201d</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u771f\u5e78\u798f\u5462\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u9690\u9690\u6709\u4e9b\u5ac9\u5992\u7684\u53e3\u6c14\uff0c\u201c\u8fd9\u9879\u94fe\u4e0a\u9644\u9b54\u4e86\uff1f\u201d</p><p>\u201c\u6709\u4e9b\u5b9d\u77f3\u5df2\u7ecf\u9644\u9b54\u4e86\uff0c\u6709\u4e9b\u6211\u6b63\u5728\u5f04\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6309\u7167\u76ee\u524d\u7684\u8fdb\u5ea6\uff0c\u56de\u9634\u9b42\u57ce\u4e4b\u524d\uff0c\u5e94\u8be5\u80fd\u5b8c\u6210\u3002\u201d</p><p>\u201c\u90fd\u6709\u4ec0\u4e48\u9b54\u6cd5\u6548\u679c\u554a\u3002\u201d</p><p>\u743c\u6069\u5174\u81f4\u6b63\u9ad8\uff0c\u89c1\u5979\u95ee\u8d77\uff0c\u4fbf\u62ff\u8d77\u9879\u94fe\uff0c\u6307\u70b9\u7740\u4e0a\u9762\u7684\u5b9d\u77f3\u9010\u4e00\u5411\u5979\u4ecb\u7ecd\uff0c\u201c\u8fd9\u91cc\u9762\u6709\u4e24\u9897\u9644\u7740\u795e\u672f\uff0c\u662f\u8bf7\u4f0a\u5361\u6c99\u57ce\u7684\u9996\u5e2d\u5927\u7267\u5e08\u94bb\u5203\u5148\u751f\u5e2e\u5fd9\u7684\uff0c\u6bd4\u5982\u8fd9\u679a\u94bb\u77f3\uff0c\u6bcf\u5929\u53ef\u4ee5\u6fc0\u53d1\u4e00\u6b21\u6cbb\u7597\u672f\uff0c\u739b\u7459\uff0c\u9644\u4e0a\u4e86\u2018\u5b89\u5b81\u5fc3\u795e\u2019\u3002\u6709\u4e24\u9897\u6ca1\u6709\u9644\u9b54\uff0c\u53ea\u662f\u501f\u52a9\u5b9d\u77f3\u672c\u8eab\u7684\u7279\u6027\uff0c\u50cf\u7425\u73c0\uff0c\u4f69\u6234\u5728\u8eab\u4e0a\u53ef\u4ee5\u63d0\u9ad8\u62b5\u6297\u75be\u75c5\u7684\u80fd\u529b\uff0c\u7ea2\u5b9d\u77f3\u53ef\u4ee5\u589e\u52a0\u597d\u8fd0\u6c14\u3002\u5269\u4e0b\u8fd9\u56db\u9897\uff0c\u6211\u6253\u7b97\u5206\u522b\u9644\u4e0a\u9690\u8eab\u3001\u955c\u5f71\u3001\u718a\u4e4b\u5fcd\u8010\u548c\u67ad\u4e4b\u6d1e\u5bdf\u6cd5\u672f\u3002\u76ee\u524d\u8fd8\u53ea\u5b8c\u6210\u4e86\u4e00\u4e2a\u9690\u8eab\u672f\u3002\u201d</p><p>\u9690\u8eab\u3001\u955c\u5f71\uff0c\u8fd9\u5f88\u597d\u7406\u89e3\uff0c\u90fd\u662f\u4f4e\u9636\u5374\u975e\u5e38\u5b9e\u7528\u7684\u9632\u8eab\u9b54\u6cd5\uff0c\u718a\u4e4b\u5fcd\u8010\u662f\u589e\u5f3a\u4f53\u8d28\u7684\uff0c\u73ca\u5609\u8eab\u4f53\u6709\u4e9b\u504f\u67d4\u5f31\uff0c\u4e5f\u6b63\u9002\u5408\u3002\u4f46\u67ad\u4e4b\u6d1e\u5bdf\u2026\u2026\u8981\u5b83\u505a\u4ec0\u4e48\uff1f</p><p>\u201c\u67ad\u4e4b\u6d1e\u5bdf\u662f\u80fd\u589e\u5f3a\u4eba\u7684\u611f\u77e5\u80fd\u529b\u554a\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u5176\u4e2d\u4e00\u4e2a\u91cd\u8981\u4f5c\u7528\u5c31\u662f\u8ba9\u4eba\u8eab\u4f53\u66f4\u654f\u611f\u2026\u2026\u201d</p><p>\u8299\u857e\u72c4\u6014\u4e86\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u8138\u4e0a\u987f\u65f6\u4e00\u7247\u7ea2\u6655\u6cdb\u8d77\uff0c\u8bf4\u4e0d\u51fa\u7684\u5a07\u7f9e\u53ef\u7231\u3002\u201c\u90a3\u8fd8\u6709\u8fd9\u56db\u9897\u9ed1\u73cd\u73e0\u5462\uff1f\u201d\u5979\u63a9\u9970\u7740\uff0c\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u5b83\u4eec\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u5b83\u4eec\u554a\uff0c\u6211\u51c6\u5907\u5728\u4e0a\u9762\u9644\u4e00\u4e9b\u5c0f\u620f\u6cd5\uff0c\u201d\u743c\u6069\u5f88\u8ba4\u771f\u5730\u8bf4\uff0c\u201c\u7b2c\u4e00\u9897\u6253\u7b97\u9644\u4e0a\u7535\u51fb\u6548\u679c\uff0c\u7b2c\u4e8c\u9897\u9644\u4e0a\u707c\u70ed\u6548\u679c\uff0c\u7b2c\u4e09\u9897\u9644\u4e0a\u5bd2\u51b0\u6548\u679c\uff0c\u90fd\u5f88\u8f7b\u5fae\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u4f24\u5230\u4eba\uff0c\u6700\u540e\u4e00\u9897\uff0c\u6211\u7ed9\u5b83\u53d6\u540d\u53eb\u5c01\u95e8\u73cd\u73e0\uff0c\u51c6\u5907\u8ba9\u5b83\u80fd\u591f\u81a8\u80c0\u548c\u6536\u7f29\u3002\u53e6\u5916\u6bcf\u9897\u73cd\u73e0\u4e0a\u8fd8\u8981\u989d\u5916\u6052\u5b9a\u9707\u98a4\u672f\uff0c\u53ef\u4ee5\u9ad8\u9891\u7387\u9707\u52a8\u3002\u6240\u6709\u8fd9\u4e9b\u6548\u679c\uff0c\u90fd\u53ea\u8981\u4e00\u4e2a\u53e3\u4ee4\u5c31\u80fd\u542f\u52a8\uff0c\u5f88\u65b9\u4fbf\u7684\uff0c\u800c\u4e14\u6bcf\u5929\u4f7f\u7528\u7684\u6b21\u6570\u4e0d\u9650\uff0c\u4e0d\u50cf\u524d\u9762\u90a3\u4e9b\u5b9d\u77f3\uff0c\u9b54\u6cd5\u6548\u679c\u4e00\u5929\u53ea\u6709\u4e00\u6b21\u3002\u5bf9\u4e86\uff0c\u8fd9\u6839\u94f6\u4e1d\u7ec6\u5e26\u4e0a\uff0c\u6211\u6253\u7b97\u6052\u5b9a\u6d3b\u5316\u7ef3\u6cd5\u672f\uff0c\u514d\u5f97\u4e07\u4e00\u9677\u5165\u4f53\u5185\u5f04\u4e0d\u51fa\u6765\uff0c\u6bd4\u8f83\u9ebb\u70e6\u3002\u201d</p><p>\u5c11\u5973\u7684\u8138\u66f4\u7ea2\u4e86\uff0c\u548c\u743c\u6069\u5728\u4e00\u8d77\u4e45\u4e86\uff0c\u5979\u663e\u7136\u542c\u660e\u767d\u4e86\u5176\u4e2d\u7684\u771f\u5b9e\u542b\u4e49\u3002\u4ec0\u4e48\u9879\u94fe\u4f1a\u9700\u8981\u7535\u51fb\u707c\u70ed\u6548\u679c\uff0c\u8fd8\u4f1a\u201c\u9677\u5165\u4f53\u5185\u201d\u2026\u2026</p><p>\u201c\u4f60\u2026\u2026\u4f60\u5c31\u6253\u7b97\u8fd9\u4e48\u6b3a\u8d1f\u73ca\u5609\u59d0\u59d0\u554a\uff1f\u201d\u5979\u6084\u58f0\u95ee\u3002</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u70b9\u5934\uff0c\u201c\u662f\u4e0d\u662f\u89c9\u5f97\u5f88\u6709\u521b\u610f\uff1f\u201d</p><p>\u201c\u2026\u2026\u662f\u5f88\u6709\u521b\u610f\uff1f\u201d</p><p>\u201c\u90a3\u4f60\u8981\u4e0d\u8981\u6765\u5c1d\u8bd5\u4e00\u4e0b\u5462\uff1f\u201d</p><p>\u8299\u857e\u72c4\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u4e0d\u8981\uff01\u201d</p><p>\u201c\u771f\u7684\u4e0d\u8981\uff1f\u201d</p><p>\u201c\u4e0d\u8981\u4e0d\u8981\uff0c\u201d\u5c11\u5973\u8fde\u8fde\u63a8\u8131\uff0c\u201c\u6211\u6015\u3002\u201d</p><p>\u201c\u6015\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u53cd\u6b63\u2026\u2026\u53cd\u6b63\u5c31\u662f\u5bb3\u6015\u561b\uff0c\u201d\u8299\u857e\u72c4\u6446\u624b\uff0c\u201c\u4f60\u8fd8\u662f\u627e\u522b\u4eba\u597d\u4e86\uff0c\u838e\u73de\u514b\u5c31\u53ef\u4ee5\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u9017\u4f60\u5566\uff0c\u73b0\u5728\u90fd\u8fd8\u6ca1\u505a\u5b8c\u5462\uff0c\u4f60\u60f3\u5c1d\u8bd5\u4e5f\u4e0d\u884c\u3002\u4e0d\u8fc7\u4ee5\u540e\u6709\u65f6\u95f4\u7684\u8bdd\uff0c\u6211\u4e13\u95e8\u4e3a\u4f60\u505a\u4e00\u4e2a\uff0c\u600e\u4e48\u6837\uff1f\u201d</p><p>\u5c11\u5973\u7684\u53cc\u988a\u5df2\u7ecf\u6ee1\u662f\u7ea2\u971e\uff0c\u4eff\u4f5b\u706b\u70e7\u4e00\u822c\uff0c\u201c\u8fd8\u662f\u4e0d\u8981\u5566\u3002\u201d\u5979\u4f4e\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u7b11\u7740\u6478\u6478\u5979\u7684\u8138\uff0c\u201c\u5973\u5b69\u5b50\u8bf4\u4e0d\u8981\uff0c\u610f\u601d\u5176\u5b9e\u5c31\u662f\u8981\uff0c\u662f\u8fd9\u4e2a\u9053\u7406\u5bf9\u5427\uff0c\u201d\u4ed6\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u6211\u660e\u767d\u4e86\u3002\u201d</p><p>\u201c\u8ba8\u538c\uff01\u201d</p><p>\u5176\u5b9e\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u4e00\u4ef6\u65b0\u4ea7\u54c1\u51fa\u5382\u4e4b\u524d\uff0c\u662f\u5e94\u8be5\u8981\u8fdb\u884c\u8c03\u8bd5\u68c0\u9a8c\u7684\uff0c\u8fd9\u6837\u624d\u80fd\u53ca\u65f6\u53d1\u73b0\u4e0d\u8db3\uff0c\u6539\u8fdb\u5b8c\u5584\uff0c\u505a\u5f97\u66f4\u597d\uff0c\u8fd9\u526f\u9879\u94fe\u867d\u7136\u8fd8\u53ea\u662f\u4e2a\u534a\u6210\u54c1\uff0c\u4f46\u5148\u62ff\u6765\u8fdb\u884c\u5c40\u90e8\u8bd5\u9a8c\uff0c\u4e5f\u662f\u6ca1\u6709\u95ee\u9898\u7684\u3002\u8299\u857e\u72c4\u5c31\u7b97\u4e0d\u80af\u732e\u8eab\u5f53\u201c\u8bd5\u9a8c\u4f53\u201d\uff0c\u743c\u6069\u4e5f\u53ef\u4ee5\u53bb\u627e\u838e\u73de\u514b\u6216\u8005\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5979\u4eec\u80af\u5b9a\u4f1a\u4e50\u610f\u8bd5\u7528\uff0c\u800c\u4e14\u80fd\u591f\u63d0\u4f9b\u5404\u79cd\u6709\u4ef7\u503c\u7684\u53cd\u9988\u610f\u89c1\uff0c\u6bd5\u7adf\u9b45\u9b54\u548c\u5353\u5c14\u5728\u8fd9\u65b9\u9762\u90fd\u7b97\u662f\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4f5c\u4e3a\u201c\u5185\u6d4b\u4eba\u5458\u201d\u6bd4\u8299\u857e\u72c4\u66f4\u9002\u5408\u3002\u4f46\u8003\u8651\u5230\u73ca\u5609\u7684\u611f\u53d7\uff0c\u743c\u6069\u8fd8\u662f\u6253\u6d88\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002</p><p>\u8fd9\u4e0d\u662f\u666e\u901a\u7684\u4e1c\u897f\uff0c\u662f\u9001\u7ed9\u59d0\u59d0\u7684\u751f\u65e5\u793c\u7269\uff0c\u800c\u4e14\u2026\u2026\u662f\u6781\u5176\u201c\u79c1\u5bc6\u201d\u7684\u793c\u7269\u3002\u5982\u679c\u8ba9\u4eba\u5148\u8bd5\u7528\u7684\u8bdd\uff0c\u5c31\u7b97\u5b9e\u8d28\u4e0a\u4e0d\u4f1a\u6709\u4efb\u4f55\u4e0d\u826f\u5f71\u54cd\uff0c\u4f46\u4ece\u5fc3\u7406\u610f\u4e49\u4e0a\u800c\u8a00\uff0c\u59d0\u59d0\u77e5\u9053\u4e86\u80af\u5b9a\u4f1a\u4e0d\u592a\u9ad8\u5174\uff0c\u5c31\u7b97\u9690\u7792\u4e0d\u8bf4\uff0c\u743c\u6069\u81ea\u5df1\u4e5f\u4f1a\u4e0d\u592a\u8212\u670d\u3002</p><p>\u7531\u6b64\u53ef\u89c1\uff0c\u539f\u88c5\u8fd9\u79cd\u6982\u5ff5\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u5bf9\u4ec0\u4e48\u4eba\u90fd\u5f88\u91cd\u8981\u2026\u2026</p><p>\u65f6\u95f4\u8fc7\u5f97\u5f88\u5feb\uff0c\u8f6c\u773c\u95f4\u4fbf\u5df2\u7ecf\u5230\u4e86\u743c\u6069\u7684\u751f\u65e5\uff0c\u6309\u7167\u8ba1\u5212\uff0c\u8fc7\u4e86\u4eca\u591c\uff0c\u660e\u5929\u4fbf\u8981\u542f\u7a0b\u4e0a\u8def\uff0c\u79bb\u5f00\u963f\u65af\u5361\u7279\u62c9\u3002\u4e0b\u5348\u4e94\u70b9\u949f\uff0c\u743c\u6069\u5728\u81ea\u5df1\u5367\u5ba4\u91cc\u57cb\u5934\u52aa\u529b\uff0c\u51c6\u5907\u4e3a\u9879\u94fe\u4e0a\u7684\u4e00\u679a\u661f\u7ea2\u77f3\u6ce8\u5165\u955c\u5f71\u672f\uff0c\u6b63\u8fdb\u884c\u5230\u4e86\u6700\u540e\u5173\u952e\u7684\u4e00\u6b65\uff0c\u773c\u770b\u5c31\u8981\u5927\u529f\u544a\u6210\uff0c\u201c\u7b03\u7b03\u201d\u7684\u6572\u95e8\u58f0\u7a81\u7136\u54cd\u8d77\u3002</p><p>\u867d\u7136\u5f88\u8f7b\uff0c\u4f46\u56e0\u4e3a\u5468\u56f4\u4e00\u7247\u5bc2\u9759\uff0c\u6240\u4ee5\u663e\u5f97\u683c\u5916\u6e05\u6670\u7a81\u5140\uff0c\u743c\u6069\u6b63\u81ea\u5168\u795e\u8d2f\u6ce8\uff0c\u88ab\u8fd9\u6572\u95e8\u58f0\u4e00\u6270\uff0c\u7565\u7565\u5206\u5fc3\uff0c\u52a8\u4f5c\u4e5f\u5c31\u7a0d\u5fae\u6162\u4e86\u90a3\u4e48\u56db\u5206\u4e4b\u4e00\u79d2\uff0c\u4fbf\u542c\u5f97\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u90a3\u679a\u661f\u7ea2\u77f3\u50cf\u662f\u88ab\u4e00\u67c4\u65e0\u5f62\u91cd\u9524\u6572\u4e0b\u4f3c\u7684\uff0c\u81ea\u4e2d\u5fc3\u5f80\u56db\u5468\u8513\u5ef6\u51fa\u65e0\u6570\u7ec6\u5c0f\u7684\u88c2\u75d5\uff0c\u6084\u7136\u7834\u788e\uff0c\u6210\u4e86\u4e00\u5806\u788e\u6676\u3002</p><p>\u5b9d\u77f3\u788e\u88c2\u7684\u540c\u65f6\uff0c\u743c\u6069\u8111\u4e2d\u4e5f\u662f\u4e00\u6ce2\u7a81\u5982\u5176\u6765\u7684\u5267\u70c8\u523a\u75db\uff0c\u773c\u524d\u9635\u9635\u53d1\u9ed1\uff0c\u8fc7\u4e86\u56db\u4e94\u79d2\u949f\u624d\u6162\u6162\u6062\u590d\u8fc7\u6765\uff0c\u53ea\u89c9\u80cc\u4e0a\u5df2\u7ecf\u88ab\u51b7\u6c57\u6d78\u900f\u3002\u4ed6\u6df1\u5438\u53e3\u6c14\uff0c\u76b1\u8d77\u7709\u5934\uff0c\u5fc3\u4e2d\u9887\u89c9\u4e0d\u5feb\uff0c\u8fd9\u79cd\u4e3a\u7269\u54c1\u9644\u9b54\u7684\u5de5\u4f5c\uff0c\u96be\u5ea6\u9887\u9ad8\uff0c\u6b65\u9aa4\u7e41\u7410\uff0c\u9700\u8981\u70bc\u91d1\u5e08\u9ad8\u5ea6\u96c6\u4e2d\u7cbe\u795e\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u5c31\u529f\u4e8f\u4e00\u7bd1\uff0c\u767d\u767d\u6d6a\u8d39\u4e86\u6750\u6599\u3002\u867d\u8bf4\u77ee\u4eba\u591f\u4e49\u6c14\uff0c\u4e34\u522b\u8d60\u9001\u7684\u5b9d\u77f3\u4e0d\u5c11\uff0c\u4e00\u9897\u505a\u5e9f\u4e86\uff0c\u518d\u6362\u4e00\u9897\u5c31\u662f\uff0c\u4f46\u603b\u662f\u53c8\u8981\u4ece\u5934\u518d\u6765\uff0c\u6d6a\u8d39\u65f6\u95f4\u7cbe\u529b\uff0c\u9ebb\u70e6\u5f97\u5f88\u3002\u4e3a\u4e86\u907f\u514d\u6253\u6270\uff0c\u4ed6\u65e9\u5c31\u5df2\u7ecf\u544a\u8bc9\u8fc7\u540c\u4f34\uff0c\u6ca1\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\u60c5\u4e0d\u8981\u6765\u627e\u4ed6\uff0c\u5c31\u8fde\u8299\u857e\u72c4\u4e5f\u90fd\u53ea\u662f\u591c\u91cc\u8fc7\u6765\u201c\u4f8d\u5bdd\u201d\uff0c\u5176\u4f59\u65f6\u95f4\u81ea\u5df1\u6d3b\u52a8\uff0c\u5927\u591a\u662f\u548c\u8299\u8389\u5a05\u51fa\u53bb\u6e38\u89c8\u57ce\u5e02\uff0c\u901b\u8857\u8d2d\u7269\u3002</p><p>\u201c\u8fdb\u6765\u3002\u201d\u4ed6\u6709\u4e9b\u4e0d\u9ad8\u5174\u5730\u8bf4\u3002</p><p>\u623f\u95e8\u88ab\u8f7b\u8f7b\u63a8\u5f00\u4e86\uff0c\u4e00\u4e2a\u4eba\u5f71\u968f\u7740\u8f7b\u76c8\u7684\u811a\u6b65\u58f0\u8d70\u5230\u8fd1\u524d\uff0c\u743c\u6069\u5c06\u684c\u4e0a\u7684\u5b9d\u77f3\u788e\u6e23\u626b\u8fdb\u5783\u573e\u6876\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u5012\u662f\u6709\u4e9b\u60ca\u8bb6\u3002\u4ed6\u672c\u4ee5\u4e3a\u8fdb\u6765\u7684\u662f\u838e\u73de\u514b\uff0c\u4f46\u5374\u6ca1\u60f3\u5230\u4f1a\u662f\u8299\u857e\u72c4\u3002\u5c0f\u5973\u5b69\u4e00\u5411\u4e56\u5de7\u542c\u8bdd\uff0c\u600e\u4e48\u4f1a\u660e\u77e5\u9053\u4ed6\u6709\u4e8b\u8fd8\u8fdb\u6765\u6253\u6270\uff1f</p><p>\u96be\u9053\u53d1\u751f\u4ec0\u4e48\u8981\u7d27\u4e8b\u60c5\u4e86\uff1f\u5374\u4e5f\u4e0d\u50cf\uff0c\u56e0\u4e3a\u8299\u857e\u72c4\u5e76\u65e0\u4e1d\u6beb\u7d27\u5f20\u614c\u4e71\u7684\u795e\u60c5\uff0c\u4e00\u5982\u5e73\u5e38\uff0c\u751a\u81f3\u611f\u89c9\u8fd8\u591a\u4e86\u51e0\u5206\u6de1\u5b9a\u4ece\u5bb9\u7684\u6c14\u8d28\u3002\u5979\u50cf\u662f\u521a\u521a\u6d17\u6d74\u51fa\u6765\uff0c\u4e4c\u9ed1\u957f\u53d1\u5782\u80a9\uff0c\u9690\u7ea6\u6709\u4e9b\u6e7f\u6f09\u6f09\u7684\u6c34\u6c14\uff0c\u8116\u5b50\u4e0a\u6302\u7740\u743c\u6069\u4e0a\u6b21\u9001\u7684\u90a3\u679a\u661f\u84dd\u77f3\u62a4\u8eab\u7b26\uff0c\u7a7f\u7740\u6de1\u7d2b\u8272\u8fde\u8863\u88d9\uff0c\u8fd9\u662f\u5979\u6700\u559c\u6b22\u7684\u4e00\u4ef6\u8863\u670d\uff0c\u9732\u51fa\u4fee\u957f\u7389\u817f\uff0c\u811a\u4e0a\u662f\u4e00\u53cc\u767d\u8272\u51c9\u978b\uff0c\u7b80\u7b80\u5355\u5355\uff0c\u6ca1\u6709\u4ec0\u4e48\u82b1\u9970\uff0c\u975e\u5e38\u7684\u6587\u9759\u5a34\u96c5\u3002\u5c11\u5973\u8d70\u5230\u743c\u6069\u8eab\u8fb9\uff0c\u9759\u9759\u5730\u770b\u7740\u4ed6\uff0c\u4e5f\u6ca1\u8bf4\u8bdd\uff0c\u773c\u4e2d\u795e\u8272\u4f3c\u4e4e\u6709\u4e9b\u5947\u602a\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u8299\u857e\u72c4\uff1f\u201d\u743c\u6069\u539f\u672c\u4e0d\u592a\u9ad8\u5174\uff0c\u89c1\u662f\u5979\u4e5f\u5c31\u7f62\u4e86\uff0c\u91cd\u65b0\u53c8\u4ece\u5b9d\u77f3\u888b\u4e2d\u53d6\u51fa\u4e00\u9897\u96d5\u7422\u597d\u7684\u661f\u7ea2\u77f3\u6765\uff0c\u653e\u5728\u684c\u4e0a\u51c6\u5907\u52a0\u5de5\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u4e8b\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5c31\u662f\u60f3\u4f60\u966a\u966a\u6211\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u8f6c\u8fc7\u6905\u5b50\u4f38\u81c2\u63fd\u7740\u5979\u7684\u7ea4\u8170\uff0c\u5c06\u5979\u62b1\u5230\u6000\u91cc\uff0c\u5750\u5728\u81ea\u5df1\u817f\u4e0a\uff0c\u201c\u4e0d\u662f\u8bf4\u597d\u4e86\uff0c\u665a\u4e0a\u4e5d\u70b9\u4ee5\u540e\u90fd\u966a\u4f60\u4e48\uff0c\u8fd9\u8fd8\u6ca1\u5230\u65f6\u95f4\u5462\u3002\u201d</p><p>\u201c\u6211\u60f3\u4f60\u561b\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4e00\u523b\u90fd\u7b49\u4e0d\u53ca\u3002\u201d</p><p>\u867d\u7136\u5728\u6492\u5a07\uff0c\u8bed\u6c14\u5374\u662f\u6709\u4e9b\u6de1\u6de1\u7684\uff0c\u751a\u81f3\u9690\u7ea6\u6709\u4e00\u4e1d\u51b0\u51b7\u610f\u5473\u3002\u743c\u6069\u5fd9\u4e86\u4e00\u5929\uff0c\u6b63\u81ea\u5934\u6655\u8111\u6da8\uff0c\u4e0d\u662f\u5f88\u6e05\u9192\uff0c\u4e5f\u6ca1\u5728\u610f\u3002\u521a\u624d\u7684\u90a3\u6b21\u6ce8\u5165\u6cd5\u672f\u5931\u8d25\uff0c\u4e0d\u4ec5\u4ec5\u662f\u6bc1\u6389\u4e86\u4e00\u9897\u73cd\u8d35\u5b9d\u77f3\uff0c\u800c\u4e14\u4ee4\u4ed6\u8fd9\u4e2a\u70bc\u91d1\u5e08\u672c\u4eba\u4e5f\u53d7\u5230\u53cd\u566c\u5f71\u54cd\uff0c\u611f\u89c9\u7cbe\u529b\u601d\u8651\u90fd\u6563\u4e71\u5f00\u53bb\uff0c\u65e0\u6cd5\u518d\u96c6\u4e2d\u8d77\u6765\uff0c\u5c31\u7b97\u52c9\u5f3a\u7ee7\u7eed\u7684\u8bdd\uff0c\u6210\u529f\u7387\u4e5f\u662f\u4e0d\u9ad8\u3002\u201c\u597d\u597d\uff0c\u201d\u4ed6\u7b11\u4e86\u7b11\uff0c\u201c\u966a\u4f60\u2026\u2026\u5514\uff0c\u4e94\u70b9\u949f\u4e86\u554a\uff0c\u4e5f\u5feb\u5230\u665a\u9910\u65f6\u95f4\u4e86\uff0c\u8981\u4e0d\u8981\u5148\u4e0b\u53bb\u2026\u2026\u201d</p><p>\u201c\u4e0d\u8981\uff0c\u201d\u5c11\u5973\u6447\u5934\uff0c\u201c\u6211\u8fd8\u4e0d\u997f\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u6709\u70b9\u997f\u4e86\u554a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u5403\u6211\u597d\u4e86\u3002\u201d</p><p>\u743c\u6069\u634f\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u665a\u4e0a\u518d\u5403\u4f60\uff0c\u5230\u65f6\u5019\u4fdd\u8bc1\u8ba9\u4f60\u6c42\u9976\u90fd\u6765\u4e0d\u53ca\u3002\u6211\u4eec\u5148\u53bb\u5403\u665a\u9910\uff0c\u7136\u540e\u51fa\u53bb\u901b\u8857\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u4e0d\u597d\uff0c\u201d\u5c11\u5973\u6447\u5934\uff0c\u201c\u8299\u8389\u5a05\u8ba9\u6211\u4eca\u665a\u966a\u5979\u7761\uff0c\u6211\u7b54\u5e94\u4e86\u3002\u6240\u4ee5\u6211\u73b0\u5728\u5c31\u8981\u2026\u2026\u201d</p><p>\u743c\u6069\u5931\u7b11\uff0c\u96be\u602a\u5979\u8fd9\u4e2a\u65f6\u5019\u53bb\u6d17\u6fa1\u6362\u8863\u670d\uff0c\u539f\u6765\u662f\u4e3a\u8fd9\u4e2a\u3002\u8299\u857e\u72c4\u7231\u6d01\uff0c\u53ea\u8981\u6709\u6761\u4ef6\uff0c\u6bcf\u6b21\u6b22\u597d\u524d\u90fd\u5b9a\u8981\u4ed4\u7ec6\u6c90\u6d74\uff0c\u6e05\u6d01\u8eab\u4f53\uff0c\u5df2\u7136\u6210\u4e86\u4e60\u60ef\u3002\u201c\u73b0\u5728\u5c31\u8981\u554a\uff0c\u201d\u743c\u6069\u8c03\u7b11\uff0c\u201c\u4e5f\u6ca1\u95ee\u9898\uff0c\u4e0d\u8fc7\u6709\u4e2a\u6761\u4ef6\u3002\u4f60\u8bf4\u7ed9\u6211\u51c6\u5907\u4e86\u751f\u65e5\u793c\u7269\u7684\uff0c\u62ff\u51fa\u6765\u770b\u770b\uff0c\u5982\u679c\u8ba9\u6211\u6ee1\u610f\u4e86\uff0c\u90a3\u6211\u5c31\u8ba9\u4f60\u5fc3\u6ee1\u610f\u8db3\u3002\u201d</p><p>\u201c\u751f\u65e5\u793c\u7269\u5df2\u7ecf\u6446\u5728\u4f60\u9762\u524d\u4e86\u554a\uff0c\u5c31\u7b49\u4f60\u81ea\u5df1\u62c6\u5305\u88c5\u9a8c\u8d27\u4e86\u3002\u201d</p><p>\u201c\u4f60\uff1f\u201d\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u4e0d\u884c\uff0c\u8fd9\u4e2a\u4e0d\u80fd\u7b97\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u7b97\uff1f\u201d</p><p>\u201c\u4f60\u672c\u6765\u5c31\u5df2\u7ecf\u6211\u7684\u4eba\u4e86\u561b\uff0c\u201d\u743c\u6069\u7406\u6240\u5f53\u7136\u5730\u8bf4\uff0c\u201c\u600e\u4e48\u80fd\u62ff\u5c5e\u4e8e\u6211\u7684\u4e1c\u897f\u6765\u5f53\u793c\u7269\uff0c\u53c8\u53cd\u8fc7\u6765\u9001\u7ed9\u6211\u5462\u3002\u201d</p><p>\u5c11\u5973\u4e5f\u8f7b\u8f7b\u7b11\u8d77\u6765\u3002</p><p>\u201c\u6211\u8fd8\u6ca1\u6709\u5c5e\u4e8e\u4f60\u5462\uff0c\u81f3\u5c11\u8fd8\u6ca1\u6709\u5b8c\u5168\u90fd\u7ed9\u4f60\uff0c\u201d\u5979\u5a07\u58f0\u8bf4\uff0c\u8138\u4e0a\u795e\u60c5\u5a9a\u6001\u65e0\u9650\uff0c\u201c\u8fd8\u6709\u4e00\u5904\u5730\u65b9\uff0c\u6ca1\u8ba9\u4f60\u54c1\u5c1d\u8fc7\u5462\u3002\u201d</p><p>\u8299\u857e\u72c4\u6240\u8bf4\u6ca1\u9519\uff0c\u5979\u867d\u7136\u5728\u521d\u8bc6\u65f6\u5c31\u5df2\u7ecf\u628a\u5904\u5b50\u4e4b\u8eab\u732e\u51fa\uff0c\u8fd9\u4e24\u5e74\u6765\u548c\u743c\u6069\u5728\u4e00\u8d77\uff0c\u4ec0\u4e48\u82b1\u6837\u90fd\u8bd5\u8fc7\uff0c\u4efb\u4ed6\u7d22\u9700\u54c1\u5c1d\uff0c\u4f46\u8eab\u4e0a\u786e\u5b9e\u8fd8\u7559\u6709\u4e00\u5757\u6700\u540e\u7684\u5904\u5973\u5730\u6ca1\u88ab\u5f00\u53d1\u3002\u743c\u6069\u5012\u4e5f\u5076\u5c14\u8d77\u8fc7\u5ff5\u5934\uff0c\u4f46\u4e00\u76f4\u4e5f\u6ca1\u592a\u653e\u5728\u5fc3\u4e0a\uff0c\u5f80\u5f80\u5c31\u7ed9\u5fd8\u4e86\u3002\u5982\u4eca\u65e2\u7136\u5c11\u5973\u4e3b\u52a8\u6295\u6000\u9001\u62b1\uff0c\u4f5c\u4e3a\u751f\u65e5\u793c\u7269\u5949\u4e0a\uff0c\u82e5\u662f\u4e0d\u6536\u7684\u8bdd\uff0c\u90a3\u5c31\u6789\u79f0\u8272\u72fc\uff0c\u4ee5\u540e\u90fd\u53ef\u4ee5\u5230\u5904\u5411\u4eba\u5439\u5618\u81ea\u5df1\u662f\u4e2a\u6b63\u4eba\u541b\u5b50\u4e86\u3002</p><p>\u4ed6\u4eb2\u543b\u7740\u8299\u857e\u72c4\u7684\u5634\u5507\uff0c\u53cc\u624b\u5728\u5979\u8eab\u4f53\u4e0a\u6e38\u8d70\u7740\uff0c\u6162\u6162\u8c03\u60c5\uff0c\u5c11\u5973\u4e56\u4e56\u8fce\u5408\u3002\u743c\u6069\u611f\u89c9\u5979\u4f3c\u4e4e\u52a8\u4f5c\u6709\u4e9b\u50f5\u786c\uff0c\u4e0d\u5982\u5e73\u65f6\u81ea\u5728\uff0c\u4f30\u8ba1\u662f\u60f3\u5230\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\uff0c\u7ec8\u7a76\u5fc3\u91cc\u7d27\u5f20\u5bb3\u6015\uff0c\u4e5f\u6ca1\u6709\u5f88\u5728\u610f\u3002\u201c\u653e\u5fc3\uff0c\u201d\u4ed6\u67d4\u58f0\u8bf4\uff0c\u201c\u522b\u5bb3\u6015\uff0c\u4e0d\u4f1a\u5f04\u75bc\u4f60\u7684\u3002\u201d</p><p>\u8fd9\u81ea\u7136\u662f\u9b3c\u626f\uff0c\u743c\u6069\u56fa\u7136\u4e0d\u7b97\u7231\u597d\u6b64\u9053\uff0c\u4e5f\u6709\u8fc7\u51e0\u6b21\u7ecf\u9a8c\uff0c\u4ece\u6ca1\u89c1\u8fc7\u7b2c\u4e00\u6b21\u4f1a\u4e0d\u75bc\u7684\u3002\u5c31\u7b97\u662f\u6885\u83f2\u65af\u90a3\u6837\u575a\u5f3a\u7684\u5973\u5b69\u5b50\uff0c\u4f9d\u65e7\u90fd\u4f1a\u75bc\u5f97\u54ed\u51fa\u6765\u3002\u53ea\u4e0d\u8fc7\u6709\u4e9b\u8bdd\u5c31\u7b97\u660e\u77e5\u9053\u662f\u9b3c\u626f\u4e5f\u8981\u8fd9\u4e48\u8bf4\uff0c\u53cd\u6b63\u5bf9\u65b9\u7231\u542c\u3002</p><p>\u8299\u857e\u72c4\u55ef\u4e86\u4e00\u58f0\uff0c\u8eab\u4f53\u6e10\u6e10\u6709\u4e9b\u653e\u677e\u3002\u743c\u6069\u8d81\u673a\u52a0\u5feb\u901f\u5ea6\uff0c\u56e0\u4e3a\u5f7c\u6b64\u592a\u8fc7\u719f\u6089\uff0c\u8f7b\u6613\u5c31\u80fd\u638c\u63e1\u654f\u611f\u70b9\uff0c\u6ca1\u8fc7\u7247\u523b\u5c11\u5973\u4fbf\u5df2\u7ecf\u5a07\u5598\u5401\u5401\uff0c\u8138\u988a\u6f6e\u7ea2\uff0c\u4e00\u53cc\u661f\u7738\u534a\u7741\u534a\u95ed\uff0c\u6c34\u6c14\u76c8\u76c8\uff0c\u8fd9\u662f\u52a8\u60c5\u7684\u5f81\u5146\u4e86\u3002\u743c\u6069\u7684\u624b\u8d81\u673a\u6084\u6084\u63a2\u5165\u8299\u857e\u72c4\u7684\u88d9\u5e95\uff0c\u6cbf\u7740\u5927\u817f\u4e0a\u79fb\uff0c\u4e00\u70b9\u70b9\u5c06\u5979\u7684\u4e1d\u7ec7\u5185\u88e4\u892a\u4e86\u4e0b\u6765\uff0c\u5c11\u5973\u7a0d\u7a0d\u6297\u62d2\uff0c\u5373\u4fbf\u653e\u5f03\u62b5\u6297\u3002</p><p>\u201c\u522b\u9017\u6211\u5566\u3002\u201d\u5979\u4f4e\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u5c06\u5979\u62b1\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u5e8a\u8fb9\u653e\u4e0b\uff0c\u5148\u628a\u81ea\u5df1\u8863\u670d\u8131\u5149\uff0c\u63a5\u7740\u5c0f\u5fc3\u66ff\u5979\u892a\u4e0b\u51c9\u978b\u548c\u8fde\u8863\u88d9\uff0c\u53c8\u5c06\u7389\u9888\u4e0a\u7684\u661f\u84dd\u77f3\u62a4\u8eab\u7b26\u53d6\u4e0b\uff0c\u5c11\u5973\u7684\u5a07\u8eaf\u4fbf\u8fd1\u4e4e\u8d64\u88f8\uff0c\u53ea\u5269\u4e0b\u4e0a\u8eab\u4e00\u4ef6\u9ed1\u8272\u6587\u80f8\uff0c\u662f\u4ece\u524d\u9762\u5f00\u6263\u7684\u3002\u743c\u6069\u521a\u521a\u4f38\u624b\u5378\u5f00\uff0c\u4e24\u56e2\u7855\u5927\u9999\u8f6f\u7684\u4e73\u8089\u4fbf\u5f39\u4e86\u51fa\u6765\uff0c\u9a84\u50b2\u5730\u633a\u7acb\u5728\u7a7a\u6c14\u4e2d\u3002\u201c\u54a6\uff0c\u201d\u4ed6\u5fae\u89c9\u5947\u602a\uff0c\u201c\u600e\u4e48\u611f\u89c9\u53d8\u5927\u4e86\u4e00\u53f7\u4f3c\u7684\uff1f\u201d</p><p>\u8fd8\u6ca1\u7b49\u4ed6\u7ec6\u60f3\u8fd9\u4e2a\u95ee\u9898\uff0c\u5c11\u5973\u5df2\u7ecf\u73af\u81c2\u6402\u4f4f\u4ed6\u7684\u8116\u9888\uff0c\u6a31\u5507\u5fae\u5f20\uff0c\u547c\u6c14\u5982\u5170\uff0c\u201c\u543b\u6211\u3002\u201d\u5979\u5a9a\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u5c06\u8111\u4e2d\u90a3\u70b9\u7591\u60d1\u629b\u4e4b\u4e91\u5916\uff0c\u4f4e\u5934\u543b\u4e0a\u5c11\u5973\u7684\u6a31\u5507\uff0c\u611f\u89c9\u5979\u4eca\u5929\u6d82\u7684\u5507\u818f\u662f\u6a58\u5b50\u5473\u9053\uff0c\u548c\u5e73\u5e38\u4e60\u60ef\u7528\u7684\u871c\u6843\u5473\u4e0d\u592a\u4e00\u6837\uff0c\u53ef\u80fd\u662f\u65b0\u4e70\u7684\u3002\u4e24\u4eba\u5507\u820c\u4ea4\u7f20\uff0c\u7f20\u7ef5\u534a\u664c\uff0c\u7ec8\u4e8e\u604b\u604b\u4e0d\u820d\u5730\u5206\u5f00\u3002\u5c11\u5973\u7741\u5f00\u773c\u770b\u7740\u4ed6\uff0c\u7136\u540e\u53c8\u9759\u9759\u95ed\u4e0a\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u50cf\u662f\u505a\u4e86\u4ec0\u4e48\u91cd\u8981\u51b3\u5b9a\u4e00\u822c\uff0c\u7ffb\u8fc7\u8eab\u8dea\u8d77\uff0c\u4e0a\u534a\u8eab\u4f0f\u5728\u5e8a\u5355\u4e0a\uff0c\u7389\u81c0\u7fd8\u8d77\uff0c\u7b49\u5f85\u7740\u4fb5\u5165\u3002</p><p>\u5979\u611f\u89c9\u5230\u7537\u6027\u7684\u5927\u624b\u6309\u4e0a\u81ea\u5df1\u7684\u8eab\u4f53\uff0c\u5c06\u4e30\u6ee1\u7684\u81c0\u4e18\u5206\u5f00\uff0c\u7d27\u63a5\u7740\u88ab\u4e00\u6839\u575a\u786c\u6eda\u70eb\u7684\u4e1c\u897f\u9876\u4f4f\u4e86\u3002\u5c11\u5973\u7d27\u95ed\u7740\u773c\u775b\uff0c\u54ac\u7740\u94f6\u7259\uff0c\u8eab\u4f53\u65e0\u6cd5\u6291\u5236\u5730\u8f7b\u5fae\u98a4\u6296\u7740\uff0c\u51c6\u5907\u8fce\u63a5\u90a3\u6495\u88c2\u7684\u5267\u75db\u3002\u7136\u800c\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5bf9\u65b9\u5728\u5979\u7684\u81c0\u95f4\u505c\u7559\u7247\u523b\uff0c\u7136\u540e\u5f80\u4e0b\u79fb\u53bb\u3002\u201c\u4f60\u2026\u2026\u5f04\u9519\u4e86\uff01\u201d\u5979\u5598\u606f\u7740\u63d0\u9192\uff0c\u201c\u4e0d\u662f\u90a3\u91cc\uff01\u201d</p><p>\u743c\u6069\u5728\u5979\u7684\u7f8e\u81c0\u4e0a\u8f7b\u8f7b\u62cd\u4e86\u4e00\u62cd\uff0c\u201c\u6ca1\u9519\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u592a\u7d27\u5f20\u4e86\uff0c\u5148\u50cf\u5e73\u5e38\u90a3\u6837\u505a\u4e00\u6b21\uff0c\u8ba9\u4f60\u653e\u677e\u4e0b\u6765\u3002\u201d</p><p>\u201c\u4e0d\u8981\u2026\u2026\u201d</p><p>\u5c11\u5973\u7126\u6025\u5730\u53cd\u5bf9\uff0c\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u4f46\u4e5f\u6ca1\u653e\u5728\u5fc3\u4e0a\u3002\u4ed6\u63e1\u7740\u8299\u857e\u72c4\u7684\u8170\u80a2\u5f00\u59cb\u524d\u8fdb\uff0c\u5c11\u5973\u8bd5\u56fe\u60f3\u8981\u9003\u8131\uff0c\u4f46\u5df2\u7ecf\u592a\u8fdf\u3002\u56e0\u4e3a\u4e24\u4eba\u4e4b\u95f4\u5df2\u7ecf\u6709\u8fc7\u65e0\u6570\u6b21\u6b22\u597d\u7ecf\u9a8c\uff0c\u743c\u6069\u4e5f\u6ca1\u6709\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u800c\u662f\u5f84\u76f4\u95ef\u5165\uff0c\u4f46\u4ed6\u7acb\u523b\u4fbf\u5bdf\u89c9\u51fa\u4e0d\u5bf9\uff0c\u5c11\u5973\u7684\u4f53\u5185\u7d27\u7a84\u5f97\u5f02\u4e4e\u5bfb\u5e38\uff0c\u7edd\u5bf9\u8fd8\u662f\u672a\u7ecf\u4eba\u4e8b\u7684\u5904\u5b50\uff0c\u800c\u4e14\u521a\u624d\u8fdb\u5165\u65f6\u50cf\u662f\u9047\u5230\u4e86\u4ec0\u4e48\u67d4\u8f6f\u969c\u788d\uff0c\u88ab\u81ea\u5df1\u76f4\u63a5\u6495\u5f00\u3002</p><p>\u4ed6\u60ca\u8bb6\u4e4b\u4e0b\uff0c\u614c\u5fd9\u9000\u51fa\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u6620\u5165\u773c\u5e18\u7684\u662f\u70b9\u70b9\u843d\u7ea2\uff0c\u5370\u5728\u5c11\u5973\u6d01\u767d\u7389\u80a1\u95f4\uff0c\u4eff\u4f5b\u96ea\u5730\u4e0a\u7684\u70b9\u70b9\u5bd2\u6885\uff0c\u6781\u5177\u7f8e\u611f\u3002</p><p>\u201c\u4f60\u2026\u2026\u8299\u8389\u5a05\uff1f\u201d</p></article>"}}}]);
