(window.webpackJsonp=window.webpackJsonp||[]).push([[405],{428:function(p){p.exports={chapterTitle:null,title:"\u7b2c405\u7ae0 \u518d\u8bae\u6807\u738b",content:"<article><h2>\u7b2c405\u7ae0 \u518d\u8bae\u6807\u738b</h2><p>\u3000\u3000\u82cf\u6d25\u4e1c\u5750\u4e0b\u6765\u5148\u6252\u4e86\u4e24\u53e3\u996d\u8fdb\u809a\u5b50\uff0c\u5f00\u4f1a\u5230\u73b0\u5728\uff0c\u771f\u6709\u4e9b\u997f\u4e86\uff0c\u5feb\u901f\u56bc\u4e86\u4e24\u53e3\uff0c\u90fd\u5dee\u70b9\u56bc\u5230\u820c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f00\u4f1a\u65f6\uff0c\u4e5f\u542c\u4e86\u4e00\u4e9b\u7262\u9a9a\uff0c\u5916\u9762\u7684\u5927\u73af\u5883\uff0c\u516c\u53f8\u5185\u90e8\u7684\u5c0f\u73af\u5883\uff0c\u90fd\u5b58\u5728\u4e00\u4e9b\u95ee\u9898\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u8425\u9500\u4e3a\u738b\u7684\u7ecf\u6d4e\u65f6\u4ee3\uff0c\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u7684\u6210\u529f\uff0c\u4e5f\u662f\u6700\u5927\u9650\u5ea6\u7684\u5f97\u76ca\u4e8e\u5f20\u606a\u9ad8\u77bb\u8fdc\u77a9\u7684\u5e02\u573a\u8425\u9500\u7b56\u7565\u3002\u5e02\u573a\u90e8\u5185\u90e8\u5458\u5de5\u7684\u81ea\u6211\u81a8\u80c0\u5c31\u662f\u5728\u6240\u96be\u514d\u7684\uff0c\u5c31\u662f\u5176\u4ed6\u90e8\u95e8\u7684\u5458\u5de5\u4e5f\u4f1a\u7406\u6240\u5f53\u7136\u7684\u62d4\u9ad8\u5e02\u573a\u90e8\u5728\u516c\u53f8\u5168\u5c40\u4e2d\u7684\u91cd\u8981\u6027\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5468\u6e38\u8c03\u53bb\u8d1f\u8d23\u65b0\u5149\u7eb8\u4e1a\u4e4b\u540e\uff0c\u8ba9\u6768\u4e91\u62c5\u4efb\u5e02\u573a\u90e8\u603b\u76d1\u4e00\u804c\uff0c\u8ba9\u8d1f\u8d23\u751f\u4ea7\u7684\u9c81\u5e86\u751f\u9876\u4e0a\u526f\u603b\u7684\u4f4d\u5b50\uff0c\u4ed6\u5b9e\u9645\u7684\u505a\u6cd5\u662f\u5c06\u5e02\u573a\u90e8\u653e\u5728\u76f8\u5bf9\u4e0d\u90a3\u4e48\u91cd\u8981\u7684\u4f4d\u7f6e\u4e0a\uff0c\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u52a0\u5267\u4e86\u67d0\u4e9b\u4eba\u5fc3\u91cc\u7684\u5931\u8861\u3002</p><p>\u3000\u3000\u68d2\u5b50\u4e5f\u4e0d\u662f\u968f\u4fbf\u5c31\u53ef\u4ee5\u6325\u5230\u8c01\u7684\u5c41\u80a1\u4e0a\uff0c\u5728\u5305\u53a2\u91cc\u804a\u4e86\u5f88\u4e45\uff0c\u5f20\u606a\u62ac\u624b\u770b\u4e86\u770b\u8868\uff0c\u5df2\u7ecf\u8fc7\u5341\u4e00\u70b9\u4e86\uff0c\u5218\u660e\u8f89\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u6253\u7535\u8bdd\u7ed9\u4ed6\uff0c\u591a\u5c11\u6709\u4e9b\u5931\u671b\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u518d\u7b49\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7709\u6708\u60ac\u5728\u7a7a\u4e2d\uff0c\u8f66\u7a97\u5916\u6708\u5149\u5982\u6d77\uff0c\u8fdb\u5165\u5341\u6708\uff0c\u624d\u662f\u6d77\u5dde\u5e02\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u79cb\u5b63\uff0c\u5341\u6708\u4e0b\u65ec\uff0c\u4f1a\u4e00\u9635\u79cb\u96e8\u4e00\u9635\u51c9\uff0c\u6b64\u65f6\u7684\u591c\u665a\u53ea\u662f\u6709\u4e9b\u6e05\u51c9\uff0c\u800c\u7edd\u65e0\u5bd2\u610f\u3002</p><p>\u3000\u3000\u8f66\u7a97\u6253\u5f00\uff0c\u5f20\u606a\u81c2\u8098\u9694\u7740\u8f66\u7a97\u4e0a\uff0c\u671b\u7740\u524d\u65b9\u5de8\u5927\u7684\u8c61\u5c71\u5c71\u4f53\uff0c\u5c71\u4e0a\u706f\u5149\u7a00\u5ed6\uff0c\u66f4\u50cf\u4e00\u5ea7\u5de8\u5927\u7684\u9634\u5f71\u77d7\u7acb\u5728\u524d\u9762\u3002</p><p>\u3000\u3000\u6768\u4e91\u81ea\u5df1\u5f00\u8f66\uff0c\u5728\u8c61\u5c71\u524d\u62d0\u5165\u901a\u6d77\u8def\u5f80\u4ed6\u5bb6\u7684\u89c2\u6d0b\u9547\u65b9\u5411\u53bb\u4e86\uff1b\u5f20\u606a\u8ba9\u82cf\u6d25\u4e1c\u5750\u4ed6\u7684\u8f66\u4e00\u8d77\u56de\u5e02\u533a\u3002</p><p>\u3000\u3000\u8c61\u5c71\u5357\u5d16\u9661\u5ced\uff0c\u60df\u4e00\u76d8\u65cb\u5230\u534a\u5c71\u8170\u8c61\u5c71\u68ee\u6797\u516c\u56ed\u5927\u95e8\u7684\u9053\u8def\u4ece\u8c61\u5c71\u4e1c\u5317\u89d2\u800c\u4e0a\uff0c\u90a3\u6761\u8def\u53e3\u5f00\u4e86\u51e0\u5bb6KTV\uff0c\u9713\u8679\u706f\u95ea\u70c1\uff0c\u4eba\u6c14\u9887\u65fa\u3002\u7ecf\u8fc7\u8c61\u5c71\u8def\u53e3\uff0c\u770b\u5230\u8def\u53e3\u4e00\u5bb6KTV\u95e8\u524d\u7ad9\u7740\u4e24\u4e2a\u4eba\u5728\u8bf4\u8bdd\uff0c\u624b\u91cc\u90fd\u5939\u7740\u9999\u70df\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u5e02\u573a\u90e8\u534e\u5357\u529e\u4e3b\u4efb\u66f9\u65b0\u9633\uff0c\u53e6\u4e00\u4e2a\u4eba\uff0c\u5f20\u606a\u4e5f\u518d\u719f\u6089\u4e0d\u8fc7\uff0c\u662f\u66fe\u7ecf\u5dee\u70b9\u5c06\u6d77\u88d5\u641e\u57ae\u3001\u5982\u4eca\u662f\u79d1\u738b\u7535\u5668\u7684\u603b\u7ecf\u7406\u8c22\u77bb\uff0c\u4e24\u4eba\u5927\u6982\u5728\u91cc\u9762\u9152\u559d\u591a\u4e86\uff0c\u51fa\u6765\u62bd\u8ddf\u70df\u900f\u900f\u6c14\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u82cf\u6d25\u4e1c\u770b\u4e86\u770b\u5f20\u606a\uff0c\u6709\u4e9b\u62c5\u5fe7\u4ed6\u7684\u53cd\u5e94\u3002</p><p>\u3000\u3000\u534e\u5357\u5730\u533a\u662f\u7231\u8fbe\u7535\u5b50\u6700\u91cd\u8981\u7684\u5e02\u573a\u533a\u57df\u4e4b\u4e00\uff0c\u7531\u4e8e\u534e\u5357\u7684\u7535\u5b50\u5de5\u4e1a\u76f8\u5bf9\u8f83\u53d1\u8fbe\uff0c\u5f71\u789f\u673a\u4ea7\u5546\u591a\u6570\u96c6\u4e2d\u5728\u82b1\u90fd\u3001\u4e2d\u5c71\u3001\u5e7f\u5dde\u7b49\u5730\uff0c\u4f7f\u5f97\u534e\u5357\u5e02\u573a\u7ade\u4e89\u6700\u4e3a\u6fc0\u70c8\u3002\u7231\u8fbe\u7535\u5b50\u5728\u534e\u5357\u7684\u5e02\u573a\u4efd\u989d\u4ece\u6765\u90fd\u6ca1\u80fd\u8d85\u8fc7\u5168\u56fd\u5e73\u5747\u6c34\u5e73\uff0c\u8fd9\u662f\u6700\u4e3b\u8981\u7684\u539f\u56e0\uff0c\u66f9\u65b0\u9633\u5012\u662f\u76f8\u5f53\u6709\u80fd\u529b\u7684\u4e00\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u8c01\u81ea\u4ee5\u4e3a\u638c\u63e1\u4e86\u6e20\u9053\u5c31\u638c\u63e1\u4e86\u516c\u53f8\u547d\u8109\uff0c\u6211\u7edd\u4e0d\u633d\u7559\u4ed6\u3002\u201d\u5f20\u606a\u5f88\u8f7b\u677e\u7684\u5410\u51fa\u4e00\u53e5\u8bdd\uff0c\u201c\u6b63\u6cf0\u4e0e\u9526\u57ce\u5408\u8d44\u6210\u7acb\u7684\u79d1\u738b\u7535\u5668\u8fd9\u4e24\u4e09\u4e2a\u6708\u7684\u529b\u5ea6\u76f8\u5f53\u5927\uff0c\u4ed6\u4eec\u60f3\u4ece\u7231\u8fbe\u7535\u5b50\u6316\u4e9b\u4ed6\u4eec\u6240\u9700\u8981\u7684\u4eba\uff0c\u4e5f\u662f\u6781\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u2026\u2026\u201d\u8fd9\u4e9b\u4e8b\uff0c\u65e9\u5c31\u662f\u80fd\u60f3\u5230\u7684\uff0c\u81ea\u7136\u6ca1\u4ec0\u4e48\u592a\u610f\u5916\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5085\u4fca\u5f00\u8f66\u6ca1\u6709\u51cf\u901f\uff0c\u8f6c\u773c\u5c31\u5c06\u90a3\u95f4KTV\u629b\u5728\u540e\u9762\uff0c\u82cf\u6d25\u4e1c\u4ecd\u6709\u4e9b\u4e0d\u653e\u5fc3\u7684\u56de\u5934\u5f20\u671b\uff0c\u66f9\u65b0\u9633\u4e0e\u8c22\u77bb\u5df2\u7ecf\u7ed9\u623f\u5b50\u6321\u4f4f\u770b\u4e0d\u89c1\u4e86\uff0c\u5fc3\u91cc\u60f3\u7740\u5c31\u66f9\u65b0\u9633\u4e00\u4e2a\u4eba\u5fc3\u601d\u4e0d\u7a33\u8fd8\u597d\u529e\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u6211\u4e4b\u524d\u5c31\u6709\u8003\u8651\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u5c06\u9500\u552e\u4e0e\u5e02\u573a\u62c6\u6210\u4e24\u4e2a\u90e8\u95e8\uff0c\u8ba9\u6768\u4e91\u4e13\u95e8\u8d1f\u8d23\u54c1\u724c\u8fd0\u8425\u8fd9\u4e00\u5757\uff0c\u9500\u552e\u90e8\u4e0b\u9762\u6309\u533a\u57df\u8bbe\u7acb\u9500\u552e\u516c\u53f8\uff1f\u201d\u82cf\u6d25\u4e1c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6210\u7acb\u9500\u552e\u516c\u53f8\u5b89\u629a\u5404\u8def\u8bf8\u4faf\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e0d\u5931\u4e3a\u5b89\u629a\u5404\u8def\u8bf8\u4faf\u7684\u597d\u529e\u6cd5\uff0c\u4f46\u662f\u6ca1\u6709\u5fc5\u8981\u2026\u2026\u201d\u5f20\u606a\u672c\u610f\u662f\u8981\u6253\u538b\u4e00\u4e0b\u5404\u5730\u8bf8\u4faf\uff0c\u6210\u7acb\u9500\u552e\u90e8\uff0c\u867d\u7136\u6709\u4e86\u5730\u65b9\u5b89\u7f6e\u5218\u660e\u8f89\uff0c\u4f46\u662f\u4f1a\u52a9\u6da8\u4e0b\u9762\u5404\u5927\u533a\u9500\u552e\u4e3b\u7ba1\u7684\u6c14\u7130\uff0c\u8fd9\u4e0d\u662f\u4ed6\u6240\u613f\u610f\u770b\u5230\u7684\u5c40\u9762\uff0c\u201c\u5e02\u573a\u90e8\u7684\u91cd\u5fc3\u5e94\u8be5\u5728\u5e02\u573a\u672c\u90e8\uff0c\u800c\u4e0d\u662f\u5404\u4e2a\u5927\u533a\u3002\u8981\u6210\u7acb\u9500\u552e\u90e8\uff0c\u4e5f\u53ea\u662f\u5728\u5e02\u573a\u90e8\u4e0b\u9762\u6210\u7acb\u9500\u552e\u5206\u90e8\u8d1f\u8d23\u5b9e\u9645\u7684\u9500\u552e\u5de5\u4f5c\u3002\u9500\u552e\u5728\u6211\u773c\u91cc\u6ca1\u6709\u90a3\u4e48\u91cd\u8981\uff0c\u5373\u4fbf\u5c06\u5404\u5927\u533a\u7684\u9500\u552e\u529e\u4e8b\u5904\u64a4\u6d88\u4e5f\u65e0\u5927\u788d\uff0c\u4e00\u4e2a\u7701\u7684\u9500\u552e\u5de5\u4f5c\uff0c\u7531\u4e00\u540d\u666e\u901a\u7684\u9500\u552e\u5de5\u7a0b\u5e08\u534f\u52a9\u5f53\u5730\u7684\u7ecf\u9500\u5546\u5373\u80fd\u80dc\u4efb\u2026\u2026\u201d</p><p>\u3000\u3000\u7231\u8fbe\u4e0e\u5404\u6e20\u9053\u5546\u4e4b\u95f4\u7684\u5173\u7cfb\u4e00\u5411\u4ee5\u6765\u90fd\u662f\u7231\u8fbe\u5904\u4e8e\u5f3a\u52bf\uff0c\u5e02\u573a\u90e8\u7684\u91cd\u5fc3\u662f\u5f3a\u5316\u7231\u8fbe\u54c1\u724c\u5728\u5e02\u573a\u4e0a\u7684\u5730\u4f4d\uff0c\u4e5f\u540c\u6837\u5f3a\u5316\u5728\u6e20\u9053\u5546\u5fc3\u4e2d\u7684\u5730\u4f4d\uff0c\u9500\u552e\u5de5\u4f5c\u4e00\u76f4\u4ee5\u6765\u90fd\u4e0d\u662f\u5e02\u573a\u90e8\u7684\u91cd\u5fc3\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u89c1\u5f20\u606a\u4e0d\u540c\u610f\u5c06\u9500\u552e\u5de5\u4f5c\u4ece\u5e02\u573a\u90e8\u5206\u62c6\u51fa\u6765\u6210\u7acb\u72ec\u7acb\u7684\u90e8\u95e8\uff0c\u8f7b\u8f7b\u4e00\u53f9\uff0c\u5218\u660e\u8f89\u5728\u54c1\u724c\u8fd0\u8425\u4e0a\u6ca1\u6709\u592a\u591a\u7684\u7ecf\u9a8c\uff0c\u4e5f\u4e0d\u53ef\u80fd\u7acb\u5373\u5c06\u6768\u4e91\u8c03\u4efb\u4ed6\u804c\uff0c\u8ba9\u4ed6\u4ee3\u66ff\u6768\u4e91\u53bb\u8d1f\u8d23\u5e02\u573a\u90e8\u7684\u5de5\u4f5c\uff0c\u81f3\u4e8e\u5218\u660e\u8f89\u8be5\u600e\u4e48\u5b89\u6392\uff0c\u5012\u4e0d\u662f\u4ed6\u80fd\u5efa\u8bae\u4e86\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u7a97\u5916\u7684\u591c\u8272\uff0c\u8fd8\u6ca1\u6709\u5230\u57ce\u533a\uff0c\u8def\u706f\u4e4b\u5916\u7684\u591c\u662f\u94f6\u7070\u8272\u7684\uff0c\u4eff\u4f5b\u8499\u7740\u96fe\u972d\u3002</p><p>\u3000\u3000\u201c\u5176\u4ed6\u4e8b\u60c5\u5148\u7a33\u4e00\u7a33\uff0c\u201d\u9001\u82cf\u6d25\u4e1c\u4e0b\u8f66\uff0c\u5f20\u606a\u5bf9\u4ed6\u8bf4\uff0c\u201c\u4eca\u5929\u8be5\u505a\u7684\u4e8b\u6ca1\u6709\u505a\uff0c\u660e\u5929\u7ee7\u7eed\u5427\u3002\u201d</p><p>\u3000\u3000\u5c06\u5404\u5927\u533a\u7684\u9500\u552e\u4e3b\u7ba1\u53ec\u96c6\u56de\u6765\u4e3b\u8981\u662f\u4e3a\u4e86\u8ba8\u8bba\u660e\u5e74\u7684\u5e02\u573a\u65b9\u6848\uff0c\u82cf\u6d25\u4e1c\u70b9\u70b9\u5934\uff0c\u76ee\u9001\u5f20\u606a\u4ed6\u4eec\u79bb\u5f00\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\u8d77\u65e9\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u8fc7\u6765\u63a5\u4ed6\u53bb\u76ca\u9686\u9547\uff0c\u4f1a\u524d\u5148\u5ba3\u5e03\u5bf9\u5e02\u573a\u90e8\u534e\u5357\u529e\u4e8b\u5904\u4e0b\u5c5e\u5458\u5de5\u8096\u9038\u7fa4\u7684\u5904\u5206\u51b3\u5b9a\uff0c\u76d1\u5bdf\u90e8\u4e0d\u518d\u8ffd\u7a76\u7968\u636e\u7684\u95ee\u9898\uff0c\u4f46\u662f\u53d7\u5230\u8096\u9038\u7fa4\u4e3b\u52a8\u7f34\u4e0a\u6765\u7684\u8f9e\u804c\u4fe1\uff0c\u534e\u5357\u529e\u4e8b\u5904\u4e3b\u4efb\u66f9\u65b0\u9633\u4ee5\u53ca\u5e02\u573a\u90e8\u526f\u603b\u76d1\u5218\u660e\u8f89\u90fd\u6309\u516c\u53f8\u89c4\u5b9a\u5206\u522b\u5904\u4ee5\u4e94\u767e\u3001\u4e00\u5343\u5143\u7684\u7f5a\u6b3e\uff0c\u7b97\u662f\u7ed9\u4e8b\u60c5\u4e00\u4e2a\u4e0d\u7b97\u592a\u60ca\u4eba\u7684\u5c3e\u58f0\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u5ba3\u5e03\u5904\u7f5a\u51b3\u5b9a\uff0c\u4e5f\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u91cd\u5927\u5904\u5206\uff0c\u4e0b\u9762\u4eba\u90fd\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u7ed9\u7275\u626f\u5230\u66f9\u65b0\u9633\u4e0e\u5218\u660e\u8f89\u8138\u8272\u90fd\u4e0d\u53ef\u80fd\u592a\u597d\u770b\u3002</p><p>\u3000\u3000\u5f20\u606a\u6e05\u4e86\u6e05\u55d3\u5b50\uff0c\u5f00\u53e3\u76f4\u5954\u4e3b\u9898\uff0c\u4f3c\u4e4e\u5c06\u6628\u5929\u53d1\u751f\u7684\u4e8b\u60c5\u5b8c\u5168\u5fd8\u6389\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u8981\u8ba8\u8bba\u660e\u5e74\u7684\u5e02\u573a\u8ba1\u5212\uff0c\u8be6\u7ec6\u7684\u65b9\u6848\u4e5f\u8981\u5728\u5341\u4e00\u6708\u4e4b\u524d\u62df\u5b9a\u51fa\u6765\uff0c\u8fd9\u4efd\u65b9\u6848\u80fd\u4e0d\u80fd\u8ba9\u7231\u8fbe\u7535\u5b50\u7ee7\u7eed\u83b7\u5f97\u660e\u5e74\u7684\u5e02\u573a\u8f89\u714c\uff0c\u5c31\u8981\u770b\u5728\u5ea7\u7684\u5404\u4f4d\u662f\u4e0d\u662f\u771f\u6b63\u80fd\u79f0\u5f97\u4e0a\u8fd9\u4e2a\u5708\u5b50\u91cc\u7684\u7cbe\u82f1\u4eba\u58eb\u4e86\u2026\u2026\u201d\u5728\u5ea7\u7684\u5404\u4f4d\u90fd\u77e5\u9053\u5f20\u606a\u505a\u5e02\u573a\u7684\u6c34\u5e73\uff0c\u582a\u79f0\u5947\u8ff9\u4e4b\u624b\uff0c\u542c\u4e86\u4ed6\u7684\u8fd9\u53e5\u8bdd\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u6709\u4efb\u4f55\u7684\u6cbe\u6cbe\u81ea\u559c\uff0c\u5f20\u606a\u795e\u8272\u7f13\u4e86\u7f13\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4eca\u5e74\u7231\u8fbe\u7535\u5b50\u7684\u9500\u552e\u6709\u671b\u7a81\u7834\u56db\u5341\u4ebf\uff0c\u662f\u5404\u4f4d\u5171\u52c9\u6240\u6210\uff0c\u660e\u5e74\u7684\u9500\u552e\u76ee\u6807\uff0c\u6211\u5e0c\u671b\u56fd\u5185\u80fd\u7a81\u7834\u516d\u5341\u4ebf\uff0c\u540c\u65f6\u8f83\u5927\u529b\u5ea6\u7684\u5f00\u5c55\u6d77\u5916\u9500\u552e\u5de5\u4f5c\uff0c\u4e89\u53d6\u8fbe\u6210\u5341\u4e2a\u4ebf\u7684\u9500\u552e\u989d\uff0c\u660e\u5e74\u7684\u5e02\u573a\u8ba1\u5212\u4e5f\u5c06\u662f\u8fd9\u4e9b\u76ee\u6807\u4e3a\u57fa\u7840\uff0c\u5e02\u573a\u5ba3\u4f20\u6295\u5165\u65b9\u9762\u7684\u9884\u7b97\uff0c\u4e5f\u662f\u4ee5\u9884\u6d4b\u9500\u552e\u989d\u4e3a\u57fa\u7840\uff0c\u6211\u60f3\u56fd\u5185\u90e8\u5206\u53ef\u4ee5\u505a\u5230\u516d\u4e2a\u4ebf\u7684\u6837\u5b50\uff0c\u9664\u4e86\u5e02\u573a\u90e8\u672c\u90e8\u96c6\u4e2d\u7684\u5e7f\u544a\u5ba3\u4f20\u6295\u5165\u4e4b\u5916\uff0c\u8fd8\u5c06\u52a0\u5927\u5730\u533a\u5e7f\u544a\u7684\u6295\u5165\u529b\u5ea6\uff0c\u5730\u533a\u5e7f\u544a\u6295\u653e\u4ee5\u9500\u552e\u533a\u5212\u5757\uff0c\u603b\u989d\u63a7\u5236\u5728\u4e24\u4e2a\u4ebf\u4e4b\u5185\uff0c\u5404\u9500\u552e\u533a\u4e4b\u95f4\u7684\u9884\u7b97\u6bd4\u4f8b\u5219\u4ee5\u9500\u552e\u989d\u51b3\u5b9a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62a5\u51fa\u8fd9\u6837\u7684\u6570\u636e\uff0c\u5728\u5ea7\u7684\u795e\u60c5\u90fd\u662f\u4e00\u632f\u3002</p><p>\u3000\u3000\u4eca\u5e74\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u573a\u5ba3\u4f20\u9884\u7b97\u5c06\u8fd1\u4e09\u4e2a\u4ebf\uff0c\u6b64\u65f6\u5b9e\u9645\u5df2\u82b1\u53bb\u4e24\u4e2a\u4ebf\uff0c\u5176\u4e2d\u7edd\u5927\u591a\u6570\u662f\u5e02\u573a\u672c\u90e8\u7edf\u4e00\u82b1\u6389\u4e86\uff0c\u5728\u592e\u89c6\u5c31\u6295\u5165\u4e00\u4e2a\u591a\u4ebf\uff0c\u5404\u9500\u552e\u533a\u80fd\u638c\u63e1\u7684\u5e7f\u544a\u9884\u7b97\u5341\u5206\u7684\u6709\u9650\uff0c\u800c\u4e14\u53d7\u5230\u672c\u90e8\u4e25\u683c\u7684\u9650\u5236\u3002</p><p>\u3000\u3000\u660e\u5e74\uff0c\u6574\u4f53\u7684\u5e7f\u544a\u9884\u7b97\u63d0\u9ad8\u4e00\u500d\u4e0d\u8bf4\uff0c\u5730\u533a\u5e7f\u544a\u6295\u653e\u4ee5\u9500\u552e\u533a\u5212\u5757\uff0c\u542c\u8fd9\u6837\u7684\u610f\u601d\uff0c\u516c\u53f8\u662f\u6709\u610f\u52a0\u5927\u5404\u9500\u552e\u533a\u6295\u653e\u5e7f\u544a\u7684\u81ea\u4e3b\u6743\uff0c\u50cf\u534e\u5357\u533a\u8fd9\u6837\u7684\u9500\u552e\u5927\u533a\uff0c\u660e\u5e74\u6216\u8bb8\u5c31\u80fd\u638c\u63e1\u4e09\u56db\u5343\u4e07\u7684\u5e7f\u544a\u9884\u7b97\u3002\u5f53\u4eca\u793e\u4f1a\uff0c\u638c\u63e1\u4e86\u8d22\u6743\u624d\u771f\u6b63\u638c\u63e1\u4e86\u6743\u529b\uff0c\u8fd9\u4e2a\u6d88\u606f\u81ea\u7136\u8ba9\u4eba\u632f\u5907\u3002</p><p>\u3000\u3000\u201c\u4e0b\u4e2a\u6708\u7684\u592e\u89c6\u6807\u738b\u62db\u6807\uff0c\u6211\u4eec\u662f\u52bf\u5728\u5fc5\u5f97\uff1f\u201d\u82cf\u6d25\u4e1c\u6ca1\u6709\u591a\u60f3\u9500\u552e\u533a\u638c\u63e1\u5e7f\u544a\u9884\u7b97\u7684\u95ee\u9898\uff0c\u7ed9\u660e\u5e74\u7684\u5e7f\u544a\u9884\u7b97\u5212\u51fa\u8fd9\u4e48\u5927\u4e00\u5757\uff0c\u81ea\u7136\u76ee\u6807\u76f4\u6307\u660e\u5e74\u7684\u592e\u89c6\u6807\u738b\uff0c\u4ed6\u6709\u4e9b\u8be7\u5f02\uff0c\u4fa7\u8fc7\u5934\u95ee\u5f20\u606a\u3002\u8fd9\u6bb5\u65f6\u95f4\u6765\uff0c\u90fd\u6ca1\u6709\u542c\u5f20\u606a\u63d0\u8d77\u8fc7\u8fd9\u4e8b\u60c5\uff0c\u4ee5\u4e3a\u5f20\u606a\u591a\u534a\u4f1a\u653e\u5f03\u4e0b\u4e2a\u6708\u7684\u6807\u738b\u4e89\u593a\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u53bb\u5e74\u7684\u592e\u89c6\u6807\u738b\u5b54\u5e9c\u5bb4\u9152\u7684\u5e02\u573a\u840e\u7f29\u60c5\u51b5\uff0c\u76f8\u4fe1\u5728\u5ea7\u7684\u5404\u4f4d\u90fd\u6709\u4e86\u89e3\uff0c\u7231\u8fbe\u7535\u5b50\u8981\u60f3\u660e\u5e74\u7ee7\u7eed\u8c31\u5199\u5e02\u573a\u8f89\u714c\uff0c\u5c31\u4e0d\u80fd\u8f7b\u6613\u7ed9\u522b\u4eba\u6458\u53bb\u592e\u89c6\u6807\u738b\u7684\u5149\u73af\uff0c\u201d\u5f20\u606a\u6df1\u9083\u7684\u773c\u795e\u770b\u5411\u66f9\u65b0\u9633\uff0c\u5fc3\u60f3\u8c22\u77bb\u6628\u591c\u627e\u4ed6\u4f1a\u8c08\u4ec0\u4e48\u6837\u7684\u8bdd\uff0c\u76ee\u5149\u5c31\u5728\u66f9\u65b0\u9633\u8138\u4e0a\u505c\u7559\u4e86\u4e00\u77ac\uff0c\u53c8\u7ee7\u7eed\u5f80\u4e0b\u9762\u7684\u4eba\u8138\u4e0a\u770b\u8fc7\u53bb\uff0c\u201c\u53bb\u5e74\uff0c\u6211\u4eec\u4ee5\u516b\u5343\u4e07\u7684\u4ee3\u4ef7\u62ff\u4e0b\u592e\u89c6\u6807\u738b\uff0c\u4eca\u5e74\u5c31\u6ca1\u90a3\u4e48\u8f7b\u677e\u4e86\u3002\u4e00\u662f\u6211\u4eec\u7684\u6210\u529f\u4f1a\u6781\u5927\u7684\u9f13\u821e\u5176\u4ed6\u5f71\u789f\u673a\u5382\u5bb6\uff0c\u6bd4\u5982\u79d1\u738b\u7535\u5668\uff0c\u4ed6\u4eec\u6700\u8fd1\u7684\u5e02\u573a\u5ba3\u4f20\u529b\u5ea6\u5c31\u5f88\u5927\uff0c\u6709\u7740\u660e\u663e\u7684\u6a21\u4eff\u6211\u4eec\u7684\u75d5\u8ff9\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u4f1a\u5bf9\u660e\u5e74\u7684\u592e\u89c6\u6807\u738b\u611f\u5174\u8da3\uff0c\u4e8c\u662f\u4eca\u5e74\u4e2d\u592e\u7535\u89c6\u53f0\u53ef\u80fd\u4f1a\u91c7\u53d6\u660e\u6807\u7684\u62db\u6807\u65b9\u5f0f\uff0c\u73b0\u573a\u7ade\u6807\u4f1a\u66f4\u52a0\u7684\u6fc0\u70c8\u3002\u6211\u80fd\u591f\u80af\u5b9a\uff0c\u4e0b\u4e2a\u6708\u4e0d\u4f1a\u5f00\u51fa\u4f4e\u4e8e\u4e24\u4e2a\u4ebf\u7684\u6807\u4ef7\uff0c\u6240\u4ee5\u6211\u4eec\u8981\u505a\u51fa\u4e8c\u4ebf\u4e94\u5343\u4e07\u5230\u4e09\u4ebf\u7684\u9884\u7b97\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e09\u4e2a\u4ebf\u2026\u2026\u201d\u6768\u4e91\u6697\u81ea\u4e4d\u820c\uff0c\u201c\u771f\u6709\u5fc5\u8981\u62ff\u4e09\u4e2a\u4ebf\u53bb\u4e89\u592e\u89c6\u6807\u738b\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\u5e74\u6211\u4eec\u62ff\u516b\u5343\u4e07\u7ed9\u4e2d\u592e\u7535\u89c6\u53f0\u65f6\uff0c\u522b\u4eba\u7ed9\u6211\u7b97\u8fc7\u4e00\u7b14\u8d26\uff0c\u8bf4\u6211\u4eec\u6bcf\u5929\u8981\u5f80\u4e2d\u592e\u7535\u89c6\u53f0\u9001\u4e00\u8f86\u6851\u5854\u7eb3\uff0c\u5176\u5b9e\u8fd9\u4e2a\u4eba\u4e0d\u77e5\u9053\u4e00\u8f86\u6851\u5854\u7eb3\u5176\u5b9e\u503c\u4e0d\u4e86\u5341\u4e94\u4e07\uff0c\u4e24\u8f86\u6851\u5854\u7eb3\u5dee\u4e0d\u591a\uff0c\u201d\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5f15\u8d77\u4e0b\u9762\u4e00\u9635\u7b11\uff0c\u4ed6\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u6bcf\u5929\u5f80\u592e\u89c6\u9001\u4e24\u8f86\u6851\u5854\u7eb3\u4e0d\u5047\uff0c\u4f46\u662f\u6211\u4eec\u6bcf\u5929\u8981\u4ece\u592e\u89c6\u5f00\u51fa\u4e24\u90e8\u51ef\u8fea\u62c9\u514b\u51fa\u6765\uff0c\u6211\u662f\u770b\u5230\u51ef\u8fea\u62c9\u514b\u624d\u5f80\u592e\u89c6\u9001\u6851\u5854\u7eb3\u7684\u2026\u2026\u201d\u4e0b\u9762\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u82cf\u6d25\u4e1c\u6447\u4e86\u6447\u5934\uff0c\u7b11\u7740\u4e0d\u8bf4\u4ec0\u4e48\uff0c\u53bb\u5e74\u4ed6\u4e5f\u5bf9\u5f20\u606a\u7684\u51b3\u5b9a\u5c31\u6709\u5f88\u5927\u7684\u7591\u95ee\uff0c\u5bf9\u4e8e\u5e02\u573a\uff0c\u5f20\u606a\u662f\u5929\u751f\u7684\u654f\u9510\uff0c\u522b\u4eba\u662f\u8fdc\u8fdc\u4e0d\u53ca\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u6302\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u770b\u4f3c\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u5e74\u7684\u5e02\u573a\u72b6\u51b5\u4f1a\u590d\u6742\u4e00\u4e9b\uff0c\u6b63\u56e0\u4e3a\u4f1a\u590d\u6742\u4e00\u4e9b\uff0c\u6240\u4ee5\u66f4\u8981\u62ff\u4e0b\u592e\u89c6\u6807\u738b\u8fd9\u4e2a\u5236\u9ad8\u70b9\u3002\u5f53\u7136\uff0c\u4e0d\u53ef\u80fd\u518d\u6709\u62ff\u6851\u5854\u7eb3\u6362\u51ef\u8fea\u62c9\u514b\u7684\u597d\u4e8b\u4e86\uff0c\u8fd8\u60f3\u6bcf\u5929\u4ece\u592e\u89c6\u5f00\u51fa\u4e24\u90e8\u51ef\u8fea\u62c9\u514b\u51fa\u6765\uff0c\u81f3\u5c11\u6bcf\u5929\u8981\u5f80\u91cc\u9001\u4e00\u8f86\u5954\u9a70\u624d\u884c\u2026\u2026\u8981\u77e5\u9053\uff0c\u56fd\u5185\u6c38\u8fdc\u662f\u5168\u7403\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u4e16\u754c\uff0c\u4e89\u593a\u8fd9\u4e2a\u5e02\u573a\uff0c\u82b1\u518d\u5927\u7684\u4ee3\u4ef7\u90fd\u4e0d\u8fc7\u5206\uff0c\u6211\u4eec\u59cb\u7ec8\u80fd\u4ece\u91cc\u9762\u83b7\u5f97\u8db3\u591f\u7684\u5229\u6da6\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e2d\u5348\u5728\u516c\u53f8\u7684\u9910\u5385\u91cc\u805a\u9910\uff0c\u5f20\u606a\u4e0e\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u848b\u8587\u3001\u9c81\u5e86\u751f\u3001\u5468\u4e00\u5e73\u3001\u6768\u4e91\u3001\u5218\u660e\u8f89\u7b49\u4eba\u5750\u4e00\u4e2a\u5305\u53a2\u91cc\u7528\u9910\uff0c\u5f20\u606a\u5bf9\u5218\u660e\u8f89\u8bf4\u9053\uff1a\u201c\u6709\u53ef\u80fd\u7684\u8bdd\uff0c\u660e\u5e74\u5728\u5e02\u573a\u90e8\u4e0b\u9762\u6210\u7acb\u9500\u552e\u5206\u90e8\u4e0e\u6d77\u5916\u5206\u90e8\uff0c\u7edf\u7ba1\u5404\u9500\u552e\u533a\u4ee5\u53ca\u62d3\u5c55\u6d77\u5916\u9500\u552e\u7684\u5de5\u4f5c\uff0c\u4f60\u80fd\u4e0d\u80fd\u5199\u4e2a\u8349\u6848\u7ed9\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5218\u660e\u8f89\u7591\u60d1\u7684\u770b\u4e86\u770b\u5f20\u606a\uff0c\u201c\u4e0a\u5348\u4f1a\u8bae\u521a\u8bf4\u5230\u5730\u65b9\u4e0a\u7684\u5e7f\u544a\u6295\u653e\u4ee5\u9500\u552e\u533a\u5212\u5757\uff0c\u4e0d\u662f\u8981\u5c06\u5e7f\u544a\u9884\u7b97\u4ea4\u7ed9\u5404\u9500\u552e\u533a\u81ea\u884c\u638c\u63e1\uff1f\u201d</p><p>\u3000\u3000\u201c\u660e\u5e74\u4f1a\u52a0\u5927\u5730\u65b9\u5e7f\u544a\u7684\u6295\u653e\u529b\u5ea6\uff0c\u4e0d\u8fc7\u8fd8\u662f\u7531\u672c\u90e8\u8fd9\u8fb9\u603b\u4f53\u638c\u63e1\uff0c\u5404\u9500\u552e\u533a\u534f\u52a9\uff0c\u8fd9\u4e48\u5927\u7b14\u91d1\u989d\u7684\u5e7f\u544a\u8d39\u9884\u7b97\u4e0d\u53ef\u80fd\u4ea4\u7ed9\u4e0b\u9762\u7684\u9500\u552e\u533a\u81ea\u884c\u8d1f\u8d23\uff0c\u4f1a\u51fa\u4e71\u5b50\u7684\u3002\u4ee5\u9500\u552e\u533a\u5212\u5757\uff0c\u53ea\u662f\u4e3a\u4e86\u66f4\u52a0\u7cbe\u51c6\u4e0e\u65b9\u4fbf\u3002\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u89c1\u5218\u660e\u8f89\u773c\u91cc\u6709\u4e9b\u5931\u671b\uff0c\u77e5\u9053\u4ed6\u5f00\u4f1a\u65f6\u7406\u4f1a\u9519\u81ea\u5df1\u7684\u610f\u601d\u3002\u5728\u5e02\u573a\u90e8\u4e0b\u9762\u6210\u7acb\u9500\u552e\u5206\u90e8\u7edf\u7ba1\u5404\u9500\u552e\u533a\u7684\u5de5\u4f5c\uff0c\u5b9e\u9645\u4e0a\u662f\u7ed9\u5404\u9500\u552e\u533a\u964d\u683c\uff0c\u81ea\u7136\u4e0d\u4f1a\u53bb\u52a0\u91cd\u5404\u9500\u552e\u533a\u7684\u8d22\u6743\uff0c\u8ba9\u8fd9\u4e9b\u5404\u8def\u8bf8\u4faf\u62e5\u5175\u81ea\u91cd\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u73b0\u5728\u57fa\u672c\u5168\u6743\u8d1f\u8d23\u534e\u5357\u3001\u897f\u5357\u3001\u534e\u4e2d\u4e09\u4e2a\u5927\u9500\u552e\u533a\uff0c\u5e7f\u544a\u9884\u7b97\u7531\u9500\u552e\u533a\u638c\u63e1\uff0c\u4ed6\u5c31\u80fd\u638c\u63e1\u4e09\u4e2a\u9500\u552e\u533a\u7684\u5e7f\u544a\u9884\u7b97\uff0c\u800c\u73b0\u5728\u7684\u5e7f\u544a\u9884\u7b97\u662f\u7531\u6768\u4e91\u534f\u52a9\u82cf\u6d25\u4e1c\u3001\u848b\u8587\u5177\u4f53\u8d1f\u8d23\u7684\uff0c\u8fd9\u4e5f\u6d89\u53ca\u5230\u5e02\u573a\u90e8\u6700\u5927\u7684\u4e00\u5757\u6743\u529b\uff0c\u4e5f\u662f\u5e02\u573a\u90e8\u5386\u6765\u6bd4\u5176\u4ed6\u90e8\u95e8\u6743\u91cd\u7684\u91cd\u8981\u539f\u56e0\u3002</p><p>\u3000\u3000\u60f3\u60f3\u65b0\u829c\u5e02\u4e00\u4e2a\u5730\u533a\u4e00\u5e74\u7684\u8d22\u653f\u9884\u7b97\u4e5f\u4e0d\u8fc7\u516d\u4e03\u4e2a\u4ebf\u800c\u5df2\uff0c\u5218\u660e\u8f89\u7684\u5931\u671b\uff0c\u5f20\u606a\u591a\u5c11\u80fd\u4f53\u4f1a\u5230\u3002</p></article>"}}}]);
