(window.webpackJsonp=window.webpackJsonp||[]).push([[406],{428:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u4e09\u8282 \u8389\u6cd5\u5c14\u7684\u62d2\u7edd",content:"<article><h2>\u7b2c\u516d\u5341\u4e09\u8282 \u8389\u6cd5\u5c14\u7684\u62d2\u7edd</h2><p>\u81ea\u8d1d\u5c14\u82df\u65af\u7279\u5f80\u5317\u8d70\u5546\u8def\uff0c\u518d\u6709\u4e00\u5929\u8def\u7a0b\uff0c\u4fbf\u80fd\u5230\u535a\u5f97\u4e4b\u95e8\uff1b\u800c\u5f80\u897f\u8d70\u201c\u96c4\u72ee\u4e4b\u8def\u201d\uff0c\u4e00\u76f4\u5230\u6d77\u8fb9\uff0c\u4fbf\u662f\u5927\u9646\u4e0a\u6700\u8457\u540d\u7684\u56fe\u4e66\u9986\uff1a\u70db\u5821\u3002</p><p>\u743c\u6069\u8981\u53bb\u70db\u5821\uff0c\u81ea\u7136\u662f\u4e3a\u4e86\u67e5\u9605\u8d44\u6599\u3002\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u67e5\u9605\u4e00\u5207\u6709\u5173\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u8d44\u6599\u3002</p><p>\u81ea\u4ece\u5f97\u77e5\u81ea\u5df1\u62e5\u6709\u4ec0\u4e48\u7fd4\u9f99\u5370\u8bb0\u4e4b\u540e\uff0c\u743c\u6069\u5bf9\u6709\u5173\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u4e00\u5207\u8d44\u6599\u4fbf\u7559\u4e86\u5fc3\uff0c\u6bd5\u7adf\u8fd9\u4e8b\u5173\u81ea\u5df1\u7684\u8eab\u4e16\u6765\u5386\uff0c\u751a\u81f3\u6709\u53ef\u80fd\u7275\u6d89\u5230\u9634\u9b42\u57ce\u7684\u8ba1\u5212\uff0c\u5e72\u7cfb\u5230\u81ea\u5df1\u7684\u672a\u6765\uff0c\u4e0d\u80fd\u4e0d\u614e\u91cd\u3002\u4ed6\u5f53\u7136\u53ef\u4ee5\u76f4\u63a5\u53bb\u95ee\u6b27\u51ef\uff0c\u90a3\u6837\u66f4\u65b9\u4fbf\uff0c\u66f4\u76f4\u63a5\uff0c\u66f4\u8be6\u5c3d\uff0c\u60f3\u77e5\u9053\u4e00\u6bb5\u5386\u53f2\uff0c\u8fd8\u6709\u4ec0\u4e48\u6bd4\u95ee\u90a3\u4e2a\u65f6\u4ee3\u7684\u4eba\u66f4\u6709\u6548\u2014\u2014\u4f46\u4ed6\u4e0d\u653e\u5fc3\u3002</p><p>\u8fc4\u4eca\u4e3a\u6b62\uff0c\u743c\u6069\u5bf9\u201c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u201d\u7684\u4e86\u89e3\uff0c\u867d\u7136\u7b97\u4e0d\u4e0a\u591a\uff0c\u5374\u4e5f\u6709\u4e00\u4e9b\u57fa\u672c\u6982\u5ff5\u3002\u8003\u8651\u5230\u8fd9\u4e2a\u53e4\u8001\u5e1d\u56fd\u5df2\u7ecf\u706d\u4ea1\u56db\u5343\u5e74\uff0c\u6587\u660e\u4f20\u627f\u5b8c\u5168\u65ad\u7edd\uff0c\u7edd\u5927\u591a\u6570\u4eba\u8fde\u201c\u4f0a\u739b\u65af\u5361\u201d\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\u90fd\u4e0d\u77e5\u9053\uff0c\u743c\u6069\u751a\u81f3\u90fd\u53ef\u4ee5\u5192\u5145\u201c\u4e13\u5bb6\u201d\u4e86\u3002\u4f46\u4ed6\u6240\u77e5\u9053\u7684\u8fd9\u4e9b\uff0c\u9664\u4e86\u4ece\u6885\u83f2\u65af\u548c\u6b23\u5e03\u90a3\u91cc\u542c\u5230\u8fc7\u7684\u4e00\u9cde\u534a\u722a\uff0c\u800c\u4e14\u8fd8\u672a\u7ecf\u8bc1\u5b9e\u7684\u96f6\u788e\u4fe1\u606f\uff0c\u5176\u4f59\u5168\u90fd\u662f\u6b27\u51ef\u4e00\u4e2a\u4eba\u8bf4\u7684\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u57fa\u672c\u662f\u8fd9\u4e2a\u6076\u9b54\u7684\u4e00\u9762\u4e4b\u8bcd\u3002</p><p>\u4e00\u9762\u4e4b\u8bcd\uff0c\u8fd8\u662f\u4e0d\u80fd\u5c3d\u4fe1\u7684\u3002</p><p>\u70db\u5821\u662f\u4e00\u4e2a\u516c\u5e73\u7684\u5730\u65b9\uff0c\u4f60\u8981\u60f3\u8fdb\u6765\u83b7\u53d6\u77e5\u8bc6\uff0c\u90a3\u5c31\u5f97\u540c\u6837\u4ee5\u77e5\u8bc6\u6765\u4ea4\u6362\uff1b\u4f60\u80fd\u7ed9\u70db\u5821\u63d0\u4f9b\u7684\u77e5\u8bc6\u8d8a\u201c\u73cd\u8d35\u201d\uff0c\u90a3\u4e48\u4f60\u6240\u83b7\u5f97\u7684\u5f85\u9047\u4e5f\u5c31\u76f8\u5e94\u8d8a\u597d\uff0c\u9605\u8bfb\u6743\u9650\u4e5f\u5c31\u8d8a\u9ad8\u3002\u743c\u6069\u4ee5\u524d\u548c\u6885\u83f2\u65af\u6765\u8fc7\u4e00\u6b21\uff0c\u81ea\u7136\u77e5\u9053\u8fd9\u4e2a\u89c4\u77e9\uff0c\u6240\u4ee5\u4ed6\u6709\u51c6\u5907\uff0c\u8fd8\u5728\u4f0a\u5361\u6c99\u57ce\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4ece\u77ee\u4eba\u56fe\u4e66\u9986\u91cc\u6284\u5f55\u4e86\u51e0\u672c\u53e4\u7c4d\u5e26\u5728\u8eab\u8fb9\uff0c\u6b63\u662f\u9884\u5907\u505a\u8fd9\u4e2a\u7528\u9014\u3002</p><p>\u8fd9\u51e0\u672c\u53e4\u7c4d\u4e2d\uff0c\u8bb0\u8f7d\u4e86\u4e0a\u53e4\u65f6\u671f\u77ee\u4eba\u738b\u56fd\u6df1\u9083\u6749\u90a3\u5854\u7684\u5386\u53f2\uff0c\u5176\u4e2d\u6709\u5927\u91cf\u4eba\u6587\u98ce\u4fd7\u65b9\u9762\u7684\u7ec6\u8282\u63cf\u8ff0\uff0c\u4ef7\u503c\u6781\u9ad8\u3002\u743c\u6069\u4e00\u8fb9\u5c06\u5b83\u4eec\u4ea4\u4e0a\u53bb\uff0c\u540c\u65f6\u58f0\u660e\u81ea\u5df1\u521a\u4ece\u5e7d\u6697\u5730\u57df\u7684\u4f0a\u5361\u6c99\u57ce\u65c5\u6e38\u5f52\u6765\uff0c\u7acb\u523b\u4fbf\u53d7\u5230\u91cd\u89c6\u3002\u5f53\u5929\u665a\u4e0a\uff0c\u4ed6\u88ab\u9080\u8bf7\u548c\u70db\u5821\u7684\u6700\u9ad8\u9886\u8896\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u5148\u751f\u5171\u8fdb\u665a\u9910\uff0c\u5728\u5927\u8c08\u7279\u8c08\u4e86\u4e00\u756a\u77ee\u4eba\u57ce\u5e02\u7684\u89c1\u95fb\u4e4b\u540e\uff0c\u83b7\u5f97\u4e86\u6700\u9ad8\u9605\u8bfb\u6743\u9650\uff0c\u9664\u4e86\u4e00\u4e9b\u6700\u79d8\u5bc6\u7684\u533a\u57df\u4e0d\u80fd\u8fdb\u5165\u4e4b\u5916\uff0c\u70db\u5821\u7684\u4e5d\u5ea7\u201c\u77e5\u8bc6\u9ad8\u5854\u201d\u5bf9\u4ed6\u5b8c\u5168\u5f00\u653e\u3002</p><p>\u5728\u63a5\u4e0b\u6765\u7684\u4e24\u5929\u91cc\uff0c\u743c\u6069\u57cb\u9996\u4e8e\u4e66\u5c71\u4e4b\u4e2d\uff0c\u641c\u5bfb\u7ffb\u9605\u4e00\u5207\u6709\u5173\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u8d44\u6599\u3002\u4ee4\u4ed6\u9887\u4e3a\u9057\u61be\u7684\u662f\uff0c\u5f53\u5e74\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u7684\u795e\u738b\u4eec\uff0c\u6587\u660e\u706d\u7edd\u7684\u5de5\u4f5c\u505a\u5f97\u5b9e\u5728\u5f88\u5230\u4f4d\uff0c\u5c31\u8fde\u8fd9\u5ea7\u5168\u5927\u9646\u6700\u9f0e\u9f0e\u6709\u540d\u7684\u201c\u70db\u5821\u201d\u91cc\uff0c\u4e5f\u627e\u4e0d\u5230\u591a\u5c11\u771f\u6b63\u6709\u4ef7\u503c\u7684\u4fe1\u606f\uff0c\u5927\u591a\u90fd\u662f\u201c\u636e\u8bf4\u201d\u3001\u201c\u636e\u95fb\u201d\u3001\u201c\u4f20\u8a00\u201d\u8fd9\u79cd\uff0c\u4e0d\u8fc7\u5012\u662f\u95f4\u63a5\u8bc1\u5b9e\u4e86\u6b27\u51ef\u8bf4\u8fc7\u7684\u4e00\u4e9b\u8bdd\uff0c\u4e5f\u7b97\u6709\u70b9\u6536\u83b7\u3002</p><p>\u5c31\u8fd9\u6837\uff0c\u767d\u5929\u67e5\u9605\u8d44\u6599\uff0c\u665a\u4e0a\u5728\u5ba2\u623f\u4e2d\u4f11\u606f\uff0c\u95f2\u6687\u65f6\u4fbf\u5728\u57ce\u5821\u91cc\u6563\u6563\u6b65\u3002\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u8fd9\u6b21\u6765\u70db\u5821\uff0c\u4e5f\u7b97\u662f\u6545\u5730\u91cd\u6e38\u4e86\uff0c\u522b\u6709\u4e00\u756a\u611f\u6168\u3002\u5f53\u65f6\u4ed6\u548c\u6885\u83f2\u65af\u5728\u8fd9\u91cc\u4f4f\u4e86\u51e0\u65e5\uff0c\u906d\u9047\u4e86\u5f88\u591a\u4e8b\u60c5\uff0c\u4e24\u4eba\u7684\u60c5\u4fa3\u5173\u7cfb\uff0c\u7b97\u8d77\u6765\u4e5f\u662f\u4ece\u6b64\u6b63\u5f0f\u5f00\u59cb\u8d77\u6b65\uff0c\u5386\u7ecf\u98ce\u98ce\u96e8\u96e8\uff0c\u6700\u7ec8\u53d1\u5c55\u5230\u4eca\u5929\u7684\u7a0b\u5ea6\u3002\u548c\u838e\u73de\u514b\u7684\u7b2c\u4e00\u6b21\u906d\u9047\u4e5f\u662f\u5728\u8fd9\u91cc\uff0c\u5f53\u65f6\u53cc\u65b9\u7684\u7acb\u573a\u8fd8\u662f\u654c\u5bf9\uff0c\u5f7c\u6b64\u90fd\u60f3\u6740\u4e86\u5bf9\u65b9\uff0c\u8c01\u4e5f\u6ca1\u60f3\u5230\u65f6\u9694\u4e00\u5e74\u534a\u4e4b\u540e\uff0c\u5c45\u7136\u5df2\u7ecf\u6210\u4e86\u5173\u7cfb\u4eb2\u5bc6\u7684\u540c\u4f34\u3002</p><p>\u4e16\u4e8b\u65e0\u5e38\uff0c\u5b9e\u5728\u662f\u51fa\u4e4e\u610f\u6599\uff0c\u8ba9\u4eba\u65e0\u8bdd\u53ef\u8bf4\u3002</p><p>\u8fd9\u4e00\u65e5\uff0c\u743c\u6069\u627e\u5230\u4e86\u4e00\u672c\u4ecb\u7ecd\u53e4\u5f80\u4eca\u6765\u8457\u540d\u9b54\u6cd5\u7269\u54c1\u7684\u4e66\u7c4d\uff0c\u91cc\u9762\u4e5f\u63d0\u53ca\u5230\u4e00\u4e9b\u6709\u5173\u201c\u4f0a\u739b\u65af\u5361\u4e03\u79d8\u5668\u201d\u7684\u8d44\u6599\uff0c\u5c3d\u7ba1\u57fa\u672c\u662f\u4f20\u95fb\uff0c\u4f46\u4e5f\u672a\u5c1d\u4e0d\u53ef\u4ee5\u770b\u770b\uff0c\u4f5c\u4e3a\u53c2\u8003\u3002\u743c\u6069\u89c1\u5929\u8272\u5df2\u665a\uff0c\u56fe\u4e66\u9986\u9a6c\u4e0a\u8981\u5173\u95e8\uff0c\u4fbf\u8bf7\u70db\u5821\u5185\u7684\u6284\u5199\u5458\u7528\u9b54\u6cd5\u5c06\u8fd9\u672c\u4e66\u5feb\u901f\u8a8a\u5199\u4e86\u4e00\u4efd\uff0c\u51c6\u5907\u5e26\u56de\u623f\u95f4\u91cc\u6162\u6162\u7814\u7a76\u3002</p><p>\u8a8a\u5199\u5b8c\u6bd5\uff0c\u4e5f\u5230\u4e86\u5173\u95e8\u65f6\u95f4\uff0c\u743c\u6069\u631f\u7740\u4e66\u8d70\u51fa\u9ad8\u5854\uff0c\u7a7f\u8fc7\u82b1\u56ed\uff0c\u51c6\u5907\u8fd4\u56de\u4f4f\u5904\u3002\u4ed6\u6ee1\u6000\u5fc3\u4e8b\uff0c\u5934\u8111\u4e2d\u601d\u7d22\u56de\u5fc6\u7740\u4eca\u5929\u67e5\u9605\u5230\u7684\u90a3\u4e9b\u8d44\u6599\uff0c\u6b63\u60f3\u5f97\u5165\u795e\uff0c\u773c\u89d2\u4f59\u5149\u7a81\u7136\u77a5\u89c1\u4e00\u4e2a\u719f\u6089\u7684\u8eab\u5f71\u3002</p><p>\u90a3\u662f\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u6b63\u4ece\u8349\u5730\u4e0a\u8d70\u8fc7\uff0c\u770b\u6837\u5b50\u4e5f\u662f\u521a\u4ece\u56fe\u4e66\u9986\u91cc\u51fa\u6765\uff0c\u548c\u81ea\u5df1\u4e00\u6837\u6253\u7b97\u56de\u4f4f\u5904\uff0c\u8d70\u8def\u7684\u59ff\u52bf\u975e\u5e38\u4f18\u96c5\u597d\u770b\u3002\u5979\u7a7f\u7740\u9ed1\u8272\u7684\u7d27\u8eab\u76ae\u7532\u3001\u957f\u9774\uff0c\u6234\u7740\u8fde\u81c2\u7684\u9ed1\u8272\u76ae\u5236\u624b\u5957\uff0c\u5916\u7f69\u9576\u7740\u94f6\u8fb9\u7684\u6597\u7bf7\uff0c\u6709\u4e9b\u7834\u65e7\u4e86\uff0c\u515c\u5e3d\u538b\u5f97\u4f4e\u4f4e\uff0c\u906e\u4f4f\u4e86\u5927\u534a\u8fb9\u8138\uff0c\u51e0\u7f15\u6d45\u84dd\u8272\u7684\u53d1\u4e1d\u81ea\u515c\u5e3d\u4e2d\u9759\u9759\u5782\u4e0b\uff0c\u773c\u775b\u548c\u9f3b\u6881\u5168\u90fd\u9690\u85cf\u5728\u9634\u5f71\u91cc\uff0c\u53ea\u80fd\u770b\u89c1\u7cbe\u81f4\u7684\u5634\u5507\uff0c\u5634\u89d2\u5fae\u5fae\u4e0a\u7fd8\uff0c\u4eff\u4f5b\u603b\u5728\u6d41\u9732\u7740\u4e0d\u7ecf\u610f\u7684\u6d45\u7b11\u3002\u4ece\u6253\u626e\u4e0a\u770b\uff0c\u50cf\u662f\u4e2a\u6b66\u8005\uff0c\u4f46\u5374\u6ca1\u6709\u643a\u5e26\u4efb\u4f55\u6b66\u5668\uff0c\u53cd\u800c\u6000\u4e2d\u62b1\u7740\u51e0\u672c\u539a\u539a\u7684\u4e66\uff0c\u914d\u4e0a\u90a3\u79cd\u5b89\u9759\u5a34\u96c5\u7684\u6c14\u8d28\uff0c\u5018\u82e5\u518d\u914d\u4e00\u526f\u773c\u955c\u7684\u8bdd\uff0c\u5012\u66f4\u50cf\u662f\u4e2a\u5973\u5b66\u8005\u3002</p><p>\u201c\u8389\u6cd5\u5c14\uff1f\u201d</p><p>\u743c\u6069\u7acb\u523b\u8ba4\u51fa\u4e86\u5bf9\u65b9\u7684\u8eab\u4efd\uff0c\u6b63\u662f\u5728\u62c9\u6c83\u514b\u7684\u5deb\u5e08\u4e4b\u5893\u91cc\u9047\u5230\u7684\u90a3\u4f4d\u5438\u8840\u9b3c\u7f8e\u5973\u3002\u81ea\u5df1\u8fd8\u60f3\u7740\u5230\u4e86\u535a\u5f97\u4e4b\u95e8\u540e\uff0c\u62bd\u65f6\u95f4\u53bb\u770b\u770b\u5979\uff0c\u6ca1\u60f3\u5230\u5c45\u7136\u4f1a\u5728\u8fd9\u91cc\u9047\u4e0a\u4e86\u3002</p><p>\u542c\u5230\u6709\u4eba\u53eb\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u8389\u6cd5\u5c14\u8f6c\u8fc7\u8138\u6765\uff0c\u5979\u5148\u662f\u6014\u4e86\u4e00\u6014\uff0c\u5927\u6982\u662f\u592a\u8fc7\u4e8e\u51fa\u4e4e\u610f\u6599\uff0c\u4f46\u968f\u5373\u5c31\u5fae\u5fae\u4e00\u7b11\uff0c\u62ac\u624b\u5c06\u515c\u5e3d\u8f7b\u8f7b\u6380\u8d77\uff0c\u9732\u51fa\u4e00\u5f20\u7cbe\u81f4\u7684\u9762\u5e9e\uff0c\u5fae\u5fae\u70b9\u5934\u793a\u610f\u3002</p><p>\u201c\u597d\u4e45\u4e0d\u89c1\uff0c\u743c\u6069\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u6545\u4eba\u5f02\u5730\u76f8\u9022\uff0c\u81ea\u7136\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u60c5\u3002\u4e00\u756a\u4e91\u96e8\u7f20\u7ef5\u540e\uff0c\u8389\u6cd5\u5c14\u9759\u9759\u504e\u4f9d\u5728\u743c\u6069\u6000\u91cc\uff0c\u542c\u4ed6\u8bb2\u8ff0\u5206\u522b\u8fd9\u4e00\u5e74\u591a\u6765\u6240\u7ecf\u5386\u7684\u6545\u4e8b\u3002</p><p>\u201c\u5c31\u8fd9\u6837\uff0c\u521a\u4ece\u5b89\u59c6\u90a3\u8fb9\u8fc7\u6765\uff0c\u51c6\u5907\u56de\u9634\u9b42\u57ce\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd8\u60f3\u7740\u7ecf\u8fc7\u535a\u5f97\u4e4b\u95e8\u7684\u65f6\u5019\uff0c\u53bb\u5deb\u5e08\u4e4b\u5893\u4e00\u8d9f\u5462\u3002\u201d</p><p>\u201c\u53bb\u62dc\u8bbf\u62c9\u6c83\u514b\u5148\u751f\u5417\uff1f\u201d\u8389\u6cd5\u5c14\u95ee\uff0c\u201c\u90a3\u6050\u6015\u6709\u4e9b\u4e0d\u592a\u51d1\u5de7\u5462\u3002\u201d</p><p>\u201c\u62dc\u8bbf\u4ed6\u505a\u4ec0\u4e48\uff0c\u6211\u662f\u8bf4\u53bb\u770b\u4f60\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u4e00\u4e2a\u8001\u5deb\u5996\uff0c\u6211\u6ca1\u4e8b\u624d\u4e0d\u60f3\u548c\u4ed6\u6253\u4ea4\u9053\u5462\uff0c\u5f53\u7136\u662f\u53bb\u770b\u4f60\u8fd9\u6837\u7684\u6f02\u4eae\u7f8e\u4eba\u513f\u3002\u201d</p><p>\u201c\u5bf9\u4e86\uff0c\u6211\u7ed9\u4f60\u5e26\u4e86\u4ef6\u793c\u7269\uff0c\u201d\u4ed6\u5728\u653e\u5728\u5e8a\u8fb9\u7684\u957f\u888d\u5185\u6d4b\u53e3\u888b\u91cc\u6478\u4e86\u4e00\u4f1a\uff0c\u53d6\u51fa\u4e00\u4e2a\u7cbe\u5de7\u7684\u76d2\u5b50\u6765\uff0c\u9012\u7ed9\u8389\u6cd5\u5c14\uff0c\u201c\u5728\u963f\u65af\u5361\u7279\u62c9\u7684\u5e02\u573a\u4e0a\u4e70\u7684\u3002\u201d</p><p>\u8389\u6cd5\u5c14\u63a5\u8fc7\u76d2\u5b50\u6253\u5f00\uff0c\u91cc\u9762\u662f\u4e00\u526f\u9879\u94fe\uff0c\u6d45\u9ec4\u8272\u7684\u7ec6\u85e4\u7f16\u7ec7\u6210\u675f\uff0c\u7f00\u7740\u4e09\u679a\u78a7\u7389\u96d5\u7422\u6210\u7684\u6a61\u53f6\uff0c\u4e2d\u95f4\u4e00\u679a\u8f83\u5927\uff0c\u65c1\u8fb9\u4e24\u679a\u8f83\u5c0f\u4e9b\uff0c\u6676\u83b9\u5254\u900f\uff0c\u8109\u7edc\u6e05\u6670\uff0c\u624b\u5de5\u975e\u5e38\u7cbe\u81f4\u3002\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4f60\u6bd4\u8f83\u559c\u6b22\u4ec0\u4e48\uff0c\u5fd8\u4e86\u95ee\uff0c\u201d\u743c\u6069\u6709\u4e9b\u6b49\u610f\u5730\u8bf4\uff0c\u201c\u60f3\u7740\u4f60\u662f\u6a61\u6811\u4e4b\u7236\u7684\u5fb7\u9c81\u4f9d\uff0c\u6240\u4ee5\u5c31\u2026\u2026\u201d</p><p>\u201c\u55ef\uff0c\u6211\u5f88\u559c\u6b22\u554a\uff0c\u201d\u8389\u6cd5\u5c14\u5c06\u9879\u94fe\u6258\u5728\u638c\u4e2d\u6b23\u8d4f\u7740\uff0c\u201c\u5f88\u6f02\u4eae\u3002\u201d</p><p>\u743c\u6069\u66ff\u5979\u5c06\u9879\u94fe\u6234\u4e0a\uff0c\u6d45\u9ec4\u8272\u7684\u85e4\u7ef3\u548c\u78a7\u7389\u8272\u7684\u6a61\u53f6\uff0c\u886c\u6258\u5f97\u5c11\u5973\u7684\u808c\u80a4\u96ea\u767d\u7ec6\u817b\uff0c\u67d4\u7f8e\u52a8\u4eba\u3002\u201c\u6211\u597d\u4e45\u6ca1\u6709\u6536\u5230\u8fc7\u793c\u7269\u4e86\u5462\u3002\u201d\u8389\u6cd5\u5c14\u8f7b\u58f0\u8bf4\u3002</p><p>\u201c\u4ee5\u540e\u4f1a\u7ecf\u5e38\u6709\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4f1a\u5e38\u5e38\u6765\u770b\u4f60\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\u3002\u201d\u743c\u6069\u4fdd\u8bc1\u3002</p><p>\u8389\u6cd5\u5c14\u751c\u751c\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u4e24\u4eba\u9759\u9759\u8eba\u7740\uff0c\u968f\u53e3\u95f2\u8c08\u4e00\u4f1a\uff0c\u743c\u6069\u95ee\u8d77\u8389\u6cd5\u5c14\u4e3a\u4f55\u4f1a\u5728\u70db\u5821\u3002\u201c\u662f\u6765\u9001\u4e66\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u662f\u62c9\u6c83\u514b\u5148\u751f\u6682\u65f6\u4e2d\u65ad\u4e86\u2018\u751f\u6b7b\u8f6c\u5316\u2019\u7684\u7814\u7a76\uff0c\u8fd9\u6837\u6211\u5c31\u6709\u4e86\u70b9\u7a7a\u95f2\uff0c\u8bf7\u5047\u51fa\u6765\u6563\u6563\u6b65\uff0c\u987a\u4fbf\u6765\u70db\u5821\u770b\u770b\u4e66\u3002\u201d</p><p>\u201c\u62c9\u6c83\u514b\u4e2d\u65ad\u4e86\u751f\u6b7b\u8f6c\u5316\u7684\u7814\u7a76\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u8054\u60f3\u8d77\u524d\u9762\u5979\u8bf4\u7684\u8bdd\uff0c\u201c\u5deb\u5e08\u4e4b\u5893\u51fa\u4ec0\u4e48\u4e8b\u4e86\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u51fa\u4ec0\u4e48\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u524d\u6bb5\u65f6\u95f4\uff0c\u62c9\u6c83\u514b\u5148\u751f\u53bb\u4e86\u4e00\u8d9f\u5b89\u59c6\uff0c\u201d\u8389\u6cd5\u5c14\u8bf4\uff0c\u201c\u56de\u6765\u4e4b\u540e\u5c31\u4e2d\u65ad\u4e86\u624b\u5934\u4e0a\u7684\u6240\u6709\u7814\u7a76\uff0c\u6574\u65e5\u95ed\u95e8\u4e0d\u51fa\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u89c1\uff0c\u4e5f\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u3002\u201d</p><p>\u62c9\u6c83\u514b\u53bb\u8fc7\u5b89\u59c6\uff0c\u8fd9\u4e2a\u6d88\u606f\u743c\u6069\u5df2\u7ecf\u77e5\u9053\uff0c\u81f3\u4e8e\u4ed6\u5230\u5e95\u53d7\u4e86\u4ec0\u4e48\u523a\u6fc0\u56de\u6765\uff0c\u743c\u6069\u5c31\u61d2\u5f97\u5173\u5fc3\u4e86\u3002\u5f53\u4e0b\u4e5f\u4e0d\u518d\u591a\u95ee\uff0c\u968f\u624b\u62ff\u8d77\u8389\u6cd5\u5c14\u4ece\u56fe\u4e66\u9986\u91cc\u6284\u5f55\u7684\u4e66\uff0c\u7ffb\u4e86\u4e00\u7ffb\uff0c\u53d1\u73b0\u5c45\u7136\u5168\u90fd\u662f\u6709\u5173\u57ce\u5e02\u5efa\u8bbe\u3001\u89c4\u5212\u65b9\u9762\u7684\u8d44\u6599\u548c\u8bba\u8457\uff0c\u5012\u662f\u9887\u4e3a\u5403\u60ca\u3002</p><p>\u201c\u4f60\u770b\u8fd9\u4e2a\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u8389\u6cd5\u5c14\u70b9\u70b9\u5934\uff0c\u201c\u6211\u4e00\u76f4\u5bf9\u8fd9\u65b9\u9762\u633a\u6709\u5174\u8da3\u7684\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4f60\u4e0d\u662f\u6a61\u6811\u4e4b\u7236\u7684\u5fb7\u9c81\u4f9d\u5417\uff1f\u201d\u743c\u6069\u8be7\u5f02\uff0c\u201c\u6211\u8bb0\u5f97\u4ee5\u524d\u5728\u4e66\u4e0a\u8bfb\u5230\u8fc7\uff0c\u897f\u51e1\u7eb3\u65af\u7684\u4fe1\u5f92\u662f\u6700\u53cd\u5bf9\u57ce\u5e02\u5316\u7684\uff0c\u96be\u9053\u6211\u8bb0\u9519\u4e86\uff1f\u201d</p><p>\u897f\u51e1\u7eb3\u65af\u662f\u81ea\u7136\u4e4b\u795e\uff0c\u5c0a\u79f0\u4e3a\u6a61\u6811\u4e4b\u7236\uff0c\u5723\u5fbd\u4fbf\u662f\u4e00\u679a\u6a61\u6811\u53f6\uff0c\u4ed6\u7684\u4fe1\u5f92\u4ee5\u201c\u72c2\u70ed\u6fc0\u8fdb\u7684\u81ea\u7136\u4fdd\u62a4\u4e3b\u4e49\u8005\u201d\u800c\u8457\u79f0\uff0c\u6781\u5ea6\u53cd\u5bf9\u57ce\u5e02\u5316\uff0c\u53cd\u5bf9\u6587\u660e\u6269\u5f20\u5bfc\u81f4\u7684\u8352\u91ce\u5f00\u57a6\u3001\u68ee\u6797\u780d\u4f10\uff0c\u751a\u81f3\u6709\u8c23\u8a00\u8bf4\u4ed6\u4eec\u79d8\u5bc6\u5730\u8d44\u52a9\u76d7\u532a\u56db\u5904\u52ab\u63a0\u3001\u9972\u80b2\u731b\u517d\u62e9\u5730\u91ce\u653e\uff0c\u76ee\u7684\u662f\u4e3a\u4e86\u6291\u5236\u6587\u660e\u548c\u4eba\u53e3\u7684\u8fc7\u5ea6\u81a8\u80c0\u3002\u8fd9\u79cd\u8bf4\u6cd5\u867d\u7136\u6ca1\u6709\u8bc1\u636e\uff0c\u672a\u7ecf\u8bc1\u5b9e\uff0c\u4f46\u81f3\u5c11\u4e5f\u53cd\u5e94\u4e86\u5916\u4eba\u7684\u4e00\u79cd\u89c2\u611f\u3002</p><p>\u8389\u6cd5\u5c14\u662f\u897f\u51e1\u7eb3\u65af\u7684\u5fb7\u9c81\u4f9d\uff0c\u743c\u6069\u539f\u672c\u4ee5\u4e3a\u5979\u5c31\u7b97\u4e0d\u662f\u90a3\u79cd\u6781\u7aef\u7684\u73af\u4fdd\u4e3b\u4e49\u8005\uff0c\u81f3\u5c11\u4e5f\u662f\u5bf9\u57ce\u5e02\u62b1\u6709\u6bd4\u8f83\u758f\u8fdc\u6392\u65a5\u7684\u6001\u5ea6\u624d\u5bf9\uff0c\u4e07\u4e07\u6ca1\u60f3\u5230\u5979\u5c45\u7136\u8fd8\u662f\u4e2a\u57ce\u5e02\u89c4\u5212\u5b66\u7684\u7231\u597d\u8005\u2026\u2026\u8fd9\u5b9e\u5728\u662f\u592a\u51fa\u4e4e\u610f\u6599\u4e86\u70b9\u3002</p><p>\u201c\u6ca1\u6709\u5566\uff0c\u201d\u8389\u6cd5\u5c14\u7b11\u7b11\uff0c\u201c\u4f60\u8bef\u4f1a\u4e86\uff0c\u4e5f\u662f\u6211\u521a\u624d\u6ca1\u8bf4\u6e05\u695a\u3002\u6211\u5176\u5b9e\u771f\u6b63\u611f\u5174\u8da3\u7684\uff0c\u4e0d\u662f\u57ce\u5e02\u7684\u89c4\u5212\u5efa\u8bbe\u8fd9\u79cd\u5b66\u95ee\uff0c\u4ec5\u4ec5\u662f\u57ce\u5e02\u7eff\u5316\u8fd9\u65b9\u9762\u800c\u5df2\u3002\u201d</p><p>\u201c\u57ce\u5e02\u7eff\u5316\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u57ce\u5e02\u7eff\u5316\uff0c\u201d\u8389\u6cd5\u5c14\u89e3\u91ca\uff0c\u201c\u8fd9\u662f\u6211\u4eec\u6559\u4f1a\u53d1\u660e\u7684\u4e00\u4e2a\u65b0\u672f\u8bed\uff0c\u4f60\u5927\u6982\u6ca1\u542c\u8fc7\uff0c\u610f\u601d\u5c31\u662f\u8bf4\u5728\u57ce\u5e02\u91cc\u690d\u6811\u9020\u6797\u3001\u79cd\u8349\u683d\u82b1\uff0c\u5c3d\u53ef\u80fd\u628a\u57ce\u5e02\u548c\u68ee\u6797\u81ea\u7136\u76f8\u7ed3\u5408\u8d77\u6765\u2026\u2026\u201d</p><p>\u201c\u54e6\uff0c\u8fd9\u4e2a\u8bcd\u7684\u610f\u601d\u6211\u5012\u662f\u660e\u767d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u662f\u6ca1\u60f3\u5230\u539f\u6765\u4f60\u4eec\u7684\u6559\u4e49\u4e2d\u8fd8\u5305\u542b\u8fd9\u79cd\u5185\u5bb9\uff0c\u4ee5\u524d\u5012\u662f\u6709\u4e9b\u8bef\u89e3\u4e86\u3002\u201d</p><p>\u201c\u6050\u6015\u5012\u4e5f\u4e0d\u662f\u8bef\u89e3\uff0c\u201d\u8389\u6cd5\u5c14\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u5176\u5b9e\u8fd9\u79cd\u6982\u5ff5\uff0c\u4e5f\u662f\u6700\u8fd1\u51e0\u5e74\u624d\u6709\u4eba\u63d0\u51fa\u6765\u7684\uff0c\u5728\u6559\u4f1a\u91cc\u7b97\u662f\u4e2a\u65b0\u6d41\u6d3e\u5427\uff0c\u4f46\u89c4\u6a21\u5f88\u5c0f\uff0c\u8ba4\u540c\u8005\u4e5f\u4e0d\u591a\uff0c\u6211\u6070\u597d\u662f\u5176\u4e2d\u4e4b\u4e00\u7f62\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u743c\u6069\u6765\u4e86\u5174\u81f4\uff0c\u201c\u80fd\u5177\u4f53\u8bf4\u8bf4\u770b\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u6709\u5174\u8da3\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u60f3\u542c\u542c\uff0c\u89c9\u5f97\u4f3c\u4e4e\u5f88\u6709\u8da3\u7684\u6837\u5b50\u3002\u201d</p><p>\u8389\u6cd5\u5c14\u4e8e\u662f\u7b80\u5355\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u6b63\u5982\u5916\u754c\u6240\u8ba4\u4e3a\u7684\u90a3\u6837\uff0c\u897f\u51e1\u7eb3\u65af\u6559\u4f1a\u786e\u5b9e\u4e00\u76f4\u4ee5\u6765\u79c9\u6301\u7684\u662f\u6bd4\u8f83\u6fc0\u8fdb\u7684\u81ea\u7136\u4fdd\u62a4\u4e3b\u4e49\uff0c\u7edd\u5bf9\u6392\u65a5\u57ce\u5e02\uff0c\u6392\u65a5\u73b0\u4ee3\u6587\u660e\uff0c\u5d07\u5c1a\u56de\u5f52\u81ea\u7136\u3002\u50cf\u5f00\u57a6\u8352\u91ce\uff0c\u5efa\u7acb\u57ce\u9547\u3001\u780d\u4f10\u68ee\u6797\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4ed6\u4eec\u90fd\u662f\u6781\u529b\u963b\u6b62\uff0c\u751a\u81f3\u4e0d\u60dc\u91c7\u7528\u66b4\u529b\u8840\u8165\u7684\u6781\u7aef\u624b\u6bb5\u3002\u4f46\u5728\u8fd1\u51e0\u5e74\uff0c\u6559\u4f1a\u91cc\u5f00\u59cb\u51fa\u73b0\u4e86\u53e6\u5916\u4e00\u79cd\u58f0\u97f3\uff0c\u6709\u4e00\u4e9b\u5fb7\u9c81\u4f9d\u5bf9\u4f20\u7edf\u7684\u4f5c\u6cd5\u4ea7\u751f\u5f02\u8bae\uff0c\u63d0\u51fa\u65b0\u7684\u4e3b\u5f20\u3002</p><p>\u8fd9\u4e9b\u5fb7\u9c81\u4f9d\u8ba4\u4e3a\uff1a\u4eba\u7c7b\u6bd5\u7adf\u4e0d\u662f\u7cbe\u7075\uff0c\u4e5f\u5e76\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u80fd\u6210\u4e3a\u5fb7\u9c81\u4f9d\uff0c\u6210\u4e3a\u81ea\u7136\u4e4b\u5b50\uff0c\u8981\u6c42\u4ed6\u4eec\u5b8c\u5168\u653e\u5f03\u57ce\u5e02\uff0c\u5728\u68ee\u6797\u8352\u91ce\u4e2d\u751f\u6d3b\uff0c\u8fd9\u662f\u6839\u672c\u5c31\u529e\u4e0d\u5230\u7684\u4e8b\u60c5\u3002\u4eba\u7c7b\u8981\u7e41\u884d\uff0c\u8981\u751f\u5b58\uff0c\u8981\u53d1\u5c55\uff0c\u52bf\u5fc5\u8981\u5411\u5916\u6269\u5f20\uff0c\u4fb5\u7565\u81ea\u7136\uff0c\u8fd9\u65e0\u53ef\u907f\u514d\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u4e00\u79cd\u6ce8\u5b9a\u7684\u5386\u53f2\u8d8b\u52bf\uff0c\u5df2\u7ecf\u4e0d\u53ef\u80fd\u5b8c\u5168\u963b\u6b62\u3002</p><p>\u4f20\u7edf\u7684\u5f3a\u786c\u62b5\u5236\u4f5c\u6cd5\uff0c\u975e\u4f46\u5f92\u52b3\u65e0\u529f\uff0c\u6536\u6548\u5fae\u5f31\uff0c\u53cd\u800c\u8d8a\u6765\u8d8a\u591a\u5730\u6fc0\u8d77\u4e86\u53cd\u611f\u548c\u51b2\u7a81\uff0c\u5728\u6709\u4e00\u4e9b\u5730\u533a\uff0c\u897f\u51e1\u7eb3\u65af\u6559\u4f1a\u5df2\u7ecf\u88ab\u6c11\u4f17\u666e\u904d\u89c6\u4e3a\u2018\u6587\u660e\u7684\u654c\u4eba\u2019\uff0c\u540d\u58f0\u5f88\u5dee\uff0c\u751a\u81f3\u88ab\u653f\u5e9c\u5ba3\u5e03\u4e3a\u975e\u6cd5\u7ec4\u7ec7\uff0c\u88ab\u7981\u6b62\u4f20\u6559\u3001\u9a71\u9664\u51fa\u5883\u3002\u6709\u9274\u4e8e\u6b64\uff0c\u8fd9\u4e9b\u5fb7\u9c81\u4f9d\u4eec\u63d0\u51fa\uff1a\u201c\u6559\u4f1a\u4f20\u7edf\u7684\u4f5c\u6cd5\uff0c\u5e94\u8be5\u52a0\u4ee5\u4fee\u6b63\u548c\u6539\u53d8\uff0c\u4ee5\u4fbf\u66f4\u597d\u5730\u9002\u5e94\u793e\u4f1a\u53d1\u5c55\u7684\u8d8b\u52bf\uff0c\u66f4\u597d\u5730\u5b8c\u6210\u6211\u4eec\u7684\u76ee\u6807\uff0c\u800c\u4e0d\u662f\u9002\u5f97\u5176\u53cd\uff0c\u4e24\u8d25\u4ff1\u4f24\u3002\u201d</p><p>\u5177\u4f53\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u63a8\u884c\u57ce\u5e02\u7eff\u5316\u3002</p><p>\u57ce\u5e02\u7684\u6269\u5c55\u65e0\u53ef\u907f\u514d\uff0c\u4f46\u5e76\u4e0d\u7b49\u4e8e\u8bf4\u8fd9\u4e2a\u4e16\u754c\u5c31\u8981\u88ab\u7816\u77f3\u5efa\u7b51\u6240\u7edf\u6cbb\u3002\u4eba\u7c7b\u4e5f\u662f\u81ea\u7136\u7684\u4e00\u5206\u5b50\uff0c\u201c\u57ce\u5e02\u201d\u548c\u201c\u81ea\u7136\u201d\u5e76\u4e0d\u5c31\u4e00\u5b9a\u8981\u662f\u622a\u7136\u5bf9\u7acb\uff0c\u4f60\u6b7b\u6211\u6d3b\u7684\u5173\u7cfb\uff0c\u5b8c\u5168\u53ef\u4ee5\u6709\u8c03\u548c\u7684\u4f59\u5730\u3002\u5927\u529b\u63a8\u884c\u57ce\u5e02\u7684\u7eff\u5316\u5efa\u8bbe\uff0c\u4f8b\u5982\u5728\u9053\u8def\u4e24\u65c1\u683d\u6811\uff0c\u8bbe\u7acb\u516c\u56ed\u548c\u81ea\u7136\u98ce\u666f\u533a\uff0c\u79cd\u690d\u8349\u76ae\u7b49\u79cd\u79cd\u4f5c\u6cd5\uff0c\u90fd\u662f\u53ef\u884c\u800c\u4e14\u6709\u6548\u7684\uff0c\u5bf9\u4e8e\u6062\u590d\u81ea\u7136\u7684\u201c\u4e00\u4f53\u81f3\u8861\u201d\u5927\u6709\u5e2e\u52a9\uff0c\u4e5f\u76f8\u5bf9\u66f4\u5bb9\u6613\u88ab\u666e\u904d\u63a5\u53d7\u3002</p><p>\u201c\u57ce\u5e02\u7eff\u5316\u201d\u8fd9\u79cd\u6982\u5ff5\uff0c\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\u81ea\u7136\u662f\u8001\u751f\u5e38\u8c08\uff0c\u4f46\u5c31\u8fd9\u4e2a\u4e16\u754c\u800c\u8a00\uff0c\u5374\u662f\u4e86\u4e0d\u8d77\u7684\u521b\u89c1\u3002\u867d\u7136\u8fd8\u975e\u5e38\u7c97\u758f\uff0c\u975e\u5e38\u6d45\u663e\uff0c\u4f46\u5374\u81f3\u5c11\u610f\u5473\u7740\u4e00\u79cd\u5d2d\u65b0\u7684\u7406\u8bba\u5f00\u59cb\u840c\u82bd\u3002\u4ee5\u6b64\u4e3a\u5b97\u65e8\uff0c\u897f\u51e1\u7eb3\u65af\u6559\u4f1a\u91cc\u6e10\u6e10\u5f62\u6210\u4e86\u4e00\u652f\u65b0\u7684\u6d41\u6d3e\uff0c\u8389\u6cd5\u5c14\u4fbf\u662f\u5176\u4e2d\u7684\u6210\u5458\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u743c\u6069\u70b9\u5934\uff0c\u201c\u6211\u4e5f\u89c9\u5f97\u8fd9\u662f\u6bd4\u8f83\u6b63\u786e\u7684\u65b9\u6cd5\u3002\u201d</p><p>\u201c\u4e3b\u8981\u662f\u66f4\u6709\u53ef\u884c\u6027\uff0c\u201d\u8389\u6cd5\u5c14\u8bf4\uff0c\u201c\u8fd9\u624d\u662f\u5173\u952e\u6240\u5728\u3002\u518d\u7f8e\u597d\u7684\u7406\u60f3\uff0c\u5982\u679c\u6ca1\u6709\u53ef\u884c\u6027\uff0c\u4e5f\u53ea\u662f\u7a7a\u8c08\u7f62\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u9519\u3002\u201d\u743c\u6069\u8d5e\u540c\u3002</p><p>\u4e24\u4eba\u968f\u53e3\u95f2\u804a\u7740\uff0c\u6e10\u6e10\u90fd\u6709\u4e86\u4e9b\u5026\u610f\uff0c\u743c\u6069\u770b\u7740\u5979\u7684\u7cbe\u81f4\u8138\u5e9e\uff0c\u72b9\u8c6b\u826f\u4e45\uff0c\u6700\u7ec8\u8fd8\u662f\u95ee\u51fa\u4e86\u4e00\u4e2a\u76d8\u7b97\u4e86\u5f88\u4e45\u7684\u95ee\u9898\u3002</p><p>\u201c\u8389\u6cd5\u5c14\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u5176\u5b9e\u6709\u4ef6\u4e8b\u60c5\uff0c\u6211\u4e00\u76f4\u60f3\u95ee\u4f60\uff1a\u4f60\u6709\u6ca1\u6709\u8003\u8651\u8fc7\u79bb\u5f00\u5deb\u5e08\u4e4b\u5893\u5462\uff1f\u201d</p><p>\u201c\u79bb\u5f00\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5deb\u5e08\u4e4b\u5893\u90a3\u79cd\u5730\u65b9\uff0c\u9634\u68ee\u68ee\uff0c\u9ed1\u6f06\u6f06\uff0c\u5230\u5904\u90fd\u662f\u50f5\u5c38\u9ab7\u9ac5\uff0c\u96be\u770b\u5f97\u8981\u547d\uff0c\u4f60\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u4f55\u5fc5\u603b\u4f4f\u5728\u90a3\u91cc\u3002\u201d</p><p>\u8389\u6cd5\u5c14\u6d45\u6d45\u4e00\u7b11\uff0c\u201c\u6211\u8981\u79bb\u5f00\uff0c\u90a3\u4e5f\u5f97\u62c9\u6c83\u514b\u5148\u751f\u51c6\u8bb8\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4f60\u5148\u522b\u7ba1\uff0c\u5c31\u5047\u8bbe\u4ed6\u540c\u610f\u653e\u4eba\uff0c\u4f60\u7684\u60f3\u6cd5\u5462\uff1f\u201d</p><p>\u201c\u53ef\u662f\u6211\u597d\u50cf\u4e5f\u6ca1\u522b\u7684\u5730\u65b9\u53ef\u53bb\u5440\u3002\u201d</p><p>\u201c\u600e\u4e48\u4f1a\uff0c\u4f60\u53c8\u4e0d\u662f\u51fa\u751f\u5c31\u5728\u90a3\u91cc\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u53ef\u4ee5\u56de\u5bb6\u3002\u201d</p><p>\u201c\u56de\u5bb6\u662f\u80af\u5b9a\u4e0d\u884c\u7684\uff0c\u201d\u8389\u6cd5\u5c14\u8bf4\uff0c\u201c\u6211\u7236\u6bcd\u90fd\u662f\u8654\u8bda\u7684\u5fb7\u9c81\u4f9d\uff0c\u51b3\u4e0d\u4f1a\u5bb9\u5fcd\u4e00\u4e2a\u4ea1\u7075\u5973\u513f\uff0c\u8fd9\u4f1a\u8ba9\u4ed6\u4eec\u5728\u6559\u56e2\u91cc\u62ac\u4e0d\u8d77\u5934\u7684\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4e5f\u53ef\u4ee5\u53bb\u522b\u7684\u5730\u65b9\u5c45\u4f4f\u3002\u201d</p><p>\u201c\u6211\u662f\u4e2a\u5438\u8840\u9b3c\uff0c\u201d\u8389\u6cd5\u5c14\u63d0\u9192\uff0c\u201c\u6211\u6ca1\u6709\u4f53\u6e29\uff0c\u6ca1\u6709\u5fc3\u8df3\uff0c\u4e0d\u996e\u4e0d\u98df\uff0c\u9700\u8981\u5b9a\u671f\u5438\u8840\u624d\u80fd\u7ef4\u6301\u5b58\u5728\u3002\u6211\u5728\u9633\u5149\u4e0b\u6ca1\u6709\u5f71\u5b50\uff0c\u5728\u955c\u5b50\u91cc\u770b\u4e0d\u89c1\u81ea\u5df1\uff0c\u4efb\u4f55\u7267\u5e08\u548c\u7ecf\u9a8c\u4e30\u5bcc\u7684\u5192\u9669\u8005\u90fd\u80fd\u5bdf\u89c9\u6211\u7684\u8eab\u4efd\uff0c\u5373\u4fbf\u662f\u666e\u901a\u4eba\uff0c\u65f6\u95f4\u957f\u4e86\u4e5f\u4f1a\u611f\u53d7\u5230\u6211\u8eab\u4e0a\u7684\u6b7b\u4ea1\u6c14\u606f\uff0c\u201d\u5979\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u6211\u5982\u679c\u4f4f\u5728\u4eba\u7fa4\u91cc\uff0c\u5f88\u5feb\u5c31\u4f1a\u88ab\u8bc6\u7834\u5f62\u8ff9\uff1b\u6211\u60f3\u56de\u5230\u68ee\u6797\uff0c\u4f46\u81ea\u7136\u4f1a\u62d2\u7edd\u6211\u7684\u8fdb\u5165\u3002\u6211\u5df2\u7ecf\u662f\u4e2a\u4ea1\u7075\uff0c\u4e0d\u53ef\u80fd\u518d\u8fd4\u56de\u751f\u8005\u7684\u4e16\u754c\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u5c31\u53bb\u6211\u5bb6\u597d\u4e86\uff0c\u201d\u743c\u6069\u8ba4\u771f\u5730\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u613f\u610f\u7684\u8bdd\uff0c\u8389\u6cd5\u5c14\uff0c\u6211\u5e26\u4f60\u56de\u9634\u9b42\u57ce\u3002\u90a3\u91cc\u867d\u7136\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u98ce\u666f\u79c0\u4e3d\u7684\u5730\u65b9\uff0c\u603b\u6bd4\u5deb\u5e08\u4e4b\u5893\u5f3a\u4e00\u767e\u500d\u3002\u201d</p><p>\u8389\u6cd5\u5c14\u9759\u9759\u770b\u7740\u4ed6\uff0c\u8f7b\u58f0\u7b11\u4e86\u8d77\u6765\u3002\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u743c\u6069\uff1f\u201d\u5979\u95ee\uff0c\u201c\u7b97\u662f\u4f60\u5728\u8ffd\u6c42\u6211\u5417\uff1f\u201d</p><p>\u743c\u6069\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u5973\u5b69\u5b50\uff0c\u4e0d\u5e94\u8be5\u4e00\u8f88\u5b50\u5f85\u5728\u90a3\u79cd\u6697\u65e0\u5929\u65e5\u7684\u5730\u65b9\u3002\u6211\u5e0c\u671b\u80fd\u5e2e\u4f60\u3002\u201d</p><p>\u5c11\u5973\u5782\u4e0b\u773c\u8138\uff0c\u201c\u7b97\u5566\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u6211\u4e0d\u4e60\u60ef\u9ebb\u70e6\u522b\u4eba\u3002\u201d</p><p>\u5979\u5c06\u8138\u57cb\u8fdb\u743c\u6069\u6000\u4e2d\uff0c\u6c89\u6c89\u7761\u53bb\u3002</p></article>"}}}]);
