(window.webpackJsonp=window.webpackJsonp||[]).push([[408],{430:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u4e94\u8282 \u6682\u65f6\u7684\u89e3\u51b3\u65b9\u6848",content:"<article><h2>\u7b2c\u516d\u5341\u4e94\u8282 \u6682\u65f6\u7684\u89e3\u51b3\u65b9\u6848</h2><p>\u201c\u522b\u7684\u65b9\u6cd5\uff1f\u201d\u5965\u6c83\u6c89\u601d\u7740\uff0c\u201c\u5982\u679c\u5979\u662f\u4e2a\u51e1\u4eba\u5012\u5bb9\u6613\u529e\uff0c\u76f4\u63a5\u7528\u9522\u9b42\u672f\u628a\u5979\u7684\u7075\u9b42\u56da\u7981\u5230\u5b9d\u77f3\u91cc\u53bb\uff0c\u8089\u8eab\u6bc1\u706d\u6389\u3002\u4f60\u5e26\u7740\u5979\u7684\u7075\u9b42\u5b9d\u77f3\uff0c\u5c31\u5f53\u4f5c\u662f\u547d\u5323\u597d\u4e86\u3002\u53ef\u5979\u5df2\u7ecf\u662f\u4e2a\u6076\u9b54\uff0c\u7075\u9b42\u548c\u8eaf\u4f53\u662f\u5408\u4e00\u7684\uff0c\u7528\u9522\u9b42\u672f\u4f1a\u76f4\u63a5\u6740\u6b7b\u2026\u2026\u5514\uff0c\u6709\u4e86\uff0c\u5012\u662f\u8fd8\u6709\u4e2a\u529e\u6cd5\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d</p><p>\u201c\u5b9d\u77f3\u754c\u57df\uff0c\u8fd9\u4e2a\u6cd5\u672f\u542c\u8bf4\u8fc7\u5417\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\u3002</p><p>\u201c\u6ca1\u542c\u8fc7\uff1f\u4e5f\u6b63\u5e38\u3002\u90a3\u5eb7\u6770\u8bfa\xb7\u827e\u6069\uff08Congenio Ioun\uff09\u8fd9\u4e2a\u540d\u5b57\u5462\uff0c\u603b\u8be5\u77e5\u9053\u5427\uff1f\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u5eb7\u6770\u8bfa\xb7\u827e\u6069\u4ed6\u81ea\u7136\u77e5\u9053\uff0c\u8010\u745f\u745e\u5c14\u5386\u53f2\u4e0a\u7b2c\u4e00\u4f4d\u5927\u5965\u672f\u5e08\uff0c\u867d\u7136\u8bba\u5b9e\u529b\uff0c\u8bba\u6c34\u5e73\uff0c\u5728\u5e1d\u56fd\u6240\u6709\u7684\u5927\u5965\u672f\u5e08\u4e2d\u672a\u89c1\u5f97\u662f\u591a\u4e48\u51fa\u7c7b\u62d4\u8403\u2014\u2014\u4f46\u4ed6\u662f\u201c\u7b2c\u4e00\u4e2a\u201d\uff0c\u6240\u4ee5\u8457\u540d\u3002\u636e\u8bf4\u5eb7\u6770\u8bfa\u662f\u9f99\u8109\u8005\uff0c\u7279\u522b\u7231\u597d\u5b9d\u77f3\uff0c\u53d1\u660e\u4e86\u5f88\u591a\u4e0e\u4e4b\u76f8\u5173\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u5176\u4e2d\u6700\u8457\u540d\u6210\u679c\u662f\u4ee5\u4ed6\u7684\u59d3\u6c0f\u547d\u540d\u7684\u201c\u827e\u6069\u77f3\u201d\uff0c\u5c06\u5404\u79cd\u5f3a\u5927\u9b54\u6cd5\u704c\u6ce8\u8fdb\u5b9d\u77f3\u91cc\uff0c\u968f\u8eab\u73af\u7ed5\uff0c\u81ea\u7531\u5316\u5165\u4f53\u5185\u6216\u8005\u91ca\u653e\u51fa\u6765\uff0c\u5728\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u65f6\u4ee3\u66fe\u7ecf\u662f\u5927\u5965\u672f\u5e08\u4eec\u8eab\u4efd\u5730\u4f4d\u7684\u8c61\u5f81\u4e4b\u4e00\u3002</p><p>\u201c\u5b9d\u77f3\u754c\u57df\u5c31\u662f\u5eb7\u6770\u8bfa\u53d1\u660e\u7684\u6700\u540e\u4e00\u4e2a\u6cd5\u672f\u3002\u201d\u5965\u6c83\u8bf4\u3002</p><p>\u51c6\u786e\u5730\u8bf4\uff0c\u5eb7\u6770\u8bfa\u5e76\u4e0d\u662f\u8fd9\u4e2a\u6cd5\u672f\u7684\u552f\u4e00\u53d1\u660e\u8005\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u6ca1\u771f\u6b63\u5b8c\u6210\u5c31\u53bb\u4e16\u4e86\uff0c\u662f\u540e\u6765\u7684\u5927\u5965\u672f\u5e08\u4eec\u6839\u636e\u4ed6\u9057\u7559\u4e0b\u6765\u7684\u7b14\u8bb0\uff0c\u5728\u6b64\u57fa\u7840\u4e0a\u7ee7\u7eed\u52aa\u529b\uff0c\u6700\u7ec8\u624d\u5b8c\u5584\u6210\u578b\u3002\u6240\u8c13\u201c\u5b9d\u77f3\u754c\u57df\u201d\uff0c\u5176\u5b9e\u53ef\u4ee5\u7406\u89e3\u4e3a\u201c\u9522\u9b42\u672f\u201d\u3001\u201c\u4f20\u9001\u6cd5\u9635\u201d\u548c\u201c\u6c38\u6052\u9759\u6ede\u201d\u4e09\u79cd\u9b54\u6cd5\u7684\u7cc5\u5408\u4f53\uff0c\u5deb\u5e08\u53ef\u4ee5\u5c06\u4e00\u540d\u751f\u7269\uff08\u5305\u62ec\u8eaf\u4f53\u548c\u7075\u9b42\uff09\u6444\u5165\u4e00\u679a\u5b9d\u77f3\u4e4b\u5185\uff0c\u88ab\u6444\u5165\u8005\u8fdb\u5165\u5b9d\u77f3\u4e4b\u540e\uff0c\u76f8\u5f53\u4e8e\u4e2d\u4e86\u201c\u6c38\u6052\u9759\u6ede\u201d\u6cd5\u672f\uff0c\u5904\u4e8e\u5b8c\u5168\u7684\u5047\u6b7b\u72b6\u6001\uff0c\u9700\u8981\u7684\u65f6\u5019\u53c8\u53ef\u4ee5\u968f\u610f\u518d\u53ec\u51fa\uff0c\u4e8b\u60c5\u5b8c\u4e86\u518d\u8fd4\u56de\u3002</p><p>\u901a\u4fd7\u5730\u8bf4\uff0c\u743c\u6069\u4fbf\u662f\u628a\u838e\u73de\u514b\u88c5\u8fdb\u4e00\u679a\u5b9d\u77f3\u91cc\uff0c\u968f\u8eab\u643a\u5e26\uff0c\u5e73\u65f6\u53ef\u4ee5\u8ba9\u5979\u51fa\u6765\uff0c\u6709\u5371\u9669\u7684\u65f6\u5019\u8ba9\u5979\u8eb2\u8fdb\u53bb\uff0c\u838e\u73de\u514b\u5728\u5b9d\u77f3\u91cc\u662f\u5904\u4e8e\u7761\u7f8e\u4eba\u72b6\u6001\uff0c\u8fd9\u6bb5\u65f6\u95f4\u5bf9\u5979\u800c\u8a00\u7b49\u4e8e\u662f\u4e0d\u5b58\u5728\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5b89\u5168\u6027\u81ea\u7136\u5927\u589e\u3002\u800c\u4e14\u5c31\u7b97\u6bc1\u6389\u5b9d\u77f3\uff0c\u838e\u73de\u514b\u4e5f\u4e0d\u4f1a\u56e0\u6b64\u76f4\u63a5\u6b7b\u4ea1\u6216\u53d7\u4f24\uff0c\u53ea\u4e0d\u8fc7\u662f\u6cd5\u672f\u88ab\u7834\u574f\u5931\u6548\u7f62\u4e86\uff0c\u6bd4\u5deb\u5996\u7684\u547d\u5323\u5b89\u5168\u5f97\u591a\u3002</p><p>\u8fd9\u6709\u70b9\u50cf\u6885\u83f2\u65af\u624b\u91cc\u7684\u90a3\u679a\u72ec\u89d2\u517d\u96d5\u50cf\uff0c\u4f46\u53c8\u6709\u672c\u8d28\u7684\u4e0d\u540c\uff0c\u56e0\u4e3a\u9732\u4e1d\u96c5\u5e76\u4e0d\u771f\u7684\u4f4f\u5728\u96d5\u50cf\u91cc\uff0c\u5b83\u5176\u5b9e\u662f\u5728\u81ea\u7136\u4e4b\u6bbf\u7684\u72ec\u89d2\u517d\u4e1b\u6797\u4e2d\uff0c\u88ab\u53ec\u5524\u65f6\u51fa\u73b0\u5728\u7269\u8d28\u754c\u7684\u4e5f\u5e76\u4e0d\u662f\u5b83\u7684\u672c\u4f53\uff0c\u800c\u662f\u6295\u5f71\u3002\u7c7b\u4f3c\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u743c\u6069\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e5f\u89c1\u8fc7\uff0c\u7b2c\u56db\u5bb6\u65cf\u745e\u8d39\u5fb7\u7684\u57ce\u5899\u4e0a\u5c31\u6709\u4e0d\u5c11\u6076\u9b54\u96d5\u50cf\uff0c\u53ef\u4ee5\u53ec\u5524\u51fa\u5f17\u6d1b\u9b54\uff0c\u5176\u5b9e\u4e5f\u662f\u6295\u5f71\uff0c\u672c\u4f53\u8fd8\u662f\u5728\u6df1\u6e0a\u4e4b\u4e2d\u3002\u4ee5\u5965\u6c83\u7684\u80fd\u529b\uff0c\u81ea\u7136\u4e5f\u53ef\u4ee5\u505a\u8fd9\u79cd\u9b54\u6cd5\u7269\u54c1\uff0c\u96be\u5ea6\u4e5f\u4e0d\u7b97\u7279\u522b\u9ad8\uff0c\u4f46\u6ca1\u6709\u610f\u4e49\u3002\u6df1\u6e0a\u53ef\u4e0d\u662f\u4ec0\u4e48\u5b89\u5168\u7684\u5730\u65b9\uff0c\u838e\u73de\u514b\u672c\u4f53\u7559\u5728\u90a3\u91cc\uff0c\u6bd4\u7269\u8d28\u754c\u5371\u9669\u767e\u500d\uff0c\u53ea\u4f1a\u6b7b\u5f97\u66f4\u5feb\u3002</p><p>\u8fd9\u4e2a\u529e\u6cd5\u975e\u5e38\u4e0d\u9519\uff0c\u743c\u6069\u9887\u6709\u4e9b\u52a8\u5fc3\uff0c\u65e2\u80fd\u63d0\u9ad8\u5b89\u5168\u7cfb\u6570\uff0c\u5927\u5927\u964d\u4f4e\u98ce\u9669\uff0c\u540c\u65f6\u4e5f\u4e0d\u50cf\u7b2c\u4e00\u79cd\u65b9\u6cd5\u90a3\u6837\u6b8b\u9177\uff0c\u76f8\u5bf9\u800c\u8a00\u597d\u5f88\u591a\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u90fd\u5728\u8003\u8651\u8981\u4e0d\u8981\u7d22\u6027\u7ed9\u59d0\u59d0\u73ca\u5609\u4e5f\u6765\u4e00\u4e2a\uff0c\u8fd9\u6837\u5c31\u80fd\u65f6\u523b\u5e26\u5728\u8eab\u8fb9\uff0c\u89e3\u51b3\u4e86\u4e00\u76f4\u4ee5\u6765\u5934\u75bc\u7684\u96be\u9898\u3002</p><p>\u4f46\u8001\u5deb\u5996\u63a5\u4e0b\u6765\u7684\u8bdd\uff0c\u6253\u7834\u4e86\u4ed6\u4e0d\u5207\u5b9e\u9645\u7684\u5e7b\u60f3\u3002</p><p>\u201c\u5148\u522b\u5fd9\u7740\u9ad8\u5174\uff0c\u743c\u6069\uff0c\u201d\u5965\u6c83\u7528\u4e00\u79cd\u61d2\u6d0b\u6d0b\u7684\u53e3\u6c14\u8bf4\uff0c\u201c\u5b9d\u77f3\u754c\u57df\u8fd9\u4e2a\u9b54\u6cd5\u786e\u5b9e\u5f88\u4e86\u4e0d\u8d77\uff0c\u975e\u5e38\u4e86\u4e0d\u8d77\uff0c\u79f0\u5f97\u4e0a\u662f\u5eb7\u6770\u8bfa\u6bd5\u751f\u6700\u9ad8\u6210\u5c31\u2014\u2014\u4f46\u4f60\u4e0d\u89c9\u5f97\u5f88\u5947\u602a\u5417\uff0c\u8fd9\u4e48\u4f18\u79c0\u7684\u9b54\u6cd5\uff0c\u4f60\u5374\u8fde\u540d\u5b57\u90fd\u6ca1\u542c\u8fc7\uff1f\u201d</p><p>\u88ab\u5965\u6c83\u4e00\u8bf4\uff0c\u743c\u6069\u6014\u4e86\u6014\uff0c\u53d1\u73b0\u8fd9\u786e\u5b9e\u662f\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u4ed6\u5728\u9634\u9b42\u57ce\u51fa\u751f\u957f\u5927\uff0c\u63a5\u53d7\u8fc7\u7cfb\u7edf\u7684\u9b54\u6cd5\u548c\u5386\u53f2\u6559\u80b2\uff0c\u4e5f\u77e5\u9053\u5eb7\u6770\u8bfa\u8fd9\u4f4d\u201c\u5e1d\u56fd\u5386\u53f2\u4e0a\u7b2c\u4e00\u4f4d\u5927\u5965\u672f\u5e08\u201d\uff0c\u77e5\u9053\u4ed6\u662f\u827e\u6069\u77f3\u7684\u53d1\u660e\u8005\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u4ed6\u8fd8\u53d1\u660e\u4e86\u8fd9\u4e2a\u201c\u5b9d\u77f3\u754c\u57df\u201d\u9b54\u6cd5\u3002\u8d44\u6599\u4e0a\u4ecb\u7ecd\u5eb7\u6770\u8bfa\u7684\u65f6\u5019\uff0c\u597d\u50cf\u5bf9\u6b64\u90fd\u6ca1\u63d0\u8fc7\u2026\u2026\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u6cd5\u672f\u2026\u2026\u201d\u5965\u6c83\u4ece\u9f3b\u5b50\u91cc\u53d1\u51fa\u4e24\u58f0\u51b7\u7b11\uff0c\u201c\u65e9\u5c31\u5df2\u7ecf\u6ca1\u6cd5\u7528\u4e86\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u81ea\u4ece\u4e00\u5343\u4e03\u767e\u5e74\u524d\u5e1d\u56fd\u9668\u706d\uff0c\u5b83\u5c31\u6ca1\u6cd5\u4f7f\u7528\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u89e3\u3002</p><p>\u201c\u8fd8\u80fd\u4e3a\u4ec0\u4e48\uff0c\u201d\u5965\u6c83\u6709\u4e9b\u626b\u5174\u5730\u6446\u6446\u624b\uff0c\u201c\u5f53\u7136\u662f\u56e0\u4e3a\u5bc6\u65af\u62c9\u5c01\u9501\u4e86\u9b54\u7f51\u7684\u6838\u5fc3\u2026\u2026\u4f60\u4e0d\u77e5\u9053\uff1f\u201d\u8001\u5deb\u5996\u4f3c\u4e4e\u6709\u4e9b\u60ca\u8bb6\uff0c\u201c\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\u91cc\uff0c\u96be\u9053\u90fd\u4e0d\u5f00\u9b54\u6cd5\u53f2\u8fd9\u95e8\u8bfe\u7a0b\u5417\uff1f\u201d</p><p>\u201c\u5f00\u5012\u662f\u5f00\uff0c\u4f46\u4e0d\u662f\u5fc5\u4fee\u8bfe\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u5185\u5bb9\u4e5f\u975e\u5e38\u5c11\uff0c\u57fa\u672c\u4e0a\u90fd\u662f\u7c97\u7565\u4ecb\u7ecd\uff0c\u6cdb\u6cdb\u800c\u8c08\u3002\u201d</p><p>\u201c\u592a\u5dee\u52b2\u4e86\uff0c\u201d\u5965\u6c83\u4e49\u6124\u586b\u81ba\uff0c\u201c\u9b54\u6cd5\u53f2\u8fd9\u4e48\u91cd\u8981\u7684\u4e1c\u897f\uff0c\u600e\u4e48\u80fd\u4e0d\u4f5c\u4e3a\u5fc5\u4fee\u8bfe\u5b58\u5728\uff1f\u4e00\u4e2a\u5deb\u5e08\u5982\u679c\u4e0d\u7cbe\u901a\u9b54\u6cd5\u53f2\uff0c\u90a3\u4ed6\u8fd8\u7b97\u4ec0\u4e48\u5deb\u5e08\uff01\u770b\u6765\u6211\u5e94\u8be5\u627e\u673a\u4f1a\u597d\u597d\u53bb\u6307\u70b9\u4e00\u4e0b\u590f\u591a\u5982\u4f55\u6559\u80b2\u5b66\u751f\u2026\u2026\u7b97\u4e86\uff0c\u8fd9\u4e2a\u95ee\u9898\u4ee5\u540e\u518d\u6162\u6162\u8ddf\u4f60\u89e3\u91ca\uff0c\u201d\u8001\u5deb\u5996\u4e0d\u592a\u9ad8\u5174\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u603b\u800c\u8a00\u4e4b\uff0c\u5b9d\u77f3\u754c\u57df\u8fd9\u4e2a\u6cd5\u672f\uff0c\u73b0\u5728\u5df2\u7ecf\u65e0\u6cd5\u4f7f\u7528\u4e86\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u9759\u9759\u671f\u5f85\u7740\u4e0b\u6587\u3002</p><p>\u5965\u6c83\u867d\u7136\u4e71\u4e03\u516b\u7cdf\uff0c\u4f46\u4ed6\u6bd5\u7adf\u662f\u4e2a\u5927\u5965\u672f\u5e08\uff0c\u4e0d\u662f\u75af\u5b50\uff0c\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u8bf4\u4e00\u5927\u5806\u5e9f\u8bdd\u3002\u5982\u679c\u8fd9\u4e2a\u6cd5\u672f\u5f53\u771f\u5168\u65e0\u5e0c\u671b\uff0c\u4ed6\u53c8\u4f55\u5fc5\u89e3\u91ca\u8fd9\u4e48\u534a\u5929\uff0c\u96be\u4e0d\u6210\u662f\u5411\u743c\u6069\u70ab\u8000\u81ea\u5df1\u7684\u535a\u5b66\u4e48\uff0c\u8fd9\u663e\u7136\u4e0d\u53ef\u80fd\u3002</p><p>\u5982\u4ed6\u6240\u6599\uff0c\u8001\u5deb\u5996\u53e6\u6709\u4e0b\u6587\u3002</p><p>\u201c\u771f\u6b63\u7684\u5b9d\u77f3\u754c\u57df\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u6ca1\u6cd5\u4f7f\u7528\u4e86\uff0c\u4f46\u5982\u679c\u80fd\u591f\u6ee1\u8db3\u4e00\u4e9b\u524d\u63d0\u6761\u4ef6\uff0c\u524a\u5f31\u5b83\u6240\u9700\u8981\u7684\u9b54\u529b\uff0c\u964d\u4f4e\u5b83\u7684\u96be\u5ea6\uff0c\u6211\u5012\u662f\u53ef\u4ee5\u6a21\u62df\u51fa\u4e00\u4e2a\u4f4e\u7ea7\u7248\u672c\u6765\uff0c\u6548\u679c\u662f\u5dee\u4e9b\uff0c\u4f46\u4e5f\u80fd\u5c06\u5c31\u4e86\u3002\u201d</p><p>\u201c\u9700\u8981\u6ee1\u8db3\u4ec0\u4e48\u524d\u63d0\u6761\u4ef6\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u5f88\u591a\uff0c\u5176\u4ed6\u5012\u4e5f\u5bb9\u6613\u5bf9\u4ed8\uff0c\u4f46\u5176\u4e2d\u6709\u4e24\u70b9\u6bd4\u8f83\u9ebb\u70e6\uff0c\u201d\u5965\u6c83\u5c48\u8d77\u80a5\u80d6\u7c97\u77ed\u7684\u624b\u6307\uff0c\u201c\u7b2c\u4e00\uff0c\u8fd9\u4e2a\u4f4e\u7ea7\u7248\u672c\u7684\u5b9d\u77f3\u754c\u57df\uff0c\u5b83\u6ca1\u6709\u4efb\u4f55\u5f3a\u5236\u529b\uff0c\u6240\u4ee5\u9700\u8981\u65bd\u6cd5\u5bf9\u8c61\u81ea\u613f\u914d\u5408\uff0c\u5fc5\u987b\u5b8c\u5b8c\u5168\u5168\u7684\u81ea\u613f\uff0c\u201d\u5965\u6c83\u5f3a\u8c03\uff0c\u201c\u53ea\u8981\u65bd\u6cd5\u8005\u51fa\u73b0\u53cd\u6094\uff0c\u4ea7\u751f\u62b5\u6297\u60c5\u7eea\uff0c\u751a\u81f3\u53ea\u662f\u72b9\u8c6b\u3001\u5bb3\u6015\u3001\u9000\u7f29\u4e4b\u7c7b\uff0c\u6cd5\u672f\u90fd\u4f1a\u76f4\u63a5\u5931\u6548\u3002\u4f60\u4e5f\u4e0d\u7528\u8003\u8651\u5148\u7528\u9644\u9b54\u672f\u63a7\u5236\u5bf9\u65b9\u7684\u5fc3\u667a\u60c5\u7eea\uff0c\u90a3\u662f\u6ca1\u7528\u7684\u3002\u201d</p><p>\u743c\u6069\u6c89\u541f\u7740\uff0c\u8fd9\u5012\u662f\u6709\u4e9b\u9ebb\u70e6\u3002\u5e73\u5fc3\u800c\u8bba\uff0c\u8fd9\u65b9\u6cd5\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\u662f\u4e2a\u798f\u97f3\uff0c\u5bf9\u838e\u73de\u514b\u6765\u8bf4\u5374\u662f\u5669\u8017\uff0c\u8bbe\u8eab\u5904\u5730\u60f3\u60f3\uff0c\u8c01\u613f\u610f\u88ab\u522b\u4eba\u5173\u8fdb\u4e00\u679a\u5b9d\u77f3\u91cc\uff1f\u884c\u52a8\u81ea\u7531\u5b8c\u5168\u64cd\u4e8e\u4eba\u624b\uff0c\u81ea\u5df1\u505a\u4e0d\u5f97\u534a\u70b9\u4e3b\u2014\u2014\u8fd9\u4e0d\u5c31\u548c\u56da\u72af\u5dee\u4e0d\u591a\u4e86\u4e48\u3002</p><p>\u838e\u73de\u514b\u4f1a\u540c\u610f\u8fd9\u79cd\u4f5c\u6cd5\uff1f\u4f1a\u613f\u610f\u4e56\u4e56\u914d\u5408\uff1f\u8fd9\u6050\u6015\u2026\u2026\u6709\u70b9\u96be\u5ea6\u3002</p><p>\u201c\u90a3\u7b2c\u4e8c\u70b9\u5462\uff1f\u201d</p><p>\u201c\u7b2c\u4e8c\uff0c\u56e0\u4e3a\u662f\u4ee5\u8f83\u4f4e\u9636\u7684\u9b54\u7f51\u80fd\u91cf\u5f3a\u884c\u6a21\u62df\u9ad8\u9636\u6548\u679c\uff0c\u6240\u4ee5\u5f88\u5bb9\u6613\u51fa\u73b0\u9b54\u529b\u5931\u63a7\u7684\u73b0\u8c61\uff0c\u8fd9\u5c31\u5bf9\u65bd\u6cd5\u6750\u6599\u2014\u2014\u4e5f\u5c31\u662f\u5b9d\u77f3\u2014\u2014\u7684\u8981\u6c42\u6781\u9ad8\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u7edd\u5927\u90e8\u5206\u5b9d\u77f3\u627f\u53d7\u4e0d\u4e86\u8fd9\u6837\u5e9e\u5927\u7684\u9b54\u529b\uff0c\u591a\u5219\u5341\u5929\u534a\u6708\uff0c\u5c11\u5219\u4e09\u5929\u4e94\u5929\uff0c\u751a\u81f3\u51e0\u4e2a\u5c0f\u65f6\uff0c\u5c31\u4f1a\u76f4\u63a5\u788e\u88c2\u635f\u6bc1\u6389\uff0c\u6ca1\u4ec0\u4e48\u610f\u4e49\u3002\u5c31\u6211\u6240\u77e5\uff0c\u6700\u9002\u5408\u662f\u8d1d\u88d8\u91cc\u5b9d\u77f3\uff0c\u591f\u7ed3\u5b9e\uff0c\u867d\u7136\u4e5f\u4e0d\u80fd\u6c38\u4e45\u751f\u6548\uff0c\u4f46\u81f3\u5c11\u80fd\u591f\u7528\u4e0a\u4e09\u5e74\u4e94\u8f7d\uff0c\u751a\u81f3\u5341\u5e74\u516b\u5e74\uff0c\u9a6c\u9a6c\u864e\u864e\u5148\u8fd9\u6837\u5427\u3002\u201d</p><p>\u201c\u8d1d\u88d8\u91cc\u5b9d\u77f3\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u72af\u96be\uff0c\u5965\u6c83\u6240\u8bf4\u7684\u4e24\u70b9\u9ebb\u70e6\uff0c\u7b2c\u4e00\u4e2a\u5012\u7f62\u4e86\uff0c\u672a\u5fc5\u4e0d\u80fd\u548c\u838e\u73de\u514b\u5546\u91cf\uff0c\u6bd5\u7adf\u8fd9\u4e5f\u662f\u51fa\u4e8e\u5927\u5bb6\u7684\u5b89\u5168\u8003\u8651\uff0c\u4f46\u7b2c\u4e8c\u70b9\u5374\u662f\u6709\u4e9b\u5934\u75bc\u3002\u5012\u4e0d\u662f\u8bf4\u8d1d\u88d8\u91cc\u5b9d\u77f3\u591a\u4e48\u4ef7\u503c\u6602\u8d35\u2014\u2014\u5b83\u662f\u6bd4\u94bb\u77f3\u3001\u7ea2\u5b9d\u77f3\u8981\u8d35\u4e00\u4e9b\uff0c\u4f46\u4e5f\u8fd8\u6ca1\u5230\u8ba9\u743c\u6069\u671b\u800c\u5374\u6b65\u7684\u7a0b\u5ea6\uff0c\u4e3b\u8981\u662f\u592a\u7f55\u89c1\u4e86\u3002\u8d1d\u88d8\u91cc\u5b9d\u77f3\u53c8\u79f0\u7130\u5149\u77f3\uff0c\u5448\u6df1\u7eff\u8272\uff0c\u5728\u9ed1\u6697\u4e2d\u4f1a\u53d1\u51fa\u523a\u76ee\u7684\u5149\u8292\uff0c\u5e94\u8be5\u7b97\u662f\u5df2\u77e5\u7684\u5404\u79cd\u5b9d\u77f3\u4e2d\u6700\u7ed3\u5b9e\u3001\u6700\u575a\u786c\u7684\uff0c\u6bd4\u541b\u738b\u4e4b\u6cea\u90fd\u8981\u7565\u5f3a\u4e00\u4e9b\u3002\u548c\u5176\u4ed6\u5b9d\u77f3\u6700\u5927\u7684\u4e0d\u540c\u4e4b\u5904\u5728\u4e8e\uff1a\u5b83\u4ea7\u4e8e\u6df1\u6d77\u4e4b\u4e2d\uff0c\u53ea\u6709\u90a3\u4e9b\u6700\u6709\u773c\u5149\u7684\u9c7c\u4eba\u548c\u6d77\u7cbe\u7075\u624d\u4f1a\u53d1\u73b0\uff0c\u6781\u5c11\u4f1a\u51fa\u73b0\u5728\u4eba\u7c7b\u7684\u5e02\u573a\u4e0a\uff0c\u8fd9\u5374\u8981\u743c\u6069\u5230\u54ea\u91cc\u627e\u53bb\u3002</p><p>\u5e78\u8fd0\u7684\u662f\uff0c\u4ed6\u6709\u4e00\u4e2a\u597d\u8001\u5e08\u3002</p><p>\u201c\u4f60\u8fd0\u6c14\u4e0d\u9519\uff0c\u201d\u5965\u6c83\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u201c\u524d\u6bb5\u65f6\u95f4\u6709\u6761\u7eff\u9f99\u8dd1\u5230\u8fd9\u91cc\u6765\u6363\u4e71\uff0c\u88ab\u6211\u6740\u4e86\uff0c\u987a\u4fbf\u63a5\u6536\u4e86\u5b83\u7684\u9057\u4ea7\u3002\u8fd9\u6761\u9f99\u6536\u96c6\u4e86\u4e0d\u5c11\u5b9d\u77f3\uff0c\u91cc\u9762\u6070\u597d\u5c31\u6709\u4e00\u679a\u8d1d\u88d8\u91cc\uff0c\u9001\u7ed9\u4f60\u4e86\u3002\u201d</p><p>\u743c\u6069\u987f\u65f6\u5927\u559c\u8fc7\u671b\u3002</p><p>\u203b\u203b\u203b</p><p>\u6709\u4e86\u8d1d\u88d8\u91cc\u5b9d\u77f3\uff0c\u5e76\u4e0d\u610f\u5473\u7740\u4e07\u4e8b\u5927\u5409\uff0c\u5018\u82e5\u838e\u73de\u514b\u4e0d\u613f\u610f\u914d\u5408\uff0c\u90a3\u5c31\u4f9d\u65e7\u8fd8\u662f\u6ca1\u529e\u6cd5\uff0c\u8fd9\u79cd\u4e8b\u60c5\u53c8\u65e0\u6cd5\u5f3a\u8feb\uff0c\u53ea\u80fd\u9760\u8bf4\u670d\u4e86\uff0c\u6bd5\u7adf\u53cc\u65b9\u7684\u6027\u547d\u8fd8\u662f\u8054\u7cfb\u5728\u4e00\u8d77\u7684\u3002\u5728\u4e24\u4eba\u7684\u76f8\u5904\u4e2d\uff0c\u743c\u6069\u867d\u7136\u5360\u636e\u4f18\u52bf\u5730\u4f4d\uff0c\u5374\u4e0d\u7b49\u4e8e\u8bf4\u53ef\u4ee5\u4e3a\u6240\u6b32\u4e3a\u3002</p><p>\u65e0\u8bba\u5982\u4f55\uff0c\u4e00\u76f4\u4ee5\u6765\u56f0\u6270\u7740\u743c\u6069\u7684\u5fc3\u75c5\uff0c\u603b\u7b97\u662f\u6709\u4e86\u4e2a\u89e3\u51b3\u65b9\u6848\uff0c\u867d\u7136\u53ea\u662f\u6682\u65f6\u7684\u3002\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u539f\u672c\u5c31\u662f\u8fd9\u6837\uff0c\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\uff0c\u8c01\u77e5\u9053\u4e09\u5e74\u4e94\u8f7d\u4e4b\u540e\uff0c\u4e00\u5207\u53c8\u4f1a\u53d8\u6210\u4ec0\u4e48\u6837\u5462\uff1f\u5341\u4e94\u5c81\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u4e0d\u8fc7\u662f\u4e2a\u521d\u51fa\u8305\u5e90\u7684\u5c0f\u5deb\u5e08\uff1b\u5982\u4eca\u5341\u4e03\u5c81\uff0c\u4fe8\u7136\u4e5f\u5df2\u7ecf\u6709\u70b9\u6210\u5c31\u4e86\u3002\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u672c\u6765\u5c31\u591a\u6709\u51fa\u4eba\u610f\u6599\u4e4b\u5904\uff0c\u6ca1\u6cd5\u9884\u6599\u7684\u3002</p><p>\u771f\u540d\u5951\u7ea6\u548c\u5b9d\u77f3\u754c\u57df\u7684\u4e8b\u60c5\uff0c\u6682\u65f6\u5c31\u5148\u653e\u5728\u4e00\u8fb9\u3002\u743c\u6069\u8c08\u8d77\u81ea\u5df1\u6b64\u884c\u7684\u89c1\u95fb\u7ecf\u5386\uff0c\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u4ed6\u4e0b\u5e7d\u6697\u5730\u57df\uff0c\u8fdb\u7075\u5438\u602a\u57ce\u5e02\uff0c\u6311\u52a8\u5353\u5c14\u5185\u6218\uff0c\u5f53\u4e0a\u77ee\u4eba\u82f1\u96c4\uff0c\u5c31\u8fde\u6df1\u6e0a\u548c\u5730\u72f1\u90fd\u53bb\u8f6c\u4e86\u4e00\u5708\uff0c\u5f53\u771f\u7b97\u5f97\u4e0a\u7cbe\u5f69\u65e0\u6bd4\uff0c\u7b80\u76f4\u6bd4\u541f\u6e38\u8bd7\u4eba\u7684\u6545\u4e8b\u90fd\u8981\u4f20\u5947\u3002\u5965\u6c83\u8fd9\u8001\u5deb\u5996\u5728\u5893\u7a74\u91cc\u5b85\u5f97\u592a\u4e45\uff0c\u5bf9\u5916\u9762\u7684\u4e8b\u60c5\u4e0d\u591f\u4e86\u89e3\uff0c\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u4e0d\u65f6\u63d2\u5634\u95ee\u51e0\u53e5\u3002</p><p>\u8bf4\u5b8c\u74dc\u7406\u5fb7\u65af\u57ce\u4e4b\u6218\uff0c\u743c\u6069\u4ece\u6000\u4e2d\u53d6\u51fa\u8010\u745f\u5377\u8f74\uff0c\u9012\u7ed9\u5965\u6c83\uff0c\u201c\u8fd9\u5c31\u662f\u90a3\u5f20\u4ece\u5353\u5c14\u624b\u91cc\u5f97\u6765\u7684\u5377\u8f74\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5c0f\u5c0f\u5fc3\u610f\uff0c\u8fd8\u8bf7\u8001\u5e08\u6536\u7eb3\u3002\u201d</p><p>\u5965\u6c83\u5475\u5475\u4e00\u7b11\uff0c\u4f38\u624b\u63a5\u8fc7\u5377\u8f74\uff0c\u4f4e\u5934\u89c2\u770b\uff0c\u9661\u7136\u6014\u4e86\u4e00\u6014\uff0c\u539f\u672c\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u795e\u8272\u987f\u65f6\u6d88\u5931\uff0c\u53d8\u5f97\u51dd\u91cd\u65e0\u6bd4\u3002\u201c\u8fd9\u4e2a\u5fbd\u8bb0\u2026\u2026\u743c\u6069\uff0c\u8fd9\u5f20\u5377\u8f74\u4f60\u6700\u8fd1\u7ed9\u8c01\u770b\u8fc7\uff1f\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u7136\u540e\u53cd\u5e94\u8fc7\u6765\uff0c\u8d76\u5fd9\u628a\u5728\u6cf0\u745f\u5c14\u9047\u5230\u4f0a\u65af\u5854\u548c\u601d\u601d\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\u3002\u5965\u6c83\u8d8a\u542c\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u8d8a\u662f\u53e4\u602a\uff0c\u5230\u6700\u540e\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u7eb5\u58f0\u5927\u7b11\u8d77\u6765\u3002</p><p>\u201c\u54c8\u54c8\u54c8\u54c8\u54c8\u54c8\u54c8\u54c8\uff01\u201d</p><p>\u201c\u8001\u5e08\u2026\u2026\u201d</p><p>\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u9053\u5965\u6c83\u600e\u4e48\u7a81\u7136\u5982\u6b64\u5931\u6001\uff0c\u96be\u9053\u81ea\u5df1\u521a\u624d\u8bf4\u4e86\u4ec0\u4e48\u8352\u5510\u6ed1\u7a3d\u7684\u8bdd\u4e0d\u6210\uff1f\u4f46\u60f3\u60f3\u4e5f\u6ca1\u8bf4\u9519\u4ec0\u4e48\u554a\u3002\u6b63\u81ea\u4e0d\u77e5\u6240\u63aa\u95f4\uff0c\u5965\u6c83\u52c9\u5f3a\u6446\u4e86\u6446\u624b\uff0c\u793a\u610f\u4ed6\u4e0d\u7528\u7d27\u5f20\u3002</p><p>\u201c\u6ca1\u4e8b\uff0c\u6ca1\u4e8b\uff0c\u201d\u4ed6\u6367\u8179\u72c2\u7b11\u4e0d\u6b62\uff0c\u51e0\u4e4e\u50cf\u8981\u5598\u4e0d\u8fc7\u6c14\u6765\u4f3c\u7684\uff0c\u867d\u7136\u4f5c\u4e3a\u5deb\u5996\uff0c\u4ed6\u5176\u5b9e\u6839\u672c\u5c31\u65e0\u9700\u547c\u5438\uff0c\u201c\u6211\u4e0d\u662f\u7b11\u4f60\uff0c\u6211\u662f\u7b11\u90a3\u4e2a\u5bb6\u4f19\uff0c\u98ce\u5149\u4e00\u4e16\uff0c\u5230\u6700\u540e\u5c45\u7136\u843d\u5230\u8fd9\u4e2a\u4e0b\u573a\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u5c0f\u59d1\u5a18\u2014\u2014\u54c8\u54c8\u54c8\u54c8\u54c8\u2026\u2026\u54ce\u5440\uff01\u201d</p><p>\u8001\u5deb\u5996\u7b11\u5f97\u5b9e\u5728\u592a\u8fc7\u6b22\u7545\uff0c\u4ee5\u81f3\u4e8e\u4ece\u8eba\u6905\u4e2d\u7ed9\u6454\u4e86\u4e0b\u6765\uff0c\u80a5\u80d6\u7684\u8eab\u8eaf\u6eda\u843d\u5728\u5730\uff0c\u4f3c\u4e4e\u8fd8\u50cf\u76ae\u7403\u4e00\u6837\u5f39\u4e86\u5f39\uff0c\u8ba9\u743c\u6069\u770b\u5f97\u5b9e\u5728\u65e0\u8bed\uff0c\u8fde\u5fd9\u4e0a\u524d\u5c06\u4ed6\u6276\u8d77\u3002</p><p>\u201c\u8001\u5e08\uff0c\u60a8\u5c0f\u5fc3\u70b9\uff0c\u201d\u4ed6\u5c06\u5965\u6c83\u6276\u56de\u8eba\u6905\u4e2d\uff0c\u201c\u60a8\u8bf4\u7684\u2018\u90a3\u4e2a\u5bb6\u4f19\u2019\uff0c\u4ed6\u662f\u2026\u2026\u201d</p><p>\u201c\u4f60\u5176\u5b9e\u80af\u5b9a\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u53ea\u662f\u6ca1\u6709\u5bf9\u4e0a\u53f7\u7f62\u4e86\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u6e10\u6e10\u5e73\u9759\uff0c\u53c8\u6062\u590d\u4e86\u5927\u5965\u672f\u5e08\u7684\u4ece\u5bb9\u6c14\u5ea6\uff0c\u201c\u4e0b\u6b21\u518d\u8ddf\u4f60\u89e3\u91ca\uff0c\u73b0\u5728\u522b\u95ee\u90a3\u4e48\u591a\uff0c\u4f60\u5148\u4e0b\u53bb\u4f11\u606f\u5427\uff0c\u6211\u8981\u7814\u7a76\u4e00\u4e0b\u8fd9\u5f20\u5377\u8f74\u7684\u5185\u5bb9\u3002\u201d</p><p>\u743c\u6069\u4e0d\u6562\u518d\u591a\u95ee\uff0c\u8eac\u8eab\u884c\u793c\u9000\u4e0b\u3002\u5965\u6c83\u5f85\u4ed6\u79bb\u5f00\uff0c\u62ff\u8d77\u5377\u8f74\u51dd\u89c6\u534a\u664c\uff0c\u5374\u4e0d\u662f\u770b\u5185\u5bb9\uff0c\u800c\u662f\u4e00\u76f4\u76ef\u7740\u53f3\u4e0b\u89d2\u3002\u90a3\u91cc\u770b\u4e0a\u53bb\u662f\u7a7a\u767d\uff0c\u4f46\u5728\u8001\u5deb\u5996\u7684\u773c\u4e2d\uff0c\u5374\u6e05\u6670\u6d6e\u73b0\u7740\u4e00\u4e2a\u7cbe\u81f4\u7684\u5fbd\u8bb0\u3002</p><p>\u201c\u539f\u6765\u4f60\u4e5f\u8fd8\u6ca1\u6b7b\u4e48\uff0c\u201d\u4ee5\u4f4e\u5fae\u5f97\u51e0\u4e4e\u542c\u4e0d\u89c1\u7684\u58f0\u97f3\uff0c\u4ed6\u81ea\u8a00\u81ea\u8bed\u7740\uff0c\u201c\u770b\u8d77\u6765\uff0c\u8fd9\u4e2a\u4e16\u754c\u771f\u662f\u4f1a\u53d8\u5f97\u8d8a\u6765\u8d8a\u6709\u8da3\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5965\u6c83\u81ea\u8a00\u81ea\u8bed\u7684\u65f6\u5019\uff0c\u743c\u6069\u5df2\u7ecf\u9000\u51fa\uff0c\u5e76\u6ca1\u6709\u542c\u5230\u540e\u9762\u7684\u8bdd\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u4e5f\u5df2\u7ecf\u9690\u9690\u7ea6\u7ea6\u731c\u5230\u4e86\u4e00\u4e9b\u4e1c\u897f\u3002\u9690\u85cf\u5728\u601d\u601d\u4f53\u5185\u7684\u90a3\u4e2a\u5927\u5965\u672f\u5e08\uff0c\u5965\u6c83\u8bf4\u743c\u6069\u201c\u80af\u5b9a\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u201d\uff0c\u8fd9\u5c31\u662f\u5f88\u6e05\u6670\u7684\u7ebf\u7d22\u4e86\u3002</p><p>\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u5ef6\u7eed\u4e09\u5343\u591a\u5e74\uff0c\u5927\u5965\u672f\u5e08\u4e0d\u53ef\u8ba1\u6570\uff0c\u4e00\u822c\u4eba\u6839\u672c\u8bb0\u4e0d\u5168\uff0c\u4e5f\u5c31\u53ea\u77e5\u9053\u6700\u8457\u540d\u7684\u90a3\u4e9b\uff0c\u4f55\u51b5\u9634\u9b42\u57ce\u5bf9\u5728\u8fd9\u65b9\u9762\u7684\u6559\u80b2\u5e76\u4e0d\u5f88\u770b\u91cd\u3002\u743c\u6069\u5728\u5b66\u6821\u91cc\uff0c\u65f6\u95f4\u57fa\u672c\u5168\u90fd\u82b1\u5728\u5b66\u4e60\u9b54\u6cd5\u4e0a\u53bb\u4e86\uff0c\u5bf9\u201c\u5386\u53f2\u201d\u4e5f\u6ca1\u4e0b\u591a\u5927\u529f\u592b\uff0c\u5f53\u65e5\u5e03\u96f7\u7eb3\u65af\u8ba9\u4ed6\u53bb\u627e\u62c9\u6c83\u514b\u9001\u4fe1\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u62c9\u6c83\u514b\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u540e\u6765\u624d\u77e5\u9053\u4e5f\u662f\u4e00\u4f4d\u5927\u5965\u672f\u5e08\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u80fd\u591f\u8ba9\u743c\u6069\u201c\u80af\u5b9a\u542c\u8fc7\u201d\u7684\uff0c\u90a3\u8981\u4e48\u5c31\u662f\u60ca\u4e16\u7edd\u8273\u7684\u5929\u624d\uff0c\u5927\u5965\u672f\u5e08\u4e2d\u7684\u5927\u5965\u672f\u5e08\uff1b\u8981\u4e48\u5c31\u662f\u5728\u67d0\u65b9\u9762\u6781\u6709\u7279\u8272\uff0c\u6781\u6709\u4e2a\u6027\uff0c\u8ba9\u4eba\u65e0\u6cd5\u5fd8\u8bb0\uff0c\u4f8b\u5982\u5965\u6c83\u8fd9\u6b7b\u80d6\u5b50\uff1b\u8981\u4e48\u5c31\u662f\u6709\u4ec0\u4e48\u7279\u6b8a\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u5c31\u50cf\u5eb7\u6770\u8bfa\xb7\u827e\u6069\uff0c\u867d\u7136\u4e0d\u7b97\u5f88\u5f3a\uff0c\u4f46\u4ed6\u662f\u201c\u8010\u745f\u745e\u5c14\u7b2c\u4e00\u4e2a\u5927\u5965\u672f\u5e08\u201d\uff0c\u5f00\u98ce\u6c14\u4e4b\u5148\uff0c\u9886\u6f6e\u6d41\u4e4b\u59cb\uff0c\u81ea\u7136\u6ca1\u4eba\u80fd\u591f\u5ffd\u7565\u3002</p><p>\u8fd9\u79cd\u4eba\uff0c\u6570\u91cf\u5e76\u4e0d\u4f1a\u5f88\u591a\u3002</p><p>\u201c\u662f\u53d1\u660e\u6d6e\u7a7a\u57ce\u7684\u2018\u8010\u745f\u4e4b\u7236\u2019\u4f0a\u5965\u52d2\u59c6\uff1f\u6709\u53f2\u4ee5\u6765\u6700\u5e74\u8f7b\u7684\u5929\u624d\u5361\u5c14\u8428\u65af\uff1f\u5f00\u521b\u53d1\u73b0\u5e74\u4ee3\u7684\u2018\u65c5\u8005\u2019\u6b27\u8d1d\u4f26\uff1f\u52fe\u7ed3\u8d39\u6797\u9b54\u8475\u7684\u2018\u6050\u60e7\u7537\u7235\u2019\u5965\u6d1b\u65af\u4e01\uff1f\u603b\u4e0d\u4f1a\u662f\u90a3\u4e2a\u4f20\u8bf4\u4e2d\u8fde\u6027\u522b\u90fd\u4e0d\u6e05\u695a\u7684\u795e\u79d8\u4eba\u2018\u5927\u5730\u5148\u77e5\u2019\u5427\uff1f\u201d</p><p>\u743c\u6069\u6447\u4e86\u6447\u5934\uff0c\u628a\u8fd9\u4e9b\u4eba\u540d\u6682\u65f6\u62b9\u53bb\u3002\u773c\u4e0b\u4e0d\u662f\u8003\u8651\u8fd9\u4e2a\u7684\u65f6\u5019\uff0c\u8001\u5bb6\u4f19\u4eec\u7684\u4e8b\u60c5\uff0c\u5c31\u8ba9\u8001\u5bb6\u4f19\u4eec\u53bb\u64cd\u5fc3\uff0c\u81ea\u5df1\u8fd8\u6709\u4e00\u5806\u9ebb\u70e6\u7b49\u7740\u641e\u5b9a\u5462\u3002</p><p>\u56de\u5230\u623f\u95f4\uff0c\u838e\u73de\u514b\u6b63\u5728\u68c0\u67e5\u5979\u7684\u708e\u9b54\u76ae\u7532\uff0c\u743c\u6069\u72b9\u8c6b\u7247\u523b\uff0c\u628a\u5b9d\u77f3\u754c\u57df\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\u3002\u8ba9\u4ed6\u5927\u5927\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u838e\u73de\u514b\u542c\u5b8c\u540e\u6beb\u65e0\u534a\u70b9\u60ca\u8bb6\u3001\u607c\u6012\uff0c\u5e73\u5e73\u9759\u9759\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u597d\u3002\u201d</p><p>\u201c\u4f60\u613f\u610f\uff1f\u201d</p><p>\u201c\u96be\u9053\u4f60\u5e0c\u671b\u770b\u5230\u6211\u4e0d\u613f\u610f\uff1f\u201d\u838e\u73de\u514b\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6216\u8005\u8bf4\uff0c\u6211\u4e0d\u613f\u610f\u7684\u8bdd\uff0c\u53c8\u80fd\u600e\u4e48\u6837\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u2026\u2026\u201d</p><p>\u201c\u90fd\u4e00\u6837\uff0c\u6ca1\u533a\u522b\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u5f3a\u8005\u4e3a\u5c0a\uff0c\u5f31\u8005\u670d\u4ece\uff0c\u8fd9\u672c\u6765\u5c31\u662f\u4e16\u754c\u7684\u771f\u7406\uff0c\u4e5f\u662f\u6211\u4e00\u76f4\u4fe1\u5949\u7684\u89c2\u5ff5\u3002\u4f60\u6bd4\u6211\u5f3a\uff0c\u90a3\u4e48\u4f60\u505a\u4ec0\u4e48\u81ea\u7136\u90fd\u662f\u5bf9\u7684\uff0c\u53cd\u8fc7\u6765\u4e5f\u662f\u5982\u6b64\u3002\u65e2\u7136\u4f60\u662f\u5bf9\u7684\uff0c\u90a3\u4e48\u6211\u5f53\u7136\u8981\u670d\u4ece\u4f60\u2014\u2014\u8fd9\u6709\u4ec0\u4e48\u95ee\u9898\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u95ee\u9898\uff0c\u53ea\u4e0d\u8fc7\uff0c\u201d\u743c\u6069\u82e6\u7b11\uff0c\u201c\u4f60\u771f\u8c41\u8fbe\u2026\u2026\u201d</p><p>\u201c\u563f\uff0c\u8fd9\u548c\u8c41\u8fbe\u4ec0\u4e48\u7684\u53ef\u6ca1\u5173\u7cfb\uff0c\u6211\u662f\u7eaf\u7cb9\u7684\u529f\u5229\u4e3b\u4e49\u8005\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u65e0\u8bba\u8fc7\u53bb\u600e\u6837\uff0c\u65e0\u8bba\u60c5\u613f\u4e0d\u60c5\u613f\uff0c\u73b0\u5728\u6211\u4eec\u7684\u547d\u8fd0\uff0c\u4e8b\u5b9e\u4e0a\u5df2\u7ecf\u7ed1\u5728\u4e00\u8d77\uff0c\u8ba4\u6e05\u8fd9\u4e00\u70b9\uff0c\u7136\u540e\u624d\u8c08\u5f97\u4e0a\u672a\u6765\u3002\u201d</p><p>\u201c\u90a3\u672a\u6765\u4f1a\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u838e\u73de\u514b\u8f7b\u58f0\u51b7\u7b11\uff0c\u201c\u6211\u6240\u77e5\u9053\u7684\u662f\uff1a\u6211\u4f1a\u6c38\u8fdc\u7ad9\u5728\u80dc\u5229\u8005\u4e00\u8fb9\u3002\u201d</p></article>"}}}]);
