(window.webpackJsonp=window.webpackJsonp||[]).push([[41],{64:function(p){p.exports={chapterTitle:null,title:"\u7b2c1031\u7ae0 \u51fa\u7a81\u610f\u6599\u7684\u4e00\u5df4\u638c",content:"<article><h2>\u7b2c1031\u7ae0 \u51fa\u7a81\u610f\u6599\u7684\u4e00\u5df4\u638c</h2><p>\u3000\u3000\u4e0d\u5f97\u4e0d\u627f\u8ba4\u533b\u9662\u4fdd\u536b\u5904\u7684\u5de5\u4f5c\u975e\u5e38\u7684\u8fc5\u901f\uff0c\u5f20\u606a\u4ed6\u4eec\u5c31\u5728\u75c5\u623f\u91cc\u7b80\u5355\u5403\u8fc7\u4e2d\u996d\uff0c\u82b1\u886c\u886b\u7537\u4e0e\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u8fd8\u6709\u90a3\u4e2a\u81ea\u79f0\u662f\u533b\u9662\u4fdd\u536b\u5904\u5218\u5904\u957f\u7684\u7537\u4eba\u5c31\u62ff\u4e86\u4e00\u53e0\u539a\u539a\u6750\u6599\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u7684\u8c03\u67e5\u5de5\u4f5c\u662f\u5f88\u7ec6\u81f4\u7684\uff0c\u786e\u4fdd\u4e0d\u4f1a\u51a4\u6789\u4e00\u4e2a\u597d\u4eba\uff0c\u6211\u4eec\u4fdd\u536b\u5904\u6bcf\u5e74\u90fd\u8981\u63a5\u53d7\u533a\u516c\u5b89\u5c40\u7684\u57f9\u8bad\uff0c\u6709\u8fd9\u4e9b\u6750\u6599\u5728\u624b\u91cc\uff0c\u5c31\u7b97\u4f60\u518d\u53cd\u53e3\u5426\u8ba4\uff0c\u6211\u4eec\u4e5f\u80fd\u5b9a\u4f60\u7684\u7f6a\u2026\u2026\u201d\u533b\u9662\u4fdd\u536b\u5904\u59d3\u5218\u7684\u5904\u957f\u5f97\u610f\u6d0b\u6d0b\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u6709\u6743\u529b\u5b9a\u6211\u7684\u7f6a\uff1f\u201d\u5f20\u606a\u5750\u5728\u9648\u5983\u84c9\u7684\u75c5\u5e8a\u524d\uff0c\u659c\u8fc7\u5934\u770b\u4e86\u8fd9\u7537\u4eba\u4e00\u773c\uff0c\u795e\u8272\u51b7\u6de1\u7684\u95ee\u9053\uff0c\u5c06\u4ed6\u624b\u91cc\u7684\u6750\u6599\u62ff\u8fc7\u6765\u3002</p><p>\u3000\u3000\u6709\u76d1\u63a7\u6444\u50cf\u5934\u672a\u6b63\u5e38\u5de5\u4f5c\u7684\u8bf4\u660e\u3001\u6709\u5bfc\u533b\u53f0\u62a4\u58eb\u7684\u8bc1\u8a00\uff0c\u5f53\u7136\u4e5f\u6709\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u4e0e\u5f90\u8001\u592a\u7684\u8bc1\u8a00\uff1b\u4ed6\u4eec\u8fd8\u627e\u9694\u58c1\u75c5\u623f\u627e\u4e86\u4e24\u4e2a\u4f4f\u9662\u75c5\u4eba\u5199\u4e86\u521a\u624d\u5728\u8fc7\u9053\u53d1\u751f\u52a8\u624b\u7ea0\u7eb7\u7684\u60c5\u51b5\u8bf4\u660e\uff0c\u65e0\u975e\u4e5f\u662f\u8981\u54ac\u5b9a\u5f20\u606a\u81ea\u5df1\u5728\u8fc7\u9053\u91cc\u627f\u8ba4\u649e\u5012\u4e86\u4eba\uff1b\u8fd8\u6709\u533b\u751f\u7ed9\u5f90\u8001\u592a\u7684\u590d\u8bca\u4e0e\u4f53\u68c0\u62a5\u544a\uff0c\u8fd8\u771f\u96be\u4e3a\u4ed6\u4eec\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u6574\u7406\u51fa\u8fd9\u4e48\u9f50\u5168\u7684\u590d\u8bca\u4e0e\u4f53\u68c0\u62a5\u544a\u6765\uff1b\u5f53\u7136\u4e86\uff0c\u6700\u91cd\u8981\u7684\u8fd8\u6709\u4e00\u4efd\u6709\u4e24\u540d\u9aa8\u79d1\u4e13\u5bb6\u53c2\u52a0\u4f1a\u8bca\u7684\u6cbb\u7597\u4e0e\u8eab\u4f53\u6062\u590d\u65b9\u6848\u8bf4\u660e\uff0c\u6ca1\u6709\u8fd9\u6cbb\u7597\u4e0e\u8eab\u4f53\u6062\u590d\u65b9\u6848\uff0c\u4e5f\u62b9\u4e0d\u4e0b\u8138\u6765\u72ee\u5b50\u5927\u5f00\u53e3\u3002</p><p>\u3000\u3000\u201c\u9664\u4e86\u8fd9\u4e9b\u786c\u6027\u7684\u8d54\u507f\u4e4b\u540e\uff0c\u4f60\u4eec\u649e\u5012\u4eba\u4e4b\u540e\u9003\u8dd1\u3001\u8fd8\u5728\u5927\u5385\u91cc\u7528\u4e1c\u897f\u7838\u4eba\u3001\u5728\u4f4f\u9662\u697c\u8fc7\u9053\u91cc\u52a8\u624b\u6253\u4eba\uff0c\u7ed9\u5f90\u8001\u592a\u4e0e\u4ed6\u7684\u513f\u5b50\u3001\u513f\u5ab3\u9020\u6210\u5f88\u4e25\u91cd\u7684\u5fc3\u7406\u635f\u5931\uff0c\u4ed6\u4eec\u4e5f\u9002\u5f53\u7684\u63d0\u51fa\u7cbe\u795e\u8d54\u507f\uff0c\u6211\u4eec\u521d\u6b65\u7684\u62df\u4e86\u4e00\u4efd\u548c\u89e3\u4e66\uff0c\u4f60\u8981\u6ca1\u6709\u4ec0\u4e48\u5f02\u8bae\uff0c\u5c31\u5728\u548c\u89e3\u4e66\u4e0a\u7b7e\u5b57\u2026\u2026\u201d\u533b\u9662\u4fdd\u536b\u5904\u59d3\u5218\u7684\u5904\u957f\u53c8\u9012\u4e0a\u6765\u4e24\u9875\u6253\u5370\u7eb8\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u53c8\u63a5\u8fc7\u6253\u5370\u7eb8\u770b\u4e86\u770b\u6240\u8c13\u7684\u548c\u89e3\u534f\u8bae\uff0c\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u62ac\u5934\u770b\u4e86\u770b\u53cc\u624b\u62b1\u80f8\u7ad9\u5728\u540e\u9762\u7684\u82b1\u886c\u886b\u7537\uff1a\u201c\u662f\u4e0d\u662f\u4f55\u7ecf\u7406\u4f60\u8ddf\u4f60\u7231\u4eba\u5148\u7b7e\u5b57\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5148\u7b7e\u5c31\u5148\u7b7e\uff0c\u6211\u4eec\u4e5f\u662f\u5f88\u8bb2\u4fe1\u4e49\u7684\u4eba\uff0c\u53ea\u8981\u4f60\u4e00\u6b21\u6027\u8d54\u5341\u4e00\u4e07\uff0c\u6211\u5988\u4ee5\u540e\u8981\u518d\u51fa\u4e86\u4ec0\u4e48\u95ee\u9898\uff0c\u8ddf\u4f60\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\uff0c\u201d\u82b1\u886c\u886b\u7537\u8d70\u5230\u524d\u9762\u6765\u4ece\u5f20\u606a\u624b\u91cc\u63a5\u8fc7\u548c\u89e3\u534f\u8bae\uff0c\u8ba9\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u4ece\u516c\u6587\u5305\u91cc\u62ff\u51fa\u7b14\u6765\u51c6\u5907\u51d1\u5230\u7a97\u53f0\u4e0a\u7b7e\u5b57\uff0c\u5b57\u521a\u7b7e\u5230\u4e00\u534a\uff0c\u56de\u8fc7\u5934\u6765\u95ee\uff0c\u201c\u4f60\u77e5\u9053\u6211\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u52a8\u4e86\u52a8\uff0c\u7b97\u662f\u7b11\u4e86\u4e00\u4e0b\uff0c\u6ca1\u6709\u7406\u4f1a\u82b1\u886c\u886b\u7537\u7684\u95ee\u9898\u3002\u82b1\u886c\u886b\u7537\u89c1\u4ed6\u8fd9\u4ed8\u81ed\u8138\u6a21\u6837\uff0c\u9f3b\u8154\u7684\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u65e2\u7136\u80fd\u62ff\u5230\u94b1\uff0c\u4e5f\u80fd\u6682\u65f6\u5fcd\u53d7\u4ed6\u8fd9\u81ed\u8138\uff0c\u5728\u534f\u8bae\u4e66\u4e0a\u7b7e\u4e86\u5b57\uff0c\u53c8\u4ea4\u7ed9\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u7b7e\uff0c\u7b7e\u597d\u5b57\u5c31\u5c06\u534f\u8bae\u4e66\u9012\u8fc7\u6765\uff0c\u201c\u8be5\u4f60\u4eec\u7b7e\u5b57\u4e86\uff0c\u7b7e\u5b57\u8d76\u7d27\u901a\u77e5\u4f60\u4eec\u5bb6\u91cc\u4eba\u6c47\u94b1\u8fc7\u6765\uff0c\u6211\u4eec\u53ef\u6ca1\u6709\u4ec0\u4e48\u8010\u5fc3\u2014\u2014\u544a\u8bc9\u4f60\u4eec\u4e00\u58f0\uff0c\u4f60\u4eec\u5b66\u6821\u521a\u521a\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u9a6c\u4e0a\u4f1a\u6d3e\u4eba\u8fc7\u6765\uff0c\u4f60\u4eec\u8981\u662f\u62c5\u5fc3\u6211\u6b3a\u8d1f\u4f60\u4eec\u662f\u5b66\u751f\uff0c\u53ef\u4ee5\u62d6\u5230\u4f60\u4eec\u5b66\u6821\u8001\u5e08\u8fc7\u6765\u518d\u7b7e\uff0c\u6211\u8fd9\u4efd\u8d54\u507f\u534f\u8bae\u53ef\u90fd\u662f\u6709\u7406\u6709\u636e\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5b66\u5165\u5b66\u6237\u53e3\u8981\u8fc1\u5165\u5b66\u6821\u6240\u5728\u5730\uff0c\u867d\u7136\u9648\u5983\u84c9\u4f4f\u9662\u65f6\u6ca1\u6709\u63d0\u662f\u4e1c\u6d77\u5927\u5b66\u7684\u5b66\u751f\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u4e22\u7ed9\u4ed6\u4eec\u7684\u8eab\u4efd\u8bc1\u4e0a\u7684\u5730\u5740\uff0c\u5c31\u4ee5\u4e3a\u4ed6\u4eec\u90fd\u662f\u4e1c\u5927\u7684\u5b66\u751f\uff0c\u5148\u5c06\u7535\u8bdd\u6253\u5230\u4e1c\u5927\u5b66\u751f\u5de5\u4f5c\u5904\uff0c\u5c06\u6240\u6709\u7684\u4e8b\u60c5\u90fd\u5f80\u5f20\u606a\u4ed6\u4eec\u5934\u4e0a\u683d\uff0c\u7701\u5f97\u4ed6\u4eec\u627e\u5b66\u6821\u5f53\u9760\u5c71\u8fd8\u8981\u7ea0\u7f20\uff0c\u82b1\u886c\u886b\u7537\u81ea\u4ee5\u4e3a\u8003\u8651\u5468\u5168\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u770b\u5f20\u606a\u4ee5\u53ca\u53e6\u5916\u4e09\u4e2a\u773c\u795e\u6124\u6012\u7684\u5973\u5b69\u5b50\uff0c\u5374\u51b7\u4e0d\u9632\u5085\u4fca\u4ece\u540e\u9762\u4f38\u624b\u8fc7\u6765\u6293\u4f4f\u4ed6\u7684\u8116\u5b50\u5c31\u5f80\u5916\u62d6\uff0c\u624b\u91cc\u7684\u534f\u8bae\u4e66\u7ed9\u5085\u4fca\u53e6\u4e00\u53ea\u624b\u4e00\u628a\u62a2\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d\u82b1\u886c\u886b\u7537\u4e24\u624b\u4e71\u6293\uff0c\u5374\u6293\u4e0d\u5230\u5085\u4fca\uff0c\u4e24\u817f\u7ed9\u62d6\u5728\u5730\u4e0a\u4e71\u8e6c\u7740\uff0c\u6839\u672c\u5c31\u65e0\u6cd5\u4ece\u5085\u4fca\u624b\u91cc\u6323\u624e\u5f00\u6765\uff0c\u8eab\u4f53\u5448\u56db\u5341\u4e94\u5ea6\u89d2\u5012\u683d\u7740\u76f4\u63a5\u7ed9\u62d6\u5230\u8fc7\u9053\u91cc\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u662f\u8c01\uff0c\u533b\u9662\u91cc\u4f60\u4eec\u6562\u4e71\u6765\uff01\u201d\u59d3\u5218\u7684\u5904\u957f\u58f0\u8272\u4ff1\u5389\u7684\u8bf4\u9053\uff0c\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u8981\u51b2\u8fc7\u53bb\u5e2e\u5fd9\uff0c\u4e24\u4eba\u5374\u7ed9\u4e24\u540d\u4fdd\u536b\u8d70\u8fdb\u6765\u6284\u8fc7\u80f3\u818a\u3001\u62ff\u624b\u6307\u8282\u9876\u5728\u4ed6\u4eec\u7684\u814b\u7a9d\u5b50\u91cc\u65e0\u6cd5\u53cd\u6297\u7684\u7ed9\u8bf7\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u4e5f\u4e0d\u80fd\u5728\u533b\u9662\u91cc\u5927\u6253\u51fa\u624b\uff0c\u5085\u4fca\u5c06\u4eba\u62d6\u51fa\u75c5\u623f\u653e\u4e86\u624b\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u60f3\u5e72\u4ec0\u4e48\uff0c\u5feb\u53eb\u4fdd\u5b89\u2026\u2026\u201d\u59d3\u5218\u7684\u5904\u957f\u521a\u624d\u5728\u8fc7\u9053\u91cc\u53ea\u770b\u5230\u5085\u4fca\u4e00\u4e2a\u4eba\uff0c\u8fd9\u65f6\u5019\u5374\u7ad9\u4e86\u56db\u4e94\u4e2a\u7a7f\u7740\u4e00\u5f0f\u4f11\u95f2\u77ed\u8896\u886c\u886b\u7684\u7537\u5b50\uff0c\u770b\u5230\u8fc7\u9053\u91cc\u7ad9\u7740\u62a4\u58eb\u89c2\u671b\uff0c\u5927\u53eb\u9053\uff0c\u201c\u4f60\u5feb\u53bb\u62a5\u8b66\u3002\u201d</p><p>\u3000\u3000\u201c\u75c5\u623f\u91cc\u8fd8\u6709\u75c5\u4eba\u8981\u4f11\u606f\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u53e6\u627e\u5730\u65b9\u8c08\u8d54\u507f\u7684\u4e8b\u60c5\uff1f\u201d\u5f20\u606a\u6162\u6761\u4e1d\u7406\u7684\u8d70\u51fa\u6765\uff0c\u770b\u4e86\u770b\u59d3\u5218\u7684\u5904\u957f\u8ddf\u82b1\u886c\u886b\u7537\u8fd8\u6709\u70ed\u88e4\u5999\u9f84\u5c11\u5973\uff0c\u201c\u4ed6\u4eec\u662f\u6211\u5bb6\u7684\u53f8\u673a\uff0c\u521a\u521a\u53bb\u53d6\u94b1\u2026\u2026\u201d\u4f38\u811a\u8e22\u4e86\u8e22\u653e\u5728\u8fc7\u9053\u5899\u6839\u7684\u4e00\u53ea\u9ed1\u8272\u5bc6\u7801\u7bb1\u3002</p><p>\u3000\u3000\u201c\u8bf7\u4f60\u4eec\u5728\u533b\u9662\u91cc\u7a0d\u5fae\u5ba2\u6c14\u4e00\u70b9\uff01\u201d\u59d3\u5218\u7684\u5904\u957f\u7406\u4e86\u7406\u7ed9\u6324\u76b1\u7684\u886c\u886b\uff0c\u5429\u5490\u90a3\u62a4\u58eb\uff0c\u201c\u4e0d\u8981\u62a5\u8b66\u4e86\uff0c\u53eb\u4fdd\u5b89\u4e0a\u6765\u3002\u201d</p><p>\u3000\u3000\u4f55\u5f26\u8ddf\u4e86\u51fa\u6765\uff0c\u8d34\u8eab\u7ad9\u5728\u5f20\u606a\u7684\u4fa7\u540e\uff0c\u5c0f\u58f0\u8bf4\uff1a\u201c\u6211\u8ddf\u4f60\u4eec\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u649e\u5012\u7684\u4eba\uff0c\u4f60\u5f53\u7136\u8981\u8fc7\u53bb\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u53c8\u5c06\u90a3\u53e0\u6750\u6599\u4e00\u5e76\u4ea4\u7ed9\u5085\u4fca\u62ff\u7740\uff0c\u5bf9\u59d3\u5218\u7684\u5904\u957f\u8bf4\uff0c\u201c\u65e2\u7136\u6709\u8fd9\u4e48\u591a\u8bc1\u4eba\u8bc1\u8f9e\uff0c\u90a3\u8bf7\u4f60\u627e\u4e2a\u5730\u65b9\uff0c\u5c06\u8fd9\u4e9b\u8bc1\u4eba\u90fd\u8bf7\u8fc7\u6765\u5f53\u9762\u786e\u8ba4\u4e00\u4e0b\uff0c\u8c01\u77e5\u9053\u662f\u4e0d\u662f\u4f60\u4eec\u5f04\u4e86\u4e9b\u4e1c\u897f\u968f\u4fbf\u627e\u4eba\u7b7e\u4e86\u5b57\u7cca\u5f04\u6211\u4eec\uff1f\u201d</p><p>\u3000\u3000\u4ece\u7535\u68af\u91cc\u8d70\u51fa\u6765\u51e0\u540d\u533b\u9662\u91cc\u7684\u4fdd\u5b89\uff0c\u82b1\u886c\u886b\u7537\u6c14\u52bf\u5c31\u51f6\u4e86\u8d77\u6765\uff0c\u624b\u6307\u7740\u5f20\u606a\u7684\u9f3b\u5b50\u5c31\u9a82\uff1a\u201c\u4f60\u5988\u7684\uff0c\u6709\u4eba\u8fc7\u6765\u5c31\u60f3\u62b5\u8d56\u2026\u2026\u201d\u7ed9\u5085\u4fca\u5728\u65c1\u4f38\u624b\u6293\u4f4f\u5f80\u5916\u62d7\uff0c\u82b1\u886c\u886b\u7537\u75db\u5f97\u201c\u55f7\u55f7\u201d\u76f4\u53eb\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8bc1\u636e\u786e\u51ff\uff0c\u6211\u60f3\u62b5\u8d56\u4e5f\u62b5\u8d56\u4e0d\u4e86\uff0c\u201d\u5f20\u606a\u6325\u4e86\u6325\u8ba9\u5085\u4fca\u653e\u5f00\u4ed6\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u662f\u5728\u8fc7\u9053\u5c31\u8c08\u548c\u89e3\u534f\u8bae\u8fd8\u662f\u8ddf\u533b\u9662\u501f\u95f4\u529e\u516c\u5ba4\u6216\u8005\u4f1a\u8bae\u5ba4\u2014\u2014\u8c08\u548c\u89e3\u534f\u8bae\u65f6\u8bf7\u8096\u7ecf\u7406\u6ce8\u610f\u4e00\u4e0b\u7528\u8bcd\uff0c\u6211\u5bb6\u7684\u53f8\u673a\u6027\u5b50\u90fd\u6bd4\u8f83\u7c97\u66b4\uff0c\u5230\u65f6\u5019\u518d\u5f04\u4f24\u4f60\uff0c\u8fd8\u8981\u6211\u8d54\u94b1\uff0c\u6211\u53ef\u4e0d\u5e72\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5e72\u4ec0\u4e48\uff01\u201d\u51e0\u540d\u4fdd\u5b89\u63a8\u6421\u7740\u51b2\u8fc7\u6765\u8981\u52a8\u624b\u3002</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u201d\u59d3\u5218\u7684\u5904\u957f\u4e5f\u4e0d\u6562\u5728\u533b\u9662\u5c06\u4e8b\u60c5\u641e\u5927\uff0c\u8ddf\u82b1\u886c\u886b\u7537\u8bf4\u9053\uff0c\u201c\u6211\u7406\u89e3\u4f60\u7684\u5fc3\u60c5\uff0c\u53ea\u8981\u4ed6\u4eec\u80af\u8d54\u94b1\uff0c\u4f60\u8bf4\u8bdd\u4e5f\u514b\u5236\u4e00\u4e0b\u2014\u2014\u5c31\u7b97\u4ed6\u4eec\u4e0d\u80af\u8d54\u94b1\uff0c\u4e0d\u662f\u8fd8\u6709\u8b66\u5bdf\u561b\uff01\u201d</p><p>\u3000\u3000\u82b1\u886c\u886b\u7537\u6124\u6068\u4e0d\u5df2\u7684\u6536\u4f4f\u8981\u52a8\u624b\u7684\u67b6\u5f0f\uff0c\u4f4f\u9662\u90e8\u7684\u8d1f\u8d23\u4eba\u4e5f\u8dd1\u4e86\u8fc7\u6765\uff0c\u4e5f\u5c31\u662f\u6628\u5929\u7ed9\u9648\u5983\u84c9\u52a8\u624b\u672f\u7684\u90a3\u4e2a\u53c8\u77ee\u53c8\u80d6\u7684\u5973\u4eba\uff0c\u5979\u624b\u63d2\u5728\u767d\u5927\u8902\u53e3\u888b\u91cc\uff0c\u76b1\u7740\u7709\u5934\u4e0d\u8010\u70e6\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u662f\u4e0d\u662f\u51fa\u53bb\u89e3\u51b3\uff1f\u201d\u53c8\u8bad\u65a5\u5728\u65c1\u56f4\u89c2\u7684\u62a4\u58eb\uff0c\u201c\u8be5\u5e72\u561b\u5e72\u561b\u53bb\uff0c\u4ee5\u540e\u9047\u5230\u8fd9\u79cd\u4e8b\u4e0d\u4f1a\u76f4\u63a5\u62a5\u8b66\uff01\u201d\u53c8\u5466\u559d\u4f4f\u9662\u75c5\u4eba\u8ddf\u75c5\u4eba\u5bb6\u5c5e\u56de\u75c5\u623f\u91cc\uff0c\u5c06\u8fc7\u9053\u91cc\u6e05\u7406\u4e86\u4e00\u4e0b\uff0c\u5979\u4eba\u8f6c\u8eab\u5c31\u8fdb\u4e86\u75c5\u623f\u5de1\u89c6\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u90a3\u53bb\u5341\u516b\u697c\u7684\u4f1a\u8bae\u5ba4\u8c08\u5427\uff01\u201d\u59d3\u5218\u7684\u5904\u957f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u662f\u4f60\u4eec\u7684\u5730\u65b9\uff0c\u6211\u4eec\u968f\u4fbf\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u82cf\u4e00\u5a77\u4ece\u75c5\u623f\u91cc\u8d70\u51fa\u6765\uff0c\u8ddf\u5f20\u606a\u4f4e\u58f0\u8033\u8bed\uff1a\u201c\u90a3\u7a7f\u82b1\u886c\u886b\u7684\u8ddf\u533b\u9662\u4e00\u4e2a\u526f\u9662\u957f\u8ba4\u8bc6\uff0c\u4ed6\u4eec\u8fd9\u624d\u4ee5\u4e3a\u80fd\u5403\u5b9a\u6211\u4eec\uff0c\u4f60\u6ce8\u610f\u4e9b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u60f3\u5fc5\u662f\u521a\u624d\u90a3\u4e2a\u5973\u5927\u592b\u8d70\u8fdb\u53bb\u63d0\u9192\u5979\u4eec\u4e86\uff0c\u8ba9\u5085\u4fca\u5c06\u88c5\u94b1\u7684\u5bc6\u7801\u7bb1\u63d0\u8d77\u6765\u8ddf\u7740\u4ed6\u4e0e\u4f55\u5f26\u4e00\u8d77\u4e0a\u697c\u53bb\u3002</p><p>\u3000\u3000\u51fa\u4e86\u7535\u68af\uff0c\u5927\u4f1a\u8bae\u5ba4\u5728\u8fc7\u9053\u9876\u5934\uff0c\u59d3\u5218\u7684\u5904\u957f\u8ddf\u4e24\u540d\u4fdd\u5b89\u5728\u524d\u9762\u9886\u5934\uff0c\u8d70\u5230\u4e00\u95f4\u529e\u516c\u5ba4\u95e8\u524d\uff0c\u5085\u4fca\u505c\u4e0b\u6765\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u5c31\u662f\u8fd9\u95f4\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u505c\u4e0b\u6765\u505a\u4ec0\u4e48\uff0c\u4f1a\u8bae\u5ba4\u5728\u524d\u9762\uff1f\u201d\u59d3\u5218\u7684\u5904\u957f\u4e0d\u8010\u70e6\u7684\u56de\u8fc7\u5934\u6765\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e3b\u6301\u548c\u89e3\uff0c\u59d3\u5218\u7684\u4f60\u8fd8\u4e0d\u591f\u683c\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u62ac\u5934\u770b\u4e86\u770b\u529e\u516c\u5ba4\u95e8\u6963\u4e0a\u7684\u91d1\u5c5e\u95e8\u724c\uff0c\u8bf4\u9053\uff0c\u201c\u770b\u6765\u8fd8\u662f\u8981\u9ebb\u70e6\u8fd9\u91cc\u7684\u9648\u526f\u9662\u957f\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u7ad9\u4f4f\u2026\u2026\u201d\u59d3\u5218\u7684\u6025\u7740\u8dd1\u8fc7\u6765\u8981\u6293\u5f20\u606a\u4ed6\u4eec\uff0c\u5f20\u606a\u4ed6\u4eec\u5374\u90fd\u5df2\u7ecf\u8d70\u4e86\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u88c5\u4fee\u8c6a\u534e\u7684\u529e\u516c\u5ba4\u9760\u5357\u9762\u7684\u843d\u5730\u5927\u7a97\u662f\u5f20\u6a80\u7ea2\u8272\u5927\u529e\u516c\u684c\uff0c\u6709\u4e2a\u7a7f\u7740\u767d\u5927\u8902\u7684\u767d\u80d6\u4e2d\u5e74\u4eba\u5750\u5728\u529e\u516c\u684c\u540e\u60ca\u7591\u7684\u770b\u7740\u5f20\u606a\u4ed6\u4eec\u95ef\u8fdb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u53c8\u7591\u60d1\u4e0d\u89e3\u7684\u770b\u7740\u4fdd\u536b\u5904\u7684\u5218\u5904\u957f\u8ddf\u5728\u540e\u9762\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u9648\u9662\u957f\uff0c\u4ed6\u4eec\u5c31\u662f\u649e\u5012\u5f90\u8001\u592a\u53c8\u6e9c\u8d70\u7684\u4e24\u540d\u4e1c\u5927\u5b66\u751f\uff0c\u6211\u6b63\u51c6\u5907\u9886\u4ed6\u4eec\u53bb\u4f1a\u8bae\u5ba4\u8c08\u548c\u89e3\u7684\u4e8b\u60c5\u2026\u2026\u4ed6\u4eec\u60f3\u8bf7\u9648\u9662\u957f\u4e3b\u6301\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u7684\u4e8b\u60c5\u6211\u4e5f\u521d\u6b65\u4e86\u89e3\u4e00\u4e0b\uff0c\u201d\u7a7f\u767d\u5927\u8902\u7684\u9648\u526f\u9662\u957f\u5c06\u9f3b\u6881\u4e0a\u7684\u773c\u955c\u53d6\u4e0b\u6765\u62ff\u5728\u624b\u4e0a\uff0c\u8bf4\u9053\uff0c\u201c\u57fa\u672c\u4e8b\u5b9e\u5df2\u7ecf\u6e05\u695a\uff0c\u8c08\u8d54\u507f\u5c31\u662f\u4f60\u4eec\u53cc\u65b9\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u4e0d\u597d\u5e2e\u54ea\u8fb9\u8bf4\u8bdd\uff0c\u518d\u8bf4\u6211\u624b\u5934\u8fd8\u6709\u5de5\u4f5c\u8981\u505a\u2026\u2026\u201d</p><p>\u3000\u3000\u82b1\u886c\u886b\u7537\u89c1\u5f20\u606a\u4e00\u8138\u4e0d\u77e5\u9053\u600e\u4e48\u6b7b\u7684\u795e\u8272\uff0c\u5c31\u5f97\u610f\u98de\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u9662\u957f\u60a8\u52c9\u4e3a\u5176\u96be\u62bd\u4e2a\u7a7a\u51fa\u5e2d\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u597d\u5427\uff0c\u201d\u9648\u526f\u9662\u957f\u5c06\u53e6\u4e00\u53ea\u624b\u91cc\u7684\u94a2\u7b14\u653e\u4e0b\u6765\uff0c\u6001\u5ea6\u548c\u853c\u7684\u8bf4\u9053\uff0c\u201c\u6211\u8fc7\u53bb\u4e5f\u662f\u968f\u4fbf\u8bf4\u8bf4\uff0c\u4f60\u4eec\u613f\u610f\u542c\u5c31\u542c\uff0c\u4e0d\u613f\u610f\u542c\u4e5f\u53ef\u4ee5\u4e0d\u542c\uff0c\u201d\u53c8\u7b11\u772f\u772f\u7684\u770b\u7740\u4f55\u5f26\uff0c\u201c\u542c\u8bf4\u662f\u4f60\u649e\u4e86\u4eba\uff0c\u72af\u4e86\u9519\u8bef\uff0c\u4eba\u603b\u662f\u4f1a\u4e0b\u610f\u8bc6\u7684\u9003\u907f\uff0c\u8fd9\u6ca1\u6709\u4ec0\u4e48\uff0c\u5173\u952e\u8981\u6709\u627f\u8ba4\u7684\u52c7\u6c14\u2026\u2026\u201d\u773c\u775b\u5728\u4f55\u5f26\u8eab\u4e0a\u7785\u4e86\u4e24\u773c\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u7785\u4e86\u5979\u7684\u8138\u86cb\u4e24\u773c\uff0c\u8fd8\u60f3\u8981\u4eb2\u6635\u7684\u8fc7\u6765\u62cd\u62cd\u4f55\u5f26\u7684\u80a9\u8180\u3002</p><p>\u3000\u3000\u867d\u8bf4\u9648\u526f\u9662\u957f\u7684\u8bed\u6c14\u5f88\u548c\u972d\uff0c\u8bf4\u8bdd\u4e5f\u5f88\u4e2d\u542c\uff0c\u53ea\u662f\u8fd9\u8272\u772f\u772f\u7684\u6a21\u6837\u8ba9\u4f55\u5f26\u6d51\u8eab\u8d77\u9e21\u76ae\u7599\u7629\uff0c\u5f80\u5f20\u606a\u8eab\u540e\u8eb2\u4e86\u8eb2\uff0c\u4e0d\u8ba9\u4ed6\u78b0\u81ea\u5df1\u7684\u80a9\u8180\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9664\u4e86\u53cc\u65b9\u5f53\u4e8b\u4eba\u3001\u4e3b\u6301\u548c\u89e3\u7684\u9648\u526f\u9662\u957f\u3001\u5218\u5904\u957f\u4e4b\u5916\uff0c\u5bfc\u533b\u53f0\u7684\u62a4\u58eb\u3001\u7ed9\u5f90\u8001\u592a\u4f1a\u8bca\u7684\u9aa8\u79d1\u4e13\u5bb6\u3001\u76d1\u63a7\u5ba4\u7684\u5de5\u4f5c\u4eba\u5458\u7b49\u76f8\u5173\u4eba\u5458\u90fd\u5230\u4f1a\u8bae\u5ba4\u91cc\u3002</p><p>\u3000\u3000\u9648\u526f\u9662\u957f\u6162\u6761\u4e1d\u7406\u7684\u5750\u5728\u4f1a\u8bae\u684c\u65c1\u770b\u6750\u6599\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u62ac\u5934\u95ee\u5f20\u606a\uff1a\u201c\u57fa\u672c\u4e8b\u5b9e\u5dee\u4e0d\u591a\u6e05\u695a\u4e86\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u8bf4\u7684\uff0c\u8fd8\u662f\u8bf4\u4f60\u5bf9\u8d54\u507f\u534f\u8bae\u6709\u7591\u95ee\uff1f\u6cbb\u7597\u65b9\u6848\u4e0e\u8eab\u4f53\u5eb7\u590d\u65b9\u6848\u662f\u6211\u4eec\u533b\u9662\u4e24\u540d\u6700\u6743\u5a01\u9aa8\u79d1\u4e13\u5bb6\u505a\u51fa\u6765\u7684\uff0c\u5c06\u7528\u5230\u7684\u51b6\u7597\u8d39\u7528\u3001\u8eab\u4f53\u5eb7\u590d\u8d39\u7528\u4ee5\u53ca\u8425\u517b\u8d39\u3001\u6062\u590d\u671f\uff0c\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u6211\u4eec\u533b\u9662\u6709\u8fd9\u4e2a\u6807\u51c6\uff0c\u81f3\u4e8e\u4e24\u4e07\u5143\u7cbe\u795e\u8d54\u507f\uff0c\u6211\u4e0d\u597d\u5b9a\u6027\uff0c\u8fd8\u662f\u4f60\u4eec\u81ea\u5df1\u518d\u8c08\u8c08\u2026\u2026\u201d</p><p>\u3000\u3000\u59d3\u5218\u7684\u5904\u957f\u5728\u65c1\u8fb9\u8981\u5c06\u94a2\u7b14\u9012\u7ed9\u5f20\u606a\u8ba9\u4ed6\u5728\u548c\u89e3\u534f\u8bae\u4e0a\u7b7e\u5b57\uff0c\u5f20\u606a\u6ca1\u6709\u4f38\u624b\u53bb\u63a5\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u6709\u7b14\u3002\u201d</p><p>\u3000\u3000\u5085\u4fca\u5fd9\u4ece\u4e0a\u8863\u53e3\u888b\u5c06\u94a2\u7b14\u53d6\u51fa\u6765\u9012\u7ed9\u5f20\u606a\uff0c\u5f20\u606a\u5c06\u7b14\u62ff\u5728\u624b\u91cc\uff0c\u628a\u73a9\u7684\u7247\u523b\uff0c\u5374\u662f\u8fdf\u8fdf\u7684\u4e0d\u7b7e\u5b57\u3002</p><p>\u3000\u3000\u201c\u549a\u549a\u549a\u2026\u2026\u201d\u6709\u4eba\u5728\u5916\u9762\u6572\u4f1a\u8bae\u5ba4\u7684\u95e8\uff0c\u7d27\u63a5\u7740\u6709\u4e2a\u62a4\u58eb\u63a8\u95e8\u63a2\u5934\u8fdb\u6765\u8bf4\uff0c\u201c\u4e1c\u6d77\u5927\u5b66\u7684\u8001\u5e08\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5b66\u6821\u7684\u8001\u5e08\u8fc7\u6765\u66f4\u597d\uff0c\u8fd8\u662f\u8bf4\u8fd9\u4e9b\u4e1c\u897f\u8ba9\u4f60\u4eec\u5b66\u6821\u7684\u8001\u5e08\u518d\u770b\u770b\uff1f\u201d\u9648\u526f\u9662\u957f\u8bf4\u9053\uff0c\u770b\u7740\u4e00\u4e2a\u7cbe\u795e\u77cd\u94c4\u3001\u5934\u53d1\u82b1\u767d\u7a7f\u7740\u767d\u886c\u886b\u770b\u4e0a\u53bb\u6709\u4e9b\u9762\u719f\u7684\u8001\u5934\u8ddf\u7740\u957f\u76f8\u6f02\u4eae\u7684\u5973\u90ce\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u60f3\u5230\u5b66\u6821\u91cc\u4f1a\u60ca\u52a8\u4ed6\u8fc7\u6765\uff0c\u5fd9\u7ad9\u8d77\u6765\u8d70\u5230\u95e8\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u8ba9\u4f60\u4eb2\u81ea\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u5d14\u4ed6\u4eba\u4e0d\u5728\u5efa\u90ba\uff0c\u5b66\u5de5\u5904\u5c31\u5c06\u7535\u8bdd\u76f4\u63a5\u6253\u8fc7\u6211\u4e86\uff0c\u6211\u4e2d\u5348\u6b63\u597d\u4e5f\u6709\u7a7a\u3002\u201d\u971c\u53d1\u8001\u5934\u58f0\u97f3\u6d2a\u4eae\uff0c\u8ddf\u5f20\u606a\u8bf4\u8bdd\uff0c\u5374\u6068\u4e0d\u5f97\u6240\u6709\u4eba\u90fd\u542c\u89c1\uff0c\u773c\u775b\u53c8\u626b\u4e86\u4f1a\u8bae\u5ba4\u4e00\u5708\uff0c\u624d\u770b\u7740\u5750\u5728\u4f1a\u8bae\u684c\u5bf9\u9762\u7684\u9648\u526f\u9662\u957f\uff0c\u4e5f\u4e0d\u542d\u58f0\u3002</p><p>\u3000\u3000\u9648\u526f\u9662\u957f\u89c1\u8fd9\u8001\u5934\u6c14\u5ea6\u4e0d\u51e1\uff0c\u4e5f\u7ad9\u8d77\u6765\u8bed\u6c14\u5ba2\u6c14\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e1c\u5927\u7684\u8001\u5e08\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u662f\u4e1c\u6d77\u5927\u5b66\u7684\u674e\u9e3f\u9e23\uff0c\u201d\u971c\u53d1\u8001\u5934\u597d\u50cf\u5c31\u7b49\u9648\u526f\u9662\u957f\u95ee\u8fd9\u53e5\u8bdd\uff0c\u53c8\u770b\u4e86\u4f1a\u8bae\u5ba4\u91cc\u4e00\u773c\uff0c\u624d\u4fa7\u8fc7\u5934\u58f0\u8272\u6d2a\u4eae\u7684\u4ecb\u7ecd\u8eab\u540e\u7684\u6f02\u4eae\u5973\u90ce\uff0c\u201c\u4ed6\u662f\u8087\u4e8b\u5b66\u751f\u7684\u5e74\u7ea7\u4e3b\u4efb\u5d14\u8001\u5e08\uff0c\u4f60\u4eec\u533b\u9662\u901a\u77e5\u6211\u4eec\u5b66\u6821\u8fc7\u6765\u89e3\u51b3\u5b66\u751f\u95f9\u4e8b\u7684\u95ee\u9898\uff0c\u6211\u4eec\u8fc7\u6765\u4e86\uff0c\u4f60\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u9648\u526f\u9662\u957f\u534a\u4e2a\u8eab\u5b50\u50f5\u5728\u90a3\u91cc\uff0c\u5f20\u4e86\u5f20\u5634\uff0c\u7a81\u7136\u53d1\u73b0\u8bf4\u8bdd\u597d\u56f0\u96be\uff0c\u54bd\u4e86\u4e24\u53e3\u553e\u6cab\uff0c\u624d\u8270\u96be\u7684\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u674e\u6821\u957f\u2026\u2026\u201d\u58f0\u97f3\u90fd\u6709\u4e9b\u53d8\u8c03\u3002</p><p>\u3000\u3000\u674e\u9e3f\u9e23\u7591\u60d1\u7684\u770b\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u4f60\u662f\u4e1c\u6d77\u5927\u5b66\u7684\u5b66\u751f\uff1f\u4e1c\u6d77\u5927\u5b66\u53c8\u6ca1\u6709\u533b\u79d1\uff01\u201d\u4ed6\u62d6\u4e86\u628a\u6298\u53e0\u6905\u7ed9\u5d14\u90c1\u66fc\uff0c\u201c\u5c0f\u5d14\u8001\u5e08\uff0c\u4f60\u4e5f\u5750\u3002\u201d\u4ed6\u81ea\u987e\u81ea\u53c8\u62d6\u4e86\u6298\u53e0\u6905\u5750\u4f1a\u8bae\u5ba4\u4e2d\u95f4\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u9648\u592a\u5fe0\uff0c\u662f\u674e\u6821\u957f\u60a8\u7231\u4eba\u4f55\u7fe0\u82b3\u6559\u6388\u7684\u5b66\u751f\uff01\u201d\u5e02\u7b2c\u4e09\u4eba\u6c11\u533b\u9662\u526f\u9662\u957f\u9648\u592a\u5fe0\u64e6\u4e86\u64e6\u989d\u5934\u7684\u6c57\uff0c\u6709\u4e9b\u5fc3\u865a\u7684\u8bf4\u9053\uff0c\u4ed6\u5c31\u7b97\u767d\u75f4\uff0c\u90fd\u77e5\u9053\u674e\u9e3f\u9e23\u4e0d\u4f1a\u4e3a\u4e00\u540d\u666e\u901a\u5b66\u751f\u4eb2\u81ea\u8d70\u4e00\u8d9f\u3002</p><p>\u3000\u3000\u201c\u662f\u561b\uff0c\u8001\u4f55\u53ea\u8ddf\u6211\u8bf4\u8fc7\u5e02\u4e09\u9662\u9a6c\u519b\u662f\u5979\u7684\u5b66\u751f\uff0c\u6211\u60f3\u6253\u7b97\u53bb\u627e\u9a6c\u519b\u4e86\u89e3\u60c5\u51b5\u5462\uff0c\u65e2\u7136\u4f60\u4e5f\u662f\u8001\u4f55\u7684\u5b66\u751f\uff0c\u90a3\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f60\u7ed9\u6211\uff0c\u8fd8\u6709\u8087\u4e8b\u5b66\u751f\u7684\u5e74\u7ea7\u4e3b\u4efb\u5c0f\u5d14\u8001\u5e08\u4ecb\u7ecd\u4e00\u4e0b\u2014\u2014\u6211\u542c\u5b66\u5de5\u5904\u7684\u8001\u5e08\u6c47\u62a5\u8bf4\u662f\u6211\u4eec\u5b66\u6821\u7684\u5b66\u751f\u5728\u8fd9\u91cc\u8d70\u8def\u4e0d\u5c0f\u5fc3\u78b0\u5012\u4e2a\u8001\u592a\u592a\uff0c\u5c06\u8001\u592a\u592a\u649e\u9aa8\u6298\u4e4b\u540e\u6015\u627f\u62c5\u8d23\u4efb\u60f3\u9003\u8dd1\u7ed9\u4f60\u4eec\u533b\u9662\u6293\u4f4f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e8b\u60c5\u53ef\u80fd\u6709\u4e9b\u8bef\u4f1a\uff0c\u4e0b\u9762\u521a\u505a\u51fa\u521d\u6b65\u7684\u8c03\u67e5\u7ed3\u8bba\uff0c\u6211\u4e5f\u6b63\u5728\u6838\u5b9e\uff0c\u674e\u6821\u957f\u60a8\u4eb2\u81ea\u8fc7\u6765\u5c31\u592a\u597d\u4e86\uff0c\u53ef\u80fd\u5c06\u4e8b\u60c5\u641e\u6e05\u695a\uff0c\u5c06\u8bef\u4f1a\u89e3\u6d88\u6389\u3002\u201d\u9648\u592a\u5fe0\u53c8\u4f38\u624b\u62b9\u4e86\u62b9\u989d\u5934\u7684\u6c57\u3002</p><p>\u3000\u3000\u4f55\u5f26\u53c8\u6ca1\u6709\u542c\u5230\u5f20\u606a\u5728\u8fc7\u9053\u91cc\u8ddf\u5085\u4fca\u4ed6\u4eec\u5546\u8bae\u4ec0\u4e48\uff0c\u8fd8\u53ea\u5f53\u4e1c\u5927\u6821\u957f\u662f\u5f20\u606a\u8bf7\u8fc7\u6765\u7684\uff0c\u626f\u4e86\u626f\u5f20\u606a\u7684\u8863\u8896\uff0c\u5fae\u5f20\u7740\u5ae3\u7ea2\u7c89\u6da6\u7684\u5634\u5507\uff0c\u505a\u51fa\u4e00\u4e2a\u610f\u6307\u5938\u5f20\u7684\u8868\u60c5\uff0c\u5979\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4f1a\u8bf7\u52a8\u4e1c\u5927\u7684\u6821\u957f\u51fa\u9762\u89e3\u51b3\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u6324\u4e86\u6324\u7709\u5934\uff0c\u674e\u9e3f\u9e23\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u674e\u9e3f\u9e23\u81ea\u5df1\u521a\u624d\u90fd\u89e3\u91ca\u8fc7\u6765\uff1a\u867d\u7136\u5b66\u6821\u91cc\u77e5\u9053\u4ed6\u8eab\u4efd\u7684\u4e5f\u5c31\u90a3\u4e48\u51e0\u4e2a\u4eba\uff0c\u4f46\u662f\u5b66\u5de5\u5904\u7684\u8001\u5e08\u4e0d\u90fd\u662f\u7b28\u86cb\uff0c\u4ed6\u8fd9\u4e24\u5e74\u5728\u4e1c\u5927\u6345\u51fa\u8fd9\u4e9b\u4e2a\u7bd3\u5b50\u4e5f\u5b89\u7136\u65e0\u4e8b\uff0c\u65e9\u5c31\u7ed9\u5217\u5165\u7279\u6b8a\u540d\u5355\uff0c\u53ea\u6015\u8fd8\u5728\u7279\u6b8a\u540d\u5355\u91cc\u6392\u524d\u51e0\u4f4d\u3002\u7279\u6b8a\u540d\u5355\u91cc\u7684\u5b66\u751f\u8981\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5b66\u5de5\u5904\u5f97\u5230\u6d88\u606f\u4f1a\u7b2c\u4e00\u4e2a\u901a\u77e5\u5b66\u9662\u7684\u8d1f\u8d23\u4eba\uff0c\u8d76\u5de7\u5d14\u56fd\u6052\u4e0d\u5728\u5efa\u90ba\uff0c\u5b66\u5de5\u5904\u901a\u77e5\u5d14\u90c1\u66fc\u4e4b\u540e\u518d\u76f4\u63a5\u6253\u7535\u8bdd\u7ed9\u674e\u9e3f\u9e23\u6c47\u62a5\u4e5f\u4e0d\u662f\u6ca1\u6709\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5c06\u6298\u53e0\u6905\u62d6\u5230\u8ddf\u5f20\u606a\u5e76\u6392\uff0c\u5c0f\u58f0\u8ddf\u4ed6\u8bf4\uff1a\u201c\u600e\u4e48\u6bcf\u56de\u90fd\u662f\u4f60\u60f9\u4e8b\u60c5\uff1f\u73b0\u5728\u6068\u4e0d\u5f97\u4f60\u9a6c\u4e0a\u5c31\u6bd5\u4e1a\u6389\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u8d56\u7684\u7b11\u4e86\u7b11\uff0c\u4ed6\u770b\u89c1\u9648\u526f\u9662\u957f\u9648\u592a\u5fe0\u8981\u5c06\u684c\u4e0a\u7684\u6750\u6599\u79fb\u8d70\uff0c\u4ed6\u7709\u5934\u626c\u4e86\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u526f\u9662\u957f\uff0c\u4f60\u521a\u624d\u53ef\u4e0d\u662f\u8fd9\u4e48\u8bf4\u8bdd\u7684\uff01\u201d\u5085\u4fca\u8d70\u8fc7\u53bb\u624b\u538b\u5728\u4f1a\u8bae\u684c\u4e0a\u7684\u90a3\u53e0\u6750\u6599\u4e4b\u4e0a\uff0c\u7ad9\u5728\u90a3\u91cc\u4e0d\u8ba9\u4eba\u79fb\u52a8\u6750\u6599\u3002</p><p>\u3000\u3000\u82b1\u886c\u886b\u7537\u89c1\u9648\u526f\u9662\u957f\u7684\u8bed\u6c14\u90fd\u8f6c\u4e86\uff0c\u4ed6\u4e5f\u77e5\u9053\u501f\u5761\u4e0b\u9a74\uff0c\u5806\u7b11\u7740\u8bf4\uff1a\u201c\u53ef\u80fd\u771f\u662f\u8bef\u4f1a\uff0c\u8fd8\u6ca1\u6709\u8c03\u67e5\u6e05\u695a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e1c\u5927\u6821\u957f\u8fc7\u6765\u5c31\u4e86\u4e0d\u8d77\uff0c\u201d\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u6076\u72e0\u72e0\u7684\u62c9\u4e86\u4e00\u628a\u82b1\u886c\u886b\u7537\uff0c\u201c\u90a3\u5c0f\u5b50\u521a\u624d\u5728\u4f4f\u9662\u90e8\u8fc7\u9053\u91cc\u81ea\u5df1\u90fd\u627f\u8ba4\u662f\u4ed6\u4eec\u649e\u4e86\u4eba\uff0c\u96be\u9053\u6821\u957f\u8fc7\u6765\u5c31\u80fd\u5c06\u9ed1\u6539\u6210\u767d\uff1f\u5f53\u6211\u4eec\u662f\u5413\u5927\u7684\uff0c\u8be5\u4ed6\u4eec\u8d54\u7684\uff0c\u4e00\u5206\u94b1\u90fd\u4e0d\u80fd\u5c11\u3002\u201d</p><p>\u3000\u3000\u53ea\u662f\u5750\u5728\u4f1a\u8bae\u684c\u90a3\u8fb9\u7684\u8bc1\u4eba\u3001\u4e13\u5bb6\u4eec\u7acb\u573a\u5374\u5f00\u59cb\u52a8\u6447\u8d77\u6765\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0c\u53ea\u662f\u8fd9\u65f6\u5019\u5374\u6ca1\u6709\u4eba\u8981\u7406\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u674e\u9e3f\u9e23\u56de\u5934\u770b\u4e86\u5f20\u606a\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6ca1\u60f3\u5230\u674e\u6821\u957f\u4f60\u4eb2\u81ea\u8fc7\u6765\uff0c\u6211\u5012\u8bf7\u4e86\u5176\u4ed6\u4eba\u8fc7\u6765\u8c03\u67e5\u2014\u2014\u8fd9\u79cd\u4e8b\u60c5\u516c\u8bf4\u516c\u6709\u7406\uff0c\u5a46\u8bf4\u5a46\u6709\u7406\uff0c\u6211\u96be\u9053\u8fd8\u80fd\u4ed7\u7740\u8eab\u4efd\u6b3a\u8d1f\u4ed6\u4eec\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u90a3\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u5f88\u4e0d\u5c51\u7684\u63d2\u8bdd\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u4f60\u50ee\u5012\u4e86\u4eba\u53c8\u6e9c\u8d70\uff0c\u8dd1\u5230\u5929\u8fb9\u8bf4\u7406\u53bb\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u6b63\u773c\u770b\u5979\uff0c\u7ee7\u7eed\u8ddf\u6821\u957f\u674e\u9e3f\u9e23\u8bf4\u9053\uff1a\u201c\u603b\u9700\u8981\u6709\u4eba\u8fc7\u6765\u505a\u72ec\u7acb\u8c03\u67e5\u624d\u80fd\u8ba9\u4eba\u4fe1\u670d\uff0c\u8fd8\u8981\u9ebb\u70e6\u674e\u6821\u957f\u966a\u7740\u4e00\u8d77\u7b49\u4e00\u4f1a\u513f\uff01\u201d</p><p>\u3000\u3000\u674e\u9e3f\u9e23\u70b9\u70b9\u5934\uff0c\u8fd9\u4f1a\u513f\u5de5\u592b\uff0c\u8fc7\u9053\u91cc\u5c31\u6709\u811a\u6b65\u58f0\u4f20\u6765\uff0c\u53c8\u662f\u521a\u624d\u90a3\u4e2a\u9886\u674e\u9e3f\u9e23\u8fdb\u6765\u7684\u62a4\u58eb\u6572\u95e8\u8fdb\u6765\u8bf4\uff1a\u201c\u9a6c\u9662\u957f\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u674e\u9e3f\u9e23\u5750\u5728\u90a3\u91cc\u626c\u4e86\u626c\u7709\uff0c\u95ee\u9053\uff1a\u201c\u662f\u9a6c\u519b\u5417\uff1f\u201d</p><p>\u3000\u3000\u5e02\u7b2c\u4e09\u4eba\u6c11\u533b\u9662\u9662\u957f\u9a6c\u519b\u662f\u4e2a\u5934\u9876\u5fae\u79c3\u7684\u4e2d\u5e74\u7537\u4eba\uff0c\u4ed6\u6b63\u7ad9\u5728\u95e8\u53e3\u8bf7\u4e24\u4e2a\u7a7f\u7070\u8272\u6761\u7eb9\u77ed\u8896\u886c\u886b\u7684\u4e2d\u5e74\u7537\u4eba\u8d70\u8fdb\u6765\uff0c\u542c\u5230\u91cc\u9762\u6709\u4eba\u76f4\u547c\u4ed6\u7684\u540d\u5b57\uff0c\u773c\u795e\u5f80\u91cc\u77a5\u4e86\u4e00\u773c\uff0c\u8bb6\u5f02\u7684\u547c\u9053\uff1a\u201c\u674e\u6821\u957f\uff0c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u8fc7\u51e0\u5929\u7aef\u5348\u8282\u8fd8\u60f3\u53bb\u62dc\u671b\u4f60\u8ddf\u4f55\u8001\u5e08\u5462\uff01\u201d\u53c8\u5fd9\u4ecb\u7ecd\u8eab\u8fb9\u4e24\u4e2a\u4e2d\u5e74\u4eba\uff0c\u201c\u8fd9\u4f4d\u662f\u4e1c\u6d77\u5927\u5b66\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8ba4\u8bc6\uff0c\u6211\u867d\u7136\u4e0d\u662f\u4f55\u6559\u6388\u7684\u5b66\u751f\uff0c\u4f46\u4e5f\u662f\u4ece\u5efa\u90ba\u533b\u79d1\u5927\u6bd5\u4e1a\u7684\uff0c\u201d\u524d\u9762\u90a3\u4e2a\u5634\u89d2\u6709\u75e3\u7684\u4e2d\u5e74\u7537\u4eba\u5fd9\u8d70\u8fc7\u6765\u4e0d\u8ba9\u674e\u9e3f\u9e23\u7ad9\u8d77\uff0c\u63e1\u4f4f\u4ed6\u7684\u624b\u81ea\u6211\u4ecb\u7ecd\u8bf4\u9053\uff0c\u201c\u6211\u662f\u5e02\u536b\u751f\u5c40\u7684\u5f20\u5927\u4f1f\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u9662\u957f\u9a6c\u519b\u966a\u540c\u536b\u751f\u5c40\u5c40\u957f\u5f20\u5927\u4f1f\u8d70\u8fdb\u6765\uff0c\u4f1a\u8bae\u5ba4\u91cc\u7684\u4eba\u5750\u4e0d\u4f4f\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u5c31\u674e\u9e3f\u9e23\u5b89\u7136\u82e5\u6cf0\u7684\u5750\u5728\u90a3\u91cc\uff0c\u5f20\u606a\u53ea\u662f\u5c06\u53e0\u8d77\u7684\u4e8c\u90ce\u817f\u653e\u4e0b\u6765\uff0c\u8ddf\u5f20\u5927\u4f1f\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u5c40\u957f\u4f60\u8fc7\u6765\u4e86\u3002\u201d\u4e5f\u4e0d\u7406\u7ad9\u5728\u5f20\u5927\u4f1f\u8eab\u540e\u7684\u77ee\u80d6\u4e2d\u5e74\u7537\u4eba\u3002</p><p>\u3000\u3000\u90a3\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u770b\u5230\u90a3\u4e2d\u5e74\u7537\u4eba\u5374\u662f\u4e00\u559c\uff0c\u8ddf\u82b1\u886c\u886b\u7537\u8d70\u8fc7\u6765\u70ed\u5207\u7684\u8bf4\u9053\uff1a\u201c\u7238\uff0c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u674e\u9e3f\u9e23\u770b\u4e86\u770b\u77ee\u80d6\u4e2d\u5e74\u7537\u4eba\uff0c\u7591\u60d1\u7684\u95ee\u9a6c\u519b\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u662f\u5357\u90ba\u533a\u6587\u660e\u529e\u526f\u4e3b\u4efb\u9648\u594e\u2026\u2026\u201d\u9a6c\u519b\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4ecb\u7ecd\u9648\u594e\u7684\u8eab\u4efd\uff0c\u5f20\u5927\u4f1f\u62c9\u7740\u9648\u594e\u8fc7\u6765\u8bf4\u533b\u9662\u91cc\u53d1\u751f\u4e86\u4e00\u8d77\u53d1\u751f\u5728\u533b\u9662\u91cc\u7684\u7ea0\u7eb7\uff0c\u8ba9\u4ed6\u4e00\u8d77\u53c2\u4e0e\u8c03\u67e5\uff0c\u5c31\u7ed9\u76f4\u63a5\u4ece\u529e\u516c\u5ba4\u62c9\u5230\u4f1a\u8bae\u684c\uff0c\u4ed6\u7ed9\u6cfc\u4e86\u4e00\u5934\u96fe\u6c34\uff0c\u770b\u7740\u4f1a\u8bae\u5ba4\u91cc\u7684\u8c03\u67e5\u73b0\u573a\uff0c\u770b\u7740\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u8dd1\u8fc7\u6765\u558a\u9648\u594e\u7238\u7238\uff0c\u521d\u65f6\u8fd8\u4ee5\u4e3a\u662f\u9648\u594e\u627e\u5230\u5f20\u5927\u4f1f\u8981\u62c9\u504f\u67b6\uff0c\u773c\u775b\u5f80\u4f1a\u8bae\u5ba4\u91cc\u8f6c\u4e86\u4e00\u5708\uff0c\u53c8\u89c9\u5f97\u6c14\u6c1b\u4e0d\u5bf9\uff0c\u4e5f\u4fbf\u4e0d\u591a\u542d\u58f0\u3002</p><p>\u3000\u3000\u9648\u594e\u5fc3\u91cc\u6709\u4e9b\u6e05\u695a\u4e86\uff0c\u4ed6\u662f\u63a5\u5230\u533a\u59d4\u4e66\u8bb0\u7684\u7535\u8bdd\u540e\u5c31\u76f4\u63a5\u7ed9\u5f20\u5927\u4f1f\u63a5\u4e86\u8fc7\u6765\uff0c\u770b\u5230\u4ed6\u5973\u513f\u3001\u5973\u5a7f\u5728\u73b0\u573a\uff0c\u5fc3\u60f3\u603b\u5f52\u4e0d\u4f1a\u662f\u533a\u59d4\u4e66\u8bb0\u6253\u7535\u8bdd\u8ba9\u4ed6\u8fc7\u6765\u5e2e\u4ed6\u5973\u513f\u3001\u5973\u5a7f\u7684\uff0c\u53ea\u662f\u4e0d\u6e05\u695a\u5230\u5e95\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u8d38\u7136\u5f00\u53e3\u3002</p><p>\u3000\u3000\u201c\u7238\uff0c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff0c\u201d\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u5e26\u7740\u6492\u5a07\u7684\u53e3\u6c14\uff0c\u56de\u5934\u77aa\u82b1\u886c\u886b\u7537\uff0c\u201c\u662f\u4e0d\u662f\u4f60\u5077\u5077\u7684\u7ed9\u7238\u6253\u7535\u8bdd\u4e86\uff0c\u5c31\u7b97\u6211\u7238\u4e0d\u6765\uff0c\u4f60\u8fd8\u6015\u4ed6\u4eec\u6562\u62b5\u8d56\u4e0d\u8ba4\u8d26\uff1f\u201d</p><p>\u3000\u3000\u9648\u592a\u5fe0\u8fd9\u65f6\u5019\u4e0d\u4ec5\u989d\u5934\u6d41\u6c57\uff0c\u80cc\u4e0a\u90fd\u5f00\u59cb\u6d41\u6c57\u4e86\uff0c\u53ef\u60dc\uff0c\u8fd9\u65f6\u5019\u53c8\u6ca1\u6709\u4eba\u6ce8\u610f\u4ed6\u3002</p><p>\u3000\u3000\u201c\u8bf7\u4f60\u4eec\u8fc7\u6765\u662f\u5e0c\u671b\u4f60\u4eec\u80fd\u516c\u6b63\u7684\u8c03\u67e5\uff0c\u8fd9\u91cc\u6709\u4efd\u6750\u6599\uff0c\u8fd8\u662f\u5148\u8bf7\u4f60\u4eec\u770b\u4e00\u770b\u3002\u201d\u5f20\u606a\u53ea\u8d1f\u8d23\u8bf4\u8bdd\uff0c\u5085\u4fca\u5728\u65c1\u8fb9\u8d1f\u8d23\u5c06\u6750\u6599\u9001\u7ed9\u9a6c\u519b\u3001\u9648\u594e\u3001\u5f20\u5927\u4f1f\u4ed6\u4eec\u770b\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u662f\u660e\u773c\u4eba\uff0c\u770b\u5230\u548c\u89e3\u534f\u8bae\u4e66\u7684\u8d54\u507f\u6570\u5b57\u5c31\u5927\u4f53\u77e5\u9053\u95ee\u9898\u51fa\u5728\u54ea\u91cc\u4e86\u3002</p><p>\u3000\u3000\u201c\u7238\uff0c\u5c31\u662f\u8fd9\u81ed\u4e2b\u5934\u5728\u95e8\u8bca\u5927\u5385\u91cc\u649e\u5012\u4f55\u52c7\u5979\u5988\u8fd8\u6b7b\u4e0d\u627f\u8ba4\uff0c\u201d\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u6c14\u52bf\u6c79\u6c79\u7684\u6307\u7740\u4f55\u5f26\uff0c\u53c8\u6307\u7740\u5f20\u606a\uff0c\u201c\u8fd8\u6709\u8fd9\u5c0f\u5b50\uff0c\u5c06\u4e1c\u897f\u7838\u6211\u5230\u8eab\u4e0a\u62c9\u7740\u81ed\u4e2b\u5934\u8981\u6e9c\u8d70\uff0c\u7ed9\u6211\u8ddf\u4f55\u52c7\u9886\u7740\u4eba\u5835\u4f4f\uff0c\u4ed6\u4e5f\u627f\u8ba4\u662f\u4ed6\u4eec\u649e\u4e86\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u556a\uff01\u201d\u70ed\u88e4\u5999\u9f84\u5973\u90ce\u6b63\u8bf4\u8981\u5f97\u610f\u5934\u4e0a\uff0c\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\u4e00\u8bb0\u54cd\u4eae\u7684\u8033\u62ec\u5b50\u662f\u4ed6\u7238\u7528\u5c3d\u5168\u529b\u6247\u8fc7\u6765\u7684\uff0c\u8fde\u9000\u4e86\u4e24\u6b65\u649e\u5230\u4f1a\u8bae\u684c\u4e0a\u6ca1\u7ad9\u7a33\u53c8\u683d\u5012\u5728\u5730\u4e0a\uff0c\u6574\u4e2a\u4eba\u90fd\u7ed9\u6253\u8499\u5728\u90a3\u91cc\uff0c\u534a\u4e2a\u4f1a\u8bae\u7684\u4eba\u90fd\u7ed9\u8fd9\u4e00\u5df4\u638c\u6253\u8499\u5728\u90a3\u91cc\u3002</p></article>"}}}]);
