(window.webpackJsonp=window.webpackJsonp||[]).push([[410],{433:function(p){p.exports={chapterTitle:null,title:"\u7b2c410\u7ae0 \u519b\u8bad\u6f14\u4e60",content:"<article><h2>\u7b2c410\u7ae0 \u519b\u8bad\u6f14\u4e60</h2><p>\u3000\u3000\u4e2d\u5348\u56de\u65b0\u6821\u533a\uff0c\u5f00\u8f66\u8d70\u4e86\u53e6\u4e00\u6761\u9053\uff0c\u7ea2\u6ce5\u571f\u8def\uff0c\u5927\u7247\u7684\u72d7\u5c3e\u5df4\u8349\u4e00\u8fb9\u8513\u5ef6\u5230\u6c5f\u8fb9\uff0c\u8fbd\u9614\u7684\u7070\u8272\u6c5f\u6c34\u4e0e\u6d45\u9752\u8272\u7684\u5929\u7a7a\u5728\u89c6\u91ce\u91cc\u5c3d\u60c5\u7684\u94fa\u5c55\uff0c\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u5230\u5efa\u90ba\u8fd9\u6bb5\u65f6\u95f4\u542c\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u65b0\u6d66\u6709\u8fd9\u4e48\u8010\u770b\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u56de\u5230\u65b0\u6821\u533a\uff0c\u5c06\u8f66\u505c\u5728\u62db\u5f85\u6240\u524d\u7684\u7a7a\u573a\u5730\u4e0a\uff0c\u56db\u4e2a\u4eba\u4ece\u6c34\u6ce5\u752c\u9053\u5f80\u5bbf\u820d\u533a\u90a3\u8fb9\uff0c\u5b66\u751f\u98df\u5802\u4e5f\u5728\u90a3\u91cc\uff0c\u5927\u6982\u662f\u9648\u5983\u84c9\u4e0e\u5510\u5a67\u4e24\u4eba\u8fc7\u4e8e\u60ca\u8273\u7684\u7f18\u6545\uff0c\u4e5f\u6216\u8bb8\u519b\u8bad\u7684\u8fd9\u6bb5\u65f6\u95f4\u8db3\u4ee5\u8ba9\u9648\u5983\u84c9\u98ce\u9761\u4e1c\u5927\u7684\u65b0\u6d66\u6821\u533a\uff0c\u56db\u4e2a\u4eba\u8d70\u5728\u8def\u4e0a\uff0c\u5f15\u8d77\u8bb8\u591a\u4eba\u56de\u5934\u987e\u671b\uff0c\u5f20\u606a\u4e0d\u89c9\u5f97\u53bb\u5b66\u751f\u98df\u5802\u7528\u9910\u8fd8\u662f\u4e00\u4e2a\u597d\u4e3b\u610f\u3002\u8fd8\u662f\u5728\u5bbf\u820d\u533a\u4e1c\u4fa7\u7684\u5b66\u751f\u7efc\u5408\u670d\u52a1\u697c\u91cc\u6311\u4e86\u4e00\u5bb6\u97e9\u5f0f\u9910\u9986\u7528\u9910\uff0c\u867d\u7136\u7b97\u4e0d\u4e0a\u5341\u5206\u7684\u5730\u9053\uff0c\u5012\u4e5f\u5dee\u5f3a\u4eba\u610f\u3002\u5403\u8fc7\u4e2d\u996d\u5c31\u7b49\u519b\u8bad\u6f14\u4e60\u7ed3\u675f\uff0c\u4e00\u8d77\u53bb\u4e3b\u57ce\u533a\u73a9\uff0c\u6c5f\u5357\u5cb8\u90a3\u8fb9\u7684\u522b\u5885\u7a7a\u7740\uff0c\u665a\u4e0a\u53ef\u4ee5\u7761\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u6709\u53c2\u4e0e\u8fc7\u8fd9\u6837\u7684\u519b\u8bad\u6f14\u4e60\uff0c\u4e00\u70b9\u90fd\u4e0d\u964c\u751f\uff0c\u641e\u5f97\u8ddf\u9605\u519b\u4e00\u6837\uff0c\u53ea\u662f\u6ca1\u60f3\u5230\u4eca\u5e74\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u4f1a\u51d1\u8fd9\u4e2a\u70ed\u95f9\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u56fd\u5185\u4e60\u60ef\u628a\u4ec0\u4e48\u673a\u6784\u90fd\u8ddf\u7ea7\u522b\u6302\u94a9\uff0c\u4e1c\u6d77\u5927\u5b66\u4e5f\u662f\u7701\u90e8\u7ea7\u5355\u4f4d\u3002</p><p>\u3000\u3000\u65b0\u6821\u533a\u7684\u98ce\u96e8\u64cd\u573a\u9664\u4e86\u4e3b\u5e2d\u53f0\u4e4b\u5916\uff0c\u5728\u4e1c\u4fa7\u6709\u4e00\u5217\u770b\u53f0\uff0c\u9664\u4e86\u5f20\u606a\u4ed6\u4eec\u4e09\u4e2a\u4eba\u5916\uff0c\u8fd8\u7a00\u7a00\u843d\u843d\u7684\u5750\u7740\u4e00\u4e9b\u56e0\u4e3a\u79cd\u79cd\u539f\u56e0\u6ca1\u80fd\u53c2\u52a0\u519b\u8bad\u6f14\u4e60\u7684\u65b0\u751f\u4ee5\u53ca\u8fc7\u6765\u770b\u671b\u5b50\u5973\u6216\u540c\u5b66\u7684\u5bb6\u957f\u3001\u5b66\u751f\u3002</p><p>\u3000\u3000\u65b0\u6d66\u6821\u533a\u4e94\u6240\u9662\u6821\u7684\u65b0\u751f\u96c6\u7ed3\u5728\u64cd\u573a\u5916\u4fa7\u7684\u4fbf\u9053\uff0c\u5c06\u8fd1\u6709\u4e24\u4e07\u4eba\uff0c\u7a7f\u7740\u519b\u8bad\u670d\uff0c\u6709\u4e9b\u961f\u5217\u8fd8\u624b\u6301\u7740\u4ece\u519b\u961f\u6dd8\u6c70\u4e0b\u6765\u4e13\u4e3a\u519b\u8bad\u6240\u7528\u7684\u534a\u81ea\u52a8\u6b65\u67aa\uff0c\u89c4\u6a21\u3001\u6c14\u52bf\u5012\u4e5f\u5b8f\u5927\uff0c\u4ee5\u5f80\u65b0\u751f\u519b\u8bad\u591a\u9009\u62e9\u4e94\u67f3\u5c71\u4f53\u80b2\u9986\u91cc\u8fdb\u884c\uff0c\u4eca\u5e74\u5728\u65b0\u6d66\uff0c\u4e00\u662f\u8fd9\u91cc\u7684\u65b0\u6821\u533a\u4eca\u590f\u521a\u521a\u542f\u7528\uff0c\u4e8c\u6765\u591a\u5c11\u6709\u914d\u5408\u7701\u57ce\u7684\u6c5f\u5317\u5f00\u53d1\u8ba1\u5212\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u901a\u77e5\u6f14\u4e60\u662f\u4e0b\u5348\u4e24\u70b9\u5f00\u59cb\uff0c\u9676\u664b\u4ee5\u53ca\u4e1c\u5927\u6821\u957f\u674e\u9e3f\u94ed\u7b49\u4eba\u7b49\u5230\u4e24\u70b9\u534a\u624d\u8fdb\u573a\uff0c\u7701\u91cc\u5217\u5e2d\u7684\u5b98\u5458\u4e5f\u4e0d\u662f\u592a\u591a\uff0c\u65b0\u6d66\u533a\u59d4\u4e66\u8bb0\u3001\u65b0\u6d66\u9ad8\u65b0\u79d1\u6280\u56ed\u533a\u4e3b\u4efb\u9ec4\u514b\u7fa4\u6ca1\u6709\u843d\u5728\u6700\u540e\u3002\u5f20\u606a\u5750\u5728\u770b\u53f0\u4e0a\uff0c\u80fd\u770b\u89c1\u9676\u664b\u65f6\u4e0d\u65f6\u7684\u56de\u8fc7\u5934\u8ddf\u9ec4\u514b\u7fa4\u5728\u8bf4\u8bdd\uff0c\u9ec4\u514b\u7fa4\u662f\u65b0\u6d66\u5927\u5b66\u6821\u533a\u7fa4\u5efa\u8bbe\u7684\u534f\u8c03\u8d1f\u8d23\u4eba\u4e4b\u4e00\uff0c\u80fd\u501f\u8fd9\u4e2a\u673a\u4f1a\u8ddf\u9676\u664b\u8bf4\u4e0a\u8bdd\uff0c\u5012\u662f\u7edd\u4f73\u7684\u673a\u4f1a\u3002\u4e1c\u6d77\u7701\u90a3\u4e48\u591a\u526f\u5385\uff0c\u5927\u6982\u6ca1\u6709\u591a\u5c11\u4eba\u6709\u673a\u4f1a\u8ddf\u7701\u59d4\u4e66\u8bb0\u76f4\u63a5\u5bf9\u8bdd\u3002</p><p>\u3000\u3000\u770b\u7740\u9676\u664b\u4e0e\u9ec4\u514b\u7fa4\u8bf4\u8bdd\u65f6\u671b\u4e86\u8fd9\u8fb9\u4e24\u773c\uff0c\u6216\u8bb8\u9ec4\u514b\u7fa4\u8ddf\u4ed6\u63d0\u5230\u4e86\u81ea\u5df1\uff0c\u5f20\u606a\u5f53\u7136\u77e5\u9053\u9676\u664b\u8fd9\u65f6\u5019\u4e0d\u4f1a\u8ba9\u4eba\u8fc7\u6765\u558a\u81ea\u5df1\u8fc7\u53bb\u7684\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u60f3\u8fd9\u6837\uff1a\u90a3\u4e5f\u592a\u60ca\u4e16\u9a87\u4fd7\u4e86\uff01</p><p>\u3000\u3000\u9648\u5983\u84c9\u4e0e\u5176\u4ed6\u5b66\u751f\u4ee3\u8868\u4e5f\u7ad9\u5728\u4e3b\u5e2d\u53f0\u7684\u4f4d\u7f6e\u4e86\uff0c\u6628\u665a\u90a3\u4e2a\u5bf9\u5f20\u606a\u6709\u7740\u654c\u610f\u7684\u80e1\u91d1\u661f\u81ea\u7136\u4e5f\u5728\uff0c\u542c\u9648\u5983\u84c9\u8bf4\uff0c\u4ed6\u8fd8\u5c06\u4ee3\u8868\u5168\u7701\u9ad8\u6821\u7684\u65b0\u751f\u53d1\u8a00\uff0c\u5f20\u606a\u5fc3\u91cc\u60f3\u8fd9\u53ae\u5bb6\u91cc\u7684\u5173\u7cfb\u5012\u4e5f\u662f\u4e0d\u4e00\u822c\u554a\uff01\u5f20\u606a\u8fd9\u4e48\u60f3\u7740\uff0c\u770b\u7740\u9676\u664b\u8eab\u8fb9\u4e00\u540d\u7a7f\u7740\u85cf\u9752\u8272\u897f\u670d\u7684\u4e2d\u5e74\u4eba\u62db\u624b\u8ba9\u80e1\u91d1\u661f\u8fc7\u53bb\uff0c\u5f88\u662f\u4eb2\u5207\u7684\u63fd\u7740\u80e1\u91d1\u661f\u7684\u80a9\u8180\uff0c\u597d\u50cf\u8ddf\u9676\u664b\u4ee5\u53ca\u4e1c\u5927\u6821\u957f\u674e\u9e3f\u94ed\u8fd9\u4e9b\u5927\u4f6c\u5728\u4ecb\u7ecd\u80e1\u91d1\u661f\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\uff0c\u90a3\u4e2a\u4eb2\u5207\u63fd\u7740\u80e1\u91d1\u661f\u7a7f\u85cf\u9752\u8272\u897f\u670d\u7684\u4e2d\u5e74\u4eba\u662f\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u3002\u5f20\u606a\u8f7b\u8f7b\u5482\u4e86\u5482\u5634\uff0c\u679c\u7136\u662f\u4e00\u4e2a\u6709\u5206\u91cf\u7684\u4eba\u7269\u3002\u56fd\u5185\u526f\u7701\u7ea7\u57ce\u5e02\u7684\u653f\u6cbb\u5730\u4f4d\u76f8\u5bf9\u8f83\u9ad8\uff0c\u5730\u65b9\u8d22\u653f\u76f4\u63a5\u7eb3\u5165\u4e2d\u592e\u7edf\u7b79\uff0c\u800c\u4e14\u8fd9\u4e9b\u57ce\u5e02\u7684\u4e3b\u8981\u515a\u653f\u5b98\u5458\u7684\u4e0a\u5347\u9014\u5f84\u660e\u786e\u3002\u4ece\u5e38\u52a1\u526f\u5e02\u957f\u5230\u5e02\u957f\u518d\u5230\u5e02\u59d4\u4e66\u8bb0\uff0c\u4e4b\u540e\u5c31\u6709\u673a\u4f1a\u76f4\u63a5\u51fa\u4efb\u6b63\u7701\u90e8\u7684\u9ad8\u5b98\uff0c\u80fd\u5728\u8fd9\u4e9b\u57ce\u5e02\u62c5\u4efb\u4e3b\u8981\u515a\u653f\u9886\u5bfc\u7684\u5b98\u5458\uff0c\u80cc\u666f\u90fd\u4e0d\u4f1a\u592a\u7b80\u5355\uff0c\u4e0d\u8fc7\u80e1\u5b97\u5e86\u4ece\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u7684\u4efb\u4e0a\u76f4\u63a5\u8c03\u5230\u7701\u653f\u5e9c\u62c5\u4efb\u526f\u7701\u957f\u4e4b\u540e\u5c31\u9010\u6e10\u6c89\u5bc2\uff0c\u51e0\u4e4e\u80fd\u7b97\u5f97\u4e0a\u4e0d\u5f97\u5fd7\u7684\u4e00\u7c7b\u4eba\u3002</p><p>\u3000\u3000\u8fd9\u53ea\u662f\u519b\u8bad\u524d\u5f88\u7ec6\u5fae\u7684\u4e00\u5e55\uff0c\u4e0d\u8fc7\u6709\u5fc3\u4eba\u90fd\u4f1a\u8bb0\u5728\u5fc3\u91cc\uff0c\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u4e00\u4e2a\u6709\u957f\u8fdc\u89c4\u5212\u7684\u653f\u6cbb\u4eba\u751f\u5e94\u8be5\u662f\u4ece\u5b66\u751f\u65f6\u4ee3\u5c31\u5f00\u59cb\u7684\uff0c\u4e1c\u5927\u7684\u6821\u56e2\u59d4\u526f\u4e66\u8bb0\u3001\u5b66\u4f1a\u515a\u652f\u90e8\u4e66\u8bb0\u4e4b\u7c7b\u7684\u5b66\u751f\u5b98\u5e76\u4e0d\u4ec5\u4ec5\u53ea\u662f\u953b\u70bc\u80fd\u529b\u8fd9\u4e48\u7b80\u5355\u3002</p><p>\u3000\u3000\u519b\u8bad\u6f14\u4e60\u65e0\u975e\u8d70\u5404\u79cd\u65b9\u9635\uff0c\u8fd8\u6709\u6218\u5730\u6551\u62a4\u7684\u6f14\u4e60\uff0c\u770b\u64cd\u573a\u4e0a\u7a7f\u7740\u519b\u8bad\u670d\u65b0\u751f\u7684\u8ba4\u771f\u52b2\uff0c\u5f20\u606a\u90fd\u6ca1\u597d\u610f\u601d\u62b1\u6028\u65e0\u804a\uff0c\u539f\u4ee5\u4e3a\u5f88\u5feb\u5c31\u4f1a\u8fc7\u53bb\u7684\u6f14\u4e60\uff0c\u4e00\u76f4\u6301\u7eed\u5230\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u7167\u4f8b\u662f\u9676\u664b\u3001\u674e\u9e3f\u94ed\u7b49\u4eba\u5148\u9000\u573a\uff0c\u4e00\u5e72\u4eba\u7b49\u62e5\u7740\u3002</p><p>\u3000\u3000\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u9648\u5983\u84c9\u8df3\u4e5f\u4f3c\u7684\u8d70\u5230\u770b\u53f0\u8fd9\u8fb9\u6765\uff0c\u201c\u8d70\u8fdf\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4eec\u7cfb\u4e3b\u4efb\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u5520\u53e8\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u8ba4\u8bc6\u5916\u8bed\u7cfb\u7684\u7cfb\u4e3b\u4efb\u662f\u54ea\u4e2a\uff0c\u4e0d\u8fc7\u90a3\u4e9b\u9662\u7cfb\u9886\u5bfc\u90fd\u62e5\u7740\u7701\u59d4\u4e66\u8bb0\u4e0b\u4e86\u4e3b\u5e2d\u53f0\uff0c\u5df4\u671b\u7740\u80fd\u63d2\u4e00\u53e5\u8bdd\uff0c\u4e00\u65f6\u534a\u4f1a\u8fd8\u6ca1\u6709\u8c01\u987e\u5f97\u4e0a\u8fd9\u8fb9\uff0c\u90a3\u4e9b\u53c2\u52a0\u519b\u8bad\u6f14\u4e60\u7684\u65b0\u751f\u90fd\u96c6\u7ed3\u5728\u64cd\u573a\u7684\u5357\u7247\u6682\u65f6\u7ed9\u9057\u5fd8\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u56db\u4eba\u4e0b\u4e86\u770b\u53f0\uff0c\u51c6\u5907\u4ece\u770b\u53f0\u4e0b\u9762\u7684\u4fa7\u95e8\u6e9c\u51fa\u98ce\u96e8\u64cd\u573a\u3002</p><p>\u3000\u3000\u9676\u664b\u3001\u80e1\u5b97\u5e86\u3001\u9ec4\u514b\u7fa4\u7b49\u7701\u5e02\u515a\u653f\u5b98\u5458\u7684\u8f66\u5c31\u505c\u5728\u64cd\u573a\u5916\u4fa7\u7684\u6c34\u6ce5\u752c\u9053\u4e0a\uff0c\u9676\u664b\u624b\u6276\u7740\u8f66\u95e8\uff0c\u6b63\u8ddf\u674e\u9e3f\u94ed\u8bf4\u544a\u522b\u7684\u5ba2\u5957\u8bdd\uff0c\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\u4ece\u4fa7\u95e8\u94bb\u51fa\u6765\uff0c\u8f6c\u5934\u770b\u89c1\u4ed6\u7684\u79d8\u4e66\u534a\u4e2a\u8eab\u5b50\u5df2\u7ecf\u94bb\u8fdb\u8f66\u53a2\u91cc\uff0c\u4fbf\u63a2\u5934\u8fdb\u8f66\u53a2\u8ddf\u4ed6\u8bf4\u4e86\u4e24\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u4e0e\u9676\u664b\u6b63\u5f0f\u63a5\u89e6\u8fc7\uff0c\u4f46\u5bf9\u4ed6\u8eab\u8fb9\u7684\u4eba\u90fd\u6709\u4e00\u5b9a\u7684\u4e86\u89e3\uff0c\u4e1c\u6d77\u7701\u8fd9\u4e48\u591a\u5e74\u8f7b\u6709\u4e3a\u3001\u516d\u5341\u5e74\u4ee3\u672b\u51fa\u751f\u7684\u515a\u653f\u5b98\u5458\u4e2d\uff0c\u5927\u6982\u4ee5\u9676\u664b\u7684\u79d8\u4e66\u67f3\u5fd7\u519b\u4e0e\u5f90\u5b66\u5e73\u7684\u79d8\u4e66\u674e\u4e49\u6c5f\u6700\u4e3a\u98ce\u5149\uff0c\u5f53\u7136\uff0c\u674e\u4e49\u6c5f\u5728\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u8fd8\u4e0d\u80fd\u8ddf\u67f3\u5fd7\u519b\u76f8\u6bd4\u3002\u770b\u5230\u67f3\u5fd7\u519b\u671d\u81ea\u5df1\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u80e1\u5b97\u5e86\u8fd9\u4e9b\u4eba\u4e5f\u90fd\u76f8\u5f53\u610f\u5916\u7684\u770b\u7740\u8fd9\u8fb9\uff0c\u5f20\u606a\u5927\u611f\u9ebb\u70e6\uff0c\u6068\u4e0d\u80fd\u6389\u5934\u5c31\u8d70\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u67f3\u5fd7\u519b\uff0c\u9676\u4e66\u8bb0\u7684\u79d8\u4e66\u2026\u2026\u201d\u67f3\u5fd7\u53f6\u4eca\u5e74\u624d\u4e09\u5341\u4e8c\u5c81\uff0c\u9ad8\u4e2a\uff0c\u8138\u5f00\u524a\u7626\uff0c\u8138\u4e0a\u5806\u7740\u6e29\u548c\u7684\u7b11\u5bb9\uff0c\u4e0d\u542c\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u4ec5\u770b\u4ed6\u7684\u8868\u60c5\uff0c\u597d\u50cf\u4e0e\u5f20\u606a\u8ba4\u8bc6\u597d\u4e45\u4f3c\u7684\u3002</p><p>\u3000\u3000\u89c1\u67f3\u5fd7\u519b\u8d70\u4e0a\u6765\u5c31\u5f84\u76f4\u81ea\u6211\u4ecb\u7ecd\uff0c\u8fd8\u4e00\u672c\u6b63\u7ecf\u7684\u4f38\u51fa\u624b\u6765\uff0c\u5f20\u606a\u54a7\u7259\u7b11\u4e86\u7b11\uff0c\u6709\u6837\u5b66\u6837\u7684\u8bf4\uff1a\u201c\u6211\u662f\u5f20\u606a\uff0c\u8c01\u7684\u79d8\u4e66\u90fd\u4e0d\u662f\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u65e9\u5c31\u60f3\u8ba4\u8bc6\u4f60\uff0c\u6ca1\u60f3\u5230\u4f1a\u5728\u8fd9\u91cc\u9047\u5230\u4f60\uff0c\u6709\u6ca1\u6709\u7ed9\u6211\u8bf7\u4f60\u5403\u996d\u7684\u673a\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u9676\u664b\u5df2\u7ecf\u94bb\u8fdb\u8f66\u91cc\u53bb\u4e86\uff0c\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e09\u5929\u90fd\u5728\u5efa\u90ba\uff0c\u90a3\u5c31\u8981\u770b\u67f3\u54e5\u4f60\u6709\u6ca1\u6709\u7a7a\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6709\u4f60\u7684\u624b\u673a\uff0c\u201d\u67f3\u5fd7\u519b\u505a\u51fa\u4e00\u4e2a\u6253\u7535\u8bdd\u7684\u624b\u52bf\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u7ed9\u4f60\u6253\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5934\u75bc\u4e07\u5206\u7684\u7b11\u4e86\u7b11\uff0c\u90a3\u8fb9\u7ad9\u5728\u80e1\u5b97\u5e86\u8f66\u8fb9\u7684\u80e1\u91d1\u661f\u8fd8\u4e0d\u4f1a\u63a9\u9970\u5185\u5fc3\u7684\u60c5\u7eea\uff0c\u770b\u5230\u4ed6\u9732\u51fa\u9707\u60ca\u751a\u81f3\u96be\u4ee5\u7f6e\u4fe1\u7684\u8868\u60c5\uff0c\u60f3\u5fc5\u80e1\u5b97\u5e86\u5750\u5728\u8f66\u91cc\u4e5f\u6b63\u901a\u8fc7\u540e\u89c6\u955c\u770b\u7740\u8fd9\u8fb9\uff0c\u5f20\u606a\u5fc3\u91cc\u81ea\u7136\u8212\u5766\uff0c\u4e0d\u8fc7\u66f4\u62c5\u5fc3\u4f1a\u7ed9\u5728\u573a\u7684\u4e1c\u5927\u6559\u804c\u5458\u5de5\u7559\u4e0b\u4ec0\u4e48\u6df1\u523b\u7684\u5370\u8c61\uff0c\u660e\u5e74\u8fd8\u600e\u4e48\u8fdb\u4e1c\u5927\u6df7\uff1f\u5f20\u606a\u4e0e\u67f3\u5fd7\u519b\u5bd2\u6684\u4e86\u51e0\u53e5\uff0c\u76f4\u63a5\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u5979\u4eec\u8d34\u7740\u770b\u53f0\u7684\u5916\u5899\u6298\u5411\u5f80\u5357\uff0c\u8d70\u7684\u65f6\u5019\u5f88\u6025\uff0c\u6068\u4e0d\u5f97\u62ac\u8d77\u80f3\u818a\u62ff\u624b\u906e\u4f4f\u8138\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u6765\u8bf4\uff0c\u65e2\u7136\u4e0a\u5348\u65b0\u6d66\u533a\u59d4\u4e66\u8bb0\u9ec4\u514b\u7fa4\u80fd\u65e0\u7f18\u65e0\u6545\u7684\u8fc7\u6765\u6253\u62db\u547c\uff0c\u67f3\u5fd7\u519b\u8d70\u8fc7\u6765\u8bf4\u8981\u8bf7\u5f20\u606a\u5403\u996d\u7684\u4e8b\u60c5\uff0c\u5012\u6ca1\u6709\u90a3\u4e48\u8be7\u5f02\u4e86\uff0c\u5374\u4e0d\u6653\u5f97\u67f3\u5fd7\u519b\u4e0e\u9ec4\u514b\u7fa4\u6240\u4ee3\u8868\u7684\u610f\u4e49\u662f\u5b8c\u5168\u4e0d\u540c\u7684\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u5f97\u9676\u664b\u6388\u610f\u4e3b\u52a8\u8ddf\u81ea\u5df1\u63a5\u89e6\uff0c\u4ee3\u8868\u7740\u52bf\u6001\u5c31\u7b97\u4e0d\u592a\u574f\uff0c\u4f46\u4e5f\u7b97\u4e0d\u4e0a\u597d\uff0c\u76f8\u6bd4\u8f83\u9676\u664b\u7684\u62c9\u62e2\uff0c\u5f20\u606a\u66f4\u613f\u610f\u4fdd\u7559\u5f90\u5b66\u5e73\u6beb\u65e0\u4fdd\u7559\u7684\u4fe1\u4efb\uff0c\u81ea\u5df1\u6ca1\u6709\u53ef\u80fd\u6210\u4e3a\u9676\u664b\u7684\u5fc3\u8179\u4eb2\u4fe1\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u51e0\u4e4e\u7ed5\u4e86\u65b0\u6821\u533a\u7684\u98ce\u96e8\u64cd\u573a\u8d70\u4e86\u4e00\u5708\uff0c\u5148\u966a\u9648\u5983\u84c9\u56de\u5bbf\u820d\u5c06\u5979\u8eab\u4e0a\u7684\u519b\u8bad\u670d\u6362\u4e0b\u6765\u3002\u5efa\u90ba\u5403\u7684\u73a9\u7684\uff0c\u90fd\u96c6\u4e2d\u5728\u5357\u5cb8\u7684\u4e3b\u57ce\u533a\uff0c\u5f20\u606a\u51c6\u5907\u62c9\u7740\u5979\u4eec\u665a\u4e0a\u90fd\u4f4f\u6c5f\u5357\u5cb8\u72ee\u5b50\u56ed\u7684\u522b\u5885\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5510\u5a67\u3001\u82cf\u4e00\u5a77\u5148\u966a\u9648\u5983\u84c9\u53bb\u5bbf\u820d\u697c\u51c6\u5907\u8fd9\u4e24\u5929\u4f4f\u5bbf\u7528\u7684\u4e1c\u897f\uff0c\u4ed6\u53bb\u62db\u5f85\u6240\u524d\u5c06\u8f66\u5f00\u8fc7\u6765\uff0c\u8d81\u7740\u8fd9\u5f53\u513f\uff0c\u5c06\u67f3\u5fd7\u519b\u4e3b\u52a8\u9080\u8bf7\u81ea\u5df1\u5403\u996d\u7684\u4e8b\u60c5\u8ddf\u8fdc\u5728\u65b0\u6e44\u89c6\u5bdf\u7684\u5f90\u5b66\u5e73\u6c47\u62a5\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u201c\u90a3\u662f\u597d\u4e8b\u554a\uff0c\u201d\u5f90\u5b66\u5e73\u5728\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u6709\u4e9b\u6c99\u54d1\uff0c\u80fd\u542c\u51fa\u4ed6\u7684\u75b2\u60eb\u6765\uff0c\u201c\u9676\u664b\u4e66\u8bb0\u662f\u4e2a\u6709\u80fd\u529b\u3001\u6709\u9b44\u529b\u3001\u8bb2\u7a76\u7b56\u7565\u7684\u4eba\uff0c\u6211\u5012\u662f\u611f\u89c9\u7d2f\u4e86\uff0c\u75b2\u60eb\uff0c\u4e5f\u6ca1\u6709\u8c01\u6309\u7740\u4e0d\u8ba9\u6211\u9000\u4f11\uff0c\u6709\u65f6\u5019\u518d\u60f3\u9000\u4e0b\u6765\u7b97\u4e86\uff0c\u770b\u6765\u8fd9\u4e2a\u613f\u671b\u4e0d\u4e45\u80fd\u5b9e\u73b0\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u611f\u53f9\u7740\uff0c\u8bb8\u9e3f\u4f2f\u9884\u6d4b\u7684\u4e8b\u60c5\u4f1a\u63d0\u524d\u53d1\u751f\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u5f90\u5b66\u5e73\u4e5f\u611f\u89c9\u5230\u6765\u81ea\u4e0a\u4e0b\u7684\u5c42\u5c42\u963b\u529b\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u505c\u5728\u5973\u751f\u5bbf\u820d\u524d\u7b49\u9648\u5983\u84c9\u5979\u4eec\uff0c\u770b\u89c1\u80e1\u91d1\u661f\u4e5f\u7ad9\u5728\u5bbf\u820d\u7ba1\u7406\u5ba4\u524d\u7684\u7a97\u53f0\u524d\u4f3c\u4e4e\u8ddf\u91cc\u9762\u7684\u7ba1\u7406\u5458\u89e3\u91ca\u4ec0\u4e48\uff0c\u5927\u6982\u8981\u8fdb\u5973\u751f\u5bbf\u820d\u697c\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u4e1c\u6d77\u6240\u6709\u9662\u7cfb\u7684\u5973\u751f\u5bbf\u820d\u697c\u60df\u6709\u5077\u5077\u6478\u6478\u8fdb\u53bb\u4e00\u6761\u9053\uff0c\u5bbf\u820d\u7ba1\u7406\u5458\u5927\u5988\u4f1a\u8ddf\u4f60\u5546\u91cf\u8fd9\u4e8b\uff1f\u770b\u89c1\u5510\u5a67\u3001\u9648\u5983\u84c9\u5979\u4eec\u4ece\u5bbf\u820d\u697c\u5927\u5385\u91cc\u9732\u51fa\u5934\uff0c\u6309\u4e86\u4e24\u58f0\u5587\u53ed\u63d0\u9192\u5979\u4eec\u3002\u770b\u89c1\u80e1\u91d1\u661f\u671d\u9648\u5983\u84c9\u5979\u4eec\u8d70\u8fc7\u53bb\uff0c\u4e0d\u6653\u5f97\u8fd9\u53ae\u53c8\u751f\u51fa\u4ec0\u4e48\u4e8b\u6765\u7ea0\u7f20\u4eba\u3002</p></article>"}}}]);
