(window.webpackJsonp=window.webpackJsonp||[]).push([[411],{433:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e00\u8282 \u6f02\u4eae\u59d0\u59d0",content:"<article><h2>\u7b2c\u4e00\u8282 \u6f02\u4eae\u59d0\u59d0</h2><p>\u6e29\u5b58\u7247\u523b\uff0c\u73ca\u5609\u8f7b\u8f7b\u62cd\u4e86\u62cd\u743c\u6069\u7684\u5934\uff0c\u201c\u8d77\u6765\u5566\uff0c\u201d\u5979\u5a07\u55d4\uff0c\u201c\u522b\u4e00\u76f4\u538b\u5728\u6211\u8eab\u4e0a\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u81ea\u5df1\u6709\u591a\u91cd\u554a\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u8fde\u5fd9\u7ffb\u4e0b\u8eab\u6765\uff0c\u4e56\u4e56\u8eba\u5728\u65c1\u8fb9\uff0c\u4f46\u5374\u4f9d\u65e7\u5c06\u5979\u62b1\u5f97\u7d27\u7d27\u7684\u3002\u73ca\u5609\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u4f60\u8fd9\u6837\u62b1\u7740\uff0c\u6211\u600e\u4e48\u8d77\u6765\u505a\u665a\u9910\uff1f\u201d</p><p>\u201c\u8ba9\u4ec6\u4eba\u505a\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u884c\uff0c\u4eca\u5929\u4f60\u521a\u56de\u5bb6\uff0c\u6211\u8981\u8ba9\u4f60\u5c1d\u5c1d\u59d0\u59d0\u65b0\u5b66\u7684\u624b\u827a\u3002\u201d</p><p>\u201c\u597d\u2026\u2026\u4f46\u8ba9\u6211\u518d\u62b1\u4e00\u4f1a\u3002\u201d</p><p>\u8bf4\u662f\u4e00\u4f1a\uff0c\u7ed3\u679c\u743c\u6069\u62b1\u7740\u4e0d\u653e\uff0c\u78e8\u78e8\u8e6d\u8e6d\u53c8\u8d56\u4e86\u534a\u4e2a\u591a\u5c0f\u65f6\uff0c\u65b9\u624d\u604b\u604b\u4e0d\u820d\u5730\u653e\u59d0\u59d0\u8d77\u8eab\u3002\u201c\u4f60\u7d2f\u4e86\uff0c\u518d\u7761\u4e00\u4f1a\uff0c\u201d\u73ca\u5609\u67d4\u58f0\u8bf4\uff0c\u201c\u505a\u597d\u4e86\u53eb\u4f60\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u73ca\u5609\u8d77\u8eab\u51fa\u4e86\u623f\u95f4\uff0c\u8f7b\u8f7b\u628a\u95e8\u5e26\u4e0a\uff0c\u56db\u5468\u53c8\u56de\u590d\u4e86\u9ed1\u6697\u6c89\u5bc2\u3002\u743c\u6069\u786e\u5b9e\u6709\u4e9b\u5026\u4e86\uff0c\u8eba\u5728\u5e8a\u4e0a\u4e0d\u60f3\u52a8\u5f39\uff0c\u4f46\u4e5f\u7761\u4e0d\u7740\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u7d22\u6027\u4e5f\u5c31\u7a7f\u8863\u8d77\u8eab\uff0c\u6253\u7b97\u4e0b\u697c\u53bb\u966a\u73ca\u5609\u3002</p><p>\u521a\u521a\u7a7f\u4e0a\u5916\u5957\uff0c\u6b63\u51c6\u5907\u8d70\u51fa\u623f\u95f4\uff0c\u773c\u89d2\u4f59\u5149\u7a81\u7136\u77a5\u89c1\u4e00\u70b9\u5fae\u5149\u5728\u9ed1\u6697\u4e2d\u95ea\u70c1\uff0c\u4ed6\u6709\u4e9b\u5947\u602a\uff0c\u968f\u624b\u5f00\u4e86\u706f\uff0c\u53d1\u73b0\u662f\u68b3\u5986\u53f0\u4e0a\u7684\u4e00\u652f\u957f\u7b1b\uff0c\u901a\u4f53\u4e4c\u9ed1\uff0c\u5c3e\u7aef\u9576\u5d4c\u7740\u4e00\u5757\u5c0f\u5c0f\u7684\u84dd\u5b9d\u77f3\uff0c\u521a\u624d\u770b\u89c1\u7684\u4eae\u5149\u5c31\u662f\u5b83\u53d1\u51fa\u7684\u3002\u743c\u6069\u6b64\u524d\u8fdb\u623f\u95f4\u65f6\uff0c\u5e76\u6ca1\u6709\u770b\u5230\u5b83\uff0c\u5e94\u8be5\u662f\u73ca\u5609\u5e26\u56de\u6765\u7684\u3002</p><p>\u743c\u6069\u62ff\u8d77\u957f\u7b1b\u4ed4\u7ec6\u7aef\u8be6\uff0c\u53d1\u73b0\u662f\u7528\u591c\u6c89\u6728\u5236\u6210\uff0c\u6750\u6599\u7b97\u4e0d\u4e0a\u7279\u522b\u73cd\u8d35\uff0c\u4f46\u4e5f\u4e0d\u662f\u51e1\u54c1\u4e86\uff0c\u800c\u4e14\u4f5c\u5de5\u975e\u5e38\u7cbe\u81f4\uff0c\u4ef7\u503c\u7edd\u5bf9\u4e0d\u83f2\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u4e00\u63e1\u5728\u624b\u4e2d\uff0c\u987f\u65f6\u4fbf\u6e05\u695a\u611f\u89c9\u5230\u9690\u9690\u7684\u9b54\u529b\u6ce2\u52a8\uff0c\u91cc\u9762\u663e\u7136\u8574\u542b\u4e86\u9b54\u6cd5\u3002</p><p>\u743c\u6069\u51dd\u89c6\u7247\u523b\uff0c\u5206\u6790\u51fa\u662f\u4e24\u4e2a\u7b80\u5355\u7684\u795d\u798f\u795e\u672f\uff0c\u6ca1\u4ec0\u4e48\u5371\u9669\uff0c\u7565\u7565\u653e\u4e0b\u5fc3\u6765\uff0c\u4f46\u968f\u5373\u53c8\u89c9\u5947\u602a\u3002\u8fd9\u652f\u957f\u7b1b\u81ea\u7136\u6bd4\u4e0d\u4e0a\u81ea\u5df1\u51c6\u5907\u9001\u7ed9\u73ca\u5609\u7684\u591c\u98ce\u4e4b\u7b1b\uff0c\u4f46\u4e5f\u80fd\u7b97\u662f\u8d35\u91cd\u4e86\uff0c\u653e\u5728\u5e02\u573a\u4e0a\u51fa\u552e\uff0c\u4f30\u8ba1\u80fd\u5356\u5230\u51e0\u767e\u679a\u91d1\u5e01\u3002\u9634\u9b42\u57ce\u6ca1\u6709\u62d6\u6b20\u5de5\u8d44\u7684\u4e60\u60ef\uff0c\u516c\u52a1\u5458\u5f85\u9047\u8fd8\u7b97\u4e30\u539a\uff0c\u81ea\u5df1\u79bb\u57ce\u5728\u5916\uff0c\u85aa\u6c34\u662f\u5b9a\u671f\u53d1\u5230\u73ca\u5609\u624b\u4e2d\u7684\uff0c\u800c\u4e14\u743c\u6069\u4e0a\u6b21\u79bb\u57ce\u4e4b\u524d\uff0c\u5c06\u8eab\u4e0a\u7684\u5927\u90e8\u4efd\u5b9d\u77f3\u4e5f\u90fd\u7559\u5728\u5bb6\u4e2d\uff0c\u8981\u4e70\u8fd9\u652f\u957f\u7b1b\u5012\u4e5f\u4e0d\u662f\u4e70\u4e0d\u8d77\uff0c\u4f46\u8fd9\u4e0d\u50cf\u662f\u73ca\u5609\u7684\u4f5c\u98ce\u554a\u3002</p><p>\u5170\u5c3c\u65af\u7279\u5bb6\u5386\u4ee3\u5e73\u6c11\uff0c\u4e24\u4eba\u5e74\u5e7c\u65f6\u53c8\u7236\u6bcd\u53bb\u4e16\uff0c\u743c\u6069\u5728\u5b66\u6821\u91cc\u8bfb\u4e66\uff0c\u73ca\u5609\u5219\u72ec\u81ea\u8270\u96be\u652f\u6491\u5bb6\u4e1a\uff0c\u4ece\u6765\u90fd\u662f\u7b80\u6734\u60ef\u4e86\uff0c\u7eb5\u7136\u73b0\u5728\u4e5f\u6ca1\u591a\u5c11\u6539\u53d8\uff0c\u770b\u770b\u623f\u95f4\u91cc\u7684\u9648\u8bbe\u5c31\u77e5\u9053\u3002\u53cd\u5012\u662f\u743c\u6069\uff0c\u56e0\u4e3a\u591a\u4e86\u4e00\u4e16\u7684\u8bb0\u5fc6\uff0c\u5728\u8fd9\u65b9\u9762\u6d12\u8131\u4e9b\uff0c\u8d2b\u56f0\u7684\u65f6\u5019\u5c31\u8282\u4fed\u4e9b\uff0c\u5bbd\u88d5\u7684\u65f6\u5019\u5c31\u5962\u4f88\u4e9b\uff0c\u4e0d\u90a3\u4e48\u5728\u610f\u3002\u5982\u679c\u8bf4\u73ca\u5609\u4f1a\u82b1\u51e0\u767e\u91d1\u5e01\u53bb\u4e70\u4e00\u652f\u957f\u7b1b\uff0c\u743c\u6069\u5f53\u7136\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u4f46\u786e\u5b9e\u6709\u70b9\u60ca\u8bb6\u5c31\u662f\u4e86\u3002</p><p>\u5514\uff0c\u4e5f\u662f\u4ef6\u597d\u4e8b\uff0c\u8fd9\u8bf4\u660e\u59d0\u59d0\u786e\u5b9e\u975e\u5e38\u559c\u6b22\u97f3\u4e50\uff0c\u800c\u4e14\u559c\u6b22\u7684\u4e50\u5668\u662f\u957f\u7b1b\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u81ea\u5df1\u51c6\u5907\u7684\u793c\u7269\u4e0d\u5c31\u6b63\u5408\u9002\u4e48\uff0c\u4e00\u5b9a\u4f1a\u8ba9\u5979\u9ad8\u5174\u7684\u3002</p><p>\u5ff5\u53ca\u6b64\u5904\uff0c\u743c\u6069\u4e5f\u4fbf\u4e0d\u518d\u591a\u60f3\uff0c\u968f\u624b\u5c06\u957f\u7b1b\u653e\u4e0b\uff0c\u8d70\u51fa\u623f\u95f4\uff0c\u4e0b\u697c\u53bb\u627e\u73ca\u5609\u3002</p><p>\u73ca\u5609\u6b63\u5728\u53a8\u623f\u4e2d\u5fd9\u788c\uff0c\u4e00\u8fb9\u54fc\u7740\u6b4c\u4e00\u8fb9\u5728\u714e\u725b\u6392\uff0c\u5979\u5df2\u7ecf\u8131\u53bb\u5916\u5957\uff0c\u4e0a\u8eab\u7a7f\u7740\u9ed1\u8272\u7684\u7f8a\u6bdb\u886b\uff0c\u4e0b\u8eab\u540c\u6837\u662f\u9ed1\u8272\u957f\u88e4\uff0c\u8170\u95f4\u7cfb\u7740\u4e00\u4ef6\u6d45\u84dd\u8272\u7684\u56f4\u88d9\uff0c\u7b80\u7b80\u5355\u5355\u7684\u88c5\u675f\uff0c\u7a88\u7a95\u6027\u611f\uff0c\u53c8\u900f\u7740\u51e0\u5206\u6e29\u5a49\u67d4\u7f8e\uff0c\u53ea\u662f\u770b\u89c1\u80cc\u5f71\u4fbf\u8ba9\u4eba\u6709\u4e00\u79cd\u975e\u5e38\u5b89\u5fc3\u7684\u611f\u89c9\u3002\u743c\u6069\u6084\u6084\u8d70\u5230\u8eab\u540e\u5c06\u5979\u62b1\u4f4f\uff0c\u73ca\u5609\u4e0d\u66fe\u542c\u5230\u811a\u6b65\u58f0\uff0c\u731d\u7136\u4e4b\u4e0b\u5403\u4e86\u4e00\u60ca\uff0c\u51e0\u4e4e\u4e0b\u610f\u8bc6\u5730\u5c31\u8981\u5c06\u624b\u91cc\u7684\u94f2\u5b50\u7838\u8fc7\u53bb\uff0c\u603b\u7b97\u53cd\u5e94\u5f97\u5feb\uff0c\u53ca\u65f6\u6536\u4f4f\uff0c\u201c\u5e72\u561b\u5462\uff0c\u201d\u5979\u55d4\u602a\uff0c\u201c\u8fd9\u4e48\u5927\u7684\u4eba\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u4e56\u4e00\u70b9\u3002\u201d</p><p>\u743c\u6069\u4e0d\u8bf4\u8bdd\uff0c\u53cc\u81c2\u73af\u6263\u5728\u73ca\u5609\u8170\u95f4\uff0c\u4f4e\u5934\u5728\u5979\u7684\u540e\u9888\u4e0a\u5bc6\u5bc6\u4eb2\u543b\u3002\u70bd\u70ed\u7684\u547c\u5438\u5439\u5728\u5a07\u5ae9\u808c\u80a4\u4e0a\uff0c\u6709\u4e9b\u75d2\u75d2\u7684\uff0c\u73ca\u5609\u683c\u683c\u5a07\u7b11\uff0c\u7528\u7a7a\u7740\u7684\u5de6\u624b\u5728\u4ed6\u5934\u4e0a\u8f7b\u8f7b\u62cd\u4e86\u62cd\uff0c\u201c\u522b\u95f9\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u59d0\u59d0\u8981\u505a\u996d\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u60f3\u62b1\u7740\u59d0\u59d0\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u4e48\u62b1\u7740\uff0c\u6211\u8fd8\u600e\u4e48\u52a8\u554a\uff0c\u201d\u73ca\u5609\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u7684\u665a\u9910\u53ef\u5c31\u6ca1\u7740\u843d\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u4e0d\u5403\u665a\u9910\u2026\u2026\u6211\u5403\u59d0\u59d0\u3002\u201d</p><p>\u73ca\u5609\u8f6c\u8fc7\u5934\uff0c\u5728\u4ed6\u8138\u988a\u4e0a\u4eb2\u4e86\u4e00\u53e3\uff0c\u201c\u665a\u4e0a\u7761\u89c9\u518d\u8ba9\u4f60\u62b1\u7740\uff0c\u597d\u4e0d\u597d\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u60f3\u62b1\u591a\u4e45\u5c31\u62b1\u591a\u4e45\uff0c\u59d0\u59d0\u53c8\u4e0d\u662f\u4e0d\u8ba9\uff0c\u5e72\u561b\u8fd9\u4e48\u4e00\u526f\u6025\u4e0d\u53ef\u5f85\u7684\u6837\u5b50\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u5f88\u4e45\u3001\u5f88\u4e45\u3001\u5f88\u4e45\u3001\u5f88\u4e45\u90fd\u6ca1\u6709\u62b1\u8fc7\u59d0\u59d0\u4e86\u5440\u3002\u201d</p><p>\u201c\u90a3\u6709\u6ca1\u6709\u62b1\u8fc7\u522b\u7684\u5973\u5b69\u5b50\u5462\uff1f\u201d\u73ca\u5609\u53cd\u95ee\u3002</p><p>\u201c\u2026\u2026\u62b1\u8fc7\u3002\u201d</p><p>\u201c\u8fd8\u7b97\u8001\u5b9e\uff0c\u201d\u73ca\u5609\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u5c06\u5e73\u5e95\u9505\u91cc\u7684\u725b\u6392\u7ffb\u4e86\u4e2a\u8fb9\uff0c\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\u5462\uff0c\u5979\u600e\u4e48\u6ca1\u548c\u4f60\u4e00\u8d77\uff1f\u201d</p><p>\u201c\u5979\u6709\u4e8b\u5148\u53bb\u963f\u683c\u62c9\u9686\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u5176\u5b9e\u90fd\u672a\u5fc5\u77e5\u9053\u963f\u683c\u62c9\u9686\u8fd9\u5730\u65b9\u662f\u54ea\uff0c\u4f46\u5979\u4e5f\u6ca1\u8ffd\u95ee\uff0c\u53cd\u6b63\u539f\u672c\u5c31\u662f\u95f2\u8c08\u804a\u5929\uff0c\u5e76\u4e0d\u662f\u771f\u6709\u4ec0\u4e48\u76ee\u7684\u6027\u3002\u5979\u770b\u770b\u706b\u5019\uff0c\u5c06\u725b\u6392\u76db\u5230\u76d8\u4e2d\uff0c\u9012\u7ed9\u743c\u6069\uff0c\u201c\u5148\u7aef\u8fc7\u53bb\uff0c\u522b\u653e\u5728\u8fd9\u91cc\uff0c\u53e6\u5916\uff0c\u5979\u4eec\u4e5f\u56de\u6765\u4e86\u5427\uff1f\u201d</p><p>\u201c\u5979\u4eec\uff1f\u201d</p><p>\u201c\u83ab\u5c3c\u5361\u5bb6\u7684\u90a3\u5bf9\u53cc\u80de\u80ce\uff0c\u4e0d\u662f\u548c\u4f60\u4e00\u8d77\u51fa\u53d1\u7684\u5417\uff0c\u96be\u9053\u4f60\u8fd9\u6b21\u6ca1\u628a\u5979\u4eec\u5e26\u56de\u6765\uff0c\u8fd8\u7559\u5728\u5e7d\u6697\u5730\u57df\uff1f\u201d</p><p>\u201c\u5f53\u7136\u6ca1\u6709\uff0c\u5979\u4eec\u73b0\u5728\u5e94\u8be5\u5c31\u5728\u5bb6\u5462\u3002\u201d</p><p>\u201c\u90a3\u8bf7\u5979\u4eec\u4e00\u8d77\u8fc7\u6765\u5403\u665a\u9910\u5427\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u53cd\u6b63\u4eba\u4e0d\u591a\uff0c\u5927\u5bb6\u5728\u4e00\u8d77\u4e5f\u70ed\u95f9\u4e9b\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8e0c\u8e87\uff0c\u5bfb\u601d\u7740\u73ca\u5609\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u662f\u7b80\u5355\u7684\u9080\u8bf7\u76f8\u805a\uff0c\u8fd8\u662f\u5728\u6697\u793a\u4ec0\u4e48\u3002\u5c31\u4ed6\u800c\u8a00\uff0c\u672c\u80fd\u5730\u4e0d\u5e0c\u671b\u770b\u89c1\u73ca\u5609\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u4fe9\u5728\u4e00\u8d77\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u5462\uff0c\u4f46\u8981\u53cd\u5bf9\u7684\u8bdd\uff0c\u4e00\u65f6\u53c8\u6ca1\u4ec0\u4e48\u5408\u9002\u7684\u7406\u7531\uff0c\u53cd\u800c\u5f92\u60f9\u7591\u5fc3\u3002</p><p>\u7b97\u4e86\uff0c\u53cd\u6b63\u5176\u5b9e\u4e5f\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u53ea\u662f\u6ca1\u60f3\u5230\u4f1a\u6765\u5f97\u8fd9\u4e48\u5feb\u800c\u5df2\u3002\u65e2\u7136\u8eb2\u4e0d\u8fc7\uff0c\u90a3\u5c31\u4e0d\u8eb2\u4e86\u3002\u8bf4\u5f97\u597d\u542c\u70b9\uff0c\u8fd9\u53eb\u505a\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u6562\u4f5c\u6562\u5f53\uff1b\u8bf4\u5f97\u96be\u542c\u70b9\u2026\u2026\u8fd9\u53eb\u505a\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\u3002</p><p>\u203b\u203b\u203b</p><p>\u665a\u9910\u5f88\u4e30\u76db\uff0c\u73ca\u5609\u7684\u53a8\u827a\u663e\u7136\u5927\u6709\u8fdb\u6b65\uff0c\u4e5f\u5f88\u7b26\u5408\u743c\u6069\u7684\u80c3\u53e3\uff0c\u53ef\u60dc\u4ed6\u4f9d\u65e7\u6709\u4e9b\u98df\u4e0d\u7518\u5473\uff0c\u56e0\u4e3a\u5fc3\u4e0d\u5728\u7109\u3002</p><p>\u4e09\u4e2a\u5973\u5b69\u5b50\u8c08\u7b11\u98ce\u751f\uff0c\u770b\u8d77\u6765\u6c14\u6c1b\u5341\u5206\u878d\u6d3d\uff0c\u4f46\u65e0\u5f62\u4e4b\u95f4\u4f3c\u4e4e\u4e5f\u628a\u4ed6\u8fd9\u4e2a\u552f\u4e00\u7684\u7537\u6027\u51b7\u843d\u4e86\uff0c\u743c\u6069\u5bf9\u6b64\u5012\u6ca1\u4ec0\u4e48\u4e0d\u6ee1\u7684\uff0c\u6216\u8005\u8bf4\u6c42\u4e4b\u4e0d\u5f97\uff0c\u6b63\u597d\u65c1\u89c2\u3002\u73ca\u5609\u5b8c\u7f8e\u5730\u626e\u6f14\u7740\u5973\u4e3b\u4eba\u7684\u89d2\u8272\uff0c\u4eea\u6001\u5927\u65b9\uff0c\u70ed\u60c5\u5468\u5230\uff0c\u867d\u7136\u53ea\u662f\u7d20\u5986\u5e38\u670d\uff0c\u5374\u81ea\u6709\u79cd\u534e\u8d35\u6c14\u5ea6\u9690\u9690\u6d41\u9732\uff0c\u5149\u5f69\u7167\u4eba\uff1b\u8299\u857e\u72c4\u660e\u663e\u6709\u70b9\u7d27\u5f20\uff0c\u8a00\u8bed\u4e3e\u6b62\u8c28\u5b88\u793c\u4eea\uff0c\u773c\u795e\u4e0d\u6562\u548c\u73ca\u5609\u5bf9\u89c6\uff0c\u603b\u6709\u4e9b\u5a07\u602f\u5bb3\u7f9e\u7684\u6a21\u6837\uff0c\u53cd\u5012\u662f\u8299\u8389\u5a05\u795e\u60c5\u5766\u7136\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u5012\u662f\u8ba9\u743c\u6069\u9887\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\uff0c\u5fc3\u60f3\u5e73\u65f6\u5012\u662f\u6709\u4e9b\u5c0f\u770b\u5979\u4e86\u3002</p><p>\u603b\u7b97\u652f\u6491\u5230\u665a\u5bb4\u7ed3\u675f\uff0c\u83ab\u5c3c\u5361\u59d0\u59b9\u544a\u8f9e\uff0c\u743c\u6069\u6697\u4e2d\u957f\u957f\u8212\u4e86\u53e3\u6c14\uff0c\u5148\u5c06\u5979\u4eec\u9001\u56de\u5bb6\uff0c\u518d\u56de\u6765\u7684\u65f6\u5019\uff0c\u5973\u4ec6\u5df2\u7ecf\u5728\u6536\u62fe\u9910\u684c\uff0c\u73ca\u5609\u5374\u4e0d\u89c1\u8e2a\u5f71\uff0c\u4e00\u95ee\u4e4b\u4e0b\u5f97\u77e5\u662f\u6c90\u6d74\u66f4\u8863\u53bb\u4e86\u3002\u743c\u6069\u4fbf\u81ea\u5df1\u5148\u4e0a\u697c\uff0c\u8fdb\u4e86\u59d0\u59d0\u7684\u623f\u95f4\u3002</p><p>\u8fc7\u4e86\u5927\u7ea6\u534a\u4e2a\u591a\u5c0f\u65f6\uff0c\u73ca\u5609\u88f9\u7740\u6d74\u5dfe\u63a8\u95e8\u8fdb\u6765\uff0c\u770b\u89c1\u743c\u6069\u5728\u623f\u95f4\u91cc\uff0c\u4e5f\u4e0d\u60ca\u8bb6\uff0c\u968f\u624b\u6254\u8fc7\u6765\u4e00\u6761\u6bdb\u5dfe\u3002</p><p>\u201c\u5e2e\u6211\u628a\u5934\u53d1\u64e6\u5e72\u3002\u201d\u5979\u8bf4\u3002</p><p>\u73ca\u5609\u5750\u5728\u68b3\u5986\u53f0\u524d\uff0c\u743c\u6069\u7ad9\u5728\u5979\u80cc\u540e\uff0c\u62ff\u7740\u6bdb\u5dfe\u5c0f\u5fc3\u5730\u64e6\u62ed\u7740\u3002\u73ca\u5609\u6709\u4e00\u5934\u4e4c\u9ed1\u6d53\u5bc6\u7684\u957f\u53d1\uff0c\u81ea\u5e7c\u517b\u8d77\u6765\u7684\uff0c\u76f4\u5782\u8170\u9645\uff0c\u7ec6\u81f4\u67d4\u987a\uff0c\u5149\u6d01\u5982\u7f0e\uff0c\u6f02\u4eae\u81ea\u7136\u662f\u6f02\u4eae\uff0c\u4f46\u5bfc\u81f4\u7684\u4e00\u4e2a\u751f\u6d3b\u4e0a\u7684\u9ebb\u70e6\uff0c\u4fbf\u662f\u6bcf\u6b21\u6d17\u6d74\u4e4b\u540e\u90fd\u8981\u82b1\u8d39\u5f88\u957f\u7684\u65f6\u95f4\u6765\u628a\u5934\u53d1\u5f04\u5e72\uff0c\u5426\u5219\u7b2c\u4e8c\u5929\u5934\u5c31\u4f1a\u75bc\u3002</p><p>\u201c\u59d0\u59d0\u7684\u5934\u53d1\u597d\u50cf\u53c8\u53d8\u957f\u4e86\u5f88\u591a\u5462\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f60\u8fd9\u6b21\u51fa\u53bb\uff0c\u5df2\u7ecf\u6709\u4e00\u5e74\u96f6\u4e09\u4e2a\u6708\u4e86\u561b\uff0c\u5b83\u81ea\u7136\u4e5f\u957f\u957f\u4e86\u5f88\u591a\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u4e5f\u6ca1\u60f3\u5230\u8fd9\u6b21\u5c45\u7136\u4f1a\u8fd9\u4e48\u4e45\u3002\u201d</p><p>\u73ca\u5609\u7b11\u8d77\u6765\uff0c\u201c\u6ca1\u4e8b\uff0c\u6211\u53c8\u6ca1\u602a\u4f60\uff0c\u968f\u4fbf\u804a\u5929\uff0c\u90a3\u4e48\u7d27\u5f20\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u5730\u6478\u6478\u5934\uff0c\u201c\u5bf9\u4e86\uff0c\u5dee\u70b9\u5fd8\u4e86\uff0c\u201d\u4ed6\u4ece\u65c1\u8fb9\u53d6\u8fc7\u4e00\u4e2a\u88c5\u9970\u7cbe\u7f8e\u7684\u76d2\u5b50\u6765\uff0c\u201c\u59d0\u59d0\uff0c\u5341\u516d\u5c81\u751f\u65e5\u5feb\u4e50\u3002\u201d</p><p>\u73ca\u5609\u5931\u7b11\uff0c\u201c\u6211\u5341\u4e03\u5c81\u751f\u65e5\u90fd\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\u8981\u8865\u9001\u751f\u65e5\u793c\u7269\u561b\uff0c\u8fd9\u662f\u5341\u516d\u5c81\u7684\u90a3\u4efd\u3002\u201d</p><p>\u73ca\u5609\u63a5\u8fc7\u76d2\u5b50\uff0c\u8f7b\u8f7b\u6380\u5f00\uff0c\u770b\u89c1\u4e00\u6761\u5149\u534e\u6d41\u6ea2\u7684\u5b9d\u77f3\u73cd\u73e0\u9879\u94fe\uff0c\u201c\u5f88\u6f02\u4eae\u554a\uff0c\u201d\u73ca\u5609\u6b23\u8d4f\u7740\uff0c\u201c\u5728\u54ea\u4e70\u7684\uff1f\u201d</p><p>\u201c\u6211\u5728\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\uff0c\u8bf7\u4f8f\u5112\u5de5\u5320\u5b9a\u5236\u7684\uff0c\u201d\u743c\u6069\u9887\u6709\u4e9b\u5f97\u610f\uff0c\u201c\u4e0a\u9762\u5168\u90fd\u9644\u4e86\u9b54\u6cd5\uff0c\u57fa\u672c\u662f\u6211\u4e00\u624b\u5b8c\u6210\u7684\u3002\u201d</p><p>\u201c\u7279\u5730\u4e3a\u59d0\u59d0\u505a\u7684\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u73ca\u5609\u62e3\u8d77\u9879\u94fe\uff0c\u6307\u5c16\u5728\u6bcf\u4e00\u679a\u5b9d\u77f3\u4e0a\u8f7b\u8f7b\u6ed1\u8fc7\uff0c\u201c\u7ed9\u59d0\u59d0\u8bf4\u8bf4\u770b\uff0c\u90fd\u9644\u4e86\u54ea\u4e9b\u9b54\u6cd5\u5462\uff1f\u201d</p><p>\u743c\u6069\u4e8e\u662f\u4e00\u4e00\u4ecb\u7ecd\uff0c\u201c\u8fd9\u679a\u661f\u7ea2\u77f3\u9644\u6709\u955c\u5f71\u672f\uff0c\u7d2b\u6c34\u6676\u9644\u6709\u67ad\u4e4b\u6d1e\u5bdf\uff0c\u7fe1\u7fe0\u9644\u6709\u718a\u4e4b\u5fcd\u8010\uff0c\u7d2b\u7389\u9644\u6709\u9690\u8eab\u672f\u2026\u2026\u6700\u540e\u8fd9\u679a\u94bb\u77f3\uff0c\u6bcf\u5929\u53ef\u4ee5\u6fc0\u53d1\u4e00\u6b21\u6cbb\u7597\u672f\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u56db\u9897\u9ed1\u73cd\u73e0\u5462\uff1f\u201d\u73ca\u5609\u95ee\uff0c\u63d0\u9192\u743c\u6069\u7684\u9057\u6f0f\u3002</p><p>\u201c\u54e6\uff0c\u5b83\u4eec\u5c31\u662f\u5355\u7eaf\u7684\u88c5\u9970\uff0c\u6ca1\u6709\u9644\u9b54\u4e86\uff0c\u201d\u743c\u6069\u563b\u563b\u7b11\u7740\uff0c\u201c\u4e0d\u7528\u5728\u610f\uff0c\u59d0\u59d0\uff0c\u53ea\u662f\u51fa\u4e8e\u7f8e\u89c2\u7684\u89d2\u5ea6\u6765\u8003\u8651\u3002\u201d</p><p>\u201c\u8fd9\u6837\u6bd4\u8f83\u7f8e\u89c2\uff1f\u201d\u73ca\u5609\u6709\u4e9b\u5947\u602a\u5730\u770b\u7740\u516b\u679a\u5b9d\u77f3\u4e2d\u6df7\u6742\u7740\u56db\u9897\u9ed1\u8272\u73cd\u73e0\uff0c\u603b\u89c9\u5f97\u6709\u4e9b\u4e0d\u534f\u8c03\uff0c\u4f46\u4e5f\u6ca1\u6709\u591a\u60f3\u4ec0\u4e48\u3002\u743c\u6069\u53d6\u8fc7\u9879\u94fe\uff0c\u4ed4\u7ec6\u66ff\u5979\u6234\u4e0a\u3002</p><p>\u201c\u59d0\u59d0\u771f\u7f8e\u3002\u201d\u4ed6\u7531\u8877\u5730\u8bf4\u3002</p><p>\u73ca\u5609\u751c\u751c\u4e00\u7b11\uff0c\u5bf9\u743c\u6069\u7684\u8fd9\u4efd\u793c\u7269\u4e5f\u9887\u4e3a\u6ee1\u610f\uff0c\u201c\u96be\u9053\u4e0d\u5e94\u8be5\u8fd8\u6709\u4e00\u4efd\u5417\uff1f\u201d\u5979\u6545\u610f\u677f\u7740\u8138\u95ee\uff0c\u201c\u8fd9\u662f\u5341\u516d\u5c81\u751f\u65e5\u7684\uff0c\u90a3\u5341\u4e03\u5c81\u751f\u65e5\u7684\u5462\uff1f\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u59d0\u59d0\uff0c\u5f53\u7136\u51c6\u5907\u4e86\uff0c\u201d\u743c\u6069\u53d6\u8fc7\u53e6\u4e00\u4e2a\u957f\u957f\u7684\u5323\u5b50\u6765\uff0c\u201c\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u957f\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u6709\u70b9\u5947\u602a\u3002</p><p>\u201c\u6253\u5f00\u770b\u770b\u5c31\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4fdd\u8bc1\u8ba9\u59d0\u59d0\u6ee1\u610f\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u6211\u770b\u770b\uff0c\u201d\u73ca\u5609\u6253\u5f00\u5323\u5b50\uff0c\u987f\u65f6\u773c\u775b\u4eae\u4e86\u4e00\u4eae\uff0c\u201c\u54c7\uff0c\u597d\u6f02\u4eae\u7684\u957f\u7b1b\u2014\u2014\u54a6\uff0c\u743c\u6069\uff0c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u6b63\u5728\u5b66\u957f\u7b1b\u554a\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u6211\u4eec\u662f\u59d0\u5f1f\u561b\uff0c\u6240\u8c13\u5fc3\u6709\u7075\u7280\uff0c\u201d\u743c\u6069\u5f53\u7136\u4e0d\u4f1a\u8bf4\u5176\u5b9e\u662f\u78b0\u5de7\u649e\u5bf9\uff0c\u201c\u800c\u4e14\u6211\u89c9\u5f97\u50cf\u59d0\u59d0\u8fd9\u6837\u7684\u7f8e\u4eba\u513f\uff0c\u5982\u679c\u7a7f\u4e0a\u957f\u88d9\uff0c\u5439\u594f\u957f\u7b1b\uff0c\u90a3\u80af\u5b9a\u662f\u4e16\u754c\u4e0a\u6700\u597d\u770b\u7684\u753b\u9762\u4e86\u3002\u201d</p><p>\u73ca\u5609\u62ff\u8d77\u957f\u7b1b\uff0c\u653e\u5728\u5507\u8fb9\u8bd5\u7740\u5439\u4e86\u51e0\u4e2a\u97f3\uff0c\u8f7b\u8f7b\u70b9\u5934\uff0c\u201c\u5f88\u4e0d\u9519\uff0c\u201d\u5979\u8bc4\u4ef7\uff0c\u201c\u770b\u5728\u8fd9\u652f\u957f\u7b1b\u7684\u4efd\u4e0a\uff0c\u8003\u8651\u8fd9\u6b21\u8981\u4e0d\u8981\u9976\u4e86\u4f60\u3002\u201d</p><p>\u201c\u554a\uff1f\u9976\u4e86\u6211\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u59d0\u59d0\uff0c\u6211\u505a\u9519\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u8fd8\u8ddf\u6211\u88c5\u50bb\uff1f\u201d\u73ca\u5609\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u9694\u58c1\u5bb6\u90a3\u5bf9\u59d0\u59b9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u201c\u522b\u544a\u8bc9\u6211\u4f60\u6ca1\u5bf9\u5979\u4eec\u4e0b\u624b\uff0c\u201d\u73ca\u5609\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u800c\u4e14\uff0c\u8299\u857e\u72c4\u5c31\u7b97\u4e86\uff0c\u53cd\u6b63\u4e5f\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u6ca1\u6599\u5230\u8fde\u8299\u8389\u5a05\u90fd\u88ab\u4f60\u7978\u5bb3\u4e86\u2014\u2014\u6211\u6ca1\u770b\u9519\u5427\uff1f\u201d</p><p>\u201c\u2026\u2026\u6ca1\u6709\u3002\u201d</p><p>\u201c\u4ee5\u524d\u6ca1\u770b\u51fa\u6765\u5462\uff0c\u6211\u5f1f\u5f1f\u5c45\u7136\u662f\u8fd9\u4e48\u6709\u9b45\u529b\u7684\u7537\u4eba\uff0c\u201d\u73ca\u5609\u4f3c\u7b11\u975e\u7b11\u5730\u770b\u7740\u4ed6\uff0c\u201c\u5403\u4e86\u59b9\u59b9\u4e0d\u7b97\uff0c\u8fde\u59d0\u59d0\u4e5f\u4e00\u5e76\u5f04\u5230\u624b\uff0c\u96be\u602a\u5728\u5916\u9762\u8fd9\u4e48\u957f\u65f6\u95f4\u90fd\u4e0d\u56de\u6765\uff0c\u53cd\u6b63\u6709\u5979\u4eec\u966a\u7740\uff0c\u6bcf\u5929\u4e00\u5b9a\u8fc7\u5f97\u5f88\u5feb\u4e50\u5427\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u5bf9\u4e0d\u8d77\u5566\u3002\u201d</p><p>\u743c\u6069\u81ea\u77e5\u65e0\u6cd5\u62b5\u8d56\uff0c\u7d22\u6027\u8f6f\u8bed\u6073\u6c42\uff0c\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u518d\u8bf4\uff0c\u4ed4\u7ec6\u7aef\u8be6\u8d77\u8fd9\u652f\u591c\u98ce\u4e4b\u7b1b\u6765\u3002\u743c\u6069\u770b\u7740\u73ca\u5609\u7684\u4fa7\u8138\uff0c\u89c1\u5979\u795e\u8272\u5982\u5e38\uff0c\u4ece\u5bb9\u81ea\u5728\uff0c\u5e76\u6ca1\u4ec0\u4e48\u771f\u6b63\u52a8\u6012\u7684\u8ff9\u8c61\uff0c\u4e0d\u89c9\u6709\u4e9b\u5947\u602a\uff0c\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u95ee\uff0c\u201c\u4f60\u597d\u50cf\u4e0d\u600e\u4e48\u751f\u6c14\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u96be\u9053\u4f60\u5e0c\u671b\u6211\u751f\u6c14\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u4e0d\u662f\u3002\u201d\u743c\u6069\u8fde\u5fd9\u5426\u8ba4\u3002</p><p>\u201c\u8c01\u8bf4\u6211\u6ca1\u751f\u6c14\u7684\uff1f\u201d\u73ca\u5609\u6267\u7740\u957f\u7b1b\u8f7b\u8f7b\u5728\u4ed6\u5934\u4e0a\u6572\u4e86\u4e00\u4e0b\uff0c\u201c\u544a\u8bc9\u4f60\uff1a\u59d0\u59d0\u5f88\u751f\u6c14\uff01\u7f5a\u4f60\u4eca\u665a\u522b\u7761\u4e86\uff0c\u7ed9\u6211\u8bb2\u6545\u4e8b\u3002\u201d</p><p>\u201c\u8bb2\u6545\u4e8b\uff1f\u8bb2\u4ec0\u4e48\u6545\u4e8b\uff1f\u201d</p><p>\u201c\u5c31\u8bb2\u4f60\u8fd9\u4e00\u5e74\u591a\u6765\u7684\u6545\u4e8b\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u8bb2\u4f60\u5728\u5916\u9762\u53c8\u5077\u5077\u52fe\u642d\u4e86\u591a\u5c11\u4e2a\u5973\u5b69\u5b50\uff0c\u4e00\u4e94\u4e00\u5341\u7ed9\u6211\u4ece\u5b9e\u62db\u6765\uff0c\u4e0d\u51c6\u6709\u534a\u70b9\u9690\u7792\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u65e2\u7136\u505a\u4e86\u574f\u4e8b\uff0c\u5c31\u8981\u6709\u627f\u62c5\u540e\u679c\u7684\u89c9\u609f\uff0c\u53ea\u662f\u7ed9\u73ca\u5609\u8bb2\u8bb2\u6545\u4e8b\uff0c\u8fd9\u5df2\u7ecf\u662f\u8f7b\u5f97\u4e0d\u80fd\u518d\u8f7b\u7684\u5904\u7f5a\u4e86\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8bf4\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u665a\u4e0a\u51b7\uff0c\u6211\u4eec\u5230\u5e8a\u4e0a\u53bb\u8bf4\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u5934\u53d1\u4e5f\u5df2\u7ecf\u57fa\u672c\u90fd\u5e72\u4e86\uff0c\u73ca\u5609\u5e94\u4e86\u4e00\u58f0\uff0c\u4ece\u6905\u4e2d\u7ad9\u4e86\u8d77\u6765\uff0c\u5979\u539f\u672c\u662f\u6d17\u6d74\u65b9\u51fa\uff0c\u8eab\u4e0a\u53ea\u88f9\u7740\u4e00\u4ef6\u6d74\u5dfe\uff0c\u5750\u4e86\u8fd9\u4e48\u4e45\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4e5f\u65e9\u5c31\u677e\u5f00\uff0c\u8fd9\u4e00\u8d77\u8eab\u5c31\u6ed1\u843d\u4e86\u4e0b\u6765\uff0c\u6574\u4e2a\u4eba\u987f\u65f6\u4e00\u4e1d\u4e0d\u6302\u5730\u5c55\u9732\u5728\u743c\u6069\u9762\u524d\u3002</p><p>\u5341\u4e03\u5c81\u7684\u5c11\u5973\u7389\u4f53\uff0c\u5df2\u7ecf\u53d1\u80b2\u5230\u4e86\u6700\u5b8c\u7f8e\u7684\u9636\u6bb5\uff0c\u4e4c\u9ed1\u7684\u957f\u53d1\u5982\u540c\u6d41\u6cfb\u7684\u9526\u7f0e\uff0c\u4fee\u957f\u7684\u9888\u9879\u6302\u7740\u5b9d\u77f3\u9879\u94fe\uff0c\u7cbe\u81f4\u67d4\u548c\u7684\u7ebf\u6761\uff0c\u52fe\u52d2\u51fa\u9971\u6ee1\u6d0b\u6ea2\u7684\u9752\u6625\u6d3b\u529b\u3002\u623f\u95f4\u91cc\u7684\u6d45\u9ec4\u8272\u706f\u5149\u3001\u7a97\u5916\u900f\u8fdb\u6765\u7684\u6e05\u51b7\u6708\u8272\uff0c\u548c\u65e0\u5904\u4e0d\u5728\u7684\u6de1\u6de1\u9634\u5f71\u4ea4\u9519\u5728\u4e00\u8d77\uff0c\u7f16\u7ec7\u6210\u6700\u7f8e\u4e3d\u7684\u753b\u9762\uff0c\u800c\u5c11\u5973\u7684\u88f8\u4f53\u4fbf\u662f\u8fd9\u526f\u753b\u9762\u7684\u4e2d\u5fc3\u3002\u65e0\u6cd5\u7528\u8bed\u8a00\u63cf\u7ed8\uff0c\u4e5f\u65e0\u9700\u5173\u6ce8\u5177\u4f53\u7684\u90e8\u4f4d\uff0c\u53ea\u662f\u5355\u7eaf\u5730\u770b\u7740\uff0c\u4fbf\u4f1a\u4e3a\u4e4b\u9707\u64bc\uff0c\u4e3a\u4e4b\u8d5e\u53f9\uff0c\u5fc3\u593a\u795e\u79fb\uff0c\u9b42\u7275\u68a6\u7ed5\uff0c\u65e0\u6cd5\u5fd8\u5374\u3002</p><p>\u743c\u6069\u8111\u4e2d\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u4eff\u4f5b\u4e2d\u4e86\u5de8\u5927\u7684\u7cbe\u795e\u51b2\u51fb\uff0c\u5239\u90a3\u95f4\u7a7a\u767d\u4e00\u7247\uff0c\u6240\u6709\u7684\u601d\u7ef4\u5c3d\u6570\u505c\u6ede\uff0c\u5404\u79cd\u60f3\u6cd5\u5ff5\u5934\u5168\u90fd\u629b\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u6574\u4e2a\u4eba\u5f53\u573a\u6014\u4f4f\u4e86\uff0c\u76f4\u5230\u73ca\u5609\u8f7b\u58f0\u4f4e\u5524\u624d\u56de\u8fc7\u795e\u6765\u3002\u201c\u5c0f\u5f1f\uff0c\u770b\u5446\u4e86\uff1f\u201d\u5c11\u5973\u76c8\u76c8\u7b11\u7740\uff0c\u201c\u8fd8\u4e0d\u628a\u59d0\u59d0\u62b1\u5230\u5e8a\u4e0a\u53bb\uff0c\u4f1a\u611f\u5192\u7684\u3002\u201d</p><p>\u201c\u554a\u2026\u2026\u54e6\u3002\u201d</p><p>\u743c\u6069\u614c\u5fd9\u5c06\u5979\u62b1\u8d77\uff0c\u8d70\u5230\u5e8a\u8fb9\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u653e\u4e0b\uff0c\u73ca\u5609\u62c9\u8fc7\u6bdb\u6bef\u76d6\u5728\u8eab\u4e0a\uff0c\u62cd\u4e86\u62cd\u65c1\u8fb9\u7684\u5e8a\u5355\uff0c\u201c\u4e0a\u6765\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u522b\u7ad9\u7740\u3002\u201d</p><p>\u743c\u6069\u8131\u53bb\u5916\u5957\uff0c\u5750\u5728\u59d0\u59d0\u65c1\u8fb9\uff0c\u73ca\u5609\u987a\u52bf\u5c06\u5934\u6795\u5728\u4ed6\u7684\u5927\u817f\u4e0a\uff0c\u53ef\u7231\u5730\u8737\u7f29\u8d77\u8eab\u4f53\uff0c\u88f9\u5728\u7c89\u8272\u7684\u6bdb\u6bef\u4e2d\uff0c\u5c31\u50cf\u4e00\u53ea\u6e29\u67d4\u7684\u732b\u54aa\u3002\u201c\u59d0\u59d0\u6f02\u4eae\u5417\uff1f\u201d\u5979\u7a81\u7136\u95ee\uff0c\u201c\u770b\u4f60\u521a\u624d\u90fd\u5446\u4f4f\u4e86\u4f3c\u7684\u3002\u201d</p><p>\u201c\u6f02\u4eae\uff0c\u201d\u743c\u6069\u53f9\u606f\u4e00\u822c\u5730\u8bf4\uff0c\u201c\u6f02\u4eae\u5f97\u2026\u2026\u5c31\u50cf\u68a6\u4e00\u6837\u3002\u201d</p><p>\u201c\u6709\u90a3\u4e48\u5938\u5f20\u5417\uff1f\u201d\u73ca\u5609\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u4fe1\uff0c\u201c\u5973\u5b69\u5b50\u7684\u8eab\u4f53\uff0c\u4f60\u96be\u9053\u8fd8\u770b\u5f97\u5c11\u4e86\uff0c\u65e9\u5e94\u8be5\u770b\u4e60\u60ef\u4e86\u5427\u3002\u201d</p><p>\u201c\u662f\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u4f46\u8fd9\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u5979\u4eec\u662f\u5979\u4eec\uff0c\u59d0\u59d0\u662f\u59d0\u59d0\u3002\u201d</p><p>\u201c\u59d0\u59d0\u6bd4\u5979\u4eec\u6f02\u4eae\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\u6700\u6f02\u4eae\u3002\u201d</p><p>\u201c\u827e\u5f25\u8587\u5462\uff1f\u201d\u73ca\u5609\u95ee\uff0c\u201c\u59d0\u59d0\u6bd4\u827e\u5f25\u8587\u8fd8\u6f02\u4eae\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u201d\u743c\u6069\u8c28\u614e\u5730\u9009\u62e9\u7740\u63aa\u8f9e\uff0c\u201c\u827e\u5f25\u8587\u5979\u4ece\u5c0f\u4e60\u6b66\uff0c\u591a\u5c11\u603b\u900f\u7740\u4e9b\u521a\u5065\u529b\u9053\uff0c\u59d0\u59d0\u8eab\u4f53\u7684\u7ebf\u6761\u6bd4\u8f83\u67d4\u548c\uff0c\u66f4\u50cf\u4e2a\u5973\u5b69\u5b50\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u662f\u559c\u6b22\u90a3\u79cd\uff1f\u521a\u5065\u7684\uff0c\u8fd8\u662f\u67d4\u548c\u7684\uff1f\u201d</p><p>\u743c\u6069\u77ed\u6682\u5730\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u559c\u6b22\u59d0\u59d0\u3002\u201d\u4ed6\u907f\u91cd\u5c31\u8f7b\u5730\u8bf4\u3002</p><p>\u201c\u54e6\uff0c\u53ef\u662f\u5982\u679c\u6211\u6ca1\u8bb0\u9519\uff0c\u4f60\u4ece\u5c0f\u5c31\u6bd4\u8f83\u559c\u6b22\u90a3\u79cd\u82f1\u6b66\u51db\u51db\u7684\u5973\u5b69\u5b50\u5427\uff0c\u201d\u73ca\u5609\u7b11\u541f\u541f\u5730\u770b\u7740\u4ed6\uff0c\u201c\u827e\u5f25\u8587\u4e0d\u6b63\u662f\u4f60\u5fc3\u76ee\u4e2d\u6700\u5b8c\u7f8e\u7684\u61a7\u61ac\u5417\uff1f\u201d</p><p>\u201c\u6211\u559c\u6b22\u59d0\u59d0\u3002\u201d\u743c\u6069\u575a\u6301\u3002</p><p>\u201c\u54ea\u4e2a\u59d0\u59d0\uff1f\u201d\u73ca\u5609\u53cd\u95ee\uff0c\u201c\u8bf4\u4e0d\u5b9a\u4f60\u4e5f\u4f1a\u53eb\u522b\u7684\u5973\u5b69\u5b50\u59d0\u59d0\u5462\u3002\u201d</p><p>\u201c\u73ca\u5609\uff0c\u201d\u743c\u6069\u5b9a\u5b9a\u5730\u76f4\u89c6\u7740\u5979\u7684\u773c\u775b\uff0c\u201c\u59d0\u59d0\u73ca\u5609\u3002\u201d</p><p>\u73ca\u5609\u5ae3\u7136\u4e00\u7b11\uff0c\u201c\u7b97\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6765\uff0c\u7ed9\u59d0\u59d0\u8bb2\u6545\u4e8b\u5427\u3002\u201d</p></article>"}}}]);
