(window.webpackJsonp=window.webpackJsonp||[]).push([[412],{435:function(p){p.exports={chapterTitle:null,title:"\u7b2c412\u7ae0 \u6df7\u4e711978",content:"<article><h2>\u7b2c412\u7ae0 \u6df7\u4e711978</h2><p>\u3000\u3000\u5f53\u521d\u4e5f\u662f\u53f6\u5efa\u658c\u4e0e\u5f20\u606a\u7684\u4e00\u65f6\u5efa\u8bae\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u4e3a\u6c5f\u9edb\u513f\u5929\u7c41\u7684\u7eaf\u51c0\u55d3\u97f3\u91cf\u8eab\u5b9a\u5236\uff0c\u5b59\u9759\u9999\u521b\u529e\u4e16\u7eaa\u534e\u97f3\u5236\u4f5c\u7ecf\u7eaa\u516c\u53f8\uff08\u53f6\u5efa\u658c\u4e5f\u6709\u6295\u8d44\uff09\uff0c\u5b59\u9759\u9999\u672c\u4eba\u518d\u4e5f\u65e0\u6687\u6253\u74061978\uff0c\u81ea\u7136\u8ba9\u5979\u5728\u97f3\u4e50\u5b66\u9662\u8bfb\u4e66\u7684\u59b9\u59b9\u5b59\u9759\u6aac\u7167\u7ba11978\u3002</p><p>\u3000\u30001978\u73b0\u5728\u7684\u5973\u8001\u677f\u662f\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u5efa\u8bae\u665a\u4e0a\u53bb\u5b66\u5e9c\u5df7\u73a9\uff0c\u8a00\u8bed\u95f4\u5bf9\u60ef\u4ee5\u6027\u611f\u88c5\u675f\u793a\u4f17\u3001\u884c\u4e3a\u6027\u683c\u5927\u80c6\u51fa\u4f4d\u7684\u5b59\u9759\u6aac\u9887\u4e3a\u5d07\u656c\uff0c\u5510\u5a67\u3001\u9648\u5983\u84c9\u53c8\u662f\u4e00\u4ed8\u5fc3\u52a8\u7684\u6837\u5b50\uff0c\u5f20\u606a\u5927\u611f\u5934\u75bc\u3002\u53bb\u89c1\u6c5f\u9edb\u513f\u90a3\u4e5f\u5c31\u7f62\u4e86\uff0c\u6c5f\u9edb\u513f\u8fd9\u6bb5\u65f6\u95f4\u4e3a\u5979\u4e2a\u4eba\u7684\u9996\u5f20\u97f3\u4e50\u4e13\u8f91\u7684\u4e8b\u60c5\u4f11\u5b66\u534a\u5e74\uff0c\u4e13\u7a0b\u53bb\u4e86\u9999\u6e2f\uff0c\u5510\u5a67\u6691\u5047\u62c9\u7740\u9648\u5983\u84c9\u4e00\u8d77\u53bb\u9999\u6e2f\u8fd8\u4e13\u7a0b\u5230\u5236\u4f5c\u516c\u53f8\u53c2\u89c2\u8fc7\u97f3\u4e50\u5236\u4f5c\u7684\u8fc7\u7a0b\uff0c\u4e0e\u6c5f\u9edb\u513f\u89c1\u8fc7\u9762\u3002\u9648\u5983\u84c9\u4e0e\u6c5f\u9edb\u513f\u8ba4\u8bc6\u7684\u65f6\u95f4\u66f4\u65e9\uff0c\u80fd\u63a8\u5230\u4e5d\u56db\u5e74\u51ac\u5929\u6c5f\u9edb\u513f\u968f\u8bb8\u7ef4\u5230\u6d77\u5dde\u73a9\u7684\u90a3\u6bb5\u65f6\u95f4\u3002\u4f46\u662f\u5b59\u9759\u6aac\u8fd9\u5a46\u5a18\u773c\u5149\u6bd2\u8fa3\uff0c\u5634\u5df4\u7280\u5229\uff0c\u8c01\u77e5\u9053\u5979\u770b\u5230\u5510\u5a67\u3001\u9648\u5983\u84c9\u8ddf\u7740\u81ea\u5df1\u4e00\u8d77\u53bb1978\u4f1a\u4ece\u5634\u91cc\u5192\u51fa\u4ec0\u4e48\u8bdd\u6765\uff0c\u5c31\u7b97\u6709\u6d41\u8840\u4e8b\u4ef6\u53d1\u751f\u4e5f\u4e0d\u5947\u602a\u3002</p><p>\u3000\u3000\u201c\u5c0f\u606a\uff0c\u6211\u4eec\u665a\u4e0a\u53bb1978\u600e\u4e48\u6837\uff1f\u201d\u5510\u5a67\u8eab\u5b50\u8db4\u5728\u67d4\u8f6f\u7684\u5e8a\u4e0a\uff0c\u5934\u6602\u7740\u8ddf\u7ad9\u5728\u5979\u8eab\u540e\u7684\u5f20\u606a\u8bf4\u8bdd\uff0c\u624b\u652f\u7740\u5e8a\uff0c\u5c06\u4e0a\u534a\u8eab\u6491\u8d77\u6765\uff0c\u5f62\u6210\u4e00\u9053\u60ca\u5fc3\u52a8\u9b44\u7684\u5f27\u7ebf\uff0c\u597d\u50cf\u518d\u7528\u4e00\u70b9\u529b\uff0c\u8170\u80a2\u4fbf\u4f1a\u6298\u65ad\uff0c\u5374\u53c8\u8ba9\u4eba\u5fcd\u4e0d\u4f4f\u671f\u671b\u5979\u518d\u7528\u70b9\u529b\uff0c\u4fee\u76f4\u53cc\u817f\u8ba9\u725b\u4ed4\u88e4\u7ef7\u76f4\uff0c\u5c0f\u81c0\u5fae\u7fd8\uff0c\u5df2\u6709\u5973\u4eba\u7684\u6027\u611f\u3002\u5510\u5a67\u6709\u7740\u821e\u8e48\u7684\u57fa\u7840\uff0c\u53ea\u662f\u5f53\u4e00\u540d\u821e\u8e48\u6f14\u5458\u4e0d\u662f\u5979\u5988\u7684\u5fd7\u613f\uff0c\u5979\u5988\u8ba9\u5979\u5b66\u821e\u8e48\u53ea\u662f\u4e3a\u4e86\u5851\u9020\u5f62\u4f53\u4e0e\u4f18\u96c5\u7684\u4e3e\u6b62\uff0c\u5510\u5a67\u4e00\u76f4\u62b1\u6028\u4e0d\u5df2\uff0c\u4e0d\u8fc7\u7ecf\u5e38\u6446\u51fa\u4e00\u4e9b\u9ad8\u96be\u5ea6\u7684\u52a8\u4f5c\u6765\u5413\u552c\u5f20\u606a\u3002</p><p>\u3000\u3000\u542c\u5510\u5a67\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u5f53\u7136\u4e5f\u4e0d\u80fd\u8bf4\u81ea\u5df1\u4e0d\u8ba4\u8bc6\u8fd9\u5730\u65b9\uff0c\u8bf4\u4e0d\u8ba4\u8bc6\u4e5f\u6ca1\u7528\uff0c\u82cf\u4e00\u5a77\u4e00\u5b9a\u4f1a\u81ea\u544a\u594b\u52c7\u7684\u5f53\u5411\u5bfc\uff0c\u53ea\u80fd\u8bf4\uff1a\u201c\u90a3\u4e0d\u9519\u554a\uff0c\u6211\u4eec\u665a\u4e0a\u5c31\u53bb\u90a3\u91cc\u2026\u2026\u201d\u8f6c\u8eab\u4e0b\u697c\u8ddf\u53f6\u5efa\u658c\u6253\u4e86\u7535\u8bdd\uff0c\u8ba9\u4ed6\u665a\u4e0a\u4e5f\u53bb1978\u78b0\u9762\uff0c\u5b59\u9759\u6aac\u5728\u53f6\u5efa\u658c\u9762\u524d\u8fd8\u6536\u655b\u4e00\u4e9b\uff0c\u4e0d\u7136\u8fd9\u4e48\u591a\u5973\u4eba\u51d1\u5230\u4e00\u8d77\uff0c\u4ed6\u62ff\u5979\u4eec\u5b8c\u5168\u6ca1\u6709\u529e\u6cd5\uff1b\u6b63\u597d\u4e5f\u6709\u4e9b\u4e8b\u60c5\u8ddf\u53f6\u5efa\u658c\u5546\u91cf\u3002</p><p>\u3000\u3000\u5f20\u606a\u653e\u4e0b\u7535\u8bdd\u6ca1\u591a\u4e45\uff0c\u8fd8\u6b63\u8ddf\u5510\u5a67\u5979\u4eec\u8ba8\u8bba\u665a\u996d\u5403\u4ec0\u4e48\u4e4b\u7c7b\u7684\u95ee\u9898\uff0c\u53f6\u5efa\u658c\u5c31\u5f00\u8f66\u8fc7\u6765\u4e86\uff0c\u5e78\u4e8f\u5f20\u606a\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u4e0d\u7136\u4ed6\u4e5f\u4f1a\u5934\u75bc\u665a\u996d\u53bb\u54ea\u91cc\u5403\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u9152\u5427\u4e0d\u4f1a\u8fd9\u4e48\u65e9\u8425\u4e1a\uff0c\u5f20\u606a\u4ed6\u4eec\u9a71\u8f66\u8d76\u5230\u5b66\u5e9c\u5df7\u65f6\uff0c\u9152\u5427\u7684\u95e8\u534a\u63a9\u7740\uff0c\u73bb\u7483\u95e8\u4e0e\u5496\u5561\u8272\u6df7\u7d2b\u8272\u7684\u95e8\u5e18\u4e4b\u95f4\u6302\u7740\u201c\u51c6\u5907\u4e2d\u2026\u2026\u201d\u94ed\u724c\uff0c\u542c\u8bf4\u5b59\u9759\u6aac\u81ea\u4ece\u63a5\u624b\u9152\u5427\u4ee5\u6765\u5c31\u76f8\u5f53\u7684\u8ba4\u771f\u8d1f\u8d23\uff0c\u76f8\u4fe1\u5979\u4eba\u8fd9\u65f6\u5019\u6b63\u5728\u5e97\u91cc\u4e3a\u665a\u4e0a\u7684\u8425\u4e1a\u505a\u51c6\u5907\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\uff0c\u8fd8\u6ca1\u6709\u5230\u8425\u4e1a\u65f6\u2026\u2026\u201d\u5b59\u9759\u6aac\u62ff\u7740\u5757\u62b9\u5e03\u6b63\u4ed4\u7ec6\u7684\u64e6\u6401\u5728\u684c\u4e0a\u7684\u51f3\u817f\uff0c\u8f6c\u8fc7\u8eab\u770b\u89c1\u63a8\u95e8\u8fdb\u6765\u7684\u5374\u662f\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\uff0c\u4e0b\u610f\u8bc6\u7684\u77aa\u5927\u773c\u775b\uff0c\u201c\u9760\uff0c\u600e\u4e48\u662f\u4f60\u4eec\uff1f\u201d\u770b\u5230\u5f20\u606a\u8eab\u540e\u7684\u5510\u5a67\u3001\u9648\u5983\u84c9\uff0c\u5f02\u5e38\u4e8e\u5979\u4eec\u7684\u5a07\u8273\u660e\u4e3d\uff0c\u624b\u53c9\u5230\u8170\u95f4\uff0c\u5634\u91cc\u5374\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u53c8\u4ece\u54ea\u91cc\u9a97\u6765\u65e0\u77e5\u5c11\u5973\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u5230\u5b59\u9759\u6aac\u8fd9\u4e48\u8bf4\uff0c\u5934\u90fd\u5927\u4e86\u4e24\u5708\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7a7f\u7740\u9ed1\u6f06\u76ae\u957f\u7b52\u9774\uff0c\u8170\u95f4\u7cfb\u7740\u9152\u5427\u5de5\u4f5c\u4eba\u5458\u7684\u5236\u670d\u5f0f\u56f4\u88d9\uff0c\u88d9\u6446\u5f88\u77ed\uff0c\u4f46\u662f\u5b59\u9759\u6aac\u672c\u8eab\u7a7f\u7684\u76ae\u88d9\u66f4\u77ed\uff0c\u957f\u7b52\u9774\u4e0e\u56f4\u88d9\u4e4b\u95f4\u9732\u51fa\u4e00\u622a\u767d\u5ae9\u7684\u5927\u817f\uff0c\u6709\u4e00\u79cd\u56f4\u88d9\u540e\u53ef\u80fd\u6ca1\u7a7f\u4ec0\u4e48\u4e1c\u897f\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u4ecb\u7ecd\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u7ed9\u5b59\u9759\u6aac\u8ba4\u8bc6\uff1b\u5510\u5a67\u8fdb\u5e97\u65f6\u624b\u633d\u7740\u5f20\u606a\u7684\u80f3\u818a\uff0c\u5b59\u9759\u6aac\u7591\u60d1\u7684\u76ef\u7740\u5510\u5a67\u770b\uff0c\u5728\u5979\u7684\u5370\u8c61\u91cc\uff0c\u4e00\u76f4\u611f\u89c9\u5f20\u606a\u4e0e\u8bb8\u601d\u5e94\u8be5\u6709\u7740\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5012\u662f\u4e0d\u7ba1\u8fd9\u8fb9\u7684\u70ed\u95f9\uff0c\u4ed6\u5750\u5728\u5427\u53f0\u90a3\u8fb9\uff0c\u5427\u53f0\u4e0a\u6709\u4e00\u672c\u5b66\u5e9c\u5df7\u5404\u5bb6\u5e97\u5740\u7684\u518c\u5b50\uff0c\u4ed6\u8fb9\u770b\u518c\u5b50\u8fb9\u641c\u7d22\u8bb0\u5fc6\u4e2d\u5b66\u5e9c\u5df7\u8f83\u6709\u540d\u7684\u5403\u98df\uff0c\u6253\u7535\u8bdd\u53eb\u5916\u5356\u5230\u5e97\u91cc\u6765\u5403\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\u2026\u2026\u554a\uff0c\u5983\u84c9\uff0c\u4f60\u600e\u4e48\u8ddf\u5f20\u606a\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u5427\u53f0\u540e\u5e2e\u5927\u5bb6\u62ff\u996e\u6599\u4e0e\u9152\uff0c\u8bb8\u7ef4\u624b\u91cc\u6367\u7740\u4e00\u53ea\u770b\u6837\u5b50\u5f88\u6c89\u7684\u7eb8\u7bb1\u5b50\u63a8\u95e8\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u62e7\u5934\u770b\u89c1\u8bb8\u7ef4\u63a8\u95e8\u8fdb\u6765\uff0c\u624b\u4e00\u6296\u64de\uff0c\u4e00\u74f6\u559c\u529b\u4ece\u624b\u638c\u4e0b\u6ed1\u843d\u7838\u5728\u4ed6\u7684\u811a\u80cc\u4e0a\uff0c\u9152\u74f6\u6ca1\u6709\u788e\uff0c\u817f\u80cc\u5374\u751f\u75bc\u5f02\u5e38\uff0c\u5c06\u559c\u529b\u6361\u4e86\u8d77\u6765\uff0c\u8eac\u7740\u80cc\u53bb\u63c9\u7ed9\u7838\u75db\u7684\u811a\u80cc\uff0c\u62ac\u5934\u770b\u7740\u8bb8\u7ef4\u5c06\u7eb8\u7bb1\u91cc\u653e\u5230\u5427\u53f0\u4e0a\uff0c\u5f88\u81ea\u7136\u7684\u8d70\u5230\u5427\u53f0\u540e\uff0c\u53d1\u6123\u7684\u95ee\u5979\uff1a\u201c\u4f60\u7b97\u662f\u57281978\u6253\u5de5\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u5f88\u5947\u602a\u5417\uff1f\u201d\u8bb8\u7ef4\u62db\u624b\u8ba9\u9648\u5983\u84c9\u5230\u5427\u53f0\u540e\u9762\u6765\u5e2e\u5979\u6574\u7406\u7eb8\u7bb1\u91cc\u7684\u9152\uff0c\u201c\u4f60\u600e\u4e48\u8ddf\u5f20\u606a\u8fd9\u5c0f\u5b50\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e3a\u5979\u4eba\u751f\u4e2d\u7684\u7b2c\u4e00\u5f20\u97f3\u4e50\u4e13\u8f91\u4f11\u5b66\u53bb\u4e86\u9999\u6e2f\uff0c\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u5979\u4eec\u5374\u8981\u7559\u4e0b\u6765\u7ee7\u7eed\u5b8c\u6210\u5979\u4eec\u672a\u5b8c\u7684\u5b66\u4e1a\u3002\u5b59\u9759\u6aac\u66ff\u5979\u59d0\u59d0\u6253\u74061978\uff0c\u8bb8\u7ef4\u4e4b\u524d\u6bcf\u6b21\u90fd\u4f1a\u966a\u6c5f\u9edb\u513f\u52301978\u6765\u9a7b\u5531\uff0c\u73b0\u5728\u6bcf\u5929\u4e5f\u987a\u7406\u6210\u7ae0\u7684\u90fd\u5230\u5e97\u91cc\u6765\u5e2e\u52a9\uff0c\u7b97\u662f\u4e00\u4efd\u534a\u6b63\u5f0f\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u5f20\u606a\u6574\u5929\u6691\u5047\u90fd\u5728\u65b0\u829c\u9b3c\u6df7\uff0c\u6691\u5047\u8fc7\u540e\u7684\u8fd9\u4e00\u4e2a\u6708\uff0c\u4eba\u8fd8\u662f\u7b2c\u4e00\u6b21\u79bb\u5f00\u6d77\u5dde\uff0c\u6c5f\u9edb\u513f\u7559\u5728\u9999\u6e2f\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u6691\u5047\u4e0e\u5510\u5a67\u5979\u4eec\u53bb\u9999\u6e2f\u65f6\u542c\u8bf4\u7684\uff0c\u6c5f\u9edb\u513f\u7ed9\u7231\u8fbe\u7535\u5b50\u505a\u5ba3\u4f20\u7684\u4e8b\u60c5\uff0c\u4e5f\u5b8c\u5168\u7531\u5e02\u573a\u90e8\u4e0e\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\u8d1f\u8d23\uff0c\u5f20\u606a\u5df2\u7ecf\u4e0d\u4f1a\u7ba1\u592a\u7410\u788e\u7684\u4e8b\u52a1\uff1b\u4e0e\u8bb8\u601d\u901a\u7535\u8bdd\u65f6\uff0c\u4e5f\u6ca1\u6709\u7279\u5730\u7684\u804a\u8d77\u8fc7\u8bb8\u7ef4\u7684\u8fd1\u51b5\uff0c\u8fd8\u771f\u4e0d\u6e05\u695a\u5979\u8fd9\u5b66\u671f\u5f00\u59cb\u7adf\u4f1a\u57281978\u6253\u5de5\uff0c\u4e0d\u7136\u600e\u4e48\u7740\u4eca\u665a\u4e5f\u4e0d\u4f1a\u52301978\u6765\uff0c\u770b\u9648\u5983\u84c9\u90a3\u67b6\u5f0f\uff0c\u4f3c\u4e4e\u8981\u4ecb\u7ecd\u8bb8\u7ef4\u7ed9\u5510\u5a67\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u867d\u7136\u5510\u5a67\u5bf9\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6e05\u695a\uff0c\u4f46\u662f\u76f8\u5bf9\u76f4\u63a5\u9762\u5bf9\u7684\u5fc3\u60c5\u7edd\u4e0d\u662f\u5f88\u597d\u8fc7\u7684\uff0c\u5f20\u606a\u5fc3\u60f3\uff0c\u8981\u6709\u53ef\u80fd\uff0c\u4ed6\u5b81\u53ef\u8ba9\u5510\u5a67\u4e0e\u8bb8\u601d\u4e24\u4eba\u95f4\u6c38\u8fdc\u90fd\u4e0d\u8981\u8ddf\u5bf9\u65b9\u7684\u5708\u5b50\u6709\u63a5\u89e6\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u5728\u5f88\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u7ef4\u6301\u7740\u4e00\u4e2a\u8c0e\u8a00\uff0c\u4e00\u65e6\u8c0e\u8a00\u6709\u7ed9\u6233\u7834\u7684\u53ef\u80fd\uff0c\u614c\u5f20\u7684\u53ef\u4e0d\u6b62\u5236\u9020\u8c0e\u8a00\u7684\u4eba\u3002</p><p>\u3000\u3000\u201c\u8bb8\u7ef4\u59d0\uff0c\u8fd9\u662f\u5510\u5a67\uff0c\u6211\u4e00\u76f4\u6709\u8ddf\u4f60\u8bf4\u8d77\u8fc7\uff0c\u5979\u8ddf\u5f20\u606a\u4e00\u8d77\u5230\u7701\u57ce\u6765\u73a9\u3002\u201d\u9648\u5983\u84c9\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e00\u6848\u7684\u79cd\u79cd\u5185\u5e55\u4e5f\u53ea\u9650\u4e8e\u5c11\u6570\u4eba\u77e5\u60c5\uff0c\u8bb8\u7ef4\u80fd\u731c\u5230\u8bb8\u601d\u6709\u4e00\u6bb5\u59d4\u5c48\u7684\u3001\u4e0d\u5149\u5f69\u7684\u5386\u53f2\uff0c\u5374\u4e0d\u77e5\u9053\u5177\u4f53\u7684\u7ec6\u8282\uff0c\u66f4\u4e0d\u77e5\u9053\u4e0e\u5510\u5b66\u8c26\u4e00\u5bb6\u4eba\u7684\u6069\u6028\u3002\u5510\u5a67\uff0c\u8bb8\u7ef4\u662f\u77e5\u9053\u7684\uff0c\u50cf\u5e74\u524d\u5979\u4e0e\u8bb8\u601d\u3001\u9648\u5983\u84c9\u4e09\u4eba\u4f4f\u8fdb\u5f20\u606a\u7ed9\u8bb8\u601d\u5728\u4e39\u4e95\u5e9c\u51c6\u5907\u3001\u5e76\u91cd\u65b0\u88c5\u4fee\u8fc7\u7684\u90a3\u680b\u65e7\u5b85\u65f6\uff0c\u8bdd\u9898\u626f\u5230\u5f20\u606a\u65f6\uff0c\u81ea\u7136\u800c\u7136\u7684\u4e5f\u626f\u5230\u5510\u5a67\u8eab\u4e0a\uff0c\u8bb8\u7ef4\u77e5\u9053\u5979\u662f\u6d77\u5dde\u7684\u516c\u4e3b\uff0c\u4e5f\u77e5\u9053\u5979\u4e0e\u5f20\u606a\u9752\u6885\u7af9\u9a6c\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u201c\u4f60\u597d\uff0c\u8001\u65e9\u5c31\u542c\u5983\u84c9\u63d0\u8d77\u8fc7\u4f60\uff0c\u5f20\u606a\u8ddf\u85cf\u5b9d\u4f3c\u7684\u4e0d\u5e26\u4f60\u51fa\u6765\u73a9\u2026\u2026\u201d\u8bb8\u7ef4\u4eb2\u5207\u7684\u7b11\u7740\u8ddf\u5510\u5a67\u6253\u62db\u547c\uff0c\u53c8\u8ddf\u82cf\u4e00\u5a77\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u63a8\u95e8\u8fdb\u6765\u7684\u77ac\u95f4\uff0c\u5510\u5a67\u7684\u5fc3\u810f\u5728\u90a3\u4e00\u523b\u51e0\u4e4e\u90fd\u8981\u505c\u6b62\uff0c\u8fd8\u4ee5\u4e3a\u662f\u90a3\u4e2a\u5973\u4eba\uff0c\u5fc3\u60f3\u5979\u8fd9\u65f6\u5019\u4e0d\u662f\u5728\u9999\u6e2f\u5417\uff1f\u5206\u6e05\u662f\u8bb8\u7ef4\uff0c\u53c8\u89c1\u8bb8\u7ef4\u4f3c\u4e4e\u5bf9\u4ec0\u4e48\u60c5\u51b5\u90fd\u4e0d\u6e05\u695a\uff0c\u60ac\u7740\u7684\u5fc3\u7ec8\u4e8e\u843d\u56de\u539f\u5904\uff0c\u5fc3\u91cc\u591a\u5c11\u5bf9\u5f20\u606a\u6709\u4e9b\u6028\u6124\uff0c\u89c1\u5f20\u606a\u8fd8\u5f2f\u7740\u8eab\u5b50\u6361\u5730\u4e0a\u7684\u9152\u74f6\uff0c\u72e0\u72e0\u7684\u5728\u4ed6\u8170\u4e0a\u62e7\u634f\u4e86\u4e00\u628a\u89e3\u6068\u3002</p><p>\u3000\u3000\u5472\u7259\u54a7\u5634\uff0c\u5403\u75db\u4e5f\u5f97\u4fdd\u6301\u7b11\u5bb9\uff0c\u5f20\u606a\u5c06\u559c\u529b\u6446\u5427\u53f0\u63a8\u7ed9\u53f6\u5efa\u658c\uff0c\u5f53\u613f\u4eca\u665a\u80fd\u987a\u5229\u7684\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u563f\u7136\u800c\u7b11\uff0c\u5750\u5728\u5427\u53f0\u524d\u4e5f\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5012\u662f\u5728\u5176\u4ed6\u670d\u52a1\u5458\u9646\u7eed\u4e0a\u5de5\u4e4b\u524d\uff0c\u8bb8\u7ef4\u8981\u53bb\u5e2e\u5b59\u9759\u6aac\u6574\u7406\u684c\u6905\u4e0e\u9152\u5427\u8bbe\u65bd\uff0c\u9648\u5983\u84c9\u52e4\u5feb\u7684\u8fc7\u53bb\u5e2e\u5979\u4eec\u5fd9\uff0c\u82cf\u4e00\u5a77\u8981\u53bb\u8ddf\u5b59\u9759\u6aac\u51d1\u8fd1\u4e4e\uff0c\u4e5f\u8ddf\u7740\u8fc7\u53bb\u5e2e\u5fd9\uff0c\u5fc3\u91cc\u5374\u5728\u5450\u558a\uff1a\u9664\u4e86\u81ea\u5df1\uff0c\u600e\u4e48\u90fd\u662f\u7f8e\u5973\uff1f</p><p>\u3000\u3000\u8bb8\u7ef4\u867d\u7136\u4e0d\u53ca\u9648\u5983\u84c9\u3001\u5510\u5a67\u90a3\u4e9b\u660e\u8273\uff0c\u4e3b\u8981\u662f\u5979\u7684\u575a\u6bc5\u6027\u683c\u3001\u524a\u7626\u7684\u8138\u5f62\u8ba9\u5979\u770b\u8d77\u6765\u66f4\u4e2d\u6027\u5316\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u4e2d\u6027\u5316\u7684\u5bb9\u8c8c\u66f4\u5bb9\u6613\u5f97\u5230\u540c\u6027\u7684\u8ba4\u540c\uff0c\u5728\u82cf\u4e00\u5a77\u770b\u6765\u8bb8\u7ef4\u4e5f\u662f\u5927\u7f8e\u5973\u4e00\u4e2a\uff0c\u7537\u6027\u770b\u5979\u6216\u8bb8\u4f1a\u89c9\u5f97\u8bb8\u7ef4\u867d\u7136\u660e\u7738\u7693\u7259\uff0c\u4f46\u662f\u5973\u4eba\u5473\u6709\u4e9b\u6b20\u7f3a\u3002</p><p>\u3000\u3000\u5510\u5a67\u867d\u7136\u4e0d\u613f\u610f\u8fc7\u53bb\uff0c\u53ea\u662f\u60f3\u7740\u81ea\u5df1\u8d56\u5728\u5427\u53f0\u8fd9\u8fb9\uff0c\u4f1a\u663e\u5f97\u7279\u6b8a\uff0c\u53ea\u5f97\u4e5f\u8d70\u51fa\u5427\u53f0\uff0c\u5e2e\u7740\u6574\u7406\u7ed9\u78e8\u7802\u73bb\u7483\u5206\u9694\u51fa\u6765\u7684\u534a\u5c01\u95ed\u5f0f\u5305\u53a2\u53bb\u3002</p><p>\u3000\u3000\u201c\u5973\u4eba\u591a\u4e86\u8f9b\u82e6\u5427\uff1f\u201d\u53f6\u5efa\u658c\u4e3e\u7740\u559c\u529b\u5564\u9152\uff0c\u7a81\u7136\u611f\u6168\u4e86\u4e00\u53e5\uff0c\u201c\u5510\u5a67\u597d\u50cf\u77e5\u9053\u4e9b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5f20\u606a\u82e6\u7740\u8138\u5e94\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff0c\u201c\u5c0f\u59ae\u5b50\u8111\u888b\u74dc\u4e0d\u6bd4\u6211\u7b28\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u53f6\u5efa\u658c\u53f9\u606f\u7684\u6447\u4e86\u6447\u5934\uff0c\u201c\u8f9b\u82e6\u7684\u65e5\u5b50\u6162\u6162\u4f53\u4f1a\u5427\uff0c\u6211\u770b\u4f60\u4ee5\u540e\u7684\u4eba\u751f\u8981\u6bd4\u6211\u6df7\u4e71\u554a\uff0c\u6362\u4f5c\u5176\u4ed6\u7537\u4eba\u5012\u662f\u503c\u5f97\u70ab\u8000\u7684\u4e00\u4ef6\u4e8b\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u62ff\u8a00\u8bed\u662f\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u7684\uff0c\u60f3\u7740\u7b49\u56de\u53bb\u518d\u7ed9\u5510\u5a67\u597d\u597d\u6390\u51e0\u4e0b\uff0c\u5012\u662f\u67f3\u5fd7\u519b\u5f97\u9676\u664b\u6388\u610f\u8ddf\u81ea\u5df1\u63a5\u89e6\u7684\u4e8b\u60c5\u2014\u2014\u5728\u7701\u5e02\u9886\u5bfc\u7684\u8f66\u961f\u79bb\u573a\u4e4b\u524d\u8868\u793a\u6b64\u610f\u2014\u2014\u8981\u8ddf\u53f6\u5efa\u658c\u8bf4\u4e00\u4e0b\u3002\u81f3\u4e8e\u5bf9\u5f90\u5b66\u5e73\u65e5\u540e\u4ed5\u9014\u53ef\u80fd\u9047\u632b\u7684\u731c\u6d4b\u4e0d\u9700\u8981\u660e\u8bf4\uff0c\u53f6\u5bb6\u4f1a\u6709\u81ea\u5df1\u7684\u5224\u65ad\u3002</p><p>\u3000\u3000\u8981\u662f\u80fd\u7b80\u5355\u7684\u6539\u6362\u95e8\u5ead\uff0c\u653f\u6cbb\u4e0a\u4e5f\u5c31\u6ca1\u6709\u670b\u515a\u4e00\u8bf4\uff0c\u53f6\u5efa\u658c\u6487\u4e86\u6487\u5634\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u603b\u4e0d\u80fd\u8bf4\u662f\u4ec0\u4e48\u574f\u6d88\u606f\uff0c\u7701\u59d4\u4e66\u8bb0\u5c31\u6ca1\u6709\u5bf9\u6211\u8868\u9732\u51fa\u6709\u9700\u8981\u89c1\u9762\u4e00\u8c08\u7684\u610f\u601d\u3002\u201d</p><p>\u3000\u3000\u201c\u76db\u946b\u8fd9\u4e24\u5e74\uff0c\u90fd\u662f\u5728\u5916\u7701\u5e02\u53d1\u5c55\u5bb6\u7535\u8fde\u9501\uff0c\u7701\u91cc\u53c8\u4e0d\u80fd\u516c\u5f00\u7684\u8981\u6c42\u4f60\u4eec\u53f6\u5bb6\u5c06\u4ea7\u4e1a\u90fd\u642c\u56de\u4e1c\u6d77\u6765\uff0c\u89c1\u4f60\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u8fd8\u4e0d\u5982\u4e0d\u89c1\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u653f\u6cbb\u4e0a\u7684\u4e8b\u60c5\uff0c\u8981\u60f3\u5f00\u624d\u884c\uff0c\u5f90\u5b66\u5e73\u6682\u65f6\u7684\u5148\u540e\u9000\u4e00\u6b65\uff0c\u672a\u5c1d\u4e0d\u662f\u597d\u4e8b\u3002</p><p>\u3000\u3000\u773c\u4e0b\u53d1\u5c55\u5bb6\u7535\u8fde\u9501\u5356\u573a\u662f\u53f6\u5bb6\u6700\u4e3b\u8981\u7684\u4e8b\u4e1a\uff0c\u5f53\u521d\u53f6\u5efa\u658c\u63a5\u53d7\u5f20\u606a\u7684\u5efa\u8bae\uff0c\u653e\u5f03\u4e1c\u6d77\u5e02\u573a\uff0c\u907f\u514d\u8fc7\u65e9\u7684\u4e0e\u8c22\u5bb6\u7ade\u4e89\uff0c\u96c6\u4e2d\u7cbe\u529b\u5f00\u62d3\u6d59\u6c5f\u3001\u4e0a\u6d77\u7b49\u7701\u5e02\u7684\u5e02\u573a\uff0c\u53f6\u5efa\u658c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u773c\u4e0b\u4e5f\u4e0d\u662f\u56de\u4e1c\u6d77\u76f4\u63a5\u8ddf\u6d77\u6cf0\u7ade\u4e89\u7684\u65f6\u673a\u2026\u2026\u6b63\u6cf0\u4e0e\u9526\u6e56\u5408\u8d44\u6210\u7acb\u7684\u79d1\u738b\u7535\u5668\uff0c\u6700\u8fd1\u7684\u52a8\u4f5c\u5f88\u5927\u554a\uff0c\u597d\u50cf\u60f3\u5728\u5f71\u789f\u673a\u5e02\u573a\u6709\u4e00\u756a\u4f5c\u4e3a\u3002\u79d1\u738b\u8981\u8ba9\u4ed6\u4eec\u505a\u5927\u4e86\uff0c\u76db\u946b\u5c31\u66f4\u96be\u56de\u4e1c\u6d77\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u9732\u51fa\u8f7b\u8511\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u79d1\u738b\u4e0d\u4ec5\u4e0d\u80fd\u6210\u4e3a\u4ed6\u4eec\u7684\u63d0\u6b3e\u673a\uff0c\u8fd8\u4f1a\u5206\u6563\u8c22\u5bb6\u7684\u6ce8\u610f\u529b\u4e0e\u8d22\u529b\uff0c\u5bf9\u76db\u946b\u6765\u8bf4\uff0c\u4f1a\u4e0d\u4f1a\u662f\u4e2a\u597d\u6d88\u606f\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u81ea\u7136\u662f\u597d\u6d88\u606f\uff0c\u201d\u53f6\u5efa\u658c\u6765\u4e86\u5174\u8da3\uff0c\u4e1c\u6d77\u662f\u76f8\u5f53\u91cd\u8981\u7684\u4e00\u4e2a\u5e02\u573a\uff0c\u8981\u662f\u6b63\u6cf0\u7ed9\u79d1\u738b\u7535\u5668\u5206\u6563\u7cbe\u529b\u4e0e\u8d22\u529b\uff0c\u6d77\u6cf0\u79bb\u5f00\u6b63\u6cf0\u7684\u8d44\u6e90\uff0c\u662f\u65e0\u6cd5\u7ed9\u76db\u946b\u8fdb\u5165\u4e1c\u6d77\u5e02\u573a\u5236\u9020\u592a\u5927\u969c\u788d\u7684\uff0c\u201c\u4f60\u51c6\u5907\u5bf9\u79d1\u738b\u4e0b\u624b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u8bb8\u662f\u4ed6\u4eec\u81ea\u53d6\uff0c\u6211\u54ea\u6709\u90a3\u95f2\u5de5\u592b\u7406\u4f1a\u4ed6\u4eec\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u53ea\u662f\u89c9\u5f97\u6709\u8fd9\u53ef\u80fd\uff0c\u4f60\u4eec\u63d0\u524d\u505a\u4e9b\u51c6\u5907\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u6211\u73b0\u5728\u5012\u662f\u4e13\u5fc3\u5728\u60f3\u5230\u5e95\u4ec0\u4e48\u6837\u7684\u8bdd\u624d\u80fd\u6295\u6211\u4eec\u7701\u59d4\u4e66\u8bb0\u7684\u597d\u5462\uff0c\u867d\u7136\u4e5f\u53ea\u662f\u901a\u8fc7\u67f3\u5fd7\u519b\u7684\u95f4\u63a5\u4f1a\u9762\uff0c\u4e0d\u8fc7\u6211\u4e5f\u671f\u671b\u4e0e\u4ed6\u9762\u5bf9\u9762\u8c08\u4e00\u6b21\u5462\u3002\u201d\u60df\u6709\u4e0e\u67f3\u5fd7\u519b\u89c1\u9762\u6109\u5feb\uff0c\u624d\u53ef\u80fd\u4e0e\u9676\u664b\u76f4\u63a5\u89c1\u9762\uff0c\u5f20\u606a\u5728\u60f3\u67f3\u5fd7\u519b\u5230\u5e95\u4f1a\u5728\u660e\u5929\u8fd8\u662f\u540e\u5929\u6253\u7535\u8bdd\u8fc7\u6765\uff1f</p><p>\u3000\u3000\u201c\u4e24\u8005\u4e4b\u95f4\u6ca1\u6709\u5173\u7cfb\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u8f66\u4e0a\u6709\u672c\u4e66\uff0c\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u6240\u8457\uff0c\u300a\u6d41\u884c\u7684\u56fd\u9645\u4e3b\u4e49\u300b\uff0c\u5176\u4e2d\u6536\u5f55\u4ed6\u5728\u4e5d\u56db\u5e74\u5c31\u53d1\u8868\u7684\u4e00\u7bc7\u6587\u7ae0\uff0c\u300a\u4e9a\u6d32\u5947\u8ff9\u7684\u795e\u8bdd\u300b\uff0c\u90a3\u662f\u4e00\u7bc7\u975e\u5e38\u4e0d\u5408\u65f6\u5b9c\u7684\u6587\u7ae0\uff0c\u6211\u5012\u89c9\u5f97\u5f88\u6709\u9053\u7406\uff0c\u63a8\u8350\u4f60\u4e5f\u770b\u4e00\u770b\u3002\u201d\u96f6\u516b\u5e74\u83b7\u5f97\u8bfa\u8d1d\u5c14\u7ecf\u6d4e\u5b66\u5956\u7684\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u5728\u4ed6\u4e5d\u56db\u5e74\u53d1\u8868\u7684\u8fd9\u7bc7\u6587\u7ae0\u51c6\u786e\u6307\u51fa\u4e9a\u6d32\u7ecf\u6d4e\u5728\u516b\u5341\u5e74\u4ee3\u3001\u4e5d\u5341\u5e74\u9ad8\u901f\u53d1\u5c55\u4e2d\u5b58\u5728\u7684\u6df1\u523b\u95ee\u9898\uff0c\u4e5f\u6b63\u786e\u9884\u6d4b\u5230\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6765\u88ad\u3002\u5f20\u606a\u60f3\u8b66\u54cd\u76f8\u5173\u65b9\u9762\u6ce8\u610f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u5de8\u5927\u7834\u574f\u529b\uff0c\u4e5f\u60df\u6709\u5229\u7528\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u7684\u7ecf\u6d4e\u5b66\u7406\u8bba\uff0c\u53ea\u662f\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u7684\u7ecf\u6d4e\u5b66\u7406\u8bba\u5728\u4e5d\u516d\u5e74\u8fd8\u662f\u975e\u4e3b\u6d41\u7684\uff0c\u53ea\u6015\u8ba4\u540c\u7684\u4eba\u4e0d\u4f1a\u592a\u591a\u3002</p></article>"}}}]);
