(window.webpackJsonp=window.webpackJsonp||[]).push([[415],{438:function(p){p.exports={chapterTitle:null,title:"\u7b2c415\u7ae0 \u518d\u8bbf\u53f6\u5b85",content:"<article><h2>\u7b2c415\u7ae0 \u518d\u8bbf\u53f6\u5b85</h2><p>\u3000\u3000\u7b2c\u4e8c\u5929\u4e0a\u5348\uff0c\u5f20\u606a\u5f00\u8f66\u8f7d\u7740\u5510\u5a67\u53bb\u63a5\u67f3\u5fd7\u519b\u592b\u5987\uff0c\u65b9\u4fbf\u591a\u4ea4\u6d41\uff0c\u67f3\u5fd7\u519b\u4e5f\u5c31\u6ca1\u6709\u5f00\u8f66\uff0c\u56db\u4e2a\u4eba\u5408\u4e58\u4e00\u90e8\u8f66\u524d\u5f80\u53f6\u5bb6\u79c1\u5b85\u3002</p><p>\u3000\u3000\u53f6\u5bb6\u5728\u897f\u90ca\u7684\u8001\u5b85\u79bb\u5e02\u533a\u6709\u534a\u5c0f\u65f6\u7684\u8f66\u7a0b\uff0c\u90a3\u91cc\u662f\u5efa\u90ba\u5e02\u5916\u56f4\u7684\u4e18\u5cad\u5730\u5e26\uff0c\u53f6\u5bb6\u7684\u79c1\u5b85\u5c31\u5efa\u5728\u4e00\u5ea7\u6d77\u62d4\u4e0d\u9ad8\u7684\u9752\u7fe0\u4e18\u5cad\u811a\u4e0b\u3002\u53bb\u5e74\u590f\u5929\u5f20\u606a\u7b2c\u4e00\u6b21\u9020\u8bbf\u53f6\u5b85\uff0c\u8fd8\u662f\u4e0e\u6c5f\u9edb\u513f\u4e00\u9053\uff0c\u540e\u6765\u9022\u5e74\u8fc7\u8282\u90fd\u8981\u770b\u671b\u53f6\u7956\u8303\u8001\u4eba\uff0c\u4f46\u662f\u4e0e\u53f6\u81fb\u6c11\u89c1\u9762\u5374\u662f\u53bb\u5e74\u590f\u5929\u90a3\u4e00\u6b21\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5bb6\u79c1\u4ea4\u751a\u5bc6\uff0c\u5916\u754c\u5374\u662f\u5f88\u5c11\u6709\u4eba\u77e5\u9053\uff0c\u67f3\u5fd7\u519b\u5c06\u6628\u5929\u4f1a\u9762\u60c5\u51b5\u5411\u9676\u664b\u6c47\u62a5\u4e4b\u540e\uff0c\u4ed4\u7ec6\u60f3\u4e00\u60f3\uff0c\u6b63\u503c\u5f20\u606a\u5d2d\u9732\u5934\u89d2\u4e4b\u65f6\uff0c\u6d77\u88d5\u3001\u7231\u8fbe\u7535\u5b50\u5148\u540e\u4e0e\u76db\u946b\u6709\u4e1a\u52a1\u5f80\u6765\uff0c\u65c1\u4eba\u4ec5\u4ec5\u770b\u5230\u90a3\u4e9b\u6587\u4ef6\u4e0e\u6570\u636e\u5c31\u77a0\u76ee\u7ed3\u820c\uff0c\u60f3\u5fc5\u53f6\u5bb6\u5bf9\u5f20\u606a\u90a3\u4ee4\u4eba\u60ca\u8273\u7684\u3001\u4f3c\u4e4e\u662f\u4e0e\u751f\u4ff1\u6765\u7684\u5546\u4e1a\u5929\u8d4b\u5e94\u8be5\u6709\u66f4\u6df1\u523b\u7684\u8ba4\u8bc6\uff0c\u5bc6\u5207\u4ea4\u5f80\u4e5f\u4e0d\u662f\u4ec0\u4e48\u610f\u5916\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5341\u70b9\u5de6\u53f3\u8d76\u5230\u53f6\u5bb6\uff0c\u53f6\u5efa\u658c\u53bb\u673a\u573a\u63a5\u53f6\u81fb\u6c11\uff0c\u5f20\u606a\u4e0e\u67f3\u5fd7\u519b\u4fbf\u966a\u53f6\u5bb6\u8001\u7237\u5b50\u5728\u4e2d\u5ead\u7684\u906e\u9633\u68da\u4e0b\u559d\u8336\u804a\u5929\uff0c\u53f6\u81fb\u6c11\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\u5728\u4e2d\u996d\u4e4b\u524d\u8d76\u5230\u3002</p><p>\u3000\u3000\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u5bf9\u4e9a\u6d32\u7279\u522b\u4e1c\u4e9a\u5730\u533a\u5728\u672a\u6765\u51e0\u5e74\u7ecf\u6d4e\u5f62\u52bf\u6709\u7740\u60b2\u89c2\u7684\u5224\u65ad\uff0c\u5f20\u606a\u5728\u5176\u57fa\u7840\u4e0a\u505a\u4e86\u66f4\u4e3a\u8be6\u7ec6\u7684\u7814\u7a76\u4e0e\u5206\u6790\uff0c\u53f6\u5efa\u658c\u90fd\u5728\u7535\u8bdd\u4e0e\u4ed6\u4e09\u53d4\u53f6\u81fb\u6c11\u63d0\u53ca\uff0c\u4f46\u662f\u4e00\u4e9b\u5173\u952e\u95ee\u9898\uff0c\u53f6\u81fb\u6c11\u8fd8\u8981\u5f53\u9762\u4e0e\u5f20\u606a\u8c08\u8fc7\u624d\u80fd\u786e\u8ba4\uff0c\u6628\u5929\u4e00\u5929\uff0c\u4ed6\u8ba9\u4e0b\u9762\u4eba\u91cd\u65b0\u6574\u7406\u4e9a\u6d32\u5404\u56fd\u7684\u7ecf\u6d4e\u6570\u636e\uff0c\u4f5c\u4e3a\u5916\u7ecf\u8d38\u90e8\u5206\u7ba1\u4e9a\u6d32\u53f8\u7684\u526f\u90e8\u957f\uff0c\u4ed6\u80fd\u6536\u96c6\u5230\u7684\u6570\u636e\u81ea\u7136\u6bd4\u5f20\u606a\u66f4\u5168\u9762\uff0c\u4f46\u662f\u8981\u4ece\u8fd9\u4e9b\u770b\u8d77\u6765\u8fd8\u76f8\u5f53\u4e50\u89c2\u7684\u6570\u636e\u4e2d\u770b\u51fa\u4e1c\u4e9a\u5404\u56fd\u7ecf\u6d4e\u6a21\u5f0f\u4e2d\u5b58\u5728\u7684\u6f0f\u6d1e\u5374\u975e\u5e38\u4eba\u4e4b\u6240\u80fd\u3002</p><p>\u3000\u3000\u996d\u684c\u4e0a\u804a\u8d77\u4e9a\u6d32\u5404\u56fd\u7684\u91d1\u878d\u4e0e\u7ecf\u6d4e\u73b0\u72b6\uff0c\u67f3\u5fd7\u519b\u8fd8\u4ee5\u4e3a\u662f\u5f20\u606a\u4e3a\u4e86\u8fce\u5408\u53f6\u81fb\u6c11\u7684\u8eab\u4efd\u800c\u7279\u610f\u626f\u8d77\u7684\u95f2\u804a\u8bdd\u9898\uff0c\u7b49\u5bb4\u5e2d\u5403\u5230\u4e2d\u9014\uff0c\u53f6\u81fb\u6c11\u6253\u5f00\u968f\u8eab\u7684\u516c\u6587\u5305\uff0c\u5c06\u5173\u4e8e\u4e9a\u6d32\u7ecf\u6d4e\u52a8\u6001\u7684\u5185\u53c2\u8d44\u6599\u4e00\u4efd\u4efd\u7684\u62ff\u51fa\u6765\uff0c\u67f3\u5fd7\u519b\u624d\u6653\u5f97\u53f6\u81fb\u6c11\u56de\u6765\u63a2\u4eb2\u7684\u610f\u56fe\u539f\u6765\u5728\u6b64\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u5bf9\u7ecf\u6d4e\u867d\u7136\u8c08\u4e0d\u4e0a\u7cbe\u901a\uff0c\u4f46\u662f\u4e5f\u6709\u6d89\u730e\u3002\u5728\u56fd\u5185\uff0c\u4e3a\u5b98\u4e0d\u77e5\u7ecf\u6d4e\uff0c\u524d\u9014\u4e0d\u4f1a\u8fdc\u5927\uff0c\u8fd9\u662f\u56fd\u5185\u4ee5\u7ecf\u6d4e\u5efa\u8bbe\u4e3a\u4e2d\u5fc3\u7684\u73b0\u72b6\u6240\u51b3\u5b9a\uff0c\u5b8f\u89c2\u7ecf\u6d4e\u52bf\u6001\u51e0\u4e4e\u662f\u4e3a\u5b98\u8005\u6240\u5fc5\u4fee\u3002</p><p>\u3000\u3000\u65e5\u672c\u7ecf\u6d4e\u4ece\u516b\u4e5d\u5e74\u5371\u673a\u4ee5\u6765\uff0c\u5c31\u4e00\u76f4\u9677\u5165\u8fdf\u6ede\u671f\uff0c\u8fd8\u6ca1\u6709\u5b8c\u5168\u6062\u590d\u8fc7\u6765\uff0c\u4f46\u662f\u4ee5\u201c\u56db\u5c0f\u9f99\u201d\u3001\u201c\u56db\u5c0f\u864e\u201d\u7b49\u516b\u4e2a\u56fd\u5bb6\u6216\u5730\u533a\u4ee3\u8868\u7684\u4e1c\u4e9a\u7ecf\u6d4e\uff0c\u81f3\u5c11\u5728\u4e5d\u516d\u5e74\u770b\u8d77\u6765\uff0c\u8fd8\u6b63\u5904\u4e8e\u6b23\u6b23\u5411\u8363\u7684\u52bf\u6001\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u5012\u662f\u542c\u5f20\u606a\u4e0e\u53f6\u81fb\u6c11\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\u8d8a\u804a\u8d8a\u5fc3\u60ca\uff0c\u5f20\u606a\u7684\u5546\u4e1a\u5929\u8d4b\u5012\u4e5f\u7f62\u4e86\uff0c\u6bd5\u7adf\u67f3\u5fd7\u519b\u4e5f\u672a\u66fe\u4eb2\u773c\u89c1\u8fc7\uff0c\u7279\u522b\u662f\u7ecf\u8425\u624d\u80fd\u4e0e\u5b8f\u89c2\u7ecf\u6d4e\u8ba4\u8bc6\u5e76\u4e0d\u662f\u5b8c\u5168\u91cd\u5408\u7684\u9886\u57df\uff0c\u4f46\u662f\u53f6\u81fb\u6c11\u53ef\u4ee5\u8bf4\u662f\u56fd\u5185\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u6700\u6709\u7814\u7a76\u7684\u6743\u5a01\u4eba\u58eb\u4e4b\u4e00\uff0c\u4e0d\u7136\u4e5f\u4e0d\u53ef\u80fd\u5728\u5916\u7ecf\u8d38\u90e8\u5206\u7ba1\u4e9a\u6d32\u53f8\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e74\u7eaa\u672a\u6ee1\u4e8c\u5341\u5e74\uff0c\u4e0e\u53f6\u81fb\u6c11\u5bf9\u5ea7\u7eb5\u8bba\u4e9a\u6d32\u7ecf\u6d4e\u52bf\u6001\uff0c\u5728\u67f3\u5fd7\u519b\u770b\u6765\u5df2\u7ecf\u662f\u591f\u602a\u5f02\u7684\uff0c\u6216\u8bb8\u53ef\u4ee5\u4e3a\u5f20\u606a\u90a3\u5929\u7eb5\u5176\u624d\u7684\u5546\u4e1a\u5929\u8d4b\u505a\u6ce8\u811a\u5427\u3002\u67f3\u5fd7\u519b\u63d2\u4e0d\u4e0a\u8bdd\uff0c\u53ea\u80fd\u656c\u966a\u672b\u5ea7\uff0c\u867d\u7136\u5bf9\u90a3\u4e9b\u8be6\u7ec6\u7684\u7ecf\u6d4e\u6570\u636e\u542c\u5f97\u5934\u6655\u773c\u80c0\uff0c\u4f46\u662f\u5bf9\u7ed3\u8bba\u6027\u7684\u8bdd\u5374\u542c\u5f97\u5206\u660e\uff0c\u53f6\u81fb\u6c11\u3001\u5f20\u606a\u7b49\u4eba\u5206\u660e\u5bf9\u4e1c\u4e9a\u5404\u56fd\u7684\u7ecf\u6d4e\u62b1\u6709\u6781\u60b2\u89c2\u7684\u8ba4\u8bc6\uff0c\u5728\u4ed6\u4eec\u5206\u6790\u770b\u6765\uff0c\u4e1c\u4e9a\u5404\u56fd\u7ecf\u6d4e\u3001\u91d1\u878d\u6a21\u5f0f\u5b58\u5728\u7684\u7f3a\u9677\u7b80\u76f4\u80fd\u62ff\u5343\u75ae\u767e\u5b54\u6765\u5f62\u5bb9\uff0c\u6240\u5e78\u4e1c\u4e9a\u5404\u56fd\u5b58\u5728\u7684\u5f0a\u7aef\uff0c\u5bf9\u56fd\u5185\u7ecf\u6d4e\u5374\u662f\u6709\u5229\u7684\uff1b\u5f20\u606a\u8fd8\u7279\u5730\u62ff\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u7684\u90a3\u4e2a\u9879\u76ee\u6765\u5206\u6790\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u5510\u5a67\u660e\u5929\u8981\u4e0a\u5b66\uff0c\u5f20\u606a\u770b\u624b\u8868\u5df2\u662f\u4e0b\u5348\u56db\u70b9\u8bb8\uff0c\u4fbf\u4e2d\u6b62\u8bdd\u9898\uff0c\u51c6\u5907\u544a\u8f9e\u79bb\u53bb\u3002</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u6211\u4eec\u6240\u804a\u7684\u5185\u5bb9\u6574\u7406\u4e0b\u6765\u7565\u52a0\u8865\u8ff0\u5c31\u662f\u4e00\u7bc7\u714c\u714c\u5de8\u4f5c\uff0c\u4f60\u9700\u8981\u4ec0\u4e48\u8d44\u6599\uff0c\u6211\u8ba9\u90e8\u91cc\u7684\u4e9a\u6d32\u7ecf\u6d4e\u7814\u7a76\u5ba4\u914d\u5408\u4f60\uff0c\u8fd1\u5feb\u5199\u51fa\u4e00\u7bc7\u6587\u7ae0\u51fa\u6765\u53d1\u8868\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u57fa\u672c\u89c2\u70b9\uff0c\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u5dee\u4e0d\u591a\u90fd\u6709\u8bba\u8ff0\uff0c\u5373\u4f7f\u5199\uff0c\u4e5f\u662f\u62fe\u4ed6\u7684\u7259\u60e0\u3002\u4e0d\u8fc7\uff0c\u90a3\u4e9b\u4e3b\u6d41\u7684\u7ecf\u6d4e\u5b66\u5bb6\u4f1a\u8f7b\u6613\u653e\u5f03\u81ea\u5df1\u7684\u89c2\u70b9\u5417\uff1f\u201d\u5f20\u606a\u5634\u91cc\u8fd9\u4e48\u95ee\u7740\uff0c\u5374\u5fae\u5fae\u7684\u6447\u4e86\u6447\u5934\uff0c\u201c\u716e\u719f\u7684\u9e2d\u5b50\u5634\u5df4\u5374\u786c\uff0c\u6211\u8fd9\u4e48\u4e2a\u5c0f\u4eba\u7269\u5c31\u7b97\u5199\u4e00\u767e\u7bc7\u6587\u7ae0\u51fa\u6765\uff0c\u90a3\u4e5f\u4e0e\u5e9f\u7eb8\u65e0\u5f02\u3002\u8981\u5199\u6587\u7ae0\uff0c\u8fd8\u662f\u4e09\u53d4\u4f60\u6765\u5199\u5427\uff0c\u6216\u8bb8\u80fd\u8d77\u5230\u90a3\u4e9b\u4e00\u4e9b\u4e9b\u7684\u4f5c\u7528\u3002\u4e0d\u8fc7\u8fd9\u7bc7\u6587\u7ae0\u4e00\u51fa\uff0c\u4e09\u53d4\u4f60\u5c31\u6210\u4e86\u4f17\u77e2\u4e4b\u7684\u4e86\uff0c\u5c0f\u5fc3\u90a3\u4e9b\u4e3b\u6d41\u7ecf\u6d4e\u5b66\u5bb6\u671d\u4f60\u51b7\u5632\u70ed\u8bbd\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u968f\u53f6\u5efa\u658c\u5524\u4ed6\u4e09\u53d4\uff0c\u65e0\u7591\u80fd\u62c9\u8fd1\u4e24\u5bb6\u7684\u5173\u7cfb\uff0c\u4ed6\u5012\u662f\u4e0d\u4f1a\u9738\u5360\u4ed6\u4eba\u7684\u529f\u52b3\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u7531\u4ed6\u6765\u5199\u8fd9\u7bc7\u6587\u7ae0\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u867d\u7136\u5199\u7bc7\u6587\u7ae0\u4e5f\u53ea\u662f\u5bf9\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u89c2\u70b9\u7684\u6df1\u5ea6\u9610\u8ff0\uff0c\u4f46\u662f\u8fd9\u4e9b\u9610\u8ff0\u4e00\u65e6\u7ed9\u4e8b\u5b9e\u6240\u4e00\u4e00\u8bc1\u660e\uff0c\u5176\u5f71\u54cd\u529b\u5c31\u975e\u540c\u5c0f\u89c6\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u7b97\u4e0d\u4e0a\u5bf9\u7ecf\u6d4e\u5f88\u6709\u7814\u7a76\uff0c\u6240\u4ee5\u4e5f\u6700\u5bb9\u6613\u7ed9\u7ecf\u6d4e\u6d17\u8111\uff0c\u542c\u4e86\u4e00\u4e0b\u5348\uff0c\u5934\u6655\u8111\u80c0\u81ea\u4e0d\u7528\u8bf4\uff0c\u542c\u5230\u5f20\u606a\u3001\u53f6\u81fb\u6c11\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u52bf\u6001\u7684\u8bba\u65ad\u4e5f\u6df1\u4ee5\u4e3a\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u53f6\u90e8\u957f\u6709\u5927\u4f5c\u51fa\u6765\uff0c\u6211\u5e0c\u671b\u6709\u673a\u4f1a\u8ba4\u771f\u7684\u62dc\u8bfb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u60f3\uff1a\u90a3\u662f\u5f53\u7136\uff0c\u4e0d\u8ba9\u4f60\u8bfb\uff0c\u600e\u4e48\u6709\u673a\u4f1a\u901a\u8fc7\u4f60\u53bb\u5f71\u54cd\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u5462\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8f66\u5230\u5e02\u533a\u8fb9\u7f18\u7684\u71d5\u5f52\u6e56\u8def\uff0c\u67f3\u5fd7\u519b\u5c31\u575a\u6301\u8981\u4e0b\u8f66\uff0c\u4e0d\u8ba9\u5f20\u606a\u5c06\u4ed6\u4eec\u592b\u5987\u4e8c\u4eba\u9001\u5230\u4f4f\u5b85\uff0c\u6b63\u503c\u4e0b\u73ed\u9ad8\u5cf0\u671f\uff0c\u8f66\u4ece\u5e02\u533a\u7a7f\u8fc7\u4e0d\u6653\u5f97\u8981\u803d\u6401\u591a\u5c11\u65f6\u95f4\uff0c\u8981\u662f\u9047\u4e0a\u5835\u8f66\u66f4\u5934\u75bc\uff0c\u8ba9\u5f20\u606a\u76f4\u63a5\u9a7e\u8f66\u4ece\u73af\u57ce\u5feb\u901f\u901a\u9053\u4e0a\u9ad8\u901f\uff0c\u4ed6\u4e0e\u7231\u4eba\u53ef\u4ee5\u5750\u51fa\u79df\u8f66\u56de\u53bb\u3002</p><p>\u3000\u3000\u89c1\u67f3\u5fd7\u519b\u575a\u6301\uff0c\u5f20\u606a\u4e5f\u4e0d\u8fc7\u5206\u5ba2\u5957\uff0c\u4e0e\u67f3\u5fd7\u519b\u544a\u522b\uff0c\u4ece\u73af\u57ce\u5feb\u901f\u901a\u9053\u5f80\u9ad8\u901f\u516c\u8def\u9a76\u53bb\u3002\u4e0a\u5348\u5df2\u4e0e\u9648\u5983\u84c9\u5979\u4eec\u544a\u522b\uff0c\u5510\u5a67\u5750\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u62ff\u8d77\u624b\u673a\u8981\u8ddf\u9648\u5983\u84c9\u518d\u901a\u8fc7\u7535\u8bdd\u8bf4\u4e00\u58f0\u3002\u8bd5\u7740\u62e8\u901a\u82cf\u4e00\u5a77\u5bbf\u820d\u7684\u7535\u8bdd\uff0c\u9648\u5983\u84c9\u4eba\u8fd8\u5728\u5e08\u5927\u73a9\uff0c\u4e1c\u6d77\u5927\u5b66\u7684\u4e24\u4e2a\u6821\u533a\u4e4b\u95f4\u6709\u4e13\u8f66\u63a5\u9001\u4e24\u4e2a\u6821\u533a\u7684\u5b66\u751f\u4e0e\u6559\u5e08\uff0c\u5b66\u751f\u5012\u4e5f\u7f62\u4e86\uff0c\u5f88\u591a\u4f4f\u5728\u8001\u6821\u533a\u7684\u6559\u5e08\u8981\u53bb\u65b0\u6d66\u6821\u533a\u6388\u8bfe\uff0c\u8fd9\u662f\u5b66\u6821\u5fc5\u987b\u8981\u7167\u987e\u5230\u7684\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u8ba4\u8bc6\u82cf\u4e00\u5a77\u5bbf\u820d\u7684\u5973\u5b69\u5b50\uff1f\u201d\u5510\u5a67\u624b\u6342\u7740\u901a\u8bdd\u5b54\uff0c\u4e00\u8138\u597d\u5947\u7684\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u9648\u5983\u84c9\u8fd8\u5728\u82cf\u4e00\u5a77\u5bbf\u820d\u91cc\uff1f\u201d\u5f20\u606a\u4fa7\u5934\u89c1\u5510\u5a67\u7ea2\u6da6\u7684\u5634\u5507\u5fae\u5658\u7740\uff0c\u7709\u773c\u95f4\u6709\u4e00\u79cd\u5a07\u61a8\u7684\u67d4\u5a9a\u3002</p><p>\u3000\u3000\u201c\u542c\u5983\u84c9\uff0c\u90a3\u5973\u5b69\u5b50\u771f\u7684\u5f88\u6f02\u4eae\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u60f3\u8d77\u4f55\u5f26\uff0c\u4e0e\u524d\u4e16\u76f8\u5173\u7684\u4e00\u4e2a\u6f02\u4eae\u800c\u806a\u660e\u7684\u5973\u5b69\u5b50\uff0c\u539f\u6765\u60f3\u7740\u4eba\u751f\u5373\u4f7f\u4e0d\u76f8\u89c1\u4e5f\u65e0\u59a8\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u5979\u4f1a\u662f\u82cf\u4e00\u5a77\u7684\u540c\u5b66\uff0c\u6216\u8bb8\u5076\u5c14\u6709\u76f8\u89c1\u7684\u673a\u4f1a\u5427\uff0c\u5bf9\u5510\u5a67\u7684\u597d\u5947\uff0c\u5f20\u606a\u8f7b\u8f7b\u7684\u8bf4\u9053\uff0c\u201c\u6211\u6709\u65f6\u5019\u4f1a\u505a\u4e9b\u5f88\u5947\u602a\u7684\u68a6\uff0c\u4f1a\u68a6\u89c1\u6211\u5df2\u5c06\u6211\u7684\u524d\u534a\u751f\u8fc7\u5b8c\uff0c\u6b64\u65f6\u53ea\u662f\u91cd\u65b0\u53bb\u8fc7\u90a3\u4e9b\u5e94\u8be5\u6210\u4e3a\u8bb0\u5fc6\u4e2d\u7684\u4eba\u751f\u7f62\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u5510\u5a67\u7741\u7740\u773c\u775b\u51dd\u89c6\u7740\u5f20\u606a\uff0c\u84e6\u7136\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4f60\u5f53\u6211\u662f\u4e09\u5c81\u7684\u5c0f\u5973\u5b69\u5b50\u5427\uff01\u201d\u4f38\u624b\u8f7b\u8f7b\u634f\u4e86\u634f\u5f20\u606a\u7684\u8138\u988a\uff0c\u201c\u4f60\u62ff\u8fd9\u4e2a\u501f\u53e3\u53bb\u62db\u60f9\u5176\u4ed6\u5973\u5b69\u5b50\uff0c\u771f\u7684\u5f88\u70c2\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u5f88\u70c2\u5417\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5bf9\u7740\u540e\u89c6\u955c\u91cc\u7684\u81ea\u5df1\uff0c\u5934\u6b6a\u4e0b\u6765\uff0c\u5c06\u5510\u5a67\u6e29\u51c9\u5982\u7389\u7684\u624b\u638c\u5939\u5728\u80a9\u8180\u4e0e\u8138\u988a\u4e4b\u95f4\u3002</p><p>\u3000\u3000\u201c\u662f\u5f88\u70c2\u554a\uff0c\u201d\u5510\u5a67\u8fdf\u7591\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u95ee\uff0c\u201c\u5728\u4f60\u68a6\u91cc\uff0c\u6211\u8ddf\u4f60\u4f1a\u600e\u4e48\u6837\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u4e0d\u4fe1\uff1f\u201d\u5341\u6708\u7684\u5929\u6709\u4e9b\u53d8\u5316\u65e0\u5e38\uff0c\u6709\u96e8\u70b9\u98d8\u4e0b\u6765\uff0c\u5f20\u606a\u653e\u7f13\u8f66\u901f\uff0c\u4e0e\u5510\u5a67\u5728\u4e00\u8d77\uff0c\u4e5f\u4e0d\u7126\u6025\u8d76\u56de\u6d77\u5dde\u3002</p><p>\u3000\u3000\u201c\u662f\u4e0d\u4fe1\u554a\uff0c\u53ea\u662f\u8def\u4e0a\u65e0\u804a\uff0c\u53ef\u4ee5\u542c\u542c\u554a\u3002\u201d\u5510\u5a67\u839e\u5c14\u4e00\u7b11\uff0c\u53c8\u957f\u53c8\u5a9a\u7684\u773c\u775b\u91cc\u8361\u6f3e\u51fa\u6765\u7684\u5a07\u5a9a\u8981\u6e17\u5165\u4eba\u7684\u9aa8\u5b50\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u524d\u65b9\u7684\u8def\uff0c\u843d\u5728\u8f66\u7a97\u4e0a\u7684\u96e8\u70b9\u4e0d\u5bc6\uff0c\u53ef\u4ee5\u4e00\u6ef4\u4e00\u6ef4\u7684\u6570\u51fa\u6765\uff0c\u8fc7\u4e86\u534a\u664c\u624d\u8bf4\u9053\uff1a\u201c\u5373\u4f7f\u73b0\u5728\u662f\u91cd\u590d\u68a6\u4e2d\u7684\u4eba\u751f\u554a\uff0c\u4f60\u540c\u6837\u662f\u6211\u4eba\u751f\u91cc\u6700\u91cd\u8981\u7684\u4eba\u2026\u2026\u201d\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u5f20\u606a\u60f3\u8d77\u8bb8\u601d\uff0c\u60f3\u8d77\u8bb8\u601d\u8ff7\u4eba\u7738\u5b50\u91cc\u6e05\u5bd2\u7684\u60b2\u4f24\uff0c\u5fc3\u91cc\u60f3\uff1a\u5e76\u4e0d\u662f\u5728\u91cd\u590d\u68a6\u4e2d\u7684\u4eba\u751f\u554a\u3002</p><p>\u3000\u3000\u4e00\u8def\u4e0a\u90fd\u5728\u4e0b\u96e8\uff0c\u5728\u9ad8\u901f\u670d\u52a1\u7ad9\u7b80\u5355\u5403\u4e86\u4e9b\u4e1c\u897f\uff0c\u5c06\u8f66\u505c\u5728\u670d\u52a1\u7ad9\u4e1c\u4fa7\u7684\u6811\u836b\u4e0b\u4f11\u606f\u7247\u523b\u518d\u7ee7\u7eed\u4e0a\u8def\u3002</p><p>\u3000\u3000\u6d77\u5dde\u5883\u5185\uff0c\u5374\u6ca1\u6709\u4e00\u6ef4\u96e8\uff0c\u5929\u7a7a\u8fde\u9634\u4e91\u90fd\u6ca1\u6709\uff0c\u8f66\u7a97\u5916\u7684\u6708\u5149\u8ba9\u4eba\u5982\u7f6e\u767d\u8272\u7684\u6d77\u6c34\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u773c\u671b\u7740\u5e02\u59d4\u522b\u5885\u5c06\u5230\uff0c\u5f20\u606a\u5c06\u8f66\u62d0\u5165\u6797\u836b\u95f4\u7684\u4fbf\u9053\uff0c\u505c\u5728\u7ed9\u679d\u53f6\u7b5b\u843d\u4e0b\u7684\u6708\u5149\u4e4b\u4e2d\uff0cCD\u673a\u91cc\u64ad\u653e\u7740\u67aa\u4e0e\u73ab\u7470\u4e50\u56e2\u7684\u300asweet child O\u2019mine\u300b\uff0c\u5f20\u606a\u6162\u6162\u4eb2\u543b\u5510\u5a67\u7684\u989d\u5934\u3001\u9f3b\u5b50\u3001\u8033\u6735\u3001\u5634\u5507\u2026\u2026\u5bf9\u4e8e\u53ea\u6709\u4e00\u6b21\u7684\u4eba\u751f\u6765\u8bf4\uff0c\u6709\u4e9b\u4e1c\u897f\u53ef\u4ee5\u820d\u5f03\u5c31\u820d\u5f03\u4e86\uff0c\u5f20\u606a\u53d1\u73b0\u81ea\u5df1\u505a\u4e0d\u5230\u8fd9\u70b9\uff0c\u524d\u4e00\u4e16\u7684\u820d\u5f03\uff0c\u662f\u75db\u4e0d\u6b32\u751f\u7684\u75db\uff0c\u8fd9\u4e00\u4e16\u54ea\u91cc\u8fd8\u6709\u820d\u5f03\u7684\u52c7\u6c14\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5e2e\u5510\u5a67\u6253\u5f00\u8f66\u95e8\u8ba9\u5979\u4e0b\u8f66\uff0c\u770b\u7740\u6708\u5149\u7167\u5728\u5979\u767d\u7699\u7684\u8138\u4e0a\uff0c\u95ea\u70c1\u7740\u6676\u83b9\u7684\u5149\u6cfd\uff0c\u770b\u7740\u5510\u5a67\u7559\u604b\u4e0d\u820d\u7684\u7a7f\u8fc7\u6797\u836b\u5f80\u5bb6\u65b9\u5411\u8d70\u53bb\uff0c\u5979\u5728\u6708\u5149\u4e0b\u7684\u7738\u5b50\u6d41\u8f6c\u7740\u4ee4\u4eba\u8ff7\u9189\u7684\u5e7d\u6627\u2026\u2026</p><p>\u3000\u3000\u7a81\u7136\u624b\u673a\u54cd\u4e86\uff0c\u542c\u5230\u8bb8\u601d\u7684\u58f0\u97f3\uff0c\u5f20\u606a\u5fc3\u91cc\u5fae\u5fae\u6709\u4e9b\u6536\u7d27\uff0c\u6708\u5149\u4e0b\u80fd\u542c\u89c1\u81ea\u5df1\u7684\u5fc3\u8df3\u3002</p></article>"}}}]);
