(window.webpackJsonp=window.webpackJsonp||[]).push([[417],{440:function(p){p.exports={chapterTitle:null,title:"\u7b2c417\u7ae0 \u8981\u63d0\u524d\u529e\u597d",content:"<article><h2>\u7b2c417\u7ae0 \u8981\u63d0\u524d\u529e\u597d</h2><p>\u3000\u3000\u674e\u6bc5\u534e\u4e00\u5bbf\u6ca1\u7761\u597d\uff0c\u8f97\u8f6c\u53cd\u4fa7\u5230\u5929\u5149\u5fae\u4eae\u624d\u597d\u4e0d\u5bb9\u6613\u7761\u8fc7\u53bb\uff0c\u4e00\u89c9\u7761\u6765\u611f\u89c9\u773c\u76ae\u5b50\u6709\u4e9b\u6d6e\u80bf\uff0c\u7a97\u5e18\u5df2\u7ecf\u62c9\u5f00\uff0c\u523a\u773c\u7684\u9633\u5149\u76f4\u5c04\u773c\u775b\uff0c\u4ece\u6795\u5934\u4e0b\u6478\u8d77\u624b\u8868\uff0c\u4e00\u770b\u90fd\u8fc7\u516b\u70b9\u4e86\u3002\u674e\u6bc5\u534e\u7a7f\u4e0a\u8863\u88e4\uff0c\u5916\u9762\u5f88\u5b89\u9759\uff0c\u513f\u5b50\u3001\u5973\u513f\u90fd\u53bb\u4e0a\u5b66\u4e86\uff0c\u4e0d\u4f1a\u6ca1\u90a3\u4e48\u5b89\u9759\uff0c\u63a8\u5f00\u5367\u5ba4\u95e8\u770b\u7740\u59bb\u5b50\u5750\u5728\u72ed\u7a84\u5ba2\u5385\u91cc\u7684\u65e7\u5e03\u6c99\u53d1\u4e0a\u7ec7\u6bdb\u7ebf\u8863\uff0c\u62b1\u6028\u7684\u8bf4\uff1a\u201c\u73b0\u5728\u90fd\u51e0\u70b9\u4e86\uff0c\u600e\u4e48\u4e0d\u53eb\u9192\u6211\uff1f\u90fd\u5df2\u7ecf\u8fdf\u5230\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u90a3\u8fdf\u5230\u6709\u4eba\u7ba1\u5417\uff1f\u201d\u674e\u6bc5\u534e\u59bb\u5b50\u56de\u5934\u7591\u60d1\u7684\u95ee\uff0c\u201c\u4f60\u6628\u5929\u5728\u5e8a\u4e0a\u7ffb\u6765\u8986\u53bb\u4e2a\u52b2\uff0c\u662f\u4e0d\u662f\u60f3\u54ea\u4e2a\u72d0\u72f8\u7cbe\uff1f\u201d</p><p>\u3000\u3000\u201c\u54ea\u4e2a\u72d0\u72f8\u7cbe\u6709\u81ea\u5df1\u8001\u5a46\u8ff7\u4eba\uff1f\u201d\u674e\u6bc5\u534e\u53e3\u662f\u5fc3\u975e\u7684\u8bf4\u7740\uff0c\u5fc3\u60f3\u5f20\u606a\u6628\u5929\u5728\u8425\u4e1a\u5385\u8bf4\u7684\u8bdd\u5f88\u76f4\u767d\uff0c\u67dc\u53f0\u91cc\u8fd9\u4e48\u4eba\u542c\u7740\uff0c\u4e0d\u6653\u5f97\u6709\u6ca1\u6709\u4f20\u5230\u8d75\u77f3\u7684\u8033\u6735\u91cc\u3002\u674e\u6bc5\u534e\u5fc3\u91cc\u6709\u4e9b\u5fd0\u5fd1\uff0c\u89c1\u53a8\u623f\u65e9\u9910\u51c6\u5907\u7740\u7a00\u7ca5\uff0c\u5728\u7535\u996d\u7a9d\u8fd8\u6ca1\u6709\u51b7\u4e0b\u6765\uff0c\u674e\u6bc5\u534e\u6ca1\u90a3\u8010\u5fc3\u7b49\u70ed\u7ca5\u53d8\u51b7\uff0c\u63a8\u7740\u8f66\u5c31\u51fa\u4e86\u81ea\u5bb6\u9662\u5b50\u3002\u6311\u623f\u5b50\u91cc\uff0c\u4ed6\u7279\u610f\u6311\u4e86\u5e95\u5c42\u7684\u4e2d\u5957\uff0c\u513f\u5b50\u3001\u5973\u513f\u5927\u4e86\u4e4b\u540e\uff0c\u53c8\u5728\u9662\u5b50\u91cc\u642d\u4e86\u4e00\u95f4\uff0c\u4e00\u5bb6\u56db\u53e3\u52c9\u5f3a\u591f\u7528\uff0c\u53ea\u662f\u8fd9\u6837\u7684\u623f\u5b50\u53ea\u6709\u4e00\u4e2a\u5c0f\u9910\u5385\uff0c\u6ca1\u6709\u5ba2\u5385\u7684\uff0c\u5e95\u5c42\u7684\u623f\u5b50\uff0c\u5149\u7ebf\u53c8\u6697\uff0c\u5916\u9762\u8273\u9633\u5929\uff0c\u5bb6\u91cc\u4e5f\u4e00\u6837\u53ea\u662f\u9ec4\u660f\u822c\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u6574\u4e2a\u4e0a\u5348\u90fd\u7a9d\u5728\u529e\u516c\u5ba4\u91cc\u6ca1\u6709\u51fa\u53bb\uff0c\u60f3\u7ed9\u5510\u5b66\u8c26\u7684\u79d8\u4e66\u8bb8\u745e\u6253\u7535\u8bdd\uff0c\u6628\u5929\u5f20\u606a\u5c06\u8bb8\u745e\u7684\u8054\u7cfb\u65b9\u5f0f\u90fd\u4ea4\u7ed9\u4ed6\u4eec\uff0c\u53ea\u662f\u6628\u5929\u542c\u82cf\u671d\u4e1c\u8bf4\u5f20\u606a\u4e0d\u8fc7\u662f\u540d\u6b63\u8bfb\u9ad8\u4e09\u7684\u5b66\u751f\uff0c\u867d\u7136\u662f\u5f20\u77e5\u884c\u7684\u513f\u5b50\uff0c\u4ed6\u5c31\u6ca1\u6709\u592a\u5f53\u56de\u4e8b\uff0c\u5c06\u8bb8\u745e\u5e73\u7684\u540d\u7247\u968f\u624b\u4e22\u5783\u573e\u7b52\uff0c\u4e0d\u8fc7\u4ed6\u8bb0\u4f4f\u540d\u7247\u4e0a\u7684\u624b\u673a\u53f7\u7801\uff0c\u8fd9\u73a9\u827a\uff0c\u770b\u4e24\u773c\uff0c\u4e09\u56db\u5929\u65f6\u95f4\u91cc\u4e0d\u4f1a\u5fd8\u8bb0\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u611f\u89c9\u6574\u4e2a\u4e0a\u5348\u7ed9\u90a3\u53cc\u9510\u5229\u800c\u6df1\u9083\u7684\u773c\u795e\u6ce8\u89c6\u7740\uff0c\u4e5f\u5c31\u72b9\u8c6b\u4e86\u4e00\u4e2a\u4e0a\u5348\u6ca1\u6709\u6253\u7535\u8bdd\uff0c\u76f4\u5230\u82cf\u671d\u4e1c\u558a\u4ed6\u4e00\u8d77\u53bb\u98df\u5802\u5403\u4e2d\u5348\u996d\uff0c\u4ed6\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u53bb\uff0c\u6211\u624b\u5934\u8fd8\u6709\u4e00\u4e9b\u4e8b\u2026\u2026\u201d\u770b\u7740\u529e\u516c\u5ba4\u91cc\u5176\u4ed6\u4eba\u90fd\u5df2\u79bb\u5f00\uff0c\u4ed6\u62ff\u8d77\u7535\u8bdd\u2026\u2026</p><p>\u3000\u3000\u5173\u673a\uff01\u8bb8\u745e\u5e73\u7684\u624b\u673a\u600e\u4e48\u53ef\u80fd\u5728\u8fd9\u65f6\u95f4\u5173\u673a\uff0c\u8fd8\u662f\u8bf4\u4e00\u76f4\u90fd\u5f88\u6709\u81ea\u4fe1\u7684\u8bb0\u5fc6\u529b\u51fa\u4e86\u504f\u5dee\uff1f</p><p>\u3000\u3000\u867d\u7136\u80fd\u67e5\u5230\u5e02\u653f\u5e9c\u529e\u7684\u5ea7\u673a\uff0c\u4f46\u662f\u8fd9\u79cd\u4e0d\u9760\u8c31\u7684\u4e8b\u60c5\u8fd8\u662f\u6253\u624b\u673a\u5148\u8054\u7cfb\u4e00\u4e0b\u6bd4\u8f83\u597d\uff0c\u4ed6\u8dd1\u5230\u4e09\u697c\u6e05\u7b97\u79d1\u5916\u7684\u8d70\u5eca\uff0c\u51e0\u5929\u4e0d\u89c1\u4eba\u6e05\u7406\u7684\u5783\u573e\u7b52\u4eca\u5929\u504f\u504f\u6e05\u7406\u5f97\u5e72\u51c0\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7684\u529e\u516c\u5ba4\u7b80\u6d01\u5f97\u5f88\uff0c\u9664\u4e86\u9876\u4e0a\u5929\u82b1\u7684\u4e66\u67b6\u4e4b\u5916\uff0c\u53ea\u6709\u89d2\u843d\u91cc\u7684\u4e24\u76c6\u6587\u7af9\u3002</p><p>\u3000\u3000\u770b\u7740\u803f\u5efa\u5fe0\u9001\u8d44\u6599\u6765\u8fdb\u6765\uff0c\u5f20\u606a\u60a0\u7136\u81ea\u5f97\u7684\u5750\u5728\u5510\u5b66\u8c26\u7684\u5bf9\u9762\uff0c\u8bb8\u745e\u5e73\u5012\u662f\u9887\u6709\u672c\u5206\u7684\u7ad9\u8d77\u6765\u5e2e\u803f\u5efa\u5fe0\u63a5\u4e86\u4e00\u4e0b\u8d44\u6599\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u5bf9\u5e02\u653f\u5e9c\u529e\u7684\u4eba\u90fd\u6ca1\u6709\u592a\u5927\u7684\u597d\u611f\uff0c\u6ca1\u6709\u5c06\u8fd9\u4e9b\u4eba\u6e05\u51fa\u5e02\u653f\u5e9c\u529e\u5df2\u7ecf\u662f\u76f8\u5f53\u7684\u5bb9\u5fcd\uff0c\u4ed6\u8fd9\u6b21\u5c06\u6559\u80b2\u5c40\u7684\u803f\u5efa\u5fe0\u8c03\u6765\u7ed9\u4ed6\u5f53\u79d8\u4e66\u957f\uff0c\u4ece\u6559\u80b2\u5c40\u5c40\u957f\u5230\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u6587\u4ef6\u4e0a\u770b\u662f\u5e73\u7ea7\u8c03\u52a8\uff0c\u5bf9\u4e8e\u6709\u5fc3\u7ecf\u8425\u4ed5\u9014\u7684\u4eba\uff0c\u5374\u662f\u51ff\u5b9e\u5f80\u524d\u8fdb\u4e86\u4e00\u6b65\u3002</p><p>\u3000\u3000\u201c\u5c0f\u8bb8\u4f60\u7684\u624b\u673a\u4e00\u76f4\u6ca1\u5f00\u673a\uff1f\u201d\u803f\u5efa\u5fe0\u95ee\u8bb8\u745e\u5e73\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5510\u5b66\u8c26\u62ac\u8d77\u5934\uff0c\u201c\u6211\u8ba9\u4ed6\u628a\u624b\u673a\u5173\u4e0a\u7684\u3002\u201d</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u53ea\u662f\u968f\u53e3\u8fd9\u4e48\u4e00\u95ee\uff0c\u4ed6\u867d\u7136\u662f\u8bb8\u745e\u5e73\u7684\u9886\u5bfc\uff0c\u4f46\u662f\u8bb8\u745e\u5e73\u662f\u5510\u5b66\u8c26\u4eb2\u81ea\u6311\u8fdb\u5e02\u653f\u5e9c\u7684\uff0c\u4ed6\u8fd8\u80fd\u5bf9\u8bb8\u745e\u5e73\u5de5\u4f5c\u4e0a\u7684\u4e00\u70b9\u5c0f\u5931\u8bef\u6a2a\u7709\u7ad6\u773c\uff1f\u542c\u5510\u5b66\u8c26\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u60f3\u6216\u8bb8\u6709\u522b\u7684\u4ec0\u4e48\u539f\u56e0\uff0c\u8bb8\u745e\u5e73\u4eba\u660e\u660e\u5728\u5510\u5b66\u8c26\u529e\u516c\u5ba4\u91cc\uff0c\u5403\u8fc7\u4e2d\u996d\u5374\u544a\u8bc9\u4e0b\u9762\u4eba\u8bf4\u51fa\u53bb\u4e86\uff0c\u51e0\u4e2a\u526f\u79d8\u4e66\u957f\u90fd\u627e\u4e0d\u5230\u4ed6\u4eba\u3002\u803f\u5efa\u5fe0\u6ca1\u6709\u591a\u95ee\uff0c\u5f85\u5510\u5b66\u8c26\u5728\u6587\u4ef6\u4e0a\u7b7e\u597d\u5b57\uff0c\u4fbf\u8d70\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u65f6\u95f4\u5feb\u5230\u4e86\uff0c\u8fd9\u4e2a\u674e\u6bc5\u534e\u4f1a\u4e0d\u4f1a\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\uff1f\u53ea\u662f\u4e00\u70b9\u5c0f\u5c0f\u7684\u969c\u788d\uff0c\u770b\u8d77\u6765\u50cf\u8ddf\u4ed6\u73a9\u4e86\u4e00\u4e2a\u6076\u4f5c\u5267\uff0c\u201d\u5f20\u606a\u8038\u8038\u80a9\uff0c\u201c\u4fe1\u5408\u8054\u793e\u95ee\u9898\u91cd\u91cd\uff0c\u8981\u62ff\u4fe1\u5408\u8054\u793e\u505a\u5e95\u5b50\u7ec4\u5efa\u57ce\u5546\u884c\uff0c\u8981\u8ba9\u4fe1\u5408\u8054\u793e\u771f\u6b63\u7684\u91cd\u751f\uff0c\u800c\u4e0d\u662f\u80a4\u6d45\u7684\u4ec5\u4ece\u516c\u53f8\u5f62\u5f0f\u4e0a\u6539\u4e3a\u5c0f\u578b\u5546\u4e1a\u94f6\u884c\uff0c\u8981\u8de8\u8d8a\u7684\u969c\u788d\u5f88\u591a\uff0c\u7ba1\u7406\u4e0a\u7684\u5de5\u4f5c\u53ef\u4e0d\u662f\u4e00\u822c\u4eba\u53ef\u4ee5\u80dc\u4efb\u3002\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u8f7b\u58f0\u53f9\u606f\u7684\u6447\u4e86\u6447\u5934\uff0c\u4fe1\u5408\u8054\u793e\u7684\u95ee\u9898\u771f\u7684\u5f88\u4e25\u91cd\uff0c\u6d77\u5dde\u5e02\u5171\u6709\u5341\u4e8c\u5bb6\u57ce\u5e02\u4fe1\u7528\u793e\u603b\u8d44\u4ea7\u4ec5\u5341\u4e8c\u4ebf\uff0c\u8d37\u6b3e\u89c4\u6a21\u4e0d\u8db3\u4e94\u4ebf\uff0c\u4e0d\u826f\u8d37\u6b3e\u6bd4\u4f8b\u9ad8\u8fbe42\uff05\uff0c\u5171\u6709\u5458\u5de5\u516b\u767e\u4f59\u4eba\uff0c\u6309\u7167\u5546\u4e1a\u94f6\u884c\u5b9a\u5458\u5b9a\u7f16\u7684\u6807\u51c6\uff0c\u8d85\u5458\u8fd1\u4e00\u500d\uff0c\u6709\u4e9b\u4fe1\u7528\u793e\u4e3a\u4e2a\u4eba\u627f\u5305\uff0c\u5982\u540c\u81ea\u5bb6\u5c0f\u91d1\u5e93\u5b58\uff0c\u6709\u4e9b\u4fe1\u7528\u793e\u51e0\u8fd1\u7834\u4ea7\uff0c\u8d44\u6599\u3001\u6863\u6848\u4e5f\u6b8b\u7f3a\u4e0d\u5168\u3002\u7531\u4e8e\u57ce\u5e02\u4fe1\u7528\u793e\u4e00\u5411\u4ee5\u6765\u90fd\u662f\u5730\u65b9\u80fd\u591f\u76f4\u63a5\u638c\u63e1\u7684\u878d\u8d44\u5de5\u5177\uff0c\u4fe1\u7528\u793e\u7684\u4eba\u4e8b\u5b89\u6392\u4e5f\u90fd\u7531\u5730\u65b9\u76f4\u63a5\u9886\u5bfc\uff0c\u53ef\u4ee5\u8bf4\u4fe1\u7528\u8054\u793e\u4e0e\u5730\u65b9\u4e0a\u76d8\u6839\u9519\u8282\uff0c\u4ec5\u4ec5\u8981\u89e3\u51b3\u4eba\u5458\u8d85\u7f16\u7684\u95ee\u9898\uff0c\u5c31\u7edd\u4e0d\u662f\u4e00\u4ef6\u8f7b\u677e\u7684\u6d3b\uff0c\u8981\u8ba9\u57ce\u5546\u884c\u5728\u8fd9\u4e48\u4e2a\u70c2\u644a\u5b50\u4e0a\u6d85\u78d0\u65b0\u751f\uff0c\u5f53\u771f\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u591f\u80dc\u4efb\u3002</p><p>\u3000\u3000\u6302\u5728\u5899\u58c1\u4e0a\u7684\u77f3\u82f1\u949f\u6ef4\u7b54\u6ef4\u7684\u8d70\u7740\uff0c\u5df2\u7ecf\u8fc7\u4e86\u7ea6\u5b9a\u7684\u65f6\u95f4\uff0c\u8bb8\u745e\u5e73\u8fd8\u60f3\u8bf4\u5f20\u606a\u9ad8\u4f30\u4e86\u8fd9\u4e2a\u674e\u6bc5\u534e\u7684\u97e7\u6027\uff0c\u803f\u5efa\u5fe0\u6572\u95e8\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u5e02\u957f\uff0c\u4fe1\u5408\u8054\u793e\u6709\u4e2a\u53eb\u674e\u6bc5\u534e\u7684\u8fc7\u6765\uff0c\u8bf4\u662f\u4f60\u8fd9\u4e2a\u70b9\u7ea6\u4ed6\u89c1\u9762\uff0c\u6211\u770b\u4e86\u4eca\u5929\u4e0b\u5348\u7684\u5b89\u6392\uff0c\u597d\u50cf\u6ca1\u6709\u8fd9\u56de\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7b11\u4e86\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eba\u8fd8\u5728\u5916\u9762\uff1f\u6216\u8bb8\u6709\u4ec0\u4e48\u4e8b\u5427\uff0c\u5c0f\u8bb8\u4f60\u53bb\u53eb\u4ed6\u8fdb\u6765\u3002\u201d</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u7684\u8bdd\u7b80\u660e\u627c\u8981\uff0c\u4e0d\u8fc7\u80fd\u542c\u51fa\u674e\u6bc5\u534e\u627e\u8fc7\u6765\u8ba9\u4ed6\u6321\u4e86\u4e00\u56de\uff0c\u5e94\u662f\u674e\u6bc5\u534e\u575a\u6301\uff0c\u4ed6\u624d\u8fc7\u6765\u95ee\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u5728\u63a5\u5f85\u5ba4\u5fc3\u865a\u7684\u624b\u5fc3\u5192\u6c57\uff0c\u8d1f\u8d23\u5510\u5b66\u8c26\u65e5\u7a0b\u5b89\u6392\u7684\u79d8\u4e66\u957f\u90fd\u8bf4\u6ca1\u89c1\u9762\u7684\u5b89\u6392\uff0c\u4ed6\u8fd8\u575a\u6301\u8ba9\u803f\u5efa\u5fe0\u53bb\u95ee\u4e00\u4e0b\uff0c\u90a3\u7b80\u76f4\u662f\u4e00\u811a\u8e0f\u51fa\u60ac\u5d16\u5916\u4e86\u3002</p><p>\u3000\u3000\u8bb8\u745e\u5e73\u8fdb\u6765\u65f6\uff0c\u4ed6\u5e76\u4e0d\u8ba4\u5f97\u8bb8\u745e\u5e73\uff0c\u53ea\u662f\u6b20\u7740\u8eab\u5b50\u7ad9\u8d77\u6765\uff0c\u8fd8\u53ea\u5f53\u803f\u5efa\u5fe0\u4e0d\u8010\u70e6\u89c1\u4ed6\uff0c\u968f\u4fbf\u627e\u4e2a\u4eba\u8d76\u4ed6\u8d70\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u8bb8\u745e\u5e73\uff0c\u5510\u5e02\u957f\u5728\u4ed6\u529e\u516c\u5ba4\u7b49\u4f60\u3002\u201d</p><p>\u3000\u3000\u674e\u6bc5\u534e\u4f38\u8fc7\u53bb\u63e1\u624b\uff0c\u819d\u76d6\u90fd\u78d5\u73bb\u7483\u51e0\u89d2\u4e0a\uff0c\u4e00\u9635\u54cd\u5413\u4e86\u4ed6\u4e00\u54c6\u55e6\uff0c\u53ea\u89c1\u88c5\u7740\u534a\u676f\u7eaf\u51c0\u6c34\u7684\u4e00\u6027\u6b21\u7eb8\u676f\u5728\u73bb\u7483\u51e0\u6253\u4e86\u8f6c\uff0c\u6700\u7ec8\u6ca1\u6709\u5012\u4e0b\u6765\uff0c\u4ed6\u624d\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u968f\u8bb8\u745e\u5e73\u51fa\u4e86\u63a5\u5f85\u5ba4\u5f80\u5510\u5b66\u8c26\u7684\u529e\u516c\u5ba4\u8d70\u8fc7\u6765\uff0c\u8fd9\u4e48\u4e00\u5c0f\u6bb5\u7684\u8def\uff0c\u4ed6\u52aa\u529b\u8ba9\u81ea\u5df1\u5e73\u9759\u4e0b\u6765\uff0c\u4eba\u751f\u80fd\u51e0\u6b21\u8fd9\u6837\u7684\u673a\u4f1a\u554a\uff01</p><p>\u3000\u3000\u770b\u7740\u674e\u6bc5\u534e\u5728\u63a5\u5f85\u5ba4\u91cc\u627f\u53d7\u5f88\u5927\u7684\u5fc3\u7406\u538b\u529b\u4ee5\u81f4\u624b\u8db3\u65e0\u63aa\uff0c\u8d70\u5230\u5e02\u957f\u529e\u516c\u5ba4\u547c\u5438\u4fbf\u7ec6\u5300\u4e86\uff0c\u8bb8\u745e\u5e73\u5fc3\u60f3\u8fd9\u5206\u6de1\u5b9a\u7684\u5de5\u592b\u5012\u662f\u4e0d\u9519\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u674e\u6bc5\u534e\u8ddf\u8bb8\u745e\u5e73\u8fdb\u6765\uff0c\u7ad9\u8d77\u6765\u7b11\u7740\u8bf4\uff1a\u201c\u6628\u5929\u542c\u674e\u526f\u4e3b\u4efb\u8be6\u7ec6\u7684\u89e3\u91ca\u4e86\u4fe1\u5408\u8054\u793e\u76ee\u524d\u5b58\u5728\u7684\u4e00\u4e9b\u5f0a\u7aef\uff0c\u6211\u90fd\u5dee\u4e0d\u79bb\u8f6c\u8ff0\u7ed9\u5510\u4f2f\u4f2f\u542c\u4e86\u2026\u2026\u4eca\u5929\u662f\u4e0d\u662f\u542c\u542c\u674e\u526f\u4e3b\u4efb\u6709\u4ec0\u4e48\u597d\u7684\u89e3\u51b3\u4e4b\u9053\uff1f\u201d\u540e\u9762\u4e00\u53e5\u8bdd\u662f\u626d\u5934\u95ee\u5510\u5b66\u8c26\u7684\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7ad9\u8d77\u6765\uff0c\u6307\u7740\u5f20\u606a\u8eab\u8fb9\u7684\u4e00\u5f20\u6905\u5b50\uff0c\u8bf7\u674e\u6bc5\u534e\u5750\u4e0b\u6765\uff1b\u8bb8\u745e\u5e73\u53bb\u5e2e\u674e\u6bc5\u534e\u6c8f\u4e86\u4e00\u676f\u8336\u8fdb\u6765\u3002</p><p>\u3000\u3000\u6240\u6709\u95ee\u9898\u7684\u7ea0\u7ed3\u90fd\u5728\u201c\u653f\u5e9c\u63a7\u80a1\u6743\u201d\u4e0a\uff0c\u6309\u7167\u56fd\u5bb6\u7684\u89c4\u5b9a\uff0c\u7ec4\u5efa\u57ce\u5546\u884c\uff0c\u5730\u65b9\u653f\u5e9c\u53ef\u4ee5\u62ff\u8d22\u653f\u9884\u7b97\u8d44\u91d1\u6295\u5165\uff0c\u4f46\u662f\u6301\u80a1\u4e0d\u5f97\u8d85\u8fc730\uff05\uff0c\u4f46\u662f\u5730\u65b9\u4e0a\u7684\u505a\u6cd5\u901a\u5e38\u7684\u505a\u6cd5\u662f\u62c9\u5730\u65b9\u4e0a\u7684\u56fd\u6709\u4f01\u4e1a\u5165\u80a1\uff0c\u52a0\u4e0a\u5730\u65b9\u8d22\u653f\u76f4\u63a5\u6301\u80a1\uff0c\u53d8\u76f8\u7684\u5b9e\u73b0\u63a7\u80a1\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u6839\u672c\u6027\u7684\u95ee\u9898\uff0c\u674e\u6bc5\u534e\u81ea\u7136\u63d2\u4e0d\u4e0a\u624b\uff0c\u8fd9\u6d89\u53ca\u5230\u53c2\u80a1\u80a1\u4e1c\u4e0e\u5e02\u91cc\u7684\u535a\u5f08\u3002\u9526\u6e56\u3001\u6d77\u88d5\u3001\u76db\u946b\u627f\u8bfa\u65d7\u4e0b\u4f01\u4e1a\u4e0d\u5411\u57ce\u5546\u884c\u878d\u8d44\uff0c\u5e76\u4e3a\u57ce\u5546\u884c\u53d1\u5c55\u63d0\u4f9b\u4e00\u5b9a\u7684\u5b58\u6b3e\u652f\u6301\uff0c\u4e5f\u9650\u5236\u5e02\u91cc\u76f4\u63a5\u4ece\u57ce\u5546\u884c\u8c03\u62e8\u8d44\u91d1\u7528\u4e8e\u5e02\u653f\u5de5\u7a0b\u5efa\u8bbe\u7684\u6bd4\u4f8b\uff0c\u786e\u4fdd\u7ec4\u5efa\u540e\u7684\u57ce\u5546\u884c\u4ee5\u652f\u6301\u53d1\u5c55\u4f18\u8d28\u7684\u6d77\u5dde\u6c11\u8425\u4f01\u4e1a\u4e3a\u4e3b\u7684\u4fe1\u8d37\u65b9\u5411\uff0c\u9650\u5236\u5e02\u91cc\u76f4\u63a5\u5e72\u9884\u57ce\u5546\u884c\u7684\u7ba1\u7406\u5c42\u4eba\u4e8b\u8c03\u52a8\uff0c\u786e\u4fdd\u8463\u4e8b\u4f1a\u3001\u76d1\u4e8b\u4f1a\u4e0d\u6210\u865a\u8bbe\uff0c\u4ee5\u53ca\u4fdd\u8bc1\u8463\u4e8b\u4f1a\u6210\u5458\u5bf9\u4fe1\u8d37\u5ba1\u67e5\u7684\u5426\u51b3\u6743\uff0c\u4ee5\u53ca\u53c2\u80a1\u80a1\u4e1c\u9000\u51fa\u7684\u673a\u5236\u7b49\u7b49\uff0c\u8fd9\u79cd\u79cd\u6761\u4ef6\uff0c\u5510\u5b66\u8c26\u662f\u652f\u6301\u7684\uff0c\u4f46\u662f\u8fd9\u4e9b\u534f\u8bae\uff0c\u4e0d\u4ec5\u8981\u5e02\u5e38\u59d4\u4f1a\u8bae\u5ba1\u8bae\u901a\u8fc7\uff0c\u8fd8\u4e0a\u62a5\u7701\u653f\u5e9c\u3001\u7701\u94f6\u76d1\u4f1a\u5ba1\u8bae\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u60f3\u4ece\u674e\u6bc5\u534e\u5634\u91cc\u542c\u5230\u662f\u9488\u5bf9\u57ce\u5e02\u4fe1\u7528\u793e\u76ee\u524d\u7684\u6df7\u4e71\u7ba1\u7406\u6709\u65e0\u6709\u6548\u800c\u8fc5\u901f\u7684\u89e3\u51b3\u4e4b\u9053\uff0c\u6240\u8c13\u8fc5\u901f\uff0c\u5c31\u662f\u8981\u5feb\u5200\u65a9\u4e71\u9ebb\uff0c\u6bd4\u5982\u8bf4\u5c06\u8fd1\u534a\u6570\u7684\u5197\u5458\u88c1\u64a4\u6389\uff0c\u6bd4\u5982\u8bf4\u626d\u8f6c\u773c\u4e0b\u4fe1\u7528\u793e\u516c\u4f17\u670d\u52a1\u610f\u8bc6\u4f4e\u4e0b\u7684\u73b0\u72b6\uff0c\u8fd9\u90fd\u662f\u4e9b\u963b\u529b\u6781\u5927\uff0c\u9700\u8981\u7ba1\u7406\u8005\u6709\u9b44\u529b\u53bb\u89e3\u51b3\u597d\u3001\u5374\u53c8\u4e0d\u80fd\u8ba9\u4e0b\u9762\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u662f\u806a\u660e\u4eba\uff0c\u5510\u5b66\u8c26\u4e0d\u6309\u5e38\u89c4\u7684\u627e\u5230\u81ea\u5df1\uff0c\u7edd\u5bf9\u4e0d\u5e0c\u671b\u770b\u5230\u4e00\u6761\u4e2d\u5eb8\u7684\u89e3\u51b3\u4fe1\u7528\u793e\u773c\u4e0b\u56f0\u5883\u7684\u601d\u8def\u3002\u4ed6\u7a0d\u7a0d\u6574\u7406\u4e86\u4e00\u4e0b\u601d\u8def\uff0c\u4f83\u4f83\u800c\u8c08\uff1a\u201c\u2026\u2026\u7cbe\u7b80\u4eba\u5458\u4e0e\u7ee9\u6548\u8003\u6838\u5236\u5ea6\u7684\u63a8\u884c\uff0c\u662f\u4fe1\u5408\u8054\u793e\u773c\u4e0b\u6240\u8feb\u5207\u8d70\u7684\u4e24\u6761\u6539\u5236\u4e4b\u8def\uff0c\u7cbe\u7b80\u4eba\u5458\uff0c\u5206\u5b9a\u7f16\u3001\u6dd8\u6c70\u3001\u88c1\u5458\u4e09\u6b65\u8d70\uff0c\u773c\u4e0b\u4fe1\u7528\u793e\u7684\u62bc\u4e1a\u52a1\u5b8c\u5168\u53ef\u4ee5\u5265\u79bb\u7ed9\u5e02\u516c\u5b89\u5c40\uff0c\u4ec5\u8fd9\u4e00\u9879\u5c31\u53ef\u4ee5\u7cbe\u7b80\u8fd1\u4e00\u767e\u4eba\uff0c\u5bf9\u90a3\u4e9b\u5e74\u9f84\u504f\u5927\uff0c\u4e0d\u9002\u5e94\u4e1a\u52a1\u53d1\u5c55\u7684\u5458\u5de5\uff0c\u6309\u5de5\u9f84\u5f7b\u5e95\u4e70\u65ad\uff0c\u7136\u540e\u5b9e\u884c\u672b\u4f4d\u6dd8\u6c70\u5236\uff0c\u4e0d\u4ec5\u8fdb\u4e00\u6b65\u538b\u7f29\u5728\u804c\u4eba\u5458\uff0c\u66f4\u53ef\u4ee5\u63d0\u9ad8\u5458\u5de5\u7684\u4e1a\u52a1\u6c34\u5e73\u4e0e\u670d\u52a1\u610f\u8bc6\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u674e\u6bc5\u534e\u8bf4\u5230\u8fd9\u91cc\uff0c\u671d\u5510\u5b66\u8c26\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5904\u7406\u65b9\u5f0f\u8fd8\u771f\u4e0d\u548c\u8c10\uff0c\u6211\u6709\u770b\u8fc7\u4fe1\u7528\u8054\u793e\u7684\u4eba\u5458\u5b9a\u7f16\u60c5\u51b5\uff0c\u5e72\u90e8\u8d85\u7f16\u7684\u60c5\u51b5\u66f4\u4e25\u91cd\u4e00\u4e9b\uff0c\u6211\u770b\u5230\u4ee5\u540e\u5e38\u5230\u5e02\u653f\u5e9c\u4e32\u95e8\u7684\u4eba\u5458\u53c8\u4f1a\u591a\u4e00\u4e9b\u4e86\u2026\u2026\u201d\u4e5d\u516d\u5e74\u597d\u50cf\u4e5f\u6ca1\u6709\u201c\u5efa\u8bbe\u548c\u8c10\u793e\u4f1a\u201d\u8fd9\u4e00\u8bf4\u6cd5\u3002</p><p>\u3000\u3000\u80fd\u5b89\u6392\u8fdb\u4fe1\u7528\u8054\u793e\u5e72\u90e8\u7f16\u5236\u7684\u95f2\u6742\u4eba\u7b49\u53ef\u6bd4\u4e00\u822c\u7684\u56fd\u4f01\u5de5\u4eba\u96be\u7f20\uff0c\u6709\u4e9b\u4eba\u7684\u793e\u4f1a\u6d3b\u52a8\u80fd\u529b\u5f88\u5f3a\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u8fd9\u4e9b\u4eba\u5f80\u5f80\u662f\u6210\u4e8b\u4e0d\u8db3\u3001\u8d25\u4e8b\u6709\u4f59\uff0c\u4e0d\u5c06\u4ed6\u4eec\u6e05\u7406\u51fa\u53bb\uff0c\u5b9e\u96be\u6709\u6539\u5934\u6362\u9762\u7684\u7ba1\u7406\u6574\u987f\uff1b\u8981\u5c06\u4ed6\u4eec\u6e05\u7406\u51fa\u53bb\uff0c\u4ed6\u4eec\u95f9\u4e8b\u7684\u80fd\u529b\u53c8\u8ba9\u4eba\u5934\u75bc\uff0c\u5f88\u5927\u7a0b\u5ea6\u8fd8\u8981\u770b\u5e02\u91cc\u7684\u652f\u6301\u529b\u5ea6\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8be5\u6709\u7684\u9635\u75db\uff0c\u8fd8\u662f\u8981\u5fcd\u53d7\u7684\u3002\u201d</p><p>\u3000\u3000\u674e\u6bc5\u534e\u5012\u662f\u653e\u5fc3\u4e86\u4e00\u4e9b\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u2026\u2026\u4e00\u8fb9\u7cbe\u7b80\u4eba\u5458\uff0c\u4e00\u8fb9\u5c06\u6548\u7ee9\u8003\u6838\u5236\u5ea6\u9010\u6b65\u7684\u63a8\u5e7f\u5f00\u6765\uff0c\u5b9e\u4e1a\u5ba2\u6237\u7ecf\u7406\u5236\u5ea6\uff0c\u5c06\u5b58\u3001\u8d37\u6b3e\u5229\u5dee\u5316\u89e3\u4e3a\u6a21\u62df\u5229\u6da6\uff0c\u5ba2\u6237\u7ecf\u7406\u53ef\u4ee5\u6309\u6bd4\u4f8b\u63d0\u6210\uff0c\u4f7f\u5f97\u5ba2\u6237\u7ecf\u7406\u7684\u6536\u5165\u4e0e\u5b58\u6b3e\u8054\u7cfb\u8d77\u6765\uff1b\u67dc\u53f0\u4eba\u5458\u7684\u5de5\u8d44\u6536\u5165\u4e5f\u5206\u89e3\u6210\u57fa\u672c\u5de5\u8d44\u3001\u5b58\u6b3e\u3001\u4e1a\u52a1\u91cf\u4e09\u90e8\u5206\uff0c\u67dc\u53f0\u4eba\u5458\u82e5\u662f\u4e3b\u52a8\u62c9\u6765\u5b58\u6b3e\uff0c\u53ef\u53c2\u7167\u5ba2\u6237\u7ecf\u7406\u63d0\u6210\uff1b\u5206\u793e\u4e3b\u4efb\uff0c\u4ed6\u4eec\u7684\u6536\u5165\u5fc5\u987b\u4e0e\u5206\u793e\u5229\u6da6\u6302\u94a9\uff0c\u5c06\u5206\u793e\u4e4b\u95f4\u7684\u6536\u5165\u5dee\u8ddd\u62c9\u5927\uff0c\u5373\u4f7f\u5e74\u6536\u5165\u5dee\u8ddd\u6570\u5341\u500d\uff0c\u4e5f\u6ca1\u6709\u95ee\u9898\uff1b\u8054\u793e\u7ba1\u7406\u4eba\u5458\u4e0e\u8054\u793e\u90e8\u95e8\u5458\u5de5\u7684\u6536\u5165\uff0c\u4e5f\u8981\u4e0e\u5168\u793e\u5229\u6da6\u6302\u94a9\uff0c\u6309\u5c97\u4f4d\u70b9\u503c\u4e0e\u5168\u793e\u5229\u6da6\u5b8c\u6210\u60c5\u51b5\u8ba1\u7b97\u6536\u5165\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5728\u94f6\u884c\u5185\u63a7\u548c\u7ec4\u7ec7\u67b6\u6784\u4e0a\u4e5f\u5b9e\u65bd\u6539\u9769\u548c\u4f18\u5316\uff0c\u5bf9\u4f1a\u8ba1\u3001\u7a3d\u6838\u3001\u73b0\u91d1\u7b49\u4e3b\u8981\u4e1a\u52a1\u5b9e\u884c\u96c6\u4e2d\u3001\u5782\u76f4\u5316\u7ba1\u7406\uff0c\u7279\u522b\u662f\u4fe1\u8d37\u5ba1\u67e5\u7ba1\u7406\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5408\u638c\u653e\u5728\u80f8\u524d\uff0c\u8fb9\u542c\u674e\u6bc5\u534e\u4f83\u4f83\u800c\u8c08\uff0c\u8fb9\u601d\u8003\u7740\uff0c\u7b49\u674e\u6bc5\u534e\u8bf4\u5b8c\u8fc7\u4e86\u4f1a\u513f\uff0c\u624d\u52a8\u4e86\u52a8\u8eab\u4f53\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u8d70\u8fc7\u6765\u4e0e\u674e\u6bc5\u534e\u63e1\u4e86\u63e1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8fd9\u756a\u8bdd\uff0c\u5bf9\u6211\u4eec\u5f88\u6709\u542f\u7528\uff0c\u8c22\u8c22\u4f60\u4e0b\u5348\u7279\u610f\u8fc7\u6765\u3002\u201d\u8ba9\u8bb8\u745e\u5e73\u9001\u674e\u6bc5\u534e\u51fa\u53bb\u3002</p><p>\u3000\u3000\u770b\u7740\u674e\u6bc5\u534e\u51fa\u53bb\uff0c\u5510\u5b66\u8c26\u6478\u4e86\u6478\u8111\u95e8\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u60f3\u8981\u628a\u4fe1\u7528\u8054\u793e\u51e0\u5341\u5e74\u517b\u6210\u7684\u60f0\u6027\u4e00\u4e0b\u5b50\u5272\u6389\u4e0b\u554a\uff0c\u4e0d\u8981\u8bf4\u90a3\u4e9b\u7ed9\u7cbe\u7b80\u4e0b\u53bb\u7684\u4eba\u5458\uff0c\u90a3\u4e9b\u5728\u5c97\u7684\u5458\u5de5\u4e5f\u4f1a\u8df3\u8d77\u6765\u554a\u3002\u5373\u4f7f\u5e74\u6536\u5165\u5dee\u8ddd\u6570\u5341\u500d\u4e5f\u6ca1\u6709\u95ee\u9898\uff0c\u4ed6\u662f\u6ca1\u95ee\u9898\uff0c\u6211\u8fd9\u91cc\u5c31\u5934\u5927\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8214\u4e86\u8214\u5634\u5507\uff0c\u4f9d\u7167\u674e\u6bc5\u534e\u7684\u6548\u7ee9\u8003\u6838\u5236\u5ea6\u53bb\u5b9e\u65bd\uff0c\u540c\u4e00\u4f53\u7cfb\u5185\uff0c\u540c\u7b49\u5c97\u4f4d\u7684\u6536\u5165\u5dee\u8ddd\u5c06\u53ef\u80fd\u6781\u5927\uff0c\u5341\u5e74\u540e\u8fd8\u597d\u8bf4\u4e00\u4e9b\uff0c\u5728\u4e5d\u516d\u5e74\u8fd8\u662f\u4f1a\u9020\u6210\u5f88\u5927\u7684\u51b2\u51fb\uff0c\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u5510\u4f2f\u4f2f\uff0c\u4eba\u6211\u5e2e\u4f60\u627e\u6765\u4e86\uff0c\u7528\u4e0d\u7528\uff0c\u8fd8\u662f\u5e02\u91cc\u51b3\u5b9a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u6709\u8fd9\u4e48\u597d\u8bf4\u8bdd\uff1f\u6211\u770b\u5148\u628a\u4ed6\u5438\u6536\u8fdb\u7b79\u5907\u59d4\u5458\u4f1a\u91cc\u518d\u8bf4\uff0c\u7ec4\u5efa\u57ce\u5546\u884c\u4e0d\u5bb9\u6613\uff0c\u8c01\u90fd\u60f3\u5f80\u91cc\u585e\u79c1\u8d27\uff0c\u9526\u57ce\u4e5f\u63d0\u4ea4\u7684\u53c2\u89c2\u610f\u5411\u4e66\uff0c\u5468\u5bcc\u660e\u7684\u610f\u601d\uff0c\u65e2\u7136\u76db\u946b\u90fd\u53c2\u80a1\u4e86\uff0c\u6ca1\u7406\u7531\u4e0d\u63a5\u7eb3\u9526\u57ce\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u95ee\u9898\uff0c\u9526\u6e56\u3001\u6d77\u88d5\u3001\u76db\u946b\u505a\u51fa\u7684\u627f\u8bfa\uff0c\u9526\u57ce\u8981\u80fd\u63a5\u53d7\u5c31\u884c\u3002\u201d\u5f20\u606a\u6487\u6487\u5634\uff0c\u65e0\u6240\u8c13\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u56fd\u5bb6\u4e5d\u4e94\u5e74\u7763\u4fc3\u5404\u5730\u5728\u57ce\u5e02\u4fe1\u7528\u793e\u7684\u57fa\u7840\u4e0a\u7ec4\u5efa\u57ce\u5546\u884c\uff0c\u5141\u8bb8\u79c1\u6709\u8d44\u672c\u53c2\u80a1\uff0c\u4f46\u5bf9\u53c2\u80a1\u80a1\u4e1c\u4ece\u57ce\u5546\u884c\u8d37\u6b3e\u6709\u7740\u4e25\u683c\u7684\u9650\u5236\uff08\u8fd9\u6761\u9650\u5236\u6ca1\u6709\u5e76\u6ca1\u6709\u5f97\u5230\u5f88\u597d\u7684\u6267\u884c\uff09\uff0c\u800c\u9526\u6e56\u3001\u6d77\u88d5\u3001\u76db\u946b\u5747\u505a\u51fa\u65d7\u4e0b\u4f01\u4e1a\u4e0d\u4ece\u57ce\u5546\u884c\u8d37\u6b3e\u7684\u627f\u8bfa\u3002\u5f20\u606a\u662f\u60f3\u5c06\u57ce\u5546\u884c\u505a\u4e3a\u53d1\u5c55\u4e0e\u51dd\u805a\u6d77\u5dde\u6c11\u8425\u8d44\u672c\u52bf\u529b\u7684\u91cd\u8981\u5de5\u5177\uff0c\u5e76\u6ca1\u6709\u5c06\u57ce\u5546\u884c\u5f53\u6210\u76f4\u63a5\u7684\u878d\u8d44\u5de5\u5177\uff08\u57ce\u5546\u884c\u6b64\u65f6\u7684\u89c4\u6a21\uff0c\u8fd8\u4e0d\u8db3\u4ee5\u7ed9\u9526\u6e56\u65d7\u4e0b\u7684\u4f01\u4e1a\u53d1\u5c55\u63d0\u4f9b\u8db3\u591f\u591a\u7684\u878d\u8d44\uff09\uff0c\u5f20\u606a\u60f3\u8d75\u9526\u8363\u60f3\u53c2\u80a1\u57ce\u5546\u884c\uff0c\u4e00\u65b9\u9762\u6295\u8d44\u94f6\u884c\u4e1a\u5229\u6da6\u9887\u9ad8\uff0c\u7b2c\u4e8c\u5219\u662f\u53c2\u80a1\u57ce\u5546\u884c\u4e4b\u540e\u53ef\u4ee5\u65b9\u4fbf\u7684\u878d\u8d44\uff0c\u675c\u7edd\u4ed6\u7684\u878d\u8d44\u65b9\u4fbf\u95e8\uff0c\u770b\u4ed6\u8fd8\u5269\u4e0b\u591a\u5c11\u70ed\u60c5\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4e8b\u60c5\uff0c\u8fd8\u8981\u626f\u725b\u76ae\uff0c\u5404\u6709\u5404\u7684\u8bf4\u6cd5\uff0c\u7b79\u5907\u7ec4\u65e0\u6cd5\u5f3a\u5236\u8981\u6c42\u4ed6\u4eec\u505a\u51fa\u56fd\u5bb6\u89c4\u5b9a\u4e4b\u5916\u7684\u627f\u8bfa\uff0c\u4e0d\u8fc7\u5012\u53ef\u4ee5\u62ff\u8fd9\u4e2a\u505a\u501f\u53e3\u963b\u6b62\u9526\u57ce\u6d3e\u4eba\u8fdb\u5165\u8463\u4e8b\u4f1a\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u4e5f\u884c\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u57ce\u5546\u884c\u5012\u662f\u4e0d\u4f1a\u5acc\u8d44\u672c\u91d1\u592a\u591a\u3002\u201d</p><p>\u3000\u3000\u56fd\u9645\u60ef\u4f8b\uff0c\u8981\u6c42\u5546\u4e1a\u94f6\u884c\u7684\u8d44\u672c\u91d1\u5145\u8db3\u7387\u4e0d\u5f97\u4f4e\u4e8e8\uff05\uff0c\u6839\u636e\u521d\u6b65\u8bc4\u503c\uff0c\u8981\u5bf9\u773c\u4e0b\u6d77\u5dde\u57ce\u5e02\u4fe1\u7528\u5408\u4f5c\u8054\u793e\u7684\u4e0d\u826f\u8d44\u4ea7\u8fdb\u884c\u635f\u5931\u8ba1\u63d0\u7684\u8bdd\uff0c\u4fe1\u7528\u8054\u793e\u7684\u6838\u5fc3\u8d44\u672c\u4e0e\u9644\u5c5e\u8d44\u672c\u7684\u8d44\u672c\u603b\u989d\u4e3a\u8d1f\uff0c\u4e5f\u5c31\u662f\u8bf4\u4fe1\u5408\u8054\u793e\u7684\u8d44\u672c\u91d1\u5145\u8db3\u7387\u4e3a\u8d1f\uff0c\u51c0\u8d44\u4ea7\u4e3a\u8d1f\uff0c\u8fd9\u5012\u6ca1\u6709\u4ec0\u4e48\u5947\u602a\u7684\u5730\u65b9\uff0c\u57ce\u5546\u884c\u521a\u521a\u7ec4\u5efa\u4e4b\u521d\uff0c\u8fd8\u6ca1\u6709\u4e0d\u826f\u8d44\u4ea7\u5265\u79bb\uff08\u5f53\u65f6\u5730\u65b9\u653f\u5e9c\u8fd8\u6ca1\u6709\u4fe1\u6258\u6295\u8d44\u7ba1\u7406\u516c\u53f8\u4e4b\u7c7b\u7684\u673a\u6784\u6765\u63a5\u624b\u3001\u5904\u7f6e\u4e0d\u826f\u8d44\u4ea7\uff09\u4e00\u8bf4\uff0c\u5168\u56fd\u57ce\u5546\u884c\u5e73\u5747\u4e0d\u826f\u8d37\u6b3e\u7387\u6c34\u5e73\u572830\uff05\u4ee5\u4e0a\uff0c\u9664\u4e86\u8425\u4e1a\u7f51\u70b9\u8d44\u6e90\u4e0e\u8fdb\u5165\u94f6\u884c\u4e1a\u7684\u6e20\u9053\u503c\u5f97\u773c\u998b\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u8fd8\u4e0d\u5982\u76f4\u63a5\u7834\u4ea7\u7b97\u4e86\u3002</p><p>\u3000\u3000\u773c\u4e0b\u5404\u5bb6\u5173\u6ce8\u7684\u4e00\u4e2a\u7126\u70b9\u5c31\u662f\uff0c\u57ce\u5e02\u4fe1\u7528\u793e\u957f\u671f\u5f62\u6210\u7684\u4e0d\u826f\u8d44\u65e2\u7136\u4e0d\u80fd\u5265\u79bb\u51fa\u53bb\uff0c\u90a3\u8981\u6253\u591a\u5c11\u6298\u6263\u540e\u518d\u8ba1\u5165\u57ce\u5546\u884c\uff1f\u65e0\u8bba\u8c01\u90fd\u4e0d\u4f1a\u4e3a\u4e0d\u826f\u8d44\u4ea7\u5b8c\u5168\u4e70\u5355\uff0c\u9526\u6e56\u3001\u6d77\u88d5\u3001\u76db\u946b\u662f\u5982\u6b64\uff0c\u5c31\u7b97\u60f3\u8fdb\u6765\u7684\u9526\u57ce\u4e5f\u662f\u5982\u6b64\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e50\u89c2\u7684\u4f30\u8ba1\uff0c\u5bf9\u57ce\u5e02\u4fe1\u7528\u8054\u793e\u7684\u4e24\u4ebf\u591a\u4e0d\u826f\u8d44\u4ea7\u8fdb\u884c\u635f\u5931\u8ba1\u63d0\u4e4b\u540e\uff0c\u5176\u8d44\u672c\u91d1\u5145\u8db3\u7387\u6781\u53ef\u80fd\u4f1a\u6e05\u96f6\uff08\u5e02\u91cc\u591a\u534a\u4e0d\u4f1a\u4e3a\u57ce\u5e02\u7528\u8054\u793e\u7684\u4e8f\u635f\u518d\u638f\u94b1\u51fa\u6765\uff0c\u8fd9\u662f\u5e02\u91cc\u7684\u5e95\u9650\uff0c\u660e\u773c\u4eba\u90fd\u80fd\u770b\u51fa\u6765\uff0c\u8981\u4e89\u53d6\u53ea\u80fd\u662f\u5176\u4ed6\u6761\u4ef6\uff09\uff0c\u6ce8\u5165\u4e00\u4e2a\u4ebf\u7684\u8d44\u672c\u91d1\uff0c\u52c9\u5f3a\u80fd\u8ba9\u57ce\u5546\u884c\u7684\u8d44\u672c\u91d1\u5145\u8db3\u7387\u8fbe\u52308\uff05\uff0c\u66f4\u5145\u8db3\u7684\u8d44\u672c\u91d1\uff0c\u65e0\u7591\u80fd\u4fc3\u4f7f\u57ce\u5546\u884c\u66f4\u8fc5\u901f\u7684\u53d1\u5c55\u3002</p><p>\u3000\u3000\u201c\u4e89\u53d6\u57ce\u5546\u884c\u80fd\u5728\u660e\u5929\u6625\u672b\u4e4b\u524d\u6302\u724c\uff0c\u4e5f\u7b97\u662f\u5b8c\u6210\u4e00\u6869\u5fc3\u4e8b\u3002\u201d</p><p>\u3000\u3000\u201c\u80fd\u63d0\u524d\u5230\u4eca\u5e74\u6302\u724c\u6700\u597d\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u53c8\u4e0d\u4f9d\u8d56\u57ce\u5546\u884c\u7684\u8d44\u91d1\uff0c\u662f\u4e0d\u662f\u8fd9\u6b21\u53bb\u7701\u57ce\u53c8\u6709\u4ec0\u4e48\u65b0\u7684\u60f3\u6cd5\uff1f\u201d\u5510\u5b66\u8c26\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u201c\u542c\u5510\u5a67\u8bf4\uff0c\u4f60\u8fd9\u6b21\u53bb\u7701\u57ce\uff0c\u4e0e\u9676\u664b\u4e66\u8bb0\u7684\u79d8\u4e66\u67f3\u5fd7\u519b\u89c1\u9762\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u7b2c\u4e8c\u5929\u8fd8\u4e00\u8d77\u4e0e\u53f6\u81fb\u6c11\u89c1\u4e86\u4e00\u9762\uff0c\u4ed6\u4eec\u5bf9\u6d77\u5dde\u7684\u7ecf\u6d4e\u53d1\u5c55\uff0c\u90fd\u5f88\u79f0\u8d5e\uff0c\u6709\u4e00\u4e9b\u597d\u8bdd\uff0c\u8981\u4e0d\u8981\u6211\u590d\u8ff0\u4e00\u904d\u7ed9\u4f60\u542c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5510\u5b66\u8c26\u7b11\u4e86\u7b11\uff0c\u67f3\u5fd7\u519b\u8bf4\u7684\u4e00\u4e9b\u8bdd\u80fd\u4ee3\u8868\u9676\u664b\uff0c\u53ea\u8981\u9676\u664b\u5bf9\u6d77\u5dde\u5e02\u76ee\u524d\u7684\u60c5\u51b5\u6ee1\u610f\uff0c\u5c31\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u5371\u673a\uff0c\u201c\u4e0d\u4f1a\u5149\u804a\u8fd9\u4e2a\u5427\uff0c\u804a\u804a\u4f60\u7684\u65b0\u60f3\u6cd5\u3002\u201d</p><p>\u3000\u3000\u201c\u53f6\u81fb\u6c11\u7684\u90a3\u7bc7\u6587\u7ae0\uff0c\u8fc7\u4e24\u5929\u4fbf\u4f1a\u767b\u5185\u53c2\u5427\uff0c\u5510\u4f2f\u4f2f\u4f60\u4e0d\u662f\u5df2\u7ecf\u5728\u770b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u6709\u5728\u770b\uff0c\u8fd9\u6b21\u5012\u662f\u4eab\u53d7\u8d85\u7ea7\u522b\u5f85\u9047\uff0c\u8fd9\u7bc7\u6587\u7ae0\u629b\u51fa\u6765\uff0c\u4f1a\u6380\u8d77\u4e00\u4e9b\u6ce2\u6d6a\u5427\u3002\u201d\u5510\u5b66\u8c26\u95ee\u9053\uff0c\u201c\u4f60\u4eec\u662f\u5728\u8ba8\u8bba\u8fd9\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u662f\u56fd\u5185\u5bf9\u4e1c\u4e9a\u5404\u56fd\u8d38\u6613\u7684\u91cd\u8981\u51b3\u7b56\u4eba\u4e4b\u4e00\uff0c\u7a81\u7136\u629b\u51fa\u4e0e\u4e3b\u6d41\u601d\u6f6e\u76f8\u9006\u80cc\u7684\u89c2\u70b9\uff0c\u5c31\u7b97\u662f\u53d1\u8868\u5728\u5185\u53c2\u4e0a\uff0c\u4e5f\u6ce8\u5b9a\u4f1a\u6380\u8d77\u4e9b\u6ce2\u6f9c\u7684\u3002\u5728\u53f6\u5bb6\u8001\u5b85\u8ba8\u8bba\u8fc7\u4e4b\u540e\uff0c\u53f6\u81fb\u6c11\u5f88\u5feb\u5c31\u5199\u51fa\u7bc7\u6587\u7ae0\u6765\uff0c\u5c06\u7a3f\u5b50\u4f20\u7ed9\u5f20\u606a\u5148\u8bfb\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u518d\u6307\u624b\u753b\u811a\u8bf4\u4ec0\u4e48\uff0c\u5c06\u624b\u5934\u7684\u7a3f\u5b50\u8f6c\u7ed9\u5510\u5b66\u8c26\u770b\uff0c\u53c8\u63d0\u9192\u53f6\u81fb\u6c11\u8bb0\u5f97\u4f20\u4e00\u4efd\u7ed9\u9676\u664b\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u5bf9\u4e1c\u4e9a\u5404\u56fd\u4eca\u540e\u51e0\u5e74\u7684\u7ecf\u6d4e\u52bf\u6001\u90fd\u4e0d\u5927\u4e50\u89c2\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u4e1c\u4e9a\u5404\u56fd\u7ecf\u6d4e\u52a8\u8361\uff0c\u5bf9\u56fd\u5185\u6765\u8bf4\u662f\u4e2a\u5f88\u597d\u7684\u673a\u9047\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u8bf4\uff1f\u201d\u5510\u5b66\u8c26\u521a\u770b\u6587\u7ae0\uff0c\u8fd8\u6ca1\u6709\u770b\u900f\u3002</p><p>\u3000\u3000\u201c\u4e1c\u5357\u4e9a\u5404\u56fd\u91d1\u878d\u4f53\u7cfb\u7684\u7f3a\u9677\u6240\u5bfc\u81f4\u7684\u91d1\u878d\u52a8\u8361\u5f88\u5bb9\u6613\u6ce2\u53ca\u5230\u5236\u9020\u4e1a\uff0c\u4ea7\u4e1a\u8d44\u672c\u8981\u662f\u6709\u5148\u77e5\u5148\u89c9\u7684\u672c\u4e8b\uff0c\u8fd9\u65f6\u5019\u5e94\u8be5\u9010\u6b65\u7684\u64a4\u51fa\u8fd9\u4e9b\u56fd\u5bb6\u4e0e\u5730\u533a\u4ee5\u6c42\u7a33\u59a5\uff1b\u8fd9\u4e9b\u56fd\u5bb6\u4e0e\u5730\u533a\u610f\u8bc6\u5230\u91d1\u878d\u4e0a\u5b58\u7684\u98ce\u9669\uff0c\u6b63\u786e\u7684\u505a\u6cd5\u4e5f\u662f\u91c7\u53d6\u63aa\u65bd\u5c06\u6295\u673a\u8d44\u672c\u9a71\u9010\u6e10\u51fa\u5883\uff0c\u4e5f\u4e0d\u53ef\u907f\u514d\u7684\u63d0\u9ad8\u4ea7\u4e1a\u8d44\u672c\u5728\u8fd9\u4e9b\u56fd\u5bb6\u4e0e\u5730\u533a\u7684\u751f\u5b58\u538b\u529b\uff0c\u4ea7\u4e1a\u8d44\u672c\u4ece\u8fd9\u4e9b\u56fd\u5bb6\u4e0e\u5730\u533a\u6d41\u51fa\u90a3\u4e5f\u662f\u5fc5\u7136\u7684\uff1b\u5018\u82e5\u5904\u7f6e\u4e0d\u5f53\uff0c\u53d1\u751f\u91d1\u878d\u5371\u673a\uff0c\u66f4\u4f1a\u52a0\u901f\u4ea7\u4e1a\u8d44\u672c\u7684\u9003\u79bb\uff0c\u800c\u56fd\u5185\u5341\u4e8c\u4ebf\u4eba\u53e3\u7684\u5927\u5e02\u573a\uff0c\u65e0\u7591\u662f\u4ea7\u4e1a\u8d44\u672c\u7edd\u4f73\u7684\u907f\u9669\u5730\uff0c\u4ece\u56fd\u9645\u73af\u5883\u6765\u770b\uff0c\u6211\u4ee5\u4e3a\u4eca\u540e\u51e0\u5e74\uff0c\u5e94\u8be5\u662f\u56fd\u5185\u53d1\u5c55\u62db\u5546\u5f15\u8d44\u3001\u53d1\u5c55\u5236\u9020\u4e1a\u7684\u4e00\u4e2a\u5927\u597d\u65f6\u673a\u3002\u8fd1\u4e09\u5341\u5e74\u6765\uff0c\u8d8a\u7740\u9999\u6e2f\u7684\u5d1b\u8d77\uff0c\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u534e\u5546\u5f88\u81ea\u7136\u7684\u5f62\u6210\u4ee5\u9999\u6e2f\u4e3a\u4e2d\u5fc3\u7684\u534e\u5546\u52bf\u529b\u5708\uff0c\u6539\u9769\u5f00\u653e\u5341\u591a\u5e74\uff0c\u534e\u5546\u8d44\u672c\u5411\u5185\u5730\u6269\u6563\uff0c\u660e\u5929\u9999\u6e2f\u56de\u5f52\uff0c\u5185\u5730\u5341\u4e8c\u4ebf\u4eba\u53e3\u7684\u5e02\u573a\u5f00\u53d1\uff0c\u518d\u52a0\u4e0a\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u7ecf\u6d4e\u4e0d\u7a33\u5b9a\uff0c\u4f1a\u50ac\u5316\u8fd9\u4e2a\u6269\u6563\u8fc7\u7a0b\uff0c\u6211\u60f3\u6211\u4eec\u6d77\u5dde\u53ef\u4ee5\u505a\u4e9b\u66f4\u79ef\u6781\u7684\u51c6\u5907\uff0c\u63d0\u524d\u5c06\u57ce\u5546\u884c\u529e\u597d\uff0c\u53ea\u662f\u5176\u4e2d\u4e00\u9879\u2026\u2026\u201d</p></article>"}}}]);
