(window.webpackJsonp=window.webpackJsonp||[]).push([[419],{441:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e5d\u8282 \u8299\u857e\u72c4\u7684\u542c\u95fb",content:"<article><h2>\u7b2c\u4e5d\u8282 \u8299\u857e\u72c4\u7684\u542c\u95fb</h2><p>\u743c\u6069\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\uff0c\u5018\u82e5\u4ee5\u8ddd\u79bb\u800c\u8bba\uff0c\u8299\u8389\u5a05\u5927\u6982\u662f\u6700\u758f\u8fdc\u7684\u3002\u4e24\u4eba\u539f\u672c\u5c31\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u611f\u60c5\u57fa\u7840\uff0c\u751a\u81f3\u5f7c\u6b64\u654c\u89c6\uff0c\u7a81\u7136\u6709\u4e00\u5929\u8299\u8389\u5a05\u88ab\u838e\u5c14\u5f53\u4f5c\u751f\u65e5\u793c\u7269\u76f4\u63a5\u4e22\u8fc7\u6765\uff0c\u743c\u6069\u987a\u52bf\u63a8\u5012\uff0c\u611f\u89c9\u5c31\u50cf\u662f\u5b8c\u6210\u4e86\u4e00\u4ef6\u5de5\u4f5c\u4f3c\u7684\u3002\u5c31\u50cf\u73ca\u5609\u6240\u8bf4\uff0c\u5f97\u6765\u7684\u592a\u8f7b\u6613\uff0c\u5c31\u4e0d\u4f1a\u53bb\u73cd\u60dc\uff0c\u4e0d\u662f\u81ea\u5df1\u8f9b\u82e6\u8ffd\u6c42\u5230\u624b\u7684\uff0c\u5c31\u4e0d\u4f1a\u591a\u4e48\u770b\u91cd\u3002\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u8299\u8389\u5a05\u8fd9\u4e2a\u5973\u5b69\u5b50\uff0c\u66f4\u591a\u662f\u4f5c\u4e3a\u201c\u8299\u857e\u72c4\u7684\u59d0\u59d0\u201d\u800c\u5b58\u5728\uff0c\u641e\u4e0a\u5979\u4e3b\u8981\u4e5f\u662f\u51fa\u4e8e\u60f3\u73a9\u59d0\u59b9\u53cc\u98de\u7684\u5fc3\u7406\uff0c\u4ee5\u53ca\u907f\u514d\u8299\u857e\u72c4\u53d8\u76f8\u88ab\u4eba\u4e0a\u2014\u2014\u6362\u53e5\u8bdd\u8bf4\uff0c\u5176\u5b9e\u770b\u91cd\u7684\u662f\u5979\u7684\u201c\u8eab\u4efd\u201d\uff0c\u800c\u4e0d\u662f\u5979\u672c\u4eba\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u6bd5\u7adf\u662f\u81ea\u5df1\u4e0a\u8fc7\u7684\u5973\u4eba\uff0c\u51fa\u4e8e\u7537\u6027\u7684\u72ec\u5360\u5fc3\u7406\uff0c\u743c\u6069\u4f9d\u65e7\u8fd8\u662f\u628a\u8299\u8389\u5a05\u89c6\u4e3a\u81ea\u5df1\u7684\u201c\u6240\u6709\u7269\u201d\u3002\u5982\u4eca\u8299\u8389\u5a05\u5f53\u7740\u4ed6\u7684\u9762\u79f0\u8d5e\u53e6\u5916\u4e00\u4e2a\u7537\u4eba\uff08\u8fd8\u662f\u5979\u7684\u672a\u5a5a\u592b\uff09\uff0c\u743c\u6069\u81ea\u7136\u8fd8\u662f\u4e0d\u751a\u6109\u5feb\u3002\u540c\u65f6\u4e5f\u5c31\u8d8a\u53d1\u597d\u5947\uff0c\u8299\u8389\u5a05\u548c\u5e93\u80af\u4e4b\u95f4\uff0c\u5230\u5e95\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u5462\u3002</p><p>\u7b97\u4e86\uff0c\u4e0d\u60f3\u8fd9\u4e9b\uff0c\u65e2\u7136\u5df2\u7ecf\u5546\u5b9a\u4e86\u65b9\u6cd5\uff0c\u90a3\u5c31\u7167\u6837\u6267\u884c\u4fbf\u662f\u3002\u53cd\u6b63\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u4e5f\u5f88\u60f3\u548c\u5e93\u80af\u6765\u4e00\u573a\u5802\u5802\u6b63\u6b63\u7684\u51b3\u6597\uff0c\u5012\u4e0d\u662f\u60f3\u8981\u62a5\u4ec7\u4e4b\u7c7b\uff0c\u66f4\u591a\u662f\u60f3\u501f\u6b64\u6765\u68c0\u9a8c\u5224\u65ad\u4e00\u4e0b\u81ea\u5df1\u7684\u771f\u5b9e\u6c34\u5e73\u3002\u8fd9\u4e24\u5e74\u6765\uff0c\u4ed6\u548c\u4e0d\u5c11\u5deb\u5e08\u4ea4\u8fc7\u624b\uff0c\u7b97\u662f\u6709\u4e00\u4e9b\u4f5c\u6218\u7ecf\u9a8c\uff0c\u4f46\u9664\u4e86\u51db\u4e4b\u5916\uff0c\u6781\u5c11\u6709\u5904\u4e8e\u540c\u4e00\u4e2a\u6c34\u5e73\u7ebf\u4e0a\u7684\uff0c\u5f80\u5f80\u90fd\u662f\u514b\u52b3\u62d3\u3001\u4e9a\u5f53\u65af\u3001\u6b23\u5e03\u8fd9\u79cd\u5927\u5deb\u5e08\uff0c\u603b\u662f\u4ee5\u5f31\u654c\u5f3a\uff0c\u56fa\u7136\u80fd\u591f\u5feb\u901f\u63d0\u5347\uff0c\u4f46\u7ec8\u7a76\u4e0d\u80fd\u7b97\u662f\u201c\u6b63\u5e38\u201d\u7684\u953b\u70bc\u65b9\u5f0f\uff0c\u5373\u4fbf\u662f\u51db\uff0c\u5176\u5b9e\u4e5f\u4e0d\u80fd\u7b97\u662f\u4e00\u4e2a\u201c\u6b63\u5e38\u201d\u7684\u5bf9\u624b\uff0c\u5979\u867d\u7136\u662f\u5deb\u5e08\uff0c\u8fd1\u8eab\u640f\u51fb\u5374\u6bd4\u6b66\u8005\u8fd8\u5f3a\uff0c\u800c\u4e14\u592a\u504f\u79d1\u4e86\u3002</p><p>\u76f8\u6bd4\u8d77\u6765\uff0c\u5e93\u80af\u5c31\u6700\u5408\u9002\u4e0d\u8fc7\uff0c\u4ed6\u548c\u743c\u6069\u662f\u540c\u5b66\uff0c\u90fd\u63a5\u53d7\u8fc7\u5168\u9762\u4e25\u8c28\u7684\u5b66\u9662\u6559\u80b2\uff0c\u53c8\u5404\u81ea\u6709\u7cbe\u7814\u7684\u65b9\u5411\u3002\u743c\u6069\u8fd9\u4e24\u5e74\u6765\u8fdb\u6b65\u6781\u5feb\uff0c\u4f46\u5e93\u80af\u8fdb\u5165\u5965\u672f\u5e08\u5b66\u9662\u6df1\u9020\uff0c\u4ee5\u4ed6\u7684\u5929\u8d44\u52e4\u594b\uff0c\u60f3\u5fc5\u4e5f\u5b66\u5230\u4e86\u4e0d\u5c11\u4e1c\u897f\uff0c\u6b63\u597d\u6765\u5207\u78cb\u5207\u78cb\uff0c\u8f83\u91cf\u4e00\u756a\u3002</p><p>\u201c\u597d\u5427\uff0c\u627e\u4e2a\u65f6\u95f4\u628a\u4ed6\u7ea6\u51fa\u6765\uff0c\u63d0\u51fa\u51b3\u6597\u4fbf\u662f\u3002\u201d</p><p>\u63a5\u4e0b\u6765\u7684\u4e24\u5929\u8fc7\u5f97\u6ce2\u6f9c\u4e0d\u60ca\uff0c\u4e00\u5207\u5982\u5e38\u3002\u5965\u6c83\u4f3c\u4e4e\u53c8\u5728\u95ed\u95e8\u9f13\u6363\u4ec0\u4e48\u8bd5\u9a8c\uff0c\u6ca1\u6709\u518d\u53ec\u743c\u6069\u524d\u53bb\u3002\u838e\u73de\u514b\u4f4f\u5728\u743c\u6069\u5bb6\u4e2d\uff0c\u5979\u6682\u65f6\u6ca1\u6709\u9634\u9b42\u57ce\u5c45\u6c11\u7684\u5408\u6cd5\u8eab\u4efd\uff0c\u6240\u4ee5\u4e5f\u4e0d\u9002\u5b9c\u5230\u5904\u4e71\u8dd1\uff0c\u6700\u591a\u5728\u9644\u8fd1\u8f6c\u8f6c\u3002\u8ba9\u743c\u6069\u6ca1\u60f3\u5230\u7684\u662f\uff0c\u73ca\u5609\u5c45\u7136\u548c\u838e\u73de\u514b\u4e5f\u5f88\u8c08\u5f97\u6765\uff0c\u76f8\u5904\u5f97\u975e\u5e38\u4e0d\u9519\u3002</p><p>\u838e\u73de\u514b\u4ee5\u524d\u662f\u4e2a\u6740\u624b\uff0c\u73b0\u5728\u662f\u4e2a\u6076\u9b54\uff0c\u65e0\u8bba\u6309\u7167\u4ec0\u4e48\u6807\u51c6\u6765\u770b\uff0c\u90fd\u5c5e\u4e8e\u6b63\u5e38\u4eba\u907f\u800c\u8fdc\u4e4b\u7684\u7c7b\u578b\u3002\u5f53\u7136\uff0c\u5979\u5f88\u6f02\u4eae\uff0c\u8eab\u6750\u706b\u8fa3\uff0c\u5f88\u80fd\u5438\u5f15\u7537\u6027\uff0c\u4f46\u73ca\u5609\u662f\u5973\u5b69\u5b50\uff0c\u53c8\u6ca1\u6709\u857e\u4e1d\u8fb9\u503e\u5411\uff0c\u4e0d\u4f1a\u8d2a\u604b\u5979\u7684\u7f8e\u8272\u3002\u5728\u743c\u6069\u539f\u672c\u7684\u9884\u6599\u4e2d\uff0c\u73ca\u5609\u548c\u838e\u73de\u514b\u7684\u5173\u7cfb\u5927\u6982\u4f1a\u662f\u5ba2\u6c14\u3001\u51b7\u6de1\u3001\u793c\u8c8c\u4e2d\u5e26\u7740\u758f\u8fdc\u7684\u72b6\u6001\uff0c\u5f7c\u6b64\u5404\u5e72\u5404\u7684\u4e8b\u60c5\uff0c\u89c1\u4e86\u9762\u70b9\u70b9\u5934\u800c\u5df2\uff0c\u73b0\u5728\u8fd9\u79cd\u7ed3\u679c\uff0c\u4ee4\u4ed6\u5927\u5927\u51fa\u4e4e\u610f\u6599\u3002\u66f4\u52a0\u532a\u5937\u6240\u601d\u7684\u662f\uff0c\u4e24\u4eba\u4e0d\u4f46\u5173\u7cfb\u878d\u6d3d\uff0c\u800c\u4e14\u5728\u73ca\u5609\u9762\u524d\uff0c\u838e\u73de\u514b\u5c31\u4f1a\u663e\u5f97\u683c\u5916\u4e56\u5de7\u542c\u8bdd\uff0c\u4eff\u4f5b\u6587\u9759\u7684\u5c0f\u59d1\u5a18\uff0c\u54ea\u91cc\u8fd8\u50cf\u4e2a\u6740\u4eba\u5982\u9ebb\u7684\u6740\u624b\u3002</p><p>\u201c\u6211\u8fd9\u4e2a\u59d0\u59d0\u2026\u2026\u8fd8\u771f\u662f\u6709\u4eb2\u548c\u529b\u554a\u3002\u201d</p><p>\u89c9\u5bdf\u5230\u8fd9\u4e00\u70b9\uff0c\u743c\u6069\u7ec8\u4e8e\u6709\u70b9\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u81ea\u5df1\u5728\u5916\u9762\u6cbe\u82b1\u60f9\u8349\uff0c\u52fe\u642d\u4e86\u4e00\u5927\u5806\u5973\u5b69\u5b50\uff0c\u73ca\u5609\u5403\u918b\u5f52\u5403\u918b\uff0c\u5374\u4e5f\u5e76\u6ca1\u6709\u771f\u7684\u7279\u522b\u751f\u6c14\uff0c\u6572\u6253\u6572\u6253\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u518d\u8fdb\u4e00\u6b65\u60f3\uff0c\u5f53\u521d\u743c\u6069\u53bb\u5e7d\u6697\u5730\u57df\u4e4b\u524d\uff0c\u5e26\u4e0a\u4e86\u8299\u857e\u72c4\uff0c\u4ee5\u73ca\u5609\u7684\u806a\u660e\uff0c\u4e0d\u53ef\u80fd\u731c\u4e0d\u5230\u8fd9\u4e24\u4eba\u6709\u53ef\u80fd\u4f1a\u65e7\u60c5\u91cd\u71c3\uff0c\u518d\u5ea6\u590d\u5408\uff0c\u4f46\u5979\u4e5f\u5e76\u6ca1\u6709\u51fa\u8a00\u963b\u6b62\uff0c\u751a\u81f3\u6ca1\u6709\u6d41\u9732\u51fa\u534a\u70b9\u53cd\u5bf9\u7684\u610f\u601d\uff0c\u5f53\u7136\u4e5f\u8c08\u4e0d\u4e0a\u591a\u4e48\u9ad8\u5174\uff0c\u66f4\u591a\u50cf\u662f\u2026\u2026\u50cf\u662f\u6839\u672c\u5c31\u771f\u6ca1\u5f53\u56de\u4e8b\u3002</p><p>\u5bf9\uff0c\u5c31\u662f\u8fd9\u79cd\u611f\u89c9\uff0c\u5728\u73ca\u5609\u5fc3\u91cc\uff0c\u5e76\u6ca1\u6709\u771f\u628a\u743c\u6069\u8eab\u8fb9\u7684\u90a3\u4e9b\u5973\u5b69\u5b50\u5f53\u56de\u4e8b\uff0c\u5979\u6e05\u695a\u81ea\u5df1\u5728\u5f1f\u5f1f\u5fc3\u4e2d\u7684\u5730\u4f4d\u548c\u5206\u91cf\uff0c\u8fdc\u8fdc\u8d85\u8d8a\u5979\u4eec\u4e4b\u4e0a\uff0c\u6240\u4ee5\u6839\u672c\u5c31\u65e0\u9700\u5c06\u5979\u4eec\u89c6\u4e3a\u540c\u7b49\u7ea7\u7684\u5bf9\u624b\uff0c\u5927\u53ef\u4ee5\u653e\u4f4e\u8eab\u6bb5\u53bb\u5b89\u629a\u2014\u2014\u56e0\u4e3a\u5979\u6709\u5c45\u9ad8\u4e34\u4e0b\u7684\u8d44\u683c\uff0c\u66f4\u6709\u80fd\u591f\u8ba9\u4eba\u5fc3\u60a6\u8bda\u670d\u7684\u6c14\u5ea6\u548c\u624b\u8155\uff0c\u6e29\u987a\u7684\u8299\u857e\u72c4\u4e5f\u597d\uff0c\u5014\u5f3a\u7684\u8299\u8389\u5a05\u4e5f\u597d\uff0c\u6ee1\u8eab\u90aa\u6c14\u7684\u838e\u73de\u514b\u4e5f\u597d\uff0c\u90fd\u80fd\u88ab\u5979\u8c03\u6559\u5f97\u670d\u670d\u5e16\u5e16\u3002\u552f\u4e00\u7684\u4f8b\u5916\u2026\u2026\u6050\u6015\u662f\u6885\u83f2\u65af\u3002</p><p>\u6240\u4ee5\u73ca\u5609\u771f\u6b63\u5728\u610f\u7684\uff0c\u59cb\u7ec8\u4e5f\u53ea\u6709\u6885\u83f2\u65af\u3002</p><p>\u5728\u8fd9\u4e24\u5929\u91cc\uff0c\u743c\u6069\u548c\u8299\u857e\u72c4\u628a\u7b2c\u4e8c\u3001\u7b2c\u4e09\u56fe\u4e66\u9986\u4ed4\u7ec6\u7ffb\u4e86\u4e2a\u904d\uff0c\u8fde\u7b2c\u56db\u56fe\u4e66\u9986\u90fd\u770b\u8fc7\u4e86\uff0c\u4f9d\u65e7\u4e00\u65e0\u6240\u83b7\uff0c\u534a\u70b9\u6709\u5173\u541f\u6e38\u8bd7\u4eba\u7684\u6709\u4ef7\u503c\u7684\u8d44\u6599\u90fd\u6ca1\u627e\u5230\u3002\u81f3\u4e8e\u97f3\u4e50\u5b66\u9662\u90a3\u8fb9\uff0c\u73ca\u5609\u5de7\u5999\u5730\u5411\u6559\u6388\u63a2\u8be2\u8fc7\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u7ed3\u679c\uff0c\u8fd9\u662f\u6bd4\u8f83\u4ee4\u4eba\u6cae\u4e27\u7684\u4e8b\u60c5\u3002</p><p>\u6b63\u5f53\u743c\u6069\u5f7b\u5e95\u5931\u671b\u7684\u65f6\u5019\uff0c\u838e\u73de\u514b\u63d0\u4f9b\u4e86\u4e00\u4e2a\u6709\u4ef7\u503c\u7684\u4fe1\u606f\u3002</p><p>\u201c\u4f60\u548c\u8299\u857e\u72c4\u8fd9\u4e24\u5929\u53bb\u56fe\u4e66\u9986\uff0c\u662f\u60f3\u627e\u541f\u6e38\u8bd7\u4eba\u7684\u8d44\u6599\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u770b\u6837\u5b50\u6ca1\u4ec0\u4e48\u53d1\u73b0\u5462\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u542c\u53e3\u6c14\uff0c\u83ab\u975e\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u522b\u8bef\u4f1a\uff0c\u6211\u5bf9\u541f\u6e38\u8bd7\u4eba\u4e5f\u662f\u8fd1\u4e4e\u65e0\u77e5\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4f46\u6211\u5947\u602a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u627e\u51db\u5e2e\u5fd9\u5462\uff1f\u201d</p><p>\u201c\u51db\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u627e\u5979\u6709\u4ec0\u4e48\u7528\u3002\u201d</p><p>\u201c\u51db\u7684\u8001\u5e08\u662f\u6b23\u5e03\uff0c\u4e03\u59d0\u59b9\u4e4b\u4e00\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\u554a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4e03\u59d0\u59b9\u91cc\u6709\u4e00\u4f4d\u98ce\u66b4\xb7\u94f6\u624b\uff0c\u7ad6\u7434\u624b\u540c\u76df\u7684\u4e09\u5927\u9886\u8896\u4e4b\u4e00\uff0c\u6b23\u5e03\u7684\u59d0\u59d0\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5979\u5c31\u662f\u541f\u6e38\u8bd7\u4eba\u3002\u201d</p><p>\u7ad6\u7434\u624b\u540c\u76df\u662f\u5927\u9646\u4e0a\u8d6b\u8d6b\u6709\u540d\u7684\u6b63\u4e49\u7ec4\u7ec7\uff0c\u743c\u6069\u81ea\u7136\u662f\u6709\u6240\u8033\u95fb\u7684\uff0c\u800c\u4e14\u8fd8\u548c\u5176\u4e2d\u6210\u5458\u6253\u8fc7\u4e00\u6b21\u4ea4\u9053\uff0c\u4f46\u4e86\u89e3\u786e\u5b9e\u4e0d\u591a\uff0c\u56e0\u4e3a\u6ca1\u600e\u4e48\u5173\u5fc3\u3002\u5018\u82e5\u4e0d\u662f\u838e\u73de\u514b\u8bf4\uff0c\u4ed6\u8fd8\u771f\u4e0d\u77e5\u9053\u539f\u6765\u7ad6\u7434\u624b\u7684\u9886\u8896\u662f\u4e03\u59d0\u59b9\u4e4b\u4e00\uff0c\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u3002</p><p>\u201c\u662f\u9886\u8896\u4e4b\u4e00\uff0c\u201d\u838e\u73de\u514b\u7ea0\u6b63\uff0c\u201c\u7ad6\u7434\u624b\u6709\u4e09\u4e2a\u9886\u8896\uff0c\u98ce\u66b4\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d</p><p>\u201c\u4e09\u4e2a\u9886\u8896\uff1f\u201d\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u591a\u5934\u6267\u653f\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u7ad6\u7434\u624b\u540d\u4e49\u4e0a\u662f\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u5176\u5b9e\u662f\u5206\u6210\u4e09\u4e2a\u6d3e\u7cfb\uff0c\u4e92\u4e0d\u7edf\u5c5e\uff0c\u5404\u884c\u5176\u662f\uff0c\u81ea\u7136\u5c31\u6709\u4e09\u4e2a\u9886\u8896\u4e86\u3002\u201d</p><p>\u201c\u54ea\u4e09\u4e2a\u6d3e\u7cfb\uff1f\u201d</p><p>\u201c\u7b2c\u4e00\u4e2a\u6d3e\u7cfb\u662f\u8d5b\u83b1\u4e3d\u5a05\u9886\u5bfc\u7684\u2018\u9ece\u660e\u2019\uff0c\u7b97\u662f\u6700\u6b63\u7edf\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6700\u5149\u660e\u6b63\u5927\uff0c\u516c\u5f00\u6d3b\u52a8\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u7b2c\u4e8c\u4e2a\u6d3e\u7cfb\u5c31\u662f\u98ce\u66b4\u9886\u5bfc\u7684\u2018\u5bc6\u63a2\u2019\uff0c\u559c\u6b22\u523a\u63a2\u3001\u6f5c\u4f0f\u751a\u81f3\u641e\u5404\u79cd\u6697\u6740\uff1b\u7b2c\u4e09\u4e2a\u6d3e\u7cfb\u662f\u6df1\u6c34\u57ce\u4e3b\u51ef\u5c14\u672c\u9886\u5bfc\u7684\u2018\u6708\u661f\u2019\uff0c\u884c\u8e2a\u9690\u853d\uff0c\u98ce\u683c\u8be1\u79d8\uff0c\u559c\u6b22\u5e55\u540e\u64cd\u7eb5\u3002\u201d</p><p>\u201c\u51ef\u5c14\u672c\u4e5f\u662f\u7ad6\u7434\u624b\u7684\u9886\u8896\u4e4b\u4e00\uff1f\u4ed6\u4e0d\u4e5f\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u7ad6\u7434\u624b\u7b80\u76f4\u5c31\u662f\u9b54\u6cd5\u5973\u795e\u7684\u4e0b\u5c5e\u7ec4\u7ec7\u4e86\u3002\u201d</p><p>\u201c\u5dee\u4e0d\u591a\uff0c\u98ce\u66b4\u548c\u51ef\u5c14\u672c\u90fd\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u8d5b\u83b1\u4e3d\u5a05\u867d\u7136\u4e0d\u662f\u9009\u6c11\uff0c\u4f46\u4e5f\u662f\u4fe1\u5f92\uff0c\u6574\u4e2a\u7ad6\u7434\u624b\u540c\u76df\uff0c\u9886\u5bfc\u9636\u5c42\u57fa\u672c\u90fd\u88ab\u9b54\u6cd5\u5973\u795e\u7684\u4fe1\u5f92\u628a\u6301\uff0c\u201d\u838e\u73de\u514b\u70b9\u5934\uff0c\u201c\u4e0d\u8fc7\u51ef\u5c14\u672c\u9886\u5bfc\u7684\u2018\u6708\u661f\u2019\u2026\u2026\u5f88\u53d7\u6392\u65a5\uff0c\u751a\u81f3\u4e00\u5ea6\u548c\u53e6\u5916\u4e24\u6d3e\u7ffb\u8138\uff0c\u5206\u9053\u626c\u9573\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u9886\u5bfc\u5c42\u7684\u5206\u6b67\u5427\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u51ef\u5c14\u672c\u7684\u884c\u4e8b\u4f5c\u98ce\uff0c\u548c\u7ad6\u7434\u624b\u7684\u5176\u4ed6\u9886\u8896\u683c\u683c\u4e0d\u5165\uff0c\u65e0\u6cd5\u8c03\u548c\uff0c\u6700\u7ec8\u53ea\u80fd\u5206\u88c2\u4e86\u2014\u2014\u5927\u5bb6\u90fd\u662f\u8fd9\u4e48\u8ba4\u4e3a\u7684\u3002\u201d</p><p>\u201c\u51ef\u5c14\u672c\u662f\u4ec0\u4e48\u884c\u4e8b\u4f5c\u98ce\uff1f\u201d\u743c\u6069\u597d\u5947\u3002</p><p>\u201c\u4e00\u53e5\u8bdd\u5c31\u8db3\u4ee5\u6982\u62ec\uff0c\u201d\u838e\u73de\u514b\u4e3e\u8d77\u624b\u6307\uff0c\u201c\u4e3a\u8fbe\u76ee\u7684\uff0c\u4e0d\u62e9\u624b\u6bb5\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u660e\u767d\u4e86\u3002\u201d</p><p>\u51ef\u5c14\u672c\u7684\u884c\u4e8b\u4f5c\u98ce\u95ee\u9898\uff0c\u53ef\u4ee5\u5148\u653e\u5728\u4e00\u8fb9\u4e0d\u53bb\u8003\u8651\uff0c\u743c\u6069\u6bd4\u8f83\u5173\u5fc3\u7684\u662f\u98ce\u66b4\xb7\u94f6\u624b\u3002\u8fd9\u4e16\u754c\u4e0a\u7684\u541f\u6e38\u8bd7\u4eba\u5b9e\u5728\u592a\u7f55\u89c1\uff0c\u5965\u6c83\u77e5\u9053\u4e00\u4e2a\u4ec0\u4e48\u6e05\u6b4c\u4e4b\u5973\uff0c\u7ed3\u679c\u662f\u6b7b\u4e86\u51e0\u5343\u5e74\u7684\u53e4\u4eba\uff0c\u5982\u4eca\u597d\u4e0d\u5bb9\u6613\u6709\u4e2a\u5728\u4e16\u7684\uff0c\u4e0d\u80fd\u4e0d\u5173\u6ce8\u3002</p><p>\u201c\u4f60\u60f3\u4e86\u89e3\u541f\u6e38\u8bd7\u4eba\uff0c\u4e0e\u5176\u53bb\u56fe\u4e66\u9986\u7ffb\u8d44\u6599\uff0c\u8fd8\u4e0d\u5982\u53bb\u76f4\u63a5\u8bf7\u6559\u98ce\u66b4\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u800c\u4e14\uff0c\u4f60\u8fd8\u4e0d\u662f\u60f3\u5355\u7eaf\u7684\u4e86\u89e3\uff0c\u662f\u60f3\u8ba9\u73ca\u5609\u5b66\u4e60\u5427\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u743c\u6069\u6709\u4e9b\u60ca\u8bb6\u3002</p><p>\u201c\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u770b\u4e5f\u770b\u5f97\u51fa\u6765\u5427\uff0c\u201d\u9b45\u9b54\u51b7\u7b11\uff0c\u201c\u522b\u628a\u6211\u5f53\u7b28\u86cb\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u5c82\u6562\u3002\u201d</p><p>\u838e\u73de\u514b\u6240\u8a00\u4e0d\u5dee\uff0c\u4e0e\u5176\u53bb\u56fe\u4e66\u9986\u7ffb\u8d44\u6599\uff0c\u4e0d\u5982\u76f4\u63a5\u4e0a\u95e8\u8bf7\u6559\uff0c\u7136\u800c\u743c\u6069\u548c\u8fd9\u4f4d\u98ce\u66b4\u5973\u58eb\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53cd\u800c\u548c\u5979\u7684\u59b9\u59b9\uff0c\u201c\u98ce\u66b4\u5973\u738b\u201d\u6b23\u5e03\u8fd8\u6253\u8fc7\u4ea4\u9053\uff0c\u52c9\u5f3a\u7b97\u662f\u6709\u70b9\u4ea4\u60c5\u3002\u5018\u82e5\u8bf7\u51db\u51fa\u9a6c\uff0c\u6c42\u6b23\u5e03\u5e2e\u5fd9\u4ecb\u7ecd\u7684\u8bdd\uff0c\u5012\u672a\u5fc5\u6ca1\u6709\u5e0c\u671b\u3002\u53ea\u662f\u51db\u73b0\u5728\u4e0d\u5728\u8eab\u8fb9\uff0c\u867d\u7136\u53ef\u4ee5\u501f\u52a9\u9b54\u6cd5\u4f20\u8baf\u8054\u7cfb\uff0c\u4f46\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4e09\u8a00\u4e24\u8bed\u80af\u5b9a\u8bf4\u4e0d\u6e05\u695a\uff0c\u53ea\u80fd\u7b49\u4e0b\u6b21\u89c1\u9762\u518d\u8be6\u8c08\u4e86\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u81ea\u963f\u65af\u5361\u7279\u62c9\u6682\u522b\uff0c\u5982\u4eca\u4e5f\u5df2\u7ecf\u8fc7\u53bb\u5927\u534a\u4e2a\u6708\uff0c\u5176\u95f4\u4e5f\u8054\u7cfb\u8fc7\u51e0\u6b21\u3002\u4ed6\u4eec\u624b\u4e0a\u90fd\u6709\u5b9d\u77f3\u8df3\u8dc3\u6212\u6307\uff0c\u6bcf\u5929\u53ef\u4ee5\u542f\u52a8\u4e09\u6b21\u77ed\u8baf\u672f\uff0c\u6bcf\u6b21\u80fd\u591f\u7ef4\u6301\u901a\u8bdd\u4e00\u5206\u949f\uff0c\u867d\u7136\u8c08\u60c5\u8bf4\u7231\u529e\u4e0d\u5230\uff0c\u7b80\u5355\u5730\u6c47\u62a5\u4e00\u4e0b\u8fd1\u51b5\u8fd8\u662f\u53ef\u4ee5\u7684\u3002\u5728\u6628\u65e5\u7684\u77ed\u8baf\u8054\u7cfb\u4e2d\uff0c\u6885\u83f2\u65af\u8bf4\u5979\u548c\u51db\u5df2\u7ecf\u4e58\u8239\u5728\u5760\u661f\u6d77\u4e0a\uff0c\u9884\u8ba1\u8fd8\u6709\u4e24\u4e09\u5929\u5c31\u80fd\u62b5\u8fbe\u963f\u683c\u62c9\u9686\uff0c\u89c1\u5230\u6b23\u5e03\u3002</p><p>\u9634\u9b42\u57ce\u8fd9\u8fb9\u53ec\u743c\u6069\u56de\u6765\uff0c\u4e00\u65b9\u9762\u662f\u8ff0\u804c\uff0c\u53e6\u4e00\u65b9\u9762\u8bf4\u662f\u53e6\u6709\u4efb\u547d\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\u8fd8\u6ca1\u534a\u70b9\u6d88\u606f\uff0c\u4e5f\u4e0d\u77e5\u9053\u4e0b\u4e2a\u4efb\u52a1\u662f\u4ec0\u4e48\u3002\u4f46\u9a6c\u4e0a\u5c31\u662f\u65b0\u5e74\uff0c\u63a5\u7740\u53c8\u662f\u4ef2\u51ac\u8282\uff0c\u4ee5\u5e38\u7406\u63a8\u6d4b\uff0c\u8fd9\u4e00\u4e2a\u6708\u4e4b\u5185\u90fd\u4f1a\u662f\u6bd4\u8f83\u6e05\u95f2\u7684\uff0c\u5c31\u7b97\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5e94\u8be5\u4e5f\u662f\u7b49\u5230\u4ef2\u51ac\u8282\u4e4b\u540e\u518d\u8bf4\u3002\u743c\u6069\u76d8\u7b97\u7740\uff0c\u8981\u4e0d\u8981\u8d81\u8fd9\u4e2a\u673a\u4f1a\u8bf7\u5047\uff0c\u548c\u73ca\u5609\u51fa\u53bb\u65c5\u6e38\u4e00\u8d9f\uff0c\u59d0\u59d0\u4e00\u5b9a\u4f1a\u5f88\u9ad8\u5174\u3002\u81f3\u4e8e\u53bb\u4ec0\u4e48\u5730\u65b9\uff0c\u743c\u6069\u521d\u6b65\u8003\u8651\u662f\u8ff7\u65af\u5353\u8bfa\uff0c\u90a3\u91cc\u539f\u672c\u662f\u7cbe\u7075\u738b\u56fd\uff0c\u98ce\u666f\u79c0\u4e3d\uff0c\u8ddd\u79bb\u9634\u9b42\u57ce\u53c8\u4e0d\u7b97\u5f88\u8fdc\uff0c\u6885\u83f2\u65af\u4e5f\u8bf4\u8981\u56de\u8ff7\u65af\u5353\u8bfa\u89c1\u5927\u4e3b\u6559\uff0c\u6b63\u597d\u4f1a\u5408\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u73ca\u5609\u9047\u4e0a\u6885\u83f2\u65af\u2026\u2026\u4e3a\u4ec0\u4e48\u60f3\u8d77\u8fd9\u526f\u573a\u666f\uff0c\u5fc3\u4e2d\u6d8c\u8d77\u7684\u4e0d\u662f\u5de6\u62e5\u53f3\u62b1\u7684\u9999\u8273\u751c\u871c\uff0c\u800c\u662f\u4e16\u754c\u672b\u65e5\u5c06\u81f3\u7684\u60f6\u6050\u4e0d\u5b89\uff0c\u8fde\u80cc\u540e\u90fd\u53d8\u5f97\u6709\u70b9\u51c9\u98d5\u98d5\u8d77\u6765\u2026\u2026</p><p>\u8fd9\u4e00\u65e5\uff0c\u65e9\u9910\u4e4b\u540e\uff0c\u743c\u6069\u8981\u9001\u73ca\u5609\u53bb\u5b66\u6821\u3002\u73ca\u5609\u539f\u672c\u8bf4\u4e0d\u7528\uff0c\u4f46\u89c1\u743c\u6069\u575a\u6301\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u59d0\u5f1f\u4fe9\u4e00\u8def\u95f2\u8c08\uff0c\u5230\u4e86\u5b66\u9662\u9644\u8fd1\uff0c\u8fdc\u8fdc\u5df2\u7ecf\u671b\u89c1\u6b63\u95e8\uff0c\u73ca\u5609\u6709\u4e9b\u7f9e\u6da9\uff0c\u4e0d\u597d\u610f\u601d\u518d\u8ba9\u743c\u6069\u9001\u5979\u8fdb\u53bb\uff0c\u50ac\u4ed6\u56de\u5bb6\u3002</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u662f\u4f60\u5f1f\u5f1f\uff0c\u9001\u59d0\u59d0\u4e0a\u5b66\uff0c\u7406\u6240\u5f53\u7136\uff0c\u5929\u7ecf\u5730\u4e49\u3002\u201d</p><p>\u201c\u4e16\u754c\u4e0a\u6709\u54ea\u4e2a\u5f1f\u5f1f\u50cf\u4f60\u8fd9\u6837\u7684\uff1f\u201d\u73ca\u5609\u7b11\u76c8\u76c8\u5730\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u63fd\u7740\u59d0\u59d0\u7684\u8170\uff0c\u628a\u59d0\u59d0\u62b1\u5728\u6000\u91cc\uff0c\u5c31\u5dee\u6ca1\u4eb2\u4e0a\u6765\u4e86\u3002\u201d</p><p>\u201c\u90a3\u8ba9\u6211\u4eb2\u4e00\u53e3\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u4f1a\u88ab\u4eba\u770b\u89c1\u7684\uff0c\u56de\u5bb6\u518d\u8ba9\u4f60\u2026\u2026\u201d</p><p>\u743c\u6069\u6beb\u4e0d\u7406\u776c\uff0c\u76f4\u63a5\u543b\u4e0a\u5979\u7684\u6a31\u5507\uff0c\u5c01\u4f4f\u4e86\u540e\u9762\u7684\u8bdd\uff0c\u73ca\u5609\u7565\u7565\u6323\u624e\uff0c\u968f\u5373\u4e5f\u5c31\u653e\u5f03\u62b5\u6297\uff0c\u4efb\u4ed6\u8f7b\u8584\u3002\u743c\u6069\u542e\u5438\u7740\u59d0\u59d0\u7684\u67d4\u6ed1\u820c\u5c16\uff0c\u54c1\u5c1d\u7740\u5c11\u5973\u53e3\u4e2d\u9999\u751c\u6d25\u6db2\uff0c\u8fc7\u4e86\u534a\u664c\u65b9\u624d\u653e\u5f00\u3002\u201c\u59d0\u59d0\u8138\u771f\u7ea2\u3002\u201d\u4ed6\u53d6\u7b11\u3002</p><p>\u201c\u8ba8\u538c\u5566\u3002\u201d</p><p>\u73ca\u5609\u6325\u624b\u544a\u522b\uff0c\u8d70\u8fdb\u5b66\u6821\uff0c\u743c\u6069\u76ee\u89c6\u5979\u7684\u5029\u5f71\u6d88\u5931\uff0c\u8f6c\u8eab\u56de\u5bb6\u3002\u8d70\u4e86\u7247\u523b\uff0c\u5927\u7ea6\u56db\u4e94\u5206\u949f\uff0c\u4ed6\u8f6c\u8fc7\u4e00\u4e2a\u8857\u9053\u62d0\u89d2\uff0c\u6070\u597d\u6709\u4e2a\u4eba\u4e5f\u8fce\u9762\u800c\u6765\uff0c\u9669\u4e9b\u649e\u4e0a\u3002\u4e24\u4eba\u90fd\u731d\u4e0d\u53ca\u9632\uff0c\u540c\u65f6\u9000\u540e\u4e24\u6b65\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u5404\u81ea\u6709\u4e9b\u60ca\u8bb6\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\uff01\u201d</p><p>\u201c\u5e93\u80af\uff01\u201d</p><p>\u867d\u7136\u743c\u6069\u5df2\u7ecf\u6253\u5b9a\u4e3b\u610f\uff0c\u8981\u5411\u5e93\u80af\u63d0\u51fa\u51b3\u6597\uff0c\u4e89\u593a\u8299\u8389\u5a05\uff0c\u4f46\u56e0\u4e3a\u6ca1\u60f3\u5230\u4f1a\u5728\u8fd9\u79cd\u65f6\u5019\u7a81\u7136\u906d\u9047\uff0c\u4e00\u65f6\u95f4\u53cd\u800c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\u3002\u4ed6\u8e0c\u8e87\u7740\uff0c\u8003\u8651\u662f\u62e9\u65e5\u4e0d\u5982\u649e\u65e5\uff0c\u7d22\u6027\u5c31\u63d0\u51fa\u51b3\u6597\uff0c\u8fd8\u662f\u5ba2\u6c14\u5730\u6253\u4e2a\u62db\u547c\u79bb\u5f00\uff0c\u4e0b\u6b21\u518d\u8bf4\u3002\u6b63\u72b9\u8c6b\u95f4\uff0c\u5e93\u80af\u5012\u5148\u5f00\u53e3\u4e86\u3002</p><p>\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\uff0c\u201d\u4ed6\u6162\u6162\u8bf4\uff0c\u201c\u6211\uff0c\u96f7\u66fc\u745f\xb7\u5e93\u80af\uff0c\u4e3a\u8299\u8389\u5a05\xb7\u83ab\u5c3c\u5361\u5c0f\u59d0\uff0c\u5411\u4f60\u63d0\u51fa\u51b3\u6597\u3002\u201d</p><p>\u743c\u6069\u6014\u4f4f\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u8981\u89e3\u9664\u8299\u8389\u5a05\u7684\u5a5a\u7ea6\uff0c\u552f\u4e00\u7684\u9014\u7ecf\u5c31\u662f\u743c\u6069\u6309\u7167\u53e4\u8001\u98ce\u4fd7\u5411\u5e93\u80af\u63d0\u51fa\u51b3\u6597\uff0c\u51fb\u8d25\u5bf9\u624b\uff0c\u4ece\u800c\u903c\u8feb\u5bf9\u65b9\u653e\u5f03\u3002\u8fd9\u65b9\u6cd5\u7684\u524d\u63d0\u6761\u4ef6\u4e4b\u4e00\uff0c\u662f\u5e93\u80af\u8981\u613f\u610f\u63a5\u53d7\u6311\u6218\uff0c\u5426\u5219\u514d\u8c08\u3002\u8299\u8389\u5a05\u8ba4\u4e3a\u5e93\u80af\u4f1a\u613f\u610f\uff0c\u5979\u8bf4\u5f97\u5f88\u80af\u5b9a\uff0c\u4f46\u743c\u6069\u4f9d\u65e7\u8fd8\u662f\u6709\u4e9b\u6000\u7591\u7684\u3002</p><p>\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u4ee5\u5df1\u5ea6\u4eba\u7684\u8bdd\uff0c\u5982\u679c\u6362\u4e86\u743c\u6069\u662f\u5e93\u80af\uff0c\u4ed6\u80af\u5b9a\u4e0d\u63a5\u53d7\u3002\u73b0\u5728\u7684\u60c5\u51b5\u662f\uff1a\u81ea\u5df1\u6709\u4e00\u4e2a\u6f02\u4eae\u7684\u672a\u5a5a\u59bb\uff0c\u9a6c\u4e0a\u5c31\u8981\u6210\u5a5a\uff0c\u83ab\u540d\u5176\u5999\u8df3\u51fa\u4e00\u4e2a\u4eba\u6765\u62a2\uff0c\u8981\u6c42\u51b3\u6597\u3002\u81ea\u5df1\u5982\u679c\u8d62\u4e86\uff0c\u5e76\u6ca1\u6709\u534a\u70b9\u989d\u5916\u7684\u597d\u5904\uff0c\u5982\u679c\u8f93\u4e86\u5c31\u662f\u635f\u5931\u2014\u2014\u8fd9\u79cd\u4e8f\u672c\u7684\u4e8b\u60c5\uff0c\u767d\u75f4\u624d\u4f1a\u505a\uff0c\u800c\u5e93\u80af\u600e\u4e48\u770b\u4e5f\u4e0d\u50cf\u662f\u4e2a\u767d\u75f4\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\uff0c\u743c\u6069\u731c\u5f97\u6ca1\u9519\u2014\u2014\u5e93\u80af\u786e\u5b9e\u4e0d\u662f\u767d\u75f4\uff0c\u4ed6\u662f\u75af\u5b50\uff1b\u5e93\u80af\u4e5f\u786e\u5b9e\u6ca1\u6709\u63a5\u53d7\u743c\u6069\u7684\u6311\u6218\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u62a2\u5148\u63d0\u51fa\u4e86\u51b3\u6597\u3002</p><p>\u5cf0\u56de\u8def\u8f6c\uff0c\u51fa\u4eba\u610f\u6599\uff0c\u4fbf\u662f\u743c\u6069\u4e5f\u6014\u4f4f\u4e86\uff0c\u4e0d\u77e5\u9053\u5bf9\u65b9\u5230\u5e95\u5728\u641e\u4ec0\u4e48\u540d\u5802\u3002\u201c\u4f60\u662f\u8bf4\uff0c\u4e3a\u8299\u8389\u5a05\u548c\u6211\u51b3\u6597\uff1f\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u201c\u90a3\u5982\u679c\u4f60\u8f93\u4e86\u5462\uff1f\u201d</p><p>\u201c\u90a3\u4e48\u6309\u7167\u89c4\u77e9\uff0c\u6211\u7684\u5bb6\u65cf\u5c31\u4f1a\u653e\u5f03\u5a5a\u7ea6\uff0c\u201d\u5e93\u80af\u51b7\u9759\u5730\u8bf4\uff0c\u201c\u8fd9\u96be\u9053\u4e0d\u662f\u4f60\u6240\u671f\u671b\u7684\u5417\uff1f\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e48\u6211\u63a5\u53d7\u4f60\u7684\u6311\u6218\u3002\u201d</p><p>\u201c\u65f6\u95f4\u4fbf\u662f\u660e\u65e5\u4e0a\u5348\uff0c\u5730\u70b9\u662f\u2018\u591c\u5e55\u2019\uff0c\u6ca1\u95ee\u9898\u5427\u3002\u201d</p><p>\u201c\u6ca1\u95ee\u9898\u3002\u201d</p><p>\u5e93\u80af\u70b9\u70b9\u5934\uff0c\u5f84\u76f4\u79bb\u53bb\uff0c\u743c\u6069\u7ad9\u7acb\u7247\u523b\uff0c\u9887\u89c9\u8fd9\u4e8b\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u4f46\u4e5f\u6ca1\u6709\u591a\u60f3\uff0c\u53cd\u6b63\u6700\u540e\u7ed3\u679c\u4e5f\u6ca1\u5dee\uff0c\u90a3\u5c31\u662f\u4e86\u3002\u81f3\u4e8e\u5e93\u80af\u662f\u53d1\u75af\u4e5f\u597d\uff0c\u72af\u50bb\u4e5f\u7f62\uff0c\u548c\u81ea\u5df1\u65e0\u5173\u3002</p><p>\u56de\u5230\u5bb6\u4e2d\uff0c\u53d6\u51fa\u9b54\u6cd5\u4e66\u7814\u4e60\u4e86\u4e24\u4e2a\u5c0f\u65f6\uff0c\u8bd5\u7740\u638c\u63e1\u4e00\u4e2a\u65b0\u6cd5\u672f\uff0c\u4f46\u8fdb\u5c55\u4e0d\u5feb\u3002\u770b\u770b\u65f6\u95f4\uff0c\u8ddd\u79bb\u5348\u9910\u8fd8\u65e9\uff0c\u968f\u624b\u62ff\u8fc7\u73ca\u5609\u7559\u5728\u5bb6\u4e2d\u7684\u591c\u98ce\u4e4b\u7b1b\u6765\uff0c\u8bd5\u7740\u5439\u4e86\u8d77\u6765\u3002</p><p>\u743c\u6069\u662f\u5b66\u8fc7\u957f\u7b1b\u7684\uff0c\u5426\u5219\u5728\u963f\u65af\u5361\u7279\u62c9\u4e5f\u4e0d\u4f1a\u7279\u522b\u6311\u9009\u957f\u7b1b\u7ed9\u73ca\u5609\u505a\u793c\u7269\uff0c\u4f46\u4ed6\u81ea\u4ece\u6765\u5230\u8fd9\u4e2a\u4e16\u754c\uff0c\u5df2\u7ecf\u5341\u51e0\u5e74\u6ca1\u78b0\u8fc7\uff0c\u81ea\u7136\u751f\u758f\u4e86\u5f88\u591a\u3002\u52c9\u52c9\u5f3a\u5f3a\u5439\u5b8c\u4e00\u66f2\uff0c\u81ea\u5632\u4e00\u7b11\uff0c\u6b63\u51c6\u5907\u653e\u4e0b\uff0c\u8299\u857e\u72c4\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u548c\u8299\u857e\u72c4\u7684\u5173\u7cfb\uff0c\u73ca\u5609\u5176\u5b9e\u5df2\u7ecf\u7b97\u662f\u9ed8\u8ba4\u63a5\u53d7\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u8299\u857e\u72c4\u6e10\u6e10\u4e5f\u80c6\u5927\u8d77\u6765\uff0c\u6562\u6765\u4e3b\u52a8\u627e\u743c\u6069\u3002\u4e24\u4eba\u6e29\u5b58\u7247\u523b\uff0c\u8299\u857e\u72c4\u5df2\u7ecf\u662f\u8863\u88f3\u4e0d\u6574\uff0c\u8138\u98de\u7ea2\u971e\uff0c\u53c8\u6709\u4e9b\u5bb3\u7f9e\uff0c\u6b63\u60c5\u4e0d\u81ea\u7981\u95f4\uff0c\u770b\u89c1\u65c1\u8fb9\u653e\u7740\u7684\u94f6\u8272\u957f\u7b1b\uff0c\u4e0d\u89c9\u5947\u602a\uff0c\u201c\u743c\u6069\uff0c\u4f60\u4e0d\u662f\u8981\u628a\u5b83\u9001\u7ed9\u73ca\u5609\u59d0\u59d0\u5417\uff1f\u201d</p><p>\u201c\u9001\u4e86\u554a\u3002\u201d</p><p>\u201c\u90a3\u73ca\u5609\u59d0\u59d0\u53bb\u5b66\u6821\u600e\u4e48\u6ca1\u5e26\u4e0a\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u5979\u4eec\u5b66\u6821\u91cc\u7edf\u4e00\u7ed9\u5b66\u751f\u53d1\u4e86\u957f\u7b1b\uff0c\u8bf4\u662f\u4e0d\u5141\u8bb8\u81ea\u5df1\u5e26\u4e50\u5668\uff0c\u6240\u4ee5\u7559\u5728\u5bb6\u91cc\u3002\u201d</p><p>\u8299\u857e\u72c4\u6014\u4e86\u6014\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u6700\u7ec8\u6ca1\u8bf4\u51fa\u53e3\uff0c\u4f46\u743c\u6069\u5df2\u7ecf\u6ce8\u610f\u5230\u4e86\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u95ee\uff0c\u201c\u4f60\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u2026\u2026\u6ca1\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u743c\u6069\u76b1\u8d77\u7709\u5934\uff0c\u201c\u8bf4\u3002\u201d</p><p>\u8299\u857e\u72c4\u89c1\u4ed6\u4e0d\u60a6\uff0c\u987f\u65f6\u4fbf\u80c6\u602f\u4e86\uff0c\u72b9\u8c6b\u534a\u664c\uff0c\u7ec8\u4e8e\u8fd8\u662f\u8bf4\u4e86\u51fa\u6765\uff0c\u201c\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u53ea\u4e0d\u8fc7\uff0c\u8299\u8389\u5a05\u5979\u4ee5\u524d\u4e5f\u662f\u5728\u97f3\u4e50\u5b66\u9662\u91cc\u4e0a\u8fc7\u5b66\u7684\uff0c\u6211\u8bb0\u5f97\u5979\u5f53\u65f6\u62b1\u6028\u8fc7\uff0c\u8bf4\u5b66\u6821\u4e0d\u7ed9\u5b66\u751f\u7edf\u4e00\u914d\u53d1\u4e50\u5668\uff0c\u9700\u8981\u81ea\u5df1\u51c6\u5907\u2026\u2026\u201d</p></article>"}}}]);
