(window.webpackJsonp=window.webpackJsonp||[]).push([[421],{444:function(p){p.exports={chapterTitle:null,title:"\u7b2c421\u7ae0 \u6c5f\u6d41\u6697\u6d8c",content:"<article><h2>\u7b2c421\u7ae0 \u6c5f\u6d41\u6697\u6d8c</h2><p>\u3000\u3000\u8bdd\u5323\u5b50\u6253\u5f00\uff0c\u5f20\u606a\u5012\u4e0d\u518d\u541d\u556c\u4ed6\u5728\u5e02\u573a\u8425\u9500\u65b9\u9762\u8d85\u8d8a\u56fd\u4eba\u5341\u6570\u5e74\u7684\u4e13\u4e1a\u77e5\u8bc6\uff0c\u8fd9\u4e9b\u77e5\u8bc6\u7b97\u4e0d\u4e0a\u5341\u5206\u7684\u9ad8\u6df1\uff0c\u6b27\u7f8e\u7684\u8425\u9500\u5927\u5e08\u5747\u6709\u8bba\u8ff0\uff0c\u4f46\u662f\u6b27\u7f8e\u7684\u8425\u9500\u7406\u5ff5\u4e0e\u56fd\u5185\u5e02\u573a\u7684\u5b9e\u9645\u73b0\u72b6\u76f8\u878d\u5408\u7684\u8fc7\u7a0b\u662f\u6f2b\u957f\u800c\u5145\u6ee1\u8840\u8165\u6559\u8bad\u7684\uff0c\u56fd\u5185\u7684\u8425\u9500\u7406\u5ff5\u7ecf\u8fc7\u4e5d\u5341\u5e74\u4ee3\u672b\u671f\u7684\u5927\u53cd\u601d\uff0c\u4e00\u76f4\u62d6\u52302000\u5e74\u4e4b\u540e\u624d\u9010\u6e10\u6210\u719f\u8d77\u6765\u3002</p><p>\u3000\u3000\u5728\u65c1\u4eba\u770b\u6765\uff0c\u5f20\u606a\u65e2\u7136\u80fd\u5728\u4ed6\u5341\u516d\u5c81\u7684\u65f6\u5019\u521b\u7acb\u7231\u8fbe\u7535\u5b50\uff0c\u90a3\u4ed6\u5728\u5e02\u573a\u8425\u9500\u65b9\u9762\u9ad8\u77bb\u8fdc\u77a9\u7684\u89c1\u89e3\u5df2\u4e0d\u80fd\u8ba9\u4eba\u5927\u60ca\u5c0f\u602a\u4e86\uff1a\u505a\u975e\u5e38\u4e8b\uff0c\u5fc5\u6709\u975e\u5e38\u4e4b\u5904\uff0c\u4ed6\u82e5\u6ca1\u6709\u5f02\u4e8e\u5e38\u4eba\u7684\u5546\u4e1a\u5929\u8d4b\uff0c\u8981\u505a\u6210\u90a3\u4e9b\u4e8b\u624d\u4f1a\u53eb\u4eba\u4e0d\u53ef\u601d\u8bae\u5462\u3002</p><p>\u3000\u3000\u65b0\u829c\u7684\u7535\u529b\u4f9b\u5e94\u5f88\u4e0d\u7a33\u5b9a\uff0c\u5403\u8fc7\u665a\u996d\u8fd8\u505c\u4e86\u534a\u5c0f\u65f6\u7684\u7535\uff0c\u5012\u6709\u771f\u6b63\u79c9\u70db\u591c\u8c08\u7684\u4e50\u8da3\uff0c\u671f\u95f4\u5c0f\u533a\u7269\u4e1a\u7ecf\u7406\u8fd8\u8dd1\u8fc7\u6765\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u591c\u91cc\uff0c\u5f20\u606a\u4e0e\u5b54\u957f\u6cb3\u3001\u79e6\u971c\u534e\u4ed6\u4eec\u6cdb\u6cdb\u800c\u8bba\u7684\u8ba8\u8bba\u5f53\u524d\u56fd\u5185\u7684\u767d\u9152\u5e02\u573a\uff0c\u8bdd\u9898\u4e00\u76f4\u6301\u7eed\u5230\u51cc\u6668\u624d\u6563\uff0c\u7b2c\u4e8c\u5929\u5c31\u6709\u9488\u5bf9\u6027\u7684\u8ba8\u8bba\u4e91\u6c60\u9152\u4e1a\u7684\u5e02\u573a\u6269\u5f20\u7b56\u7565\uff1b\u5bf9\u6b64\uff0c\u5b54\u957f\u6cb3\u4e5f\u6709\u4ed6\u81ea\u5df1\u72ec\u5230\u7684\u601d\u8003\u3002</p><p>\u3000\u3000\u5728\u56fd\u5185\u5e02\u573a\uff0c\u6709\u4e00\u6761\u5c61\u8bd5\u4e0d\u723d\u7684\u7ade\u4e89\u6cd5\u5219\uff0c\u90a3\u5c31\u662f\u201c\u5f97\u4e09\u5317\u8005\u6210\u8bf8\u4faf\uff0c\u5f97\u4eac\u6caa\u8005\u5f97\u5929\u4e0b\u201d\uff0c\u4eac\u6caa\u4e24\u5730\u7684\u91cd\u8981\u6027\u81ea\u4e0d\u7528\u8bf4\uff0c\u5305\u62ec\u4e1c\u5317\u3001\u534e\u5317\u3001\u897f\u5317\u4e09\u5730\u7684\u201c\u4e09\u5317\u201d\u5e02\u573a\u5e45\u5458\u8fbd\u9614\u3001\u4eba\u53e3\u4f17\u591a\uff0c\u6c11\u4f17\u6027\u60c5\u6897\u76f4\uff0c\u6d88\u8d39\u5fc3\u6001\u4e0d\u6210\u719f\uff0c\u6613\u4e8e\u88ab\u5e7f\u544a\u6d88\u606f\u6240\u63a7\u5236\uff0c\u6700\u9002\u5408\u521b\u4e1a\u9636\u6bb5\u7684\u4ea7\u54c1\u51b2\u8fdb\u53bb\u6253\u5e02\u573a\uff0c\u4e5f\u4e0d\u6015\u4f1a\u906d\u5230\u4e00\u7ebf\u54c1\u724c\u7684\u5f3a\u70c8\u72d9\u51fb\uff0c\u5f88\u591a\u5728\u56fd\u5185\u5e02\u573a\u83b7\u5f97\u6210\u529f\u7684\u6c11\u65cf\u54c1\u724c\u90fd\u662f\u4ece\u201c\u4e09\u5317\u201d\u5e02\u573a\u5f00\u59cb\u7684\u3002\u4e91\u6c60\u9152\u4e1a\u8d70\u5411\u5168\u56fd\u7684\u7b2c\u4e00\u6b65\uff0c\u5c31\u9009\u5728\u534e\u5317\uff0c\u534e\u5317\u5730\u533a\u5929\u6c14\u5bd2\u51b7\u3001\u559d\u9152\u4eba\u53e3\u4f17\u591a\uff0c\u4e5f\u662f\u5bf9\u767d\u9152\u884c\u4e1a\u6709\u5229\u7684\u4e00\u4e2a\u91cd\u8981\u56e0\u7d20\u3002</p><p>\u3000\u3000\u5b54\u957f\u6cb3\u8fc7\u4e9b\u5929\u5c31\u53bb\u6cb3\u5317\u53bb\u6253\u5e02\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u660e\u5929\u8981\u4e58\u98de\u673a\u8d76\u5f80\u9999\u6e2f\uff0c\u5f53\u591c\u5c31\u8981\u79bb\u5f00\u65b0\u829c\uff0c\u6881\u683c\u73cd\u603b\u4e0d\u653e\u5fc3\u4ed6\u4eec\u591c\u91cc\u884c\u8f66\uff0c\u575a\u6301\u8ba9\u4ed6\u4eec\u505a\u591c\u73ed\u7684\u6c5f\u8f6e\u53bb\u7701\u57ce\u3002\u508d\u665a\u51fa\u53d1\u7684\u5ba2\u8f6e\uff0c\u987a\u6c34\u800c\u4e0b\uff0c\u660e\u65e9\u51cc\u6668\u80fd\u5230\u7701\u57ce\uff0c\u8fd9\u6837\uff0c\u6768\u4e91\u4e0e\u5218\u660e\u8f89\u8fd8\u53ef\u4ee5\u7ee7\u7eed\u968f\u884c\uff0c\u4e00\u8def\u987a\u6c34\u800c\u884c\u76f4\u5230\u6d77\u5dde\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u7279\u7b49\u8231\u7684\u4e58\u5ba2\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u3001\u6768\u4e91\u3001\u5218\u660e\u8f89\u4ed6\u4eec\u6bd4\u5176\u4ed6\u4e58\u5ba2\u5148\u4e0a\u8239\u3002\u4ece\u65b0\u829c\u8d70\u6ca1\u6709\u4ec0\u4e48\u8c6a\u534e\u6e38\u8f6e\u53ef\u4e58\uff0c\u5ba2\u8f6e\u7684\u9876\u5c42\u51e0\u5957\u5ba2\u623f\u52c9\u5f3a\u53ef\u4ee5\u79f0\u5f97\u4e0a\u662f\u7279\u7b49\u8231\uff0c\u901a\u9053\u7684\u4e24\u4fa7\u5206\u5e03\u7740\u4e00\u4e9b\u8231\u5ba4\u7684\u5c0f\u95e8\uff0c\u8d70\u8fdb\u53bb\u4e5f\u4ec5\u4ec5\u662f\u582a\u6bd4\u4e00\u822c\u9152\u5e97\u7684\u6807\u51c6\u95f4\u800c\u5df2\uff0c\u4e0d\u8fc7\u6709\u53ef\u4f9b\u6d17\u6d74\u7684\u536b\u751f\u95f4\u5c31\u8ba9\u5f20\u606a\u5f88\u6ee1\u610f\u4e86\u3002</p><p>\u3000\u3000\u8d70\u65f6\u4e5f\u6ca1\u6709\u7279\u610f\u7684\u8ddf\u7fdf\u4e39\u9752\u8bf4\u4e00\u58f0\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u5e2e\u5fd9\u5c06\u884c\u674e\u7bb1\u653e\u4e0b\uff0c\u62ff\u51fa\u5728\u4e5d\u516d\u5e74\u8fd8\u5f88\u5148\u8fdb\u3001\u5374\u8ba9\u4ed6\u5f02\u5e38\u607c\u706b\u2014\u2014\u9664\u4e86\u6d4f\u89c8\u3001\u4e66\u5199\u6587\u6863\u3001\u542c\u542c\u97f3\u4e50\u3001\u5076\u5c14\u73a9\u4e00\u628a\u626b\u96f7\u6e38\u620f\uff0c\u4f3c\u4e4e\u4e0d\u80fd\u518d\u505a\u5176\u4ed6\u4e8b\u60c5\uff0c\u552e\u4ef7\u8fd8\u66b4\u8d35\uff0c\u5f20\u606a\u5f02\u5e38\u6000\u5ff5\u5341\u51e0\u5e74\u540e\u552e\u4ef7\u624d\u4e09\u56db\u5343\uff0c\u6027\u80fd\u5374\u4e0d\u77e5\u63d0\u9ad8\u4e86\u591a\u5c11\u500d\u7684\u7b14\u8bb0\u672c\u2014\u2014\u7684\u7b14\u8bb0\u672c\u7535\u8111\uff0c\u624b\u673a\u54cd\u4e86\uff0c\u5f20\u606a\u62ff\u8d77\u624b\u673a\uff0c\u5085\u4fca\u9002\u65f6\u8d70\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u662f\u7fdf\u4e39\u9752\u7684\u7535\u8bdd\u3002\u5f20\u606a\u8d70\u5230\u7a97\u524d\uff0c\u665a\u971e\u7167\u5728\u6c5f\u9762\u4e0a\uff0c\u6c5f\u6c34\u91cc\u91d1\u5149\u4e07\u70b9\u3001\u71a0\u71a0\u593a\u76ee\uff0c\u90a3\u4e9b\u85cf\u91d1\u62e5\u94f6\u7684\u6c5f\u6d6a\uff0c\u4e00\u7c07\u7c07\u7684\u5f80\u5ba2\u8f6e\u8fd9\u8fb9\u6d8c\u6765\uff0c\u4f3c\u4e4e\u80fd\u611f\u89c9\u5230\u6c5f\u6d6a\u4f20\u6765\u7684\u6d8c\u52a8\u3002</p><p>\u3000\u3000\u201c\u8d70\uff0c\u600e\u4e48\u4e0d\u8bf4\u4e00\u58f0\uff0c\u8fd8\u6253\u7b97\u8bf7\u4f60\u5403\u665a\u996d\u5462\uff1f\u201d</p><p>\u3000\u3000\u5229\u7528\u8a00\u8bed\u4e0a\u7684\u4e00\u4e9b\u6697\u793a\u6765\u62c9\u8fd1\u53cc\u65b9\u7684\u8ddd\u79bb\uff0c\u4f3c\u4e4e\u662f\u5973\u6027\u7684\u7279\u957f\u3002</p><p>\u3000\u3000\u60f3\u8d77\u7fdf\u4e39\u9752\u8273\u4e3d\u6027\u611f\u7684\u8ff7\u4eba\u5bb9\u989c\uff0c\u5f20\u606a\u9732\u51fa\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e34\u65f6\u51b3\u5b9a\u5750\u8239\u8d70\uff0c\u4f60\u8bf4\u665a\u996d\u7684\u4e8b\u554a\uff0c\u4e0d\u5750\u8239\u8d70\uff0c\u6211\u539f\u672c\u6253\u7b97\u8bf7\u7fdf\u59d0\u5403\u8fc7\u665a\u996d\u518d\u5750\u8f66\u79bb\u5f00\u65b0\u829c\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u7fdf\u4e39\u9752\u5728\u624b\u673a\u90a3\u5934\u7684\u58f0\u97f3\u6709\u7740\u5c0f\u5973\u5b69\u5b50\u7684\u96c0\u8dc3\u3002</p><p>\u3000\u3000\u5373\u4f7f\u7fdf\u4e39\u9752\u662f\u53ef\u4ee5\u4fe1\u4efb\u7684\uff0c\u4f46\u662f\u5979\u957f\u671f\u5468\u8f6c\u4e8e\u5404\u8272\u7537\u4eba\u4e4b\u95f4\uff0c\u8c01\u6653\u5f97\u5979\u4f1a\u4e0d\u4f1a\u4e60\u60ef\u6027\u7684\u5bf9\u81ea\u5df1\u4f7f\u4e9b\u5c0f\u4f0e\u4fe9\uff0c\u5bf9\u4e8e\u5979\u58f0\u97f3\u91cc\u7684\u4e9b\u8bb8\u5174\u594b\uff0c\u5f20\u606a\u5012\u6ca1\u6709\u592a\u5f53\u56de\u4e8b\uff0c\u8fd9\u4e2a\u4e16\u754c\u6700\u5927\u6700\u60e8\u70c8\u7684\u6218\u573a\uff0c\u4e0d\u5728\u5546\u573a\uff0c\u4e5f\u4e0d\u5728\u5b98\u573a\uff0c\u800c\u662f\u5728\u7537\u4eba\u4e0e\u5973\u4eba\u4e4b\u95f4\uff0c\u5f80\u5f80\u7f8e\u4e3d\u7684\u5973\u4eba\u5371\u9669\u7a0b\u5ea6\u53c8\u7279\u522b\u7684\u9ad8\u3002</p><p>\u3000\u3000\u5f20\u606a\u6d45\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u8bf4\u771f\u5c31\u662f\u771f\u7684\uff0c\u96be\u9053\u6211\u8fd8\u80fd\u8d56\u4f60\u4e00\u987f\u665a\u996d\u4e0d\u6210\uff1f\u4e0b\u56de\u5230\u65b0\u829c\u8bf7\u4f60\u5403\u996d\u5427\uff0c\u5730\u65b9\u4f60\u6765\u6311\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u6211\u4eec\u5230\u65b0\u829c\u6765\uff0c\u4f3c\u4e4e\u6ca1\u591a\u5927\u7528\u5904\u2026\u2026\u201d\u5218\u660e\u8f89\u4e0e\u6768\u4e91\u4f4f\u540c\u4e00\u4e2a\u7279\u7b49\u8231\uff0c\u6309\u8bf4\u5f20\u606a\u62c9\u5218\u660e\u8f89\u4e0e\u6768\u4e91\u5230\u65b0\u829c\u6765\u662f\u7ed9\u4e91\u6c60\u9152\u5382\u51fa\u8c0b\u732e\u7b56\uff0c\u9664\u4e86\u8ddf\u4e91\u6c60\u9152\u4e1a\u5e02\u573a\u90e8\u7684\u5458\u5de5\u8fdb\u884c\u4ea4\u6d41\u3001\u6768\u4e91\u7ed9\u4e91\u6c60\u9152\u4e1a\u7ba1\u7406\u5c42\u4e0e\u5e02\u573a\u90e8\u5458\u5de5\u4e0a\u4e86\u4e00\u5802\u6cdb\u6cdb\u800c\u8bba\u7684\u5e02\u573a\u8425\u9500\u8bfe\u4e4b\u5916\uff0c\u5728\u4e91\u6c60\u9152\u4e1a\u672a\u6765\u7684\u53d1\u5c55\u65b9\u5411\u4e0a\uff0c\u5e76\u6ca1\u6709\u7ed9\u51fa\u592a\u591a\u7684\u5efa\u8bae\uff0c\u5173\u952e\u662f\u5f20\u606a\u7684\u8868\u73b0\u8fc7\u4e8e\u51fa\u8272\u4e86\u3002</p><p>\u3000\u3000\u6768\u4e91\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u5f88\u5c11\u6709\u673a\u4f1a\u542c\u606a\u5c11\u8fd9\u4e48\u5356\u5f04\u4e86\uff0c\u4e0d\u7b80\u5355\u554a\uff0c\u8fd9\u8d9f\u7edd\u5bf9\u503c\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u8bf4\u8bdd\u5012\u4e5f\u6709\u524d\u540e\u4e0d\u4e00\u81f4\u7684\u5730\u65b9\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u5728\u60f3\u606a\u5c11\u5728\u706b\u8f66\u4e0a\u8bf4\u7684\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u5218\u660e\u8f89\u7684\u5fc3\u60c5\u5374\u6ca1\u6709\u6768\u4e91\u8fd9\u822c\u8f7b\u677e\uff0c\u5728\u6765\u65b0\u829c\u7684\u706b\u8f66\u4e0a\uff0c\u5218\u660e\u8f89\u80fd\u9690\u9690\u7ea6\u7ea6\u7684\u611f\u89c9\u5230\u5f20\u606a\u7684\u8bdd\u91cc\u6709\u8b66\u9192\u7684\u610f\u5473\uff0c\u8bf4\u4e00\u4e9b\u9700\u8981\u63a7\u5236\u975e\u7406\u6027\u5e02\u573a\u51b2\u52a8\u7684\u8bdd\u9898\uff0c\u5230\u65b0\u829c\u5374\u4eb2\u81ea\u6307\u70b9\u4e91\u6c60\u9152\u4e1a\u8be5\u5982\u4f55\u66f4\u597d\u7684\u53bb\u6253\u62fc\u5e02\u573a\uff0c\u524d\u540e\u8a00\u8bba\u591a\u5c11\u6709\u4e9b\u4e0d\u4e00\u81f4\u7684\u5730\u65b9\uff0c\u5218\u660e\u8f89\u9887\u4e3a\u56f0\u60d1\u3002</p><p>\u3000\u3000\u5218\u660e\u8000\u6ce8\u89c6\u7740\u8237\u7a97\u4e4b\u5916\u7684\u6c5f\u6d6a\uff0c\u4e0d\u77e5\u9053\u5f20\u606a\u6b64\u65f6\u4e5f\u5728\u51dd\u89c6\u665a\u971e\u4e0b\u7684\u6c5f\u6c34\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5ba2\u8f6e\u51cc\u6668\u4e94\u70b9\u9760\u4e0a\u5efa\u90ba\u6e2f\u7684\u56db\u53f7\u7801\u5934\uff0c\u8fdc\u9645\u7684\u5929\u7a7a\u5806\u7740\u9ed1\u538b\u538b\u7684\u4e4c\u4e91\uff0c\u53ea\u6709\u4e9b\u5fae\u89c2\u7684\u4eae\u5149\uff0c\u8fd9\u4e2a\u65f6\u95f4\u70b9\u8fd8\u771f\u662f\u75db\u82e6\uff0c\u8fd8\u6709\u56db\u4e2a\u5c0f\u65f6\uff0c\u98de\u673a\u624d\u8d77\u98de\uff0c\u53bb\u54ea\u91cc\u6253\u53d1\u65f6\u95f4\u90fd\u4e0d\u5408\u9002\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5085\u4fca\u4e0b\u8239\u6765\uff0c\u6768\u4e91\u4e0e\u5218\u660e\u8f89\u7ee7\u7eed\u4e58\u8239\uff0c\u4e0b\u5348\u4e09\u56db\u70b9\u949f\u4f1a\u5230\u6d77\u5dde\u3002</p><p>\u3000\u3000\u51fa\u4e86\u6c5f\u6e2f\u7801\u5934\uff0c\u51fa\u79df\u8f66\u5f88\u5c11\uff0c\u505c\u9760\u5728\u7801\u5934\u62c9\u5ba2\u7684\u51e0\u8f86\u51fa\u79df\u8f66\u5df2\u7ecf\u8ba9\u4eba\u6377\u8db3\u5148\u767b\u4e86\uff0c\u8fd8\u6709\u4e00\u8f86\u7834\u65e7\u7684\u4e2d\u5df4\u8f66\u505c\u5728\u8def\u8fb9\u62c9\u5ba2\u3002</p><p>\u3000\u3000\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u5fc3\u77e5\u8fd9\u65f6\u5019\u5750\u8fd9\u79cd\u4e2d\u5df4\u5c31\u7b49\u7740\u7ed9\u5bb0\uff0c\u4f46\u662f\u4e0d\u6653\u5f97\u51e0\u65f6\u4f1a\u6709\u51fa\u79df\u8f66\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u4f3c\u4e4e\u63a5\u4e0b\u6765\u4e24\u4e2a\u5c0f\u65f6\uff0c\u4e5f\u6ca1\u6709\u5ba2\u8f6e\u4f1a\u5728\u8fd9\u4e2a\u7801\u5934\u505c\u9760\u3002</p><p>\u3000\u3000\u624b\u673a\u603b\u662f\u4f1a\u53ca\u65f6\u7684\u54cd\u8d77\uff0c\u5f20\u606a\u62ff\u51fa\u624b\u673a\uff0c\u662f\u5f90\u5b66\u5e73\u5bb6\u7684\u7535\u8bdd\uff0c\u60f3\u4e0d\u5230\u5f90\u5b66\u5e73\u8fd9\u4e2a\u70b9\u5df2\u7ecf\u8d77\u5e8a\u4e86\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u665a\u4e0a\u8ddf\u4f60\u7238\u901a\u8fc7\u7535\u8bdd\uff0c\u5fc3\u60f3\u4f60\u8fd9\u4e2a\u70b9\u5e94\u8be5\u5230\u7801\u5934\u4e86\uff0c\u6211\u8ba9\u5c0f\u5434\u5f00\u8f66\u51fa\u6765\u63a5\u4f60\u4eec\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7528\u9ebb\u70e6\u5c0f\u5434\u5e08\u5085\u4e86\uff0c\u6211\u4eec\u5750\u8f66\u8fc7\u53bb\u2026\u2026\u201d\u5f20\u606a\u8fd9\u4e48\u8bf4\u7740\uff0c\u770b\u89c1\u5f90\u5b66\u5e73\u7684\u53f8\u673a\u5df2\u7ecf\u4ece\u505c\u5728\u6811\u836b\u4e0b\u7684\u8f66\u91cc\u8d70\u8fc7\u6765\uff0c\u5f20\u606a\u6709\u4e9b\u8c26\u7136\u7684\u8bf4\u9053\uff0c\u201c\u6211\u770b\u89c1\u5c0f\u5434\u5e08\u5085\uff0c\u592a\u8ba9\u5f90\u4f2f\u4f2f\u4f60\u8d39\u5fc3\u4e86\uff0c\u6211\u8fd8\u6253\u7b97\u6084\u6084\u7684\u8fdb\u6751\u3001\u968f\u4fbf\u627e\u4e2a\u5730\u65b9\u6253\u53d1\u4e00\u4e0b\u65f6\u95f4\uff0c\u7136\u540e\u6084\u6084\u7684\u51fa\u6751\u5462\u3002\u201d\u8fd9\u4e2a\u65f6\u95f4\u70b9\u4e5f\u6ca1\u6709\u5fc5\u8981\u53bb\u6c5f\u8fb9\u7684\u522b\u5885\uff0c\u4ed6\u5012\u662f\u60f3\u63d0\u524d\u53bb\u673a\u573a\u6253\u53d1\u65f6\u95f4\u7684\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f90\u5b66\u5e73\u5728\u7535\u8bdd\u8f7b\u58f0\u7b11\u4e86\u7b11\uff0c\u7b11\u58f0\u5f88\u8f7b\uff0c\u5927\u6982\u662f\u5728\u5367\u5ba4\u91cc\u6253\u7535\u8bdd\uff0c\u201c\u6211\u8fd9\u5e74\u7eaa\uff0c\u5929\u7a0d\u4e00\u4eae\u5c31\u4f1a\u9192\uff0c\u79bb\u5de5\u4f5c\u65f6\u95f4\u8fd8\u65e9\uff0c\u6211\u4eec\u7237\u4fe9\u603b\u80fd\u627e\u5230\u4e9b\u4e8b\u60c5\u8bf4\u8bf4\uff0c\u8fd8\u662f\u9ebb\u70e6\u4f60\u8fc7\u6765\u4e00\u8d9f\u2026\u2026\u201d</p><p>\u3000\u3000\u65b0\u6885\u82d1\u6b63\u5728\u53bb\u897f\u5357\u90ca\u673a\u573a\u7684\u8def\u4e0a\uff0c\u8f66\u5230\u65b0\u6885\u82d1\uff0c\u5929\u521a\u8499\u8499\u4eae\uff0c\u6e05\u6668\u7684\u98ce\u6709\u4e9b\u51c9\uff0c\u65b0\u6885\u82d1\u91cc\u6709\u4eba\u51fa\u6765\u6668\u8dd1\uff0c\u5f20\u606a\u4fa7\u8fc7\u5934\u65e0\u610f\u770b\u4e86\u4e00\u4e9b\uff0c\u7adf\u662f\u4e07\u5bb6\u7684\u8001\u7237\u5b50\u3001\u7701\u4eba\u5927\u526f\u4e3b\u4efb\u4e07\u5411\u524d\uff0c\u6309\u8bf4\u7325\u4eb5\u6559\u5e08\u6848\u53d1\u751f\u3001\u4e07\u52c7\u8c03\u51fa\u6d77\u5dde\u4e4b\u540e\uff0c\u4e07\u5bb6\u5e94\u8be5\u7b97\u662f\u6c89\u5bc2\u4e86\uff0c\u4f46\u662f\u56fd\u5185\u7684\u5b98\u573a\u7edd\u4e0d\u662f\u53ef\u4ee5\u62ff\u5e38\u60c5\u53bb\u63a8\u6d4b\u7684\uff0c\u4e07\u52c7\u5728\u65b0\u6cf0\u5e02\u91cc\u6bd5\u7adf\u8fd8\u662f\u526f\u5e02\u957f\uff0c\u53c8\u6ca1\u6709\u4ec0\u4e48\u81f4\u547d\u7684\u628a\u67c4\u843d\u5728\u522b\u4eba\u624b\u91cc\uff0c\u6240\u8c13\u4e00\u671d\u5929\u5b50\u4e00\u671d\u81e3\uff0c\u8bf4\u4e0d\u5b9a\u4e07\u5bb6\u5c31\u53c8\u4f1a\u6d3b\u8dc3\u8d77\u6765\u3002</p><p>\u3000\u3000\u8f66\u9a76\u8fdb\u5f90\u5b66\u5e73\u5bb6\u7684\u9662\u5b50\uff0c\u94c1\u6805\u680f\u4e0a\u722c\u6ee1\u7eff\u8272\u85e4\u7c7b\u690d\u7269\uff0c\u5728\u79cb\u98ce\u8427\u745f\u4e2d\uff0c\u7eff\u610f\u4e5f\u672a\u6709\u6d88\u9000\u4e4b\u610f\uff0c\u5f90\u5b66\u5e73\u7a7f\u7740\u8f7b\u8584\u7684\u5510\u88c5\u7ad9\u5728\u5ba2\u5385\u91cc\u4f38\u817f\u6269\u80f8\uff0c\u770b\u7740\u5f20\u606a\u7a7f\u7740\u539a\u91cd\u7684\u7070\u8272\u5916\u5957\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u90fd\u4e0d\u62b5\u6211\u8fd9\u4e2a\u8001\u5934\u5b50\u9876\u7528\uff0c\u5929\u6c14\u6ca1\u90a3\u4e48\u51b7\u3002\u201d</p><p>\u3000\u3000\u201c\u542c\u5929\u6c14\u9884\u62a5\u8bf4\u8fd9\u51e0\u5929\u8981\u964d\u6e29\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8c01\u77e5\u9053\u5929\u6c14\u9884\u62a5\u8fd9\u56de\u8fd8\u662f\u4e0d\u51c6\u3002\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5fae\u5fae\u7684\u7b11\u7740\uff0c\u9ed1\u7ef8\u5510\u88c5\uff0c\u8ba9\u4ed6\u524a\u7626\u7684\u8eab\u4f53\u770b\u4e0a\u53bb\u5f88\u5355\u8584\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u5728\u5c0f\u533a\u95e8\u53e3\u9047\u5230\u4e07\u5411\u524d\u7684\u4e8b\u60c5\uff0c\u95ee\u9053\uff1a\u201c\u4e07\u526f\u4e3b\u4efb\u6709\u6668\u8dd1\u7684\u4e60\u60ef\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ee5\u524d\u5012\u4e0d\u77e5\u9053\uff0c\u597d\u50cf\u662f\u6700\u8fd1\u624d\u5f00\u59cb\u8d77\u65e9\u8dd1\u6b65\u7684\u5427\uff0c\u201d\u5f90\u5b66\u5e73\u6487\u5634\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u5927\u6982\u662f\u542c\u5230\u4ec0\u4e48\u597d\u6d88\u606f\u4e86\u5427\u3002\u201d</p><p>\u3000\u3000\u89c1\u5f90\u5b66\u5e73\u4e00\u8138\u65e0\u6240\u8c13\u7684\u6001\u5ea6\uff0c\u5f20\u606a\u5fc3\u91cc\u6709\u4e9b\u4e0d\u597d\u53d7\uff0c\u773c\u4e0b\u5df2\u4e0e\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u8fbe\u6210\u9ed8\u5951\uff0c\u5012\u4e5f\u4e0d\u6015\u4e07\u5bb6\u51fa\u6765\u6298\u817e\u4ec0\u4e48\u3002</p></article>"}}}]);
