(window.webpackJsonp=window.webpackJsonp||[]).push([[424],{447:function(p){p.exports={chapterTitle:null,title:"\u7b2c424\u7ae0 \u53e3\u820c\u4e4b\u5feb",content:"<article><h2>\u7b2c424\u7ae0 \u53e3\u820c\u4e4b\u5feb</h2><p>\u3000\u3000\u9648\u9759\u7a7f\u7740\u4e00\u4ef6\u7d2b\u8272\u7684\u4f4e\u80f8\u665a\u88c5\uff0c\u9732\u51fa\u7684\u9999\u80a9\u67d4\u5ae9\u5706\u6ed1\uff0c\u80a4\u8272\u5982\u7389\uff0c\u8eab\u6750\u9880\u957f\u4f18\u96c5\uff0c\u5bb9\u989c\u7cbe\u81f4\u79c0\u7f8e\uff0c\u773c\u7738\u987e\u76fc\u751f\u59ff\uff0c\u8d70\u8fc7\u6765\uff0c\u633d\u7740\u5979\u7236\u4eb2\u9648\u5bb6\u5584\u7684\u80f3\u818a\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u53c8\u89c1\u5230\u4f60\uff0c\u201d\u53c8\u671d\u8bb8\u601d\u90a3\u8fb9\u7b11\u4e86\u7b11\uff0c\u201c\u8bb8\u5c0f\u59d0\uff0c\u4f60\u597d\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6446\u8131\u90a3\u4e9b\u5fd9\u4e0d\u8fed\u63a8\u8350\u81ea\u5df1\u7684\u65e0\u804a\u7cbe\u82f1\u7537\u4eba\u4eec\uff0c\u5b89\u9759\u7684\u7ad9\u5230\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u5979\u4e60\u60ef\u7d20\u9762\u5bf9\u4eba\u4e0e\u7b80\u7ea6\u800c\u663e\u5f97\u6709\u4e9b\u4fdd\u5b88\u7684\u8863\u9970\uff0c\u5373\u4f7f\u8fd9\u6837\uff0c\u4e0e\u9648\u9759\u7ad9\u5728\u4e00\u8d77\uff0c\u5bb9\u989c\u4e5f\u4e0d\u7a0d\u9eef\u6de1\u534a\u5206\u3002\u5979\u770b\u5230\u9648\u9759\u7684\u8033\u5782\u4e0a\u8fd8\u6234\u7740\u90a3\u679a\u7ed9\u8c22\u5b50\u5609\u6076\u4f5c\u5267\u4e22\u5728\u5f20\u606a\u8f66\u4e0a\u7684\u8033\u9489\uff0c\u5fc3\u91cc\u60f3\u8fd9\u8033\u9489\u6216\u8bb8\u5bf9\u5979\u5f88\u91cd\u8981\u5427\u3002</p><p>\u3000\u3000\u770b\u5230\u9648\u9759\uff0c\u5f20\u606a\u81ea\u7136\u77e5\u9053\u773c\u524d\u7684\u8fd9\u4e2a\u4e2d\u5e74\u4eba\u662f\u8c01\u4e86\uff0c\u8c22\u5251\u5357\u7684\u672a\u6765\u5cb3\u7236\uff0c\u629b\u5f00\u8c22\u5251\u5357\u4e0e\u9648\u9759\u7684\u5173\u7cfb\u4e0d\u8bf4\uff0c\u505a\u5316\u5986\u54c1\u51fa\u5bb6\u7684\u5927\u96c5\u96c6\u56e2\u603b\u88c1\u9648\u5bb6\u5584\u53f7\u79f0\u662f\u65b0\u6cf0\u7684\u9996\u5bcc\uff0c\u4e0e\u540c\u51fa\u65b0\u6cf0\u7684\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u5144\u5f1f\u7684\u79c1\u8c0a\u6781\u597d\uff0c\u5e74\u521d\u51fa\u8d44\u5165\u80a1\u6d77\u6cf0\u7535\u5668\uff0c\u800c\u8c22\u5bb6\u5bf9\u79d1\u738b\u7684\u63a7\u80a1\u6709\u4e00\u90e8\u5206\u662f\u901a\u8fc7\u6d77\u6cf0\u6765\u5b9e\u73b0\u7684\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u9648\u5bb6\u5584\u5b9e\u9645\u4e5f\u662f\u79d1\u738b\u7684\u80a1\u4e1c\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u9648\u5bb6\u5584\u9732\u51fa\u6de1\u6de1\u7b11\u610f\u7684\u8138\uff0c\u5fc3\u60f3\u4ed6\u5728\u79d1\u738b\u7684\u5229\u76ca\u644a\u8584\u4e0b\u6765\uff0c\u8fd8\u5360\u4e0d\u52305\uff05\uff0c\u6216\u8bb8\u4e0d\u4f1a\u76f4\u63a5\u8fc7\u95ee\u79d1\u738b\u7684\u5177\u4f53\u7ecf\u8425\uff0c\u4e0d\u8fc7\u7b49\u7231\u8fbe\u4e0e\u79d1\u738b\u4e4b\u95f4\u6495\u7834\u8138\u76ae\u641e\u6076\u6027\u7ade\u4e89\u65f6\uff0c\u4e0d\u6653\u5f97\u9648\u5bb6\u5584\u770b\u5230\u81ea\u5df1\u8fd8\u6709\u51e0\u5206\u7b11\u610f\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u8bf4\u9648\u5bb6\u5584\u662f\u4ed6\u7684\u8001\u670b\u53cb\uff0c\u5f20\u606a\u76f8\u4fe1\u9648\u5bb6\u5584\u8fd8\u662f\u4ece\u8c22\u5bb6\u90a3\u91cc\u77e5\u9053\u81ea\u5df1\u7684\uff0c\u4e0d\u8fc7\u8c22\u5bb6\u5bf9\u81ea\u5df1\u6240\u77e5\u4e5f\u5e94\u6709\u9650\uff1b\u845b\u660e\u5fb7\u5728\u9999\u6e2f\u7684\u8001\u670b\u53cb\u8fd9\u4e48\u591a\uff0c\u5634\u8981\u4e0d\u4e25\uff0c\u60f3\u5fc5\u81ea\u5df1\u7684\u8eab\u4efd\u5728\u9999\u6e2f\u65e9\u5c31\u4eba\u6240\u7686\u77e5\u4e86\u3002</p><p>\u3000\u3000\u9762\u5bf9\u9648\u5bb6\u5584\u7236\u5973\u7684\u95ee\u5019\uff0c\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u9648\u5148\u751f\u5ba2\u6c14\u4e86\uff0c\u6211\u90a3\u70b9\u540d\u5934\u90fd\u662f\u6076\u540d\uff0c\u4e0d\u63d0\u4e5f\u7f62\u554a\u3002\u201d\u6ca1\u6709\u7279\u522b\u4eb2\u5207\u4e0e\u8fce\u5408\u7684\u8868\u60c5\u3002</p><p>\u3000\u3000\u9648\u5bb6\u5584\u5f53\u7136\u662f\u4ece\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u5144\u5f1f\u90a3\u91cc\u77e5\u9053\u5f20\u606a\u8fd9\u4eba\uff0c\u6240\u4ee5\u5bf9\u4ed6\u7684\u201c\u52a3\u8ff9\u201d\u4e0e\u6210\u5c31\u90fd\u6709\u542c\u8bf4\u8fc7\uff0c\u8fd9\u4e2a\u65f6\u4ee3\uff0c\u5f88\u591a\u53d1\u5bb6\u81f4\u5bcc\u7684\u4f01\u4e1a\u5bb6\u5e95\u5b50\u90fd\u4e0d\u662f\u4e00\u5c18\u4e0d\u67d3\uff0c\u9648\u5bb6\u5584\u5bf9\u542c\u6765\u7684\u5173\u4e8e\u5f20\u606a\u7684\u201c\u52a3\u8ff9\u201d\u4e0d\u5728\u610f\uff0c\u5012\u662f\u5f88\u6b23\u8d4f\u4ed6\u8ba9\u8c22\u5bb6\u5403\u762a\u7684\u80fd\u529b\uff0c\u70ed\u5207\u4e4b\u4e0b\uff0c\u88ab\u5f20\u606a\u7529\u4e86\u4e00\u4e2a\u51b7\u8138\uff0c\u795e\u8272\u95f4\u6709\u4e9b\u8baa\u7136\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u53ea\u5f53\u5f20\u606a\u4e0d\u559c\u522b\u4eba\u5f53\u4f17\u8bf4\u4ed6\u7684\u4e8b\u60c5\uff0c\u7b11\u7740\u6253\u5706\u573a\uff0c\u8bf7\u9648\u5bb6\u5584\u3001\u5f20\u606a\u5750\u4e0b\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u65f6\u95f4\u79bb\u665a\u5bb4\u8fd8\u65e9\uff0c\u5927\u5bb6\u90fd\u4e09\u4e09\u4e24\u4e24\u5750\u5728\u88c5\u9970\u5962\u534e\u7684\u5ba2\u5385\u91cc\u9ad8\u8c08\u9614\u8bba\uff0c\u8c08\u8bba\u7684\u591a\u4e3a\u7ecf\u6d4e\u8bdd\u9898\uff0c\u770b\u6a21\u6837\u4e0e\u7ecf\u6d4e\u6c99\u9f99\u65e0\u5f02\u3002\u5f20\u606a\u672c\u6765\u5c31\u6ca1\u6709\u6253\u7b97\u5f53\u4f17\u591a\u8bf4\u4ec0\u4e48\uff0c\u66f4\u4f55\u51b5\u4e0d\u60f3\u6709\u4e9b\u8bdd\u4ece\u9648\u5bb6\u5584\u3001\u9648\u9759\u7236\u5973\u7684\u5634\u91cc\u4f20\u5230\u8c22\u5251\u5357\u90a3\u91cc\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u5c06\u8bdd\u9898\u5f15\u5230\u4e9a\u6d32\u672a\u6765\u51e0\u5e74\u7ecf\u6d4e\u52bf\u6001\u7684\u8bdd\u9898\u4e0a\uff0c\u5f20\u606a\u5374\u5f88\u8ba4\u771f\u7684\u6b23\u8d4f\u6302\u5728\u5899\u58c1\u4e0a\u88c5\u70b9\u7528\u7684\u7f8e\u672f\u54c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8c08\u5174\uff0c\u8bb8\u601d\u5b88\u5728\u4ed6\u8eab\u8fb9\uff0c\u5012\u662f\u4e0d\u65ad\u6709\u4eba\u8dd1\u8fc7\u6765\u5356\u5f04\u3002</p><p>\u3000\u3000\u201c\u8bb8\u5c0f\u59d0\uff0c\u4f60\u5bf9\u514b\u9c81\u683c\u66fc\u7684\u89c2\u70b9\u600e\u4e48\u770b\uff1f\u201d</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u7684\u6587\u7ae0\u867d\u7136\u53d1\u8868\u4e8e\u65b0\u534e\u5185\u53c2\u4e0a\uff0c\u4f46\u662f\u5f20\u606a\u76f8\u4fe1\u5373\u4f7f\u6ca1\u6709\u4ed6\u8f6c\u7ed9\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\uff0c\u8fd9\u7bc7\u6587\u7ae0\u4e5f\u4f1a\u4f20\u5230\u9999\u6e2f\u67d0\u4e9b\u4eba\u624b\u91cc\uff0c\u5728\u573a\u7684\u8fd9\u4e9b\u4eba\u80fd\u8bfb\u5230\uff0c\u90fd\u4e0d\u662f\u592a\u5947\u602a\u7684\u4e8b\u60c5\u3002\u4e0d\u8fc7\u53f6\u81fb\u6c11\u5728\u9999\u6e2f\u7ecf\u6d4e\u5b66\u754c\u6ca1\u6709\u591a\u5c11\u540d\u6c14\uff0c\u5c31\u7b97\u6709\u4e9b\u540d\u6c14\uff0c\u4e5f\u65e0\u6cd5\u4e0e\u514b\u9c81\u683c\u66fc\u6bd4\uff0c\u522b\u4eba\u8981\u5f15\u7ecf\u636e\u5178\uff0c\u81ea\u7136\u53ea\u4f1a\u63d0\u5230\u514b\u9c81\u683c\u66fc\u3002</p><p>\u3000\u3000\u5f20\u606a\u77a5\u4e86\u5bf9\u65b9\u4e00\u773c\uff0c\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u9752\u5e74\uff0c\u6234\u7740\u91d1\u4e1d\u773c\u955c\uff0c\u597d\u50cf\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u5f88\u6d41\u884c\u91d1\u4e1d\u6846\u773c\u955c\uff0c\u68b3\u7740\u5927\u5954\u5934\uff0c\u6253\u4e86\u5f88\u591a\u53d1\u80f6\uff0c\u53d1\u6839\u90fd\u6709\u4e9b\u767d\u6cab\uff0c\u8bb0\u5f97\u845b\u660e\u5fb7\u4ecb\u7ecd\u8bf4\u662f\u4e00\u5bb6\u4fe1\u6258\u6295\u8d44\u516c\u53f8\u7684\u7ecf\u7406\u4eba\uff0c\u9999\u6e2f\u4fe1\u6258\u6295\u8d44\u516c\u53f8\u5f88\u591a\u5c31\u662f\u901a\u5e38\u610f\u4e49\u4e0a\u7684\u79c1\u52df\u57fa\u91d1\u3002\u9999\u6e2f\u79c1\u52df\u57fa\u91d1\u7ecf\u7406\u4eba\u5b8c\u5168\u9760\u5728\u8fd9\u884c\u91cc\u7684\u4fe1\u8a89\u5403\u996d\uff0c\u5f53\u4ed6\u62c9\u5230\u7684\u6295\u8d44\u8d44\u91d1\u8d85\u8fc7\u4e24\u5343\u4e94\u767e\u4e07\u7f8e\u5143\uff0c\u5c31\u53ef\u4ee5\u5411\u9999\u6e2f\u5f53\u5c40\u7533\u8bf7\u8f6c\u4e3a\u516c\u52df\u57fa\u91d1\u3002\u8fd9\u7c7b\u4eba\u901a\u5e38\u610f\u4e49\u662f\u90fd\u7cbe\u7814\u91d1\u878d\u3001\u7ecf\u6d4e\u4e0e\u8bc1\u5238\u6295\u8d44\u7684\u9ad8\u624b\u3002\u4e0d\u8fc7\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e2d\uff0c\u6240\u8c13\u7684\u8fd9\u7c7b\u9ad8\u624b\u8df3\u697c\u7684\u4e0d\u8ba1\u5176\u6570\uff0c\u5f20\u606a\u77a5\u773c\u770b\u7740\u8fd9\u4eba\u770b\u8bb8\u601d\u7684\u773c\u795e\u4e0d\u52a0\u63a9\u9970\uff0c\u51b7\u51b7\u4e00\u7b11\uff1a\u7b49\u660e\u5e74\u4f60\u80fd\u6d3b\u4e0b\u6765\u518d\u663e\u6446\u5427\u3002</p><p>\u3000\u3000\u8bb8\u601d\u88c5\u75f4\u5356\u50bb\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u514b\u9c81\u683c\u66fc\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u53ef\u4ee5\u4e0d\u77e5\u9053\u683c\u9c81\u5409\u66fc\u5462\uff1f\u201d\u90a3\u4eba\u8be7\u5f02\u4e07\u5206\u7684\u8bf4\uff0c\u201c\u8fd9\u79cd\u4e8c\u5341\u4e5d\u5c81\u5c31\u80fd\u6210\u4e3a\u7f8e\u56fd\u603b\u7edf\u9996\u5e2d\u7ecf\u6d4e\u5b66\u5bb6\u7684\u4eba\u7269\uff0c\u8bb8\u5c0f\u59d0\u600e\u4e48\u53ef\u80fd\u4e0d\u77e5\u9053\u5462\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u90a3\u4e48\u8bf4\u53ea\u662f\u62d2\u7edd\u8fd9\u4eba\u7684\u7ea0\u7f20\uff0c\u5012\u6ca1\u60f3\u5230\u4ed6\u8fd9\u4e48\u65e0\u8d56\uff0c\u8fd8\u5927\u58f0\u55a7\u54d7\uff0c\u5c06\u5927\u5bb6\u7684\u6ce8\u610f\u529b\u90fd\u5438\u5f15\u8fc7\u6765\uff0c\u597d\u50cf\u81ea\u5df1\u771f\u50cf\u65e0\u77e5\u4e00\u6837\uff0c\u4fcf\u8138\u6da8\u7ea2\uff0c\u5012\u4e0d\u77e5\u9053\u600e\u4e48\u8bf4\u597d\u3002</p><p>\u3000\u3000\u8fd9\u4eba\u60f3\u5438\u5f15\u8bb8\u601d\u7684\u6ce8\u610f\u529b\uff0c\u624b\u6bb5\u8fd8\u771f\u6709\u4e9b\u5351\u52a3\uff0c\u5f20\u606a\u6487\u5634\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u514b\u9c81\u683c\u66fc\u7684\u89c2\u70b9\u8352\u8c2c\u7684\u5f88\uff0c\u5979\u4e0d\u613f\u610f\u8ddf\u4f60\u8ba8\u8bba\u8fd9\u4e9b\u8352\u8c2c\u7684\u89c2\u70b9\uff0c\u4f60\u5012\u662f\u8fde\u597d\u6b79\u8bdd\u90fd\u542c\u4e0d\u51fa\u6765\u3002\u83ab\u975e\u4f60\u89c9\u5f97\u514b\u9c81\u683c\u66fc\u9884\u6d4b\u4e1c\u4e9a\u5730\u533a\u53ef\u80fd\u4f1a\u53d1\u751f\u7ecf\u6d4e\u52a8\u8361\u7684\u89c2\u70b9\u6709\u4e00\u4e01\u70b9\u7684\u9053\u7406\uff1f\u201d</p><p>\u3000\u3000\u90a3\u4eba\u672c\u6765\u5c31\u60f3\u5728\u8bb8\u601d\u9762\u524d\u72e0\u72e0\u7684\u6279\u9a73\u4e00\u4e0b\u514b\u9c81\u683c\u66fc\u7684\u89c2\u70b9\uff0c\u8ba9\u5f20\u606a\u8fd9\u4e48\u4e00\u5835\uff0c\u8fd8\u771f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u4e5f\u4ee5\u4e3a\u683c\u9c81\u514b\u66fc\u7684\u89c2\u70b9\u5927\u8c2c\uff1f\u201d\u9648\u5bb6\u5584\u95ee\u9053\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u63d0\u9192\u9053\u8bf4\uff1a\u201c\u662f\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\uff0c\u4e0d\u662f\u683c\u9c81\u514b\u66fc\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u8c8c\u4f3c\u5584\u610f\u7684\u63d0\u914d\uff0c\u5176\u5b9e\u5f88\u5931\u793c\uff0c\u90fd\u6709\u4eba\u5fcd\u4e0d\u4f4f\u55e4\u7b11\u4e86\uff0c\u9648\u5bb6\u5584\u8001\u8138\u4e00\u81ca\uff0c\u7ed9\u67b6\u5728\u90a3\u91cc\uff0c\u6ca1\u6cd5\u4e0b\u53f0\u3002</p><p>\u3000\u3000\u9999\u6e2f\u7684\u7ecf\u6d4e\u6c99\u9f99\uff0c\u7279\u522b\u50cf\u5b59\u5c1a\u4e49\u7ec4\u7ec7\u7684\u8fd9\u79cd\u4e0d\u5b9a\u671f\u7684\u7ecf\u6d4e\u6c99\u9f99\uff0c\u4f1a\u5c06\u5f53\u665a\u8ba8\u8bba\u7684\u4e3b\u8981\u8bdd\u9898\u90fd\u63d0\u524d\u544a\u8bc9\u5e94\u9080\u8fc7\u6765\u7684\u5ba2\u4eba\u3002</p><p>\u3000\u3000\u9648\u5bb6\u5584\u662f\u51fa\u8eab\u8349\u83bd\u7684\u4f01\u4e1a\u5bb6\uff0c\u53c8\u4e0d\u662f\u7cbe\u7814\u7ecf\u6d4e\u7684\u5b66\u8005\uff0c\u4ed6\u8ba9\u9648\u9759\u5e2e\u4ed6\u627e\u6765\u514b\u9c81\u683c\u66fc\u7684\u6587\u7ae0\u8ba4\u771f\u8bfb\u4e86\u4e00\u4e0b\uff0c\u5012\u8ba4\u4e3a\u6709\u51e0\u5206\u9053\u7406\uff0c\u6ca1\u60f3\u5230\u8fd9\u65f6\u5019\u8bf4\u9519\u540d\u5b57\uff0c\u7ed9\u7f9e\u8fb1\u4e86\u4e00\u756a\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u9648\u5bb6\u5584\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u4e0d\u4ee5\u4e3a\u514b\u9c81\u683c\u66fc\u7684\u89c2\u70b9\u662f\u9519\u7684\uff0c\u4e0d\u8fc7\u9648\u5bb6\u5584\u80fd\u8bf4\u9519\u514b\u9c81\u683c\u66fc\u7684\u540d\u5b57\uff0c\u60f3\u8bfb\u61c2\u4ed6\u7684\u6587\u7ae0\u4f3c\u4e4e\u4e5f\u9887\u4e3a\u5403\u529b\uff0c\u6bd5\u7adf\u8fd9\u4e2a\u6bd4\u7406\u6e05\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u7684\u7ecf\u8425\u95ee\u9898\u8981\u590d\u6742\u8bb8\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u8ba8\u8bba\u514b\u9c81\u683c\u66fc\u7684\u89c2\u70b9\u592a\u628a\u63e1\u4e0d\u4f4f\u91cd\u70b9\uff0c\u6211\u60f3\u6211\u4eec\u8fd9\u91cc\u6ca1\u6709\u8c01\u771f\u6b63\u770b\u61c2\u514b\u9c81\u683c\u66fc\u7684\u5206\u6790\uff0c\u53ea\u4e0d\u8fc7\u4f1a\u8bf4\u4e00\u4e24\u53e5\u4eba\u4e91\u4ea6\u4e91\u7684\u5957\u8bdd\u7f62\u4e86\uff0c\u6216\u8005\u4ec5\u4ec5\u662f\u77e5\u9053\u514b\u9c81\u683c\u66fc\u8fd9\u4e2a\u4eba\u7f62\u4e86\u3002\u201d\u5f20\u606a\u8981\u4e0d\u63a5\u9648\u5bb6\u5584\u7684\u8bdd\uff0c\u80fd\u628a\u4ed6\u81ca\u6b7b\u5728\u90a3\u91cc\uff0c\u4ed6\u5bf9\u845b\u836b\u5747\u6089\u65e0\u597d\u611f\uff0c\u5bf9\u4ed6\u51b7\u5632\u70ed\u8bbd\u4e00\u4e0b\uff0c\u4e5f\u597d\u8ba9\u9648\u5bb6\u5584\u4e0b\u53f0\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u6211\u8bfb\u4e0d\u61c2\u514b\u9c81\u683c\u66fc\u7684\u6587\u7ae0\uff0c\u4f60\u77e5\u9053\u6211\u662f\u4ece\u54ea\u91cc\u6bd5\u4e1a\u7684\uff1f\u201d\u845b\u836b\u5747\u8ba9\u5f20\u606a\u7684\u8bdd\u4e00\u6fc0\uff0c\u7709\u5934\u4e00\u626c\uff0c\u5f88\u4e0d\u5ba2\u6c14\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6b32\u8ddf\u845b\u836b\u5747\u4e89\u6267\uff0c\u6253\u5fc3\u91cc\u5c31\u770b\u4e0d\u8d77\u8fd9\u4eba\uff0c\u6487\u5634\u671d\u9648\u9759\u5476\u4e86\u5476\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5c0f\u59d0\u4ece\u7c73\u5170\u535a\u53ef\u5c3c\u5b66\u9662\u7559\u5b66\u5f52\u6765\uff0c\u8bf7\u9648\u5c0f\u59d0\u80fd\u8bfb\u61c2\u514b\u9c81\u683c\u66fc\u7684\u6587\u7ae0\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u5fc3\u91cc\u5f53\u7136\u60f3\uff1a\u6709\u4ec0\u4e48\u96be\u61c2\u7684\uff1f\u4e0d\u8fc7\u5f20\u606a\u5e2e\u5979\u7238\u5316\u89e3\u5c34\u5c2c\uff0c\u5979\u53ea\u80fd\u987a\u7740\u4ed6\u7684\u8bed\u6c14\u6447\u4e86\u6447\uff1a\u201c\u8bfb\u4e0d\u61c2\u3002\u201d</p><p>\u3000\u3000\u7c73\u5170\u535a\u53ef\u5c3c\u5b66\u9662\u662f\u6b27\u6d32\u6700\u8457\u540d\u7684\u5546\u5b66\u9662\uff0c\u9648\u9759\u8fd9\u4e00\u6447\u5934\uff0c\u845b\u836b\u5747\u62ff\u5b66\u6821\u538b\u4eba\u7684\u8bdd\u591a\u5c11\u663e\u5f97\u6709\u4e9b\u53ef\u7b11\u3002</p><p>\u3000\u3000\u51e0\u6b21\u89c1\u9762\uff0c\u5f20\u606a\u90fd\u7ed9\u9648\u9759\u7559\u4e0b\u6df1\u523b\u7684\u5370\u8c61\uff0c\u53ea\u662f\u5f20\u606a\u7684\u6765\u9f99\u53bb\u8109\uff0c\u9648\u9759\u53c8\u600e\u4e48\u53ef\u80fd\u77e5\u9053\uff1f\u9648\u9759\u89c1\u5f20\u606a\u7adf\u7136\u77e5\u9053\u81ea\u5df1\u662f\u4ece\u7c73\u5170\u535a\u53ef\u5c3c\u5b66\u9662\u7559\u5b66\u5f52\u6765\uff0c\u8fd8\u5f53\u5f20\u606a\u6709\u5728\u7559\u610f\u81ea\u5df1\uff0c\u5fc3\u91cc\u5012\u6709\u4e00\u79cd\u5f88\u83ab\u540d\u5374\u53c8\u6709\u4e9b\u590d\u6742\u7684\u60c5\u7eea\u3002\u5979\u54ea\u91cc\u77e5\u9053\uff0c\u5f20\u606a\u53ea\u662f\u5728\u6536\u96c6\u8c22\u5251\u5357\u8d44\u6599\u8003\u8651\u5230\u5979\u662f\u8c22\u5251\u5357\u7684\u672a\u5a5a\u59bb\uff0c\u81ea\u7136\u4e5f\u8981\u77e5\u5df1\u77e5\u5f7c\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u4f3c\u4e4e\u660e\u767d\u4e0e\u5f20\u606a\u8fd9\u4eba\u4e89\u53e3\u820c\u4e4b\u5feb\u6709\u4e9b\u5931\u7b56\uff0c\u8f7b\u54fc\u4e86\u4e00\u58f0\uff0c\u4e0e\u90a3\u4e2a\u79c1\u52df\u57fa\u91d1\u7ecf\u7406\u4eba\u8d70\u5230\u4e00\u65c1\u53bb\u8bf4\u8bdd\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u4e0e\u9648\u5bb6\u5584\u3001\u9648\u9759\u7236\u5973\u51d1\u5728\u4e00\u8d77\uff0c\u800c\u662f\u7ee7\u7eed\u6b23\u8d4f\u60ac\u6302\u5728\u5899\u58c1\u4e0a\u7684\u7f8e\u672f\u54c1\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u4eca\u5929\u8bf4\u4e86\u5f88\u591a\u53cd\u8bdd\u5462\uff1f\u201d\u5b59\u5c1a\u4e49\u7aef\u7740\u4e00\u676f\u7ea2\u9152\u8d70\u8fc7\u6765\uff0c\u8f7b\u58f0\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u4e00\u7b11\uff0c\u542c\u7740\u5ba2\u5385\u89d2\u843d\u91cc\u8fd8\u6709\u4eba\u5728\u6279\u9a73\u514b\u9c81\u683c\u66fc\u5173\u4e8e\u672a\u6765\u51e0\u5904\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u52bf\u529b\u7684\u5224\u65ad\uff1a\u201c\u4ed6\u4eec\u660e\u660e\u5c31\u662f\u8bfb\u4e0d\u61c2\u554a\uff0c\u5b59\u5148\u751f\u4ee5\u4e3a\u6211\u8ddf\u80fd\u8fd9\u4e9b\u6986\u6728\u8111\u888b\u89e3\u91ca\u6e05\u695a\uff1f\u201d\u770b\u5230\u90a3\u4e2a\u79c1\u52df\u57fa\u91d1\u7ecf\u7406\u4eba\u4e0e\u845b\u836b\u5747\u7684\u5634\u8138\uff0c\u8bdd\u90fd\u61d2\u5f97\u8bf4\uff0c\u52c9\u5f3a\u8981\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e5f\u53ea\u662f\u8bf4\u53cd\u8bdd\u800c\u5df2\uff0c\u53cd\u6b63\u8fd9\u65f6\u5019\u5927\u5bb6\u90fd\u559c\u6b22\u542c\u53cd\u8bdd\uff0c\u671d\u521a\u624d\u90a3\u4e2a\u8fc7\u6765\u627e\u8bb8\u601d\u642d\u8154\u7684\u79c1\u52df\u57fa\u91d1\u7ecf\u7406\u4eba\u5476\u5476\u5634\u201c\u5b59\u5148\u751f\u8981\u6709\u8d44\u91d1\u5728\u90a3\u5bb6\u4f19\u624b\u91cc\uff0c\u8d76\u7d27\u60f3\u8f99\u64a4\u51fa\u6765\u5427\u2026\u2026\u201d\u79c1\u52df\u57fa\u91d1\u901a\u5e38\u662f\u5c01\u95ed\u6027\u7684\uff0c\u4e0d\u4f1a\u6295\u8d44\u4eba\u8bf4\u4eca\u5929\u63d0\u73b0\u5c31\u4eca\u5929\u63d0\u73b0\u7684\uff0c\u867d\u7136\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4f1a\u5728\u660e\u5e74\u4e0a\u534a\u5e74\u6709\u4e00\u6ce2\u5927\u5e02\u573a\uff0c\u4f46\u662f\u64a4\u51fa\u7684\u65f6\u673a\u5374\u4e0d\u662f\u4e00\u822c\u6295\u8d44\u4eba\u80fd\u63a7\u5236\u5f97\u4e86\u7684\uff0c\u4e00\u822c\u7684\u79c1\u52df\u57fa\u91d1\u51e0\u4e4e\u6ca1\u6709\u90fd\u4ece\u660e\u5e74\u4e2d\u7684\u7b2c\u4e00\u6b21\u6253\u51fb\u4e2d\u5168\u8eab\u800c\u9000\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u5bb6\u4f19\u6307\u4e0d\u5b9a\u5230\u65f6\u5019\u4f1a\u8f6c\u53d8\u65b9\u5411\u3001\u6210\u4e3a\u5171\u540c\u53c2\u4e0e\u6253\u51fb\u9999\u6e2f\u91d1\u878d\u5e02\u573a\u4e0e\u8bc1\u5238\u5e02\u573a\u3001\u8d81\u706b\u6253\u52ab\u7684\u4eba\u3002</p></article>"}}}]);
