(window.webpackJsonp=window.webpackJsonp||[]).push([[426],{448:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u68a6",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u68a6</h2><p>\u9884\u611f\u8fd9\u79cd\u4e1c\u897f\uff0c\u5f80\u5f80\u597d\u7684\u4e0d\u51c6\uff0c\u4e0d\u597d\u7684\u5374\u7279\u522b\u51c6\u3002\u743c\u6069\u5f88\u6e05\u695a\u8fd9\u4e2a\u9053\u7406\uff0c\u6240\u4ee5\u4ed6\u7684\u8138\u8272\u987f\u65f6\u53d8\u5f97\u96be\u770b\u8d77\u6765\u3002</p><p>\u201c\u5e03\u96f7\u7eb3\u65af\u662f\u97f3\u4e50\u5b66\u9662\u7684\u9662\u957f\uff1f\u4ed6\u8fd1\u51e0\u4e2a\u6708\u53c8\u5728\u57ce\u4e2d\u4f11\u517b\u2026\u2026\u4e0d\u4f1a\u8fd9\u4e48\u5de7\u5427\uff1f\u201d</p><p>\u8fd9\u4e0e\u5176\u8bf4\u662f\u4e00\u79cd\u731c\u6d4b\uff0c\u5012\u4e0d\u5982\u8bf4\u662f\u4e00\u79cd\u6050\u60e7\u2014\u2014\u743c\u6069\u5bb3\u6015\u8fd9\u79cd\u4e8b\u60c5\u4f1a\u5f53\u771f\u53d1\u751f\u3002\u5982\u679c\u5bf9\u624b\u662f\u5e93\u80af\uff0c\u90a3\u4e48\u4ed6\u8fd8\u4e0d\u5fc5\u591a\u4e48\u5728\u610f\uff0c\u4f46\u5982\u679c\u5bf9\u624b\u6362\u6210\u4e86\u9634\u9b42\u738b\u5b50\u2026\u2026\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u201c\u90a3\u5c31\u53bb\u95ee\u73ca\u5609\u597d\u4e86\u554a\uff0c\u201d\u838e\u73de\u514b\u7ec8\u4e8e\u6709\u4e9b\u4e0d\u8010\u70e6\u8d77\u6765\uff0c\u201c\u76f4\u622a\u4e86\u5f53\uff0c\u6e05\u6e05\u695a\u695a\uff0c\u4ec0\u4e48\u95ee\u9898\u90fd\u89e3\u51b3\u4e86\u3002\u4f60\u4ee5\u524d\u867d\u7136\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u82f1\u9510\u679c\u6bc5\uff0c\u5374\u4e5f\u7b97\u662f\u6709\u51b3\u65ad\u7684\u4eba\uff0c\u8be5\u4e0b\u51b3\u5fc3\u7684\u65f6\u5019\u4ece\u6765\u4e0d\u542b\u7cca\uff0c\u600e\u4e48\u5230\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\uff0c\u5c31\u5f04\u5f97\u77bb\u524d\u987e\u540e\uff0c\u62d6\u62d6\u62c9\u62c9\u7684\uff0c\u4e00\u70b9\u7537\u4eba\u6837\u5b50\u90fd\u6ca1\u6709\uff01\u201d</p><p>\u838e\u73de\u514b\u662f\u6740\u624b\u51fa\u8eab\uff0c\u867d\u7136\u64c5\u957f\u7f8e\u8272\u5a9a\u60d1\uff0c\u9aa8\u5b50\u91cc\u7ec8\u7a76\u8fd8\u662f\u4fe1\u5949\u7684\u5f31\u8089\u5f3a\u98df\u7684\u6cd5\u5219\uff0c\u559c\u6b22\u5e72\u5e72\u8106\u8106\uff0c\u4e8b\u60c5\u4e45\u62d6\u4e0d\u51b3\uff0c\u4e0d\u514d\u5c31\u6709\u4e9b\u4e0d\u8010\u3002\u5176\u5b9e\u8299\u857e\u72c4\u540c\u6837\u4e5f\u6709\u8fd9\u79cd\u7591\u60d1\uff0c\u53ea\u662f\u4e0d\u6562\u95ee\u51fa\u53e3\u7f62\u4e86\u3002\u5982\u679c\u8bf4\u4e00\u5f00\u59cb\uff0c\u743c\u6069\u81ea\u5ea6\u4e8b\u60c5\u8fd8\u5728\u638c\u63e1\u4e4b\u4e2d\uff0c\u5728\u81ea\u5df1\u53ef\u4ee5\u89e3\u51b3\u7684\u8303\u56f4\u4e4b\u5185\uff0c\u4e0d\u613f\u610f\u53bb\u95ee\u73ca\u5609\uff0c\u90a3\u53ef\u4ee5\u7406\u89e3\uff1b\u4f46\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u518d\u786c\u8981\u5982\u6b64\uff0c\u53ea\u6015\u4f1a\u5f04\u5f97\u65e0\u53ef\u6536\u62fe\u3002\u8fd9\u79cd\u7b80\u5355\u7684\u9053\u7406\uff0c\u4eba\u4eba\u90fd\u80fd\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u5c31\u60f3\u4e0d\u901a\u4f3c\u7684\u3002</p><p>\u5728\u6b64\u671f\u95f4\uff0c\u8299\u8389\u5a05\u6084\u58f0\u8be2\u95ee\u8299\u857e\u72c4\uff0c\u5927\u7ea6\u4e5f\u660e\u767d\u4e86\u4e8b\u60c5\u7684\u7ecf\u8fc7\u7f18\u7531\uff0c\u542c\u5230\u838e\u73de\u514b\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u7531\u5f97\u51b7\u7b11\u8d77\u6765\uff0c\u201c\u4ed6\u4e0d\u662f\u4e0d\u660e\u767d\uff0c\u662f\u4e0d\u6562\u3002\u201d</p><p>\u201c\u4e0d\u6562\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u4e0d\u6562\uff0c\u201d\u8299\u8389\u5a05\u8bf4\uff0c\u201c\u4ed6\u5148\u5df2\u7ecf\u5728\u73ca\u5609\u59d0\u59d0\u9762\u524d\u6446\u51fa\u4e86\u59ff\u6001\uff0c\u73b0\u5728\u53c8\u53bb\u95ee\uff0c\u4ed6\u6015\u73ca\u5609\u59d0\u59d0\u8bf4\u4ed6\u51fa\u5c14\u53cd\u5c14\uff0c\u7b11\u8bdd\u4ed6\u5fc3\u80f8\u72ed\u7a84\u2014\u2014\u8fd9\u662f\u7b2c\u4e00\u4e2a\u4e0d\u6562\uff0c\u4ed6\u6015\u4e22\u4e86\u9762\u5b50\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\u3002</p><p>\u201c\u7b2c\u4e8c\u4e2a\u4e0d\u6562\uff0c\u662f\u4ed6\u5fc3\u865a\uff0c\u6015\u73ca\u5609\u59d0\u59d0\u8bf4\u51fa\u4ec0\u4e48\u4ed6\u4e0d\u613f\u610f\u542c\u5230\u7684\u8bdd\uff0c\u201d\u8299\u8389\u5a05\u8bf4\uff0c\u201c\u5f88\u591a\u65f6\u5019\uff0c\u62d2\u7edd\u5e76\u4e0d\u610f\u5473\u7740\u52c7\u6c14\u3002\u73ca\u5609\u59d0\u59d0\u60f3\u5411\u4ed6\u89e3\u91ca\uff0c\u4ed6\u5374\u6253\u65ad\u2014\u2014\u8fd9\u5176\u5b9e\u6839\u672c\u5c31\u4e0d\u662f\u4ec0\u4e48\u4fe1\u4efb\uff0c\u4e0d\u8fc7\u662f\u80c6\u602f\u7f62\u4e86\u3002\u201d</p><p>\u743c\u6069\u4f9d\u65e7\u6c89\u9ed8\u3002</p><p>\u201c\u7b2c\u4e09\u4e2a\u4e0d\u6562\uff0c\u662f\u6015\u7834\u574f\u4e86\u5f7c\u6b64\u95f4\u7684\u4fe1\u4efb\uff0c\u201d\u8299\u8389\u5a05\u7684\u8bed\u6c14\u7a0d\u7a0d\u67d4\u548c\u4e86\u4e9b\uff0c\u201c\u4ed6\u4e0d\u60f3\u8d28\u95ee\u73ca\u5609\uff0c\u4e5f\u4e0d\u60f3\u542c\u5230\u73ca\u5609\u627f\u8ba4\u8bf4\u9a97\u4e86\u4ed6\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u201d\u8299\u857e\u72c4\u4e0d\u89e3\uff0c\u201c\u5176\u5b9e\u73b0\u5728\u743c\u6069\u5df2\u7ecf\u77e5\u9053\uff0c\u73ca\u5609\u59d0\u59d0\u662f\u2026\u2026\u81f3\u5c11\u5728\u90a3\u652f\u957f\u7b1b\u7684\u4e8b\u60c5\u4e0a\uff0c\u662f\u9a97\u4e86\u4ed6\u554a\u3002\u201d</p><p>\u201c\u4e8b\u60c5\u6ca1\u5230\u6700\u540e\u4e00\u6b65\uff0c\u5c31\u4e0d\u8981\u4e0b\u5b9a\u8bba\uff0c\u201d\u8299\u8389\u5a05\u8bf4\uff0c\u201c\u4f55\u51b5\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5c31\u7b97\u5927\u5bb6\u5fc3\u77e5\u809a\u660e\uff0c\u4f46\u6709\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u90a3\u662f\u622a\u7136\u4e0d\u540c\u3002\u8bdd\u6ca1\u8bf4\u51fa\u53e3\uff0c\u90a3\u5c31\u8fd8\u6709\u8f6c\u571c\u7684\u4f59\u5730\uff0c\u8bdd\u4e00\u65e6\u8bf4\u51fa\u53e3\uff0c\u90a3\u5c31\u6ca1\u6cd5\u6536\u56de\u6765\u4e86\uff0c\u4f1a\u5728\u5fc3\u91cc\u7559\u4e0b\u9634\u5f71\u7684\u3002\u201d</p><p>\u8299\u857e\u72c4\u770b\u770b\u6c89\u9ed8\u4e0d\u8bed\u7684\u743c\u6069\uff0c\u518d\u770b\u770b\u81ea\u5df1\u59d0\u59d0\uff0c\u9887\u6709\u4e9b\u60ca\u8bb6\uff0c\u4e0d\u660e\u767d\u8299\u8389\u5a05\u600e\u4e48\u80fd\u5982\u6b64\u7cbe\u786e\u5730\u628a\u63e1\u743c\u6069\u7684\u5fc3\u601d\uff0c\u8bf4\u5f97\u5934\u5934\u662f\u9053\u3002\u838e\u73de\u514b\u53cd\u5e94\u5feb\u4e9b\uff0c\u683c\u683c\u4e00\u7b11\uff0c\u201c\u662f\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd8\u662f\u8299\u8389\u5a05\u806a\u660e\u3002\u201d</p><p>\u201c\u4e0d\u662f\u6211\u806a\u660e\uff0c\u201d\u8299\u8389\u5a05\u6de1\u6de1\u8bf4\uff0c\u201c\u8fd9\u662f\u7ecf\u9a8c\u3002\u201d</p><p>\u786e\u5b9e\u662f\u7ecf\u9a8c\uff0c\u8fd9\u51e0\u4e2a\u5973\u5b69\u5b50\u91cc\uff0c\u552f\u6709\u8299\u8389\u5a05\u6709\u8fc7\u6b63\u5e38\u7684\u604b\u7231\uff0c\u6240\u4ee5\u80fd\u591f\u8f7b\u6613\u660e\u767d\u8fd9\u4e9b\u60c5\u4eba\u4e4b\u95f4\u7684\u7ec6\u5fae\u5fc3\u601d\u3002\u8299\u857e\u72c4\u6027\u60c5\u592a\u8fc7\u67d4\u987a\uff0c\u548c\u743c\u6069\u4e4b\u95f4\u7684\u201c\u604b\u60c5\u201d\u5176\u5b9e\u4e0d\u80fd\u7b97\u662f\u6b63\u5e38\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u6709\u4e9b\u7578\u5f62\uff1b\u81f3\u4e8e\u838e\u73de\u514b\uff0c\u5979\u548c\u7537\u4eba\u4e0a\u5e8a\u7684\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u8981\u8bf4\u771f\u6b63\u7684\u604b\u7231\uff0c\u90a3\u5c31\u662f\u5b8c\u5168\u7a7a\u767d\u3002</p><p>\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u8fd9\u4e2a\u95ee\u9898\u8fd8\u662f\u548c\u6709\u7ecf\u9a8c\u7684\u4eba\u8ba8\u8bba\u6bd4\u8f83\u9760\u8c31\u3002</p><p>\u201c\u90a3\u4f60\u89c9\u5f97\uff0c\u6211\u73b0\u5728\u5e94\u8be5\u600e\u4e48\u505a\uff1f\u201d\u743c\u6069\u95ee\u8299\u8389\u5a05\u3002</p><p>\u201c\u53bb\u95ee\u5979\u3002\u201d</p><p>\u743c\u6069\u8fdf\u7591\u3002</p><p>\u201c\u95ee\u4e2a\u6e05\u695a\uff0c\u603b\u597d\u8fc7\u81ea\u5df1\u5728\u8fd9\u91cc\u80e1\u601d\u4e71\u60f3\uff0c\u201d\u8299\u8389\u5a05\u9759\u9759\u8bf4\uff0c\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u7ed5\u662f\u7ed5\u4e0d\u8fc7\u53bb\uff0c\u8eb2\u662f\u8eb2\u4e0d\u5f00\u6765\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u8fd8\u4e0d\u5982\u76f4\u63a5\u8fce\u4e0a\u3002\u8be5\u9762\u5bf9\u7684\uff0c\u7ec8\u7a76\u662f\u8981\u9762\u5bf9\uff0c\u8981\u53d1\u751f\u7684\uff0c\u5c31\u7b97\u4f60\u4e0d\u63a5\u53d7\uff0c\u81f3\u5c11\u4e5f\u8981\u77e5\u9053\u5b83\u662f\u4ec0\u4e48\u2014\u2014\u66f4\u4f55\u51b5\uff0c\u4f60\u5bf9\u4f60\u81ea\u5df1\uff0c\u5bf9\u73ca\u5609\u59d0\u59d0\uff0c\u603b\u8be5\u6709\u51e0\u5206\u4fe1\u5fc3\u624d\u662f\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u4e86\u534a\u65e5\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u201c\u591a\u8c22\u3002\u201d\u4ed6\u8f7b\u58f0\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u51b3\u5b9a\u867d\u7136\u505a\u4e86\uff0c\u4f46\u771f\u8981\u4ed8\u8bf8\u5b9e\u65bd\uff0c\u5374\u8fd8\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u7684\u3002\u743c\u6069\u51e0\u6b21\u60f3\u627e\u673a\u4f1a\u5f00\u53e3\uff0c\u8bdd\u5230\u5634\u8fb9\u53c8\u7f29\u4e86\u56de\u53bb\u3002\u665a\u9910\u4e4b\u540e\uff0c\u73ca\u5609\u53d6\u8fc7\u957f\u7b1b\uff0c\u4e3a\u743c\u6069\u5439\u594f\u65b0\u5b66\u7684\u66f2\u5b50\uff0c\u8fd9\u6b21\u662f\u4e00\u9996\u6708\u5149\u66f2\uff0c\u4f18\u96c5\u660e\u4e3d\uff0c\u4eff\u4f5b\u6e05\u6cc9\u53ee\u549a\u6b22\u5feb\u6d41\u6dcc\uff0c\u6d17\u5c3d\u5fc3\u5934\u5c18\u57c3\u3002\u5979\u7684\u6280\u6cd5\u65e5\u76ca\u7eaf\u719f\uff0c\u743c\u6069\u5374\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u4e0d\u65f6\u8d70\u795e\uff0c\u73ca\u5609\u89c1\u4e86\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u6ca1\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u201c\u5c0f\u5f1f\uff0c\u7d2f\u4e86\uff1f\u201d\u5979\u67d4\u58f0\u95ee\u3002</p><p>\u201c\u55ef\uff0c\u6709\u70b9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4eca\u5929\u88ab\u53ec\u53bb\u8ff0\u804c\uff0c\u8bf4\u4e86\u6574\u6574\u4e00\u4e0a\u5348\u3002\u201d</p><p>\u201c\u90a3\u65e9\u70b9\u4f11\u606f\u5427\u3002\u201d</p><p>\u201c\u597d\u3002\u201d</p><p>\u548c\u5f80\u5e38\u4e00\u6837\uff0c\u59d0\u5f1f\u4fe9\u76f8\u62e5\u800c\u7720\uff0c\u73ca\u5609\u5f88\u5feb\u7761\u7740\uff0c\u53d1\u51fa\u5747\u5300\u5e73\u7f13\u7684\u547c\u5438\u58f0\uff0c\u743c\u6069\u770b\u7740\u9ed1\u6c89\u6c89\u7684\u5929\u82b1\u677f\uff0c\u8111\u5b50\u91cc\u4e71\u4e03\u516b\u7cdf\uff0c\u8bbe\u60f3\u7740\u5404\u79cd\u53ef\u80fd\u6027\uff0c\u59cb\u7ec8\u9f13\u4e0d\u8d77\u6700\u540e\u90a3\u70b9\u52c7\u6c14\u3002\u8ff7\u8ff7\u7cca\u7cca\uff0c\u534a\u7761\u534a\u9192\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u7ecf\u662f\u5348\u591c\u65f6\u5206\uff0c\u6e05\u51b7\u7684\u6708\u5149\u4ece\u7a97\u6237\u4e2d\u6d41\u6cfb\u8fdb\u6765\uff0c\u5728\u5730\u677f\u4e0a\u4e0e\u9634\u5f71\u4ea4\u9519\u7f16\u7ec7\u6210\u7e41\u590d\u7684\u56fe\u6848\uff0c\u7cbe\u5de7\u800c\u9759\u8c27\u3002</p><p>\u4e07\u7c41\u4ff1\u5bc2\u3002</p><p>\u743c\u6069\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u6162\u6162\u5750\u8d77\u6765\uff0c\u53ea\u89c9\u5168\u8eab\u71e5\u70ed\uff0c\u70e6\u95f7\u4e0d\u582a\uff0c\u4ed6\u6b63\u8003\u8651\u53bb\u6d74\u5ba4\u51b2\u4e2a\u51b7\u6c34\u6fa1\uff0c\u521a\u521a\u4fa7\u8eab\u51c6\u5907\u4e0b\u5e8a\uff0c\u7a81\u7136\u611f\u89c9\u8eab\u65c1\u6709\u52a8\u9759\uff0c\u8f6c\u8138\u4e00\u770b\uff0c\u53d1\u73b0\u73ca\u5609\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u9192\u4e86\uff0c\u5168\u8eab\u88f9\u5728\u6bef\u5b50\u91cc\uff0c\u5b9a\u5b9a\u5730\u770b\u7740\u4ed6\uff0c\u660e\u4eae\u7684\u773c\u775b\u5728\u9ed1\u6697\u4e2d\u5982\u661f\u8fb0\u95ea\u70c1\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u5c0f\u5f1f\uff1f\u201d\u5979\u8f7b\u58f0\u95ee\uff0c\u201c\u8fd9\u4e48\u665a\u4e86\u8fd8\u4e0d\u7761\uff1f\u201d</p><p>\u201c\u5443\uff0c\u6211\u2026\u2026\u7761\u4e0d\u7740\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u51e0\u5929\u4e00\u76f4\u7761\u4e0d\u5b89\u7a33\u7684\u6837\u5b50\uff0c\u6709\u5fc3\u4e8b\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u6ca1\u6709\u3002\u201d\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u5426\u8ba4\u3002</p><p>\u73ca\u5609\u5fae\u5fae\u76b1\u7709\uff0c\u4e5f\u6ca1\u591a\u8bf4\u4ec0\u4e48\uff0c\u7ffb\u8fc7\u8eab\u6765\u6253\u7b97\u7ee7\u7eed\u7761\u3002\u8fd9\u4e0b\u5b50\u5374\u51fa\u4e86\u95ee\u9898\uff0c\u59d0\u5f1f\u4fe9\u539f\u672c\u662f\u5e76\u80a9\u800c\u7720\uff0c\u540e\u6765\u743c\u6069\u60f3\u4e0b\u5e8a\uff0c\u6162\u6162\u5750\u8d77\u8eab\uff0c\u5982\u4eca\u80cc\u9760\u7740\u5e8a\u5934\uff0c\u73ca\u5609\u8fd9\u4e00\u7ffb\u8eab\uff0c\u8138\u4fbf\u6b63\u597d\u8d34\u5728\u743c\u6069\u7684\u5927\u817f\u4e0a\uff0c\u800c\u4e14\u662f\u6839\u90e8\u2026\u2026</p><p>\u743c\u6069\u5fc3\u7eea\u4e0d\u4f73\uff0c\u539f\u672c\u6ca1\u6709\u591a\u5c11\u6b32\u5ff5\u3002\u4f46\u4ed6\u7ec8\u7a76\u662f\u4e2a\u9752\u5e74\u7537\u5b50\uff0c\u8840\u6c14\u6b63\u76db\uff0c\u88ab\u4e00\u4e2a\u5973\u5b69\u5b50\u8d34\u5728\u8fd9\u79cd\u654f\u611f\u90e8\u4f4d\uff0c\u661f\u7738\u8ff7\u8499\uff0c\u5410\u6c14\u5982\u5170\uff0c\u56e0\u4e3a\u672a\u53ca\u68b3\u6d17\u6253\u626e\uff0c\u53d1\u9afb\u6709\u4e9b\u6563\u4e71\uff0c\u5507\u89d2\u8fb9\u8fd8\u7c98\u7740\u51e0\u7f15\u9752\u4e1d\uff0c\u770b\u8d77\u6765\u81ea\u6709\u4e00\u79cd\u6175\u61d2\u5a9a\u6001\uff0c\u54ea\u91cc\u8fd8\u7981\u53d7\u5f97\u4f4f\u3002\u987f\u65f6\u5c0f\u8179\u4e2d\u4eff\u4f5b\u6709\u4e00\u56e2\u706b\u7130\u5728\u718a\u718a\u71c3\u70e7\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4fbf\u4e0b\u8eab\u633a\u8d77\uff0c\u51e0\u4e4e\u5c06\u7761\u8863\u90fd\u6491\u7834\uff0c\u6b63\u8d34\u4e0a\u59d0\u59d0\u7684\u8138\u988a\u3002</p><p>\u201c\u5514\uff0c\u597d\u70eb\u2026\u2026\u201d</p><p>\u867d\u7136\u9694\u7740\u4e00\u5c42\u7761\u8863\u5e03\u6599\uff0c\u90a3\u79cd\u60ca\u4eba\u7684\u70ed\u529b\u4f9d\u65e7\u76f4\u900f\u8fc7\u6765\u3002\u73ca\u5609\u6b63\u8ff7\u8ff7\u7cca\u7cca\u95f4\uff0c\u7a81\u7136\u611f\u89c9\u6709\u4ec0\u4e48\u6eda\u70eb\u7684\u4e1c\u897f\u7a81\u7136\u62b5\u7740\u81ea\u5df1\u7684\u8138\uff0c\u4e0b\u610f\u8bc6\u5730\u4f38\u624b\u5c31\u53bb\u6293\u3002\u743c\u6069\u4e00\u60ca\u4e4b\u4e0b\uff0c\u4e0d\u6562\u79fb\u52a8\uff0c\u5fc3\u4e2d\u66f4\u9690\u9690\u6709\u51e0\u5206\u671f\u5f85\u3002\u73ca\u5609\u53ea\u89c9\u4f3c\u4e4e\u63e1\u4f4f\u4e86\u4e00\u6bb5\u6eda\u70eb\u786c\u7269\uff0c\u4eff\u4f5b\u8fd8\u662f\u6d3b\u7684\u4e00\u822c\uff0c\u5728\u81ea\u5df1\u638c\u5fc3\u8f7b\u8f7b\u5f39\u8df3\uff0c\u4e00\u65f6\u95f4\u4e5f\u6014\u4f4f\uff0c\u8fc7\u4e86\u534a\u523b\u7ec8\u4e8e\u56de\u8fc7\u795e\u6765\uff0c\u7741\u5f00\u773c\u775b\u3002</p><p>\u201c\u5c0f\u5f1f\uff0c\u53c8\u6765\u6b3a\u8d1f\u59d0\u59d0\u5462\uff0c\u201d\u5979\u4ef0\u8d77\u8138\uff0c\u4f3c\u7b11\u975e\u7b11\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u4e0d\u4e56\u554a\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u90a3\u4e2a\u2014\u2014\u201d</p><p>\u201c\u4e4b\u6240\u4ee5\u7761\u4e0d\u7740\uff0c\u662f\u56e0\u4e3a\u6709\u59d0\u59d0\u5728\u65c1\u8fb9\uff0c\u5bf9\u5427\uff1f\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u53cd\u5e94\u8fc7\u6765\uff0c\u8fd9\u662f\u65e9\u4e0a\u4ed6\u5728\u5b66\u6821\u95e8\u53e3\u5bf9\u73ca\u5609\u8bf4\u7684\u8bdd\u3002\u201c\u62b1\u7740\u59d0\u59d0\u4f60\u8fd9\u6837\u7684\u5927\u7f8e\u4eba\u5728\u6000\u91cc\uff0c\u53ea\u80fd\u770b\u4e0d\u80fd\u5403\uff0c\u4e0d\u7ba1\u54ea\u4e2a\u7537\u4eba\u90fd\u4f1a\u7761\u4e0d\u597d\u7684\u3002\u201d\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u8fd9\u53e5\u8bdd\u4e5f\u4e0d\u80fd\u7b97\u9519\uff0c\u743c\u6069\u4e4b\u6240\u4ee5\u7761\u4e0d\u7740\uff0c\u4e00\u65b9\u9762\u662f\u6709\u5fc3\u4e8b\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u786e\u5b9e\u662f\u6b32\u706b\u96be\u8010\uff0c\u8fd9\u6ecb\u5473\u5b9e\u5728\u4e0d\u597d\u53d7\u3002</p><p>\u201c\u95ed\u4e0a\u773c\u775b\u3002\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u95ed\u4e0a\u773c\u775b\uff0c\u201d\u73ca\u5609\u91cd\u590d\uff0c\u867d\u7136\u58f0\u97f3\u5f88\u8f7b\uff0c\u5374\u81ea\u6709\u4e00\u79cd\u4ee4\u4eba\u65e0\u6cd5\u8fdd\u6297\u7684\u529b\u91cf\uff0c\u201c\u59d0\u59d0\u77e5\u9053\u4e00\u79cd\u6cbb\u7597\u5931\u7720\u7684\u65b9\u6cd5\uff0c\u5f88\u6709\u6548\u7684\u2014\u2014\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u51c6\u770b\u3002\u201d</p><p>\u743c\u6069\u4e0d\u89e3\uff0c\u4f46\u8fd8\u662f\u4f9d\u8a00\u4e56\u4e56\u95ed\u4e0a\u4e86\u773c\u775b\u3002\u73ca\u5609\u6491\u8d77\u8eab\u6765\uff0c\u534a\u8dea\u5728\u5e8a\u4e0a\uff0c\u4f38\u624b\u5c06\u743c\u6069\u7684\u7761\u88e4\u892a\u4e0b\uff0c\u521a\u521a\u62c9\u4e0b\u5c0f\u534a\u622a\uff0c\u4fbf\u89c1\u4e00\u6761\u7d2b\u7ea2\u8272\u5de8\u9f99\u5f39\u4e86\u51fa\u6765\uff0c\u6b63\u62cd\u5728\u7c89\u988a\u4e0a\uff0c\u53d1\u51fa\u556a\u5730\u4e00\u58f0\u8f7b\u54cd\uff0c\u5012\u662f\u5413\u4e86\u5979\u4e00\u8df3\u3002\u201c\u6da8\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u662f\u4e0d\u662f\u5f88\u96be\u53d7\u554a\uff0c\u96be\u602a\u7761\u4e0d\u7740\uff0c\u201d\u5c11\u5973\u67d4\u58f0\u8bf4\uff0c\u201c\u8ba9\u59d0\u59d0\u5e2e\u4f60\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u3002\u201d</p><p>\u73ca\u5609\u683c\u683c\u5a07\u7b11\uff0c\u9999\u820c\u5fae\u5410\uff0c\u5728\u6775\u5c16\u4e0a\u8f7b\u8f7b\u6253\u8f6c\uff0c\u6e29\u6fe1\u8214\u8210\uff0c\u7136\u540e\u5f20\u53e3\u6162\u6162\u542b\u5165\u3002\u5979\u66fe\u7ecf\u548c\u8299\u857e\u72c4\u95fa\u4e2d\u5bc6\u8bed\uff0c\u77e5\u9053\u743c\u6069\u7684\u559c\u597d\uff0c\u5fc3\u7406\u4e0a\u65e9\u6709\u51c6\u5907\uff0c\u4f46\u7ec8\u7a76\u521d\u8bd5\u6b64\u9053\uff0c\u5168\u65e0\u7ecf\u9a8c\uff0c\u65e0\u8bba\u5982\u4f55\u52aa\u529b\u4e5f\u53ea\u80fd\u52c9\u5f3a\u541e\u8fdb\u5c0f\u534a\u622a\uff0c\u53cd\u800c\u62b5\u5f97\u54bd\u5589\u9690\u9690\u4f5c\u75bc\uff0c\u5c0f\u5634\u585e\u5f97\u6ee1\u6ee1\uff0c\u51e0\u4e4e\u8fde\u547c\u5438\u90fd\u900f\u4e0d\u8fc7\u6765\u3002\u8fde\u8bd5\u51e0\u6b21\uff0c\u6700\u7ec8\u8fd8\u662f\u53ea\u80fd\u65e0\u5948\u653e\u5f03\u4e86\uff0c\u201c\u771f\u662f\u7684\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u5a07\u55d4\u7740\uff0c\u201c\u5e72\u561b\u8fd9\u4e48\u5927\u3002\u201d</p><p>\u4e00\u8fb9\u57cb\u6028\uff0c\u5979\u4fa7\u8eab\u5367\u7740\uff0c\u5c06\u5934\u6795\u5728\u743c\u6069\u5927\u817f\u4e0a\uff0c\u8138\u8d34\u7740\u5185\u4fa7\uff0c\u5de6\u81c2\u4f38\u51fa\uff0c\u7389\u624b\u63e1\u7740\u90a3\u6761\u6602\u626c\u5de8\u9f99\uff0c\u8f7b\u8f7b\u629a\u6478\u5957\u5f04\u3002\u5979\u52a8\u4f5c\u5e76\u4e0d\u719f\u7ec3\uff0c\u4e5f\u5e76\u4e0d\u5feb\uff0c\u4f46\u638c\u5fc3\u4e30\u8174\u67d4\u817b\uff0c\u9999\u6c57\u5fae\u6c81\uff0c\u7565\u5e26\u6e29\u6e7f\uff0c\u7ea4\u624b\u76c8\u76c8\u63e1\u7740\uff0c\u8ba9\u743c\u6069\u51e0\u4e4e\u6709\u79cd\u5176\u5b9e\u662f\u5728\u5973\u5b50\u4f53\u81a3\u5185\u7684\u9519\u89c9\u3002\u800c\u4e14\u4eff\u4f5b\u662f\u59d0\u5f1f\u95f4\u7684\u5fc3\u6709\u7075\u7280\uff0c\u73ca\u5609\u52a8\u4f5c\u867d\u7136\u4e0d\u5feb\uff0c\u5374\u603b\u662f\u80fd\u591f\u6070\u5230\u597d\u5904\u5730\u628a\u63e1\u743c\u6069\u7684\u5fc3\u601d\uff0c\u7ea4\u7ea4\u7389\u6307\u987a\u7740\u4ed6\u7684\u6b32\u671b\u6162\u6162\u629a\u6170\uff0c\u7ec6\u7ec6\u6311\u9017\uff0c\u7eb5\u7136\u662f\u5982\u6b64\u6deb\u7cdc\u7684\u573a\u666f\uff0c\u5374\u4e5f\u88ab\u5979\u6f14\u7ece\u5f97\u4f18\u7f8e\u96c5\u81f4\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u5728\u6f14\u594f\u4e50\u5668\u4e00\u822c\u3002</p><p>\u201c\u53c8\u53d8\u5927\u4e86\u2026\u2026\u201d</p><p>\u5c11\u5973\u4f4e\u4f4e\u60ca\u547c\uff0c\u58f0\u97f3\u53c8\u5a07\u53c8\u5a9a\uff0c\u547c\u51fa\u7684\u70ed\u6c14\u5439\u62c2\u4e0a\u6765\uff0c\u8ba9\u743c\u6069\u60c5\u4e0d\u81ea\u7981\u5730\u5168\u8eab\u98a4\u6296\uff0c\u73ca\u5609\u89c1\u72b6\u751c\u751c\u4e00\u7b11\uff0c\u624b\u4e0a\u901f\u5ea6\u8d8a\u53d1\u52a0\u5feb\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u611f\u89c9\u81ea\u5df1\u5df2\u7ecf\u6fd2\u4e34\u5373\u5c06\u7206\u53d1\u7684\u8fb9\u7f18\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u73ca\u5609\u7a81\u7136\u505c\u4f4f\u4e86\u624b\u3002</p><p>\u201c\u662f\u4e0d\u662f\u5feb\u8981\u2026\u2026\u201d\u5979\u5df2\u7ecf\u6f6e\u7ea2\u7684\u8138\u4e0a\u6cdb\u8d77\u4e00\u62b9\u7f9e\u8272\uff0c\u201c\u5feb\u8981\u51fa\u6765\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u53ef\u662f\u5e8a\u5355\u662f\u65b0\u6362\u7684\u5462\uff0c\u201d\u5c11\u5973\u5658\u8d77\u5c0f\u5634\uff0c\u201c\u4e0d\u60f3\u628a\u5b83\u5f04\u810f\u4e86\uff0c\u600e\u4e48\u529e\uff1f\u201d</p><p>\u743c\u6069\u6b64\u65f6\u5fc3\u8ff7\u795e\u4e71\uff0c\u54ea\u91cc\u8fd8\u56de\u7b54\u5f97\u51fa\u8fd9\u4e2a\u95ee\u9898\uff0c\u4f46\u73ca\u5609\u539f\u672c\u4e5f\u6ca1\u6709\u671f\u671b\u7b54\u6848\u3002\u5979\u7565\u60f3\u4e86\u60f3\uff0c\u62ac\u624b\u6311\u8d77\u5782\u4e0b\u7684\u51e0\u7f15\u4e71\u53d1\uff0c\u62c2\u5728\u8033\u540e\uff0c\u4f4e\u4e0b\u5934\u53bb\uff0c\u5f20\u53e3\u542b\u4f4f\u3002\u4eff\u4f5b\u555c\u996e\u9999\u751c\u7684\u725b\u5976\uff0c\u73ca\u5609\u5589\u5934\u6eda\u52a8\uff0c\u53d1\u51fa\u8f7b\u5fae\u7684\u5495\u565c\u58f0\uff0c\u4e00\u53e3\u53e3\u5c06\u743c\u6069\u6eda\u70eb\u7684\u4e73\u767d\u7cbe\u534e\u6162\u6162\u541e\u54bd\u5165\u8179\uff0c\u70b9\u6ef4\u4e0d\u5269\u3002</p><p>\u201c\u8212\u670d\u5417\uff0c\u5c0f\u5f1f\u3002\u201d</p><p>\u6b22\u6109\u4e4b\u540e\uff0c\u743c\u6069\u8eba\u4e0b\u6765\u5598\u606f\uff0c\u73ca\u5609\u50cf\u4e00\u53ea\u6e29\u987a\u4e56\u5de7\u7684\u5c0f\u732b\u54aa\u8737\u5728\u4ed6\u6000\u4e2d\uff0c\u8be2\u95ee\u5f1f\u5f1f\u7684\u611f\u53d7\uff0c\u8138\u4e0a\u63a9\u4e0d\u53bb\u7684\u8c03\u76ae\u7b11\u610f\uff0c\u9690\u9690\u8fd8\u6709\u51e0\u5206\u5f97\u610f\u6a21\u6837\uff0c\u4eff\u4f5b\u5929\u771f\u70c2\u6f2b\u7684\u5b69\u5b50\u4f3c\u7684\uff0c\u770b\u5f97\u743c\u6069\u5fc3\u4e2d\u4e00\u8361\u3002\u201c\u8212\u670d\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u8c22\u8c22\u59d0\u59d0\u3002\u201d</p><p>\u4ed6\u4eb2\u543b\u7740\u73ca\u5609\u7684\u79c0\u53d1\u3001\u989d\u5934\uff0c\u7136\u540e\u4e00\u70b9\u70b9\u4e0b\u79fb\uff0c\u7709\u6bdb\u3001\u9f3b\u6881\u3001\u8138\u988a\uff0c\u6700\u540e\u662f\u5634\u5507\u3002\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u4f4e\u4f4e\u547c\u5524\u7740\uff0c\u5598\u606f\u7740\uff0c\u547c\u5438\u6eda\u70eb\uff0c\u201c\u6211\u60f3\u8981\u2026\u2026\u201d</p><p>\u201c\u60f3\u8981\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u60f3\u8981\u59d0\u59d0\u3002\u201d</p><p>\u73ca\u5609\u9759\u9759\u51dd\u89c6\u7740\u4ed6\uff0c\u773c\u4e2d\u5c3d\u662f\u6e29\u67d4\u4e4b\u8272\uff0c\u201c\u59d0\u59d0\u7b49\u4f60\uff0c\u5df2\u7ecf\u7b49\u4e86\u5f88\u4e45\u5462\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f60\u8fd8\u8981\u8ba9\u59d0\u59d0\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\uff1f\u201d</p><p>\u5979\u95ed\u4e0a\u773c\u775b\uff0c\u8fce\u63a5\u671f\u5f85\u5df2\u4e45\u7684\u547d\u8fd0\uff1b\u4ed6\u8f7b\u8f7b\u543b\u904d\u5c11\u5973\u7684\u5168\u8eab\u6bcf\u4e00\u5bf8\u808c\u80a4\uff0c\u7136\u540e\u7f13\u7f13\u4fb5\u5165\u3002\u5979\u5fcd\u4e0d\u4f4f\u5730\u98a4\u6817\u7740\uff0c\u54ac\u7d27\u5634\u5507\uff0c\u604d\u60da\u4e2d\u9519\u89c9\u81ea\u5df1\u7684\u8eab\u4f53\u5df2\u7ecf\u88ab\u6495\u88c2\uff0c\u4f46\u6655\u7729\u7684\u75db\u695a\u4e2d\u4f9d\u65e7\u900f\u51fa\u4e00\u4e1d\u4e1d\u6e05\u6670\u7684\u751c\u871c\u5e78\u798f\u3002\u575a\u786c\u5982\u94c1\u7684\u70bd\u70ed\u5de8\u7269\u7a81\u7834\u6e7f\u6da6\u7684\u82b1\u854a\uff0c\u4e00\u70b9\u70b9\u5730\u6324\u5165\u5a07\u5ae9\u82b1\u5f84\uff0c\u5f00\u57a6\u7740\u672a\u7ecf\u4eba\u4e8b\u7684\u5904\u5973\u5730\u3002\u5979\u7528\u5c3d\u5168\u529b\u7d27\u7d27\u6402\u62b1\u7740\u4ed6\uff0c\u8ba9\u4ed6\u5b8c\u5168\u62b5\u8fbe\u81ea\u5df1\u7684\u6700\u6df1\u5904\u3002</p><p>\u4ed6\u4eec\u878d\u4e3a\u4e00\u4f53\u3002</p><p>\u203b\u203b\u203b</p><p>\u623f\u95f4\u91cc\u6e10\u6e10\u6c89\u5bc2\u4e0b\u53bb\uff0c\u5598\u606f\u58f0\u53d8\u5f97\u6e05\u6670\u53ef\u95fb\u3002\u73ca\u5609\u8737\u7f29\u5728\u5f1f\u5f1f\u6000\u4e2d\uff0c\u7ea2\u6655\u672a\u6d88\u7684\u8138\u4e0a\u8fd8\u4f9d\u7a00\u6302\u7740\u6cea\u75d5\uff0c\u743c\u6069\u7684\u80f8\u53e3\u4e0a\uff0c\u5df2\u7ecf\u88ab\u5979\u7684\u8d1d\u9f7f\u54ac\u51fa\u4e00\u6392\u7ec6\u7ec6\u7684\u7259\u5370\uff0c\u4e24\u4eba\u7684\u4e0b\u8eab\u8fd8\u7d27\u5bc6\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u73ca\u5609\u7a0d\u4e00\u52a8\u5f39\u4fbf\u89c9\u4f53\u5185\u706b\u8fa3\u8fa3\u7684\u75bc\u75db\uff0c\u5fcd\u4e0d\u4f4f\u5012\u5438\u51b7\u6c14\u3002\u201c\u5927\u574f\u86cb\uff0c\u201d\u5979\u53c8\u55d4\u53c8\u6028\u5730\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u90a3\u4e48\u51f6\uff0c\u60f3\u628a\u59d0\u59d0\u5f04\u6b7b\u554a\u3002\u600e\u4e48\u6c42\u9976\u90fd\u4e0d\u80af\u653e\u2026\u2026\u201d</p><p>\u743c\u6069\u4f4e\u5934\u4eb2\u543b\u7740\u5979\u7684\u8138\u988a\uff0c\u201c\u5bf9\u4e0d\u8d77\u5566\uff0c\u59d0\u59d0\uff0c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u5b9e\u5728\u5fcd\u4e0d\u4f4f\uff0c\u59d0\u59d0\u7684\u8eab\u4f53\u592a\u8bf1\u4eba\u3002\u201d</p><p>\u201c\u4ece\u5c0f\u5c31\u8fd9\u6837\uff0c\u201d\u73ca\u5609\u5658\u8d77\u5c0f\u5634\uff0c\u201c\u6bcf\u6b21\u6b3a\u8d1f\u5b8c\u4e86\uff0c\u8fd8\u603b\u662f\u628a\u8d23\u4efb\u63a8\u5230\u59d0\u59d0\u8eab\u4e0a\uff0c\u8bf4\u7684\u597d\u50cf\u662f\u59d0\u59d0\u6545\u610f\u52fe\u5f15\u4f60\u3002\u201d</p><p>\u201c\u672c\u6765\u5c31\u662f\uff0c\u201d\u743c\u6069\u8c03\u7b11\uff0c\u201c\u59d0\u59d0\u672c\u6765\u5c31\u662f\u5728\u52fe\u5f15\u6211\u561b\u3002\u201d</p><p>\u73ca\u5609\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u4e5f\u6ca1\u53cd\u9a73\uff0c\u9759\u9759\u5730\u8eba\u5728\u4ed6\u6000\u4e2d\u3002\u201c\u5c0f\u5f1f\uff0c\u201d\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u7a81\u7136\u8f7b\u58f0\u95ee\uff0c\u201c\u59d0\u59d0\u597d\u5403\u4e48\uff1f\u201d</p><p>\u201c\u5f53\u7136\u597d\u5403\uff0c\u201d\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u73b0\u5728\u6211\u552f\u4e00\u540e\u6094\u7684\uff0c\u5c31\u662f\u4ee5\u524d\u5c45\u7136\u6d6a\u8d39\u4e86\u90a3\u4e48\u591a\u65f6\u95f4\u3002\u201d</p><p>\u201c\u6bd4\u4f60\u5c1d\u8fc7\u7684\u5176\u4ed6\u5973\u5b69\u5b50\u5462\uff1f\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u201c\u5e72\u561b\u8bf4\u8fd9\u4e2a\u554a\uff0c\u59d0\u59d0\u3002\u201d</p><p>\u201c\u5c31\u662f\u95ee\u95ee\u800c\u5df2\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u662f\u4f60\u59d0\u59d0\uff0c\u73b0\u5728\u8fd8\u662f\u4f60\u60c5\u4eba\uff0c\u5c06\u6765\u662f\u4f60\u59bb\u5b50\uff0c\u6211\u6709\u6743\u5229\u77e5\u9053\u5427\u3002\u201d</p><p>\u201c\u5f53\u7136\u662f\u4f60\u6700\u597d\u5566\uff0c\u201d\u743c\u6069\u62b1\u7d27\u5979\uff0c\u201c\u4e16\u754c\u4e0a\u6709\u54ea\u4e2a\u5973\u5b69\u5b50\uff0c\u80fd\u6bd4\u5f97\u4e0a\u6211\u7684\u59d0\u59d0\u5462\u3002\u201d</p><p>\u201c\u827e\u5f25\u8587\u5462\uff1f\u201d\u73ca\u5609\u53cd\u95ee\uff0c\u201c\u548c\u6211\u76f8\u6bd4\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd9\u4e2a\uff0c\u4e0d\u597d\u8bf4\u5462\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u8ffd\u95ee\uff0c\u201c\u4f60\u521a\u624d\u4e0d\u662f\u8bf4\u59d0\u59d0\u6700\u597d\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u201d\u743c\u6069\u4f4e\u4e0b\u5934\uff0c\u5728\u5979\u8033\u8fb9\u8f7b\u58f0\u8bf4\uff0c\u201c\u56e0\u4e3a\u6211\u8fd8\u6ca1\u771f\u6b63\u5c1d\u8fc7\u827e\u5f25\u8587\uff0c\u6240\u4ee5\u6ca1\u6cd5\u6bd4\u8f83\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u743c\u6069\u7684\u56de\u7b54\uff0c\u663e\u7136\u5b8c\u5168\u51fa\u4e4e\u73ca\u5609\u7684\u610f\u6599\u3002\u201c\u4f60\u4eec\u4e0d\u662f\u65e9\u5c31\u2026\u2026\u201d</p><p>\u201c\u662f\uff0c\u4f46\u662f\u2026\u2026\u201d\u743c\u6069\u539f\u672c\u642d\u5728\u73ca\u5609\u88f8\u80cc\u4e0a\u7684\u53f3\u624b\u7f13\u7f13\u4e0b\u79fb\uff0c\u63b0\u5f00\u4e30\u6ee1\u633a\u7fd8\u7684\u81c0\u4e18\uff0c\u6307\u5c16\u8f7b\u8f7b\u63c9\u6309\uff0c\u201c\u662f\u7528\u8fd9\u91cc\u3002\u201d</p><p>\u4ed6\u7b80\u5355\u628a\u4e8b\u60c5\u7684\u7f18\u7531\u89e3\u91ca\u4e86\u4e00\u904d\uff0c\u73ca\u5609\u65e2\u662f\u60ca\u5947\uff0c\u53c8\u89c9\u4e0d\u53ef\u601d\u8bae\uff0c\u201c\u4f60\u4eec\u4e00\u76f4\u90fd\u8fd9\u6837\u2026\u2026\u53ef\u662f\uff0c\u201d\u5979\u6084\u6084\u62ac\u773c\u770b\u4e86\u770b\u743c\u6069\uff0c\u201c\u5973\u5b69\u5b50\u7684\u90a3\u5730\u65b9\uff0c\u4e5f\u2026\u2026\u4e5f\u53ef\u4ee5\u4e48\uff1f\u201d</p><p>\u201c\u53ef\u4ee5\u554a\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u522b\u6709\u4e00\u79cd\u6ecb\u5473\u5462\uff0c\u7b49\u54ea\u5929\u6709\u7a7a\uff0c\u6211\u4e5f\u8ba9\u59d0\u59d0\u8bd5\u8bd5\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u4ed6\u539f\u672c\u53ea\u662f\u4fe1\u53e3\u73a9\u7b11\uff0c\u6599\u60f3\u73ca\u5609\u4e00\u5b9a\u4f1a\u5413\u5f97\u82b1\u5bb9\u53d8\u8272\uff0c\u8fde\u58f0\u62d2\u7edd\uff0c\u8fd9\u4e5f\u662f\u7edd\u5927\u591a\u6570\u5973\u5b69\u5b50\u7684\u6b63\u5e38\u53cd\u5e94\uff0c\u5373\u4fbf\u6885\u83f2\u65af\u7b2c\u4e00\u6b21\u542c\u5230\u7684\u65f6\u5019\u4e5f\u662f\u5982\u6b64\u3002\u8ba9\u4ed6\u610f\u5916\u7684\u662f\uff0c\u73ca\u5609\u5374\u5fae\u5fae\u4f4e\u4e0b\u5934\u53bb\uff0c\u201c\u55ef\uff0c\u597d\u554a\uff0c\u201d\u5979\u8f7b\u58f0\u7b54\u5e94\uff0c\u201c\u53ea\u8981\u4f60\u559c\u6b22\u5c31\u884c\u3002\u201d</p><p>\u201c\u59d0\u59d0\u2026\u2026\u201d</p><p>\u201c\u59d0\u59d0\u662f\u4f60\u7684\u5440\uff0c\u201d\u6e05\u695a\u5730\u77e5\u9053\u743c\u6069\u7684\u5fc3\u601d\uff0c\u73ca\u5609\u5fae\u5fae\u7b11\u7740\u89e3\u91ca\uff0c\u201c\u59d0\u59d0\u662f\u4f60\u7684\uff0c\u4ec0\u4e48\u90fd\u662f\u3002\u4f60\u4e0d\u662f\u8bf4\u5417\uff1f\u5f1f\u5f1f\u8fd9\u79cd\u751f\u7269\uff0c\u5c31\u662f\u7528\u6765\u6b3a\u8d1f\u59d0\u59d0\u7684\uff0c\u90a3\u4e48\u59d0\u59d0\u5462\uff0c\u5c31\u662f\u8ba9\u4f60\u8fd9\u4e2a\u5f1f\u5f1f\u6b3a\u8d1f\u7684\u3002\u53ea\u8981\u4f60\u9ad8\u5174\uff0c\u4ec0\u4e48\u90fd\u53ef\u4ee5\u3002\u201d</p><p>\u5979\u5c06\u8138\u8d34\u5728\u4ed6\u7684\u80f8\u53e3\uff0c\u4eff\u4f5b\u5728\u542c\u5fc3\u8df3\uff0c\u53c8\u4eff\u4f5b\u662f\u6c89\u6c89\u7761\u53bb\u3002\u743c\u6069\u770b\u7740\u5979\u7684\u9762\u5bb9\uff0c\u4e0d\u89c9\u4e5f\u6709\u4e86\u4e9b\u5026\u610f\u3002\u4ed6\u539f\u672c\u60f3\u7740\u8fd9\u662f\u59d0\u59d0\u5904\u5b50\u521d\u591c\uff0c\u4e0d\u6562\u8086\u610f\uff0c\u8c01\u6599\u73ca\u5609\u5929\u751f\u5a9a\u9aa8\uff0c\u82b1\u5f84\u7d27\u81f4\uff0c\u66f2\u6298\u5e7d\u6df1\uff0c\u4f53\u5185\u6e29\u70ed\u6e7f\u6fe1\uff0c\u5a07\u5ae9\u871c\u8089\u5c42\u5c42\u8fed\u8fed\u88f9\u7f20\u4e0a\u6765\uff0c\u6bd4\u4ed6\u5c1d\u8bd5\u8fc7\u7684\u4efb\u4f55\u5973\u5b50\u90fd\u8981\u6765\u5f97\u9500\u9b42\uff0c\u4e00\u65f6\u95f4\u60c5\u4e0d\u81ea\u7981\uff0c\u65e9\u628a\u601c\u60dc\u7684\u5ff5\u5934\u629b\u5230\u4e5d\u9704\u4e91\u5916\uff0c\u6a2a\u51b2\u76f4\u95ef\uff0c\u5927\u52a0\u5f81\u631e\uff0c\u5230\u6700\u540e\u90fd\u5df2\u7ecf\u8bb0\u4e0d\u6e05\u7a76\u7adf\u8981\u4e86\u5979\u591a\u5c11\u6b21\uff0c\u5982\u4eca\u53ea\u89c9\u5934\u6655\u773c\u82b1\uff0c\u5168\u8eab\u53d1\u8f6f\uff0c\u6e10\u6e10\u4fbf\u4e5f\u60f3\u7761\u4e86\u8fc7\u53bb\u3002</p><p>\u6b63\u8ff7\u8ff7\u7cca\u7cca\u95f4\uff0c\u73ca\u5609\u7a81\u7136\u8f7b\u8f7b\u52a8\u4e86\u52a8\uff0c\u5728\u4ed6\u6000\u91cc\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5c06\u5149\u6d01\u7684\u88f8\u80cc\u8d34\u7740\u4ed6\u80f8\u819b\uff0c\u201c\u5c0f\u5f1f\uff1f\u201d\u5979\u8f7b\u8f7b\u547c\u5524\u3002</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u542b\u7cca\u5e94\u4e86\u58f0\u3002</p><p>\u201c\u59d0\u59d0\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u597d\u3002\u201d</p><p>\u201c\u4ee5\u540e\u6bcf\u5929\u90fd\u966a\u7740\u59d0\u59d0\uff0c\u6bcf\u591c\u59d0\u59d0\u90fd\u50cf\u8fd9\u6837\u670d\u4f8d\u4f60\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u597d\u3002\u201d</p><p>\u201c\u90a3\u6709\u4e86\u59d0\u59d0\uff0c\u662f\u4e0d\u662f\u5c31\u591f\u4e86\uff1f\u201d\u73ca\u5609\u4f4e\u58f0\u8bf4\uff0c\u201c\u59d0\u59d0\u4e00\u5fc3\u4e00\u610f\u5bf9\u4f60\uff0c\u4f60\u4e5f\u4e00\u5fc3\u4e00\u610f\u5bf9\u59d0\u59d0\uff0c\u4e0d\u8981\u5176\u4ed6\u5973\u5b69\u5b50\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u743c\u6069\u6b63\u8981\u8131\u53e3\u800c\u51fa\u8bf4\u201c\u597d\u201d\uff0c\u9661\u7136\u8111\u4e2d\u4e00\u4e1d\u6e05\u660e\u95ea\u8fc7\uff0c\u987f\u65f6\u8fdf\u7591\u8d77\u6765\u3002\u73ca\u5609\u7a0d\u7a0d\u7b49\u5f85\uff0c\u89c1\u4ed6\u6c89\u9ed8\u4e0d\u8bed\uff0c\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u201c\u7b97\u5566\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u4ece\u5c0f\u770b\u4f60\u957f\u5927\uff0c\u59d0\u59d0\u8fd8\u4e0d\u4e86\u89e3\u4f60\u4e48\u3002\u8d2a\u5fc3\u5f97\u8981\u547d\uff0c\u770b\u8d77\u6765\u6700\u5bb9\u6613\u6ee1\u8db3\uff0c\u5176\u5b9e\u600e\u4e48\u90fd\u4e0d\u591f\u3002\u59d0\u59d0\u4e00\u4e2a\u4eba\uff0c\u5c31\u7b97\u662f\u518d\u597d\uff0c\u4e5f\u586b\u4e0d\u9971\u4f60\u7684\u80c3\u53e3\u5462\u3002\u201d</p><p>\u201c\u59d0\u59d0\u65e9\u5c31\u77e5\u9053\u8fd9\u70b9\uff0c\u53ea\u662f\u2026\u2026\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u201c\u59d0\u59d0\u771f\u7684\uff0c\u771f\u7684\u597d\u4e0d\u7518\u5fc3\u597d\u4e0d\u7518\u5fc3\u554a\u3002\u201d</p><p>\u5979\u5782\u4e0b\u773c\u5e18\uff0c\u4e24\u884c\u6e05\u6cea\u81ea\u8138\u988a\u6ed1\u8fc7\uff0c\u6e17\u5165\u6795\u4e2d\uff0c\u6162\u6162\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u203b\u203b\u203b</p><p>\u68a6\u3002</p><p>\u743c\u6069\u53d1\u89c9\u81ea\u5df1\u4eff\u4f5b\u7f6e\u8eab\u4e8e\u4e00\u5904\u6797\u95f4\u8349\u5730\u4e0a\uff0c\u6811\u51a0\u4ead\u4ead\u5982\u76d6\uff0c\u906e\u853d\u4e86\u5927\u90e8\u4efd\u7684\u9633\u5149\uff0c\u8ba9\u8fd9\u5757\u7a7a\u95f4\u663e\u5f97\u6709\u4e9b\u9eef\u6de1\u3002\u78a7\u7eff\u7684\u9752\u8349\u4e2d\uff0c\u661f\u661f\u70b9\u70b9\u5e03\u6ee1\u4e86\u7d2b\u8272\u7684\u82b1\u6735\uff0c\u4ed6\u4f4e\u5934\u7ec6\u770b\uff0c\u53d1\u89c9\u662f\u9634\u5f71\u5170\uff0c\u4f46\u548c\u5e73\u65f6\u6240\u5e38\u89c1\u7684\u54c1\u79cd\u663e\u7136\u4e0d\u540c\uff0c\u66f4\u6f02\u4eae\uff0c\u4f46\u4e5f\u66f4\u8be1\u5f02\uff0c\u6709\u79cd\u865a\u5e7b\u4e0d\u5b9e\u7684\u611f\u89c9\u3002</p><p>\u62ac\u8d77\u5934\uff0c\u770b\u89c1\u4e0d\u8fdc\u5904\u7684\u767d\u8272\u5854\u5c16\uff0c\u90a3\u662f\u97f3\u4e50\u5b66\u9662\u7684\u4e3b\u697c\uff0c\u6574\u5ea7\u9634\u9b42\u57ce\u4e2d\u72ec\u6b64\u4e00\u5bb6\u3002\u201c\u8fd9\u91cc\u8ddd\u79bb\u97f3\u4e50\u5b66\u9662\u5f88\u8fd1\uff1f\u201d\u743c\u6069\u521a\u521a\u95ea\u8fc7\u8fd9\u4e2a\u5ff5\u5934\uff0c\u7136\u540e\u89c6\u91ce\u4e2d\u51fa\u73b0\u4e86\u4e24\u4e2a\u4eba\u5f71\uff0c\u6b63\u4ece\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\u3002</p><p>\u4ed6\u4eec\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u4f46\u5374\u8f6c\u773c\u95f4\u4fbf\u8fdb\u5165\u6797\u4e2d\uff0c\u743c\u6069\u6b32\u5f85\u56de\u907f\u5df2\u7ecf\u6765\u4e0d\u53ca\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u4e5f\u5bf9\u743c\u6069\u89c6\u800c\u4e0d\u89c1\uff0c\u4eff\u4f5b\u6839\u672c\u5c31\u628a\u4ed6\u5f53\u4f5c\u4e86\u4e00\u56e2\u7a7a\u6c14\uff0c\u76f4\u63a5\u4ece\u8eab\u4fa7\u8d70\u8fc7\u3002</p><p>\u4e0d\u77e5\u9053\u4ec0\u4e48\u7f18\u6545\uff0c\u660e\u660e\u8ddd\u79bb\u5f88\u8fd1\uff0c\u743c\u6069\u5374\u5b8c\u5168\u65e0\u6cd5\u770b\u6e05\u695a\u4ed6\u4eec\u7684\u76f8\u8c8c\uff0c\u53ea\u80fd\u4ece\u8eab\u5f62\u8f6e\u5ed3\u4e0a\u5224\u65ad\u662f\u4e00\u7537\u4e00\u5973\uff0c\u800c\u4e14\u5e94\u8be5\u90fd\u5f88\u5e74\u8f7b\u3002\u5973\u5b50\u4f3c\u4e4e\u8eab\u4f53\u4e0d\u9002\uff0c\u6b65\u4f10\u6709\u4e9b\u865a\u6d6e\uff0c\u7537\u5b50\u4ece\u8eab\u65c1\u6400\u6276\u7740\u5979\u3002\u66f4\u5947\u602a\u7684\u662f\uff0c\u743c\u6069\u9690\u7ea6\u89c9\u5f97\u4ed6\u4eec\u7684\u8eab\u5f62\u8f6e\u5ed3\u90fd\u9887\u6709\u51e0\u5206\u773c\u719f\uff0c\u597d\u50cf\u662f\u81ea\u5df1\u8ba4\u8bc6\u7684\u4eba\uff0c\u4f46\u5374\u53c8\u5b9e\u5728\u60f3\u4e0d\u8d77\u662f\u8c01\u3002</p><p>\u7537\u5b50\u7a7f\u7740\u7070\u8272\u957f\u888d\uff0c\u7f69\u7740\u7070\u8272\u6597\u7bf7\uff0c\u5168\u8eab\u4eff\u4f5b\u90fd\u7b3c\u7f69\u5728\u9634\u5f71\u4e4b\u4e2d\uff0c\u5f71\u5f71\u7ef0\u7ef0\uff0c\u865a\u5e7b\u4e0d\u5b9e\uff0c\u743c\u6069\u770b\u4e86\u7247\u523b\uff0c\u53ea\u89c9\u5934\u6655\u773c\u82b1\uff0c\u5c06\u76ee\u5149\u79fb\u5411\u4ed6\u65c1\u8fb9\u7684\u5973\u5b50\uff0c\u731b\u7136\u95f4\u5fc3\u5934\u4e00\u8df3\u3002</p><p>\u90a3\u540d\u5973\u5b50\u7684\u8170\u95f4\uff0c\u522b\u7740\u4e00\u652f\u957f\u7b1b\uff0c\u770b\u5916\u5f62\u89c4\u683c\uff0c\u548c\u73ca\u5609\u90a3\u652f\u591c\u6c89\u6728\u957f\u7b1b\u975e\u5e38\u8fd1\u4f3c\u3002</p><p>\u73ca\u5609\uff1f</p><p>\u743c\u6069\u9519\u6115\u4e4b\u4e0b\uff0c\u52aa\u529b\u60f3\u8981\u770b\u6e05\u5973\u5b50\u7684\u76f8\u8c8c\uff0c\u5374\u59cb\u7ec8\u65e0\u6cd5\u6210\u529f\u3002\u4ed6\u4eec\u5728\u8349\u5730\u7684\u4e2d\u95f4\u7ad9\u4f4f\uff0c\u50cf\u662f\u4ea4\u8c08\u4e86\u51e0\u53e5\uff0c\u4f46\u743c\u6069\u4ec0\u4e48\u90fd\u542c\u4e0d\u89c1\uff0c\u53ea\u80fd\u770b\u5230\u53e3\u5507\u5fae\u52a8\u3002\u4ed6\u6ca1\u5b66\u8fc7\u8bfb\u5507\u672f\uff0c\u65e0\u6cd5\u5224\u65ad\u5185\u5bb9\u3002\u4f46\u6709\u4e00\u70b9\u8fd8\u662f\u53ef\u4ee5\u770b\u51fa\u6765\u7684\uff1a\u5979\u5728\u7b11\uff0c\u5f88\u5f00\u5fc3\uff0c\u975e\u5e38\u6109\u5feb\uff0c\u800c\u4e14\u795e\u60c5\u4eb2\u5bc6\uff0c\u663e\u7136\u5173\u7cfb\u975e\u540c\u5bfb\u5e38\u3002</p><p>\u743c\u6069\u5992\u706b\u4e2d\u70e7\uff0c\u6b32\u5f85\u4e0a\u524d\uff0c\u4f46\u4ed6\u5bf9\u4e8e\u8fd9\u4e24\u4eba\u800c\u8a00\u50cf\u662f\u4e00\u56e2\u5e7b\u5f71\uff0c\u54ea\u6015\u7ad9\u5728\u9762\u524d\u4e5f\u65e0\u6cd5\u5f15\u8d77\u4ed6\u4eec\u7684\u6ce8\u610f\uff0c\u4eff\u4f5b\u6839\u672c\u5c31\u4e0d\u5b58\u5728\u4e00\u822c\u3002</p><p>\u201c\u96be\u4e0d\u6210\u2026\u2026\u8fd9\u662f\u68a6\u5883\uff1f\u201d</p><p>\u5ff5\u5934\u521a\u521a\u95ea\u8fc7\uff0c\u743c\u6069\u7684\u773c\u89d2\u9661\u7136\u95ea\u8fc7\u4e00\u62b9\u91d1\u5c5e\u5149\u6cfd\uff0c\u60ca\u8bb6\u4e4b\u4e0b\u8f6c\u773c\u770b\u53bb\uff0c\u53d1\u73b0\u90a3\u540d\u7537\u5b50\u6b63\u7ad9\u5728\u5973\u5b50\u7684\u80cc\u540e\uff0c\u5de6\u624b\u8d34\u5728\u8170\u95f4\uff0c\u53f3\u624b\u5374\u81ea\u6597\u7bf7\u4e4b\u4e2d\uff0c\u6084\u65e0\u58f0\u606f\u5730\u53d6\u51fa\u4e00\u67c4\u5bd2\u5149\u95ea\u95ea\u7684\u5315\u9996\uff0c\u500f\u5ffd\u4e3e\u8d77\uff0c\u731b\u7136\u7167\u7740\u5973\u5b50\u75be\u523a\u8fc7\u53bb\u3002</p><p>\u201c\u5c0f\u5fc3\uff01\u201d</p><p>\u743c\u6069\u5931\u58f0\u558a\u51fa\uff0c\u5374\u8fde\u81ea\u5df1\u90fd\u542c\u4e0d\u5230\uff0c\u53ea\u89c1\u7537\u5b50\u624b\u4e2d\u5315\u9996\u95ea\u7535\u822c\u523a\u51fa\uff0c\u5728\u5373\u5c06\u8fd1\u8eab\u65f6\u5973\u5b50\u7ec8\u4e8e\u5bdf\u89c9\uff0c\u8f6c\u8eab\u56de\u65cb\uff0c\u4e00\u4e2a\u6f02\u4eae\u7684\u4fa7\u8e22\u51fb\u4e2d\u7537\u5b50\u7684\u819d\u76d6\uff0c\u4ee4\u4ed6\u6454\u5012\u5728\u5730\u3002\u4f46\u5979\u7684\u5de6\u81c2\u4f9d\u65e7\u8fd8\u662f\u88ab\u5315\u9996\u5212\u5f00\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u5f88\u6d45\uff0c\u4f46\u5979\u4eff\u4f5b\u53d7\u4e86\u81f4\u547d\u91cd\u521b\u4e00\u822c\uff0c\u8eab\u4f53\u6447\u6447\u6643\u6643\uff0c\u7ad9\u7acb\u4e0d\u7a33\uff0c\u8ddf\u7740\u4e5f\u6454\u5012\u5728\u8349\u5730\u4e2d\uff0c\u6fc0\u8d77\u4e00\u7247\u94f6\u8272\u82b1\u7c89\u3002</p><p>\u5315\u9996\u4e0a\u6709\u6bd2\uff01</p><p>\u743c\u6069\u7acb\u523b\u660e\u767d\u4e86\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u4ed6\u5b8c\u5168\u65e0\u80fd\u4e3a\u529b\uff0c\u53ea\u80fd\u65c1\u89c2\u3002\u7537\u5b50\u6323\u624e\u7740\u7ad9\u8d77\uff0c\u8d70\u5230\u5973\u5b50\u8eab\u65c1\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u4ea4\u8c08\u4e86\u51e0\u53e5\uff0c\u743c\u6069\u4f9d\u65e7\u4ec0\u4e48\u90fd\u542c\u4e0d\u89c1\uff0c\u4f46\u4ed6\u80fd\u591f\u6e05\u695a\u5730\u770b\u89c1\u5973\u5b50\u6e10\u6e10\u9eef\u6de1\u7684\u9762\u5bb9\uff0c\u4ee5\u53ca\u773c\u4e2d\u7684\u54c0\u4f24\u795e\u8272\u3002\u5728\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u6e05\u695a\u5730\u610f\u8bc6\u5230\u4e00\u4ef6\u4e8b\uff1a</p><p>\u5979\u5c31\u662f\u73ca\u5609\uff01</p><p>\u201c\u59d0\u59d0\uff01\u201d</p><p>\u4ed6\u65e2\u662f\u60f6\u6025\uff0c\u53c8\u662f\u6124\u6012\uff0c\u5374\u4ec0\u4e48\u90fd\u505a\u4e0d\u4e86\u3002\u731b\u7136\u95f4\u53ea\u89c9\u4e00\u9635\u5267\u70c8\u5fc3\u60b8\uff0c\u5c31\u50cf\u8eab\u4f53\u91cc\u6f5c\u4f0f\u7740\u4ec0\u4e48\u6d2a\u8352\u731b\u517d\uff0c\u6b63\u81ea\u82cf\u9192\uff0c\u8dc3\u8dc3\u6b32\u51fa\u3002\u4eff\u4f5b\u5fc3\u7075\u611f\u5e94\u4e00\u822c\uff0c\u8eba\u5728\u82b1\u4e1b\u4e2d\u7684\u5973\u5b50\u7a81\u7136\u50cf\u662f\u610f\u8bc6\u5230\u4e86\u4ec0\u4e48\uff0c\u5fae\u5fae\u4fa7\u8138\uff0c\u671d\u743c\u6069\u8fd9\u8fb9\u770b\u4e86\u4e00\u773c\u3002</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u5979\u662f\u5426\u770b\u89c1\u4e86\u81ea\u5df1\uff0c\u4f46\u63a5\u4e0b\u6765\uff0c\u4ed6\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u80fd\u591f\u6e05\u695a\u5730\u542c\u89c1\u4ed6\u4eec\u7684\u5bf9\u8bdd\u3002</p><p>\u201c\u63e1\u4f4f\u6211\u7684\u624b\u3002\u201d\u5973\u5b50\u6073\u6c42\uff0c\u5403\u529b\u5730\u5c06\u624b\u5411\u7537\u5b50\u4f38\u53bb\u3002</p><p>\u7537\u5b50\u51dd\u89c6\u7740\u4ed6\uff0c\u65e0\u52a8\u4e8e\u8877\u3002</p><p>\u201c\u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u5c06\u5b64\u72ec\u5730\u6b7b\u53bb\uff0c\u6bcd\u4eb2\u3002\u201d\u4ed6\u4f4e\u58f0\u8bf4\u3002</p><p>\u7136\u540e\u591c\u5e55\u964d\u4e34\u3002</p></article>"}}}]);
