(window.webpackJsonp=window.webpackJsonp||[]).push([[427],{450:function(p){p.exports={chapterTitle:null,title:"\u7b2c296\u7ae0 \u9b54\u517d\u4e89\u9738",content:"<article><h2>\u7b2c296\u7ae0 \u9b54\u517d\u4e89\u9738</h2><p>\u3000\u3000\u4ece\u6280\u672f\u6269\u5f20\u3001\u591a\u5143\u5316\u6218\u7565\u3001\u8d44\u672c\u5316\u8fd0\u4f5c\u4e09\u4e2a\u65b9\u9762\u5236\u5b9a\u7231\u8fbe\u7535\u5b50\u7684\u4e24\u5e74\u53d1\u5c55\u89c4\u5212\uff0c\u5f20\u606a\u8981\u6c42\u4e09\u6708\u521d\u5c31\u80fd\u62ff\u8349\u7a3f\u51fa\u6765\u8fdb\u884c\u8fdb\u4e00\u6b65\u7684\u8ba8\u8bba\uff0c\u82cf\u6d25\u4e1c\u662f\u603b\u8d1f\u8d23\u4eba\uff0c\u4e01\u69d0\u3001\u5468\u6e38\u3001\u9676\u884c\u5065\u3001\u9c81\u5e86\u751f\u3001\u848b\u8587\u5206\u522b\u8d1f\u8d23\u4e00\u5757\u7684\u64b0\u5199\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u6625\u8282\u773c\u671b\u7740\u5c31\u5230\u4e09\u6708\uff0c\u4e5f\u5c31\u4e8c\u5341\u5929\u7684\u65f6\u95f4\uff0c\u8d76\u7740\u6625\u8282\u671f\u95f4\uff0c\u516c\u53f8\u7684\u65e5\u5e38\u4e8b\u52a1\u901a\u5e38\u4f1a\u683c\u5916\u7684\u5fd9\u4e71\uff0c\u5f20\u606a\u7684\u8981\u6c42\u8fd8\u771f\u8ba9\u4eba\u4e0d\u80fd\u8f7b\u677e\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5176\u4ed6\u4eba\u8fd8\u597d\u4e00\u4e9b\uff0c\u6bd5\u7adf\u90fd\u662f\u65e5\u5e38\u5728\u601d\u8003\u7684\u95ee\u9898\uff0c\u73b0\u5728\u53ea\u662f\u7cfb\u7edf\u7684\u5c06\u601d\u8def\u6574\u7406\u4e00\u4e0b\uff0c\u9676\u884c\u5065\u8d1f\u8d23\u7684\u90a3\u4e00\u5757\u76f8\u5f53\u751f\u758f\uff0c\u867d\u7136\u624b\u91cc\u5934\u6709\u4e00\u4e9b\u9002\u5408\u7684\u58f3\u516c\u53f8\u7684\u8d44\u6599\uff0c\u4e0d\u4ec5\u4ec5\u5609\u4fe1\u5b9e\u4e1a\u4e00\u5bb6\uff0c\u8fd8\u6709\u5176\u4ed6\u591a\u5bb6\u5728\u9999\u6e2f\u80a1\u5e02\u516c\u5f00\u4e0a\u5e02\uff0c\u90fd\u662f\u8fd1\u5e74\u6765\u4e1a\u7ee9\u4e0d\u662f\u5f88\u7406\u60f3\u7684\u7535\u5b50\u5236\u9020\u4f01\u4e1a\u3002\u4e1a\u7ee9\u597d\u7684\u7535\u5b50\u4e0a\u5e02\u516c\u53f8\uff0c\u5176\u63a7\u5236\u4eba\u662f\u4e0d\u4f1a\u8f7b\u6613\u653e\u5f03\u63a7\u80a1\u6743\u7684\uff0c\u800c\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u8fd9\u65f6\u8fd8\u6ca1\u6709\u5b9e\u529b\u5168\u9762\u6536\u8d2d\u4e00\u5bb6\u4f18\u8d28\u7684\u4e0a\u5e02\u516c\u53f8\uff0c\u53ea\u80fd\u4e70\u4e0b\u5dee\u4e0d\u591a\u53ea\u5269\u4e0b\u4e00\u5f20\u7a7a\u58f3\u7684\u4e0a\u5e02\u516c\u53f8\u8fdb\u884c\u8d44\u672c\u5316\u8fd0\u4f5c\u3002</p><p>\u3000\u3000\u8ddf\u8fd9\u4e9b\u58f3\u516c\u53f8\u6ca1\u6709\u8fdb\u884c\u6df1\u5165\u7684\u63a5\u89e6\uff0c\u8981\u5728\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u8bbe\u8ba1\u597d\u8d44\u672c\u5316\u8fd0\u4f5c\u7684\u8def\u7ebf\uff0c\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u9676\u884c\u5065\u6068\u4e0d\u80fd\u9a6c\u4e0a\u5c06\u624b\u5934\u7684\u4e8b\u60c5\u4e22\u4e0b\uff0c\u5230\u9999\u6e2f\u4e0e\u8fd9\u4e9b\u58f3\u516c\u53f8\u63a5\u89e6\u3002</p><p>\u3000\u3000\u4f1a\u8bae\u7ed3\u901f\uff0c\u5929\u5df2\u7ecf\u9ed1\u4e0b\u6765\uff0c\u665a\u4e0a\u8fd8\u662f\u7559\u5728\u804c\u5de5\u98df\u5802\u5c31\u9910\u3002</p><p>\u3000\u3000\u804c\u5de5\u7efc\u5408\u697c\u5305\u62ec\u9752\u5e74\u804c\u5de5\u5bbf\u820d\u3001\u804c\u5de5\u98df\u5802\u3001\u804c\u5de5\u6587\u5316\u5a31\u4e50\u5ba4\u7b49\u533a\u57df\uff0c\u804c\u5de5\u98df\u5802\u627f\u5305\u7ed9\u5434\u5929\u5b9d\u7684\u5efa\u90ba\u9152\u5e97\u7ecf\u8425\u3002\u9664\u4e86\u5de5\u4f5c\u9910\u4e4b\u5916\uff0c\u804c\u5de5\u98df\u5802\u4e5f\u80fd\u627f\u5305\u5c0f\u578b\u7684\u5bb4\u5e2d\uff0c\u53ea\u8981\u63d0\u524d\u901a\u77e5\u4e00\u58f0\uff0c\u98df\u6750\u53ef\u4ee5\u4ece\u5efa\u90ba\u9152\u5e97\u4e34\u65f6\u8c03\u6765\u3002\u4e0d\u8fc7\u901a\u5e38\u53ea\u6709\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u805a\u9910\u624d\u7528\u5f97\u7740\uff0c\u6709\u91cd\u8981\u5ba2\u6237\uff0c\u4e2d\u5348\u7528\u5de5\u4f5c\u9910\uff0c\u665a\u4e0a\u8fd8\u662f\u8981\u53bb\u4e00\u5b9a\u7ea7\u522b\u7684\u9152\u5e97\u62db\u5f85\u3002</p><p>\u3000\u3000\u8bb8\u601d\u89c9\u5f97\u804c\u5de5\u98df\u5802\u7684\u4f19\u98df\u76f8\u5f53\u4e0d\u9519\uff0c\u5c31\u7b97\u665a\u4e0a\u5403\u5de5\u4f5c\u9910\u4e5f\u4e0d\u8d56\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u606a\u5c11\u773c\u4e0b\u5bf9\u5de5\u5382\u91cc\uff0c\u4e5f\u5c31\u804c\u5de5\u98df\u5802\u8fd9\u5757\u7ed9\u4e88\u76f4\u63a5\u7684\u6307\u5bfc\uff0c\u8fd9\u4e2a\u8981\u529e\u4e0d\u597d\uff0c\u4e0d\u662f\u62b9\u606a\u5c11\u7684\u9762\u5b50\uff1f\u201d</p><p>\u3000\u3000\u5143\u4ef6\u5668\u5206\u516c\u53f8\u52a0\u4e0a\u603b\u90e8\u3001\u7814\u53d1\u4e2d\u5fc3\u3001\u5408\u8d44\u516c\u53f8\u7684\u804c\u5458\u5927\u7ea6\u6709\u56db\u767e\u4eba\u5de6\u53f3\uff0c\u76ca\u9686\u9547\u90a3\u8fb9\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u91cc\u5df2\u7ecf\u5efa\u6210\u7684\u5de5\u5382\u89c4\u6a21\u662f\u8fd9\u91cc\u7684\u4e24\u500d\uff0c\u4e5d\u516d\u5e74\u80fd\u5b8c\u6210\u7684\u5728\u5efa\u9879\u76ee\u89c4\u6a21\u66f4\u52a0\u5e9e\u5927\uff0c\u4e3a\u4e86\u6276\u6301\u5468\u8fb9\u7684\u914d\u5957\u5de5\u5382\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u5efa\u8bbe\u516c\u7528\u8bbe\u65bd\u65f6\uff0c\u8fd8\u8003\u8651\u5230\u5411\u6574\u4e2a\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u5f00\u653e\uff0c\u800c\u4e0d\u4ec5\u4ec5\u670d\u52a1\u4e8e\u7231\u8fbe\u7535\u5b50\u7684\u5de5\u5382\uff0c\u4ec5\u804c\u5de5\u98df\u5802\u7684\u57fa\u5efa\u8d39\u7528\u5c31\u6295\u5165\u4e24\u5343\u4e07\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u5e95\u5c31\u80fd\u8fbe\u5230\u56db\u5343\u4eba\u7684\u5c31\u9910\u89c4\u6a21\uff0c\u8981\u662f\u4e0d\u4ea4\u7ed9\u4e13\u4e1a\u7684\u9152\u5e97\u627f\u5305\uff0c\u4ec5\u6b64\u4e00\u9879\u5de5\u4f5c\uff0c\u5c31\u8db3\u4ee5\u5c06\u884c\u653f\u7ba1\u7406\u90e8\u5341\u51e0\u53f7\u4eba\u62d6\u57ae\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u76f4\u63a5\u53d1\u5230\u5458\u5de5\u624b\u4e2d\u7684\u4f19\u98df\u8865\u52a9\u6bcf\u4e2a\u5de5\u4f5c\u65e5\u4e3a\u516d\u5143\uff0c\u5458\u5de5\u5728\u804c\u5de5\u98df\u5802\u5c31\u9910\uff0c\u7231\u8fbe\u7535\u5b50\u518d\u989d\u5916\u76f4\u63a5\u8865\u8d34\u4e8c\u5143\u83dc\u91d1\uff0c\u6bcf\u9910\u516b\u5143\u7684\u5de5\u4f5c\u9910\u6807\u51c6\uff0c\u5927\u6982\u662f\u4e5d\u4e94\u5e74\u6d77\u5dde\u5730\u533a\u7684\u6700\u9ad8\u5de5\u4f5c\u9910\u6807\u51c6\uff0c\u6bd4\u5e02\u653f\u5e9c\u660e\u9762\u4e0a\u7684\u5de5\u4f5c\u9910\u6807\u51c6\u90fd\u8981\u9ad8\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u8fc7\uff0c\u770b\u4e00\u4e2a\u5de5\u5382\u7684\u73b0\u4ee3\u5316\u6c34\u5e73\uff0c\u5916\u754c\u4f1a\u76f4\u63a5\u5c06\u76ee\u5149\u653e\u5230\u804c\u5de5\u98df\u5802\u4e0a\u3002\u804c\u5de5\u98df\u5802\u90fd\u641e\u4e0d\u597d\uff0c\u5176\u4ed6\u7684\u5de5\u5382\u5efa\u8bbe\u90fd\u5962\u8c08\u3002</p><p>\u3000\u3000\u5373\u4f7f\u7ed9\u5916\u9762\u7684\u9152\u5e97\u627f\u5305\uff0c\u4e5f\u8981\u5f15\u5165\u7ade\u4e89\u673a\u5236\uff0c\u5143\u5668\u4ef6\u5b50\u516c\u53f8\u8fd9\u8fb9\u7684\u89c4\u6a21\u5c0f\uff0c\u4f46\u662f\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u90a3\u91cc\uff0c\u8981\u5f15\u5165\u4e09\u5230\u56db\u5bb6\u9152\u5e97\u8054\u5408\u627f\u529e\uff0c\u751a\u81f3\u66f4\u591a\uff0c\u7ed9\u6bcf\u4e2a\u627f\u529e\u5355\u4f4d\u51e0\u4e2a\u7a97\u53e3\uff0c\u83dc\u80b4\u7684\u5f62\u5f0f\u4e5f\u8981\u5c3d\u53ef\u80fd\u4e30\u5bcc\uff0c\u7531\u5458\u5de5\u81ea\u4e3b\u9009\u62e9\u5c31\u9910\u3002</p><p>\u3000\u3000\u5de5\u4f5c\u9910\u7684\u5229\u6da6\u5f53\u7136\u4e0d\u53ef\u80fd\u8ddf\u5bb4\u5e2d\u76f8\u6bd4\uff0c\u4f46\u5229\u6da6\u4f1a\u76f8\u5f53\u7a33\u5b9a\uff0c\u5143\u5668\u4ef6\u5b50\u516c\u53f8\u8fd9\u91cc\u5012\u4e5f\u7f62\u4e86\uff0c\u5173\u952e\u662f\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u7684\u804c\u5de5\u98df\u5802\uff0c\u6bcf\u5929\u7a33\u5b9a\u7684\u56db\u5343\u591a\u4eba\u7684\u5c31\u9910\u89c4\u6a21\uff0c\u8fd8\u8981\u8003\u8651\u5230\u672a\u6765\u7684\u53d1\u5c55\u524d\u666f\uff0c\u90a3\u5c31\u662f\u4e00\u5757\u6781\u8bf1\u4eba\u7684\u80a5\u8089\uff0c\u5c31\u8fde\u897f\u57ce\u996d\u5e97\u90fd\u5341\u5206\u60f3\u63d2\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u53ef\u4e0d\u8ba4\u4e3a\u8ddf\u5f20\u606a\u7684\u79c1\u4eba\u4ea4\u60c5\u5c31\u80fd\u786e\u4fdd\u5efa\u90ba\u9152\u5e97\u80fd\u6324\u8fdb\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u6240\u4ee5\u5728\u627f\u5305\u5143\u5668\u4ef6\u5b50\u516c\u53f8\u7684\u804c\u5de5\u98df\u5802\u65f6\u5c31\u5341\u5206\u5356\u529b\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u5c06\u4ed6\u5728\u793e\u4f1a\u4e0a\u53ae\u6df7\u4e86\u4e24\u5e74\u591a\u65f6\u95f4\u7684\u513f\u5b50\u5434\u5c0a\u9001\u5230\u6fb3\u5927\u5229\u4e9a\u5b66\u4e60\u9910\u996e\u7ba1\u7406\u3002</p><p>\u3000\u3000\u5434\u5c0a\u4ed6\u672c\u4eba\u8fd9\u6bb5\u65f6\u95f4\u53d7\u5230\u7684\u89e6\u52a8\u4e5f\u5341\u5206\u7684\u5927\uff0c\u8ddf\u4ed6\u8001\u5b50\u5b66\u7740\u6253\u7406\u9152\u5e97\u7684\u4e8b\u52a1\u4ee5\u6765\uff0c\u4e5f\u89c9\u5f97\u4ed6\u809a\u5b50\u91cc\u4ec5\u6df7\u5230\u9ad8\u4e2d\u5c31\u9a6c\u9a6c\u864e\u864e\u6bd5\u4e1a\u7684\u58a8\u6c34\u5b8c\u5168\u4e0d\u8db3\u4ee5\u5e94\u4ed8\u73b0\u4ee3\u9910\u996e\u4e1a\u7ba1\u7406\u7684\u9700\u8981\uff0c\u51fa\u53bb\u5b66\u4e60\u8fd8\u662f\u4ed6\u4e3b\u52a8\u8981\u6c42\u7684\u3002</p><p>\u3000\u3000\u56fd\u4eba\u5bf9\u540e\u4ee3\u7684\u671f\u671b\u90fd\u76f8\u5f53\u7684\u9ad8\uff0c\u5434\u5929\u5b9d\u4e5f\u4e0d\u4f8b\u5916\uff0c\u7edd\u4e0d\u5e0c\u671b\u6323\u4e0b\u5343\u4e07\u5bb6\u4ea7\u7ed9\u8d25\u5bb6\u5b50\u8d25\u5149\uff0c\u4ed6\u513f\u5b50\u4e3b\u52a8\u8981\u6c42\u51fa\u53bb\u8fdb\u4fee\uff0c\u8001\u6cea\u7eb5\u6a2a\uff0c\u8fd9\u5176\u4e2d\u79cd\u79cd\u7684\u53d8\u5316\uff0c\u90fd\u4e0d\u80fd\u5426\u8ba4\u5f20\u606a\u90a3\u6781\u5f3a\u7684\u611f\u67d3\u529b\u8d77\u7740\u4f5c\u7528\uff0c\u5927\u6982\u662f\u5f20\u606a\u8ba9\u4ed6\u513f\u5b50\u77e5\u9053\u5f53\u4e00\u4e2a\u6df7\u6df7\u662f\u6ca1\u6709\u524d\u9014\u7684\u3002\u5c31\u4e3a\u4e86\u8fd9\u4e00\u70b9\uff0c\u5434\u5929\u5b9d\u4e5f\u8981\u5c06\u7231\u8fbe\u7535\u5b50\u4ea4\u7ed9\u4ed6\u627f\u529e\u7684\u98df\u5802\u529e\u597d\u3002</p><p>\u3000\u3000\u5f88\u591a\u5458\u5de5\u65e2\u4f7f\u4e0b\u73ed\u4e4b\u540e\uff0c\u4e5f\u559c\u6b22\u5230\u804c\u5de5\u98df\u5802\u7528\u9910\uff0c\u7279\u522b\u662f\u4f4f\u804c\u5de5\u5bbf\u820d\u7684\u9752\u5e74\u5458\u5de5\uff0c\u66f4\u4e0d\u4f1a\u81ea\u5df1\u5f00\u706b\uff0c\u4e5f\u4e0d\u4f1a\u5230\u5916\u9762\u7684\u5c0f\u5e97\u91cc\u7cca\u5f04\u4e00\u987f\u3002\u804c\u5de5\u98df\u5802\u9664\u4e86\u4f19\u98df\u4e30\u5bcc\u3001\u642d\u914d\u5408\u7406\u3001\u4ef7\u683c\u516c\u9053\uff0c\u8fd8\u80fd\u4eab\u53d7\u4e8c\u5143\u83dc\u91d1\u7684\u8865\u8d34\u3002\u8c01\u77e5\u9053\u4ed6\u5f53\u5929\u662f\u5426\u5728\u5c97\uff1f\u4e8c\u5143\u83dc\u91d1\u662f\u76f4\u63a5\u8865\u8d34\u5230\u83dc\u4ef7\u91cc\u7684\u3002\u6709\u4e9b\u5458\u5de5\u8fd8\u559c\u6b22\u4e70\u4e00\u4e9b\u70b9\u5fc3\u5f53\u7b2c\u4e8c\u5929\u7684\u65e9\u9910\u3002</p><p>\u3000\u3000\u7528\u8fc7\u665a\u9910\uff0c\u5f20\u606a\u7531\u82cf\u6d25\u4e1c\u4ed6\u4eec\u966a\u7740\u5230\u4e8c\u697c\u7684\u7efc\u5408\u5a31\u4e50\u5ba4\u53bb\u770b\u4e00\u770b\uff0c\u53c2\u89c2\u4e00\u4e0b\u9752\u5e74\u804c\u5de5\u7684\u4f11\u95f2\u751f\u6d3b\uff0c\u8fd9\u5f88\u80fd\u8ba9\u5f20\u606a\u56de\u60f3\u8d77\u4ed6\u5927\u5b66\u6bd5\u4e1a\u4f4f\u804c\u5de5\u5bbf\u820d\u7684\u90a3\u6bb5\u65f6\u5149\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u521a\u521a\u8bbe\u7acb\u7684\u8ba1\u7b97\u673a\u5ba4\uff0c\u4eba\u5e76\u4e0d\u591a\uff0c\u800c\u4e14\u6709\u4e03\u516b\u4e2a\u4eba\u56f4\u7740\u6700\u89d2\u843d\u91cc\u7684\u4e00\u53f0\u8ba1\u7b97\u673a\uff0c\u5f20\u606a\u77a5\u773c\u671b\u8fc7\u53bb\uff0c\u5750\u7740\u7684\u90a3\u4eba\u7adf\u7136\u5728\u73a9\u9b54\u517d\u4e89\u9738\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u60f3\u5230\u66b4\u96ea\u5df2\u7ecf\u5f00\u53d1\u51fa\u4e8c\u4ee3\u7684\u9b54\u517d\u4e89\u9738\uff0c\u66f4\u6ca1\u6709\u60f3\u5230\u56fd\u5185\u7684\u76d7\u7248\u80fd\u8fd9\u4e48\u8fc5\u901f\u7684\u8ddf\u4e0a\uff0c\u867d\u7136\u7e41\u4f53\u6c49\u5316\u7684\u6c34\u5e73\u5e76\u4e0d\u9ad8\uff0c\u4f46\u662f\u52c9\u5f3a\u591f\u7528\uff0c\u7279\u522b\u662f\u5bf9\u4e5d\u5341\u5e74\u4ee3\u4e2d\u53f6\u7684\u9752\u5e74\u6765\u8bf4\uff0c\u9b54\u517d\u4e89\u9738\u5177\u6709\u7edd\u5bf9\u7684\u5438\u5f15\u529b\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u53f6\uff0c\u88c5\u4e00\u53f0\u5bb6\u7528\u8ba1\u7b97\u673a\u81f3\u5c11\u8981\u4e0a\u4e07\u5143\u624d\u591f\u7528\uff0c\u4e8c\u697c\u7684\u8ba1\u7b97\u673a\u623f\u6709\u4e09\u5341\u53f0\u8ba1\u7b97\u673a\uff0c\u53ef\u60dc\u65e9\u4e9b\u5e74\u6bd5\u4e1a\u7684\u5927\u5b66\u751f\uff0c\u4f1a\u719f\u7ec3\u4f7f\u7528\u8ba1\u7b97\u673a\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u867d\u7136\u5fae\u8f6f\u5df2\u7ecf\u63a8\u51fa95\u89c6\u7a97\u7cfb\u7edf\uff0c\u4f46\u662f\u9ad8\u6821\u9662\u6821\u7684\u8ba1\u7b97\u673a\u8bfe\u7a0b\u4e00\u76f4\u5230\u4e5d\u4e03\u5e74\u8fd8\u5728\u6559\u6388DOS\u7cfb\u7edf\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u662f\u4e5d\u4e03\u5e74\u4e0b\u534a\u5e74\u8fdb\u5165\u5927\u5b66\u4e4b\u540e\u5f00\u59cb\u73a9\u4e00\u4ee3\u7684\u5e1d\u56fd\u65f6\u4ee3\u5f00\u59cb\u4ed6\u7684\u6e38\u620f\u751f\u6daf\uff0c\u8bb0\u5f97\u7ea2\u8b66\u4e5f\u662f\u4e5d\u4e03\u5e74\u4e0a\u5e02\u7684\u5427\uff0c\u6697\u9ed1\u7834\u574f\u795e\u5462\uff0c\u5df2\u7ecf\u5f00\u53d1\u51fa\u6765\uff0c\u8fd8\u662f\u5feb\u4e86\uff1f\u661f\u9645\u4e89\u9738\u597d\u50cf\u8981\u665a\u4e00\u4e9b\u3002\u9b54\u517d\u4e89\u9738\u7cfb\u5217\uff0c\u5f20\u606a\u76f4\u63a5\u73a9\u7684\u4e09\u4ee3\uff0c\u6ca1\u6709\u73a9\u8fc7\u63a5\u89e6\u8fc7\u4e8c\u4ee3\u7684\u9b54\u517d\u4e89\u9738\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u90a3\u540d\u9752\u5e74\u5458\u5de5\u7684\u8eab\u540e\uff0c\u60f3\u770b\u770b\u4e8c\u4ee3\u7684\u9b54\u517d\u4e89\u9738\u5230\u5e95\u8ddf\u4e09\u4ee3\u6709\u54ea\u4e9b\u4e0d\u540c\u3002</p><p>\u3000\u3000\u4ed6\u5f80\u524d\u8d70\uff0c\u82cf\u6d25\u4e1c\u4ed6\u4eec\u90fd\u5f97\u8ddf\u7740\uff0c\u56f4\u7740\u770b\u7684\u9752\u5e74\u804c\u5de5\u9996\u5148\u770b\u5230\u4ed6\u4eec\uff0c\u5f88\u81ea\u89c9\u7684\u7ad9\u76f4\u8170\u6253\u62db\u547c\uff0c\u90a3\u4e2a\u73a9\u6e38\u620f\u7684\u9752\u5e74\u73a9\u5f97\u6b63\u5165\u795e\uff0c\u76f4\u5230\u5f20\u606a\u7ad9\u5230\u4ed6\u8eab\u540e\uff0c\u624d\u60ca\u89c9\u7684\u62ac\u5934\uff0c\u770b\u5230\u516c\u53f8\u7684\u8001\u603b\u90fd\u56f4\u5728\u4ed6\u540e\u9762\uff0c\u4e00\u65f6\u95f4\u90fd\u6709\u4e9b\u53d1\u6123\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u53cd\u5e94\u624d\u5408\u9002\u3002\u4ed6\u5927\u6982\u89c9\u5f97\u5728\u516c\u53f8\u8001\u603b\u9762\u524d\u73a9\u6e38\u620f\u5f88\u4e0d\u5408\u9002\uff0c\u51e0\u4e4e\u672c\u80fd\u7684\u5c06\u6e38\u620f\u754c\u9762\u5207\u6389\u3002</p><p>\u3000\u3000\u201c\u501f\u6211\u73a9\u4e00\u628a\uff1f\u201d\u5f20\u606a\u5bf9\u4ed6\u8bf4\u3002</p><p>\u3000\u3000\u90a3\u9752\u5e74\u5c31\u50f5\u76f4\u7684\u5750\u4e86\u8d77\u6765\uff0c\u6709\u4e9b\u614c\u4e71\uff0c\u6362\u4f5c\u8c01\u90fd\u65e0\u6cd5\u5e73\u9759\u5bf9\u5f85\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u4e0a\u53bb\uff0c\u5c06\u6e38\u620f\u754c\u5207\u6362\u56de\u6765\uff0c\u4e8c\u4ee3\u7684\u9b54\u517d\u7adf\u7136\u6709\u56db\u79cd\u8d44\u6e90\uff0c\u9664\u4e86\u9ec4\u91d1\u4e0e\u6728\u6750\u4e4b\u5916\uff0c\u8fd8\u6709\u77ff\u77f3\u4e0e\u77f3\u6cb9\u3002\u9b54\u517d\u4e89\u9738\u662f\u4e00\u6b3e\u8bb2\u7a76\u64cd\u4f5c\u6280\u672f\u7684\u6e38\u620f\uff0c\u5bf9\u6218\u7565\u3001\u6218\u672f\u7684\u8981\u6c42\u76f8\u5f53\u8f83\u4f4e\uff0c\u4e09\u4ee3\u5c31\u5c06\u77ff\u77f3\u4e0e\u77f3\u6cb9\u4e24\u9879\u8d44\u6e90\u53d6\u6d88\u6389\u4e86\u3002\u53e6\u5916\u4e8c\u4ee3\u91cc\u5c31\u51fa\u73b0\u7684\u6218\u4e89\u8ff7\u96fe\u6982\u5ff5\uff0c\u5373\u4f7f\u6709\u5f88\u591a\u4e0d\u540c\u4e4b\u5904\uff0c\u4f46\u662f\u5f20\u606a\u8fd8\u80fd\u73a9\u4e0a\u624b\uff0c\u9009\u4e86\u4e00\u5f20\u5c0f\u5730\u56fe\uff0c\u5f88\u723d\u5feb\u7684\u73a9\u4e86\u4e00\u628a\uff0c\u624d\u62ac\u5934\u95ee\u90a3\u4e2a\u9752\u5e74\uff1a\u201c\u8fd9\u6e38\u620f\u789f\u4f60\u4ece\u54ea\u91cc\u4e70\u7684\uff0c\u80fd\u4e0d\u80fd\u501f\u7ed9\u6211\u5e26\u56de\u53bb\u88c5\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u672c\u6765\u8ba1\u7b97\u673a\u5ba4\u6709\u7981\u6b62\u5458\u5de5\u79c1\u81ea\u5b89\u88c5\u8f6f\u4ef6\u7684\u89c4\u5b9a\uff0c\u82cf\u6d25\u4e1c\u662f\u77e5\u9053\u7684\uff0c\u4f46\u662f\u4ed6\u4e0d\u4f1a\u677f\u7740\u8138\u6559\u8bad\u4e00\u540d\u666e\u901a\u7684\u3001\u4ed6\u90fd\u4e0d\u77e5\u9053\u540d\u5b57\u7684\u5458\u5de5\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u4e3b\u52a8\u8ddf\u4eba\u5bb6\u501f\u6e38\u620f\u789f\uff0c\u989d\u5934\u5dee\u70b9\u5192\u51fa\u51b7\u6c57\u6765\u3002</p><p>\u3000\u3000\u4e01\u69d0\u8138\u6487\u5230\u4e00\u65c1\uff0c\u8fd9\u4e2a\u5458\u5de5\u662f\u7814\u53d1\u90e8\u4eca\u5e74\u521a\u62db\u8fdb\u6765\u7684\u672c\u79d1\u6bd5\u4e1a\u751f\uff0c\u6b63\u5904\u4e8e\u57f9\u8bad\u9636\u6bb5\uff0c\u4ed6\u8ba4\u5f97\uff0c\u8fd9\u65f6\u5019\u8fd8\u88c5\u4e0d\u8ba4\u5f97\u597d\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e73\u65f6\u5f88\u5c11\u5728\u5de5\u5382\u91cc\u51fa\u73b0\uff0c\u666e\u901a\u5458\u5de5\u90fd\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u53ef\u662f\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u9c81\u5e86\u751f\u4ed6\u4eec\u90fd\u662f\u516c\u53f8\u7684\u9ad8\u5c42\uff0c\u966a\u7740\u5f20\u606a\u8ddf\u516c\u53f8\u7684\u4e00\u540d\u666e\u901a\u5458\u5de5\u501f\u76d7\u7248\u6e38\u620f\u789f\uff0c\u770b\u5f20\u606a\u7684\u610f\u601d\uff0c\u8fd8\u60f3\u8ddf\u8fd9\u540d\u5458\u5de5\u5207\u78cb\u4e00\u4e0b\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u8981\u6d41\u51b7\u6c57\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u8ddf\u665a\u6674\u3001\u8bb8\u601d\u8bf4\uff1a\u201c\u8fd9\u6e38\u620f\u5f88\u597d\u73a9\u7684\uff0c\u4f60\u4eec\u8981\u5b66\uff0c\u7b49\u4f1a\u513f\u6211\u6559\u4f60\u4eec\u2026\u2026\u201d\u5f20\u606a\u65e9\u5c31\u914d\u4e86\u8ba1\u7b97\u673a\uff0c\u4f46\u662f\u4e5d\u5341\u5e74\u4ee3\u4e2d\u53f6\uff0c\u56fd\u5185\u7684\u7f51\u7edc\u8fd8\u53ea\u662f\u8bd5\u8fd0\u884c\uff0c\u8ba1\u7b97\u673a\u5927\u6982\u5b9e\u7528\u6027\u66f4\u5f3a\u4e00\u4e9b\uff0c\u9664\u4e86\u7ecf\u5178\u7684\u626b\u96f7\u6e38\u620f\u5f20\u606a\u767e\u73a9\u4e0d\u538c\uff0c\u8ba1\u7b97\u673a\u5c31\u8ba9\u4ed6\u627e\u4e0d\u5230\u592a\u591a\u53ef\u4ee5\u6253\u53d1\u65f6\u95f4\u7684\u4e1c\u897f\uff0c\u96be\u5f97\u770b\u5230\u66b4\u96ea\u7adf\u7136\u5df2\u7ecf\u63a8\u51fa\u4e8c\u4ee3\u7684\u9b54\u517d\u4e89\u9738\uff0c\u600e\u4e48\u4f1a\u8f7b\u6613\u9519\u8fc7\u5462\u3002</p></article>"}}}]);
