(window.webpackJsonp=window.webpackJsonp||[]).push([[427],{449:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e03\u8282 \u8c01\u9001\u7684\u7b1b\u5b50",content:"<article><h2>\u7b2c\u5341\u4e03\u8282 \u8c01\u9001\u7684\u7b1b\u5b50</h2><p>\u201c\u59d0\u59d0\uff01\u201d</p><p>\u743c\u6069\u970d\u7136\u60ca\u9192\uff0c\u9669\u4e9b\u4ece\u5e8a\u4e0a\u5f39\u8d77\u6765\uff0c\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u53d1\u73b0\u81ea\u5df1\u80cc\u4e0a\u51b7\u6c57\u6d94\u6d94\uff0c\u8fde\u5e8a\u5355\u90fd\u5df2\u7ecf\u6e7f\u900f\u3002\u770b\u770b\u7a97\u5916\u900f\u8fdb\u6765\u7684\u6668\u5149\uff0c\u5927\u7ea6\u662f\u65e9\u4e0a\u4e03\u516b\u70b9\u949f\u5de6\u53f3\uff0c\u82e5\u662f\u5e73\u65f6\uff0c\u73ca\u5609\u65e9\u5df2\u8d77\u5e8a\u68b3\u6d17\uff0c\u51c6\u5907\u53bb\u5b66\u6821\uff0c\u73b0\u5728\u5374\u504e\u5728\u81ea\u5df1\u8eab\u65c1\uff0c\u6b63\u7761\u5f97\u9999\u751c\u3002\u611f\u89c9\u5230\u743c\u6069\u7684\u52a8\u9759\uff0c\u5979\u6709\u4e9b\u4e0d\u6ee1\u5730\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u7ffb\u4e86\u4e2a\u8eab\u7ee7\u7eed\u7f8e\u68a6\u3002</p><p>\u201c\u5e78\u597d\u662f\u4e2a\u68a6\u3002\u201d</p><p>\u743c\u6069\u5598\u606f\u7740\uff0c\u624b\u8db3\u9ebb\u75f9\uff0c\u80f8\u53e3\u4e00\u9635\u9635\u5730\u53d1\u95f7\uff0c\u4eff\u4f5b\u88ab\u5343\u65a4\u91cd\u7269\u538b\u7740\u4f3c\u7684\uff0c\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u8fc7\u4e86\u597d\u534a\u664c\u624d\u6e10\u6e10\u6062\u590d\u3002\u4ed6\u770b\u7740\u73ca\u5609\u719f\u7761\u7684\u9762\u5bb9\uff0c\u8f7b\u8f7b\u5e2e\u5979\u538b\u597d\u6bdb\u6bef\uff0c\u6162\u6162\u9677\u5165\u6c89\u601d\u3002</p><p>\u8fd9\u4e2a\u68a6\u5f88\u53e4\u602a\u3002</p><p>\u743c\u6069\u5bf9\u5fc3\u7406\u5b66\u7565\u6709\u6d89\u730e\uff0c\u636e\u4ed6\u6240\u77e5\uff0c\u4e16\u754c\u4e0a\u7684\u68a6\uff0c\u65e0\u8bba\u662f\u7f8e\u68a6\u8fd8\u662f\u6076\u68a6\uff0c\u65e0\u8bba\u518d\u8be1\u5f02\u7684\u5185\u5bb9\uff0c\u518d\u79bb\u5947\u7684\u5883\u9047\uff0c\u65e0\u4e00\u4e0d\u662f\u201c\u81ea\u5df1\u201d\u7684\u2014\u2014\u68a6\u89c1\u5347\u5b98\u53d1\u8d22\uff0c\u68a6\u89c1\u548c\u7f8e\u5973\u4e0a\u5e8a\uff0c\u68a6\u89c1\u88ab\u72d7\u54ac\uff0c\u68a6\u89c1\u88ab\u4eba\u8ffd\u6740\uff0c\u68a6\u89c1\u548c\u4eb2\u53cb\u8bf4\u8bdd\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u6240\u6709\u7684\u8fd9\u4e00\u5207\uff0c\u5fc5\u5b9a\u90fd\u6709\u201c\u81ea\u5df1\u201d\u7684\u5b58\u5728\uff0c\u4ee5\u201c\u81ea\u5df1\u201d\u4e3a\u68a6\u5883\u7684\u4e2d\u5fc3\u3002\u5c31\u50cf\u743c\u6069\u524d\u6bb5\u65f6\u95f4\u51e0\u6b21\u505a\u7684\u4e00\u4e2a\u68a6\uff0c\u81ea\u5df1\u5efa\u7acb\u4e86\u6d6e\u7a7a\u57ce\uff0c\u548c\u6240\u6709\u7684\u5973\u5b69\u5b50\u5feb\u4e50\u751f\u6d3b\u5728\u4e00\u8d77\uff0c\u8fd9\u540c\u6837\u4e5f\u90fd\u662f\u4ee5\u4ed6\u81ea\u5df1\u4e3a\u68a6\u7684\u4e2d\u5fc3\uff0c\u4e00\u5207\u4ee5\u4ed6\u4e3a\u8f6c\u79fb\u7684\u3002</p><p>\u4f46\u5728\u8fd9\u4e2a\u68a6\u91cc\uff0c\u743c\u6069\u7684\u4f4d\u7f6e\u53ea\u662f\u4e00\u4e2a\u201c\u65c1\u89c2\u8005\u201d\uff0c\u4ed6\u4ec0\u4e48\u90fd\u4e0d\u80fd\u505a\uff0c\u4e5f\u4ec0\u4e48\u90fd\u6ca1\u505a\uff0c\u53ea\u662f\u50cf\u4e2a\u5e7b\u5f71\u4e00\u6837\u7ad9\u5728\u65c1\u8fb9\uff0c\u770b\u7740\u4e8b\u60c5\u7684\u53d1\u751f\uff0c\u4ece\u5934\u5230\u5c3e\u90fd\u6ca1\u6709\u53c2\u4e0e\u5176\u95f4\uff0c\u751a\u81f3\u8fde\u534a\u70b9\u201c\u5f71\u54cd\u201d\u90fd\u6ca1\u6709\uff0c\u8fde\u81ea\u5df1\u7684\u201c\u5b58\u5728\u201d\u90fd\u6ca1\u6709\u88ab\u68a6\u5883\u4e2d\u7684\u4e24\u4e2a\u4eba\u610f\u8bc6\u5230\u3002\u8fd9\u5b8c\u5168\u4e0d\u7b26\u5408\u5e38\u7406\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u68a6\u7684\u5185\u5bb9\u4e5f\u5f88\u8be1\u5f02\uff0c\u7b80\u5355\u5f52\u7eb3\u603b\u7ed3\u7684\u8bdd\uff0c\u5c31\u662f\u4e00\u573a\u7a81\u5982\u5176\u6765\u7684\u8c0b\u6740\u3002\u51fa\u73b0\u7684\u90a3\u4e00\u7537\u4e00\u5973\uff0c\u743c\u6069\u538b\u6839\u770b\u4e0d\u6e05\u695a\u76f8\u8c8c\uff0c\u4f46\u504f\u504f\u89c9\u5f97\u8eab\u5f62\u8f6e\u5ed3\u90fd\u6709\u51e0\u5206\u773c\u719f\uff0c\u50cf\u662f\u8ba4\u8bc6\u7684\u4eba\u3002\u4ece\u7537\u5b50\u6700\u540e\u90a3\u53e5\u8bdd\u201c\u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u5c06\u5b64\u72ec\u5730\u6b7b\u53bb\uff0c\u6bcd\u4eb2\u201d\u6765\u5224\u65ad\uff0c\u4ed6\u4eec\u7684\u5173\u7cfb\u662f\u6bcd\u5b50\u2014\u2014\u4f46\u743c\u6069\u5e76\u4e0d\u8bb0\u5f97\u81ea\u5df1\u8ba4\u8bc6\u8fd9\u6837\u4e00\u5bf9\u6bcd\u5b50\u3002</p><p>\u5728\u68a6\u5883\u7684\u6700\u540e\u90e8\u5206\uff0c\u743c\u6069\u7a81\u7136\u610f\u8bc6\u5230\u90a3\u540d\u88ab\u8c0b\u6740\u7684\u5973\u5b50\u662f\u73ca\u5609\uff0c\u90a3\u79cd\u611f\u89c9\u662f\u5982\u6b64\u771f\u5207\uff0c\u51e0\u4e4e\u786e\u5b9a\u65e0\u7591\uff0c\u4f46\u5982\u4eca\u9192\u6765\u518d\u60f3\uff0c\u4fbf\u53c8\u4e0d\u80fd\u80af\u5b9a\u8d77\u6765\u3002\u68a6\u91cc\u7684\u4e8b\u60c5\uff0c\u539f\u672c\u5c31\u662f\u6a21\u6a21\u7cca\u7cca\uff0c\u8c01\u80fd\u8bf4\u5f97\u6e05\u695a\uff0c\u4f55\u51b5\u4ed6\u6839\u672c\u5c31\u6ca1\u770b\u6e05\u695a\u76f8\u8c8c\uff0c\u53c8\u5982\u4f55\u80fd\u591f\u80af\u5b9a\u3002\u8bf4\u4e0d\u5b9a\u662f\u5148\u89c9\u5f97\u8eab\u5f62\u773c\u719f\uff0c\u53c8\u89c1\u5979\u8170\u95f4\u643a\u5e26\u7684\u957f\u7b1b\u548c\u73ca\u5609\u7684\u591c\u6c89\u6728\u957f\u7b1b\u76f8\u4f3c\uff0c\u6240\u4ee5\u4e0b\u610f\u8bc6\u5730\u505a\u4e86\u8054\u60f3\u7f62\u4e86\u3002\u5426\u5219\u7684\u8bdd\uff0c\u68a6\u91cc\u7684\u5973\u5b50\u771f\u662f\u73ca\u5609\uff0c\u90a3\u8fd9\u6574\u4e2a\u4e8b\u60c5\u5982\u4f55\u89e3\u91ca\uff1f\u73ca\u5609\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u5927\u7684\u513f\u5b50\uff1f</p><p>\u201c\u6309\u7167\u901a\u5e38\u7684\u8bf4\u6cd5\uff0c\u68a6\u662f\u4e00\u79cd\u9884\u5146\uff0c\u662f\u672a\u6765\u2014\u2014\u96be\u4e0d\u6210\u8fd9\u610f\u601d\u662f\u8bf4\uff0c\u5c06\u6765\u6211\u548c\u73ca\u5609\u751f\u4e0b\u513f\u5b50\uff0c\u4ed6\u957f\u5927\u4e86\uff0c\u4f1a\u628a\u73ca\u5609\u6740\u4e86\uff1f\u8fd9\u4e48\u8bf4\u597d\u50cf\u8fd8\u771f\u6709\u51e0\u5206\u9053\u7406\uff0c\u6211\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u96be\u602a\u770b\u7740\u773c\u719f\u2026\u2026\u4f46\u8fd9\u4e5f\u672a\u514d\u9884\u5146\u5f97\u592a\u957f\u8fdc\u4e86\u70b9\u5427\u3002\u201d</p><p>\u743c\u6069\u6447\u4e86\u6447\u5934\uff0c\u89c9\u5f97\u8fd9\u5b9e\u5728\u662f\u8352\u8bde\u65e0\u7a3d\uff0c\u6c89\u541f\u7247\u523b\uff0c\u7d22\u6027\u4e5f\u5c31\u653e\u4e0b\uff0c\u4e0d\u518d\u53bb\u60f3\u3002\u5de6\u53f3\u4e5f\u5c31\u662f\u4e2a\u68a6\u7f62\u4e86\uff0c\u4eba\u4e00\u751f\u505a\u65e0\u6570\u7684\u68a6\uff0c\u5e38\u6709\u8352\u8bde\u65e0\u7a3d\u7684\u60c5\u5f62\uff0c\u5018\u82e5\u6bcf\u4e2a\u90fd\u8981\u4ed4\u7ec6\u63a8\u6572\u8ba1\u8f83\u7684\u8bdd\uff0c\u90a3\u5c31\u7b97\u662f\u5f17\u6d1b\u4f0a\u5fb7\u518d\u4e16\uff0c\u8363\u683c\u590d\u751f\uff0c\u53ea\u6015\u4e5f\u8981\u5410\u8840\u8d25\u9000\u7684\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u59d0\u59d0\u7684\u521d\u591c\u6625\u5bb5\uff0c\u4e0d\u505a\u7f8e\u68a6\u5374\u505a\u8fd9\u79cd\u602a\u68a6\uff0c\u8ba9\u4eba\u786e\u5b9e\u6709\u4e9b\u4e0d\u723d\uff0c\u8fd8\u662f\u8d76\u5feb\u5fd8\u8bb0\u6bd4\u8f83\u597d\u3002\u60f3\u8d77\u4eca\u5929\u8fd8\u8981\u53bb\u89c1\u5e03\u96f7\u7eb3\u65af\uff0c\u867d\u7136\u6ca1\u6709\u5177\u4f53\u7ea6\u65f6\u95f4\uff0c\u4f46\u592a\u665a\u4e86\u603b\u662f\u4e0d\u59a5\u3002\u743c\u6069\u6084\u6084\u8d77\u8eab\uff0c\u53bb\u697c\u4e0b\u5429\u5490\u5973\u4ec6\u51c6\u5907\u65e9\u9910\uff0c\u7136\u540e\u53bb\u6d17\u6f31\uff0c\u4e00\u5207\u5f04\u5b8c\u56de\u5230\u623f\u95f4\uff0c\u53d1\u73b0\u73ca\u5609\u4e5f\u5df2\u7ecf\u9192\u4e86\uff0c\u9759\u9759\u5750\u5728\u5e8a\u5934\uff0c\u8d64\u88f8\u7684\u8eab\u4f53\u88f9\u5728\u6bdb\u6bef\u4e2d\uff0c\u9732\u51fa\u6d01\u767d\u53cc\u80a9\uff0c\u795e\u60c5\u5b89\u8be6\u5e73\u548c\uff0c\u89c1\u4ed6\u56de\u6765\uff0c\u5fae\u5fae\u4e00\u7b11\u3002</p><p>\u743c\u6069\u8d70\u5230\u5e8a\u8fb9\u4eb2\u4e86\u4eb2\u5979\u7684\u989d\u5934\uff0c\u201c\u59d0\u59d0\uff0c\u518d\u591a\u7761\u4f1a\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u4eca\u5929\u522b\u53bb\u5b66\u6821\u4e86\u3002\u201d</p><p>\u201c\u60f3\u53bb\u4e5f\u53bb\u4e0d\u6210\u4e86\uff0c\u201d\u73ca\u5609\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4e0b\u9762\u75bc\u5f97\u5389\u5bb3\uff0c\u521a\u624d\u51e0\u4e4e\u90fd\u5750\u4e0d\u8d77\u6765\u2026\u2026\u4f60\u8fd9\u5c0f\u574f\u86cb\uff0c\u6628\u665a\u53ef\u771f\u6709\u7cbe\u795e\uff0c\u628a\u59d0\u59d0\u6b3a\u8d1f\u7684\u591f\u545b\u3002\u201d</p><p>\u201c\u59d0\u59d0\u662f\u7b2c\u4e00\u6b21\u561b\uff0c\u5973\u5b69\u5b50\u7b2c\u4e00\u6b21\u90fd\u4f1a\u6709\u70b9\u75bc\uff0c\u4ee5\u540e\u5c31\u4e0d\u4f1a\u4e86\uff0c\u201d\u743c\u6069\u5c06\u5979\u62b1\u5728\u6000\u91cc\uff0c\u201c\u4eca\u665a\u6211\u8bb0\u4f4f\u4e86\uff0c\u4fdd\u8bc1\u8f7b\u8f7b\u7684\uff0c\u4e0d\u4f1a\u518d\u628a\u59d0\u59d0\u5f04\u75bc\u3002\u201d</p><p>\u201c\u8c01\u8bf4\u4eca\u665a\u8fd8\u8981\u8ba9\u4f60\u2026\u2026\u53bb\u966a\u9694\u58c1\u5bb6\u90a3\u5bf9\u59d0\u59b9\u53bb\uff0c\u6709\u4e24\u4e2a\u90fd\u5728\u7b49\u7740\u4f60\u5462\uff0c\u522b\u6765\u9a9a\u6270\u59d0\u59d0\u3002\u201d</p><p>\u201c\u4e0d\u5e72\uff0c\u6211\u5c31\u559c\u6b22\u6b3a\u8d1f\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8d56\u7740\u5979\uff0c\u201c\u6211\u8981\u59d0\u59d0\u5929\u5929\u665a\u4e0a\u90fd\u966a\u7740\u6211\u3002\u201d</p><p>\u73ca\u5609\u6b32\u8a00\u53c8\u6b62\uff0c\u6700\u7ec8\u7b11\u4e86\u4e00\u7b11\uff0c\u6ca1\u8bf4\u4ec0\u4e48\u3002\u201c\u597d\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u59d0\u59d0\u5929\u5929\u90fd\u966a\u7740\u4f60\uff0c\u53ea\u8981\u4f60\u4e0d\u89c9\u5f97\u538c\u70e6\u5c31\u884c\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\u2026\u2026\u59d0\u59d0\u662f\u6211\u4ece\u5c0f\u7684\u68a6\u60f3\u548c\u61a7\u61ac\u554a\uff0c\u600e\u4e48\u53ef\u80fd\u4f1a\u538c\u70e6\u5462\u3002\u201d</p><p>\u201c\u68a6\u60f3\u73b0\u5728\u5df2\u7ecf\u5b9e\u73b0\u4e86\u5440\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u53ef\u4ee5\u53bb\u8ffd\u9010\u65b0\u7684\u76ee\u6807\u4e86\uff0c\u8fd8\u6709\u90a3\u4e48\u591a\u6f02\u4eae\u5973\u5b69\u5b50\u5728\u7b49\u7740\u4f60\u5462\u3002\u201d</p><p>\u743c\u6069\u7b11\u7740\u634f\u4e86\u634f\u5979\u7684\u8138\u86cb\uff0c\u201c\u59d0\u59d0\uff0c\u600e\u4e48\u4e00\u591c\u8fc7\u6765\uff0c\u8bf4\u8bdd\u7684\u611f\u89c9\u90fd\u53d8\u4e86\uff0c\u50cf\u4e2a\u5c0f\u5973\u4eba\u4f3c\u7684\u3002\u201d</p><p>\u73ca\u5609\u683c\u683c\u4e00\u7b11\uff0c\u201c\u672c\u6765\u5c31\u662f\u4f60\u628a\u59d0\u59d0\u53d8\u6210\u4e86\u5973\u4eba\u561b\uff0c\u8fd9\u53eb\u81ea\u4f5c\u81ea\u53d7\u2014\u2014\u5f00\u73a9\u7b11\u5566\uff0c\u4f60\u4eca\u5929\u8fd8\u6709\u4e8b\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u6276\u5979\u8d77\u8eab\uff0c\u73ca\u5609\u521a\u8981\u7ad9\u8d77\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u554a\u4e86\u4e00\u58f0\uff0c\u53cc\u817f\u4e00\u8f6f\u9669\u4e9b\u6454\u5012\uff0c\u5e78\u597d\u743c\u6069\u5728\u65c1\u8fb9\uff0c\u4e00\u628a\u5c06\u5979\u6258\u4f4f\uff0c\u5e73\u653e\u5728\u5e8a\u4e0a\u3002</p><p>\u201c\u90fd\u662f\u4f60\uff0c\u59d0\u59d0\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\u4e86\uff0c\u600e\u4e48\u529e\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u59d0\u59d0\u8eba\u7740\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u6765\u670d\u4f8d\u59d0\u59d0\u3002\u201d</p><p>\u4ed6\u53bb\u6253\u4e86\u4e00\u76c6\u6e29\u6c34\uff0c\u7528\u4e1d\u5e15\u6d78\u6e7f\u4e86\uff0c\u6253\u7b97\u5148\u66ff\u73ca\u5609\u64e6\u62ed\u8eab\u4f53\u3002\u6380\u5f00\u6bdb\u6bef\uff0c\u53d1\u73b0\u6d01\u767d\u5e8a\u5355\u4e0a\u4e00\u5757\u5757\u5e72\u6db8\u7684\u6c34\u6e0d\uff0c\u70b9\u7f00\u7740\u661f\u661f\u70b9\u70b9\u7684\u843d\u7ea2\uff0c\u4eff\u4f5b\u6735\u6735\u6885\u82b1\uff0c\u548c\u59d0\u59d0\u7389\u4f53\u76f8\u4e92\u6620\u886c\uff0c\u770b\u8d77\u6765\u522b\u5177\u4e00\u756a\u6deb\u7cdc\u7f8e\u611f\u3002\u4f38\u624b\u63e1\u4f4f\u5c11\u5973\u7cbe\u81f4\u7684\u8db3\u8e1d\uff0c\u5c06\u5979\u53cc\u817f\u6446\u6210\u5c48\u5206\u59ff\u52bf\uff0c\u4ed4\u7ec6\u5bdf\u770b\u79c1\u5904\uff0c\u53d1\u73b0\u5df2\u7ecf\u660e\u663e\u7ea2\u80bf\uff0c\u5206\u5916\u4ee4\u4eba\u601c\u60dc\u3002\u73ca\u5609\u88ab\u4ed6\u6446\u6210\u8fd9\u79cd\u7f9e\u4eba\u59ff\u52bf\uff0c\u53c8\u89c1\u4ed6\u76ee\u4e0d\u8f6c\u775b\u76ef\u7740\u81ea\u5df1\u4e0b\u4f53\uff0c\u4e0d\u7531\u5f97\u53c8\u7f9e\u53c8\u6c14\uff0c\u4e24\u988a\u9161\u7ea2\uff0c\u51e0\u4e4e\u8981\u706b\u8fa3\u8fa3\u5730\u70e7\u8d77\u6765\u3002</p><p>\u201c\u522b\u770b\u5566\uff0c\u201d\u5979\u5a07\u55d4\uff0c\u201c\u6628\u665a\u8fd8\u6ca1\u770b\u591f\u4e48\uff0c\u4e00\u5927\u65e9\u7684\uff0c\u5c31\u77e5\u9053\u6b3a\u8d1f\u59d0\u59d0\u3002\u201d</p><p>\u743c\u6069\u7b11\u7740\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7528\u4e1d\u5e15\u8638\u6c34\u66ff\u73ca\u5609\u5c06\u4e0b\u8eab\u6c61\u7269\u62ed\u51c0\u3002\u4ed6\u52a8\u4f5c\u5df2\u7ecf\u5c3d\u53ef\u80fd\u8f7b\u67d4\uff0c\u4f46\u73ca\u5609\u6bd5\u7adf\u662f\u5904\u5b50\u7834\u8eab\uff0c\u5a07\u5ae9\u65e0\u6bd4\uff0c\u4f9d\u65e7\u75bc\u5f97\u5389\u5bb3\u3002\u5c11\u5973\u7709\u5934\u8f7b\u8e59\uff0c\u7d27\u7d27\u54ac\u7740\u5634\u5507\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5fcd\u8010\u8fc7\u53bb\u3002\u743c\u6069\u518d\u66ff\u5979\u64e6\u4e86\u904d\u8eab\u4f53\uff0c\u6362\u4e0a\u4e00\u5957\u65b0\u7761\u8863\uff0c\u91cd\u65b0\u6253\u6c34\u8ba9\u73ca\u5609\u6d17\u6f31\uff0c\u6276\u5979\u9760\u7740\u5e8a\u5934\u5750\u597d\uff0c\u4e0b\u697c\u53bb\u5c06\u65e9\u9910\u7aef\u4e86\u4e0a\u6765\u3002</p><p>\u201c\u5750\u7740\uff0c\u201d\u73ca\u5609\u67d4\u58f0\u8bf4\uff0c\u201c\u8dd1\u4e0a\u8dd1\u4e0b\u7684\u7d2f\u4e86\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u80fd\u670d\u4f8d\u59d0\u59d0\uff0c\u5fc3\u91cc\u9ad8\u5174\u5f97\u5f88\u5462\uff0c\u4e00\u70b9\u90fd\u4e0d\u7d2f\u3002\u201d</p><p>\u4ed6\u5f85\u73ca\u5609\u5403\u5b8c\u65e9\u9910\uff0c\u6536\u62fe\u5b8c\u6bd5\uff0c\u51c6\u5907\u9001\u4e0b\u697c\u53bb\uff0c\u770b\u770b\u65f6\u95f4\u4e5f\u5dee\u4e0d\u591a\u8be5\u53bb\u89c1\u5e03\u96f7\u7eb3\u65af\u4e86\u3002\u201c\u59d0\u59d0\u4f60\u5728\u5bb6\u597d\u597d\u4f11\u606f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e8b\u60c5\u5b8c\u4e86\u5c31\u56de\u6765\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u7ad9\u8d77\u8eab\uff0c\u6b63\u5f85\u51fa\u95e8\uff0c\u4e00\u773c\u77a5\u89c1\u68b3\u5986\u53f0\u4e0a\u653e\u7740\u7684\u90a3\u652f\u591c\u6c89\u6728\u957f\u7b1b\uff0c\u4e0d\u7531\u5f97\u5fae\u5fae\u4e00\u6014\u3002\u82e5\u8bf4\u8d77\u6765\uff0c\u4ed6\u8fd9\u4e9b\u5929\u7684\u5934\u75bc\u70e6\u607c\uff0c\u5168\u90fd\u6e90\u4e8e\u6b64\u7269\uff0c\u51e0\u6b21\u90fd\u6709\u60f3\u5077\u5077\u628a\u5b83\u7838\u788e\u7684\u51b2\u52a8\uff0c\u4f46\u6b64\u65f6\u518d\u770b\uff0c\u5374\u5df2\u7ecf\u6ca1\u90a3\u79cd\u611f\u89c9\u4e86\u3002\u6628\u591c\u548c\u73ca\u5609\u6625\u98ce\u4e00\u5ea6\uff0c\u4e0d\u4ec5\u4ec5\u662f\u8ba9\u4ed6\u5b9e\u73b0\u4e86\u4e00\u76f4\u4ee5\u6765\u7684\u68a6\u60f3\uff0c\u5f7b\u5e95\u5360\u6709\u4e86\u59d0\u59d0\uff0c\u66f4\u8ba9\u4ed6\u7684\u5fc3\u6001\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6709\u4e86\u5fae\u5999\u7684\u53d8\u5316\u3002\u201c\u59d0\u59d0\u5f85\u6211\u4e00\u5fc3\u4e00\u610f\uff0c\u8fde\u81ea\u5df1\u90fd\u7ed9\u4e86\u6211\uff0c\u6211\u4e5f\u662f\u771f\u7684\u559c\u6b22\u59d0\u59d0\uff0c\u6b64\u60c5\u6b64\u5fc3\u7edd\u65e0\u534a\u70b9\u865a\u5047\u3002\u65e2\u7136\u5982\u6b64\uff0c\u4e24\u4eba\u4e4b\u95f4\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u8bf4\u4e0d\u5f00\u7684\uff0c\u6211\u96be\u9053\u8fd8\u80fd\u6000\u7591\u59d0\u59d0\u5f85\u6211\u7684\u5fc3\u610f\u4e48\uff0c\u90a3\u4fbf\u771f\u662f\u53ef\u7b11\u4e86\u3002\u201d</p><p>\u5176\u5b9e\u8fd9\u4e2a\u9053\u7406\uff0c\u4ed6\u4ee5\u524d\u4e5f\u5e76\u4e0d\u662f\u4e0d\u77e5\u9053\u3002\u53ea\u662f\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\u5f80\u5f80\u5982\u6b64\uff0c\u9053\u7406\u600e\u4e48\u8bf4\u90fd\u884c\uff0c\u600e\u4e48\u60f3\u90fd\u53ef\u4ee5\u901a\uff0c\u5173\u952e\u5728\u4e8e\u5fc3\u6001\u3002\u5fc3\u6001\u4e0d\u5bf9\uff0c\u518d\u6b63\u786e\u7684\u9053\u7406\uff0c\u4e5f\u4f9d\u65e7\u4f1a\u60a3\u5f97\u60a3\u5931\uff0c\u754f\u9996\u754f\u5c3e\uff1b\u5fc3\u6001\u5bf9\u4e86\uff0c\u81ea\u7136\u4e00\u5207\u770b\u5f00\uff0c\u98ce\u8f7b\u4e91\u6de1\uff0c\u4ee5\u524d\u7684\u56f0\u6270\u987f\u65f6\u90fd\u53d8\u5f97\u4e0d\u503c\u4e00\u63d0\uff0c\u4e0d\u5728\u8bdd\u4e0b\u3002\u743c\u6069\u592a\u8fc7\u770b\u91cd\u73ca\u5609\uff0c\u4e00\u65e6\u6267\u7740\uff0c\u5c31\u5931\u4e86\u6e05\u660e\uff0c\u4e71\u4e86\u65b9\u5bf8\uff1b\u5982\u4eca\u7434\u745f\u65e2\u8c10\uff0c\u6b22\u597d\u5df2\u6bd5\uff0c\u795e\u6e05\u6c14\u723d\u4e4b\u4e0b\u56de\u5934\u518d\u770b\uff0c\u5c31\u89c9\u5f97\u81ea\u5df1\u4ee5\u524d\u5c45\u7136\u4e3a\u8fd9\u70b9\u533a\u533a\u5c0f\u4e8b\u56f0\u6270\uff0c\u5f53\u771f\u662f\u611a\u4e0d\u53ef\u53ca\uff0c\u53ef\u7b11\u5f97\u5f88\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u56de\u8fc7\u5934\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c\u6211\u60f3\u95ee\u4f60\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u201c\u60f3\u95ee\u6211\u90a3\u652f\u957f\u7b1b\u662f\u8c01\u9001\u7684\uff1f\u201d\u73ca\u5609\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\u3002</p><p>\u743c\u6069\u6014\u4f4f\u3002</p><p>\u201c\u60ca\u8bb6\u4ec0\u4e48\uff0c\u4f60\u662f\u6211\u5f1f\u5f1f\uff0c\u4f60\u5fc3\u91cc\u60f3\u4ec0\u4e48\uff0c\u6211\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\uff0c\u201d\u73ca\u5609\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u5012\u662f\u4f60\u72b9\u8c6b\u4e86\u8fd9\u4e48\u4e45\uff0c\u8ba9\u6211\u51fa\u4e4e\u610f\u6599\uff0c\u59d0\u59d0\u5df2\u7ecf\u7b49\u4e86\u5f88\u4e45\u5462\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u2026\u2026\u4f60\u662f\u6545\u610f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u6545\u610f\u7684\uff0c\u201d\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u8299\u8389\u5a05\u53bb\u8fc7\u97f3\u4e50\u5b66\u9662\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6211\u5f53\u7136\u77e5\u9053\uff1b\u5b66\u9662\u7684\u5f88\u591a\u89c4\u77e9\uff0c\u8fd8\u662f\u5979\u544a\u8bc9\u6211\u7684\u3002\u4f60\u6210\u5929\u548c\u5979\u4eec\u59d0\u59b9\u4fe9\u5728\u4e00\u8d77\uff0c\u81ea\u7136\u5f88\u5bb9\u6613\u5c31\u4f1a\u53d1\u73b0\u59d0\u59d0\u662f\u5728\u9a97\u4f60\u3002\u8fd9\u4e48\u7b80\u5355\u7684\u9053\u7406\uff0c\u90fd\u60f3\u4e0d\u660e\u767d\uff1f\u201d</p><p>\u201c\u53ef\u662f\u59d0\u59d0\u4f60\u5e72\u561b\u8981\u9a97\u6211\uff1f\u201d\u743c\u6069\u4e0d\u89e3\u3002</p><p>\u201c\u56e0\u4e3a\u59d0\u59d0\u559c\u6b22\u554a\u3002\u201d\u73ca\u5609\u7406\u6240\u5f53\u7136\u5730\u56de\u7b54\u3002</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u59d0\u59d0\u60f3\u770b\u770b\u4f60\u62c5\u5fc3\uff0c\u770b\u770b\u4f60\u7740\u6025\uff0c\u770b\u770b\u4f60\u7d27\u5f20\uff0c\u770b\u770b\u4f60\u5403\u918b\u7684\u6837\u5b50\u554a\uff0c\u201d\u73ca\u5609\u7b11\u76c8\u76c8\u5730\u8bf4\uff0c\u201c\u770b\u7740\u4f60\u60f3\u95ee\u53c8\u4e0d\u6562\u95ee\uff0c\u60f3\u8bf4\u53c8\u4e0d\u6562\u8bf4\uff0c\u6101\u7709\u4e0d\u5c55\uff0c\u4e3e\u6b62\u5931\u63aa\uff0c\u5f88\u6709\u8da3\u5462\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff01\u201d</p><p>\u743c\u6069\u4e00\u65f6\u95f4\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u597d\uff0c\u8fd9\u4e9b\u5929\u6765\u7684\u62c5\u5fe7\uff0c\u6574\u65e5\u6574\u591c\u7684\u56f0\u60d1\uff0c\u7ffb\u6765\u8986\u53bb\u7684\u5fc3\u7406\u6323\u624e\uff0c\u539f\u6765\u90fd\u4e0d\u8fc7\u662f\u4e2a\u73a9\u7b11\uff0c\u5f04\u5f97\u81ea\u5df1\u50cf\u4e2a\u5927\u50bb\u74dc\u4e00\u6837\u3002\u5018\u82e5\u6362\u4e86\u662f\u522b\u4eba\uff0c\u4ed6\u65e9\u5c31\u52c3\u7136\u5927\u6012\uff0c\u5c31\u7b97\u4e0d\u76f4\u63a5\u7ffb\u8138\uff0c\u4e5f\u4f1a\u62c2\u8896\u800c\u53bb\uff0c\u4f46\u5982\u4eca\u9762\u5bf9\u7684\u662f\u73ca\u5609\uff0c\u90a3\u4e5f\u53ea\u80fd\u5f3a\u81ea\u6309\u637a\u4e0b\u6765\u4e86\u3002</p><p>\u201c\u751f\u6c14\u4e86\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u4e5f\u6ca1\u6709\u5566\uff0c\u201d\u743c\u6069\u6709\u70b9\u4e0d\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u53ea\u662f\u59d0\u59d0\u4f60\u8fd9\u73a9\u7b11\u5f00\u5f97\u4e5f\u672a\u514d\u2026\u2026\u8ba9\u6211\u62c5\u5fc3\u4e86\u8fd9\u4e48\u4e45\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u8bf4\u4fe1\u4efb\u59d0\u59d0\u4e48\uff0c\u201d\u73ca\u5609\u8f7b\u8f7b\u6253\u65ad\uff0c\u201c\u90a3\u4f60\u62c5\u5fc3\u4ec0\u4e48\u5462\uff1f\u4f60\u5fc3\u91cc\u6709\u8bdd\uff0c\u8fd9\u4e48\u591a\u5929\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u76f4\u63a5\u95ee\u59d0\u59d0\uff0c\u504f\u504f\u8981\u81ea\u5df1\u80e1\u4e71\u731c\u6d4b\u5462\uff1f\u201d</p><p>\u743c\u6069\u54d1\u53e3\u65e0\u8a00\u3002</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u59d0\u59d0\u3002\u201d\u8fc7\u4e86\u534a\u664c\uff0c\u4ed6\u8d70\u5230\u5e8a\u8fb9\uff0c\u5c06\u73ca\u5609\u62b1\u5728\u6000\u91cc\uff0c\u8f7b\u8f7b\u5728\u5979\u8033\u8fb9\u8bf4\u3002</p><p>\u201c\u600e\u4e48\u7a81\u7136\u8bf4\u5bf9\u4e0d\u8d77\u554a\u3002\u201d</p><p>\u201c\u662f\u6211\u7684\u9519\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u6ca1\u80fd\u5b8c\u5168\u4fe1\u4efb\u59d0\u59d0\uff0c\u6240\u4ee5\u624d\u4f1a\u5f04\u51fa\u8fd9\u4e9b\u7b11\u8bdd\u6765\uff0c\u5982\u679c\u6211\u2026\u2026\u201d</p><p>\u73ca\u5609\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u7b28\u86cb\uff0c\u59d0\u59d0\u95ee\u4f60\uff0c\u4ec0\u4e48\u662f\u5b8c\u5168\u7684\u4fe1\u4efb\u554a\uff1f\u201d</p><p>\u201c\u5b8c\u5168\u7684\u4fe1\u4efb\u2026\u2026\u5c31\u662f\u65e0\u6761\u4ef6\u7684\u4fe1\u4efb\u554a\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u90a3\u5982\u679c\u4f60\u4eb2\u773c\u770b\u89c1\u59d0\u59d0\u548c\u522b\u7684\u7537\u5b69\u5b50\u537f\u537f\u6211\u6211\uff0c\u4f60\u8fd8\u80fd\u4fe1\u4efb\u59d0\u59d0\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u5f88\u591a\u65f6\u5019\u4e0d\u8981\u8bf4\u5f97\u592a\u7edd\u5bf9\uff0c\u592a\u7406\u60f3\u5316\u7684\u4e1c\u897f\uff0c\u5f80\u5f80\u5c31\u6ca1\u6709\u4ec0\u4e48\u610f\u4e49\u3002\u4f60\u4fe1\u4efb\u59d0\u59d0\uff0c\u59d0\u59d0\u5f53\u7136\u9ad8\u5174\uff0c\u4f46\u5982\u679c\u8bf4\u771f\u7684\u4fe1\u4efb\u5230\u4f60\u6240\u8bf4\u5f97\u90a3\u79cd\u7a0b\u5ea6\uff0c\u59d0\u59d0\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u4f60\u90fd\u4e0d\u62c5\u5fc3\uff0c\u4e0d\u5728\u610f\uff0c\u4e0d\u7d27\u5f20\uff0c\u4e0d\u7740\u6025\u2014\u2014\u90a3\u6211\u8fd8\u8981\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u62cd\u4e86\u62cd\u743c\u6069\u7684\u8138\uff0c\u201c\u6216\u8bb8\u827e\u5f25\u8587\u4f1a\u559c\u6b22\u8fd9\u6837\uff0c\u5979\u575a\u5f3a\u521a\u6bc5\uff0c\u606a\u5b88\u672c\u5fc3\uff0c\u4e0d\u4e3a\u5916\u7269\u6240\u79fb\uff0c\u5979\u672c\u8eab\u5c31\u662f\u4e2a\u6781\u7aef\u2026\u2026\u5c31\u662f\u4e2a\u5b8c\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u62b1\u7740\u5b8c\u7f8e\u7684\u61a7\u61ac\uff0c\u5e0c\u671b\u5b8c\u7f8e\u7684\u7231\u60c5\uff0c\u8981\u6c42\u5b8c\u7f8e\u7684\u4fe1\u4efb\uff0c\u4f46\u59d0\u59d0\u4e0d\u662f\u5462\uff0c\u201d\u5979\u5ae3\u7136\u5fae\u7b11\uff0c\u201c\u59d0\u59d0\u5462\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u666e\u901a\u4eba\u7f62\u4e86\u2014\u2014\u597d\u5566\u597d\u5566\uff0c\u201d\u5979\u8f7b\u8f7b\u54ac\u7740\u743c\u6069\u7684\u8033\u5782\uff0c\u58f0\u97f3\u65e2\u5a07\u4e14\u5a9a\uff0c\u201c\u59d0\u59d0\u4e0d\u8be5\u5f00\u8fd9\u4e2a\u73a9\u7b11\uff0c\u4ee5\u540e\u4fdd\u8bc1\u4e0d\u4f1a\u4e86\uff0c\u770b\u5728\u59d0\u59d0\u88ab\u4f60\u6b3a\u8d1f\u5f97\u8fd9\u4e48\u60e8\u7684\u4efd\u4e0a\u2026\u2026\u8fd9\u6b21\u5c31\u539f\u8c05\u59d0\u59d0\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u67d4\u60c5\u653b\u52bf\u4e4b\u4e0b\uff0c\u743c\u6069\u987f\u65f6\u8d25\u4e0b\u9635\u6765\u3002\u201c\u59d0\u59d0\uff0c\u4f60\u53ef\u771f\u8fc7\u5206\uff0c\u8fd9\u4e9b\u5929\u628a\u6211\u6298\u78e8\u574f\u4e86\u5462\uff0c\u201d\u4ed6\u5f00\u73a9\u7b11\uff0c\u201c\u611f\u89c9\u667a\u529b\u90fd\u4e0b\u964d\u4e86\u534a\u622a\u3002\u4e0d\u8fc7\u8bf4\u8d77\u6765\uff0c\u6211\u5012\u771f\u6ca1\u6599\u5230\u59d0\u59d0\u4f1a\u4e70\u8fd9\u6837\u8d35\u91cd\u7684\u957f\u7b1b\uff0c\u5927\u6982\u4ece\u4e00\u5f00\u59cb\uff0c\u5fc3\u91cc\u5176\u5b9e\u5c31\u6709\u7591\u8651\u5427\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u4e0a\u5f53\u4e86\u3002\u201d</p><p>\u201c\u4f60\u6ca1\u5f04\u9519\u554a\uff0c\u201d\u73ca\u5609\u70b9\u70b9\u5934\uff0c\u201c\u5b83\u786e\u5b9e\u4e0d\u662f\u6211\u4e70\u7684\uff0c\u662f\u522b\u4eba\u9001\u7684\u3002\u201d</p><p>\u743c\u6069\u6014\u4f4f\uff0c\u201c\u8c01\uff1f\u201d</p><p>\u201c\u4f60\u8ba4\u8bc6\u7684\uff0c\u5e03\u96f7\u7eb3\u65af\xb7\u5766\u8212\u5c14\u6bbf\u4e0b\u3002\u201d</p></article>"}}}]);
