(window.webpackJsonp=window.webpackJsonp||[]).push([[428],{450:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516b\u8282 \u5f80\u4e8b\u548c\u6545\u4eba",content:"<article><h2>\u7b2c\u5341\u516b\u8282 \u5f80\u4e8b\u548c\u6545\u4eba</h2><p>\u4e16\u4e8b\u7684\u5947\u5999\uff0c\u5c31\u5728\u4e8e\u603b\u80fd\u8ba9\u4eba\u51fa\u4e4e\u610f\u6599\u3002\u5f53\u4f60\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u80e1\u601d\u4e71\u60f3\u7684\u65f6\u5019\uff0c\u6700\u540e\u53d1\u73b0\u53ea\u662f\u4e00\u573a\u73a9\u7b11\uff1b\u800c\u5f53\u4f60\u7ec8\u4e8e\u5982\u91ca\u91cd\u8d1f\uff0c\u4ee5\u4e3a\u4e00\u5207\u90fd\u5df2\u7ecf\u8fc7\u53bb\uff0c\u5374\u53d1\u73b0\u539f\u672c\u7684\u731c\u6d4b\u5c45\u7136\u662f\u73b0\u5b9e\u3002</p><p>\u201c\u5e03\u96f7\u7eb3\u65af\uff1f\u201d\u743c\u6069\u4e00\u6014\u3002</p><p>\u201c\u662f\u554a\uff0c\u6211\u4e0d\u662f\u544a\u8bc9\u4f60\uff0c\u8fd1\u6765\u8ba4\u8bc6\u4e86\u4e00\u4e2a\u65b0\u670b\u53cb\u4e48\uff0c\u5c31\u662f\u4ed6\u4e86\u3002\u201d</p><p>\u201c\u59d0\u59d0\u4f60\u2026\u2026\u4f60\u600e\u4e48\u8ba4\u8bc6\u4ed6\u7684\uff1f\u201d</p><p>\u201c\u5728\u5b66\u6821\u91cc\u8ba4\u8bc6\u7684\u3002\u201d</p><p>\u97f3\u4e50\u5b66\u9662\u7684\u540e\u65b9\u662f\u4e00\u5904\u82b1\u56ed\uff0c\u5176\u4e2d\u6709\u4e00\u7247\u5c0f\u6811\u6797\uff0c\u50fb\u9759\u4f18\u96c5\uff0c\u73ca\u5609\u5728\u5b66\u6821\u7684\u65f6\u5019\uff0c\u6bcf\u5929\u4e2d\u5348\u90fd\u4e60\u60ef\u53bb\u90a3\u91cc\u7ec3\u4e60\u5439\u7b1b\u3002\u6709\u4e00\u65e5\uff0c\u5979\u5439\u5b8c\u4e00\u66f2\uff0c\u7a81\u7136\u542c\u89c1\u65c1\u8fb9\u6811\u836b\u4e0b\u6709\u4eba\u51fa\u8a00\u6307\u70b9\u3002\u539f\u6765\u5e03\u96f7\u7eb3\u65af\u5728\u57ce\u4e2d\u7597\u517b\uff0c\u95f2\u6687\u65e0\u4e8b\u6765\u5b66\u9662\u770b\u770b\uff0c\u4e5f\u5728\u8fd9\u91cc\u4f11\u606f\uff0c\u6070\u597d\u9047\u89c1\uff0c\u4e24\u4eba\u5c31\u6b64\u8ba4\u8bc6\u3002</p><p>\u201c\u4ece\u90a3\u65e5\u4e4b\u540e\uff0c\u6211\u6bcf\u5929\u4e2d\u5348\u53bb\u7ec3\u4e60\uff0c\u53d1\u73b0\u4ed6\u4e5f\u90fd\u4f1a\u5728\u573a\uff0c\u5728\u65c1\u8fb9\u542c\uff0c\u7136\u540e\u6307\u70b9\u51e0\u53e5\u3002\u4ed6\u8bf4\u8bdd\u4e0d\u591a\uff0c\u4f46\u603b\u662f\u80fd\u591f\u4e00\u8bed\u4e2d\u7684\uff0c\u5207\u4e2d\u8981\u5bb3\uff0c\u8ba9\u6211\u83b7\u76ca\u532a\u6d45\u5462\u3002\u201d</p><p>\u201c\u7136\u540e\u4ed6\u5c31\u9001\u4e86\u4f60\u90a3\u652f\u957f\u7b1b\uff1f\u201d\u743c\u6069\u7684\u8bed\u6c14\u91cc\u6709\u4e9b\u9178\u6e9c\u6e9c\u7684\u5473\u9053\u3002</p><p>\u73ca\u5609\u7ed3\u8bc6\u65b0\u670b\u53cb\uff0c\u8fd9\u4e0d\u662f\u95ee\u9898\uff0c\u670b\u53cb\u4e4b\u95f4\u8d60\u9001\u793c\u7269\uff0c\u4e5f\u4e0d\u662f\u95ee\u9898\u2014\u2014\u4f46\u8fd9\u652f\u957f\u7b1b\u672a\u514d\u592a\u8fc7\u8d35\u91cd\u4e86\u70b9\u3002\u4e00\u4e2a\u7537\u5b50\u8d60\u9001\u8fd9\u6837\u8d35\u91cd\u7684\u4e1c\u897f\uff0c\u73ca\u5609\u5766\u7136\u63a5\u53d7\uff0c\u8fd9\u603b\u662f\u8ba9\u743c\u6069\u5fc3\u91cc\u6709\u4e9b\u4e0d\u592a\u8212\u670d\u3002</p><p>\u201c\u5ac9\u5992\u4e86\uff1f\u201d\u73ca\u5609\u542b\u7b11\u770b\u7740\u4ed6\u3002</p><p>\u201c\u54ea\u6709\u2026\u2026\u662f\u5566\u662f\u5566\uff0c\u201d\u743c\u6069\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u8c01\u8ba9\u6211\u6709\u4e2a\u8fd9\u4e48\u6f02\u4eae\u7684\u59d0\u59d0\u5462\uff0c\u81ea\u7136\u8981\u770b\u5f97\u7d27\u70b9\u2026\u2026\u59d0\u59d0\uff0c\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u5730\u8bf4\uff0c\u201c\u4ed6\u4e0d\u4f1a\u662f\u559c\u6b22\u4e0a\u4f60\u4e86\u5427\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\u3002</p><p>\u201c\u5018\u82e5\u5982\u6b64\uff0c\u6211\u8fd8\u4f1a\u6536\u4ed6\u7684\u793c\u7269\u4e48\uff1f\u201d\u5979\u6de1\u6de1\u53cd\u95ee\uff0c\u201c\u5728\u4f60\u7684\u5fc3\u91cc\uff0c\u59d0\u59d0\u662f\u90a3\u79cd\u4e0d\u77e5\u9053\u8fdb\u9000\u5206\u5bf8\u7684\u5973\u5b69\u5b50\uff1f\u201d</p><p>\u867d\u7136\u662f\u88ab\u8d23\u5907\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5e03\u96f7\u7eb3\u65af\u786e\u5b9e\u662f\u4e2a\u4ee4\u4ed6\u5934\u75bc\u751a\u81f3\u754f\u60e7\u7684\u201c\u60c5\u654c\u201d\uff0c\u5018\u82e5\u4ed6\u771f\u5bf9\u73ca\u5609\u6709\u610f\uff0c\u90a3\u9ebb\u70e6\u53ef\u5c31\u5927\u4e86\uff0c\u4ed6\u96be\u4e0d\u6210\u8fd8\u6562\u53bb\u548c\u5e03\u96f7\u7eb3\u65af\u51b3\u6597\u4e0d\u6210\uff0c\u5bf9\u65b9\u53ef\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u5927\u5deb\u5e08\uff0c\u7eb5\u7136\u73b0\u5728\u6709\u4f24\u5728\u8eab\uff0c\u90a3\u4e5f\u4e0d\u662f\u743c\u6069\u53ef\u4ee5\u5bf9\u4ed8\u7684\u3002</p><p>\u201c\u5bf9\u4e0d\u8d77\u5566\u59d0\u59d0\uff0c\u6211\u4e0d\u662f\u90a3\u610f\u601d\uff0c\u201d\u743c\u6069\u8d76\u7d27\u9053\u6b49\uff0c\u201c\u6211\u53ea\u662f\u2026\u2026\u90a3\u4e2a\uff0c\u6bd4\u8f83\u62c5\u5fc3\u561b\u3002\u201d</p><p>\u201c\u4f60\u5440\uff0c\u4ece\u5c0f\u5c31\u8fd9\u6837\uff0c\u5fc3\u4e8b\u91cd\uff0c\u60f3\u5f97\u591a\uff0c\u201d\u73ca\u5609\u8bed\u6c14\u61d2\u61d2\u7684\uff0c\u201c\u4ed6\u786e\u5b9e\u633a\u770b\u91cd\u6211\uff0c\u8fd9\u70b9\u6211\u4e5f\u80fd\u611f\u89c9\u5f97\u51fa\u6765\uff0c\u4f46\u4e0d\u662f\u90a3\u79cd\u7537\u5973\u95f4\u7684\u559c\u6b22\u3002\u4ed6\u5f85\u6211\u4e0d\u9519\uff0c\u6559\u4e86\u6211\u5f88\u591a\u4e1c\u897f\uff0c\u6240\u4ee5\u6211\u4e5f\u4e0d\u597d\u62d2\u7edd\u3002\u81f3\u4e8e\u8fd9\u652f\u957f\u7b1b\u4e48\uff0c\u4e0e\u5176\u8bf4\u662f\u9001\u6211\uff0c\u4e0d\u5982\u8bf4\u662f\u9001\u7ed9\u4ed6\u81ea\u5df1\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u59d0\u59d0\u4f60\u8bf4\u4ec0\u4e48\uff0c\u6211\u600e\u4e48\u542c\u4e0d\u61c2\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u76f4\u89c9\u7f62\u4e86\uff0c\u201d\u73ca\u5609\u8f7b\u63cf\u6de1\u5199\u5730\u5e26\u8fc7\uff0c\u201c\u5feb\u53bb\u5427\uff0c\u522b\u8bef\u4e86\u65f6\u95f4\uff0c\u65e9\u70b9\u56de\u6765\uff0c\u59d0\u59d0\u5728\u5bb6\u7b49\u4f60\u3002\u201d</p><p>\u743c\u6069\u89c1\u73ca\u5609\u4e0d\u60f3\u89e3\u91ca\uff0c\u4e5f\u4fbf\u4e0d\u518d\u591a\u95ee\uff0c\u5728\u5979\u5507\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u6b63\u51c6\u5907\u79bb\u5f00\uff0c\u9661\u7136\u4e00\u4e1d\u7075\u5149\u5728\u8111\u4e2d\u63a0\u8fc7\uff0c\u987f\u65f6\u6574\u4e2a\u4eba\u50cf\u88ab\u7535\u51fb\u4e00\u822c\u9707\u4e86\u9707\u3002\u73ca\u5609\u6b64\u65f6\u8fd8\u8d34\u5728\u4ed6\u6000\u4e2d\uff0c\u7acb\u523b\u5bdf\u89c9\u5230\u4e86\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u60ca\u8bb6\u5730\u95ee\uff0c\u201c\u4f60\u8eab\u4f53\u4e0d\u8212\u670d\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u2026\u2026\u59d0\u59d0\uff0c\u4f60\u521a\u624d\u8bf4\uff0c\u4f60\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u8ba4\u8bc6\u5e03\u96f7\u7eb3\u65af\u6bbf\u4e0b\u7684\uff1f\u201d</p><p>\u201c\u5b66\u6821\u540e\u9762\u7684\u82b1\u56ed\u91cc\u554a\u3002\u201d</p><p>\u201c\u662f\u4e00\u5904\u5c0f\u6811\u6797\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u662f\u4e0d\u662f\u6709\u8349\u5730\uff0c\u8fd8\u957f\u6ee1\u4e86\u9634\u5f71\u5170\uff0c\u548c\u5e73\u5e38\u89c1\u5230\u7684\u90a3\u79cd\u4e0d\u592a\u4e00\u6837\uff0c\u82b1\u74e3\u662f\u7d2b\u8272\u7684\uff0c\u82b1\u7c89\u94f6\u8272\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u73ca\u5609\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u4f60\u4e5f\u53bb\u8fc7\uff1f\u201d</p><p>\u201c\u6211\u2026\u2026\u6211\u5728\u68a6\u91cc\u89c1\u8fc7\u3002\u201d</p><p>\u73ca\u5609\u6014\u4e86\u6014\uff0c\u683c\u683c\u7b11\u8d77\u6765\uff0c\u4ee5\u4e3a\u5f1f\u5f1f\u5728\u5f00\u73a9\u7b11\u3002\u7136\u800c\u743c\u6069\u5e76\u975e\u5f00\u73a9\u7b11\uff0c\u4ed6\u786e\u786e\u5b9e\u5b9e\u662f\u5728\u68a6\u91cc\u89c1\u8fc7\u2014\u2014\u5c31\u662f\u4e0d\u4e45\u524d\u505a\u7684\u90a3\u4e2a\u53e4\u602a\u7684\u68a6\u3002\u4ed6\u6e05\u695a\u5730\u8bb0\u5f97\uff0c\u5728\u68a6\u91cc\u770b\u89c1\u4e86\u97f3\u4e50\u5b66\u9662\u7684\u767d\u8272\u5c16\u5854\uff0c\u77e5\u9053\u5c31\u5728\u5de6\u8fd1\u4e0d\u8fdc\u3002\u73ca\u5609\u8bf4\u5728\u540e\u82b1\u56ed\u5c0f\u6811\u6797\u91cc\u548c\u5e03\u96f7\u7eb3\u65af\u8ba4\u8bc6\uff0c\u743c\u6069\u5f53\u65f6\u6ca1\u5728\u610f\uff0c\u521a\u624d\u7a81\u7136\u53cd\u5e94\u8fc7\u6765\uff1a\u8fd9\u5730\u65b9\uff0c\u83ab\u975e\u5c31\u662f\u68a6\u5883\u91cc\u90a3\u4e2a\u8c0b\u6740\u73b0\u573a\uff1f</p><p>\u663e\u7136\u6b63\u662f\u3002</p><p>\u8fd9\u6837\u4e00\u6765\u4e8b\u60c5\u5c31\u66f4\u53e4\u602a\uff0c\u743c\u6069\u8fde\u97f3\u4e50\u5b66\u9662\u7684\u5927\u95e8\u90fd\u6ca1\u8fdb\u53bb\u8fc7\uff0c\u66f4\u6ca1\u53bb\u8fc7\u4ec0\u4e48\u82b1\u56ed\uff0c\u7406\u5f53\u662f\u5168\u65e0\u534a\u70b9\u5370\u8c61\u624d\u5bf9\uff0c\u600e\u4e48\u4f1a\u68a6\u89c1\uff0c\u53c8\u600e\u4e48\u4f1a\u68a6\u5f97\u8fd9\u4e48\u51c6\uff0c\u5c45\u7136\u548c\u73b0\u5b9e\u60c5\u5f62\u4e00\u6a21\u4e00\u6837\uff1f\u5018\u82e5\u8fd9\u4e5f\u662f\u5de7\u5408\u7684\u8bdd\uff0c\u90a3\u4e5f\u672a\u514d\u5de7\u5408\u5f97\u592a\u8fc7\u79bb\u8c31\u3002</p><p>\u800c\u4ed6\u771f\u6b63\u60ca\u9a87\u7684\uff0c\u5176\u5b9e\u8fd8\u4e0d\u662f\u8fd9\u4e2a\u3002\u5f53\u65f6\u68a6\u91cc\u770b\u89c1\u7684\u90a3\u540d\u7537\u5b50\uff0c\u90a3\u4e2a\u8c0b\u6740\u8005\uff0c\u743c\u6069\u770b\u4e0d\u6e05\u76f8\u8c8c\uff0c\u5374\u603b\u89c9\u5f97\u6709\u4e9b\u719f\u6089\u2014\u2014\u73b0\u5728\u4ed6\u7ec8\u4e8e\u60f3\u4e86\u8d77\u6765\uff0c\u90a3\u5bb6\u4f19\u7684\u8eab\u5f62\u8f6e\u5ed3\u548c\u884c\u52a8\u4e3e\u6b62\uff0c\u548c\u5e03\u96f7\u7eb3\u65af\u5c45\u7136\u6709\u90a3\u4e48\u51e0\u5206\u76f8\u4f3c\u4e4b\u611f\u3002</p><p>\u201c\u68a6\u91cc\u7684\u90a3\u4e2a\u7537\u5b50\u662f\u5e03\u96f7\u7eb3\u65af\uff1f\u90a3\u4ed6\u6740\u7684\u53c8\u662f\u8c01\uff1f\u73ca\u5609\uff1f\u96be\u9053\u8bf4\u8fd9\u4e2a\u68a6\u8fd8\u771f\u662f\u9884\u5146\uff0c\u662f\u5728\u6697\u793a\u5e03\u96f7\u7eb3\u65af\u5bf9\u59d0\u59d0\u5fc3\u6000\u6740\u673a\uff1f\u201d</p><p>\u8fd9\u4e48\u8bf4\u4f3c\u4e4e\u4e5f\u6709\u70b9\u9053\u7406\uff0c\u4f46\u8f6c\u5ff5\u518d\u60f3\u5c31\u53d1\u73b0\u4e0d\u5bf9\uff0c\u68a6\u91cc\u90a3\u5bf9\u7537\u5973\u662f\u6bcd\u5b50\u5173\u7cfb\uff0c\u5e03\u96f7\u7eb3\u65af\u7684\u6bcd\u4eb2\u53ef\u4e0d\u662f\u73ca\u5609\uff0c\u662f\u963f\u62c9\u838e\u738b\u540e\u2026\u2026\u7b49\u7b49\uff0c\u963f\u62c9\u838e\u738b\u540e\uff1f</p><p>\u963f\u62c9\u838e\u738b\u540e\u4e0d\u5c31\u662f\u88ab\u8c0b\u6740\u7684\u4e48\uff1f</p><p>\u8fd9\u5df2\u7ecf\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u743c\u6069\u77e5\u9053\u5f97\u4e0d\u591a\uff0c\u4f46\u57fa\u672c\u7684\u4f20\u95fb\u603b\u4e5f\u662f\u542c\u8fc7\u7684\u3002\u963f\u62c9\u838e\u738b\u540e\u662f\u9634\u9b42\u57ce\u4e3b\u590f\u591a\u7684\u7231\u4fa3\uff0c\u6b7b\u4e8e\u8c0b\u6740\uff0c\u79bb\u5947\u7684\u662f\u51f6\u624b\u5374\u4e00\u76f4\u6ca1\u6709\u88ab\u6293\u5230\uff0c\u6210\u4e3a\u4e00\u6869\u60ac\u6848\u3002\u8fd9\u79cd\u4e8b\u60c5\u5982\u679c\u53d1\u751f\u5728\u7269\u8d28\u754c\u7684\u5176\u4ed6\u5730\u65b9\uff0c\u90a3\u5012\u4e5f\u5c31\u7f62\u4e86\uff0c\u4e16\u754c\u4e0a\u6740\u4eba\u6848\u4ef6\u591a\u4e86\u53bb\u4e86\uff0c\u4e0d\u80fd\u7834\u83b7\u7684\u4e5f\u591a\u4e86\u53bb\u4e86\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u53d1\u751f\u5728\u9634\u9b42\u57ce\u91cc\uff0c\u90a3\u5c31\u5f88\u4e0d\u7b80\u5355\u4e86\uff0c\u66f4\u4f55\u51b5\u88ab\u8c0b\u6740\u8005\u662f\u9634\u9b42\u57ce\u4e3b\u7684\u738b\u540e\u3002</p><p>\u201c\u8fd9\u53ef\u662f\u6709\u9b54\u6cd5\u7684\u5947\u5e7b\u4e16\u754c\u554a\uff0c\u590f\u591a\u662f\u8010\u745f\u65f6\u4ee3\u7855\u679c\u4ec5\u5b58\u7684\u5927\u5965\u672f\u5e08\uff0c\u5e03\u96f7\u7eb3\u65af\u66f4\u662f\u9876\u7ea7\u7684\u9884\u8a00\u5e08\uff0c\u963f\u62c9\u838e\u738b\u540e\u5c31\u5728\u57ce\u91cc\u88ab\u8c0b\u6740\uff0c\u4ed6\u4eec\u5c45\u7136\u8fde\u51f6\u624b\u662f\u8c01\u90fd\u627e\u4e0d\u51fa\u6765\uff1f\u8fd9\u79cd\u4e8b\u60c5\u600e\u4e48\u53ef\u80fd\u4f1a\u53d1\u751f\u3002\u201d</p><p>\u743c\u6069\u4ee5\u524d\u4ece\u6ca1\u5173\u5fc3\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u5982\u4eca\u7a0d\u7a0d\u4e00\u60f3\uff0c\u4fbf\u610f\u8bc6\u5230\u8fd9\u4ef6\u4e8b\u5176\u4e2d\u6709\u7edd\u5927\u7684\u7834\u7efd\u2014\u2014\u6216\u8005\u8bf4\u662f\u7591\u56e2\u3002\u4ed6\u77e5\u9053\u7684\u4fe1\u606f\u592a\u5c11\uff0c\u4e5f\u65e0\u4ece\u53bb\u5206\u6790\u63a8\u65ad\uff0c\u4f46\u5374\u4e0d\u7b49\u4e8e\u4e0d\u80fd\u731c\u6d4b\uff0c\u6216\u8005\u8bf4\u80e1\u601d\u4e71\u60f3\u3002</p><p>\u201c\u5047\u8bbe\u6211\u68a6\u89c1\u7684\u90a3\u540d\u7537\u5b50\u771f\u662f\u5e03\u96f7\u7eb3\u65af\uff0c\u5e03\u96f7\u7eb3\u65af\u7684\u6bcd\u4eb2\u662f\u963f\u62c9\u838e\u738b\u540e\u2026\u2026\u90a3\u5c82\u4e0d\u662f\u8bf4\u5e03\u96f7\u7eb3\u65af\u5f11\u6bcd\uff1f\u4ed6\u672c\u4eba\u662f\u9634\u9b42\u57ce\u91cc\u6700\u9ad8\u660e\u7684\u9884\u8a00\u5e08\uff0c\u5018\u82e5\u771f\u662f\u4ed6\u4e0b\u624b\uff0c\u4e8b\u540e\u8981\u63a9\u76d6\u771f\u76f8\uff0c\u5012\u4e5f\u5e76\u4e0d\u662f\u4e0d\u53ef\u80fd\u2014\u2014\u5e72\uff0c\u4e0d\u4f1a\u662f\u771f\u7684\u5427\u3002\u96be\u4e0d\u6210\u6211\u68a6\u89c1\u7684\uff0c\u4e0d\u662f\u4ec0\u4e48\u672a\u6765\u9884\u5146\uff0c\u800c\u662f\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5f80\u4e8b\uff1f\u201d</p><p>\u53ef\u662f\uff0c\u53ef\u662f\u4e3a\u4ec0\u4e48\u5728\u68a6\u91cc\uff0c\u81ea\u5df1\u4f1a\u628a\u963f\u62c9\u838e\u738b\u540e\u8bef\u8ba4\u4e3a\u662f\u59d0\u59d0\uff1f</p><p>\u7535\u5149\u706b\u77f3\u4e4b\u95f4\uff0c\u743c\u6069\u7684\u8111\u4e2d\u5df2\u7ecf\u7ffb\u6765\u8986\u53bb\u95ea\u8fc7\u65e0\u6570\u5ff5\u5934\uff0c\u4f46\u5374\u4ec0\u4e48\u90fd\u60f3\u4e0d\u6e05\u695a\uff0c\u53cd\u800c\u8d8a\u6765\u8d8a\u8ff7\u7cca\uff0c\u751a\u81f3\u8fde\u5934\u90fd\u5f00\u59cb\u9690\u9690\u4f5c\u75bc\u8d77\u6765\u3002\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u7a81\u7136\u505a\u90a3\u79cd\u5947\u602a\u7684\u68a6\uff1f\u68a6\u91cc\u7684\u573a\u666f\u7a76\u7adf\u662f\u9884\u5146\u8fd8\u662f\u771f\u5b9e\uff1f\u68a6\u91cc\u770b\u89c1\u7684\u7537\u5b50\u771f\u662f\u5e03\u96f7\u7eb3\u65af\uff1f\u5e03\u96f7\u7eb3\u65af\u53c8\u6709\u4ec0\u4e48\u7406\u7531\u8981\u53bb\u8c0b\u6740\u81ea\u5df1\u7684\u4eb2\u751f\u6bcd\u4eb2\uff1f</p><p>\u6700\u6700\u5173\u952e\u7684\u4e00\u70b9\u662f\uff1a\u8fd9\u548c\u73ca\u5609\u53c8\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f</p><p>\u4e00\u9635\u4e00\u9635\u7684\u5267\u75db\u4eff\u4f5b\u6f6e\u6c34\u822c\u51b2\u51fb\u7740\u4ed6\u7684\u8111\u6d77\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u601d\u7eea\uff0c\u743c\u6069\u53ea\u89c9\u773c\u524d\u53d1\u9ed1\uff0c\u4e00\u4e2a\u8e09\u8dc4\u7ad9\u7acb\u4e0d\u7a33\uff0c\u8dcc\u5750\u5728\u5e8a\u8fb9\u3002\u73ca\u5609\u5413\u574f\u4e86\uff0c\u4e0d\u987e\u81ea\u5df1\u8eab\u4f53\u4e0d\u9002\uff0c\u614c\u5fd9\u5c06\u4ed6\u62b1\u4f4f\uff0c\u201c\u5c0f\u5f1f\uff01\u5c0f\u5f1f\uff01\u201d\u5979\u7126\u6025\u547c\u5524\u7740\uff0c\u9669\u4e9b\u8fde\u773c\u6cea\u90fd\u6d41\u4e0b\u6765\uff0c\u201c\u4f60\u600e\u4e48\u4e86\uff0c\u522b\u5413\u552c\u59d0\u59d0\uff01\u201d</p><p>\u201c\u2026\u2026\u6211\u2026\u2026\u6211\u6ca1\u4e8b\uff0c\u201d\u8fc7\u4e86\u51e0\u79d2\u949f\uff0c\u743c\u6069\u6162\u6162\u56de\u8fc7\u795e\u6765\uff0c\u201c\u6ca1\u4e8b\u4e86\uff0c\u59d0\u59d0\uff0c\u521a\u624d\u4e0d\u77e5\u9053\u600e\u4e48\u641e\u7684\uff0c\u7a81\u7136\u5934\u6655\uff0c\u201d\u4ed6\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u6276\u7740\u5e8a\u6cbf\u7ad9\u8d77\uff0c\u201c\u5927\u6982\u6628\u665a\u6ca1\u7761\u597d\u5427\u3002\u201d</p><p>\u73ca\u5609\u5c06\u4fe1\u5c06\u7591\uff0c\u89c1\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u5168\u65e0\u534a\u70b9\u8840\u8272\uff0c\u4e5f\u4e0d\u6562\u518d\u591a\u95ee\uff0c\u52c9\u529b\u5c06\u4ed6\u6276\u8d77\u3002\u201c\u4eca\u5929\u522b\u53bb\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8bf7\u4e2a\u5047\u5728\u5bb6\u4f11\u606f\u5427\u3002\u201d</p><p>\u201c\u6211\u6ca1\u4e8b\u4e86\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u4f11\u606f\u7740\u7247\u523b\uff0c\u8111\u4e2d\u5267\u75db\u5df2\u7ecf\u6563\u53bb\uff0c\u6e10\u6e10\u6062\u590d\u4e86\u51e0\u5206\u7cbe\u795e\uff0c\u201c\u6ca1\u7761\u597d\uff0c\u6709\u70b9\u5934\u6655\u800c\u5df2\u3002\u59d0\u59d0\u5728\u5bb6\u4f11\u606f\uff0c\u6211\u53bb\u4e00\u8d9f\u5c31\u56de\u6765\u2014\u2014\u5bf9\u4e86\uff0c\u201d\u4ed6\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u59d0\u59d0\u4e0d\u65b9\u4fbf\u8d70\u52a8\uff0c\u4e00\u4e2a\u4eba\u5728\u623f\u95f4\u91cc\u95f7\u5f97\u5f88\uff0c\u8981\u4e0d\u8981\u6211\u8bf7\u8299\u857e\u72c4\u6216\u8005\u8299\u8389\u5a05\u6765\u966a\u4f60\uff1f\u201d</p><p>\u201c\u4e0d\u7528\uff0c\u522b\u9ebb\u70e6\u4eba\u5bb6\u4e86\uff0c\u201d\u73ca\u5609\u60f3\u4e86\u60f3\uff0c\u201c\u8ba9\u838e\u73de\u514b\u966a\u6211\u8bf4\u8bf4\u8bdd\u5c31\u597d\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u6709\u4e9b\u8be7\u5f02\uff0c\u73ca\u5609\u4e0d\u9009\u5173\u7cfb\u4eb2\u8fd1\u7684\u83ab\u5c3c\u5361\u59d0\u59b9\uff0c\u53cd\u800c\u9009\u8ba4\u8bc6\u6ca1\u591a\u4e45\u7684\u838e\u73de\u514b\u2026\u2026\u7b97\u4e86\uff0c\u838e\u73de\u514b\u673a\u654f\u4f36\u4fd0\uff0c\u9605\u5386\u4e30\u5bcc\uff0c\u4f5c\u804a\u5929\u5bf9\u8c61\u4e5f\u786e\u5b9e\u633a\u5408\u9002\u3002\u53cd\u6b63\u73ca\u5609\u53c8\u4e0d\u662f\u5355\u7eaf\u5c11\u5973\uff0c\u4e0d\u6015\u5979\u88ab\u5e26\u574f\u4e86\u3002</p><p>\u4ed6\u8d77\u8eab\u51fa\u95e8\uff0c\u548c\u838e\u73de\u514b\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u8ba9\u5979\u5e2e\u5fd9\u7167\u987e\u73ca\u5609\uff0c\u81ea\u5df1\u52a8\u8eab\u524d\u5f80\u738b\u5bab\u3002</p><p>\u203b\u203b\u203b</p><p>\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u91cd\u8981\u7684\u4e8b\u60c5\u627e\u4ed6\uff0c\u65e0\u975e\u5c31\u662f\u8c08\u8c08\u5e7d\u6697\u5730\u57df\u7684\u7ecf\u5386\uff0c\u8bf4\u8bf4\u5353\u5c14\u548c\u77ee\u4eba\u7684\u60c5\u51b5\uff0c\u4ee5\u53ca\u5728\u6df1\u6e0a\u4e2d\u7684\u906d\u9047\u3002\u8fd9\u4e9b\u743c\u6069\u6628\u65e5\u5c31\u5df2\u7ecf\u5bf9\u519b\u4e8b\u59d4\u5458\u4f1a\u90fd\u8bf4\u8fc7\uff0c\u4ee5\u5e03\u96f7\u7eb3\u65af\u7684\u8eab\u4efd\uff0c\u80af\u5b9a\u5df2\u7ecf\u770b\u8fc7\u62a5\u544a\uff0c\u6240\u4ee5\u4e5f\u90fd\u662f\u8f7b\u63cf\u6de1\u5199\uff0c\u7c97\u7565\u626b\u8fc7\u3002\u738b\u5b50\u771f\u6b63\u611f\u5174\u8da3\u7684\uff0c\u53cd\u800c\u662f\u743c\u6069\u5728\u6cf0\u745f\u5c14\u9047\u89c1\u7684\u90a3\u4e00\u5bf9\u9752\u5e74\u7537\u5973\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u3002</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u90a3\u4f4d\u53eb\u505a\u601d\u601d\u7684\u5973\u5deb\u5e08\uff0c\u4f53\u5185\u9690\u4f0f\u7740\u4e00\u4f4d\u5927\u5965\u672f\u5e08\uff1f\u201d</p><p>\u201c\u6211\u731c\u6d4b\u662f\u5982\u6b64\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u4e0d\u80fd\u80af\u5b9a\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u9ed8\u9ed8\u70b9\u5934\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u7136\u540e\u4e3b\u52a8\u8f6c\u79fb\u4e86\u8bdd\u9898\u3002\u743c\u6069\u5fc3\u4e2d\u6709\u4e8b\uff0c\u4e0d\u514d\u6709\u4e9b\u8d70\u795e\uff0c\u95f2\u8c08\u534a\u664c\uff0c\u627e\u4e2a\u673a\u4f1a\u8bf4\u8d77\u73ca\u5609\u7684\u4e8b\u60c5\uff0c\u611f\u8c22\u738b\u5b50\u8fd9\u6bb5\u65f6\u95f4\u6765\u7684\u7167\u987e\uff0c\u4ee5\u53ca\u8d60\u9001\u7684\u957f\u7b1b\u3002</p><p>\u201c\u76f8\u9022\u5076\u9047\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u7167\u987e\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u8bf4\uff0c\u76ee\u5149\u95ea\u52a8\uff0c\u4eff\u4f5b\u770b\u51fa\u743c\u6069\u7684\u5fc3\u601d\uff0c\u201c\u81f3\u4e8e\u90a3\u652f\u957f\u7b1b\uff0c\u5012\u4e5f\u4e0d\u5fc5\u8c22\u6211\u3002\u4e0e\u5176\u8bf4\u662f\u6211\u9001\u7ed9\u5979\uff0c\u8fd8\u4e0d\u5982\u8bf4\u662f\u9001\u7ed9\u6211\u81ea\u5df1\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u51e0\u4e4e\u662f\u4e00\u6a21\u4e00\u6837\u7684\u8bdd\uff0c\u73ca\u5609\u8fd9\u4e48\u8bf4\uff0c\u5e03\u96f7\u7eb3\u65af\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u743c\u6069\u5012\u8fd8\u5f53\u771f\u7cca\u6d82\u4e86\u3002\u5e03\u96f7\u7eb3\u65af\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u5766\u767d\u5730\u8bf4\uff0c\u6211\u4e4b\u6240\u4ee5\u4f1a\u8ba4\u8bc6\u4f60\u59d0\u59d0\uff0c\u5f88\u5927\u4e00\u4e2a\u539f\u56e0\uff0c\u662f\u5979\u50cf\u6211\u7684\u2026\u2026\u50cf\u6211\u7684\u4e00\u4f4d\u6545\u4eba\u3002\u201d</p><p>\u6545\u4eba\uff1f\u4e0d\u4f1a\u662f\u4f60\u8001\u5988\u5427\uff1f</p><p>\u743c\u6069\u5fc3\u4e2d\u731b\u5730\u8df3\u4e86\u4e00\u8df3\uff0c\u8868\u9762\u4e0a\u4f9d\u65e7\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u662f\u5417\uff1f\u201d\u4ed6\u53cd\u95ee\uff0c\u201c\u957f\u5f97\u5f88\u50cf\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u5012\u4e0d\u662f\u8bf4\u76f8\u8c8c\u50cf\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u89e3\u91ca\uff0c\u201c\u4e3b\u8981\u662f\u6c14\u8d28\uff0c\u5c24\u5176\u662f\u5439\u7b1b\u7684\u65f6\u5019\uff0c\u90a3\u79cd\u611f\u89c9\u771f\u7684\u5f88\u50cf\uff0c\u201d\u4ed6\u987f\u4e86\u987f\uff0c\u201c\u50cf\u6781\u4e86\u3002\u201d</p><p>\u4e8e\u662f\u743c\u6069\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\u3002</p><p>\u201c\u90a3\u771f\u662f\u592a\u5de7\u4e86\uff0c\u201d\u4ed6\u6545\u610f\u8bf4\uff0c\u201c\u6709\u673a\u4f1a\u5012\u8fd8\u771f\u60f3\u89c1\u89c1\u3002\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u7b11\u8d77\u6765\uff0c\u201c\u90a3\u6050\u6015\u4e0d\u884c\uff0c\u5979\u5df2\u7ecf\u53bb\u4e16\u5f88\u591a\u5e74\u4e86\u3002\u201d</p><p>\u201c\u6211\u5f88\u62b1\u6b49\u3002\u201d\u743c\u6069\u8fde\u5fd9\u8bf4\u3002</p><p>\u201c\u6ca1\u4e8b\u3002\u201d</p><p>\u201c\u60a8\u9001\u7ed9\u6211\u59d0\u59d0\u7684\u90a3\u652f\u957f\u7b1b\uff0c\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u743c\u6069\u53c8\u8bd5\u63a2\u5730\u95ee\uff0c\u201c\u662f\u5979\u7684\u9057\u7269\uff1f\u201d</p><p>\u5e03\u96f7\u7eb3\u65af\u6709\u4e9b\u5947\u602a\u5730\u770b\u4e86\u743c\u6069\u4e00\u773c\uff0c\u4e0d\u77e5\u9053\u4ed6\u600e\u4e48\u4f1a\u7a81\u7136\u8fd9\u4e48\u95ee\uff0c\u4f46\u8fd8\u662f\u6447\u4e86\u6447\u5934\u3002\u201c\u4e0d\u662f\uff0c\u8fd9\u662f\u6211\u81ea\u5df1\u505a\u7684\uff0c\u201d\u4ed6\u89e3\u91ca\uff0c\u201c\u6211\u90a3\u4f4d\u6545\u4eba\uff0c\u786e\u5b9e\u6709\u4e00\u652f\u7c7b\u4f3c\u7684\u957f\u7b1b\uff0c\u4f46\u5728\u5979\u53bb\u4e16\u7684\u65f6\u5019\u5931\u8e2a\u4e86\uff0c\u9001\u7ed9\u4f60\u59d0\u59d0\u7684\u8fd9\u652f\u662f\u6211\u81ea\u5df1\u4eff\u9020\u7684\u3002\u201d</p><p>\u63d0\u53ca\u6545\u4eba\uff0c\u5e03\u96f7\u7eb3\u65af\u4f3c\u4e4e\u53d8\u5f97\u6709\u4e9b\u610f\u5174\u9611\u73ca\uff0c\u743c\u6069\u89c1\u72b6\u4e5f\u4e0d\u6562\u518d\u591a\u95ee\uff0c\u6015\u4ed6\u770b\u51fa\u4ec0\u4e48\u7834\u7efd\u3002\u4ed6\u5fc3\u4e2d\u6b64\u65f6\u7eb7\u4e71\u5982\u9ebb\uff0c\u53ea\u60f3\u8d76\u5feb\u56de\u53bb\uff0c\u4e00\u4e2a\u4eba\u9759\u4e0b\u5fc3\u6765\u6574\u7406\u601d\u7eea\uff0c\u597d\u597d\u60f3\u60f3\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u6b63\u8981\u8d77\u8eab\u544a\u8f9e\uff0c\u5e03\u96f7\u7eb3\u65af\u7a81\u7136\u62ac\u4e86\u62ac\u624b\uff0c\u793a\u610f\u4ed6\u5750\u4e0b\uff0c\u201c\u5bf9\u4e86\uff0c\u5dee\u70b9\u5fd8\u4e86\u4ef6\u91cd\u8981\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u4f60\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u627e\u5230\u7684\u90a3\u5f20\u8010\u745f\u5377\u8f74\uff0c\u5965\u6c83\u5148\u751f\u5df2\u7ecf\u8f6c\u4ea4\u5230\u6211\u4eec\u624b\u4e0a\uff0c\u201d\u5e03\u96f7\u7eb3\u65af\u5fae\u7b11\uff0c\u201c\u8fd9\u662f\u5927\u529f\u4e00\u4ef6\uff0c\u7406\u5f53\u5609\u5956\u3002\u4f60\u6709\u4ec0\u4e48\u60f3\u6cd5\uff0c\u4e0d\u59a8\u544a\u8bc9\u6211\uff0c\u6211\u4f1a\u5411\u4e0a\u53cd\u6620\uff0c\u5c3d\u91cf\u6ee1\u8db3\u3002\u201d</p><p>\u743c\u6069\u7a0d\u7a0d\u4e00\u6014\uff0c\u6ca1\u60f3\u5230\u8fd8\u6709\u8fd9\u4e00\u51fa\u3002\u4ed6\u5f53\u65f6\u628a\u5377\u8f74\u9001\u7ed9\u5965\u6c83\uff0c\u5fc3\u91cc\u53ea\u5f53\u662f\u5b5d\u656c\u8001\u5e08\u7684\u793c\u7269\uff0c\u8fd8\u771f\u6ca1\u60f3\u8fc7\u62a5\u916c\uff0c\u8fd9\u5012\u7b97\u662f\u5929\u4e0a\u6389\u4e0b\u7684\u610f\u5916\u9985\u997c\u4e86\u3002\u6b63\u8e0c\u8e87\u7740\uff0c\u4e0d\u77e5\u9053\u8be5\u63d0\u4ec0\u4e48\u8981\u6c42\u597d\uff0c\u7a81\u7136\u95f4\u60f3\u8d77\u4e00\u53e5\u8bdd\uff0c\u4e00\u53e5\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u4e0d\u8bb0\u5f97\u4ece\u54ea\u672c\u4e66\u4e0a\u770b\u5230\u7684\u8bdd\u3002</p><p>\u5170\u5c3c\u65af\u7279\u6709\u6069\u5fc5\u507f\u3001\u6709\u4ec7\u5fc5\u62a5\uff01</p><p>\u201c\u6bbf\u4e0b\uff0c\u201d\u4ed6\u90d1\u91cd\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u201c\u6211\u60f3\u5411\u60a8\u8bf7\u6559\u4e00\u4ef6\u5341\u5e74\u524d\u7684\u5f80\u4e8b\u3002\u201d</p></article>"}}}]);
