(window.webpackJsonp=window.webpackJsonp||[]).push([[434],{456:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u56db\u8282 \u51b3\u751f\u6b7b",content:"<article><h2>\u7b2c\u4e8c\u5341\u56db\u8282 \u51b3\u751f\u6b7b</h2><p>\u5982\u679c\u8bf4\u743c\u6069\u6700\u6015\u9047\u5230\u4ec0\u4e48\u6837\u7684\u5bf9\u624b\uff0c\u7b54\u6848\u5f88\u7b80\u5355\uff0c\u4e0d\u662f\u5a01\u540d\u5353\u8457\u7684\u9009\u6c11\uff0c\u4e0d\u662f\u90aa\u6c14\u51db\u7136\u7684\u5deb\u5996\uff0c\u4e0d\u662f\u8be1\u8c32\u72e1\u8bc8\u7684\u9b54\u9b3c\uff0c\u751a\u81f3\u4e0d\u662f\u81f3\u9ad8\u65e0\u4e0a\u7684\u795e\u660e\u2014\u2014\u800c\u662f\u75af\u5b50\u3002</p><p>\u56e0\u4e3a\u75af\u5b50\u65e0\u53ef\u7406\u55bb\u3002</p><p>\u65e0\u8bba\u662f\u4e0a\u8f88\u5b50\u8fd8\u662f\u8fd9\u8f88\u5b50\uff0c\u65e0\u8bba\u662f\u505a\u5f8b\u5e08\u8fd8\u662f\u505a\u5deb\u5e08\uff0c\u743c\u6069\u90fd\u662f\u4e0d\u6298\u4e0d\u6263\u7684\u7406\u6027\u81f3\u4e0a\u4e3b\u4e49\u8005\u3002\u78b0\u5230\u4efb\u4f55\u95ee\u9898\uff0c\u906d\u9047\u4efb\u4f55\u9ebb\u70e6\uff0c\u4ed6\u90fd\u4e60\u60ef\u5c3d\u53ef\u80fd\u7528\u903b\u8f91\u548c\u667a\u6167\u6765\u89e3\u51b3\uff0c\u800c\u975e\u66b4\u529b\u2014\u2014\u6216\u8005\u8bf4\uff0c\u66b4\u529b\u662f\u653e\u5728\u6700\u540e\u624d\u8003\u8651\u7684\u624b\u6bb5\u3002\u5982\u679c\u80fd\u591f\u5408\u4f5c\uff0c\u90a3\u4e48\u5c31\u4e0d\u8981\u5bf9\u6297\uff1b\u5982\u679c\u80fd\u591f\u534f\u5546\uff0c\u90a3\u4e48\u5c31\u4e0d\u8981\u7ffb\u8138\uff1b\u8c08\u5224\u548c\u4ea4\u6613\u80fd\u591f\u89e3\u51b3\u7684\u95ee\u9898\uff0c\u90a3\u5c31\u7edd\u4e0d\u8981\u52a8\u7528\u62f3\u5934\uff0c\u4e5f\u4e0d\u8981\u6d6a\u8d39\u5492\u8bed\u3002</p><p>\u8fd9\u662f\u4e00\u79cd\u5f88\u4f18\u79c0\u7684\u7406\u5ff5\uff0c\u8ba9\u4ed6\u603b\u662f\u80fd\u591f\u5934\u8111\u51b7\u9759\uff0c\u601d\u7ef4\u660e\u6670\uff0c\u770b\u6e05\u5f62\u52bf\uff0c\u5ea6\u8fc7\u96be\u5173\u3002\u743c\u6069\u81ea\u4ece\u6bd5\u4e1a\u51fa\u9634\u9b42\u57ce\u4ee5\u6765\uff0c\u8fed\u906d\u9669\u5883\uff0c\u5c61\u9047\u5f3a\u654c\uff0c\u4e4b\u6240\u4ee5\u80fd\u591f\u4e00\u8def\u6709\u60ca\u65e0\u9669\uff0c\u9022\u51f6\u5316\u5409\uff0c\u751a\u81f3\u73b0\u5728\u8fd8\u7b97\u6df7\u5f97\u5c0f\u6709\u8d77\u8272\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5f52\u529f\u4e8e\u6b64\u3002\u7136\u800c\u8fd9\u79cd\u7406\u5ff5\uff0c\u6216\u8005\u8bf4\u5904\u4e8b\u65b9\u6cd5\uff0c\u6709\u4e00\u4e2a\u8981\u547d\u7684\u7f3a\u9677\uff0c\u5c31\u662f\u5b83\u5fc5\u987b\u5efa\u7acb\u5728\u201c\u5927\u5bb6\u90fd\u6709\u7406\u6027\uff0c\u81f3\u5c11\u6709\u57fa\u7840\u7684\u7406\u6027\u201d\u8fd9\u4e2a\u524d\u63d0\u4e0a\uff0c\u4e00\u65e6\u9047\u4e0a\u5b8c\u5168\u4e0d\u6309\u5e38\u7406\u51fa\u724c\uff0c\u65e0\u6cd5\u6c9f\u901a\u65e0\u6cd5\u4ea4\u6d41\u7684\u654c\u4eba\uff0c\u90a3\u5c31\u96be\u4ee5\u5e94\u4ed8\u4e86\u3002</p><p>\u73b0\u5728\u4ed6\u5c31\u9047\u4e0a\u4e86\u4e00\u4e2a\u3002</p><p>\u5e26\u7740\u94c1\u9762\u5177\u7684\u7537\u4eba\u2014\u2014\u7b80\u79f0\u94c1\u9762\u4eba\u2014\u2014\u5b8c\u5168\u6ca1\u6709\u534a\u70b9\u6253\u7b97\u534f\u5546\u4ea4\u6d89\u7684\u610f\u601d\uff0c\u5bf9\u743c\u6069\u7684\u8be2\u95ee\u4e5f\u5145\u8033\u4e0d\u95fb\uff0c\u4ed6\u53f3\u624b\u4e2d\u7684\u5c0f\u523b\u5200\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u6254\u6389\uff0c\u53cc\u624b\u63e1\u7740\u9752\u94dc\u6218\u65a7\u7684\u957f\u67c4\uff0c\u4eff\u4f5b\u4e00\u4e2a\u72c2\u6218\u58eb\u822c\u5486\u54ee\u6012\u543c\u7740\u76f4\u51b2\u8fc7\u6765\uff0c\u770b\u90a3\u67b6\u52bf\u5206\u660e\u5c31\u662f\u8981\u628a\u743c\u6069\u61d2\u8170\u5288\u6210\u4e24\u622a\u3002</p><p>\u8fd9\u79cd\u65f6\u5019\uff0c\u4efb\u4f55\u7cbe\u786e\u7684\u7406\u667a\u548c\u534e\u4e3d\u7684\u53e3\u624d\u90fd\u5df2\u7ecf\u4e27\u5931\u4f5c\u7528\uff0c\u80fd\u591f\u5bf9\u6297\u66b4\u529b\u7684\uff0c\u4f9d\u65e7\u53ea\u80fd\u662f\u66b4\u529b\u3002</p><p>\u743c\u6069\u6cd5\u6756\u4e00\u6307\uff0c\u671d\u94c1\u9762\u4eba\u53d1\u51fa\u4e00\u9053\u53d8\u5f62\u672f\uff0c\u9b54\u6cd5\u51c6\u786e\u51fb\u4e2d\u4e86\u76ee\u6807\u2014\u2014\u7136\u540e\u5f84\u76f4\u53cd\u5f39\u4e86\u56de\u6765\u3002\u743c\u6069\u731d\u4e0d\u53ca\u9632\uff0c\u767b\u65f6\u88ab\u81ea\u5df1\u7684\u53d8\u5f62\u672f\u7b3c\u7f69\uff0c\u5e78\u597d\u4ed6\u662f\u53d8\u5316\u5e08\uff0c\u5bf9\u672c\u4e13\u4e1a\u7684\u6cd5\u672f\u6297\u6027\u5f88\u5f3a\uff0c\u53ea\u662f\u8e09\u8dc4\u9000\u4e86\u534a\u6b65\u5373\u4fbf\u6ca1\u4e8b\u3002</p><p>\u201c\u4ed6\u4e5f\u6709\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff1f\u8fd8\u662f\u5176\u4ed6\u9632\u5fa1\u6cd5\u672f\uff1f\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e0d\u6562\u518d\u5ea6\u65bd\u6cd5\u3002\u6b64\u65f6\u94c1\u9762\u4eba\u5df2\u7ecf\u8feb\u8fd1\uff0c\u743c\u6069\u5de6\u624b\u4e00\u6325\uff0c\u4e09\u70b9\u9ed1\u661f\u4ece\u638c\u5fc3\u98de\u51fa\uff0c\u5728\u843d\u5230\u5730\u4e0a\u7684\u77ac\u95f4\u5316\u4f5c\u4e09\u4e2a\u9ad8\u5927\u7c97\u58ee\u7684\u98df\u4eba\u9b54\u9b54\u50cf\uff0c\u7360\u7259\u5c16\u89d2\uff0c\u5168\u8eab\u4e4c\u5149\u95ea\u95ea\uff0c\u624b\u4e2d\u63e1\u7740\u72fc\u7259\u5927\u68d2\uff0c\u4e00\u53ea\u5728\u524d\uff0c\u4e24\u53ea\u5728\u540e\uff0c\u5448\u54c1\u5b57\u5f62\u5411\u731b\u51b2\u800c\u6765\u7684\u94c1\u9762\u4eba\u8fce\u4e0a\u53bb\u3002</p><p>\u8f6c\u77ac\u4e4b\u95f4\uff0c\u6700\u524d\u9762\u7684\u98df\u4eba\u9b54\u9b54\u50cf\u5df2\u7ecf\u548c\u5bf9\u624b\u649e\u4e0a\u3002\u94c1\u9762\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u4eff\u4f5b\u6674\u7a7a\u91cc\u9661\u7136\u6253\u4e86\u4e2a\u9739\u96f3\u4e00\u822c\uff0c\u6218\u65a7\u6a2a\u6325\uff0c\u6b63\u5288\u5728\u9b54\u50cf\u7684\u8170\u95f4\u3002\u743c\u6069\u7684\u8fd9\u5e45\u8428\u74e6\u68cb\u9b54\u50cf\u5206\u7ea2\u9ed1\u4e24\u79cd\uff0c\u5206\u522b\u662f\u7528\u7ea2\u7389\u9ad3\u548c\u9ed1\u66dc\u77f3\u5236\u6210\uff0c\u6750\u8d28\u4e5f\u7b97\u9887\u4e3a\u575a\u786c\uff0c\u4f46\u6bd4\u8d77\u91d1\u94c1\u7ec8\u7a76\u8fd8\u662f\u4e0d\u5982\uff0c\u6297\u4e0d\u4f4f\u6218\u65a7\u7684\u950b\u9510\u3002\u53ea\u542c\u5f97\u7830\u7136\u5de8\u54cd\uff0c\u788e\u77f3\u6251\u9762\u98de\u6e85\u4e2d\uff0c\u94c1\u9762\u4eba\u7684\u6218\u65a7\u6df1\u6df1\u5d4c\u5165\u4e86\u9b54\u50cf\u7684\u8179\u90e8\uff0c\u51e0\u4e4e\u5c06\u5b83\u62e6\u8170\u65a9\u6210\u4e24\u65ad\u3002</p><p>\u5018\u82e5\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u5b83\u65e9\u5df2\u5f53\u573a\u6bd9\u547d\u3002\u8428\u74e6\u68cb\u9b54\u50cf\u662f\u4f0a\u739b\u65af\u5361\u5947\u68b0\u5e08\u7684\u5f97\u610f\u4f5c\u54c1\uff0c\u6709\u81ea\u6211\u4fee\u590d\u7684\u80fd\u529b\u2014\u2014\u4f46\u53d7\u4e86\u5982\u6b64\u91cd\u521b\u662f\u5426\u8fd8\u80fd\u4fee\u590d\uff0c\u743c\u6069\u5fc3\u91cc\u4e5f\u662f\u534a\u70b9\u6ca1\u5e95\u3002\u53ea\u662f\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u8003\u8651\u8fd9\u4e9b\u4e8b\u60c5\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u65e0\u58f0\u5730\u53d1\u51fa\u6307\u4ee4\u3002\u8fd9\u53ea\u88ab\u780d\u4e2d\u7684\u9b54\u50cf\u77f3\u773c\u4e2d\u5fae\u5149\u4e00\u70c1\uff0c\u731b\u7136\u4e22\u4e0b\u4e86\u624b\u4e2d\u7684\u72fc\u7259\u68d2\uff0c\u5de8\u7075\u53cc\u624b\u5341\u6307\u7b95\u5f20\uff0c\u671d\u7740\u6218\u65a7\u6293\u53bb\u3002</p><p>\u94c1\u9762\u4eba\u4e00\u65a7\u780d\u4e2d\uff0c\u6b63\u5f85\u6536\u56de\u518d\u780d\uff0c\u6218\u65a7\u5374\u6df1\u9677\u5728\u9b54\u50cf\u4f53\u5185\uff0c\u4e00\u4e0b\u5b50\u6ca1\u6709\u62d4\u51fa\uff0c\u88ab\u8fd9\u4e00\u803d\u6401\uff0c\u9b54\u50cf\u5df2\u7ecf\u6b7b\u6b7b\u5730\u63e1\u4f4f\u4e86\u6218\u65a7\u7684\u65a7\u67c4\u3002\u94c1\u9762\u4eba\u867d\u7136\u51f6\u608d\uff0c\u529b\u6c14\u7ec8\u7a76\u6bd4\u4e0d\u5f97\u9b54\u50cf\uff0c\u767b\u65f6\u4fbf\u593a\u4e0d\u56de\u6765\u3002\u4ed6\u66b4\u8e81\u5730\u95f7\u543c\u4e86\u4e00\u58f0\uff0c\u53f3\u624b\u62ac\u8d77\uff0c\u638c\u5fc3\u4e2d\u9634\u5f71\u53d8\u5e7b\uff0c\u51dd\u805a\u6210\u838e\u5c14\u5723\u5fbd\u7684\u5f62\u6001\uff0c\u968f\u5373\u4e00\u638c\u62cd\u5728\u9b54\u50cf\u7684\u80f8\u53e3\u3002</p><p>\u4ed6\u8fd9\u4e00\u62cd\u529b\u9053\u5e76\u4e0d\u91cd\uff0c\u4f46\u4ee5\u624b\u638c\u4e3a\u4e2d\u5fc3\uff0c\u9634\u5f71\u5982\u6f6e\u6c34\u822c\u77ac\u95f4\u6269\u6563\u8513\u5ef6\uff0c\u8fd1\u5343\u78c5\u91cd\u7684\u5e9e\u5927\u9b54\u50cf\u6328\u4e86\u8fd9\u8f7b\u98d8\u98d8\u4e00\u51fb\uff0c\u4eff\u4f5b\u88ab\u4e07\u5428\u5de8\u9524\u7838\u4e2d\u822c\uff0c\u7830\u5730\u4e00\u58f0\u4fbf\u76f4\u76f4\u5730\u5f80\u540e\u6454\u98de\u51fa\u53bb\u3002\u53ea\u662f\u6218\u65a7\u4f9d\u65e7\u6df1\u5d4c\u5728\u9b54\u50cf\u4f53\u5185\uff0c\u65a7\u67c4\u53c8\u88ab\u5b83\u6b7b\u6b7b\u6263\u4f4f\uff0c\u51e0\u4e4e\u5df2\u7ecf\u8fde\u4e3a\u4e00\u4f53\uff0c\u94c1\u9762\u4eba\u4e5f\u63e1\u4e0d\u4f4f\uff0c\u53ea\u80fd\u4efb\u5b83\u8131\u624b\u98de\u51fa\u3002</p><p>\u6c89\u91cd\u7684\u811a\u6b65\u58f0\u903c\u8fd1\uff0c\u53e6\u5916\u4e24\u5c0a\u98df\u4eba\u9b54\u9b54\u50cf\u5df2\u7ecf\u5728\u743c\u6069\u7684\u6307\u4ee4\u4e0b\u6254\u6389\u72fc\u7259\u68d2\uff0c\u4e00\u5de6\u4e00\u53f3\u5939\u51fb\u4e0a\u6765\u3002\u5b83\u4eec\u6446\u51fa\u76f8\u6251\u4f3c\u7684\u59ff\u52bf\uff0c\u56db\u53ea\u80f3\u818a\u9ad8\u4e3e\uff0c\u4e24\u9762\u5408\u56f4\uff0c\u663e\u7136\u662f\u8981\u51ed\u501f\u5e9e\u5927\u7684\u4f53\u578b\u548c\u529b\u91cf\u5c06\u94c1\u9762\u4eba\u64d2\u62b1\u4f4f\uff0c\u4ee4\u4ed6\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u773c\u770b\u4e24\u5c0a\u5e9e\u7136\u5927\u7269\u4eff\u4f5b\u94c1\u58c1\u822c\u6324\u538b\u800c\u6765\uff0c\u94c1\u9762\u4eba\u4e0d\u95ea\u4e0d\u907f\uff0c\u7ad9\u7acb\u539f\u5730\u3002\u65e2\u7136\u4ed6\u5982\u6b64\u914d\u5408\uff0c\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u5ba2\u6c14\uff0c\u5728\u4ed6\u7684\u795e\u8bc6\u6307\u6325\u4e0b\uff0c\u4e24\u5c0a\u9b54\u50cf\u7684\u56db\u53ea\u5de8\u638c\u5206\u6beb\u4e0d\u5dee\u5730\u6263\u4f4f\u4e86\u76ee\u6807\u7684\u53cc\u81c2\u548c\u53cc\u80a9\uff0c\u4ee4\u4ed6\u52a8\u5f39\u4e0d\u5f97\u3002\u89c1\u6b64\u60c5\u5f62\uff0c\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u9601\u4e0b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u5e76\u65e0\u6076\u610f\u2026\u2026\u201d</p><p>\u201c\u55e4\uff01\u201d</p><p>\u4e00\u8bed\u672a\u6bd5\uff0c\u5c16\u9510\u523a\u8033\u7684\u58f0\u97f3\u5207\u5272\u5927\u6c14\uff0c\u770b\u8d77\u6765\u5df2\u7ecf\u88ab\u5b8c\u5168\u5236\u4f4f\u7684\u94c1\u9762\u4eba\u957f\u888d\u9661\u7136\u95f4\u50cf\u662f\u9f13\u6c14\u822c\u81a8\u80c0\u8d77\u6765\uff0c\u4e24\u5708\u65e0\u5f62\u7684\u5200\u5203\u4ece\u4ed6\u4f53\u5185\u5f39\u51fa\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u9ad8\u901f\u6025\u8f6c\u7740\u5f62\u6210\u6b7b\u4ea1\u6f29\u6da1\uff0c\u649e\u4e0a\u4e86\u98df\u4eba\u9b54\u9b54\u50cf\u3002\u77ed\u77ed\u4e24\u79d2\u949f\uff0c\u5728\u743c\u6069\u8fd8\u6ca1\u6709\u5b8c\u5168\u53cd\u5e94\u8fc7\u6765\u4e4b\u524d\uff0c\u4e24\u5c0a\u9b54\u50cf\u5df2\u7ecf\u88ab\u5207\u6210\u4e86\u4e0d\u89c4\u5219\u7684\u6570\u5341\u5757\uff0c\u6563\u843d\u5728\u5730\u3002</p><p>\u8fd9\u4e24\u679a\u9b54\u50cf\u68cb\u5b50\u6bc1\u4e86\u3002</p><p>\u5018\u82e5\u8bf4\u88ab\u62e6\u8170\u65a9\u65ad\u8fd8\u6709\u4fee\u590d\u7684\u53ef\u80fd\uff0c\u50cf\u8fd9\u6837\u88ab\u5927\u5378\u516b\u5757\uff0c\u51c6\u786e\u5730\u8bf4\u662f\u5927\u5378\u51e0\u5341\u5757\uff0c\u4efb\u662f\u8c01\u4e5f\u6e05\u695a\u77e5\u9053\u5b83\u4eec\u5f7b\u5e95\u5b8c\u86cb\u4e86\u3002\u5931\u53bb\u4e86\u8fd9\u6837\u4f18\u79c0\u7684\u5b9d\u7269\uff0c\u743c\u6069\u81ea\u7136\u5fc3\u75bc\uff0c\u7136\u800c\u73b0\u5728\u4ed6\u538b\u6839\u6ca1\u6709\u60cb\u60dc\u7684\u4f59\u6687\uff0c\u56e0\u4e3a\u5c31\u5728\u9b54\u50cf\u88ab\u5251\u5203\u58c1\u969c\u5f7b\u5e95\u6467\u6bc1\u7684\u540c\u65f6\uff0c\u94c1\u9762\u4eba\u4e5f\u6084\u65e0\u58f0\u606f\u5730\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002</p><p>\u4ed6\u662f\u9003\u8d70\u4e86\uff0c\u8fd8\u662f\u8981\u53cd\u51fb\uff1f</p><p>\u8fd9\u4e2a\u5ff5\u5934\u521a\u521a\u5728\u743c\u6069\u8111\u6d77\u4e2d\u6d6e\u73b0\uff0c\u5c31\u542c\u5f97\u80cc\u540e\u8299\u857e\u72c4\u60ca\u53eb\u4e00\u58f0\u3002\u4ed6\u5306\u5fd9\u8f6c\u8eab\uff0c\u4fbf\u89c1\u90a3\u4e2a\u5bb6\u4f19\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u7ad9\u5728\u4e86\u81ea\u5df1\u80cc\u540e\uff0c\u4e24\u4eba\u76f8\u8ddd\u4e0d\u5230\u4e00\u5c3a\u2014\u2014\u6362\u53e5\u8bdd\u8bf4\uff0c\u51e0\u4e4e\u662f\u8138\u8d34\u7740\u8138\u3002\u8ddd\u79bb\u5982\u6b64\u4e4b\u8fd1\uff0c\u743c\u6069\u53ef\u4ee5\u6e05\u695a\u5730\u770b\u89c1\u5bf9\u65b9\u94c1\u9762\u5177\u4e0a\u86db\u7f51\u822c\u5bc6\u5e03\u7740\u626d\u66f2\u7684\u82b1\u7eb9\uff0c\u6cdb\u7740\u9634\u68ee\u68ee\u7684\u51b7\u5149\uff0c\u800c\u5728\u8fd9\u4e9b\u82b1\u7eb9\u4e2d\u95f4\uff0c\u4e00\u53cc\u6ee1\u8574\u72c2\u70ed\u6740\u610f\u7684\u773c\u795e\u6b63\u903c\u89c6\u7740\u4ed6\uff0c\u4eff\u4f5b\u62e9\u4eba\u800c\u566c\u7684\u731b\u517d\u3002</p><p>\u4ed6\u600e\u4e48\u8fc7\u6765\u7684\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u4f20\u9001\uff1f</p><p>\u65e0\u6687\u601d\u8003\u8fd9\u4e9b\u95ee\u9898\uff0c\u743c\u6069\u672c\u80fd\u5730\u5f80\u540e\u9000\uff0c\u60f3\u8981\u62c9\u5f00\u8ddd\u79bb\uff0c\u7136\u800c\u94c1\u9762\u4eba\u5df2\u7ecf\u4e3e\u8d77\u53f3\u624b\uff0c\u638c\u5fc3\u4e2d\u9634\u5f71\u6c47\u805a\uff0c\u518d\u5ea6\u51dd\u6210\u4e86\u838e\u5c14\u5723\u5fbd\uff0c\u4ed6\u968f\u5373\u4e94\u6307\u5e76\u62e2\uff0c\u53d8\u638c\u4e3a\u62f3\uff0c\u547c\u5730\u4e00\u62f3\u671d\u743c\u6069\u80f8\u53e3\u76f4\u6363\u4e86\u8fc7\u6765\uff0c\u5176\u901f\u5feb\u82e5\u95ea\u7535\u3002\u743c\u6069\u8eb2\u95ea\u4e0d\u53ca\uff0c\u88ab\u4ed6\u4e00\u62f3\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u7838\u4e2d\uff0c\u900f\u660e\u7684\u9634\u5f71\u5728\u94c1\u9762\u4eba\u7684\u62f3\u5934\u4e0a\u7ffb\u817e\u8df3\u8dc3\u7740\uff0c\u4eff\u4f5b\u4e00\u56e2\u56e2\u7ec6\u5c0f\u7684\u706b\u7130\uff0c\u4e89\u5148\u6050\u540e\u5730\u6d8c\u5165\u76ee\u6807\u7684\u8eab\u4f53\u3002</p><p>\u201c\u6b7b\u5427\uff01\u201d\u94c1\u9762\u4eba\u6012\u559d\u3002</p><p>\u8fd9\u662f\u4f24\u5bb3\u672f\uff0c\u548c\u6cbb\u7597\u672f\u4e00\u6837\u90fd\u662f\u7267\u5e08\u7684\u6700\u57fa\u672c\u795e\u672f\uff0c\u4f46\u6027\u8d28\u5b8c\u5168\u9006\u53cd\u3002\u6cbb\u7597\u672f\u9a71\u52a8\u6b63\u80fd\u91cf\u6551\u4eba\uff0c\u4f24\u5bb3\u672f\u51dd\u805a\u8d1f\u80fd\u91cf\u6740\u4eba\u3002\u8d8a\u5f3a\u7684\u7267\u5e08\uff0c\u6cbb\u7597\u672f\u6548\u679c\u8d8a\u5f3a\uff0c\u51e0\u4e4e\u80fd\u591f\u751f\u6b7b\u4eba\u8089\u767d\u9aa8\uff0c\u540c\u6837\u4f24\u5bb3\u672f\u4e5f\u4f1a\u6548\u679c\u8d8a\u5f3a\uff0c\u53cd\u638c\u4e4b\u95f4\u5c31\u80fd\u53d6\u4eba\u6027\u547d\u3002\u94c1\u9762\u4eba\u8fd9\u4e00\u62f3\u7838\u4e0b\u6765\uff0c\u867d\u7136\u529b\u9053\u5176\u5b9e\u4e0d\u91cd\uff0c\u4f46\u8574\u542b\u7684\u8d1f\u80fd\u91cf\u96c4\u6d51\u65e0\u5339\uff0c\u8db3\u591f\u77ac\u95f4\u6bd9\u6740\u6700\u5f3a\u58ee\u7684\u5de8\u4eba\uff0c\u8981\u6740\u743c\u6069\u5e94\u8be5\u662f\u7ef0\u7ef0\u6709\u4f59\u3002</p><p>\u7136\u800c\u743c\u6069\u5b89\u7136\u65e0\u6059\u3002</p><p>\u6c79\u6d8c\u7684\u9634\u5f71\u706b\u7130\u6d8c\u5165\u8eab\u4f53\uff0c\u8086\u8650\u4fb5\u7565\u7740\uff0c\u8981\u70e7\u6bc1\u4ed6\u7684\u6bcf\u4e00\u70b9\u751f\u673a\u2014\u2014\u7136\u540e\u5b83\u4eec\u5c31\u5728\u5239\u90a3\u95f4\u5168\u90fd\u7184\u706d\u4e86\u3002\u56e0\u4e3a\u7184\u706d\u5f97\u5982\u6b64\u7a81\u7136\uff0c\u8fde\u743c\u6069\u81ea\u5df1\u90fd\u4e00\u65f6\u6ca1\u53cd\u5e94\u8fc7\u6765\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u53ea\u611f\u89c9\u90a3\u4e9b\u9634\u5f71\u706b\u7130\u5c31\u50cf\u662f\u65e0\u6570\u9053\u5954\u6d8c\u7684\u6fc0\u6d41\uff0c\u5374\u6ce8\u5165\u4e86\u6df1\u4e0d\u53ef\u6d4b\u7684\u6c6a\u6d0b\u5927\u6d77\uff0c\u77ac\u95f4\u5c31\u88ab\u878d\u5408\uff0c\u88ab\u541e\u566c\uff0c\u4e00\u5e72\u4e8c\u51c0\uff0c\u534a\u70b9\u75d5\u8ff9\u4e0d\u7559\u3002</p><p>\u94c1\u9762\u4eba\u4e5f\u6014\u4e86\u6014\uff0c\u663e\u7136\u8fd9\u7ed3\u679c\u5b8c\u5168\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\uff0c\u4f46\u4ed6\u53cd\u5e94\u6781\u5feb\uff0c\u53cd\u81c2\u6a2a\u626b\uff0c\u671d\u743c\u6069\u7684\u5934\u90e8\u6253\u8fc7\u6765\u3002\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u62ac\u624b\u4e00\u683c\uff0c\u5374\u6321\u4e86\u4e2a\u7a7a\uff0c\u94c1\u9762\u4eba\u5728\u4e24\u4eba\u80f3\u818a\u5373\u5c06\u76f8\u4ea4\u7684\u90a3\u4e00\u5239\u90a3\u9661\u7136\u8be1\u5f02\u5730\u626d\u66f2\uff0c\u4eff\u4f5b\u7ae0\u9c7c\u7684\u89e6\u624b\u4e00\u822c\uff0c\u4ee5\u5b8c\u5168\u4e0d\u53ef\u80fd\u7684\u89d2\u5ea6\u4ece\u743c\u6069\u7684\u9632\u5fa1\u4e2d\u7a7f\u900f\u8fc7\u6765\uff0c\u4e00\u628a\u6390\u4f4f\u4e86\u4ed6\u7684\u8116\u9888\u3002</p><p>\u201c\u4f60\u600e\u4e48\u4f1a\u6709\u5f71\u706b\uff01\u201d\u94c1\u9762\u4eba\u5486\u54ee\u7740\uff0c\u624b\u81c2\u4e0a\u808c\u8089\u9f13\u80c0\uff0c\u7528\u529b\u5f80\u4e0a\u4e00\u63d0\uff0c\u786c\u751f\u751f\u5c06\u743c\u6069\u6574\u4e2a\u4eba\u53cc\u811a\u79bb\u5730\u63d0\u4e86\u8d77\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u610f\u8bc6\u5230\u81ea\u5df1\u72af\u4e86\u4e2a\u9519\u8bef\u3002</p><p>\u8fd9\u91cc\u662f\u838e\u5c14\u795e\u6bbf\uff0c\u800c\u773c\u524d\u8fd9\u4e2a\u75af\u5b50\u822c\u7684\u94c1\u9762\u4eba\u6781\u53ef\u80fd\u5c31\u662f\u90a3\u4f4d\u201c\u591c\u89c6\u8005\u201d\uff0c\u6559\u4f1a\u7684\u7b2c\u4e8c\u53f7\u4eba\u7269\uff0c\u6240\u4ee5\u743c\u6069\u4e00\u76f4\u4e0d\u6562\u5f53\u771f\u4e0b\u6740\u624b\uff0c\u4ed6\u8fd8\u4e0d\u60f3\u548c\u6574\u4e2a\u6559\u4f1a\u751a\u81f3\u6574\u4e2a\u9634\u9b42\u57ce\u4e3a\u654c\u3002\u6b63\u662f\u51fa\u4e8e\u8fd9\u79cd\u8003\u8651\uff0c\u4ed6\u884c\u4e8b\u4fbf\u7559\u4e86\u4f59\u5730\uff0c\u4ece\u4e00\u5f00\u59cb\u4fbf\u6ca1\u6709\u4f7f\u7528\u5927\u5a01\u529b\u7684\u6740\u4f24\u9b54\u6cd5\uff0c\u4e5f\u6ca1\u6709\u547d\u4ee4\u9b54\u50cf\u76f4\u63a5\u653b\u51fb\uff0c\u800c\u662f\u6254\u6389\u6b66\u5668\uff0c\u53ea\u6253\u7b97\u5c06\u4ed6\u5236\u4f4f\u4fbf\u7f62\u3002</p><p>\u60f3\u6cd5\u5f88\u597d\uff0c\u7136\u800c\u4ed6\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff1a\u5982\u679c\u5bf9\u624b\u4e0d\u662f\u90a3\u4f4d\u591c\u89c6\u8005\uff0c\u53ea\u662f\u4e2a\u666e\u901a\u7267\u5e08\uff0c\u90a3\u4e48\u4ee5\u838e\u5c14\u5bf9\u743c\u6069\u6240\u8868\u73b0\u51fa\u6765\u7684\u770b\u91cd\u9752\u7750\uff0c\u7eb5\u7136\u6740\u4e86\u4e5f\u672a\u89c1\u5f97\u6709\u591a\u8981\u7d27\uff1b\u53cd\u8fc7\u6765\uff0c\u5982\u679c\u5bf9\u624b\u771f\u7684\u662f\u591c\u89c6\u8005\uff0c\u90a3\u4e48\u743c\u6069\u771f\u6b63\u5e94\u8be5\u8003\u8651\u7684\u4e0d\u662f\u5982\u4f55\u624b\u4e0b\u7559\u60c5\uff0c\u800c\u662f\u600e\u6837\u624d\u80fd\u4fdd\u4f4f\u81ea\u5df1\u7684\u6027\u547d\u3002</p><p>\u73b0\u5728\u4ed6\u7ec8\u4e8e\u660e\u767d\u4e86\u8fd9\u4e00\u70b9\u2014\u2014\u7136\u800c\u4f3c\u4e4e\u5df2\u7ecf\u592a\u665a\u4e86\u3002</p><p>\u88ab\u4eba\u6390\u7740\u8116\u5b50\u63d0\u5728\u7a7a\u4e2d\uff0c\u743c\u6069\u773c\u524d\u4e00\u9635\u9635\u5730\u53d1\u9ed1\uff0c\u610f\u8bc6\u9010\u6e10\u6da3\u6563\u6a21\u7cca\uff0c\u518d\u8fc7\u51e0\u79d2\u949f\u65f6\u95f4\u53ea\u6015\u5c31\u8981\u7a92\u606f\u800c\u6b7b\u4e86\u3002\u5e78\u597d\u4ed6\u5e76\u975e\u5b64\u7acb\u65e0\u63f4\uff0c\u65c1\u8fb9\u8fd8\u6709\u4e00\u4e2a\u8299\u857e\u72c4\uff0c\u5c0f\u5973\u5b69\u773c\u89c1\u743c\u6069\u9047\u9669\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c\u4e00\u628a\u6458\u4e0b\u5de6\u624b\u6234\u7740\u7684\u523b\u6709\u7fe0\u9e1f\u5bb6\u5fbd\u7684\u94f6\u6212\u6307\uff0c\u53d8\u5f62\u6210\u4e00\u67c4\u94f6\u5149\u95ea\u95ea\u7684\u77ed\u5251\uff0c\u671d\u7740\u94c1\u9762\u4eba\u76f4\u523a\u8fc7\u6765\u3002</p><p>\u5979\u662f\u5deb\u5e08\uff0c\u821e\u5200\u7528\u5251\u5e76\u975e\u6240\u957f\uff0c\u867d\u7136\u56e0\u4e3a\u51fa\u8eab\u8d35\u65cf\u7684\u7f18\u6545\uff0c\u4ece\u5c0f\u4e5f\u5b66\u4e60\u8fc7\u5251\u672f\uff0c\u4f46\u663e\u7136\u5e76\u4e0d\u5982\u4f55\u9ad8\u660e\u3002\u53ea\u662f\u5979\u4eca\u5929\u51fa\u95e8\u65f6\u538b\u6839\u5c31\u6ca1\u60f3\u8fc7\u8981\u548c\u4eba\u6253\u67b6\uff0c\u6cd5\u6756\u3001\u5deb\u5e08\u888d\u3001\u65bd\u6cd5\u6750\u6599\u4e00\u6837\u90fd\u6ca1\u5e26\uff0c\u5982\u4eca\u5c31\u662f\u60f3\u65bd\u6cd5\u4e5f\u4e0d\u53ef\u80fd\u3002\u5e78\u597d\u5979\u624b\u4e0a\u8fd8\u6234\u4e86\u80fd\u591f\u53d8\u5316\u77ed\u5251\u7684\u5bb6\u4f20\u6212\u6307\uff0c\u53ea\u80fd\u62ff\u6765\u5c06\u5c31\u7528\u4e86\u3002\u53ea\u662f\u5979\u7684\u5251\u672f\u59d4\u5b9e\u592a\u5dee\u52b2\uff0c\u94c1\u9762\u4eba\u53ea\u662f\u773c\u89d2\u4f59\u5149\u4e00\u77a5\uff0c\u5de6\u624b\u4e94\u6307\u5e76\u62e2\uff0c\u4e00\u638c\u659c\u5207\uff0c\u6b63\u4e2d\u5979\u7684\u624b\u8155\u3002\u8299\u857e\u72c4\u53ea\u89c9\u624b\u8155\u5267\u75db\uff0c\u624b\u6307\u62ff\u634f\u4e0d\u4f4f\uff0c\u77ed\u5251\u5f53\u5577\u5760\u5730\uff0c\u5979\u6b63\u8981\u6361\u8d77\u518d\u523a\uff0c\u94c1\u9762\u4eba\u4e00\u62f3\u51fb\u51fa\u7838\u5728\u5979\u7684\u8138\u988a\u4e0a\uff0c\u987f\u65f6\u4fbf\u6655\u5012\u8fc7\u53bb\u3002</p><p>\u867d\u7136\u8f7b\u800c\u6613\u4e3e\u5c31\u88ab\u6253\u5012\uff0c\u4f46\u8299\u857e\u72c4\u7684\u52aa\u529b\u5e76\u6ca1\u6709\u767d\u8d39\u3002\u94c1\u9762\u4eba\u8981\u5206\u795e\u5bf9\u4ed8\u5979\uff0c\u6390\u7740\u743c\u6069\u8116\u9888\u7684\u624b\u638c\u529b\u9053\u5c31\u4e0d\u7531\u5f97\u5f31\u4e86\u51e0\u5206\u3002\u743c\u6069\u5f97\u8fd9\u4e00\u7f13\uff0c\u795e\u667a\u7a0d\u7a0d\u6062\u590d\uff0c\u5fc3\u77e5\u8fd9\u662f\u751f\u6b7b\u5173\u5934\uff0c\u4e0d\u6562\u8fdf\u7591\uff0c\u805a\u8d77\u6700\u540e\u4e00\u70b9\u529b\u6c14\uff0c\u731b\u7136\u4e00\u628a\u6263\u4f4f\u4e86\u94c1\u9762\u4eba\u7684\u624b\u8155\u3002</p><p>\u201c\u54fc\uff01\u201d</p><p>\u94c1\u9762\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u6b63\u8981\u624b\u4e0a\u52a0\u52b2\u628a\u743c\u6069\u6390\u6b7b\uff0c\u7a81\u7136\u53d1\u89c9\u4e0d\u77e5\u4f55\u65f6\uff0c\u81ea\u5df1\u7684\u624b\u638c\u5df2\u7ecf\u5b8c\u5168\u5931\u53bb\u611f\u89c9\uff0c\u4e0d\u542c\u4f7f\u5524\u3002\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u51dd\u795e\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u5708\u7070\u8272\u6ce2\u7eb9\u4eff\u4f5b\u6d9f\u6f2a\u822c\uff0c\u6b63\u6cbf\u7740\u81ea\u5df1\u7684\u80f3\u818a\u5feb\u901f\u5f80\u4e0a\u8513\u5ef6\uff0c\u6240\u8fc7\u4e4b\u5904\u65e0\u8bba\u662f\u8863\u7269\u8fd8\u662f\u8840\u8089\uff0c\u5c3d\u7686\u53d8\u6210\u4e86\u51b7\u51b0\u51b0\u7684\u77f3\u5934\u3002</p><p>\u6084\u65e0\u58f0\u606f\u95f4\uff0c\u743c\u6069\u53d1\u52a8\u4e86\u77f3\u5316\u672f\u3002</p><p>\u94c1\u9762\u4eba\u6390\u7740\u743c\u6069\u7684\u8116\u9888\uff0c\u8fd9\u539f\u672c\u662f\u5f88\u806a\u660e\u7684\u505a\u6cd5\u3002\u56e0\u4e3a\u4efb\u4f55\u4eba\u88ab\u6390\u7740\u8116\u9888\u5c31\u65e0\u6cd5\u8bf4\u8bdd\uff08\u4ea1\u7075\u9664\u5916\uff09\uff0c\u6ca1\u6cd5\u8bf4\u8bdd\u5c31\u65e0\u6cd5\u5ff5\u5492\uff0c\u6ca1\u6cd5\u5ff5\u5492\u5c31\u4e0d\u80fd\u65bd\u6cd5\uff0c\u4e00\u4e2a\u5deb\u5e08\u5728\u6218\u6597\u4e2d\u4e0d\u80fd\u65bd\u6cd5\uff0c\u90a3\u5c31\u5b8c\u5168\u4e0d\u8db3\u4e3a\u60e7\u3002\u95ee\u9898\u5728\u4e8e\u8fd9\u662f\u5e38\u7406\uff0c\u800c\u743c\u6069\u6070\u6070\u662f\u4e2a\u53d8\u6001\uff0c\u6216\u8005\u8bf4\u4ed6\u6709\u4e2a\u53d8\u6001\u7684\u8001\u5e08\u3002</p><p>\u62dc\u5965\u6c83\u4f20\u6388\u7684\u6cd5\u672f\u9ed8\u53d1\u6280\u5de7\u6240\u8d50\uff0c\u743c\u6069\u662f\u4e0d\u5fc5\u5ff5\u5492\u5c31\u80fd\u591f\u76f4\u63a5\u65bd\u6cd5\u7684\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u4ed6\u8fd9\u4e2a\u77f3\u5316\u672f\u5e76\u4e0d\u662f\u50cf\u901a\u5e38\u60c5\u51b5\u90a3\u6837\u8fdc\u7a0b\u5c04\u51fa\uff0c\u800c\u662f\u76f4\u63a5\u63a5\u89e6\u5bf9\u65b9\u7684\u8eab\u4f53\u65bd\u6cd5\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5c31\u7b97\u662f\u94c1\u9762\u4eba\u771f\u6709\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u4e5f\u6ca1\u7528\u2014\u2014\u6cd5\u672f\u9006\u8f6c\u53ea\u80fd\u53cd\u5f39\u8fdc\u7a0b\u6cd5\u672f\u653b\u51fb\u3002</p><p>\u4ee5\u5e38\u7406\u800c\u8bba\uff0c\u94c1\u9762\u4eba\u5e94\u8be5\u662f\u5728\u77f3\u5316\u672f\u53d1\u52a8\u7684\u77ac\u95f4\u5c31\u53d8\u6210\u4e00\u5c0a\u77f3\u50cf\uff0c\u4f46\u4ed6\u663e\u7136\u5bf9\u9b54\u6cd5\u6709\u7740\u5f88\u5f3a\u7684\u62b5\u6297\u529b\uff0c\u867d\u7136\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\u4e2d\u62db\uff0c\u4f46\u6cd5\u672f\u5374\u4e5f\u672a\u80fd\u53d1\u6325\u5b8c\u5168\u7684\u5a01\u529b\uff0c\u751f\u6548\u65f6\u95f4\u88ab\u5f3a\u884c\u5ef6\u957f\u4e86\u2014\u2014\u4f46\u4e5f\u53ea\u662f\u7a0d\u7a0d\u5ef6\u957f\u7247\u523b\u7f62\u4e86\u3002\u4e24\u4e2a\u547c\u5438\u4e4b\u95f4\uff0c\u77f3\u5316\u672f\u7684\u9b54\u6cd5\u6ce2\u7eb9\u5df2\u7ecf\u8d8a\u8fc7\u4e86\u94c1\u9762\u4eba\u7684\u8098\u90e8\uff0c\u773c\u770b\u5c31\u8981\u8513\u5ef6\u5230\u80a9\u5934\u3002\u8fc7\u4e86\u80a9\u5934\uff0c\u4fbf\u81f3\u8116\u9888\u548c\u8111\u888b\uff0c\u800c\u4e00\u65e6\u8111\u888b\u88ab\u77f3\u5316\uff0c\u90a3\u4e48\u6574\u4e2a\u4eba\u4e5f\u5c31\u545c\u547c\u54c0\u54c9\u4e86\u3002</p><p>\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u94c1\u9762\u4eba\u731b\u7136\u5de6\u624b\u5408\u638c\uff0c\u91cd\u91cd\u5207\u5728\u81ea\u5df1\u7684\u53f3\u80a9\u4e0a\u3002\u4ed6\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u6cd5\u672f\uff0c\u638c\u7f18\u53d8\u5f97\u950b\u9510\u5982\u5200\uff0c\u95ea\u70c1\u7740\u51b0\u51b7\u7684\u91d1\u5c5e\u5149\u6cfd\uff0c\u4e00\u638c\u4fbf\u5c06\u81ea\u5df1\u7684\u6574\u4e2a\u53f3\u81c2\u7ed9\u780d\u4e86\u4e0b\u6765\u3002</p><p>\u6251\u901a\u4e00\u58f0\uff0c\u5927\u534a\u622a\u5df2\u7ecf\u77f3\u5316\u7684\u80f3\u818a\u8fde\u7f00\u7740\u6b8b\u4f59\u8840\u8089\u6389\u843d\u5728\u5730\uff0c\u94c1\u9762\u4eba\u6342\u7740\u4f24\u53e3\u5f80\u540e\u4fbf\u9000\uff0c\u9c9c\u8840\u4ece\u6307\u7f1d\u95f4\u98d9\u5c04\u800c\u51fa\u3002\u56e0\u4e3a\u8ddd\u79bb\u592a\u8fd1\uff0c\u743c\u6069\u88ab\u55b7\u4e86\u4e2a\u6ee1\u8138\u9c9c\u7ea2\uff0c\u4ed6\u5374\u4e0d\u7ba1\u4e0d\u987e\uff0c\u95f7\u543c\u4e00\u58f0\uff0c\u5168\u8eab\u4e94\u5904\u9b54\u6cd5\u523a\u9752\u63a5\u8fde\u5149\u8292\u7206\u8d77\uff0c\u50a8\u5907\u5176\u4e2d\u7684\u4e94\u9053\u5f3a\u5316\u6cd5\u672f\u540c\u65f6\u53e0\u52a0\uff0c\u4e00\u5f2f\u8170\uff0c\u4eff\u4f5b\u51f6\u731b\u7684\u730e\u8c79\u822c\u548c\u8eab\u6251\u4e0a\uff0c\u671d\u7740\u94c1\u9762\u4eba\u7684\u80f8\u8179\u90e8\u4f4d\u4e00\u722a\u6293\u6765\u3002</p><p>\u743c\u6069\u4e0d\u66fe\u7ec3\u8fc7\u5927\u529b\u91d1\u521a\u6307\u6216\u8005\u9e70\u722a\u529f\uff0c\u8fd9\u4e00\u6293\u770b\u4f3c\u6ca1\u4ec0\u4e48\u5a01\u80c1\uff0c\u7136\u800c\u6307\u5c16\u95ea\u70c1\u8df3\u8dc3\u7684\u7fe0\u7eff\u661f\u5149\uff0c\u5374\u6e05\u695a\u610f\u5473\u7740\u4e00\u4e2a\u89e3\u79bb\u672f\u5df2\u7ecf\u84c4\u52bf\u5f85\u53d1\u3002</p></article>"}}}]);
