(window.webpackJsonp=window.webpackJsonp||[]).push([[435],{458:function(p){p.exports={chapterTitle:null,title:"\u7b2c435\u7ae0 \u6ca1\u6709\u4e3e\u724c",content:"<article><h2>\u7b2c435\u7ae0 \u6ca1\u6709\u4e3e\u724c</h2><p>\u3000\u3000\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u7684\u7b2c\u4e00\u67615\u79d2\u5e7f\u544a\u7531\u79d1\u738b\u7535\u5668\u4ee53\u4ebf5\u5343\u4e07\u7684\u60ca\u4eba\u9ad8\u4ef7\u7ade\u5f97\uff0c\u5dee\u4e0d\u591a\u662f\u53bb\u5e74\u7231\u8fbe\u7535\u5b50\u51fa\u4ef7\u76844.5\u500d\uff0c\u8c2d\u5e0c\u677e\u624b\u4e2d\u5c0f\u69cc\u6572\u54cd\uff0c\u73b0\u573a\u5c31\u54cd\u8d77\u96f7\u9e23\u822c\u7684\u638c\u58f0\u3002</p><p>\u3000\u3000\u8fd9\u662f\u4e5d\u516d\u5e74\u592e\u89c6\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u6700\u52a8\u4eba\u5fc3\u9b44\u7684\u4e00\u69cc\uff0c\u4e5f\u662f\u7b2c\u4e00\u69cc\uff0c\u8fd9\u4e00\u69cc\u5c06\u4eba\u7684\u8840\u6db2\u90fd\u6572\u6cb8\u817e\u8d77\u6765\uff0c\u63a5\u4e0b\u8fd9\u4e2a\u9ec4\u91d1\u65f6\u4ee3\u5269\u4e0b\u768412\u67615\u79d2\u5e7f\u544a\u7684\u7ade\u62cd\u6bd4\u9884\u60f3\u4e2d\u8981\u8840\u8165\u8bb8\u591a\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u4eba\u4eec\u5bf9\u65b0\u95fb\u8054\u64ad\u91cc\u7684\u80e1\u8a00\u4e71\u8bed\u8fd8\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u518d\u52a0\u4e0a\u5f53\u65f6\u4eba\u4eec\u7684\u5a31\u4e50\u8282\u76ee\u8fd8\u5341\u5206\u6709\u9650\uff0c\u7535\u89c6\u8282\u76ee\u4e5f\u8c08\u4e0d\u4e0a\u975e\u5e38\u4e30\u5bcc\uff0c\u51e0\u4e4e\u6240\u6709\u7701\u5e02\u7535\u89c6\u53f0\u4e03\u70b9\u6863\u90fd\u4f1a\u8f6c\u8ba9\u592e\u89c6\u7684\u65b0\u95fb\u8054\u64ad\uff0c\u4f7f\u5f97\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u768465\u79d2\u949f\u5f53\u4e4b\u65e0\u6127\u7684\u6210\u4e3a\u6700\u6709\u4ef7\u503c\u7684\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\uff0c\u5373\u4f7f\u8bf4\u662f\u5168\u7403\u6700\u5177\u4ef7\u503c\u7684\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u4e5f\u4e0d\u4e3a\u8fc7\uff0c\u6bd5\u7adf\u8986\u76d6\u5168\u7403\u56db\u5206\u4e4b\u4e00\u4eba\u53e3\u7684\u5e02\u573a\u3002</p><p>\u3000\u3000\u6839\u636e\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u7ade\u4ef7\u89c4\u5219\uff0c\u5382\u5546\u7ade\u4ef7\u5728\u6240\u6709\u5165\u56f4\u5382\u5546\u4e2d\u4e0d\u80fd\u4f4e\u4e8e\u524d\u5341\u4e09\u540d\uff0c\u5728\u540c\u884c\u4e1a\u5382\u5546\u4e0d\u80fd\u4f4e\u4e8e\u524d\u4e09\u540d\uff0c\u624d\u80fd\u6324\u5165\u8fd9\u4e2a\u65f6\u6bb5\uff0c\u9664\u4e86\u79d1\u738b\u5df2\u7ecf\u593a\u5f97\u7b2c\u4e00\u67615\u79d2\u5e7f\u544a\u4e4b\u5916\uff0c\u8fd8\u6709\u516b\u5bb6\u5165\u56f4\u5f71\u789f\u673a\u5382\u5546\u6ce8\u5b9a\u8981\u4e3a\u5269\u4e0b\u4e24\u4e2a\u7535\u5b50\u7c7b\u540d\u989d\u4e89\u5f97\u5934\u7834\u8840\u6d41\u3002</p><p>\u3000\u3000\u6b65\u6b65\u9ad8\u4ee5\u4e00\u4ebf\u4e94\u5343\u516b\u767e\u4e07\u593a\u5f97\u7535\u5b50\u7c7b\u7b2c\u4e8c\u67615\u79d2\u5e7f\u544a\uff0c\u7231\u591a\u662f\u4e00\u4ebf\u56db\u5343\u4e07\u593a\u5f97\u7535\u5b50\u7c7b\u7b2c\u4e09\u67615\u79d2\u5e7f\u544a\u3002\u5728\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u4e2d\uff0c\u79e6\u6c60\u9152\u5382\u4ee53.2\u4ebf\u7684\u9ad8\u4ef7\u593a\u5f97\u6807\u738b\u4e4b\u540e\uff0c\u7535\u5b50\u7c7b\u524d\u4e09\u540d\u6ca1\u6709\u4e00\u5bb6\u7684\u7ade\u4ef7\u7a81\u7834\u4e00\u4ebf\uff0c\u73b0\u5728\u4e09\u5bb6\u7535\u5b50\u7c7b\u5382\u5546\u6bcf\u4e00\u5bb6\u7684\u7ade\u4ef7\u90fd\u8fdc\u8fdc\u8d85\u8fc7\u4e00\u4ebf\uff0c\u4e5f\u53ea\u80fd\u62b1\u6028\u7231\u8fbe\u7535\u5b50\u5728\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\u5e26\u6765\u592a\u8000\u773c\u7684\u5149\u8292\u3002</p><p>\u3000\u3000\u5176\u4ed6\u884c\u4e1a\u4e4b\u95f4\u7684\u4e89\u593a\u6bd4\u8f83\u5c11\uff0c\u5165\u56f4\u7684\u4e94\u5bb6\u767d\u9152\u4f01\u4e1a\u5e26\u6765\u4e00\u4e9b\u5c0f\u9ad8\u6f6e\uff0c\u4ee5\u738b\u6548\u91d1\u4e3a\u5382\u5546\u4ee3\u8868\u7684\u53e4\u4e95\u8d21\u9152\u5f02\u9152\u7a81\u8d77\uff0c\u8d85\u8d8a\u5728\u6807\u738b\u7ade\u593a\u4e2d\u51fa\u624b\u4e0d\u51e1\u7684\u5510\u6cb3\u9152\u4e1a\uff0c\u6210\u4e3a\u767d\u9152\u7c7b\u7b2c\u4e00\uff0c\u5510\u6cb3\u9152\u4e1a\u4e3a\u767d\u9152\u7c7b\u7b2c\u4e8c\uff0c\u7b2c\u4e09\u5bb6\u767d\u9152\u4f01\u4e1a\u7684\u7ade\u62a5\u4ef7\u672a\u80fd\u8fdb\u5165\u603b\u62a5\u4ef7\u6392\u524d\u5341\u4e09\u7ed9\u6dd8\u6c70\uff0c\u82b1\u4e86\u4e00\u4e2a\u4e0a\u5348\u7684\u65f6\u95f4\u624d\u5c06\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u768465\u79d2\u949f\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7ade\u62cd\u5b8c\uff0c\u6700\u4f4e\u4e00\u5bb6\u7ade\u62a5\u4ef7\u4e3a\u516b\u5343\u4e8c\u767e\u4e07\uff0c\u5341\u4e09\u5bb6\u5382\u5546\u62a5\u4ef7\u90fd\u8d85\u8fc7\u53bb\u5e74\u7231\u8fbe\u7535\u5b50\u4e3a\u6807\u738b\u5e7f\u544a\u6240\u4ed8\u51fa\u7684\u4ee3\u4ef7\u3002</p><p>\u3000\u300065\u79d2\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7684\u603b\u7ade\u6807\u4ef7\u8d85\u8fc7\u5341\u4e94\u4ebf\u3002</p><p>\u3000\u3000\u8c2d\u5e0c\u677e\u4eb2\u81ea\u4e3b\u6301\u6807\u738b\u5e7f\u544a\u7684\u7ade\u6807\u4e4b\u540e\u5c31\u611f\u5230\u7b4b\u75b2\u529b\u5c3d\uff0c\u867d\u7136\u4e4b\u540e\u7684\u7ade\u6807\u7559\u7ed9\u4e13\u4e1a\u4eba\u58eb\u4e3b\u6301\uff0c\u5979\u5750\u5728\u4e3b\u5e2d\u53f0\u4ecd\u4e0d\u5f97\u5e73\u9759\uff0c\u8fd9\u79cd\u6781\u5ea6\u4ea2\u594b\u7684\u72b6\u6001\u662f\u76f8\u5f53\u6d88\u8017\u4eba\u4f53\u529b\u4e86\uff0c\u4e0a\u5348\u534a\u573a\u62db\u5546\u5927\u4f1a\u7684\u603b\u7ed3\u81f4\u8f9e\uff0c\u5979\u90fd\u4e0d\u5f97\u4e0d\u59d4\u6258\u522b\u4eba\u4ee3\u66ff\u3002</p><p>\u3000\u3000\u4e0e\u4f1a\u573a\u4e0a\u5382\u5546\u4ee3\u8868\u8840\u6db2\u7ed9\u70b9\u71c3\u76f8\u5bf9\u5e94\u7684\uff0c\u5219\u662f\u573a\u5916\u5404\u5bb6\u62a5\u793e\u3001\u7535\u89c6\u53f0\u7b49\u5a92\u4f53\u7684\u8bb0\u8005\u70ed\u60c5\u9ad8\u6da8\uff0c\u79d1\u738b\u7535\u5668\u4e09\u70b9\u4e94\u4ebf\u7684\u6807\u738b\u62a5\u4ef7\u8fdc\u8fdc\u8d85\u51fa\u4e4b\u524d\u5a92\u4f53\u7684\u9884\u6d4b\uff0c\u800c\u4e4b\u524d\u5a92\u4f53\u6700\u5bc4\u4e88\u539a\u671b\u7684\u7231\u8fbe\u7535\u5b50\u7adf\u7136\u6574\u4e2a\u4e0a\u5348\u90fd\u6ca1\u6709\u4e3e\u724c\uff0c\u5382\u5546\u4ee3\u8868\uff0c\u636e\u8bf4\u662f\u7231\u8fbe\u7535\u5b50\u7684\u603b\u88c1\u7adf\u7136\u6574\u4e2a\u4e0a\u5348\u90fd\u5728\u772f\u7740\u773c\u775b\u6253\u778c\u7761\u3002\u5728\u592e\u89c6\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u524d\u5915\uff0c\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u9ad8\u5c42\u96c6\u4f53\u8df3\u69fd\u79d1\u738b\u7535\u5668\uff0c\u592a\u5bb9\u6613\u8ba9\u4eba\u6709\u620f\u5267\u6027\u7684\u8054\u60f3\u4e86\u3002</p><p>\u3000\u3000\u867d\u7136\u5728\u62db\u5546\u5927\u4f1a\u4e4b\u524d\uff0c\u5f88\u591a\u5a92\u4f53\u7684\u8bb0\u8005\u9488\u5bf9\u6b64\u4e8b\u91c7\u8bbf\u4e86\u6b64\u65f6\u5df2\u7ecf\u8df3\u5230\u79d1\u738b\u7535\u5668\u62c5\u4efb\u5e02\u573a\u90e8\u603b\u76d1\u7684\u5218\u660e\u8f89\uff0c\u4f46\u662f\u8fd9\u65f6\u5019\u624d\u80fd\u80af\u5b9a\u8fd9\u5c31\u662f\u6807\u738b\u7cfb\u5217\u62a5\u9053\u91cc\u6700\u70ed\u7684\u70ed\u70b9\u3002</p><p>\u3000\u3000\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u79d1\u738b\u7535\u5668\u66b4\u51fa\u592e\u89c6\u672c\u5c4a\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u6700\u5927\u7684\u51b7\u95e8\u2014\u2014\u4f3c\u4e4e\u4e09\u5c4a\u62db\u5546\u5927\u4f1a\u7684\u6807\u738b\u4f01\u4e1a\u90fd\u662f\u5a92\u4f53\u9884\u6d4b\u4e4b\u5916\u7684\u9ed1\u9a6c\u4f01\u4e1a\u2014\u2014\u8fd9\u81ea\u7136\u662f\u5a92\u4f53\u6700\u5173\u6ce8\u7684\u7126\u70b9\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u8425\u9500\u56e2\u961f\u5728\u592e\u89c6\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u524d\u5915\u201c\u53db\u51fa\u201d\u6295\u654c\uff0c\u6210\u5c31\u672c\u5c4a\u6700\u5927\u7684\u9ed1\u9a6c\uff01</p><p>\u3000\u3000\u6700\u5177\u7ade\u4e89\u5b9e\u529b\u7684\u2014\u2014\u636e\u4f20\u5e26\u7740\u6570\u989d\u5de8\u5927\u7684\u94f6\u884c\u627f\u5151\u6c47\u7968\u6765\u5230\u5317\u4eac\u2014\u2014\u7231\u8fbe\u7535\u5b50\u7adf\u7136\u6574\u4e2a\u4e0a\u5348\u90fd\u6ca1\u6709\u4e3e\u4e00\u6b21\u724c\uff0c\u8fd9\u80cc\u540e\u7a76\u7adf\u6709\u7740\u600e\u6837\u7684\u5185\u5e55\uff1f</p><p>\u3000\u3000\u597d\u5947\u5fc3\u5c06\u5a92\u4f53\u8bb0\u8005\u7684\u5185\u5fc3\u6414\u5f97\u5176\u75d2\u65e0\u6bd4\u3001\u4ea2\u594b\u5f02\u5e38\u3002</p><p>\u3000\u3000\u66f4\u4f55\u51b5\u7231\u8fbe\u7535\u5b50\u4e0e\u79d1\u738b\u7535\u5668\u90fd\u662f\u540c\u4e00\u4e2a\u5730\u65b9\u4e0a\u7684\u4f01\u4e1a\uff0c\u5728\u573a\u7684\u5a92\u4f53\u8bb0\u8005\u751a\u81f3\u8fd8\u4e0d\u662f\u6bcf\u4e00\u4e2a\u90fd\u80fd\u77e5\u9053\u6d77\u5dde\u4f4d\u4e8e\u54ea\u4e2a\u5730\u65b9\uff1f</p><p>\u3000\u3000\u62db\u5546\u5927\u4f1a\u4e2d\u5348\u4f11\u606f\u671f\u95f4\uff0c\u5a92\u4f53\u8bb0\u8005\u4eec\u77aa\u5927\u773c\u775b\uff0c\u770b\u7740\u4ece\u4f1a\u573a\u9c7c\u8d2f\u800c\u51fa\u7684\u5382\u5546\u4ee3\u8868\u4eec\uff0c\u751f\u6015\u6f0f\u8fc7\u6bcf\u4e00\u4e2a\u6709\u65b0\u95fb\u4ef7\u503c\u7684\u4eba\u7269\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u6709\u8bb0\u8005\u53d1\u73b0\u80fd\u591f\u8fdb\u5165\u4f1a\u573a\u7684\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u8bb0\u8005\u5df2\u7ecf\u5728\u91cc\u9762\u56f4\u7740\u79d1\u738b\u7535\u5668\u3001\u7231\u8fbe\u7535\u5b50\u3001\u6b65\u6b65\u9ad8\u7b49\u5382\u5546\u4ee3\u8868\u5f00\u59cb\u7b80\u77ed\u7684\u91c7\u8bbf\uff0c\u7ed9\u592e\u89c6\u5de5\u4f5c\u4eba\u5458\u6321\u5728\u4f1a\u573a\u5916\u7684\u5176\u4ed6\u5a92\u4f53\u8bb0\u8005\u7eb7\u7eb7\u5927\u58f0\u8d77\u54c4\uff0c\u6297\u8bae\u592e\u89c6\u7684\u8fd9\u79cd\u7279\u6743\u884c\u4e3a\u3002</p><p>\u3000\u3000\u592e\u89c6\u5b88\u5728\u4f1a\u573a\u95e8\u53e3\u7684\u5de5\u4f5c\u4eba\u5458\u81ea\u7136\u4e0d\u4e88\u7406\u4f1a\uff0c\u4e2d\u5348\u53ef\u4f9b\u91c7\u8bbf\u7684\u65f6\u95f4\u8fd9\u4e48\u77ed\uff0c\u81ea\u7136\u8981\u6ee1\u8db3\u5148\u592e\u89c6\u81ea\u5df1\u4eba\u7684\u91c7\u8bbf\u8981\u6c42\uff0c\u62db\u5546\u5927\u4f1a\u7ed3\u675f\u4e4b\u540e\uff0c\u4f1a\u6709\u4e13\u95e8\u7684\u8bb0\u8005\u62db\u5f85\u4f1a\u3002\u4f1a\u573a\u79e9\u5e8f\u7684\u8d1f\u8d23\u4eba\u89c1\u5916\u9762\u8bb0\u8005\u95f9\u5f97\u51f6\uff0c\u4ed6\u6ca1\u6709\u7ad9\u51fa\u6765\u89e3\u91ca\u4ec0\u4e48\uff0c\u4e5f\u6ca1\u6709\u4e0e\u573a\u5916\u7684\u8bb0\u8005\u6c9f\u901a\uff0c\u76f4\u63a5\u5c06\u4f1a\u573a\u91cc\u9762\u7684\u8b66\u536b\u8c03\u5230\u95e8\u53e3\u5c06\u4f1a\u573a\u51fa\u5165\u53e3\u5835\u4e0a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u6ca1\u6709\u51fa\u624b\u4e89\u62a2\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u7684\u5e7f\u544a\u8d44\u6e90\uff0c\u5e76\u4e0d\u4ee3\u8868\u4e0b\u4e00\u5e74\u5ea6\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u5c31\u6ca1\u6709\u5408\u4f5c\uff0c\u4e0b\u5348\u7684\u62db\u5546\u5927\u4f1a\u662f\u7ade\u6807\u6b21\u8981\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\uff0c\u82cf\u6d25\u4e1c\u5c31\u4e0d\u7528\u518d\u772f\u7740\u773c\u775b\u6253\u778c\u7761\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u5f20\u606a\u9886\u8fd9\u4e48\u591a\u4eba\u8fc7\u6765\uff0c\u90fd\u5404\u6709\u5206\u5de5\uff1a\u82cf\u6d25\u4e1c\u4f5c\u4e3a\u7231\u8fbe\u7535\u5b50\u7684\u6b63\u5f0f\u4ee3\u8868\u53c2\u52a0\u62db\u5546\u5927\u4f1a\u5e94\u4ed8\u5a92\u4f53\u7684\u91c7\u8bbf\uff1b\u8c22\u665a\u6674\u662f\u4f5c\u4e3a\u7231\u8fbe\u7535\u5b50\u7684\u5e55\u540e\u8001\u677f\u4e4b\u4e00\u8d70\u5230\u53f0\u524d\u6765\u4e0e\u592e\u89c6\u7684\u9ad8\u5c42\u63a5\u6d3d\uff0c\u6d3d\u8c08\u4e0b\u4e00\u5e74\u5ea6\u5728\u5e02\u573a\u5ba3\u4f20\u4e0a\u7684\u5408\u4f5c\uff0c\u8c2d\u5e0c\u677e\u4e5f\u662f\u5973\u4eba\uff0c\u6709\u7740\u77e5\u6027\u7f8e\uff0c\u5973\u4eba\u4e0e\u5973\u4eba\u6253\u4ea4\u9053\u603b\u4f1a\u597d\u4e00\u4e9b\uff1b\u8bb8\u601d\u4e0e\u4e01\u69d0\u4f1a\u4e0eTI\u5177\u4f53\u6d3d\u8c08\u5408\u8d44\u6210\u7acbVCD\u673a\u82af\u5de5\u5382\u7684\u4e8b\u60c5\uff0c\u867d\u7136\u4e0d\u6015\u98de\u5229\u6d66\u5361\u8116\u5b50\uff0c\u4f46\u662f\u80fd\u8ff7\u60d1\u4ed6\u4eec\u591a\u4e45\u5c31\u8981\u8ff7\u60d1\u4ed6\u4eec\u591a\u4e45\uff0c\u5f20\u606a\u8003\u8651\u4ed6\u4eec\u8fd9\u4e00\u65b9\u8fd8\u662f\u4ee5\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u516c\u53f8\u7684\u540d\u4e49\u8fdb\u884c\u6295\u8d44\uff0c\u4e4b\u524d\u4e0eTI\u516c\u53f8\u7684\u534f\u8bae\uff0c\u4e5f\u662f\u4ee5\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u516c\u53f8\u7684\u540d\u4e49\u8fdb\u884c\u7684\u3002</p><p>\u3000\u3000\u6700\u5927\u7684\u60ac\u5ff5\u5df2\u7ecf\u4e0d\u5b58\u5728\uff0c\u8bb8\u601d\u3001\u4e01\u69d0\u8fd8\u6709\u9a6c\u6d77\u9f99\u5148\u4e00\u6b65\u79bb\u5f00\u4f1a\u573a\uff0c\u6ca1\u6709\u65f6\u95f4\u4e00\u8d77\u5403\u4e2d\u996d\uff0c\u5c31\u5148\u5f00\u8f66\u53bbTI\u516c\u53f8\u8fdb\u884c\u6b63\u5f0f\u7684\u62dc\u8bbf\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e0e\u54c1\u724c\u8fd0\u8425\u90e8\u7ecf\u7406\u5218\u6d77\u71d5\u6628\u5929\u5df2\u7ecf\u62dc\u8bbf\u8fc7\u8c2d\u5e0c\u677e\uff0c\u8c2d\u5e0c\u677e\u4e00\u4e0a\u5348\u4e5f\u5145\u6ee1\u4e86\u7591\u95ee\uff1a\u7231\u8fbe\u7535\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u4e3e\u724c\uff1f\u81ea\u7136\u9080\u8bf7\u8c22\u665a\u6674\u4e00\u8d77\u5171\u8fdb\u5348\u9910\uff0c\u60f3\u8fdb\u4e00\u6b65\u6478\u6e05\u7231\u8fbe\u7535\u5b50\u7684\u610f\u56fe\u3002</p><p>\u3000\u3000\u5728\u8c2d\u5e0c\u677e\u770b\u6765\uff1a\u7231\u8fbe\u7535\u5b50\u4e0d\u53ef\u80fd\u6ca1\u94b1\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4e0d\u91cd\u89c6\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5e7f\u544a\u8d44\u6e90\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u7231\u8fbe\u7535\u5b50\u5728\u56fd\u5185\u7684\u5e02\u573a\u5730\u4f4d\u5df2\u7ecf\u7a33\u5982\u78d0\u77f3\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4e09\u4eba\u7ed9\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u8bb0\u8005\u7ea6\u5728\u4f1a\u573a\u4e00\u89d2\u8fdb\u884c\u91c7\u8bbf\uff0c\u90a3\u4e24\u4e2a\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u8bb0\u8005\u53cd\u5e94\u76f8\u5f53\u8fdf\u949d\uff0c\u6b64\u65f6\u8fd8\u4e0d\u77e5\u9053\u5218\u660e\u8f89\u62c9\u4eba\u96c6\u4f53\u8df3\u69fd\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u8be2\u95ee\u82cf\u6d25\u4e1c\u4e0a\u5348\u4e3a\u4ec0\u4e48\u6ca1\u6709\u4e3e\u724c\u6b64\u7c7b\u7684\u65e0\u8425\u517b\u95ee\u9898\uff0c\u82cf\u6d25\u4e1c\u81ea\u7136\u4e0d\u80fd\u8bf4\u5e94\u8be5\u5bf9\u592e\u89c6\u6807\u738b\u6982\u5ff5\u5e94\u8be5\u8fdb\u884c\u53cd\u601d\u3001\u6807\u738b\u5e7f\u544a\u4ef7\u503c\u5b58\u5728\u4e25\u91cd\u9ad8\u4f30\u4e4b\u7c7b\u7684\u8bdd\uff0c\u53ea\u6709\u6577\u884d\u5e94\u4ed8\u3002</p><p>\u3000\u3000\u5f20\u606a\u8eab\u8fb9\u53ea\u6709\u5085\u4fca\u966a\u7740\uff0c\u4ed6\u4e0d\u6025\u7740\u79bb\u5f00\u4f1a\u573a\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u79d1\u738b\u7535\u5668\uff0c\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u3001\u53e4\u4e95\u8d21\u9152\u7b49\u5165\u699c\u5382\u5546\u4ee3\u8868\u90fd\u6709\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u8bb0\u8005\u8fdb\u884c\u91c7\u8bbf\uff0c\u81ea\u7136\u662f\u8c22\u5251\u5357\u90a3\u7fa4\u4eba\u6700\u53d7\u91cd\u89c6\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5f53\u770b\u4e00\u51fa\u597d\u620f\uff0c\u81ea\u7136\u820d\u4e0d\u5f97\u79bb\u5f00\uff0c\u9664\u4e86\u4ed6\u4eec\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e9b\u5382\u5546\u4ee3\u8868\u4e0d\u6025\u7740\u51fa\u53bb\u5403\u996d\uff0c\u5728\u73b0\u573a\u770b\u5165\u699c\u5382\u5546\u7ed9\u91c7\u8bbf\u7684\u573a\u9762\u3002</p><p>\u3000\u3000\u770b\u5230\u4f1a\u573a\u5916\u7684\u8bb0\u8005\u4e0e\u592e\u89c6\u5de5\u4f5c\u4eba\u5458\u5728\u4f1a\u573a\u5165\u53e3\u4e89\u6267\u8d77\u6765\uff0c\u5f20\u606a\u90fd\u5dee\u70b9\u8981\u4fef\u4ef0\u5927\u7b11\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\u4e86\uff0c\u4e00\u76f4\u5230\u5341\u51e0\u5e74\u540e\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u4e00\u76f4\u90fd\u65e0\u6cd5\u6446\u6b63\u4e0e\u5730\u65b9\u5a92\u4f53\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u592e\u89c6\u7684\u8001\u5927\u4f5c\u98ce\uff0c\u8ba9\u5730\u65b9\u5a92\u4f53\u5fc3\u91cc\u76f8\u5f53\u4e0d\u723d\uff0c\u5f20\u606a\u867d\u7136\u4e0d\u6e05\u695a\u8fd9\u79cd\u60c5\u7eea\u6ecb\u751f\u4e8e\u4f55\u65f6\uff0c\u4f46\u786e\u4fe1\u4e0d\u4f1a\u665a\u8fc7\u4e5d\u516d\u5e74\u3002</p><p>\u3000\u3000\u5728\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u4e2d\uff0c\u79e6\u6c60\u4ee53.2\u4ebf\u91cd\u91d1\u7b2c\u4e8c\u6b21\u593a\u5f97\u592e\u89c6\u5e7f\u544a\u6807\u738b\u4e4b\u540e\uff0c\u6700\u5148\u4e88\u4ee5\u8d28\u7591\u7684\u5c31\u662f\u5317\u4eac\u5f53\u5730\u7684\u5a92\u4f53\uff0c\u4ed6\u8bb0\u5f97\u662f\u5317\u4eac\u7535\u89c6\u53f0\u4e24\u540d\u8bb0\u8005\u6f5c\u5165\u5c71\u4e1c\u6f4d\u574a\u5b9e\u5730\u8c03\u67e5\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u6beb\u4e0d\u7559\u60c5\u9762\u7684\u5c06\u79e6\u6c60\u8273\u4e3d\u5149\u5f69\u7684\u6807\u738b\u5916\u8863\u5265\u6389\uff0c\u63ed\u9732\u79e6\u6c60\u5728\u751f\u4ea7\u3001\u9500\u552e\u4e0a\u6781\u5176\u6df7\u4e71\uff0c\u4ee5\u53ca\u79e6\u6c60\u4ece\u56db\u5ddd\u8d2d\u8fdb\u52a3\u8d28\u6563\u88c5\u767d\u9152\u52fe\u5151\u540e\u9ad8\u4ef7\u9500\u552e\u7684\u4e8b\u5b9e\uff0c\u5728\u4e0d\u5230\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\uff0c\u5168\u56fd\u5404\u5730\u8ddf\u8fdb\u7684\u5a92\u4f53\u629b\u51fa\u5927\u91cf\u7684\u63ed\u9732\u62a5\u9053\uff0c\u5c06\u79e6\u6c60\u9152\u5382\u6233\u5f97\u4f53\u65e0\u5b8c\u80a4\uff0c\u79e6\u6c60\u9152\u5382\u7684\u8f89\u714c\u5c31\u50cf\u53ea\u4e94\u5f69\u6591\u6593\u7684\u6ce1\u6cab\u77ac\u65f6\u7834\u706d\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u6d88\u8d39\u5e02\u573a\u7684\u4e0d\u6210\u719f\u7279\u6027\uff0c\u6700\u5927\u9650\u5ea6\u7684\u5938\u5927\u5e7f\u544a\u5ba3\u4f20\u7684\u4f5c\u7528\uff0c\u7136\u800c\u5bf9\u6d88\u8d39\u5e02\u573a\u4e0d\u6210\u719f\u7279\u6027\u3001\u5bf9\u94fa\u5929\u76d6\u5730\u5c42\u51fa\u4e0d\u7a77\u7684\u5546\u4e1a\u624b\u6bb5\u7684\u53cd\u601d\u662f\u4ece\u5a92\u4f53\u5148\u5f00\u59cb\u7684\u3002</p><p>\u3000\u3000\u5c31\u50cf\u53bb\u5e74\u5f20\u606a\u5728\u593a\u53d6\u592e\u89c6\u5e7f\u544a\u6807\u738b\u4e4b\u524d\u5c31\u5927\u641e\u5f62\u8c61\u5de5\u7a0b\uff0c\u5c31\u662f\u6015\u5a92\u4f53\u7684\u53cd\u601d\u5148\u53cd\u601d\u5230\u7231\u8fbe\u7535\u5b50\u5934\u4e0a\u3002\u4eca\u5e74\u4f1a\u4e0d\u4f1a\u53cd\u601d\u5230\u79d1\u738b\u7535\u5668\u5934\u4e0a\uff1f\u5f20\u606a\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\uff0c\u770b\u4f1a\u573a\u51fa\u5165\u53e3\u7684\u55a7\u95f9\uff0c\u81f3\u5c11\u77e5\u9053\u4e2d\u592e\u7535\u89c6\u53f0\u4e0e\u5730\u65b9\u5a92\u4f53\u6709\u7740\u4e0d\u548c\u8c10\u7684\u9634\u5f71\uff0c\u6216\u8bb8\u53ef\u4ee5\u5077\u5077\u6478\u6478\u7684\u63a8\u6ce2\u52a9\u6f9c\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u60f3\u60f3\u81ea\u5df1\u7684\u5fc3\u7406\u8fd8\u771f\u662f\u9634\u6697\uff0c\u5f20\u606a\u90fd\u4e0d\u597d\u610f\u601d\u7684\u4f4e\u5934\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\uff0c\u60f3\u8d77\u4ec0\u4e48\u9ad8\u5174\u7684\u4e8b\u60c5\u6765\uff1f\u201d\u5085\u4fca\u90fd\u89c9\u5f97\u5f20\u606a\u7b11\u5f97\u8be1\u5f02\u3002</p><p>\u3000\u3000\u201c\u79d1\u738b\u53ef\u80fd\u4f1a\u6b7b\u5f97\u5f88\u60e8\uff0c\u201d\u5f20\u606a\u5012\u4e0d\u63a9\u9970\u81ea\u5df1\u7684\u707f\u70c2\u7b11\u5bb9\u4e86\uff0c\u201c\u60f3\u5230\u79d1\u738b\u6b7b\u5f97\u5f88\u60e8\u7684\u60c5\u5f62\uff0c\u4f60\u4e0d\u89c9\u5f97\u5f88\u597d\u7b11\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u65e0\u5948\u7684\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u523b\u610f\u7792\u4ed6\uff0c\u771f\u6b63\u62ff\u4ed6\u5f53\u5fc3\u8179\u770b\uff0c\u5085\u4fca\u5f53\u7136\u77e5\u9053\u81ea\u5df1\u8be5\u600e\u4e48\u53bb\u505a\uff0c\u77e5\u9053\u5c31\u7b97\u4e86\uff0c\u7edd\u5bf9\u4e0d\u80fd\u53bb\u6df1\u7a76\u3001\u53bb\u8054\u60f3\uff0c\u66f4\u4e0d\u80fd\u6cc4\u9732\u7ed9\u4ed6\u4eba\u77e5\u9053\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u79d1\u738b\u7535\u5668\u4e0e\u7231\u8fbe\u7535\u5b50\u90fd\u662f\u6d77\u5dde\u7684\u4f01\u4e1a\uff0c\u8bf7\u95ee\u8c22\u603b\uff0c\u79d1\u738b\u7535\u5668\u8fd9\u6b21\u7ade\u4e0b\u6211\u4eec\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u6807\u738b\u5e7f\u544a\u65f6\u4ee3\uff0c\u662f\u4e0d\u662f\u53d7\u5230\u7231\u8fbe\u7535\u5b50\u83b7\u5f97\u5de8\u5927\u5e02\u573a\u6210\u529f\u7684\u542f\u53d1\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u89c1\u5f97\u5149\u6253\u5f97\u6709\u4e9b\u5f3a\uff0c\u5fae\u5fae\u4fa7\u8fc7\u5934\u60f3\u907f\u4e00\u4e0b\u5f3a\u5149\uff0c\u5374\u770b\u89c1\u4e0d\u8fdc\u5904\u62b1\u80f8\u7ad9\u7740\u7684\u5f20\u606a\u5634\u89d2\u9732\u51fa\u707f\u70c2\u65e0\u6bd4\u7684\u7b11\u5bb9\u3002\u7231\u8fbe\u7535\u5b50\u6574\u4e2a\u4e0a\u5348\u90fd\u6ca1\u6709\u4e3e\u724c\uff0c\u867d\u7136\u6700\u7ec8\u7684\u6807\u738b\u7ade\u4ef7\u79bb\u4ed6\u8ba1\u7b97\u7684\u5e95\u9650\u8fd8\u6709\u4e00\u5b9a\u7684\u5dee\u8ddd\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u6574\u4e2a\u4e0a\u5348\u90fd\u6ca1\u6709\u4e3e\u724c\u7684\u4e8b\u5b9e\uff0c\u4eff\u4f5b\u5de8\u5927\u7684\u9634\u5f71\u8986\u76d6\u5728\u4ed6\u7684\u4e94\u810f\u516d\u8151\uff0c\u8ba9\u4ed6\u7684\u4fe1\u5fc3\u6781\u7f55\u89c1\u7684\u51fa\u73b0\u4e00\u4e1d\u52a8\u6447\uff0c\u8fd9\u65f6\u5019\u53c8\u770b\u89c1\u5f20\u606a\u707f\u70c2\u5f97\u8fc7\u5206\u7684\u7b11\u5bb9\uff0c\u4ece\u4ed6\u5fc3\u7684\u88c2\u7f1d\u91cc\u94bb\u51fa\u4e00\u4e1d\u7126\u8e81\uff1a\u4ed6\u5230\u5e95\u6709\u4ec0\u4e48\u5f97\u610f\u7684\uff1f</p><p>\u3000\u3000\u201c\u8bf7\u95ee\u8c22\u603b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u8c22\u5251\u5357\u9661\u7136\u56de\u8fc7\u795e\uff0c\u770b\u7740\u5750\u5728\u5bf9\u9762\u7684\u8bb0\u8005\u6b63\u7591\u60d1\u7684\u770b\u7740\u81ea\u5df1\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u80fd\u4e0d\u80fd\u8bf7\u4f60\u518d\u8bf4\u4e00\u904d\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u5bf9\u8c22\u5251\u5357\u6ce8\u610f\u529b\u4e0d\u96c6\u4e2d\u7684\u53cd\u5e94\u4e5f\u611f\u5230\u5947\u602a\uff0c\u5fae\u5fae\u4fa7\u76ee\u770b\u8fc7\u53bb\uff0c\u5f20\u606a\u8138\u4e0a\u7684\u7b11\u5bb9\u8fd8\u6ca1\u6709\u6d88\u5931\uff1a\u771f\u662f\u5947\u602a\u7684\u4e00\u4e2a\u7537\u4eba\uff0c\u6216\u8bb8\u8fd8\u79f0\u4e0d\u4e0a\u7537\u4eba\uff0c\u4ed6\u624d\u591a\u5927\uff1f</p><p>\u3000\u3000\u9648\u9759\u4e0d\u7531\u5f97\u7684\u60f3\u7740\u90a3\u5929\u5348\u540e\u7684\u9ad8\u901f\u516c\u8def\uff0c\u5f20\u606a\u9a7e\u9a76\u7740\u6700\u65b0\u6b3e\u7684\u94f6\u7070\u8272VOLVO\u8dd1\u8f66\uff0c\u88ab\u8c22\u5b50\u5609\u534a\u5f3a\u8feb\u7684\u8981\u6c42\u6362\u7740\u5f00\uff0c\u6216\u8bb8\u6ca1\u6709\u4e00\u4e2a\u7537\u4eba\u4f1a\u88c5\u804b\u4f5c\u54d1\u771f\u5c31\u540c\u610f\u4e00\u4e2a\u964c\u751f\u7684\u5973\u5b69\u5b50\u62ff\u7740\u65b0\u4e0a\u8def\u7684\u8dd1\u8f66\u72c2\u98da\u5427\uff1f\u5f20\u606a\u504f\u504f\u5c31\u8fd9\u6837\u505a\u4e86\u3002\u5b50\u5609\u5f53\u6000\u597d\u610f\u7684\u5c06\u81ea\u5df1\u7684\u8033\u9489\u4e22\u5728\u8dd1\u8f66\uff0c\u6709\u54ea\u4e2a\u7537\u4eba\u4f1a\u5c06\u8fd9\u679a\u6765\u5386\u4e0d\u660e\u7684\u8033\u9489\u9001\u7ed9\u4e00\u4f4d\u7f8e\u4e3d\u5230\u6781\u81f4\u7684\u5973\u4eba\uff1f\u5f20\u606a\u504f\u504f\u5c31\u8fd9\u6837\u505a\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u65e0\u6cd5\u60f3\u8c61\u5b50\u5609\u77e5\u9053\u4e8b\u5b9e\u540e\u4f1a\u4e0d\u4f1a\u6293\u72c2\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0b\u610f\u8bc6\u7684\u6478\u4e86\u6478\u5de6\u8033\u4e0a\u7684\u8fd9\u679a\u8033\u9489\uff0c\u8fd9\u679a\u8bb8\u601d\u5728\u845b\u5bb6\u5927\u5b85\u8fd8\u7ed9\u5979\u7684\u8033\u9489\uff0c\u8bb8\u601d\u4eba\u600e\u4e48\u4e0d\u5728\u8fd9\u91cc\uff1f</p></article>"}}}]);
