(window.webpackJsonp=window.webpackJsonp||[]).push([[436],{459:function(p){p.exports={chapterTitle:null,title:"\u7b2c436\u7ae0 \u4e0d\u505a\u597d\u4e8b\u5f88\u591a\u5e74",content:"<article><h2>\u7b2c436\u7ae0 \u4e0d\u505a\u597d\u4e8b\u5f88\u591a\u5e74</h2><p>\u3000\u3000\u5f53\u7136\uff0c\u9648\u9759\u5bf9\u5f20\u606a\u4e5f\u5e76\u4e0d\u90fd\u662f\u8fd9\u4e9b\u5947\u602a\u7684\u5370\u8c61\uff0c\u60f3\u8d77\u8fd9\u4e9b\u5929\u6765\u5076\u5c14\u7684\u63a5\u89e6\uff0c\u5374\u611f\u89c9\u5f20\u606a\u7684\u6c14\u91cf\u504f\u5c0f\u4e86\u4e00\u4e9b\uff0c\u5bf9\u5218\u660e\u8f89\u7684\u79bb\u804c\u8fc7\u4e8e\u803f\u803f\u4e8e\u6000\u4e86\u4e00\u4e9b\uff0c\u5f53\u7136\uff0c\u5979\u53ea\u662f\u4ece\u8c22\u5251\u5357\u77e5\u9053\u90e8\u5206\u7684\u771f\u76f8\u624d\u4f1a\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u5f88\u591a\u4e8b\u60c5\u90fd\u662f\u5979\u5230\u79d1\u738b\u4e4b\u524d\u90fd\u505a\u597d\u4e86\u7684\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u6574\u4e2a\u4e0a\u5348\u90fd\u6ca1\u6709\u4e3e\u724c\uff0c\u800c\u5f20\u606a\u6b64\u65f6\u707f\u70c2\u7684\u7b11\u5bb9\uff0c\u540c\u6837\u7ed9\u9648\u9759\u7559\u4e0b\u5f88\u591a\u7684\u7591\u60d1\u4e0e\u4e0d\u89e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u62b1\u80f8\u7ad9\u7740\uff0c\u770b\u7740\u592e\u89c6\u7684\u5de5\u4f5c\u4eba\u5458\u5c06\u573a\u5916\u7684\u8bb0\u8005\u6321\u5728\u5916\u9762\uff0c\u573a\u9762\u641e\u5f97\u6709\u4e9b\u50f5\uff0c\u4ed6\u505a\u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u63d0\u9192\u82cf\u6d25\u4e1c\u7684\u6ce8\u610f\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u6ce8\u610f\u5230\u5f20\u606a\u5f15\u8d77\u4ed6\u6ce8\u610f\u7684\u624b\u52bf\uff0c\u4e5f\u5f88\u5feb\u7684\u6ce8\u610f\u5230\u573a\u5916\u8bb0\u8005\u4e0e\u592e\u89c6\u5de5\u4f5c\u4eba\u5458\u7684\u4e89\u6267\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u6574\u4e2a\u4e0a\u5348\u90fd\u6ca1\u6709\u4e3e\u724c\uff0c\u90a3\u5c31\u4e0d\u518d\u662f\u592e\u89c6\u5a92\u4f53\u4e0e\u5176\u80fd\u5f71\u54cd\u5230\u7684\u5a92\u4f53\u91cd\u70b9\u5ba3\u4f20\u5bf9\u8c61\uff0c\u6ca1\u6709\u5fc5\u8981\u4e0e\u592e\u89c6\u7684\u8bb0\u8005\u7ea0\u7f20\u592a\u4e45\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u770b\u4e86\u8c22\u5251\u5357\u90a3\u8fb9\u4e00\u773c\uff0c\u79d1\u738b\u6b63\u63a5\u53d7\u592e\u89c6\u7684\u91cd\u70b9\u91c7\u8bbf\uff0c\u4ed6\u8f6c\u773c\u95f4\u5c31\u9886\u4f1a\u5230\u5f20\u606a\u8981\u544a\u8bc9\u4ed6\u7684\u610f\u601d\uff0c\u6b49\u7136\u7684\u5bf9\u5750\u5728\u4ed6\u9762\u524d\u7684\u8bb0\u8005\u8bf4\uff1a\u201c\u771f\u662f\u5bf9\u4e0d\u8d77\uff0c\u4eca\u5929\u7684\u5fc3\u60c5\u5f88\u6fc0\u52a8\uff0c\u65e9\u6668\u8d77\u6765\u5c31\u5403\u8fdb\u4ec0\u4e48\u4e1c\u897f\uff0c\u73b0\u5728\u809a\u5b50\u997f\u5f97\u96be\u53d7\uff0c\u91c7\u8bbf\u662f\u4e0d\u662f\u7a0d\u540e\u2026\u2026\u201d</p><p>\u3000\u3000\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u8bb0\u8005\u4e5f\u6709\u5173\u7cfb\u6237\uff0c\u7231\u8fbe\u7535\u5b50\u4e0a\u5348\u610f\u5916\u7684\u5931\u58f0\uff0c\u8ba9\u592e\u89c6\u65b0\u95fb\u90e8\u4e8b\u5148\u7684\u5b89\u6392\u5c31\u6709\u5931\u63aa\u4e86\uff0c\u8fd9\u4e24\u540d\u8bb0\u8005\u90fd\u4e0d\u662f\u4e8b\u5148\u5b89\u6392\u7684\u4eba\u5458\uff0c\u4ed6\u4eec\u89c1\u82cf\u6d25\u4e1c\u56e0\u4e3a\u809a\u5b50\u997f\u4e86\u7684\u539f\u56e0\u7adf\u7136\u62d2\u7edd\u592e\u89c6\u7ed9\u4ed6\u4eec\u7684\u91c7\u8bbf\u673a\u4f1a\uff0c\u6123\u4e86\u597d\u4e00\u4f1a\u513f\u90fd\u6ca1\u6709\u56de\u8fc7\u795e\u6765\uff1b\u82cf\u6d25\u4e1c\u8fd9\u8fb9\u5c06\u6587\u4ef6\u8d44\u6599\u6536\u62e2\u6574\u9f50\uff0c\u90fd\u505a\u597d\u51c6\u5907\u8d70\u7684\u59ff\u6001\uff0c\u4ed6\u4eec\u8fd8\u5728\u53d1\u5446\u3002\u82cf\u6d25\u4e1c\u5f88\u5927\u65b9\u7684\u4f38\u51fa\u624b\uff1a\u201c\u8fd8\u5e0c\u671b\u4e24\u4f4d\u5927\u8bb0\u8005\u5e2e\u7231\u8fbe\u7535\u5b50\u591a\u5199\u51e0\u53e5\u597d\u8bdd\u2026\u2026\u201d\u65c1\u8fb9\u8425\u9500\u4e2d\u5fc3\u526f\u7ecf\u7406\u8d75\u6d77\u821f\u5c06\u4e24\u4efd\u6863\u6848\u888b\u9012\u8fc7\u53bb\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u516c\u53f8\u7684\u4ecb\u7ecd\u8d44\u6599\uff0c\u8bf7\u4e24\u4f4d\u5927\u8bb0\u8005\u8f9b\u82e6\u770b\u4e00\u4e0b\u2026\u2026\u201d\u4e24\u540d\u8bb0\u8005\u63a5\u8fc7\u6863\u6848\u888b\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u6863\u6848\u888b\u91cc\u9664\u4e86\u4e00\u53e0\u6d3b\u9875\u7eb8\u505a\u7684\u6587\u4ef6\u4e4b\u5916\u8fd8\u88c5\u6709\u4e00\u53ea\u4fe1\u5c01\uff0c\u5c31\u5fc3\u9886\u795e\u4f1a\u7684\u8bf4\u9053\uff1a\u201c\u884c\uff0c\u6211\u4eec\u4f1a\u8ba4\u771f\u770b\u8d35\u516c\u53f8\u7684\u8d44\u6599\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u4e9b\u573a\u5916\u7684\u8bb0\u8005\u8fd8\u5728\u8ddf\u5835\u5728\u51fa\u5165\u53e3\u7684\u592e\u89c6\u5de5\u4f5c\u4eba\u5458\u8d77\u54c4\u55a7\u56b7\uff0c\u770b\u5230\u82cf\u6d25\u4e1c\u4e00\u884c\u4e09\u4eba\u8d70\u51fa\u6765\uff0c\u201c\u54d7\u201d\u7684\u90fd\u4e22\u5f00\u592e\u89c6\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u5c06\u82cf\u6d25\u4e1c\u56f4\u4e86\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u8bf7\u95ee\uff0c\u4f60\u662f\u4e0d\u662f\u7231\u8fbe\u7535\u5b50\u7684\u82cf\u603b\uff1f\u201d</p><p>\u3000\u3000\u770b\u7740\u65e0\u6570\u652f\u8bdd\u7b52\u3001\u5f55\u97f3\u7b14\u9012\u5230\u8ddf\u524d\uff0c\u82cf\u6d25\u4e1c\u6d51\u7136\u5fd8\u8bb0\u521a\u624d\u8bf4\u7684\u809a\u5b50\u997f\u4e86\u7684\u4e8b\u60c5\uff0c\u8138\u4e0a\u4fdd\u6301\u7740\u4eb2\u5207\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8bb0\u8005\u670b\u53cb\u5bf9\u6211\u4eec\u7231\u8fbe\u7535\u5b50\u8fd8\u5f88\u5173\u5fc3\u561b\uff0c\u6211\u662f\u82cf\u6d25\u4e1c\u3002\u201d\u4ed6\u9009\u4e86\u4e00\u4e2a\u597d\u4f4d\u7f6e\uff0c\u5c3d\u53ef\u80fd\u591a\u7684\u8ba9\u5a92\u4f53\u8bb0\u8005\u56f4\u5728\u4ed6\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u201c\u8bf7\u95ee\u82cf\u603b\uff0c\u6211\u770b\u5230\uff0c\u6574\u4e2a\u4e0a\u5348\u7684\u7ade\u6807\u73b0\u573a\uff0c\u82cf\u603b\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u90fd\u6ca1\u6709\u4e3e\u724c\uff0c\u4e0e\u4e4b\u524d\u5a92\u4f53\u5173\u4e8e\u7231\u8fbe\u7535\u5b50\u518d\u6b21\u63fd\u4e0b\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\u7684\u9884\u6d4b\u5dee\u8ddd\u5f88\u5927\uff0c\u6211\u60f3\u95ee\u4e00\u4e0b\uff0c\u7231\u8fbe\u7535\u5b50\u51fa\u4e8e\u600e\u6837\u7684\u8003\u8651\uff0c\u653e\u5f03\u4e0a\u5348\u7684\u4e3e\u724c\u673a\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u5bf9\u4e0b\u4e00\u5e74\u5ea6\u7684\u5e7f\u544a\u5ba3\u4f20\u6709\u65e2\u5b9a\u7684\u8ba1\u5212\u4e0e\u65b9\u6848\uff0c\u4e0a\u5348\u7684\u7ade\u6807\u73b0\u573a\u5f88\u70ed\u95f9\uff0c\u4f46\u662f\u4e0e\u7231\u8fbe\u7535\u5b50\u65e2\u5b9a\u7684\u8ba1\u5212\u4e0e\u65b9\u6848\u6709\u51fa\u5165\uff0c\u6240\u4ee5\u6211\u80fd\u6574\u4e2a\u4e0a\u5348\u90fd\u772f\u7740\u773c\u775b\u517b\u795e\uff0c\u4e3a\u4e86\u80fd\u53c2\u52a0\u8fd9\u4e48\u76db\u5927\u7684\u62db\u5546\u5927\u4f1a\uff0c\u6628\u5929\u665a\u4e0a\u5174\u594b\u5f97\u6709\u4e9b\u5931\u7720\uff0c\u6211\u5012\u662f\u5f88\u60f3\u95ed\u4e0a\u773c\u775b\u7761\u4e00\u89c9\u6765\u7740\uff0c\u611f\u89c9\u53c8\u592a\u5931\u793c\u4e86\u2026\u2026\u201d\u82cf\u6d25\u4e1c\u54c8\u54c8\u4e00\u7b11\uff0c\u611f\u67d3\u5468\u56f4\u7684\u8bb0\u8005\u4e5f\u8ddf\u7740\u7b11\u8d77\u6765\u3002</p><p>\u3000\u3000\u8eab\u4e3a\u8bb0\u8005\uff0c\u804c\u4e1a\u7d20\u517b\u5c31\u662f\u6316\u6398\u65b0\u95fb\u4ef7\u503c\uff0c\u8fd9\u4e48\u8f7b\u6613\u5c31\u653e\u8fc7\u82cf\u6d25\u4e1c\uff0c\u8fd8\u771f\u4ee5\u4e3a\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u67d0\u4e9b\u8bb0\u8005\u5927\u4eba\u554a\uff1f</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u653e\u5f03\u4e0a\u5348\u7684\u7ade\u4ef7\u673a\u4f1a\uff0c\u662f\u4e0d\u662f\u8ddf\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u9ad8\u7ea7\u7ecf\u7406\u5218\u660e\u8f89\u5148\u751f\u7387\u4f17\u8df3\u69fd\u5230\u79d1\u738b\u7535\u5668\u6709\u5173\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u627f\u8ba4\u5218\u660e\u8f89\u5148\u751f\u662f\u804c\u4e1a\u6c34\u5e73\u5f88\u9ad8\u7684\u7ecf\u7406\u4eba\uff0c\u4f46\u662f\u4ed6\u7684\u79bb\u5f00\u5bf9\u7231\u8fbe\u7535\u5b50\u6ca1\u6709\u4e1d\u6beb\u5f71\u54cd\uff0c\u4e5f\u4e0d\u4f1a\u9020\u6210\u4efb\u4f55\u635f\u5931\uff0c\u7231\u8fbe\u7535\u5b50\u4e0d\u53ef\u80fd\u56e0\u4e3a\u4ed6\u4e0e\u4e00\u5c0f\u90e8\u5206\u4eba\u7684\u79bb\u5f00\uff0c\u5c31\u653e\u5f03\u4e00\u4e2a\u5e74\u5ea6\u7684\u5e7f\u544a\u5ba3\u4f20\u8ba1\u5212\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u6ca1\u6709\u53ef\u80fd\u5218\u660e\u8f89\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u573a\u65b9\u6848\u5e26\u5230\u79d1\u738b\u7535\u5668\uff0c\u6253\u4e71\u7231\u8fbe\u7535\u5b50\u65e2\u5b9a\u7684\u8ba1\u5212\uff0c\u4ece\u800c\u88ab\u8feb\u653e\u5f03\u4e0a\u5348\u7684\u7ade\u4ef7\u673a\u4f1a\uff1f\u7231\u8fbe\u7535\u5b50\u5728\u8fd9\u4ef6\u4e8b\u60c5\u662f\u4e0d\u662f\u6709\u96be\u8a00\u4e4b\u9690\uff1f\u201d</p><p>\u3000\u3000\u201c\u96be\u8a00\u4e4b\u9690\uff1f\u201d\u82cf\u6d25\u4e1c\u7b11\u4e86\u7b11\uff0c\u8fd9\u8bb0\u8005\u95ee\u7684\u8bdd\u771f\u8d34\u4ed6\u7684\u5fc3\uff0c\u4e0d\u8fc7\u4ed6\u8fd8\u5f97\u6ce8\u610f\u63aa\u8f9e\uff0c\u81f3\u5c11\u4e0d\u80fd\u8ba9\u5e02\u91cc\u89c9\u5bdf\u5230\u4ed6\u4eec\u5728\u76f4\u63a5\u653b\u51fb\u79d1\u738b\u7535\u5668\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u4f4d\u8bb0\u8005\u670b\u53cb\u771f\u6709\u60f3\u8c61\u529b\uff0c\u7231\u8fbe\u7535\u5b50\u6ca1\u6709\u4ec0\u4e48\u96be\u8a00\u4e4b\u9690\uff0c\u79d1\u738b\u7535\u5668\u662f\u4e00\u5bb6\u5f88\u60f3\u505a\u5927\u4e8b\u4e1a\u7684\u4f01\u4e1a\uff0c\u6211\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u795d\u8d3a\u4ed6\u4eec\u83b7\u5f97\u7b2c\u4e00\u6b65\u7684\u6210\u529f\u3002\u201d</p><p>\u3000\u3000\u201c\u82cf\u603b\u6ca1\u6709\u5426\u8ba4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u627f\u8ba4\u3002\u201d\u73a9\u6587\u5b57\u6e38\u620f\uff0c\u82cf\u6d25\u4e1c\u4e5f\u4e0d\u6bd4\u8c01\u5dee\uff0c\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u5199\u6587\u7ae0\u65f6\u4ed6\u4eec\u5982\u4f55\u5ef6\u4f38\uff0c\u90a3\u662f\u4ed6\u4eec\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u6839\u636e\u6709\u4e9b\u5a92\u4f53\u9884\u6d4b\uff0c\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u7684\u8d44\u91d1\u5b9e\u529b\u96c4\u539a\uff0c\u5373\u4f7f\u6709\u9ad8\u7ea7\u7ecf\u7406\u5728\u8fd9\u65f6\u5019\u7387\u4f17\u53db\u51fa\u7231\u8fbe\uff0c\u7231\u8fbe\u4e5f\u662f\u5e94\u8be5\u6709\u5b9e\u529b\u62ff\u4e0b\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\uff0c\u6211\u60f3\u95ee\u82cf\u603b\uff0c\u7231\u8fbe\u7535\u5b50\u4f1a\u4e3a\u4ec0\u4e48\u6700\u7ec8\u9009\u62e9\u653e\u5f03\uff1f\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8138\u4e0a\u4fdd\u6301\u7740\u771f\u5207\u7684\u7b11\u5bb9\uff0c\u5a92\u4f53\u5df2\u7ecf\u8ba4\u5b9a\u8fd9\u624d\u662f\u4e8b\u5b9e\u7684\u771f\u76f8\uff0c\u76f8\u5bf9\u4e8e\u559c\u6b22\u6316\u4f20\u5947\u6027\u8d28\u65b0\u95fb\u7d20\u6750\u7684\u5a92\u4f53\u4e5f\u66f4\u559c\u6b22\u65b0\u95fb\u66f2\u6298\u52a8\u4eba\u4e00\u4e9b\uff0c\u9664\u4e86\u592e\u89c6\u80fd\u76f4\u63a5\u5f71\u54cd\u5230\u5a92\u4f53\u4f1a\u5f31\u5316\u62a5\u9053\u8fd9\u65b9\u9762\u7684\u4e8b\u5b9e\uff0c\u76f8\u4fe1\u66f4\u591a\u7684\u5a92\u4f53\u66f4\u559c\u6b22\u51d1\u70ed\u95f9\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u770b\u5230\u5f20\u606a\u4e0e\u5085\u4fca\u6df7\u4e71\u8bb0\u8005\u7684\u961f\u4f0d\u91cc\uff0c\u8138\u4e0a\u8fd8\u90fd\u542b\u7740\u8be1\u5f02\u7684\u7b11\uff0c\u7b11\u7740\u56de\u7b54\u521a\u624d\u90a3\u4f4d\u8bb0\u8005\u7684\u63d0\u95ee\uff1a\u201c\u6211\u521a\u521a\u8bf4\u8fc7\uff0c\u7231\u8fbe\u4e0d\u4f1a\u56e0\u4e3a\u67d0\u4f4d\u9ad8\u7ea7\u7ecf\u7406\u4eba\u4e0e\u4e00\u5c0f\u90e8\u5206\u4eba\u7684\u79bb\u804c\u800c\u906d\u53d7\u4ec0\u4e48\u635f\u5931\uff0c\u5e7f\u800c\u544a\u4e4b\u662f\u5fc5\u987b\u7684\uff0c\u4e0d\u7136\u7231\u8fbe\u7684\u52aa\u529b\u6ca1\u4eba\u77e5\u9053\uff0c\u4f46\u662f\u7231\u8fbe\u66f4\u613f\u610f\u5c06\u8d44\u91d1\u82b1\u5728\u5b9e\u5b9e\u5728\u5728\u7684\u5730\u65b9\uff0c\u7231\u8fbe\u7684\u8fd0\u8425\u7406\u5ff5\u662f\u2018\u65b0\u751f\u6d3b\u3001\u65b0\u9009\u62e9\u2019\uff0c\u6539\u5584\u4e00\u4e2a\u4eba\u7684\u751f\u6d3b\uff0c\u6539\u5584\u4e00\u90e8\u5206\u4eba\u7684\u751f\u6d3b\uff0c\u662f\u7231\u8fbe\u7684\u5927\u76ee\u6807\uff0c\u505a\u51fa\u66f4\u597d\u7684\u4ea7\u54c1\uff0c\u505a\u51fa\u66f4\u65b0\u7684\u4ea7\u54c1\uff0c\u5c31\u662f\u6211\u4eec\u6700\u5927\u7684\u52aa\u529b\u65b9\u5411\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6084\u6084\u8ddf\u5085\u4fca\u8bf4\uff1a\u201c\u8001\u82cf\u7684\u5634\u5df4\u5b50\u8fd9\u4e48\u5389\u5bb3\uff0c\u6211\u770b\u5468\u6e38\u6765\u90fd\u672a\u5fc5\u6bd4\u4ed6\u8bf4\u5f97\u66f4\u597d\u3002\u201d</p><p>\u3000\u3000\u5404\u7701\u5e02\u5a92\u4f53\u5f80\u5230\u5317\u4eac\u6765\u53c2\u52a0\u8fd9\u6b21\u62db\u5546\u5927\u4f1a\u7684\u8bb0\u8005\u90fd\u53ef\u4ee5\u8bf4\u662f\u8fd9\u4e2a\u884c\u4e1a\u91cc\u7684\u7cbe\u82f1\uff0c\u7acb\u5373\u5c31\u6709\u4eba\u6293\u4f4f\u82cf\u6d25\u4e1c\u8bdd\u91cc\u7684\u8bdd\u5934\uff0c\u95ee\u9053\uff1a\u201c\u96be\u9053\u7231\u8fbe\u7535\u5b50\u5c31\u4e0d\u60f3\u505a\u51fa\u66f4\u591a\u7684\u4ea7\u54c1\uff0c\u8ba9\u66f4\u591a\u7684\u77e5\u9053\u7231\u8fbe\u7535\u5b50\u7684\u52aa\u529b\u5417\uff1f\u201d\u5b8c\u5168\u4eff\u7167\u82cf\u6d25\u4e1c\u7684\u8bed\u6c14\uff0c\u5927\u5bb6\u90fd\u542c\u4e86\u4e00\u7b11\uff0c\u90fd\u77e5\u9053\u8fd9\u4f4d\u8bb0\u8005\u7684\u8a00\u4e0b\u4e4b\u610f\u662f\u8bf4\uff1a\u7231\u8fbe\u7535\u5b50\u5e94\u8be5\u62ff\u4e0b\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\uff0c\u8ba9\u66f4\u591a\u7684\u4eba\u77e5\u9053\u7231\u8fbe\u7535\u5b50\u7684\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u5728\u5927\u5bb6\u7b49\u82cf\u6d25\u4e1c\u56de\u7b54\u65f6\uff0c\u5f20\u606a\u6a21\u4eff\u8bb0\u8005\u7684\u8bed\u6c14\u95ee\u9053\uff1a\u201c\u82cf\u603b\u662f\u4e0d\u662f\u8ba4\u4e3a\u7f8a\u6bdb\u51fa\u5728\u7f8a\u8eab\u4e0a\uff0c\u7231\u8fbe\u7535\u5b50\u8981\u662f\u638f\u51fa\u51e0\u4e2a\u4ebf\u6765\u4e89\u6807\u738b\uff0c\u8fd9\u7b14\u94b1\u6700\u7ec8\u8fd8\u662f\u7531\u6d88\u8d39\u8005\u627f\u62c5\uff0c\u8fd9\u5bf9\u6d88\u8d39\u8005\u662f\u6781\u4e0d\u516c\u5e73\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u80f8\u524d\u6302\u7740\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u7279\u9080\u5609\u5bbe\u80f8\u724c\uff0c\u8fd9\u80f8\u724c\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u53d1\u7ed9\u5176\u4ed6\u5a92\u4f53\u8bb0\u8005\u7684\u80f8\u724c\u5f88\u50cf\uff0c\u5468\u56f4\u7684\u8bb0\u8005\u90fd\u89c9\u5f97\u8fd9\u53e5\u8bdd\u5f88\u6709\u9053\u7406\uff0c\u90fd\u7eb7\u7eb7\u8bb0\u4e0b\u6765\uff0c\u8bdd\u7b52\u4e0e\u5f55\u97f3\u7b14\u90fd\u8fd8\u5bf9\u7740\u82cf\u6d25\u4e1c\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u5f53\u7136\u4e0d\u4f1a\u6b63\u9762\u80af\u5b9a\u5f20\u606a\u7684\u7591\u95ee\uff0c\u90a3\u7b80\u76f4\u5c31\u662f\u76f4\u63a5\u653b\u51fb\u4e2d\u592e\u7535\u89c6\u53f0\u5bf9\u6807\u738b\u6982\u5ff5\u7684\u7092\u4f5c\uff0c\u4ed6\u53ea\u662f\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u9053\uff1a\u201c\u4f01\u4e1a\u662f\u4ee5\u76c8\u5229\u4e3a\u76ee\u7684\u7684\uff0c\u65e0\u8bba\u662f\u505a\u51fa\u66f4\u597d\u7684\u4ea7\u54c1\uff0c\u8fd8\u662f\u505a\u51fa\u66f4\u65b0\u7684\u4ea7\u54c1\uff0c\u8fd8\u662f\u505a\u51fa\u66f4\u591a\u7684\u4ea7\u54c1\uff0c\u90fd\u662f\u4e3a\u4e86\u5e0c\u671b\u4f01\u4e1a\u80fd\u751f\u5b58\u3001\u80fd\u76c8\u5229\u2026\u2026\u201d</p><p>\u3000\u3000\u81f3\u4e8e\u8bdd\u5916\u7684\u610f\u601d\u5c31\u8981\u8fd9\u4e9b\u5a92\u4f53\u81ea\u5df1\u63e3\u6469\u5ef6\u4f38\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u5728\u63a5\u53d7\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u91cd\u70b9\u91c7\u8bbf\u65f6\uff0c\u4e5f\u6ce8\u610f\u5230\u82cf\u6d25\u4e1c\u5728\u4f1a\u573a\u51fa\u53e3\u5904\u7ed9\u573a\u5916\u8bb0\u8005\u56f4\u6367\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u672c\u8eab\u5bf9\u5a92\u4f53\u7092\u4f5c\u8fd9\u4e00\u5757\u6bd4\u8f83\u719f\u6089\uff0c\u7279\u522b\u4ece\u5218\u660e\u8f89\u90a3\u91cc\u77e5\u9053\u66f4\u591a\u6709\u7528\u7684\u4fe1\u606f\uff0c\u867d\u7136\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u8fd8\u6709\u4e0b\u5348\u534a\u573a\uff0c\u4f46\u662f\u4e2d\u5348\u7684\u91c7\u8bbf\uff0c\u6700\u5feb\u4f1a\u520a\u767b\u5728\u4eca\u5929\u7684\u665a\u62a5\u4e0a\uff0c\u62db\u5546\u5927\u4f1a\u5b8c\u5168\u7ed3\u675f\u4e4b\u540e\u7684\u91c7\u8bbf\u4e2d\u592e\u7535\u89c6\u53f0\u4f1a\u5728\u65b0\u95fb\u91cc\u64ad\u51fa\uff0c\u4f46\u90a3\u662f\u7b80\u5355\u7684\u65b0\u95fb\u7a3f\uff0c\u771f\u6b63\u6709\u503e\u5411\u6027\u7684\u901a\u8baf\u7a3f\u591a\u534a\u8fd8\u662f\u4f1a\u7531\u7eb8\u8d28\u5a92\u4f53\u520a\u767b\uff0c\u90a3\u6700\u5feb\u4e5f\u8981\u7b49\u5230\u660e\u5929\u7684\u6668\u62a5\u3002</p><p>\u3000\u3000\u8981\u662f\u4e00\u4e2a\u5730\u65b9\u4eca\u5929\u7684\u665a\u62a5\u9884\u8bbe\u67d0\u79cd\u7acb\u573a\uff0c\u6bd4\u5982\u8bf4\u5728\u5218\u660e\u8f89\u8df3\u69fd\u4e8b\u4ef6\u4e2d\u540c\u60c5\u7231\u8fbe\u3001\u8c34\u8d23\u79d1\u738b\uff0c\u7531\u4e8e\u5730\u65b9\u4e0a\u7684\u5ba3\u4f20\u53e3\u8fd8\u5904\u4e8e\u7edf\u4e00\u7ba1\u7406\u7684\u72b6\u6001\uff0c\u90a3\u8fd9\u4e2a\u7acb\u573a\u5c31\u5f88\u53ef\u80fd\u4f1a\u88ab\u4fdd\u6301\u4e0b\u53bb\uff0c\u8fd9\u4e2a\u5730\u65b9\u660e\u5929\u7684\u6668\u62a5\u4f1a\u5982\u6b64\uff0c\u79d1\u738b\u8981\u82b1\u5f88\u5927\u7684\u529b\u6c14\u4e5f\u672a\u5fc5\u80fd\u7ea0\u6b63\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u5230\u82cf\u6d25\u4e1c\u5728\u4f1a\u573a\u51fa\u53e3\u5904\u7ed9\u573a\u5916\u8bb0\u8005\u56f4\u6367\uff0c\u5c31\u7acb\u5373\u6709\u8fd9\u6837\u7684\u62c5\u5fe7\uff0c\u4ed6\u53c8\u4e0d\u80fd\u5c06\u592e\u89c6\u7684\u8bb0\u8005\u6487\u4e0b\uff0c\u5c0f\u58f0\u8ba9\u9648\u9759\u51fa\u53bb\u770b\u4e00\u773c\uff0c\u542c\u7740\u82cf\u6d25\u4e1c\u4f1a\u5bf9\u5a92\u4f53\u8bb0\u8005\u8bf4\u4ec0\u4e48\uff0c\u8981\u6709\u4ec0\u4e48\u8fc7\u5206\u7684\u8bdd\uff0c\u4ed6\u4eec\u8981\u7acb\u5373\u53ec\u96c6\u5a92\u4f53\u6f84\u6e05\uff0c\u4e88\u4ee5\u53cd\u51fb\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8eab\u6750\u4e2d\u7b49\uff0c\u7ed9\u56f4\u5728\u4e2d\u95f4\u81ea\u7136\u770b\u4e0d\u5230\u5916\u56f4\u7684\u9648\u9759\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u4e5f\u6ca1\u6709\u770b\u5230\u9648\u9759\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4e4b\u524d\u5bf9\u8bb0\u8005\u7684\u56de\u7b54\u867d\u7136\u6709\u4e9b\u503e\u5411\uff0c\u6709\u4e9b\u6697\u793a\uff0c\u4e0d\u8fc7\u8fd8\u7b97\u514b\u5236\uff0c\u5f20\u606a\u5192\u5145\u8bb0\u8005\u7684\u90a3\u53e5\u95ee\u8bdd\uff0c\u8ba9\u9648\u9759\u73b2\u73d1\u5254\u900f\u7684\u9f3b\u6881\u5dee\u70b9\u6c14\u6b6a\u4e86\uff0c\u8fd9\u660e\u663e\u662f\u5bf9\u79d1\u738b\u7684\u653b\u51fb\uff1a\u6240\u8c13\u7f8a\u6bdb\u51fa\u5728\u7f8a\u8eab\u4e0a\uff0c\u79d1\u738b\u82b13.5\u4ebf\u62ff\u4e0b\u592e\u89c6\u7684\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\uff0c\u8fd93.5\u4ebf\u6700\u7ec8\u8fd8\u5f97\u8981\u6d88\u8d39\u8005\u627f\u62c5\uff01</p><p>\u3000\u3000\u8981\u662f\u771f\u6709\u8bb0\u8005\u8fd9\u4e48\u95ee\uff0c\u9648\u9759\u6216\u8bb8\u4f1a\u8d5e\u540c\uff0c\u504f\u504f\u662f\u5f20\u606a\u5728\u90a3\u91cc\u7f16\u8bdd\uff0c\u9648\u9759\u6697\u81ea\u5550\u4e86\u4e00\u53e3\uff1a\u5c0f\u4eba\uff01\u63a5\u4e0b\u6765\u7684\u8bdd\u5c31\u6ca1\u6709\u5fc5\u8981\u542c\u4e86\uff0c\u5979\u8f6c\u8eab\u8d70\u56de\u4f1a\u573a\uff0c\u5012\u662f\u5916\u56f4\u7684\u8bb0\u8005\u8ba4\u51fa\u5979\u662f\u79d1\u738b\u7684\u5382\u5546\u4ee3\u8868\u4e4b\u4e00\uff0c\u82cf\u6d25\u4e1c\u6b63\u8bb2\u5f97\u7cbe\u5f69\uff0c\u6ca1\u6709\u4eba\u60f3\u7740\u7acb\u5373\u53bb\u6293\u4f4f\u5979\u91c7\u8bbf\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u7ec6\u8282\u5012\u53ef\u4ee5\u6e32\u67d3\u4e00\u4e0b\uff0c\u53ef\u89c1\u4e24\u5bb6\u4f01\u4e1a\u6709\u76f8\u5f53\u6df1\u7684\u5bbf\u6028\uff0c\u5373\u4f7f\u5bf9\u65b9\u5728\u63a5\u53d7\u91c7\u8bbf\u65f6\u4e5f\u6df1\u6df1\u6212\u5907\u7740\u3002</p><p>\u3000\u3000\u9648\u9759\u8d70\u56de\u4f1a\u573a\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u624d\u6709\u770b\u5230\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u9000\u51fa\u6765\uff0c\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u505a\u4e86\u4e00\u56de\u5c0f\u4eba\uff1f\u201d\u53c8\u7b11\u9053\uff0c\u201c\u5f97\uff0c\u53c8\u4e0d\u662f\u4e00\u56de\u4e24\u56de\u4e86\u3002\u201d</p><p>\u3000\u3000\u592e\u89c6\u8bb0\u8005\u7684\u91c7\u8bbf\u7ed3\u675f\u4e4b\u540e\uff0c\u9648\u9759\u5c06\u82cf\u6d25\u4e1c\u3001\u5f20\u606a\u80cc\u540e\u9634\u4eba\u7684\u4e8b\u60c5\u8bf4\u7ed9\u8c22\u5251\u5357\u542c\uff0c\u4ed6\u542c\u540e\u76b1\u7740\u7709\u5934\uff0c\u5218\u660e\u8f89\u8bf4\u9053\uff1a\u201c\u7231\u8fbe\u6216\u8bb8\u80fd\u63a7\u5236\u4e00\u4e24\u5bb6\u62a5\u793e\u4e71\u5199\u6587\u7ae0\uff0c\u5176\u4ed6\u5730\u65b9\u5a92\u4f53\u5e94\u8be5\u4f1a\u4e0e\u592e\u89c6\u4fdd\u6301\u9ad8\u5ea6\u4e00\u81f4\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u7740\u573a\u5916\u7684\u8bb0\u8005\u4f3c\u4e4e\u7b49\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u8d70\u51fa\u53bb\uff0c\u867d\u7136\u77e5\u9053\u4f1a\u906d\u5230\u4e00\u4e9b\u8d28\u7591\uff0c\u4e0d\u8fc7\u5fc5\u987b\u8d70\u51fa\u53bb\uff0c\u4e0d\u8981\u4eca\u5929\u5404\u5730\u5a92\u4f53\u7684\u665a\u62a5\u5c40\u9762\u8fd8\u662f\u4f1a\u5b58\u5728\u4e00\u5b9a\u7684\u98ce\u9669\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u770b\u5230\u8c22\u5251\u5357\u4ed6\u4eec\u4e00\u884c\u4eba\u8d70\u8fc7\u6765\uff0c\u4ed6\u5bf9\u5a92\u4f53\u8bb0\u8005\u4eec\u7b11\u7740\u8bf4\uff1a\u201c\u79d1\u738b\u7684\u5382\u5546\u4ee3\u8868\u624d\u5e94\u8be5\u662f\u4eca\u5929\u7684\u4e3b\u89d2\uff0c\u4f60\u4eec\u770b\u662f\u4e0d\u662f\u653e\u6211\u4eec\u51fa\u53bb\u5403\u4e2d\u996d\uff1f\u201d</p><p>\u3000\u3000\u8bb0\u8005\u4eec\u8005\u5bbd\u5b8f\u5927\u8c05\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u4e0d\u7136\u4ed6\u4eec\u81ea\u5df1\u4e5f\u4f1a\u9677\u5165\u77db\u76fe\uff1a\u662f\u76ef\u7740\u82cf\u6d25\u4e1c\u7ee7\u7eed\u6df1\u6316\u4e0b\u53bb\uff0c\u8fd8\u662f\u8ddf\u7740\u8fc7\u53bb\u91c7\u8bbf\u79d1\u738b\u7684\u5382\u5546\u4ee3\u8868\uff1f</p><p>\u3000\u3000\u770b\u7740\u8bb0\u8005\u8702\u62e5\u800c\u6765\uff0c\u8c22\u5251\u5357\u867d\u7136\u6709\u4e9b\u75b2\u60eb\uff0c\u8fd8\u5f88\u4eb2\u5207\u7684\u7b11\u4e86\u7b11\uff0c\u5f53\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u4e0e\u5085\u4fca\u4e5f\u5939\u6742\u5728\u8bb0\u8005\u7fa4\u91cc\u56f4\u8fc7\u6765\uff0c\u8138\u8272\u5c31\u6709\u4e9b\u6302\u4e0d\u4f4f\u3002\u679c\u7136\uff0c\u7b2c\u4e09\u4e2a\u95ee\u9898\uff0c\u5f20\u606a\u5c31\u62a2\u5230\u673a\u4f1a\u53d1\u96be\uff1a\u201c\u542c\u8bf4\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u9ad8\u7ea7\u7ecf\u7406\u4eba\u5218\u660e\u8f89\u5148\u751f\u7387\u9886\u5e02\u573a\u90e8\u4e00\u7fa4\u9ad8\u7ea7\u9500\u552e\u4e3b\u7ba1\u6295\u5954\u79d1\u738b\uff0c\u79d1\u738b\u5728\u593a\u5f97\u592e\u89c6\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\u4e4b\u65f6\uff0c\u4f1a\u4e0d\u4f1a\u5229\u7528\u5218\u660e\u8f89\u4ee5\u53ca\u90a3\u4e9b\u9ad8\u7ea7\u9500\u552e\u4e3b\u7ba1\uff0c\u76f4\u63a5\u5c06\u7231\u8fbe\u7535\u5b50\u5728\u5404\u5730\u7684\u6e20\u9053\u5546\u62a2\u8fc7\u53bb\uff1f\u5c31\u6211\u76f4\u89c9\u800c\u8a00\uff0c\u79d1\u738b\u593a\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u540e\uff0c\u76f4\u63a5\u62a2\u5360\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u573a\u4efd\u989d\u662f\u6700\u4e3a\u4fbf\u5229\u7684\uff0c\u6240\u8c13\u6345\u4eba\u4e00\u5200\u4e0d\u59a8\u518d\u6345\u4e00\u5200\uff0c\u79d1\u738b\u4f1a\u4e0d\u4f1a\u8fd9\u4e48\u505a\uff1f\u201d</p><p>\u3000\u3000\u5218\u660e\u8f89\u7f9e\u6127\u96be\u5f53\uff0c\u4f4e\u4e0b\u5934\u4e0d\u6562\u770b\u5f20\u606a\u9510\u5229\u3001\u6df1\u9083\u7684\u773c\u775b\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u773c\u775b\u4e00\u655b\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u8fd9\u4f4d\u8bb0\u8005\u670b\u53cb\u662f\u54ea\u5bb6\u5a92\u4f53\u7684\uff0c\u4f60\u7684\u80f8\u724c\u6211\u770b\u4e0d\u8fc7\u6e05\u695a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u80f8\u724c\u7ffb\u8fc7\u6765\uff1a\u201c\u6211\u662f\u5efa\u90ba\u6668\u62a5\u7684\u8bb0\u8005\u7b80\u5fd7\u5eb7\uff0c\u8bf7\u8c22\u5148\u751f\u5bf9\u6b64\u6709\u4ec0\u4e48\u7591\u95ee\uff1f\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u7684\u540c\u5b66\u7b80\u5fd7\u5eb7\uff08\u4e4b\u524d\u5199\u7701\u7ec4\u7ec7\u90e8\u957f\u7b80\u5fd7\u5eb7\u662f\u4e3a\u7b14\u8bef\uff09\u4f5c\u4e3a\u5efa\u90ba\u6668\u62a5\u7684\u7279\u6d3e\u8bb0\u8005\u73b0\u5728\u5374\u4e0e\u4ed6\u7684\u540c\u4e8b\u88ab\u8feb\u53bb\u6e38\u89c8\u6545\u5bab\u4e86\uff0c\u597d\u5728\u5f20\u606a\u7b54\u5e94\u7ed9\u4ed6\u4e00\u7bc7\u4e13\u8bbf\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u901a\u8baf\u7a3f\u4e5f\u8ba9\u4ed6\u4eec\u5148\u53d1\uff0c\u603b\u6bd4\u6df7\u5728\u4e00\u7fa4\u8bb0\u8005\u540e\u9762\u62fe\u4eba\u7259\u6167\u8981\u597d\u4e00\u4e9b\u3002\u8fd9\u65f6\u5019\u8ddf\u8fc7\u6765\uff0c\u90fd\u672a\u5fc5\u80fd\u62a2\u5230\u95ee\u8bdd\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5f53\u7136\u4e0d\u60f3\u73b0\u573a\u6f14\u53d8\u6210\u4e00\u51fa\u95f9\u5267\uff0c\u5f20\u606a\u7684\u6405\u5c40\u4e0e\u6311\u8845\u7684\u95ee\u8bdd\uff0c\u8ba9\u5176\u4ed6\u8bb0\u8005\u4e5f\u89c9\u5f97\u5f88\u75db\u5feb\uff0c\u90fd\u6309\u5948\u4f4f\u7b49\u8c22\u5251\u5357\u56de\u7b54\uff0c\u4e0d\u63d0\u95ee\u65b0\u7684\u95ee\u9898\uff0c\u8c22\u5251\u5357\u80f8\u53e3\u5835\u7740\u4e00\u53e3\u6c14\uff0c\u4f46\u662f\u4ed6\u505a\u5f97\u4e86\u521d\u4e00\uff0c\u5c31\u5f97\u5141\u8bb8\u522b\u4eba\u505a\u5341\u4e94\uff0c\u8fd8\u662f\u5316\u89e3\u773c\u91cc\u7684\u7a98\u5883\u6700\u91cd\u8981\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5e73\u9759\u81ea\u5df1\u7684\u60c5\u7eea\uff0c\u5f88\u5feb\u7684\u7ec4\u7ec7\u8d77\u8bed\u8a00\uff0c\u8bf4\u9053\uff1a\u201c\u79d1\u738b\u4e0e\u7231\u8fbe\u90fd\u662f\u6d77\u5dde\u5e02\u653f\u5e9c\u91cd\u70b9\u6276\u6301\u7684\u6c11\u8425\u4f01\u4e1a\uff0c\u6d77\u5dde\u5e02\u653f\u5e9c\u4ece\u6765\u90fd\u662f\u9f13\u52b1\u4e24\u5bb6\u4f01\u4e1a\u516c\u5e73\u7ade\u4e89\u3001\u79ef\u6781\u7ade\u4e89\u7684\uff0c\u8fd9\u4e5f\u6709\u5229\u4e8e\u641e\u6d3b\u5730\u65b9\u7ecf\u6d4e\uff0c\u4fc3\u8fdb\u4f01\u4e1a\u7ade\u4e89\u529b\u6c34\u5e73\u7684\u63d0\u9ad8\uff0c\u7ade\u4e89\u4ece\u6765\u90fd\u4e0d\u662f\u574f\u4e8b\u60c5\uff01\u8fd9\u4e2a\u8bb0\u8005\u670b\u53cb\u7684\u95ee\u9898\uff0c\u6211\u53ea\u80fd\u56de\u7b54\uff0c\u79d1\u738b\u4f1a\u5728\u4e00\u4e2a\u5408\u6cd5\u3001\u5408\u4e4e\u5f53\u524d\u5546\u4e1a\u89c4\u5219\u7684\u5e73\u53f0\u4e0a\u53bb\u8ddf\u7231\u8fbe\u7535\u5b50\u7ade\u4e89\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u5f71\u789f\u673a\u884c\u4e1a\u505a\u5f97\u5f88\u51fa\u8272\uff0c\u79d1\u738b\u8981\u8fce\u5934\u8d76\u4e0a\uff0c\u5f53\u7136\u4f1a\u5c06\u7231\u8fbe\u7535\u5b50\u4f5c\u4e3a\u81ea\u5df1\u8ffd\u9010\u7684\u76ee\u6807\uff0c\u6211\u4eec\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u5c31\u5728\u4e00\u5e74\u4e4b\u524d\uff0c\u7231\u8fbe\u7535\u5b50\u8fd8\u662f\u4e00\u5bb6\u89c4\u6a21\u5f88\u5c0f\u7684\u7535\u5b50\u4f01\u4e1a\uff0c\u8fd9\u4e5f\u4f7f\u6211\u4eec\u5728\u8d85\u8d8a\u7231\u8fbe\u7684\u4fe1\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u7684\u53e3\u624d\u5012\u662f\u4e00\u6d41\u7684\uff0c\u8fd8\u62ff\u5468\u5bcc\u660e\u6765\u538b\u4eba\uff0c\u89c1\u5176\u4ed6\u8bb0\u8005\u90fd\u5dee\u70b9\u7ed9\u4ed6\u7684\u8bdd\u8ff7\u7cca\u4f4f\uff0c\u5f20\u606a\u4e0d\u5931\u65f6\u673a\u7684\u53c8\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u8c22\u603b\u8ba4\u4e3a\u5728\u5173\u952e\u65f6\u523b\u901a\u8fc7\u6316\u4eba\u6765\u83b7\u5f97\u7ade\u4e89\u5bf9\u624b\u5546\u4e1a\u4fe1\u606f\uff0c\u901a\u8fc7\u6316\u4eba\u6765\u62a2\u593a\u7ade\u4e89\u5bf9\u624b\u7684\u5e02\u573a\u8d44\u6e90\u662f\u5408\u4e4e\u5f53\u524d\u5546\u4e1a\u9053\u5fb7\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u8bb0\u8005\u670b\u53cb\u7684\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e0e\u524d\u4e00\u4e2a\u95ee\u9898\u6ca1\u6709\u4ec0\u4e48\u533a\u522b\uff0c\u8bf7\u4e0b\u4e00\u4e2a\u8bb0\u8005\u670b\u53cb\u63d0\u95ee\u3002\u201d\u8c22\u5251\u5357\u4e0d\u518d\u4e0e\u5f20\u606a\u7ea0\u7f20\uff0c\u76f4\u63a5\u5c06\u4ed6\u5ffd\u89c6\u6389\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u7ea0\u7f20\uff0c\u5728\u9648\u9759\u770b\u6765\u591a\u5c11\u6709\u4e9b\u65e0\u7406\u53d6\u95f9\u4e0e\u6405\u5c40\u7684\u610f\u5473\uff0c\u5979\u90a3\u53cc\u6781\u7f8e\u7684\u674f\u76ee\u77aa\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5374\u6beb\u4e0d\u4e3a\u610f\u7684\u7b11\u4e86\u7b11\uff0c\u660e\u4fca\u7684\u8138\u4e0a\u6302\u7740\u5982\u6b64\u4e0d\u4e3a\u610f\u7684\u7b11\u5bb9\uff0c\u6709\u7740\u4e00\u79cd\u7279\u522b\u90aa\u9b45\u7684\u5473\u9053\uff0c\u4ed6\u7ad9\u5728\u8fd9\u91cc\uff0c\u5012\u4e0d\u662f\u4e3a\u4e86\u6405\u5c40\uff0c\u800c\u8981\u8b66\u544a\u8c22\u5251\u5357\uff0c\u4ed6\u4eec\u8981\u901a\u8fc7\u5218\u660e\u8f89\u7b49\u4eba\u7684\u5173\u7cfb\u5c06\u534e\u5357\u3001\u534e\u4e2d\u7b49\u5730\u7684\u6e20\u9053\u5546\u62c9\u8fc7\u53bb\uff0c\u7231\u8fbe\u7535\u5b50\u4e0d\u4f1a\u5750\u89c6\u4e0d\u7406\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5085\u4fca\u8f6c\u8eab\u9000\u5230\u8bb0\u8005\u7fa4\u7684\u5916\u56f4\uff0c\u7b49\u5176\u4ed6\u8bb0\u8005\u6ce8\u610f\u90fd\u653e\u5230\u8c22\u5251\u5357\u7b49\u4eba\u8eab\u4e0a\uff0c\u624d\u7a7f\u8fc7\u4f1a\u573a\u5916\u7684\u5927\u5385\uff0c\u62d0\u5165\u697c\u68af\uff0c\u4e0e\u82cf\u6d25\u4e1c\u4ed6\u4eec\u6c47\u5408\u53bb\u5403\u4e2d\u996d\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5012\u662f\u4e00\u76f4\u770b\u7740\u5f20\u606a\u4ed6\u4eec\u79bb\u5f00\uff0c\u5f20\u606a\u770b\u4f3c\u6405\u5c40\u7684\u8d23\u95ee\uff0c\u6b63\u662f\u4ed6\u5fc3\u91cc\u7684\u6253\u7b97\u3002</p><p>\u3000\u3000\u5f71\u789f\u673a\u5e02\u573a\uff0c\u7231\u8fbe\u5360\u636e40\uff05\u7684\u5e02\u573a\u4efd\u989d\uff0c\u65b0\u79d1\u4e3a20\uff05\uff0c\u6b65\u6b65\u9ad8\u4e3a12\uff05\uff0c\u79d1\u738b\u6b64\u65f6\u624d2\uff05\uff0c\u4ece2\uff05\u63d0\u5230\u4e0e\u592e\u89c6\u6807\u738b\u76f8\u5339\u914d\u7684\u5e02\u573a\u4efd\u989d\uff0c\u81ea\u7136\u4e0d\u80fd\u7b49\u7740\u5f71\u789f\u673a\u5e02\u573a\u7684\u81ea\u7136\u589e\u6da8\uff0c\u867d\u7136\u79d1\u738b\u53ef\u4ee5\u5efa\u7acb\u81ea\u5df1\u7684\u8425\u9500\u6e20\u9053\uff0c\u4f46\u662f\u6700\u8fc5\u901f\u3001\u6700\u4fbf\u6377\u7684\u65b9\u6cd5\u5c31\u662f\u501f\u52a9\u7231\u8fbe\u7535\u5b50\u5df2\u7ecf\u6210\u719f\u7684\u6e20\u9053\u3002</p><p>\u3000\u3000\u5728\u5e02\u573a\u8fd0\u4f5c\u4e2d\uff0c\u6e20\u9053\u5546\u7684\u9009\u62e9\u662f\u6781\u5176\u91cd\u8981\u7684\u4e00\u73af\uff0c\u6bd5\u7adf\u6e20\u9053\u5546\u51b3\u5b9a\u7740\u7ec8\u7aef\u7684\u63a8\u9500\u80fd\u529b\uff0c\u8fd9\u5f80\u5f80\u662f\u5382\u5546\u5f88\u96be\u63a7\u5236\u7684\u4e00\u73af\uff0c\u4e00\u5bb6\u6709\u5b9e\u529b\u3001\u6709\u7ecf\u9a8c\u7684\u6e20\u9053\u5546\u5728\u94fa\u8d27\u529b\u5ea6\u3001\u7ec8\u7aef\u5ba3\u4f20\u529b\u5ea6\u4e0e\u6c34\u51c6\u4e0a\u90fd\u662f\u503c\u5f97\u4fe1\u8d56\u7684\u3002</p><p>\u3000\u3000\u4e00\u822c\u8bf4\u6765\uff0c\u7231\u8fbe\u4e0e\u79d1\u738b\u90fd\u4e0d\u53ef\u80fd\u7981\u6b62\u65d7\u4e0b\u7684\u6e20\u9053\u5546\u53bb\u4ee3\u7406\u522b\u5bb6\u7684\u4ea7\u54c1\uff08\u5927\u533a\u6216\u7701\u7ea7\u7684\u6e20\u9053\u5546\u9664\u5916\uff09\uff0c\u4f46\u662f\u79d1\u738b\u82e5\u501f\u9274\u7231\u8fbe\u7535\u5b50\u5411\u7ecf\u9500\u5546\u6536\u53d6\u9ad8\u989d\u4fdd\u8bc1\u91d1\u7684\u505a\u6cd5\uff0c\u4e24\u8005\u4e4b\u95f4\u5c31\u4f1a\u51fa\u73b0\u6392\u62c6\u3002\u9ad8\u989d\u4fdd\u8bc1\u91d1\u8ba9\u4e00\u822c\u7ecf\u9500\u5546\u5f88\u96be\u540c\u65f6\u9009\u62e9\u4ee3\u7406\u4e24\u5bb6\u7684\u4ea7\u54c1\uff0c\u4e00\u662f\u7ecf\u6d4e\u5b9e\u529b\u4e0a\u7684\u9650\u5236\uff0c\u4e00\u662f\u6e20\u9053\u5546\u5b9e\u9645\u8fd0\u4f5c\u8d77\u4f1a\u5341\u5206\u56f0\u96be\uff0c\u5168\u5fc3\u5168\u610f\u505a\u4e00\u79cd\u4ea7\u54c1\u5bb9\u6613\uff0c\u5206\u5fc3\u505a\u4e24\u79cd\u4ea7\u54c1\uff0c\u5c31\u4f1a\u51fa\u73b0\u77db\u76fe\u3002</p><p>\u3000\u3000\u5982\u679c\u90fd\u8981\u9080\u5de8\u989d\u4fdd\u8bc1\u91d1\uff0c\u6e20\u9053\u5546\u6743\u8861\u5229\u5f0a\uff0c\u81ea\u7136\u53ea\u4f1a\u9009\u62e9\u4e00\u5bb6\u83b7\u5229\u6700\u4e30\u7684\u4ea7\u54c1\u4ee3\u7406\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u8fd9\u4e9b\u4eba\u8df3\u69fd\u8fc7\u6765\u6700\u5927\u7684\u4ef7\u503c\uff0c\u5728\u8c22\u5251\u5357\u770b\u6765\uff0c\u8fd8\u662f\u53ef\u4ee5\u901a\u8fc7\u4ed6\u4eec\u5c06\u7231\u8fbe\u7535\u5b50\u5df2\u7ecf\u5efa\u8bbe\u6210\u719f\u7684\u6e20\u9053\u8d44\u6e90\u636e\u4e3a\u5df1\u7528\uff0c\u5f53\u7136\u4ec5\u9760\u4ed6\u4eec\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u4f46\u662f\u52a0\u4e0a\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u8fd9\u4e00\u6761\uff0c\u90a3\u5c31\u5927\u6709\u53ef\u4e3a\u3002</p><p>\u3000\u3000\u4ece\u7231\u8fbe\u83b7\u53d6\u90e8\u5206\u6e20\u9053\u5546\uff0c\u4f1a\u7834\u574f\u7231\u8fbe\u6e20\u9053\u7f51\u7edc\u7684\u5b8c\u6574\u6027\u8fd8\u662f\u5176\u6b21\uff0c\u6e20\u9053\u5546\u7684\u6d41\u5931\uff0c\u5df2\u7ecf\u6536\u9080\u4e0a\u6765\u7684\u4fdd\u8bc1\u91d1\u5fc5\u987b\u9000\u8fd8\uff0c\u4f1a\u4f7f\u7231\u8fbe\u7535\u5b50\u7684\u6d41\u52a8\u8d44\u91d1\u53d7\u5230\u538b\u7f29\uff0c\u4ece\u800c\u5f15\u8d77\u4e00\u7cfb\u5217\u4e0d\u826f\u7684\u5faa\u73af\u53cd\u5e94\uff0c\u8fd9\u624d\u662f\u8c22\u5251\u5357\u4e50\u4e8e\u770b\u5230\u5c40\u9762\u3002</p><p>\u3000\u3000\u8fd9\u672c\u6765\u5c31\u662f\u4e00\u5f00\u59cb\u5c31\u5c16\u9510\u5f97\u65e0\u6cd5\u8c03\u548c\u7684\u77db\u76fe\uff0c\u5f20\u606a\u7684\u53cd\u5e94\uff0c\u5012\u4e5f\u8ba9\u8c22\u5251\u5357\u77e5\u9053\u5f20\u606a\u5f88\u6e05\u695a\u4e8b\u60c5\u7684\u540e\u679c\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u91cc\u60f3\uff1a\u8981\u6765\u5c31\u6765\u5427\u3002\u4f46\u662f\u8c22\u5251\u5357\u4e5f\u89c9\u5f97\u5f88\u5947\u602a\uff1a\u7231\u8fbe\u7535\u5b50\u4e0a\u5348\u4e3a\u4ec0\u4e48\u6ca1\u6709\u4e3e\u724c\uff0c\u7231\u8fbe\u7535\u5b50\u5c31\u7b97\u4e0d\u4e89\u6807\u738b\uff0c\u4f46\u662f\u80fd\u6324\u8fdb\u7535\u5b50\u699c\u524d\u4e09\uff0c\u5bf9\u7a33\u5b9a\u4ed6\u4eec\u7684\u6e20\u9053\u5546\u7f51\u7edc\u6709\u7740\u975e\u6bd4\u5bfb\u5e38\u7684\u610f\u4e49\u3002\u8fd8\u662f\u8bf4\u4ed6\u4eec\u5728\u653e\u5f03\u4e4b\u540e\u624d\u610f\u8bc6\u6e20\u9053\u5546\u8d44\u6e90\u6709\u53ef\u80fd\u88ab\u62a2\u5360\u7684\u540e\u679c\uff1f\u8fd9\u4e00\u70b9\u4f3c\u4e4e\u66f4\u4e0d\u53ef\u80fd\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e2d\u592e\u7535\u89c6\u53f0\u9644\u8fd1\u7684\u9910\u9986\u6089\u5df2\u4eba\u6ee1\u4e3a\u60a3\uff0c\u5f20\u606a\u4ed6\u4eec\u8d70\u4e86\u597d\u51e0\u5bb6\uff0c\u90fd\u8fd8\u5728\u8def\u4e0a\u6643\u8361\uff0c\u9644\u8fd1\u4e00\u5bb6\u80af\u5fb7\u57fa\uff0c\u7b49\u4f4d\u5b50\u7684\u4e0d\u6bd4\u5750\u7740\u7684\u4eba\u5c11\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u4e0d\u803d\u6401\u4e0b\u5348\u7684\u7ade\u6807\uff0c\u4e5f\u4e0d\u4f1a\u5750\u8f66\u8d70\u8fdc\uff0c\u8fd9\u9644\u8fd1\u7ecf\u5e38\u5835\u8f66\uff0c\u7ed9\u5835\u5728\u5916\u9762\u8fdb\u4e0d\u6765\uff0c\u90a3\u8fd8\u8981\u7528\u4e24\u6761\u817f\u8dd1\u56de\u6765\uff0c\u592a\u4e0d\u5212\u7b97\u3002\u4ece\u80af\u5fb7\u57fa\u4e70\u6765\u5916\u5356\uff0c\u5c31\u5750\u5728\u5916\u9762\u7684\u53f0\u9636\u4e0a\u51c6\u5907\u8981\u5403\u8d77\u6765\uff0c\u5341\u4e00\u6708\u7684\u5317\u4eac\u5df2\u7ecf\u6709\u4e9b\u6e05\u5bd2\uff0c\u5927\u4e2d\u5348\uff0c\u592a\u9633\u7ed9\u4e91\u906e\u4f4f\uff0c\u5317\u4eac\u7684\u98ce\u6c99\u53c8\u5927\uff0c\u5750\u5728\u5ba4\u5916\u53f0\u9636\u4e0a\u5403\u996d\uff0c\u8fd8\u771f\u4e0d\u662f\u597d\u4eab\u53d7\u3002</p><p>\u3000\u3000\u5012\u662f\u665a\u6674\u4e0e\u8c2d\u5e0c\u677e\u5403\u8fc7\u5348\u9910\u540e\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8ba9\u4ed6\u4eec\u53bb\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5c0f\u9910\u5385\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff1a\u201c\u521a\u624d\u5c0f\u5c0f\u7684\u6bc1\u4e86\u4e00\u628a\u4e2d\u592e\u7535\u89c6\u53f0\u2026\u2026\u201d\u8d28\u7591\u592e\u89c6\u6807\u738b\u7684\u610f\u4e49\u4e0e\u4ef7\u503c\uff0c\u4e0e\u5c0f\u6bc1\u4e00\u628a\u592e\u89c6\u76f8\u5f53\uff0c\u5f20\u606a\u867d\u7136\u5634\u91cc\u8fd9\u4e48\u8bf4\uff0c\u5374\u722c\u8d77\u6765\u5c31\u5f80\u4e2d\u592e\u7535\u89c6\u53f0\u91cc\u9762\u8d70\uff0c\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u91cc\u592e\u89c6\u9ad8\u5c42\u6240\u4eab\u53d7\u7684\u5c0f\u9910\u5385\uff0c\u90a3\u611f\u89c9\u8fd8\u4e0d\u662f\u4e00\u822c\u7684\u723d\u5229\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4ed6\u4eec\u5e94\u8be5\u4e5f\u5728\u90a3\u91cc\u5403\u996d\uff0c\u8981\u80fd\u7ee7\u7eed\u788d\u4ed6\u7684\u773c\uff0c\u5f20\u606a\u8fd8\u662f\u4e50\u610f\u505a\u7684\u3002</p><p>\u3000\u3000\u5230\u5c0f\u9910\u5385\u4e4b\u540e\uff0c\u8c22\u5251\u5357\u3001\u9648\u9759\u7b49\u4eba\u4ee5\u53ca\u5176\u4ed6\u7ade\u4e0b\u65b0\u95fb\u8054\u64ad\u4e4b\u540e\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7684\u5341\u4e8c\u5bb6\u5382\u5546\u4ee3\u8868\u90fd\u5728\u5c0f\u9910\u5385\u91cc\u7528\u9910\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u7528\u8fc7\u9910\uff0c\u5979\u4e0e\u54c1\u724c\u8fd0\u8425\u90e8\u7ecf\u7406\u5218\u6d77\u71d5\u5728\u90a3\u91cc\u7b49\u5f20\u606a\u4ed6\u4eec\u8fc7\u6765\u5403\u996d\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u76ee\u793a\u966a\u8c22\u5251\u5357\u4ed6\u4eec\u540c\u684c\u5403\u996d\u7684\u4e00\u540d\u4e2d\u5e74\u80d6\u5b50\u662f\u592e\u89c6\u5206\u7ba1\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u526f\u53f0\u957f\uff0c\u526f\u90e8\u7ea7\u7684\u4eba\u7269\uff0c\u8ddf\u53bb\u5e74\u4e0d\u662f\u540c\u4e00\u4eba\u3002</p><p>\u3000\u3000\u592e\u89c6\u4e0d\u4f1a\u541d\u556c\u4e00\u9910\u5348\u996d\u90fd\u4e0d\u4f9b\u5e94\uff0c\u8c2d\u5e0c\u677e\u79bb\u5f00\u4e4b\u524d\u6253\u8fc7\u62db\u547c\uff0c\u5f20\u606a\u4ed6\u4eec\u9009\u4e86\u6bd4\u8f83\u504f\u50fb\u7684\u957f\u6761\u684c\u4f9d\u6b21\u5750\u4e0b\uff0c\u5f20\u606a\u62db\u624b\u8ba9\u5218\u6d77\u71d5\u4e0e\u8d75\u6d77\u821f\u5934\u51d1\u8fc7\u6765\uff1a\u201c\u5403\u8fc7\u996d\uff0c\u4f60\u4eec\u4fe9\u5e2e\u6211\u53bb\u4f7f\u4e2a\u574f\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u5bf9\u5f20\u606a\u7684\u76f4\u767d\u9887\u4e3a\u65e0\u5948\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4e86\u8ba9\u8c22\u5bb6\u4eba\u5fc3\u75db\uff0c\u6211\u53ef\u4e5f\u662f\u65e0\u6240\u4e0d\u7528\u5176\u6781\u554a\uff0c\u201d\u5f20\u606a\u5b09\u76ae\u7b11\u8138\u7684\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u53bb\u5e74\u593a\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u540e\u8fd8\u4e0e\u592e\u89c6\u8fdb\u884c\u8c08\u5224\u7684\u4e8b\u60c5\uff0c\u4f60\u4eec\u53bb\u7ed9\u6211\u5077\u5077\u6478\u6478\u7684\u6cc4\u9732\u51fa\u53bb\uff0c\u4e0d\u8981\u8ba9\u8001\u5b9e\u4eba\u5403\u4e86\u4e8f\u2026\u2026\u201d</p><p>\u3000\u3000\u79d1\u738b\u7838\u4e0b3.5\u4ebf\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u53ea\u4e3a\u4e00\u67615\u79d2\u5e7f\u544a\uff0c\u60f3\u5fc5\u8c22\u5251\u5357\u4ece\u5218\u660e\u8f89\u90a3\u91cc\u5145\u5206\u4e86\u89e3\u5230\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u7684\u8c08\u5224\u7ecf\u9a8c\uff0c\u5df2\u7ecf\u5145\u5206\u505a\u597d\u4e0e\u592e\u89c6\u8ba8\u4ef7\u8fd8\u4ef7\u7684\u51c6\u5907\u3002\u592e\u89c6\u8fd9\u6b21\u53ea\u5c06\u9ec4\u91d1\u65f6\u6bb5\u7684\u5e7f\u544a\u8d44\u6e90\u62ff\u51fa\u6765\u7ade\u6807\uff0c\u5c31\u662f\u505a\u597d\u5c06\u4e00\u4e9b\u6b21\u8981\u5e7f\u544a\u65f6\u6bb5\u8865\u507f\u7ed9\u51fa\u53bb\u7684\u51c6\u5907\uff0c\u4e0d\u4ec5\u53ea\u7ed9\u79d1\u738b\u4e00\u5bb6\uff0c\u5176\u4ed6\u4ee5\u9ad8\u4ef7\u7ade\u4e0b\u5176\u4ed6\u5341\u4e8c\u6761\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7684\u5382\u5546\u90fd\u4f1a\u6709\u4e00\u5b9a\u7684\u8865\u507f\u3002</p><p>\u3000\u3000\u592e\u89c6\u62ff\u51fa\u6765\u8fdb\u884c\u8865\u507f\u7684\u6b21\u8981\u5e7f\u544a\u65f6\u6bb5\u4e5f\u4e0d\u662f\u65e0\u9650\u5236\u7684\uff0c\u8fd9\u5bb6\u591a\u4e86\uff0c\u90a3\u5bb6\u5c31\u5c11\uff0c\u79d1\u738b\u591a\u4e86\uff0c\u90a3\u5269\u4e0b\u5341\u4e8c\u5bb6\u5c31\u5c11\u4e86\uff0c\u8fd8\u8981\u770b\u5404\u81ea\u8c08\u5224\u7684\u529f\u5e95\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u6709\u4e0a\u9762\u4eba\u6279\u6761\u5b50\u3001\u6253\u62db\u547c\u4e4b\u7c7b\u7684\u52a8\u4f5c\u3002</p><p>\u3000\u3000\u5f20\u606a\u552f\u6050\u5176\u4ed6\u5341\u4e8c\u5bb6\u5382\u5546\u4e0d\u77e5\u9053\u771f\u5b9e\u60c5\u51b5\uff0c\u6240\u4ee5\u8ba9\u5218\u6d77\u71d5\u3001\u8d75\u6d77\u821f\u5077\u5077\u6478\u6478\u7684\u5c06\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u8ba9\u8fd9\u5341\u4e8c\u5bb6\u5382\u5546\u72e0\u72e0\u7684\u8ddf\u79d1\u738b\u4e89\u4e00\u4e89\u3002</p><p>\u3000\u3000\u5f53\u8c22\u5251\u5357\u81ea\u4ee5\u4e3a\u4ece\u5218\u660e\u8f89\u90a3\u91cc\u5f97\u6765\u7684\u5173\u952e\u6d88\u606f\u5374\u6210\u4e3a\u4f17\u6240\u7686\u77e5\u7684\u79d8\u5bc6\uff0c\u4e0d\u6653\u5f97\u4ed6\u4f1a\u505a\u4f55\u611f\u60f3\uff0c\u53ef\u60dc\u554a\uff0c\u4e0d\u80fd\u8fd9\u65f6\u5019\u8d70\u8fc7\u53bb\u5f53\u7740\u4ed6\u7684\u9762\u5c06\u79d8\u5bc6\u6233\u7834\uff01\u5f20\u606a\u5185\u5fc3\u6df1\u5904\u6709\u65f6\u4e5f\u6709\u5982\u6b64\u6076\u8da3\u5473\u7684\u503e\u5411\u3002</p><p>\u3000\u3000\u592e\u89c6\u8fde\u7eed\u4e09\u5c4a\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7684\u8f89\u714c\u6210\u529f\uff0c\u8c2d\u5e0c\u677e\u8fd9\u4e2a\u5e7f\u544a\u4fe1\u606f\u90e8\u4e3b\u4efb\u7684\u4f4d\u5b50\u4e5f\u5e94\u8be5\u5f88\u5feb\u5c31\u8981\u6210\u4e3a\u4f17\u4eba\u773c\u998b\u7684\u6c34\u871c\u4ed9\u6843\u5427\uff1f\u8fd9\u4e9b\u505a\uff0c\u5373\u4f7f\u4f1a\u4f20\u5230\u8c2d\u5e0c\u677e\u8033\u6735\u91cc\uff0c\u5927\u6982\u4e5f\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u95ee\u9898\u5427\u3002\u4e0d\u8fc7\u7ed9\u4eba\u5bb6\u589e\u52a0\u9ebb\u70e6\u603b\u4e0d\u80fd\u7b97\u662f\u5f88\u9053\u5fb7\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u5c3d\u91cf\u4e0d\u8981\u8ba9\u522b\u4eba\u77e5\u9053\u624d\u597d\u3002</p><p>\u3000\u3000\u542c\u7740\u5f20\u606a\u9762\u6388\u673a\u5b9c\u7684\u6559\u5218\u6d77\u71d5\u3001\u8d75\u6d77\u821f\u5982\u4f55\u5077\u5077\u6478\u6478\u7684\u53bb\u4f7f\u574f\uff0c\u665a\u6674\u5f88\u65e0\u8c13\u7684\u6a2a\u4e86\u4ed6\u4e00\u773c\uff1a\u4ece\u8c22\u5251\u5357\u6559\u5506\u5218\u660e\u8f89\u5728\u5982\u6b64\u5173\u952e\u65f6\u523b\u62c9\u4eba\u8df3\u69fd\uff0c\u5c31\u5b8c\u5168\u6ca1\u6709\u5fc5\u8981\u5bf9\u4ed6\u4eec\u518d\u5bb9\u60c5\u4e86\uff1b\u8981\u4e0d\u662f\u5076\u5c14\u770b\u5230\u534e\u5357\u529e\u7684\u9500\u552e\u4eba\u5458\u5728\u9152\u5427\u95e8\u53e3\u4e0e\u8c22\u77bb\u8d70\u5728\u4e00\u8d77\uff0c\u8981\u4e0d\u662f\u65e9\u5c31\u770b\u5230\u5218\u660e\u8f89\u5fc3\u601d\u4e0d\u7a33\uff0c\u8ba9\u4ed6\u4eec\u7a81\u7136\u8fd9\u4e48\u641e\u4e00\u4e0b\uff0c\u7231\u8fbe\u7535\u5b50\u4e00\u5b9a\u4f1a\u975e\u5e38\u7684\u72fc\u72c8\u4e0d\u582a\u3002\u5c31\u7b97\u5982\u6b64\uff0c\u5e02\u573a\u90e8\u4f9d\u7136\u5f88\u6df7\u4e71\uff0c\u7279\u522b\u662f\u534e\u5357\u529e\u90a3\u8fb9\uff0c\u77ed\u77ed\u4e94\u5929\u65f6\u95f4\uff0c\u6768\u4e91\u90fd\u5f80\u8fd4\u98de\u4e86\u4e09\u8d9f\u53bb\u6551\u6025\u3002</p><p>\u3000\u3000\u4e0b\u5348\uff0c\u5df2\u7ecf\u7ade\u4e0b\u65b0\u95fb\u8054\u64ad\u4e4b\u540e\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7684\u5382\u5546\u5c31\u65e0\u9700\u518d\u53bb\u73b0\u573a\uff0c\u4f1a\u76f4\u63a5\u4e0e\u592e\u89c6\u76f8\u5173\u7b7e\u7f72\u5408\u540c\uff0c\u5218\u6d77\u71d5\u3001\u8d75\u6d77\u821f\u81ea\u7136\u6ca1\u6709\u5bbd\u88d5\u7684\u65f6\u95f4\u966a\u5f20\u606a\u4ed6\u4eec\u5750\u4e0b\u6765\u4e00\u8d77\u4eab\u53d7\u592e\u89c6\u4e3a\u4ed6\u4eec\u9ad8\u5c42\u4e13\u95e8\u51c6\u5907\u7684\u5348\u9910\u3002</p><p>\u3000\u3000\u5f20\u606a\u7aef\u7740\u81ea\u52a9\u9910\u76d8\uff0c\u8d70\u56de\u5230\u5ea7\u4f4d\u4e0a\uff0c\u63a5\u8fc7\u665a\u6674\u5e2e\u4ed6\u7aef\u6765\u7684\u86cb\u76ae\u867e\u4ec1\u6c64\uff0c\u8ddf\u82cf\u6d25\u4e1c\u8bf4\uff1a\u201c\u79d1\u738b\u773c\u4e0b\u6240\u8c13\u7684\u7ecf\u9500\u5546\u7f51\u7edc\u592a\u8106\u5f31\uff0c\u8fd9\u4e2a\u7f51\u7edc\u4e00\u4e2a\u6708\u624d\u4e00\u4e07\u53f0\u7684\u91cf\uff0c\u73b0\u5728\u8fde\u7ed9\u4ed6\u4eec\u4ed8\u5229\u606f\u90fd\u4e0d\u591f\u7684\u3002\u4ee5\u73b0\u5728\u7684\u5229\u6da6\u7a7a\u95f4\uff0c\u79d1\u738b\u7684\u9500\u91cf\u8981\u8d70\u5230\u5341\u4e07\u53f0\u4ee5\u4e0a\uff0c\u624d\u53ef\u80fd\u8fbe\u5230\u76c8\u4e8f\u5e73\u8861\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u5c3d\u5feb\u8ba9\u4ed6\u4eec\u7684\u7ecf\u9500\u5546\u7f51\u7edc\u5347\u7ea7\uff0c\u6311\u9009\u6709\u7ecf\u9a8c\u3001\u6709\u5b9e\u529b\u7684\u6e20\u9053\u5546\u5408\u4f5c\u3002\u6211\u4eec\u554a\uff0c\u7a33\u5b9a\u6e20\u9053\u5546\u7f51\u7edc\u662f\u5f88\u5173\u952e\u7684\u4e00\u6b65\uff0c\u79d1\u738b\u4e00\u5b9a\u4f1a\u4ece\u8fd9\u4e0a\u9762\u4e0b\u624b\uff0c\u7ecf\u9500\u5546\u5927\u4f1a\u8fd8\u662f\u6309\u53bb\u5e74\u7684\u65f6\u95f4\u529e\uff0c\u79d1\u738b\u4e5f\u4e00\u5b9a\u4f1a\u5229\u7528\u90a3\u4e2a\u65f6\u673a\uff0c\u6211\u4eec\u8981\u597d\u597d\u51c6\u5907\uff0c\u53ea\u8981\u79d1\u738b\u65e0\u6cd5\u4ece\u6211\u4eec\u8fd9\u91cc\u62a2\u5360\u7ecf\u9500\u5546\u8d44\u6e90\uff0c\u4ed6\u4eec\u5c31\u8f7b\u677e\u4e0d\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6807\u738b\u5e7f\u544a\u8981\u7b49\u5230\u660e\u5e74\u4e00\u6708\u4efd\u624d\u6b63\u5f0f\u64ad\u51fa\uff0c\u5e74\u524d\u7684\u9500\u91cf\u5feb\u901f\u4e0a\u5347\uff0c\u4e3b\u8981\u662f\u5feb\u901f\u62a2\u5360\u6e20\u9053\u5546\u3001\u5b8c\u6210\u6e20\u9053\u5546\u7f51\u7edc\u5efa\u8bbe\u6240\u5e26\u6765\u7684\u589e\u957f\uff0c\u201d\u82cf\u6d25\u4e1c\u5f88\u8d5e\u540c\u5f20\u606a\u7684\u5224\u65ad\uff0c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u6e20\u9053\u5546\u8d44\u6e90\u5e76\u4e0d\u662f\u65e0\u9650\u53ef\u6316\u6398\u7684\uff0c\u79d1\u738b\u62a2\u4e0d\u5230\u6211\u4eec\u7684\uff0c\u5c31\u4f1a\u62a2\u5176\u4ed6\u5382\u5546\u7684\uff0c\u4ed6\u4eec\u5fc5\u5b9a\u4e5f\u4f1a\u5b66\u6211\u4eec\u6536\u4fdd\u8bc1\u91d1\u7684\u90a3\u5957\uff0c\u4e0d\u7136\u4ed6\u4eec\u7684\u6d41\u52a8\u8d44\u91d1\u5c31\u4f1a\u4e25\u91cd\u4e0d\u8db3\uff0c\u9664\u975e\u4ed6\u4eec\u613f\u610f\u4ece\u5176\u4ed6\u5730\u65b9\u6536\u53d6\u4e94\u5230\u516d\u4ebf\u7684\u73b0\u91d1\u8fc7\u6765\u3002\u4ed6\u4eec\u8981\u5411\u7ecf\u9500\u5546\u6536\u9ad8\u989d\u7684\u4fdd\u8bc1\u91d1\uff0c\u90a3\u4f1a\u8ba9\u7ecf\u9500\u5546\u6392\u65a5\u5176\u4ed6\u7684\u5f71\u789f\u673a\u5382\u5546\uff0c\u4e0d\u4ec5\u79d1\u738b\u4e00\u5bb6\u5982\u6b64\uff0c\u6b65\u6b65\u9ad8\u597d\u4e00\u4e9b\uff0c\u4f46\u662f\u7231\u591a\u4e5f\u505a\u51fa\u5982\u6b64\u5f3a\u52bf\u7684\u59ff\u6001\uff0c\u4ee5\u4e00\u4ebf\u56db\u5343\u4e07\u6324\u5165\u7535\u5b50\u699c\u524d\u4e09\uff0c\u624b\u6bb5\u4e4b\u51cc\u5389\uff0c\u5927\u6982\u4e0d\u4f1a\u6bd4\u79d1\u738b\u5dee\u592a\u591a\uff0c\u6211\u770b\u5f71\u789f\u673a\u4ea7\u4e1a\u4ece\u5e7f\u544a\u5927\u6218\u5f00\u59cb\u5f88\u5feb\u5c31\u4f1a\u8f6c\u53d8\u6210\u751f\u5b58\u4e4b\u6218\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4fdd\u8bc1\u91d1\u8fd9\u5957\uff0c\u5f20\u606a\u5c31\u662f\u5077\u5b66\u7231\u591a\u7684\u3002\u7231\u591a\u7684\u80e1\u5fd7\u6807\u4e00\u5b9a\u4f1a\u7528\u8fd9\u4e00\u62db\uff0c\u4e0d\u7136\u4e00\u7a77\u4e8c\u767d\u7684\u80e1\u5fd7\u6807\u6839\u672c\u5c31\u6ca1\u6709\u8d44\u91d1\u6765\u6e90\u3002\u4ec5\u79d1\u738b\u4e00\u5bb6\u8fd8\u597d\u8bf4\uff0c\u7231\u591a\u4e5f\u5982\u5386\u53f2\u91cd\u6f14\u4e00\u6837\u6324\u5165\u7535\u5b50\u699c\u524d\u4e09\uff0c\u90a3\u56e0\u7231\u591a\u5f15\u8d77\u7684\u5f71\u789f\u673a\u5e02\u573a\u7684\u8165\u98ce\u8840\u96e8\u66f4\u52a0\u4e0d\u53ef\u907f\u514d\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u5fc5\u7136\u7684\u8d8b\u52bf\uff0c\u5927\u6982\u60df\u6709\u6211\u4eec\u7231\u8fbe\u7684\u8fc5\u901f\u8870\u843d\uff0c\u8ba9\u51fa\u6700\u5927\u7684\u4e00\u5757\u86cb\u7cd5\u624d\u80fd\u907f\u514d\u8fd9\u79cd\u5c40\u9762\u7684\u51fa\u73b0\uff0c\u201d\u5f20\u606a\u5b09\u7b11\u6012\u9a82\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u53ef\u60dc\u554a\uff0c\u6211\u5df2\u7ecf\u597d\u591a\u5e74\u6ca1\u6709\u505a\u5584\u4e8b\u4e86\u3002\u201d</p></article>"}}}]);
