(window.webpackJsonp=window.webpackJsonp||[]).push([[436],{458:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516d\u8282 \u8fd9\u662f\u6211\u5bb6",content:"<article><h2>\u7b2c\u4e8c\u5341\u516d\u8282 \u8fd9\u662f\u6211\u5bb6</h2><p>\u201c\u591c\u5973\u58eb\uff1f\u201d</p><p>\u743c\u6069\u5b9a\u4e86\u5b9a\u795e\uff0c\u968f\u5373\u660e\u767d\u5bf9\u65b9\u7684\u8eab\u4efd\u3002\u4e0a\u6b21\u5728\u963f\u65af\u5361\u7279\u62c9\u7684\u65f6\u5019\uff0c\u4ed6\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0a\u5e8a\uff0c\u7ed3\u679c\u838e\u5c14\u5723\u8005\u964d\u4e34\uff0c\u8981\u743c\u6069\u53eb\u5979\u201c\u59d0\u59d0\u201d\u3002\u743c\u6069\u5f53\u65f6\u9887\u6709\u4e9b\u5947\u602a\uff0c\u4e0d\u660e\u767d\u591c\u5973\u58eb\u4e3a\u4f55\u4f1a\u6709\u8fd9\u79cd\u7231\u597d\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u4f9d\u8a00\u4ece\u547d\uff0c\u540e\u6765\u4e5f\u5c31\u6ca1\u600e\u4e48\u5728\u610f\u4e86\u3002\u65b9\u624d\u56e0\u4e3a\u611f\u89c9\u5230\u5bf9\u65b9\u7684\u8eab\u6750\u548c\u4f53\u9999\u4e0e\u73ca\u5609\u8fd1\u4f3c\uff0c\u9ed1\u6697\u4e4b\u4e2d\u53c8\u770b\u4e0d\u89c1\uff0c\u5bfc\u81f4\u4ed6\u8ba4\u9519\u4e86\u4eba\uff0c\u4e0b\u610f\u8bc6\u5730\u53eb\u4e86\u4e00\u58f0\u59d0\u59d0\uff0c\u6ca1\u60f3\u5230\u8bef\u6253\u8bef\u649e\u8fd8\u7b97\u78b0\u5bf9\u4e86\u3002</p><p>\u77e5\u9053\u662f\u9047\u4e0a\u719f\u4eba\uff0c\u4ed6\u4e0d\u7981\u4e0b\u610f\u8bc6\u5730\u5fc3\u60c5\u4e00\u677e\uff0c\u4f46\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u4e0d\u5bf9\u3002\u838e\u5c14\u4e0a\u6b21\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u5979\u548c\u9634\u9b42\u57ce\u4e3b\u590f\u591a\u6709\u7ea6\u5b9a\uff0c\u4e0d\u80fd\u5728\u9634\u9b42\u57ce\u4e2d\u5723\u8005\u964d\u4e34\u2014\u2014\u90a3\u5979\u73b0\u5728\u600e\u4e48\u4f1a\u51fa\u73b0\u5728\u81ea\u5df1\u8eab\u8fb9\uff1f</p><p>\u5f53\u7136\uff0c\u4e0d\u80fd\u5723\u8005\u964d\u4e34\uff0c\u8fd8\u53ef\u4ee5\u76f4\u63a5\u5316\u8eab\u4e0b\u51e1\uff0c\u4f46\u8fd9\u79cd\u53ef\u80fd\u6027\u5b9e\u5728\u592a\u4f4e\uff0c\u76f4\u63a5\u5c31\u88ab\u743c\u6069\u5ffd\u7565\u4e0d\u8ba1\u4e86\u3002\u795e\u7947\u5316\u8eab\u4e34\u51e1\uff0c\u90a3\u662f\u8981\u8017\u8d39\u592a\u591a\u795e\u529b\u7684\uff0c\u5386\u53f2\u4e0a\u80fd\u4eab\u53d7\u8fd9\u79cd\u5f85\u9047\u7684\u6ca1\u51e0\u4f4d\uff0c\u6700\u6700\u8457\u540d\u7684\u4fbf\u662f\u8428\u739b\u65af\u7279\uff0c\u5f00\u59cb\u662f\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u5316\u8eab\u4e34\u51e1\uff0c\u64e2\u5347\u4ed6\u4e3a\u9009\u6c11\uff0c\u4e2d\u95f4\u662f\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\u5316\u8eab\u4e34\u51e1\uff0c\u5265\u593a\u4ed6\u7684\u9009\u6c11\u8d44\u683c\uff0c\u6700\u540e\u662f\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u5316\u8eab\u4e34\u51e1\uff0c\u4e00\u9524\u5b50\u5c06\u4ed6\u8f70\u6740\u81f3\u6e23\u2014\u2014\u8fd9\u7ecf\u5386\u5f53\u771f\u662f\u534e\u4e3d\u7cbe\u5f69\uff0c\u4ee4\u4eba\u4e0d\u5f97\u4e0d\u819c\u62dc\u3002\u743c\u6069\u5411\u6765\u5f88\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u4e0d\u6562\u6bd4\u7f8e\u524d\u8d24\uff0c\u4e0d\u4f1a\u8ba4\u4e3a\u838e\u5c14\u4e3a\u4e86\u4ed6\u80fd\u5982\u6b64\u5927\u52a8\u5e72\u6208\u3002</p><p>\u95ee\u9898\u662f\uff0c\u65e2\u975e\u5723\u8005\u964d\u4e34\uff0c\u53c8\u975e\u5316\u8eab\u4e0b\u51e1\uff0c\u81ea\u5df1\u8fd8\u80fd\u88ab\u5973\u795e\u62b1\u5728\u6000\u91cc\u2026\u2026\u96be\u4e0d\u6210\u8fd9\u91cc\u662f\u591c\u5973\u58eb\u7684\u795e\u57df\uff1f\u4f20\u8bf4\u4e2d\u7684\u5931\u843d\u4e4b\u6bbf\uff1f\u81ea\u5df1\u600e\u4e48\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f\u662f\u88ab\u94c1\u9762\u4eba\u90a3\u957f\u97ad\u4e00\u51fb\u9001\u8fc7\u6765\u7684\uff1f\u96be\u9053\u90a3\u5176\u5b9e\u662f\u4e00\u4e2a\u4f4d\u9762\u4f20\u9001\u6cd5\u672f\uff1f\u4f46\u4f3c\u4e4e\u53c8\u4e0d\u50cf\u554a\u3002\u81ea\u5df1\u73b0\u5728\u53c8\u662f\u5904\u4e8e\u4ec0\u4e48\u72b6\u6001\uff1f\u662f\u5b9e\u4f53\uff0c\u8fd8\u662f\u7075\u9b42\uff1f\u662f\u6d3b\u7740\uff0c\u8fd8\u662f\u5df2\u7ecf\u6302\u4e86\uff1f</p><p>\u6b63\u601d\u7eea\u7eb7\u4e71\u95f4\uff0c\u611f\u89c9\u838e\u5c14\u5728\u4ed6\u7684\u989d\u5934\u4e0a\u8f7b\u8f7b\u4e00\u543b\u3002\u4eff\u4f5b\u88ab\u5f00\u542f\u4e86\u67d0\u9053\u95f8\u95e8\uff0c\u8eab\u4f53\u91cc\u90a3\u79cd\u8f6f\u7ef5\u7ef5\u7684\u65e0\u529b\u611f\u5239\u90a3\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u96be\u4ee5\u5fcd\u53d7\u7684\u9178\u80c0\u5267\u75db\uff0c\u800c\u4e14\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u5904\u90fd\u5728\u75bc\uff0c\u4eff\u4f5b\u808c\u8089\u88ab\u4e00\u5bf8\u5bf8\u6495\u88c2\u5f00\u6765\u4f3c\u7684\u3002</p><p>\u201c\u554a\u54df\uff01\u201d</p><p>\u731d\u4e0d\u53ca\u9632\u88ab\u5267\u75db\u88ad\u51fb\uff0c\u743c\u6069\u5fcd\u4e0d\u4f4f\u53eb\u51fa\u58f0\u6765\uff0c\u51b7\u6c57\u77ac\u95f4\u6d78\u900f\u4e86\u80cc\u540e\u7684\u8863\u88f3\u3002\u4ed6\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u54ac\u7d27\u7259\u5173\u6b63\u52c9\u529b\u5fcd\u8010\uff0c\u4e00\u53ea\u67d4\u82e5\u65e0\u9aa8\u7684\u7389\u624b\u5728\u4ed6\u8138\u988a\u4e0a\u629a\u6478\u7740\uff0c\u8f7b\u8f7b\u634f\u4e86\u634f\uff0c\u548c\u7166\u7684\u6696\u6d41\u987f\u65f6\u6084\u7136\u6ce8\u5165\u743c\u6069\u8eab\u4f53\uff0c\u8f6c\u773c\u95f4\u6d41\u904d\u5168\u8eab\uff0c\u5982\u70c8\u65e5\u878d\u96ea\u822c\u5c06\u6240\u6709\u75bc\u75db\u6d88\u9664\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002</p><p>\u201c\u771f\u662f\u80e1\u95f9\u5462\uff0c\u5c0f\u5bb6\u4f19\uff0c\u201d\u838e\u5c14\u5c06\u4ed6\u6402\u5728\u6000\u4e2d\uff0c\u8f7b\u58f0\u8d23\u5907\u7740\uff0c\u8bed\u6c14\u91cc\u5374\u6ca1\u6709\u591a\u5c11\u5f53\u771f\u55d4\u602a\u7684\u610f\u5473\uff0c\u201c\u63a5\u8fde\u53e0\u52a0\u4e24\u4e2a\u52a0\u901f\u672f\u2026\u2026\u4f60\u4ee5\u4e3a\u81ea\u5df1\u662f\u90a3\u79cd\u808c\u8089\u53d1\u8fbe\u7684\u91ce\u86ee\u4eba\u4e48\u3002\u201d</p><p>\u52a0\u901f\u672f\u662f\u80fd\u591f\u5f3a\u884c\u63d0\u5347\u4eba\u7684\u884c\u52a8\u901f\u5ea6\u7684\u6cd5\u672f\uff0c\u800c\u4e14\u662f\u53ef\u4ee5\u65e0\u9650\u53e0\u52a0\u7684\uff0c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\uff0c\u53ea\u8981\u4e0d\u505c\u5730\u53e0\u52a0\u52a0\u901f\u672f\uff0c\u53d7\u672f\u8005\u7684\u884c\u52a8\u901f\u5ea6\u5c31\u80fd\u8d8b\u5411\u65e0\u7a77\u5927\u2014\u2014\u7136\u800c\u8fd9\u53ea\u662f\u7406\u8bba\uff0c\u538b\u6839\u4e0d\u53ef\u80fd\u53d8\u6210\u73b0\u5b9e\uff0c\u56e0\u4e3a\u4eba\u7684\u8eab\u4f53\u662f\u6709\u8d1f\u8377\u6781\u9650\u7684\u3002\u5c24\u5176\u662f\u5deb\u5e08\u8fd9\u79cd\u7d20\u6765\u4ee5\u4f53\u8d28\u5dee\u8457\u79f0\u7684\u4eba\u7fa4\uff0c\u636e\u743c\u6069\u6240\u77e5\uff0c\u6709\u4e9b\u5bb6\u4f19\u751a\u81f3\u90fd\u4e0d\u6562\u5bf9\u81ea\u5df1\u7528\u52a0\u901f\u672f\uff0c\u751f\u6015\u52a8\u4f5c\u9aa4\u7136\u52a0\u5feb\u8eab\u4f53\u65e0\u6cd5\u9002\u5e94\uff0c\u5494\u5693\u4e00\u58f0\u80f3\u818a\u65ad\u4e86\u817f\u9aa8\u6298\u4e86\u3002\u743c\u6069\u5df2\u7ecf\u7b97\u662f\u5deb\u5e08\u4e2d\u7684\u5f3a\u58ee\u8005\uff0c\u4f46\u540c\u65f6\u53e0\u52a0\u4e24\u4e2a\u52a0\u901f\u672f\uff0c\u8eab\u4f53\u4f9d\u65e7\u96be\u4ee5\u627f\u53d7\uff0c\u5c31\u50cf\u662f\u957f\u671f\u4e0d\u8fd0\u52a8\u7684\u4eba\u5728\u5174\u594b\u5242\u7684\u4f5c\u7528\u4e0b\u8dd1\u4e86\u4e2a\u9a6c\u62c9\u677e\uff0c\u7ed3\u679c\u53ef\u60f3\u800c\u77e5\u3002</p><p>\u201c\u6ca1\u529e\u6cd5\u554a\uff0c\u201d\u743c\u6069\u82e6\u7b11\u7740\u56de\u7b54\uff0c\u4e00\u8fb9\u5728\u9ed1\u6697\u4e2d\u5750\u8d77\u8eab\u6765\uff0c\u4ed6\u4e0d\u4e60\u60ef\u8fd9\u6837\u8eba\u7740\u548c\u4eba\u8bf4\u8bdd\uff0c\u201c\u9047\u4e0a\u90a3\u4e2a\u6234\u94c1\u9762\u5177\u7684\u5bb6\u4f19\u2026\u2026\u591c\u5973\u58eb\uff0c\u4ed6\u662f\u60a8\u7684\u7267\u5e08\u5417\uff1f\u201d</p><p>\u201c\u62d2\u7edd\u56de\u7b54\u3002\u201d</p><p>\u201c\u5443\u2026\u2026\u54e6\uff0c\u5bf9\u4e0d\u8d77\uff0c\u201d\u743c\u6069\u6014\u4e86\u6014\uff0c\u7acb\u523b\u610f\u8bc6\u5230\u95ee\u9898\u6240\u5728\uff0c\u968f\u5373\u8d76\u5feb\u6539\u53e3\uff0c\u201c\u5973\u795e\u59d0\u59d0\uff0c\u90a3\u4e2a\u94c1\u9762\u4eba\u662f\u60a8\u7684\u7267\u5e08\u5417\uff1f\u201d</p><p>\u201c\u7b97\u4f60\u673a\u7075\uff0c\u201d\u838e\u5c14\u8f7b\u7b11\uff0c\u201c\u5bf9\uff0c\u4ed6\u662f\u6211\u7684\u7267\u5e08\u3002\u201d</p><p>\u201c\u591c\u89c6\u8005\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4f60\u8fd8\u771f\u80c6\u5927\u5462\uff0c\u660e\u660e\u662f\u6765\u53c2\u52a0\u796d\u5178\uff0c\u7ed3\u679c\u5728\u6211\u7684\u795e\u6bbf\u91cc\u548c\u5c0f\u59d1\u5a18\u5077\u60c5\uff0c\u8fd8\u64c5\u95ef\u7981\u5730\uff0c\u548c\u6211\u7684\u7267\u5e08\u52a8\u624b\u6253\u4e86\u4e00\u67b6\u2014\u2014\u4f60\u8bf4\uff0c\u59d0\u59d0\u5e94\u8be5\u600e\u6837\u60e9\u7f5a\u4f60\u5462\uff1f\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u867d\u7136\u660e\u77e5\u838e\u5c14\u5e76\u6ca1\u6709\u5f53\u771f\u8981\u8ffd\u7a76\u7684\u610f\u601d\uff0c\u4f46\u88ab\u5979\u4e00\u8bf4\uff0c\u743c\u6069\u8fd8\u662f\u9887\u6709\u4e9b\u5c34\u5c2c\uff0c\u201c\u548c\u8299\u857e\u72c4\uff0c\u55ef\uff0c\u73a9\u5f97\u592a\u9ad8\u5174\u4e86\u70b9\uff0c\u803d\u8bef\u4e86\u53c2\u52a0\u796d\u5178\uff0c\u8fd9\u662f\u6211\u4e0d\u5bf9\u5566\u3002\u4e0d\u8fc7\u59d0\u59d0\u554a\uff0c\u4f60\u8bf4\u4ec0\u4e48\u64c5\u95ef\u7981\u5730\u662f\u6307\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u60f3\u62b5\u8d56\u4e48\uff0c\u201d\u838e\u5c14\u7b11\u541f\u541f\u5730\u8bf4\uff0c\u201c\u4f60\u4eec\u6700\u540e\u6253\u67b6\u7684\u90a3\u4e2a\u5730\u65b9\uff0c\u5c31\u662f\u7981\u5730\u554a\u3002\u201d</p><p>\u201c\u90a3\u7247\u5c0f\u6811\u6797\uff1f\u53ef\u662f\u6211\u4e5f\u6ca1\u770b\u5230\u4ec0\u4e48\u2018\u7981\u5730\u2019\u7684\u6807\u8bc6\u554a\u3002\u201d</p><p>\u201c\u660e\u660e\u6709\uff0c\u662f\u4f60\u81ea\u5df1\u591c\u91cc\u6ca1\u770b\u89c1\u597d\u4e0d\u597d\uff0c\u201d\u838e\u5c14\u5c48\u6307\u5728\u4ed6\u5934\u4e0a\u4e0d\u8f7b\u4e0d\u91cd\u6572\u4e86\u4e00\u4e0b\uff0c\u201c\u770b\u4e0d\u89c1\u8def\u5c31\u4e0d\u8981\u5230\u5904\u4e71\u8dd1\uff0c\u53c8\u4e0d\u662f\u81ea\u5df1\u4e00\u4e2a\u4eba\uff0c\u8fd8\u5e26\u7740\u4e2a\u5c0f\u59d1\u5a18\u5462\uff0c\u8fd9\u4e48\u4e0d\u7a33\u91cd\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\u5bf9\u4e0d\u8d77\uff0c\u201d\u743c\u6069\u7b11\u563b\u563b\u5730\u8bf4\uff0c\u201c\u53ef\u662f\u82e5\u975e\u5982\u6b64\uff0c\u6211\u600e\u4e48\u4f1a\u53c8\u80fd\u9047\u4e0a\u59d0\u59d0\u4f60\u5462\u3002\u201d</p><p>\u4ed6\u662f\u4e2a\u65e0\u4fe1\u8005\uff0c\u6700\u591a\u7b97\u662f\u4e2a\u6cdb\u4fe1\u8005\uff0c\u5bf9\u795e\u660e\u5e76\u65e0\u591a\u5c11\u771f\u6b63\u7684\u656c\u754f\uff0c\u548c\u838e\u5c14\u53c8\u5df2\u7ecf\u9762\u5bf9\u9762\u5730\u6253\u8fc7\u51e0\u6b21\u4ea4\u9053\uff0c\u751a\u81f3\u90fd\u6709\u8fc7\u4eb2\u5bc6\u63a5\u89e6\uff0c\u201c\u6df1\u5165\u201d\u4e86\u89e3\uff0c\u8a00\u8bed\u4ea4\u8c08\u95f4\u4fbf\u4e0d\u4f1a\u50cf\u5176\u4ed6\u4fe1\u5f92\u90a3\u6837\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u6218\u6218\u5162\u5162\uff0c\u800c\u662f\u66f4\u8f7b\u677e\u81ea\u5728\u4e9b\uff0c\u751a\u81f3\u6562\u4e8e\u5f00\u70b9\u73a9\u7b11\u3002\u201c\u4e0d\u8fc7\u59d0\u59d0\u554a\uff0c\u201d\u4ed6\u8d81\u673a\u95ee\uff0c\u201c\u8fd9\u91cc\u5230\u5e95\u662f\u54ea\u91cc\u5462\uff1f\u201d</p><p>\u201c\u81ea\u7136\u662f\u6211\u5bb6\u5566\u3002\u201d</p><p>\u4f60\u5bb6\u2026\u2026\u679c\u7136\u662f\u5df2\u7ecf\u5230\u4e86\u838e\u5c14\u7684\u795e\u57df\u4e48\uff1f</p><p>\u201c\u90a3\u6211\u2026\u2026\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u4f60\u6ca1\u6b7b\uff0c\u6211\u662f\u628a\u4f60\u76f4\u63a5\u4ece\u7269\u8d28\u754c\u62c9\u4e86\u8fc7\u6765\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48\u53c8\u8f7b\u8f7b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u201c\u4f60\u8fd9\u4e2a\u7b28\u86cb\uff0c\u5c45\u7136\u548c\u9b45\u9b54\u7b7e\u4ec0\u4e48\u771f\u540d\u5951\u7ea6\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u6837\u4e00\u6765\uff0c\u4f60\u5982\u679c\u88ab\u4eba\u6740\u6b7b\uff0c\u7075\u9b42\u4e5f\u5c31\u968f\u4e4b\u6e6e\u706d\uff0c\u5c31\u7b97\u60f3\u505a\u6211\u7684\u7948\u5e76\u8005\u90fd\u529e\u4e0d\u5230\uff0c\u66f4\u522b\u63d0\u590d\u6d3b\u4e86\u3002\u201d</p><p>\u8fd9\u70b9\u743c\u6069\u81ea\u7136\u6e05\u695a\uff0c\u771f\u540d\u5951\u7ea6\u4e4b\u6240\u4ee5\u4f1a\u4ee4\u5deb\u5e08\u4eec\u671b\u800c\u5374\u6b65\uff0c\u81ea\u4ece\u53d1\u660e\u51fa\u6765\u540e\u5c31\u88ab\u675f\u4e4b\u9ad8\u9601\uff0c\u4e0d\u5c31\u662f\u56e0\u4e3a\u8fd9\u4e25\u82db\u7684\u201c\u751f\u547d\u7ed1\u5b9a\u201d\u4e48\u3002\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u51e1\u4eba\u906d\u9047\u610f\u5916\u8eab\u6545\uff0c\u53ea\u8981\u6b7b\u4ea1\u65f6\u95f4\u4e0d\u957f\uff0c\u5c38\u4f53\u6ca1\u6709\u4e25\u91cd\u6bc1\u635f\uff0c\u795e\u7947\u662f\u53ef\u4ee5\u5c06\u4ed6\u7684\u7075\u9b42\u9001\u56de\u7269\u8d28\u754c\uff0c\u4ee5\u795e\u529b\u5c06\u4ed6\u590d\u6d3b\u7684\u3002\u5f53\u7136\uff0c\u795e\u7947\u57fa\u672c\u4e0d\u4f1a\u4e3b\u52a8\u8fd9\u4e48\u505a\uff0c\u5f80\u5f80\u662f\u5728\u9ad8\u9636\u7267\u5e08\u7684\u8bf7\u6c42\u4e0b\u624d\u4f1a\u51fa\u624b\uff0c\u8fd9\u4e5f\u5c31\u662f\u6240\u8c13\u201c\u590d\u6d3b\u672f\u201d\u7684\u672c\u8d28\u4e86\u2014\u2014\u4e25\u683c\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u590d\u6d3b\u672f\u5e76\u4e0d\u662f\u4e00\u79cd\u9b54\u6cd5\uff0c\u800c\u662f\u5411\u795e\u7947\u63d0\u4ea4\u4e86\u4e00\u4efd\u7533\u8bf7\uff0c\u81f3\u4e8e\u8fd9\u4efd\u7533\u8bf7\u80fd\u5426\u5f97\u5230\u6279\u51c6\uff0c\u90a3\u5c31\u8981\u770b\u8fd9\u4f4d\u7267\u5e08\u7684\u201c\u795e\u7737\u201d\u6709\u591a\u6df1\u4e86\u3002\u56e0\u4e3a\u590d\u6d3b\u672f\u7684\u4ee3\u4ef7\u592a\u9ad8\uff0c\u80fd\u591f\u771f\u6b63\u6709\u5e78\u4eab\u53d7\u5230\u8fd9\u79cd\u5f85\u9047\u7684\u4eba\u5be5\u5be5\u65e0\u51e0\uff0c\u4f46\u5bf9\u4e8e\u548c\u90aa\u9b54\u7b7e\u8ba2\u4e86\u771f\u540d\u5951\u7ea6\u7684\u5deb\u5e08\u6765\u8bf4\uff0c\u538b\u6839\u662f\u8fde\u8fd9\u70b9\u7406\u8bba\u4e0a\u5b58\u5728\u7684\u6e3a\u832b\u673a\u4f1a\u90fd\u65ad\u7edd\u4e86\u3002\u56e0\u4e3a\u5f7c\u6b64\u7684\u7075\u9b42\u901a\u8fc7\u771f\u540d\u8fde\u63a5\u5728\u4e00\u8d77\uff0c\u4e00\u6b7b\u5168\u6b7b\uff0c\u4e00\u706d\u5168\u706d\uff0c\u5b8c\u5168\u6ca1\u5f97\u6551\u3002\u53ea\u4e0d\u8fc7\uff0c\u5f53\u65f6\u5728\u6df1\u6e0a\u91cc\u5f62\u52bf\u6240\u8feb\uff0c\u4e0d\u5f97\u4e0d\u7b7e\uff0c\u5982\u4eca\u4e8b\u5df2\u81f3\u6b64\uff0c\u518d\u8bf4\u8fd9\u4e9b\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\u3002\u53cd\u6b63\u5965\u6c83\u5df2\u7ecf\u66ff\u4ed6\u7528\u8d1d\u88d8\u91cc\u5b9d\u77f3\u5236\u4f5c\u4e86\u5b9d\u77f3\u754c\u57df\uff0c\u5c06\u838e\u73de\u514b\u5bb9\u7eb3\u5176\u4e2d\uff0c\u7b97\u662f\u6682\u65f6\u6027\u5730\u89e3\u51b3\u4e86\u8fd9\u4e2a\u9ebb\u70e6\uff0c\u4e5f\u5c31\u65e0\u9700\u591a\u60f3\u4e86\u3002</p><p>\u4ed6\u5728\u9ed1\u6697\u4e2d\u65e0\u58f0\u5730\u7b11\u4e86\u7b11\uff0c\u8f6c\u79fb\u4e86\u8bdd\u9898\uff0c\u201c\u59d0\u59d0\uff0c\u662f\u4f60\u628a\u6211\u6551\u4e0b\u6765\u7684\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u5176\u5b9e\u8981\u4e0d\u662f\u4f60\u6709\u6211\u7ed9\u7684\u5f71\u706b\uff0c\u65e9\u5c31\u88ab\u4ed6\u6740\u6389\u4e86\u3002\u201d</p><p>\u8fd9\u70b9\u743c\u6069\u4e5f\u6e05\u695a\uff0c\u94c1\u9762\u4eba\u662f\u838e\u5c14\u7684\u7267\u5e08\uff0c\u4f7f\u7528\u7684\u795e\u672f\u662f\u5979\u6240\u8d50\uff0c\u504f\u504f\u743c\u6069\u7684\u5f71\u706b\u6b63\u662f\u5973\u795e\u7684\u795e\u529b\u5177\u73b0\uff0c\u795e\u672f\u5bf9\u795e\u529b\uff0c\u53c8\u672c\u51fa\u540c\u6e90\uff0c\u5c31\u50cf\u540c\u4e00\u4e2a\u7cfb\u7edf\u5185\u4f4e\u9636\u649e\u4e0a\u4e86\u9ad8\u9636\uff0c\u5b8c\u5168\u88ab\u538b\u5236\u4f4f\u4e86\uff0c\u6839\u672c\u4e0d\u8d77\u4f5c\u7528\u3002\u5426\u5219\u94c1\u9762\u4eba\u5f00\u59cb\u90a3\u4e00\u8bb0\u4f24\u5bb3\u672f\u5c31\u8db3\u591f\u51fb\u6740\u4ed6\u4e86\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u5012\u8fd8\u6709\u53e6\u5916\u4e00\u4ef6\u4e8b\u503c\u5f97\u6ce8\u610f\u3002\u743c\u6069\u4f53\u5185\u5177\u6709\u5f71\u706b\uff0c\u636e\u4ed6\u81ea\u5df1\u5206\u6790\u63a8\u6d4b\uff0c\u6781\u6709\u53ef\u80fd\u662f\u5728\u585e\u5c14\u63a8\u5012\u90a3\u4f4d\u53eb\u505a\u83f2\u5a05\u7684\u5973\u5974\uff0c\u7ed3\u679c\u83f2\u5a05\u88ab\u838e\u5c14\u5723\u8005\u9644\u4f53\uff0c\u5077\u5077\u5c06\u5f71\u706b\u4f20\u7ed9\u4e86\u743c\u6069\u3002\u4e0a\u6b21\u5728\u963f\u65af\u5361\u7279\u62c9\uff0c\u838e\u5c14\u9644\u4f53\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u743c\u6069\u66fe\u7ecf\u7528\u8a00\u8bed\u8bd5\u63a2\uff0c\u60f3\u786e\u8ba4\u6b64\u4e8b\uff0c\u838e\u5c14\u5374\u59cb\u7ec8\u6ca1\u6709\u6b63\u9762\u56de\u7b54\uff0c\u5de7\u5999\u5730\u907f\u5f00\u8fd9\u4e2a\u8bdd\u9898\u3002\u76f4\u5230\u521a\u624d\uff0c\u5979\u8bf4\u201c\u8981\u4e0d\u662f\u4f60\u6709\u6211\u7ed9\u7684\u5f71\u706b\u201d\uff0c\u8fd9\u624d\u7b97\u662f\u90e8\u5206\u5730\u627f\u8ba4\u4e86\u743c\u6069\u4f53\u5185\u5f71\u706b\u7684\u6765\u6e90\uff0c\u4f46\u4f9d\u65e7\u6ca1\u6709\u8bf4\u660e\u662f\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u901a\u8fc7\u4f55\u79cd\u65b9\u5f0f\u7ed9\u4e88\u743c\u6069\u7684\u3002</p><p>\u591c\u5973\u58eb\u8bf4\u8bdd\uff0c\u6c38\u8fdc\u662f\u8fd9\u6837\u6a21\u68f1\u4e24\u53ef\uff0c\u542b\u7cca\u5176\u8f9e\u5417\uff1f\u5230\u5e95\u662f\u6267\u638c\u9690\u79d8\u7684\u5973\u795e\u554a\u3002</p><p>\u65e2\u7136\u5982\u6b64\uff0c\u743c\u6069\u4e5f\u4e0d\u60f3\u81ea\u627e\u6ca1\u8da3\uff0c\u786c\u8981\u7ea0\u7f20\u8fd9\u4e2a\u95ee\u9898\uff0c\u53cc\u65b9\u5fc3\u7167\u4e0d\u5ba3\u4fbf\u662f\u4e86\u3002\u201c\u90a3\u4ed6\u6700\u540e\u7528\u51fa\u6765\u7684\u957f\u97ad\u662f\u4ec0\u4e48\u6cd5\u672f\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u600e\u4e48\u5f71\u706b\u62b5\u6321\u4e0d\u4f4f\uff1f\u6211\u7684\u6212\u6307\u4e5f\u65e0\u6cd5\u9006\u8f6c\u53cd\u5f39\u3002\u201d</p><p>\u201c\u90a3\u662f\u6211\u8d50\u4e88\u7684\u2018\u591c\u4e4b\u97ad\u2019\uff0c\u5176\u4e2d\u4e5f\u540c\u6837\u704c\u6ce8\u4e86\u5f71\u706b\uff0c\u4f60\u7684\u6212\u6307\u5f53\u7136\u4e0d\u80fd\u53cd\u5f39\u5b83\u4e86\u3002\u81f3\u4e8e\u5f71\u706b\u4e48\uff0c\u201d\u838e\u5c14\u683c\u683c\u5a07\u7b11\uff0c\u201c\u4f60\u8fd8\u6839\u672c\u4e0d\u4f1a\u7528\u5462\u3002\u201d</p><p>\u6cd5\u672f\u9006\u8f6c\u672c\u8eab\u662f\u4e00\u79cd\u9b54\u6cd5\uff0c\u5b83\u4e5f\u53ea\u80fd\u53cd\u5f39\u9b54\u6cd5\uff0c\u4e0d\u80fd\u53cd\u5f39\u66f4\u9ad8\u9636\u7684\u795e\u529b\uff0c\u8fd9\u70b9\u5728\u6df1\u6e0a\u65ad\u57df\u9547\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u5f97\u5230\u9a8c\u8bc1\u3002\u5f53\u65f6\u4ed6\u5076\u9047\u6b23\u5e03\u548c\u51db\uff0c\u56e0\u4e3a\u88ab\u53d1\u73b0\u4e86\u8eab\u5177\u5f71\u706b\uff0c\u906d\u5230\u6b23\u5e03\u7684\u88ad\u51fb\uff0c\u743c\u6069\u5c31\u6ca1\u6cd5\u7528\u6212\u6307\u62b5\u6321\u9009\u6c11\u7684\u94f6\u706b\u3002\u800c\u4e14\u65e2\u7136\u53cc\u65b9\u90fd\u662f\u5f71\u706b\uff0c\u90fd\u662f\u795e\u529b\uff0c\u5f7c\u6b64\u4f4d\u9636\u76f8\u5f53\uff0c\u4e5f\u5c31\u65e0\u6240\u8c13\u8c01\u538b\u5236\u8c01\uff0c\u94c1\u9762\u4eba\u662f\u838e\u5c14\u7684\u4e3b\u6559\u7ea7\u7267\u5e08\uff0c\u743c\u6069\u5374\u662f\u4e2a\u5192\u724c\u9009\u6c11\uff0c\u6839\u672c\u4e0d\u80fd\u771f\u6b63\u8fd0\u7528\u5f71\u706b\uff0c\u81ea\u7136\u5c31\u654c\u4e0d\u8fc7\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u542c\u838e\u5c14\u7684\u8bed\u6c14\uff0c\u5979\u65e2\u7136\u7279\u5730\u70b9\u51fa\u8fd9\u70b9\u2026\u2026\u83ab\u975e\u5979\u8981\u8ba9\u743c\u6069\u6210\u4e3a\u5979\u771f\u6b63\u7684\u9009\u6c11\u4e48\uff1f\u5018\u82e5\u5979\u771f\u6709\u6b64\u610f\uff0c\u90a3\u81ea\u5df1\u662f\u5e94\u5141\uff0c\u8fd8\u662f\u62d2\u7edd\uff1f</p></article>"}}}]);
