(window.webpackJsonp=window.webpackJsonp||[]).push([[437],{460:function(p){p.exports={chapterTitle:null,title:"\u7b2c437\u7ae0 \u9648\u9759\u7684\u8003\u8651",content:"<article><h2>\u7b2c437\u7ae0 \u9648\u9759\u7684\u8003\u8651</h2><p>\u3000\u30003\u4ebf5\u5343\u4e07\u662f\u4ec0\u4e48\u6982\u5ff5\uff0c\u4ec5\u4ec5\u4e3a\u65b0\u95fb\u8054\u64ad\u4e4b\u540e\u76845\u79d2\u949f\uff0c\u4e00\u5e74\u5c31\u8981\u4ed8\u51fa\u8fd9\u4e48\u591a\u94b1\uff1f</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5728\u8fc7\u53bb\u4e00\u5e74\u7684\u8f89\u714c\u6210\u5c31\u8bc1\u660e\u8fd9\u51e0\u4e4e\u53ea\u6709\u4e00\u77ac\u606f\u4e4b\u77ed\u76845\u79d2\u949f\u5728\u4f01\u4e1a\u54c1\u724c\u6269\u5f20\u4e0a\u7684\u4ef7\u503c\uff0c\u5bf9\u4e8e\u8c22\u5251\u5357\u4e5f\u6df1\u4fe1\u4e0d\u7591\uff0c\u6807\u738b\u7684\u4ef7\u503c\u7edd\u4e0d\u4ec5\u4ec5\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u65b0\u95fb\u8054\u64ad\u4e4b\u540e\u76845\u79d2\u949f\u5e7f\u544a\u65f6\u6bb5\uff0c\u540c\u65f6\u610f\u5473\u7740\u4e0b\u4e00\u5e74\u5ea6\u638c\u63e1\u7740\u6574\u4e2a\u884c\u4e1a\u7684\u8bdd\u8bed\u6743\uff0c\u4e0d\u4ec5\u9650\u5236\u540c\u884c\u4e1a\u4e09\u5bb6\u4f01\u4e1a\u540c\u65f6\u8fdb\u5165\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\uff0c\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u4e3a\u4e86\u81ea\u8eab\u7684\u5229\u76ca\u8bc9\u6c42\uff0c\u4e00\u5b9a\u4f1a\u6295\u5165\u5de8\u5927\u7684\u5a92\u4f53\u8d44\u6e90\u7092\u4f5c\u201c\u6807\u738b\u201d\u6982\u5ff5\uff0c\u8c22\u5251\u5357\u4ece\u5218\u660e\u8f89\u90a3\u91cc\u542c\u5230\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff1a\u6807\u738b\u6982\u5ff5\u7684\u7092\u4f5c\uff0c\u662f\u592e\u89c6\u5e7f\u544a\u8d44\u6e90\u5e02\u573a\u5316\u8fd0\u4f5c\u7684\u5fc5\u8981\u624b\u6bb5\uff0c\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u7ed9\u4ed6\u4eec\u6240\u62e5\u6709\u7684\u5e7f\u544a\u8d44\u6e90\u505a\u7684\u4e00\u6b21\u534e\u4e3d\u7684\u8d85\u5927\u578b\u7684\u5ba3\u4f20\uff0c\u800c\u4e2d\u592e\u7535\u89c6\u53f0\u6240\u62e5\u6709\u516c\u5171\u4fe1\u606f\u8d44\u6e90\u662f\u5176\u4ed6\u5730\u65b9\u5a92\u4f53\u65e0\u6cd5\u60f3\u8c61\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u8bdd\uff0c\u90fd\u662f\u5218\u660e\u8f89\u4ece\u5f20\u606a\u90a3\u91cc\u542c\u6765\u7684\uff0c\u5f53\u7136\u4ed6\u4e3a\u4e86\u5728\u65b0\u4e1c\u5bb6\u9762\u524d\u62e8\u9ad8\u81ea\u5df1\uff0c\u5f88\u81ea\u7136\u7684\u5c06\u8fd9\u4e9b\u8ba4\u8bc6\u5f53\u6210\u4ed6\u81ea\u5df1\u7684\u9886\u609f\uff0c\u8fd9\u4e00\u70b9\u5012\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u65e0\u53ef\u539a\u975e\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u5218\u660e\u8f89\u867d\u7136\u6709\u6c14\u5ea6\u4e0a\u6709\u6240\u6b20\u7f3a\uff0c\u4f46\u662f\u5bf9\u5e02\u573a\u8fd0\u4f5c\u7684\u67d0\u4e9b\u89c1\u89e3\u6bd4\u4ed6\u8fd8\u8981\u6df1\u523b\u3002\u9648\u9759\u4e5f\u662f\u5341\u4e00\u6708\u4e94\u65e5\u4ece\u9999\u6e2f\u98de\u56de\u5185\u5730\u4e4b\u540e\u624d\u4e0e\u5218\u660e\u8f89\u6709\u63a5\u89e6\uff0c\u5979\u53bb\u5e74\u4ece\u6b27\u6d32\u6700\u8457\u540d\u7684\u5546\u5b66\u9662\u7c73\u5170\u535a\u53ef\u5c3c\u5b66\u9662\u5b66\u6210\u5f52\u6765\uff0c\u6709\u7740\u975e\u5e38\u4e30\u5bcc\u7684\u7406\u8bba\u77e5\u8bc6\uff0c\u4f46\u662f\u56fd\u5185\u771f\u5b9e\u7684\u5e02\u573a\u53c8\u8ba9\u5979\u6709\u6478\u4e0d\u7740\u5934\u8111\u7684\u56f0\u60d1\uff0c\u6240\u5b66\u7684\u9ad8\u6df1\u7406\u8bba\u77e5\u8bc6\u65e0\u6cd5\u6709\u6548\u7684\u6307\u5bfc\u5b9e\u8df5\u5de5\u4f5c\uff0c\u8ba9\u5979\u6709\u65e0\u7528\u6b66\u4e4b\u5730\u7684\u7f3a\u61be\u3002\u4e0e\u5218\u660e\u8f89\u6709\u8fc7\u51e0\u6b21\u4ea4\u6d41\u4e4b\u540e\uff0c\u4e00\u4e9b\u95ee\u9898\u8c41\u7136\u5f00\u6717\uff0c\u83b7\u76ca\u532a\u6d45\uff0c\u5bf9\u5218\u660e\u8f89\u8fd9\u4e2a\u6bd5\u4e1a\u4e8e\u56fd\u5185\u4e8c\u6d41\u5927\u5b66\u3001\u5374\u6210\u957f\u4e8e\u56fd\u5185\u5e02\u573a\u8425\u9500\u5b9e\u6218\u4e2d\u7684\u7cbe\u82f1\u578b\u4eba\u624d\u81ea\u7136\u662f\u63a8\u5d07\u500d\u81f3\uff0c\u4e5f\u8ba4\u53ef\u4ed6\u5728\u79d1\u738b\u5e02\u573a\u90e8\u603b\u76d1\u7684\u5730\u4f4d\uff0c\u81ea\u7136\u4f1a\u53d7\u5230\u5218\u660e\u8f89\u7684\u4e00\u4e9b\u5f71\u54cd\u3002</p><p>\u3000\u3000\u65e0\u8bba\u51fa\u4e8e\u600e\u4e48\u7684\u9700\u8981\uff0c\u5218\u660e\u8f89\u90fd\u4e0d\u4f1a\u62e8\u9ad8\u5f20\u606a\u7684\u5f62\u8c61\uff0c\u66f4\u591a\u7684\u5c06\u7231\u8fbe\u5f53\u524d\u7684\u8f89\u714c\u5f52\u529f\u4e8e\u5f20\u606a\u5e78\u8fd0\u7684\u7f51\u7f57\u5230\u4e00\u6279\u582a\u79f0\u4e1a\u754c\u7cbe\u82f1\u7684\u4eba\u624d\uff0c\u4e01\u69d0\u4e4b\u4e8e\u7814\u53d1\u3001\u9c81\u5e86\u751f\u4e4b\u4e8e\u751f\u4ea7\u3001\u848b\u8587\u4e4b\u4e8e\u8d22\u52a1\u3001\u5468\u6e38\u3001\u6768\u4e91\u2014\u2014\u5305\u62ec\u4ed6\u5218\u660e\u8f89\u2014\u2014\u4e4b\u4e8e\u5e02\u573a\uff0c\u82cf\u6d25\u4e1c\u7edf\u8f96\u5168\u5c40\uff0c\u8fd9\u79cd\u79cd\u4eba\u7684\u5408\u529b\u624d\u4fc3\u6210\u7231\u8fbe\u7535\u5b50\u5f53\u4eca\u7684\u8f89\u714c\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u3001\u9648\u9759\u3001\u8c22\u77bb\u3001\u8d75\u9526\u8363\u3001\u8d75\u53f8\u660e\u7b49\u4eba\uff0c\u90fd\u4e3a\u9526\u6e56\u7684\u6210\u529f\u611f\u5230\u4e0d\u53ef\u601d\u8bae\uff0c\u611f\u5230\u4e07\u5206\u7684\u8be7\u5f02\uff0c\u4ed6\u4eec\u53ef\u4ee5\u901a\u8fc7\u79cd\u79cd\u6e20\u9053\u53bb\u8c03\u67e5\u9526\u6e56\u7684\u5404\u79cd\u60c5\u62a5\uff0c\u8fd9\u79cd\u8c03\u67e5\u5bf9\u4e8e\u4ed6\u4eec\u8ba4\u8bc6\u5f20\u606a\u8fd9\u4e2a\u4eba\uff0c\u5374\u6709\u7740\u96fe\u91cc\u770b\u82b1\u7684\u6726\u80e7\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u79cd\u79cd\u8a00\u884c\u592a\u8ba9\u4eba\u7591\u60d1\u4e0d\u89e3\uff0c\u4f5c\u4e3a\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u8fd8\u662f\u76f8\u5f53\u7684\u9ad8\u8c03\uff0c\u4fdd\u9556\u3001\u8dd1\u8f66\u3001\u79c1\u4eba\u52a9\u7406\uff0c\u5468\u8f6c\u4e8e\u5404\u8272\u7f8e\u5973\u4e4b\u95f4\uff1b\u4f5c\u4e3a\u4e00\u4e2a\u5728\u5e55\u540e\u638c\u63e1\u5982\u6b64\u5de8\u5927\u8d22\u5bcc\u7684\u9752\u5e74\uff0c\u5374\u672a\u514d\u592a\u6c89\u5bc2\u4e86\u3002</p><p>\u3000\u3000\u73b0\u4ee3\u793e\u4f1a\u662f\u4e00\u4e2a\u53bb\u795e\u5316\u7684\u5f00\u653e\u6027\u793e\u4f1a\uff0c\u4e00\u4e2a\u7406\u667a\u6b63\u5e38\u7684\u4eba\u90fd\u4e0d\u53ef\u80fd\u65e0\u9650\u5236\u7684\u53bb\u5d07\u62dc\u4ed6\u7684\u540c\u7c7b\uff0c\u4f46\u662f\u4ed6\u4eec\u8981\u7ed9\u9526\u6e56\u7684\u5de8\u5927\u6210\u529f\u627e\u5230\u5408\u7406\u7684\u89e3\u91ca\u3002\u8fd9\u4e00\u70b9\u4e00\u76f4\u8ba9\u4ed6\u4eec\u90fd\u5f88\u56f0\u60d1\uff0c\u5218\u660e\u8f89\u7684\u8a00\u8bba\u5f88\u8d34\u5408\u4ed6\u4eec\u5185\u5fc3\u6700\u9700\u8981\u7684\u90a3\u4e2a\u7b54\u6848\uff1a\u5f20\u606a\u5c11\u5e74\u8001\u6210\u4e0d\u5047\uff0c\u6709\u624d\u534e\u4e0d\u5047\uff0c\u4f46\u4ed6\u6700\u5927\u7684\u6210\u529f\u662f\u4ed6\u5e78\u8fd0\u7684\u7f51\u7f57\u4e86\u4e00\u6279\u90fd\u65e0\u6127\u4e8e\u4e1a\u5185\u7cbe\u82f1\u7684\u4eba\u624b\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u5f53\u4e4b\u65e0\u6127\u662f\u521b\u5efa\u7231\u8fbe\u7535\u5b50\u7684\u5143\u8001\uff0c\u5f20\u606a\u90fd\u65e0\u6cd5\u5426\u8ba4\u8fd9\u4e00\u70b9\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u8fd8\u662f\u4ed6\u4ece\u5b89\u5fbd\u4e07\u71d5\u96c6\u56e2\u8bf7\u5230\u6d77\u5dde\u7684\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u5148\u540e\u5206\u522b\u51fa\u4efb\u4e24\u5bb6\u516c\u53f8\u7684\u603b\u88c1\uff0c\u8fd8\u90fd\u76f8\u7ee7\u5728\u76f8\u5173\u4f01\u4e1a\u6301\u80a1\uff0c\u5230\u6d77\u5dde\u4e0d\u5230\u4e24\u5e74\u65e9\u5c31\u6210\u4e86\u5343\u4e07\u5bcc\u7fc1\uff1b\u5468\u6e38\u51fa\u4efb\u65b0\u5149\u7eb8\u4e1a\u603b\u88c1\uff0c\u867d\u7136\u5e74\u85aa\u53ea\u6709\u4e09\u5341\u4e07\uff0c\u4f46\u4ed6\u5728\u65b0\u5149\u7eb8\u4e1a\u3001\u7231\u8fbe\u7535\u5b50\u7b49\u76f8\u5173\u4f01\u4e1a\u90fd\u6709\u81ea\u5df1\u7684\u80a1\u4efd\uff1b\u8fd9\u5012\u4e5f\u7f62\u4e86\uff0c\u848b\u8587\u3001\u6768\u4e91\u3001\u5468\u4e00\u5e73\u5728\u7231\u8fbe\u7535\u5b50\u7684\u5f85\u9047\uff0c\u624d\u8ba9\u5218\u660e\u8f89\u5185\u5fc3\u4ea7\u751f\u6700\u5927\u7684\u5931\u8861\uff0c\u848b\u8587\u662f\u5206\u7ba1\u8d22\u52a1\u7684\u526f\u603b\u88c1\uff0c\u5e76\u4e14\u5f97\u5230\u8fc7\u4e00\u6b21\u7684\u80a1\u6743\u6fc0\u52b1\uff0c\u4ec5\u4ece\u7231\u8fbe\u7535\u5b50\u521b\u5efa\u7684\u5386\u4e2d\u6765\u8bf4\uff0c\u6768\u4e91\u3001\u5468\u4e00\u5e73\u90fd\u6bd4\u4ed6\u665a\u5230\u7231\u8fbe\u7535\u5b50\uff0c\u8fd9\u65f6\u5019\u4e5f\u90fd\u72ec\u81ea\u8d1f\u8d23\u4e00\u4e2a\u4e00\u7ea7\u90e8\u95e8\uff0c\u62ff\u5230\u80a1\u6743\u6fc0\u52b1\u4e5f\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\u3002\u4ee5\u7231\u8fbe\u7535\u5b50\u5f53\u524d\u7684\u76c8\u5229\u6c34\u5e73\uff0c\u90a3\u6015\u5f88\u5c11\u90e8\u5206\u7684\u80a1\u6743\u6fc0\u52b1\u90fd\u610f\u5473\u4e0a\u767e\u4e07\u7684\u8d22\u5bcc\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u5f20\u606a\u5374\u8981\u5218\u660e\u8f89\u53bb\u8d1f\u8d23\u4e00\u4e2a\u4e8c\u7ea7\u90e8\u95e8\u3002</p><p>\u3000\u3000\u5982\u6b64\u62b1\u6028\u7684\u5218\u660e\u8f89\u7684\u786e\u5bb9\u6613\u5f15\u8d77\u522b\u4eba\u7684\u540c\u60c5\uff0c\u9648\u9759\u4e5f\u8ba4\u4e3a\u8981\u5218\u660e\u8f89\u4ec5\u4ec5\u53bb\u8d1f\u8d23\u4e00\u4e2a\u4e8c\u7ea7\u90e8\u95e8\u6709\u4e9b\u592a\u5c48\u624d\uff0c\u66f4\u4f55\u51b5\u5218\u660e\u8f89\u7ed9\u7231\u8fbe\u7535\u5b50\u4eca\u5929\u7684\u8f89\u714c\u7acb\u4e0b\u6c57\u9a6c\u529f\u52b3\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u9648\u9759\u4e0e\u5218\u660e\u8f89\u63a5\u89e6\u4e5f\u5c31\u8fd9\u51e0\u5929\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u8fd9\u51e0\u5929\u7684\u63a5\u89e6\u8ba9\u5979\u5bf9\u5f20\u606a\u7684\u8ba4\u8bc6\u66f4\u590d\u6742\u4e86\uff1a\u4e00\u4e2a\u6709\u7740\u4ee4\u4eba\u60ca\u8273\u5546\u4e1a\u5929\u8d4b\u7684\u9752\u5e74\uff0c\u6709\u7740\u660e\u4fca\u6e05\u79c0\u7684\u8ff7\u4eba\u8138\u5e9e\uff0c\u51e0\u4e4e\u80fd\u79f0\u5f97\u4e0a\u5b8c\u7f8e\uff0c\u8981\u662f\u6c14\u91cf\u4e0d\u90a3\u4e48\u72ed\u9698\u7684\u8bdd\u3002\u4eca\u5929\u5f20\u606a\u7684\u8868\u73b0\uff0c\u66f4\u8ba9\u5979\u5931\u671b\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u90a3\u5929\uff0c\u5f20\u606a\u4ece\u9999\u6e2f\u8d76\u56de\u6765\uff0c\u5728\u4f60\u529e\u516c\u5ba4\u91cc\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d\u5230\u4e86\u4e0e\u8c2d\u5e0c\u677e\u7ea6\u5b9a\u7684\u65f6\u95f4\uff0c\u8c22\u5251\u5357\u4ece\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u5927\u697c\u8d35\u5bbe\u5ba4\u91cc\u7684\u6d0b\u7070\u8272\u6c99\u53d1\u4e0a\u7ad9\u8d77\u6765\uff0c\u6709\u610f\u65e0\u610f\u7684\u95ee\u5218\u660e\u8f89\u4e00\u53e5\uff0c\u624b\u5374\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u5c06\u6536\u62fe\u597d\u7684\u8d44\u6599\u4ea4\u7ed9\u9648\u9759\uff0c\u8ba9\u5979\u5e2e\u81ea\u5df1\u62ff\u7740\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u5fc3\u91cc\u4e00\u51db\uff0c\u4ed6\u60f3\u8d77\u5f20\u606a\u90a3\u5929\u5173\u4e0a\u529e\u516c\u5ba4\u95e8\u4e4b\u540e\u5c31\u8bf4\u7684\u90a3\u53e5\u8bdd\uff1a\u201c\u6211\u8981\u4e0d\u4ece\u9999\u6e2f\u8d76\u56de\u6765\uff0c\u79d1\u738b\u90a3\u4e9b\u4eba\u600e\u4e48\u80fd\u77e5\u9053\u4f60\u5bf9\u7231\u8fbe\u7684\u91cd\u8981\u6027\uff1f\u4f60\u7ed9\u7231\u8fbe\u505a\u51fa\u8fd9\u4e48\u5927\u7684\u8d21\u732e\uff0c\u8fd9\u4efd\u793c\uff0c\u6211\u8981\u9001\u7ed9\u4f60\u7684\u2026\u2026\u201d\u4e4b\u540e\u53ea\u662f\u534a\u4e2a\u591a\u5c0f\u65f6\u7684\u95f2\u626f\u800c\u5df2\uff0c\u8fd9\u6837\u7684\u771f\u76f8\u81ea\u7136\u65e0\u6cd5\u5f00\u53e3\u4e0e\u8c22\u5251\u5357\u5b9e\u8bdd\uff0c\u8bf4\u4e86\u522b\u4eba\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u5218\u660e\u8f89\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u65e0\u975e\u662f\u4e9b\u633d\u7559\u7684\u8bdd\uff0c\u5230\u6700\u540e\u4e5f\u6709\u4e9b\u6495\u7834\u8138\uff0c\u5f20\u606a\u4eca\u5929\u7684\u4f5c\u4e3a\uff0c\u60f3\u5fc5\u4f60\u4e5f\u770b\u5230\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff1f\u201d\u8c22\u5251\u5357\u5634\u89d2\u6302\u51fa\u6d45\u7b11\uff0c\u5f20\u606a\u4eca\u5929\u7684\u8868\u73b0\u4f3c\u4e4e\u6b63\u8868\u660e\u5218\u660e\u8f89\u7684\u79bb\u53bb\u51fb\u4e2d\u7231\u8fbe\u7684\u75db\u5904\u4e86\u3002</p><p>\u3000\u3000\u201c\u592e\u89c6\u4f1a\u4e0d\u4f1a\u505a\u51fa\u8ba9\u6b65\uff1f\u201d\u9648\u9759\u5bf9\u63a5\u4e0b\u6765\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u8ba8\u4ef7\u8fd8\u4ef7\u5fc3\u4e2d\u6ca1\u5e95\uff0c\u5979\u4e0e\u5218\u660e\u8f89\u966a\u8c22\u5251\u5357\u8fc7\u53bb\u8ddf\u4e2d\u592e\u7535\u89c6\u53f0\u5e7f\u544a\u4fe1\u606f\u90e8\u771f\u6b63\u7b7e\u7f72\u5408\u4f5c\u534f\u8bae\u3002</p><p>\u3000\u3000\u201c\u90a3\u662f\u4e00\u5b9a\u7684\uff0c\u201d\u8c22\u5251\u5357\u5f88\u6709\u81ea\u4fe1\u7684\u8bf4\u9053\uff0c\u4ed6\u5f88\u8d5e\u540c\u5218\u660e\u8f89\u7684\u90a3\u53e5\u8bdd\uff1a\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u592e\u89c6\u6bd4\u8c01\u90fd\u5e0c\u671b\u593a\u5f97\u6807\u738b\u7684\u4f01\u4e1a\u80fd\u56e0\u6b64\u83b7\u5f97\u5947\u8ff9\u822c\u7684\u5e02\u573a\u4f73\u7ee9\uff0c\u8ba9\u5168\u56fd\u6240\u6709\u7684\u4f01\u4e1a\u770b\u5230\u592e\u89c6\u5e7f\u544a\u7684\u9b54\u529b\u3002\u4ed6\u8f6c\u8eab\u4eb2\u871c\u7684\u634f\u4e86\u634f\u9648\u9759\u67d4\u5ae9\u7684\u8138\u988a\uff0c\u201c\u592e\u89c6\u7edd\u4e0d\u4f1a\u505a\u51fa\u81ea\u7838\u62db\u724c\u7684\u4e8b\u60c5\uff0c\u8fd9\u5c31\u662f\u592e\u89c6\u7684\u5e95\u7ebf\uff0c\u53ea\u8981\u77e5\u9053\u592e\u89c6\u7684\u5e95\u7ebf\uff0c\u8ddf\u592e\u89c6\u5408\u4f5c\u8d77\u6765\u5c31\u5982\u6c34\u5f97\u9c7c\u3002\u201d</p><p>\u3000\u3000\u53ef\u60dc\uff0c\u8c22\u5251\u5357\u4ed6\u4e0d\u77e5\u9053\u8fd9\u4e00\u6b21\u7684\u5bf9\u624b\u8fd8\u4e0d\u662f\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u4e3b\u4efb\u8c2d\u5e0c\u677e\uff0c\u4f9d\u7136\u662f\u6f5c\u85cf\u5728\u80cc\u540e\u7684\u5f20\u606a\u3002</p><p>\u3000\u3000\u592e\u89c6\u65e9\u6709\u7ed9\u4e0a\u5348\u593a\u6807\u7684\u5341\u4e09\u5bb6\u5382\u5546\u8fdb\u884c\u8865\u507f\u7684\u610f\u601d\uff0c\u4f46\u662f\u51c6\u5907\u62ff\u51fa\u6765\u8865\u507f\u7684\u5e7f\u544a\u8d44\u6e90\u4e5f\u662f\u6709\u9650\u7684\uff0c\u9700\u8981\u5728\u8fd9\u5341\u4e09\u5bb6\u5382\u5546\u4e4b\u95f4\u8fdb\u884c\u5206\u914d\u3002</p><p>\u3000\u3000\u82e5\u662f\u5176\u4ed6\u5341\u4e8c\u5bb6\u5382\u5546\u90fd\u4e0d\u77e5\u9053\u5728\u4e2d\u592e\u7535\u89c6\u53f0\u91cc\u8fd8\u53ef\u4ee5\u8ba8\u4ef7\u8fd8\u4ef7\u7684\u5b9e\u60c5\uff0c\u592e\u89c6\u4e00\u5b9a\u4f1a\u5c3d\u53ef\u80fd\u7684\u7ed9\u6807\u738b\u4f01\u4e1a\u6700\u591a\u7684\u8d44\u6e90\uff0c\u751a\u81f3\u90fd\u4e0d\u9700\u8981\u8c22\u5251\u5357\u81ea\u5df1\u53bb\u52aa\u529b\u3002</p><p>\u3000\u3000\u73b0\u5728\uff0c\u5341\u4e09\u5bb6\u5382\u5546\u8c01\u4e0d\u60f3\u83b7\u5f97\u66f4\u591a\u7684\u5e7f\u544a\u8d44\u6e90\uff1f</p><p>\u3000\u3000\u4f1a\u54ed\u7684\u5b69\u5b50\u6709\u5976\u5403\uff0c\u5927\u5bb6\u90fd\u5230\u4e2d\u592e\u7535\u89c6\u53f0\u6765\u62a2\u6807\u738b\u5e7f\u544a\u4e86\uff0c\u8fd9\u70b9\u6d45\u663e\u7684\u9053\u7406\uff0c\u53c8\u600e\u4e48\u4f1a\u662f\u4ed6\u4eec\u7406\u4f1a\u4e0d\u900f\u7684\u3002</p><p>\u3000\u3000\u8c2d\u5e0c\u677e\u8fd8\u6253\u7b97\u5c06\u8fd9\u8fb9\u7684\u4e8b\u60c5\u5904\u7406\u5b8c\u4e4b\u540e\u53bb\u73b0\u573a\u770b\u4e00\u770b\uff0c\u7ed3\u675f\u7684\u81f4\u8f9e\u4e5f\u51c6\u5907\u597d\u4e86\uff0c\u4f46\u662f\u5979\u770b\u5230\u7684\u5c40\u9762\u8ba9\u5979\u5fcd\u4e0d\u4f4f\u60f3\u6293\u72c2\uff0c\u8fd9\u5341\u4e09\u5bb6\u5382\u5546\u4f3c\u4e4e\u6bcf\u4e00\u5bb6\u90fd\u6293\u4f4f\u592e\u89c6\u7684\u8f6f\u808b\uff0c\u4e0d\u80af\u8f7b\u6613\u5c31\u8303\uff0c\u8c2d\u5e0c\u677e\u4e0d\u5f97\u4e0d\u6307\u793a\u5b89\u6392\u5728\u9ec4\u660f\u65f6\u5206\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\u63a8\u8fdf\uff0c\u4f46\u662f\u4e0e\u5341\u4e09\u5bb6\u5382\u5546\u6328\u4e2a\u8c08\u5224\uff0c\u5c31\u7b97\u6709\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u90fd\u6709\u53ef\u80fd\u8c08\u4e0d\u51fa\u5934\u7eea\u3002\u6700\u540e\u53f0\u957f\u4eb2\u81ea\u62cd\u677f\uff0c\u7d27\u6025\u5c06\u672c\u6765\u5b89\u6392\u4e0b\u5348\u7ade\u6807\u7684\u4e00\u4e9b\u6b21\u8981\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u64a4\u4e0b\u6765\uff0c\u589e\u52a0\u5bf9\u8fd9\u4e9b\u5382\u5546\u7684\u8865\u507f\u529b\u5ea6\uff0c\u4f46\u662f\u5177\u4f53\u7684\u8865\u507f\u65b9\u6848\u5fc5\u9700\u7531\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u51b3\u5b9a\uff0c\u5382\u5546\u4e0d\u59a5\u534f\u7684\u8bdd\uff0c\u53f0\u91cc\u5b81\u53ef\u5e9f\u6807\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e2d\u592e\u7535\u89c6\u53f0\u6709\u8fd9\u4e48\u5927\u7684\u5e95\u6c14\uff0c\u4e5f\u6e90\u4e8e\u8c2d\u5e0c\u677e\u4e2d\u5348\u4e0e\u8c22\u665a\u6674\u5171\u8fdb\u5348\u9910\u65f6\uff0c\u8c22\u665a\u6674\u5bf9\u79d1\u738b\u593a\u6807\u7684\u8d44\u91d1\u5b9e\u529b\u8868\u793a\u6000\u7591\uff0c\u6697\u793a\u79d1\u738b\u7535\u5668\u4e00\u65e6\u65e0\u529b\u627f\u62c5\u4e0e\u592e\u89c6\u4e4b\u95f4\u7684\u5408\u4f5c\uff0c\u7231\u8fbe\u7535\u5b50\u613f\u610f\u63a5\u8fc7\u6765\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u5408\u4f5c\uff0c\u4f9d\u7167\u53bb\u5e74\u7684\u5408\u4f5c\u65b9\u6848\uff0c\u7231\u8fbe\u7535\u5b50\u53ef\u4ee5\u51fa\u4ef7\u4e24\u4ebf\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u597d\u5427\uff0c\u51fa\u73b0\u773c\u4e0b\u7684\u5c40\u9762\uff0c\u8c2d\u5e0c\u677e\u4e5f\u4e0d\u4f1a\u5bf9\u7231\u8fbe\u7535\u5b50\u6beb\u65e0\u6000\u7591\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u9ad8\u7ea7\u7ecf\u7406\u4eba\u62c9\u4eba\u8df3\u69fd\u5230\u79d1\u738b\u7535\u5668\uff0c\u4e5f\u662f\u4e00\u4e2a\u4e8b\u5b9e\uff0c\u5e76\u4e0d\u4ee3\u8868\u5176\u4ed6\u5382\u5546\u6ca1\u6709\u806a\u660e\u4eba\u3002</p><p>\u3000\u3000\u8c2d\u5e0c\u677e\u53ea\u80fd\u5f3a\u786c\u7684\u8981\u6c42\u79d1\u738b\u63a5\u53d7\u53bb\u5e74\u592e\u89c6\u4e0e\u7231\u8fbe\u7535\u5b50\u4e4b\u95f4\u7684\u5408\u4f5c\u65b9\u6848\uff0c\u4e0d\u7136\u5979\u6ca1\u6709\u80fd\u529b\u5b89\u629a\u4eca\u5929\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u4e0d\u9519\uff0c\u5b8c\u5168\u662f\u592e\u89c6\u4e0e\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u5ea6\u7684\u5408\u4f5c\u65b9\u6848\uff0c\u53ea\u4e0d\u8fc7\u7231\u8fbe\u4e3a\u6b64\u53ea\u652f\u4ed8\u4e0d\u5230\u516b\u5343\u4e07\uff0c\u79d1\u738b\u5728\u4e00\u5e74\u540e\u7684\u73b0\u5728\u5374\u8981\u652f\u4ed8\u4e09\u4ebf\u4e94\u5343\u4e07\u3002\u8fd9\u4e0e\u8c22\u5251\u5357\u7b49\u4eba\u5fc3\u4e2d\u7684\u671f\u5f85\u6709\u5f88\u5927\u7684\u5dee\u8ddd\uff0c\u4e00\u76f4\u62d6\u5ef6\u5230\u4e0b\u5348\u4e94\u70b9\u949f\uff0c\u592e\u89c6\u624d\u5728\u4ed8\u6b3e\u65b9\u5f0f\u4e0a\u6709\u6240\u9000\u6b65\u3002</p><p>\u3000\u3000\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u5408\u4f5c\u65b9\u6848\uff0c\u5e7f\u544a\u8d39\u9010\u6708\u652f\u4ed8\uff0c\u79d1\u738b\u9010\u6708\u652f\u4ed880\uff05\uff0c\u5728\u4e0b\u4e00\u5c4a\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u4e4b\u524d\u7ed3\u6e05\u4f59\u6b3e\uff0c\u8fd9\u4e00\u65b9\u6848\u4ec5\u80fd\u591f\u7a0d\u7a0d\u7f13\u89e3\u79d1\u738b\u524d\u671f\u7684\u8d44\u91d1\u538b\u529b\u3002</p><p>\u3000\u3000\u4e2d\u592e\u7535\u89c6\u53f0\u5012\u4e5f\u4e0d\u6015\u6709\u54ea\u5bb6\u4f01\u4e1a\u4f1a\u8d56\u8d26\uff0c\u5f53\u7136\u8fd9\u5bb6\u4f01\u4e1a\u82e5\u662f\u7834\u4ea7\u5012\u95ed\uff0c\u8fd9\u4e2a\u95ee\u9898\u53ea\u80fd\u522b\u8bf4\u3002</p><p>\u3000\u3000\u79d1\u738b\u677e\u4e86\u53e3\u5b50\uff0c\u5269\u4e0b\u7684\u5341\u4e8c\u5bb6\u5382\u5546\u90fd\u6709\u4e86\u53c2\u7167\u7684\u6807\u51c6\uff0c\u592e\u89c6\u8865\u507f\u7684\u5e7f\u544a\u8d44\u6e90\u6bd4\u4ed6\u4eec\u6240\u8bbe\u60f3\u7684\u8981\u591a\uff0c\u90fd\u975e\u5e38\u4e50\u610f\u7684\u5728\u6700\u7ec8\u534f\u8bae\u4e0a\u7b7e\u5b57\u3002</p><p>\u3000\u3000\u8c2d\u5e0c\u677e\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u62b9\u7740\u4e00\u5934\u6c57\uff0c\u5374\u5728\u60f3\u660e\u5e74\u7684\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u5e94\u8be5\u63d0\u524d\u9884\u9632\u8fd9\u79cd\u60c5\u51b5\u7684\u51fa\u73b0\uff0c\u53bb\u5e74\u7ed9\u7231\u8fbe\u7535\u5b50\u641e\u5f97\u63aa\u624b\u4e0d\u53ca\uff0c\u4eca\u5e74\u5012\u662f\u4e00\u8d77\u6765\u95f9\u7ffb\u5929\uff0c\u5374\u4e0d\u6653\u5f97\u8fd9\u5c06\u662f\u53f0\u91cc\u65e5\u540e\u5c06\u5979\u4ece\u5e7f\u544a\u4fe1\u606f\u90e8\u4e3b\u4efb\u5b9d\u5ea7\u4e0a\u62ff\u4e0b\u6765\u7684\u501f\u53e3\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u8c2d\u5e0c\u677e\u4e5f\u6ca1\u529e\u6cd5\u6b47\u529b\uff0c\u4e0b\u5348\u7684\u62db\u5546\u5927\u4f1a\u5df2\u7ecf\u8fdb\u5165\u5c3e\u58f0\u4e86\uff0c\u5979\u4e5f\u5b8c\u5168\u987e\u4e0d\u4e0a\uff0c\u7531\u4e00\u540d\u526f\u53f0\u957f\u8fc7\u53bb\u5750\u9547\uff0c\u5979\u8981\u7740\u624b\u5b89\u6392\u65b0\u95fb\u53d1\u5e03\u4f1a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u3001\u5218\u660e\u8f89\u3001\u9648\u9759\u4e09\u4eba\u56de\u5230\u592e\u89c6\u7ed9\u4ed6\u4eec\u4e13\u95e8\u51c6\u5907\u7684\u8d35\u5bbe\u5ba4\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u8c22\u77bb\u4e0e\u90c1\u840d\u624d\u4ece\u5916\u9762\u56de\u6765\uff0c\u8c22\u77bb\u9a82\u9a82\u51bd\u51bd\u7684\u8bf4\uff1a\u201c\u90a3\u9e1f\u6bdb\u8fd8\u6ca1\u957f\u9f50\u7684\u5c0f\u5b50\uff0c\u771f\u4e0d\u6653\u5f97\u4ed6\u4eec\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u4ed6\u4eec\u6574\u4e2a\u4e0b\u5348\u90fd\u6ca1\u6709\u4e3e\u724c\uff1f\u201d\u8c22\u5251\u5357\u8be7\u5f02\u7684\u95ee\uff0c\u7231\u8fbe\u7535\u5b50\u4e0d\u53ef\u80fd\u5b8c\u5168\u653e\u5f03\u592e\u89c6\u7684\u5e7f\u544a\u8d44\u6e90\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u6709\u51fa\u624b\uff0c\u516b\u70b9\u6863\u7535\u89c6\u5267\u7247\u5934\u7247\u5c3e\u5171\u4e09\u5341\u79d2\u5e7f\u544a\u65f6\u6bb5\uff0c\u4ed6\u4eec\u7b2c\u4e00\u4e2a\u4e3e\u724c\uff0c\u800c\u4e14\u76f4\u63a5\u62a5\u4ef7\u4e24\u5343\u516d\u767e\u4e07\uff0c\u771f\u4ed6\u5a18\u7684\u90aa\u95e8\u4e86\uff0c\u6123\u662f\u6ca1\u4eba\u4e3e\u724c\u8ddf\u4ed6\u4eec\u4e89\uff1b\u63a5\u4e0b\u6765\u65b0\u79d1\u7535\u5b50\u4e3e\u724c\u4e5f\u662f\u4e00\u6837\uff0c\u76f4\u63a5\u558a\u51fa\u4e00\u4e2a\u534a\u9ad8\u4e0d\u9ad8\u7684\u4ef7\u7801\uff0c\u4e5f\u6ca1\u6709\u4eba\u8ddf\u4ed6\u4eec\u7ade\u4e89\u3002\u9664\u4e86\u2018\u516b\u70b9\u6863\u7535\u89c6\u5267\u7247\u5934\u7247\u5c3e\u63d2\u2019\u4e4b\u5916\uff0c\u7231\u8fbe\u7535\u5b50\u8fd8\u62ff\u4e0b\u592e\u89c6\u51e0\u4e2a\u680f\u76ee\u7684\u8d5e\u52a9\u5546\u5e7f\u544a\uff0c\u6625\u8282\u8054\u8c0a\u665a\u4f1a\u7684\u8d5e\u52a9\u5546\u5e7f\u544a\uff0c\u4ed6\u4eec\u4e5f\u62ff\u8d70\u4e00\u4e2a\uff0c\u4e0d\u8fc7\u4e0b\u5348\u7684\u62db\u5546\u5927\u4f1a\uff0c\u5b8c\u5168\u4e0d\u89c1\u4e0a\u5348\u7684\u8840\u8165\u6c14\uff0c\u7231\u8fbe\u7535\u5b50\u62ff\u4e0b\u8fd9\u4e9b\u5e7f\u544a\u65f6\u6bb5\uff0c\u603b\u7ade\u4ef7\u624d\u516d\u5343\u4e07\uff0c\u6bd4\u4ed6\u4eec\u53bb\u5e74\u6295\u592e\u89c6\u7684\u94b1\u8fd8\u5c11\u3002\u201d\u8c22\u77bb\u4e0b\u5348\u53bb\u4f1a\u573a\u76ef\u7740\u7231\u8fbe\u7535\u5b50\u6709\u4ec0\u4e48\u52a8\u9759\uff0c\u770b\u5230\u4e0b\u5348\u4f1a\u573a\u5e73\u6de1\u5982\u6c34\u7684\u6e05\u51b7\uff0c\u60f3\u8d77\u4e0a\u5348\u4ed6\u4eec\u79d1\u738b\u51e0\u4e4e\u8e0f\u7740\u788e\u5c38\u8089\u96e8\u51b2\u51fa\u91cd\u56f4\uff0c\u593a\u5f97\u6807\u738b\u540e\u7684\u6fc0\u52a8\u987f\u65f6\u7ed9\u51b2\u6389\u4e00\u534a\uff0c\u4ed6\u4e0d\u662f\u4e0d\u660e\u767d\u5176\u4e2d\u7684\u9053\u7406\u2014\u2014\u6700\u6709\u5b9e\u529b\u7684\u5382\u5546\u5df2\u7ecf\u4e0d\u5728\uff0c\u7231\u8fbe\u7535\u5b50\u4e0a\u5348\u7684\u6c89\u9ed8\u4e0d\u4ec5\u7ed9\u79d1\u738b\u4f17\u4eba\u5f88\u5927\u7684\u5fc3\u7406\u538b\u529b\uff0c\u4e5f\u7ed9\u5176\u4ed6\u5382\u5546\u5f88\u5927\u538b\u529b\uff0c\u518d\u52a0\u4e0a\u4e0b\u5348\u7ade\u62cd\u7684\u5e7f\u544a\u8d44\u6e90\u76f8\u5bf9\u4e30\u5bcc\uff0c\u7231\u8fbe\u4e00\u51fa\u624b\u5c31\u662f\u9ad8\u4ef7\uff0c\u8868\u8fbe\u51fa\u5f88\u5f3a\u70c8\u7684\u6b32\u671b\uff0c\u81ea\u7136\u4eba\u6ca1\u6709\u613f\u610f\u51fa\u6765\u8ddf\u7231\u8fbe\u4e89\u62a2\u2014\u2014\u8fd9\u4e9b\u9053\u7406\u4ed6\u4e0d\u662f\u4e0d\u61c2\uff0c\u53ea\u662f\u6709\u4e9b\u4e0d\u7518\u5fc3\u7f62\u4e86\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u8f7b\u8f7b\u62cd\u4e86\u62cd\u8c22\u77bb\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u606a\u8fd9\u4eba\u96be\u7f20\uff0c\u4f60\u53c8\u4e0d\u662f\u7b2c\u4e00\u5929\u624d\u77e5\u9053\uff0c\u63a5\u4e0b\u6765\uff0c\u624d\u662f\u8981\u6253\u8d77\u7cbe\u795e\u6765\u7684\u65f6\u523b\uff0c\u8981\u505a\u7684\u4e8b\u60c5\u592a\u591a\u4e86\uff0c\u8fd8\u8981\u9884\u9632\u5f20\u606a\u4ed6\u5728\u80cc\u540e\u4f7f\u7eca\uff0c\u6211\u60f3\u63a5\u4e0b\u6765\u7684\u8fd9\u6bb5\u65f6\u95f4\uff0c\u5927\u5bb6\u90fd\u4e0d\u4f1a\u8f7b\u677e\uff0c\u6211\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u662f\u7ed9\u5927\u5bb6\u6253\u6253\u9884\u9632\u9488\uff0c\u4e0d\u8981\u5230\u65f6\u5019\u602a\u6211\u903c\u4f60\u4eec\u592a\u7d27\u3002\u201d</p><p>\u3000\u3000\u8c22\u77bb\u8bf4\u9053\uff1a\u201c\u6253\u5e02\u573a\u7684\u4e8b\u60c5\uff0c\u6211\u5c31\u8131\u624b\u4e86\uff0c\u6211\u8d1f\u8d23\u5e74\u524d\u5c06\u6708\u4ea7\u91cf\u63d0\u9ad8\u5230\u5341\u4e07\u53f0\uff0c\u4f46\u662f\u81f3\u5c11\u8fd8\u7f3a\u4e00\u4e2a\u4ebf\u7684\u8d44\u91d1\u3002\u6d77\u5dde\u5e02\u7684\u96f6\u914d\u4ef6\u5546\uff0c\u90fd\u4ed6\u5a18\u7684\u8ddf\u7231\u8fbe\u7a7f\u4e00\u6761\u88e4\u5b50\u7684\uff0c\u4e1c\u897f\u53ef\u4ee5\u4e70\uff0c\u4f46\u90fd\u8981\u6c42\u63d0\u524d\u4ed8\u6b3e\uff0c\u66f4\u4e0d\u8981\u8bf4TI\u3001\u65af\u9ad8\u67cf\u3001\u7d22\u5c3c\u3001\u98de\u5229\u6d66\u8fd9\u4e9b\u4f9b\u8d27\u5546\u4e86\uff0c\u8fd9\u6837\u6211\u4eec\u5728\u91c7\u8d2d\u73af\u8282\u817e\u632a\u4e0d\u51fa\u4e00\u5206\u94b1\u51fa\u6765\uff0c\u201d\u8c22\u77bb\u63d0\u8d77\u8fd9\u4e2a\u5c31\u4e00\u809a\u5b50\u706b\uff0c\u4ed6\u4ee5\u524d\u505a\u5b66\u4e60\u673a\u65f6\uff0c\u54ea\u4e2a\u4f9b\u5e94\u5546\u4e0d\u7ed9\u4ed6\u5360\u4e09\u4e2a\u6708\u4ee5\u4e0a\u7684\u6b3e\uff1f\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u90fd\u8ddf\u8fd9\u4e9b\u4f9b\u8d27\u5546\u73b0\u6b3e\u73b0\u8d27\u4ea4\u6613\uff0c\u6b64\u65f6\u7684\u79d1\u738b\u96be\u9053\u8fd8\u80fd\u6bd4\u7231\u8fbe\u5bf9\u4f9b\u5e94\u5546\u66f4\u5f3a\u52bf\uff0c\u201c\u5143\u914d\u4ef6\u8981\u63d0\u524d\u4e00\u4e2a\u6708\u91c7\u8d2d\uff0c\u90a3\u5c31\u8981\u591a\u5360\u7528\u6211\u4eec\u4e00\u4e2a\u6708\u7684\u8d44\u91d1\u6d41\u52a8\uff0c\u4e5f\u5c31\u662f\u8bf4\u8fd9\u4e00\u4e2a\u4ebf\uff0c\u4f60\u9a6c\u4e0a\u5c31\u5f97\u7ed9\u6211\u51c6\u5907\u597d\uff0c\u4ee5\u540e\u4ea7\u91cf\u8981\u4e0a\u4e8c\u5341\u4e07\u53f0\uff0c\u4ec5\u751f\u4ea7\u8fd9\u8fb9\u7684\u6d41\u52a8\u8d44\u91d1\u5c31\u8981\u56db\u4e2a\u4ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u6574\u673a\u7ec4\u88c5\u5382\u5546\u5728\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u4e0e\u5341\u51e0\u5e74\u540e\u7684\u201c\u5c71\u5be8\u5382\u201d\u6982\u5ff5\u76f8\u4eff\uff0c\u5176\u5b9e\u6b64\u65f6\u4e5f\u6709\u6240\u8c13\u7684\u201c\u95e8\u677f\u5382\u201d\u3001\u201c\u5c71\u5be8\u5382\u201d\u7684\u8bf4\u6cd5\uff0c\u53ea\u662f\u4e0d\u5e7f\u4e3a\u4eba\u77e5\u7f62\u4e86\uff0c\u6574\u673a\u7ec4\u88c5\u5382\u5546\u5728\u56fa\u5b9a\u8d44\u4ea7\u4e0a\u7684\u6295\u8d44\u76f8\u5f53\u6709\u9650\uff0c\u5012\u662f\u6d41\u52a8\u8d44\u91d1\u7684\u9700\u6c42\u91cf\u76f8\u5bf9\u8f83\u5927\uff0c\u79d1\u738b\u82e5\u662f\u65e0\u6cd5\u4ece\u4f9b\u5e94\u5546\u90a3\u91cc\u5360\u6b3e\uff0c\u8d44\u91d1\u538b\u529b\u4f1a\u66f4\u5927\u3002\u6b63\u6cf0\u4e0e\u9526\u57ce\u5408\u8d44\u7ec4\u5efa\u79d1\u738b\u751f\u4ea7\u5f71\u789f\u673a\uff0c\u52a0\u4e0a\u8c22\u77bb\u7684\u4e2a\u4eba\u8d44\u91d1\u4e0e\u6d77\u6cf0\u7684\u6295\u8d44\uff0c\u603b\u8d44\u672c\u91d1\u8fd8\u4e0d\u8db3\u4e00\u4e2a\u4ebf\uff0c\u8c22\u5251\u5357\u8ba1\u5212\u5927\u5e72\u4e00\u628a\uff0c\u62c9\u9648\u5bb6\u5584\u7684\u5927\u96c5\u96c6\u56e2\u5165\u80a1\uff0c\u51e0\u5bb6\u6ce8\u8d44\u624d\u8fbe\u5230\u4e24\u4e2a\u4ebf\u3002\u8c22\u5bb6\uff08\u901a\u8fc7\u6b63\u6cf0\u3001\u6d77\u6cf0\uff0c\u5305\u62ec\u8c22\u77bb\u4e2a\u4eba\u6295\u8d44\uff09\u6240\u5360\u80a1\u4efd49\uff05\uff0c\u5927\u96c5\u96c6\u56e220\uff05\uff0c\u9526\u57ce\u7ea631\uff05\u3002\u524d\u540e\u5171\u4e24\u4e2a\u4ebf\u7684\u6ce8\u8d44\uff0c\u9664\u4e86\u5fc5\u8981\u7684\u56fa\u5b9a\u8bbe\u65bd\u6295\u8d44\u4e4b\u5916\uff0c\u4e3b\u8981\u8d44\u91d1\u4e0e\u4e4b\u524d\u7684\u76c8\u5229\u90fd\u6295\u5165\u5230\u6e20\u9053\u5efa\u8bbe\u4e0e\u5e02\u573a\u5ba3\u4f20\u4e0a\uff0c\u4e00\u4e2a\u534a\u6708\u6240\u9700\u7684\u751f\u4ea7\u6d41\u52a8\u8d44\u91d1\u7ea6\u4e09\u5343\u4e07\uff0c\u8fd8\u5269\u4e0b\u516b\u5343\u4e07\u6d41\u52a8\u8d44\u91d1\uff0c\u8c22\u5251\u5357\u7262\u7262\u634f\u5728\u81ea\u5df1\u624b\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e3a\u4e86\u652f\u6301\u6d77\u5dde\u5e02\u7535\u5b50\u5de5\u4e1a\u7684\u53d1\u5c55\uff0c\u4e0d\u4ec5\u5728\u6280\u672f\u4e0a\u7ed9\u4e88\u652f\u6301\uff0c\u8fd8\u7981\u6b62\u8d22\u52a1\u62d6\u6b20\u4f9b\u8d27\u5546\u7684\u8d27\u6b3e\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5728\u9009\u62e9\u4f9b\u8d27\u5546\u65f6\uff0c\u9664\u4e86\u8003\u5bdf\u6700\u7ec8\u7684\u96f6\u914d\u4ef6\u8d28\u91cf\uff0c\u8fd8\u53c2\u4e0e\u5230\u4f9b\u8d27\u5546\u751f\u4ea7\u7ba1\u7406\u4e4b\u4e2d\u53bb\u2014\u2014\u7ba1\u7406\u6df7\u4e71\u7684\u4f9b\u8d27\u5546\u65e0\u6cd5\u4fdd\u8bc1\u80fd\u7a33\u5b9a\u4f9b\u5e94\u8d28\u91cf\u53ef\u9760\u7684\u4ea7\u54c1\u2014\u2014\u4fc3\u4f7f\u914d\u5957\u5382\u5546\u8ddf\u7740\u7231\u8fbe\u7535\u5b50\u4e00\u8d77\u5347\u7ea7\u3002</p><p>\u3000\u3000\u9664\u4e86\u89e3\u7801\u677f\u3001\u673a\u82af\u4e24\u4e2a\u6838\u5fc3\u7ec4\u4ef6\u4e4b\u5916\uff0c\u6d77\u5dde\u5e02\u5df2\u7ecf\u5f62\u6210\u5b8c\u5584\u7684\u96f6\u914d\u4ef6\u4f9b\u5e94\u88e2\uff0c\u79d1\u738b\u82e5\u662f\u4e0d\u501f\u52a9\u8fd9\u6761\u96f6\u914d\u4ef6\u4f9b\u5e94\u88e2\uff0c\u90a3\u5c31\u8981\u53bb\u5e7f\u4e1c\u4e00\u5e26\u5bfb\u627e\u96f6\u914d\u4ef6\u4f9b\u5e94\u5546\uff0c\u9996\u5148\u4f9b\u8d27\u5468\u671f\u4f1a\u5ef6\u957f\u4e94\u5230\u4e03\u5929\uff0c\u8fd0\u8f93\u53ca\u5546\u52a1\u6210\u672c\u589e\u52a0\uff0c\u96f6\u914d\u4ef6\u4ea7\u54c1\u8d28\u91cf\u63a7\u5236\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u4e5f\u4f1a\u589e\u52a0\u989d\u5916\u7684\u6210\u672c\u3002</p><p>\u3000\u3000\u501f\u52a9\u6d77\u5dde\u5e02\u96f6\u914d\u4ef6\u4f9b\u5e94\u88e2\u90a3\u81ea\u7136\u662f\u65e0\u7591\u7684\uff0c\u8c22\u5251\u5357\u4f38\u51fa\u4e00\u6839\u624b\u6307\u9876\u7740\u4e0b\u5df4\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4f9b\u5e94\u5546\u90fd\u8ddf\u7231\u8fbe\u7a7f\u4e00\u6761\u88e4\u5b50\u7684\uff0c\u6211\u4eec\u8981\u9884\u9632\u5f20\u606a\u4ece\u8fd9\u4e0a\u9762\u7ed9\u6211\u4eec\u4f7f\u7eca\uff0c\u4ee5\u524d\u4ea7\u91cf\u5c11\u8fd8\u597d\u8bf4\uff0c\u4ee5\u540e\u5c31\u4e0d\u540c\u4e86\uff0c\u4e07\u4e00\u7231\u8fbe\u4ece\u8fd9\u4e9b\u4f9b\u8d27\u5546\u626b\u8d27\uff0c\u54ea\u6015\u5c06\u53ea\u662f\u5c06\u6700\u5bfb\u5e38\u7684\u9559\u4e1d\u9489\u626b\u6389\uff0c\u5c31\u591f\u6211\u4eec\u54ed\u51e0\u5929\u7684\uff0c\u96f6\u914d\u4ef6\u8fd9\u4e00\u5757\uff0c\u6211\u4eec\u4e0d\u80fd\u4ec5\u4ec5\u4f9d\u8d56\u4e8e\u6d77\u5dde\u5f53\u5730\u7684\uff0c\u5e7f\u4e1c\u4e2d\u5c71\u7684\u4f9b\u5e94\u88e2\uff0c\u6211\u4eec\u4e5f\u8981\u8fc5\u901f\u5b8c\u5584\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u5b66\u6210\u5f52\u6765\u6709\u4e00\u5e74\u591a\u4e86\uff0c\u66fe\u7ecf\u60f3\u53bb\u6d77\u6cf0\u5de5\u4f5c\uff0c\u540e\u6765\u7559\u5728\u5927\u96c5\u5e2e\u5979\u7236\u4eb2\u9648\u5bb6\u5584\u53c2\u4e0e\u5927\u96c5\u96c6\u56e2\u7684\u54c1\u724c\u8fd0\u8425\uff0c\u5927\u96c5\u96c6\u56e2\u5728\u54c1\u724c\u8fd0\u8425\u4e0a\u6709\u81ea\u5df1\u4e00\u5957\u81ea\u4ee5\u4e3a\u6210\u719f\u7684\u5957\u8def\uff0c\u9648\u5bb6\u5584\u53ea\u662f\u8ba9\u9648\u9759\u53bb\u5b9e\u4e60\uff0c\u6ca1\u6709\u5141\u8bb8\u5979\u6307\u624b\u5212\u811a\u53bb\u505a\u4ec0\u4e48\u6539\u9769\uff0c\u8ba4\u4e3a\u5979\u7684\u7ecf\u9a8c\u8fd8\u4e0d\u8db3\u4ee5\u5e94\u4ed8\u56fd\u5185\u590d\u6742\u7684\u5e02\u573a\u3002\u5927\u6982\u4e5f\u5c31\u662f\u8fd9\u51e0\u5929\uff0c\u624d\u8ba9\u5979\u771f\u6b63\u9886\u7565\u5230\u5546\u6218\u7684\u9669\u6076\u5904\u3002</p><p>\u3000\u3000\u8c22\u77bb\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u77e5\u9053\uff0c\u5df2\u7ecf\u8fdb\u884c\u5dee\u4e0d\u591a\u4e86\uff0c\u6211\u62bd\u65f6\u95f4\u4f1a\u518d\u8d70\u4e00\u8d9f\uff0c\u6211\u4eec\u6700\u8584\u5f31\u7684\u5730\u65b9\u8fd8\u662f\u5728\u7535\u8def\u677f\u4e0a\uff0c\u7231\u8fbe\u662f\u81ea\u5df1\u8bbe\u8ba1\u3001\u751f\u4ea7\u7535\u8def\u677f\u2026\u2026\u201d</p><p>\u3000\u3000\u629b\u5f00\u89e3\u7801\u677f\u4e0e\u673a\u82af\uff0c\u7535\u8def\u8bbe\u8ba1\u4e0e\u5404\u7cfb\u7edf\u63a7\u5236\uff0c\u53ef\u4ee5\u8bf4\u662f\u5f71\u789f\u673a\u884c\u4e1a\u91cc\u6700\u4e3b\u8981\u7684\u6280\u672f\u6d3b\uff0c\u5f71\u789f\u673a\u4e3b\u8981\u8d28\u91cf\u95ee\u9898\u5c31\u51fa\u5728\u7535\u8def\u8bbe\u8ba1\u7684\u5408\u7406\u6027\u4e0e\u7535\u8def\u677f\u7684\u7a33\u5b9a\u6027\u4e0a\u3002</p><p>\u3000\u3000TI\u3001\u65af\u9ad8\u67cf\u4e0e\u98de\u5229\u6d66\u4e3a\u4e86\u5784\u65ad\u89e3\u7801\u677f\u3001\u673a\u82af\u7684\u751f\u4ea7\uff0c\u5728\u51fa\u552e\u89e3\u7801\u677f\u3001\u673a\u82af\u7684\u540c\u65f6\uff0c\u4e5f\u51fa\u552e\u4e0e\u89e3\u7801\u677f\u3001\u673a\u82af\u914d\u5957\u7684\u7535\u8def\u8bbe\u8ba1\u4e0e\u7cfb\u7edf\u63a7\u5236\u89e3\u51b3\u65b9\u6848\uff0c\u5dee\u4e0d\u591a\u5305\u62ec\u5f53\u524d\u5e02\u573a\u7684\u51e0\u6b3e\u4e3b\u6d41\u5f71\u789f\u673a\uff0c\u4e5f\u6709\u4e13\u95e8\u7684\u7535\u5b50\u5382\u5546\u751f\u4ea7\u5f71\u789f\u673a\u914d\u5957\u7684\u7535\u8def\u677f\uff0c\u6d77\u5dde\u4e5f\u6709\uff0c\u7231\u8fbe\u7535\u5b50\u867d\u7136\u4e0d\u9700\u8981\u7535\u8def\u677f\uff0c\u751a\u81f3\u8fd8\u6709\u591a\u4f59\u7684\u7535\u8def\u677f\u6d41\u51fa\u6765\uff0c\u4f46\u662f\u6d77\u5dde\u9664\u4e86\u7231\u8fbe\u3001\u79d1\u738b\u4e4b\u5916\uff0c\u8fd8\u6709\u5341\u591a\u5bb6\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\uff0c\u76f8\u4fe1\u79d1\u738b\u593a\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u540e\uff0c\u6d77\u5dde\u7684\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u4f1a\u66f4\u591a\u3002</p><p>\u3000\u3000\u5f71\u789f\u673a\u751f\u4ea7\u5df2\u7ecf\u7b80\u5316\u5230\u53ea\u8981\u6709\u8d44\u91d1\u96c7\u4e24\u4e09\u4e2a\u519c\u6c11\u5de5\u90fd\u80fd\u751f\u4ea7\u7684\u5730\u6b65\uff0c\u7b80\u5316\u662f\u7b80\u5316\u4e86\uff0c\u4f46\u662f\u6076\u679c\u4e5f\u5f88\u660e\u663e\uff1a\u4f1a\u538b\u5236\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u6280\u672f\u7814\u53d1\u6295\u5165\u7684\u70ed\u60c5\uff0c\u4e5f\u9020\u6210\u5f71\u789f\u673a\u751f\u4ea7\u6210\u672c\u7684\u4e0a\u6d6e\u3002</p><p>\u3000\u3000\u7231\u8fbe\u4e0eTI\u5408\u8d44\u6210\u7acb\u7684\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u4e5f\u9760\u51fa\u552e\u4e0eTI\u89e3\u7801\u677f\u914d\u5957\u7684\u7cfb\u7edf\u63a7\u5236\u89e3\u51b3\u65b9\u6848\u8425\u5229\uff0c\u5229\u6da6\u867d\u7136\u4e0d\u80fd\u4e0e\u7231\u8fbe\u7535\u5b50\u76f8\u6bd4\uff0c\u4f46\u662f\u868a\u5b50\u817f\u518d\u7626\uff0c\u522e\u4e0b\u6765\u4e5f\u6709\u4e00\u4e9b\u94b1\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u7231\u8fbe\u7535\u5b50\u5728\u7cfb\u7edf\u63a7\u5236\u7f16\u7a0b\u4e0e\u7535\u8def\u8bbe\u8ba1\u4e0a\u6709\u7740\u5f88\u5f3a\u7684\u5b9e\u529b\uff0c\u4e5f\u627f\u53d7\u7231\u8fbe\u7535\u5b50\u5728\u8fd9\u4e0a\u9762\u6709\u4e00\u5b9a\u7684\u6210\u672c\u4f18\u52bf\uff0c\u4f46\u662f\u4ed6\u5e76\u4e0d\u8ba4\u4e3a\u8fd9\u662f\u5f53\u524d\u5e02\u573a\u7ade\u4e89\u7684\u5173\u952e\u56e0\u7d20\uff0c\u4e0d\u8fc7\u79d1\u738b\u7684\u786e\u6709\u5fc5\u8981\u5728\u8fd9\u65b9\u9762\u52a0\u5f3a\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u6bd5\u7adf\u79d1\u738b\u843d\u540e\u7231\u8fbe\u6574\u6574\u4e00\u5e74\uff0c\u4e0d\u8db3\u7684\u5730\u6b65\u4e3e\u76ee\u7686\u662f\uff0c\u8c22\u5251\u5357\u4e0d\u4f1a\u56e0\u4e3a\u8fd9\u4e9b\u5c31\u4e27\u5931\u81ea\u4fe1\u7684\u3002</p></article>"}}}]);
