(window.webpackJsonp=window.webpackJsonp||[]).push([[438],{461:function(p){p.exports={chapterTitle:null,title:"\u7b2c438\u7ae0 \u671f\u5f85\u6740\u624b\u950f",content:"<article><h2>\u7b2c438\u7ae0 \u671f\u5f85\u6740\u624b\u950f</h2><p>\u3000\u3000\u8c22\u5251\u5357\u4e0e\u8c22\u77bb\u3001\u9648\u9759\u3001\u5218\u660e\u8f89\u3001\u90c1\u840d\u7b49\u4eba\u5728\u8c2d\u5e0c\u677e\u4e13\u95e8\u7ed9\u4ed6\u4eec\u5b89\u6392\u7684\u8d35\u5bbe\u5ba4\u91cc\u4f11\u606f\uff0c\u8d75\u53f8\u660e\u5374\u4e0d\u89c1\u8e2a\u5f71\uff0c\u8c22\u5251\u5357\u95ee\u8c22\u77bb\u8d75\u53f8\u660e\u53bb\u4e86\u54ea\u91cc\uff0c\u8c22\u77bb\u644a\u644a\u624b\uff1a\u201c\u6211\u4e0e\u90c1\u7ecf\u7406\u53bb\u4f1a\u573a\uff0c\u5012\u6ca1\u6709\u6ce8\u610f\u4ed6\u53bb\u54ea\u91cc\uff0c\u4ed6\u4e5f\u6ca1\u6709\u8ddf\u6211\u8bf4\u8d77\u8981\u53bb\u54ea\u91cc\u3002\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u5c06\u4ed6\u513f\u5b50\u585e\u5230\u516c\u53f8\u953b\u70bc\uff0c\u671f\u671b\u80fd\u6210\u6750\uff0c\u79d1\u738b\u8d1f\u8d23\u751f\u4ea7\u4e0e\u91c7\u8d2d\u7684\u526f\u603b\u624d\u662f\u9526\u57ce\u6b63\u5f0f\u7684\u4ee3\u8868\u3002\u79d1\u738b\u7535\u5668\u6bd5\u7adf\u662f\u5728\u6d77\u5dde\u7684\u5730\u76d8\u4e0a\uff0c\u51e1\u4e8b\u90fd\u8981\u4ef0\u4ed7\u8d75\u9526\u8363\u8fd9\u4e2a\u5730\u5934\u86c7\uff0c\u8c22\u5251\u5357\u81ea\u7136\u8981\u5e2e\u4ed6\u5c06\u513f\u5b50\u7167\u770b\u597d\u3002</p><p>\u3000\u3000\u8d75\u53f8\u660e\u53bb\u5e74\u82e5\u4e0d\u662f\u7ed9\u9000\u5b66\u8fdb\u5de5\u8bfb\u5b66\u6821\u5173\u4e86\u4e00\u9635\u5b50\uff0c\u4eca\u5e74\u6307\u4e0d\u5b9a\u5728\u54ea\u4e2a\u5927\u6821\u7ee7\u7eed\u7978\u5bb3\u4eba\u95f4\uff0c\u5012\u662f\u7ed9\u8fd9\u4e48\u4e00\u6574\uff0c\u4eba\u8001\u5b9e\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u521a\u8981\u62e8\u7535\u8bdd\u7ed9\u8d75\u53f8\u660e\uff0c\u60f3\u8ba9\u4ed6\u4e00\u8d77\u7ecf\u5386\u4e00\u4e0b\u65b0\u95fb\u53d1\u5e03\u4f1a\u7684\u573a\u9762\uff1b\u8d75\u53f8\u660e\u4ece\u5916\u9762\u63a8\u95e8\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u53bb\u4e86\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u8bf4\u4eca\u5929\u7684\u665a\u62a5\u53ef\u80fd\u5c31\u4f1a\u6709\u62a5\u9053\uff0c\u6211\u53bb\u4e70\u62a5\u7eb8\u4e86\uff0c\u8fd9\u9644\u8fd1\u6ca1\u6709\u62a5\u4ead\uff0c\u8d70\u8fdc\u4e86\u4e00\u4e9b\u3002\u201d\u8d75\u53f8\u660e\u5c06\u4e00\u53e0\u62a5\u7eb8\u9012\u8fc7\u6765\uff0c\u201c\u5317\u4eac\u665a\u62a5\u6709\u62a5\u9053\u4e0d\u5947\u602a\uff0c\u9f50\u9c81\u665a\u62a5\u7684\u53cd\u5e94\u600e\u4e48\u8fd9\u4e48\u5feb\uff1f\u6211\u5c31\u627e\u4e86\u4e00\u5bb6\u5c0f\u62a5\u4ead\uff0c\u6682\u65f6\u53ea\u6709\u8fd9\u4e24\u5bb6\u62a5\u7eb8\u6709\u62a5\u9053\u3002\u201d</p><p>\u3000\u3000\u62a5\u7eb8\u7684\u53d1\u884c\uff0c\u5236\u597d\u7248\uff0c\u5c06\u7248\u9762\u5206\u9001\u5404\u5730\u5370\u5237\uff0c\u73b0\u4ee3\u7684\u901a\u8baf\u4f20\u8f93\u8bbe\u5907\u53d1\u8fbe\uff0c\u5404\u5730\u51e0\u4e4e\u90fd\u662f\u540c\u6b65\u5370\u5237\u53d1\u884c\uff0c\u8d75\u53f8\u660e\u5947\u602a\u5728\u5317\u4eac\u9500\u552e\u7684\u9f50\u9c81\u665a\u62a5\u4f1a\u6709\u8fd9\u4e48\u5feb\u7684\u53cd\u5e94\uff0c\u7eaf\u7cb9\u662f\u4ed6\u89c1\u8bc6\u4e0d\u591f\uff0c\u5012\u662f\u4ed6\u80fd\u60f3\u5230\u53bb\u4e70\u62a5\u7eb8\uff0c\u6bd4\u8d77\u4ed6\u4e4b\u524d\u7684\u4eba\u751f\uff0c\u7b97\u662f\u4e00\u4e2a\u8fdb\u6b65\uff0c\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u60f3\u770b\u665a\u62a5\u4f1a\u600e\u4e48\u5199\u4e2d\u5348\u7684\u91c7\u8bbf\uff1f\u201d</p><p>\u3000\u3000\u770b\u4e86\u4e4b\u540e\uff0c\u8c22\u5251\u5357\u53c8\u540e\u6094\u5728\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e4b\u524d\u770b\u4e86\uff0c\u592a\u5f71\u54cd\u60c5\u7eea\u4e86\u3002\u9f50\u9c81\u65e5\u62a5\u8fd8\u597d\u4e00\u4e9b\uff0c\u5317\u4eac\u65e5\u62a5\u5374\u6ca1\u6709\u90a3\u4e48\u5ba2\u6c14\uff0c\u5c06\u65b0\u95fb\u7d20\u6750\u5c3d\u53ef\u80fd\u5199\u5f97\u66f2\u6298\u52a8\u4eba\u6216\u8bb8\u662f\u8bb0\u8005\u7684\u804c\u4e1a\u4e60\u60ef\uff0c\u5317\u4eac\u65e5\u62a5\u4ee5\u534a\u7248\u7bc7\u5e45\u7a81\u51fb\u62a5\u9053\u4e86\u5218\u660e\u8f89\u7b49\u4eba\u7684\u8df3\u69fd\u4e8b\u4ef6\uff0c\u5c06\u7231\u8fbe\u5f7b\u5e95\u653e\u5f03\u6807\u738b\u4e4b\u4e89\u4e0e\u79d1\u738b\u5728\u6807\u738b\u8840\u8165\u4e89\u593a\u4e2d\u5f02\u519b\u7a81\u8d77\u8054\u7cfb\u8d77\u6765\uff0c\u5728\u7f16\u8005\u6309\u91cc\uff0c\u6307\u51fa\u79d1\u738b\u5173\u952e\u65f6\u523b\u6316\u89d2\u6709\u7a83\u53d6\u7ade\u4e89\u5bf9\u624b\u5546\u4e1a\u673a\u5bc6\u7684\u5acc\u7591\u3002</p><p>\u3000\u3000\u5317\u4eac\u65e5\u62a5\u793e\u5f52\u53e3\u5317\u4eac\u5e02\u59d4\u5ba3\u4f20\u90e8\uff0c\u8c22\u5251\u5357\u3001\u5218\u660e\u8f89\u5728\u8003\u8651\u5230\u4e2d\u592e\u7535\u89c6\u53f0\u62e5\u6709\u6700\u5e9e\u5927\u7684\u5a92\u4f53\u8d44\u6e90\u65f6\uff0c\u5374\u5ffd\u89c6\u6389\u5730\u65b9\u5a92\u4f53\u4e5f\u6709\u81ea\u5df1\u7684\u5229\u76ca\u8bc9\u6c42\uff0c\u66f4\u4e0d\u53ef\u80fd\u770b\u5230\u5730\u65b9\u5a92\u4f53\u7279\u522b\u662f\u5730\u65b9\u7701\u5e02\u7535\u89c6\u53f0\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u4e4b\u95f4\u5b58\u5728\u7684\u90a3\u4e9b\u77db\u76fe\u3002</p><p>\u3000\u3000\u5c31\u50cf\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u5728\u5317\u4eac\u65e5\u62a5\u793e\u6240\u5c5e\u5a92\u4f53\u6295\u4e0b\u51e0\u5341\u4e07\u7684\u5e7f\u544a\u8d39\uff0c\u5317\u4eac\u65e5\u62a5\u793e\u5728\u62a5\u9053\u7231\u8fbe\u4e0e\u79d1\u738b\u7684\u5229\u76ca\u7ea0\u7eb7\u65f6\uff0c\u600e\u4e48\u53ef\u80fd\u6ca1\u6709\u81ea\u5df1\u7684\u7acb\u573a\u53d6\u5411\u5462\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u60f3\u8d77\u79d1\u738b\u7ade\u4e0b\u6807\u738b\u5e7f\u544a\u65f6\u5168\u573a\u5382\u5546\u4ee3\u8868\u90fd\u7ad9\u8d77\u6765\u9f13\u638c\u7684\u60c5\u5f62\uff0c\u4ed6\u5f53\u65f6\u4ece\u4eba\u7f1d\u91cc\u770b\u5230\u552f\u6709\u5f20\u606a\u5e73\u9759\u5982\u5e38\u7684\u5750\u5728\u4e00\u65c1\u5634\u89d2\u9732\u51fa\u82e5\u6709\u82e5\u65e0\u7684\u6d45\u7b11\uff0c\u8fd9\u591a\u5c11\u7ed9\u4ed6\u7559\u4e0b\u4e9b\u5fc3\u7406\u9634\u5f71\uff0c\u6ca1\u60f3\u5230\u574f\u4e8b\u60c5\u6765\u5f97\u8fd8\u771f\u5feb\u3002</p><p>\u3000\u3000\u201c\u8fd8\u597d\uff0c\u201d\u8c22\u5251\u5357\u60f3\u4e86\u7247\u523b\uff0c\u9732\u51fa\u7b11\u5bb9\u6709\u6545\u4f5c\u8f7b\u677e\u4e4b\u5acc\uff0c\u201c\u7ecf\u9500\u5546\u4e0d\u4f1a\u56e0\u4e3a\u6a21\u7cca\u7684\u5546\u4e1a\u9053\u5fb7\u6982\u5ff5\u800c\u72b9\u8c6b\u4e0d\u51b3\uff0c\u8fd9\u79cd\u95ee\u9898\u4e0d\u7528\u53bb\u4e89\u8fa9\uff0c\u8d8a\u4e89\u8fa9\u8d8a\u7ea0\u7f20\u4e0d\u6e05\uff0c\u90a3\u624d\u4f1a\u4e2d\u90a3\u5c0f\u5b50\u7684\u5708\u5957\u3002\u201d</p><p>\u3000\u3000\u201c\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e0a\uff0c\u4f1a\u4e0d\u4f1a\u6709\u8bb0\u8005\u5201\u96be\uff1f\u201d\u9648\u9759\u62c5\u5fc3\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u592e\u89c6\u7ec4\u7ec7\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u592e\u89c6\u4f1a\u8ba9\u522b\u4eba\u7838\u81ea\u5df1\u7684\u573a\u5b50\u5417\uff1f\u201d\u8c22\u5251\u5357\u5012\u4e0d\u62c5\u5fc3\u4e0b\u9762\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u8003\u8651\u662f\u4e0d\u662f\u6709\u4e13\u95e8\u6210\u7acb\u5a92\u4f53\u516c\u5173\u90e8\u95e8\u7684\u5fc5\u8981\uff0c\u53ef\u4ee5\u62ff\u5e7f\u544a\u6295\u653e\u8fd9\u4e00\u4e2a\u5229\u5668\uff0c\u6765\u6539\u53d8\u5730\u65b9\u5a92\u4f53\u7684\u7acb\u573a\u3002\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u8fd9\u4e2a\u65f6\u5019\u5c31\u4e13\u95e8\u8ba9\u848b\u8587\u8d1f\u8d23\u8fd9\u65b9\u9762\u7684\u4e8b\u60c5\uff0c\u5f53\u65f6\u7231\u8fbe\u7535\u5b50\u8fd8\u4e0d\u7528\u62c5\u5fc3\u4f1a\u6709\u4ec0\u4e48\u4eba\u4f1a\u6545\u610f\u4f7f\u7eca\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u63a5\u4e0b\u6765\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e4f\u5584\u53ef\u9648\uff0c\u6ca1\u6709\u54ea\u5bb6\u5a92\u4f53\u7684\u8bb0\u8005\u4f1a\u516c\u7136\u5728\u773c\u76ae\u5b50\u5e95\u4e0b\u6311\u6218\u592e\u89c6\u7684\u6743\u5a01\u800c\u523b\u610f\u5201\u96be\u8c22\u5251\u5357\u90a3\u7fa4\u4eba\u7684\uff0c\u8fd9\u79cd\u4e4f\u5584\u53ef\u9648\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u4ed6\u4e5f\u6ca1\u6709\u673a\u4f1a\u4e9a\u641e\u8c22\u5251\u5357\u4e00\u628a\uff0c\u8fd8\u8981\u5c06\u8bb0\u8005\u8bc1\u8fd8\u7ed9\u7b80\u5fd7\u5eb7\u3002</p><p>\u3000\u3000\u524d\u9762\u8fd8\u597d\u8bf4\uff0c\u6709\u7231\u8fbe\u7535\u5b50\u7684\u4e00\u7bc7\u4e13\u8bbf\uff0c\u5c31\u7b97\u662f\u5f88\u4e0d\u9519\u7684\u6210\u7ee9\uff0c\u4f46\u662f\u9519\u8fc7\u8fd9\u4e2a\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u5c31\u8981\u7b97\u7b80\u5fd7\u5eb7\u7684\u5931\u804c\u4e86\u3002\u867d\u7136\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e4f\u5584\u53ef\u9648\uff0c\u4f46\u662f\u4ed6\u4e0e\u540c\u4e8b\u6b64\u884c\u7684\u76ee\u7684\u5c31\u5728\u4e8e\u6b64\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u8c22\u665a\u6674\u4e0e\u5085\u4fca\u5c06\u82cf\u6d25\u4e1c\u4e22\u4e0b\u2014\u2014\u771f\u6b63\u60f3\u6316\u6398\u65b0\u95fb\u7d20\u624d\u7684\u5a92\u4f53\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u5174\u8da3\u4e0d\u4f1a\u6bd4\u79d1\u738b\u4f4e\uff0c\u4e00\u5207\u770b\u8d77\u6765\u8fd8\u6709\u8fd9\u4e48\u591a\u4f20\u5947\u8272\u5f69\u2014\u2014\u524d\u5f80\u4e09\u91cc\u5c6f\uff0c\u9648\u4fe1\u751f\u4e86\u89e3\u5230\u4eca\u5929\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u540e\uff0c\u6253\u7535\u8bdd\u518d\u7ea6\u4ed6\u8fc7\u53bb\u5403\u996d\uff0c\u8bb8\u601d\u4e0e\u4e01\u69d0\u7531\u9a6c\u6d77\u9f99\u966a\u7740\u4e00\u76f4\u90fd\u5728TI\u4e2d\u56fd\u516c\u53f8\u91cc\u6d3d\u8c08\u4e8b\u60c5\uff0c\u4e0e\u9648\u4fe1\u751f\u4e00\u9053\u76f4\u63a5\u53bb\u5403\u996d\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u534e\u5317\u533a\u529e\u4e8b\u5904\u7684\u4e24\u8f86\u5c0f\u8f66\uff0c\u4e00\u8f86\u8f66\u7559\u7ed9\u82cf\u6d25\u4e1c\u4ed6\u4eec\uff0c\u4e00\u8f86\u8f66\u4e2d\u5348\u8ba9\u8bb8\u601d\u3001\u4e01\u69d0\u3001\u9a6c\u6d77\u5f00\u53bbTI\u516c\u53f8\uff0c\u5f20\u606a\u4e0e\u665a\u6674\u8fd8\u6709\u5085\u4fca\u4fbf\u53bb\u6324\u5730\u94c1\uff0c\u8fd9\u65f6\u5019\u505a\u8f66\u53bb\u4e09\u91cc\u5c6f\u6709\u4e00\u534a\u4ee5\u4e0a\u7684\u6982\u7387\u4f1a\u7ed9\u5835\u5728\u8def\u4e0a\uff0cTI\u4e2d\u56fd\u516c\u53f8\u7684\u529e\u516c\u5730\u5740\u5c31\u5728\u4e09\u91cc\u5c6f\u8fb9\u4e0a\uff0c\u90a3\u81ea\u7136\u53e6\u8bf4\u3002</p><p>\u3000\u3000\u4e00\u6574\u5929\u90fd\u5728\u522e\u98ce\uff0c\u51fa\u4e2d\u592e\u7535\u89c6\u53f0\u5c31\u89c9\u5f97\u6c14\u6e29\u964d\u5f97\u5389\u5bb3\uff0c\u6d77\u5dde\u73b0\u5728\u7684\u6c14\u6e29\u8fd8\u6709\u5341\u4e09\u56db\u5ea6\uff0c\u5317\u4eac\u8fd9\u65f6\u5019\u6cb3\u6d41\u90fd\u5f00\u59cb\u7ed3\u539a\u51b0\u4e86\u3002\u5f80\u5730\u94c1\u53e3\u8d70\u53bb\u7684\u65f6\u5019\uff0c\u4eba\u745f\u745f\u53d1\u6599\uff0c\u6324\u8fdb\u5730\u94c1\u91cc\uff0c\u5374\u53c8\u89c9\u5f97\u95f7\u70ed\u3002\u51ac\u5929\u7684\u5317\u4eac\u5c31\u662f\u5982\u6b64\uff0c\u5357\u65b9\u4eba\u5230\u5317\u4eac\u8981\u662f\u4e0d\u4e60\u60ef\u539a\u5916\u5957\u5185\u76f4\u63a5\u7a7f\u886c\u886b\u7684\u7a7f\u8863\u6cd5\uff0c\u5c31\u4f1a\u5ba4\u5916\u5acc\u51b7\uff0c\u5ba4\u5916\u5acc\u70ed\u3002</p><p>\u3000\u3000\u8d76\u5230\u524d\u5929\u76f8\u89c1\u7684\u65e5\u5f0f\u9910\u9986\uff0c\u9648\u4fe1\u751f\u3001\u8bb8\u601d\u3001\u4e01\u69d0\u3001\u9a6c\u6d77\u9f99\u4ed6\u4eec\u90fd\u5728\u91cc\u9762\uff0c\u5f20\u606a\u6311\u7740\u84dd\u5370\u5e03\u5e18\u8fdb\u53bb\uff0c\u6413\u7740\u624b\uff0c\u4ece\u5730\u94c1\u53e3\u8d70\u8fc7\u6765\uff0c\u51bb\u5f97\u5389\u5bb3\uff0c\u5c4b\u91cc\u867d\u7136\u6709\u6696\u6c14\u7247\uff0c\u8fd9\u4f1a\u513f\u5de5\u592b\u8fd8\u6ca1\u6709\u7f13\u8fc7\u52b2\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u5f88\u671f\u5f85\u7231\u8fbe\u80fd\u6301\u7eed\u8fc7\u53bb\u4e00\u5e74\u7684\u8f89\u714c\u7684\uff0c\u201d\u9648\u4fe1\u751f\u7b11\u7740\u7ad9\u8d77\u6765\u8bf7\u5f20\u606a\u5165\u5ea7\uff0c\u201c\u4f60\u4eec\u8fd9\u4e00\u62db\u679c\u7136\u5f88\u6f02\u4eae\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u4ee5\u4e3a\u9648\u5148\u751f\u4f1a\u671f\u5f85\u4e0b\u53bb\u5462\uff0c\u201d\u5f20\u606a\u6413\u6413\u624b\uff0c\u5750\u5230\u8bb8\u601d\u8eab\u8fb9\u7684\u4f4d\u5b50\uff0c\u53c8\u95ee\u9648\u4fe1\u751f\uff0c\u201c\u6211\u4eec\u54ea\u4e00\u62db\u6f02\u4eae\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8df3\u69fd\u4e8b\u4ef6\u7684\u5904\u7406\u3002\u201d\u9648\u4fe1\u751f\u7b11\u7740\u8bf4\uff0c\u201c\u6574\u4e2a\u4e0a\u5348\u4e00\u6b21\u724c\u90fd\u4e0d\u4e3e\uff0c\u591a\u4e48\u9c9c\u660e\u7684\u5bf9\u6bd4\uff01\u628a\u5a92\u4f53\u7684\u597d\u5947\u5fc3\u90fd\u52fe\u5f15\u51fa\u6765\u4e86\uff0c\u6211\u5f53\u65f6\u8ddf\u5f20\u4e9a\u5e73\u5728\u901a\u7535\u8bdd\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u60f3\u8dd1\u5230\u73b0\u573a\u53bb\u770b\u4e00\u770b\u4f60\u4eec\u5230\u5e95\u5728\u641e\u4ec0\u4e48\u9b3c\u3002\u6211\u8ba9\u516c\u53f8\u7684\u4eba\u5927\u6982\u6536\u96c6\u4e86\u4e00\u4e0b\uff0c\u6536\u96c6\u5230\u7684\u6709\u4e8c\u5341\u4e03\u5bb6\u665a\u62a5\u62a5\u9053\u4e86\u8df3\u69fd\u4e8b\u4ef6\uff0c\u7acb\u573a\u90fd\u503e\u5411\u540c\u60c5\u7231\u8fbe\uff0c\u6211\u60f3\u5176\u4ed6\u62a5\u9053\u6b64\u4e8b\u7684\u5a92\u4f53\u90fd\u5dee\u4e0d\u591a\u4f1a\u662f\u8fd9\u4e2a\u7acb\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u535a\u53d6\u540c\u60c5\uff0c\u535a\u53d6\u66f4\u591a\u4eba\u7684\u540c\u60c5\uff0c\u5e94\u8be5\u8bf4\u662f\u4e2a\u6280\u672f\u6d3b\u3002\u201d\u5f20\u606a\u5f88\u8ba4\u771f\u7684\u8bf4\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5e74\uff0c\u5a92\u4f53\u8bb0\u4f4f\u79d1\u738b\u7684\u540c\u65f6\uff0c\u4e5f\u4e0d\u4f1a\u5fd8\u5374\u7231\u8fbe\uff0c\u53ea\u4e0d\u8fc7\u73b0\u5728\u7684\u7231\u8fbe\u4e0d\u7a00\u7f55\u589e\u52a0\u8fd9\u70b9\u77e5\u540d\u5ea6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u80fd\u589e\u52a0\u4e00\u70b9\u662f\u4e00\u70b9\uff0c\u201d\u5f20\u606a\u62ff\u8fc7\u4f20\u5230\u4ed6\u624b\u91cc\u7684\u83dc\u5355\uff0c\u201c\u8bf4\u8d77\u6765\uff0c\u5728\u79d1\u738b\u7684\u5e7f\u544a\u6b63\u5f0f\u5728\u592e\u89c6\u64ad\u653e\u4e4b\u524d\uff0c\u8fd9\u6bb5\u65f6\u95f4\u7684\u7092\u4f5c\uff0c\u5bf9\u7ecf\u9500\u5546\u7684\u5f71\u54cd\u6700\u5927\uff0c\u6240\u4ee5\u7231\u8fbe\u4e5f\u9700\u8981\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u7684\u7092\u4f5c\uff0c\u6bd4\u8d77\u6f2b\u4e16\u754c\u7684\u72c2\u8f70\u4e71\u70b8\uff0c\u6211\u66f4\u559c\u6b22\u968f\u98ce\u6f5c\u5165\u591c\u5f0f\u7684\u6f5c\u79fb\u9ed8\u5316\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u8fd8\u6545\u610f\u9732\u4e86\u4e00\u4e2a\u7834\u7efd\uff0c\u6c5f\u5c0f\u59d0\u6ca1\u6709\u968f\u884c\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u6c5f\u9edb\u513f\u662f\u7231\u8fbe\u7535\u5b50\u7684\u5f62\u8c61\u4ee3\u8a00\u4eba\uff0c\u7231\u8fbe\u8981\u4e89\u6807\u738b\uff0c\u54ea\u6709\u4e0d\u5c06\u6c5f\u9edb\u513f\u4e00\u8d77\u643a\u5e26\u8fc7\u6765\u5ba3\u4f20\u7684\u9053\u7406\uff1a\u201c\u6c5f\u5c0f\u59d0\u6709\u5979\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u6ca1\u5fc5\u8981\u4e3a\u4e00\u4e9b\u65e0\u8c13\u7684\u4e8b\u5b9e\u52b3\u70e6\u5979\uff0c\u4e5f\u8bf4\u4e0d\u4e0a\u7834\u7efd\u4e0d\u7834\u7efd\uff0c\u4eba\u90fd\u6709\u81ea\u5df1\u7684\u601d\u7ef4\u6b7b\u89d2\uff0c\u5728\u4ed6\u7684\u601d\u7ef4\u6b7b\u89d2\u91cc\uff0c\u4f60\u5c31\u662f\u9732\u4e00\u767e\u4e2a\u7834\u7efd\uff0c\u4ed6\u4e5f\u770b\u4e0d\u5230\u3002\u201d</p><p>\u3000\u3000\u201c\u601d\u7ef4\u6b7b\u89d2\uff1f\u201d</p><p>\u3000\u3000\u201c\u5bf9\u5e02\u573a\u624b\u6bb5\u7684\u8fc7\u5ea6\u8ff7\u4fe1\u3002\u201d</p><p>\u3000\u3000\u201c3.5\u4ebf\uff0c\u4e0d\u503c\uff01\u201d\u9648\u4fe1\u751f\u4e5f\u5f88\u80af\u5b9a\u7684\u4e0b\u4e86\u8fd9\u4e2a\u65ad\u8bed\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u76f4\u63a5\u627f\u8ba4\u81ea\u5df1\u6709\u610f\u5751\u79d1\u738b\uff0c\u5927\u5bb6\u5fc3\u91cc\u660e\u767d\u5c31\u884c\u3002\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u73b0\u5728\u5230\u5317\u4eac\u8857\u5934\uff0c\u6311\u4e00\u767e\u4e2a\u4eba\uff0c\u5927\u6982\u6709\u516b\u5341\u4e2a\u4eba\u77e5\u9053\u7231\u8fbe\u7535\u5b50\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u786e\u6ca1\u6709\u5fc5\u8981\u82b1\u8fd9\u4e48\u591a\u94b1\u518d\u8ba9\u77e5\u540d\u5ea6\u63d0\u9ad8\u51e0\u4e2a\u767e\u5206\u70b9\uff0c\u4fdd\u6301\u73b0\u5728\u7684\u77e5\u540d\u5ea6\u662f\u5fc5\u8981\u7684\uff0c\u6807\u738b\u4ef7\u7801\u518d\u4f4e\u4e00\u4e9b\uff0c\u7231\u8fbe\u7535\u5b50\u5f53\u7136\u4e5f\u4f1a\u8003\u8651\u51fa\u624b\uff0c\u6b63\u5982\u9648\u5148\u751f\u6240\u8bf4\uff0c3.5\u4ebf\uff0c\u5bf9\u7231\u8fbe\u6765\u8bf4\uff0c\u4e0d\u503c\uff0c\u7231\u8fbe\u5e02\u573a\u90e8\u76ee\u524d\u5e94\u8be5\u5dee\u91cd\u8003\u8651\u7f8e\u8a89\u5ea6\u7684\u95ee\u9898\uff0c\u7231\u8fbe\u9700\u8981\u57f9\u517b\u6d88\u8d39\u8005\u7684\u5fe0\u8bda\u5ea6\uff0c\u7231\u8fbe\u7684\u76ee\u6807\u53ef\u4e0d\u662f\u53ea\u5356\u5f71\u789f\u673a\u4e00\u79cd\u4ea7\u54c1\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u601d\u91cf\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u63a5\u89e6\u7684\u4eba\u4e5f\u4e0d\u5c11\uff0c\u8ba4\u8bc6\u5230\u8fd9\u4e00\u70b9\u7684\uff0c\u4e0d\u662f\u6ca1\u6709\uff0c\u4f46\u662f\u4e2d\u56fd\u4eba\u4e0d\u662f\u8bb2\u7a76\u77e5\u884c\u5408\u4e00\u5417\uff1f\u6211\u89c1\u8bc6\u6d45\uff0c\u8fd8\u6ca1\u6709\u9047\u5230\u6709\u5176\u4ed6\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u7406\u8bba\u4e0e\u5b9e\u8df5\u76f8\u7ed3\u5408\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u771f\u7684\u5f88\u671f\u5f85\u7231\u8fbe\u6301\u7eed\u7684\u8f89\u714c\u2026\u2026\u201d\u9648\u4fe1\u751f\u8bf4\u9053\uff0c\u201c\u6709\u4e9b\u4e8b\u60c5\u4e0d\u8be5\u6211\u73b0\u5728\u95ee\uff0c\u4f46\u662f\u6211\u53c8\u514b\u5236\u4e0d\u4f4f\uff0c\u4eca\u5929\u662f\u514b\u5236\u4e0d\u4f4f\u4e86\u3002\u6211\u60f3\u79d1\u738b\u4e0b\u4e00\u6b65\u4f1a\u62a2\u7231\u8fbe\u7684\u6e20\u9053\u5546\uff0c\u7231\u8fbe\u600e\u4e48\u5e94\u4ed8\uff1f\u4ef7\u683c\u6218\uff1f\u201d\u9648\u4fe1\u751f\u77e5\u9053\u7231\u8fbe\u7684\u6210\u672c\u4f18\u52bf\uff0c\u77e5\u9053\u4ef7\u683c\u6218\u65e0\u7591\u662f\u5f20\u606a\u624b\u91cc\u6700\u7280\u5229\u7684\u4e00\u5f20\u724c\uff0c\u79d1\u738b\u4ee5\u53ca\u5176\u4ed6\u4e8c\u4e09\u6d41\u7684\u5f71\u789f\u673a\u5382\u5546\u662f\u7ecf\u4e0d\u8fc7\u8fd9\u5f20\u724c\u6253\u51fb\u7684\uff0c\u76ee\u524d\u80fd\u53ea\u6709\u65b0\u79d1\u52c9\u5f3a\u80fd\u8ddf\u4e0a\u3002</p><p>\u3000\u3000\u65b0\u79d1\u7535\u5b50\u4e3a\u4e86\u6253\u7834\u89e3\u7801\u677f\u7ed9TI\u3001\u65af\u9ad8\u67cf\u4e24\u5bb6\u516c\u53f8\u5784\u65ad\u7684\u5e02\u573a\u683c\u5c40\uff0c\u5728\u7f8e\u56fd\u7845\u8c37\u627e\u5230\u4e00\u5bb6\u53f0\u6e7e\u4eba\u7684\u4f01\u4e1a\u6765\u5f00\u53d1\u89e3\u7801\u677f\u6280\u672f\uff0c\u65b0\u79d1\u7535\u5b50\u672c\u8eab\u5728\u7535\u8def\u8bbe\u8ba1\u4e0e\u7cfb\u7edf\u63a7\u5236\u8bbe\u8ba1\u4e0a\u6709\u5f88\u5f3a\u7684\u6280\u672f\u5b9e\u529b\uff0c\u7231\u8fbe\u8981\u4e3b\u52a8\u6253\u4ef7\u683c\u6218\uff0c\u9648\u4fe1\u751f\u4f30\u8ba1\u4e5f\u53ea\u6709\u65b0\u79d1\u80fd\u52c9\u5f3a\u6491\u4e0b\u6765\uff0c\u6491\u5230\u89e3\u7801\u677f\u5784\u65ad\u683c\u5c40\u7ed9\u6253\u7834\u7684\u90a3\u4e00\u5929\u3002</p><p>\u3000\u3000\u201c\u4ef7\u683c\u6218\uff0c\u5229\u7528\u6210\u672c\u4f18\u52bf\uff0c\u5c06\u6240\u6709\u7684\u7ade\u4e89\u5bf9\u624b\u62d6\u57ae\uff1f\u201d\u5f20\u606a\u7b11\u7740\u6447\u4e86\u6447\uff0c\u201c\u7231\u8fbe\u4e0d\u662f\u4e0d\u60f3\u505a\u4ef7\u683c\u5c60\u592b\uff0c\u4f46\u662f\u8fd9\u4e2a\u4ef7\u683c\u6218\u5b9e\u5728\u6ca1\u6709\u592a\u591a\u7684\u4f59\u5473\uff0c\u5c31\u7b97\u5c06\u6240\u6709\u7ade\u4e89\u5bf9\u624b\u62d6\u57ae\uff0c\u5360\u636e\u767e\u5206\u516b\u4e5d\u5341\u7684\u5e02\u573a\u4efd\u989d\uff0c\u8fd8\u6ca1\u6709\u5f00\u59cb\u4eab\u53d7\u5784\u65ad\u5229\u6da6\u5462\uff0cVCD\u5c31\u8981\u7ed9\u6dd8\u6c70\u4e86\u3002\u6b64\u65f6\u95f7\u58f0\u6323\u94b1\u6700\u8981\u7d27\uff0c\u4ed6\u4eec\u8981\u6253\u4ef7\u683c\u6218\uff0c\u7231\u8fbe\u6b65\u6b65\u8ddf\u4e0a\u5c31\u662f\u4e86\uff0c\u4e07\u4e8b\u90fd\u6ca1\u6709\u5fc5\u8981\u8ddf\u94b1\u8fc7\u4e0d\u53bb\u3002\u201d</p><p>\u3000\u3000\u89e3\u7801\u677f\u7684\u5784\u65ad\u6700\u591a\u4e0d\u8fc7\u4e24\u5e74\uff0c\u6700\u5feb\u5f88\u53ef\u80fd\u53ea\u5269\u4e0b\u4e00\u5e74\u7684\u7f8e\u597d\u65f6\u5149\uff0c\u5f20\u606a\u4e0d\u8bf4\uff0c\u60f3\u5fc5\u9648\u4fe1\u751f\u4e5f\u80fd\u660e\u767d\uff0c\u4e5f\u5c31\u662f\u8bf4\u5f20\u606a\u6700\u5927\u7684\u4e00\u5757\u6210\u672c\u4f18\u52bf\u80fd\u4fdd\u6301\u7684\u65f6\u95f4\u4e5f\u4e0d\u957f\uff0c\u4e3b\u52a8\u53bb\u505a\u8fd9\u4e2a\u4ef7\u683c\u5c60\u592b\uff0c\u5c31\u6709\u8ddf\u94b1\u8fc7\u4e0d\u53bb\u7684\u610f\u5473\uff0c\u5f20\u606a\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5c06\u79d1\u738b\u5f53\u6210\u81ea\u5df1\u7684\u4e3b\u8981\u7ade\u4e89\u5bf9\u624b\u7684\u3002</p><p>\u3000\u3000\u201c\u90a3\u7231\u8fbe\u8fd8\u6709\u522b\u7684\u51c6\u5907\uff1f\u201d\u9648\u4fe1\u751f\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u771f\u4e00\u70b9\u8010\u5fc3\u90fd\u6ca1\u6709\uff1f\u201d\u5f20\u606a\u95ee\u9648\u4fe1\u751f\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u9648\u4fe1\u751f\u7b11\u4e86\u7b11\uff0c\u4ed6\u8fd8\u662f\u5e0c\u671b\u5f20\u606a\u591a\u900f\u9732\u4e9b\u5e95\u7ec6\u7ed9\u4ed6\uff0c\u5f20\u606a\u8981\u4e0eTI\u5408\u8d44\u505a\u673a\u82af\uff0c\u6ca1\u6709\u4e00\u4e9b\u66f4\u5177\u4f53\u7684\u4fe1\u5fc3\uff0c\u4ed6\u4e5f\u4e0d\u597d\u8ddf\u603b\u90e8\u7684\u90a3\u4e9b\u5927\u9f3b\u5b50\u4f6c\u505a\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u201c\u518d\u8fc7\u534a\u4e2a\u6708\uff0c\u7231\u8fbe\u5c31\u4f1a\u7ec4\u7ec7\u7ecf\u9500\u5546\u5927\u4f1a\uff0c\u8fd8\u60f3\u5230\u65f6\u5019\u8bf7\u9648\u5148\u751f\uff0c\u8fd8\u6709\u98de\u5229\u6d66\u7684\u5f20\u4e9a\u5e73\u5148\u751f\u3001\u65af\u9ad8\u67cf\u7684\u827e\u9ed8\u5148\u751f\uff0c\u7d22\u5c3c\u90a3\u8fb9\uff0c\u6211\u8fd8\u4f1a\u53d1\u51fd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u6709\u534a\u4e2a\u6708\u554a\uff0c\u201d\u9648\u4fe1\u751f\u82e6\u607c\u7684\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u7231\u8fbe\u5728\u7ecf\u9500\u5546\u5927\u4f1a\u4e0a\u62ff\u51fa\u6740\u624b\u950f\uff0c\u963b\u6b62\u7ecf\u9500\u5546\u8d44\u6e90\u7684\u6d41\u5931\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u4e86\uff0c\u201c\u6211\u4f1a\u56e0\u4e3a\u80e1\u601d\u4e71\u60f3\u6b7b\u5f88\u591a\u8111\u7ec6\u80de\u7684\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u662f\u5f88\u671f\u5f85\u7231\u8fbe\u80fd\u6301\u7eed\u4e0a\u4e00\u5e74\u5ea6\u7684\u8f89\u714c\uff0c\u524d\u5929\u8bf4\u8fd9\u53e5\u8bdd\u65f6\uff0c\u4ed6\u8fd8\u4e0d\u5341\u5206\u80af\u5b9a\u7231\u8fbe\u5728\u592e\u89c6\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u5012\u662f\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u4e0a\u5348\u7684\u5f02\u5e38\uff0c\u53c8\u628a\u4ed6\u7684\u597d\u5947\u5fc3\u7ed9\u540a\u4e86\u8d77\u6765\uff0c\u8ba9\u4ed6\u66f4\u671f\u5f85\u7231\u8fbe\u65e5\u540e\u7684\u8f89\u714c\u4e86\u3002</p><p>\u3000\u3000\u4e0e\u5f20\u606a\u63a5\u89e6\u6709\u4e00\u5e74\u534a\u65f6\u95f4\u4e86\uff0c\u8fd9\u4e00\u5e74\u534a\u4e5f\u6b63\u662f\u7231\u8fbe\u7535\u5b50\u9010\u6e10\u7efd\u653e\u5149\u8292\u7684\u65f6\u673a\uff0c\u9648\u4fe1\u751f\u672c\u4eba\u662f\u5f88\u671f\u671b\u80fd\u4fc3\u6210TI\u516c\u53f8\u4e0e\u7231\u8fbe\u7535\u5b50\u5408\u8d44\u6210\u7acb\u673a\u82af\u5de5\u5382\u8fd9\u4ef6\u4e8b\u7684\u3002</p><p>\u3000\u3000\u201c\u9648\u5148\u751f\u8981\u7b49\u4e0d\u53ca\uff0c\u4e0d\u5982\u6211\u8bf7\u4f60\u660e\u5929\u5c31\u5230\u6d77\u5dde\u8d70\u4e00\u8d70\uff1f\u201d\u5f20\u606a\u772f\u8d77\u773c\u775b\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u786e\u5b9a\u6211\u80fd\u770b\u5230\u503c\u5f97\u671f\u5f85\u7684\u4e1c\u897f\uff1f\u201d\u9648\u4fe1\u751f\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8c01\u53c8\u77e5\u9053\u9648\u5148\u751f\u4f60\u7684\u671f\u5f85\u503c\u6709\u591a\u9ad8\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u4e0d\u8fc7\u79d1\u738b\u7684\u4eba\u4f1a\u62dc\u8bbf\u4f60\u4eec\uff0c\u4f60\u4e0d\u51fa\u9762\u62db\u5f85\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u5143\u5668\u4ef6\u90e8\u7684\u7ecf\u7406\u8d1f\u8d23\uff0c\u6211\u5c31\u5077\u4e00\u56de\u61d2\uff0c\u518d\u8bf4\u6211\u53bb\u6d77\u5dde\uff0c\u4e5f\u53ef\u4ee5\u987a\u5229\u62dc\u8bbf\u79d1\u738b\u7535\u5668\u7684\uff0c\u201d\u9648\u4fe1\u751f\u7b11\u4e86\u7b11\uff0c\u201c\u4e4b\u524d\u4e00\u76f4\u6ca1\u6709\u542c\u8bf4\u8fc7\u79d1\u738b\u7535\u5668\uff0c\u4f1a\u4e0d\u4f1a\u662f\u6211\u7b2c\u4e00\u773c\u770b\u5230\u7684\u7231\u8fbe\u90a3\u822c\u6a21\u6837\uff1f\u201d</p><p>\u3000\u3000TI\u4e0e\u65af\u9ad8\u67cf\u5bc6\u8ba2\u534f\u8bae\u5e73\u5206\u89e3\u7801\u677f\u5e02\u573a\uff0c\u5e73\u5206\u7684\u4f9d\u636e\u5c31\u662f\u7535\u5b50\u5de5\u4e1a\u6742\u5fd7\u6bcf\u6708\u5bf9\u4e3b\u8981\u5f71\u789f\u673a\u54c1\u724c\u5546\u505a\u51fa\u7684\u5e02\u573a\u4efd\u989d\u7edf\u8ba1\uff0c\u79d1\u738b\u5f02\u519b\u5d1b\u8d77\uff0c\u82e5\u90fd\u9009\u62e9\u65af\u9ad8\u67cf\u7684\u89e3\u7801\u677f\uff0c\u65af\u9ad8\u67cf\u4fbf\u9700\u8981\u5728\u5176\u4ed6\u5f71\u789f\u673a\u5382\u5546\u9762\u524d\u4fdd\u6301\u514b\u5236\u3002\u5bf9TI\u4e0e\u65af\u9ad8\u67cf\u4e24\u5bb6\u516c\u53f8\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\u60c5\u4e0d\u662f\u76f8\u4e92\u7ade\u4e89\uff0c\u800c\u4e14\u8981\u6781\u529b\u538b\u5236\u4e0d\u8ba9\u7b2c\u4e09\u65b9\u6324\u8fdb\u89e3\u7801\u677f\u5e02\u573a\u3002</p><p>\u3000\u3000\u5f53\u5e74\u4e3a\u4e86\u8bf4\u670dTI\u91cd\u542f\u89e3\u7801\u82af\u7247\u7684\u7814\u7a76\uff0c\u5f20\u606a\u5343\u65b9\u767e\u8ba1\u7684\u8ddfTI\u8463\u4e8b\u957f\u5b89\u5409\u4f2f\u5148\u751f\u8054\u7cfb\uff0c\u9648\u4fe1\u751f\u53d7\u5b89\u5409\u4f2f\u59d4\u6258\u5bf9\u7231\u8fbe\u7535\u5b50\u8fdb\u884c\u8bbf\u95ee\uff0c\u90a3\u65f6\u7684\u7231\u8fbe\u8fd8\u53ea\u662f\u5c06\u539f\u6765\u53ea\u4f1a\u751f\u4ea7\u5192\u724c\u865a\u52a3\u6536\u97f3\u673a\u7684\u58f0\u8fbe\u7535\u5b50\u5382\u4e70\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u8bf4\u5b9e\u8bdd\uff0c\u79d1\u738b\u6bd4\u90a3\u65f6\u7684\u7231\u8fbe\u8981\u597d\u4e00\u4e9b\uff0c\u5927\u6982\u4e5f\u53ea\u597d\u4e00\u4e9b\uff0c\u5708\u4e86\u4e00\u5927\u7247\u5730\uff0c\u5efa\u7684\u5374\u90fd\u662f\u7ec4\u88c5\u5de5\u5382\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bc4\u4ef7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u867d\u7136\u6709\u4e0d\u548c\u8c10\u7684\u58f0\u97f3\uff0c\u4f46\u662f\u4ece\u4eca\u5929\u665a\u4e0a\u5f00\u59cb\uff0c\u79d1\u738b\u7535\u5b50\u5fc5\u5c06\u662f\u5168\u56fd\u5a92\u4f53\u6700\u5173\u6ce8\u7684\u7126\u70b9\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u6d77\u5dde\u5e02\u9a7b\u4eac\u529e\u7684\u5de5\u4f5c\u4eba\u5458\u6709\u6ca1\u6709\u5168\u4f53\u51fa\u52a8\u53bb\u7ed9\u8c22\u5251\u5357\u4ed6\u4eec\u58ee\u58f0\u52bf\uff0c\u53bb\u5e74\u8fd9\u4e2a\u65f6\u5019\uff0c\u9a7b\u4eac\u529e\u4e3b\u4efb\u7f57\u4eac\u8dd1\u524d\u8dd1\u540e\u7684\uff0c\u86ee\u8ba9\u4eba\u611f\u52a8\u7684\u3002\u636e\u5f20\u606a\u6240\u77e5\uff0c\u98de\u5229\u6d66\u3001\u7d22\u5c3c\u3001\u65af\u9ad8\u67cf\u7684\u4ee3\u8868\u90fd\u53bb\u62dc\u8bbf\u8c22\u5251\u5357\uff0c\u9648\u4fe1\u751f\u867d\u7136\u6ca1\u6709\u51fa\u9762\uff0c\u8fd8\u662f\u8ba9TI\u4e2d\u56fd\u516c\u53f8\u5143\u5668\u90e8\u7ecf\u7406\u53bb\u62dc\u8bbf\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u79c1\u8c0a\u5f52\u79c1\u8c0a\uff0c\u751f\u610f\u5f52\u751f\u610f\uff0c\u867d\u7136\u9648\u4fe1\u751f\u5bf9\u79d1\u738b\u7684\u672a\u6765\u4e5f\u4e0d\u770b\u597d\uff0c\u4f46\u662f\u4e0d\u53ef\u5426\u8ba4\u79d1\u738b\u7535\u5668\u5c06\u662f\u4e0b\u4e00\u5e74\u5ea6\u6700\u70ed\u95e8\u7684\u7535\u5b50\u4f01\u4e1a\u4e4b\u4e00\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u5fc5\u987b\u8981\u4e89\u7684\u5927\u5ba2\u6237\uff0c\u79d1\u738b\u5728\u8fd9\u4e24\u4e2a\u5173\u952e\u5143\u5668\u4ef6\u4e0a\uff0c\u4e5f\u5e94\u8be5\u4e00\u6837\u80fd\u591f\u4eab\u53d7\u4e0e\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u540c\u7b49\u7684\u6298\u6263\u4f18\u60e0\u3002</p><p>\u3000\u3000\u81f3\u4e8eTI\u4e0e\u7231\u8fbe\u7684\u534f\u8bae\u4ef7\uff0c\u8fd8\u662f\u4e24\u5bb6\u516c\u53f8\u7684\u9ad8\u5ea6\u673a\u5bc6\uff0c\u5c31\u7b97\u534f\u8bae\u4ef7\u5e9f\u6b62\uff0c\u4e0d\u8fc7\u5341\u5e74\u516b\u5e74\uff0c\u4e24\u5bb6\u516c\u53f8\u662f\u4e0d\u4f1a\u89e3\u5bc6\u7684\uff0c\u534f\u8bae\u4ef7\u4e5f\u53ef\u4ee5\u8bf4\u6210\u6b67\u89c6\u4ef7\uff0c\u4f1a\u5c06\u5784\u65ad\u7684\u9762\u76ee\u516c\u7136\u66b4\u9732\u65e0\u7591\uff0c\u4e00\u7ecf\u6f0f\u9732\u4f1a\u5f15\u8d77\u5176\u4ed6\u516c\u53f8\u7684\u5f3a\u70c8\u6297\u8bae\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u82cf\u6d25\u4e1c\u4e0e\u8d75\u6d77\u821f\u5728\u8fd9\u91cc\u518d\u76ef\u4e24\u5929\uff0c\u9a6c\u6d77\u9f99\u7559\u4e0b\u6765\u6253\u6253\u4e0b\u624b\uff0c\u770b\u770b\u5a92\u4f53\u7684\u53cd\u5e94\u518d\u505a\u51b3\u5b9a\uff0c\u4ed6\u4e0e\u5176\u4ed6\u4eba\u5c31\u4e58\u5750\u7b2c\u4e8c\u5929\u4e0a\u5348\u4e5d\u70b9\u949f\u7684\u98de\u673a\u8fd4\u56de\u6d77\u5dde\uff0c\u9080\u8bf7\u9648\u4fe1\u751f\u968f\u884c\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e0a\u6b21\u5230\u6d77\u5dde\u6765\uff0c\u662f\u5728\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u6240\u4ece\u8ddf\u7231\u8fbe\u7535\u5b50\u5143\u5668\u4ef6\u5206\u516c\u53f8\u501f\u7684\u4e34\u65f6\u529e\u516c\u5730\u5740\u8fc1\u5165\u65b0\u7684\u7814\u7a76\u697c\u4e4b\u65f6\uff0c\u662f\u4e5d\u516d\u5e74\u6625\u8282\u4e4b\u524d\u7684\u4e8b\u60c5\uff0c\u90a3\u65f6\u76ca\u9686\u9547\u4e1c\u5357\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u624d\u6709\u96cf\u5f62\uff0c\u53c8\u8fc7\u53bb\u4e5d\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u4eca\u5929\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u5df2\u7ecf\u5efa\u6210\u89c4\u6a21\u8fbe\u56db\u5343\u4ea9\uff0c\u9664\u4e86\u89e3\u7801\u677f\u4e0e\u673a\u82af\u4e4b\u5916\uff0c\u7535\u5b50\u5de5\u4e1a\u56ed\u5df2\u6db5\u76d6\u5f71\u789f\u673a\u751f\u4ea7\u6240\u9700\u7684\u96f6\u90e8\u4ef6\u5382\u5546\uff0c\u79d1\u738b\u7535\u5b50\u4e5f\u5c06\u4ed6\u4eec\u7684\u7ec4\u88c5\u8f66\u95f4\u5efa\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u91cc\uff0c\u5f20\u606a\u81ea\u7136\u4e5f\u65e0\u6cd5\u963b\u6b62\u8fd9\u4ef6\u4e8b\uff0c\u9664\u4e86\u79d1\u738b\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u5bb6\u89c4\u6a21\u8f83\u5927\u7684\u7ec4\u88c5\u4f01\u4e1a\u8fdb\u9a7b\u56ed\u533a\uff0c\u6d77\u5dde\u8fd8\u6709\u4e00\u4e9b\u5f71\u789f\u673a\u7ec4\u88c5\u5382\uff0c\u7eaf\u7cb9\u662f\u8d34\u724c\u5047\u5192\u5176\u4ed6\u5f71\u789f\u673a\u54c1\u724c\uff0c\u4e3b\u8981\u8fd8\u662f\u5047\u5192\u7231\u8fbe\uff0c\u81ea\u7136\u4e0d\u4f1a\u8fdb\u9a7b\u56ed\u533a\uff1b\u9664\u4e86\u8fd9\u4e9b\u4e4b\u5916\uff0c\u8fd8\u6709\u90b5\u81f3\u521a\u4ed6\u4eec\u90a3\u5bb6\u4e13\u95e8\u751f\u4ea7\u5149\u76d8\u7684\u591a\u5a92\u4f53\u5de5\u5382\u3002</p><p>\u3000\u3000\u201c\u5dee\u4e0d\u591a\u90fd\u5f62\u6210\u5b8c\u6574\u7684\u5f71\u789f\u673a\u7684\u5de5\u4e1a\u88e2\u4e86\uff01\u201d\u9648\u4fe1\u751f\u611f\u6168\u9053\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6709\u673a\u82af\uff0c\u4e0d\u80fd\u751f\u4ea7\u89e3\u7801\u677f\uff0c\u8fd9\u4e2a\u79bb\u5b8c\u6574\u8fd8\u5dee\u592a\u8fdc\u7684\u8ddd\u79bb\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u91cc\u6700\u9ad8\u7684\u5efa\u7b51\u5374\u662f\u7231\u8fbe\u7535\u5b50\u5efa\u4e8e\u5de5\u4e1a\u56ed\u533a\u4ee5\u897f\u3001\u7d27\u90bb\u7535\u5b50\u5de5\u4e1a\u56ed\u7684\u516d\u5c42\u804c\u5de5\u516c\u5bd3\uff0c\u7ad9\u5230\u804c\u5de5\u516c\u5bd3\u7684\u9876\u5c42\u5929\u53f0\u4e0a\uff0c\u53ef\u4ee5\u5c06\u6574\u4e2a\u7535\u5b50\u5de5\u4e1a\u56ed\u4e00\u89c8\u65e0\u4f59\u3002</p><p>\u3000\u3000\u9664\u4e86\u5df2\u5efa\u6210\u7684\u5382\u533a\u5916\uff0c\u7535\u5b50\u5de5\u4e1a\u5f80\u4e1c\u53c8\u6e05\u51fa\u56db\u4e94\u5343\u4ea9\u7684\u571f\u5730\uff0c\u53ef\u4ee5\u9884\u89c1\uff0c\u4e0d\u4e45\u7684\u672a\u6765\uff0c\u6d77\u5dde\u7684\u54c1\u724c\u5f71\u789f\u673a\u4ea7\u91cf\u5c06\u5360\u5230\u56fd\u5185\u5e02\u573a\u4efd\u989d\u768450\uff05\u751a\u81f360\uff05\u4ee5\u4e0a\uff0c\u90a3\u5757\u571f\u5730\u5f88\u5feb\u5c31\u4f1a\u7ed9\u586b\u6ee1\u7684\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5382\u533a\u5357\u4fa7\u672c\u6765\u5c31\u6709\u4e0a\u5343\u4ea9\u7684\u51c0\u5730\u662f\u7231\u8fbe\u7535\u5b50\u7684\u9884\u7559\u53d1\u5c55\u7528\u5730\uff0c\u7231\u8fbe\u7535\u5b50\u5df2\u7ecf\u4ece\u8fd9\u5757\u57ce\u5357\u533a\u7ed9\u4ed6\u4eec\u7684\u9884\u7559\u5730\u91cc\u4e70\u4e0b\u4e24\u767e\u4ea9\u5efa\u8bbe\u5382\u623f\u3002</p><p>\u3000\u3000\u7ad9\u5728\u804c\u5de5\u516c\u5bd3\u7684\u9876\u5c42\u5929\u53f0\uff0c\u9648\u4fe1\u751f\u770b\u5230\u529e\u516c\u697c\u3001\u5382\u623f\u90fd\u5dee\u4e0d\u591a\u5df2\u7ecf\u5efa\u6210\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u51c6\u5907\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5efa\u6210\u540e\uff0c\u5143\u5668\u4ef6\u5206\u516c\u53f8\u4f1a\u8fc1\u8fdb\u53bb\u3002\u201d</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5143\u5668\u4ef6\u5206\u516c\u53f8\u76ee\u524d\u4f4d\u4e8e\u57ce\u5c71\u8def\u7684\u5382\u533a\u91cc\uff0c\u90a3\u4e2a\u5382\u533a\u7d27\u51d1\u522b\u81f4\uff0c\u7ecf\u8fc7\u6269\u5efa\uff0c\u89c4\u6a21\u8fd8\u4e0d\u8db3\u516b\u5341\u4ea9\uff0c\u4f46\u662f\u5c31\u751f\u4ea7\u7231\u8fbe\u7535\u5b50\u6240\u9700\u7684\u7535\u8def\u677f\uff0c\u5df2\u8db3\u8db3\u6709\u4f59\u4e86\uff0c\u5f53\u7136\u6ca1\u6709\u5fc5\u8981\u5728\u8fd9\u91cc\u518d\u5efa\u4e00\u4e2a\u89c4\u6a21\u5927\u4e00\u500d\u4ee5\u4e0a\u7684\u65b0\u5382\u533a\uff0c\u518d\u8bf4\u5143\u5668\u4ef6\u5206\u516c\u53f8\u642c\u8fdb\u65b0\u5382\u533a\uff0c\u90a3\u7231\u8fbe\u6295\u5165\u4e5f\u4e0d\u5c11\u7684\u8001\u5382\u533a\u7528\u6765\u505a\u4ec0\u4e48\uff0c\u76ee\u524d\u79d1\u6280\u516c\u53f8\u8fd8\u5728\u90a3\u91cc\uff0c\u9ad8\u5927\u5bbd\u5e7f\u7684\u5382\u623f\uff0c\u641e\u7814\u53d1\u53c8\u6d3e\u4e0d\u4e0a\u7528\u573a\uff1b\u9648\u4fe1\u751f\u7b11\u7740\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5df2\u7ecf\u5c06\u673a\u82af\u5de5\u5382\u8ba1\u7b97\u8fdb\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u7231\u8fbe\u8fd8\u6709\u5176\u4ed6\u4e00\u4e9b\u8ba1\u5212\uff0c\u673a\u82af\u7684\u9879\u76ee\u8c08\u4e0d\u59a5\uff0c\u6211\u4eec\u4f1a\u8003\u8651\u4e0a\u4e00\u4e2a\u638c\u4e0a\u5b66\u4e60\u673a\u7684\u9879\u76ee\uff0c\u201d\u5176\u5b9e\u673a\u82af\u7684\u9879\u76ee\u80fd\u8c08\u4e0b\u6765\uff0c\u7231\u8fbe\u4e5f\u4f1a\u5728\u5e74\u524d\u4e0a\u638c\u4e0a\u5b66\u4e60\u673a\uff0c\u8fd9\u4e00\u5757\u53ef\u4ee5\u5ef6\u4f38\u5230\u638c\u4e0a\u7535\u8111\u7b49\u8bf8\u591a\u4ea7\u54c1\uff0c\u201c\u5c31\u673a\u82af\u800c\u8a00\uff0c\u7d22\u5c3c\u3001\u98de\u5229\u6d66\u90fd\u6709\u4e86\u4e09\u789f\u3001\u53cc\u9762\u8bfb\u53d6\u673a\u82af\uff0c\u8fd9\u4e9b\u673a\u82af\u7684\u7ed3\u6784\u6bd4\u8f83\u590d\u6742\uff0c\u8fd8\u6709\u4e00\u4e9b\u96be\u5ea6\uff0c\u4e0d\u8fc7\u5355\u9762\u8bfb\u53d6\u3001\u5355\u789f\u673a\u82af\uff0c\u53ea\u6709TI\u6709\u51b3\u5fc3\u8ddf\u6211\u4eec\u5408\u4f5c\uff0c\u6211\u60f3\u8fd9\u4e9b\u5382\u623f\u9a6c\u4e0a\u5c31\u53ef\u4ee5\u7528\u5f97\u4e0a\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u6fc0\u5149\u8bfb\u53d6\u5934\u7684\u5de5\u827a\u6761\u4ef6\uff0c\u7231\u8fbe\u80fd\u8ddf\u5f97\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u6709\u65f6\u5019\uff0c\u6709\u4e9b\u4ea7\u54c1\u7684\u7406\u8bba\u6280\u672f\u4e0d\u96be\u638c\u638c\u63e1\uff0c\u4f46\u662f\u56fd\u5185\u5de5\u5382\u7684\u5de5\u827a\u6761\u4ef6\u5dee\uff0c\u751f\u4ea7\u51fa\u6765\u7684\u4ea7\u54c1\u8d28\u91cf\u4e0d\u7a33\u5b9a\uff0c\u6839\u672c\u6ca1\u6709\u5b9e\u529b\u4e0e\u6d77\u5916\u5236\u9020\u5de8\u5934\u6765\u7ade\u4e89\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u8fd9\u65b9\u9762\uff0c\u5143\u5668\u4ef6\u5382\u4e00\u76f4\u5728\u6539\u5584\uff0c\u5f53\u7136\u5dee\u8ddd\u8fd8\u662f\u5b58\u5728\u7684\uff0c\u9700\u8981TI\u6d3e\u4eba\u6307\u5bfc\u3002\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u771f\u662f\u5f88\u6709\u628a\u63e1\u80fd\u6253\u52a8\u6211\u554a\u3002\u201d\u9648\u4fe1\u751f\u7b11\u4e86\u8d77\u6765\u3002</p></article>"}}}]);
