(window.webpackJsonp=window.webpackJsonp||[]).push([[439],{462:function(p){p.exports={chapterTitle:null,title:"\u7b2c439\u7ae0 \u987e\u5168\u5927\u5c40",content:"<article><h2>\u7b2c439\u7ae0 \u987e\u5168\u5927\u5c40</h2><p>\u3000\u3000\u89c1\u5f20\u606a\u5bf9\u5408\u8d44\u5efa\u673a\u82af\u5de5\u5382\u7684\u5982\u6b64\u6709\u4fe1\u5fc3\uff0c\u9648\u4fe1\u751f\u7684\u597d\u5947\u5fc3\u5df2\u7ecf\u7ed9\u540a\u5230\u6781\u70b9\uff0c\u53ef\u662f\u5f20\u606a\u975e\u5e38\u6709\u8010\u5fc3\u7684\u966a\u4ed6\u53c2\u89c2\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u9648\u4fe1\u751f\u90fd\u8010\u4e0d\u4f4f\u8981\u62b1\u6028\u4e86\uff1a\u201c\u597d\u5947\u5fc3\u4f1a\u6298\u78e8\u6b7b\u4eba\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u603b\u8981\u6709\u65f6\u95f4\u7ed9\u4e01\u69d0\u505a\u51c6\u5907\uff0c\u597d\u5427\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u79d1\u6280\u516c\u53f8\u3002\u201d</p><p>\u3000\u3000\u4e0b\u697c\u68af\u65f6\uff0c\u5f20\u606a\u8ddf\u9648\u4fe1\u751f\u4ecb\u7ecd\u4ed6\u5c06\u7814\u53d1\u4e2d\u5fc3\u4ece\u7231\u8fbe\u7535\u5b50\u5206\u62c6\u51fa\u6765\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u6ce8\u91cd\u6280\u672f\u4e0a\u7684\u6295\u5165\uff0c\u8fd9\u4e00\u70b9\u9648\u4fe1\u751f\u65e9\u5c31\u6709\u6240\u9886\u7565\uff0c\u4e0d\u7136TI\u4e0e\u7231\u8fbe\u7684\u5408\u4f5c\u7edd\u4e0d\u53ef\u80fd\u8d70\u5230\u4eca\u5e74\u7684\u6df1\u5ea6\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u95ee\u9053\uff1a\u201c\u90a3\u7231\u8fbe\u7535\u5b50\u5c31\u6ca1\u6709\u7814\u53d1\u4e2d\u5fc3\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u6709\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4ece\u516c\u53f8\u5b8c\u6574\u7ed3\u6784\u4e0a\uff0c\u6709\u4fdd\u7559\u7814\u53d1\u4e2d\u5fc3\u7684\u5fc5\u8981\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e0e\u9526\u6e56\u7535\u5b50\u7814\u7a76\u9662\u4e0b\u8bbe\u7684\u89c6\u542c\u6280\u672f\u7814\u7a76\u5ba4\u662f\u91cd\u5408\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u662f\u4e0d\u662f\u53ef\u4ee5\u7406\u89e3\u4e3a\u9526\u6e56\u7535\u5b50\u7814\u7a76\u9662\u7684\u89e6\u624b\u5df2\u7ecf\u4f38\u5230\u89c6\u542c\u6280\u672f\u9886\u57df\u4e4b\u5916\u4e86\uff1f\u201d\u9648\u4fe1\u751f\u8ba4\u771f\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u4eca\u5e74\u662f\u6402\u4e86\u4e00\u4e9b\u94b1\uff0c\u80fd\u82b1\u4ec0\u4e48\u5730\u65b9\u53bb\uff1f\u201d\u5f20\u606a\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u9053\uff0c\u201c\u7ec4\u5efa\u9526\u6e56\u7535\u5b50\u7814\u7a76\u6240\uff0c\u4e5f\u662f\u4e3a\u4e86\u65b9\u4fbf\u7814\u7a76\u7684\u9886\u57df\u5e7f\u4e00\u4e9b\uff0c\u4e0d\u7136\u90fd\u5728\u7231\u8fbe\u7535\u5b50\u7814\u53d1\u4e2d\u5fc3\u7684\u540d\u4e49\u4e0b\u641e\u8fd9\u4e9b\u9879\u76ee\uff0c\u5185\u884c\u4eba\u770b\u4e86\u4e5f\u4f1a\u7b11\u6211\u4eec\u4e0d\u52a1\u6b63\u4e1a\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u9648\u4fe1\u751f\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u5012\u5f88\u60f3\u770b\u770b\u662f\u600e\u6837\u4e0d\u52a1\u6b63\u4e1a\u6cd5\uff0c\u4f60\u521a\u624d\u8bf4\u51c6\u5907\u4e0a\u4e00\u4e2a\u638c\u4e0a\u5b66\u4e60\u673a\u7684\u4ea7\u54c1\uff0c\u6280\u672f\u5e94\u8be5\u662f\u5b8c\u5168\u6210\u719f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fc7\u53bb\u4e24\u5e74\u65f6\u95f4\uff0c\u5728\u5f71\u789f\u673a\u63a7\u5236\u7cfb\u7edf\u7684\u7814\u7a76\u4e0a\u79ef\u7d2f\u4e86\u4e00\u4e9b\u7ecf\u9a8c\uff0c\u73b0\u5728\u5c31\u60f3\u5230\u5176\u4ed6\u5730\u65b9\u8bd5\u8bd5\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7231\u8fbe\u5728\u5f71\u789f\u673a\u63a7\u5236\u7cfb\u7edf\u4e0a\u7684\u6280\u672f\u4f18\u52bf\uff0c\u9648\u4fe1\u751f\u662f\u6e05\u695a\u7684\uff0cTI\u5c06\u89e3\u7801\u82af\u7247\u5916\u5c42\u5e94\u7528\u6280\u672f\u7684\u5f00\u53d1\u90fd\u4e22\u7ed9\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\uff0c\u4fbf\u662f\u770b\u4e2d\u6cf0\u82af\u53ef\u4ee5\u501f\u52a9\u7231\u8fbe\u5728\u8fd9\u65b9\u9762\u7684\u4f18\u52bf\uff0c\u9648\u4fe1\u751f\u8fd8\u76f8\u4fe1\u7231\u8fbe\u7535\u5b50\u5e94\u8be5\u5df2\u7ecf\u638c\u63e1\u89e3\u7801\u82af\u3001\u6fc0\u5149\u8bfb\u53d6\u5934\u4e4b\u5916\u7684\u6240\u6709\u5f71\u789f\u673a\u6280\u672f\u3002</p><p>\u3000\u3000TI\u3001\u65af\u9ad8\u67cf\u8fd8\u6709\u98de\u5229\u6d66\u90fd\u4f1a\u5411\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u63d0\u4f9b\u5b8c\u6574\u7684\u7cfb\u7edf\u89e3\u51b3\u65b9\u6848\uff0c\u968f\u7740\u5f71\u789f\u673a\u529f\u80fd\u7684\u65e5\u76ca\u5b8c\u5584\uff0c\u7cfb\u7edf\u63a7\u5236\u65e5\u76ca\u590d\u6742\uff0c\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u7684\u751f\u4ea7\u4e5f\u4e0d\u662f\u4e3b\u6d41\u5f71\u789f\u673a\u5382\u5546\u6240\u80fd\u80dc\u4efb\uff0c\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u53ca\u65f6\u4ecb\u5165\uff0c\u5f7b\u5e95\u4f7f\u5f97\u5f71\u789f\u673a\u7684\u751f\u4ea7\u6210\u4e3a\u51e0\u4e2a\u519c\u6c11\u5de5\u5c31\u80fd\u80dc\u4efb\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5143\u5668\u4ef6\u5206\u516c\u53f8\u6240\u751f\u4ea7\u7684\u5f71\u789f\u673a\u63a7\u5236\u82af\u7247\uff0c\u9664\u4e86\u529f\u80fd\u66f4\u4f18\u8d8a\u4e4b\u5916\uff0c\u8d28\u91cf\u7a33\u5b9a\u6027\u4e5f\u4e1d\u6beb\u4e0d\u6bd4\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u5dee\uff0c\u7231\u8fbe\u7535\u5b50\u4ec5\u5728\u8fd9\u4e00\u5757\u5c31\u6bd4\u5176\u4ed6\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u964d\u4f4e\u4e0d\u5c11\u6210\u672c\uff0c\u53ea\u6709\u65b0\u79d1\u7535\u5b50\u5728\u8fd9\u4e0a\u9762\u80fd\u4e0e\u7231\u8fbe\u7535\u5b50\u76f8\u63d0\u5e76\u8bba\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u6210\u7acb\u8fd8\u4e0d\u8db3\u4e24\u5e74\uff0c\u65b0\u79d1\u7535\u5b50\u5374\u65e9\u5c31\u662f\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u7684\u9f99\u5934\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u6240\u4ee5\u7231\u8fbe\u7535\u5b50\u66f4\u80fd\u5f15\u8d77\u9648\u4fe1\u751f\u7684\u5173\u6ce8\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u8003\u8651\u5176\u4ed6\u5f71\u789f\u673a\u5382\u5546\u53ef\u80fd\u6709\u7684\u62b5\u5236\uff0c\u7231\u8fbe\u7535\u5b50\u5143\u5668\u4ef6\u5206\u516c\u53f8\u5728\u6280\u672f\u6c34\u5e73\u4e0a\u5b8c\u5168\u53ef\u4ee5\u8ddf\u90a3\u4e9b\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u7ade\u4e89\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u7684\u8ba2\u5355\uff0c\u4e0d\u8fc7\u4ec5\u7231\u8fbe\u7535\u5b50\u5728\u5f71\u789f\u673a\u6240\u5360\u7684\u5e02\u573a\u4efd\u989d\u5b9e\u9645\u4e5f\u4ee3\u8868\u5176\u5728\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u5e02\u573a\u4e0a\u7684\u4efd\u989d\uff0c\u6ca1\u6709\u5fc5\u8981\u5192\u7740\u72af\u4f17\u6012\u7684\u5371\u9669\u518d\u53bb\u4e89\u8ba2\u5355\uff1b\u5373\u4f7f\u4e0d\u505a\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u7684\u5916\u5355\uff0c\u4f46\u662f\u5229\u7528\u8fd9\u65b9\u9762\u7684\u4f18\u52bf\u53bb\u5f00\u53d1\u5176\u4ed6\u4ea7\u54c1\uff0c\u4e5f\u6b63\u597d\u8ba9\u7231\u8fbe\u7535\u5b50\u7684\u54c1\u724c\u4f18\u52bf\u66f4\u5145\u5206\u7684\u90fd\u53d1\u6325\u51fa\u6765\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e5f\u8003\u8651\u5230\u7231\u8fbe\u7535\u5b50\u8fdf\u65e9\u4f1a\u8d70\u51fa\u8fd9\u4e00\u6b65\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e5f\u4e0d\u76f8\u4fe1\u7231\u8fbe\u7535\u5b50\u53ea\u6709\u8fd9\u4e48\u4e00\u70b9\u79c1\u8d27\uff0c\u5f53\u7136\uff0c\u8fd9\u90fd\u662f\u9526\u6e56\u7684\u5546\u4e1a\u673a\u5bc6\uff0c\u9648\u4fe1\u751f\u4e5f\u4e0d\u5962\u671b\u5f20\u606a\u4f1a\u503e\u56ca\u76f8\u544a\uff0c\u8fd8\u662f\u5148\u770b\u770b\u7231\u8fbe\u7535\u5b50\u5728\u89c6\u542c\u6280\u672f\u4e0a\u6709\u4ec0\u4e48\u6740\u624b\u950f\u5427\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u542c\u5f20\u606a\u4eb2\u53e3\u5426\u8ba4\u7231\u8fbe\u5229\u7528\u6210\u672c\u4f18\u52bf\u6253\u4ef7\u683c\u6218\u8fd9\u5f20\u724c\u7684\u53ef\u80fd\uff0c\u4e5f\u5c31\u731c\u5230\u7231\u8fbe\u907f\u514d\u79d1\u738b\u4ece\u4ed6\u4eec\u7684\u7897\u91cc\u5206\u8d70\u4e00\u676f\u7fb9\u7684\u53e6\u4e00\u5f20\u724c\u5c31\u662f\u6280\u672f\u9886\u5148\u8fd9\u5f20\u724c\u3002</p><p>\u3000\u3000\u6210\u672c\u63a7\u5236\u4e0e\u6280\u672f\u9886\u5148\u624d\u80fd\u79f0\u5f97\u4e00\u5bb6\u4f01\u4e1a\u7684\u6838\u5fc3\u7ade\u4e89\u529b\u3002</p><p>\u3000\u3000\u76ee\u524d\u5f71\u789f\u673a\u751f\u4ea7\u5df2\u7ecf\u5b8c\u5168\u5b9e\u9645\u7ec4\u4ef6\u5316\uff0c\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u4e3a\u4e86\u52a0\u5f3a\u5728\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u4e0a\u7684\u5784\u65ad\u529b\u5ea6\uff0c\u4e5f\u52a0\u5f3a\u6280\u672f\u6295\u5165\uff0c\u5f53\u524d\u7231\u8fbe\u6240\u63a8\u51fa\u7684\u51e0\u6b3e\u5f71\u789f\u673a\uff0c\u5176\u63a7\u5236\u7cfb\u7edf\u3001\u4f3a\u670d\u7cfb\u7edf\u90e8\u5206\uff0c\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u90fd\u6709\u76f8\u5bf9\u5e94\u7684\u82af\u7247\u4ea7\u54c1\u63d0\u4f9b\uff0c\u4ece\u76ee\u524d\u7684\u5e02\u573a\u5b9e\u9645\u60c5\u51b5\u6765\u770b\uff0c\u7231\u8fbe\u7535\u5b50\u5e76\u6ca1\u6709\u592a\u5f3a\u7684\u6280\u672f\u4f18\u52bf\u3002</p><p>\u3000\u3000\u4e1a\u5185\u867d\u7136\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u6280\u672f\u5b9e\u529b\u6ca1\u6709\u592a\u9ad8\u7684\u8bc4\u4ef7\uff0c\u81f3\u5c11\u4e0d\u8ba4\u4e3a\u7231\u8fbe\u7535\u5b50\u7684\u6280\u672f\u5b9e\u529b\u6bd4\u65b0\u79d1\u7535\u5b50\u4ee5\u53ca\u90a3\u4e9b\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u66f4\u5f3a\uff0c\u4e0d\u8fc7\u7531\u4e8eTI\u4e00\u76f4\u4e0e\u7231\u8fbe\u6709\u7740\u5bc6\u5207\u6280\u672f\u5408\u4f5c\u7684\u5173\u7cfb\uff0c\u9648\u4fe1\u751f\u5bf9\u7231\u8fbe\u8fd8\u662f\u6709\u5f88\u9ad8\u7684\u671f\u5f85\u503c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6b63\u503c\u4e0b\u73ed\u65f6\u95f4\uff0c\u57ce\u5c71\u8def\u662f\u5f00\u53d1\u533a\u901a\u5f80\u4e3b\u57ce\u533a\u7684\u4e3b\u8981\u9053\u8def\uff0c\u8f66\u6d41\u62e5\u585e\uff0c\u524d\u4e24\u5e74\u90fd\u8fd8\u6ca1\u6709\u89c9\u5f97\u57ce\u5c71\u8def\u8fd9\u65f6\u95f4\u6709\u8fd9\u4e48\u5835\u3002\u5f20\u606a\u7684\u8f66\u4e5f\u7ed9\u5939\u5728\u8f66\u6cb3\u4e4b\u4e2d\uff0c\u7f13\u6162\u5411\u524d\u8717\u884c\u3002</p><p>\u3000\u3000\u624b\u673a\u54cd\u8d77\uff0c\u662f\u5b8b\u57f9\u660e\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u6309\u4e86\u901a\u8bdd\u952e\uff0c\u5b8b\u57f9\u660e\u7684\u58f0\u97f3\u5c31\u4f20\u4e86\u8fc7\u6765\uff1a\u201c\u4f60\u56de\u6d77\u5dde\u4e86\uff0c\u5728\u4f60\u7684\u8f66\u91cc\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u770b\uff0c\u4ed6\u56de\u6d77\u5dde\u8fd8\u6ca1\u6709\u7279\u610f\u901a\u77e5\u8c01\uff0c\u5b8b\u57f9\u660e\u5e94\u8be5\u770b\u5230\u81ea\u5df1\u7684\u8f66\u4e86\uff1a\u201c\u662f\u7684\uff0c\u4e5d\u70b9\u949f\u7684\u98de\u673a\uff0c\u4e0b\u5348\u4e00\u70b9\u949f\u624d\u5230\u5e02\u533a\uff0c\u4f60\u5728\u54ea\u8f86\u8f66\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u8111\u888b\u522b\u6643\u6765\u6643\u53bb\u770b\u4e86\uff0c\u6211\u5c31\u5750\u5728\u4f60\u540e\u9762\u7684\u9ed1\u522b\u514b\u91cc\uff0c\u5510\u5e02\u957f\u4e5f\u5728\uff0c\u521a\u53bb\u5f00\u53d1\u533a\u56de\u6765\u3002\u6ca1\u542c\u8bf4\u4f60\u4eca\u5929\u4f1a\u4ece\u5317\u4eac\u56de\u6765\uff0c\u82cf\u6d25\u4e1c\u4ed6\u4eba\u4e0d\u8fd8\u5317\u4eac\u63a5\u53d7\u501f\u8bbf\u5417\uff1f\u6211\u521a\u521a\u8fd8\u5728\u65b0\u95fb\u91cc\u770b\u5230\u4ed6\u9732\u9762\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5b8b\u57f9\u660e\u5927\u6982\u662f\u966a\u5510\u5b66\u8c26\u53bb\u5f00\u53d1\u533a\u8003\u5bdf\u4f01\u4e1a\u53bb\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u966aTI\u7684\u9648\u603b\u88c1\u5148\u56de\u6765\u3002\u5077\u5077\u7684\u8fdb\u6751\uff0c\u6253\u67aa\u7684\u4e0d\u8981\uff0c\u79d1\u738b\u7684\u90a3\u5e2e\u4eba\u8fd8\u4ee5\u4e3a\u6211\u7559\u5728\u5317\u4eac\u8ddf\u4ed6\u4eec\u4f7f\u574f\u5462\uff0c\u8ba9\u4ed6\u4eec\u63d0\u5fc3\u540a\u80c6\u7740\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u8fd8\u6ca1\u6709\u60f3\u5230\u8981\u901a\u77e5\u4f60\u4eec\u5462\uff0c\u6211\u4eec\u73b0\u5728\u53bb\u7814\u7a76\u9662\uff0c\u4f60\u4eec\u665a\u4e0a\u6709\u6ca1\u6709\u5176\u4ed6\u7684\u5b89\u6392\uff1f\u201d</p><p>\u3000\u3000\u201c\u54ea\u4e2a\u7814\u7a76\u9662\uff0c\u9526\u6e56\u8fd8\u662f\u6cf0\u5fd2\uff1f\u201d\u5b8b\u57f9\u660e\u5bf9\u9526\u6e56\u7684\u60c5\u51b5\u81ea\u7136\u662f\u4e86\u5982\u6307\u638c\u3002</p><p>\u3000\u3000\u518d\u5f80\u524d\u5f00\u4e00\u6bb5\uff0c\u62d0\u5165\u6d2a\u6c5f\u8def\u5c31\u53ef\u4ee5\u53bb\u4f4d\u4e8e\u6d77\u5dde\u5927\u5b66\u4e1c\u4fa7\u7684\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u3002</p><p>\u3000\u3000\u201c\u7535\u5b50\u7814\u7a76\u9662\uff0c\u57ce\u5c71\u8def\u90a3\u5bb6\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u6211\u4eec\u7684\u8f66\u5b50\u5c31\u8ddf\u5728\u4f60\u4eec\u540e\u9762\u3002\u201d</p><p>\u3000\u3000\u5230\u6d2a\u6c5f\u8def\uff0c\u8f66\u8f86\u7ed9\u5206\u6d41\u6389\u4e00\u90e8\u5206\uff0c\u8def\u51b5\u603b\u7b97\u597d\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u8fd9\u65f6\u79bb\u5143\u5668\u4ef6\u5206\u516c\u53f8\u5f88\u8fd1\u4e86\u3002</p><p>\u3000\u3000\u9526\u6e56\u7535\u5b50\u7814\u7a76\u9662\u4e0e\u5143\u5668\u4ef6\u5206\u516c\u53f8\u8fd8\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u5728\u5143\u5668\u4ef6\u5206\u516c\u53f8\u524d\u505c\u4e0b\u6765\uff0c\u7b49\u540e\u9762\u7684\u9ed1\u8272\u522b\u514b\u5f00\u8fc7\u6765\uff0c\u5f20\u606a\u8dd1\u8fc7\u6765\u5e2e\u5510\u5b66\u8c26\u3001\u5b8b\u57f9\u660e\u6253\u5f00\u8f66\u95e8\uff0c\u5510\u5b66\u8c26\u4e0e\u5b8b\u57f9\u660e\u8fd8\u5728\u8ba8\u8bba\u5f00\u53d1\u533a\u4e0e\u4e3b\u57ce\u533a\u4e4b\u95f4\u8fde\u63a5\u7684\u9053\u8def\u8981\u72e0\u72e0\u4fee\u4e00\u756a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8c61\u5c71\u4e1c\u9e93\u4e0e\u5f00\u53d1\u533a\u3001\u76ca\u9686\u9547\u8fde\u63a5\u7684\u9053\u8def\u8fd8\u662f\u533a\u53bf\u4e00\u7ea7\u7684\u5e72\u9053\uff0c\u9053\u8def\u72ed\u7a84\u4e0d\u8bf4\uff0c\u8fd8\u6ca1\u6709\u4eba\u884c\u9053\u4e0e\u975e\u673a\u52a8\u8f66\u9053\u3002</p><p>\u3000\u3000\u201c\u516d\u8f66\u9053\uff1f\u201d\u5f20\u606a\u542c\u5230\u4ed6\u4eec\u804a\u5929\u7684\u5185\u5bb9\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u770b\u81f3\u5c11\u8981\u516b\u8f66\u9053\uff0c\u5f97\u8003\u8651\u4eca\u540e\u5341\u5e74\u7684\u53d1\u5c55\uff0c\u4ee5\u6d77\u5dde\u5f53\u524d\u7684\u52bf\u5934\uff0c\u516b\u8f66\u9053\u4e5f\u9876\u591a\u7ba1\u56db\u4e94\u5e74\uff0c\u6700\u597d\u4e1c\u9762\u518d\u4fee\u4e00\u6761\u8def\u5206\u6d41\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8bf4\u8d77\u6765\u8f7b\u677e\uff0c\u4e00\u6761\u516d\u8f66\u9053\u7684\u8def\uff0c\u5c31\u6709\u4eba\u5728\u9a82\u6211\u662f\u8d25\u5bb6\u5b50\uff0c\u8981\u4e0a\u516b\u8f66\u9053\uff0c\u80fd\u591a\u5c11\u4eba\u6000\u7591\u6211\u75af\u4e86\uff1f\u201d\u5510\u5b66\u8c26\u4e0b\u4e86\u8f66\uff0c\u7b11\u7740\u8bf4\u4e86\u5f20\u606a\u4e00\u901a\uff0c\u4ed6\u4e5f\u8ba4\u4e3a\u57fa\u7840\u8bbe\u65bd\u6295\u8d44\u8981\u6709\u66f4\u5927\u7684\u8fdc\u89c1\uff0c\u4e0d\u8fc7\u5728\u5f00\u53d1\u533a\u4e0e\u4e3b\u57ce\u533a\u4e4b\u95f4\u5efa\u8fd9\u4e48\u9ad8\u7ea7\u7684\u5e72\u9053\uff0c\u963b\u529b\u592a\u5927\u4e86\u3002\u4ed6\u770b\u5230\u9648\u4fe1\u751f\u8d70\u8fc7\u6765\uff0c\u5f88\u70ed\u60c5\u7684\u8d70\u8fc7\u53bb\u8ddf\u4ed6\u63e1\u624b\uff1a\u201c\u9648\u5148\u751f\uff0c\u6b22\u8fce\u5230\u6d77\u5dde\u6765\u505a\u5ba2\u3002\u201d</p><p>\u3000\u3000\u201c\u90fd\u8bf4\u662f\u5916\u6765\u7684\u548c\u5c1a\u597d\u5ff5\u7ecf\u4e86\u2026\u2026\u201d\u5f20\u606a\u5600\u5495\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u4f60\u60f3\u600e\u7684\uff0c\u6211\u4e0b\u6765\u7ed9\u4f60\u5f00\u8f66\u95e8\uff1f\u201d\u5510\u5b66\u8c26\u56de\u5934\u770b\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f1a\u6298\u5bff\u7684\uff0c\u5927\u597d\u65f6\u5149\u7b49\u7740\u6211\uff0c\u8fd9\u4ef6\u4e8b\u6211\u7edd\u4e0d\u6562\u5962\u60f3\u3002\u201d\u5f20\u606a\u8146\u7740\u8138\u8bf4\u7b11\uff0c\u4ed6\u5728\u5510\u5b66\u8c26\u9762\u524d\u603b\u662f\u665a\u8f88\uff0c\u56de\u5934\u5429\u5490\u5085\u4fca\uff0c\u8ba9\u4ed6\u8fd9\u65f6\u5019\u53bb\u5b66\u6821\u63a5\u5510\u5a67\u4e0e\u987e\u5efa\u840d\u3002</p><p>\u3000\u3000\u201c\u8fd8\u8981\u7b49\u8c01\uff1f\u201d\u5b8b\u57f9\u660e\u89c1\u5f20\u606a\u5750\u5728\u95e8\u53e3\u4e5f\u6ca1\u6709\u8981\u8fdb\u53bb\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u201c\u5149\u7535\u7814\u7a76\u9662\u7684\u9996\u5e2d\u79d1\u5b66\u5bb6\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u7684\u9996\u5e2d\u4e3b\u4efb\u5de5\u7a0b\u5e08\u4e01\u78ca\uff0c\u539f\u662fTI\u4e2d\u56fd\u516c\u53f8\u7684\u9996\u5e2d\u5de5\u7a0b\u5e08\uff0c\u662f\u9648\u4fe1\u751f\u63a8\u8350\u4ed6\u5230\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u4e3b\u6301DVD\u8bfb\u53d6\u6280\u672f\u7684\u7814\u53d1\uff0c\u4e5f\u4ee3\u8868TI\u516c\u53f8\u5728\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u7684\u6743\u76ca\u3002</p><p>\u3000\u3000\u7b49\u4f1a\u513f\u5403\u665a\u996d\u65f6\uff0c\u9664\u4e86\u9648\u4fe1\u751f\u3001\u4e01\u78ca\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u5176\u4ed6\u5916\u4eba\uff0c\u53ef\u4ee5\u8bf7\u987e\u5efa\u840d\u4e0e\u5510\u5a67\u4e00\u8d77\u5403\u665a\u996d\uff0c\u56de\u5230\u6d77\u5dde\uff0c\u5f20\u606a\u5c31\u7ed9\u8bb8\u601d\u653e\u4e86\u5047\uff0c\u8ba9\u5979\u4f11\u606f\u51e0\u5929\uff0c\u5728\u7ecf\u9500\u5546\u5927\u4f1a\u53ec\u5f00\u4e4b\u524d\uff0c\u4ed6\u8fd8\u62bd\u7a7a\u53bb\u4e00\u8d9f\u9999\u6e2f\uff0c\u4e4b\u524d\u4e0e\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7684\u6c9f\u901a\u7ed9\u5218\u660e\u8f89\u7684\u8df3\u69fd\u4e8b\u60c5\u6253\u65ad\uff0c\u603b\u4e0d\u80fd\u62d6\u5230\u5143\u65e6\u4e4b\u540e\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e4b\u524d\u7684\u9648\u5217\u5ba4\u6446\u653e\u7740\u5df2\u7ecf\u63a8\u5411\u5e02\u573a\u7684\u51e0\u6b3e\u5f71\u789f\u673a\uff0c\u73b0\u5728\uff0c\u8fd9\u51e0\u6b3e\u5f71\u789f\u673a\u90fd\u5df2\u7ecf\u64a4\u6389\uff0c\u6446\u4e0a\u5341\u51e0\u53f0\u6837\u673a\uff0c\u6a21\u6837\u90fd\u7b97\u4e0d\u4e0a\u8010\u770b\uff0c\u5c31\u7b2c\u4e00\u53f0\u5f71\u789f\u673a\u662f\u5e02\u573a\u53ef\u4ee5\u4e70\u5230\u7684\u90a3\u79cd\u7cbe\u81f4\u789f\u673a\u3002</p><p>\u3000\u3000\u201c\u591a\u5c11\u53f0\uff1f\u201d\u9648\u4fe1\u751f\u7591\u60d1\u7684\u95ee\u5f20\u606a\uff0c\u201c\u5341\u516b\u53f0\u90fd\u662f\u4e0d\u4e00\u6837\u7684\u6837\u673a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5176\u5b9e\u53ea\u6709\u4e00\u53f0\uff0c\u201d\u4e01\u69d0\u8d1f\u8d23\u89e3\u91ca\uff0c\u8bf4\u9053\uff0c\u201c\u7b2c\u4e00\u53f0\u662f\u5f53\u524d\u5e02\u573a\u4e0a\u6700\u65b0\u6b3e\u7684\u5f71\u789f\u673a\uff0c\u771f\u6b63\u7684\u6837\u673a\u662f\u6700\u540e\u4e00\u53f0\uff0c\u6709\u5341\u516d\u9879\u8f83\u5927\u7684\u6539\u8fdb\uff0c\u606a\u5c11\u7684\u610f\u601d\u662f\u5c06\u5341\u516d\u9879\u6539\u8fdb\uff0c\u4e00\u9879\u4e00\u9879\u7684\u62ff\u51fa\u6765\u5c55\u793a\uff0c\u6bd4\u8f83\u6709\u8bf4\u670d\u529b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2018\u606a\u5c11\u2019\uff0c\u6bcf\u4e2a\u4eba\u90fd\u8fd9\u4e48\u5524\u4f60\uff1f\u201d\u5510\u5b66\u8c26\u5f88\u5c11\u542c\u5230\u8fd9\u4e2a\u79f0\u547c\uff0c\u611f\u89c9\u65b0\u9c9c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8146\u7740\u8138\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e0b\u4e00\u6b65\u63a8\u8fd9\u79cd\u673a\u578b\uff0c\u201d\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u4f60\u6253\u7b97\u5c06\u7ecf\u9500\u5546\u5927\u4f1a\u641e\u6210\u65b0\u54c1\u53d1\u5e03\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u62ff\u51fa\u6765\u7ed9\u9648\u4fe1\u751f\u770b\u7684\u81ea\u7136\u662f\u8d85\u7ea7VCD\u789f\u673a\uff0c\u5171\u6709\u5341\u516d\u9879\u8f83\u91cd\u8981\u7684\u4e13\u5229\u6280\u672f\uff0c\u4e00\u9879\u4e00\u9879\u7684\u62ff\u51fa\u6765\u5c55\u793a\uff0c\u81ea\u7136\u6709\u98ce\u9a9a\u5356\u5f04\u4e4b\u5acc\uff0c\u4e0d\u8fc7\u5f88\u9002\u5408\u5728\u65b0\u54c1\u53d1\u5e03\u4f1a\u4e0a\u8425\u9020\u4e00\u4e9b\u6c14\u6c1b\u3002\u9648\u4fe1\u751f\u679c\u7136\u770b\u5f97\u900f\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u81f3\u4e8e\u600e\u4e48\u505a\uff0c\u4e0d\u662f\u8fd8\u8981\u8ddf\u4f60\u5546\u91cf\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ddf\u6211\u6709\u4ec0\u4e48\u597d\u5546\u91cf\u7684\uff1f\u7231\u8fbe\u5728\u7ecf\u9500\u5546\u5927\u4f1a\u5c55\u793a\u65b0\u7684\u6280\u672f\uff0c\u65b0\u6b3e\u789f\u673a\u4e0a\u5e02\uff0c\u6709\u4ec0\u4e48\u597d\u8ddf\u6211\u5546\u91cf\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u770b\u8fc7\u5c31\u4f1a\u660e\u767d\u3002\u201d\u5f20\u606a\u6545\u4f5c\u9ad8\u6df1\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u79d1\u738b\u4e5f\u662f\u6d77\u5dde\u7684\u4f01\u4e1a\uff0c\u9648\u4fe1\u751f\u8fd8\u4e0d\u77e5\u9053\u5510\u5b66\u8c26\u3001\u5b8b\u57f9\u660e\u8fd9\u4e24\u4f4d\u6d77\u5dde\u5e02\u7684\u4e3b\u8981\u5b98\u5458\u4f1a\u5982\u4f55\u770b\u5f85\u4e24\u5bb6\u4f01\u4e1a\u4e4b\u95f4\u7684\u7ade\u4e89\uff0c\u8bf4\u8bdd\u5c31\u5f88\u4fdd\u5b88\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u5c31\u5148\u8010\u4e0b\u6027\u5b50\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u3001\u5b8b\u57f9\u660e\u4e0d\u61c2\u6280\u672f\uff0c\u542c\u4e01\u69d0\u8bf4\u6709\u5341\u516d\u9879\u6539\u8fdb\uff0c\u5c31\u7b49\u7740\u4e00\u9879\u4e00\u9879\u7684\u5c55\u793a\u3002</p><p>\u3000\u3000\u4e01\u69d0\u4eb2\u81ea\u64cd\u5200\u5c55\u793a\u7231\u8fbe\u5728\u89c6\u542c\u6280\u672f\u4e0a\u7684\u5341\u516d\u9879\u6539\u8fdb\u3002</p><p>\u3000\u3000\u5728\u539f\u59cb\u56fe\u50cf\u7684\u753b\u9762\u4e0a\u53e0\u52a0\u56fe\u7247\u548c\u6587\u5b57\uff0c\u53ef\u4ee5\u663e\u793a\u591a\u8fbe4\u79cd\u8bed\u8a00\u7684\u5b57\u5e55\u53ca\u5404\u5f0f\u5404\u6837\u7684\u900f\u660e\u6216\u975e\u900f\u660e\u7684\u5f69\u8272\u56fe\u6807\uff1b\u591a\u79cd\u591a\u6837\u7684\u64ad\u653e\u65b9\u5f0f\uff0c\u524d\u540e\u6162\u653e\u3001\u6162\u901f\u68c0\u7d22\u3001\u9010\u5e27\u64ad\u653e\uff0c\u4e5d\u753b\u9762\u9884\u89c8\uff0c\u5bf9\u753b\u9762\u8fdb\u884c\u53d8\u7126\u5904\u7406\uff1bPAL\u4e0eNTSC\u5236\u5f0f\u8f6c\u6362\uff0c\u9ad8\u6e05\u6670\u5ea6\u9759\u6b62\u753b\u9762\uff0c\u97f3\u54cd\u8868\u73b0\u80fd\u529b\u8981\u4f18\u4e8e\u73b0\u6709\u5f71\u673a\uff0c\u5341\u4e94\u7ea7\u53d8\u8c03\u6df7\u54cd\u5904\u7406\uff0c\u8fd8\u6709\u6d88\u58f0\u3001\u52a9\u5531\u7b49\u529f\u80fd\uff1b\u591a\u5c42\u4ea4\u4e92\u83dc\u5355\uff0c\u5177\u5907\u6807\u51c6\u7684PBC\u56de\u653e\u529f\u80fd\u3002</p><p>\u3000\u3000\u4e01\u69d0\u9010\u4e00\u5c55\u793a\u5341\u516d\u9879\u6539\u8fdb\uff0c\u5510\u5b66\u8c26\u4e0e\u5b8b\u57f9\u660e\u53ea\u662f\u5916\u884c\u770b\u70ed\u95f9\uff0c\u9648\u4fe1\u751f\u4e0e\u4e01\u78ca\u5374\u6068\u4e0d\u5f97\u5c06\u6700\u540e\u4e00\u53f0\u6837\u673a\u62c6\u5f00\uff0c\u9648\u4fe1\u751f\u6307\u7740\u6700\u540e\u4e00\u53f0\u6837\u673a\u95ee\uff1a\u201c\u5341\u516d\u9879\u6539\u8fdb\u90fd\u5728\u6700\u540e\u4e00\u53f0\u6837\u673a\u80fd\u4f53\u73b0\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e01\u69d0\u8ddf\u6211\u662f\u8fd9\u4e48\u8bf4\u7684\uff0c\u8c01\u77e5\u9053\u4ed6\u6709\u6ca1\u6709\u7cca\u5f04\u6211\uff1f\u4f60\u4eec\u90fd\u662f\u4e13\u5bb6\uff0c\u5e2e\u6211\u770b\u4e00\u770b\u4ed6\u4eec\u6709\u6ca1\u6709\u7cca\u5f04\u6211\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u4e01\u69d0\u5c06\u6700\u540e\u4e00\u53f0\u6837\u673a\u8fde\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e0e\u4e01\u78ca\u81ea\u7136\u80fd\u770b\u5230\u66f4\u591a\u6280\u672f\u4e0a\u7684\u7ec6\u8282\uff0c\u5728\u89c6\u9891\u64ad\u653e\u4e2d\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u770b\u5230\u5f71\u789f\u673a\u64ad\u653e\u5e38\u89c1\u7684\u9a6c\u8d5b\u514b\u73b0\u8c61\uff0c\u6e05\u6670\u5ea6\u63d0\u9ad8\u5f88\u591a\uff0c\u8fd9\u65f6\u5019\u4e01\u78ca\u53c8\u6ce8\u610f\u5230\u4e00\u4e2a\u7ec6\u8282\uff0c\u56de\u5934\u56de\u4e01\u69d0\uff1a\u201c\u653e\u7684\u662f\u6807\u51c6\u5b58\u50a8\u5bb9\u91cf\u5149\u76d8\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u7684\u3002\u201d\u4e01\u69d0\u70b9\u70b9\u5934\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e5f\u6ce8\u610f\u5230\u753b\u9762\u5de6\u4e0b\u89d2\u7684\u65f6\u95f4\u663e\u793a\uff0c\u65f6\u95f4\u8f83VCD\u6807\u51c6\u5b58\u50a8\u5bb9\u91cf\u8981\u77ed\u8bb8\u591a\uff0c\u53ef\u80fd\u89c6\u9891\u7247\u6bb5\u672c\u8eab\u7684\u957f\u5ea6\u504f\u77ed\uff0c\u4ed6\u5bf9\u4e01\u69d0\u8bf4\uff1a\u201c\u80fd\u4e0d\u80fd\u628a\u7b2c\u4e00\u53f0\u789f\u673a\u4e5f\u8fde\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u4e01\u69d0\u7b11\u7740\u7167\u505a\uff0c\u521a\u521a\u6240\u6709\u7684\u6837\u673a\u4e00\u53f0\u53f0\u5c55\u793a\u65f6\uff0c\u9648\u4fe1\u751f\u80fd\u611f\u89c9\u5230\u6e05\u6670\u5ea6\u6709\u5f88\u5927\u7684\u63d0\u9ad8\uff0c\u4f46\u662f\u6ca1\u6709\u76f4\u63a5\u7684\u6bd4\u8f83\uff0c\u4e01\u78ca\u6ce8\u610f\u5230\u89c6\u9891\u65f6\u95f4\u7684\u95ee\u9898\uff0c\u7b2c\u4e00\u53f0\u5f71\u789f\u673a\u8fde\u4e0a\u7535\u89c6\u6253\u5f00\uff0c\u9648\u4fe1\u751f\u5fcd\u4e0d\u4f4f\u6478\u7740\u4e0b\u5df4\uff0c\u770b\u4e86\u770b\u4e01\u78ca\uff0c\u4ed6\u5728\u6280\u672f\u4e0a\u4e5f\u4e0d\u662f\u5f88\u80af\u5b9a\uff0c\u4e01\u78ca\u8bf4\u9053\uff1a\u201c\u4ec5\u4ec5\u5bf9\u56fe\u50cf\u89e3\u7801\u7cfb\u7edf\u7684\u4f18\u5316\uff0c\u5f88\u96be\u505a\u5230\u8fd9\u4e00\u70b9\u5417\uff1f\u201d\u8bed\u6c14\u7684\u7591\u95ee\u5012\u662f\u6781\u6de1\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u6ca1\u6709\u76f4\u63a5\u95ee\u6280\u672f\u4e0a\u7684\u7ec6\u8282\uff0c\u8fd9\u6bd5\u7adf\u662f\u7231\u8fbe\u7684\u6280\u672f\u673a\u5bc6\uff0c\u4ed6\u53ea\u662f\u4fa7\u8fc7\u5934\u6765\u95ee\u5f20\u606a\uff1a\u201c\u8fd9\u53f0\u6837\u673a\uff0c\u4f60\u4eec\u6295\u5165\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u56db\u5343\u4e07\uff1f\u201d\u5f20\u606a\u4e0d\u786e\u5b9a\u7684\u95ee\u4e01\u69d0\u3002</p><p>\u3000\u3000\u201c\u5dee\u4e00\u4e9b\u3002\u201d\u4e01\u69d0\u56de\u7b54\u9053\u3002</p><p>\u3000\u3000\u201c\u5567\u5567\uff0c\u201d\u9648\u4fe1\u751f\u5482\u4e86\u4e24\u4e0b\u5634\uff0c\u53c8\u95ee\u9053\uff0c\u201c\u7a33\u5b9a\u6027\u80fd\u4fdd\u8bc1\uff1f\u201d</p><p>\u3000\u3000\u201c\u96c6\u6210\u5ea6\u65e0\u6cd5\u5c3d\u5584\u5c3d\u7f8e\uff0c\u63a8\u51fa\u4ea7\u54c1\u4e0d\u6210\u95ee\u9898\u3002\u201d\u4e01\u69d0\u56de\u7b54\u9053\uff0c\u8d28\u91cf\u7a33\u5b9a\u6027\u65e0\u6cd5\u4fdd\u8bc1\uff0c\u4ef7\u503c\u5c31\u8981\u5927\u6253\u6298\u6263\uff0c\u65e9\u5728\u516d\u6708\u521d\u5c31\u62ff\u51fa\u8d85\u7ea7VCD\u7684\u96cf\u5f62\uff0c\u8fd9\u534a\u5e74\u7684\u65f6\u95f4\u4e3b\u8981\u5728\u63d0\u9ad8\u8d28\u91cf\u7a33\u5b9a\u6027\u3001\u63d0\u9ad8\u96c6\u6210\u5ea6\u4e0a\u4e0b\u529f\u592b\uff0c\u5728\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e0a\u5b9e\u73b0\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u7684\u529f\u80fd\uff0c\u8981\u6709\u53ef\u9760\u7684\u7a33\u5b9a\u6027\uff0c\u96be\u5ea6\u53ef\u60f3\u800c\u77e5\u3002\u5f53\u521d\u4e09\u661f\u5c31\u662f\u5728\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u60f3\u5b9e\u73b0\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u7684\u529f\u80fd\uff0c\u65f6\u95f4\u4ed3\u4fc3\u4e86\u4e9b\uff0c\u8d28\u91cf\u4e0d\u591f\u7a33\u5b9a\uff0c\u6700\u7ec8\u4e09\u661f\u8003\u8651\u5230\u8981\u7ef4\u6301\u4e09\u661f\u54c1\u724c\u7684\u7f8e\u8a89\u5ea6\uff0c\u5fcd\u75db\u6682\u65f6\u9000\u51fa\u8fd9\u5757\u5e02\u573a\u3002</p><p>\u3000\u3000\u201c\u80fd\u4e0d\u80fd\u6253\u5f00\u770b\u4e00\u773c\uff1f\u201d\u9648\u4fe1\u751f\u5962\u4f88\u7684\u63d0\u51fa\u8fd9\u4e2a\u8bf7\u6c42\uff0c\u6709\u4e01\u78ca\u5728\uff0c\u5f88\u591a\u6280\u672f\u4e0a\u7684\u95ee\u9898\u770b\u4e00\u773c\u5c31\u80fd\u770b\u4e2a\u5927\u6982\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u76ee\u7684\u5c31\u662f\u8981\u8ba9\u9648\u4fe1\u751f\u5fc3\u75d2\u75d2\uff0c\u8ba9\u4e01\u69d0\u5c06\u6837\u673a\u9762\u677f\u6253\u5f00\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u770b\u7740\u6837\u673a\u91cc\u7684\u7ed3\u6784\uff0c\u63a7\u5236\u82af\u7247\u4e0e\u89e3\u7801\u677f\u4e4b\u95f4\u591a\u4e86\u4e00\u7ec4\u82af\u7247\u7ec4\uff0c\u96c6\u6210\u5ea6\u76f8\u5bf9\u6765\u8bf4\u4ee3\u8868\u8d28\u91cf\u7684\u53ef\u9760\u6027\u4e0e\u7a33\u5b9a\u6027\uff0c\u7231\u8fbe\u65e2\u7136\u5c06\u6240\u6709\u7684\u9644\u52a0\u7ed3\u6784\u90fd\u82af\u7247\u5316\uff0c\u60f3\u5fc5\u8fd9\u4e00\u5757\u5df2\u7ecf\u7422\u78e8\u5f88\u4e45\u4e86\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u9648\u4fe1\u751f\u4e5f\u6709\u4ed6\u7684\u611f\u6168\uff0c\u4ed6\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u6307\u7740\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u5c0f\u5b50\uff0c\u4e00\u4e2a\u52b2\u7684\u56b7\u56b7\u7231\u8fbe\u7535\u5b50\u7684\u7814\u53d1\u4eba\u5458\u4e25\u91cd\u532e\u4e4f\uff0c\u8bf4\u6cf0\u5fd2\u90a3\u8fb9\u8981\u6211\u4eecTI\u591a\u652f\u63f4\uff0c\u6700\u540e\u5012\u597d\uff0c\u6cf0\u5fd2\u90a3\u8fb9\u516d\u4e2a\u4e3b\u4efb\u5de5\u7a0b\u5e08\uff0cTI\u6d3e\u51fa\u4e94\u4e2a\uff0c\u4f60\u4eec\u7231\u8fbe\u53ea\u6d3e\u51fa\u4e00\u4e2a\uff0c\u4f60\u4eec\u641e\u8fd9\u73a9\u827a\u600e\u4e48\u6ca1\u89c9\u5f97\u7814\u53d1\u4eba\u5458\u7f3a\u4e4f\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8146\u7740\u8138\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u5c31\u662f\u4e3a\u4e86\u641e\u8fd9\u4e1c\u897f\uff0c\u624d\u89c9\u5f97\u4eba\u624b\u7f3a\u4e4f\u561b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e13\u5229\u6e05\u5355\u80fd\u7ed9\u6211\u4eec\u770b\u4e00\u773c\uff1f\u201d\u9648\u4fe1\u751f\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8981\u662f\u6025\u7740\u660e\u5929\u56de\u53bb\uff0c\u6211\u8ba9\u4e01\u69d0\u9a6c\u4e0a\u627e\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u9648\u4fe1\u751f\u5c31\u4e0d\u6025\u4e8e\u4e00\u65f6\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e0e\u5b8b\u57f9\u660e\u7eaf\u7cb9\u662f\u5916\u884c\u770b\u70ed\u95f9\uff0c\u4e0d\u8fc7\u4ece\u9648\u4fe1\u751f\u4e0e\u4e01\u78ca\u7684\u53cd\u5e94\u4e0a\u80fd\u770b\u51fa\u4e9b\u7aef\u502a\u6765\uff0c\u5510\u5b66\u8c26\u5173\u5fc3\u7684\u95ee\u9053\uff1a\u201c\u6837\u673a\u7684\u6c34\u51c6\u5f88\u9ad8\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u591a\u9ad8\u8fd8\u8981\u9648\u603b\u88c1\u8bc4\u4ef7\u4e00\u58f0\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u7231\u8fbe\u4ece\u6765\u6ca1\u6709\u5c06\u79d1\u738b\u5f53\u6210\u81ea\u5df1\u7684\u4e3b\u8981\u7ade\u4e89\u5bf9\u624b\u3002\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u665a\u4e00\u5e74\uff0c\u4ef7\u503c\u53ef\u80fd\u5c31\u65e0\u6cd5\u5145\u5206\u4f53\u73b0\u51fa\u6765\u3002\u201d\u9648\u4fe1\u751f\u53ea\u662f\u7b80\u5355\u7684\u8bf4\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u770b\u95ee\u9898\u76f8\u5f53\u7684\u900f\u5f7b\uff0c\u8ba9\u5f20\u606a\u6000\u7591\u4ed6\u7684\u4e3b\u8981\u7cbe\u529b\u90fd\u96c6\u4e2d\u5230\u8fd9\u4e0a\u9762\u6765\u4e86\uff0c\u9884\u6d4b\u660e\u5e74\u4e2d\u90a3\u4e9b\u6d77\u5916\u7535\u5b50\u5de5\u4e1a\u5de8\u5934\u5c31\u80fd\u63a8\u51fa\u6210\u719f\u7684DVD\u789f\u673a\uff0c\u5728\u6d77\u5916\u7535\u5b50\u5de5\u4e1a\u5de8\u5934\u8054\u5408\u63a8\u5e7fDVD\u7684\u5c40\u52bf\u4e0b\uff0c\u8d85\u7ea7VCD\u7684\u751f\u5b58\u7a7a\u95f4\u5c31\u4f1a\u5f88\u7a84\uff0c\u66f4\u9003\u4e0d\u8131\u7ed9DVD\u6dd8\u6c70\u7684\u547d\u8fd0\uff0c\u4e00\u5e74\u7684\u65f6\u95f4\u771f\u7684\u5f88\u5173\u952e\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5510\u5a67\u5728\u95e8\u53e3\u63a2\u5934\u5f80\u91cc\u770b\u4e86\u770b\uff0c\u6709\u5916\u4eba\u5728\uff0c\u5c31\u505a\u51fa\u4e56\u4e56\u5973\u7684\u6837\u5b50\uff0c\u8ddf\u5404\u4eba\u6253\u4e86\u58f0\u62db\u547c\uff0c\u8ddf\u5979\u7238\u8bf4\u9053\uff1a\u201c\u7238\uff0c\u6211\u8ddf\u5988\u5988\u5728\u63a5\u5f85\u5ba4\u7b49\u4f60\u4eec\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u8fd9\u8fb9\u7ed3\u675f\u4e86\uff0c\u53ef\u4ee5\u5403\u996d\u4e86\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u7684\u98df\u5802\u4e4b\u524d\u7531\u5434\u5929\u5b9d\u3001\u5434\u5929\u5b9d\u5c06\u540d\u4e0b\u4ea7\u4e1a\u7f6e\u5165\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u516c\u53f8\u4e4b\u540e\u3001\u5c31\u7531\u4e16\u7eaa\u9910\u996e\u627f\u5305\uff0c\u4e0b\u5348\u4e00\u70b9\u949f\u5230\u5e02\u533a\uff0c\u5f20\u606a\u6709\u63d0\u524d\u901a\u77e5\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u90a3\u8fb9\u7684\u98df\u5802\u7f6e\u529e\u4e00\u684c\u83dc\uff0c\u6c34\u51c6\u76f8\u5f53\u4e0d\u5dee\uff0c\u9648\u4fe1\u751f\u5403\u4e86\u4e4b\u540e\u5c31\u5efa\u8bae\u665a\u4e0a\u8fd8\u5728\u98df\u5802\u91cc\u7528\u9910\uff0c\u7701\u65f6\u7701\u4e8b\uff0c\u4ed6\u5230\u6d77\u5dde\u6765\u5012\u4e0d\u662f\u56fe\u4eab\u53d7\u7684\uff0c\u6709\u65f6\u95f4\u8fd8\u662f\u8981\u591a\u770b\u770b\u7231\u8fbe\u7535\u5b50\u85cf\u7740\u591a\u5c11\u79c1\u8d27\u3002</p><p>\u3000\u3000\u591c\u91cc\u5c31\u5728\u5143\u5668\u4ef6\u5206\u516c\u53f8\u7684\u98df\u5802\u91cc\u7528\u9910\uff0c\u4e5f\u6709\u4e13\u95e8\u7684\u5305\u53a2\uff0c\u6761\u4ef6\u4e0d\u6bd4\u9152\u5e97\u5dee\u3002</p><p>\u3000\u3000\u53bb\u98df\u5802\u7528\u9910\u65f6\uff0c\u5510\u5b66\u8c26\u4e0e\u5f20\u606a\u8d70\u5728\u540e\u9762\uff0c\u5bf9\u4ed6\u8bf4\uff1a\u201c\u7231\u8fbe\u662f\u4e0d\u662f\u4e0d\u7528\u62c5\u5fc3\u6765\u81ea\u79d1\u738b\u7684\u7ade\u4e89\uff1f\u201d</p><p>\u3000\u3000\u201c\u76ee\u524d\u662f\u3002\u201d\u5f20\u606a\u70b9\u5934\u8bf4\uff0c\u201c\u65e0\u8bba\u662f\u6210\u672c\u8fd8\u662f\u6280\u672f\uff0c\u79d1\u738b\u90fd\u5dee\u5f88\u8fdc\uff0c\u6240\u8c13\u7684\u7ade\u4e89\u53ea\u662f\u4ed6\u4eec\u81ea\u5df1\u4e00\u53a2\u60c5\u613f\u7f62\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u5c31\u597d\uff0c\u201d\u5510\u5b66\u8c26\u70b9\u70b9\u5934\uff0c\u201c\u6628\u5929\u5e02\u91cc\u4e13\u95e8\u4e3a\u7231\u8fbe\u4e0e\u79d1\u738b\u7684\u4e8b\u60c5\u5f00\u8fc7\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\u6628\u5929\u7ed9\u8c22\u5251\u5357\u7684\u9634\u5f71\u591f\u6df1\u4e86\uff0c\u4ed6\u501f\u5e02\u91cc\u7684\u52bf\u538b\u7231\u8fbe\u4e5f\u5f88\u6b63\u5e38\uff0c\u8bf4\u9053\uff0c\u201c\u5468\u5bcc\u660e\u662f\u4e0d\u662f\u62c5\u5fc3\u6211\u5bf9\u79d1\u738b\u4e0b\u6740\u624b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u4e00\u70b9\u65e0\u6cd5\u5426\u8ba4\uff0c\u79d1\u738b\u62ff\u4e0b\u592e\u89c6\u6807\u738b\uff0c\u5bf9\u6d77\u5dde\u7684\u7ecf\u6d4e\u662f\u6709\u76f4\u63a5\u4fc3\u8fdb\u7684\u3002\u5e02\u91cc\u5c31\u6b64\u7edf\u4e00\u4e86\u610f\u89c1\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u53ef\u4ee5\u4e0d\u7528\u53bb\u7ba1\u4ed6\uff0c\u6211\u8bf4\u8bf4\u6211\u7684\u7acb\u573a\uff0c\u9996\u5148\u4e0d\u80fd\u4e3a\u4e86\u610f\u6c14\u5c31\u81ea\u76f8\u6b8b\u6740\uff0c\u90a3\u6837\u5bf9\u7231\u8fbe\u6ca1\u6709\u597d\u5904\uff0c\u5bf9\u6d77\u5dde\u7ecf\u6d4e\u5efa\u8bbe\u7834\u574f\u4e5f\u5927\uff0c\u5f71\u54cd\u4e5f\u4e0d\u597d\uff0c\u65e2\u7136\u7231\u8fbe\u4e0d\u5c06\u79d1\u738b\u653e\u5728\u773c\u91cc\uff0c\u90a3\u6211\u4e5f\u5c31\u6ca1\u6709\u592a\u5927\u7684\u62c5\u5fc3\uff0c\u6211\u60f3\u5728\u7231\u8fbe\u5728\u5bf9\u5f85\u79d1\u738b\u7684\u7ade\u4e89\u65f6\uff0c\u662f\u4e0d\u662f\u8003\u8651\u8ba9\u4ed6\u4eec\u4e5f\u7ed9\u6d77\u5dde\u7684\u7ecf\u6d4e\u505a\u8d21\u732e\uff1f\u201d</p><p>\u3000\u3000\u201c\u5510\u4f2f\u4f2f\u4e5f\u62c5\u5fc3\u6211\u4f1a\u5c06\u79d1\u738b\u4e00\u68cd\u5b50\u6253\u6b7b\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u6211\u603b\u662f\u652f\u6301\u4f60\u8fd9\u8fb9\u7684\uff0c\u4e0d\u8fc7\u4e5f\u601d\u91cf\u4e86\u5f88\u4e45\uff0c\u53d1\u73b0\u8981\u505a\u5230\u516c\u6b63\u771f\u662f\u5f88\u4e0d\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u4e5f\u62c5\u5fc3\u6211\u8fd9\u4e48\u8bf4\u4f1a\u8ba9\u4f60\u675f\u624b\u675f\u811a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u7238\u4e5f\u4f1a\u8ba9\u6211\u987e\u5927\u5c40\u7684\uff0c\u201d\u5f20\u606a\u72e1\u9ee0\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u771f\u5b9e\u7684\u60f3\u6cd5\u662f\uff0c\u8981\u662f\u4e00\u68cd\u5b50\u5c06\u79d1\u738b\u6253\u6b7b\uff0c\u53cd\u800c\u4f1a\u8ba9\u8c22\u5bb6\u4e13\u6ce8\u4e8e\u5176\u4ed6\u4ea7\u4e1a\uff0c\u6700\u597d\u7684\u505a\u6cd5\u5c31\u662f\u534a\u6b7b\u4e0d\u6d3b\u540a\u7740\u4ed6\u4eec\uff0c\u8ba9\u4ed6\u4eec\u4e3a\u6d77\u5dde\u7684\u7ecf\u6d4e\u5efa\u8bbe\u505a\u4e00\u4e9b\u8d21\u732e\u3002\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u5012\u662f\u6709\u5c11\u5e74\u4eba\u7684\u72e1\u9ee0\uff0c\u63d0\u9192\u4ed6\u8bf4\uff1a\u201c\u4e0d\u8981\u73a9\u706b\u592a\u8fc7\u4e86\u3002\u201d</p></article>"}}}]);
