(window.webpackJsonp=window.webpackJsonp||[]).push([[44],{66:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u6d3b\u5316\u8679\u5f69\u9f99\u9cde\u76fe",content:"<article><h2>\u7b2c\u4e8c\u8282 \u6d3b\u5316\u8679\u5f69\u9f99\u9cde\u76fe</h2><p>\u8be5\u6b7b\uff0c\u600e\u4e48\u628a\u8fd9\u4e48\u8981\u7d27\u7684\u4e8b\u60c5\u7ed9\u5fd8\u4e86\u3002\u4ed6\u6572\u7740\u81ea\u5df1\u7684\u8111\u888b\uff0c\u540e\u6094\u7740\uff0c\u4f46\u5982\u4eca\u5df2\u7ecf\u8d70\u51fa\u8fd9\u4e48\u8fdc\u4e86\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8fd8\u8fd4\u56de\u9634\u9b42\u57ce\u53bb\uff0c\u518d\u627e\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u8bf4\u81ea\u5df1\u80fd\u529b\u5fae\u8584\uff0c\u4e0d\u80fd\u62c5\u6b64\u91cd\u4efb\uff0c\u8bf7\u4ed6\u53e6\u9009\u9ad8\u660e\u3002\u201c\u7b97\u4e86\u3002\u201d\u743c\u6069\u6447\u6447\u5934\uff0c\u53cd\u6b63\u542c\u8bf4\u901a\u7528\u8bed\u8bed\u6cd5\u89c4\u5219\u6781\u5176\u7b80\u5355\uff0c\u975e\u5e38\u6613\u5b66\uff0c\u7b49\u8d70\u51fa\u8fd9\u7247\u6c99\u6f20\uff0c\u5230\u4e86\u57ce\u9547\u91cc\u518d\u60f3\u529e\u6cd5\u5c31\u662f\uff0c\u5e94\u8be5\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u3002</p><p>\u743c\u6069\u628a\u6212\u6307\u5148\u653e\u8fdb\u957f\u888d\u53e3\u888b\u91cc\uff0c\u53c8\u53d6\u51fa\u80f8\u9488\u6765\u3002\u548c\u6212\u6307\u7684\u7e41\u590d\u4f5c\u98ce\u76f8\u53cd\uff0c\u80f8\u9488\u4e0a\u6ca1\u6709\u4efb\u4f55\u6587\u5b57\u6216\u8005\u82b1\u7eb9\uff0c\u5e73\u6ed1\u4e00\u7247\uff0c\u6837\u5f0f\u4e5f\u975e\u5e38\u666e\u901a\u3002</p><p>\u4e0d\u8fc7\uff0c\u4ec5\u4ec5\u662f\u770b\u8d77\u6765\u666e\u901a\u800c\u5df2\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4e0d\u53ef\u80fd\u95f2\u5f97\u65e0\u804a\uff0c\u6254\u51e0\u4e2a\u6beb\u65e0\u4ef7\u503c\u7684\u4e1c\u897f\u5728\u6b21\u5143\u888b\u91cc\uff0c\u9001\u7ed9\u743c\u6069\u3002\u800c\u4e14\u4f5c\u4e3a\u5df2\u7ecf\u6bd5\u4e1a\u7684\u6b63\u5f0f\u5deb\u5e08\uff0c\u743c\u6069\u4e5f\u81ea\u7136\u80fd\u6e05\u695a\u5730\u611f\u89c9\u5230\u8fd9\u51e0\u4ef6\u7269\u54c1\u4e2d\u6240\u8574\u85cf\u7684\u5f3a\u5927\u529b\u91cf\u3002</p><p>\u5e94\u8be5\u90fd\u662f\u9b54\u6cd5\u7269\u54c1\uff0c\u4ed6\u731c\u6d4b\u7740\uff0c\u81f3\u4e8e\u5177\u4f53\u6709\u4ec0\u4e48\u4f5c\u7528\uff0c\u90a3\u5c31\u4e0d\u662f\u7b80\u5355\u7684\u8089\u773c\u89c2\u5bdf\u6240\u80fd\u77e5\u9053\u7684\u4e86\uff0c\u9700\u8981\u66f4\u8fdb\u4e00\u6b65\u7684\u5206\u6790\u3002</p><p>\u4ed6\u5c06\u6307\u5c16\u6309\u5728\u80f8\u9488\u4e0a\uff0c\u96c6\u4e2d\u7cbe\u795e\uff0c\u611f\u53d7\u5176\u4e2d\u8574\u85cf\u7684\u529b\u91cf\u3002\u7ed3\u679c\u4ee4\u4ed6\u5f88\u9ad8\u5174\uff0c\u8fd9\u662f\u4e00\u679a\u9632\u5fa1\u80f8\u9488\uff0c\u53ea\u8981\u4f69\u5e26\u7740\uff0c\u5c31\u80fd\u5728\u8eab\u4f53\u5468\u56f4\u81ea\u7136\u5f62\u6210\u4e00\u5c42\u65e0\u5f62\u7684\u9632\u62a4\u529b\u573a\uff0c\u62b5\u5fa1\u653b\u51fb\u3002</p><p>\u5f88\u7b80\u5355\uff0c\u4f46\u5f88\u5b9e\u7528\uff0c\u5bf9\u4e8e\u8106\u5f31\u7684\u5deb\u5e08\u800c\u8a00\u5c24\u5176\u5982\u6b64\u3002</p><p>\u743c\u6069\u4f69\u4e0a\u80f8\u9488\uff0c\u7136\u540e\u4ece\u53e3\u888b\u91cc\u638f\u51fa\u6212\u6307\uff0c\u518d\u6b21\u95ed\u4e0a\u773c\u775b\uff0c\u51dd\u805a\u5fc3\u795e\u53bb\u63a2\u6d4b\u5176\u4e2d\u7684\u529b\u91cf\uff0c\u4f46\u8fd9\u6b21\u4ed6\u5931\u8d25\u4e86\u3002\u6212\u6307\u4e0a\u4eff\u4f5b\u6709\u4e00\u5c42\u65e0\u5f62\u7684\u8584\u819c\uff0c\u5c06\u4ed6\u7684\u610f\u8bc6\u963b\u9694\u5728\u5916\u3002\u743c\u6069\u63a5\u8fde\u5c1d\u8bd5\u4e86\u51e0\u6b21\uff0c\u6700\u7ec8\u53ea\u80fd\u653e\u5f03\u4e86\uff0c\u51fa\u73b0\u8fd9\u79cd\u60c5\u51b5\uff0c\u4e00\u822c\u6765\u8bf4\u662f\u56e0\u4e3a\u6212\u6307\u4e2d\u6240\u8574\u85cf\u7684\u529b\u91cf\u8fc7\u4e8e\u5f3a\u5927\uff0c\u975e\u81ea\u5df1\u76ee\u524d\u6240\u80fd\u63a5\u89e6\u9a7e\u9a6d\u7684\u7f18\u6545\u3002</p><p>\u4ed6\u76b1\u7740\u7709\u5934\uff0c\u4e0d\u660e\u767d\u5e03\u96f7\u7eb3\u65af\u7ed9\u81ea\u5df1\u8fd9\u679a\u7528\u4e0d\u4e86\u7684\u6212\u6307\u662f\u4ec0\u4e48\u7528\u610f\u3002\u5bfb\u601d\u4e86\u534a\u5929\uff0c\u4e0d\u5f97\u5176\u89e3\uff0c\u4ed6\u5c06\u6212\u6307\u5148\u6234\u5728\u5de6\u624b\u98df\u6307\u4e0a\uff0c\u518d\u53bb\u770b\u6700\u540e\u4e00\u4ef6\uff0c\u4e5f\u662f\u4ed6\u6700\u89c9\u5f97\u83ab\u540d\u5176\u5999\u7684\u4e00\u4ef6\u4e1c\u897f\u3002</p><p>\u90a3\u9762\u5c0f\u76fe\u724c\u3002</p><p>\u76fe\u724c\u7684\u8868\u9762\u975e\u5e38\u51b0\u51c9\uff0c\u6307\u5c16\u89e6\u6478\u4e0a\u53bb\uff0c\u6709\u79cd\u6ed1\u817b\u7684\u611f\u89c9\u3002\u6e05\u51b7\u7684\u6708\u5149\u67d4\u67d4\u6d41\u6dcc\uff0c\u6d12\u5728\u76fe\u724c\u4e0a\uff0c\u5fae\u5fae\u7684\u8679\u5f69\u5149\u8292\u95ea\u70c1\u7740\uff0c\u53d8\u5e7b\u51fa\u4e0d\u540c\u7684\u56fe\u6848\u3002</p><p>\u743c\u6069\u63d0\u8d77\u76fe\u724c\uff0c\u5f88\u8f7b\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u5206\u91cf\u3002\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u8fd9\u662f\u4e2a\u597d\u6d88\u606f\uff0c\u56e0\u4e3a\u5982\u679c\u771f\u8981\u4f7f\u7528\u76fe\u724c\u7684\u8bdd\uff0c\u81ea\u7136\u662f\u8d8a\u8f7b\u8d8a\u597d\u2014\u2014\u5f53\u7136\uff0c\u6700\u597d\u662f\u4e0d\u7528\u3002</p><p>\u4ed6\u5c06\u6574\u4e2a\u5de6\u624b\u638c\u5fc3\u6309\u5728\u76fe\u724c\u9762\u4e0a\uff0c\u96c6\u4e2d\u7cbe\u795e\uff0c\u610f\u8bc6\u7f13\u7f13\u6c89\u5165\uff0c\u7136\u540e\u4ed6\u8111\u6d77\u4e2d\u7a81\u7136\u54cd\u8d77\u4e00\u4e2a\u58f0\u97f3\u3002</p><p>\u90a3\u662f\u4e00\u4e2a\u975e\u5e38\u51b0\u51b7\u3001\u575a\u786c\uff0c\u4e0d\u5e26\u534a\u70b9\u60c5\u611f\u7684\u58f0\u97f3\u3002</p><p>\u201c\u53e3\u4ee4\uff01\u201d</p><p>\u53e3\u4ee4\uff1f\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u8fd9\u53c8\u4e0d\u662f\u5728\u7535\u8111\u4e0a\u6253\u5f00\u4ec0\u4e48\u52a0\u5bc6\u6587\u6863\uff0c\u6216\u8005\u8fdb\u5165\u4ec0\u4e48\u5bc6\u95e8\uff0c\u9700\u8981\u4ec0\u4e48\u53e3\u4ee4\uff1f</p><p>\u4ed6\u7741\u5f00\u773c\u775b\uff0c\u5de6\u53f3\u770b\u7740\uff0c\u786e\u8ba4\u5468\u56f4\u6ca1\u6709\u5176\u4ed6\u4eba\uff0c\u6700\u540e\u786e\u8ba4\u58f0\u97f3\u6765\u81ea\u9762\u524d\u7684\u5c0f\u76fe\u724c\u3002\u4e8e\u662f\u4ed6\u518d\u6b21\u95ed\u4e0a\u773c\uff0c\u5c1d\u8bd5\u7740\uff0c\u7528\u610f\u8bc6\u53bb\u89e6\u6478\u5b83\u3002</p><p>\u518d\u4e00\u6b21\u7684\uff0c\u51b0\u51b7\u575a\u786c\u7684\u58f0\u97f3\u54cd\u8d77\u3002\u201c\u53e3\u4ee4\uff01\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u7740\uff0c\u4ed6\u4e0d\u6562\u8d38\u7136\u56de\u7b54\uff0c\u6015\u4e00\u4e2a\u4e0d\u751a\u51fa\u4ec0\u4e48\u5dee\u9519\u3002\u4f46\u90a3\u4e2a\u58f0\u97f3\u65e0\u89c6\u4ed6\u7684\u8e0c\u8e87\uff0c\u201c\u53e3\u4ee4\uff01\u201d\u5b83\u50ac\u4fc3\u7740\u3002</p><p>\u4e00\u4e2a\u5ff5\u5934\u4ece\u743c\u6069\u8111\u4e2d\u6d6e\u8d77\uff0c\u6216\u8bb8\u2026\u2026</p><p>\u201c\u73ca\u5609\u3002\u201d\u4ed6\u4f4e\u58f0\u4f46\u6e05\u695a\u5730\u8bf4\u3002</p><p>\u8bdd\u4e00\u51fa\u53e3\uff0c\u968f\u5373\u611f\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u743c\u6069\u7741\u5f00\u773c\uff0c\u89c1\u5c0f\u5c0f\u7684\u4e09\u89d2\u5f62\u76fe\u724c\u5df2\u7ecf\u81ea\u884c\u6d6e\u7a7a\u817e\u8d77\uff0c\u5728\u7a7a\u4e2d\u7f13\u7f13\u8f6c\u52a8\uff0c\u6cdb\u51fa\u8679\u5149\u56db\u5c04\uff0c\u7167\u5f97\u6574\u4e2a\u5e10\u7bf7\u91cc\u5149\u602a\u9646\u79bb\uff0c\u4e03\u5f69\u53d8\u5e7b\u3002</p><p>\u5f3a\u637a\u4f4f\u5fc3\u5934\u7684\u6fc0\u52a8\uff0c\u743c\u6069\u5df2\u7ecf\u9690\u7ea6\u77e5\u9053\u8fd9\u9762\u76fe\u724c\u7684\u771f\u6b63\u4ef7\u503c\u4e86\u3002\u4ed6\u5728\u5fc3\u91cc\u4e0b\u4e86\u4e00\u4e2a\u547d\u4ee4\uff0c\u6216\u8005\u8bf4\u4ec5\u4ec5\u8d77\u4e86\u4e2a\u5ff5\u5934\uff0c\u4f46\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u76fe\u724c\u5df2\u7ecf\u611f\u5e94\u5230\u4e86\uff0c\u5b83\u5feb\u901f\u5730\u5e73\u79fb\uff0c\u6321\u5728\u743c\u6069\u5de6\u4fa7\uff0c\u5c31\u4eff\u4f5b\u6709\u4eba\u5728\u624b\u6301\u7740\u5b83\u79fb\u52a8\u4f3c\u7684\u3002</p><p>\u8fd9\u662f\u4e2a\u6d3b\u5316\u76fe\u3002</p><p>\u5deb\u5e08\u65bd\u6cd5\u65f6\u4e0d\u65b9\u4fbf\u6301\u76fe\uff0c\u4f46\u4e0d\u6301\u76fe\u5f88\u591a\u65f6\u5019\u53c8\u8fc7\u4e8e\u8106\u5f31\uff0c\u6d3b\u5316\u76fe\u6b63\u662f\u4e3a\u5f25\u8865\u8fd9\u4e00\u7f3a\u9677\u800c\u5236\u3002\u53ea\u6d88\u4e3b\u4eba\u4e0b\u4e00\u4e2a\u53e3\u4ee4\uff0c\u5b83\u5c31\u53ef\u4ee5\u81ea\u884c\u6d6e\u7a7a\uff0c\u5b9b\u5982\u6709\u4eba\u4f7f\u7528\u4e00\u6837\uff0c\u76f8\u5f53\u4e8e\u5deb\u5e08\u591a\u957f\u51fa\u4e00\u53ea\u65e0\u5f62\u7684\u624b\uff0c\u5176\u4ef7\u503c\u53ef\u60f3\u800c\u77e5\u3002</p><p>\u4e16\u95f4\u9b54\u6cd5\u7269\u54c1\u6210\u5343\u4e0a\u4e07\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7edf\u4e00\u7684\u4ef7\u503c\u8861\u91cf\u6807\u51c6\uff0c\u4f46\u4e00\u822c\u6765\u8bf4\uff0c\u80fd\u81ea\u884c\u62e5\u6709\u667a\u80fd\u7684\u9b54\u6cd5\u7269\u54c1\u6700\u4e3a\u96be\u5f97\uff0c\u53ef\u8c13\u662f\u4e07\u4e2d\u65e0\u4e00\uff0c\u53ef\u9047\u4e0d\u53ef\u6c42\uff1b\u50cf\u6d3b\u5316\u76fe\u8fd9\u79cd\uff0c\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u201c\u534a\u667a\u80fd\u201d\u9b54\u6cd5\u7269\u54c1\uff0c\u80fd\u5728\u5deb\u5e08\u7684\u6307\u4ee4\u4e0b\u884c\u52a8\uff0c\u4e5f\u662f\u6781\u5176\u73cd\u8d35\u96be\u5f97\u4e86\u3002</p><p>\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u5deb\u5e08\uff0c\u68a6\u5bd0\u4ee5\u6c42\u80fd\u5f04\u5230\u4e00\u9762\u6d3b\u5316\u76fe\u800c\u4e0d\u53ef\u5f97\u5462\u3002\u5c06\u4e00\u4ef6\u65e0\u751f\u547d\u7684\u76fe\u724c\u53d8\u6210\u6d3b\u5316\u7269\u4f53\uff0c\u8fd9\u6b63\u5c5e\u4e8e\u53d8\u5316\u5b66\u6d3e\u7684\u8303\u7574\uff0c\u6240\u4ee5\u743c\u6069\u975e\u5e38\u6e05\u695a\u5176\u96be\u5ea6\u3002\u66f4\u4f55\u51b5\u8fd9\u8fd8\u662f\u8679\u5f69\u9f99\u9cde\u505a\u7684\uff0c\u8d8a\u9ad8\u9636\u7684\u6750\u6599\uff0c\u9644\u4e0a\u9b54\u6cd5\u5c31\u8d8a\u96be\uff0c\u628a\u4e00\u9762\u666e\u901a\u7684\u6728\u76fe\u6d3b\u5316\uff0c\u548c\u628a\u9f99\u9cde\u76fe\u6d3b\u5316\uff0c\u96be\u5ea6\u76f8\u5dee\u5b8c\u5168\u4e0d\u53ef\u4ee5\u9053\u91cc\u8ba1\u3002</p><p>\u6700\u540e\uff0c\u4ed6\u5c55\u5f00\u90a3\u51e0\u5f20\u5377\u8f74\u3002</p><p>\u5deb\u5e08\u53ef\u4ee5\u5c06\u9b54\u6cd5\u50a8\u5b58\u5230\u5377\u8f74\u4e2d\uff0c\u5f25\u8865\u81ea\u8eab\u51c6\u5907\u6cd5\u672f\u6709\u9650\u7684\u7f3a\u9677\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u7528\u5377\u8f74\u6765\u65bd\u5c55\u4e00\u4e9b\u6bd4\u8f83\u9ad8\u7ea7\uff0c\u81ea\u5df1\u8fd8\u4e0d\u80fd\u76f4\u63a5\u65bd\u5c55\u7684\u6cd5\u672f\u3002\u5f88\u591a\u8001\u5deb\u5e08\u5f80\u5f80\u4f1a\u5236\u4f5c\u4e00\u4e9b\u9b54\u6cd5\u5377\u8f74\u8d60\u9001\u7ed9\u81ea\u5df1\u7684\u5b66\u751f\uff0c\u8ba9\u4ed6\u4eec\u5728\u5916\u51fa\u65f6\u9632\u8eab\u4fdd\u547d\u3002</p><p>\u743c\u6069\u624b\u91cc\u8fd9\u51e0\u5f20\u5377\u8f74\uff0c\u5176\u4e2d\u8574\u85cf\u7684\u9b54\u6cd5\u5305\u62ec\u4e86\u4efb\u610f\u95e8\u3001\u77f3\u80a4\u672f\u3001\u53d8\u5f62\u672f\u3001\u7d22\u5c14\u77f3\u884c\u672f\u3001\u5a9a\u60d1\u602a\u7269\u548c\u79d8\u6cd5\u773c\u7b49\uff0c\u90fd\u662f\u6bd4\u743c\u6069\u76ee\u524d\u7684\u65bd\u6cd5\u80fd\u529b\u7a0d\u9ad8\u4e00\u4e9b\uff0c\u4f46\u53c8\u4e0d\u4f1a\u5dee\u8ddd\u592a\u8fc7\u79bb\u8c31\u7684\u9b54\u6cd5\uff0c\u8fd9\u6709\u5229\u4e8e\u4fdd\u8bc1\u65bd\u6cd5\u8005\u7684\u5b89\u5168\u3002\u56e0\u4e3a\u5deb\u5e08\u4f7f\u7528\u5377\u8f74\u82e5\u662f\u8d85\u8fc7\u81ea\u5df1\u7684\u80fd\u529b\u592a\u591a\uff0c\u5219\u975e\u5e38\u5bb9\u6613\u906d\u5230\u6cd5\u672f\u53cd\u566c\u3002</p><p>\u6b21\u5143\u888b\u3001\u9632\u5fa1\u80f8\u9488\u3001\u6d3b\u5316\u9f99\u9cde\u76fe\uff0c\u8fd8\u6709\u4e00\u679a\u4e0d\u77e5\u9053\u6709\u4ec0\u4e48\u4f5c\u7528\u7684\u6212\u6307\uff0c\u4ee5\u53ca\u51e0\u74f6\u9b54\u6cd5\u836f\u6c34\u2026\u2026\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u4ed6\u8fd9\u4e48\u591a\u4e1c\u897f\uff0c\u6050\u6015\uff0c\u8fd9\u6b21\u4efb\u52a1\u4e0d\u662f\u60f3\u50cf\u5f97\u90a3\u4e48\u7b80\u5355\u5462\u2026\u2026</p></article>"}}}]);
