(window.webpackJsonp=window.webpackJsonp||[]).push([[441],{463:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e00\u8282 \u7cbe\u7075\u7684\u8fd4\u4e61",content:"<article><h2>\u7b2c\u4e09\u5341\u4e00\u8282 \u7cbe\u7075\u7684\u8fd4\u4e61</h2><p>\u8ff7\u65af\u5353\u8bfa\u88ab\u6c38\u805a\u5c9b\u7684\u7cbe\u7075\u653b\u5360\u4e86\uff1f</p><p>\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u505c\u4f4f\u811a\u6b65\u3002\u8ff7\u65af\u5353\u8bfa\u548c\u4ed6\u5012\u6ca1\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f46\u5374\u662f\u6885\u83f2\u65af\u7684\u5c45\u6240\u3002\u867d\u8bf4\u4ece\u65f6\u95f4\u4e0a\u6765\u5224\u65ad\uff0c\u5723\u6b66\u58eb\u73b0\u5728\u5e94\u8be5\u8fd8\u5728\u963f\u683c\u62c9\u9686\uff0c\u9876\u591a\u662f\u5728\u56de\u7a0b\u8def\u4e0a\uff0c\u4e0d\u81f3\u4e8e\u5377\u5165\u6218\u4e71\u3002\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u4e0d\u6015\u4e00\u4e07\u5c31\u6015\u4e07\u4e00\uff0c\u6bd5\u7adf\u8fd9\u4e2a\u4e16\u754c\u4e0a\u53ef\u662f\u6709\u4f20\u9001\u9b54\u6cd5\u5b58\u5728\u7684\uff0c\u5982\u679c\u8bf4\u6b23\u5e03\u5973\u738b\u4e00\u9ad8\u5174\uff0c\u76f4\u63a5\u5f00\u4f20\u9001\u95e8\u628a\u6885\u83f2\u65af\u9001\u56de\u4e86\u8ff7\u65af\u5353\u8bfa\uff0c\u90a3\u4e5f\u4e0d\u662f\u4e0d\u53ef\u80fd\u3002\u504f\u504f\u8fd9\u6bb5\u65f6\u95f4\u4ed6\u53c8\u4e00\u76f4\u90fd\u8054\u7cfb\u4e0d\u4e0a\u6885\u83f2\u65af\uff0c\u6b63\u81ea\u5fc3\u4e2d\u9690\u9690\u4e0d\u5b89\u5462\u3002</p><p>\u8fc7\u53bb\u8be2\u95ee\u51e0\u53e5\uff0c\u5374\u4e5f\u4e0d\u5f97\u8981\u9886\uff0c\u5728\u8def\u8fb9\u8c08\u8bdd\u7684\u8fd9\u4e24\u4e2a\u53ea\u662f\u666e\u901a\u5e02\u6c11\uff0c\u521a\u521a\u9053\u542c\u9014\u8bf4\u5230\u4e00\u70b9\u6d88\u606f\uff0c\u95f2\u7740\u65e0\u804a\u4f5c\u4e3a\u8c08\u8d44\u6253\u53d1\u65f6\u95f4\u7f62\u4e86\u3002\u4e0d\u8fc7\u5230\u6700\u540e\uff0c\u4ed6\u4eec\u603b\u7b97\u8fd8\u662f\u63d0\u4f9b\u4e86\u4e00\u4e2a\u6709\u4ef7\u503c\u7684\u4fe1\u606f\uff1a\u201c\u4f60\u8981\u60f3\u77e5\u9053\u8be6\u60c5\uff0c\u53ef\u4ee5\u53bb\u661f\u67ab\u8857\u7b2c\u516b\u5341\u516b\u53f7\u3002\u201d</p><p>\u201c\u661f\u67ab\u8857\u7b2c\u516b\u5341\u516b\u53f7\uff1f\u90a3\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d</p><p>\u201c\u60c5\u62a5\u4ea4\u6613\u6240\uff0c\u201d\u5e02\u6c11\u8bf4\uff0c\u201c\u2018\u7ea2\u7fbd\u2019\u5f00\u7684\uff0c\u5728\u90a3\u91cc\u4f60\u53ea\u8981\u4ea4\u5f97\u8d77\u94b1\uff0c\u5c31\u80fd\u83b7\u5f97\u4e00\u5207\u60f3\u8981\u7684\u4fe1\u606f\u3002\u201d</p><p>\u201c\u7ea2\u7fbd\u201d\u662f\u8fdc\u5c71\u5927\u5546\u4f1a\u7684\u540d\u79f0\uff0c\u5728\u8fd9\u5ea7\u5546\u4e1a\u57ce\u5e02\uff0c\u5b83\u5176\u5b9e\u662f\u7ad9\u5728\u57ce\u4e3b\u80cc\u540e\u771f\u6b63\u7684\u7edf\u6cbb\u8005\u3002\u6240\u8c13\u201c\u4e00\u5207\u60f3\u8981\u7684\u4fe1\u606f\u201d\u81ea\u7136\u662f\u5938\u5f20\uff0c\u4f46\u743c\u6069\u4e5f\u4e0d\u5728\u610f\uff0c\u95ee\u660e\u4e86\u8def\u7ebf\uff0c\u53d1\u73b0\u8ddd\u79bb\u6b64\u5904\u4e5f\u4e0d\u8fdc\u3002\u201c\u4f60\u4eec\u5148\u53bb\u9910\u9986\u5427\uff0c\u201d\u4ed6\u5bf9\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u8fc7\u53bb\u770b\u770b\uff0c\u5f88\u5feb\u56de\u6765\u3002\u201d</p><p>\u73ca\u5609\u6447\u6447\u5934\uff0c\u201c\u4e00\u8d77\u53bb\u5427\u3002\u201d</p><p>\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u4e5f\u65e0\u5f02\u8bae\uff0c\u4e8e\u662f\u4e00\u8d77\u524d\u5f80\u90a3\u5ea7\u60c5\u62a5\u4ea4\u6613\u6240\u3002\u8f6c\u8fc7\u4e24\u6761\u8857\u9053\uff0c\u743c\u6069\u4fbf\u770b\u89c1\u4e86\u5706\u62f1\u578b\u5927\u95e8\u548c\u91d1\u8272\u5927\u62db\u724c\uff0c\u4ee5\u53ca\u62db\u724c\u4e0a\u65b9\u63d2\u7740\u7684\u4e00\u652f\u5de8\u5927\u7684\u7eef\u7ea2\u8272\u7fbd\u6bdb\u3002\u95e8\u4e0b\u4eba\u6765\u4eba\u5f80\uff0c\u7edc\u7ece\u4e0d\u7edd\uff0c\u663e\u7136\u751f\u610f\u5174\u9686\u3002\u743c\u6069\u8d70\u8fdb\u53bb\uff0c\u5148\u7814\u7a76\u4e86\u4e00\u4f1a\u8d34\u5728\u5899\u58c1\u4e0a\u7684\u201c\u4ea4\u6613\u6307\u5357\u201d\uff0c\u7136\u540e\u62db\u8fc7\u4f8d\u8005\uff0c\u9012\u8fc7\u4e00\u4e2a\u5c0f\u53e3\u888b\uff0c\u91cc\u9762\u88c5\u7740\u516d\u9897\u94bb\u77f3\uff0c\u662f\u79bb\u5f00\u5e7d\u6697\u5730\u57df\u65f6\u4f0a\u5361\u6c99\u57ce\u7684\u77ee\u4eba\u4eec\u6240\u8d60\uff0c\u8db3\u591f\u5728\u8fd9\u5ea7\u57ce\u5e02\u91cc\u4e70\u4e0b\u4e00\u5ea7\u4e2d\u7b49\u89c4\u6a21\u7684\u5b85\u9662\u4e86\u3002\u4f8d\u8005\u63a5\u8fc7\u4e00\u770b\uff0c\u8138\u8272\u987f\u65f6\u53d8\u5f97\u606d\u656c\u8d77\u6765\uff0c\u9886\u7740\u743c\u6069\u7a7f\u8fc7\u957f\u5eca\uff0c\u8d70\u8fdb\u4e00\u95f4\u5305\u53a2\uff0c\u7136\u540e\u81ea\u5df1\u8eac\u8eab\u9000\u51fa\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u4fbf\u6709\u4e00\u4f4d\u6234\u7740\u84dd\u6c34\u6676\u773c\u955c\u7a7f\u7740\u7c73\u767d\u8272\u5957\u88d9\u7684\u91d1\u53d1\u7f8e\u4eba\u6572\u95e8\u8fdb\u6765\uff0c\u6bb7\u52e4\u5730\u95ee\u743c\u6069\u7b49\u4eba\u6709\u4ec0\u4e48\u9700\u8981\u4e86\u89e3\u7684\u3002</p><p>\u201c\u6211\u60f3\u77e5\u9053\u8ff7\u65af\u5353\u8bfa\u7684\u60c5\u51b5\uff0c\u201d\u743c\u6069\u6709\u4e9b\u5fc3\u6025\uff0c\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\uff0c\u201c\u542c\u8bf4\u6c38\u805a\u5c9b\u7684\u7cbe\u7075\u653b\u5360\u4e86\u90a3\u91cc\uff1f\u201d</p><p>\u201c\u786e\u5b9e\u5982\u6b64\uff0c\u201d\u91d1\u53d1\u7f8e\u4eba\u5fae\u7b11\u7740\u56de\u7b54\uff0c\u201c\u4e0d\u8fc7\u8bf4\u653b\u5360\u5e76\u4e0d\u662f\u5f88\u8d34\u5207\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u63a5\u7ba1\u3002\u201d</p><p>\u201c\u63a5\u7ba1\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u53ea\u662f\u63a5\u7ba1\uff0c\u5e76\u6ca1\u6709\u53d1\u751f\u5927\u89c4\u6a21\u7684\u6218\u4e89\u548c\u6d41\u8840\u51b2\u7a81\u3002\u201d</p><p>\u91d1\u53d1\u7f8e\u4eba\u663e\u7136\u770b\u51fa\u743c\u6069\u7684\u62c5\u5fe7\uff0c\u731c\u6d4b\u4ed6\u662f\u6709\u670b\u53cb\u5728\u8ff7\u65af\u5353\u8bfa\uff0c\u6240\u4ee5\u7279\u610f\u5f3a\u8c03\u53ea\u662f\u201c\u63a5\u7ba1\u201d\u5e76\u975e\u201c\u653b\u5360\u201d\uff0c\u4e8b\u5b9e\u4e0a\u5979\u4e5f\u731c\u5f97\u516b\u4e5d\u4e0d\u79bb\u5341\u3002\u743c\u6069\u95fb\u8a00\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u6ca1\u6709\u53d1\u751f\u6218\u4e89\u5417\uff0c\u90a3\u5c31\u597d\u2026\u2026\u5c0f\u59d0\uff0c\u53ef\u5426\u4e3a\u6211\u4eec\u8bb2\u8ff0\u4e00\u4e0b\u6574\u4e2a\u4e8b\u60c5\u7684\u7ecf\u8fc7\u5462\uff1f\u201d</p><p>\u201c\u4e50\u610f\u6548\u52b3\uff0c\u201d\u5979\u6e29\u67d4\u5fae\u7b11\uff0c\u201c\u4e0d\u8fc7\u8981\u8bf4\u8fd9\u4ef6\u4e8b\u2026\u2026\u6050\u6015\u8981\u4ece\u5f88\u4e45\u4ee5\u524d\u8bf4\u8d77\u624d\u884c\u3002\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\u3002\u201d</p><p>\u91d1\u53d1\u7f8e\u4eba\u70b9\u70b9\u5934\uff0c\u5f00\u59cb\u8bb2\u8ff0\u6765\u9f99\u53bb\u8109\u3002\u6574\u4e2a\u4e8b\u4ef6\u7684\u7f18\u7531\uff0c\u786e\u5b9e\u8981\u4e0a\u6eaf\u5230\u516d\u767e\u591a\u5e74\u524d\uff0c\u5f53\u65f6\u7cbe\u7075\u5728\u5927\u9646\u7684\u52bf\u529b\u5df2\u7ecf\u5168\u9762\u8870\u9000\uff0c\u4eba\u7c7b\u56fd\u5bb6\u5219\u5174\u76db\u84ec\u52c3\uff0c\u5448\u73b0\u51fa\u538b\u5012\u6027\u4f18\u52bf\u3002\u4f5c\u4e3a\u7cbe\u7075\u540d\u57ce\u7684\u8ff7\u65af\u5353\u8bfa\uff0c\u4fbf\u662f\u5728\u6b64\u65f6\u88ab\u6765\u81ea\u4e0b\u5c42\u754c\u7684\u90aa\u9b54\u6240\u6467\u6bc1\uff0c\u6574\u4e2a\u79d1\u66fc\u7d22\u68ee\u6797\u7684\u7cbe\u7075\u4eec\u906d\u53d7\u91cd\u521b\u3002\u540e\u6765\u7cbe\u7075\u738b\u5ef7\u53d1\u51fa\u4e86\u201c\u5927\u64a4\u9000\u201d\u7684\u547d\u4ee4\uff0c\u9664\u4e86\u6781\u5c11\u6570\u7cbe\u7075\u6297\u547d\u4e0d\u9075\u4e4b\u5916\uff0c\u7edd\u5927\u90e8\u5206\u90fd\u4f9d\u4ee4\u884c\u4e8b\uff0c\u653e\u5f03\u5728\u5927\u9646\u7684\u5c45\u6240\uff0c\u64a4\u9000\u5230\u6d77\u5916\u7684\u6c38\u805a\u5c9b\u4e0a\u3002\u8ff7\u65af\u5353\u8bfa\u7684\u7cbe\u7075\u4eec\u5728\u64a4\u9000\u4e4b\u524d\uff0c\u5c06\u8fd9\u5ea7\u57ce\u5e02\u7684\u63a7\u5236\u6743\uff08\u51c6\u786e\u5730\u8bf4\uff0c\u53ea\u662f\u90e8\u5206\u63a7\u5236\u6743\uff0c\u6216\u8005\u8bf4\u6cd5\u7406\u4e0a\u7684\u5168\u90e8\u63a7\u5236\u6743\uff0c\u56e0\u4e3a\u8ff7\u65af\u5353\u8bfa\u7684\u5f88\u591a\u533a\u57df\u5df2\u7ecf\u88ab\u5404\u79cd\u602a\u7269\u3001\u90aa\u9b54\u5b9e\u9645\u5360\u636e\u4e86\uff0c\u771f\u6b63\u8fd8\u5904\u4e8e\u7cbe\u7075\u63a7\u5236\u4e4b\u4e0b\u7684\u4e0d\u8db3\u4e09\u5206\u4e4b\u4e00\uff09\u79fb\u4ea4\u7ed9\u4e86\u4e00\u5411\u5173\u7cfb\u826f\u597d\u7684\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\uff0c\u5176\u4e2d\u5c31\u5305\u62ec\u201c\u8ff7\u9501\u201d\u3002\u5f53\u7136\uff0c\u539f\u672c\u7684\u8ff7\u9501\u6838\u5fc3\u662f\u7cbe\u7075\u65cf\u795e\u5668\uff0c\u88ab\u5e26\u8d70\u4e86\uff0c\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u4e8e\u662f\u7528\u5723\u7269\u9ece\u660e\u4e4b\u77f3\u4ee3\u66ff\uff0c\u4f5c\u4e3a\u65b0\u7684\u6838\u5fc3\u8fd0\u8f6c\u8ff7\u9501\u3002</p><p>\u201c\u540e\u6765\u4e0d\u77e5\u4f55\u6545\uff0c\u9ece\u660e\u4e4b\u77f3\u610f\u5916\u5931\u8e2a\u4e86\u3002\u201d\u91d1\u53d1\u7f8e\u4eba\u8bf4\u3002</p><p>\u743c\u6069\u55ef\u4e86\u4e00\u58f0\uff0c\u201c\u8fd9\u4e2a\u6211\u4e5f\u542c\u8bf4\u8fc7\uff0c\u8bf7\u7ee7\u7eed\u3002\u201d</p><p>\u9ece\u660e\u4e4b\u77f3\u5931\u8e2a\uff0c\u8ff7\u9501\u81ea\u7136\u6025\u5267\u8870\u5f31\uff0c\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u53ea\u5f97\u53c8\u53e6\u627e\u6838\u5fc3\uff0c\u597d\u5728\u4ed6\u4eec\u624b\u91cc\u8fd8\u771f\u6709\u51e0\u4ef6\u795e\u5668\uff0c\u8fc7\u4e86\u6bb5\u65f6\u95f4\u53c8\u5c06\u8ff7\u9501\u91cd\u5851\u8d77\u6765\u3002\u7136\u800c\u5728\u8fd9\u6bb5\u7a7a\u9699\u91cc\uff0c\u90a3\u4e9b\u90aa\u9b54\u3001\u602a\u7269\u8d8a\u53d1\u7316\u7357\uff0c\u4fb5\u5360\u4e86\u66f4\u591a\u7684\u533a\u57df\u3002\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u7684\u7267\u5e08\u4eec\u6b63\u7126\u5934\u70c2\u989d\uff0c\u5e94\u4ed8\u4e0d\u6687\u4e4b\u9645\uff0c\u7a81\u7136\u6c38\u805a\u5c9b\u7684\u7cbe\u7075\u4eec\u53c8\u6d3e\u4f7f\u8005\u524d\u6765\uff0c\u8bf4\u662f\u8981\u91cd\u8fd4\u8ff7\u65af\u5353\u8bfa\uff0c\u6536\u56de\u63a7\u5236\u6743\uff0c\u8bf7\u4ed6\u4eec\u8d70\u4eba\u3002</p><p>\u4ece\u9053\u7406\u4e0a\u8bf4\uff0c\u8ff7\u65af\u5353\u8bfa\u4ee5\u524d\u662f\u7cbe\u7075\u4eec\u7684\u5730\u76d8\uff0c\u53ea\u662f\u5728\u5927\u64a4\u9000\u524d\u4ea4\u7ed9\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u6682\u65f6\u7ba1\u7406\uff0c\u5982\u4eca\u65e2\u7136\u6b63\u4e3b\u4eec\u53c8\u56de\u6765\u4e86\uff0c\u7ba1\u7406\u4eba\u81ea\u7136\u5e94\u8be5\u62f1\u624b\u8ba9\u4f4d\u3002\u95ee\u9898\u662f\u7cbe\u7075\u4eec\u8fd9\u4e00\u8d70\uff0c\u65f6\u95f4\u5b9e\u5728\u592a\u957f\uff0c\u504f\u504f\u53cc\u65b9\u7684\u79cd\u65cf\u5dee\u5f02\u53c8\u5bfc\u81f4\u4ed6\u4eec\u7684\u65f6\u95f4\u6982\u5ff5\u5b8c\u5168\u4e0d\u540c\uff0c\u4e8e\u662f\u4fbf\u6709\u4e86\u4e89\u6267\u3002</p><p>\u5bf9\u4e8e\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u6765\u8bf4\uff0c\u4ed6\u4eec\u662f\u516d\u767e\u591a\u5e74\u524d\u8fdb\u9a7b\u8ff7\u65af\u5353\u8bfa\uff0c\u5efa\u7acb\u795e\u6bbf\uff0c\u7956\u7956\u8f88\u8f88\u751f\u6d3b\u4e8e\u6b64\uff0c\u5df2\u7ecf\u624e\u4e0b\u6839\u6765\u3002\u6309\u7167\u8d39\u4f26\u5927\u9646\u7684\u901a\u884c\u5386\u6cd5\uff0c\u4eba\u7c7b\u4ee5\u4e8c\u5341\u4e94\u5e74\u4e3a\u4e00\u4ee3\uff0c\u4e5f\u5c31\u662f\u8bf4\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u5df2\u7ecf\u5728\u8ff7\u65af\u5353\u8bfa\u9a7b\u5b88\u4e86\u4e8c\u5341\u591a\u4ee3\u4e86\u3002\u795e\u6bbf\u7684\u7b2c\u4e00\u4efb\u4e3b\u6559\uff0c\u662f\u73b0\u4efb\u4e3b\u6559\u7684\u7237\u7237\u7684\u7237\u7237\u7684\u7237\u7237\u7684\u7237\u7237\u2026\u2026\u7684\u7237\u7237\u8f88\u3002\u8fd9\u4e48\u9065\u8fdc\u7684\u4e8b\u60c5\uff0c\u9b3c\u624d\u8bb0\u5f97\u6e05\u695a\u3002\u5bf9\u4e8e\u73b0\u5728\u7684\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u6765\u8bf4\uff0c\u4ed6\u4eec\u65e9\u5c31\u9ed8\u8ba4\u81ea\u5df1\u624d\u662f\u8fd9\u7247\u571f\u5730\u7684\u771f\u6b63\u4e3b\u4eba\uff0c\u5982\u4eca\u7a81\u7136\u5192\u51fa\u6765\u4e00\u7fa4\u7cbe\u7075\uff0c\u8bf4\u201c\u8fd9\u5730\u65b9\u5176\u5b9e\u662f\u6211\u7684\uff0c\u5f53\u5e74\u6211\u79bb\u5bb6\u51fa\u95e8\u65c5\u6e38\uff0c\u628a\u5b83\u4ea4\u7ed9\u4f60\u7237\u7237\u7684\u7237\u7237\u7684\u7237\u7237\u2026\u2026\u7684\u7237\u7237\u4ee3\u7ba1\uff0c\u73b0\u5728\u6211\u56de\u6765\u4e86\uff0c\u8bf7\u4ea4\u8fd8\u7ed9\u6211\u5427\uff0c\u4f60\u4eec\u8f9b\u82e6\u4e86\uff0c\u53ef\u4ee5\u8d70\u4e86\u201d\u2014\u2014\u8fd9\u7b80\u76f4\u662f\u83ab\u540d\u5176\u5999\u3002\u5c31\u597d\u50cf\u4f60\u6709\u4e00\u5ea7\u7956\u4f20\u7684\u5b85\u5b50\uff0c\u67d0\u65e5\u7a81\u7136\u6709\u4eba\u8bf4\uff0c\u6b64\u5730\u539f\u5c5e\u4e8e\u6211\uff0c\u53ea\u662f\u5728\u5143\u671d\u672b\u5e74\u7684\u65f6\u5019\u5929\u4e0b\u5927\u4e71\uff0c\u6211\u51fa\u95e8\u907f\u96be\uff0c\u628a\u5b85\u9662\u4ea4\u7ed9\u4f60\u7684\u7956\u5b97\u770b\u7ba1\uff0c\u5982\u4eca\u8be5\u8fd8\u7ed9\u6211\u4e86\u2014\u2014\u5982\u679c\u6709\u4eba\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u5c31\u7b97\u4ed6\u62ff\u51fa\u4ec0\u4e48\u5730\u5951\uff0c\u4e5f\u4f1a\u88ab\u4f60\u76f4\u63a5\u4e00\u811a\u8e39\u98de\u5427\u3002</p><p>\u7136\u800c\u5bf9\u4e8e\u7cbe\u7075\u6765\u8bf4\uff0c\u4e8b\u60c5\u53c8\u5b8c\u5168\u4e24\u6837\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u5bff\u547d\u957f\u8fbe\u5343\u5e74\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5bf9\u4e8e\u7cbe\u7075\u800c\u8a00\uff0c\u4e0d\u8fc7\u5c31\u662f\u5e74\u8f7b\u7684\u65f6\u5019\u51fa\u95e8\uff0c\u5e74\u8fc8\u4ee5\u540e\u8fd4\u4e61\uff0c\u53d1\u73b0\u539f\u672c\u7684\u7ba1\u5bb6\u5df2\u7ecf\u4ee5\u4e3b\u4eba\u81ea\u5c45\uff0c\u751a\u81f3\u8fd8\u60f3\u5360\u7740\u4ea7\u4e1a\u4e0d\u80af\u8fd4\u56de\u2014\u2014\u8fd9\u5f53\u771f\u662f\u5c82\u6709\u6b64\u7406\u3002</p><p>\u53cc\u65b9\u4e3a\u6b64\u4e89\u6267\u4e0d\u4e0b\uff0c\u539f\u672c\u5c40\u9762\u5c31\u8fd9\u6837\u50f5\u4f4f\u4e86\uff0c\u8c01\u6599\u5c31\u5728\u53bb\u5e74\u5e74\u5e95\u7684\u67d0\u65e5\uff0c\u8ff7\u65af\u5353\u8bfa\u7684\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u7a81\u7136\u906d\u9047\u88ad\u51fb\uff0c\u795e\u804c\u4eba\u5458\u88ab\u5c60\u6740\u5927\u534a\uff0c\u4ec5\u6709\u5c11\u6570\u4eba\u9003\u5f97\u6027\u547d\u3002\u636e\u5e78\u5b58\u8005\u8bf4\uff0c\u88ad\u51fb\u8005\u662f\u4e00\u7fa4\u602a\u7269\uff0c\u5176\u4e2d\u5305\u62ec\u6df1\u72f1\u70bc\u9b54\u3001\u9b54\u88d4\u7cbe\u7075\u3001\u80fd\u4f7f\u7528\u5404\u79cd\u795e\u8d50\u6cd5\u672f\u7684\u4eba\u7c7b\u3001\u77f3\u5de8\u4eba\u548c\u4ea1\u7075\uff0c\u5176\u4e2d\u9886\u5934\u7684\u662f\u53ea\u8679\u5f69\u9f99\u3002</p><p>\u201c\u8679\u5f69\u9f99\uff1f\u201d</p><p>\u8fd9\u4e16\u754c\u4e0a\u7684\u8679\u5f69\u9f99\u51e4\u6bdb\u9e9f\u89d2\uff0c\u4f1a\u5728\u7269\u8d28\u754c\u6e38\u8361\u7684\u66f4\u5c48\u6307\u53ef\u6570\uff0c\u800c\u743c\u6069\u6070\u597d\u5c31\u8ba4\u8bc6\u90a3\u4e48\u4e00\u53ea\u2026\u2026\u4e0d\u4f1a\u8fd9\u4e48\u5de7\u5427\u3002</p><p>\u201c\u55ef\uff0c\u786e\u5b9e\u662f\u8679\u5f69\u9f99\uff0c\u201d\u91d1\u53d1\u7f8e\u4eba\u8bf4\uff0c\u201c\u636e\u67d0\u4e9b\u6d88\u606f\u6e20\u9053\u53cd\u9988\uff0c\u8fd9\u53ea\u8679\u5f69\u9f99\u4f3c\u4e4e\u548c\u7cbe\u7075\u4eec\u6709\u6240\u5173\u8054\u3002\u52a0\u4e0a\u8fd9\u4ef6\u4e8b\u786e\u5b9e\u592a\u8fc7\u51d1\u5de7\uff0c\u6240\u4ee5\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u7cbe\u7075\u662f\u8fd9\u573a\u4e8b\u6545\u7684\u5e55\u540e\u6307\u4f7f\u8005\u3002\u4f46\u5f88\u5feb\u6c38\u805a\u5c9b\u548c\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u5c31\u540c\u65f6\u53d1\u8868\u4e86\u58f0\u660e\uff0c\u4e00\u81f4\u5426\u8ba4\u8fd9\u79cd\u731c\u6d4b\u3002\u968f\u540e\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u9000\u51fa\u8ff7\u65af\u5353\u8bfa\uff0c\u628a\u90a3\u91cc\u79fb\u4ea4\u7ed9\u4e86\u7cbe\u7075\u3002\u201d</p><p>\u743c\u6069\u6c89\u601d\u4e86\u7247\u523b\uff0c\u201c\u90a3\u63d0\u5c14\u6559\u4f1a\u5462\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u6211\u8bb0\u5f97\u8ff7\u65af\u5353\u8bfa\u4e5f\u6709\u63d0\u5c14\u7684\u795e\u6bbf\u5427\u3002\u201d</p><p>\u201c\u54e6\uff0c\u662f\u6709\u4e00\u5ea7\u63d0\u5c14\u795e\u6bbf\uff0c\u4ed6\u4eec\u4e5f\u968f\u4e4b\u64a4\u9000\u4e86\uff0c\u201d\u91d1\u53d1\u7f8e\u4eba\u8bf4\uff0c\u201c\u4ed6\u4eec\u64a4\u9000\u5230\u4e86\u5854\u745f\u8c37\u3002\u201d</p><p>\u63d0\u5c14\u6559\u4f1a\u516d\u5927\u6559\u533a\u4e4b\u4e00\u7684\u8c37\u5730\u6559\u533a\uff0c\u603b\u90e8\u662f\u5728\u5854\u745f\u8c37\uff08\u79d1\u66fc\u7d22\u68ee\u6797\u7684\u4e1c\u5357\u90e8\uff0c\u6bd7\u90bb\u6851\u6bd4\u4e9a\uff09\uff0c\u8ff7\u65af\u5353\u8bfa\u7684\u795e\u6bbf\u662f\u5728\u52a8\u8361\u5e74\u4ee3\u7ed3\u675f\u4e4b\u540e\uff08\u5341\u516d\u5e74\u524d\uff09\u5efa\u7acb\u8d77\u6765\u7684\u3002\u5170\u68ee\u5fb7\u5c14\u795e\u6bbf\u64a4\u9000\u4e4b\u540e\uff0c\u63d0\u5c14\u6559\u4f1a\u4e5f\u5c31\u968f\u4e4b\u64a4\u9000\uff0c\u5e76\u672a\u906d\u53d7\u4ec0\u4e48\u6253\u51fb\u6216\u635f\u5931\u3002</p><p>\u542c\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u603b\u7b97\u662f\u5f7b\u5e95\u653e\u4e0b\u5fc3\u6765\u3002\u53ea\u8981\u786e\u5b9a\u6885\u83f2\u65af\u6ca1\u4e8b\u5c31\u884c\uff0c\u81f3\u4e8e\u8ff7\u65af\u5353\u8bfa\u7684\u4e89\u593a\uff0c\u7cbe\u7075\u548c\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7684\u6069\u6028\uff0c\u4ed6\u6ca1\u4ec0\u4e48\u5174\u8da3\u7406\u776c\uff0c\u542c\u8fc7\u5c31\u7b97\u3002\u552f\u4e00\u5bf9\u90a3\u4e2a\u8679\u5f69\u9f99\u7684\u4e8b\u60c5\u7b97\u662f\u63d0\u8d77\u70b9\u5174\u8da3\u2014\u2014\u4f46\u4e5f\u5c31\u662f\u70b9\u5174\u8da3\u7f62\u4e86\u3002</p><p>\u6309\u54a8\u8be2\u65f6\u95f4\u4ed8\u4e86\u6b3e\uff0c\u53c8\u82b1\u6389\u4e24\u679a\u7eff\u5b9d\u77f3\uff0c\u743c\u6069\u4e00\u884c\u4eba\u8d70\u51fa\u8fd9\u5ea7\u60c5\u62a5\u4ea4\u6613\u6240\uff0c\u524d\u5f80\u9009\u5b9a\u7684\u9910\u9986\u5403\u6d77\u9c9c\u3002\u8fdc\u5c71\u57ce\u662f\u4e2a\u5927\u6d77\u6e2f\uff0c\u6d77\u9c9c\u662f\u6781\u5bfb\u5e38\u7684\u98df\u54c1\uff0c\u4f46\u5bf9\u4e8e\u5728\u9634\u9b42\u57ce\u957f\u5927\u7684\u73ca\u5609\u7b49\u4eba\u6765\u8bf4\uff0c\u90a3\u5c31\u662f\u65b0\u5947\u7f8e\u5473\u4e86\u3002\u56e0\u4e3a\u4e86\u5374\u6389\u4e00\u6869\u5fc3\u4e8b\uff0c\u5927\u5bb6\u7684\u98df\u6b32\u90fd\u4e0d\u9519\uff0c\u5348\u996d\u540e\u63a5\u7740\u53bb\u901b\u8857\u3002\u8fdc\u5c71\u57ce\u7684\u7e41\u534e\u8ba9\u4e09\u4f4d\u5973\u5b69\u5b50\u5927\u5f00\u773c\u754c\uff0c\u60ca\u53f9\u4e0d\u5df2\uff0c\u7ed3\u679c\u5927\u5305\u5c0f\u5305\u7684\u5149\u8863\u670d\u5c31\u4e70\u4e86\u51e0\u5341\u4ef6\uff0c\u978b\u5b50\u4e70\u4e86\u5341\u51e0\u53cc\uff0c\u5176\u4f59\u5c0f\u96f6\u788e\u66f4\u662f\u4e0d\u8ba1\u5176\u6570\u2014\u2014\u53cd\u6b63\u6709\u743c\u6069\u8fd9\u4e2a\u81ea\u52a8\u4ed8\u6b3e\u673a\uff0c\u539f\u672c\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u8fd8\u6709\u4e9b\u77dc\u6301\uff0c\u4e0d\u597d\u610f\u601d\u82b1\u4ed6\u7684\u94b1\uff0c\u4f46\u5728\u73ca\u5609\u7684\u529d\u8bf4\u4e4b\u4e0b\u5f88\u5feb\u5c31\u201c\u5815\u843d\u201d\u4e86\uff0c\u7cbe\u795e\u6296\u64de\u5730\u6295\u8eab\u4e8e\u70ed\u706b\u671d\u5929\u7684\u8d2d\u7269\u6d3b\u52a8\u4e2d\u3002\u597d\u5728\u743c\u6069\u6709\u68cb\u5b50\u9b54\u50cf\uff0c\u76f4\u63a5\u62db\u51fa\u4e24\u4e2a\uff0c\u8ba9\u5b83\u4eec\u5f53\u82e6\u529b\u80cc\u7740\u5305\u88f9\uff0c\u81ea\u5df1\u4e24\u624b\u7a7a\u7a7a\u8ddf\u7740\u5c31\u884c\uff0c\u5076\u5c14\u5bf9\u5973\u5b69\u5b50\u4eec\u9009\u4e2d\u7684\u5546\u54c1\u53d1\u8868\u4e00\u4e24\u53e5\u770b\u6cd5\uff0c\u751a\u662f\u8f7b\u677e\u3002</p><p>\u4e0d\u8fc7\u5728\u73ca\u5609\u5979\u4eec\u8d2d\u7269\u7684\u65f6\u5019\uff0c\u743c\u6069\u4e0d\u65f6\u80fd\u591f\u770b\u5230\u8857\u9053\u4e0a\u6709\u5168\u526f\u6b66\u88c5\u7684\u58eb\u5175\u6765\u5f80\uff0c\u7a7a\u6c14\u4e2d\u9887\u6709\u51e0\u5206\u7d27\u5f20\uff0c\u611f\u89c9\u50cf\u662f\u8981\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4f3c\u7684\u3002\u743c\u6069\u5f00\u59cb\u4e0d\u5728\u610f\uff0c\u540e\u6765\u4e5f\u6709\u4e9b\u62c5\u5fe7\u8d77\u6765\uff0c\u665a\u4e0a\u56de\u5230\u65c5\u9986\u4e4b\u540e\uff0c\u4ed6\u4fbf\u62db\u6765\u4f8d\u8005\uff0c\u6253\u542c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\u3002</p><p>\u201c\u5176\u5b9e\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u5927\u4e8b\u5566\uff0c\u201d\u4f8d\u8005\u62ff\u7740\u743c\u6069\u9012\u7ed9\u7684\u4e00\u679a\u91d1\u5e01\uff0c\u9887\u4e3a\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\u662f\u542c\u8bf4\u90a3\u4e9b\u6563\u63d0\u4eba\u53c8\u5728\u8c03\u96c6\u519b\u961f\uff0c\u60f3\u6253\u6211\u4eec\u7684\u4e3b\u610f\uff0c\u6240\u4ee5\u9884\u4f5c\u6212\u5907\u7f62\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u4eec\u770b\u8d77\u6765\u5e76\u4e0d\u62c5\u5fc3\uff1f\u201d</p><p>\u201c\u62c5\u5fc3\u4ec0\u4e48\uff0c\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\uff0c\u201d\u4f8d\u8005\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u90a3\u5e2e\u8df3\u6881\u5c0f\u4e11\uff0c\u4e0d\u8db3\u4e3a\u60e7\u7684\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u4f8d\u8005\u867d\u7136\u8bf4\u5f97\u8f7b\u677e\uff0c\u743c\u6069\u5374\u8fd8\u662f\u6709\u4e9b\u4e0d\u653e\u5fc3\uff0c\u7ee7\u7eed\u8ffd\u95ee\u7ec6\u8282\uff0c\u4f8d\u8005\u539f\u672c\u4e0d\u60f3\u5e9f\u8bdd\uff0c\u4f46\u770b\u5728\u91d1\u5e01\u7684\u4efd\u4e0a\uff0c\u8fd8\u662f\u8001\u8001\u5b9e\u5b9e\u5411\u743c\u6069\u89e3\u91ca\u3002\u539f\u6765\u8fdc\u5c71\u57ce\u4f5c\u4e3a\u6708\u6d77\u5730\u533a\u8457\u540d\u7684\u5546\u4e1a\u90fd\u5e02\uff0c\u548c\u5317\u90e8\u7684\u6563\u63d0\u5c14\u5821\u2014\u2014\u8d39\u4f26\u5927\u9646\u6700\u8457\u540d\u7684\u8001\u724c\u9ed1\u9053\u7ec4\u7ec7\u6563\u5854\u6797\u4f1a\u7684\u5927\u672c\u8425\u2014\u2014\u4e00\u76f4\u90fd\u662f\u5904\u4e8e\u7ade\u4e89\u5bf9\u624b\u7684\u5173\u7cfb\u3002\u6563\u5854\u6797\u4f1a\u5c61\u6b21\u60f3\u8981\u5411\u5357\u90e8\u7684\u79d1\u66fc\u7d22\u3001\u8c37\u5730\u3001\u6851\u6bd4\u4e9a\u6269\u5c55\uff0c\u90fd\u53d7\u963b\u4e8e\u8fdc\u5c71\u57ce\uff0c\u56e0\u4e3a\u540e\u8005\u6b63\u597d\u5c31\u627c\u5b88\u5728\u54bd\u5589\u8981\u9053\u4e0a\u3002\u5f53\u7136\uff0c\u4ee5\u8fdc\u5c71\u4e00\u57ce\u4e4b\u529b\uff0c\u539f\u672c\u662f\u4e0d\u53ef\u80fd\u548c\u6563\u5854\u6797\u4f1a\u4e89\u96c4\u7684\u2014\u2014\u4f46\u80cc\u540e\u6709\u4e86\u585e\u5c14\u7684\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u9f0e\u7acb\u652f\u6301\uff0c\u90a3\u60c5\u5f62\u5c31\u53c8\u5927\u5927\u4e0d\u540c\u3002\u800c\u4e14\u636e\u4f20\u8a00\u8bf4\uff0c\u5c31\u8fde\u9634\u5f71\u8c37\u7684\u9009\u6c11\u4eec\u90fd\u548c\u8fdc\u5c71\u57ce\u6709\u7740\u4e00\u5b9a\u7684\u5408\u4f5c\u5173\u7cfb\uff0c\u8fd9\u4e5f\u53ef\u4ee5\u7406\u89e3\uff0c\u56e0\u4e3a\u4e00\u65e6\u8fdc\u5c71\u57ce\u88ab\u6563\u5854\u6797\u4f1a\u6240\u5f81\u670d\uff0c\u8c37\u5730\u5c31\u8981\u76f4\u63a5\u9762\u4e34\u6563\u63d0\u5c14\u5821\u5927\u519b\u7684\u5165\u4fb5\uff0c\u8fd9\u662f\u9b54\u6cd5\u5973\u795e\u7684\u6559\u4f1a\u7edd\u4e0d\u613f\u610f\u770b\u5230\u7684\u3002</p><p>\u5728\u5386\u53f2\u4e0a\uff0c\u6563\u63d0\u5c14\u5821\u5bf9\u8fdc\u5c71\u5e02\u53d1\u8d77\u8fc7\u4e03\u6b21\u8fdb\u653b\uff0c\u6700\u8fd1\u7684\u4e00\u6b21\u662f\u5341\u5e74\u524d\u2014\u2014\u7136\u540e\u4ed6\u4eec\u6bcf\u6b21\u90fd\u5931\u8d25\u4e86\u3002\u8fdc\u5c71\u5e02\u7684\u57ce\u9632\u519b\u7b97\u4e0d\u4e0a\u8bad\u7ec3\u6709\u7d20\uff0c\u4f46\u5386\u4efb\u57ce\u4e3b\u4fee\u7b51\u4e86\u6bd4\u7cbe\u91d1\u8fd8\u575a\u56fa\u7684\u73af\u72b6\u57ce\u5899\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u53c8\u5e2e\u52a9\u6784\u5efa\u4e86\u5e9e\u5927\u7684\u9632\u5fa1\u9b54\u6cd5\u9635\uff0c\u6e90\u6e90\u4e0d\u65ad\u5730\u63d0\u4f9b\u6d77\u91cf\u7684\u519b\u706b\uff0c\u518d\u52a0\u4e0a\u9634\u5f71\u8c37\u7684\u6697\u4e2d\u652f\u6301\uff0c\u6210\u529f\u5730\u4ee4\u6563\u63d0\u4eba\u5c61\u6b21\u94e9\u7fbd\u800c\u5f52\u3002</p><p>\u6b63\u56e0\u4e3a\u5c61\u6218\u5c61\u80dc\uff0c\u4ece\u65e0\u8d25\u7ee9\uff0c\u6240\u4ee5\u8fdc\u5c71\u4eba\u9762\u5bf9\u6563\u63d0\u4eba\u65f6\u5e95\u6c14\u5341\u8db3\uff0c\u867d\u7136\u542c\u95fb\u524d\u65b9\u6d88\u606f\uff0c\u8bf4\u662f\u6563\u63d0\u5c14\u5821\u53c8\u5728\u8c03\u96c6\u519b\u961f\uff0c\u610f\u6b32\u5357\u4e0b\uff0c\u5374\u90fd\u538b\u6839\u4e0d\u5f53\u56de\u4e8b\u3002\u201c\u65e0\u9700\u62c5\u5fc3\uff0c\u5148\u751f\uff0c\u201d\u4f8d\u8005\u5b89\u6170\u743c\u6069\uff0c\u201c\u90a3\u5e2e\u6563\u63d0\u7b28\u86cb\u662f\u7edd\u5bf9\u653b\u4e0d\u8fdb\u672c\u57ce\u7684\uff0c\u5728\u5916\u9762\u5439\u51e0\u5929\u98ce\u6652\u51e0\u5929\u592a\u9633\uff0c\u5230\u65f6\u5019\u5c31\u4f1a\u7070\u6e9c\u6e9c\u64a4\u9000\u2014\u2014\u542c\u8bf4\u4ed6\u4eec\u8fd9\u6b21\u8fd8\u627e\u4e86\u4e2a\u5a18\u4eec\u6765\u505a\u7edf\u5e05\uff0c\u53ef\u89c1\u5df2\u7ecf\u662f\u75c5\u6025\u4e71\u6295\u533b\u4e86\uff0c\u201d\u4ed6\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u5973\u4eba\u5728\u5bb6\u5e26\u5e26\u5b69\u5b50\u5c31\u597d\uff0c\u5f53\u5c06\u519b\u5e26\u5175\u6253\u4ed7\uff0c\u90a3\u4e0d\u662f\u5f00\u73a9\u7b11\u5417\uff0c\u60a8\u8bf4\u662f\u4e0d\u662f\uff0c\u5148\u751f\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u743c\u6069\u4e0d\u52a8\u58f0\u8272\u5730\u5e94\u7740\uff0c\u5fc3\u60f3\u5973\u4eba\u4f1a\u4e0d\u4f1a\u5e26\u5175\u6253\u4ed7\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u5374\u7edd\u5bf9\u6709\u6740\u4eba\u5982\u780d\u74dc\u5207\u83dc\u7684\u5389\u5bb3\u89d2\u8272\uff0c\u56e0\u4e3a\u6211\u6070\u597d\u5c31\u8ba4\u8bc6\u4e00\u4e2a\u3002\u6563\u5854\u6797\u4f1a\u4f5c\u4e3a\u8001\u724c\u9ed1\u9053\u7ec4\u7ec7\uff0c\u65e2\u7136\u5c61\u8d25\u4e4b\u4e0b\u5377\u571f\u91cd\u6765\uff0c\u53c8\u8bf7\u4e00\u4f4d\u5973\u5b50\u505a\u7edf\u5e05\uff0c\u90a3\u6b64\u4eba\u5c31\u5fc5\u5b9a\u6709\u975e\u51e1\u4e4b\u5904\uff0c\u600e\u4e48\u53ef\u4ee5\u7b49\u95f2\u89c6\u4e4b\u3002\u5982\u679c\u8fdc\u5c71\u57ce\u4e0a\u4e0b\u5168\u662f\u8fd9\u79cd\u76f2\u76ee\u4e50\u89c2\u7684\u5fc3\u6001\uff0c\u53ea\u6015\u8981\u5403\u5927\u4e8f\u3002</p><p>\u4e0d\u8fc7\u6b64\u4e8b\u53cd\u6b63\u4e0e\u5df1\u65e0\u5173\uff0c\u6563\u63d0\u5c14\u5821\u548c\u8fdc\u5c71\u57ce\u6709\u6bb5\u8ddd\u79bb\u5462\uff0c\u4e2d\u95f4\u8fd8\u6321\u7740\u4e00\u4e2a\u4fde\u62c9\u65af\u9547\uff0c\u662f\u8fdc\u5c71\u57ce\u7684\u9644\u5eb8\uff0c\u6563\u63d0\u5c14\u5821\u5f97\u5148\u628a\u5b83\u653b\u4e0b\uff0c\u7136\u540e\u624d\u80fd\u653b\u6253\u8fdc\u5c71\u3002\u4ece\u8def\u7a0b\u548c\u5e38\u7406\u6765\u63a8\u65ad\uff0c\u6563\u63d0\u5c14\u5821\u7684\u519b\u961f\u6ca1\u6709\u4e09\u56db\u5929\u7684\u529f\u592b\u662f\u4e0d\u4f1a\u5175\u4e34\u57ce\u4e0b\u7684\uff0c\u800c\u743c\u6069\u660e\u5929\u4e0b\u5348\u5c31\u8d70\u4eba\u4e86\u3002</p><p>\u7b97\u4e86\uff0c\u8c28\u614e\u8d77\u89c1\uff0c\u8fd8\u662f\u660e\u5929\u4e00\u65e9\u5c31\u52a8\u8eab\u5427\u3002\u5175\u51f6\u6218\u5371\u4e4b\u5730\uff0c\u4e0d\u5b9c\u4e45\u7559\u3002</p><p>\u62b1\u7740\u8fd9\u79cd\u6253\u7b97\uff0c\u743c\u6069\u56de\u623f\u95f4\u4f11\u606f\u3002\u4eca\u665a\u4ed6\u6ca1\u6709\u7559\u5728\u73ca\u5609\u5367\u5ba4\uff0c\u4e5f\u6ca1\u6709\u53bb\u627e\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\uff0c\u800c\u662f\u72ec\u5b88\u7a7a\u623f\uff0c\u9501\u4e0a\u95e8\uff0c\u4ece\u6000\u4e2d\u6478\u51fa\u838e\u5c14\u7ed9\u7684\u90a3\u679a\u5723\u5fbd\u6765\uff0c\u5728\u706f\u5149\u4e0b\u4ed4\u7ec6\u7814\u7a76\u3002</p><p>\u838e\u5c14\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u901a\u8fc7\u8fd9\u679a\u5723\u5fbd\uff0c\u53ef\u4ee5\u8ba9\u4ed6\u6682\u65f6\u6027\u5730\u8fd0\u7528\u5f71\u706b\u3002\u800c\u5982\u4f55\u8fd0\u7528\u5f71\u706b\uff0c\u5177\u4f53\u7684\u65b9\u6cd5\u8fd9\u679a\u5723\u5fbd\u4e5f\u4f1a\u6559\u743c\u6069\u2014\u2014\u7136\u800c\u5723\u5fbd\u662f\u6b7b\u7269\uff0c\u53c8\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u5982\u4f55\u6559\u6388\uff1f</p><p>\u7814\u7a76\u534a\u664c\uff0c\u4e0d\u5f97\u8981\u9886\uff0c\u6b63\u51c6\u5907\u5c06\u5b83\u6536\u8d77\u6765\uff0c\u5ffd\u7136\u90a3\u679a\u5723\u5fbd\u7684\u4e2d\u5fc3\u6709\u5fae\u5149\u95ea\u4e86\u95ea\uff0c\u4eff\u4f5b\u662f\u4eba\u7684\u773c\u775b\u8f7b\u8f7b\u4e00\u7728\u3002\u743c\u6069\u6014\u4e86\u6014\uff0c\u5b9a\u775b\u518d\u770b\uff0c\u5374\u53c8\u522b\u65e0\u5f02\u72b6\uff0c\u4ed6\u4ee5\u4e3a\u662f\u81ea\u5df1\u7684\u9519\u89c9\uff0c\u81ea\u5632\u4e00\u7b11\uff0c\u5ffd\u7136\u4e00\u9635\u6c89\u6c89\u5026\u610f\u88ad\u6765\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4fbf\u4f0f\u6848\u7761\u53bb\u3002</p><p>\u6726\u6726\u80e7\u80e7\u4e4b\u4e2d\uff0c\u53d1\u89c9\u81ea\u5df1\u4eff\u4f5b\u7f6e\u8eab\u591c\u7a7a\uff0c\u4e0a\u4e0b\u5de6\u53f3\u524d\u540e\u5c3d\u662f\u865a\u65e0\u9ed1\u6697\uff0c\u6df1\u4e0d\u89c1\u5e95\u3002\u4ed6\u60f6\u7136\u56de\u8eab\u56db\u987e\uff0c\u6b63\u4e0d\u77e5\u6240\u63aa\u95f4\uff0c\u5ffd\u7136\u4e00\u70b9\u7d2b\u8272\u661f\u5149\u4ece\u9ed1\u6697\u4e2d\u98de\u9a70\u6fc0\u5c04\u800c\u6765\uff0c\u5728\u4ed6\u9762\u524d\u6b62\u4f4f\uff0c\u4eff\u4f5b\u5fae\u5c0f\u706b\u7130\u822c\u8df3\u8dc3\u7740\uff0c\u968f\u5373\u7ffb\u817e\u6269\u5927\uff0c\u5e7b\u5316\u51fa\u4e00\u5e55\u5e55\u900f\u660e\u7684\u666f\u8c61\u6765\u3002</p><p>\u743c\u6069\u51dd\u89c6\u7740\u8fd9\u4e9b\u5e7b\u8c61\uff0c\u7cbe\u795e\u5239\u90a3\u95f4\u4fbf\u88ab\u5b8c\u5168\u5438\u4e86\u8fdb\u53bb\uff0c\u5343\u4e07\u526f\u6216\u5996\u5f02\u8be1\u8c32\uff0c\u6216\u75af\u72c2\u626d\u66f2\uff0c\u6216\u8840\u8165\u6740\u622e\uff0c\u6216\u5149\u5f71\u4ea4\u6fc0\u7684\u753b\u9762\u5728\u4ed6\u610f\u8bc6\u91cc\u4e00\u4e00\u95ea\u8fc7\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u8111\u4e2d\u8f70\u5730\u4e00\u58f0\uff0c\u518d\u5ea6\u6e05\u9192\u8fc7\u6765\uff0c\u4f9d\u65e7\u662f\u8db3\u8e0f\u865a\u7a7a\uff0c\u773c\u524d\u7684\u5e7b\u8c61\u5c3d\u6570\u6d88\u5931\uff0c\u4f9d\u65e7\u6536\u655b\u6210\u4e00\u70b9\u7d2b\u8272\u661f\u706b\uff0c\u968f\u5373\u5c04\u5165\u4ed6\u7684\u7709\u5fc3\u5f53\u4e2d\u3002</p><p>\u7136\u540e\u743c\u6069\u60ca\u9192\u3002</p><p>\u4ed6\u53d1\u73b0\u81ea\u5df1\u6b63\u8db4\u5728\u684c\u4e0a\uff0c\u4eff\u4f5b\u662f\u6253\u4e86\u4e2a\u76f9\uff0c\u706f\u706b\u4f9d\u65e7\u660f\u9ec4\uff0c\u90a3\u679a\u6697\u591c\u5723\u5fbd\u9759\u9759\u5730\u88ab\u63e1\u5728\u624b\u5fc3\u91cc\uff0c\u51b0\u51b0\u7684\uff0c\u786c\u786c\u7684\uff0c\u6ca1\u6709\u534a\u70b9\u6e29\u5ea6\u3002\u5468\u906d\u4e07\u7c41\u4ff1\u5bc2\uff0c\u743c\u6069\u770b\u770b\u65f6\u95f4\uff0c\u53d1\u73b0\u5df2\u7ecf\u662f\u51cc\u6668\u56db\u70b9\u591a\u949f\u4e86\uff0c\u81ea\u5df1\u7761\u4e86\u63a5\u8fd1\u516b\u4e2a\u5c0f\u65f6\uff0c\u96be\u602a\u80f3\u818a\u9178\u9ebb\u7684\u8981\u547d\u3002\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u6d3b\u52a8\u6d3b\u52a8\u80a2\u4f53\uff0c\u95ed\u4e0a\u773c\u505a\u4e86\u51e0\u4e2a\u6df1\u547c\u5438\uff0c\u7136\u540e\u65e0\u58f0\u5730\u5fae\u7b11\u8d77\u6765\u3002</p><p>\u901a\u8fc7\u68a6\u5883\u7684\u65b9\u5f0f\uff0c\u5723\u5fbd\u5c06\u5f71\u706b\u7684\u5404\u79cd\u8fd0\u7528\u65b9\u6cd5\u4f20\u6388\u7ed9\u4e86\u743c\u6069\uff0c\u7406\u8bba\u77e5\u8bc6\u5df2\u7ecf\u5177\u5907\uff0c\u63a5\u4e0b\u6765\u8981\u505a\u7684\u4fbf\u662f\u5728\u5b9e\u8df5\u4e2d\u719f\u6089\u638c\u63e1\u4e86\u3002\u5047\u4ee5\u65f6\u65e5\uff0c\u743c\u6069\u4fbf\u80fd\u591f\u50cf\u4e00\u4e2a\u771f\u6b63\u7684\u838e\u5c14\u9009\u6c11\u90a3\u6837\uff0c\u81ea\u7531\u5730\u8fd0\u4f7f\u5f71\u706b\u5851\u6210\u5404\u79cd\u6548\u679c\u3002\u66f4\u5de7\u5999\u7684\u662f\uff0c\u505a\u5230\u8fd9\u4e9b\u7684\u524d\u63d0\uff0c\u662f\u5fc5\u987b\u5148\u6fc0\u53d1\u8fd9\u679a\u5723\u5fbd\u7684\u529b\u91cf\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u838e\u5c14\u7684\u8fd9\u679a\u5723\u5fbd\u5c31\u50cf\u662f\u4e00\u4e2a\u5f00\u5173\uff0c\u743c\u6069\u53ef\u4ee5\u81ea\u7531\u5f00\u542f\u2014\u2014\u5f00\u542f\u4e86\uff0c\u4ed6\u5c31\u53d8\u6210\u838e\u5c14\u7684\u9009\u6c11\uff1b\u4e0d\u5f00\u542f\uff0c\u4ed6\u5c31\u4f9d\u7136\u53ea\u662f\u4e2a\u666e\u901a\u7684\u5deb\u5e08\u3002\u5f53\u7136\uff0c\u5723\u5fbd\u6bcf\u6b21\u88ab\u6fc0\u53d1\uff0c\u6700\u591a\u53ea\u80fd\u6301\u7eed\u5341\u5206\u949f\uff0c\u968f\u540e\u5c31\u8981\u9759\u6ede\u4e8c\u5341\u56db\u5c0f\u65f6\uff0c\u624d\u80fd\u591f\u518d\u5ea6\u4f7f\u7528\u3002</p><p>\u838e\u5c14\u8fd9\u4e48\u505a\uff0c\u65e2\u53ef\u4ee5\u8ba9\u743c\u6069\u4e0d\u5fc5\u7acb\u523b\u51b3\u5b9a\u505a\u5979\u7684\u9009\u6c11\uff0c\u53c8\u53ef\u4ee5\u4e34\u65f6\u6027\u5730\u8fd0\u7528\u5f71\u706b\u7684\u529b\u91cf\uff08\u6bcf\u5929\u5341\u5206\u949f\uff09\uff0c\u5b9e\u5728\u53ef\u4ee5\u8bf4\u662f\u5bf9\u743c\u6069\u4f53\u8d34\u5165\u5fae\u3002\u5982\u679c\u5979\u80fd\u5bf9\u6240\u6709\u7684\u4fe1\u5f92\u90fd\u8fd9\u6837\u7737\u987e\uff0c\u76f8\u4fe1\u591c\u5973\u58eb\u65e9\u5c31\u5df2\u7ecf\u4e00\u7edf\u795e\u754c\u4e86\u3002</p><p>\u6b63\u6f2b\u65e0\u8fb9\u9645\u5730\u60f3\u7740\uff0c\u5ffd\u7136\u95f4\u7a97\u5916\u4f20\u6765\u4e00\u9635\u5de8\u5927\u55a7\u54d7\uff0c\u6574\u4e2a\u65c5\u9986\u7684\u5ba2\u4eba\u90fd\u88ab\u60ca\u52a8\u8d77\u6765\u3002\u743c\u6069\u5fae\u5fae\u4e00\u60ca\uff0c\u51fa\u95e8\u67e5\u770b\u600e\u4e48\u56de\u4e8b\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u4f8d\u8005\u4f20\u56de\u6d88\u606f\uff0c\u4ed6\u7684\u8138\u8272\u6709\u4e9b\u82cd\u767d\u3002</p><p>\u201c\u4fde\u62c9\u65af\u9547\u88ab\u6563\u63d0\u5c14\u5821\u7684\u519b\u961f\u653b\u9677\u4e86\u3002\u201d</p></article>"}}}]);
