(window.webpackJsonp=window.webpackJsonp||[]).push([[443],{465:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u5fc3\u7075\u611f\u5e94\u7684\u95ee\u9898",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u5fc3\u7075\u611f\u5e94\u7684\u95ee\u9898</h2><p>\u201c\u8fd9\u5973\u4eba\u597d\u5389\u5bb3\uff01\u201d\u80d6\u57ce\u4e3b\u5f17\u96f7\u65af\u638f\u51fa\u624b\u5e15\u4e00\u8fb9\u64e6\u6c57\u4e00\u8fb9\u8bf4\uff0c\u201c\u5965\u53e4\u65af\u90fd\u4e5f\u7b97\u662f\u8fdc\u5c71\u57ce\u6709\u6570\u7684\u9ad8\u624b\u4e86\uff0c\u5c45\u7136\u5728\u5979\u9762\u524d\u8d70\u4e0d\u8fc7\u4e00\u5408\u3002\u201d</p><p>\u743c\u6069\u9ed8\u9ed8\u70b9\u5934\uff0c\u90a3\u4e2a\u585e\u5c14\u9a91\u58eb\u7b97\u4e0d\u7b97\u9ad8\u624b\uff0c\u4ed6\u4e0d\u597d\u8bc4\u4ef7\uff0c\u4f46\u8fd9\u4f4d\u6563\u63d0\u519b\u7edf\u5e05\u67aa\u6cd5\u4e4b\u72e0\u8fa3\uff0c\u51fa\u624b\u4e4b\u8fc5\u6377\uff0c\u5374\u662f\u4efb\u8c01\u90fd\u770b\u5f97\u51fa\u6765\u7684\u3002\u8fd9\u4e9b\u5e74\u4ed6\u8f97\u8f6c\u5927\u9646\u5404\u5730\uff0c\u906d\u9047\u5f3a\u654c\u65e0\u6570\uff0c\u5982\u679c\u53ea\u5355\u7eaf\u8bba\u6b66\u6280\uff0c\u80fd\u591f\u548c\u6885\u83f2\u65af\u76f8\u5339\u654c\u7684\u4eba\u5be5\u5be5\u65e0\u51e0\uff0c\u800c\u8fd9\u4f4d\u6b4c\u66e6\u96c5\u5c0f\u59d0\u80af\u5b9a\u662f\u5176\u4e2d\u4e4b\u4e00\u3002</p><p>\u5e78\u597d\u5979\u4eec\u53ea\u662f\u6c14\u8d28\u6709\u795e\u4f3c\u4e4b\u5904\uff0c\u5bb9\u8c8c\u8eab\u6bb5\u90fd\u660e\u663e\u4e0d\u540c\uff0c\u5426\u5219\u743c\u6069\u771f\u8981\u6000\u7591\u5bf9\u65b9\u662f\u4e0d\u662f\u6885\u83f2\u65af\u5047\u626e\u7684\u4e86\u3002</p><p>\u585e\u5c14\u9a91\u58eb\u548c\u90e8\u5c5e\u4eec\u77ac\u95f4\u88ab\u6d88\u706d\uff0c\u6781\u5927\u5730\u9707\u6151\u4e86\u8fdc\u5c71\u57ce\u7684\u5b88\u519b\u2014\u2014\u4f46\u7ec8\u7a76\u4e5f\u53ea\u662f\u9707\u6151\u7f62\u4e86\uff0c\u8ddd\u79bb\u7d2b\u53d1\u5973\u5b50\u6b4c\u66e6\u96c5\u6240\u8bf4\u7684\u201c\u5f00\u95e8\u6295\u964d\u201d\u8fd8\u5dee\u5f97\u8fdc\u3002\u65e2\u7136\u91ce\u6218\u6253\u4e0d\u8fc7\uff0c\u90a3\u5e72\u8106\u4e0d\u6253\u5c31\u662f\u4e86\uff0c\u8eb2\u5728\u575a\u56fa\u57ce\u5899\u540e\u9762\u9632\u5b88\u5373\u53ef\uff0c\u53c8\u8f7b\u677e\u53c8\u5b89\u5168\u3002\u5bf9\u65b9\u518d\u5f3a\u5927\uff0c\u7ec8\u7a76\u4e5f\u5c31\u662f\u51e0\u5341\u4e2a\u9a91\u5175\uff0c\u6b4c\u66e6\u96c5\u6b66\u6280\u518d\u9ad8\u660e\uff0c\u96be\u9053\u8fd8\u80fd\u4e00\u67aa\u628a\u57ce\u5899\u7ed9\u6345\u4e2a\u7f3a\u53e3\u4e0d\u6210\u3002\u8017\u7740\u5c31\u8017\u7740\uff0c\u62d6\u7740\u5c31\u62d6\u7740\uff0c\u53cd\u6b63\u518d\u8fc7\u51e0\u5929\u63f4\u519b\u5c31\u4f1a\u5230\u4e86\u3002</p><p>\u62b1\u7740\u8fd9\u79cd\u5ff5\u5934\uff0c\u57ce\u5934\u4e0a\u8fde\u7bad\u90fd\u61d2\u5f97\u5c04\uff0c\u53cd\u6b63\u8ddd\u79bb\u8fd9\u4e48\u8fdc\uff0c\u5bf9\u65b9\u53c8\u90fd\u7a7f\u7740\u91cd\u7532\uff0c\u8fd8\u4e0d\u5982\u7701\u70b9\u529b\u6c14\u3002\u6b4c\u66e6\u96c5\u9ed8\u4e0d\u4f5c\u58f0\u5730\u7b49\u5f85\u4e86\u5927\u6982\u5341\u5206\u949f\uff0c\u89c1\u8fdc\u5c71\u57ce\u663e\u7136\u6ca1\u6709\u5f00\u95e8\u51fa\u964d\u4e4b\u610f\uff0c\u5979\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u5c31\u53ea\u597d\u6253\u4e00\u4ed7\u4e86\u3002\u201d\u5979\u4f9d\u65e7\u662f\u5e73\u5e73\u6de1\u6de1\u5730\u8bf4\u7740\uff0c\u5c06\u957f\u67aa\u6302\u56de\u978d\u4e0b\uff0c\u5de6\u624b\u4e3e\u8d77\u91cd\u5251\uff0c\u5728\u865a\u7a7a\u4e2d\u4e00\u5212\u3002</p><p>\u4eff\u4f5b\u53d7\u5230\u611f\u5e94\u4e00\u822c\uff0c\u5de8\u5927\u7684\u96f7\u9e23\u58f0\u5728\u82cd\u7a79\u6df1\u5904\u9aa4\u7136\u54cd\u8d77\uff0c\u8fde\u7ef5\u4e0d\u7edd\uff0c\u968f\u5373\u5de8\u5927\u7684\u9634\u5f71\u906e\u5929\u853d\u65e5\u5730\u7b3c\u7f69\u4e0b\u6765\u3002\u743c\u6069\u60ca\u8bb6\u5730\u4ef0\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u5728\u6781\u9ad8\u7a7a\u4e2d\uff0c\u6570\u4ee5\u767e\u5343\u8ba1\u7684\u5e9e\u5927\u9e1f\u7c7b\u6b63\u671d\u7740\u8fdc\u5c71\u57ce\u547c\u5578\u98de\u81f3\u3002</p><p>\u201c\u9a71\u4f7f\u98de\u9e1f\u653b\u57ce\u4e48\uff1f\u8fd9\u53c8\u6709\u4ec0\u4e48\u7528\u2026\u2026\u800c\u4e14\u8fd9\u4f4d\u6b4c\u66e6\u96c5\u5c0f\u59d0\u5e94\u8be5\u4e0d\u662f\u5fb7\u9c81\u4f0a\u5427\u3002\u201d</p><p>\u6b63\u731c\u6d4b\u7740\uff0c\u90a3\u4e9b\u602a\u9e1f\u5df2\u7136\u98de\u8fd1\uff0c\u5728\u57ce\u5934\u4e0a\u7a7a\u76d8\u65cb\u805a\u96c6\u7740\uff0c\u5217\u6210\u677e\u677e\u6563\u6563\u7684\u9635\u578b\u3002\u743c\u6069\u8fd9\u624d\u770b\u5f97\u5206\u660e\uff0c\u539f\u6765\u662f\u4e9b\u602a\u9e1f\uff0c\u5176\u4e2d\u53c8\u6709\u4e24\u7c7b\uff0c\u4e00\u7c7b\u4f53\u578b\u5e9e\u5927\uff0c\u5f2f\u5599\u5982\u94a9\uff0c\u5229\u722a\u4f3c\u5200\uff0c\u5f20\u5f00\u7684\u4e00\u5bf9\u7fbd\u7ffc\u8db3\u6709\u4e8c\u5341\u5c3a\uff0c\u4eff\u4f5b\u5de8\u9e70\uff0c\u8eaf\u5e72\u5374\u662f\u9a6c\u578b\uff0c\u53e6\u4e00\u7c7b\u4f53\u578b\u8f83\u5c0f\uff0c\u8c8c\u4f3c\u7ffc\u9f99\uff0c\u957f\u5599\u76ae\u7ffc\uff0c\u7ffc\u7f18\u751f\u722a\u3002\u4e24\u79cd\u602a\u7269\u80cc\u4e0a\u90fd\u5b89\u7740\u96d5\u978d\uff0c\u5750\u7740\u9a91\u624b\uff0c\u5374\u662f\u6e05\u4e00\u8272\u7684\u7eff\u6761\u7eb9\u9ed1\u8272\u957f\u888d\uff0c\u6b63\u662f\u6563\u5854\u6797\u4f1a\u5deb\u5e08\u7684\u6807\u51c6\u5236\u670d\u3002</p><p>\u201c\u9e6b\u9a6c\uff1f\u201d\u743c\u6069\u56de\u5fc6\u7740\u4ee5\u524d\u5728\u5b66\u6821\u91cc\u770b\u8fc7\u7684\u602a\u7269\u56fe\u9274\uff0c\u8fa8\u8ba4\u51fa\u524d\u9762\u4e00\u79cd\u602a\u7269\u7684\u540d\u79f0\uff0c\u201c\u4e0d\u662f\u8bf4\u8fd9\u79cd\u602a\u7269\u6027\u683c\u6781\u66b4\u70c8\uff0c\u5f88\u96be\u9a6f\u670d\u5f53\u5750\u9a91\u7684\uff0c\u6563\u5854\u6797\u4f1a\u600e\u4e48\u80fd\u5f04\u5230\u8fd9\u4e48\u591a\uff0c\u8d77\u7801\u6709\u56db\u4e94\u767e\u53ea\u4e86\u3002\u201d</p><p>\u6b63\u601d\u5fd6\u7740\uff0c\u53d1\u89c9\u65c1\u8fb9\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\uff0c\u4ed6\u8f6c\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u57ce\u4e3b\u5f17\u96f7\u65af\u5148\u751f\u6b63\u53cc\u773c\u53d1\u76f4\uff0c\u5706\u6eda\u6eda\u7684\u80d6\u8138\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u53d8\u5f97\u715e\u767d\u4e00\u7247\uff0c\u5168\u65e0\u534a\u70b9\u8840\u8272\uff0c\u8fde\u7259\u9f7f\u90fd\u5728\u8f7b\u5fae\u6253\u98a4\u3002\u201c\u5929\u2026\u2026\u5929\u7a79\u519b\u56e2\uff1f\u201d\u4ed6\u53d1\u51fa\u547b\u541f\u822c\u7684\u58f0\u97f3\uff0c\u201c\u4ed6\u4eec\u5c45\u7136\u53c8\u5efa\u8d77\u4e86\u5929\u7a79\u519b\u56e2\uff1f\u201d</p><p>\u743c\u6069\u5bf9\u6563\u5854\u6797\u4f1a\u4e0d\u719f\uff0c\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u5929\u7a79\u519b\u56e2\uff0c\u66f4\u4e0d\u660e\u767d\u8fd9\u80d6\u5b50\u4e3a\u4f55\u5bb3\u6015\u5230\u8fd9\u79cd\u7a0b\u5ea6\u3002\u8bda\u7136\uff0c\u51e0\u767e\u8fd1\u5343\u4eba\u9a7e\u9a6d\u7740\u9e6b\u9a6c\u7ffc\u9f99\u906e\u5929\u853d\u65e5\u800c\u6765\uff0c\u975e\u5e38\u6709\u9707\u64bc\u529b\u548c\u89c6\u89c9\u6548\u679c\u2014\u2014\u4f46\u90a3\u53c8\u5982\u4f55\uff1f\u5c31\u7b97\u8fd9\u4ec0\u4e48\u5929\u7a79\u519b\u56e2\u662f\u8d85\u65f6\u4ee3\u7684\u8f70\u70b8\u673a\u2014\u2014\u5355\u51ed\u8f70\u70b8\u673a\uff0c\u6216\u8005\u52a0\u4e0a\u57ce\u4e0b\u8fd9\u51e0\u5341\u4e2a\u68a6\u9b47\u9a91\u58eb\uff0c\u90a3\u4e5f\u662f\u6ca1\u6cd5\u653b\u9677\u4e00\u5ea7\u57ce\u6c60\u7684\u5427\u3002\u66f4\u4f55\u51b5\u8fdc\u5c71\u57ce\u7b3c\u7f69\u7740\u65e0\u6570\u9632\u5fa1\u6cd5\u9635\uff0c\u60f3\u4ece\u7a7a\u4e2d\u8f70\u70b8\u4e5f\u6ca1\u90a3\u4e48\u5bb9\u6613\u5462\u3002</p><p>\u60f3\u662f\u8fd9\u4e48\u60f3\uff0c\u4f46\u51fa\u4e8e\u8c28\u614e\u8d77\u89c1\uff0c\u4ed6\u8fd8\u662f\u63a8\u4e86\u63a8\u5df2\u7ecf\u88ab\u60ca\u5446\u7684\u5f17\u96f7\u65af\uff0c\u6b63\u8981\u8be2\u95ee\u8fd9\u4ec0\u4e48\u5929\u7a79\u519b\u56e2\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u5374\u89c1\u80d6\u57ce\u4e3b\u5927\u4eba\u5982\u68a6\u521d\u9192\u822c\uff0c\u80e1\u4e71\u62b9\u4e86\u628a\u989d\u5934\u4e0a\u7684\u51b7\u6c57\uff0c\u4e5f\u4e0d\u548c\u743c\u6069\u6253\u62db\u547c\uff0c\u8f6c\u8eab\u5c31\u4e0b\u57ce\u53bb\u4e86\u3002\u597d\u5728\u4ed6\u8fd9\u4e2a\u57ce\u4e3b\u539f\u672c\u5c31\u662f\u6446\u8bbe\uff0c\u522b\u4eba\u4e5f\u4e0d\u6ce8\u610f\uff0c\u743c\u6069\u76b1\u4e86\u76b1\u7709\uff0c\u62c9\u7740\u73ca\u5609\u7d27\u968f\u5176\u540e\u3002\u522b\u770b\u5f17\u96f7\u65af\u8eab\u8eaf\u80a5\u80d6\uff0c\u8dd1\u8d77\u6765\u8fd8\u771f\u662f\u5feb\uff0c\u4e09\u6b65\u4e24\u6b65\u4e0a\u4e86\u81ea\u5df1\u7684\u9a6c\u8f66\uff0c\u4e00\u6e9c\u70df\u626c\u97ad\u800c\u53bb\u3002</p><p>\u201c\u8fd9\u5bb6\u4f19\u5230\u5e95\u5728\u6015\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u6211\u4eec\u4e5f\u5148\u56de\u65c5\u5e97\u5427\uff0c\u201d\u73ca\u5609\u5728\u65c1\u8fb9\u8f7b\u58f0\u8bf4\uff0c\u201c\u522b\u8ba9\u5979\u4eec\u62c5\u5fc3\u3002\u201d</p><p>\u743c\u6069\u5514\u4e86\u4e00\u58f0\uff0c\u660e\u767d\u73ca\u5609\u7684\u610f\u601d\u3002\u8fd9\u4e00\u4ed7\u53cd\u6b63\u548c\u81ea\u5df1\u6ca1\u5173\u7cfb\uff0c\u8fdc\u5c71\u57ce\u6218\u80dc\u4e86\uff0c\u743c\u6069\u8d70\u8def\uff1b\u6563\u63d0\u519b\u6218\u80dc\u4e86\uff0c\u96be\u9053\u8fd8\u7279\u5730\u6765\u96be\u4e3a\u743c\u6069\u4e00\u4e2a\u6e38\u5ba2\uff0c\u7167\u6837\u8fd8\u662f\u8d70\u8def\uff1b\u5de6\u53f3\u65e0\u6240\u8c13\uff0c\u4e0d\u5982\u56de\u5bb6\u7761\u89c9\u53bb\uff0c\u9759\u5019\u7ed3\u679c\u4fbf\u662f\u3002</p><p>\u4ed6\u6765\u7684\u65f6\u5019\u662f\u5750\u5f17\u96f7\u65af\u7684\u9a6c\u8f66\uff0c\u73b0\u5728\u53ea\u597d\u6b65\u884c\u56de\u53bb\u3002\u521a\u8d70\u5230\u534a\u8def\uff0c\u7a81\u7136\u542c\u5f97\u57ce\u95e8\u5904\u4f20\u6765\u4e00\u9635\u60ca\u5929\u52a8\u5730\u7684\u9707\u54cd\uff0c\u6eda\u6eda\u70df\u5c18\u5f25\u6f2b\u817e\u8d77\uff0c\u4eff\u4f5b\u6709\u6bb5\u57ce\u5899\u5012\u584c\u4e86\u4e00\u822c\uff0c\u9ad8\u7a7a\u4e2d\u805a\u96c6\u7684\u5929\u7a79\u519b\u56e2\u4e58\u673a\u63a5\u4e8c\u8fde\u4e09\u5730\u4fef\u51b2\u800c\u4e0b\uff0c\u5411\u57ce\u5934\u7684\u5b88\u519b\u91ca\u653e\u51fa\u5404\u79cd\u653b\u51fb\u6cd5\u672f\uff0c\u6295\u63b7\u71c3\u70e7\u5f39\u548c\u70bd\u706b\u80f6\u3002\u800c\u57ce\u5e02\u4e0a\u7a7a\u7b3c\u7f69\u7684\u5e9e\u5927\u6cd5\u9635\uff0c\u4e0d\u77e5\u4f55\u6545\u7a81\u7136\u6709\u5927\u91cf\u7684\u7b26\u6587\u9eef\u6de1\u6d88\u5931\uff0c\u6216\u8005\u81ea\u52a8\u7206\u88c2\u6467\u6bc1\uff0c\u7075\u7ebf\u9519\u4e71\u65ad\u88c2\uff0c\u9632\u5fa1\u987f\u65f6\u51fa\u73b0\u5de8\u5927\u7684\u7a7a\u9699\u3002\u7d27\u63a5\u7740\uff0c\u6574\u5ea7\u8fdc\u5c71\u57ce\u7684\u6bcf\u4e00\u5904\uff0c\u6bcf\u4e2a\u89d2\u843d\uff0c\u90fd\u4f20\u6765\u5c71\u547c\u6d77\u5578\u822c\u7684\u5450\u558a\u58f0\u3002</p><p>\u201c\u6563\u63d0\u519b\u5df2\u7ecf\u5165\u57ce\u4e86\uff01\u201d</p><p>\u203b\u203b\u203b</p><p>1374DR\uff0c\u4e25\u51ac\u4e4b\u6708\uff08Hammer\uff0c\u8d39\u4f26\u8c37\u5730\u5386\u6cd5\u7684\u4e00\u6708\uff09\uff0c\u8ddd\u79bb\u4ef2\u51ac\u8282\u8fd8\u6709\u4e09\u5929\uff0c\u6563\u63d0\u5c14\u5821\u7684\u5927\u519b\u653b\u9677\u4e86\u8fdc\u5c71\u57ce\u3002</p><p>\u4e3a\u4e86\u8fd9\u6b21\u80dc\u5229\uff0c\u6563\u5854\u6797\u4f1a\u53ef\u4ee5\u8bf4\u662f\u505a\u4e86\u5145\u8db3\u7684\u51c6\u5907\u3002\u8fd9\u5341\u5e74\u5f53\u4e2d\uff0c\u4ed6\u4eec\u5411\u8fdc\u5c71\u57ce\u79d8\u5bc6\u6d3e\u9063\u4e86\u6570\u4ee5\u5343\u8ba1\u7684\u95f4\u8c0d\uff0c\u5927\u90e8\u5206\u90fd\u6210\u529f\u5730\u624e\u4e0b\u6839\u6765\uff0c\u6709\u7684\u53d8\u6210\u5546\u8d29\uff0c\u6709\u7684\u53d8\u6210\u5de5\u5320\uff0c\u6709\u7684\u53d8\u6210\u4f4e\u7ea7\u5b98\u540f\uff0c\u751a\u81f3\u8fd8\u6709\u6781\u5c11\u6570\u6210\u529f\u6df7\u8fdb\u4e86\u653f\u5e9c\u9ad8\u5c42\uff0c\u4ed6\u4eec\u5b9a\u5c45\u3001\u89c2\u5bdf\u3001\u6536\u96c6\u60c5\u62a5\u3001\u4f20\u9012\u6d88\u606f\u3002\u800c\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u5185\u4e71\uff0c\u8fdc\u5c71\u57ce\u4e3b\u7684\u4ea4\u66ff\uff0c\u66f4\u8fdb\u4e00\u6b65\u52a0\u5267\u4e86\u6563\u5854\u6797\u4f1a\u7684\u6e17\u900f\uff0c\u6709\u5f88\u591a\u524d\u4efb\u8fdc\u5c71\u57ce\u4e3b\u7684\u4eb2\u4fe1\u76f4\u63a5\u8f6c\u6295\u9635\u8425\uff0c\u8fd8\u6709\u5f88\u591a\u6210\u4e3a\u5185\u5e94\u2014\u2014\u5176\u4e2d\u751a\u81f3\u5305\u62ec\u67d0\u4e9b\u5c06\u9886\u3002\u800c\u5728\u653b\u57ce\u4e4b\u524d\uff0c\u90a3\u7fa4\u521a\u521a\u88ab\u653e\u8fdb\u57ce\u7684\u96be\u6c11\uff0c\u5176\u4e2d\u53c8\u6f5c\u85cf\u4e86\u6570\u5341\u540d\u6563\u5854\u6797\u4f1a\u4e25\u683c\u6311\u9009\u51fa\u6765\u7684\u7cbe\u9510\u3002</p><p>\u5185\u5916\u5939\u653b\u4e4b\u4e0b\uff0c\u8fdc\u5c71\u575a\u56fa\u7684\u57ce\u95e8\u88ab\u6253\u5f00\uff0c\u6b4c\u66e6\u96c5\u9886\u519b\u5165\u57ce\u3002\u5176\u5b9e\u771f\u8bba\u8d77\u6765\uff0c\u6563\u63d0\u519b\u7684\u5927\u90e8\u961f\u6b64\u65f6\u8fd8\u5728\u8def\u4e0a\uff0c\u5c31\u51ed\u8fd9\u70b9\u5148\u5934\u90e8\u961f\u52a0\u4e0a\u5185\u5e94\uff0c\u8f83\u4e4b\u8fdc\u5c71\u57ce\u7684\u5175\u529b\u8fd8\u662f\u8fdc\u8fdc\u5904\u4e8e\u52a3\u52bf\u3002\u4f46\u6253\u4ed7\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5f88\u591a\u65f6\u5019\u8bb2\u7684\u5c31\u662f\u4e00\u4e2a\u58eb\u6c14\uff0c\u58eb\u6c14\u4e00\u5d29\u6e83\uff0c\u4eba\u6570\u518d\u591a\u4e5f\u662f\u767d\u642d\u3002\u5927\u91cf\u5927\u91cf\u7684\u58eb\u5175\u5c31\u8fd9\u6837\u76f4\u63a5\u7f34\u68b0\u6295\u964d\uff0c\u5c11\u6570\u62b5\u6297\u8005\u4e5f\u4e0d\u6210\u7ec4\u7ec7\uff0c\u5f88\u5feb\u88ab\u65a9\u6740\u6b86\u5c3d\uff0c\u6563\u63d0\u519b\u8fc5\u901f\u5360\u9886\u4e86\u5e02\u653f\u5e9c\u3001\u519b\u706b\u5e93\u7b49\u8981\u5730\uff0c\u63a5\u7ba1\u5168\u57ce\uff0c\u968f\u5373\u53d1\u5e03\u6212\u4e25\u4ee4\u3002\u552f\u4e00\u7684\u7f8e\u4e2d\u4e0d\u8db3\uff0c\u662f\u6ca1\u6709\u6293\u5230\u57ce\u4e3b\u5f17\u96f7\u65af\uff0c\u90a3\u4e2a\u80d6\u8001\u5934\u6e9c\u5f97\u98de\u5feb\uff0c\u4e0d\u77e5\u9053\u8eb2\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u57ce\u6c60\u6613\u4e3b\uff0c\u5e02\u6c11\u6050\u60e7\uff0c\u7eb7\u7eb7\u95ed\u95e8\u4e0d\u51fa\uff0c\u800c\u6563\u63d0\u519b\u4f3c\u4e4e\u519b\u7eaa\u6781\u4e25\uff0c\u5e76\u6ca1\u53d1\u751f\u4ec0\u4e48\u62a2\u63a0\u70e7\u6740\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\u6574\u4f53\u6765\u8bf4\u5c40\u52bf\u8fd8\u7b97\u7a33\u5b9a\u3002\u743c\u6069\u4e5f\u61d2\u5f97\u51fa\u95e8\uff0c\u8eb2\u5728\u65c5\u5e97\u91cc\u5b85\u4e86\u4e24\u5929\uff0c\u53cd\u6b63\u6709\u7f8e\u4eba\u76f8\u4f34\uff0c\u4e0d\u89c9\u5bc2\u5bde\uff0c\u95f2\u6687\u7684\u65f6\u5019\u5c31\u6084\u6084\u953b\u70bc\u5f71\u706b\uff0c\u6709\u51e0\u79cd\u6700\u7b80\u5355\u7684\u7528\u6cd5\u5012\u662f\u5df2\u7ecf\u57fa\u672c\u638c\u63e1\u4e86\u3002</p><p>\u4e24\u65e5\u4e4b\u540e\uff0c\u6563\u63d0\u5c14\u5821\u7684\u5927\u519b\u5f00\u5230\uff0c\u6212\u4e25\u4ee4\u53d6\u6d88\uff0c\u5546\u65c5\u884c\u4eba\u53c8\u53ef\u4ee5\u81ea\u7531\u8fdb\u51fa\u5f80\u6765\u4e86\u3002\u743c\u6069\u7b49\u4eba\u8d76\u5feb\u79bb\u57ce\uff0c\u524d\u5f80\u6851\u6bd4\u4e9a\u3002</p><p>\u4ece\u8fdc\u5c71\u57ce\u51fa\u53d1\uff0c\u6cbf\u7740\u6708\u6d77\u516c\u8def\u5357\u4e0b\uff0c\u8fc7\u963f\u6c99\u5df4\u6cb3\uff0c\u76f4\u5230\u6851\u6bd4\u4e9a\u7684\u9996\u90fd\u6b27\u675c\u6797\u57ce\uff0c\u8def\u7ebf\u5f88\u6f2b\u957f\uff0c\u9a6c\u8f66\u4e5f\u5f97\u8dd1\u4e0a\u4e2a\u4e94\u516d\u5929\uff0c\u4e2d\u9014\u5374\u518d\u65e0\u4ec0\u4e48\u57ce\u9547\u53ef\u4f9b\u4f4f\u5bbf\u3002\u743c\u6069\u96c7\u7684\u9a6c\u8f66\u5012\u662f\u591f\u5bbd\u655e\uff0c\u5c06\u5c31\u5c06\u5c31\u4e5f\u80fd\u7761\uff0c\u4f46\u6ca1\u5fc5\u8981\u8650\u5f85\u81ea\u5df1\uff0c\u6bcf\u5230\u665a\u95f4\uff0c\u627e\u4e00\u5904\u7a7a\u5730\uff0c\u76f4\u63a5\u62ff\u51fa\u90a3\u4e2a\u4ec0\u4e48\u201c\u5965\u6c83\u7684\u9752\u94dc\u8c6a\u5b85\u201d\uff0c\u53d8\u6210\u623f\u5b50\u5c31\u4f4f\u8fdb\u53bb\u3002\u5965\u6c83\u660e\u660e\u662f\u4e2a\u5deb\u5996\uff0c\u623f\u5b50\u5012\u662f\u8bbe\u8ba1\u5f97\u5b8c\u5168\u7b26\u5408\u6d3b\u4eba\u7684\u6807\u51c6\uff0c\u6709\u4ec6\u4eba\uff0c\u6709\u53a8\u5e08\uff0c\u6709\u4e30\u7f8e\u5927\u9910\uff0c\u6709\u8212\u9002\u8f6f\u5e8a\uff0c\u8fd8\u6709\u8d85\u5927\u8d85\u8c6a\u534e\u7684\u6d74\u7f38\u2014\u2014\u7b80\u76f4\u5c31\u662f\u5c0f\u578b\u6e38\u6cf3\u6c60\uff0c\u522b\u8bf4\u662f\u548c\u73ca\u5609\u6d17\u9e33\u9e2f\u6d74\uff0c\u5c31\u7b97\u628a\u53cc\u80de\u80ce\u548c\u9b45\u9b54\u90fd\u62c9\u8fdb\u6765\u4e5f\u6ca1\u95ee\u9898\u3002\u53ef\u60dc\u8fd9\u79cd\u9999\u8273\u573a\u666f\u53ea\u5728\u8111\u5b50\u91cc\u60f3\u8fc7\uff0c\u6682\u65f6\u8fd8\u6ca1\u6709\u4ed8\u8bf8\u5b9e\u8df5\u3002</p><p>\u5728\u8def\u4e0a\u7684\u65f6\u5019\uff0c\u743c\u6069\u7ec8\u4e8e\u548c\u6885\u83f2\u65af\u518d\u5ea6\u53d6\u5f97\u4e86\u8054\u7cfb\uff0c\u5723\u6b66\u58eb\u4e5f\u5df2\u7ecf\u5f97\u77e5\u4e86\u8ff7\u65af\u5353\u8bfa\u7684\u4e8b\u60c5\u3002\u201c\u6211\u73b0\u5728\u8fd8\u5728\u963f\u683c\u62c9\u9686\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u660e\u5929\u52a8\u8eab\u51fa\u53d1\u3002\u6211\u4e0d\u53bb\u8ff7\u65af\u5353\u8bfa\u4e86\uff0c\u76f4\u63a5\u56de\u5854\u745f\u8c37\uff0c\u4f60\u529e\u5b8c\u4e8b\u53bb\u5854\u745f\u8c37\u627e\u6211\u5427\u3002\u201d</p><p>\u201c\u597d\u7684\uff0c\u51db\u600e\u4e48\u6837\uff1f\u201d</p><p>\u201c\u8fd8\u597d\uff0c\u201d\u6885\u83f2\u65af\u542b\u7cca\u5730\u8bf4\uff0c\u201c\u5979\u4f1a\u548c\u6211\u4e00\u8d77\u56de\u5854\u745f\u8c37\uff0c\u89c1\u9762\u518d\u8bf4\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u672c\u6765\u8fd8\u60f3\u987a\u4fbf\u95ee\u95ee\u5979\uff0c\u8ba4\u4e0d\u8ba4\u8bc6\u6563\u63d0\u5c14\u5821\u4e00\u4e2a\u53eb\u505a\u6b4c\u66e6\u96c5\u7684\u5973\u5b50\uff0c\u4f46\u77ed\u8baf\u672f\u7ef4\u6301\u65f6\u95f4\u5f88\u77ed\uff0c\u53ea\u5f97\u7f62\u4e86\u3002\u8054\u7cfb\u65ad\u4e86\u8fd9\u4e48\u4e45\uff0c\u73b0\u5728\u77e5\u9053\u6885\u83f2\u65af\u548c\u51db\u65e0\u6059\uff0c\u4e5f\u4fbf\u653e\u4e0b\u5fc3\u6765\uff0c\u5176\u4ed6\u4e8b\u60c5\u90fd\u4e0d\u5fc5\u5728\u610f\uff0c\u629b\u4e4b\u8111\u540e\u3002</p><p>\u8fd9\u4e00\u65e5\uff0c\u4ed6\u4eec\u5230\u4e86\u4e2a\u53eb\u505a\u57c3\u585e\u5e03\u62c9\u7684\u5730\u65b9\uff0c\u5929\u8272\u5df2\u665a\uff0c\u4e8e\u662f\u5c31\u5730\u624e\u8425\u3002\u6839\u636e\u5730\u56fe\u663e\u793a\uff0c\u660e\u5929\u5c31\u80fd\u51fa\u79d1\u66fc\u7d22\u68ee\u6797\uff0c\u8fdb\u5165\u6851\u6bd4\u4e9a\u7684\u56fd\u5883\u3002\u653e\u51fa\u9752\u94dc\u623f\u5b50\uff0c\u665a\u9910\u6bd5\uff0c\u5927\u5bb6\u5404\u81ea\u56de\u623f\u95f4\u4f11\u606f\uff0c\u743c\u6069\u4f9d\u65e7\u548c\u73ca\u5609\u5728\u4e00\u8d77\uff0c\u773c\u770b\u591c\u8272\u6e10\u6df1\uff0c\u6b63\u5f85\u6c42\u6b22\uff0c\u5374\u88ab\u59d0\u59d0\u63a8\u5f00\u3002</p><p>\u201c\u4eca\u5929\u4e0d\u884c\u3002\u201d\u5979\u7ea2\u7740\u8138\u8bf4\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u4e0d\u65b9\u4fbf\u3002\u201d</p><p>\u201c\u4e0d\u65b9\u4fbf\uff1f\u5443\u2026\u2026\u201d\u743c\u6069\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff1f\u201d</p><p>\u201c\u665a\u9910\u7684\u65f6\u5019\uff0c\u4e0d\u597d\u610f\u601d\u8ddf\u4f60\u8bf4\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6240\u4ee5\u8fd9\u51e0\u5929\u90fd\u4e0d\u884c\u5566\u3002\u201d</p><p>\u201c\u54e6\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u6211\u4e5f\u6b63\u597d\u4f11\u606f\u51e0\u5929\u3002\u201d</p><p>\u201c\u4f60\u4f11\u606f\u4ec0\u4e48\uff0c\u90a3\u8fb9\u8fd8\u6709\u4e09\u4e2a\u5c0f\u7f8e\u4eba\u7b49\u7740\u4f60\u5462\uff0c\u201d\u73ca\u5609\u7565\u5e26\u6028\u6c14\u5730\u8bf4\uff0c\u201c\u8d76\u5feb\u53bb\u5427\uff0c\u522b\u5728\u8fd9\u91cc\u78e8\u8e6d\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u53bb\uff0c\u6211\u8981\u966a\u7740\u59d0\u59d0\u3002\u201d</p><p>\u201c\u4f46\u59d0\u59d0\u4e0d\u8981\u4f60\u966a\uff0c\u201d\u73ca\u5609\u61d2\u61d2\u5730\u8eba\u5728\u5e8a\u4e0a\uff0c\u7528\u817f\u8f7b\u8f7b\u8e39\u4ed6\uff0c\u201c\u6eda\u5427\u6eda\u5427\uff0c\u8fd9\u51e0\u5929\u90fd\u522b\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u59d0\u59d0\u4f60\u600e\u4e48\u7a81\u7136\u53d8\u5f97\u8fd9\u4e48\u5927\u65b9\uff1f\u201d</p><p>\u201c\u5927\u65b9\u4f60\u4e2a\u5934\uff0c\u201d\u73ca\u5609\u6ca1\u597d\u6c14\u5730\u7838\u4e86\u4e2a\u62b1\u6795\u8fc7\u6765\uff0c\u201c\u8fd9\u4e9b\u5929\u90fd\u88ab\u6211\u4e00\u4e2a\u4eba\u5360\u7740\uff0c\u8fd8\u4e0d\u77e5\u9053\u5979\u4eec\u5fc3\u91cc\u6709\u591a\u6068\u6211\u5462\uff0c\u201d\u5979\u7684\u58f0\u97f3\u7a81\u7136\u53d8\u4f4e\u4e0b\u6765\uff0c\u201c\u6709\u4e00\u4e2a\u827e\u5f25\u8587\u5df2\u7ecf\u591f\u9ebb\u70e6\u4e86\uff0c\u6211\u53ef\u4e0d\u60f3\u518d\u591a\u7ad6\u51e0\u4e2a\u654c\u4eba\u554a\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u73ca\u5609\u540e\u534a\u53e5\u8bdd\u8bf4\u5f97\u592a\u8f7b\uff0c\u743c\u6069\u6ca1\u542c\u6e05\u695a\uff0c\u5f85\u8981\u8ffd\u95ee\uff0c\u5979\u5374\u53c8\u600e\u4e48\u90fd\u4e0d\u80af\u8bf4\u4e86\u3002\u201c\u597d\u56f0\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u51e0\u5929\u767d\u5929\u8d76\u8def\uff0c\u665a\u4e0a\u88ab\u4f60\u6298\u817e\uff0c\u90fd\u6709\u9ed1\u773c\u5708\u4e86\uff0c\u6211\u5f97\u8d76\u5feb\u7761\u7f8e\u5bb9\u89c9\u2014\u2014\u51fa\u53bb\u7684\u65f6\u5019\u8bb0\u5f97\u5e26\u4e0a\u95e8\u3002\u201d</p><p>\u743c\u6069\u5c31\u8fd9\u6837\u88ab\u8d76\u4e86\u51fa\u6765\uff0c\u4ed6\u5728\u73ca\u5609\u7684\u5367\u5ba4\u95e8\u5916\u7ad9\u7740\uff0c\u72b9\u8c6b\u5230\u5e95\u662f\u53bb\u627e\u8299\u857e\u72c4\u8fd8\u662f\u8299\u8389\u5a05\u8fd8\u662f\u838e\u73de\u514b\uff0c\u6216\u8005\u5e72\u8106\u5168\u90e8\u63a8\u5012\u3002\u60f3\u4e86\u534a\u5929\uff0c\u6700\u7ec8\u51b3\u5b9a\u4eca\u665a\u5148\u53bb\u627e\u53cc\u80de\u80ce\uff0c\u81ea\u4ece\u56de\u9634\u9b42\u57ce\u4e4b\u540e\uff0c\u867d\u7136\u548c\u8299\u857e\u72c4\u8fd8\u5077\u5077\u73a9\u8fc7\u51e0\u6b21\uff0c\u4f46\u59d0\u59b9\u4fe9\u4e00\u8d77\u53cc\u98de\u7684\u673a\u4f1a\u5c31\u6ca1\u6709\u4e86\uff0c\u6b63\u597d\u91cd\u6e29\u65e7\u68a6\u3002\u53cc\u80de\u80ce\u867d\u7136\u957f\u76f8\u522b\u65e0\u4e8c\u81f4\uff0c\u88f8\u4f53\u5728\u5e8a\u4e0a\u770b\u4e0d\u51fa\u533a\u522b\uff0c\u4f46\u771f\u6b63\u54c1\u5c1d\u8d77\u6765\u5473\u9053\u8fd8\u662f\u660e\u663e\u4e0d\u540c\u7684\uff0c\u800c\u4e14\u56e0\u4e3a\u6709\u5fc3\u7075\u611f\u5e94\u7684\u5b58\u5728\uff0c\u522b\u6709\u4e00\u756a\u6ecb\u5473\u3002</p><p>\u60f3\u8d77\u5fc3\u7075\u611f\u5e94\uff0c\u743c\u6069\u5012\u662f\u7a81\u7136\u5192\u51fa\u4e2a\u5ff5\u5934\uff1a\u81ea\u4ece\u51fa\u9634\u9b42\u57ce\u4ee5\u6765\uff0c\u4ed6\u4e00\u76f4\u90fd\u662f\u966a\u7740\u73ca\u5609\uff0c\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u53ea\u80fd\u72ec\u5b88\u7a7a\u623f\uff0c\u90a3\u4e48\u5979\u4eec\u4f1a\u4e0d\u4f1a\u2026\u2026\u5514\uff0c\u505a\u67d0\u79cd\u4e0d\u548c\u8c10\u4f46\u5f88\u6709\u7f8e\u611f\u7684\u4e8b\u60c5\u5462\uff0c\u6bd5\u7adf\u5979\u4eec\u4e5f\u548c\u51db\u5728\u4e00\u8d77\u5f85\u8fc7\uff0c\u96be\u4fdd\u4e0d\u53d7\u5f71\u54cd\u3002\u6839\u636e\u4e00\u4efd\u8c03\u67e5\u8d44\u6599\u7684\u663e\u793a\uff0c\u5973\u6027\u5bf9\u767e\u5408\u7684\u62b5\u6297\u529b\uff0c\u539f\u672c\u5c31\u6bd4\u7537\u6027\u5bf9\u73bb\u7483\u7684\u62b5\u6297\u529b\u8981\u5f31\u5f97\u591a\u3002\u5982\u679c\u771f\u662f\u5982\u6b64\u7684\u8bdd\uff0c\u90a3\u5c31\u6709\u4e2a\u95ee\u9898\u4e86\uff0c\u56e0\u4e3a\u5fc3\u7075\u611f\u5e94\u7684\u5b58\u5728\uff0c\u4f1a\u4e0d\u4f1a\u59d0\u59d0\u65e2\u6709\u81ea\u5df1\u7684\u611f\u89c9\uff0c\u53c8\u6709\u59b9\u59b9\u7684\u611f\u89c9\uff0c\u800c\u59b9\u59b9\u65e2\u6709\u81ea\u5df1\u7684\u611f\u89c9\uff0c\u53c8\u6709\u59d0\u59d0\u7684\u611f\u89c9\u2026\u2026\u800c\u8fd9\u79cd\u611f\u89c9\u80fd\u5426\u53e0\u52a0\u5462\uff1f\u59d0\u59d0\u65e2\u6709\u81ea\u5df1\u7684\u611f\u89c9\uff0c\u53c8\u6709\u59b9\u59b9\u7684\u611f\u89c9\uff0c\u4e5f\u5c31\u662f\u53cc\u500d\uff1b\u7136\u540e\u59b9\u59b9\u611f\u5e94\u5230\u59d0\u59d0\u7684\u53cc\u500d\u611f\u89c9\uff0c\u52a0\u4e0a\u81ea\u5df1\u4e00\u4efd\uff0c\u4e8e\u662f\u6210\u4e86\u4e09\u500d\uff1b\u7136\u540e\u59d0\u59d0\u4f1a\u76f8\u5e94\u611f\u53d7\u5230\u56db\u500d\uff0c\u7136\u540e\u59b9\u59b9\u4f1a\u76f8\u5e94\u611f\u53d7\u5230\u4e94\u500d\u2026\u2026\u8fd9\u6837\u4e0b\u53bb\u5c82\u4e0d\u662f\u6210\u4e86\u65e0\u9650\u5faa\u73af\uff1f\u8fd8\u662f\u8bf4\u5feb\u611f\u65e0\u9650\u53e0\u52a0\uff0c\u77ac\u95f4\u5c31\u2026\u2026\u6216\u8005\u867d\u7136\u611f\u89c9\u80fd\u591f\u53e0\u52a0\uff0c\u4f46\u5728\u4f20\u9012\u7684\u8fc7\u7a0b\u4e2d\u4f1a\u6709\u4e00\u5b9a\u7684\u635f\u8017\u7387\uff0c\u90a3\u4e48\u8fd9\u5c31\u5b58\u5728\u201c\u6781\u9650\u201d\u4e86\uff0c\u8981\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u8fd8\u5f97\u5148\u77e5\u9053\u635f\u8017\u7387\u662f\u5426\u56fa\u5b9a\uff0c\u5982\u679c\u56fa\u5b9a\uff0c\u53ef\u4ee5\u7528\u7b49\u6bd4\u6570\u5217\uff0c\u5982\u679c\u4e0d\u56fa\u5b9a\uff0c\u597d\u50cf\u5fc5\u987b\u7528\u5230\u5fae\u79ef\u5206\uff0c\u7136\u800c\u81ea\u5df1\u5df2\u7ecf\u5fd8\u5f97\u5dee\u4e0d\u591a\u4e86\u2026\u2026</p><p>\u597d\u5427\uff0c\u603b\u4e4b\u8fd9\u786e\u5b9e\u662f\u4e2a\u503c\u5f97\u7814\u7a76\u7684\u95ee\u9898\uff0c\u8981\u4e0d\uff0c\u73b0\u5728\u5c31\u5f00\u59cb\u7740\u624b\u6536\u96c6\u6570\u636e\uff1f</p><p>\u4ed6\u5174\u81f4\u52c3\u52c3\uff0c\u6253\u7b97\u60f3\u8981\u53bb\u5b9e\u8df5\u68c0\u9a8c\u4e00\u4e0b\uff0c\u4fbf\u5728\u6b64\u65f6\u9661\u7136\u5fc3\u5934\u8b66\u5146\u6d8c\u8d77\u3002\u6709\u4eba\u6b63\u5728\u9760\u8fd1\u8fd9\u5ea7\u623f\u5b50\uff0c\u89e6\u53d1\u4e86\u6700\u5916\u5c42\u7684\u6cd5\u672f\u8b66\u6212\u3002\u201c\u654c\u4eba\uff1f\u8fd8\u662f\u91ce\u5916\u7684\u602a\u7269\uff1f\u201d\u4ed6\u5fae\u5fae\u4e00\u51db\uff0c\u62bd\u51fa\u6cd5\u6756\u5feb\u6b65\u671d\u95e8\u53e3\u8d70\u53bb\u3002</p><p>\u9b54\u6cd5\u8b66\u62a5\u4e0d\u65ad\u5728\u8111\u4e2d\u54cd\u8d77\uff0c\u663e\u793a\u6765\u4eba\u5df2\u7ecf\u8d70\u5230\u95e8\u524d\uff0c\u7136\u540e\u7b03\u7b03\u5730\u6572\u95e8\u3002\u743c\u6069\u9ed8\u8bf5\u53e3\u4ee4\uff0c\u7136\u540e\u4ed6\u7684\u89c6\u7ebf\u7a7f\u900f\u95e8\u677f\uff0c\u770b\u89c1\u4e86\u5916\u9762\u7684\u4eba\u3002\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5c45\u7136\u8fd8\u662f\u4e2a\u719f\u4eba\uff0c\u5c31\u5728\u524d\u51e0\u5929\u4ed6\u4eec\u8fd8\u5728\u4e00\u8d77\u804a\u5929\u8bf4\u8bdd\u3002</p><p>\u8fdc\u5c71\u57ce\u7684\u80d6\u8001\u5934\u57ce\u4e3b\u3001\u5927\u5974\u96b6\u5546\u4eba\u5f17\u96f7\u65af\u3002</p><p>\u743c\u6069\u6253\u5f00\u95e8\uff0c\u5f17\u96f7\u65af\u89c1\u662f\u4ed6\uff0c\u4e0d\u7531\u5f97\u4e5f\u6014\u4f4f\u4e86\u3002\u743c\u6069\u5c06\u4ed6\u8bf7\u8fdb\u5ba4\u5185\uff0c\u53ea\u89c1\u80d6\u8001\u5934\u72fc\u72c8\u4e0d\u582a\uff0c\u5934\u53d1\u4e71\u7cdf\u7cdf\u7684\uff0c\u8138\u51bb\u5f97\u53d1\u9752\uff0c\u8eab\u4e0a\u7684\u888d\u5b50\u88ab\u626f\u70c2\u4e86\u51e0\u4e2a\u5927\u53e3\u5b50\uff0c\u8138\u4e0a\u6cbe\u6ee1\u4e86\u7070\u5c18\uff0c\u8fd8\u6709\u5c1a\u672a\u5b8c\u5168\u6108\u5408\u7684\u4f24\u75d5\uff0c\u54ea\u91cc\u8fd8\u6709\u534a\u70b9\u57ce\u4e3b\u7684\u6837\u5b50\u3002</p><p>\u5f02\u754c\u5974\u4ec6\u98d8\u8fc7\u6765\uff0c\u9001\u4e0a\u70ed\u5496\u5561\uff0c\u5f17\u96f7\u65af\u559d\u4e86\u51e0\u53e3\uff0c\u6162\u6162\u7f13\u8fc7\u795e\u6765\u3002\u539f\u6765\u5f53\u65e5\u4ed6\u4e00\u89c1\u5230\u6563\u63d0\u519b\u7684\u5929\u7a79\u519b\u56e2\uff0c\u7acb\u523b\u62d4\u817f\u5c31\u8dd1\uff0c\u597d\u5728\u5b98\u90b8\u4e0b\u9762\u662f\u6709\u4e00\u6761\u79d8\u9053\u901a\u5f80\u57ce\u5916\u7684\uff0c\u4e0d\u77e5\u9053\u662f\u5386\u53f2\u4e0a\u54ea\u4e00\u4efb\u57ce\u4e3b\u6240\u4fee\u5efa\u3002\u5f17\u96f7\u65af\u614c\u614c\u5f20\u5f20\uff0c\u5e26\u4e86\u51e0\u4e2a\u62a4\u536b\u4ece\u79d8\u9053\u9003\u8131\uff0c\u539f\u672c\u662f\u60f3\u8fd4\u56de\u585e\u5c14\uff0c\u7ed3\u679c\u6ca1\u8d70\u591a\u8fdc\u5c31\u88ab\u6563\u63d0\u519b\u53d1\u73b0\u3002\u4e00\u8def\u8ffd\u6740\u4e4b\u4e0b\uff0c\u4ed6\u7684\u62a4\u536b\u4eec\u6b7b\u4f24\u6b86\u5c3d\uff0c\u53ea\u6709\u81ea\u5df1\u4fa5\u5e78\u8131\u8eab\uff0c\u5750\u9a91\u4e5f\u6b7b\u4e86\uff0c\u614c\u4e0d\u62e9\u8def\u5730\u9003\u5230\u8fd9\u91cc\uff0c\u6070\u597d\u770b\u89c1\u4e00\u5ea7\u623f\u5b50\uff0c\u4e8e\u662f\u6253\u7b97\u6572\u95e8\u501f\u5bbf\u3002</p><p>\u65e2\u7136\u662f\u719f\u4eba\uff0c\u6536\u7559\u5012\u4e5f\u65e0\u59a8\uff0c\u53cd\u6b63\u7a7a\u623f\u95f4\u591a\u5f97\u662f\u3002\u743c\u6069\u4e00\u8fb9\u547d\u4ee4\u53a8\u623f\u51c6\u5907\u665a\u9910\uff0c\u4e00\u8fb9\u8ba9\u5f02\u754c\u5974\u4ec6\u53bb\u66f4\u8863\u5ba4\u62ff\u5957\u65b0\u8863\u670d\u6765\u2014\u2014\u5e78\u597d\u8fd9\u623f\u5b50\u7684\u8bbe\u8ba1\u8005\u662f\u5965\u6c83\uff0c\u8001\u5deb\u5996\u4e5f\u662f\u4e2a\u80d6\u5b50\uff0c\u66f4\u8863\u5ba4\u91cc\u51c6\u5907\u7684\u8863\u670d\u90fd\u6309\u4ed6\u81ea\u5df1\u7684\u8eab\u6750\u6765\uff0c\u5168\u90fd\u662f\u52a0\u5bbd\u52a0\u80a5\u7248\u7684\uff0c\u5426\u5219\u5f17\u96f7\u65af\u8fd8\u7a7f\u4e0d\u4e0a\u3002</p><p>\u4e0d\u8fc7\uff0c\u600e\u4e48\u603b\u89c9\u5f97\u5ffd\u7565\u4e86\u4ec0\u4e48\u91cd\u8981\u4e8b\u60c5\u4f3c\u7684\u5462\u3002</p><p>\u743c\u6069\u76b1\u76b1\u7709\uff0c\u4e00\u65f6\u95f4\u60f3\u4e0d\u8d77\u6765\uff0c\u4e5f\u4fbf\u653e\u5f03\u3002\u53a8\u623f\u5f88\u5feb\u51c6\u5907\u4e86\u665a\u9910\u7aef\u4e0a\u6765\uff0c\u5f17\u96f7\u65af\u4e00\u8def\u88ab\u8ffd\u6740\uff0c\u5df2\u7ecf\u4e24\u5929\u6ef4\u6c34\u672a\u8fdb\u4e86\uff0c\u65e9\u5c31\u997f\u5f97\u4e24\u773c\u53d1\u7eff\u5149\uff0c\u89c1\u4e86\u98df\u7269\u76f4\u6251\u4e0a\u53bb\uff0c\u8fde\u9910\u5177\u90fd\u4e0d\u7528\uff0c\u5927\u53e3\u5927\u53e3\u4fbf\u541e\u54bd\u8d77\u6765\u3002\u201c\u771f\u662f\u7f8e\u5473\u554a\uff01\u201d\u767e\u5fd9\u4e4b\u4e2d\uff0c\u4ed6\u603b\u7b97\u8bb0\u8d77\u65c1\u8fb9\u8fd8\u6709\u4e2a\u4e3b\u4eba\uff0c\u62ac\u8d77\u8138\u6765\u542b\u542b\u7cca\u7cca\u5730\u79f0\u8d5e\u4e86\u53e5\uff0c\u5634\u91cc\u8fd8\u53fc\u7740\u6839\u9999\u80a0\uff0c\u201c\u592a\u611f\u8c22\u4f60\u4e86\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u201c\u597d\u8bf4\u597d\u8bf4\uff0c\u201d\u743c\u6069\u8d76\u5feb\u56de\u7b54\uff0c\u201c\u60a8\u5403\u6162\u70b9\uff0c\u522b\u564e\u7740\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u60f3\u5f53\u5e74\u6211\u5728\u519b\u961f\u91cc\u7684\u65f6\u5019\u2026\u2026\u201d</p><p>\u8bdd\u5230\u8fd9\u91cc\u5c31\u65ad\u4e86\uff0c\u5f17\u96f7\u65af\u53cc\u624b\u6367\u8d77\u4e00\u7897\u6c64\uff0c\u5495\u565c\u5495\u565c\u5730\u5168\u704c\u4e86\u4e0b\u53bb\uff0c\u7136\u540e\u62b9\u4e86\u62b9\u5634\u5df4\uff0c\u7ee7\u7eed\u5bf9\u4ed8\u5176\u4ed6\u98df\u7269\u3002\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u5403\u9971\u559d\u8db3\uff0c\u5f17\u96f7\u65af\u60a0\u95f2\u5730\u8eba\u5728\u6c99\u53d1\u91cc\u5254\u7740\u7259\uff0c\u539f\u672c\u51bb\u5f97\u53d1\u9752\u7684\u80d6\u8138\u603b\u7b97\u53c8\u6709\u4e86\u7ea2\u5149\u3002\u201c\u60a8\u4e0b\u4e00\u6b65\u6253\u7b97\u600e\u4e48\u529e\u5462\uff1f\u201d\u743c\u6069\u95ee\u4ed6\uff0c\u201c\u51c6\u5907\u56de\u585e\u5c14\u662f\u5417\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u5012\u6709\u70b9\u9ebb\u70e6\u5462\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u9644\u8fd1\u90fd\u6ca1\u6709\u4ec0\u4e48\u57ce\u9547\u6d77\u6e2f\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6211\u51c6\u5907\u5148\u5230\u6b27\u675c\u6797\u57ce\uff0c\u201d\u5f17\u96f7\u65af\u8bf4\uff0c\u201c\u6851\u6bd4\u4e9a\u90a3\u91cc\uff0c\u6211\u8fd8\u6709\u662f\u51e0\u4e2a\u670b\u53cb\u7684\uff0c\u5230\u4e86\u6b27\u675c\u6797\u518d\u53bb\u53f6\u5f18\u6e2f\uff0c\u4e58\u8239\u56de\u585e\u5c14\u5427\u2014\u2014\u8bdd\u8bf4\u60a8\u8fd9\u662f\u51c6\u5907\u53bb\u54ea\u91cc\u5462\uff1f\u201d</p><p>\u201c\u6211\u4eec\u662f\u8981\u53bb\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\uff0c\u4e5f\u8981\u7ecf\u8fc7\u6b27\u675c\u6797\u57ce\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5012\u662f\u987a\u8def\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u90a3\u53ef\u592a\u597d\u4e86\uff0c\u201d\u5f17\u96f7\u65af\u53c8\u60ca\u53c8\u559c\uff0c\u201c\u6211\u6b63\u6101\u7740\u600e\u4e48\u53bb\u6b27\u675c\u6797\u5462\uff0c\u8def\u8fd9\u4e48\u8fdc\uff0c\u6211\u53c8\u6ca1\u4e86\u5750\u9a91\u3002\u8eab\u4e0a\u867d\u7136\u8fd8\u5e26\u7740\u70b9\u94b1\uff0c\u53ef\u8fd9\u6cbf\u9014\u4e5f\u6ca1\u5730\u65b9\u4e70\u9a6c\u554a\u3002\u201d</p><p>\u201c\u90a3\u5c31\u4e00\u8d77\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4eec\u6709\u9a6c\u8f66\u2026\u2026\u5514\uff1f\u201d</p><p>\u8b66\u5146\u518d\u5ea6\u9661\u7136\u51b2\u8fdb\u4ed6\u7684\u610f\u8bc6\uff0c\u6bd4\u65b9\u624d\u5f17\u96f7\u65af\u5f15\u8d77\u7684\u8981\u5f3a\u70c8\u5341\u500d\u751a\u81f3\u767e\u500d\uff0c\u6709\u5389\u5bb3\u89d2\u8272\u6b63\u671d\u8fd9\u91cc\u8feb\u8fd1\uff0c\u800c\u4e14\u4f3c\u4e4e\u6000\u6709\u660e\u663e\u7684\u654c\u610f\u3002\u743c\u6069\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5ffd\u7136\u60f3\u8d77\u6765\u81ea\u5df1\u4e00\u76f4\u5ffd\u7565\u4ec0\u4e48\u4e86\u3002</p><p>\u8fd9\u4e2a\u80d6\u5b50\u662f\u88ab\u4eba\u4e00\u8def\u8ffd\u6740\u8fc7\u6765\u7684\u2014\u2014\u90a3\u4e48\u8ffd\u6740\u8005\u662f\u8c01\uff0c\u73b0\u5728\u5728\u54ea\u91cc\uff1f</p><p>\u743c\u6069\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u610f\u8bc6\u548c\u6574\u5ea7\u623f\u5b50\u7684\u9b54\u6cd5\u9632\u5fa1\u878d\u5408\u4e00\u4f53\uff0c\u7136\u540e\u4ed6\u6e05\u695a\u5730\u770b\u89c1\u4e86\u654c\u4eba\uff1a\u7d2b\u53d1\u5982\u7f0e\u7684\u5973\u5b50\uff0c\u91d1\u5251\u9ed1\u7532\uff0c\u624b\u7ef0\u957f\u67aa\uff0c\u6b63\u7b56\u9a6c\u5f90\u5f90\u800c\u6765\u3002</p></article>"}}}]);
