(window.webpackJsonp=window.webpackJsonp||[]).push([[444],{466:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u56db\u8282 \u5730\u4e3b\u4e4b\u8c0a",content:"<article><h2>\u7b2c\u4e09\u5341\u56db\u8282 \u5730\u4e3b\u4e4b\u8c0a</h2><p>\u201c\u5c45\u7136\u662f\u6563\u63d0\u519b\u7684\u7edf\u5e05\u4eb2\u81ea\u6765\u8ffd\u6740\uff0c\u4f60\u8fd9\u80d6\u5b50\u53ef\u771f\u6709\u9762\u5b50\u3002\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u60f3\u7740\uff0c\u773c\u89d2\u4f59\u5149\u77a5\u7740\u5750\u5728\u6c99\u53d1\u4e2d\u62cd\u7740\u809a\u76ae\u60a0\u95f2\u5254\u7259\u7684\u5f17\u96f7\u65af\uff0c\u4e00\u65f6\u95f4\u62ff\u4e0d\u5b9a\u4e3b\u610f\u662f\u8981\u76f4\u63a5\u628a\u4ed6\u63a8\u51fa\u95e8\u53bb\uff0c\u8fd8\u662f\u8981\u4e3a\u4e86\u4ed6\u6253\u4e0a\u4e00\u67b6\u3002\u524d\u8005\u592a\u6ca1\u4e49\u6c14\uff0c\u600e\u4e48\u8bf4\u81ea\u5df1\u4e0a\u6b21\u53bb\u827e\u5c14\u4f2f\u5854\uff0c\u8fd9\u6b21\u5728\u8fdc\u5c71\u57ce\uff0c\u8fd9\u80d6\u8001\u5934\u90fd\u662f\u70ed\u60c5\u6b3e\u5f85\u7684\uff0c\u800c\u4e14\u9634\u9b42\u57ce\u548c\u585e\u5c14\u6bd5\u7adf\u662f\u76df\u53cb\uff1b\u540e\u8005\u53c8\u592a\u5371\u9669\uff0c\u8fd8\u4f1a\u91cd\u91cd\u5f97\u7f6a\u6563\u5854\u6797\u4f1a\uff0c\u771f\u662f\u4ee4\u4eba\u597d\u751f\u4e3a\u96be\u3002</p><p>\u5f53\u7136\uff0c\u6700\u5173\u952e\u7684\u95ee\u9898\uff0c\u5176\u5b9e\u662f\u743c\u6069\u6ca1\u628a\u63e1\u80fd\u591f\u80dc\u8fc7\u8fd9\u4f4d\u6b4c\u66e6\u96c5\u5c0f\u59d0\u3002\u5426\u5219\u4e8b\u60c5\u5c31\u597d\u529e\u591a\u4e86\uff0c\u8352\u90ca\u91ce\u5916\uff0c\u6708\u9ed1\u98ce\u9ad8\uff0c\u5bf9\u65b9\u597d\u50cf\u53c8\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u76f4\u63a5\u6740\u4eba\u706d\u53e3\u4fbf\u662f\u2026\u2026\u5443\uff0c\u8fd9\u4e48\u6f02\u4eae\u7684\u7f8e\u4eba\u513f\uff0c\u76f4\u63a5\u6740\u4e86\u592a\u53ef\u60dc\uff0c\u4e0d\u5982\u5148\u505a\u70b9\u90aa\u6076\u7684\u4e8b\u60c5\u5427\u3002</p><p>\u201c\u7b49\u7b49\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u633a\u5947\u602a\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u4e00\u8def\u8ffd\u6740\u5f17\u96f7\u65af\uff1f\u201d</p><p>\u743c\u6069\u7709\u5934\u76b1\u8d77\uff0c\u7a81\u7136\u610f\u8bc6\u5230\u8fd9\u5176\u4e2d\u7684\u8e4a\u8df7\u3002\u6b4c\u66e6\u96c5\u662f\u6563\u63d0\u519b\u7684\u7edf\u5e05\uff0c\u5982\u4eca\u521a\u521a\u653b\u5360\u8fdc\u5c71\uff0c\u8bf8\u4e8b\u7e41\u5fd9\uff0c\u6b63\u8be5\u5728\u57ce\u5185\u9547\u5b88\uff0c\u4e3a\u4f55\u4f1a\u5b64\u8eab\u4e00\u4eba\u524d\u6765\u8ffd\u6740\u5f17\u96f7\u65af\uff0c\u8fd9\u80d6\u8001\u5934\u6709\u90a3\u4e48\u91cd\u8981\u4e48\uff1f\u4ed6\u4ee5\u524d\u662f\u8fdc\u5c71\u57ce\u7684\u57ce\u4e3b\uff0c\u4f4d\u9ad8\u6743\u91cd\uff0c\u4f46\u73b0\u5728\u4e0d\u8fc7\u662f\u4e2a\u60f6\u60f6\u9003\u7a9c\u7684\u4e27\u5bb6\u72ac\u7f62\u4e86\uff0c\u5c31\u7b97\u8981\u8ffd\u6740\uff0c\u968f\u4fbf\u8c03\u4e00\u961f\u58eb\u5175\u5c31\u662f\u4e86\uff0c\u4e5f\u65e0\u9700\u4e3b\u5e05\u4eb2\u81ea\u51fa\u9a6c\u5427\u3002</p><p>\u4ed6\u8eab\u4e0a\u6709\u4ec0\u4e48\u91cd\u8981\u4e1c\u897f\uff0c\u6216\u8005\u5b9d\u7269\uff0c\u6216\u8005\u60c5\u62a5\uff0c\u662f\u6563\u5854\u6797\u4f1a\u5fd7\u5728\u5fc5\u5f97\u7684\uff1f\u8fd8\u662f\u8bf4\uff0c\u4ed6\u548c\u6b4c\u66e6\u96c5\u6709\u4ec0\u4e48\u79c1\u4eba\u4ec7\u6028\uff1f</p><p>\u4e00\u5ff5\u53ca\u6b64\uff0c\u4ed6\u9661\u7136\u9192\u89c9\uff0c\u56de\u8fc7\u8138\u6765\u76ef\u7740\u5f17\u96f7\u65af\u3002\u80d6\u8001\u5934\u88ab\u4ed6\u770b\u5f97\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u5c34\u5c2c\u5730\u5475\u5475\u7b11\u4e86\u4e24\u58f0\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u4ed6\u95ee\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u743c\u6069\u6de1\u6de1\u8bf4\u3002</p><p>\u4ed6\u5fc3\u4e2d\u6697\u81ea\u607c\u706b\uff0c\u5df2\u7136\u660e\u767d\u81ea\u5df1\u662f\u88ab\u8fd9\u80d6\u8001\u5934\u6545\u610f\u62d6\u4e0b\u4e86\u6c34\u3002\u60f3\u60f3\u4e5f\u662f\uff0c\u8fd9\u524d\u4e0d\u7740\u6751\u540e\u4e0d\u7740\u5e97\u7684\u8352\u90ca\u91ce\u5916\uff0c\u4e00\u5ea7\u9752\u94dc\u623f\u5b50\u7a81\u5140\u77d7\u7acb\uff0c\u53ea\u8981\u7a0d\u6709\u9b54\u6cd5\u5e38\u8bc6\u7684\u4eba\u90fd\u80fd\u731c\u5230\uff0c\u8fd9\u5341\u6709\u516b\u4e5d\u662f\u67d0\u4f4d\u5deb\u5e08\u7684\u4e34\u65f6\u5c45\u6240\u2014\u2014\u800c\u4e14\u662f\u975e\u5e38\u9ad8\u660e\u7684\u5deb\u5e08\uff0c\u5426\u5219\u505a\u4e0d\u51fa\u8fd9\u623f\u5b50\u3002\u5f17\u96f7\u65af\u81ea\u5df1\u5c31\u7b97\u4e0d\u61c2\u9b54\u6cd5\uff0c\u4f46\u4ed6\u6765\u81ea\u9b54\u6cd5\u5e1d\u56fd\u585e\u5c14\uff0c\u8eab\u4e3a\u827e\u5c14\u5854\u4f2f\u603b\u7763\u7684\u53d4\u7236\uff0c\u51b3\u4e0d\u53ef\u80fd\u6ca1\u6709\u8fd9\u70b9\u5e38\u8bc6\u3002\u4ed6\u5012\u672a\u5fc5\u77e5\u9053\u623f\u5b50\u91cc\u4f4f\u7684\u662f\u743c\u6069\uff0c\u4f46\u65e2\u7136\u660e\u77e5\u8eab\u540e\u6709\u8ffd\u5175\uff0c\u8fd8\u6572\u95e8\u6295\u5bbf\uff0c\u8fd9\u663e\u7136\u5c31\u662f\u5b58\u4e86\u5ac1\u7978\u7684\u5fc3\u601d\uff0c\u6307\u671b\u8fd9\u5ea7\u9752\u94dc\u623f\u5b50\u91cc\u7684\u4e3b\u4eba\u66ff\u4ed6\u6321\u707e\u3002\u81f3\u4e8e\u522b\u4eba\u80fd\u4e0d\u80fd\u6321\u4f4f\uff0c\u5c31\u7b97\u6321\u4f4f\u4e86\u4f1a\u4e0d\u4f1a\u56de\u8fc7\u5934\u627e\u4ed6\u7b97\u5e10\u2026\u2026\u5927\u6982\u662f\u75c5\u6025\u4e71\u6295\u533b\uff0c\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\u3002</p><p>\u5176\u5b9e\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u5018\u82e5\u5f17\u96f7\u65af\u5766\u767d\u5904\u5883\uff0c\u76f4\u63a5\u6073\u6c42\uff0c\u743c\u6069\u672a\u5fc5\u4e0d\u4f1a\u7b54\u5e94\u76f8\u52a9\uff0c\u6bd5\u7adf\u5927\u5bb6\u4e5f\u7b97\u76f8\u8bc6\u4e00\u573a\uff0c\u80fd\u5e2e\u5c31\u5e2e\uff0c\u843d\u4e2a\u4eba\u60c5\u3002\u901a\u8fc7\u8fd9\u4e9b\u5929\u6765\u7684\u4e0d\u65ad\u7ec3\u4e60\uff0c\u743c\u6069\u5bf9\u5f71\u706b\u7684\u638c\u63e1\u8fd0\u7528\u5df2\u7ecf\u7b97\u662f\u5c0f\u6709\u6240\u6210\uff0c\u751a\u81f3\u8fde\u9b54\u7f51\u7b2c\u4e03\u5c42\u90fd\u5df2\u7ecf\u9690\u9690\u80fd\u591f\u8054\u7ed3\u6210\u529f\uff0c\u81ea\u4fe1\u5fc3\u4e5f\u968f\u4e4b\u5927\u589e\uff0c\u6b63\u60f3\u7740\u627e\u4e2a\u5bf9\u624b\u8bd5\u4e0a\u51e0\u62db\uff0c\u770b\u770b\u5a01\u529b\u5982\u4f55\u3002\u4f46\u65e2\u7136\u8fd9\u80d6\u5b50\u8ddf\u81ea\u5df1\u800d\u5fc3\u773c\u2026\u2026\u90a3\u4f60\u5c31\u81ea\u6c42\u591a\u798f\u5427\u3002</p><p>\u6b63\u601d\u5fd6\u7740\uff0c\u5916\u9762\u6b4c\u66e6\u96c5\u5df2\u7ecf\u5230\u4e86\u623f\u5b50\u524d\uff0c\u5979\u9759\u9759\u770b\u4e86\u7247\u523b\uff0c\u5c06\u957f\u67aa\u6302\u56de\u978d\u8fb9\uff0c\u8dc3\u4e0b\u9a6c\uff0c\u8d70\u8fd1\u51e0\u6b65\uff0c\u8ddd\u79bb\u95e8\u53e3\u5927\u7ea6\u5341\u5c3a\u7684\u5730\u65b9\u7ad9\u5b9a\u3002\u201c\u6211\u662f\u6563\u63d0\u5c14\u5821\u7684\u6b4c\u66e6\u96c5\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6df1\u591c\u524d\u6765\uff0c\u5192\u6627\u6253\u6270\uff0c\u8bf7\u4e3b\u4eba\u76f8\u8d50\u4e00\u89c1\u3002\u201d</p><p>\u5979\u58f0\u97f3\u4e0d\u5927\uff0c\u5374\u6e05\u6e05\u695a\u695a\u5730\u4f20\u4e86\u8fdb\u6765\uff0c\u800c\u4e14\u5728\u6574\u4e2a\u5ba2\u5385\u3001\u957f\u5eca\u4e2d\u90fd\u632f\u8361\u56de\u54cd\u3002\u8fd9\u5ea7\u9752\u94dc\u623f\u5b50\u56e0\u4e3a\u9644\u7740\u9b54\u6cd5\u7684\u7f18\u6545\uff0c\u9694\u97f3\u6548\u679c\u6781\u597d\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u5916\u9762\u65e0\u8bba\u5982\u4f55\u5435\u95f9\u4e5f\u4e0d\u4f1a\u5f71\u54cd\u5230\u91cc\u9762\u7684\u4eba\u4f11\u606f\uff0c\u66f4\u4e0d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u5168\u5ba4\u56de\u8361\u7684\u60c5\u5f62\u3002\u4e4b\u6240\u4ee5\u4f1a\u5982\u6b64\uff0c\u552f\u4e00\u7684\u89e3\u91ca\uff0c\u5c31\u662f\u5bf9\u65b9\u7684\u8fd9\u53e5\u8bdd\u5e76\u4e0d\u662f\u5f53\u771f\u4ee5\u201c\u58f0\u97f3\u201d\u7684\u65b9\u5f0f\u4f20\u8fdb\u6765\uff0c\u800c\u662f\u76f4\u63a5\u7528\u9b54\u6cd5\u8fdb\u884c\u5171\u9e23\u3002\u867d\u7136\u662f\u5728\u95e8\u5916\u8bf4\u8bdd\uff0c\u5176\u5b9e\u5c31\u76f8\u5f53\u4e8e\u662f\u5728\u5ba4\u5185\uff0c\u4f30\u8ba1\u662f\u501f\u52a9\u67d0\u79cd\u9b54\u6cd5\u9053\u5177\u7684\u6548\u679c\u4e86\u3002</p><p>\u743c\u6069\u671d\u5f17\u96f7\u65af\u770b\u53bb\uff0c\u53d1\u73b0\u80d6\u8001\u5934\u4e5f\u6b63\u770b\u7740\u4ed6\uff0c\u4e00\u4e2a\u662f\u5fc3\u4e2d\u6709\u6570\uff0c\u4e00\u4e2a\u662f\u4eba\u8001\u6210\u7cbe\uff0c\u76ee\u5149\u4e00\u9519\uff0c\u987f\u65f6\u4fbf\u5fc3\u4e2d\u4e86\u7136\uff0c\u5f7c\u6b64\u90fd\u5475\u5475\u7b11\u4e86\u8d77\u6765\u3002\u201c\u770b\u6765\u7ed9\u60a8\u6dfb\u9ebb\u70e6\u4e86\u5462\uff0c\u201d\u5f17\u96f7\u65af\u8bf4\uff0c\u201c\u8fd9\u5973\u4eba\u53ef\u771f\u662f\u56fa\u6267\u554a\uff0c\u5c45\u7136\u4e00\u76f4\u8ffd\u5230\u8fd9\u91cc\u3002\u201d</p><p>\u201c\u4e0d\u9ebb\u70e6\uff0c\u201d\u743c\u6069\u4e5f\u5fae\u7b11\uff0c\u201c\u6709\u5ba2\u4e0a\u95e8\uff0c\u6211\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u4fbf\u662f\u3002\u201d</p><p>\u6b64\u65f6\u6b63\u5728\u623f\u95f4\u91cc\u4f11\u606f\u7684\u56db\u4e2a\u5973\u5b69\u5b50\u4e5f\u95fb\u58f0\u51fa\u6765\uff0c\u5979\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u53c8\u770b\u7740\u5750\u5728\u6c99\u53d1\u4e2d\u7684\u5f17\u96f7\u65af\uff0c\u4e0d\u660e\u6240\u4ee5\u3002\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u73ca\u5609\u95ee\uff0c\u201c\u8c01\u5728\u8bf4\u8bdd\uff1f\u8fd9\u4f4d\u5148\u751f\u53c8\u662f\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4eca\u665a\u6bd4\u8f83\u70ed\u95f9\uff0c\u6709\u4e24\u4e2a\u5ba2\u4eba\u7f62\u4e86\u3002\u201d</p><p>\u4ed6\u8ba9\u73ca\u5609\u548c\u53cc\u80de\u80ce\u5148\u56de\u623f\u95f4\u53bb\uff0c\u53ea\u7559\u4e0b\u4e86\u838e\u73de\u514b\u3002\u867d\u7136\u4e0d\u60f3\u53d1\u751f\u51b2\u7a81\uff0c\u4f46\u8fd9\u79cd\u4e8b\u60c5\u672a\u5fc5\u80fd\u7531\u5f97\u81ea\u5df1\uff0c\u8be5\u505a\u7684\u51c6\u5907\u8fd8\u662f\u8981\u505a\u3002\u73ca\u5609\u57fa\u672c\u6ca1\u6709\u6218\u6597\u529b\uff0c\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u4e5f\u53ef\u4ee5\u5ffd\u7565\uff0c\u4e07\u4e00\u771f\u6253\u8d77\u6765\uff0c\u552f\u6709\u9b45\u9b54\u80fd\u5e2e\u5f97\u4e0a\u624b\u3002</p><p>\u773c\u770b\u51c6\u5907\u5c31\u7eea\uff0c\u4ed6\u9ed8\u8bf5\u51fa\u53e3\u4ee4\uff0c\u8ba9\u6b63\u95e8\u81ea\u52a8\u655e\u5f00\u3002\u201c\u8bf7\u8fdb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u80fd\u6709\u8fd9\u6837\u7f8e\u4e3d\u7684\u5c0f\u59d0\u6df1\u591c\u9020\u8bbf\uff0c\u5b9e\u5728\u662f\u6211\u7684\u8363\u5e78\u3002\u201d</p><p>\u201c\u975e\u5e38\u611f\u8c22\u3002\u201d\u6b4c\u66e6\u96c5\u8bf4\uff0c\u7136\u540e\u8d70\u4e86\u8fdb\u6765\uff0c\u94c1\u8d28\u6218\u9774\u8e0f\u5728\u9752\u94dc\u5730\u677f\u4e0a\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\u3002\u5979\u8fdb\u5165\u5ba2\u5385\uff0c\u5148\u770b\u89c1\u743c\u6069\u548c\u838e\u73de\u514b\uff0c\u4f3c\u4e4e\u5fae\u89c9\u60ca\u8bb6\uff0c\u63a5\u7740\u770b\u89c1\u5750\u5728\u6c99\u53d1\u91cc\u7684\u5f17\u96f7\u65af\uff0c\u5979\u76ef\u7740\u80d6\u8001\u5934\u770b\u4e86\u7247\u523b\uff0c\u7136\u540e\u5c06\u76ee\u5149\u518d\u5ea6\u8f6c\u56de\u743c\u6069\u8138\u4e0a\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u3002\u201d\u743c\u6069\u81ea\u6211\u4ecb\u7ecd\u3002</p><p>\u6b4c\u66e6\u96c5\u5fae\u5fae\u8eac\u8eab\uff0c\u7b97\u662f\u884c\u793c\uff0c\u201c\u5e78\u4f1a\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6ca1\u6599\u5230\u6211\u4eec\u4f1a\u5728\u8fd9\u91cc\u518d\u6b21\u76f8\u9047\u3002\u201d</p><p>\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u518d\u6b21\u76f8\u9047\uff1f\u6211\u4eec\u4ee5\u524d\u89c1\u8fc7\u5417\uff1f\u201d</p><p>\u201c\u89c1\u8fc7\uff0c\u201d\u6b4c\u66e6\u96c5\u8bf4\uff0c\u201c\u56db\u5929\u524d\uff0c\u5728\u8fdc\u5c71\u57ce\uff0c\u60a8\u5f53\u65f6\u5c31\u548c\u8fd9\u4f4d\u5f17\u96f7\u65af\u5148\u751f\u7ad9\u5728\u4e00\u8d77\u3002\u201d</p><p>\u201c\u2026\u2026\u5c0f\u59d0\uff0c\u4f60\u7684\u89c2\u5bdf\u529b\u548c\u8bb0\u5fc6\u529b\u771f\u662f\u60ca\u4eba\u3002\u201d</p><p>\u5f53\u65f6\u8fdc\u5c71\u57ce\u5934\u4e0a\uff0c\u5c11\u8bf4\u4e5f\u6709\u51e0\u767e\u53f7\u4eba\uff0c\u743c\u6069\u548c\u5f17\u96f7\u65af\u90fd\u662f\u5d07\u5c1a\u5b89\u5168\u7b2c\u4e00\u7684\u4eba\uff0c\u7ad9\u5f97\u4f4d\u7f6e\u65e2\u4e0d\u9760\u524d\uff0c\u4e5f\u4e0d\u663e\u773c\uff0c\u800c\u662f\u8eb2\u5728\u4eba\u5806\u91cc\u3002\u6b4c\u66e6\u96c5\u5f53\u65f6\u5728\u57ce\u4e0b\uff0c\u76f8\u8ddd\u5c11\u8bf4\u4e5f\u6709\u4e24\u767e\u6b65\u7684\u8ddd\u79bb\uff0c\u5979\u4e5f\u4e0d\u8fc7\u5c31\u662f\u671d\u57ce\u4e0a\u626b\u4e86\u4e24\u773c\uff0c\u5c45\u7136\u5c31\u628a\u743c\u6069\u7ed9\u8bb0\u4f4f\u4e86\uff0c\u8fd9\u53ef\u5b9e\u5728\u662f\u592a\u5f3a\u4e86\u3002\u4e0d\u8fc7\u8fd9\u5c31\u80fd\u7b97\u662f\u201c\u89c1\u8fc7\u201d\u4e48\uff1f\u597d\u5427\uff0c\u743c\u6069\u786e\u5b9e\u770b\u89c1\u4e86\u5979\uff0c\u5979\u4e5f\u770b\u89c1\u4e86\u743c\u6069\uff0c\u8fd9\u81ea\u7136\u5c31\u662f\u89c1\u8fc7\u4e86\u2026\u2026</p><p>\u4f69\u670d\u5f52\u4f69\u670d\uff0c\u7136\u800c\u743c\u6069\u7acb\u523b\u53d1\u73b0\u4e0d\u5999\u3002\u5bf9\u65b9\u8bb0\u5f97\u81ea\u5df1\uff0c\u8bb0\u5f97\u81ea\u5df1\u5f53\u65f6\u662f\u548c\u8fdc\u5c71\u57ce\u7684\u5b88\u519b\u4e00\u8d77\u7ad9\u5728\u57ce\u5934\uff0c\u800c\u4e14\u7279\u5730\u70b9\u660e\u81ea\u5df1\u201c\u5f53\u65f6\u5c31\u548c\u8fd9\u4f4d\u5f17\u96f7\u65af\u5148\u751f\u7ad9\u5728\u4e00\u8d77\u201d\uff0c\u504f\u504f\u81ea\u5df1\u73b0\u5728\u53c8\u6536\u7559\u4e86\u5f17\u96f7\u65af\u2014\u2014\u6613\u5730\u800c\u5904\u7684\u8bdd\uff0c\u743c\u6069\u4f1a\u600e\u4e48\u60f3\uff1f\u4f1a\u600e\u4e48\u5224\u65ad\u8fd9\u4e24\u4eba\u7684\u5173\u7cfb\uff1f</p><p>\u5f88\u663e\u7136\uff0c\u743c\u6069\u7684\u731c\u6d4b\uff0c\u6b63\u662f\u5bf9\u65b9\u6b64\u65f6\u5fc3\u4e2d\u6240\u60f3\uff0c\u5979\u7684\u624b\u5df2\u7ecf\u6084\u6084\u5730\u6309\u4e0a\u4e86\u8170\u95f4\u7684\u5251\u67c4\u3002\u201c\u60a8\u662f\u7ea2\u888d\u5deb\u5e08\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u4e0d\u662f\u3002\u201d\u743c\u6069\u8d76\u5feb\u5426\u8ba4\u3002</p><p>\u6b4c\u66e6\u96c5\u70b9\u70b9\u5934\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u7a81\u7136\u4e00\u9635\u5c16\u9510\u523a\u8033\u7684\u560e\u560e\u602a\u7b11\u58f0\u4ece\u5979\u8170\u95f4\u53d1\u51fa\u6765\uff0c\u201c\u522b\u542c\u4ed6\u7684\u6b4c\u66e6\u96c5\uff0c\u4ed6\u5728\u9a97\u4f60\uff0c\u4ed6\u5c31\u662f\u4e2a\u7ea2\u888d\u5deb\u5e08\u3002\u201d</p><p>\u743c\u6069\u65e2\u60ca\u4e14\u6012\uff0c\u4e0d\u77e5\u9053\u662f\u8c01\u5728\u80e1\u8bf4\u516b\u9053\uff0c\u6307\u9e7f\u4e3a\u9a6c\uff0c\u4f46\u6b4c\u66e6\u96c5\u663e\u7136\u4e0d\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u201c\u786e\u5b9a\u5417\uff0c\u683c\u9c81\uff1f\u201d\u5979\u76ef\u7740\u743c\u6069\uff0c\u53e3\u4e2d\u53d1\u95ee\u3002</p><p>\u201c\u786e\u5b9a\uff0c\u201d\u90a3\u4e2a\u96be\u542c\u7684\u58f0\u97f3\u518d\u5ea6\u4f20\u51fa\uff0c\u743c\u6069\u603b\u7b97\u53d1\u73b0\u5b83\u7684\u6765\u6e90\u662f\u6b4c\u66e6\u96c5\u8170\u95f4\u7684\u91d1\u5251\uff0c\u201c\u6211\u6e05\u695a\u95fb\u5230\u4e86\u9b54\u6cd5\u523a\u9752\u7684\u5473\u9053\uff0c\u55ef\u54fc\uff0c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u3001\u4e94\u2014\u2014\u597d\u5bb6\u4f19\uff0c\u4e94\u4e2a\u9b54\u6cd5\u523a\u9752\uff0c\u8fd9\u5c0f\u5b50\u4f4d\u9636\u4e0d\u4f4e\u5462\uff0c\u5e94\u8be5\u662f\u4e2a\u73af\u6cd5\u5bfc\u5e08\u3002\u201d</p><p>\u743c\u6069\u77a0\u76ee\u7ed3\u820c\uff0c\u65e0\u8a00\u4ee5\u5bf9\u3002</p><p>\u4ed6\u8eab\u4e0a\u786e\u5b9e\u662f\u6709\u9b54\u6cd5\u523a\u9752\uff0c\u8fd9\u662f\u7ea2\u888d\u5deb\u5e08\u7684\u4e24\u5927\u72ec\u95e8\u79d8\u6280\u4e4b\u4e00\uff08\u53e6\u4e00\u4e2a\u662f\u201c\u73af\u6cd5\u201d\uff09\uff0c\u56e0\u4e3a\u4e0a\u6b21\u9634\u9b42\u57ce\u548c\u585e\u5c14\u7ed3\u76df\uff0c\u8054\u624b\u51fb\u6740\u5deb\u5996\u4e4b\u795e\u7ef4\u6c99\u4f26\u7684\u5316\u8eab\u4e00\u5f79\uff0c\u743c\u6069\u51fa\u751f\u5165\u6b7b\uff0c\u6ca1\u6709\u529f\u52b3\u4e5f\u6709\u82e6\u52b3\uff0c\u8fd9\u624d\u83b7\u5f97\u7684\u5956\u8d4f\u3002\u95ee\u9898\u662f\u4ed6\u7684\u4e94\u4e2a\u523a\u9752\u5206\u522b\u7eb9\u5728\u56db\u80a2\u548c\u80f8\u53e3\u4e0a\uff0c\u9694\u7740\u8863\u670d\u6839\u672c\u770b\u4e0d\u89c1\uff0c\u81f3\u4e8e\u8fd9\u67c4\u91d1\u5251\u8bf4\u4ec0\u4e48\u201c\u95fb\u5230\u4e86\u9b54\u6cd5\u523a\u9752\u7684\u5473\u9053\u201d\uff0c\u7b80\u76f4\u5c31\u662f\u532a\u5937\u6240\u601d\u2014\u2014\u9b54\u6cd5\u523a\u9752\u600e\u4e48\u4f1a\u6709\u5473\u9053\uff0c\u53c8\u4e0d\u662f\u7528\u6cb9\u6f06\u55b7\u6d82\u7684\u2026\u2026</p><p>\u597d\u5427\uff0c\u8fd9\u4e0b\u5b50\u9ebb\u70e6\u66f4\u5927\u4e86\u3002</p><p>\u201c\u539f\u6765\u9601\u4e0b\u662f\u4e00\u4f4d\u73af\u6cd5\u5bfc\u5e08\uff0c\u201d\u6b4c\u66e6\u96c5\u8bed\u6c14\u5e73\u6de1\uff0c\u610f\u6001\u95f2\u95f2\uff0c\u773c\u4e2d\u7684\u950b\u8292\u5374\u662f\u51cc\u5389\u903c\u4eba\uff0c\u201c\u5931\u656c\u4e86\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e5f\u5c31\u4e0d\u5fc5\u591a\u8d39\u53e3\u820c\uff0c\u6d6a\u8d39\u5f7c\u6b64\u65f6\u95f4\u3002\u73b0\u5728\u6211\u8981\u5e26\u8d70\u8fd9\u4f4d\u5f17\u96f7\u65af\u5148\u751f\uff0c\u4e0d\u77e5\u60a8\u610f\u4e0b\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u77a5\u4e86\u773c\u5f17\u96f7\u65af\uff0c\u6b63\u60f3\u8bf4\u201c\u6211\u6ca1\u610f\u89c1\u201d\uff0c\u80d6\u8001\u5934\u7a81\u7136\u4ece\u6c99\u53d1\u91cc\u8df3\u4e86\u8d77\u6765\uff0c\u4ee5\u654f\u6377\u65e0\u6bd4\u7684\u6b65\u4f10\u5f80\u540e\u9003\u5f00\uff0c\u201c\u94a5\u5319\u6211\u5df2\u7ecf\u7ed9\u4e86\u4ed6\uff01\u201d\u4ed6\u51b2\u7740\u6b4c\u66e6\u96c5\u5927\u58f0\u56b7\uff0c\u201c\u4f60\u627e\u4ed6\u8981\u5c31\u597d\uff0c\u4e0d\u8981\u627e\u6211\uff01\u201d</p><p>\u6211\u9760\uff01</p><p>\u743c\u6069\u6012\u706b\u4e0a\u6d8c\uff0c\u6b63\u5f85\u8f6c\u8eab\u53bb\u627e\u8fd9\u6b7b\u80d6\u5b50\u7b97\u5e10\uff0c\u7136\u800c\u65c1\u8fb9\u65e0\u5f62\u7684\u51db\u51bd\u6c14\u606f\u903c\u6765\uff0c\u4ee4\u4ed6\u534a\u70b9\u4e0d\u6562\u5206\u795e\u3002\u201c\u6211\u6839\u672c\u4e0d\u77e5\u9053\u4ec0\u4e48\u94a5\u5319\uff0c\u201d\u4ed6\u6309\u637a\u6027\u5b50\uff0c\u89e3\u91ca\u8bf4\uff0c\u201c\u95fb\u6240\u672a\u95fb\u3002\u201d</p><p>\u6b4c\u66e6\u96c5\u76ee\u5149\u95ea\u52a8\uff0c\u89c6\u7ebf\u6765\u56de\u5728\u743c\u6069\u548c\u5f17\u96f7\u65af\u8eab\u4e0a\u6253\u91cf\uff0c\u6700\u7ec8\u6447\u4e86\u6447\u5934\uff0c\u201c\u62b1\u6b49\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4ea4\u51fa\u94a5\u5319\uff0c\u6211\u4e0d\u559c\u6b22\u6740\u4eba\u3002\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u6211\u4e5f\u4e0d\u559c\u6b22\u6740\u4eba\u3002\u201d\u4ed6\u8bf4\uff0c\u9661\u7136\u540e\u9000\uff0c\u540c\u65f6\u63b7\u51fa\u4e86\u4e24\u679a\u8428\u74e6\u68cb\u5b50\u3002</p><p>\u4e24\u4e2a\u5168\u526f\u6b66\u88c5\u7684\u5353\u5c14\u6218\u58eb\u9b54\u50cf\u6325\u5251\u6301\u76fe\uff0c\u4e00\u5de6\u4e00\u53f3\u671d\u7740\u6b4c\u66e6\u96c5\u5939\u51fb\u800c\u6765\u3002\u743c\u6069\u9000\u5230\u5899\u8fb9\uff0c\u9ed8\u8bf5\u53e3\u4ee4\uff0c\u53cc\u624b\u5f80\u4e0b\u865a\u7a7a\u4e00\u6309\uff0c\u539f\u672c\u655e\u5f00\u7684\u6b63\u95e8\u7acb\u523b\u53c8\u81ea\u52a8\u5173\u95ed\u8d77\u6765\uff0c\u5c06\u654c\u4eba\u7684\u9000\u8def\u5c01\u6b7b\u3002\u838e\u73de\u514b\u65e9\u6709\u51c6\u5907\uff0c\u4e00\u89c1\u743c\u6069\u540e\u9000\uff0c\u5de6\u624b\u638c\u5fc3\u7acb\u523b\u5e7b\u51fa\u6dec\u6bd2\u77ed\u5251\uff0c\u4e00\u987f\u8db3\u542f\u52a8\u4e86\u9774\u5b50\u7684\u52a0\u901f\u6548\u679c\uff0c\u95ea\u7535\u822c\u7ed5\u5230\u6b4c\u66e6\u96c5\u80cc\u540e\uff0c\u7167\u7740\u94e0\u7532\u7684\u7a7a\u9699\u90e8\u4f4d\u523a\u53bb\u3002</p><p>\u201c\u9535\uff01\u201d</p><p>\u773c\u770b\u5c31\u8981\u4e00\u51fb\u5f97\u624b\uff0c\u6b4c\u66e6\u96c5\u7a81\u7136\u4fa7\u8eab\uff0c\u9b45\u9b54\u7684\u77ed\u5251\u987f\u65f6\u4fbf\u504f\u4e86\u65b9\u4f4d\uff0c\u6b63\u523a\u5728\u94e0\u7532\u7684\u7532\u53f6\u4e0a\uff0c\u8fde\u70b9\u75d5\u8ff9\u90fd\u6ca1\u7559\u4e0b\uff0c\u53cd\u800c\u6709\u80a1\u602a\u5f02\u7684\u529b\u9053\u500f\u5ffd\u53cd\u5f39\u56de\u6765\uff0c\u838e\u73de\u514b\u7684\u624b\u8155\u5982\u906d\u9488\u624e\uff0c\u523a\u75db\u5f7b\u9aa8\uff0c\u9669\u4e9b\u8fde\u77ed\u5251\u90fd\u63e1\u4e0d\u4f4f\u3002\u5979\u5927\u60ca\u4e4b\u4e0b\uff0c\u614c\u5fd9\u540e\u9000\uff0c\u6b4c\u66e6\u96c5\u4e5f\u4e0d\u8ffd\u51fb\uff0c\u53f3\u624b\u81ea\u8170\u95f4\u62d4\u51fa\u91d1\u5251\uff0c\u53cc\u624b\u63e1\u67c4\uff0c\u671d\u7740\u743c\u6069\u4e00\u6b65\u6b65\u903c\u8fd1\u3002</p><p>\u201c\u6740\u554a\u6740\u554a\u6740\u554a\u6740\u554a\u2014\u2014\u201d\u90a3\u4e2a\u96be\u542c\u7684\u58f0\u97f3\u5728\u91d1\u5251\u4e2d\u75af\u72c2\u53eb\u56a3\u3002</p><p>\u201c\u95ed\u5634\uff0c\u683c\u9c81\uff01\u201d</p><p>\u6b4c\u66e6\u96c5\u5389\u58f0\u5475\u65a5\u7740\uff0c\u67b6\u4f4f\u4e00\u5c0a\u6218\u58eb\u9b54\u50cf\u4ece\u5de6\u8fb9\u53d1\u51fa\u7684\u653b\u51fb\uff0c\u7136\u540e\u987a\u52bf\u6325\u5251\u4e0b\u5288\uff0c\u6218\u58eb\u9b54\u50cf\u51c6\u786e\u5730\u7528\u76fe\u724c\u8fce\u5411\u8fd9\u4e00\u5251\u3002\u4f46\u5c31\u5728\u5251\u76fe\u76f8\u4ea4\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u6b4c\u66e6\u96c5\u9661\u7136\u4f4e\u559d\u4e86\u58f0\uff0c\u6234\u7740\u94c1\u624b\u5957\u7684\u53cc\u624b\u4e0a\u7206\u53d1\u51fa\u523a\u773c\u7684\u78a7\u5149\uff0c\u8fde\u5e26\u91d1\u5251\u90fd\u88ab\u67d3\u5f97\u6bb7\u6bb7\u53d1\u7eff\uff0c\u201c\u7830\u201d\u5730\u5c06\u9b54\u50cf\u7684\u76fe\u724c\u786c\u751f\u751f\u7834\u6210\u4e24\u534a\uff0c\u5251\u52bf\u4e0d\u8870\uff0c\u91cd\u91cd\u5288\u4e2d\u4e86\u80a9\u90e8\uff0c\u5c06\u5b83\u6267\u5251\u7684\u53f3\u81c2\u780d\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u53f3\u8fb9\uff01\u201d\u91d1\u5251\u53d1\u51fa\u8b66\u544a\u3002</p><p>\u6b4c\u66e6\u96c5\u7eb5\u8eab\u8dc3\u8d77\uff0c\u6b63\u597d\u8eb2\u8fc7\u53e6\u4e00\u5c0a\u9b54\u50cf\u7684\u653b\u51fb\uff0c\u5979\u5168\u8eab\u94e0\u7532\uff0c\u52a8\u4f5c\u5374\u8f7b\u76c8\u5f97\u4eff\u4f5b\u98de\u9e1f\uff0c\u4e0b\u5760\u65f6\u4e00\u811a\u91cd\u91cd\u8e0f\u5728\u9b54\u50cf\u7684\u76fe\u724c\u4e0a\uff0c\u501f\u7740\u53cd\u5f39\u4e4b\u529b\u6574\u4e2a\u4eba\u4eff\u4f5b\u79bb\u5f26\u4e4b\u7bad\u822c\u524d\u51b2\uff0c\u671d\u7740\u743c\u6069\u731b\u6251\u3002</p><p>\u201c\u86db\u7f51\uff01\u201d</p><p>\u968f\u7740\u743c\u6069\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4e8c\u5341\u56db\u9053\u7f06\u7ef3\u7c97\u7ec6\u3001\u94f6\u5149\u95ea\u95ea\u7684\u86db\u4e1d\u4ece\u5929\u82b1\u677f\u3001\u5730\u9762\u548c\u5899\u58c1\u7684\u6697\u5b54\u4e2d\u540c\u65f6\u6fc0\u5c04\u800c\u51fa\uff0c\u77ac\u95f4\u4ea4\u9519\u7f16\u7ec7\u6210\u4e00\u5f20\u5de8\u5927\u7684\u86db\u7f51\uff0c\u4eff\u4f5b\u4e00\u9762\u5899\u58c1\u822c\u6321\u5728\u743c\u6069\u7684\u9762\u524d\u3002\u6b4c\u66e6\u96c5\u8eab\u5728\u534a\u7a7a\uff0c\u65e0\u4ece\u8eb2\u907f\uff0c\u4e00\u5934\u649e\u8fdb\u4e86\u8fd9\u5f20\u86db\u7f51\uff0c\u7136\u540e\u5c31\u50cf\u53ea\u5c0f\u866b\u822c\u88ab\u7262\u7262\u7c98\u4f4f\u4e86\u3002\u743c\u6069\u5f97\u610f\u5730\u7b11\u8d77\u6765\uff0c\u53d6\u6d88\u4e86\u5df2\u7ecf\u84c4\u52bf\u5f85\u53d1\u7684\u77f3\u5316\u672f\u3002</p><p>\u201c\u5c0f\u59d0\uff0c\u4e0d\u8981\u5728\u4e00\u540d\u5deb\u5e08\u7684\u4f4f\u6240\u548c\u4ed6\u53d1\u751f\u51b2\u7a81\uff0c\u8fd9\u662f\u5e38\u8bc6\u554a\u3002\u201d</p><p>\u5965\u6c83\u7ed9\u4ed6\u7684\u8fd9\u5ea7\u9752\u94dc\u623f\u5b50\uff0c\u4e0d\u4ec5\u4ec5\u662f\u7528\u6765\u906e\u98ce\u6321\u96e8\u7684\uff0c\u8001\u5deb\u5996\u8fd8\u7279\u5730\u505a\u4e86\u5f3a\u5316\uff0c\u589e\u52a0\u4e86\u7a7a\u95f4\u67b7\u9501\u548c\u94dc\u5899\u94c1\u58c1\u4e24\u9053\u9632\u5fa1\u3002\u524d\u8005\u80fd\u591f\u5c01\u95ed\u4e00\u5207\u4f20\u9001\u9b54\u6cd5\uff0c\u8ba9\u5916\u9762\u7684\u654c\u4eba\u65e0\u6cd5\u76f4\u63a5\u4f20\u9001\u5230\u5ba4\u5185\u8fdb\u884c\u7a81\u88ad\uff0c\u5f53\u7136\u91cc\u9762\u7684\u4eba\u4e5f\u6ca1\u6cd5\u4f20\u9001\u51fa\u53bb\uff1b\u540e\u8005\u5219\u662f\u7528\u9b54\u6cd5\u628a\u4e00\u5ea7\u623f\u5b50\u53d8\u6210\u575a\u56fa\u7684\u8981\u585e\u5821\u5792\u2014\u2014\u6240\u8c13\u201c\u94dc\u5899\u94c1\u58c1\u201d\uff0c\u5e76\u4e0d\u662f\u8bf4\u5f53\u771f\u628a\u5899\u58c1\u53d8\u6210\u94dc\u94c1\uff0c\u8fd9\u623f\u5b50\u672c\u6765\u5c31\u662f\u9752\u94dc\u505a\u7684\u3002\u94dc\u5899\u94c1\u58c1\u7684\u771f\u6b63\u542b\u4e49\uff0c\u662f\u4e00\u7cfb\u5217\u9b54\u6cd5\u9632\u5fa1\u6548\u679c\u7684\u603b\u79f0\uff0c\u56f0\u4f4f\u6b4c\u66e6\u96c5\u7684\u8fd9\u4e2a\u86db\u7f51\u672f\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5f53\u7136\uff0c\u5bfb\u5e38\u7684\u86db\u7f51\u672f\u7edd\u5bf9\u56f0\u4e0d\u4f4f\u8fd9\u6837\u5f3a\u529b\u7684\u6218\u58eb\uff0c\u53ea\u6709\u8fd9\u79cd\u7ecf\u8fc7\u5965\u6c83\u5f3a\u5316\u7684\u7248\u672c\u624d\u80fd\u505a\u5230\u3002</p><p>\u743c\u6069\u4e4b\u6240\u4ee5\u4f1a\u8bf7\u6b4c\u66e6\u96c5\u8fdb\u623f\u5b50\uff0c\u4e00\u65b9\u9762\u81ea\u7136\u662f\u4ee5\u793c\u76f8\u5f85\uff0c\u5e0c\u671b\u80fd\u591f\u5ba2\u5ba2\u6c14\u6c14\uff0c\u53cc\u65b9\u5750\u4e0b\u6765\u8c08\u8c08\uff0c\u548c\u5e73\u89e3\u51b3\u95ee\u9898\u662f\u6700\u597d\uff1b\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u505a\u4e86\u7ffb\u8138\u52a8\u624b\u7684\u6253\u7b97\u3002\u771f\u8981\u6253\u8d77\u6765\u7684\u8bdd\uff0c\u5728\u81ea\u5df1\u7684\u5730\u76d8\uff0c\u81ea\u5df1\u7684\u4e3b\u573a\uff0c\u6709\u5404\u79cd\u9632\u5fa1\u9b54\u6cd5\u7684\u534f\u52a9\uff0c\u80dc\u7b97\u603b\u662f\u4f1a\u5927\u589e\u3002\u800c\u73b0\u5728\u7684\u4e8b\u5b9e\uff0c\u4f3c\u4e4e\u4e5f\u8bc1\u660e\u4e86\u4ed6\u7684\u5224\u65ad\u3002</p><p>\u5728\u5fc3\u4e2d\u5bf9\u6b7b\u80d6\u5b50\u8001\u5e08\u8868\u793a\u611f\u8c22\u4e4b\u540e\uff0c\u743c\u6069\u5f00\u59cb\u8003\u8651\u5982\u4f55\u5904\u7f6e\u773c\u524d\u7684\u4fd8\u864f\u3002</p></article>"}}}]);
