(window.webpackJsonp=window.webpackJsonp||[]).push([[445],{467:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e94\u8282 \u6218\u7565\u8f6c\u8fdb",content:"<article><h2>\u7b2c\u4e09\u5341\u4e94\u8282 \u6218\u7565\u8f6c\u8fdb</h2><p>\u743c\u6069\u4e0a\u8f88\u5b50\u559c\u6b22\u73a9\u7535\u8111\u6e38\u620f\uff0c\u5c24\u5176\u662f\u7b56\u7565\u56de\u5408\u5236\u6e38\u620f\uff0c\u6700\u5178\u578b\u7684\u81ea\u7136\u662f\u4e09\u56fd\u7c7b\u578b\uff0c\u4f8b\u5982\u4e09\u56fd\u5fd7\u4e09\u56fd\u7fa4\u82f1\u4f20\u4e4b\u7c7b\u3002\u5728\u8fd9\u4e9b\u6e38\u620f\u91cc\uff0c\u6293\u5230\u4e00\u4e2a\u654c\u65b9\u6b66\u5c06\u2014\u2014\u5c24\u5176\u662f\u5389\u5bb3\u89d2\u8272\uff0c\u9996\u5148\u8981\u505a\u7684\u662f\u62db\u964d\u3002\u5982\u679c\u62db\u964d\u6210\u529f\uff0c\u81ea\u7136\u7686\u5927\u6b22\u559c\uff1b\u5982\u679c\u62db\u964d\u4e0d\u6210\uff0c\u90a3\u4e48\u5927\u81f4\u6709\u4e09\u79cd\u9009\u62e9\uff1a\u65a9\u6740\u3001\u91ca\u653e\u548c\u56da\u7981\u3002</p><p>\u201c\u653e\u662f\u7edd\u5bf9\u653e\u4e0d\u5f97\uff0c\u56da\u7981\u4e5f\u592a\u5192\u9669\u3002\u5982\u679c\u81ea\u5df1\u6709\u5ea7\u57ce\u5821\u5e84\u56ed\u4ec0\u4e48\u7684\uff0c\u6316\u51e0\u4e2a\u5730\u7262\uff0c\u51ff\u51e0\u95f4\u5bc6\u5ba4\uff0c\u73a9\u73a9\u76d1\u7981\u6e38\u620f\u5012\u4e5f\u4e0d\u9519\uff0c\u73b0\u5728\u5374\u662f\u5728\u8d76\u8def\uff0c\u54ea\u6709\u7cbe\u529b\u770b\u7ba1\u5979\u3002\u800c\u4e14\u59d0\u59d0\u672c\u6765\u5c31\u5df2\u7ecf\u5f88\u5403\u918b\u4e86\uff0c\u8981\u662f\u770b\u5230\u81ea\u5df1\u53c8\u5f04\u4e86\u4e2a\u7f8e\u5973\u4fd8\u864f\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f1a\u751f\u6c14\u6210\u4ec0\u4e48\u6837\u5b50\u2026\u2026\u7b97\u4e86\u7b97\u4e86\uff0c\u8fd8\u662f\u6740\u4e86\u5427\uff0c\u7b80\u5355\u7701\u5fc3\uff0c\u4e00\u4e86\u767e\u4e86\u3002\u201d</p><p>\u4e3b\u610f\u65e2\u5b9a\uff0c\u743c\u6069\u6084\u65e0\u58f0\u606f\u5730\u4e0b\u4e86\u4e2a\u547d\u4ee4\uff0c\u6218\u58eb\u9b54\u50cf\u7684\u77f3\u773c\u5fae\u5149\u4e00\u95ea\uff0c\u8f6c\u8eab\u671d\u7740\u6b4c\u66e6\u96c5\u5954\u6765\uff0c\u6253\u7b97\u5c06\u5979\u62e6\u8170\u780d\u6210\u4e24\u622a\u3002\u7136\u800c\u5b83\u811a\u6b65\u521a\u521a\u8fc8\u51fa\uff0c\u88ab\u7c98\u5728\u86db\u7f51\u4e2d\u7684\u6b4c\u66e6\u96c5\u9661\u7136\u53cc\u76ee\u4e00\u7741\uff0c\u7709\u5fc3\u7efd\u653e\u51fa\u4e00\u70b9\u8000\u773c\u767d\u5149\uff0c\u5411\u56db\u9762\u516b\u65b9\u6269\u6563\u800c\u53bb\u3002\u575a\u97e7\u5982\u94a2\u7f06\u7684\u86db\u7f51\u88ab\u8fd9\u767d\u5149\u4e00\u7167\uff0c\u987f\u65f6\u5316\u4f5c\u7247\u7247\u98de\u7070\uff0c\u6d88\u6563\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002</p><p>\u6b4c\u66e6\u96c5\u8131\u56f0\u800c\u51fa\uff0c\u4ece\u534a\u7a7a\u4e2d\u5760\u4e0b\uff0c\u53f3\u811a\u521a\u521a\u8e0f\u4e0a\u5730\u9762\uff0c\u968f\u5373\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u4e4c\u9ed1\u7684\u706b\u7130\u4ece\u8db3\u5e95\u8f70\u7136\u817e\u8d77\uff0c\u65cb\u8f6c\u8426\u7ed5\uff0c\u51dd\u805a\u6210\u4e00\u5708\u706b\u76fe\u3002\u6218\u58eb\u9b54\u50cf\u6070\u5728\u6b64\u65f6\u9760\u8fd1\uff0c\u649e\u4e0a\u706b\u76fe\uff0c\u968f\u5373\u6574\u4e2a\u77f3\u5934\u8eab\u8eaf\u50cf\u662f\u88ab\u6cfc\u4e86\u6c7d\u6cb9\u822c\uff0c\u718a\u718a\u71c3\u70e7\u8d77\u6765\uff0c\u77ac\u95f4\u53c8\u6062\u590d\u6210\u4e86\u4e00\u679a\u68cb\u5b50\u3002\u6b4c\u66e6\u96c5\u7684\u706b\u76fe\u5148\u662f\u4e4c\u9ed1\uff0c\u7ee7\u800c\u8d64\u7ea2\uff0c\u518d\u53d8\u6df1\u78a7\uff0c\u53c8\u8f6c\u6e5b\u84dd\uff0c\u6700\u540e\u5316\u4f5c\u767d\u70bd\u2014\u2014\u8f6c\u77ac\u4e4b\u95f4\u5df2\u7ecf\u53d8\u5e7b\u4e94\u8272\uff0c\u6700\u7ec8\u51dd\u805a\u6210\u4e00\u4ef6\u4e94\u8272\u7f24\u7eb7\u7684\u900f\u660e\u957f\u6597\u7bf7\uff0c\u5c06\u7d2b\u53d1\u5973\u5b50\u7684\u7a88\u7a95\u8eab\u6bb5\u5305\u88f9\u5728\u5185\uff0c\u7136\u540e\u5979\u518d\u5ea6\u524d\u51b2\u3002</p><p>\u201c\u53d8\u5f62\uff01\u201d</p><p>\u743c\u6069\u91ca\u653e\u51fa\u6700\u62ff\u624b\u7684\u6cd5\u672f\uff0c\u65e0\u5f62\u9b54\u6cd5\u80fd\u91cf\u4ece\u865a\u7a7a\u4e2d\u6d8c\u51fa\uff0c\u77ac\u95f4\u7b3c\u7f69\u6b4c\u66e6\u96c5\u5168\u8eab\uff0c\u5374\u5728\u4e0b\u4e00\u77ac\u95f4\u88ab\u76f4\u63a5\u5f39\u5f00\u4e86\u3002\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u7acb\u523b\u88ab\u62c9\u8fd1\uff0c\u6b4c\u66e6\u96c5\u4f4e\u559d\u4e00\u58f0\uff0c\u91d1\u5251\u4e0a\u5149\u8292\u7efd\u653e\uff0c\u4eff\u4f5b\u9661\u7136\u95f4\u53d8\u5927\u4e86\u4e00\u500d\uff0c\u7167\u7740\u743c\u6069\u659c\u5288\u4e0b\u6765\u3002</p><p>\u201c\u54e6\u5475\u5475\u5475\u5475\u2014\u2014\u554a\u54df\uff0c\u597d\u75bc\u597d\u75bc\u597d\u75bc\uff01\u201d</p><p>\u91d1\u5251\u5c16\u58f0\u602a\u53eb\u7740\uff0c\u649e\u4e0a\u4e86\u4e00\u67c4\u8840\u7ea2\u8272\u7684\u77ed\u5251\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u838e\u73de\u514b\u8d76\u56de\u743c\u6069\u8eab\u524d\uff0c\u66ff\u4ed6\u6321\u4e0b\u8fd9\u4e00\u51fb\u3002\u6b4c\u66e6\u96c5\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u91d1\u5251\u6325\u52a8\uff0c\u6025\u98ce\u66b4\u96e8\u822c\u653b\u8fc7\u6765\uff0c\u838e\u73de\u514b\u7d27\u54ac\u7259\u5173\uff0c\u5de6\u683c\u53f3\u6321\uff0c\u8f6c\u77ac\u95f4\u5df2\u7ecf\u63a5\u4e0b\u4e86\u5bf9\u624b\u4e03\u5251\uff0c\u5c45\u7136\u534a\u6b65\u4e0d\u9000\u3002</p><p>\u56e0\u4e3a\u662f\u5728\u7269\u8d28\u754c\uff0c\u838e\u73de\u514b\u5e73\u65e5\u91cc\u603b\u662f\u4ee5\u4eba\u7c7b\u5973\u5b50\u7684\u5f62\u6001\u51fa\u73b0\uff0c\u907f\u514d\u60f9\u4eba\u6ce8\u76ee\u3002\u4f46\u6b64\u65f6\u5728\u5979\u7684\u5934\u9876\u4e0a\uff0c\u4e00\u5bf9\u5c16\u5c16\u5f2f\u89d2\u7a7f\u900f\u8910\u53d1\uff0c\u6bb7\u7ea2\u6b32\u6ef4\uff0c\u539f\u672c\u767d\u7699\u5982\u7389\u7684\u8d64\u88f8\u53cc\u81c2\u4e0a\uff0c\u6d6e\u73b0\u7740\u8840\u7ba1\u7ecf\u8109\u4f3c\u7684\u7d2b\u8272\u7ec6\u7ebf\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\uff0c\u4ea4\u9519\u7f16\u7ec7\u6210\u5404\u79cd\u90aa\u9b54\u7b26\u6587\uff0c\u95ea\u70c1\u4e0d\u5b9a\u2014\u2014\u83c1\u82f1\u9b45\u9b54\u7684\u5f62\u6001\u5b8c\u5168\u663e\u9732\uff0c\u8fd9\u610f\u5473\u7740\u5979\u5df2\u7ecf\u662f\u7aed\u5c3d\u5168\u529b\u3002</p><p>\u201c\u8fdf\u7f13\uff01\u5b9a\u8eab\uff01\u201d</p><p>\u501f\u7740\u838e\u73de\u514b\u7684\u963b\u6321\uff0c\u743c\u6069\u518d\u5ea6\u91ca\u653e\u4e86\u4e24\u4e2a\u6cd5\u672f\uff0c\u7136\u800c\u5b83\u4eec\u65e0\u4e00\u4f8b\u5916\u90fd\u88ab\u76f4\u63a5\u5f39\u5f00\u4e86\u3002\u4f3c\u4e4e\u662f\u62ab\u4e0a\u90a3\u4ef6\u4e94\u5f69\u6597\u7bf7\u7684\u5173\u7cfb\uff0c\u6b4c\u66e6\u96c5\u5bf9\u9b54\u6cd5\u7684\u62b5\u6297\u80fd\u529b\u8fbe\u5230\u4e86\u60ca\u4eba\u7684\u7a0b\u5ea6\uff0c\u743c\u6069\u81ea\u5ea6\u5c31\u7b97\u662f\u53d1\u51fa\u5a01\u529b\u6700\u5927\u7684\u89e3\u79bb\u672f\uff0c\u6210\u529f\u7387\u4e5f\u672a\u5fc5\u80fd\u6709\u4e00\u6210\u3002\u4ed6\u76b1\u8d77\u7709\u5934\uff0c\u518d\u5ea6\u5ff5\u51fa\u53e3\u4ee4\uff0c\u5730\u677f\u4e0a\u70df\u96fe\u817e\u8d77\uff0c\u4e24\u5177\u9876\u76d4\u8d2f\u7532\u7684\u9ab7\u9ac5\u6b66\u58eb\u4ece\u4e2d\u8fc8\u51fa\uff0c\u6325\u821e\u5927\u780d\u5200\u51b2\u5411\u6b4c\u66e6\u96c5\u3002</p><p>\u4f5c\u4e3a\u6700\u9876\u7ea7\u7684\u4ea1\u7075\u5e08\uff0c\u5965\u6c83\u81ea\u7136\u4e0d\u4f1a\u5fd8\u4e86\u5728\u4ed6\u7684\u201c\u9752\u94dc\u8c6a\u5b85\u201d\u91cc\u50a8\u5907\u4e00\u7fa4\u4ea1\u7075\u4f8d\u536b\uff0c\u5e73\u65f6\u85cf\u5728\u5730\u677f\u4e0b\u9762\uff0c\u9700\u8981\u7684\u65f6\u5019\u76f4\u63a5\u62db\u51fa\uff0c\u8981\u8bf4\u5b83\u4eec\u591a\u5f3a\u608d\u5012\u4e5f\u672a\u89c1\u5f97\uff0c\u4f46\u6570\u91cf\u591a\u5c31\u662f\u6700\u5927\u7684\u4f18\u70b9\u3002\u8fd9\u95f4\u5ba2\u5385\u91cc\u4e00\u5171\u6709\u516b\u5341\u5757\u9752\u94dc\u5730\u677f\uff0c\u6bcf\u5757\u4e0b\u9762\u90fd\u6f5c\u4f0f\u7740\u4e00\u5177\u9ab7\u9ac5\u6b66\u58eb\uff0c\u4e00\u5171\u4fbf\u662f\u516b\u5341\u4e2a\u3002\u5982\u679c\u9700\u8981\u7684\u8bdd\uff0c\u8fd8\u53ef\u4ee5\u4ece\u5176\u4ed6\u623f\u95f4\u8c03\u96c6\uff0c\u6e90\u6e90\u4e0d\u65ad\u5730\u6d8c\u4e0a\u6765\uff0c\u5c31\u7b97\u662f\u518d\u5f3a\u529b\u7684\u6218\u58eb\u4e5f\u8981\u88ab\u7d2f\u6b7b\u3002</p><p>\u201c\u4ee5\u73ed\u6069\u4e4b\u540d\uff0c\u547d\u5c14\u7b49\u542c\u6211\u9a71\u9063\uff01\u201d</p><p>\u773c\u89c1\u4e24\u5177\u9ab7\u9ac5\u6b66\u58eb\u5954\u6765\uff0c\u6b4c\u66e6\u96c5\u8f7b\u58f0\u4f4e\u559d\uff0c\u51db\u51db\u90aa\u5a01\u4ece\u5979\u8eab\u4e0a\u53d1\u51fa\u3002\u5584\u795e\u7684\u795e\u804c\u8005\uff0c\u5927\u591a\u53ef\u4ee5\u501f\u52a9\u795e\u5a01\u8ba9\u4ea1\u7075\u5c18\u5f52\u5c18\uff0c\u571f\u5f52\u571f\uff0c\u8fd9\u53eb\u505a\u201c\u8d85\u5ea6\u201d\uff1b\u90aa\u795e\u7684\u795e\u804c\u8005\uff0c\u5927\u591a\u53ef\u4ee5\u501f\u52a9\u795e\u5a01\u53cd\u8fc7\u6765\u63a7\u5236\u4ea1\u7075\uff0c\u4f9b\u81ea\u5df1\u9a71\u9063\uff0c\u8fd9\u53eb\u505a\u201c\u65a5\u559d\u201d\uff0c\u6b4c\u66e6\u96c5\u7528\u7684\u5c31\u662f\u540e\u8005\u3002\u4f46\u4e0d\u77e5\u662f\u5979\u5728\u8fd9\u65b9\u9762\u9020\u8be3\u4e0d\u8db3\uff0c\u8fd8\u662f\u5965\u6c83\u505a\u51fa\u6765\u7684\u4ea1\u7075\u6bd4\u8f83\u5389\u5bb3\uff0c\u4e24\u5177\u9ab7\u9ac5\u6b66\u58eb\u88ab\u201c\u65a5\u559d\u201d\u51fb\u4e2d\uff0c\u5374\u53ea\u662f\u987f\u4e86\u4e00\u987f\uff0c\u968f\u5373\u53c8\u518d\u5ea6\u51b2\u4e0a\u6765\u3002</p><p>\u6b4c\u66e6\u96c5\u6325\u821e\u91d1\u5251\uff0c\u4e09\u79d2\u949f\u540e\u4e24\u5177\u9ab7\u9ac5\u6b66\u58eb\u53d8\u6210\u4e86\u4e24\u5806\u788e\u9aa8\uff0c\u4f46\u743c\u6069\u5df2\u7ecf\u53c8\u62db\u51fa\u4e86\u56db\u5177\u2014\u2014\u518d\u8fc7\u4e09\u79d2\u949f\uff0c\u4ed6\u4fbf\u53ef\u4ee5\u53c8\u62db\u51fa\u516d\u5177\uff0c\u7136\u540e\u662f\u516b\u5177\u3001\u5341\u5177\uff0c\u6700\u591a\u80fd\u591f\u540c\u65f6\u62db\u51fa\u5341\u4e8c\u5177\u3002</p><p>\u773c\u89c1\u5c31\u8981\u9677\u5165\u9ab7\u9ac5\u6b66\u58eb\u7684\u5305\u56f4\u5708\uff0c\u6b4c\u66e6\u96c5\u731b\u7136\u540e\u8dc3\u534a\u6b65\uff0c\u91d1\u5251\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u5343\u4e07\u70b9\u8367\u8367\u7eff\u5149\u75af\u72c2\u6d8c\u51fa\uff0c\u8426\u7ed5\u5251\u8eab\uff0c\u707f\u70c2\u82e5\u65e5\uff0c\u7136\u540e\u78a7\u5149\u4eff\u4f5b\u6f6e\u6c34\u6d2a\u6d41\u822c\u4ece\u5251\u5203\u4e2d\u731b\u70c8\u7206\u53d1\u51fa\u6765\uff0c\u5411\u56db\u9762\u516b\u65b9\u8f70\u7136\u6269\u6563\u3002</p><p>\u201c\u8fd4\u56de\uff01\u201d</p><p>\u4e00\u89c1\u6b4c\u66e6\u96c5\u6446\u51fa\u7684\u67b6\u52bf\uff0c\u743c\u6069\u4fbf\u77e5\u9053\u5979\u8981\u7528\u90a3\u62db\uff0c\u5f53\u65e5\u5728\u8fdc\u5c71\u57ce\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u4e86\uff0c\u5982\u4f55\u4f1a\u4e0d\u63d0\u9632\u3002\u4ed6\u6015\u838e\u73de\u514b\u907f\u8ba9\u4e0d\u53ca\uff0c\u6025\u5fd9\u5ff5\u51fa\u5492\u8a00\uff0c\u6302\u5728\u80f8\u53e3\u7684\u8d1d\u88d8\u91cc\u540a\u5760\u4e2d\u5149\u8292\u4e00\u95ea\uff0c\u9b45\u9b54\u7684\u8eab\u5f62\u9aa4\u7136\u95f4\u53d8\u5f97\u900f\u660e\uff0c\u7136\u540e\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\uff0c\u5979\u5df2\u7ecf\u88ab\u6536\u56de\u5230\u5b9d\u77f3\u4e4b\u5185\u3002</p><p>\u838e\u73de\u514b\u521a\u521a\u6d88\u5931\uff0c\u78a7\u5149\u4fbf\u94fa\u5929\u76d6\u5730\u5730\u5e2d\u5377\u800c\u6765\uff0c\u56db\u5177\u9ab7\u9ac5\u6b66\u58eb\u9996\u5f53\u5176\u51b2\uff0c\u88ab\u9b54\u6cd5\u5f3a\u5316\u8fc7\u7684\u8eab\u8eaf\u5267\u70c8\u9707\u98a4\u7740\uff0c\u7136\u540e\u788e\u6210\u5343\u4e07\u5757\u6676\u767d\u8272\u7684\u9aa8\u6e23\uff0c\u5176\u4f59\u9910\u684c\u3001\u8336\u51e0\u3001\u6c99\u53d1\u7b49\u7269\u7eb7\u7eb7\u5316\u4f5c\u7c89\u672b\uff0c\u5ba2\u5385\u4e2d\u95f4\u51fa\u73b0\u4e00\u5757\u6574\u9f50\u7684\u5706\u5f62\u7a7a\u5730\u3002\u6b4c\u66e6\u96c5\u4e00\u51fb\u626b\u9664\u969c\u788d\uff0c\u66f4\u4e0d\u8fdf\u7591\uff0c\u7bad\u6b65\u524d\u51b2\uff0c\u671d\u743c\u6069\u8feb\u6765\u3002</p><p>\u201c\u6211\u7684\u5144\u5f1f\u662f\u4e00\u628a\uff0b12\u7684\u6218\u9524\uff01\u201d</p><p>\u9ad8\u547c\u7740\u4ee4\u4eba\u8d39\u89e3\u7684\u53e3\u53f7\uff0c\u91d1\u5251\u5f53\u5934\u5288\u4e0b\u3002\u743c\u6069\u65e9\u6709\u51c6\u5907\uff0c\u8679\u5f69\u9f99\u9cde\u76fe\u65cb\u8f6c\u98de\u51fa\uff0c\u6321\u4e0b\u4e86\u8fd9\u4e00\u51fb\u3002\u6b4c\u66e6\u96c5\u624b\u4e0a\u52a0\u52b2\uff0c\u5c06\u9f99\u9cde\u76fe\u649e\u5230\u4e00\u8fb9\uff0c\u6b63\u8981\u4e3e\u5251\u518d\u780d\uff0c\u743c\u6069\u53cc\u624b\u5408\u63e1\uff0c\u98df\u6307\u76f8\u62b5\uff0c\u4e00\u652f\u534a\u900f\u660e\u7684\u9634\u5f71\u7bad\u4ece\u6307\u5c16\u8ff8\u51fa\u3002</p><p>\u201c\u55e4\uff01\u201d</p><p>\u53cc\u65b9\u76f8\u8ddd\u592a\u8fd1\uff0c\u6b4c\u66e6\u96c5\u65e0\u4ece\u95ea\u907f\uff0c\u88ab\u9634\u5f71\u7bad\u6b63\u4e2d\u80f8\u53e3\uff0c\u4e94\u5f69\u6597\u7bf7\u548c\u9ed1\u8272\u94e0\u7532\u90fd\u672a\u80fd\u53d1\u6325\u534a\u70b9\u963b\u6321\u4f5c\u7528\uff0c\u9634\u5f71\u7bad\u5f84\u76f4\u900f\u8fc7\uff0c\u6ca1\u5165\u4f53\u5185\u3002\u5979\u7684\u7f8e\u4e3d\u9762\u5bb9\u9aa4\u7136\u626d\u66f2\u8d77\u6765\uff0c\u663e\u51fa\u6781\u5ea6\u75db\u82e6\u7684\u795e\u8272\uff0c\u539f\u672c\u4fee\u957f\u5f3a\u5065\u7684\u53cc\u817f\u4eff\u4f5b\u5239\u90a3\u95f4\u4e27\u5931\u529b\u6c14\uff0c\u6574\u4e2a\u4eba\u5f80\u4e0b\u4fbf\u5012\uff0c\u603b\u7b97\u662f\u53ca\u65f6\u7528\u91d1\u5251\u6491\u4f4f\uff0c\u534a\u8dea\u5728\u5730\u3002\u201c\u5f71\u706b\uff01\u201d\u91d1\u5251\u5c16\u58f0\u53eb\u8d77\u6765\uff0c\u201c\u8fd9\u5c0f\u5b50\u662f\u838e\u5c14\u7684\u9009\u6c11\uff01\u201d</p><p>\u201c\u731c\u5bf9\u4e86\uff01\u201d</p><p>\u743c\u6069\u51b7\u7b11\uff0c\u518d\u5ea6\u53d1\u51fa\u4e00\u9053\u9634\u5f71\u7bad\u3002\u8fd9\u662f\u5f71\u706b\u6700\u7b80\u5355\u7684\u8fd0\u7528\u65b9\u6cd5\u4e4b\u4e00\uff0c\u5176\u5b9e\u5c31\u662f\u628a\u5b83\u96c6\u4e2d\u6210\u675f\u53d1\u51fa\uff0c\u901f\u5ea6\u5e76\u4e0d\u662f\u5f88\u5feb\uff0c\u6240\u4ee5\u5fc5\u987b\u8fd1\u8ddd\u79bb\u653b\u51fb\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u88ab\u8eab\u624b\u654f\u6377\u7684\u5bf9\u624b\u95ea\u907f\u8fc7\u53bb\u3002\u4f46\u56e0\u4e3a\u662f\u795e\u529b\u7684\u5173\u7cfb\uff0c\u5bfb\u5e38\u7684\u9b54\u6cd5\u9632\u5fa1\u65e0\u6cd5\u62b5\u6321\uff0c\u5f71\u706b\u548c\u94f6\u706b\u53c8\u6709\u4e0d\u540c\uff0c\u5b83\u6ca1\u6709\u58f0\u52bf\u714a\u8d6b\u7684\u534e\u4e3d\uff0c\u5374\u66f4\u52a0\u7684\u9634\u68ee\u8be1\u8c32\uff0c\u4e00\u65e6\u547d\u4e2d\u751f\u7269\uff0c\u4fbf\u76f4\u63a5\u900f\u80a4\u5165\u4f53\uff0c\u9500\u9aa8\u878d\u9ad3\uff0c\u7834\u574f\u751f\u7406\u673a\u80fd\uff0c\u541e\u566c\u8840\u8089\u6d3b\u529b\uff0c\u4eff\u4f5b\u6700\u5371\u9669\u7684\u6bd2\u836f\u3002\u6b4c\u66e6\u96c5\u80f8\u53e3\u4e2d\u4e86\u4e00\u51fb\uff0c\u80fd\u591f\u4e0d\u5f53\u573a\u5012\u4e0b\uff0c\u5df2\u7ecf\u4ee4\u743c\u6069\u975e\u5e38\u60ca\u8bb6\u4e86\u3002</p><p>\u773c\u770b\u7b2c\u4e8c\u9053\u9634\u5f71\u7bad\u53c8\u8981\u547d\u4e2d\u76ee\u6807\uff0c\u6b4c\u66e6\u96c5\u7684\u6218\u9774\u9661\u7136\u5149\u8292\u5927\u76db\uff0c\u968f\u5373\u5979\u6574\u4e2a\u4eba\u659c\u659c\u5f80\u540e\u9ad8\u901f\u5012\u649e\u51fa\u53bb\uff0c\u582a\u582a\u907f\u5f00\u5f71\u706b\u7684\u653b\u51fb\u3002\u8fd9\u6b21\u6210\u529f\u95ea\u907f\u8017\u5c3d\u4e86\u5979\u597d\u5bb9\u6613\u79ef\u6512\u8d77\u6765\u7684\u6b8b\u4f59\u4f53\u529b\uff0c\u6b4c\u66e6\u96c5\u534a\u8dea\u5728\u5730\uff0c\u5927\u53e3\u5927\u53e3\u5598\u606f\u7740\uff0c\u957f\u957f\u7d2b\u53d1\u62ab\u6563\u5728\u80a9\u5934\u548c\u80cc\u90e8\uff0c\u5782\u843d\u5230\u5730\uff0c\u906e\u4f4f\u4e86\u82cd\u767d\u7684\u8138\u988a\u3002</p><p>\u8fde\u53d1\u4e24\u9053\u9634\u5f71\u7bad\uff0c\u8fd9\u5df2\u7ecf\u662f\u743c\u6069\u76ee\u524d\u7684\u6781\u9650\uff0c\u4ed6\u5bf9\u5f71\u706b\u7684\u638c\u63e1\u6bd5\u7adf\u8fd8\u8fdc\u8fdc\u4e0d\u591f\u719f\u7ec3\uff0c\u597d\u5728\u5bf9\u624b\u663e\u7136\u4e5f\u5df2\u7ecf\u6682\u65f6\u4e27\u5931\u4e86\u53cd\u6297\u80fd\u529b\u3002\u5deb\u5e08\u518d\u5ea6\u5ff5\u51fa\u53e3\u4ee4\uff0c\u516d\u5177\u9ab7\u9ac5\u6b66\u58eb\u4ece\u5730\u5e95\u5192\u51fa\uff0c\u4e00\u5b57\u6392\u5f00\uff0c\u7136\u540e\u6325\u821e\u780d\u5200\uff0c\u8fc8\u7740\u6574\u9f50\u5212\u4e00\u7684\u6b65\u4f10\uff0c\u5494\u5693\u5494\u5693\u5730\u671d\u7740\u7d2b\u53d1\u5973\u5b50\u5c0f\u8dd1\u8fc7\u53bb\u3002</p><p>\u201c\u563f\uff0c\u6b4c\u66e6\u96c5\uff0c\u5feb\u8d77\u6765\u5feb\u8d77\u6765\uff01\u201d\u91d1\u5251\u53eb\u56b7\u7740\uff0c\u201c\u6211\u4eec\u8be5\u6218\u7565\u8f6c\u8fdb\u4e86\u3002\u201d</p><p>\u6b4c\u66e6\u96c5\u62ac\u8d77\u6765\u8138\uff0c\u51b7\u51b7\u76ef\u7740\u743c\u6069\uff0c\u7136\u540e\u64ae\u5507\u547c\u5578\u4e86\u4e00\u58f0\u3002\u5578\u97f3\u521a\u843d\uff0c\u95e8\u53e3\u5904\u4fbf\u4f20\u6765\u4e00\u58f0\u8f70\u7136\u7206\u54cd\uff0c\u6574\u4e2a\u9752\u94dc\u623f\u5b50\u90fd\u88ab\u9707\u5f97\u731b\u70c8\u6643\u4e86\u6643\uff0c\u743c\u6069\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u9669\u4e9b\u4e00\u8de4\u6454\u5012\u3002\u4ed6\u60ca\u9a87\u4e4b\u4e0b\uff0c\u8fd8\u6ca1\u5b8c\u5168\u56de\u8fc7\u795e\uff0c\u5c31\u89c1\u4e00\u9053\u95ea\u7535\u822c\u7684\u9ed1\u5f71\u4ece\u95e8\u53e3\u51b2\u8fdb\u5927\u5385\uff0c\u505c\u5728\u6b4c\u66e6\u96c5\u8eab\u65c1\uff0c\u6b63\u662f\u5979\u90a3\u5339\u9ed1\u9a6c\u3002</p><p>\u4e0d\uff0c\u4e0d\u5bf9\uff0c\u8fd9\u4e0d\u662f\u4ec0\u4e48\u9ed1\u9a6c\u2026\u2026</p><p>\u786e\u5b9e\u662f\u9a8f\u9a6c\u7684\u5f62\u4f53\uff0c\u4f46\u5b83\u7684\u773c\u775b\u5374\u662f\u5b8c\u5168\u7684\u6df1\u7d2b\uff0c\u957f\u957f\u7684\u987b\u9aef\u98d8\u62c2\u5728\u988c\u4e0b\uff0c\u6574\u6574\u9f50\u9f50\u7684\u9a6c\u9b03\u4e2d\uff0c\u4e00\u652f\u4fee\u957f\u7b14\u76f4\u7684\u9ed1\u8272\u72ec\u89d2\u50b2\u7136\u72ec\u7acb\uff0c\u6676\u83b9\u900f\u660e\uff0c\u4eff\u4f5b\u7409\u7483\uff0c\u82e5\u4e0d\u662f\u5728\u5ba4\u5185\u706f\u5149\u4e4b\u4e0b\u8fd8\u771f\u4e0d\u5bb9\u6613\u53d1\u89c9\u2014\u2014\u8fd9\u5206\u660e\u662f\u4e00\u53ea\u9ed1\u8272\u7684\u72ec\u89d2\u517d\u3002</p><p>\u7136\u800c\u4e16\u754c\u4e0a\u6709\u9ed1\u8272\u7684\u72ec\u89d2\u517d\u4e48\uff1f</p><p>\u4e0d\u7b49\u743c\u6069\u60f3\u660e\u767d\u8fd9\u4e2a\u95ee\u9898\uff0c\u9ed1\u8272\u72ec\u89d2\u517d\u5f2f\u8170\u7528\u89d2\u4e00\u6311\uff0c\u8f7b\u8f7b\u5de7\u5de7\u5730\u5c06\u6b4c\u66e6\u96c5\u7529\u5230\u5b83\u7684\u80cc\u4e0a\u3002\u516d\u5177\u9ab7\u9ac5\u6b66\u58eb\u56f4\u4e0a\u6765\uff0c\u4e89\u5148\u6050\u540e\u5730\u6325\u5200\u780d\u4e0b\uff0c\u72ec\u89d2\u517d\u8f7b\u8511\u5730\u6253\u4e86\u4e2a\u54cd\u9f3b\uff0c\u5de6\u524d\u8e44\u5c48\u8d77\uff0c\u7136\u540e\u5f80\u5730\u9762\u4e0a\u91cd\u91cd\u4e00\u8e0f\u3002</p><p>\u65e0\u5f62\u7684\u51b2\u51fb\u6ce2\u5448\u5706\u73af\u72b6\u5411\u56db\u5468\u6269\u6563\uff0c\u516d\u5177\u9ab7\u9ac5\u6b66\u58eb\u5c3d\u6570\u88ab\u9707\u98de\u51fa\u53bb\uff0c\u649e\u5728\u5899\u58c1\u4e0a\uff0c\u771f\u6b63\u5730\u6563\u4e86\u9aa8\u67b6\u3002\u72ec\u89d2\u517d\u66f4\u4e0d\u8fdf\u7591\uff0c\u8f6c\u8eab\u5954\u51fa\u623f\u5b50\uff0c\u8f6c\u773c\u4fbf\u6d88\u5931\u5728\u6c89\u6c89\u591c\u5e55\u5f53\u4e2d\u3002\u201c\u540e\u4f1a\u6709\u671f\uff0c\u5deb\u5e08\uff0c\u201d\u6b4c\u66e6\u96c5\u51b0\u51b7\u7684\u58f0\u97f3\u987a\u7740\u51db\u98ce\u4f20\u6765\uff0c\u201c\u6211\u4eec\u4e00\u5b9a\u8fd8\u4f1a\u518d\u89c1\u9762\u3002\u201d</p><p>\u201c\u968f\u65f6\u606d\u5019\u3002\u201d\u743c\u6069\u8f7b\u58f0\u8bf4\uff0c\u4eff\u4f5b\u81ea\u8a00\u81ea\u8bed\uff0c\u7136\u540e\u5c06\u76ee\u5149\u8f6c\u5411\u8eb2\u5728\u89d2\u843d\u91cc\u7684\u80d6\u8001\u5934\uff0c\u5fae\u7b11\u6d6e\u73b0\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u6e29\u548c\u3001\u8bda\u6073\uff0c\u800c\u4e14\u7565\u5e26\u817c\u8146\u3002\u201c\u4f3c\u4e4e\uff0c\u6211\u4eec\u4e4b\u95f4\u4e5f\u6709\u70b9\u4e8b\u60c5\u9700\u8981\u505a\u4e2a\u4e86\u7ed3\u5462\uff0c\u5f17\u96f7\u65af\u5148\u751f\uff0c\u201d\u4ed6\u95ee\uff0c\u201c\u60a8\u610f\u4e0b\u5982\u4f55\uff1f\u201d</p></article>"}}}]);
