(window.webpackJsonp=window.webpackJsonp||[]).push([[445],{468:function(p){p.exports={chapterTitle:null,title:"\u7b2c445\u7ae0 \u591c\u8272\u5bc2\u5be5",content:"<article><h2>\u7b2c445\u7ae0 \u591c\u8272\u5bc2\u5be5</h2><p>\u3000\u3000\u63a5\u4e0b\u6765\u7684\u9152\u5e2d\u591a\u5c11\u6709\u4e9b\u6c89\u95f7\uff0c\u5f20\u606a\u5012\u662f\u7709\u98de\u8272\u821e\u4f9d\u65e7\uff0c\u8c22\u5251\u5357\u3001\u8d75\u9526\u8363\u7b49\u4eba\u5219\u5fc3\u4e8b\u91cd\u91cd\uff0c\u8c22\u77bb\u7070\u7709\u571f\u8138\u66f4\u4e0d\u7528\u63d0\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5f00\u53e3\u8bf4\u8bdd\uff0c\u79d1\u738b\u8981\u60f3\u4eceTI\u62ff\u5230\u957f\u671f\u4f9b\u8d27\u534f\u8bae\uff0c\u4fdd\u8bc1\u91d1\u4f5c\u4e3a\u9644\u52a0\u6761\u4ef6\u5c31\u6210\u4e3a\u5b9a\u5c40\uff0c\u827e\u9ed8\u6ca1\u6709\u8868\u6001\uff0c\u8c22\u5251\u5357\u4e5f\u80fd\u9884\u60f3\u771f\u5230\u8c08\u5224\u65f6\uff0c\u65af\u9ad8\u67cf\u672a\u5fc5\u6bd4TI\u597d\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u867d\u7136\u5177\u4f53\u8981\u652f\u4ed8\u591a\u5c11\u6bd4\u4f8b\u7684\u4fdd\u8bc1\u91d1\u53ef\u4ee5\u5728\u8c08\u5224\u4e2d\u626f\u76ae\uff0c\u4f46\u6bd5\u7adf\u8981\u5360\u7528\u79d1\u738b\u7684\u4e00\u90e8\u5206\u6d41\u52a8\u8d44\u91d1\uff1b\u597d\u5728\u6b63\u6cf0\u3001\u5927\u96c5\u90fd\u5728\u5341\u51e0\u5e74\u7684\u79ef\u7d2f\uff0c\u8d44\u91d1\u5b9e\u529b\u8fd8\u7b97\u96c4\u539a\uff0c\u6d77\u6cf0\u7684\u6d41\u52a8\u8d44\u91d1\u867d\u7136\u7d27\u5f20\u4e00\u4e0b\uff0c\u4ecd\u53ef\u4ee5\u4ee5\u62c6\u501f\u7684\u540d\u4e49\u4ece\u6b63\u6cf0\u96c6\u56e2\u8c03\u7528\uff0c\u53e6\u5916\u5728\u60e0\u5c71\u3001\u65b0\u6cf0\u7684\u878d\u8d44\u91d1\u6e20\u9053\u8fd8\u7b97\u901a\u7545\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u53ea\u662f\u8981\u8003\u8651\u957f\u671f\u4f9b\u8d27\u534f\u8bae\u4e2d\u53ef\u80fd\u5b58\u5728\u7684\u79cd\u79cd\u9677\u9631\u96be\u514d\u663e\u5f97\u5fc3\u601d\u6c89\u91cd\uff0c\u4ed6\u5e0c\u671b\u8fd9\u4efd\u534f\u8bae\u80fd\u6210\u4e3a\u4e00\u4efd\u4f18\u5148\u4f9b\u8d27\u7684\u534f\u8bae\uff0c\u800c\u4e0d\u80fd\u53cd\u8fc7\u6765\u5236\u7ea6\u79d1\u738b\u9488\u5bf9\u5e02\u573a\u53d8\u5316\u6240\u505a\u51fa\u53ca\u65f6\u8c03\u6574\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u7684\u611f\u53d7\u5374\u662f\u4e0d\u540c\uff0c\u5e9c\u5929\u5927\u53a6\u7684\u6210\u529f\u51fa\u552e\uff0c\u4e00\u5ea6\u7f13\u89e3\u4e86\u9526\u57ce\u96c6\u56e2\u7684\u8d44\u91d1\u538b\u529b\uff0c\u4f46\u662f\u9526\u57ce\u96c6\u56e2\u6bd5\u7adf\u4e0d\u80fd\u8ddf\u8c22\u5bb6\u7684\u6b63\u6cf0\u96c6\u56e2\u76f8\u63d0\u5e76\u8bba\uff0c\u52a0\u4e0a\u8d75\u9526\u8363\u4e00\u4e0b\u5b50\u62ff\u4e0b\u6c99\u7530\u897f\u7247\u8fd1\u4e09\u5206\u4e4b\u4e00\u571f\u5730\u5f00\u53d1\u5730\u4ea7\u9879\u76ee\uff0c\u624b\u91cc\u6709\u9650\u7684\u8d44\u91d1\u5c31\u7ed9\u644a\u8584\u4e86\uff0c\u4f7f\u5f97\u9879\u76ee\u7684\u6574\u4f53\u8fdb\u5c55\u7f13\u6162\u4e0b\u6765\uff0c\u8fd9\u4e0e\u79d1\u738b\u4e0b\u534a\u5e74\u6765\u4ece\u9526\u57ce\u5730\u4ea7\u65d7\u4e0b\u62bd\u8c03\u5de5\u7a0b\u961f\u52a0\u901f\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u7684\u5382\u533a\u5efa\u8bbe\u4e5f\u6709\u76f8\u5f53\u7684\u5173\u7cfb\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u4e9b\u90fd\u52a0\u91cd\u9526\u57ce\u96c6\u56e2\u7684\u8d44\u91d1\u538b\u529b\u3002</p><p>\u3000\u3000\u5f71\u789f\u673a\u662f\u597d\u8d5a\uff0c\u5728\u4e0e\u8c22\u5bb6\u5408\u4f5c\u4e4b\u524d\uff0c\u9526\u57ce\u53ea\u5229\u7528\u65d7\u4e0b\u7684\u7535\u5b50\u5382\u8d34\u724c\u751f\u4ea7\uff0c\u5c31\u635e\u4e86\u597d\u51e0\u767e\u4e07\uff0c\u4e0e\u8c22\u5bb6\u5408\u4f5c\u6b63\u5f0f\u7ec4\u5efa\u79d1\u738b\u7535\u5668\u4e4b\u540e\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u867d\u7136\u76c8\u5229\u90fd\u6295\u5165\u5e02\u573a\u5ba3\u4f20\uff0c\u4f46\u662f\u4e5f\u7b97\u76f8\u5f53\u6210\u529f\u7684\u5c06\u4ea7\u9500\u91cf\u63d0\u5347\u5230\u6bcf\u6708\u4e00\u4e07\u591a\u53f0\uff0c\u5982\u679c\u8f6c\u800c\u91c7\u53d6\u4fdd\u5b88\u7684\u5e02\u573a\u7b56\u7565\uff0c\u4ee5\u5f53\u524d\u54c1\u724c\u5f71\u789f\u673a\u5e02\u573a\u7684\u5229\u6da6\u6c34\u5e73\uff0c\u79d1\u738b\u7535\u5668\u6bcf\u6708\u4e5f\u80fd\u63a0\u83b7\u4e09\u56db\u767e\u4e07\u7684\u51c0\u5229\uff0c\u5e76\u4e14\u4f1a\u7a33\u5b9a\u7684\u63d0\u5347\uff0c\u6bd4\u8d77\u9526\u8363\u4e0e\u8c22\u5bb6\u524d\u540e\u6295\u5165\u624d\u516d\u5343\u4e07\u7684\u8d44\u672c\uff0c\u5f53\u7136\u53ef\u4ee5\u79f0\u5f97\u4e0a\u662f\u66b4\u5229\uff0c\u800c\u4e14\u79d1\u738b\u5728\u56fa\u5b9a\u8d44\u4ea7\u4e0a\u7684\u6295\u8d44\u6bd4\u4f8b\u8981\u8fdc\u5c0f\u4e8e\u4e00\u822c\u7684\u5b9e\u4f53\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u8f89\u714c\u6210\u5c31\u76f8\u6bd4\uff0c\u79d1\u738b\u7535\u5668\u7684\u90a3\u4e9b\u6210\u529f\u53ea\u80fd\u7b97\u662f\u8367\u8367\u866b\u706b\uff0c\u8981\u6ca1\u6709\u535a\u53d6\u6700\u4e30\u539a\u66b4\u5229\u7684\u91ce\u5fc3\uff0c\u8d75\u9526\u8363\u4e5f\u4e0d\u662f\u8d75\u9526\u8363\u3001\u8c22\u5251\u5357\u4e5f\u4e0d\u662f\u8c22\u5251\u5357\u4e86\uff1b\u589e\u8d44\u3001\u6269\u4ea7\u3001\u52a0\u5927\u5bf9\u54c1\u724c\u5ba3\u4f20\u7684\u529b\u5ea6\uff0c\u4ee5\u53ca\u8c22\u5251\u5357\u4e3b\u6301\u79d1\u738b\u7535\u5668\u62ff\u4e0b\u592e\u89c6\u6807\u738b\uff0c\u90fd\u662f\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4e00\u7cfb\u5217\u7684\u589e\u8d44\uff0c\u629b\u5f00\u4e4b\u524d\u7684\u76c8\u5229\u4e0e\u9526\u57ce\u7535\u5b50\u5382\u7684\u56fa\u5b9a\u8d44\u4ea7\uff0c\u9526\u57ce\u524d\u540e\u4e5f\u5f80\u79d1\u738b\u6295\u5165\u8fd1\u56db\u5343\u4e07\u7684\u8d44\u91d1\uff0c\u5510\u5b66\u8c26\u4e3b\u6301\u6d77\u5dde\u7ecf\u6d4e\uff0c\u9526\u57ce\u96c6\u56e2\u5728\u6d77\u5dde\u878d\u8d44\u54ea\u6709\u90a3\u4e48\u987a\u7545\uff0c\u8d75\u9526\u8363\u51e0\u4e4e\u5c06\u96c6\u56e2\u7684\u6d41\u52a8\u8d44\u91d1\u538b\u5230\u6781\u9650\uff0c\u624d\u524d\u540e\u7b79\u51fa\u56db\u5343\u4e07\u6765\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u540c\u610f\u9648\u5bb6\u5584\u7684\u5927\u96c5\u96c6\u56e2\u53c2\u4e0e\u8fdb\u6765\u5206\u98df\u4e00\u53e3\u3002</p><p>\u3000\u3000\u9152\u5e2d\u4e4b\u524d\uff0c\u8c22\u5251\u5357\u627e\u4ed6\u5546\u91cf\u79d1\u738b\u8fd8\u7f3a\u4e94\u5343\u4e07\u7684\u6d41\u52a8\u8d44\u91d1\uff0c\u8fd9\u4e9b\u94b1\u5fc5\u987b\u9a6c\u4e0a\u5c31\u8981\u82b1\u51fa\u53bb\uff0c\u4e0d\u7136\u5c31\u53ef\u4ee5\u7b49\u5230\u4ece\u7ecf\u9500\u5546\u624b\u91cc\u62ff\u6765\u4fdd\u8bc1\u91d1\u6765\u589e\u52a0\u516c\u53f8\u7684\u6d41\u52a8\u8d44\u91d1\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u7b14\u878d\u8d44\u644a\u5230\u9526\u57ce\u5934\u4e0a\u662f\u4e00\u5343\u4e94\u767e\u4e07\uff0c\u8d75\u9526\u8363\u9152\u5e2d\u4e4b\u524d\u4e00\u76f4\u4e3a\u6b64\u53d1\u6101\uff0c\u597d\u5728\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u6b23\u7136\u8868\u6001\uff0c\u5e02\u91cc\u7edf\u4e00\u610f\u89c1\u8981\u652f\u6301\u79d1\u738b\u7684\u53d1\u5c55\u81ea\u7136\u4e0d\u662f\u8bf4\u8bf4\u800c\u5df2\u3002</p><p>\u3000\u3000\u9526\u57ce\u5730\u4ea7\u5148\u524d\u5411\u5efa\u8bbe\u94f6\u884c\u7533\u8bf7\u7684\u4e00\u7b14\u62b5\u62bc\u8d37\u6b3e\u4e00\u76f4\u672a\u5f97\u901a\u8fc7\uff0c\u8d75\u9526\u8363\u4e5f\u591a\u6b21\u5e0c\u671b\u5468\u5bcc\u660e\u80fd\u51fa\u9762\u6253\u58f0\u62db\u547c\uff0c\u5468\u5bcc\u660e\u4e4b\u524d\u63a8\u4e09\u963b\u56db\uff0c\u4eca\u5929\u5012\u662f\u5f88\u723d\u5feb\u7684\u7b54\u5e94\u50ac\u4fc3\u5efa\u884c\u8fd9\u51e0\u5929\u5c31\u5c06\u8d37\u6b3e\u6279\u7ed9\u9526\u57ce\u3002</p><p>\u3000\u3000\u90a3\u662f\u4e00\u7b14\u4e09\u5343\u4e07\u7684\u8d37\u6b3e\uff0c\u8d75\u9526\u8363\u62ff\u5e9c\u5929\u5927\u53a6\u4f4e\u5c42\u7684\u7269\u4e1a\u4f5c\u62b5\u62bc\uff0c\u8003\u8651\u5148\u632a\u4e00\u90e8\u5206\u586b\u5165\u79d1\u738b\u7535\u5668\uff0c\u8fd8\u6709\u4e00\u90e8\u5206\u786e\u5b9e\u5176\u4ed6\u5730\u65b9\u6709\u6025\u7528\uff0c\u5012\u662f\u6ca1\u60f3\u5230\u53c8\u63d0\u5230\u4fdd\u8bc1\u91d1\u4e00\u9879\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9664\u4e86\u79d1\u738b\u8fd9\u8fb9\uff0c\u9526\u57ce\u5176\u4ed6\u5730\u65b9\u7684\u8d44\u91d1\u538b\u529b\u4f9d\u7136\u5f97\u4e0d\u5230\u7f13\u89e3\uff0c\u5fc3\u60c5\u81ea\u7136\u4e0d\u80fd\u7b97\u597d\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u3001\u5f20\u6653\u5065\u7b49\u4eba\u5bf9\u6b63\u6cf0\u3001\u9526\u57ce\u3001\u5927\u96c5\u96c6\u56e2\u7684\u5b9e\u529b\u9887\u4e3a\u770b\u91cd\uff0c\u5bf9\u79d1\u738b\u593a\u5f97\u592e\u89c6\u6807\u738b\u540e\u7684\u53d1\u5c55\u4e5f\u5f88\u671f\u5f85\uff0c\u9152\u5e2d\u4e0a\u9664\u4e86\u827e\u9ed8\u4e00\u4e2a\u52b2\u7684\u5927\u5938\u7231\u8fbe\u7535\u5b50\u8ba9\u4ed6\u4eec\u7565\u6709\u4e0d\u723d\u4e4b\u5916\uff0c\u76f8\u5bf9\u8bf4\u6765\uff0c\u4eca\u665a\u7684\u60c5\u7eea\u9971\u6ee1\uff0c\u4e5f\u5f88\u662f\u4eab\u53d7\u5bf9\u672a\u6765\u7684\u5411\u5f80\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5c06\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u3001\u5f20\u6653\u5065\u9001\u8d70\uff0c\u770b\u4e86\u770b\u624b\u8868\uff0c\u5df2\u7ecf\u8fc7\u4e86\u591c\u91cc\u4e5d\u70b9\uff0c\u5bf9\u9648\u9759\u8bf4\u9053\uff1a\u201c\u6211\u591c\u91cc\u8981\u56de\u4e00\u8d9f\u60e0\u5c71\uff0c\u4f60\u662f\u966a\u6211\u56de\u53bb\uff0c\u8fd8\u662f\u2026\u2026\u7b97\u4e86\uff0c\u8fd9\u4e9b\u5929\u4f60\u4e5f\u591f\u8f9b\u82e6\u7684\uff0c\u7559\u5728\u8fd9\u91cc\u597d\u597d\u4f11\u606f\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u624b\u653e\u5728\u5c0f\u8179\u524d\uff0c\u62ff\u7cbe\u81f4\u9cc4\u9c7c\u76ae\u5305\u538b\u7740\u9ad8\u5f39\u7d27\u8eab\u88d9\u6446\uff0c\u89c6\u91ce\u91cc\uff0c\u5f20\u606a\u6b63\u966a\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u4e0a\u8f66\uff0c\u60f3\u8d77\u5728\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u91cc\u8fd8\u7b54\u5e94\u8bf4\u5403\u8fc7\u996d\u540e\u8981\u62db\u5f85\u827e\u9ed8\u53bb\u6d77\u5dde\u9152\u5427\uff0c\u73b0\u5728\u81ea\u7136\u4e0d\u7528\u53bb\uff0c\u4e0d\u8fc7\u5979\u4e5f\u542c\u51fa\u8c22\u5251\u5357\u4e0d\u5e0c\u671b\u81ea\u5df1\u966a\u4ed6\u56de\u60e0\u5c71\u7684\u610f\u601d\uff0c\u5979\u5634\u89d2\u8815\u52a8\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u7238\u7238\u90a3\u8fb9\uff0c\u6211\u5148\u6253\u7535\u8bdd\u8bf4\u4e00\u58f0\u3002\u201d</p><p>\u3000\u3000\u5927\u96c5\u96c6\u56e2\u5dee\u4e0d\u591a\u8fd8\u8981\u62ff\u51fa\u4e24\u5343\u4e07\u6765\uff0c\u9648\u9759\u5bf9\u7236\u4eb2\u516c\u53f8\u7684\u60c5\u51b5\u6bd4\u8f83\u6e05\u695a\uff0c\u957f\u4e45\u4ee5\u6765\u90fd\u4e13\u6ce8\u505a\u65e5\u7528\u5316\u5de5\u7684\u4ea7\u54c1\uff0c\u644a\u5b50\u6ca1\u6709\u94fa\u5f00\uff0c\u89c4\u6a21\u867d\u7136\u4e0d\u6bd4\u6b63\u6cf0\u96c6\u56e2\uff0c\u624b\u91cc\u634f\u7740\u7684\u8d44\u91d1\u5374\u4e0d\u6bd4\u6b63\u6cf0\u96c6\u56e2\u5c11\uff0c\u8fd9\u4e00\u4e24\u5e74\uff0c\u624d\u5f00\u59cb\u5411\u5176\u4ed6\u9886\u57df\u6295\u8d44\u3002</p><p>\u3000\u3000\u9648\u9759\u4e00\u76f4\u5728\u7c73\u5170\u535a\u53ef\u5c3c\u5b66\u9662\u63a5\u53d7\u9ad8\u7b49\u6559\u80b2\uff0c\u56de\u56fd\u624d\u4e00\u5e74\u591a\u65f6\u95f4\uff0c\u4ece\u5979\u5185\u5fc3\u6df1\u5904\u5e76\u4e0d\u5341\u5206\u7684\u8ba4\u540c\u8c22\u5251\u5357\u7ed9\u79d1\u738b\u8bbe\u8ba1\u7684\u53d1\u5c55\u8def\u7ebf\uff0c\u53ea\u662f\u5979\u7236\u4eb2\u9648\u5bb6\u5584\u672c\u4eba\u5bf9\u8fd9\u4e00\u6761\u8def\u7ebf\u5374\u5341\u5206\u7684\u6b23\u8d4f\uff0c\u5979\u4e5f\u65e0\u6cd5\u8bf4\u4ec0\u4e48\u3002\u5979\u4e0e\u5979\u7236\u4eb2\u5728\u4f01\u4e1a\u7ecf\u8425\u4e0a\u7684\u5206\u6b67\u65e9\u5728\u5979\u5728\u5927\u96c5\u96c6\u56e2\u5e02\u573a\u90e8\u5de5\u4f5c\u65f6\u5c31\u6709\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u5230\u79d1\u738b\u624d\u4e89\u5435\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u56de\u5934\u770b\u4e86\u773c\uff0c\u770b\u5230\u827e\u9ed8\u6b63\u77ee\u7740\u8eab\u5b50\u94bb\u8fdb\u8f66\u91cc\uff0c\u4ee5\u4e3a\u9648\u9759\u5728\u770b\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u6b63\u5f0f\u63d0\u5230\u8fd9\u4e8b\u60c5\uff0c\u4f60\u660e\u5929\u5c31\u7ee7\u7eed\u8ddf\u8fdb\u2026\u2026\u201d\u6253\u5f00\u8f66\u95e8\uff0c\u94bb\u8fdb\u8f66\u4e4b\u524d\uff0c\u53c8\u95ee\u9648\u9759\uff0c\u201c\u8981\u4e0d\u8981\u6211\u5148\u9001\u4f60\u56de\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7528\uff0c\u6211\u81ea\u5df1\u56de\u53bb\uff0c\u4e0d\u803d\u6401\u4f60\u65f6\u95f4\u4e86\uff0c\u8def\u4e0a\u5c0f\u5fc3\u4e00\u70b9\u3002\u201d</p><p>\u3000\u3000\u867d\u7136\u9648\u9759\u5230\u6d77\u5dde\u8fd8\u6ca1\u6709\u51e0\u5929\uff0c\u4e0d\u8fc7\u5728\u5979\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u5230\u79d1\u738b\u4e4b\u524d\uff0c\u5979\u7236\u4eb2\u9648\u5bb6\u5584\u5c31\u5728\u5e9c\u5929\u5927\u53a6\u4e8c\u5341\u516b\u5c42\u4e70\u4e0b\u4e00\u5957\u516c\u5bd3\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u94bb\u8fdb\u8f66\u91cc\uff0c\u6253\u7740\u65b9\u5411\u6cbf\u7740\u6797\u836b\u95f4\u7684\u6c34\u6ce5\u752c\u9053\u9a76\u51fa\u6587\u5c71\u996d\u5e97\uff0c\u9a76\u4e0a\u9526\u6e56\u8def\u5c31\u62ff\u51fa\u7535\u8bdd\u6765\u62e8\u4e86\u4e00\u4e2a\u53f7\uff1a\u201c\u5a49\u79c0\uff0c\u6211\u73b0\u5728\u5c31\u56de\u60e0\u5c71\uff0c\u4f60\u5728\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u770b\u7740\u8c22\u5251\u5357\u7684\u5148\u9a76\u51fa\u53bb\uff0c\u5f20\u606a\u5750\u5230\u526f\u9a7e\u9a76\u4f4d\u4e0a\uff0c\u5c06\u5b89\u5168\u5e26\u7cfb\u597d\uff0c\u4ece\u540e\u89c6\u955c\u91cc\u770b\u5230\u9648\u9759\u8fd8\u7ad9\u5728\u90a3\u91cc\uff0c\u8c22\u77bb\u7684\u8f66\u4e5f\u5df2\u7ecf\u542f\u52a8\u8d77\u6765\uff0c\u5c31\u60f3\u5230\u8c22\u5251\u5357\u591c\u91cc\u8981\u51fa\u6d77\u5dde\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u6b63\u5e38\uff0c\u8c22\u5251\u5357\u4ed6\u4eec\u521a\u4ece\u5317\u4eac\u56de\u6765\uff0c\u5317\u4eac\u7684\u60c5\u51b5\u5927\u6982\u4e5f\u8981\u8ddf\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u4ed6\u4eec\u6c47\u62a5\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u8c22\u5251\u5357\u8d76\u56de\u60e0\u5c71\u9664\u4e86\u6c47\u62a5\u5317\u4eac\u4e4b\u884c\u7684\u60c5\u51b5\uff0c\u8fd8\u5411\u6b63\u6cf0\u8bf7\u6c42\u8d44\u91d1\u63f4\u52a9\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u4e86\u4e00\u4f1a\u513f\u795e\uff0c\u5085\u4fca\u5df2\u7ecf\u5c06\u8f66\u5f00\u51fa\u505c\u8f66\u573a\uff0c\u4ece\u540e\u89c6\u955c\u91cc\u518d\u770b\u4e0d\u5230\u5a77\u5a77\u7acb\u4e8e\u521d\u51ac\u6e05\u5bd2\u591c\u98ce\u4e2d\u7684\u9648\u9759\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u8fd9\u4e48\u65e9\u5c31\u5c06\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u4ed6\u4eec\u9001\u56de\u5bbe\u9986\u4f11\u606f\uff0c\u8f66\u5b50\u5f00\u51fa\u6587\u5c71\u996d\u624d\u95ee\u5085\u4fca\uff1a\u201c\u9664\u4e86\u56db\u51e4\u6865\uff0c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u4f1a\u8ba8\u963f\u9ed8\u540c\u5fd7\u7684\u559c\u6b22\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e39\u4e95\u5df7\u6709\u51e0\u5bb6\u65b0\u5f00\u7684\u9152\u5427\uff0c\u53bb\u4e0d\u53bb\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u65f6\u5019\uff0c\u6211\u600e\u4e48\u6ca1\u5370\u8c61\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4f60\u591a\u4e45\u6ca1\u53bb\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4ed4\u7ec6\u60f3\u60f3\uff0c\u8bb8\u601d\u5728\u60e0\u5c71\u4e4b\u884c\u8fc7\u540e\u56de\u6d77\u5dde\u4f4f\u51e0\u5929\u4eba\u5c31\u4e00\u76f4\u7559\u5728\u9999\u6e2f\uff0c\u8bb8\u9e3f\u4f2f\u7b49\u590f\u5b63\u6c5b\u671f\u8fc7\u540e\uff0c\u5c31\u4e00\u76f4\u4e3a\u65b0\u5149\u7eb8\u4e1a\u7684\u8425\u6797\u8ba1\u5212\u5954\u6ce2\uff0c\u9648\u5983\u84c9\u8003\u5165\u4e1c\u6d77\u5927\u5b66\uff0c\u79bb\u5f00\u6d77\u5dde\u8fd8\u6ca1\u6709\u56de\u8fc7\u5bb6\uff0c\u5510\u5a67\u4e5f\u4e0d\u4f1a\u8ba9\u4ed6\u966a\u7740\u53bb\u4e39\u4e95\u5df7\u73a9\uff0c\u4ed4\u7ec6\u60f3\u6765\uff0c\u4eca\u5e74\u590f\u5929\u8fc7\u540e\uff0c\u4ed6\u5c31\u6ca1\u6709\u53bb\u8fc7\u4e39\u4e95\u5df7\u3002</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u4e00\u4e2a\u79cb\u5929\u6ca1\u53bb\uff0c\u80fd\u6709\u591a\u5927\u7684\u53d8\u5316\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u771f\u5f00\u4e86\u51e0\u5bb6\u9152\u5427\uff0c\u4f60\u4e0d\u4f1a\u6dae\u6211\u4eec\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u6211\u7684\u8001\u677f\uff0c\u6211\u80fd\u6709\u51e0\u4e2a\u80c6\u5b50\u6dae\u4f60\uff1f\u201d\u5085\u4fca\u7b11\u7740\u8bf4\uff0c\u5728\u524d\u9762\u8def\u53e3\u5904\u6389\u5934\u8f66\u5934\uff0c\u5f80\u6c99\u7530\u5f00\u8fc7\u53bb\uff0c\u53c8\u56de\u5230\u6587\u5c71\u996d\u5e97\u95e8\u53e3\u65f6\uff0c\u770b\u89c1\u9648\u9759\u5728\u524d\u9762\u94bb\u8fdb\u4e00\u8f86\u51fa\u79df\u8f66\u3002</p><p>\u3000\u3000\u5085\u4fca\u7ed9\u5f20\u606a\u5f00\u8f66\u5f88\u7a33\u91cd\uff0c\u4e00\u4f1a\u513f\u5c31\u7ed9\u51fa\u79df\u8f66\u7529\u4e0d\u89c1\u4e86\u3002</p><p>\u3000\u3000\u827e\u9ed8\u5012\u662f\u5f88\u9057\u61be\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5c0f\u59d0\u8fd8\u8bf4\u665a\u4e0a\u8981\u5e26\u6211\u4eec\u53bb\u9152\u5427HAPPY\u5462\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u544a\u8bc9\u4ed6\uff1a\u201c\u8fd9\u79cd\u793c\u8282\u6027\u7684\u8bdd\u4fdd\u5b88\u7684\u542c\u542c\u5c31\u53ef\u4ee5\u4e86\uff0c\u6211\u4eec\u4e0d\u6b63\u5e26\u4f60\u53bb\u9152\u5427\u6f47\u6d12\u5417\uff1f\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\uff0c\u6700\u597d\u4e0d\u8981\u5bf9\u522b\u4eba\u7684\u5973\u4eba\u52a8\u5fc3\u601d\uff01\u201d\u5b8c\u5168\u6ca1\u6709\u5728\u9152\u5e2d\u4e0a\u7684\u62d8\u8c28\u3002</p><p>\u3000\u3000\u8f66\u5b50\u9a76\u5230\u5e9c\u5929\u5927\u53a6\u4e0e\u4e39\u4e95\u5df7\u4e4b\u95f4\u7684\u5df7\u9053\uff0c\u770b\u89c1\u4e00\u8f86\u51fa\u79df\u8f66\u5728\u516b\u9526\u73cd\u5317\u9762\u7684\u4e00\u95f4\u9152\u5427\u524d\u505c\u4e0b\u6765\uff0c\u9648\u9759\u624b\u634f\u7740\u5764\u5305\u4e0b\u8f66\uff0c\u7ed9\u9713\u8679\u706f\u5149\u4e00\u6620\uff0c\u6709\u7740\u767d\u9886\u5973\u90ce\u591c\u5e55\u4e2d\u5bc2\u5be5\u7684\u6027\u611f\u3002</p></article>"}}}]);
