(window.webpackJsonp=window.webpackJsonp||[]).push([[446],{469:function(p){p.exports={chapterTitle:null,title:"\u7b2c446\u7ae0 \u9152\u5427\u8ff7\u60c5",content:"<article><h2>\u7b2c446\u7ae0 \u9152\u5427\u8ff7\u60c5</h2><p>\u3000\u3000\u770b\u7740\u767d\u9886\u5973\u90ce\u88c5\u626e\u7684\u9648\u9759\u8fdb\u5165\u9152\u5427\uff0c\u5085\u4fca\u5c06\u8f66\u5f00\u5230\u5e9c\u5929\u5927\u53a6\u80cc\u540e\u7684\u505c\u8f66\u573a\uff0c\u5f20\u606a\u4e0b\u8f66\u6765\uff0c\u770b\u7740\u9152\u5427\u7684\u65b9\u5411\uff0c\u8fd8\u662f\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u516b\u9526\u73cd\u79c1\u623f\u83dc\u9986\u540e\u9762\u90a3\u680b\u5b85\u5b50\u597d\u50cf\u4e5f\u662f\u9526\u6e56\u7684\uff0c\u5012\u4e0d\u6e05\u695a\u8fd9\u4e24\u4e09\u4e2a\u6708\u5df2\u7ecf\u6539\u6210\u4e00\u95f4\u9152\u5427\u4e86\u3002</p><p>\u3000\u3000\u201c\u54c7\uff0c\u6d77\u5dde\u7adf\u7136\u6709\u8fd9\u6837\u7684\u5730\u65b9\uff0c\u771f\u662f\u6f02\u4eae\uff01\u201d\u827e\u9ed8\u5927\u4e3a\u8d5e\u53f9\uff0c\u8fd8\u62b1\u6028\u5f20\u606a\u592a\u4e0d\u591f\u610f\u601d\uff0c\u201c\u6d77\u5dde\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u5730\u65b9\uff0c\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u9152\u5427\uff0c\u4f60\u7adf\u7136\u88c5\u4f5c\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u7684\u786e\u4e0d\u77e5\u9053\uff0c\u6709\u4e09\u4e2a\u6708\u6211\u90fd\u6ca1\u6709\u8fc7\u6765\u7684\uff0c\u53d8\u5316\u8fd8\u633a\u5927\uff01\u201d\u5f20\u606a\u644a\u644a\u624b\uff0c\u4ee5\u793a\u65e0\u8f9c\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5750\u5728\u90a3\u91cc\u770b\u7740\uff0c\u4e5f\u5567\u5567\u53eb\u5947\uff1a\u201c\u5916\u9762\u770b\u8d77\u6765\u662f\u5f88\u6709\u5473\u9053\uff0c\u4e0d\u6653\u5f97\u91cc\u9762\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u51b3\u5b9a\u5c06\u6c99\u7530\u4e1c\u7247\u7684\u53e4\u5efa\u7b51\u7fa4\u4fdd\u5b58\u4e0b\u6765\u4ee5\u5f85\u65e5\u540e\u5e02\u91cc\u8d22\u653f\u5bbd\u88d5\u540e\u4fee\u7f2e\uff0c\u4ed6\u4ee5\u9526\u6e56\u7684\u540d\u4e49\uff0c\u4e0d\u65ad\u51fa\u8d44\u8d2d\u4e70\u4e39\u4e95\u5df7\u7684\u5b85\u5b50\u3002</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u662f\u6d77\u5dde\u53e4\u57ce\u5546\u5ba6\u805a\u96c6\u5c45\u4f4f\u7684\u5730\u65b9\uff0c\u5f88\u591a\u662f\u62e5\u6709\u796d\u7956\u5802\u3001\u7ee3\u697c\u3001\u4e66\u623f\u3001\u957f\u5de5\u9662\u3001\u82b1\u623f\u3001\u5bb6\u719f\u7684\u4e09\u8fdb\u5f0f\u56db\u5408\u9662\uff0c\u957f\u8fd1\u4e03\u767e\u7c73\u7684\u4e39\u4e95\u5df7\uff0c\u82e5\u6309\u7167\u5b8c\u6574\u7ed3\u6784\u6765\u8ba1\u7b97\uff0c\u9662\u843d\u53ea\u6709\u4e8c\u5341\u516d\u95f4\uff0c\u623f\u5c4b\u5374\u6709\u4e09\u767e\u516d\u5341\u591a\u95f4\uff0c\u5efa\u56fd\u540e\u641e\u5206\u623f\u5c06\u8fd9\u4e9b\u5efa\u7b51\u90fd\u7ed9\u5206\u62c6\u5f97\u56db\u96f6\u516b\u843d\uff0c\u5927\u9662\u843d\u7ed9\u62c6\u6210\u5c0f\u5ead\u9662\uff0c\u5c0f\u5ead\u9662\u91cc\u53c8\u5efa\u7b80\u6613\u623f\uff0c\u4e0d\u82b1\u5927\u529b\u6c14\u4fee\u7f2e\uff0c\u5f88\u96be\u770b\u5230\u539f\u6765\u7684\u9762\u8c8c\u3002</p><p>\u3000\u3000\u53bb\u5e74\uff0c\u5f20\u606a\u8981\u505a\u7684\u4e8b\u60c5\u5f88\u591a\uff0c\u624b\u91cc\u5934\u7684\u8d44\u91d1\u504f\u7d27\uff0c\u53ea\u662f\u4e70\u4e0b\u4e94\u516d\u5904\u79c1\u5b85\u4e0e\u4e39\u4e95\u5df7\u5357\u7aef\u4e34\u524d\u8857\u7684\u68cb\u9986\u6728\u697c\uff0c\u6700\u5148\u6295\u5165\u8d44\u91d1\u4fee\u7f2e\u6539\u9020\u7684\u5c31\u662f\u68cb\u9986\u4e0e\u4e24\u5904\u79c1\u5b85\uff0c\u68cb\u9986\u5b8c\u5168\u662f\u4fee\u65e7\u5982\u65e7\uff0c\u79c1\u5b85\u5219\u53ea\u662f\u6574\u4f53\u5efa\u7b51\u4fdd\u6301\u660e\u6e05\u7684\u97f5\u5473\uff0c\u5185\u90e8\u5374\u8fdb\u884c\u9002\u5408\u73b0\u4ee3\u751f\u6d3b\u7684\u5927\u6539\u9020\uff0c\u6295\u5165\u7684\u8d44\u91d1\u90fd\u662f\u8d2d\u623f\u6b3e\u7684\u6570\u500d\u3002</p><p>\u3000\u3000\u5e74\u521d\u65f6\uff0c\u9648\u5947\u60f3\u9009\u4e2a\u5730\u65b9\u7cbe\u5fc3\u529e\u4e00\u5bb6\u79c1\u623f\u83dc\u9986\uff0c\u5f20\u606a\u901a\u8fc7\u8bb8\u9e3f\u4f2f\u8fc2\u56de\u529d\u9648\u5947\u5c06\u79c1\u623f\u83dc\u9986\u529e\u5728\u4e39\u4e95\u5df7\uff0c\u5f20\u606a\u51fa\u8d44\u5c06\u9648\u5947\u770b\u4e2d\u7684\u9662\u843d\u4e70\u4e0b\uff0c\u8fd8\u57ab\u8d44\u7ed9\u9648\u5947\u7684\u79c1\u623f\u83dc\u9986\u88c5\u6f62\uff0c\u4e5f\u662f\u51b3\u5b9a\u5c06\u516b\u9526\u73cd\u79c1\u623f\u83dc\u9986\u6253\u9020\u6210\u6d77\u5dde\u79c1\u623f\u83dc\u4e2d\u7684\u7edd\u4f73\u7cbe\u54c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u522b\u7684\u7528\u610f\uff0c\u4ed6\u89c9\u5f97\u8981\u5c06\u6c99\u7530\u4e1c\u7247\u7684\u53e4\u5efa\u7b51\u7fa4\u90fd\u4fdd\u7559\u4e0b\u6765\uff0c\u60df\u4e00\u53ef\u884c\u5c31\u662f\u5546\u4e1a\u4e0e\u65c5\u6e38\u5f00\u53d1\u4e00\u9014\u3002</p><p>\u3000\u3000\u5e9c\u5929\u5927\u53a6\u5efa\u9020\u3001\u6c99\u7530\u524d\u8857\u62d3\u5bbd\uff0c\u4e39\u4e95\u5df7\u5468\u56f4\u7684\u5927\u73af\u5883\u5df2\u7ecf\u6539\u5584\uff0c\u8fd9\u91cc\u2014\u2014\u81f3\u5c11\u662f\u4e39\u4e95\u5df7\u7d27\u90bb\u5e9c\u5929\u5927\u53a6\u7684\u897f\u4fa7\u2014\u2014\u5177\u5907\u5546\u4e1a\u5f00\u53d1\u7684\u6761\u4ef6\uff0c\u5f20\u606a\u662f\u60f3\u9648\u5947\u7684\u516b\u9526\u73cd\u79c1\u623f\u83dc\u9986\u5148\u6765\u8bd5\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u6709\u4ed6\u7684\u7528\u610f\uff0c\u81ea\u7136\u4e0d\u4f1a\u8ba9\u9648\u5947\u4ed6\u4eec\u6765\u62c5\u5176\u4e2d\u7684\u5546\u4e1a\u98ce\u9669\uff0c\u6240\u4ee5\u4ed6\u624d\u57ab\u8d44\u7ed9\u9648\u5947\u7684\u79c1\u623f\u83dc\u9986\u88c5\u6f62\u3002</p><p>\u3000\u3000\u6c99\u7530\u897f\u7247\u62c6\u51fa\u4e00\u5806\u6ca1\u6709\u53ca\u65f6\u59a5\u5f53\u5b89\u7f6e\u7684\u62c6\u8fc1\u6237\uff0c\u5f04\u51fa\u5f88\u5927\u7684\u6028\u6c14\uff0c\u7279\u522b\u662f\u4e07\u52c7\u56e0\u4e3a\u6559\u5e08\u7325\u4eb5\u6848\u4e00\u4e8b\u7ed9\u8c03\u51fa\u6d77\u5dde\uff0c\u6c99\u7530\u4e1c\u7247\u7684\u62c6\u8fc1\u5c31\u6682\u65f6\u505c\u6b47\u4e0b\u6765\u3002\u5e02\u91cc\u6709\u89c4\u5b9a\u6c99\u7530\u8fd9\u91cc\u7684\u5efa\u7b51\u4e0d\u8bb8\u53ef\u62c6\u9664\u91cd\u5efa\uff0c\u4f46\u4e5f\u6ca1\u6709\u7981\u6b62\u623f\u4ea7\u4ea4\u6613\u4e0e\u623f\u5c4b\u4fee\u7f2e\u3002</p><p>\u3000\u3000\u4eca\u5e74\u6765\uff0c\u5f20\u606a\u624b\u5934\u7684\u8d44\u91d1\u5bbd\u88d5\u4e9b\uff0c\u5c31\u4ee5\u9526\u6e56\u7684\u540d\u4e49\u4e0d\u65ad\u7684\u82b1\u9ad8\u4ef7\u8d2d\u8fdb\u79c1\u5b85\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e4b\u524d\u8d2d\u7f6e\u7269\u4e1a\u8fd8\u5077\u5077\u6478\u6478\u7684\uff0c\u4e09\u6708\u521d\u5c06\u9526\u6e56\u4ece\u665a\u6674\u90a3\u91cc\u5b8c\u5168\u7f6e\u5165\u81ea\u5df1\u7684\u540d\u4e0b\uff0c\u8fd9\u4e9b\u4e8b\u5c31\u660e\u76ee\u5f20\u80c6\u8d77\u6765\uff0c\u9526\u6e56\u52a8\u7528\u5dee\u4e0d\u591a\u4e00\u5343\u4e94\u767e\u4e07\u7684\u8d44\u91d1\uff0c\u5dee\u4e0d\u591a\u5c31\u5c06\u5360\u5730\u4e94\u5341\u4ea9\u591a\u4e00\u70b9\u7684\u6574\u6761\u4e39\u4e95\u5df7\u90fd\u7ed9\u5403\u4e86\u4e0b\u6765\uff0c\u652f\u4ed8\u7ed9\u4f4f\u6237\u7684\u8d2d\u623f\u4ef7\u8981\u8fdc\u8fdc\u9ad8\u8fc7\u5e02\u91cc\u7ed9\u51fa\u7684\u62c6\u8fc1\u6807\u51c6\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u6d77\u5dde\u5e02\u4e2d\u5fc3\u533a\uff0c\u50cf\u5c0f\u9526\u6e56\u9644\u8fd1\u7684\u623f\u4ef7\u8fd8\u53ea\u6709\u4e00\u5343\u516d\u6bcf\u5e73\u65b9\u7c73\uff0c\u5e02\u91cc\u7edf\u4e00\u7ed9\u51fa\u7684\u62c6\u8fc1\u8865\u507f\u6807\u51c6\u662f\u516d\u767e\u4e94\u5341\u6bcf\u5e73\u65b9\uff0c\u9526\u6e56\u79c1\u4e0b\u91cc\u4e0e\u4f4f\u623f\u7b7e\u8ba2\u7684\u534f\u8bae\u4ef7\u662f\u62c6\u8fc1\u8865\u507f\u6807\u51c6\u7684\u4e09\u500d\u3002</p><p>\u3000\u3000\u9526\u6e56\u660e\u76ee\u5f20\u80c6\u4e4b\u540e\uff0c\u5e02\u91cc\u81ea\u7136\u542c\u5230\u98ce\u58f0\uff0c\u5468\u5bcc\u660e\u4ee5\u53ca\u5728\u4e07\u52c7\u79bb\u5f00\u4e4b\u540e\u635e\u5230\u4fbf\u5b9c\u5206\u7ba1\u8fd9\u4e00\u5757\u7684\u5e38\u52a1\u526f\u5e02\u957f\u65b9\u5b8f\u6e05\u5c31\u76f8\u5f53\u607c\u706b\uff0c\u9526\u6e56\u8fd9\u4e00\u641e\uff0c\u4ee5\u540e\u6c99\u7530\u4e1c\u7247\u7684\u62c6\u8fc1\u5de5\u4f5c\u5c31\u65e0\u6cd5\u8fdb\u884c\u4e0b\u53bb\uff0c\u4f46\u662f\u5f53\u65f6\u65b0\u5149\u7eb8\u4e1a\u629b\u51fa\u5341\u4ebf\u8425\u6797\u9879\u76ee\u4e4b\u540e\u53c8\u629b\u51fa\u4e94\u5341\u4ebf\u7684\u5236\u6d46\u9879\u76ee\uff0c\u5728\u6574\u4e2a\u4e1c\u6d77\u7701\u91cc\u98ce\u5934\u65e0\u4e24\uff0c\u66f4\u4f55\u51b5\u5510\u5b66\u8c26\u3001\u675c\u5c0f\u5c71\u3001\u5b8b\u57f9\u660e\u90fd\u516c\u7136\u8d28\u7591\u6709\u65e0\u5c06\u6574\u4e2a\u6c99\u7530\u90fd\u62c6\u6389\u5efa\u5546\u4e1a\u533a\u7684\u5fc5\u8981\uff0c\u6c99\u7530\u897f\u7247\u62c6\u8fc1\u5efa\u8bbe\u5b9e\u60c5\u53c8\u662f\u5982\u4f55\uff0c\u6709\u610f\u89c1\u7684\u4eba\u4e5f\u65e0\u6cd5\u5410\u51fa\u4ec0\u4e48\u58f0\u97f3\u3002\u73b0\u5728\uff0c\u867d\u7136\u4ece\u7701\u91cc\u4f20\u6765\u4e00\u4e9b\u597d\u6d88\u606f\uff0c\u4f46\u662f\u5f90\u5b66\u5e73\u6bd5\u7adf\u8fd8\u5728\u4f4d\u5b50\u4e0a\uff0c\u518d\u52a0\u4e0a\u7231\u8fbe\u7535\u5b50\u3001\u65b0\u5149\u7eb8\u4e1a\u98ce\u5934\u6b63\u5065\uff0c\u5468\u5bcc\u660e\u4e5f\u65e0\u5948\u627f\u8ba4\uff0c\u5373\u4f7f\u5e02\u91cc\u6ca1\u6709\u5510\u5b66\u8c26\u7b49\u4eba\u5236\u8098\uff0c\u5f20\u606a\u8fd9\u4e2a\u672a\u6ee1\u4e8c\u5341\u7684\u5c11\u5e74\uff0c\u4e5f\u4e0d\u662f\u4ed6\u8fd9\u4e2a\u5e02\u59d4\u4e66\u8bb0\u80fd\u8f7b\u6613\u64ac\u52a8\u7684\u3002\u6240\u4ee5\u6ca1\u6709\u4efb\u4f55\u4e0d\u5229\u7684\u6d88\u606f\u53cd\u9988\u5230\u5f20\u606a\u90a3\u91cc\u53bb\u3002</p><p>\u3000\u3000\u867d\u7136\u624d\u82b1\u4e00\u5343\u4e94\u767e\u591a\u4e07\u5c31\u5dee\u4e0d\u591a\u5c06\u6574\u6761\u4e39\u4e95\u5df7\u90fd\u4e70\u4e0b\u6765\uff0c\u4f46\u662f\u4fee\u7f2e\u6539\u9020\u6240\u9700\u7684\u8d44\u91d1\u66f4\u4e3a\u5e9e\u5927\u3002\u5f20\u606a\u624b\u5934\u7684\u8d44\u91d1\u8fd8\u4e0d\u5341\u8db3\u5bbd\u88d5\uff0c\u8ba9\u8521\u7eef\u5a1f\u5904\u7406\u8fd9\u4e00\u5757\u8d44\u4ea7\u65f6\uff0c\u53ea\u662f\u8ba9\u5979\u627e\u4e9b\u6709\u7ecf\u9a8c\u7684\u53e4\u5efa\u7b51\u4fee\u7f2e\u65b9\u9762\u7684\u5efa\u7b51\u5de5\u7a0b\u516c\u53f8\uff0c\u5c06\u8fd9\u4e9b\u9662\u843d\u91cc\u7834\u574f\u539f\u6709\u7ed3\u6784\u7684\u7b80\u6613\u5c4b\u68da\u3001\u9694\u5899\u3001\u9e21\u9e2d\u72d7\u820d\u7b49\u7b49\u8bf8\u5982\u6b64\u7c7b\u7684\u4e1c\u897f\u90fd\u6e05\u7406\u51fa\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u5e9c\u5929\u5927\u53a6\u4e1c\u8fb9\u7684\u4fa7\u9053\u91cc\uff0c\u5f80\u5317\u770b\u8fc7\u53bb\uff0c\u8fde\u7740\u65b0\u5f00\u4e86\u56db\u4e94\u5bb6\u5bb6\u5e97\uff0c\u90fd\u5e94\u8be5\u662f\u4eca\u5929\u590f\u5929\u8fc7\u540e\u9646\u7eed\u5f00\u5f20\u7684\uff0c\u5f20\u606a\u77e5\u9053\u8521\u7eef\u5a1f\u73b0\u5728\u4e0d\u53ef\u80fd\u52a8\u7528\u9526\u6e56\u7684\u8d44\u91d1\u5bf9\u8fd9\u4e9b\u5b85\u5b50\u8fdb\u884c\u4fee\u7f2e\u6539\u9020\uff0c\u90a3\u4e48\u5e97\u94fa\u7684\u88c5\u6f62\u60f3\u5fc5\u90fd\u662f\u5e97\u4e3b\u79df\u4e0b\u623f\u5b50\u540e\u81ea\u884c\u6295\u5165\uff0c\u5fc3\u91cc\u5947\u602a\uff1a\u6709\u4eba\u6562\u5230\u8fd9\u91cc\u6765\u6295\u8d44\u5f00\u5e97\u4e86\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u5f88\u65e9\u2014\u2014\u5927\u7ea6\u5728\u516d\u6708\u5e95\u2014\u2014\u5c31\u542c\u8521\u7eef\u5a1f\u63d0\u5230\u8fc7\u6709\u4eba\u6709\u79df\u8fd9\u4e9b\u5b85\u5b50\u7684\u610f\u601d\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u5173\u5fc3\u8fc7\uff0c\u53ea\u8ba9\u8521\u7eef\u5a1f\u770b\u7740\u5904\u7406\u3002\u81ea\u5df1\u7684\u610f\u601d\uff0c\u8521\u7eef\u5a1f\u4e5f\u662f\u660e\u767d\u7684\uff0c\u5c31\u662f\u4e0d\u80fd\u8ba9\u4eba\u518d\u7cdf\u8e4b\u8fd9\u4e9b\u53e4\u5efa\u7b51\u4e86\u3002</p><p>\u3000\u3000\u7ad9\u5728\u5916\u9762\u770b\u5916\u5899\u3001\u95e8\u9762\u88c5\u6f62\uff0c\u5012\u662f\u4e00\u5f0f\u7684\u6a21\u4eff\u516b\u9526\u73cd\u79c1\u623f\u83dc\u9986\u7684\u98ce\u683c\uff0c\u516b\u9526\u73cd\u83dc\u9986\u95e8\u5916\u58c1\u4e0a\u6a2a\u6302\u7740\u201c\u4e39\u4e95\u5df72\u53f7\u201d\u7684\u7bc6\u5199\u6851\u6728\u5236\u95e8\u724c\uff0c\u6709\u60ac\u6302\u5f0f\u5bab\u706f\u60ac\u5728\u95e8\u724c\u4e4b\u4e0a\uff0c\u4eae\u5ea6\u9002\u4e2d\uff0c\u97f5\u5473\u975e\u5e38\u3002\u9648\u9759\u8fdb\u53bb\u7684\u90a3\u9152\u5427\uff0c\u95e8\u5ead\u7684\u5904\u7f6e\u4e0e\u516b\u9526\u73cd\u83dc\u9986\u76f8\u4f3c\uff0c\u4e5f\u662f\u5927\u5c0f\u76f8\u4eff\u3001\u7eb9\u7406\u6e05\u6670\u7684\u6851\u53f6\u95e8\u724c\uff0c\u4e0a\u9762\u5374\u5199\u7740\u201c\u540e\u8857\u9152\u5427\u201d\u56db\u5b57\uff0c\u201c\u4e39\u4e95\u5df76\u53f7\u201d\u5374\u662f\u7528\u5c0f\u4e24\u53f7\u7684\u6977\u4e66\u5199\u5728\u5176\u4e0b\uff0c\u518d\u5f80\u5317\uff0c\u770b\u4e0d\u771f\u5207\uff0c\u4f46\u90fd\u770b\u4e2a\u5927\u6982\uff0c\u6b3e\u5f0f\u4e0e\u5927\u5c0f\u90fd\u76f8\u4eff\uff0c\u5f62\u6210\u6574\u4f53\u7684\u98ce\u683c\uff0c\u56db\u4e94\u5bb6\u5e97\u90fd\u662f\u5982\u6b64\uff0c\u518d\u5f80\u5317\u5c31\u662f\u5f20\u606a\u81ea\u5df1\u51fa\u8d44\u6539\u9020\u8fc7\u7684\u4e00\u680b\u79c1\u5b85\uff0c\u5317\u9762\u8fd8\u597d\u50cf\u5c31\u6ca1\u6709\u4ec0\u4e48\u5e97\u4e86\uff0c\u5145\u5176\u91cf\u4e5f\u5c31\u5df7\u5b50\u4e00\u4fa7\u4e03\u5206\u4e4b\u4e00\u7684\u89c4\u6a21\uff1b\u4ed6\u4e13\u95e8\u7ed9\u8bb8\u601d\u6539\u9020\u7684\u90a3\u680b\u79c1\u5b85\u5728\u4e39\u4e95\u5df7\u7684\u5c3e\u7aef\u4e0a\uff0c\u79bb\u8fd9\u8fb9\u6709\u516d\u767e\u591a\u7c73\uff0c\u53ea\u80fd\u770b\u5230\u6a21\u7cca\u7684\u5f71\u5b50\u3002</p><p>\u3000\u3000\u73b0\u5728\u8fd8\u53ea\u80fd\u8bf4\u662f\u5916\u5899\u4e0e\u95e8\u5ead\u4f5c\u4e86\u5904\u7f6e\uff0c\u52a0\u4e86\u60ac\u6302\u5f0f\u5bab\u706f\u5145\u5f53\u8def\u706f\u7167\u660e\u4e0d\u5931\u53e4\u5473\uff0c\u7ad9\u5728\u4e00\u89d2\u770b\u8fc7\u53bb\u2014\u2014\u770b\u4e0d\u6e05\u5317\u9762\u7684\u9648\u65e7\u4e0e\u7834\u843d\u2014\u2014\u90a3\u81ea\u7136\u662f\u5f88\u6709\u6c14\u52bf\uff0c\u5012\u4e0d\u6653\u5f97\u91cc\u9762\u4f1a\u662f\u600e\u6837\u3002</p><p>\u3000\u3000\u827e\u9ed8\u5012\u662f\u8feb\u4e0d\u53ca\u5f85\u60f3\u8fdb\u53bb\u770b\u7740\u7a76\u7adf\uff0c\u4ed6\u5728\u56fd\u5185\u8fd8\u6ca1\u6709\u770b\u5230\u8fd9\u4e48\u6709\u5473\u9053\u7684\u5efa\u7b51\uff0c\u4f38\u624b\u5de6\u53f3\u6402\u7740\u9648\u4fe1\u751f\u3001\u5f20\u606a\uff0c\u56de\u5934\u5374\u5bf9\u4ed6\u7684\u52a9\u624b\u8bf4\uff1a\u201c\u4f60\u4eec\u81ea\u5df1\u627eHAPPY\u53bb\uff0c\u4e0d\u8981\u8ddf\u7740\u6211\u4eec\u53d7\u62d8\u675f\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u4ed6\u8fd9\u8bdd\u7edd\u5bf9\u662f\u6015\u4ed6\u7684\u52a9\u624b\u7b49\u4f1a\u513f\u4f1a\u6307\u51fa\u4ed6\u5728\u9152\u5427\u91cc\u7684\u884c\u4e3a\u4e0d\u68c0\u70b9\u3002</p><p>\u3000\u3000\u4e01\u69d0\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4e01\u5de5\u8bf7\u5468\u5c0f\u59d0\u4e0e\u5e38\u7ecf\u7406\u5230\u524d\u9762\u68cb\u9986\u559d\u8336\u53bb\uff0c\u6211\u89c9\u5f97\u90a3\u91cc\u624d\u503c\u5f97\u63a8\u8350\u7ed9\u5468\u5c0f\u59d0\u4e0e\u5e38\u7ecf\u7406\u770b\u4e00\u770b\u3002\u201d</p><p>\u3000\u3000\u201c\u884c\uff0c\u4f60\u9886\u7740\u4ed6\u4eec\u5728\u8fd9\u91cc\u8f6c\u4e00\u8f6c\uff0c\u767d\u5929\u4e5f\u6ca1\u6709\u65f6\u95f4\u62db\u5f85\uff0c\u5f88\u662f\u62b1\u6b49\uff0c\u6709\u4e8b\u8054\u7edc\u6211\u3002\u201d</p><p>\u3000\u3000\u8d70\u8fdb\u9152\u5427\uff0c\u8f6c\u8fc7\u7167\u58c1\u5e94\u8be5\u662f\u9662\u4e2d\u5ead\uff0c\u5374\u9519\u843d\u6709\u81f4\u7684\u6392\u6ee1\u5ea7\u4f4d\uff0c\u5f20\u606a\u62ac\u5934\u770b\u5929\uff0c\u5934\u9876\u539f\u6765\u7ed9\u534a\u900f\u660e\u7684\u592a\u9633\u677f\u906e\u4f4f\uff0c\u67b6\u9876\u7684\u7ec6\u94a2\u6881\u4e5f\u7528\u5851\u6599\u5236\u7684\u9752\u85e4\u53f6\u88c5\u9970\uff0c\u4e2d\u5ead\u4e24\u4fa7\u662f\u6728\u5236\u6276\u62e6\uff0c\u6709\u88c5\u9970\u7740\u6f06\u7eb9\u7684\u5eca\u67f1\uff0c\u4e3b\u623f\u7684\u8033\u95e8\u6089\u5df2\u62c6\u9664\uff0c\u53a2\u623f\u3001\u8033\u623f\u4e0e\u8d70\u5eca\u4e4b\u95f4\u7684\u95e8\u7a97\u90fd\u5df2\u53d6\u6389\uff0c\u53ea\u7559\u5728\u4e0b\u7a97\u53f0\uff0c\u4e0d\u8fc7\u95e8\u6963\u3001\u95e8\u69db\u3001\u95e8\u67f1\u7b49\u90fd\u4fdd\u7559\u4e0b\u6765\uff0c\u6574\u4e2a\u4e00\u8fdb\u9662\u843d\u5f62\u6210\u4e00\u4e2a\u5b8c\u6574\u53c8\u6709\u5206\u9694\u7684\u7a7a\u95f4\uff0c\u5374\u4e0d\u4f1a\u5355\u8c03\uff0c\u5de6\u53f3\u53a2\u623f\u662f\u697c\u623f\uff0c\u56de\u5b57\u5f62\u5427\u53f0\u4f4d\u4e8e\u4e2d\u5ead\u4e2d\u5fc3\uff0c\u91cc\u9762\u7684\u706f\u5149\u4e5f\u76f8\u5f53\u6709\u6c34\u51c6\uff0c\u8fd9\u65f6\u4e5d\u70b9\u521a\u8fc7\uff0c\u5e94\u8be5\u662f\u9152\u5427\u521a\u8fdb\u5165\u8425\u4e1a\u72b6\u6001\u7684\u65f6\u5019\uff0c\u4e0d\u8fc7\u91cc\u9762\u5df2\u7ecf\u6709\u4e0d\u5c11\u4eba\uff0c\u9648\u9759\u5c31\u5750\u5728\u5427\u53f0\u524d\u7684\u9ad8\u811a\u51f3\u4e0a\uff0c\u5427\u53f0\u91cc\u7684\u9152\u5f85\u5c06\u4e00\u676f\u6de1\u6f84\u8272\u7684\u9152\u9012\u7ed9\u5979\u3002</p><p>\u3000\u3000\u9648\u9759\u8fdb\u9152\u5427\u4e4b\u540e\u5c31\u5c06\u5979\u8eab\u4e0a\u90a3\u4ef6\u7a0d\u663e\u539a\u91cd\u7684\u5916\u5957\u8131\u4e0b\u653e\u5728\u819d\u76d6\u4e0a\uff0c\u7a7f\u7740\u6de1\u7d2b\u8272\u7684\u7a84\u8377\u53f6\u8fb9\u8936\u76b1\u9886\u895f\u886c\u886b\uff0c\u662f\u8fd9\u4e2a\u9152\u5427\u91cc\u6700\u7cbe\u81f4\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u8896\u7ba1\u5377\u4e86\u534a\u622a\uff0c\u9732\u51fa\u6d01\u767d\u6676\u83b9\u7684\u5c0f\u81c2\uff0c\u8116\u5b50\u4fee\u957f\u4f18\u96c5\uff0c\u808c\u80a4\u5149\u6da6\u5982\u7389\uff0c\u9762\u5bb9\u6709\u7740\u843d\u5bde\u7684\u7cbe\u7f8e\uff0c\u6216\u8bb8\u9152\u5427\u91cc\u7684\u7537\u58eb\u66f4\u6ce8\u610f\u5979\u80f8\u524d\u90a3\u7ed9\u4e25\u5b9e\u5305\u5728\u886c\u886b\u91cc\u7684\u90a3\u4e00\u5bf9\u9971\u6ee1\u633a\u7acb\u7684\u4e73\u5cf0\uff0c\u6709\u7740\u6027\u611f\u800c\u4f18\u96c5\u7684\u9b45\u529b\u3002</p><p>\u3000\u3000\u827e\u9ed8\u8fd9\u5bb6\u4f19\u5fcd\u4e0d\u4f4f\u5c31\u5439\u4e86\u4e00\u58f0\u53e3\u54e8\u3002</p><p>\u3000\u3000\u9648\u9759\u4fa7\u8fc7\u5934\u770b\u5230\u5f20\u606a\u4ed6\u4eec\uff0c\u867d\u7136\u5979\u6709\u6000\u7591\u5f20\u606a\u4ed6\u4eec\u5c3e\u968f\u81ea\u5df1\uff0c\u8fd8\u662f\u5f88\u8282\u5236\u7684\u5c06\u9152\u676f\u653e\u4e0b\uff0c\u8d70\u8fc7\u6765\u6253\u62db\u547c\uff1a\u201c\u55e8\uff0c\u6ca1\u60f3\u5230\u4f60\u4eec\u4e5f\u80fd\u627e\u5230\u8fd9\u91cc\uff0c\u8fd9\u771f\u662f\u4e00\u4e2a\u4e0d\u9519\u7684\u5730\u65b9\uff0c\u827e\u9ed8\u5148\u751f\u89c9\u4e0d\u89c9\u5f97\uff1f\u6211\u521a\u5230\u6d77\u5dde\u6765\u5c31\u559c\u6b22\u4e0a\u8fd9\u91cc\uff0c\u867d\u7136\u4e5f\u6ca1\u6709\u51e0\u5929\u3002\u201d</p><p>\u3000\u3000\u5927\u65b9\u70ed\u60c5\uff0c\u5e72\u7ec3\u6d12\u8131\uff0c\u4e0e\u5f20\u606a\u5f53\u521d\u5728\u9ad8\u901f\u516c\u8def\u9047\u5230\u7684\u70ed\u8fa3\u5973\u90ce\u5f62\u8c61\u5927\u76f8\u5f84\u5ead\uff0c\u773c\u524d\u7684\u9648\u9759\u591a\u5c11\u6709\u4e9b\u7ecf\u5546\u8005\u7684\u7cbe\u660e\u6c14\u8d28\uff0c\u5f20\u606a\u5fc3\u60f3\uff1a\u6216\u8bb8\u5979\u4e00\u76f4\u5c06\u81ea\u5df1\u5f53\u6210\u5546\u4e1a\u5bf9\u624b\u6765\u770b\u5f85\u3002</p><p>\u3000\u3000\u201c\u4f60\u559c\u6b22\u7684\u5730\u65b9\u5dee\u70b9\u5c31\u7ed9\u5168\u62c6\u6389\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u201d\u9648\u9759\u7684\u8bed\u6c14\u8ba9\u522b\u4eba\u542c\u6765\u5979\u5bf9\u5f20\u606a\u7684\u8bdd\u5f88\u4e0d\u53ef\u601d\u8bae\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u62c6\u6389\uff0c\u6211\u5728\u56fd\u5185\u8fd8\u6ca1\u6709\u770b\u5230\u7b2c\u4e8c\u5904\u8fd9\u4e48\u6709\u5473\u9053\u7684\u5730\u65b9\u3002\u201d\u9648\u9759\u9080\u8bf7\u5f20\u606a\u4ed6\u4eec\u5230\u6276\u62e6\u5904\u7684\u5ea7\u4f4d\u4e0a\u5750\u4e0b\uff0c\u8ba9\u5f85\u5e94\u751f\u5c06\u5979\u7559\u5728\u5427\u53f0\u4e0a\u7684\u90a3\u676f\u9152\u4e0e\u9152\u5355\u62ff\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8bf4\u8fd9\u91cc\u4f1a\u7ed9\u5168\u62c6\u6389\uff1f\u201d\u9648\u9759\u793c\u8c8c\u7684\u5c06\u9152\u5355\u9012\u7ed9\u5750\u5728\u5979\u5bf9\u9762\u7684\u9648\u4fe1\u751f\u4e0e\u827e\u9ed8\uff0c\u5374\u5bf9\u5f20\u606a\u521a\u624d\u7684\u8bdd\u5f88\u597d\u5947\uff0c\u8bf4\u8bdd\u65f6\uff0c\u5fae\u4fa7\u7740\u5934\u5c06\u53d1\u4e1d\u64a9\u5230\u8033\u540e\uff0c\u6563\u51fa\u6210\u719f\u5973\u6027\u7684\u8ff7\u4eba\u9b45\u529b\u3002</p><p>\u3000\u3000\u201c\u5e02\u91cc\u7684\u7edf\u4e00\u89c4\u5212\uff0c\u8ddf\u897f\u9762\u4e00\u6837\uff0c\u90fd\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\u3002\u201d\u5f20\u606a\u7b80\u660e\u627c\u8981\u7684\u8bf4\u9053\uff0c\u5c06\u9999\u70df\u4ece\u515c\u91cc\u638f\u51fa\u6765\uff0c\u9648\u4fe1\u751f\u70df\u763e\u8f83\u91cd\uff0c\u81ea\u53d6\u51fa\u4e00\u652f\u9999\u70df\uff0c\u62ff\u8d77\u53f0\u5b50\u4e0a\u7684\u8721\u70db\u70b9\u7740\u4e86\u3002\u5f20\u606a\u56db\u987e\u53c8\u770b\u4e86\u4e00\u773c\u5e97\u91cc\u7684\u88c5\u6f62\u98ce\u683c\uff0c\u6709\u4e9b\u5143\u7d20\u662f\u6284\u88ad\u4ed6\u5728\u6539\u9020\u516b\u9526\u73cd\u83dc\u9986\u3001\u4e39\u4e95\u5df7\u4e24\u680b\u79c1\u5b85\u6240\u82b1\u7684\u5fc3\u601d\uff0c\u63a2\u8fc7\u5934\u95ee\u5750\u5728\u684c\u5b50\u4fa7\u9762\u7684\u5085\u4fca\uff1a\u201c\u8fd9\u91cc\u662f\u8c01\u7684\u5e97\uff1f\u201d\u5f20\u606a\u5fc3\u60f3\uff1a\u4e00\u822c\u4eba\u4e0d\u4f1a\u5728\u5e02\u91cc\u89c4\u5212\u672a\u6539\u7684\u60c5\u51b5\u4e0b\u5728\u88c5\u6f62\u4e0a\u6295\u5165\u8fd9\u4e48\u94b1\uff0c\u771f\u6709\u4eba\u8fd9\u4e48\u505a\uff0c\u5f20\u606a\u8fd8\u60f3\u8ba9\u8521\u7eef\u5a1f\u514d\u4eba\u5bb6\u534a\u5e74\u7684\u79df\u91d1\u9f13\u52b1\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u201c\u90b5\u54e5\u4e00\u76f4\u8981\u7792\u7740\u4f60\uff0c\u8bf4\u8981\u7b49\u4f60\u81ea\u5df1\u770b\u5230\u5413\u4e00\u8df3\u597d\u4e86\u3002\u201d\u5085\u4fca\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u6709\u8fd9\u4e48\u5927\u7684\u52a8\u4f5c\uff0c\u5f20\u606a\u80fd\u7406\u89e3\uff0c\u95ee\u5085\u4fca\uff1a\u201c\u53e6\u4e00\u4fa7\u4e5f\u6709\u52a8\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u201c\u597d\u50cf\u8bf4\u9752\u77f3\u5939\u9053\u4e0d\u6574\u4e00\u4e0b\uff0c\u53e6\u4e00\u4fa7\u4e0d\u597d\u5f04\u3002\u201d</p><p>\u3000\u3000\u5e02\u653f\u7ba1\u7f51\u8981\u6539\uff0c\u9752\u77f3\u94fa\u5c31\u7684\u5939\u9053\u8981\u91cd\u94fa\uff0c\u8fd9\u4e9b\u52a8\u9759\u7684\u786e\u8fd8\u86ee\u5927\uff0c\u5c31\u7b97\u4e0d\u7528\u5e02\u91cc\u638f\u94b1\uff0c\u81ea\u5df1\u8981\u5347\u7ea7\u5e02\u653f\u7ba1\u7f51\uff0c\u8fd8\u8981\u5f97\u5230\u5e02\u91cc\u7684\u6279\u590d\u624d\u884c\u3002</p><p>\u3000\u3000\u201c\u4f60\u8ba4\u8bc6\u8fd9\u91cc\u7684\u8001\u677f\uff1f\u201d\u9648\u9759\u542c\u5230\u8fd9\u91cc\uff0c\u5fcd\u4e0d\u4f4f\u63d2\u5634\u95ee\u3002</p><p>\u3000\u3000\u201c\u7b97\u662f\u8ba4\u8bc6\uff0c\u90fd\u4e0d\u662f\u4f60\u4eec\u79d1\u738b\u559c\u6b22\u7684\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4f1a\u4e0d\u559c\u6b22\uff1f\u201d\u9648\u9759\u8bed\u6c14\u7a0d\u63ba\u4e86\u4e00\u4e9b\u5c0f\u5973\u4eba\u7684\u5473\u9053\uff0c\u660e\u4eae\u7684\u7738\u5b50\u6014\u6014\u7684\u770b\u7740\u5f20\u606a\uff0c\u201c\u770b\u8fd9\u5bb6\u5e97\uff0c\u5c31\u77e5\u9053\u4e3b\u4eba\u7684\u54c1\u5473\u4e0d\u5dee\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u4e3b\u8981\u662f\u8bbe\u8ba1\u5e08\u7684\u529f\u52b3\uff0c\u8fd8\u6709\u5f88\u591a\u5143\u7d20\u662f\u6284\u88ad\u81ea\u5df1\u7684\uff0c\u90b5\u81f3\u521a\u6709\u80fd\u529b\u4e0d\u5047\uff0c\u683c\u8c03\u591a\u9ad8\u96c5\u5374\u672a\u5fc5\uff0c\u5f53\u7136\u6709\u88c5\u9ad8\u96c5\u7684\u65f6\u5019\uff0c\u7279\u6b8a\u4ed6\u73b0\u5728\u4e13\u5fc3\u7ecf\u8425\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u96c6\u56e2\uff0c\u5012\u662f\u65f6\u65f6\u523b\u523b\u4e0d\u5fd8\u9ad8\u96c5\u7684\u683c\u8c03\u3002</p><p>\u3000\u3000\u9648\u9759\u53c8\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u5f88\u53ef\u60dc\uff0c\u6574\u6761\u5df7\u5b50\u53ea\u6709\u8fd9\u4e48\u4e00\u5c0f\u6bb5\u662f\u8fd9\u6837\u5b50\uff0c\u8981\u6574\u4e2a\u7684\u90fd\u6539\u6210\u9152\u5427\u4e00\u6761\u8857\uff0c\u60f3\u5fc5\u4f1a\u975e\u5e38\u6709\u7279\u8272\u3002\u201d</p><p>\u3000\u3000\u201c\u9648\u9759\u5bf9\u8fd9\u4e2a\u5012\u5f88\u6709\u5174\u8da3\uff0c\u201d\u5f20\u606a\u5fc3\u60f3\u9648\u9759\u5927\u6982\u5bf9\u6c99\u7530\u8fd9\u4e00\u7247\u6bd4\u8f83\u719f\u6089\u4e86\uff0c\u4e5f\u4e50\u610f\u5411\u4e00\u4e2a\u7f8e\u5973\u591a\u8bf4\u4e9b\u8bdd\uff0c\u201c\u8fd9\u6761\u5df7\u5b50\u5357\u5317\u6709\u4e03\u767e\u7c73\uff0c\u8981\u7b97\u5b8c\u6574\u7684\u9662\u843d\u53ea\u6709\u4e8c\u5341\u591a\u95f4\uff0c\u8fd9\u95f4\u9152\u5427\u53ea\u662f\u4e00\u5ea7\u4e09\u8fdb\u5f0f\u56db\u5408\u9662\u7684\u4e00\u8fdb\u7f62\u4e86\uff0c\u5168\u90e8\u90fd\u6539\u9020\u8fd9\u6837\u89c4\u6a21\u7684\u9152\u5427\uff0c\u5dee\u4e0d\u591a\u8981\u6709\u4e03\u516b\u5341\u5bb6\u4e4b\u591a\uff0c\u6574\u4e2a\u6d77\u5dde\u5e02\u9700\u8981\u8fd9\u4e48\u591a\u5bb6\u4e2d\u9ad8\u7ea7\u9152\u5427\uff1f\u201d</p><p>\u3000\u3000\u771f\u6b63\u4f5c\u4e3a\u4e2d\u9ad8\u6863\u6d88\u8d39\u573a\u6240\u7684\u9152\u5427\uff0c\u5728\u6d77\u5dde\u7684\u5e02\u573a\u5e76\u4e0d\u5927\uff0c\u6d77\u5dde\u5e76\u4e0d\u662f\u4e00\u4e2a\u5341\u5206\u6ce8\u91cd\u4f11\u95f2\u4e0e\u4eab\u53d7\u7684\u57ce\u5e02\uff0c\u5e74\u8f7b\u4e00\u4ee3\u7684\u6d88\u8d39\u65f6\u5c1a\u8fd8\u6ca1\u6709\u57f9\u517b\u8d77\u6765\uff0c\u8fd9\u7c7b\u9152\u5427\u4e5f\u4e0d\u53d7\u66b4\u5bcc\u8d77\u6765\u7684\u90a3\u6279\u4eba\u6b22\u8fce\uff0c\u5373\u4f7f\u5341\u5e74\u540e\uff0c\u66f4\u591a\u7684\u4e5f\u662f\u63d0\u4f9b\u8f6f\u8272\u60c5\u670d\u52a1\u6216\u63d0\u4f9b\u8272\u60c5\u6d88\u606f\u7684\u4f1a\u6240\u3001\u4ff1\u4e50\u90e8\u3001KTV\u3001\u9152\u5427\u7b49\u3002\u7b49\u6d77\u5dde\u5e02\u7684\u767d\u9886\u9636\u5c42\u6709\u4e86\u4e00\u5b9a\u7684\u89c4\u6a21\uff0c\u8fd9\u79cd\u60c5\u51b5\u4f1a\u6709\u6240\u597d\u8f6c\uff0c\u4f46\u662f\u73b0\u5728\u5c06\u8fd9\u6574\u6761\u5df7\u5b50\u90fd\u641e\u6210\u7279\u8272\u9152\u5427\u5c31\u53ea\u80fd\u6298\u672c\u7ecf\u8425\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u53ea\u662f\u968f\u610f\u611f\u6168\u4e86\u4e00\u53e5\uff0c\u542c\u5f20\u606a\u8bf4\u4e86\u8fd9\u4e48\u4e00\u901a\uff0c\u53ea\u5f53\u4ed6\u62ff\u5546\u4e1a\u7ecf\u8425\u4e0a\u7684\u6559\u8bad\u81ea\u5df1\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u4e50\u610f\uff0c\u827e\u9ed8\u7f20\u7740\u5979\u8bf4\u8bdd\uff0c\u4fbf\u63a5\u8fc7\u8bdd\u9898\u53bb\u3002</p><p>\u3000\u3000\u97f3\u4e50\u6d41\u6dcc\uff0c\u6709\u4e9b\u719f\u6089\uff0c\u5f20\u606a\u542c\u4e86\u534a\u5929\u672a\u80fd\u60f3\u662f\u4ec0\u4e48\uff0c\u4fbf\u4e0d\u518d\u60f3\uff0c\u6253\u7535\u8bdd\u95ee\u8bb8\u601d\u5728\u54ea\u91cc\uff0c\u542c\u8bb8\u601d\u5728\u5bb6\u91cc\u8ddf\u5979\u5988\u804a\u5929\uff0c\u4fbf\u8bf4\u8981\u5f00\u8f66\u53bb\u63a5\u5979\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u8eab\u6765\uff0c\u8ba9\u9648\u4fe1\u751f\u4e0e\u827e\u9ed8\u5728\u8fd9\u91cc\u7a0d\u5019\uff0c\u4ece\u5085\u4fca\u90a3\u62ff\u6765\u8f66\u94a5\u5319\u4fbf\u53bb\u63a5\u8bb8\u601d\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u5973\u4eba\uff0c\u9648\u9759\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u8bb8\u601d\u624d\u662f\u90a3\u79cd\u5a07\u5a9a\u5165\u9aa8\u3001\u6625\u8272\u6d53\u90c1\u7684\u5973\u4eba\uff0c\u4e5f\u80fd\u770b\u51fa\u5979\u4e0e\u5f20\u606a\u4e4b\u95f4\u4e0d\u540c\u5bfb\u5e38\u7684\u5173\u7cfb\uff0c\u5973\u4eba\u7684\u76f4\u611f\u4e0d\u5bb9\u5ffd\u89c6\uff0c\u9648\u9759\u4e5f\u542c\u8bf4\u8fc7\u5f20\u606a\u4e0e\u5510\u5b66\u8c26\u8fd8\u5728\u8bfb\u9ad8\u4e2d\u7684\u5973\u513f\u662f\u9752\u6885\u7af9\u9a6c\u7684\u604b\u4eba\uff0c\u8fd9\u4e2a\u57ce\u5e02\u91cc\u8fd8\u6d41\u4f20\u7740\u8bb8\u591a\u5f20\u606a\u7684\u7ea8\u7ed4\u4e8b\u8ff9\uff0c\u90a3\u5e94\u8be5\u5c31\u662f\u82b1\u82b1\u516c\u5b50\u4e86\uff0c\u5373\u4f7f\u4ed6\u6709\u7740\u8ba9\u4eba\u60ca\u8273\u7684\u5546\u4e1a\u5929\u8d4b\uff0c\u8ddf\u4ed6\u662f\u4e0d\u662f\u82b1\u82b1\u516c\u5b50\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u6216\u8bb8\u4e5f\u53ea\u6709\u8fd9\u6837\u7684\u7537\u4eba\u624d\u6709\u6536\u85cf\u90a3\u4e9b\u7cbe\u81f4\u5230\u6781\u70b9\u7684\u5973\u4eba\u7684\u8d44\u683c\u5427\uff0c\u53ef\u60dc\u7f8e\u5973\u5e76\u4e0d\u662f\u4e00\u4ef6\u53ef\u4ee5\u957f\u4e45\u4fdd\u5b58\u7684\u827a\u672f\u54c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f88\u5feb\u5c31\u4e0e\u8bb8\u601d\u8fdb\u6765\uff0c\u9648\u9759\u5fc3\u60f3\uff1a\u4e5f\u8bb8\u4ed6\u4eec\u5728\u8f6c\u8fc7\u7167\u58c1\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u624b\u8fd8\u662f\u7275\u5728\u4e00\u8d77\u7684\u5427\u3002</p><p>\u3000\u3000\u9648\u9759\u5f88\u96be\u4ece\u5f20\u606a\u660e\u4fca\u6e05\u79c0\u8138\u5e9e\u770b\u51fa\u82b1\u82b1\u516c\u5b50\u7684\u6c14\u8d28\u6765\uff0c\u5012\u662f\u6709\u7740\u4f18\u96c5\u6e05\u6de1\u7684\u4e16\u5bb6\u5b50\u5f1f\u6c14\u5ea6\uff0c\u5bf9\u81ea\u5df1\u7684\u611f\u89c9\u53c8\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u660e\u660e\u662f\u4e2a\u6ee5\u60c5\u53c8\u8584\u60c5\u7684\u4eba\u554a\uff01</p><p>\u3000\u3000\u9648\u9759\u4f18\u96c5\u7684\u7ad9\u8d77\u6765\uff0c\u5c06\u8eab\u8fb9\u7684\u5ea7\u4f4d\u8ba9\u7ed9\u8bb8\u601d\uff0c\u8bb8\u601d\u5c06\u5916\u5957\u8131\u4e0b\u6765\uff0c\u5979\u91cc\u9762\u7a7f\u7740\u5979\u5988\u7ed9\u5979\u624b\u5de5\u7ec7\u7684\u6d45\u5496\u5561\u8272\u6bdb\u7ebf\u8863\uff1b\u5f20\u606a\u624b\u91cc\u8fd8\u63d0\u7740\u4e00\u53ea\u7eb8\u888b\uff0c\u91cc\u9762\u88c5\u7740\u4e00\u4ef6\u8bb8\u601d\u5979\u5988\u7ed9\u4ed6\u7ec7\u7684\u6bdb\u7ebf\u8863\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7684\u5230\u6765\uff0c\u8ba9\u9648\u9759\u611f\u5230\u5c24\u5176\u7684\u5bc2\u5bde\uff0c\u867d\u7136\u5728\u4e00\u5904\u4e0d\u9519\u7684\u5730\u65b9\uff0c\u4f46\u6bd5\u7adf\u662f\u5f02\u4e61\u7684\u57ce\u5e02\uff0c\u5468\u56f4\u5750\u7740\u53c8\u662f\u5979\u6240\u5fc5\u987b\u5c0f\u5fc3\u5e94\u4ed8\u7684\u4eba\uff0c\u4e00\u65f6\u95f4\u6709\u4e9b\u987e\u5f71\u81ea\u601c\uff0c\u5fc3\u60f3\uff1a\u5373\u4f7f\u5f20\u606a\u662f\u591a\u60c5\u7684\uff0c\u4f46\u662f\u5bf9\u4e8e\u8bb8\u601d\u8fd9\u4e48\u4e00\u4e2a\u8273\u5a9a\u503e\u57ce\u7684\u5973\u5b50\u6765\u8bf4\uff0c\u4e5f\u662f\u53ef\u4ee5\u503c\u5f97\u4f9d\u9760\u4e0e\u4fe1\u8d56\u7684\u3002</p><p>\u3000\u3000\u5728\u9152\u5427\u91cc\u6c89\u8ff7\u7684\u559d\u7740\u9152\uff0c\u9648\u4fe1\u751f\u4e5f\u6ca1\u6709\u5728\u6587\u5c71\u996d\u5e97\u91cc\u90a3\u4e48\u62d8\u675f\uff0c\u4e01\u69d0\u3001\u4e01\u78ca\u4e0e\u827e\u9ed8\u3001\u9648\u4fe1\u751f\u7684\u52a9\u7406\u8fc7\u4e86\u5341\u70b9\u534a\u624d\u8fc7\u6765\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5f53\u4e2d\u591a\u4e86\u90b5\u81f3\u521a\uff0c\u5f20\u606a\u90fd\u4e0d\u6e05\u695a\u4ed6\u4eec\u600e\u4e48\u9047\u4e0a\u7684\uff0c\u8fd8\u662f\u4e01\u69d0\u7279\u610f\u627e\u5230\u90b5\u81f3\u521a\uff1f\u4ed6\u4eec\u6328\u7740\u5f20\u606a\u4ed6\u4eec\u90a3\u684c\u5750\u4e0b\u3002\u60f3\u5fc5\u4ed6\u4eec\u521a\u521a\u8d70\u4e86\u4e0d\u5c11\u5730\u65b9\uff0c\u827e\u9ed8\u90a3\u4e2a\u767d\u5929\u770b\u8d77\u6765\u8fc7\u5206\u4e25\u8083\u7684\u52a9\u624b\u5468\u71d5\u971e\u5230\u9152\u5427\u91cc\u5012\u5f88\u5174\u594b\uff0c\u5411\u5f80\u7684\u8bf4\u9053\uff1a\u201c\u771f\u662f\u6f02\u4eae\uff0c\u6211\u771f\u60f3\u5728\u8fd9\u91cc\u4e70\u680b\u5b85\u5b50\uff0c\u8ba9\u6211\u5148\u751f\u8ddf\u513f\u5b50\u90fd\u4f4f\u8fc7\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u5468\u5c0f\u59d0\u4e00\u5bb6\u60f3\u56de\u56fd\u5b9a\u5c45\uff1f\u201d\u5f20\u606a\u773c\u775b\u53d1\u4eae\uff0c\u6002\u607f\u5979\u8bf4\uff0c\u201c\u4f60\u9a6c\u4e0a\u8ddf\u4f60\u8001\u677f\u8f9e\u804c\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e24\u4e2a\u4eba\uff0c\u4f60\u53ef\u4ee5\u4efb\u9009\u4e00\u4e2a\u5f53\u8001\u677f\uff0c\u4e39\u4e95\u5df7\u7684\u5b85\u5b50\uff0c\u4f60\u9009\u4e00\u680b\uff0c\u6211\u8ba9\u8001\u90b5\u5728\u4f60\u5bb6\u4eba\u56de\u56fd\u4e4b\u524d\u7167\u4f60\u7684\u610f\u601d\u90fd\u51c6\u5907\u59a5\u5f53\uff0c\u8d39\u7528\u90fd\u7b97\u9526\u6e56\u7684\u3002\u201d</p><p>\u3000\u3000\u827e\u9ed8\u6297\u8bae\u9053\uff1a\u201c\u8fd9\u4e2a\u8bdd\u9898\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u5077\u5077\u6478\u6478\u7684\u80cc\u7740\u6211\u8c08\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u54c8\u54c8\u7b11\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u7b11\u7740\u5bf9\u827e\u9ed8\u8bf4\uff0c\u201c\u4f60\u4e5f\u53ef\u4ee5\u5f00\u51fa\u540c\u6837\u7684\u6761\u4ef6\u633d\u7559\u4f18\u79c0\u7684\u5458\u5de5\u554a\u3002\u201d\u5f53\u7136\uff0c\u4ed6\u77e5\u9053\u5468\u71d5\u971e\u8fd9\u4e9b\u9ad8\u7ea7\u804c\u5458\u5728\u65af\u9ad8\u67cf\u7684\u5f85\u9047\u4e0d\u4f1a\u4f4e\uff0c\u8ba1\u5212\u4e0e\u65af\u9ad8\u67cf\u5728\u6d77\u5dde\u641e\u4e2a\u5408\u4f5c\u9879\u76ee\uff0c\u8ba9\u5468\u71d5\u971e\u5230\u6d77\u5dde\u6765\u8d1f\u8d23\u66f4\u73b0\u5b9e\u4e00\u4e9b\uff0c\u53c8\u6307\u7740\u90b5\u81f3\u521a\u5411\u9648\u9759\u4ecb\u7ecd\u8bf4\uff1a\u201c\u4ed6\u5c31\u662f\u8fd9\u5bb6\u5e97\u7684\u5e55\u540e\u8001\u677f\uff0c\u4f60\u770b\u4ed6\u50cf\u662f\u5f88\u6709\u54c1\u5473\u7684\u4eba\u5417\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8eab\u6750\u4e2d\u7b49\uff0c\u5934\u53d1\u4e0d\u957f\uff0c\u65b9\u8138\uff0c\u773c\u775b\u5f88\u6709\u4eae\uff0c\u4e5f\u80fd\u8c08\u5f97\u4e0a\u5112\u96c5\u6c14\u5ea6\uff0c\u5085\u4fca\u5c06\u4f4d\u5b50\u6362\u7ed9\u4ed6\u5750\u8fc7\u6765\u3002</p><p>\u3000\u3000\u9648\u9759\u5e72\u7ec3\u5927\u65b9\u7684\u4ece\u5979\u7cbe\u81f4\u7684\u5764\u5305\u91cc\u62ff\u51fa\u540d\u7247\u4e0e\u90b5\u81f3\u521a\u4e92\u6362\uff0c\u770b\u5230\u540d\u7247\u4e0a\u7684\u540d\u5b57\uff0c\u81ea\u7136\u5c31\u77e5\u9053\u662f\u8c01\u4e86\uff0c\u5979\u5230\u6d77\u5dde\u6765\uff0c\u4e0d\u53ef\u80fd\u4e0d\u4e86\u89e3\u4e00\u4e9b\u6d77\u5dde\u5e02\u7684\u98ce\u60c5\u4eba\u7269\uff0c\u7279\u522b\u662f\u8d75\u9526\u8363\u3001\u8c22\u77bb\u7b49\u4eba\u65e9\u5c31\u5c06\u90b5\u81f3\u521a\u89c6\u4f5c\u5510\u5b66\u8c26\u3001\u5f20\u606a\u4e00\u7cfb\uff0c\u4ece\u5218\u660e\u8f89\u90a3\u91cc\u53c8\u53ef\u80fd\u77e5\u9053\u66f4\u591a\u4ed6\u4eec\u5408\u4f5c\u7684\u7ec6\u8282\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b56\u5212\u4e86\u56db\u51e4\u6865\u9879\u76ee\uff0c\u90b5\u81f3\u521a\u662f\u5177\u4f53\u7684\u6267\u884c\u4eba\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5408\u4f5c\u6700\u65e9\u5f00\u59cb\u4e8e\u90a3\u65f6\u3002\u90b5\u81f3\u521a\u6709\u8bb8\u591a\u5730\u65b9\u662f\u8ba9\u5218\u660e\u8f89\u94a6\u4f69\u7684\uff0c\u5218\u660e\u8f89\u4e5f\u662f\u4e3a\u4e86\u62d4\u9ad8\u81ea\u5df1\u7684\u9700\u8981\uff0c\u90fd\u4e00\u5b9a\u7a0b\u5ea6\u62d4\u9ad8\u4e86\u805a\u96c6\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u90a3\u7fa4\u4eba\uff0c\u4e0d\u4ec5\u5305\u62ec\u82cf\u6d25\u4e1c\u3001\u5468\u6e38\u3001\u9c81\u5e86\u751f\u3001\u848b\u8587\u3001\u8521\u7eef\u5a1f\u7b49\u4eba\uff0c\u4e5f\u5305\u62ec\u76db\u9752\u3001\u90b5\u81f3\u521a\u7b49\u4eba\uff0c\u4e5f\u5305\u62ec\u5f20\u606a\u7684\u5802\u53d4\u5f20\u77e5\u975e\u3002</p><p>\u3000\u3000\u201c\u8fd9\u95f4\u7684\u9152\u5427\u4ee5\u53ca\u8fd9\u5c0f\u534a\u6761\u5df7\u5b50\u7684\u98ce\u683c\u975e\u5e38\u7684\u72ec\u7279\uff0c\u5927\u6982\u662f\u6211\u5728\u56fd\u5185\u89c1\u5230\u6700\u7f8e\u7684\u5efa\u7b51\uff0c\u201d\u9648\u9759\u5012\u662f\u4e00\u76f4\u90fd\u60f3\u8ddf\u90b5\u81f3\u521a\u8fd9\u4e9b\u4f20\u8bf4\u4e2d\u6d77\u5dde\u7684\u5546\u4e1a\u7cbe\u82f1\u63a5\u89e6\u7684\uff0c\u4e0d\u8fc7\u4eca\u665a\u7684\u6c1b\u56f4\u7684\u786e\u6709\u4e9b\u5947\u602a\uff0c\u6709\u8eab\u5904\u654c\u8425\u7684\u60b2\u58ee\uff0c\u9648\u9759\u6c14\u5ea6\u4f18\u96c5\u7684\u8bf4\u9053\uff0c\u201c\u60f3\u95ee\u90b5\u5148\u751f\u4e00\u53e5\uff0c\u4f60\u6ca1\u6709\u5c06\u8fd9\u4e00\u98ce\u683c\u8d2f\u5f7b\u6574\u6761\u4e39\u4e95\u5df7\u7684\u8ba1\u5212\u5417\uff1f\u90a3\u6837\uff0c\u6211\u60f3\u8fd9\u5c06\u662f\u6d77\u5dde\u6700\u5177\u9b45\u529b\u7684\u4e00\u89d2\u3002\u201d</p></article>"}}}]);
