(window.webpackJsonp=window.webpackJsonp||[]).push([[447],{469:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e03\u8282 \u5bc2\u5bde\u4e2a\u5934",content:"<article><h2>\u7b2c\u4e09\u5341\u4e03\u8282 \u5bc2\u5bde\u4e2a\u5934</h2><p>\u539f\u672c\u77f3\u5316\u4e86\u4e00\u4e2a\u80d6\u8001\u5934\uff0c\u7ed3\u679c\u8ff8\u51fa\u6765\u4e00\u4e2a\u9ec4\u91d1\u94e0\u7532\u7684\u9ad8\u5927\u8eab\u5f71\uff0c\u8fd8\u53e3\u53e3\u58f0\u58f0\u201c\u6742\u79cd\u201d\u3001\u201c\u672c\u738b\u201d\uff0c\u8fd9\u53d8\u5316\u7740\u5b9e\u592a\u7a81\u5140\uff0c\u4ee5\u81f3\u4e8e\u743c\u6069\u7684\u8111\u5b50\u4e00\u65f6\u95f4\u90fd\u6ca1\u80fd\u8f6c\u8fc7\u5f2f\u6765\u3002\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u6709\u4e00\u4ef6\u4e8b\u662f\u786e\u5b9a\u65e0\u7591\u7684\uff1a\u773c\u524d\u8fd9\u5bb6\u4f19\uff0c\u5168\u8eab\u4e0a\u4e0b\u6ca1\u6709\u4e00\u4e1d\u4e00\u6beb\u53ef\u4ee5\u79f0\u4e4b\u4e3a\u201c\u5584\u610f\u201d\u7684\u4e1c\u897f\u3002</p><p>\u201c\u4f60\u662f\u8c01\uff1f\u201d\u4ed6\u6212\u5907\u5730\u95ee\uff0c\u624b\u4e2d\u5df2\u7ecf\u63e1\u4f4f\u4e86\u6cd5\u6756\u3002</p><p>\u60ac\u6d6e\u5728\u534a\u7a7a\u4e2d\u7684\u9ec4\u91d1\u8eab\u5f71\u4e0d\u60a6\u5730\u671d\u4ed6\u770b\u8fc7\u6765\uff0c\u90a3\u662f\u4e2a\u9762\u5bb9\u4fca\u79c0\u7684\u9752\u5e74\u4eba\uff0c\u91d1\u53d1\u8d64\u77b3\uff0c\u4eea\u8868\u5802\u5802\uff0c\u5e26\u7740\u6781\u5ea6\u7684\u50b2\u6162\u795e\u60c5\u3002\u5f53\u4ed6\u6ce8\u89c6\u743c\u6069\u7684\u65f6\u5019\uff0c\u7a7a\u6c14\u4e2d\u793a\u5a01\u822c\u5730\u95ea\u70c1\u7740\u8f9f\u556a\u505a\u54cd\u7684\u7535\u706b\u82b1\uff0c\u4e00\u9053\u9053\u7ec6\u788e\u7684\u95ea\u7535\u6253\u5728\u9752\u94dc\u5730\u9762\u4e0a\uff0c\u4e0d\u65f6\u5192\u51fa\u4e00\u7f15\u8f7b\u70df\u3002\u201c\u539f\u6765\u662f\u53ea\u4f0a\u739b\u65af\u5361\u7684\u877c\u8681\uff0c\u201d\u4ed6\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u9f3b\u5b54\u91cc\u8f7b\u8511\u5730\u54fc\u51fa\u58f0\uff0c\u201c\u96be\u9053\u672c\u738b\u8fd8\u6ca1\u6709\u5c06\u4f60\u4eec\u65a9\u5c3d\u6740\u7edd\u5417\u2026\u2026\u5514\uff1f\u201d</p><p>\u8bb6\u5f02\u58f0\u4ece\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u53e3\u4e2d\u53d1\u51fa\uff0c\u50cf\u662f\u7a81\u7136\u53d1\u73b0\u4e86\u4ec0\u4e48\u6781\u5ea6\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u8d64\u7ea2\u7684\u53cc\u77b3\u6b7b\u6b7b\u76ef\u7740\u743c\u6069\u6000\u91cc\u7684\u73ca\u5609\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u4ed4\u7ec6\u6253\u91cf\u7740\uff0c\u8138\u4e0a\u795e\u60c5\u5148\u662f\u8be7\u5f02\uff0c\u7ee7\u800c\u91ca\u7136\uff0c\u6700\u540e\u604d\u7136\u5927\u609f\u822c\uff0c\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002</p><p>\u201c\u539f\u6765\u8fd9\u91cc\u8fd8\u6709\u4e00\u4f4d\u6545\u4eba\uff01\u201d\u4ed6\u8138\u4e0a\u867d\u7136\u5728\u7b11\uff0c\u8bed\u8c03\u5374\u662f\u523a\u9aa8\u7684\u51b0\u51b7\uff0c\u7535\u5149\u5728\u53cc\u773c\u4e2d\u5371\u9669\u5730\u95ea\u70c1\u7740\uff0c\u6740\u610f\u6e05\u6670\u5f97\u4eff\u4f5b\u5df2\u7ecf\u51dd\u805a\u6210\u5b9e\u4f53\uff0c\u201c\u5f88\u4e45\u4e0d\u89c1\u4e86\uff0c\u5c0f\u59d1\u5a18\uff0c\u524d\u6b21\u4e00\u522b\uff0c\u500f\u5ffd\u5343\u5e74\uff0c\u672c\u738b\u5bf9\u4f60\u53ef\u662f\u5341\u5206\u7684\u60f3\u5ff5\u5462\u3002\u201d</p><p>\u8bf4\u7740\u4ee4\u4eba\u8d39\u89e3\u7684\u8bdd\uff0c\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u51cc\u7a7a\u5411\u524d\u8d70\u6765\uff0c\u4f38\u624b\u50cf\u662f\u8981\u6765\u6293\u73ca\u5609\u4f3c\u7684\u3002\u743c\u6069\u4e0d\u5047\u601d\u7d22\uff0c\u6a2a\u8de8\u4e00\u6b65\u5c06\u73ca\u5609\u6321\u5728\u8eab\u540e\uff0c\u201c\u6eda\u5f00\uff01\u201d\u4ed6\u5389\u58f0\u8bf4\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u7d2b\u6c34\u6676\u6cd5\u6756\uff0c\u516d\u9897\u7eef\u7ea2\u4e4b\u6cea\u6b21\u7b2c\u4eae\u8d77\uff0c\u8054\u6210\u4e00\u7ebf\uff0c\u7fe0\u7eff\u7684\u5149\u7403\u5728\u6756\u7aef\u5f00\u59cb\u51dd\u805a\uff0c\u5373\u5c06\u6210\u578b\u3002</p><p>\u9ec4\u91d1\u9752\u5e74\u7684\u8eab\u5f62\u4e00\u6ede\uff0c\u968f\u5373\u8138\u4e0a\u9732\u51fa\u52c3\u7136\u5927\u6012\u7684\u795e\u60c5\uff0c\u201c\u4f0a\u739b\u65af\u5361\u7684\u722c\u866b\uff0c\u80c6\u6562\u963b\u6321\u672c\u738b\u7684\u53bb\u8def\u5417\uff1f\u201d\u4ed6\u4f4e\u559d\u7740\uff0c\u72c2\u98ce\u5728\u8eab\u4f53\u5468\u56f4\u76d8\u65cb\u547c\u5578\uff0c\u5939\u6742\u7740\u9690\u9690\u96f7\u9e23\uff0c\u5341\u4f59\u9053\u94f6\u8272\u548c\u7d2b\u8272\u7684\u660e\u4eae\u95ea\u7535\u540c\u65f6\u8ff8\u51fa\uff0c\u8f70\u5728\u743c\u6069\u8eab\u4e0a\u3002</p><p>\u7136\u540e\u5b83\u4eec\u88ab\u5c3d\u6570\u53cd\u5f39\u4e86\u56de\u53bb\u3002</p><p>\u9ec4\u91d1\u9752\u5e74\u88ab\u81ea\u5df1\u53d1\u51fa\u7684\u95ea\u7535\u51fb\u4e2d\u4e86\uff0c\u5c3d\u7ba1\u6beb\u53d1\u65e0\u635f\uff0c\u4f46\u8fd9\u663e\u7136\u4ee4\u4ed6\u7684\u6012\u610f\u8fdb\u4e00\u6b65\u9ad8\u6da8\uff0c\u4fca\u7f8e\u7684\u9762\u5bb9\u626d\u66f2\u8d77\u6765\uff0c\u53d8\u5f97\u5b8c\u5168\u72f0\u72de\u3002\u865a\u7a7a\u6f29\u6da1\u5728\u4ed6\u80cc\u540e\u6084\u7136\u51fa\u73b0\uff0c\u98ce\u66b4\u548c\u95ea\u7535\u81ea\u5176\u4e2d\u8dc3\u51fa\uff0c\u4ea4\u9519\u878d\u5408\uff0c\u51dd\u805a\u6210\u56db\u5177\u95ea\u95ea\u53d1\u5149\u7684\u5175\u5668\uff0c\u9065\u9065\u5bf9\u51c6\u4e86\u743c\u6069\u3002\u4e24\u67c4\u5251\u3001\u4e00\u652f\u957f\u621f\uff0c\u8fd8\u6709\u4e00\u628a\u5bbd\u5203\u6218\u65a7\uff0c\u5b83\u4eec\u5168\u90fd\u9576\u91d1\u5d4c\u7389\uff0c\u6781\u5c3d\u534e\u4e3d\uff0c\u6563\u53d1\u7740\u63a9\u85cf\u4e0d\u4f4f\u7684\u5a01\u538b\u548c\u523a\u76ee\u7684\u7075\u5149\uff0c\u770b\u8d77\u6765\u90fd\u662f\u54c1\u9636\u6781\u9ad8\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u751a\u81f3\u53ef\u4ee5\u79f0\u4e4b\u4e3a\u795e\u5668\uff0c\u4f46\u5374\u5168\u662f\u901a\u4f53\u900f\u660e\uff0c\u4eff\u4f5b\u865a\u4f53\uff0c\u5e76\u975e\u5b9e\u7269\u3002</p><p>\u201c\u53d7\u6b7b\u5427\uff0c\u6742\u79cd\uff01\u201d\u9ec4\u91d1\u9752\u5e74\u5ba3\u544a\u7740\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u4e3e\u8d77\u624b\u81c2\u5f80\u4e0b\u4e00\u6325\u3002</p><p>\u56db\u67c4\u5175\u5668\u4eff\u4f5b\u5f97\u5230\u53f7\u4ee4\u4e00\u822c\uff0c\u81ea\u52a8\u98de\u5c04\u800c\u4e0b\u3002\u743c\u6069\u65e9\u5728\u5b83\u4eec\u73b0\u8eab\u65f6\u4fbf\u6697\u81ea\u8b66\u60d5\uff0c\u89c1\u72b6\u8fde\u5fd9\u540e\u9000\u534a\u6b65\uff0c\u900f\u660e\u7684\u529b\u573a\u5899\u4ece\u5730\u9762\u77ac\u95f4\u5347\u8d77\uff0c\u76f4\u8fbe\u5c4b\u9876\uff0c\u6321\u4f4f\u4e86\u6240\u6709\u653b\u51fb\u3002\u6b66\u5668\u4eec\u649e\u5728\u5899\u4e0a\uff0c\u50cf\u7409\u7483\u822c\u7c89\u788e\u6210\u672b\uff0c\u800c\u5149\u6ed1\u7684\u529b\u573a\u5899\u9762\u4e0a\u4e5f\u7acb\u523b\u51fa\u73b0\u4e86\u51e0\u9053\u88c2\u7f1d\uff0c\u6025\u901f\u8513\u5ef6\u6269\u6563\uff0c\u6700\u7ec8\u201c\u5494\u5693\u5494\u5693\u201d\u5730\u788e\u6210\u4e86\u51e0\u5341\u5757\u4e0d\u89c4\u5219\u7684\u6676\u4f53\uff0c\u5c31\u6b64\u5d29\u6e83\u3002</p><p>\u8fd9\u4e9b\u201c\u5b9d\u7269\u201d\u90fd\u662f\u5047\u8d27\u3002</p><p>\u743c\u6069\u5728\u5fc3\u4e2d\u505a\u51fa\u4e86\u8fd9\u4e2a\u5224\u65ad\uff0c\u4ed6\u5b8c\u5168\u4e0d\u901a\u5851\u80fd\u672f\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u5851\u6210\u529b\u573a\u5899\uff0c\u8fd9\u662f\u9752\u94dc\u623f\u5b50\u7684\u9644\u5e26\u529f\u80fd\uff0c\u51fa\u81ea\u5965\u6c83\u624b\u7b14\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5982\u679c\u5bf9\u65b9\u5c04\u4e0b\u7684\u8fd9\u56db\u67c4\u5175\u5668\uff0c\u5f53\u771f\u62e5\u6709\u5982\u5b83\u4eec\u90a3\u523a\u76ee\u7075\u5149\u76f8\u5339\u914d\u7684\u5a01\u80fd\u7684\u8bdd\uff0c\u5c31\u7b97\u662f\u8001\u5deb\u5996\u7cbe\u5fc3\u5f3a\u5316\u7684\u529b\u573a\u5899\uff0c\u7167\u6837\u4e5f\u5e94\u8be5\u88ab\u4e00\u51fb\u5373\u6e83\uff0c\u800c\u5175\u5668\u672c\u8eab\u6839\u672c\u5c31\u4e0d\u4f1a\u6709\u4e1d\u6beb\u635f\u4f24\u624d\u5bf9\u3002</p><p>\u5c3d\u7ba1\u53d1\u73b0\u4e86\u8fd9\u70b9\uff0c\u4ed6\u4f9d\u65e7\u4e0d\u6562\u5927\u610f\uff0c\u6b64\u524d\u548c\u6b4c\u66e6\u96c5\u6fc0\u6218\u4e00\u573a\uff0c\u65e9\u4e0a\u51c6\u5907\u7684\u9b54\u6cd5\u5df2\u7ecf\u7528\u53bb\u4e0d\u5c11\uff0c\u521a\u624d\u53c8\u5728\u5f17\u96f7\u65af\u8eab\u4e0a\u6d6a\u8d39\u4e86\u4e00\u4e2a\u6697\u793a\u548c\u4e00\u4e2a\u77f3\u5316\u672f\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u5f3a\u5f29\u4e4b\u672b\uff0c\u4f46\u4e5f\u7edd\u975e\u51c6\u5907\u5145\u5206\u3002\u800c\u4e14\u56e0\u4e3a\u662f\u5728\u5ba4\u5185\uff0c\u53c8\u662f\u591c\u95f4\uff0c\u539f\u672c\u5df2\u7ecf\u6253\u7b97\u4f11\u606f\uff0c\u5f88\u591a\u88c5\u5907\u2014\u2014\u5305\u62ec\u7075\u5316\u6597\u7bf7\u3001\u77f3\u884c\u957f\u9774\u2014\u2014\u90fd\u6ca1\u6709\u7a7f\u6234\u5728\u8eab\u4e0a\uff0c\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u548c\u4e00\u4e2a\u964c\u751f\u7684\u654c\u4eba\u6218\u6597\uff0c\u4e0d\u5f97\u4e0d\u63d0\u8d77\u5341\u4e8c\u5206\u7684\u7cbe\u795e\uff0c\u8c28\u614e\u4ece\u4e8b\u3002</p><p>\u53c8\u4e00\u9762\u529b\u573a\u5899\u5feb\u901f\u5347\u8d77\uff0c\u540c\u65f6\u516b\u5177\u5168\u526f\u6b66\u88c5\u7684\u9ab7\u9ac5\u6b66\u58eb\u81ea\u5730\u5e95\u5192\u51fa\uff0c\u6325\u5200\u6301\u76fe\u56f4\u653b\u4e0a\u6765\u3002\u743c\u6069\u5df2\u7ecf\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u8981\u7528\u4eba\u6d77\u6218\u672f\u628a\u5bf9\u65b9\u6df9\u6b7b\u3002\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u778b\u76ee\u6012\u559d\uff0c\u96f7\u9e23\u822c\u7684\u97f3\u6ce2\u5411\u56db\u9762\u6269\u6563\u632f\u8361\uff0c\u516b\u5177\u9ab7\u9ac5\u6b66\u58eb\u77ac\u95f4\u53d8\u6210\u4e86\u516b\u5806\u9aa8\u6e23\uff0c\u529b\u573a\u5899\u5219\u4ee5\u6781\u9ad8\u7684\u9891\u7387\u201c\u53ee\u53ee\u5f53\u5f53\u201d\u5730\u731b\u70c8\u9707\u98a4\u8d77\u6765\uff0c\u5316\u4f5c\u65e0\u6570\u788e\u6676\uff0c\u88ab\u72c2\u98ce\u5439\u6563\u3002</p><p>\u4e00\u51fb\u626b\u6e05\u969c\u788d\uff0c\u4ed6\u5927\u8e0f\u6b65\u8d70\u4e0a\u524d\u6765\uff0c\u98d3\u98ce\u5728\u8eab\u5468\u73af\u7ed5\uff0c\u4eff\u4f5b\u62a4\u76fe\uff0c\u8d64\u7ea2\u53cc\u77b3\u4e2d\u6ee1\u8574\u7740\u51cc\u5389\u6740\u6c14\uff0c\u80cc\u540e\u7684\u7a7a\u95f4\u6025\u5267\u626d\u66f2\uff0c\u95ea\u7535\u518d\u5ea6\u4ece\u6f29\u6da1\u4e2d\u8ff8\u51fa\uff0c\u51dd\u805a\u6210\u6570\u91cf\u66f4\u591a\u7684\u6b66\u5668\uff0c\u7ea6\u6709\u4e94\u516d\u5341\u4ef6\uff0c\u79cd\u7c7b\u4e5f\u66f4\u7e41\u6742\uff0c\u6709\u5200\uff0c\u6709\u5251\uff0c\u6709\u6218\u65a7\uff0c\u6709\u957f\u621f\uff0c\u6709\u6208\u77db\uff0c\u8fd8\u6709\u5f88\u591a\u5947\u5f62\u602a\u72b6\uff0c\u743c\u6069\u538b\u6839\u53eb\u4e0d\u51fa\u540d\u5b57\u7684\u4e1c\u897f\uff0c\u661f\u7f57\u68cb\u5e03\u5730\u6392\u5217\u5728\u865a\u7a7a\u4e2d\uff0c\u4eff\u4f5b\u7480\u74a8\u7684\u7fa4\u661f\u95ea\u8000\uff0c\u5bd2\u5149\u95ea\u95ea\u7684\u950b\u9510\u7784\u51c6\u4e86\u743c\u6069\uff0c\u53ea\u7b49\u4e00\u58f0\u4ee4\u4e0b\u3002</p><p>\u5de8\u5927\u7684\u5371\u9669\u5f81\u5146\u5239\u90a3\u95f4\u81ea\u743c\u6069\u5fc3\u5e95\u6d6e\u73b0\uff0c\u523a\u9aa8\u7684\u5bd2\u610f\u4ee4\u4ed6\u7684\u5168\u8eab\u6c57\u6bdb\u90fd\u6218\u6817\u5730\u7ad6\u4e86\u8d77\u6765\u3002\u5f88\u663e\u7136\uff0c\u5bf9\u65b9\u4e0b\u4e00\u77ac\u95f4\u5c31\u4f1a\u8ba9\u8fd9\u6b66\u5668\u7fa4\u540c\u65f6\u8f70\u4e0b\uff0c\u6570\u91cf\u5982\u6b64\u4e4b\u5e9e\u5927\uff0c\u5c31\u7b97\u662f\u518d\u53ec\u5524\u51fa\u529b\u573a\u5899\u4e5f\u7edd\u5bf9\u65e0\u6cd5\u963b\u6321\u3002\u800c\u81ea\u5df1\u8eab\u5904\u4e0d\u7b97\u72ed\u5c0f\u5374\u4e5f\u4e0d\u7b97\u5f00\u9614\u7684\u5ba4\u5185\uff0c\u88ab\u7a7a\u95f4\u67b7\u9501\u5c01\u95ed\u4e86\u4f20\u9001\u8f6c\u79fb\u7684\u53ef\u80fd\uff0c\u8981\u5982\u4f55\u624d\u80fd\u8eb2\u5f00\u8fd9\u4e00\u51fb\uff1f\u66f4\u4f55\u51b5\uff0c\u81ea\u5df1\u80cc\u540e\u8fd8\u7ad9\u7740\u73ca\u5609\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u8f7b\u58f0\u7b11\u7740\uff0c\u201c\u6211\u4eec\u597d\u50cf\u6709\u5927\u9ebb\u70e6\u4e86\u5462\u3002\u201d</p><p>\u751f\u6b7b\u5173\u5934\uff0c\u743c\u6069\u5374\u4f9d\u65e7\u80fd\u7b11\u5f97\u51fa\u6765\uff0c\u8fd9\u5012\u5e76\u975e\u662f\u6545\u4f5c\u9547\u5b9a\uff0c\u7eaf\u7cb9\u662f\u4e00\u79cd\u672c\u80fd\uff0c\u6216\u8005\u8bf4\u662f\u4e60\u60ef\u3002\u73ca\u5609\u7684\u8eab\u4f53\u539f\u672c\u56e0\u4e3a\u6050\u60e7\u800c\u5267\u70c8\u98a4\u6296\u7740\uff0c\u542c\u5230\u4ed6\u7684\u8bdd\u53cd\u800c\u9547\u9759\u4e0b\u6765\uff0c\u201c\u6740\u4e86\u4ed6\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u7ad9\u5728\u743c\u6069\u8eab\u540e\uff0c\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u8ba8\u538c\u8fd9\u5bb6\u4f19\u3002\u201d</p><p>\u201c\u597d\u554a\u3002\u201d</p><p>\u743c\u6069\u7406\u6240\u5f53\u7136\u5730\u7b54\u5e94\u7740\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7684\u9ed1\u8272\u9ab7\u9ac5\u5934\u9aa8\u51fa\u73b0\u5728\u53f3\u624b\u4e2d\uff0c\u8fd9\u662f\u5965\u6c83\u9001\u4ed6\u9752\u94dc\u8c6a\u5b85\u7684\u65f6\u5019\uff0c\u987a\u624b\u653e\u5728\u67d0\u4ef6\u623f\u95f4\u91cc\u7684\u793c\u7269\uff0c\u539f\u672c\u662f\u6253\u7b97\u751f\u6b7b\u5173\u5934\u62ff\u6765\u6551\u547d\u7684\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e48\u5feb\u4fbf\u5c31\u7528\u4e0a\u4e86\u3002</p><p>\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u4e3e\u8d77\u624b\u81c2\uff0c\u51c6\u5907\u4e00\u6325\u800c\u4e0b\uff0c\u800c\u743c\u6069\u4e5f\u6084\u6084\u63e1\u7d27\u4e86\u624b\u638c\uff0c\u6253\u7b97\u5c06\u9ab7\u9ac5\u5934\u9aa8\u634f\u788e\uff0c\u540c\u65f6\u6cd5\u6756\u9876\u7aef\u7684\u7fe0\u7eff\u5149\u7403\u5df2\u7ecf\u5b8c\u5168\u6210\u5f62\uff0c\u84c4\u52bf\u5f85\u53d1\u3002\u4fbf\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u9661\u7136\u201c\u5494\u5693\u201d\u5730\u4e00\u58f0\u5728\u8033\u8fb9\u6e05\u8106\u54cd\u8d77\u3002</p><p>\u4eff\u4f5b\u662f\u9aa8\u9abc\u65ad\u88c2\uff0c\u53c8\u4eff\u4f5b\u662f\u91d1\u5c5e\u649e\u51fb\uff0c\u660e\u660e\u662f\u5f88\u8f7b\u5fae\u7684\u58f0\u97f3\uff0c\u5728\u8fd9\u66b4\u98ce\u547c\u5578\u7684\u5927\u5385\u4e2d\u5374\u662f\u663e\u5f97\u5982\u6b64\u6e05\u6670\u3002\u7a7a\u6c14\u4e2d\u567c\u556a\u4f5c\u54cd\u7684\u95ea\u7535\u9aa4\u7136\u95f4\u7184\u706d\u4e86\uff0c\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u5175\u5668\u4e5f\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5730\u5feb\u901f\u6d88\u5931\uff0c\u4eff\u4f5b\u4ece\u672a\u51fa\u73b0\u3002\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u4f4e\u4e0b\u5934\uff0c\u53d1\u73b0\u81ea\u5df1\u7684\u80f8\u53e3\u90e8\u4f4d\u4e0d\u77e5\u4f55\u65f6\u51fa\u73b0\u4e86\u4e00\u4e2a\u9ed1\u8272\u7a7a\u6d1e\uff0c\u65e0\u5c3d\u7684\u865a\u7a7a\u5728\u5176\u4e2d\u626d\u66f2\u65cb\u8f6c\uff0c\u4e0d\u65ad\u6269\u5927\u3002</p><p>\u201c\u5567\uff0c\u8fd9\u5c31\u627f\u53d7\u4e0d\u4f4f\u4e86\u5417\uff1f\u539f\u672c\u8fd8\u60f3\u591a\u73a9\u4e00\u4f1a\u5462\uff0c\u51e1\u4eba\u7684\u8eab\u4f53\uff0c\u771f\u662f\u8106\u5f31\u554a\uff0c\u201d\u4ed6\u610f\u72b9\u672a\u5c3d\u5730\u4f4e\u541f\u7740\uff0c\u518d\u5ea6\u62ac\u8d77\u5934\u6765\uff0c\u201c\u653e\u4f60\u4e00\u9a6c\uff0c\u6742\u79cd\uff0c\u201d\u4ed6\u5bf9\u743c\u6069\u8bf4\uff0c\u7136\u540e\u5c06\u76ee\u5149\u8f6c\u5411\u8eb2\u5728\u5deb\u5e08\u80cc\u540e\u7684\u73ca\u5609\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u671f\u5f85\u4e0b\u6b21\u7684\u518d\u4f1a\u5427\uff0c\u5c0f\u59d1\u5a18\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u6211\u624d\u4e0d\u60f3\u89c1\u5230\u4f60\uff0c\u201d\u73ca\u5609\u9f13\u8db3\u52c7\u6c14\u56de\u7b54\uff0c\u201c\u6c38\u8fdc\u4e0d\u60f3\u3002\u201d</p><p>\u4eff\u4f5b\u542c\u5230\u4ec0\u4e48\u8352\u8bde\u4e4b\u6781\u7684\u8bdd\uff0c\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u7eb5\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u4ed6\u7684\u8eab\u5f62\u5728\u7b11\u58f0\u4e2d\u6e10\u6e10\u53d8\u5f97\u900f\u660e\uff0c\u6700\u7ec8\u5b8c\u5168\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u203b\u203b\u203b</p><p>\u63a5\u8fde\u7ecf\u5386\u4e24\u573a\u5927\u6218\uff0c\u539f\u672c\u6574\u6d01\u5178\u96c5\u7684\u5ba2\u5385\u5df2\u7ecf\u88ab\u5f04\u6210\u4e86\u5e9f\u589f\uff0c\u9752\u94dc\u5730\u677f\u548c\u5899\u9762\u4e0a\u904d\u5e03\u7740\u95ea\u7535\u707c\u70e7\u7684\u75d5\u8ff9\u3001\u5bb6\u5177\u548c\u9ab7\u9ac5\u7684\u788e\u6e23\u88ab\u72c2\u98ce\u5377\u8d77\uff0c\u6563\u843d\u5404\u5904\u3002\u743c\u6069\u548c\u73ca\u5609\u7ad9\u5728\u8fd9\u5806\u5e9f\u589f\u5f53\u4e2d\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u51e0\u4e4e\u8981\u6000\u7591\u521a\u624d\u6240\u7ecf\u5386\u7684\u662f\u4e00\u573a\u68a6\u5883\u3002</p><p>\u4f46\u8fd9\u663e\u7136\u4e0d\u662f\u68a6\u5883\u3002</p><p>\u91d1\u5149\u5b8c\u5168\u6536\u655b\u9eef\u6de1\uff0c\u9ec4\u91d1\u94e0\u7532\u7684\u9752\u5e74\u6d88\u5931\u4e86\uff0c\u7d27\u63a5\u7740\uff0c\u4e00\u4e2a\u80a5\u80d6\u7684\u8eab\u5f71\u4ece\u534a\u7a7a\u4e2d\u6389\u843d\u4e0b\u6765\uff0c\u6454\u5728\u5730\u677f\u4e0a\uff0c\u6e85\u8d77\u70df\u5c18\u65e0\u6570\uff0c\u6b63\u662f\u88ab\u743c\u6069\u77f3\u5316\u7684\u5f17\u96f7\u65af\u3002\u4ed6\u770b\u8d77\u6765\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u53cc\u773c\u7d27\u95ed\uff0c\u5168\u8eab\u50f5\u76f4\u53d1\u786c\uff0c\u624b\u8db3\u56db\u80a2\u51b0\u51c9\uff0c\u5982\u679c\u4e0d\u662f\u8fd8\u6709\u70b9\u5fc3\u8df3\u548c\u8109\u640f\uff0c\u57fa\u672c\u4e0a\u5c31\u662f\u4e2a\u6b7b\u4eba\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u7591\u60d1\u4f17\u591a\uff0c\u4f46\u4e5f\u65e0\u6687\u591a\u95ee\u3002\u6b64\u65f6\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u4e5f\u90fd\u8fd4\u56de\u5ba2\u5385\uff0c\u5979\u4eec\u542c\u5230\u4e86\u5ba2\u5385\u7684\u52a8\u9759\uff0c\u4f46\u4e0d\u77e5\u9053\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u201c\u5148\u51fa\u53d1\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8def\u4e0a\u518d\u89e3\u91ca\u3002\u201d</p><p>\u4ed6\u5c06\u623f\u5b50\u6536\u8d77\uff0c\u6062\u590d\u6210\u9752\u94dc\u5323\uff0c\u4e00\u884c\u4eba\u5750\u8fdb\u9a6c\u8f66\u3002\u743c\u6069\u62db\u51fa\u838e\u73de\u514b\uff0c\u8ba9\u5979\u628a\u5f17\u96f7\u65af\u4e94\u82b1\u5927\u7ed1\u8d77\u6765\uff0c\u4e5f\u6254\u8fdb\u4e86\u8f66\u53a2\u7684\u89d2\u843d\u91cc\uff0c\u6253\u7b97\u7b49\u5230\u4e86\u6b27\u675c\u6797\u57ce\u518d\u505a\u5ba1\u8baf\uff0c\u8299\u8389\u5a05\u7ed9\u80d6\u8001\u5934\u65bd\u52a0\u4e86\u4e24\u4e2a\u6cbb\u7597\u672f\uff0c\u514d\u5f97\u4ed6\u53d7\u4e0d\u4e86\u8def\u4e0a\u7684\u98a0\u7c38\u800c\u65ad\u6c14\u3002\u9a6c\u8f66\u7684\u8f66\u592b\u663e\u7136\u6709\u4e9b\u4e0d\u60a6\uff0c\u5634\u91cc\u561f\u561f\u56d4\u56d4\u7684\uff0c\u8fd9\u5f88\u6b63\u5e38\uff0c\u5927\u534a\u591c\u7684\u7761\u5f97\u6b63\u9999\uff0c\u7a81\u7136\u88ab\u53eb\u8d77\u6765\u8d76\u8def\uff0c\u4efb\u662f\u8c01\u90fd\u4f1a\u6709\u60f3\u6cd5\uff0c\u4f46\u5f53\u743c\u6069\u989d\u5916\u52a0\u4ed8\u4e24\u679a\u91d1\u5e01\u505a\u5c0f\u8d39\u65f6\uff0c\u987f\u65f6\u4ed6\u4e00\u5207\u7684\u6028\u6c14\u548c\u4e0d\u5feb\u90fd\u6d88\u6563\u5f97\u65e0\u5f71\u65e0\u8e2a\u4e86\uff0c\u7cbe\u795e\u6296\u64de\u5730\u8dc3\u4e0a\u8f66\u8f95\uff0c\u7529\u8d77\u9a6c\u97ad\u9a71\u8f66\u51fa\u53d1\u3002</p><p>\u73ca\u5609\u5750\u5728\u8f66\u53a2\u91cc\uff0c\u770b\u7740\u8f66\u592b\u5728\u6708\u5149\u4e0b\u7684\u80cc\u5f71\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u59d0\u59d0\u3002\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u7a81\u7136\u5728\u60f3\uff0c\u6211\u4eec\u4ee5\u524d\u2026\u2026\u6211\u4eec\u5c0f\u65f6\u5019\uff0c\u4e5f\u662f\u8fd9\u6837\u5462\u3002\u201d</p><p>\u201c\u8fd9\u6837\uff1f\u201d\u743c\u6069\u4f9d\u65e7\u6ca1\u660e\u767d\u73ca\u5609\u7684\u610f\u601d\uff0c\u201c\u4ec0\u4e48\u6837\uff1f\u201d</p><p>\u201c\u4e3a\u4e86\u751f\u6d3b\u800c\u5954\u6ce2\u8f9b\u52b3\uff0c\u4e3a\u4e86\u91d1\u94b1\u800c\u62c5\u5fe7\u70e6\u607c\uff0c\u201d\u73ca\u5609\u8f7b\u58f0\u8bf4\uff0c\u201c\u6bcf\u5929\u9192\u6765\u8111\u5b50\u91cc\u60f3\u7740\u7684\u5c31\u662f\u751f\u610f\uff0c\u80fd\u591a\u8d5a\u51e0\u4e2a\u94dc\u5e01\u5c31\u4f1a\u9ad8\u5174\u534a\u5929\u5462\u3002\u201d</p><p>\u743c\u6069\u63e1\u4f4f\u5979\u67d4\u8f6f\u7684\u5c0f\u624b\uff0c\u201c\u90fd\u8fc7\u53bb\u4e86\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e9b\u90fd\u8fc7\u53bb\u4e86\u3002\u201d</p><p>\u73ca\u5609\u7b11\u4e86\u7b11\uff0c\u201c\u7b28\u86cb\uff0c\u6211\u5e76\u4e0d\u662f\u89c9\u5f97\u540e\u6094\u3001\u96be\u53d7\u6216\u8005\u4f24\u611f\u4ec0\u4e48\u7684\u554a\uff0c\u81ea\u5df1\u7684\u7ecf\u5386\uff0c\u81ea\u5df1\u7684\u4eba\u751f\uff0c\u6709\u4ec0\u4e48\u4e0d\u613f\u56de\u9996\u7684\u5462\u3002\u6211\u53ea\u662f\u60f3\uff0c\u5176\u5b9e\u90a3\u65f6\u5019\uff0c\u6211\u4eec\u60f3\u7684\u5c11\uff0c\u8981\u6c42\u7684\u5c11\uff0c\u5fc3\u601d\u5355\u7eaf\uff0c\u4e5f\u5c31\u5bb9\u6613\u6ee1\u8db3\u3002\u73b0\u5728\u751f\u6d3b\u5bcc\u8db3\u4e86\uff0c\u8863\u98df\u65e0\u5fe7\u4e86\uff0c\u60f3\u5f97\u5374\u4e5f\u591a\u4e86\uff0c\u53c8\u6709\u4e86\u65b0\u7684\u70e6\u607c\u5462\u3002\u201d</p><p>\u743c\u6069\u60f3\u8981\u56de\u7b54\uff0c\u5f20\u4e86\u5f20\u5634\u5374\u53c8\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\u3002\u201c\u672c\u8be5\u5982\u6b64\u5427\uff0c\u201d\u4ed6\u6700\u540e\u8bf4\uff0c\u201c\u8fd9\u5c31\u662f\u4eba\u751f\u554a\u3002\u201d</p><p>\u201c\u5207\uff0c\u201d\u73ca\u5609\u4e0d\u5c51\uff0c\u201c\u4f60\u624d\u591a\u5927\uff0c\u5c31\u88c5\u6df1\u6c89\u5728\u8fd9\u91cc\u8c08\u4eba\u751f\u3002\u201d</p><p>\u743c\u6069\u6447\u4e86\u6447\u5934\uff0c\u610f\u5174\u9611\u73ca\u5730\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u53f9\u6c14\u505a\u4ec0\u4e48\u3002\u201d\u73ca\u5609\u4e0d\u4f9d\u4e0d\u9976\u5730\u8ffd\u95ee\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u6211\u53f9\u7684\u4e0d\u662f\u6c14\uff0c\u662f\u5bc2\u5bde\u3002\u201d</p><p>\u73ca\u5609\u6014\u4e86\u4e24\u79d2\u949f\uff0c\u7136\u540e\u53cd\u5e94\u8fc7\u6765\uff0c\u7830\u7830\u7830\u4e00\u901a\u7c89\u62f3\u628a\u743c\u6069\u6253\u4e86\u4e2a\u773c\u5192\u91d1\u661f\u3002\u201c\u4e00\u7fa4\u5973\u5b69\u5b50\u8eab\u8fb9\u966a\u7740\uff0c\u4f60\u8fd8\u5728\u8fd9\u91cc\u8bf4\u5bc2\u5bde\u2014\u2014\u6211\u5bc2\u4f60\u4e2a\u5934\uff01\u5bde\u4f60\u4e2a\u5934\uff01\u201d</p><p>\u73ca\u5609\u8eab\u5148\u58eb\u5352\u505a\u4e86\u8868\u7387\uff0c\u63a5\u7740\u838e\u73de\u514b\u548c\u8299\u8389\u5a05\u53c2\u6218\uff0c\u6700\u540e\u8fde\u4e00\u76f4\u7b11\u7740\u529d\u963b\u7684\u8299\u857e\u72c4\u90fd\u88ab\u5377\u4e86\u8fdb\u6765\uff0c\u534a\u771f\u534a\u5047\u5730\u5077\u5077\u7ed9\u4e86\u743c\u6069\u51e0\u62f3\u3002\u597d\u5728\u5927\u5bb6\u90fd\u6ca1\u5f53\u771f\u7528\u529b\uff0c\u5973\u5b69\u5b50\u7684\u62f3\u5934\u4e5f\u4e0d\u91cd\uff0c\u53ea\u5f53\u662f\u73a9\u7b11\u6253\u95f9\u3002\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u957f\u591c\u5f88\u5feb\u8fc7\u53bb\uff0c\u4e1c\u65b9\u5df2\u7ecf\u51fa\u73b0\u5fae\u5f31\u66d9\u5149\uff0c\u539f\u672c\u9ed1\u6c89\u6c89\u7684\u5929\u7a7a\u53d8\u6210\u4e86\u82cd\u84dd\uff0c\u6e05\u6668\u7684\u98ce\u5e26\u7740\u5bd2\u610f\uff0c\u5439\u62c2\u5728\u8138\u4e0a\u51c9\u98d5\u98d5\u7684\uff0c\u8fdc\u5904\u5df2\u7ecf\u9690\u9690\u7ea6\u7ea6\u770b\u89c1\u4e86\u4e00\u6392\u9752\u7070\u8272\u7684\u8fde\u7ef5\u7fa4\u5c71\uff0c\u6309\u7167\u5730\u56fe\u663e\u793a\uff0c\u90a3\u91cc\u6709\u4e2a\u5173\u9698\uff0c\u8fc7\u4e86\u4e4b\u540e\u4fbf\u80fd\u770b\u89c1\u963f\u5df4\u6c99\u6cb3\u4e86\u3002</p><p>\u56e0\u4e3a\u62c5\u5fc3\u8ffd\u5175\u7684\u7f18\u6545\uff0c\u9a6c\u8f66\u8dd1\u5f97\u5f88\u5feb\uff0c\u5728\u743c\u6069\u7684\u6388\u610f\u4e0b\uff0c\u838e\u73de\u514b\u5077\u5077\u7ed9\u62c9\u8f66\u7684\u56db\u5339\u9a6c\u5206\u522b\u624e\u4e86\u4e00\u652f\u6bd2\u9488\uff0c\u8ba9\u5b83\u4eec\u7684\u5954\u8dd1\u901f\u5ea6\u6bd4\u8d77\u5e73\u65f6\u5feb\u4e86\u5c06\u8fd1\u4e00\u534a\uff0c\u5f53\u7136\u8fd9\u662f\u4ee5\u5927\u5e45\u5ea6\u7f29\u77ed\u5bff\u547d\u4e3a\u4ee3\u4ef7\u7684\u3002\u4f5c\u4e3a\u8865\u507f\uff0c\u743c\u6069\u6253\u7b97\u7b49\u5230\u4e86\u6b27\u675c\u6797\u57ce\u540e\uff0c\u4ee5\u79c1\u4eba\u8d60\u793c\u7684\u540d\u4e49\u518d\u9001\u8f66\u592b\u4e24\u9897\u84dd\u5b9d\u77f3\u3002\u4e8b\u5b9e\u8bc1\u660e\uff0c\u8fd9\u79cd\u82b1\u8d39\u5b8c\u5168\u503c\u5f97\uff0c\u5c31\u5728\u4ed6\u4eec\u8d8a\u8fc7\u963f\u5df4\u6c99\u6cb3\u5927\u6865\u65f6\uff0c\u8fdc\u5904\u9ad8\u7a7a\u4e2d\u4f20\u6765\u4e86\u547c\u5578\u98ce\u58f0\u548c\u51c4\u5389\u9e23\u53eb\uff0c\u9a7e\u9a6d\u7740\u9e6b\u9a6c\u548c\u7ffc\u9f99\u7684\u201c\u5929\u7a79\u519b\u56e2\u201d\u5728\u5929\u8fb9\u51fa\u73b0\uff0c\u4eff\u4f5b\u4e4c\u4e91\u822c\u9ed1\u538b\u538b\u5730\u63a8\u6d8c\u8fc7\u6765\u3002</p><p>\u5e78\u8fd0\u7684\u662f\uff0c\u4e00\u65e6\u8d8a\u8fc7\u963f\u5df4\u6c99\u6cb3\u5927\u6865\uff0c\u5c31\u610f\u5473\u7740\u8fdb\u5165\u4e86\u6851\u6bd4\u4e9a\u7684\u56fd\u5883\u3002\u6851\u6bd4\u4e9a\u867d\u7136\u653f\u5c40\u6df7\u4e71\uff0c\u521a\u521a\u7ed3\u675f\u4e86\u4e00\u573a\u5185\u6218\uff0c\u6b63\u5904\u4e8e\u865a\u5f31\u65f6\u671f\uff0c\u4f46\u5b83\u6bd5\u7adf\u4e5f\u662f\u8d39\u4f26\u5927\u9646\u4e0a\u6709\u6570\u7684\u5927\u56fd\uff0c\u8fdc\u975e\u8fdc\u5c71\u57ce\u53ef\u6bd4\u3002\u6563\u63d0\u5c14\u5821\u9664\u975e\u6253\u7b97\u7acb\u523b\u5168\u9762\u5f00\u6218\uff0c\u5426\u5219\u5012\u8fd8\u4e0d\u6562\u5f53\u771f\u8d8a\u5883\u8ffd\u51fb\u3002\u4f55\u51b5\u8fdb\u5165\u6851\u6bd4\u4e9a\u5883\u5185\u540e\uff0c\u8def\u4e0a\u6765\u5f80\u7684\u5546\u65c5\u884c\u4eba\u6025\u5267\u589e\u591a\uff0c\u743c\u6069\u7b49\u4eba\u4e58\u5750\u7684\u9a6c\u8f66\u4e0a\u53c8\u6ca1\u6709\u8d34\u6807\u7b7e\uff0c\u5c31\u7b97\u60f3\u641c\u5bfb\u4e5f\u65e0\u4ece\u7740\u624b\u3002\u5929\u7a79\u519b\u56e2\u5728\u963f\u5df4\u6c99\u6cb3\u8fb9\u5de1\u6e38\u4e86\u7247\u523b\uff0c\u6700\u7ec8\u8fd8\u662f\u5168\u4f53\u8fd4\u822a\u3002</p><p>\u5230\u5e95\u8fd9\u6b7b\u80d6\u5b50\u8eab\u4e0a\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u90a3\u679a\u94a5\u5319\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u503c\u5f97\u6563\u5854\u6797\u4f1a\u5982\u6b64\u52b3\u5e08\u52a8\u4f17\uff1f\u90a3\u4e2a\u91d1\u7532\u7ea2\u77b3\u7684\u5bb6\u4f19\uff0c\u7a81\u7136\u51fa\u73b0\u53c8\u7a81\u7136\u6d88\u5931\uff0c\u4ed6\u5230\u5e95\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u8bf4\u7684\u8bdd\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u743c\u6069\u4e00\u8def\u4e0a\u90fd\u5728\u601d\u7d22\u8fd9\u4e9b\u95ee\u9898\uff0c\u7136\u800c\u4e0d\u5f97\u5176\u89e3\u3002\u5f17\u96f7\u65af\u5012\u662f\u9192\u4e86\uff0c\u7136\u800c\u4ed6\u95ed\u53e3\u4e0d\u8a00\uff0c\u65e0\u8bba\u95ee\u4ec0\u4e48\u90fd\u4e0d\u56de\u7b54\uff0c\u743c\u6069\u4e5f\u6beb\u65e0\u529e\u6cd5\u3002</p><p>\u201c\u7b97\u4e86\uff0c\u73b0\u5728\u65c5\u9014\u5954\u6ce2\uff0c\u505a\u4ec0\u4e48\u90fd\u4e0d\u65b9\u4fbf\u3002\u8fd8\u662f\u5c3d\u5feb\u8d76\u5230\u6b27\u675c\u6797\u57ce\uff0c\u4fee\u6574\u51e0\u5929\uff0c\u987a\u4fbf\u628a\u8fd9\u4e9b\u4e8b\u60c5\u505a\u4e2a\u89e3\u51b3\u5427\u3002\u201d</p><p>\u62b1\u7740\u8fd9\u79cd\u60f3\u6cd5\uff0c\u4e00\u884c\u4eba\u5feb\u9a6c\u52a0\u97ad\uff0c\u663c\u591c\u8d76\u8def\uff0c\u4e24\u5929\u4e4b\u540e\uff0c\u4ed6\u4eec\u62b5\u8fbe\u4e86\u6851\u6bd4\u4e9a\u7684\u9996\u90fd\u6b27\u675c\u6797\u57ce\u3002</p></article>"}}}]);
