(window.webpackJsonp=window.webpackJsonp||[]).push([[449],{471:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e5d\u8282 \u94a5\u5319\u7684\u53d6\u820d",content:"<article><h2>\u7b2c\u4e09\u5341\u4e5d\u8282 \u94a5\u5319\u7684\u53d6\u820d</h2><p>\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u7684\u795e\u738b\uff0c\u5176\u5b9e\u66f4\u8fd1\u4f3c\u4e8e\u201c\u5723\u8005\u201d\u3002\u4ed6\u4eec\u80fd\u591f\u8d50\u4e88\u4fe1\u5f92\u795e\u672f\uff0c\u4f46\u5f71\u54cd\u8303\u56f4\u53ea\u53ca\u4e8e\u4ee5\u795e\u738b\u4e3a\u4e2d\u5fc3\u7684\u4e00\u5b9a\u8303\u56f4\u4e4b\u5185\uff0c\u800c\u4e0d\u662f\u50cf\u5176\u4ed6\u795e\u7947\u90a3\u6837\u80fd\u666e\u904d\u5730\u8d50\u4e88\u795e\u672f\uff0c\u65e0\u6240\u8c13\u8ddd\u79bb\u8fdc\u8fd1\uff0c\u65e0\u8bba\u662f\u5929\u6daf\u6d77\u89d2\u3002\u56e0\u4e3a\u8fd9\u79cd\u7f18\u6545\uff0c\u8fd9\u4e24\u56fd\u7684\u653f\u4f53\u90fd\u662f\u5c01\u5efa\u5236\u517c\u653f\u6559\u5408\u4e00\u5236\uff0c\u5e38\u5e38\u662f\u4e00\u4e2a\u795e\u738b\u7387\u9886\u7740\u796d\u53f8\u4eec\u5360\u636e\u4e00\u5ea7\u57ce\u5e02\uff0c\u5404\u81ea\u4e3a\u653f\uff0c\u4eff\u4f5b\u8bf8\u4faf\u3002\u795e\u738b\u662f\u541b\u4e3b\uff0c\u4f46\u4ed6\u4eec\u5f80\u5f80\u6df1\u5c45\u7b80\u51fa\uff0c\u751a\u81f3\u538b\u6839\u4e0d\u95ee\u5916\u4e8b\uff0c\u4e8e\u662f\u796d\u53f8\u5c31\u6210\u4e86\u5b9e\u9645\u7684\u638c\u6743\u8005\uff0c\u8eab\u4efd\u5730\u4f4d\u975e\u5e38\u5c0a\u8d35\u3002\u4e0d\u540c\u795e\u738b\u7684\u796d\u53f8\u6709\u7740\u4e0d\u540c\u7684\u79f0\u547c\uff0c\u5409\u52d2\u4eca\u7684\u796d\u53f8\uff0c\u5c31\u53eb\u505a\u201c\u82db\u5f8b\u4f8d\u8005\u201d\uff0c\u4e4b\u6240\u4ee5\u4f1a\u6709\u8fd9\u4e2a\u79f0\u547c\uff0c\u662f\u56e0\u4e3a\u5409\u52d2\u91d1\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u66b4\u541b\uff0c\u70ed\u7231\u5236\u5b9a\u5404\u79cd\u4e0d\u8fd1\u60c5\u7406\u7684\u4e25\u9177\u5f8b\u6cd5\u6765\u7edf\u6cbb\u4eba\u6c11\u3002</p><p>\u90a3\u8fd9\u4e2a\u82db\u5f8b\u4f8d\u8005\uff0c\u4e0d\u8001\u8001\u5b9e\u5b9e\u5728\u6069\u745f\u5f85\u7740\uff0c\u4e3a\u4f55\u4f1a\u5192\u5145\u5f17\u96f7\u65af\uff0c\u4ed6\u7684\u76ee\u7684\u53c8\u4f55\u5728\u5462\uff1f</p><p>\u201c\u94a5\u5319\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4e3a\u4e86\u5bfb\u627e\u94a5\u5319\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff1f\u201d</p><p>\u5728\u8def\u4e0a\u7684\u65f6\u5019\uff0c\u743c\u6069\u65e9\u5c31\u4ed4\u7ec6\u641c\u8fc7\u6b7b\u80d6\u5b50\u7684\u8eab\uff0c\u4e00\u5171\u53ea\u627e\u5230\u8fc7\u4e24\u4ef6\u6709\u4ef7\u503c\u7684\u4e1c\u897f\uff0c\u4e00\u662f\u90a3\u679a\u636e\u8bf4\u80fd\u5f00\u542f\u8fdc\u5c71\u5e02\u9b54\u6cd5\u9635\u7684\u94a5\u5319\uff0c\u4e8c\u662f\u672c\u8584\u8584\u7684\u9b54\u6cd5\u4e66\uff0c\u6b64\u65f6\u90fd\u653e\u5728\u624b\u8fb9\u3002\u542c\u838e\u73de\u514b\u63d0\u8d77\uff0c\u4ed6\u5c06\u90a3\u679a\u94a5\u5319\u62ff\u4e86\u8d77\u6765\uff0c\u201c\u96be\u9053\u6069\u745f\u4eba\u4e5f\u6253\u8fdc\u5c71\u5e02\u7684\u4e3b\u610f\uff1f\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4ed6\u627e\u7684\u662f\u53e6\u5916\u4e00\u679a\u94a5\u5319\u2014\u2014\u5f00\u542f\u5409\u52d2\u4eca\u7684\u2018\u738b\u4e4b\u5b9d\u5e93\u2019\u7684\u94a5\u5319\u3002\u201d</p><p>\u201c\u738b\u4e4b\u5b9d\u5e93\uff1f\u90a3\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u542c\u540d\u5b57\u5c31\u77e5\u9053\u554a\uff0c\u662f\u5409\u52d2\u4eca\u7684\u85cf\u5b9d\u5e93\u561b\u3002\u201d</p><p>\u838e\u73de\u514b\u63a5\u7740\u89e3\u91ca\uff0c\u539f\u6765\u5409\u52d2\u4eca\u8fd9\u5bb6\u4f19\u662f\u4e2a\u66b4\u541b\uff0c\u4ed6\u7edf\u6cbb\u6069\u745f\u671f\u95f4\uff0c\u6a2a\u5f81\u66b4\u655b\uff0c\u641c\u522e\u4e86\u8d85\u51fa\u60f3\u50cf\u7684\u6d77\u91cf\u73cd\u5b9d\uff0c\u90fd\u88ab\u79d8\u5bc6\u6536\u85cf\u5728\u67d0\u4e2a\u5730\u65b9\uff0c\u5728\u4f20\u8bf4\u4e2d\u88ab\u79f0\u4e3a\u201c\u738b\u4e4b\u5b9d\u5e93\u201d\uff0c\u5409\u52d2\u4eca\u8fd8\u7279\u5730\u4e3a\u5b83\u4eb2\u624b\u94f8\u9020\u4e86\u4e00\u67c4\u94a5\u5319\u3002\u5341\u516d\u5e74\u524d\uff0c\u5409\u52d2\u4eca\u88ab\u6740\uff0c\u8fd9\u67c4\u94a5\u5319\u4e5f\u968f\u4e4b\u5931\u8e2a\uff0c\u4e0b\u843d\u4e0d\u660e\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u6b7b\u80d6\u5b50\u2026\u2026\u5443\uff0c\u82db\u5f8b\u4f8d\u8005\u4ec0\u4e48\u7684\uff0c\u662f\u60f3\u8fdb\u5165\u738b\u4e4b\u5b9d\u5e93\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u4ed6\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u53e6\u6709\u4e3b\u4f7f\u8005\uff0c\u662f\u5409\u52d2\u4eca\u7684\u738b\u540e\u3002\u201d</p><p>\u201c\u5409\u52d2\u4eca\u2026\u2026\u4ed6\u8fd8\u6709\u738b\u540e\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u4ed6\u662f\u795e\u738b\u561b\uff0c\u522b\u8bf4\u738b\u540e\uff0c\u8fd8\u6709\u4e00\u5927\u5806\u5983\u5b50\u5462\u3002\u201d</p><p>\u8fd9\u5012\u4e5f\u662f\uff0c\u56e0\u4e3a\u6240\u8c13\u201c\u795e\u738b\u201d\uff0c\u662f\u795e\u7947\u4ee5\u9644\u4f53\u51e1\u4eba\u7684\u65b9\u5f0f\u957f\u4e45\u505c\u7559\u5728\u7269\u8d28\u754c\uff0c\u540c\u6837\u4e5f\u8981\u996e\u98df\u4f11\u606f\uff0c\u540c\u6837\u4e5f\u4f1a\u751f\u8001\u75c5\u6b7b\uff0c\u7136\u540e\u901a\u8fc7\u79d8\u6cd5\u4e00\u4ee3\u4ee3\u5730\u8f6c\u751f\u3002\u4ece\u4e25\u683c\u610f\u4e49\u4e0a\u8bf4\uff0c\u4ed6\u4eec\u5176\u5b9e\u5df2\u7ecf\u4e0d\u662f\u795e\u7947\uff0c\u800c\u662f\u5177\u6709\u5e9e\u5927\u795e\u529b\u7684\u51e1\u4eba\u2014\u2014\u65e2\u7136\u662f\u51e1\u4eba\uff0c\u6709\u4e03\u60c5\u516d\u6b32\uff0c\u559c\u6012\u54c0\u4e50\uff0c\u90a3\u4e48\u6709\u738b\u540e\u6709\u5ad4\u5983\uff0c\u5012\u4e5f\u4e0d\u662f\u4ec0\u4e48\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u60c5\u3002</p><p>\u636e\u838e\u73de\u514b\u4ece\u90a3\u4f4d\u82db\u5f8b\u4f8d\u8005\u53e3\u4e2d\u4e86\u89e3\u5230\u7684\u4fe1\u606f\uff0c\u5409\u52d2\u4eca\u6b7b\u540e\uff0c\u6069\u745f\u5206\u88c2\u6210\u5927\u5927\u5c0f\u5c0f\u51e0\u5341\u5bb6\u52bf\u529b\uff0c\u9677\u5165\u5168\u9762\u6218\u4e71\uff0c\u800c\u90bb\u56fd\u7a46\u7f55\u745e\u5fb7\u4e5f\u8d81\u673a\u5927\u4e3e\u5165\u4fb5\uff0c\u5982\u4eca\u5df2\u7ecf\u653b\u9677\u4e86\u6069\u745f\u7684\u5927\u534a\u56fd\u571f\u3002\u4e3a\u4e86\u633d\u6551\u56fd\u5bb6\u5371\u4ea1\uff0c\u5409\u52d2\u4eca\u7684\u738b\u540e\uff0c\u5e0c\u671b\u80fd\u591f\u6253\u5f00\u738b\u4e4b\u5b9d\u5e93\uff0c\u501f\u52a9\u5176\u4e2d\u7684\u8d22\u5b9d\u4e4b\u529b\u6765\u5e73\u5b9a\u6218\u4e71\uff0c\u6253\u8d25\u4fb5\u7565\u8005\u3002\u8981\u6253\u5f00\u5b9d\u5e93\uff0c\u5c31\u5f97\u5148\u627e\u5230\u94a5\u5319\uff0c\u738b\u540e\u901a\u8fc7\u67d0\u79cd\u79d8\u5bc6\u6e20\u9053\uff0c\u786e\u5b9a\u4e86\u94a5\u5319\u5c31\u5728\u8fdc\u5c71\u5e02\u8fd9\u7247\u533a\u57df\uff0c\u81f3\u4e8e\u66f4\u7cbe\u786e\u7684\u4f4d\u7f6e\uff0c\u90a3\u5c31\u53ea\u80fd\u7528\u4eba\u624b\u6765\u641c\u5bfb\u4e86\u3002</p><p>\u6070\u597d\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u585e\u5c14\u7684\u5927\u5974\u96b6\u8d29\u5b50\u5f17\u96f7\u65af\u6765\u5230\u6069\u745f\u3002\u56e0\u4e3a\u585e\u5c14\u548c\u7a46\u7f55\u745e\u5fb7\u4e24\u56fd\u662f\u6b7b\u654c\uff0c\u800c\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u73b0\u5728\u53c8\u662f\u654c\u5bf9\uff0c\u6839\u636e\u201c\u654c\u4eba\u7684\u654c\u4eba\u5c31\u662f\u670b\u53cb\u201d\u8fd9\u4e2a\u9053\u7406\uff0c\u585e\u5c14\u548c\u6069\u745f\u4e24\u56fd\u7684\u5173\u7cfb\u5012\u662f\u7a7a\u524d\u826f\u597d\uff0c\u6765\u5f80\u5bc6\u5207\u3002\u585e\u5c14\u63d0\u4f9b\u4e86\u5927\u91cf\u7684\u63f4\u52a9\uff0c\u5e2e\u52a9\u6069\u745f\u5bf9\u6297\u7a46\u7f55\u745e\u5fb7\u3002\u5f17\u96f7\u65af\u5728\u6069\u745f\u505a\u5ba2\u65f6\uff0c\u5076\u7136\u8bf4\u8d77\u81ea\u5df1\u5df2\u7ecf\u88ab\u4efb\u547d\u4e3a\u8fdc\u5c71\u5e02\u7684\u57ce\u4e3b\uff0c\u5373\u5c06\u8d70\u9a6c\u4e0a\u4efb\uff0c\u7ed3\u679c\u8fd9\u53e5\u8bdd\u7ed9\u4ed6\u5e26\u6765\u4e86\u6740\u8eab\u4e4b\u7978\u3002\u8fd9\u4f4d\u53eb\u505a\u535a\u6c99\u8fe6\u7684\u82db\u5f8b\u4f8d\u8005\uff0c\u542c\u5230\u6d88\u606f\uff0c\u89c9\u5f97\u8fd9\u5b9e\u5728\u662f\u5343\u8f7d\u96be\u9022\u7684\u597d\u673a\u4f1a\uff0c\u4fbf\u5c06\u5f17\u96f7\u65af\u6740\u4e86\uff0c\u81ea\u5df1\u5192\u540d\u9876\u66ff\uff0c\u524d\u6765\u8fdc\u5c71\u57ce\u8d74\u4efb\u3002</p><p>\u201c\u53ef\u662f\u5982\u679c\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u6211\u524d\u5e74\u5728\u585e\u5c14\u89c1\u5230\u7684\u90a3\u4e2a\u5f17\u96f7\u65af\uff0c\u8fd8\u5e94\u8be5\u662f\u672c\u4eba\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u6b21\u89c1\u5230\u7684\u662f\u5192\u724c\u8d27\uff0c\u90a3\u4e48\u4ed6\u600e\u4e48\u4f1a\u8ba4\u8bc6\u6211\u7684\uff1f\u201d</p><p>\u201c\u90a3\u6709\u4ec0\u4e48\u96be\u7684\uff0c\u7528\u9b54\u6cd5\u8bfb\u53d6\u8bb0\u5fc6\u5c31\u662f\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5173\u952e\u7684\u91cd\u8981\u7684\u8bb0\u5fc6\u81ea\u7136\u96be\u4ee5\u8bfb\u53d6\uff0c\u4f46\u8ba4\u8bc6\u4f60\u8fd9\u79cd\u5c0f\u4e8b\u60c5\uff0c\u8981\u77e5\u9053\u8fd8\u662f\u5f88\u5bb9\u6613\u7684\u3002\u795e\u672f\u91cc\u4e5f\u662f\u6709\u8fd9\u79cd\u9b54\u6cd5\u7684\uff0c\u5409\u52d2\u91d1\u7684\u82db\u5f8b\u796d\u53f8\u5c31\u633a\u64c5\u957f\u6b64\u9053\u3002\u201d</p><p>\u201c\u53ef\u662f\u5409\u52d2\u91d1\u4e0d\u662f\u65e9\u5c31\u6b7b\u4e86\u5417\uff1f\u4ed6\u7684\u796d\u53f8\u54ea\u91cc\u8fd8\u6709\u795e\u672f\u53ef\u7528\u3002\u201d</p><p>\u201c\u6781\u5c11\u6570\u796d\u53f8\u8fd8\u662f\u6709\u7684\uff0c\u636e\u8bf4\u662f\u90a3\u4f4d\u5409\u52d2\u91d1\u7684\u738b\u540e\uff0c\u80fd\u591f\u50cf\u795e\u7947\u4e00\u6837\u5411\u4fe1\u5f92\u8d50\u4e88\u795e\u672f\uff0c\u4f46\u5fc5\u987b\u662f\u5728\u5979\u9644\u8fd1\u7684\u4e00\u5b9a\u8303\u56f4\u5185\u624d\u6709\u6548\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u90a3\u5979\u5c82\u4e0d\u5c31\u662f\u795e\u738b\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u6bd4\u4e00\u822c\u7684\u795e\u738b\u8981\u5dee\u5f97\u591a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u6700\u5f31\u7684\u795e\u738b\uff0c\u81f3\u5c11\u4e5f\u80fd\u652f\u6301\u4e00\u4e2a\u57ce\u5e02\u7684\u4fe1\u5f92\uff0c\u4f46\u8fd9\u4f4d\u738b\u540e\u7684\u80fd\u529b\u5f71\u54cd\u8303\u56f4\u5927\u6982\u53ea\u6709\u4e00\u5ea7\u57ce\u5821\u3002\u4f46\u6211\u6240\u77e5\u9053\u7684\u4e5f\u5c31\u8fd9\u4e9b\uff0c\u66f4\u5177\u4f53\u7684\u60c5\u5f62\uff0c\u6211\u4e5f\u5c31\u95ee\u4e0d\u51fa\u6765\u4e86\u3002\u201d</p><p>\u201c\u771f\u5947\u602a\u3002\u201d\u743c\u6069\u81ea\u8a00\u81ea\u8bed\u3002</p><p>\u201c\u5bf9\u8fd9\u4f4d\u738b\u540e\u4ea7\u751f\u5174\u8da3\u4e86\uff1f\u201d\u838e\u73de\u514b\u7b11\u7740\u6253\u8da3\uff0c\u201c\u4ee5\u540e\u6709\u7a7a\u53bb\u6069\u745f\uff0c\u8bb0\u5f97\u4e0a\u95e8\u6c42\u89c1\u5427\u3002\u636e\u90a3\u4e2a\u5bb6\u4f19\u8bf4\uff0c\u738b\u540e\u53ef\u662f\u4f4d\u5929\u59ff\u56fd\u8272\u7684\u7f8e\u4eba\u5462\uff0c\u503e\u56fd\u503e\u57ce\uff0c\u8273\u4e3d\u65e0\u53cc\uff0c\u4efb\u4f55\u7537\u4eba\u53ea\u8981\u770b\u5979\u4e00\u773c\uff0c\u7acb\u523b\u5c31\u4f1a\u88ab\u4fd8\u83b7\uff0c\u4e56\u4e56\u6210\u4e3a\u88d9\u4e0b\u4e4b\u81e3\u3002\u201d</p><p>\u743c\u6069\u201c\u54c8\u201d\u5730\u4e00\u58f0\u7b11\u51fa\u6765\uff0c\u201c\u771f\u6709\u90a3\u4e48\u6f02\u4eae\uff0c\u5979\u53ea\u8981\u5f80\u6218\u573a\u4e0a\u4e00\u7ad9\uff0c\u81ea\u7136\u4e24\u519b\u4fef\u9996\u542c\u547d\uff0c\u522b\u8bf4\u5e73\u5b9a\u6218\u4e71\uff0c\u5c31\u7b97\u662f\u7edf\u4e00\u4e16\u754c\u90fd\u884c\u4e86\u3002\u54ea\u91cc\u8fd8\u9700\u8981\u627e\u94a5\u5319\uff0c\u5f00\u5b9d\u5e93\uff0c\u8d39\u8fd9\u4e9b\u5fc3\u601d\u2014\u2014\u9664\u975e\uff0c\u201d\u4ed6\u6545\u610f\u770b\u4e86\u770b\u73ca\u5609\uff0c\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u9664\u975e\u5979\u50cf\u6211\u59d0\u59d0\u4e00\u6837\u6f02\u4eae\uff0c\u90a3\u5012\u6709\u53ef\u80fd\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u542b\u7b11\uff0c\u65e2\u65e0\u81ea\u5f97\u4e4b\u8272\uff0c\u4e5f\u65e0\u7f9e\u6da9\u4e4b\u610f\uff0c\u4eff\u4f5b\u53ea\u662f\u542c\u5230\u743c\u6069\u8bf4\u4e86\u4e00\u4ef6\u4f17\u6240\u5468\u77e5\u7684\u4e8b\u5b9e\u3002</p><p>\u838e\u73de\u514b\u626b\u4e86\u9762\u524d\u7684\u4e24\u4eba\u4e00\u773c\uff0c\u7ee7\u7eed\u521a\u624d\u7684\u8bdd\u9898\u3002\u82db\u5f8b\u4f7f\u8005\u5192\u5145\u5f17\u96f7\u65af\u5230\u4e86\u8fdc\u5c71\uff0c\u4ed6\u8eab\u4e3a\u57ce\u4e3b\uff0c\u505a\u8d77\u4e8b\u60c5\u6765\u81ea\u7136\u65b9\u4fbf\uff0c\u53ef\u4ee5\u8c03\u52a8\u65e0\u6570\u4eba\u529b\u7269\u529b\uff0c\u52a0\u4e0a\u51e0\u5206\u8fd0\u6c14\uff0c\u5c45\u7136\u5f53\u771f\u88ab\u4ed6\u627e\u5230\u4e86\u94a5\u5319\uff0c\u6b63\u51c6\u5907\u9001\u56de\u6069\u745f\uff0c\u7ed3\u679c\u78b0\u4e0a\u6563\u63d0\u5c14\u5821\u524d\u6765\u653b\u57ce\u3002\u800c\u4e14\u4e0d\u77e5\u600e\u4e48\u8d70\u6f0f\u4e86\u98ce\u58f0\uff0c\u6563\u5854\u6797\u4f1a\u4e5f\u77e5\u9053\u4e86\u8fd9\u679a\u94a5\u5319\u7684\u4e8b\u60c5\uff0c\u6b4c\u66e6\u96c5\u4eb2\u81ea\u6765\u8ffd\u6740\uff0c\u535a\u6c99\u8fe6\u867d\u7136\u66fe\u7ecf\u662f\u5409\u52d2\u4eca\u7684\u9ad8\u9636\u796d\u53f8\uff0c\u4f46\u73b0\u5728\u795e\u7947\u5df2\u7ecf\u6b7b\u4ea1\uff0c\u4ed6\u4e27\u5931\u4e86\u5168\u90e8\u795e\u672f\uff0c\u81ea\u7136\u4e0d\u662f\u5bf9\u624b\u3002\u8dcc\u8dcc\u649e\u649e\u52c9\u5f3a\u4fdd\u547d\u9003\u4ea1\uff0c\u6700\u7ec8\u9047\u4e0a\u4e86\u743c\u6069\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u5bb6\u4f19\u662f\u600e\u4e48\u5192\u5145\u5f17\u96f7\u65af\u7684\uff1f\u201d\u743c\u6069\u597d\u5947\uff0c\u201c\u4ec0\u4e48\u4f2a\u88c5\u9b54\u6cd5\u8fd9\u4e48\u5f3a\uff0c\u5c45\u7136\u80fd\u7792\u8fc7\u7ea2\u888d\u5deb\u5e08\u4eec\u3002\u201d</p><p>\u585e\u5c14\u662f\u9b54\u6cd5\u5e1d\u56fd\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u4e5f\u662f\u5927\u9646\u4e0a\u89c4\u6a21\u540d\u5217\u524d\u4e09\u7684\u5deb\u5e08\u7ec4\u7ec7\uff0c\u535a\u6c99\u8fe6\u5192\u5145\u7684\u8fd9\u4e2a\u5f17\u96f7\u65af\u867d\u7136\u4e0d\u662f\u5deb\u5e08\uff0c\u4f46\u5374\u662f\u5927\u5974\u96b6\u8d29\u5b50\uff0c\u662f\u827e\u5c14\u4f2f\u5854\u603b\u7763\u7684\u53d4\u7236\uff0c\u514d\u4e0d\u4e86\u4f1a\u548c\u7ea2\u888d\u5deb\u5e08\u6253\u4ea4\u9053\u3002\u4e0d\u7ba1\u4ed6\u7528\u4ec0\u4e48\u9b54\u6cd5\u6539\u5934\u6362\u9762\uff0c\u63a9\u9970\u672f\u4e5f\u597d\uff0c\u53d8\u5f62\u672f\u4e5f\u7f62\uff0c\u666e\u901a\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u5deb\u5e08\u603b\u662f\u5e94\u8be5\u80fd\u5bdf\u89c9\u5230\u8ff9\u8c61\u7684\u3002</p><p>\u201c\u4e0d\u662f\u9b54\u6cd5\uff0c\u5c31\u662f\u5355\u7eaf\u7684\u6613\u5bb9\uff0c\u201d\u9b45\u9b54\u89e3\u91ca\uff0c\u201c\u636e\u8bf4\u90a3\u4f4d\u5409\u52d2\u4eca\u7684\u738b\u540e\u6781\u5176\u64c5\u957f\u6613\u5bb9\u672f\uff0c\u79f0\u5f97\u4e0a\u662f\u51fa\u795e\u5165\u5316\uff0c\u80fd\u591f\u628a\u4e00\u4e2a\u4eba\u901a\u8fc7\u5316\u5986\u53d8\u6210\u4e00\u53ea\u5730\u7cbe\uff0c\u8fd9\u70b9\u5c0f\u4e8b\u81ea\u7136\u96be\u4e0d\u5012\u5979\u3002\u201d</p><p>\u6613\u5bb9\u2026\u2026\u5514\uff0c\u8fd9\u8fd8\u771f\u662f\u4e2a\u597d\u529e\u6cd5\u3002</p><p>\u5728\u8fd9\u79cd\u5947\u5e7b\u4e16\u754c\uff0c\u56e0\u4e3a\u9b54\u6cd5\u975e\u5e38\u53d1\u8fbe\u7684\u7f18\u6545\uff0c\u522b\u8bf4\u662f\u5deb\u5e08\uff0c\u5c31\u7b97\u662f\u666e\u901a\u4eba\uff0c\u4e00\u65e6\u9047\u5230\u8d85\u8d8a\u5e38\u89c4\u7684\u73b0\u8c61\uff0c\u7b2c\u4e00\u53cd\u5e94\u5c31\u662f\u201c\u8fd9\u662f\u9b54\u6cd5\u201d\u3002\u5c31\u548c\u743c\u6069\u521a\u624d\u4e00\u6837\uff0c\u4e0b\u610f\u8bc6\u5730\u5c31\u4f1a\u60f3\u535a\u6c99\u8fe6\u7528\u7684\u662f\u4ec0\u4e48\u4f2a\u88c5\u9b54\u6cd5\uff0c\u5374\u6ca1\u60f3\u8fc7\u539f\u6765\u53ea\u662f\u6613\u5bb9\u5316\u5986\u800c\u5df2\u3002\u800c\u4e14\u56e0\u4e3a\u6839\u672c\u4e0d\u662f\u9b54\u6cd5\uff0c\u6240\u4ee5\u4e5f\u6ca1\u6709\u7075\u5149\uff0c\u6ca1\u6709\u6ce2\u52a8\uff0c\u518d\u9ad8\u660e\u7684\u5deb\u5e08\u4e5f\u96be\u4ee5\u5bdf\u89c9\uff0c\u7528\u6765\u5bf9\u4ed8\u7ea2\u888d\u5deb\u5e08\u662f\u6700\u5408\u9002\u4e0d\u8fc7\u4e86\u3002</p><p>\u4e8b\u60c5\u7684\u524d\u56e0\uff0c\u57fa\u672c\u5df2\u7ecf\u5f04\u6e05\u695a\u4e86\u3002\u90a3\u73b0\u5728\u8fd8\u5269\u4e0b\u4e24\u4e2a\u95ee\u9898\uff1a\u7b2c\u4e00\uff0c\u80fd\u591f\u5f00\u542f\u738b\u4e4b\u5b9d\u5e93\u7684\u94a5\u5319\u5728\u54ea\u91cc\uff1f\u7b2c\u4e8c\uff0c\u5409\u52d2\u4eca\u53c8\u4e3a\u4f55\u4f1a\u7a81\u7136\u8df3\u51fa\u6765\uff1f</p><p>\u201c\u94a5\u5319\u88ab\u4ed6\u541e\u4e0b\u53bb\u4e86\u3002\u201d\u838e\u73de\u514b\u8bf4\u3002</p><p>\u201c\u541e\u4e0b\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u56e0\u4e3a\u88ab\u6b4c\u66e6\u96c5\u8ffd\u6740\uff0c\u6b7b\u80d6\u5b50\u62c5\u5fc3\u65e0\u6cd5\u9003\u8131\uff0c\u4e8e\u662f\u7d22\u6027\u628a\u94a5\u5319\u7ed9\u541e\u4e86\u4e0b\u53bb\u3002\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u4eba\u541e\u91d1\u662f\u4f1a\u6b7b\u7684\uff0c\u4f46\u90a3\u679a\u94a5\u5319\u662f\u795e\u738b\u4eb2\u624b\u953b\u9020\uff0c\u541e\u4e0b\u53bb\u4e4b\u540e\u5c31\u548c\u4ed6\u7684\u8eab\u4f53\u878d\u4e3a\u4e00\u4f53\u3002\u6240\u4ee5\u5409\u52d2\u4eca\u7684\u51fa\u73b0\u4e5f\u5c31\u7406\u6240\u5f53\u7136\u4e86\uff0c\u56e0\u4e3a\u6b64\u65f6\u7684\u535a\u6c99\u8fe6\uff0c\u5176\u5b9e\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u5df2\u7ecf\u7b97\u662f\u5409\u52d2\u4eca\u7684\u5723\u8005\u3002\u5f53\u7136\uff0c\u56e0\u4e3a\u94a5\u5319\u4e0a\u53ea\u6709\u4e00\u70b9\u795e\u529b\u6b8b\u4f59\uff0c\u5177\u73b0\u51fa\u6765\u7684\u5409\u52d2\u4eca\u4e5f\u53ea\u80fd\u662f\u865a\u5f31\u72b6\u6001\uff0c\u8fd8\u6ca1\u600e\u4e48\u52a8\u624b\u5c31\u81ea\u5df1\u8d25\u9000\u4e86\u3002</p><p>\u7136\u800c\u95ee\u9898\u662f\uff0c\u6069\u745f\u7684\u738b\u540e\u4e4b\u6240\u4ee5\u8981\u627e\u94a5\u5319\uff0c\u5c31\u662f\u4e3a\u4e86\u5f00\u542f\u738b\u4e4b\u8d22\u5b9d\u3002\u5982\u4eca\u94a5\u5319\u88ab\u8fd9\u80d6\u5b50\u541e\u4e86\uff0c\u90a3\u5c82\u4e0d\u662f\u767d\u5fd9\u6d3b\u4e00\u573a\u4e86\u4e48\u3002</p><p>\u201c\u653e\u5fc3\uff0c\u65e2\u7136\u80fd\u541e\u4e0b\u53bb\uff0c\u81ea\u7136\u80fd\u53d6\u51fa\u6765\uff0c\u201d\u9b45\u9b54\u8f7b\u63cf\u6de1\u5199\u5730\u8bf4\uff0c\u201c\u53ea\u662f\u9700\u8981\u70b9\u975e\u5e38\u624b\u6bb5\u7f62\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff1f\u96be\u9053\u5f00\u819b\u7834\u809a\u76f4\u63a5\u53d6\uff1f\u4f46\u4f60\u4e0d\u662f\u8bf4\uff0c\u94a5\u5319\u88ab\u541e\u4e0b\uff0c\u76f4\u63a5\u5c31\u548c\u8eab\u4f53\u878d\u5408\u4e86\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u76f4\u63a5\u878d\u5408\u4e86\uff0c\u6240\u4ee5\u5f00\u819b\u7834\u809a\u4e5f\u6ca1\u7528\uff0c\u800c\u4e14\u4e5f\u6ca1\u5fc5\u8981\u90a3\u4e48\u8840\u8165\u6b8b\u5fcd\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u6700\u7b80\u5355\u4e5f\u662f\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u628a\u4eba\u6740\u4e86\u3002\u4eba\u4e00\u6b7b\uff0c\u94a5\u5319\u5c31\u4f1a\u81ea\u52a8\u91cd\u65b0\u51dd\u805a\u51fa\u6765\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u662f\u6253\u7b97\u56de\u5230\u6069\u745f\u4e4b\u540e\u81ea\u6740\uff1f\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u770b\u7740\u838e\u73de\u514b\uff0c\u201c\u6211\u73b0\u5728\u771f\u597d\u5947\u4e00\u4ef6\u4e8b\u60c5\uff1a\u4f60\u5230\u5e95\u662f\u7528\u4ec0\u4e48\u65b9\u6cd5\u8ba9\u4ed6\u62db\u4f9b\u7684\uff1f\u201d</p><p>\u65e2\u7136\u8fd9\u4f4d\u82db\u5f8b\u4f8d\u8005\u5df2\u7ecf\u62b1\u7740\u5fc5\u6b7b\u4e4b\u5ff5\uff0c\u820d\u8eab\u4e4b\u5fc3\uff0c\u4e3a\u4e86\u628a\u94a5\u5319\u5e26\u56de\u6069\u745f\uff0c\u8fde\u81ea\u5df1\u7684\u6027\u547d\u90fd\u7f6e\u4e4b\u5ea6\u5916\uff0c\u53ef\u89c1\u662f\u201c\u6b7b\u58eb\u201d\u4e00\u6d41\u7684\u4eba\u7269\uff0c\u96be\u602a\u8fde\u7cbe\u795e\u63a7\u5236\u6cd5\u672f\u90fd\u80fd\u62b5\u5fa1\u3002\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u9b45\u9b54\u5230\u5e95\u662f\u5982\u4f55\u641e\u5b9a\u7684\uff0c\u743c\u6069\u5b9e\u5728\u662f\u5f88\u597d\u5947\u3002\u5018\u82e5\u4e0d\u662f\u4ed6\u6709\u8a00\u5728\u5148\uff0c\u201c\u4e0d\u51c6\u8272\u8bf1\u201d\uff0c\u743c\u6069\u90fd\u8981\u6000\u7591\u838e\u73de\u514b\u662f\u4e0d\u662f\u7528\u4e86\u7f8e\u4eba\u8ba1\u3002</p><p>\u838e\u73de\u514b\u4f3c\u7b11\u975e\u7b11\u5730\u770b\u7740\u743c\u6069\uff0c\u53c8\u770b\u4e86\u770b\u73ca\u5609\uff0c\u201c\u4f60\u786e\u5b9a\u771f\u7684\u60f3\u77e5\u9053\uff1f\u201d\u5979\u795e\u60c5\u5996\u5a9a\u5730\u8bf4\uff0c\u201c\u6211\u5012\u4e0d\u4ecb\u610f\u544a\u8bc9\u4f60\uff0c\u4f46\u5982\u679c\u542c\u4e86\u665a\u4e0a\u505a\u5669\u68a6\uff0c\u53ef\u522b\u627e\u6211\u7b97\u8d26\u5462\u3002\u201d</p><p>\u201c\u5443\u2026\u2026\u90a3\u5c31\u7b97\u4e86\u5427\u3002\u201d</p><p>\u743c\u6069\u8f6c\u5ff5\u518d\u4e09\uff0c\u6700\u7ec8\u8fd8\u662f\u7b97\u4e86\u3002\u4ed6\u81ea\u5ea6\u5fc3\u7406\u7d20\u8d28\u8fd8\u884c\uff0c\u5012\u4e0d\u6015\u505a\u5669\u68a6\uff0c\u4f46\u73ca\u5609\u6b64\u65f6\u5c31\u5728\u65c1\u8fb9\u5462\u3002\u8fd8\u662f\u7b49\u4ee5\u540e\u6709\u7a7a\uff0c\u79c1\u4e0b\u95ee\u838e\u73de\u514b\u5f97\u4e86\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u8fd9\u5bb6\u4f19\u600e\u4e48\u5904\u7406\u5462\uff1f\u201d</p><p>\u4e8b\u60c5\u57fa\u672c\u5f04\u6e05\u695a\uff0c\u8be5\u4e86\u89e3\u7684\u4e5f\u90fd\u4e86\u89e3\uff0c\u8fd9\u80d6\u5b50\u4fbf\u5df2\u7ecf\u4e27\u5931\u4e86\u4ef7\u503c\uff0c\u4e00\u76f4\u5e26\u5728\u8eab\u4e0a\u8fd8\u662f\u4e2a\u7d2f\u8d58\u3002\u65e2\u7136\u5982\u6b64\u2026\u2026</p><p>\u201c\u4f60\u770b\u7740\u529e\u5427\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u867d\u7136\u6ca1\u6709\u660e\u767d\u8bf4\u51fa\uff0c\u4f46\u610f\u601d\u662f\u5f88\u660e\u663e\u7684\uff0c\u4fbf\u662f\u76f4\u63a5\u6740\u6389\u4e86\u4e8b\uff0c\u8fd8\u80fd\u987a\u4fbf\u62ff\u5230\u90a3\u679a\u94a5\u5319\uff0c\u8bf4\u4e0d\u5b9a\u4ee5\u540e\u6709\u673a\u4f1a\uff0c\u8fd8\u80fd\u53bb\u6069\u745f\u5f00\u542f\u738b\u4e4b\u5b9d\u5e93\uff0c\u53d1\u7b14\u6a2a\u8d22\u3002\u53cd\u6b63\u8fd9\u5bb6\u4f19\u5176\u5b9e\u662f\u4e2a\u5192\u724c\u8d27\uff0c\u4e0d\u662f\u771f\u7684\u5f17\u96f7\u65af\u672c\u4eba\uff0c\u4e0d\u5fc5\u62c5\u5fc3\u7ea2\u888d\u5deb\u5e08\u627e\u4e0a\u95e8\u7b97\u8d26\u3002\u81f3\u4e8e\u53e6\u5916\u90a3\u679a\u94a5\u5319\uff0c\u80fd\u591f\u5f00\u542f\u8fdc\u5c71\u57ce\u9632\u5fa1\u6cd5\u9635\u7684\uff0c\u5c31\u5148\u62ff\u7740\u518d\u8bf4\uff0c\u4ee5\u540e\u6709\u7a7a\u8fd8\u7ed9\u585e\u5c14\uff0c\u8fd8\u80fd\u7b97\u4f5c\u4e00\u4efd\u4eba\u60c5\u3002</p><p>\u73ca\u5609\u81ea\u7136\u660e\u767d\u743c\u6069\u7684\u610f\u601d\uff0c\u4f46\u4e5f\u53ea\u505a\u6ca1\u542c\u89c1\uff0c\u5168\u65e0\u6240\u8c13\u3002\u838e\u73de\u514b\u8d77\u8eab\u51fa\u95e8\uff0c\u8fc7\u4e86\u5341\u5206\u949f\u540e\u8fd4\u56de\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u679a\u91d1\u707f\u707f\u7684\u94a5\u5319\uff0c\u9020\u578b\u6709\u4e9b\u53e4\u602a\uff0c\u5f62\u5982\u98de\u9e1f\u5f20\u5f00\u53cc\u7ffc\u3002\u5728\u94a5\u5319\u7684\u6b63\u53cd\u4e24\u9762\uff0c\u5206\u522b\u523b\u7740\u4e00\u884c\u6587\u5b57\uff0c\u743c\u6069\u770b\u4e0d\u61c2\uff0c\u4f46\u5176\u4e2d\u4e00\u4e2a\u8bcd\u4ed6\u662f\u8ba4\u8bc6\u7684\uff0c\u201c\u661f\u8fb0\u201d\uff0c\u53c8\u505a\u201c\u795e\u660e\u201d\uff0c\u4e00\u8bcd\u4e24\u4e49\uff0c\u6b63\u662f\u82cf\u7f8e\u5c14\u7684\u6954\u5f62\u6587\u5b57\u3002</p><p>\u201c\u5982\u4f55\uff0c\u8981\u4e0d\u8981\u4e5f\u541e\u4e0b\u53bb\u8bd5\u8bd5\uff1f\u201d\u838e\u73de\u514b\u5c06\u94a5\u5319\u9012\u7ed9\u743c\u6069\u3002</p><p>\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002\u838e\u73de\u514b\u5f53\u7136\u662f\u5728\u5f00\u73a9\u7b11\uff0c\u4f46\u5979\u4e0d\u77e5\u9053\u7684\u662f\uff0c\u743c\u6069\u8fd8\u771f\u52a8\u8fc7\u8fd9\u4e2a\u5fc3\u601d\u3002\u4ed6\u53ef\u662f\u80fd\u6d88\u878d\u795e\u529b\u7684\uff0c\u522b\u4eba\u541e\u4e86\u94a5\u5319\uff0c\u6216\u8bb8\u4f1a\u6709\u88ab\u5409\u52d2\u91d1\u9644\u4f53\u593a\u8eaf\u4ec0\u4e48\u7684\u62c5\u5fc3\uff0c\u4ed6\u5374\u534a\u70b9\u4e0d\u6015\u3002\u5df4\u5c14\u7684\u795e\u529b\u4ed6\u90fd\u541e\u566c\u8fc7\u4e24\u6b21\uff0c\u8fd8\u6015\u5409\u52d2\u91d1\u4e0d\u6210\u3002</p><p>\u7b97\u8d77\u6765\uff0c\u4ed6\u6bcf\u6b21\u5438\u6536\u795e\u529b\uff0c\u90fd\u80fd\u8ba9\u9b54\u6cd5\u9020\u8be3\u83b7\u5f97\u663e\u8457\u63d0\u5347\u3002\u7b2c\u4e00\u6b21\u5728\u70db\u5821\uff0c\u5438\u6536\u4e86\u838e\u73de\u514b\u7684\u795e\u529b\uff0c\u968f\u540e\u5f88\u5feb\u8054\u7ed3\u5230\u9b54\u7f51\u7b2c\u56db\u5c42\uff1b\u7b2c\u4e8c\u6b21\u5728\u585e\u5c14\uff0c\u5438\u6536\u4e86\u83f2\u5a05\u7684\u795e\u529b\uff0c\u4e00\u4e3e\u51dd\u7ed3\u6210\u4e86\u771f\u540d\uff0c\u8de8\u5165\u9ad8\u9636\u5deb\u5e08\u7684\u884c\u5217\uff08\u5f53\u7136\u53ea\u6015\u4e5f\u6709\u5f71\u706b\u7684\u529f\u52b3\uff09\u3002\u81ea\u4ece\u8fdb\u5165\u5e7d\u6697\u5730\u57df\u4ee5\u6765\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u4e00\u76f4\u90fd\u505c\u7559\u5728\u9b54\u7f51\u7b2c\u516d\u5c42\u7684\u9636\u6bb5\uff0c\u8ddd\u79bb\u9b54\u7f51\u7b2c\u4e03\u5c42\u53ea\u5dee\u4e00\u7ebf\uff0c\u5374\u59cb\u7ec8\u65e0\u6cd5\u7a81\u7834\u3002\u5982\u4eca\u5f97\u5230\u5409\u52d2\u91d1\u7684\u94a5\u5319\uff0c\u5012\u8fd8\u771f\u662f\u4e2a\u597d\u673a\u4f1a\uff0c\u5982\u679c\u80fd\u591f\u541e\u566c\u3001\u878d\u5408\uff0c\u6599\u60f3\u80fd\u591f\u5f88\u5feb\u66f4\u8fdb\u4e00\u5c42\u5427\u3002</p><p>\u53ea\u662f\u5982\u679c\u541e\u4e0b\u53bb\uff0c\u94a5\u5319\u5c31\u6ca1\u4e86\uff0c\u4ee5\u540e\u5c31\u6ca1\u6cd5\u5f00\u542f\u738b\u4e4b\u5b9d\u5e93\u3002\u867d\u7136\u4e0d\u77e5\u9053\u5b9d\u5e93\u91cc\u5230\u5e95\u90fd\u6709\u4e9b\u4ec0\u4e48\uff0c\u4f46\u5409\u52d2\u91d1\u597d\u6b79\u4e00\u65b9\u795e\u738b\uff0c\u7edf\u6cbb\u6069\u745f\u6570\u5343\u5e74\uff0c\u4ed6\u53c8\u662f\u4e2a\u6a2a\u5f81\u66b4\u655b\u7684\u66b4\u541b\uff0c\u6599\u60f3\u5b9d\u5e93\u91cc\u597d\u4e1c\u897f\u80af\u5b9a\u4e0d\u5c11\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u5f3a\u529b\u7684\u9b54\u6cd5\u7269\u54c1\u751a\u81f3\u795e\u5668\u3002\u5426\u5219\u5409\u52d2\u91d1\u7684\u738b\u540e\u4e5f\u4e0d\u4f1a\u60f3\u501a\u9760\u738b\u4e4b\u5b9d\u5e93\u6765\u5e73\u5b9a\u6218\u4e71\uff0c\u6253\u8d25\u7a46\u7f55\u745e\u5fb7\u7684\u4fb5\u7565\u519b\u4e86\u3002</p><p>\u6240\u4ee5\u8fd9\u5c31\u9700\u8981\u6743\u8861\u4e86\uff0c\u662f\u8981\u773c\u524d\u89e6\u624b\u53ef\u53ca\u7684\u5229\u76ca\uff0c\u8fd8\u662f\u8981\u4ee5\u540e\u53ef\u80fd\u4f1a\u83b7\u5f97\u7684\u597d\u5904\u3002\u5f53\u7136\uff0c\u4e0d\u501f\u52a9\u94a5\u5319\uff0c\u743c\u6069\u4e5f\u5e76\u4e0d\u662f\u5c31\u4e0d\u80fd\u7a81\u7834\u63d0\u5347\uff0c\u53ea\u662f\u65f6\u95f4\u4f1a\u5f80\u540e\u63a8\u8fdf\u7f62\u4e86\uff1b\u800c\u53cd\u8fc7\u6765\u8bf4\uff0c\u5c31\u7b97\u4fdd\u7559\u94a5\u5319\uff0c\u4e5f\u672a\u5fc5\u5c31\u4e00\u5b9a\u80fd\u627e\u5230\u5e76\u6253\u5f00\u738b\u4e4b\u5b9d\u5e93\uff0c\u8fd9\u4e00\u5207\u90fd\u8fd8\u53ea\u662f\u672a\u77e5\u6570\u3002</p><p>\u5982\u4f55\u53d6\u820d\u5462\uff1f</p><p>\u743c\u6069\u770b\u4e86\u770b\u8eab\u65c1\u7684\u73ca\u5609\uff0c\u7136\u540e\u6709\u4e86\u51b3\u5b9a\u3002</p></article>"}}}]);
