(window.webpackJsonp=window.webpackJsonp||[]).push([[450],{473:function(p){p.exports={chapterTitle:null,title:"\u7b2c450\u7ae0 \u5f20\u4e9a\u5e73\u7684\u9009\u62e9",content:"<article><h2>\u7b2c450\u7ae0 \u5f20\u4e9a\u5e73\u7684\u9009\u62e9</h2><p>\u3000\u3000\u5218\u660e\u8f89\u4e0e\u968f\u4ed6\u4ece\u7231\u8fbe\u7535\u5b50\u8df3\u69fd\u5230\u79d1\u738b\u7684\u5341\u540d\u9ad8\u7ea7\u9500\u552e\u4e3b\u7ba1\u4ece\u5341\u4e09\u65e5\u5c31\u79bb\u5f00\u6d77\u5dde\u5230\u534e\u5357\u3001\u534e\u4e2d\u4e24\u5730\u62dc\u8bbf\u4ee5\u524d\u7684\u5ba2\u6237\uff0c\u90fd\u662f\u5f53\u5730\u76f8\u5f53\u6709\u5b9e\u529b\u7684\u7ecf\u9500\u5546\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e0d\u8bbe\u5730\u533a\u603b\u4ee3\uff0c\u6240\u8c13\u7684\u201c\u4e09\u7ea7\u201d\u4ee3\u7406\u53ea\u662f\u6839\u636e\u5404\u4e2a\u5730\u533a\u5e02\u573a\u89c4\u6a21\u7684\u5927\u5c0f\u5206\u7c7b\u5e76\u6536\u53d6\u4e00\u5230\u4e94\u767e\u4e07\u4e0d\u7b49\u7684\u4fdd\u8bc1\u91d1\uff0c\u8bf4\u201c\u4e09\u7c7b\u201d\u4ee3\u7406\u5236\u5ea6\u6216\u8bb8\u66f4\u51c6\u786e\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u8fd9\u4e48\u4e00\u641e\uff0c\u51e0\u4e4e\u5c06\u5404\u5730\u6709\u5b9e\u529b\u7684\u7535\u5668\u4ee3\u7406\u5546\u90fd\u7f51\u7f57\u5230\u4ed6\u4eec\u7684\u6e20\u9053\u5546\u7f51\u7edc\u91cc\uff0c\u5728\u5b8c\u6574\u7684\u5546\u54c1\u6d41\u901a\u8fc7\u7a0b\u4e2d\uff0c\u7ecf\u9500\u5546\u662f\u76f8\u5f53\u91cd\u8981\u7684\u4e00\u73af\uff0c\u662f\u76f4\u63a5\u5f71\u54cd\u5e02\u573a\u7ec8\u7aef\u7684\u4e00\u73af\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u70b9\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u5f88\u6e05\u695a\uff0c\u5218\u660e\u8f89\u8ddf\u5728\u5f20\u606a\u8eab\u8fb9\u6709\u4e24\u5e74\u591a\u7684\u65f6\u5019\uff0c\u4ece\u5f20\u606a\u90a3\u91cc\u770b\u5230\u7684\uff0c\u542c\u5230\u7684\uff0c\u611f\u53d7\u5230\u7684\u4e5f\u662f\u8fd9\u4e2a\u7ed3\u8bba\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u4e0e\u968f\u4ed6\u4ece\u7231\u8fbe\u7535\u5b50\u8df3\u69fd\u79d1\u738b\u7684\u5341\u540d\u9ad8\u7ea7\u9500\u552e\u4e3b\u7ba1\uff0c\u5fc3\u91cc\u4e5f\u5f88\u6e05\u695a\u81ea\u5df1\u5bf9\u79d1\u738b\u6700\u5927\u7684\u4ef7\u503c\u662f\u4ec0\u4e48\uff0c\u5229\u7528\u4ed6\u4eec\u5728\u7231\u8fbe\u7535\u5b50\u79ef\u7d2f\u4e0b\u6765\u7684\u4eba\u8109\uff0c\u5e2e\u79d1\u738b\u8fc5\u901f\u5efa\u7acb\u8d77\u4e00\u4e2a\u6709\u76f8\u5f53\u901a\u7545\u7684\u3001\u80fd\u591f\u5bf9\u5404\u5730\u7ec8\u7aef\u5e02\u573a\u65bd\u52a0\u5f71\u54cd\u529b\u7684\u6e20\u9053\u5546\u7f51\u7edc\u3002</p><p>\u3000\u3000\u53ea\u7559\u7ed9\u4ed6\u4eec\u5341\u5929\u7684\u65f6\u95f4\u5728\u5916\u9762\u5954\u8dd1\uff0c\u5230\u4e8c\u5341\u4e09\u3001\u4e8c\u5341\u56db\u65e5\uff0c\u7231\u8fbe\u7535\u5b50\u5404\u5730\u7684\u7ecf\u9500\u5546\u90fd\u4f1a\u805a\u96c6\u5230\u6d77\u5dde\uff0c\u4ee5\u7231\u8fbe\u7535\u5b50\u4e00\u60ef\u7684\u63a7\u5236\u4eba\u7684\u624b\u6bb5\uff0c\u5218\u660e\u8f89\u53ea\u6015\u5f88\u96be\u5728\u7231\u8fbe\u7535\u5b50\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u524d\u63a5\u89e6\u5230\u4ed6\u60f3\u63a5\u89e6\u7684\u4eba\uff08\u63a5\u89e6\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u4e24\u4eba\u4e4b\u96be\uff0c\u8ba9\u4ed6\u4eec\u5fc3\u6709\u4f59\u60b8\uff09\u3002\u8981\u662f\u8fd9\u4e9b\u7ecf\u9500\u5546\u90fd\u5148\u8ddf\u7231\u8fbe\u7535\u5b50\u7b7e\u59a5\u4e86\u660e\u5e74\u7684\u4ee3\u7406\u5408\u540c\uff0c\u4ed6\u4eec\u4e8b\u540e\u518d\u591a\u8d39\u5507\u820c\u4e5f\u6ca1\u6709\u7528\uff0c\u6240\u4ee5\u867d\u7136\u4f1a\u5f88\u8f9b\u82e6\uff0c\u4ed6\u8fd8\u662f\u8981\u5229\u7528\u5b9d\u8d35\u7684\u5341\u5929\u65f6\u95f4\uff0c\u5c06\u4ed6\u638c\u63e1\u7684\u7ecf\u9500\u5546\u90fd\u9010\u4e00\u62dc\u8bbf\u5230\u3002</p><p>\u3000\u3000\u4e8c\u5341\u4e09\u65e5\uff0c\u5218\u660e\u8f89\u5148\u8fd4\u56de\u6d77\u5dde\uff0c\u4eba\u5df2\u7ecf\u7d2f\u5230\u6cbe\u4e0a\u6c99\u53d1\u5c31\u80fd\u7761\u7740\u7684\u5730\u6b65\uff0c\u5218\u660e\u8f89\u5750\u5230\u4ed6\u5728\u5e9c\u5929\u5927\u53a6\u5341\u4e8c\u5c42\u897f\u5357\u89d2\u529e\u516c\u5ba4\u7684\u771f\u76ae\u5bbd\u6276\u624b\u9ad8\u80cc\u529e\u516c\u6905\u4e0a\u5f88\u5feb\u5c31\u7761\u7740\u4e86\uff0c\u4ed6\u6628\u5929\u966a\u4e0a\u6d77\u6c38\u57ce\u4ea4\u5bb6\u7535\u7684\u8001\u677f\u65bd\u6bc5\u5a31\u5230\u51cc\u6668\u4e24\u70b9\uff0c\u7136\u800c\u4ece\u4e0a\u6d77\u76f4\u63a5\u5f00\u8f66\u56de\u6d77\u5dde\uff0c\u53ea\u6765\u5f97\u53ca\u5728\u516c\u53f8\u6d17\u624b\u95f4\u91cc\u6d17\u6f31\u4e00\u4e0b\uff0c\u5c31\u5230\u4e86\u4e0a\u73ed\u65f6\u95f4\u3002</p><p>\u3000\u3000\u68a6\u89c1\u7535\u8bdd\u54cd\u4e86\u4e00\u9635\uff0c\u5218\u660e\u8f89\u5373\u4f7f\u5728\u68a6\u91cc\u4e5f\u61d2\u5f97\u63a5\u7535\u8bdd\uff0c\u76f4\u5230\u611f\u89c9\u6709\u4eba\u5728\u63a8\u4ed6\uff0c\u4ed6\u624d\u60ca\u9192\u8fc7\u6765\uff0c\u5dee\u70b9\u4ece\u6905\u5b50\u4e0a\u8dcc\u5012\uff0c\u770b\u7740\u4ed6\u7684\u79d8\u4e66\u7ad9\u5728\u529e\u516c\u684c\u6342\u7740\u5634\u5077\u7b11\uff0c\u5218\u660e\u8f89\u6413\u4e86\u6413\u8138\uff0c\u95ee\u5979\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u8fd9\u4e2a\u79d8\u4e66\u662f\u4eba\u4e8b\u90e8\u7edf\u4e00\u62db\u8fdb\u6765\u7684\uff0c\u957f\u76f8\u6e05\u79c0\uff0c\u5218\u660e\u8f89\u4e5f\u4e0d\u597d\u610f\u601d\u8ddf\u4eba\u5bb6\u677f\u7740\u8138\u3002\u79d1\u738b\u9664\u4e86\u603b\u7ecf\u7406\u3001\u526f\u603b\uff0c\u5c31\u4ed6\u914d\u5907\u4e86\u79d8\u4e66\u3002\u8c22\u5251\u5357\u5728\u8fd9\u91cc\u4e5f\u6ca1\u6709\u79d8\u4e66\uff0c\u8fd9\u6bb5\u65f6\u95f4\u7684\u4e8b\u60c5\u5fd9\u5b8c\u4e4b\u540e\uff0c\u8c22\u5251\u5357\u8fd8\u8981\u56de\u53bb\u4e3b\u6301\u4ed6\u7684\u6d77\u6cf0\u7535\u5668\u7684\u3002</p><p>\u3000\u3000\u201c\u8463\u4e8b\u957f\u6253\u7535\u8bdd\u8fc7\u6765\u627e\u4f60\u53bb\u4ed6\u529e\u516c\u5ba4\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u6709\u6253\u6211\u529e\u516c\u5ba4\u7684\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u201c\u54cd\u4e86\u597d\u4e00\u9635\uff0c\u4f60\u90fd\u6ca1\u9192\u3002\u201d</p><p>\u3000\u3000\u539f\u6765\u662f\u771f\u542c\u89c1\u7535\u8bdd\u94c3\u4e86\uff0c\u6f5c\u610f\u8bc6\u91cc\u5e0c\u671b\u662f\u68a6\u4e2d\uff0c\u53ea\u4e3a\u4e86\u591a\u7761\u4e00\u4f1a\u513f\uff0c\u5218\u660e\u8f89\u4e0d\u6562\u803d\u6401\uff0c\u5c06\u897f\u88c5\u7a7f\u8d77\u6765\uff0c\u8dd1\u5230\u8c22\u5251\u5357\u5728\u5341\u4e8c\u5c42\u4e1c\u5357\u89d2\u4e0a\u7684\u529e\u516c\u5ba4\u53bb\u3002</p><p>\u3000\u3000\u79d1\u738b\u603b\u7ad9\u5360\u636e\u5341\u5230\u5341\u4e8c\u5c42\uff0c\u56db\u4e94\u5343\u5e73\u7c73\u7684\u529e\u516c\u9762\u79ef\uff0c\u76f8\u5bf9\u6765\u8bf4\u592a\u7a7a\u65f7\u4e86\uff0c\u9ad8\u7ea7\u7ba1\u7406\u8005\u7684\u529e\u516c\u5ba4\u90fd\u5728\u5341\u4e8c\u5c42\uff0c\u5404\u4e2a\u90e8\u95e8\u7684\u529e\u516c\u5ba4\u4e5f\u5f88\u5bbd\u655e\uff0c\u4e0d\u50cf\u7231\u8fbe\u7535\u5b50\uff0c\u884c\u653f\u8d22\u52a1\u90e8\u95e8\u7684\u529e\u516c\u5ba4\u6df7\u5728\u4e00\u8d77\uff0c\u5e02\u573a\u54c1\u7ba1\u552e\u670d\u90e8\u95e8\u7684\u529e\u516c\u5ba4\u6df7\u5728\u4e00\u8d77\uff0c\u751f\u4ea7\u90e8\u95e8\u7684\u529e\u516c\u5ba4\u653e\u5728\u751f\u4ea7\u533a\uff0c\u7814\u53d1\u90e8\u95e8\u653e\u5728\u5e02\u533a\u3002</p><p>\u3000\u3000\u201c\u8c22\u603b\u2026\u2026\u201d\u5218\u660e\u8f89\u63a8\u95e8\u79f0\u547c\u5750\u5728\u529e\u516c\u684c\u524d\u8ddf\u9648\u9759\u8c08\u4e8b\u60c5\u7684\u8c22\u5251\u5357\uff0c\u53d1\u73b0\u8c22\u77bb\u4e5f\u5750\u5728\u529e\u516c\u5ba4\u4e00\u89d2\u7684\u6c99\u53d1\u4e0a\uff0c\u611f\u89c9\u5f88\u522b\u626d\u7684\u53c8\u5524\u4e86\u4e00\u58f0\uff0c\u201c\u8c22\u603b\u4e5f\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u51cc\u6668\u5f00\u8f66\u8d76\u56de\u6765\u5f88\u7d2f\u5427\uff0c\u7535\u8bdd\u6253\u4e86\u4e24\u56de\u90fd\u6ca1\u6709\u628a\u4f60\u5435\u9192\uff1f\u201d\u8c22\u5251\u5357\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u597d\u50cf\u6709\u542c\u89c1\uff0c\u611f\u89c9\u662f\u505a\u68a6\uff0c\u5c31\u61d2\u5f97\u63a5\u4e86\uff0c\u6ca1\u60f3\u5230\u771f\u662f\u7535\u8bdd\u9192\u4e86\u3002\u201d\u5218\u660e\u8f89\u89e3\u91ca\u9053\uff0c\u79d8\u4e66\u5c06\u81ea\u5df1\u8db4\u684c\u4e0a\u7761\u89c9\u7684\u4e8b\u60c5\u544a\u8bc9\u8c22\u5251\u5357\u4e86\uff0c\u4e5f\u6ca1\u6709\u4e0d\u597d\u8bf4\u7684\u3002</p><p>\u3000\u3000\u201c\u8ba9\u4f60\u56de\u53bb\u7761\u4e00\u89c9\u518d\u5230\u516c\u53f8\u6765\uff0c\u4f60\u8fd8\u4e0d\u4e50\u610f\uff0c\u201d\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u7ad9\u8d77\u6765\uff0c\u8bf7\u5218\u660e\u8f89\u5230\u529e\u516c\u5ba4\u8f6c\u89d2\u5904\u7684\u4f11\u606f\u533a\u5750\u4e0b\uff0c\u201c\u53bb\u5e74\uff0c\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\uff0c\u8fd8\u6709\u827e\u9ed8\u90fd\u662f\u8fd9\u65f6\u5019\u5230\u6d77\u5dde\u6765\uff0c\u80cc\u5730\u91cc\u6709\u4ec0\u4e48\u6545\u4e8b\uff0c\u4ece\u5e02\u573a\u7684\u53cd\u5e94\u6765\u770b\uff0c\u4ed6\u4eec\u5e94\u8be5\u6709\u79d8\u5bc6\u534f\u8bae\u624d\u662f\u3002\u201d</p><p>\u3000\u3000\u201c\u636e\u6211\u6240\u77e5\u4e3b\u8981\u8c08\u4e24\u4e2a\u4e8b\u60c5\uff0c\u201d\u5218\u660e\u8f89\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u770b\u5230\u9648\u9759\u5e2e\u4ed6\u6c8f\u6765\u5496\u5561\uff0c\u88c5\u4f5c\u60f6\u6050\u7684\u63a5\u8fc7\uff0c\u5c06\u70ed\u5496\u5561\u653e\u73bb\u7483\u51e0\u4e0a\uff0c\u7ee7\u7eed\u521a\u624d\u7684\u8bdd\u9898\uff0c\u201c\u4e00\u4e2a\u662f\u98de\u5229\u6d66\u673a\u82af\u7684\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\uff0c\u5f53\u65f6\u98de\u5229\u6d66\u7684\u4ea7\u80fd\u5f00\u5230\u6700\u5927\uff0c\u6bcf\u6708\u4e5f\u53ea\u80fd\u751f\u4ea7\u4e8c\u5341\u4e94\u5957\u673a\u82af\uff0c\u7231\u8fbe\u7535\u5b50\u4e00\u4e0b\u5b50\u5305\u4e0b\u5341\u4e07\u5957\u2026\u2026\u8fd8\u6709\u5c31\u662f\u4e0eTI\u516c\u53f8\u5408\u8d44\u6210\u7acb\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u6240\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u90fd\u662f\u5f20\u606a\u4eb2\u81ea\u8c08\u4e0b\u6765\u7684\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\u4e5f\u6ca1\u542c\u4ed6\u8bf4\u8d77\u8fc7\u3002\u201d</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u521d\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7ec4\u7ec7\u4eba\u624b\u5f00\u53d1\u5f71\u789f\u673a\u6837\u673a\uff0c\u4e00\u76f4\u5230\u516d\u6708\uff0c\u5f20\u606a\u624d\u6b63\u5f0f\u4ece\u9526\u6e56\u62bd\u8c03\u5468\u6e38\u3001\u5218\u660e\u8f89\u3001\u5468\u4e00\u5e73\u3001\u848b\u8587\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u5404\u4e2a\u90e8\u95e8\u90fd\u7ec4\u5efa\u8d77\u6765\uff0c\u5218\u660e\u8f89\u4e4b\u524d\u5f88\u77ed\u7684\u4e00\u6bb5\u65f6\u95f4\u662f\u5c5e\u4e8e\u501f\u7528\u6027\u8d28\uff0c\u4e5d\u516d\u5e74\u4e09\u6708\u4efd\u8fd8\u662f\u56de\u9526\u6e56\u7b56\u5212\u90e8\u5de5\u4f5c\uff0c\u6240\u4ee5\u5bf9\u4ed6\u6765\u8bf4\uff0c\u4e5d\u516d\u5e74\u521d\u4e00\u76f4\u5230\u516d\u6708\u5e95\u5f00\u59cb\u505a\u5e02\u573a\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ed6\u4eec\u53ea\u662f\u7ec4\u7ec7\u4eba\u624b\u5f00\u53d1\u6837\u673a\u800c\u5df2\uff0c\u5f88\u591a\u7684\u4e8b\u60c5\uff0c\u4ed6\u90fd\u4e0d\u6e05\u695a\uff0c\u6bd5\u7adf\u6d89\u53ca\u5230\u4e0eTI\u516c\u53f8\u7684\u90a3\u4efd\u79d8\u5bc6\u534f\u8bae\uff0c\u4e0d\u4ec5\u4ed6\u4e0d\u6e05\u695a\uff0c\u5468\u4e00\u5e73\u3001\u6768\u4e91\u4ed6\u4eec\u90fd\u4e0d\u6e05\u695a\uff0c\u848b\u8587\u3001\u5468\u6e38\u4e5f\u662f\u786e\u5b9a\u8981\u63d0\u526f\u603b\u88c1\u4e4b\u540e\u624d\u77e5\u9053\u8fd9\u4e9b\u516c\u53f8\u7684\u673a\u5bc6\u3002</p><p>\u3000\u3000\u201c\u5f53\u65f6\u673a\u82af\u90a3\u4e48\u7d27\u4fcf\uff0c\u5f20\u4e9a\u5e73\u4e3a\u4ec0\u4e48\u4f1a\u7b54\u5e94\u4e00\u4e0b\u5b50\u7ed9\u7231\u8fbe\u5341\u4e07\u5957\uff1f\u201d\u8c22\u5251\u5357\u770b\u7740\u5218\u660e\u8f89\u3002</p><p>\u3000\u3000\u201c\u5f53\u65f6\u5f20\u4e9a\u5e73\u66f4\u770b\u597d\u65b0\u79d1\u7535\u5b50\uff0c\u4e0d\u8fc7\u65b0\u79d1\u9009\u62e9\u8ddf\u7d22\u5c3c\u5408\u4f5c\uff0c\u751a\u81f3\u63a8\u8fdf\u4ea7\u54c1\u4e0a\u5e02\u7684\u65f6\u95f4\uff0c\u6070\u597d\u7231\u8fbe\u593a\u4e0b\u6807\u738b\uff0c\u5f20\u606a\u5f53\u65f6\u4e5f\u5a01\u80c1\u5f20\u4e9a\u5e73\u8bf4\u8981\u8ddf\u7d22\u5c3c\u5408\u4f5c\uff0c\u624d\u4e89\u4e0b\u90a3\u4efd\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f20\u4e9a\u5e73\u4e0d\u8be5\u8fd9\u4e48\u8f6f\u5f31\u624d\u662f\u3002\u201d\u8c22\u5251\u5357\u5fc3\u91cc\u8fd8\u662f\u6709\u7591\u95ee\uff0c\u7d22\u5c3c\u4e00\u76f4\u5230\u4eca\u5e74\u4e94\u6708\u4efd\u624d\u62ff\u51fa\u8d28\u91cf\u7a33\u5b9a\u7684\u673a\u82af\u4ea7\u54c1\uff0c\u5c31\u7b97\u7231\u8fbe\u7535\u5b50\u5728\u7cfb\u7edf\u63a7\u5236\u7684\u6280\u672f\u4e0a\u80fd\u53ca\u65f6\u8ddf\u8fdb\uff0c\u4e5f\u8981\u6d6a\u8d39\u5f88\u957f\u7684\u65f6\u95f4\uff0c\u5f20\u4e9a\u5e73\u4e0d\u53ef\u80fd\u5ffd\u89c6\u8fd9\u70b9\uff0c\u600e\u4e48\u53ef\u80fd\u8f7b\u89c6\u5c31\u8ba9\u5f20\u606a\u8981\u631f\u5462\uff1f</p><p>\u3000\u3000\u201c\u5f20\u4e9a\u5e73\u672c\u8eab\u5f88\u652f\u6301\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u7684\u53d1\u5c55\uff0c\u5176\u5b9e\u5728\u593a\u6807\u738b\u4e4b\u524d\uff0c\u5927\u6982\u662f\u5341\u6708\u521d\uff0c\u5f20\u606a\u5c31\u8ddf\u98de\u5229\u6d66\u5728\u8c08\u4f18\u5148\u4f9b\u8d27\u65b9\u9762\u7684\u4e8b\u60c5\uff0c\u90a3\u65f6\u5019\uff0c\u5f20\u4e9a\u5e73\u5012\u6ca1\u6709\u7406\u776c\u6211\u4eec\uff0c\u5c31\u5728\u53bb\u5e74\u5341\u6708\u4efd\uff0c\u7231\u8fbe\u5bf9\u5f20\u4e9a\u5e73\u505a\u4e86\u5f88\u591a\u5de5\u4f5c\uff0c\u4e00\u76f4\u5230\u7231\u8fbe\u62ff\u4e0b\u6807\u738b\uff0c\u6709\u4e9b\u4e8b\u5c31\u987a\u7406\u6210\u7ae0\u4e86\u3002\u201d\u5218\u660e\u8f89\u89e3\u91ca\u9053\u3002</p><p>\u3000\u3000\u201c\u4e5f\u80fd\u8bf4\u5f97\u901a\uff0c\u201d\u8c22\u5251\u5357\u63ea\u7740\u7684\u7709\u5934\u4e00\u76f4\u6ca1\u6709\u8212\u5c55\u5f00\uff0c\u4ed6\u95ee\u5218\u660e\u8f89\uff0c\u201c\u90a3\u8fd9\u6b21\u4f60\u89c9\u5f97\u4f1a\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u5dee\u4e0d\u591a\u5df2\u7ecf\u5728\u6d77\u5dde\u4f4f\u4e86\u5927\u534a\u4e2a\u6708\uff0c\u5f20\u4e9a\u5e73\u524d\u5929\u4e5f\u5230\u4e86\u6d77\u5dde\u3002\u201d</p><p>\u3000\u3000\u201c\u5f20\u4e9a\u5e73\u4e5f\u63d0\u524d\u5230\u4e86\u6d77\u5dde\uff1f\u201d\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u5728\u6d77\u5dde\u7684\u4e8b\uff0c\u5218\u660e\u8f89\u77e5\u9053\uff0c\u4ed6\u4e0d\u77e5\u9053\u5f20\u4e9a\u5e73\u9759\u4e5f\u5df2\u7ecf\u5230\u4e86\u6d77\u5dde\u3002</p><p>\u3000\u3000\u201c\u662f\u7684\uff0c\u800c\u4e14\u6211\u4eec\u8fd8\u6ca1\u6709\u63a5\u89e6\u5230\u3002\u201d\u8c22\u5251\u5357\u5fc3\u5934\u538b\u7740\u7684\u9634\u5f71\u5f88\u6c89\u91cd\uff0c\u79d1\u738b\u8981\u5927\u5c55\u624b\u811a\u4e4b\u9645\uff0c\u65e0\u8bba\u662fTI\u3001\u65af\u9ad8\u67cf\uff0c\u8fd8\u662f\u98de\u5229\u6d66\uff0c\u53ea\u8981\u7a0d\u7a0d\u505a\u4e00\u70b9\u624b\u811a\uff0c\u5c31\u8db3\u4ee5\u7ed9\u79d1\u738b\u9020\u6210\u5f88\u6076\u52a3\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u201c\u89e3\u7801\u677f\u7684\u4ea7\u80fd\u5f39\u6027\u5927\uff0c\u7279\u522bTI\u662f\u4e16\u754c\u6700\u5927\u7684\u51e0\u4e2a\u534a\u5bfc\u4f53\u82af\u7247\u5236\u9020\u5546\u4e4b\u4e00\uff0c\u800c\u4e14\u6211\u4eec\u8ddfTI\u3001\u65af\u9ad8\u67cf\u90fd\u7b7e\u7f72\u4e86\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\uff0c\u8fd9\u4e00\u5757\u5c31\u4e0d\u5b58\u5728\u4ec0\u4e48\u95ee\u9898\uff0c\u201d\u60f3\u60f3\u56db\u5343\u4e07\u7684\u4fdd\u8bc1\u91d1\uff0c\u8c22\u5251\u5357\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u51a4\uff0c\u83ab\u660e\u5176\u5999\u7684\u5c31\u7ed9\u5f20\u606a\u5728\u9152\u5e2d\u4e0a\u6446\u4e86\u4e00\u9053\uff0c\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u8ddf\u98de\u5229\u6d66\u7b7e\u7f6e\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\u65f6\uff0c\u5c31\u6839\u672c\u6ca1\u6709\u63d0\u4fdd\u8bc1\u91d1\u4e00\u8bf4\uff0c\u4ee5\u5f53\u65f6\u7d27\u4fcf\u7684\u673a\u82af\u5e02\u573a\u884c\u60c5\uff0c\u7231\u8fbe\u4e00\u4e2a\u6708\u6d88\u5316\u4e0d\u4e86\u5341\u4e07\u5957\u673a\u82af\uff0c\u8f6c\u624b\u5c31\u80fd\u767d\u635e\u4e0d\u5c11\u94b1\uff0c\u5f53\u65f6\u7684\u786e\u6709\u5382\u5546\u76f4\u63a5\u8f6c\u5356\u673a\u82af\u53d1\u4e86\u4e00\u7b14\u6a2a\u8d22\uff0c\u201c\u673a\u82af\u5c31\u4e0d\u540c\u4e86\uff0c\u98de\u5229\u6d66\u4e0a\u6d77\u5de5\u5382\u4e00\u3001\u4e8c\u671f\u7684\u4ea7\u91cf\u4e5f\u5feb\u63a5\u8fd1\u8bbe\u8ba1\u4ea7\u80fd\u4e86\uff0c\u4e5f\u6ca1\u6709\u542c\u8bf4\u6709\u7ee7\u7eed\u6269\u4ea7\u7684\u610f\u5411\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5d\u516d\u5e74\uff0c\u98de\u5229\u6d66\u673a\u82af\u6240\u5360\u5e02\u573a\u4efd\u989d\u63a5\u8fd180\uff05\uff0c\u4ed6\u4eec\u53ef\u80fd\u8ba4\u4e3a\u4e0b\u4e00\u5e74\u5ea6\u4f1a\u906d\u5230\u7d22\u5c3c\u3001\u5148\u950b\u7684\u5f3a\u70c8\u7ade\u4e89\uff0c\u624d\u6ca1\u6709\u6269\u4ea7\u7684\u8ba1\u5212\uff0c\u8ddf\u89e3\u7801\u677f\u5e02\u573a\u7ed9\u4e24\u5bb6\u516c\u53f8\u5784\u65ad\u7684\u5c40\u9762\u4e0d\u540c\uff0c\u673a\u82af\u91cc\u7684\u5173\u952e\u90e8\u4ef6\u6fc0\u5149\u8bfb\u53d6\u5934\uff0c\u5148\u950b\u3001\u7d22\u5c3c\u7b49\u591a\u5bb6\u65e5\u7cfb\u5382\u5bb6\u80fd\u751f\u4ea7\uff0c\u98de\u5229\u6d66\u4e0d\u53ef\u80fd\u4e0d\u8003\u8651\u8fd9\u4e00\u70b9\uff0c\u98de\u5229\u6d66\u5982\u6b64\u51b7\u6de1\uff0c\u6211\u4eec\u53ef\u4ee5\u8003\u8651\u5148\u62e9\u7d22\u5c3c\u7684\u673a\u82af\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7d22\u5c3c\u7684\u673a\u82af\u2026\u2026\u201d\u8c22\u5251\u5357\u53cc\u624b\u4f38\u5230\u8116\u540e\u62b1\u8d77\u5934\uff0c\u8003\u8651\u5218\u660e\u8f89\u7684\u8fd9\u9879\u5efa\u8bae\u3002</p><p>\u3000\u3000\u8c22\u77bb\u8bf4\u9053\uff1a\u201c\u7d22\u5c3c\u7684\u673a\u82af\u662f\u4e13\u4f9b\u65b0\u79d1\u7684\uff0c\u6211\u4eec\u8981\u7528\u7d22\u5c3c\u7684\u673a\u82af\uff0c\u7cfb\u7edf\u63a7\u5236\u7f16\u7a0b\u600e\u4e48\u89e3\u51b3\uff0c\u63a7\u5236\u82af\u7247\u4ece\u54ea\u91cc\u4e70\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e9b\u4e1c\u897f\uff0c\u6211\u4eec\u59cb\u7ec8\u8981\u505a\u8d77\u6765\u7684\uff0c\u201d\u8c22\u5251\u5357\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5927\u817f\uff0c\u201c\u5218\u90e8\u957f\u7684\u8fd9\u4e2a\u60f3\u6cd5\u4e0d\u9519\uff0c\u6211\u4eec\u4e0b\u5348\u5c31\u53bb\u62dc\u8bbf\u7d22\u5c3c\u7684\u4ee3\u8868\uff0c\u542c\u8bf4\u4ed6\u4eec\u4e5f\u63d0\u524d\u8fc7\u6765\uff0c\u4e0d\u8fc7\u8ddf\u7231\u8fbe\u63a5\u89e6\u6ca1\u6709\u8fd9\u4e48\u4eb2\u5bc6\uff0c\u60f3\u5fc5\u8fd9\u65f6\u5019\u4e5f\u6b63\u843d\u5bde\u7740\uff0c\u6211\u4eec\u6682\u65f6\u6ca1\u6709\u6280\u672f\uff0c\u5b8c\u5168\u53ef\u4ee5\u5411\u7d22\u5c3c\u63d0\u8981\u6c42\u8ba9\u4ed6\u4eec\u652f\u63f4\u6211\u4eec\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u8bf4\u9053\uff1a\u201c\u5916\u5c42\u5e94\u7528\u6280\u672f\u5e94\u8be5\u662f\u65b0\u79d1\u8054\u5408\u7d22\u5c3c\u4e00\u8d77\u5f00\u53d1\u7684\uff0c\u65b0\u79d1\u5927\u6982\u4e0d\u53ef\u80fd\u5c06\u5916\u5c42\u5e94\u7528\u6280\u672f\u5411\u540c\u884c\u4e1a\u5185\u7684\u4f01\u4e1a\u5f00\u653e\u5427\uff1f\u201d\u79d1\u738b\u593a\u5f97\u592e\u89c6\u6807\u738b\u5bf9\u65b0\u79d1\u7535\u5b50\u4e5f\u6709\u76f4\u63a5\u7684\u51b2\u51fb\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u5148\u8ddf\u7d22\u5c3c\u63a5\u89e6\u518d\u8bf4\uff0c\u4e3b\u8981\u662f\u7ed9\u98de\u5229\u6d66\u65bd\u52a0\u538b\u529b\uff0c\u770b\u4ed6\u4eec\u662f\u88c5\u51fa\u6765\u7684\u51b7\u6f20\uff0c\u8fd8\u662f\u771f\u4e0d\u91cd\u89c6\u6211\u4eec\u79d1\u738b\uff0c\u603b\u662f\u4ece\u7d22\u5c3c\u8d2d\u4e70\u673a\u82af\u662f\u6700\u540e\u4e00\u5957\u65b9\u6848\u3002\u201d\u8c22\u5251\u5357\u89c9\u5f97\u6682\u65f6\u53ea\u80fd\u8fd9\u6837\uff0c\u673a\u82af\u7684\u4f9b\u9700\u5e73\u8861\u5f88\u5bb9\u6613\u7ed9\u4eba\u4e3a\u63a7\u5236\uff0c\u5e74\u521d\u7684\u5b9e\u4f8b\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u7231\u8fbe\u7535\u5b50\u4e0e\u98de\u5229\u6d66\u7684\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\u6253\u7834\u4e86\u4f9b\u9700\u5e73\u8861\uff0c\u624d\u4f7f\u5f97\u5176\u4ed6\u5f71\u789f\u673a\u5382\u5546\u5728\u4e00\u5230\u4e94\u6708\u4e25\u91cd\u53d7\u9650\u4e8e\u673a\u82af\u7684\u4f9b\u5e94\u800c\u65e0\u6cd5\u589e\u52a0\u4ea7\u91cf\uff0c\u60df\u72ec\u7231\u8fbe\u6bcf\u4e2a\u6708\u72ec\u4eab\u5341\u4e07\u5957\u673a\u82af\u4f9b\u5e94\u3002\u8981\u660e\u5e74\u7684\u5f71\u789f\u673a\u5e02\u573a\u518d\u6b21\u53d7\u673a\u82af\u7684\u4f9b\u5e94\u91cf\u9650\u5236\uff0c\u7231\u8fbe\u518d\u73a9\u8fd9\u4e00\u624b\uff0c\u79d1\u738b\u54ea\u6015\u5728\u5e02\u573a\u5ba3\u4f20\u4e0a\u6295\u5165\u518d\u591a\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8d85\u8d8a\u7231\u8fbe\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e3b\u52a8\u62dc\u8bbf\u4e0e\u7d22\u5c3c\u5e94\u9080\u6d3e\u5f80\u6d77\u5dde\u53c2\u52a0\u7231\u8fbe\u7535\u5b50\u7ecf\u9500\u5546\u5927\u4f1a\u7684\u5143\u5668\u4ef6\u90e8\u4e2d\u56fd\u533a\u603b\u7ecf\u7406\u7ed9\u98de\u5229\u6d66\u65bd\u52a0\u538b\u529b\u7684\u4e3e\u52a8\uff0c\u7684\u786e\u8d77\u4e86\u4e9b\u4f5c\u7528\uff0c\u5f20\u4e9a\u5e73\u4e8c\u5341\u56db\u65e5\u4e0b\u5348\u62bd\u7a7a\u5230\u79d1\u738b\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u7684\u751f\u4ea7\u57fa\u5730\u53c2\u89c2\u4e86\u4e00\u5708\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u5f88\u4f24\u8c22\u5251\u5357\u4ed6\u4eec\u7684\u5fc3\uff0c\u5f20\u4e9a\u5e73\u770b\u7740\u5218\u660e\u8f89\u8bf4\uff1a\u201c\u5218\u90e8\u957f\u662f\u4ece\u7231\u8fbe\u51fa\u6765\u7684\u4eba\uff0c\u4e24\u5bb6\u516c\u53f8\u5728\u751f\u4ea7\u7ba1\u7406\u4e0a\uff0c\u4f60\u80fd\u770b\u4ec0\u4e48\u5f02\u540c\u6765\uff1f\u201d</p><p>\u3000\u3000\u5218\u660e\u8f89\u8bed\u585e\uff0c\u642a\u585e\u9053\uff1a\u201c\u6211\u5e73\u65f6\u5bf9\u8fd9\u4e00\u5757\u5173\u5fc3\u4e0d\u591f\uff1f\u201d\u4ed6\u5728\u7231\u8fbe\u7535\u5b50\uff0c\u6bcf\u56de\u51e0\u4e4e\u90fd\u662f\u88ab\u8feb\u7740\u62c9\u53bb\u966a\u5f20\u606a\u53c2\u52a0\u751f\u4ea7\u8f66\u95f4\uff0c\u4ed6\u4ece\u6765\u5c31\u6ca1\u6709\u8003\u8651\u5230\u4ed6\u4eec\u505a\u5e02\u573a\u7684\u4eba\u8fd8\u8981\u5bf9\u751f\u4ea7\u4e86\u89e3\u591a\u900f\u5f7b\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u8bf4\u9053\uff1a\u201c\u82cf\u6d25\u4e1c\u3001\u9c81\u5e86\u751f\u5728\u751f\u4ea7\u7ba1\u7406\u4e0a\u5f88\u6709\u6c34\u5e73\uff0c\u6211\u4eec\u79d1\u738b\u4f1a\u52aa\u529b\u6539\u8fdb\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u8c22\u8463\u4e8b\u957f\u771f\u5e94\u8be5\u5c06\u82cf\u6d25\u4e1c\u3001\u9c81\u5e86\u751f\u4e24\u4eba\u4e2d\u81f3\u5c11\u6316\u4e00\u4e2a\u8fc7\u6765\uff0c\u201d\u5f20\u4e9a\u5e73\u6487\u4e86\u6487\u5634\uff0c\u53bb\u5e74\u7231\u8fbe\u8fd8\u662f\u4e00\u5bb6\u5c0f\u5382\u7684\u65f6\u5019\uff0c\u5728\u751f\u4ea7\u7ba1\u7406\u4e0a\u7684\u6c34\u5e73\u5c31\u4e0d\u662f\u6b64\u65f6\u7684\u79d1\u738b\u80fd\u6bd4\u7684\uff0c\u4ed6\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u4e4b\u524d\uff0c\u6211\u4eec\u5728\u7535\u8bdd\u91cc\u6c9f\u901a\u8fc7\uff0c\u6211\u4eec\u5f88\u671f\u5f85\u79d1\u738b\u4e0b\u4e00\u5e74\u5ea6\u80fd\u521b\u4e0b\u5e02\u573a\u4f73\u7ee9\uff0c\u660e\u5e74\uff0c\u6211\u4eec\u6ca1\u6709\u8ddf\u4efb\u4f55\u4e00\u5bb6\u4f01\u4e1a\u7b7e\u7f6e\u4ec0\u4e48\u957f\u671f\u4f9b\u5e94\u534f\u8bae\uff0c\u6240\u4ee5\u4e0e\u79d1\u738b\u4e4b\u95f4\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u7b7e\u8fd9\u4efd\u534f\u8bae\u3002\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u7d22\u5c3c\u540c\u610f\u4f18\u5148\u5411\u79d1\u738b\u4f9b\u8d27\u5462\uff1f\u201d\u8c22\u5251\u5357\u5c0f\u5c0f\u7684\u903c\u4e86\u4e00\u6b65\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u7d22\u5c3c\u6b63\u52a0\u5927\u673a\u82af\u7684\u4f9b\u7ed9\uff0c\u660e\u5e74\u673a\u82af\u51fa\u73b0\u7d27\u7f3a\u7684\u53ef\u80fd\u6027\u6bd4\u8f83\u5c0f\uff0c\u90a3\u5c31\u6ca1\u6709\u5fc5\u8981\u7b7e\u4ec0\u4e48\u4f18\u5148\u4f9b\u8d27\u7684\u534f\u8bae\u4e86\u3002\u201d\u5c31\u7b97\u4e0d\u8003\u8651\u5176\u4ed6\uff0c\u5f20\u4e9a\u5e73\u5bf9\u79d1\u738b\u8fd9\u4e48\u4e00\u5bb6\u4e0d\u91cd\u89c6\u5546\u4e1a\u8bda\u4fe1\u7684\u4f01\u4e1a\u4e5f\u6ca1\u6709\u592a\u5927\u7684\u597d\u611f\uff0c\u81ea\u7136\u4e0d\u60f3\u4e00\u4efd\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\u6210\u4e3a\u79d1\u738b\u624b\u91cc\u6253\u51fb\u5176\u4ed6\u4f01\u4e1a\u7684\u5de5\u5177\uff0c\u5f20\u4e9a\u5e73\u8bed\u6c14\u8fd8\u662f\u7f13\u4e86\u7f13\uff0c\u201c\u79d1\u738b\u8981\u575a\u6301\u7684\u8bdd\uff0c\u6211\u770b\u662f\u4e0d\u662f\u7b49\u7231\u8fbe\u7535\u5b50\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u5f00\u8fc7\u518d\u8c08\u8fd9\u4ef6\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u7b49\u5230\u7231\u8fbe\u7535\u5b50\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u4e4b\u540e\uff1f\u201d\u8c22\u5251\u5357\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u7231\u8fbe\u5728\u7ecf\u9500\u5546\u5927\u4f1a\u6709\u4ec0\u4e48\u5927\u52a8\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u4e0d\u65b9\u4fbf\u8bf4\uff0c\u201d\u5f20\u4e9a\u5e73\u62d2\u7edd\u900f\u9732\u7231\u8fbe\u7684\u5546\u4e1a\u673a\u5bc6\uff0c\u8c22\u5251\u5357\u8fd9\u4e48\u95ee\u5c31\u5f88\u5931\u793c\uff0c\u5f20\u4e9a\u5e73\u4ed6\u770b\u5411\u5218\u660e\u8f89\uff0c\u201c\u5218\u90e8\u957f\u4e5f\u4e0d\u6e05\u695a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6e05\u695a\u4ec0\u4e48\uff1f\u201d\u5218\u660e\u8f89\u6709\u4e9b\u6478\u4e0d\u6e05\u52a8\u8111\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u6765\u5218\u90e8\u957f\u4e00\u76f4\u5f88\u4e13\u6ce8\u4e8e\u5e02\u573a\u8fd0\u4f5c\u554a\u2026\u2026\u201d\u5012\u4e5f\u4e0d\u518d\u5bf9\u5218\u660e\u8f89\u505a\u4ec0\u4e48\u8bc4\u4ef7\u3002</p><p>\u3000\u3000\u53c2\u89c2\u5b8c\u79d1\u738b\u7684\u5de5\u5382\uff0c\u5f20\u4e9a\u5e73\u8c22\u7edd\u8c22\u5251\u5357\u4ed6\u4eec\u7684\u9152\u5bb4\u62db\u5f85\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u4e0a\u8fd8\u6709\u4e00\u4e9b\u4e8b\u60c5\u8981\u5904\u7406\uff0c\u673a\u4f1a\u7559\u5f85\u4ee5\u540e\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u4e0e\u4ed6\u7684\u52a9\u624b\u4ece\u4e0a\u6d77\u5f00\u8f66\u8fc7\u6765\uff0c\u4e5f\u6ca1\u6709\u8981\u79d1\u738b\u6d3e\u8f66\u9001\u4ed6\u4eec\uff0c\u8c22\u5251\u5357\u70ed\u60c5\u60f3\u5b89\u6392\u7740\u5e2e\u4ed6\u4eec\u6307\u8def\uff0c\u5f20\u4e9a\u5e73\u8bf4\u9053\uff1a\u201c\u4e0d\u9700\u8981\uff0c\u8fd9\u91cc\u5230\u57ce\u5c71\u8def\uff0c\u8fd9\u51e0\u5929\u90fd\u6211\u4eec\u8d70\u4e86\u597d\u51e0\u56de\u4e86\uff0c\u8ba4\u8bc6\u3002\u201d</p><p>\u3000\u3000\u542c\u5f20\u4e9a\u5e73\u8fd9\u4e48\u8bf4\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u53c8\u5835\u4e86\u4e00\u5c42\uff0c\u542c\u5f20\u4e9a\u5e73\u521a\u624d\u7684\u53e3\u6c14\uff0c\u7231\u8fbe\u7535\u5b50\u8fd9\u6b21\u6709\u4ec0\u4e48\u4e1c\u897f\uff0c\u5218\u660e\u8f89\u5e94\u8be5\u6709\u77e5\u9053\u624d\u662f\uff0c\u8c22\u5251\u5357\u4e5f\u4e0d\u8ba4\u4e3a\u5218\u660e\u8f89\u8fd8\u6709\u4ec0\u4e48\u9690\u7792\u81ea\u5df1\u7684\uff0c\u773c\u5149\u6240\u9650\u800c\u5df2\uff0c\u53ea\u662f\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6ca1\u6709\u8ffd\u95ee\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8c22\u77bb\u5fcd\u4e0d\u4f4f\u5927\u58f0\u62b1\u6028\uff1a\u201c\u4e3a\u4ec0\u4e48\u7231\u8fbe\u4ed6\u4eec\u593a\u5f97\u6807\u738b\uff0c\u98de\u5229\u6d66\u3001\u65af\u9ad8\u67cf\u3001TI\u90fd\u4e00\u4e2a\u4e2a\u6765\u6367\u573a\uff0c\u5bf9\u6211\u4eec\u5374\u4e0d\u7406\u4e0d\u776c\uff1f\u6211\u4eec\u6709\u54ea\u70b9\u4e0d\u5982\u7231\u8fbe\uff01\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u6709\u4eca\u5929\u7684\u8f89\u714c\uff0c\u81ea\u7136\u6709\u4ed6\u6210\u529f\u7684\u5730\u65b9\uff0c\u201d\u9648\u9759\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u4e0d\u662f\u4e5f\u5728\u5b66\u4ed6\u4eec\u7684\u6a21\u5f0f\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u5c0f\u62b1\u6028\u4e00\u4e0b\uff0c\u201d\u8c22\u77bb\u6709\u4e9b\u4e0d\u60a6\uff0c\u4f46\u53c8\u4e0d\u80fd\u8bf4\u592a\u91cd\u7684\u8bdd\uff0c\u659c\u773c\u770b\u7740\u5218\u660e\u8f89\uff0c\u201c\u4f60\u8bf4\u6211\u4eec\u6709\u54ea\u70b9\u4e0d\u5982\u7231\u8fbe\uff1f\u4e0d\u8981\u8ddf\u73b0\u5728\u7684\u7231\u8fbe\u6bd4\uff0c\u4e00\u5e74\u524d\uff0c\u7231\u8fbe\u6709\u6bd4\u6211\u4eec\u505a\u5f97\u66f4\u597d\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u4e86\u9648\u9759\u4e00\u773c\uff0c\u4ed6\u5fc3\u91cc\u6e05\u695a\uff1a\u7231\u8fbe\u5e94\u8be5\u8fd8\u6709\u4e9b\u4ec0\u4e48\u5730\u65b9\uff0c\u662f\u522b\u4eba\u5b66\u4e0d\u6765\u7684\uff0c\u4f01\u4e1a\u4e5f\u5e94\u6709\u4f01\u4e1a\u7684\u7279\u8d28\uff0c\u4e0d\u8fc7\u8fd9\u4e9b\u8bdd\uff0c\u4ed6\u90fd\u4e0d\u4f1a\u8bf4\u51fa\u53e3\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u4e5f\u77e5\u9053\u7d22\u5c3c\u4f1a\u8d81\u8fd9\u4e2a\u673a\u4f1a\u79ef\u6781\u4e89\u53d6\u79d1\u738b\u6210\u4e3a\u4ed6\u4eec\u7684\u4e3b\u8981\u5ba2\u6237\uff0c\u8fd9\u5927\u6982\u662f\u7d22\u5c3c\u8fc5\u901f\u8ffd\u8d76\u98de\u5229\u6d66\u7684\u6700\u4f73\u65f6\u673a\uff0c\u4f46\u662f\u7d22\u5c3c\u65b9\u9762\u4f1a\u8003\u8651\u65b0\u79d1\u7535\u5b50\u5bf9\u6b64\u4e8b\u7684\u6001\u5ea6\u3002\u79d1\u738b\u593a\u4e0b\u660e\u5e74\u7684\u592e\u89c6\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u6709\u51b2\u51fb\uff0c\u5bf9\u65b0\u79d1\u7535\u5b50\u4e5f\u6709\u540c\u6837\u7684\u51b2\u51fb\u3002</p><p>\u3000\u3000\u8981\u662f\u4e8b\u60c5\u4ec5\u4ec5\u662f\u5982\u6b64\uff0c\u5f20\u4e9a\u5e73\u867d\u7136\u4e0d\u4f1a\u4e0e\u79d1\u738b\u7b7e\u6240\u8c13\u7684\u4f18\u5148\u4f9b\u8d27\u534f\u8bae\uff0c\u4f46\u662f\u4e5f\u4f1a\u4ee5\u76f8\u5f53\u79ef\u6781\u7684\u6001\u5ea6\u4e0e\u8ddf\u4ed6\u8c08\u5408\u4f5c\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4f46\u662f\u6d89\u53ca\u5230\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u7684\u5f00\u53d1\uff0c\u4e00\u5207\u53c8\u53d8\u5f97\u4e0d\u4e00\u6837\u3002</p><p>\u3000\u3000\u53bb\u5e74\u5e95\uff0cTI\u3001\u65af\u9ad8\u67cf\u3001\u98de\u5229\u6d66\u4e0e\u7231\u8fbe\u7535\u5b50\u805a\u5728\u4e00\u8d77\uff0c\u98de\u5229\u6d66\u5f97\u5230\u597d\u5904\u5c31\u662f\u7ea6\u5b9a\u51e0\u5bb6\u53ea\u5f00\u4ee5\u57fa\u4e8e\u98de\u5229\u6d66\u673a\u82af\u7684\u7cfb\u7edf\u5e94\u7528\u4e0e\u63a7\u5236\u6280\u672f\uff0c\u81ea\u7136\u800c\u7136\u7684\u5c06\u98de\u5229\u6d66\u5f53\u65f6\u6700\u5927\u7684\u6f5c\u5728\u7ade\u4e89\u5bf9\u624b\u7d22\u5c3c\u6392\u65a5\u5728\u5916\u3002\u4f46\u662f\u5f71\u789f\u673a\u7cfb\u7edf\u5e94\u7528\u4e0e\u63a7\u5236\u6280\u672f\u7684\u96be\u5ea6\u5e76\u4e0d\u5927\uff0c\u5f20\u606a\u7ea0\u96c6\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u7b49\u4e00\u7fa4\u5de5\u7a0b\u5e08\u57cb\u5934\u82e6\u5e72\u4e86\u56db\u4e2a\u6708\uff0c\u5dee\u4e0d\u591a\u5c31\u7422\u78e8\u900f\u4e86\u3002</p><p>\u3000\u3000\u65b0\u79d1\u7535\u5b50\u5f03\u98de\u5229\u6d66\u7528\u7d22\u5c3c\u673a\u82af\u65f6\uff0c\u4e5f\u540c\u6b65\u5f00\u53d1\u76f8\u5e94\u7684\u5e94\u7528\u6280\u672f\uff0c\u6240\u4ee5\u4e94\u6708\u521d\u7d22\u5c3c\u673a\u82af\u4e0b\u7ebf\uff0c\u65b0\u79d1\u7535\u5b50\u4e5f\u5c31\u7d27\u8ddf\u7740\u63a8\u51fa\u4ea7\u54c1\uff0c\u4f46\u662f\u6240\u6709\u7684\u914d\u5957\u6280\u672f\u90fd\u662f\u65b0\u79d1\u7535\u5b50\u72ec\u529b\u7814\u53d1\u51fa\u6765\uff0c\u5e76\u6ca1\u6709\u8f6c\u8ba9\u7ed9\u7d22\u5c3c\u53bb\u652f\u6301\u5176\u4ed6\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u6765\u8ddf\u81ea\u5df1\u7ade\u4e89\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u76f8\u4fe1\u7d22\u5c3c\u8fd9\u65f6\u5019\u5dee\u4e0d\u591a\u5df2\u7ecf\u5c06\u76f8\u5173\u5e94\u7528\u6280\u672f\u7422\u78e8\u900f\u5f7b\uff0c\u5e02\u573a\u4e0a\u4e5f\u5f88\u5feb\u6709\u4e13\u95e8\u7684\u63a7\u5236\u82af\u7247\u95ee\u4e16\uff0c\u4e5f\u5c31\u662f\u8bf4\u7d22\u5c3c\u73b0\u5728\u624d\u5177\u5907\u4e0e\u98de\u5229\u6d66\u6b63\u9762\u5728\u673a\u82af\u5e02\u573a\u7ade\u4e89\u7684\u80fd\u529b\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u4e4b\u524d\u6709\u8003\u8651\u5230\u4e0b\u4e00\u5e74\u5ea6\u5c31\u4f1a\u9762\u4e34\u7d22\u5c3c\u7684\u6fc0\u70c8\u7ade\u4e89\uff0c\u4e0a\u6d77\u5de5\u5382\u4e5f\u6ca1\u6709\u7ee7\u7eed\u6269\u5efa\u7684\u8ba1\u5212\uff0c\u5728\u5230\u6d77\u5dde\u6765\u4e4b\u524d\uff0c\u4ed6\u4e00\u76f4\u5728\u8003\u8651\u4e0e\u7d22\u5c3c\u5728\u673a\u82af\u5e02\u573a\u7ade\u4e89\u7684\u95ee\u9898\uff0c\u4f46\u662f\u7231\u8fbe\u6c38\u8fdc\u90fd\u6709\u8ba9\u4eba\u60ca\u8273\u7684\u6b23\u559c\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u7231\u8fbe\u80fd\u72ec\u529b\u5f00\u53d1\u51fa\u8d85\u7ea7VCD\u7cfb\u7edf\u6240\u9700\u7684\u6240\u6709\u6280\u672f\uff0c\u5728\u4ed6\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u7684\u65f6\u5019\uff0c\u7231\u8fbe\u5df2\u7ecf\u4e0eTI\u3001\u65af\u9ad8\u67cf\u7b7e\u7f72\u8054\u5408\u5f00\u53d1\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u7684\u5408\u4f5c\u534f\u8bae\uff1b\u9664\u4e86\u89e3\u7801\u82af\u7247\u76f8\u5173\u7684\u4e13\u5229\u6280\u672f\u4e4b\u5916\uff0c\u7231\u8fbe\u8fd8\u62e5\u6709\u51e0\u9879\u63a7\u5236\u82af\u7247\u7684\u4e13\u5229\u6280\u672f\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u77e5\u9053\uff0c\u8981\u8fc5\u901f\u7684\u5c06\u8d85\u7ea7VCD\u63a8\u5e7f\u51fa\u53bb\uff0c\u9664\u4e86\u63a8\u51fa\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u4e4b\u5916\uff0c\u7231\u8fbe\u7535\u5b50\u4e5f\u4e0d\u53ef\u80fd\u5c06\u63a7\u5236\u82af\u7247\u4e13\u5229\u6280\u672f\u634f\u5728\u624b\u91cc\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u5c06\u4e13\u5229\u6280\u672f\u8f6c\u8ba9\u7ed9\u7d22\u5c3c\uff0c\u90a3\u7d22\u5c3c\u5c31\u6709\u53ef\u80fd\u501f\u52a9\u8d85\u7ea7VCD\u5728\u673a\u82af\u9886\u57df\u5168\u9762\u8d85\u8d8a\u98de\u5229\u6d66\uff0c\u5f20\u4e9a\u5e73\u8ba4\u771f\u7814\u7a76\u8fc7\uff0c\u8981\u5728\u77ed\u671f\u65f6\u7814\u7a76\u51fa\u66ff\u4ee3\u6280\u672f\uff0c\u96be\u5ea6\u5f88\u5927\uff0c\u66f4\u4f55\u51b5\u7231\u8fbe\u7535\u5b50\u4e0eTI\u3001\u65af\u9ad8\u67cf\u7b7e\u7f72\u7684\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u5f00\u53d1\u5408\u4f5c\u534f\u8bae\u6709\u6346\u7ed1\u7684\u6027\u8d28\uff0c\u4f1a\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u6392\u62c6\u66ff\u4ee3\u6280\u672f\uff1b\u7231\u8fbe\u7535\u5b50\u82e5\u5c06\u4e13\u5229\u6280\u672f\u8f6c\u8ba9\u7ed9\u98de\u5229\u6d66\uff0c\u90a3\u7d22\u5c3c\u5c06\u6c38\u8fdc\u90fd\u6ca1\u6709\u8d85\u8d8a\u98de\u5229\u6d66\u7684\u53ef\u80fd\uff0c\u9664\u975e\u5230DVD\u5f71\u789f\u673a\u65f6\u4ee3\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u7684\u8981\u6c42\u8ba9\u4ed6\u9887\u4e3a\u72b9\u8c6b\uff0c\u5f20\u606a\u8981\u5efa\u673a\u82af\u5de5\u5382\uff0c\u9700\u8981\u98de\u5229\u6d66\u624b\u91cc\u7684\u51e0\u9879\u4e13\u5229\u6280\u672f\uff0c\u5f53\u7136\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u6240\u4e5f\u4f1a\u62ff\u51fa\u4e24\u9879\u5206\u91cf\u8f83\u91cd\u7684\u4e13\u5229\u6280\u672f\u589e\u52a0\u4ea4\u6362\u7684\u7b79\u7801\uff0c\u5176\u4e2d\u90a3\u9879\u6fc0\u5149\u8bfb\u53d6\u5934\u5feb\u95e8\u9632\u5c18\u4e13\u5229\u6280\u672f\uff0c\u5f20\u4e9a\u5e73\u5c31\u5f88\u773c\u998b\uff1a\u6fc0\u5149\u8bfb\u53d6\u5934\u8fdb\u5165\u7070\u5c18\u4f1a\u4e25\u91cd\u7f29\u77ed\u4f7f\u7528\u5bff\u547d\uff0c\u98de\u5229\u6d66\u4e5f\u4e00\u76f4\u5728\u7814\u7a76\u6fc0\u5149\u8bfb\u53d6\u5934\u7684\u9632\u5c18\u6280\u672f\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u9762\u4e34\u4e00\u4e2a\u9009\u62e9\uff0c\u5728\u789f\u673a\u673a\u82af\u9886\u57df\uff0c\u8981\u4e48\u8ddf\u7d22\u5c3c\u7ade\u4e89\uff0c\u8981\u4e48\u8ddf\u7231\u8fbe\u7ade\u4e89\uff1b\u62d2\u7edd\u5f20\u606a\u7684\u8981\u6c42\uff0c\u7b80\u76f4\u5c31\u662f\u5c06TI\u4e0e\u65af\u9ad8\u67cf\u4e5f\u63a8\u5411\u7d22\u5c3c\u3002</p></article>"}}}]);
