(window.webpackJsonp=window.webpackJsonp||[]).push([[452],{474:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e8c\u8282 \u53d1\u73b0",content:"<article><h2>\u7b2c\u56db\u5341\u4e8c\u8282 \u53d1\u73b0</h2><p>\u54f2\u4eba\u8bf4\uff0c\u5e78\u798f\u5f80\u5f80\u76f8\u4f3c\uff0c\u4e0d\u5e78\u5343\u5dee\u4e07\u522b\u3002\u743c\u6069\u73b0\u5728\u5c31\u65b0\u53d1\u73b0\u4e86\u4e00\u4ef6\u4e0d\u6298\u4e0d\u6263\u7684\u4e0d\u5e78\uff1a\u9762\u5bf9\u7740\u4e56\u5de7\u542c\u8bdd\u7684\u6f02\u4eae\u7f8e\u5973\uff0c\u6b63\u51c6\u5907\u63d0\u67aa\u4e0a\u9a6c\uff0c\u7a81\u7136\u6709\u4eba\u4e0d\u8bc6\u65f6\u52a1\u5730\u8dd1\u6765\u6253\u6270\u3002</p><p>\u5982\u679c\u6709\u53ef\u80fd\u7684\u8bdd\uff0c\u743c\u6069\u5f88\u60f3\u76f4\u63a5\u4e00\u638c\u628a\u5bf9\u65b9\u9694\u7a7a\u62cd\u6b7b\uff0c\u7136\u540e\u7ee7\u7eed\u548c\u838e\u73de\u514b\u505a\u8be5\u505a\u5e76\u4e14\u7231\u505a\u7684\u4e8b\u60c5\u3002\u95ee\u9898\u5728\u4e8e\u9996\u5148\u4ed6\u6ca1\u7ec3\u8fc7\u9694\u5c71\u6253\u725b\u7684\u5288\u7a7a\u638c\uff0c\u800c\u89e3\u79bb\u672f\u4e4b\u7c7b\u4e5f\u4e0d\u53ef\u80fd\u9694\u7740\u95e8\u677f\u4f24\u4eba\uff1b\u5176\u6b21\u4ed6\u4e5f\u4e0d\u6562\u4f24\u4eba\uff0c\u5bf9\u65b9\u597d\u6b79\u662f\u6b64\u5730\u7684\u8d1f\u8d23\u4eba\uff0c\u9634\u9b42\u57ce\u7684\u540c\u50da\uff0c\u4e0d\u662f\u4ec0\u4e48\u8def\u4eba\u7532\u6742\u5175\u4e59\uff1b\u6700\u540e\uff0c\u62c9\u52a0\u65e2\u7136\u8bf4\u6709\u5927\u53d1\u73b0\uff0c\u663e\u7136\u662f\u6307\u641c\u5bfb\u661f\u9668\u57ce\u4e4b\u4e8b\uff0c\u90a3\u4e48\u5f17\u6717\u897f\u65af\u79d1\u4e5f\u5e94\u8be5\u56de\u6765\u4e86\u5427\uff0c\u8fd9\u662f\u5173\u7cfb\u7236\u6bcd\u6b7b\u4ea1\u771f\u76f8\u7684\u6b63\u4e8b\uff0c\u4e0d\u53ef\u803d\u6401\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u9b45\u9b54\u7684\u5c0f\u83ca\u82b1\uff0c\u53ea\u80fd\u5148\u4e0d\u91c7\u6458\u4e86\u3002</p><p>\u6ca1\u4e8b\uff0c\u4e0b\u6b21\u7ee7\u7eed\u5c31\u662f\uff0c\u53cd\u6b63\u662f\u76d8\u5b50\u91cc\u7684\u83dc\uff0c\u65e9\u5403\u665a\u5403\u90fd\u4e00\u6837\uff0c\u4e0d\u5fc5\u6025\u4e8e\u4e00\u65f6\u3002</p><p>\u62cd\u4e86\u62cd\u838e\u73de\u514b\u7684\u7fd8\u81c0\u8ba9\u5979\u8d77\u8eab\uff0c\u4e24\u4eba\u7a7f\u597d\u8863\u670d\u6253\u5f00\u95e8\uff0c\u62c9\u52a0\u77a5\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u660e\u663e\u770b\u51fa\u521a\u521a\u4e91\u96e8\u8fc7\u7684\u75d5\u8ff9\uff0c\u4f46\u8fd9\u662f\u522b\u4eba\u7684\u79c1\u751f\u6d3b\uff0c\u81ea\u7136\u5f53\u505a\u6ca1\u770b\u89c1\u3002\u201c\u6211\u4eec\u53ef\u80fd\u627e\u5230\u661f\u9668\u57ce\u4e86\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u53ef\u80fd\uff1f\u53ef\u80fd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u6b63\u51c6\u5907\u63a8\u5012\u7f8e\u5973\u7ed3\u679c\u88ab\u6253\u65ad\uff0c\u743c\u6069\u6db5\u517b\u518d\u597d\u4e5f\u618b\u7740\u4e00\u809a\u5b50\u706b\uff0c\u8bf4\u8bdd\u8bed\u6c14\u81ea\u7136\u5c31\u5f88\u751f\u786c\u3002\u62c9\u52a0\u4e5f\u4e0d\u5728\u610f\uff0c\u201c\u6211\u4eec\u5728\u6d77\u5e95\u627e\u5230\u4e86\u4e00\u4ef6\u4e1c\u897f\uff0c\u6781\u6709\u53ef\u80fd\u662f\u6765\u81ea\u661f\u9668\u57ce\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u770b\u770b\u4fbf\u77e5\uff0c\u201d\u62c9\u52a0\u8bf4\uff0c\u201c\u5728\u5730\u4e0b\u5ba4\uff0c\u8ddf\u6211\u6765\u3002\u201d</p><p>\u743c\u6069\u8ba9\u838e\u73de\u514b\u7559\u4e0b\uff0c\u81ea\u5df1\u8ddf\u7740\u62c9\u52a0\u4e0b\u697c\uff0c\u7ecf\u8fc7\u72ed\u7a84\u7684\u697c\u68af\uff0c\u8d70\u8fdb\u4e00\u95f4\u4eae\u5802\u5802\u7684\u5730\u4e0b\u5ba4\u3002\u51e0\u4e2a\u8eab\u7a7f\u9ed1\u888d\u548c\u7070\u888d\u7684\u7537\u4eba\u6216\u7ad9\u6216\u8e72\uff0c\u56f4\u7740\u4ec0\u4e48\u4e1c\u897f\uff0c\u501f\u7740\u5934\u9876\u5de8\u578b\u9b54\u6cd5\u706f\u7684\u5149\u4eae\u4ed4\u7ec6\u89c2\u5bdf\u3002\u4ed6\u4eec\u5168\u795e\u8d2f\u6ce8\uff0c\u4ee5\u81f3\u4e8e\u8fde\u6709\u4eba\u8fdb\u6765\u90fd\u6ca1\u53d1\u89c9\u3002\u743c\u6069\u8d70\u5230\u65c1\u8fb9\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u8db3\u7403\u5927\u5c0f\u7684\u7389\u767d\u8272\u7403\u4f53\uff0c\u6446\u5728\u6728\u5934\u652f\u67b6\u4e0a\uff0c\u4e0a\u9762\u96d5\u523b\u7740\u5bc6\u5bc6\u9ebb\u9ebb\u5230\u8ba9\u4eba\u4e00\u770b\u5c31\u4f1a\u76ee\u7729\u5934\u6655\u7684\u7eb9\u8def\u548c\u56fe\u6848\u3002\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4ed6\u52c9\u5f3a\u96c6\u4e2d\u7cbe\u795e\u770b\u4e86\u7247\u523b\uff0c\u4e0d\u5f97\u5176\u89e3\uff0c\u4e8e\u662f\u95ee\u62c9\u52a0\u3002</p><p>\u201c\u5929\u7a79\u4eea\u3002\u201d</p><p>\u201c\u5929\u7a79\u4eea\uff0c\u8fd9\u540d\u5b57\u6709\u70b9\u8033\u719f\u2026\u2026\u5514\uff0c\u5360\u661f\u672f\u7528\u7684\u9053\u5177\u4e48\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d\u62c9\u52a0\u8bf4\u3002</p><p>\u201c\u4ece\u6d77\u91cc\u6253\u635e\u4e0a\u6765\u7684\u5c31\u662f\u8fd9\u4e1c\u897f\uff1f\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u600e\u4e48\u786e\u5b9a\u5b83\u662f\u6765\u81ea\u661f\u9668\u57ce\u5462\uff1f\u96be\u9053\u2026\u2026\u201d\u4ed6\u8111\u5b50\u7ec8\u7a76\u8f6c\u5f97\u5feb\uff0c\u201c\u661f\u9668\u57ce\u7684\u57ce\u4e3b\u662f\u4e00\u4f4d\u5360\u661f\u5e08\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u62c9\u52a0\u8bf4\uff0c\u663e\u7136\u5bf9\u743c\u6069\u7684\u8bdd\u89c9\u5f97\u5341\u5206\u53ef\u7b11\uff0c\u201c\u5965\u5609\u83b1\u65af\u5973\u58eb\u4e0d\u4ec5\u4ec5\u662f\u5360\u661f\u5e08\uff0c\u800c\u4e14\u662f\u7ee7\u2018\u5927\u5730\u5148\u77e5\u2019\u4e4b\u540e\uff0c\u5e1d\u56fd\u5360\u661f\u754c\u7684\u7b2c\u4e00\u4eba\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u96be\u9053\u8fde\u8fd9\u4e2a\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u201d</p><p>\u5e9f\u8bdd\uff0c\u6211\u53c8\u4e0d\u662f\u9884\u8a00\u5e08\uff0c\u66f4\u4e0d\u662f\u5360\u661f\u5e08\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u3002</p><p>\u5360\u661f\u672f\u662f\u9884\u8a00\u9b54\u6cd5\u7684\u4e00\u4e2a\u5206\u652f\uff0c\u800c\u4e14\u662f\u6781\u5176\u51b7\u50fb\u7684\u5206\u652f\u2014\u2014\u56e0\u4e3a\u5b9e\u5728\u662f\u592a\u8fc7\u51b7\u50fb\uff0c\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\u91cc\u751a\u81f3\u90fd\u4e0d\u5f00\u8bbe\u8fd9\u95e8\u8bfe\u7a0b\uff0c\u743c\u6069\u4e4b\u6240\u4ee5\u8fd8\u77e5\u9053\u70b9\u76f8\u5173\u4fe1\u606f\uff0c\u662f\u65e0\u610f\u95f4\u542c\u8299\u857e\u72c4\u8bf4\u7684\u3002\u8299\u857e\u72c4\u662f\u9884\u8a00\u4e13\u4e1a\u7684\u5b66\u751f\uff0c\u867d\u7136\u5979\u4e5f\u4e0d\u5b66\u5360\u661f\u672f\uff0c\u4f46\u6559\u6388\u5728\u8bb2\u8bfe\u65f6\u591a\u5c11\u8fd8\u662f\u4f1a\u63d0\u53ca\u4e00\u4e9b\u3002</p><p>\u5916\u884c\u4eba\u542c\u5230\u201c\u9884\u8a00\u672f\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u5927\u90e8\u5206\u90fd\u4f1a\u8ba4\u4e3a\u662f\u9884\u6d4b\u672a\u6765\u7684\u9b54\u6cd5\uff0c\u5176\u5b9e\u8fd9\u662f\u8bef\u89e3\u3002\u9884\u8a00\u672f\u4e3b\u8981\u7684\u4f5c\u7528\u662f\u4fa6\u67e5\u63a2\u6d4b\u3001\u4f20\u9012\u8baf\u606f\u3001\u5206\u6790\u60c5\u62a5\u3001\u63ed\u793a\u79d8\u5bc6\u3002\u6b64\u6b21\u6d3e\u6765\u6253\u635e\u9668\u661f\u57ce\u7684\u9634\u9b42\u57ce\u4eba\u5458\uff0c\u5927\u591a\u90fd\u662f\u5e03\u96f7\u7eb3\u65af\u7684\u5b66\u751f\u6216\u90e8\u4e0b\uff0c\u4ee5\u9884\u8a00\u5e08\u5c45\u591a\uff0c\u4ed6\u4eec\u80fd\u591f\u5728\u832b\u832b\u5927\u6d77\u4e2d\u641c\u7d22\u5b9a\u4f4d\u76ee\u6807\uff0c\u4f46\u6ca1\u4e00\u4e2a\u80fd\u9884\u6d4b\u660e\u5929\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u3002\u901a\u5e38\u7684\u8bf4\u6cd5\u662f\uff1a\u201c\u9884\u89c1\u672a\u6765\uff0c\u90a3\u662f\u8fde\u795e\u7947\u90fd\u505a\u4e0d\u5230\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u800c\u5360\u661f\u672f\u7684\u8981\u4e49\uff0c\u4fbf\u5728\u4e8e\u9884\u6d4b\u672a\u6765\u3002</p><p>\u6240\u8c13\u5360\u661f\u672f\uff0c\u5185\u5bb9\u975e\u5e38\u5e7f\u6cdb\uff0c\u7c97\u7565\u800c\u8a00\u5c31\u662f\u4ee5\u901a\u8fc7\u89c2\u6d4b\u661f\u8c61\u7684\u65b9\u5f0f\u9884\u6d4b\u672a\u6765\u7684\u9b54\u6cd5\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u5f88\u591a\u5deb\u5e08\u90fd\u62d2\u7edd\u627f\u8ba4\u5360\u661f\u672f\u662f\u4e00\u79cd\u9b54\u6cd5\uff0c\u8ba4\u4e3a\u53ea\u7b97\u662f\u68a6\u5453\u548c\u80e1\u8bf4\u516b\u9053\uff0c\u800c\u4e14\u4e8b\u5b9e\u4e0a\uff0c\u7edd\u5927\u591a\u6570\u7684\u5360\u661f\u5e08\u4e5f\u7684\u786e\u90fd\u662f\u9a97\u5b50\uff0c\u6216\u8005\u5984\u60f3\u72c2\u3002\u8299\u857e\u72c4\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5360\u661f\u672f\u4e4b\u6240\u4ee5\u8870\u5fae\uff0c\u4e00\u4e2a\u6700\u91cd\u8981\u7684\u539f\u56e0\u5c31\u662f\u5bf9\u5360\u661f\u5e08\u7684\u8d44\u8d28\u8981\u6c42\u592a\u9ad8\u3002\u8fd9\u79cd\u8d44\u8d28\u4e0d\u662f\u6307\u8840\u7edf\u6216\u9057\u4f20\u57fa\u56e0\uff0c\u800c\u662f\u8d85\u4e00\u6d41\u7684\u667a\u529b\u548c\u7406\u6027\uff0c\u52a0\u4e0a\u8d85\u4e00\u6d41\u7684\u76f4\u89c9\u548c\u654f\u611f\u2014\u2014\u5177\u5907\u524d\u8005\u7684\u4eba\u662f\u6709\u7684\uff0c\u5177\u5907\u540e\u8005\u7684\u4eba\u4e5f\u662f\u6709\u7684\uff0c\u867d\u7136\u90fd\u5f88\u5c11\u2014\u2014\u4f46\u4e24\u8005\u540c\u65f6\u90fd\u517c\u5907\u7684\u4eba\uff0c\u90a3\u5c31\u51e4\u6bdb\u9e9f\u89d2\u4e86\u3002\u5c24\u5176\u662f\u5deb\u5e08\u8fd9\u79cd\u7fa4\u4f53\uff0c\u5f80\u5f80\u90fd\u662f\u7406\u667a\u6709\u4f59\u800c\u611f\u6027\u4e0d\u8db3\uff0c\u76f4\u89c9\u8fdf\u949d\u5f97\u4e00\u584c\u7cca\u6d82\u3002\u6240\u4ee5\u53e4\u5f80\u4eca\u6765\uff0c\u6253\u7740\u201c\u5360\u661f\u5e08\u201d\u540d\u5934\u7684\u5bb6\u4f19\u5f88\u591a\uff0c\u4f46\u771f\u6b63\u7684\u5360\u661f\u5e08\uff0c\u5176\u5b9e\u4e24\u53ea\u624b\u5c31\u80fd\u6570\u5f97\u8fc7\u6765\uff0c\u5176\u4e2d\u6700\u8457\u540d\u7684\u81ea\u7136\u662f\u5927\u5730\u5148\u77e5\u548c\u963f\u5170\u591a\uff0c\u524d\u8005\u662f\u5eb7\u6770\u8bfa\xb7\u827e\u6069\u7684\u8001\u5e08\uff08\u5eb7\u6770\u8bfa\u662f\u8010\u745f\u5386\u53f2\u4e0a\u7b2c\u4e00\u4f4d\u5927\u5965\u672f\u5e08\uff09\uff0c\u9884\u8a00\u4e86\u8010\u745f\u5e1d\u56fd\u7684\u5d1b\u8d77\u548c\u8f89\u714c\uff1b\u540e\u8005\u5219\u88ab\u8ba4\u4e3a\u662f\u8010\u745f\u5e1d\u56fd\u9668\u706d\u4e4b\u540e\u8fd9\u4e00\u5343\u4e03\u767e\u5e74\u6765\u6700\u4f1f\u5927\u7684\u9884\u8a00\u5e08\uff0c\u51c6\u786e\u9884\u6d4b\u4e86\u8bf8\u591a\u91cd\u5927\u7684\u5386\u53f2\u4e8b\u4ef6\uff0c\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u4ed6\u662f\u9884\u8a00\u4e4b\u795e\u8428\u5f17\u62c9\u65af\u7684\u9009\u6c11\u3002</p><p>\u81f3\u4e8e\u8fd9\u4f4d\u5965\u5609\u83b1\u65af\u5973\u58eb\uff0c\u743c\u6069\u5b64\u964b\u5be1\u95fb\uff0c\u5012\u786e\u5b9e\u6ca1\u542c\u8bf4\u8fc7\uff0c\u6599\u60f3\u4e5f\u4e0d\u8fc7\u5c14\u5c14\uff0c\u6240\u8c13\u201c\u5927\u5730\u5148\u77e5\u4e4b\u540e\u5e1d\u56fd\u5360\u661f\u754c\u7b2c\u4e00\u4eba\u201d\u7684\u8bf4\u6cd5\uff0c\u4f30\u8ba1\u4e0d\u662f\u5439\u5618\uff0c\u5c31\u662f\u65f6\u65e0\u82f1\u96c4\u4f7f\u7ad6\u5b50\u6210\u540d\u3002\u5426\u5219\u7684\u8bdd\uff0c\u5979\u600e\u4e48\u9884\u6d4b\u4e0d\u5230\u5927\u707e\u53d8\u5373\u5c06\u6765\u4e34\uff0c\u65e9\u65e9\u627e\u4e2a\u5730\u65b9\u628a\u6d6e\u7a7a\u57ce\u5b89\u5168\u964d\u843d\u4e0b\u6765\u5462\uff0c\u8fd8\u5728\u5929\u4e0a\u98de\uff0c\u7ed3\u679c\u6389\u8fdb\u4e86\u5760\u661f\u6d77\u91cc\uff0c\u540d\u526f\u5176\u5b9e\u7684\u201c\u661f\u9668\u201d\u4e86\u2014\u2014\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u5979\u9884\u8a00\u4f3c\u4e4e\u8fd8\u662f\u633a\u51c6\u7684\u3002</p><p>\u60f3\u662f\u8fd9\u4e48\u60f3\uff0c\u5634\u4e0a\u81ea\u7136\u4e0d\u80fd\u8bf4\uff0c\u597d\u5728\u62c9\u52a0\u5148\u751f\u4e5f\u6ca1\u6709\u548c\u4ed6\u8fd9\u4e2a\u5916\u884c\u8ba4\u771f\u8ba1\u8f83\u7684\u610f\u601d\uff0c\u743c\u6069\u987a\u52bf\u8f6c\u79fb\u8bdd\u9898\uff0c\u95ee\u8d77\u53d1\u73b0\u8fd9\u4e2a\u5929\u7a79\u4eea\u7684\u7ecf\u8fc7\u3002\u539f\u6765\u662f\u6d3e\u51fa\u53bb\u641c\u5bfb\u7684\u4eba\u5458\uff0c\u5386\u7ecf\u51e0\u5929\u7684\u5f92\u52b3\u65e0\u529f\u540e\uff0c\u8fd9\u4e00\u65e5\u6b63\u51c6\u5907\u8fd4\u822a\uff0c\u5076\u7136\u5728\u4e00\u8258\u8def\u8fc7\u7684\u6e14\u8239\u4e0a\u53d1\u73b0\u4e86\u8fd9\u4e1c\u897f\u3002\u5929\u7a79\u4eea\u662f\u5360\u661f\u672f\u7684\u4e13\u7528\u9053\u5177\uff0c\u6e14\u6c11\u4eec\u4e0d\u8ba4\u8bc6\uff0c\u53ea\u662f\u6253\u6e14\u7684\u65f6\u5019\u635e\u8d77\uff0c\u89c9\u5f97\u4f3c\u4e4e\u662f\u4e2a\u53e4\u7269\uff0c\u6253\u7b97\u62ff\u56de\u57ce\u91cc\u5356\u70b9\u94b1\u3002\u9634\u9b42\u57ce\u7684\u641c\u5bfb\u4eba\u5458\u81ea\u7136\u8bc6\u8d27\uff0c\u4e00\u89c1\u4e4b\u4e0b\u5927\u559c\u8fc7\u671b\uff0c\u76f4\u63a5\u4e70\u4e0b\uff0c\u7136\u540e\u4e00\u8fb9\u6d3e\u4eba\u628a\u5929\u7a79\u4eea\u9001\u56de\u4e4c\u62c9\u65af\u76ae\u5c14\u57ce\uff0c\u4e00\u8fb9\u524d\u5f80\u6e14\u6c11\u53d1\u73b0\u5929\u7a79\u4eea\u7684\u90a3\u7247\u6d77\u57df\u7ee7\u7eed\u641c\u5bfb\uff0c\u5e0c\u671b\u6709\u6240\u53d1\u73b0\u3002\u743c\u6069\u60f3\u89c1\u7684\u5f17\u6717\u897f\u65af\u79d1\u4e5f\u6ca1\u6709\u56de\u6765\uff0c\u6b64\u65f6\u6b63\u5728\u6d77\u4e0a\u7ee7\u7eed\u5de5\u4f5c\u5462\u3002</p><p>\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u51e0\u4e2a\u9274\u5b9a\u4eba\u5458\u5df2\u7ecf\u505a\u51fa\u4e86\u7ed3\u8bba\uff0c\u786e\u8ba4\u8fd9\u5ea7\u5929\u7a79\u4eea\u786e\u5b9e\u6765\u81ea\u661f\u9668\u57ce\uff0c\u4e00\u4e2a\u6700\u6709\u529b\u7684\u8bc1\u636e\u5c31\u662f\u5929\u7a79\u4eea\u7684\u6700\u4e0b\u65b9\uff0c\u975e\u5e38\u9690\u853d\u5730\u6d6e\u96d5\u7740\u4e00\u652f\u542b\u82de\u5f85\u653e\u7684\u6800\u5b50\u82b1\uff0c\u8fd9\u6b63\u662f\u661f\u9668\u57ce\u57ce\u4e3b\u5965\u5609\u83b1\u65af\u5973\u58eb\u7684\u4e2a\u4eba\u6807\u5fd7\u3002</p><p>\u8fd9\u662f\u4e2a\u91cd\u5927\u53d1\u73b0\uff0c\u610f\u5473\u7740\u6210\u529f\u89e6\u624b\u53ef\u53ca\uff0c\u7ecf\u8fc7\u5927\u534a\u5e74\u6765\u7684\u8f9b\u82e6\u52aa\u529b\uff0c\u5982\u4eca\u7ec8\u4e8e\u770b\u89c1\u4e86\u66d9\u5149\uff0c\u62c9\u52a0\u7b49\u4eba\u81ea\u7136\u90fd\u5f88\u9ad8\u5174\u3002\u743c\u6069\u5bf9\u6b64\u4e0d\u5173\u5fc3\uff0c\u4e5f\u542c\u4e0d\u61c2\u4ed6\u4eec\u8bf4\u7684\u90a3\u4e9b\u4e13\u4e1a\u672f\u8bed\uff0c\u5fc3\u60f3\u8fd8\u662f\u56de\u53bb\u7ee7\u7eed\u63a8\u5012\u9b45\u9b54\u5427\uff0c\u521a\u4e00\u8f6c\u8eab\uff0c\u9669\u4e9b\u649e\u4e0a\u4e86\u5728\u8eab\u540e\u7ad9\u7740\u7684\u4e00\u4e2a\u4eba\uff0c\u5012\u662f\u5413\u4e86\u4e00\u8df3\u3002\u5b9a\u775b\u4e00\u770b\u539f\u6765\u662f\u73ca\u5609\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u201d</p><p>\u73ca\u5609\u6ca1\u6709\u56de\u7b54\uff0c\u5979\u6b63\u5168\u795e\u8d2f\u6ce8\u5730\u51dd\u89c6\u7740\u90a3\u4e2a\u7389\u767d\u8272\u7684\u5929\u7a79\u4eea\uff0c\u76ee\u5149\u9510\u5229\uff0c\u50cf\u662f\u80fd\u591f\u7a7f\u900f\u8868\u9762\u770b\u5230\u6838\u5fc3\u4f3c\u7684\uff0c\u795e\u60c5\u975e\u5e38\u8ba4\u771f\u3002\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u5374\u4e5f\u4e0d\u6562\u518d\u51fa\u58f0\u6253\u6270\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u73ca\u5609\u4eff\u4f5b\u5982\u68a6\u521d\u9192\uff0c\u5168\u8eab\u9707\u4e86\u4e00\u9707\uff0c\u6574\u4e2a\u4eba\u7684\u611f\u89c9\u201c\u7f13\u548c\u201d\u4e0b\u6765\uff0c\u201c\u8fd9\u662f\u5929\u7a79\u4eea\uff0c\u5bf9\u5427\uff0c\u5c0f\u5f1f\u3002\u201d\u5979\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u662f\u554a\u2026\u2026\u59d0\u59d0\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d</p><p>\u9664\u4e86\u743c\u6069\u521a\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u62c9\u52a0\u8bf4\u8fc7\u4e00\u6b21\u5929\u7a79\u4eea\uff0c\u540e\u6765\u5728\u573a\u6240\u6709\u4eba\u90fd\u6ca1\u518d\u63d0\u8fc7\u5b83\u7684\u540d\u5b57\uff0c\u53ea\u662f\u7528\u201c\u8fd9\u4e1c\u897f\u201d\u4ee3\u66ff\u3002\u73ca\u5609\u663e\u7136\u662f\u4e2d\u9014\u624d\u8fdb\u6765\u7684\uff0c\u5979\u600e\u4e48\u4f1a\u77e5\u9053\uff1f</p><p>\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u5c31\u662f\u7a81\u7136\u77e5\u9053\u4e86\uff0c\u201d\u73ca\u5609\u5fae\u5fae\u8e59\u7709\uff0c\u201c\u800c\u4e14\u8fd9\u4e1c\u897f\uff0c\u597d\u5947\u602a\uff0c\u6211\u8d8a\u770b\u8d8a\u719f\u6089\uff0c\u597d\u50cf\u4ee5\u524d\u7ecf\u5e38\u89c1\u5230\u4f3c\u7684\u3002\u201d</p><p>\u2026\u2026\u59d0\u59d0\uff0c\u4f60\u6700\u8fd1\u600e\u4e48\u603b\u662f\u770b\u4ec0\u4e48\u90fd\u89c9\u5f97\u773c\u719f\uff0c\u4e0a\u6b21\u770b\u5409\u52d2\u4eca\u89c9\u5f97\u773c\u719f\uff0c\u8fd9\u6b21\u770b\u5929\u7a79\u4eea\u53c8\u89c9\u5f97\u773c\u719f\uff0c\u4f60\u4e00\u76f4\u8fd9\u6837\u4e0b\u53bb\uff0c\u4f5c\u4e3a\u5f1f\u5f1f\u6211\u7684\u538b\u529b\u5f88\u5927\u554a\u3002</p><p>\u73ca\u5609\u6c89\u541f\u4e0d\u8bed\uff0c\u770b\u7740\u5929\u7a79\u4eea\uff0c\u795e\u60c5\u50cf\u662f\u5728\u6781\u529b\u56de\u5fc6\u3001\u82e6\u82e6\u601d\u7d22\uff0c\u5374\u53c8\u5b9e\u5728\u662f\u60f3\u4e0d\u8d77\u6765\u3002\u201c\u59d0\u59d0\uff0c\u522b\u60f3\u4e86\uff0c\u4f60\u662f\u8fd9\u4e9b\u5929\u592a\u7d2f\u4e86\u800c\u5df2\uff0c\u5bb9\u6613\u51fa\u9519\u89c9\uff0c\u201d\u743c\u6069\u63fd\u7740\u73ca\u5609\uff0c\u60f3\u5e26\u5979\u56de\u623f\u95f4\uff0c\u201c\u597d\u597d\u7761\u4e0a\u4e00\u89c9\uff0c\u81ea\u7136\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u4e86\u2014\u2014\u5bf9\u4e86\uff0c\u201d\u4ed6\u538b\u4f4e\u58f0\u97f3\uff0c\u51d1\u8fd1\u8033\u8fb9\uff0c\u201c\u4eca\u5929\u662f\u7b2c\u516d\u5929\u4e86\uff0c\u59d0\u59d0\u90a3\u4e2a\u5e94\u8be5\u5b8c\u4e86\u5427\uff0c\u8981\u4e0d\u8981\u6211\u966a\u7740\u2026\u2026\u201d</p><p>\u5982\u679c\u662f\u4e00\u822c\u7684\u5973\u5b69\u5b50\uff0c\u88ab\u8fd9\u6837\u4e00\u8bf4\u4e5f\u5c31\u5fc3\u4e0b\u91ca\u7136\uff0c\u89c9\u5f97\u201c\u53ef\u80fd\u771f\u662f\u6700\u8fd1\u592a\u7d2f\u4e86\uff0c\u662f\u9519\u89c9\u5427\u201d\uff0c\u81f3\u5c11\u4f1a\u6682\u65f6\u653e\u5f00\u5ff5\u5934\u4e0d\u60f3\u3002\u4eba\u662f\u5f88\u5bb9\u6613\u52a8\u6447\u7684\u751f\u7269\uff0c\u5973\u5b69\u5b50\u5c24\u5176\u5982\u6b64\uff0c\u5728\u4fe1\u4efb\u7684\u4eba\u9762\u524d\u66f4\u662f\u5982\u6b64\u3002\u7136\u800c\u73ca\u5609\u504f\u504f\u5c31\u662f\u7279\u4f8b\uff0c\u5979\u6027\u683c\u5916\u4f3c\u6e29\u67d4\uff0c\u5185\u6781\u521a\u5f3a\uff0c\u4e00\u65e6\u8ba4\u51c6\u7684\u4e8b\u60c5\uff0c\u5c31\u7b97\u662f\u743c\u6069\u4e5f\u6ca1\u6cd5\u8bf4\u670d\u5979\u3002\u201c\u4e0d\u662f\u9519\u89c9\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u8f7b\u58f0\u4f46\u575a\u5b9a\u5730\u8bf4\uff0c\u201c\u6211\u7684\u786e\u66fe\u7ecf\u89c1\u8fc7\u5b83\uff0c\u53ea\u662f\u4e00\u65f6\u95f4\u60f3\u4e0d\u8d77\u6765\u3002\u4f46\u8fd9\u7edd\u5bf9\u4e0d\u662f\u4ec0\u4e48\u9519\u89c9\uff0c\u6211\u81ea\u5df1\u5f88\u6e05\u695a\u3002\u201d</p><p>\u59d0\u59d0\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u53ef\u4e0d\u53ef\u4ee5\u4e0d\u8981\u8fd9\u4e48\u81ea\u4fe1\u2026\u2026</p><p>\u743c\u6069\u4e00\u65f6\u95f4\u4e5f\u65e0\u8bdd\u53ef\u8bf4\uff0c\u6b64\u65f6\u62c9\u52a0\u7b49\u4eba\u5600\u5600\u5495\u5495\u5546\u8bae\u51fa\u4e86\u4e2a\u7ed3\u679c\uff0c\u51b3\u5b9a\u5168\u4f53\u51fa\u52a8\uff0c\u524d\u5f80\u53d1\u73b0\u5929\u7a79\u4eea\u7684\u90a3\u7247\u6d77\u57df\u641c\u5bfb\uff0c\u6bd5\u5176\u529f\u4e8e\u4e00\u5f79\u3002\u62c9\u52a0\u95ee\u743c\u6069\u662f\u5426\u613f\u610f\u540c\u53bb\uff0c\u743c\u6069\u6b63\u5f85\u63a8\u8131\uff0c\u73ca\u5609\u5df2\u7ecf\u62a2\u5148\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u6211\u4eec\u53bb\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u59d0\u59d0\uff01\u201d\u743c\u6069\u53eb\u8d77\u6765\u3002</p><p>\u201c\u542c\u6211\u7684\uff0c\u5c0f\u5f1f\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u6709\u79cd\u5f3a\u70c8\u7684\u76f4\u89c9\uff0c\u544a\u8bc9\u6211\u5e94\u8be5\u8d70\u8fd9\u4e00\u8d9f\u3002\u201d</p><p>\u5979\u8bed\u8c03\u5e73\u6de1\uff0c\u4f46\u5176\u4e2d\u900f\u7740\u65a9\u9489\u622a\u94c1\u7684\u610f\u5473\uff0c\u534a\u70b9\u4e0d\u5bb9\u8fdd\u62d7\uff0c\u743c\u6069\u4e3a\u5979\u6c14\u52bf\u6240\u6444\uff0c\u7adf\u4e0d\u6562\u53cd\u9a73\u3002\u201c\u90a3\u597d\u5427\uff0c\u201d\u62c9\u52a0\u8bf4\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u5c31\u9a6c\u4e0a\u505a\u51c6\u5907\uff0c\u660e\u5929\u4e00\u65e9\u51fa\u53d1\u3002\u201d</p></article>"}}}]);
