(window.webpackJsonp=window.webpackJsonp||[]).push([[452],{475:function(p){p.exports={chapterTitle:null,title:"\u7b2c318\u7ae0 \u79c1\u4eba\u52a9\u7406",content:"<article><h2>\u7b2c318\u7ae0 \u79c1\u4eba\u52a9\u7406</h2><p>\u3000\u3000\u4e5d\u4e03\u5e74\u590f\u5b63\u7684\u5370\u5c3c\u68ee\u6797\u5927\u706b\u4f7f\u5f97\u5168\u7403\u6728\u6d46\u4f9b\u5e94\u6301\u7eed\u5403\u7d27\uff0c\u6b64\u65f6\u6295\u8d44\u8425\u5efa\u7684\u901f\u4e30\u6797\u5c31\u662f\u4e00\u9879\u6781\u6709\u4ef7\u503c\u7684\u4f18\u8d28\u8d44\u4ea7\uff0c\u867d\u7136\u79bb\u8f6e\u4f10\u671f\u8fd8\u5f88\u8fdc\uff0c\u4f46\u662f\u4f5c\u4e3a\u6709\u4ef7\u503c\u3001\u6709\u589e\u503c\u524d\u666f\u7684\u8d44\u4ea7\uff0c\u4e00\u6837\u53ef\u4ee5\u62ff\u6765\u5305\u88c5\u51fa\u552e\uff0c\u66f4\u4f55\u51b5\u56fd\u5185\u6b63\u79ef\u6781\u7684\u63a8\u52a8\u6728\u6d46\u5728\u9020\u7eb8\u5de5\u4e1a\u4e2d\u7684\u5e94\u7528\uff0c\u800c\u56fd\u5185\u7684\u5929\u7136\u6797\u8d44\u6e90\u53c8\u662f\u90a3\u6837\u7684\u6025\u7f3a\uff0c\u4e0a\u901f\u4e30\u6797\u9879\u76ee\u4e5f\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u4ece\u6295\u8d44\u7684\u89d2\u5ea6\u8003\u8651\uff0c\u98ce\u9669\u4e5f\u662f\u6781\u5fae\u7684\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6240\u8c13\u7684\u516d\u5341\u4ebf\u8bf4\u767d\u4e86\u5c31\u662f\u4e00\u4e2a\u5929\u5927\u7684\u5e4c\u5b50\uff0c\u76ee\u7684\u5728\u4e8e\u8bf1\u4f7f\u5730\u65b9\u653f\u5e9c\u79ef\u6781\u7684\u534f\u52a9\u9526\u6e56\u5728\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u641e\u901f\u4e30\u6797\u9879\u76ee\uff0c\u5728\u6700\u77ed\u7684\u65f6\u95f4\u91cc\u6539\u5584\u5c0f\u6c5f\u6d41\u57df\u7684\u751f\u6001\u73af\u5883\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u516c\u4ea4\u8f66\u4e0a\u5c06\u4ed6\u7684\u8003\u8651\u8ddf\u665a\u6674\u7ec6\u7ec6\u89e3\u8bf4\u4e86\u4e00\u904d\uff0c\u665a\u6674\u62cd\u4e86\u62cd\u80f8\u53e3\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u4e9b\u7406\u7531\u8fd8\u771f\u6ca1\u529e\u6cd5\u8ddf\u516c\u53f8\u7684\u5176\u4ed6\u4eba\u89e3\u91ca\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u8bf4\u6ca1\u6709\u5fc5\u8981\u5c06\u516c\u53f8\u7684\u53d1\u5c55\u8ddf\u516c\u76ca\u4e8b\u4e1a\u626f\u5f97\u592a\u7d27\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u770b\u4e00\u4e2a\u4eba\u7684\u5fc3\u6709\u591a\u5927\u3002\u5929\u7136\u6797\u8d44\u6e90\u4fdd\u62a4\u662f\u5fc5\u9700\u7684\uff0c\u7136\u800c\u56fd\u5185\u7684\u6728\u6750\u9700\u6c42\u5374\u5728\u9010\u5e74\u7684\u589e\u52a0\uff0c\u8fd9\u4e4b\u95f4\u7684\u77db\u76fe\u53ea\u80fd\u53d1\u5c55\u5de5\u4e1a\u539f\u6599\u6797\u6765\u89e3\u51b3\uff0c\u8fd9\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u4ec5\u4ec5\u4ece\u6295\u8d44\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u6211\u4eec\u53ea\u662f\u63d0\u524d\u4e86\u4e00\u6b65\u800c\u5df2\uff0c\u867d\u8bf4\u6709\u4e00\u5b9a\u7684\u5f0a\u7aef\uff0c\u4f46\u4e5f\u4e0d\u662f\u6ca1\u6709\u4e00\u70b9\u989d\u5916\u7684\u597d\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u6e29\u67d4\u7684\u770b\u7740\u5f20\u606a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u603b\u662f\u652f\u6301\u4f60\u7684\u2026\u2026\u201d\u665a\u6674\u7a7f\u7740\u7070\u767d\u683c\u5b50\u7684\u5462\u5b50\u5927\u8863\uff0c\u655e\u5f00\u7684\u9886\u895f\u9732\u51fa\u73ab\u7ea2\u8272\u7684\u7f8a\u7ed2\u6bdb\u8863\uff0c\u886c\u5f97\u5979\u7684\u8138\u86cb\u5a07\u8273\u800c\u6210\u719f\uff0c\u5f20\u606a\u8bdd\u91cc\u6240\u4f53\u73b0\u7684\u793e\u4f1a\u8d23\u4efb\u611f\uff0c\u8ba9\u5979\u611f\u89c9\u5230\u4ed6\u8eab\u4e0a\u66f4\u5438\u5f15\u4eba\u7684\u9b44\u529b\u6240\u5728\uff0c\u8fd9\u5b8c\u5168\u662f\u6210\u719f\u7684\u7565\u5e26\u5fe7\u601d\u7684\u7537\u4eba\u7684\u7075\u9b42\uff0c\u65e0\u6127\u4e8e\u8ba9\u5979\u6c89\u8ff7\u75f4\u9189\u3002\u4fbf\u662f\u4ece\u665a\u6674\u81ea\u8eab\u6765\u8bf4\uff0c\u5979\u51fa\u8eab\u4e8e\u653f\u5546\u5bb6\u5ead\uff0c\u5c11\u5973\u65f6\u671f\u53c8\u5728\u56fd\u5916\u8bfb\u4e66\uff0c\u4e5f\u9887\u4e3a\u8ba4\u540c\u5f20\u606a\u7684\u89c2\u70b9\u3002</p><p>\u3000\u3000\u8d8a\u5f80\u5e02\u533a\uff0c\u516c\u4ea4\u8f66\u4e0a\u4eba\u8d8a\u591a\uff0c\u665a\u6674\u6084\u6084\u5c06\u624b\u4f38\u8fc7\u53bb\uff0c\u63e1\u4f4f\u5f20\u606a\u7684\u624b\uff0c\u76f4\u5230\u4e0b\u8f66\u624d\u677e\u5f00\u3002\u665a\u6674\u76ae\u80a4\u4fdd\u517b\u5f97\u597d\uff0c\u8fd8\u5982\u5c11\u5973\u822c\u5a07\u5ae9\uff0c\u6b64\u65f6\u7709\u773c\u751f\u5a9a\uff0c\u53ea\u5982\u4e8c\u5341\u56db\u4e94\u5c81\u7684\u6a21\u6837\uff0c\u5f20\u606a\u88c5\u8001\u6210\u60ef\u4e86\uff0c\u628a\u4ed6\u968f\u4fbf\u5f80\u54ea\u91cc\u4e00\u4e22\uff0c\u6ca1\u6709\u4eba\u4f1a\u628a\u4ed6\u8ddf\u5b66\u751f\u8054\u7cfb\u8d77\u6765\u3002\u4ed6\u4eec\u63e1\u7740\u624b\uff0c\u7d27\u6328\u7740\u4ed6\u4eec\u7684\u4eba\u770b\u4e86\u4e5f\u53ea\u628a\u4ed6\u4eec\u5f53\u6210\u4e00\u5bf9\u60c5\u4fa3\u3002</p><p>\u3000\u3000\u516c\u4ea4\u8f66\u76f4\u5230\u516c\u53f8\u6240\u5728\u7684\u5927\u697c\u524d\uff0c\u4e24\u4eba\u624d\u677e\u5f00\u624b\uff0c\u5f20\u606a\u62ff\u624b\u653e\u5230\u9f3b\u524d\u95fb\u4e86\u95fb\uff0c\u8fd8\u7559\u6709\u4f59\u9999\uff0c\u665a\u6674\u5ae3\u7136\u4e00\u7b11\uff0c\u5f02\u6837\u7684\u611f\u89c9\u5728\u5fc3\u91cc\u9759\u9759\u6d41\u8d9f\u7740\uff0c\u4e24\u4eba\u5374\u5b9e\u5728\u6ca1\u6709\u72ec\u5904\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e00\u76f4\u8ba9\u8fd9\u8fb9\u6d77\u88d5\u516c\u53f8\u7269\u8272\u5408\u683c\u7684\u52a9\u7406\u4eba\u9009\uff0c\u4e0b\u4e86\u8f66\uff0c\u62ac\u624b\u770b\u4e86\u770b\u5496\u5561\u8272\u7684\u829d\u5e15\u8155\u8868\uff0c\u5df2\u7ecf\u8fc7\u4e86\u7ea6\u5b9a\u7684\u65f6\u95f4\uff0c\u516c\u4ea4\u8f66\u4ece\u65b0\u6885\u82d1\u5f00\u8fc7\u6765\u7adf\u8981\u4e09\u523b\u949f\uff0c\u4e0e\u5f20\u606a\u6025\u6b65\u5f80\u5927\u697c\u91cc\u8d70\uff0c\u6709\u4e00\u4e2a\u4ece\u516c\u4ea4\u8f66\u524d\u95e8\u4e0b\u6765\u7684\u5973\u5b69\u5b50\u6025\u51b2\u51b2\u7684\u8d85\u8fc7\u4ed6\u4eec\uff0c\u78b0\u4e86\u8c22\u665a\u6674\u4e00\u4e0b\uff0c\u5f88\u614c\u4e71\u7684\u9053\u6b49\u3002</p><p>\u3000\u30001\u7c7363\u300164\u5de6\u53f3\u7684\u4e2d\u7b49\u8eab\u6750\uff0c\u7a7f\u7740\u5496\u5561\u8272\u7684\u897f\u88c5\u5957\u88d9\uff0c\u811a\u4e0b\u5374\u7a7f\u7740\u767d\u8272\u7684\u5f39\u529b\u7403\u978b\uff0c\u5f88\u6e05\u6f88\u767d\u51c0\u7684\u9762\u5b54\uff0c\u6ca1\u600e\u4e48\u5316\u5986\uff0c\u6240\u4ee5\u80fd\u51c6\u786e\u7684\u5224\u65ad\u5979\u7684\u5e74\u9f84\u5927\u7ea6\u5728\u4e8c\u5341\u56db\u4e94\u5c81\u5de6\u53f3\uff0c\u867d\u7136\u662f\u5f88\u614c\u4e71\u7684\u9053\u6b49\uff0c\u4f46\u662f\u8138\u4e0a\u8fd8\u5e26\u7740\u771f\u8bda\u7684\u6de1\u6de1\u7b11\u5bb9\uff0c\u770b\u5979\u8f6c\u8eab\u79bb\u53bb\u7126\u6025\u770b\u624b\u8868\u7684\u6837\u5b50\uff0c\u5f20\u606a\u5c0f\u58f0\u95ee\u665a\u6674\uff1a\u201c\u83ab\u4e0d\u662f\u8d76\u7740\u6765\u9762\u8bd5\u7684\u5973\u5b69\u5b50\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u8bf4\u9053\uff1a\u201c\u4e0d\u6e05\u695a\uff0c\u9762\u8bd5\u4eba\u7684\u8d44\u6599\u6211\u8fd8\u6ca1\u6709\u770b\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u8fdb\u4e86\u5927\u697c\uff0c\u770b\u89c1\u90a3\u4e2a\u5973\u5b69\u5b50\u7ad9\u5728\u7535\u68af\u524d\u516c\u53f8\u7684\u94ed\u724c\uff0c\u89c6\u7ebf\u5c31\u505c\u5728\u5341\u4e8c\u697c\u6d77\u88d5\u63a7\u80a1\u4e0a\u4e0b\uff0c\u5f20\u606a\u62ac\u624b\u8ba9\u665a\u6674\u770b\u4e86\u770b\uff0c\u8f7b\u58f0\u7b11\u7740\u8bf4\uff1a\u201c\u8fdf\u5230\u4e86\u5462\u3002\u201d</p><p>\u3000\u3000\u90a3\u5973\u5b69\u5b50\u8033\u6735\u5c16\uff0c\u542c\u5230\u5f20\u606a\u7684\u8bdd\uff0c\u8138\u4e00\u7ea2\uff0c\u8fdb\u4e86\u7535\u68af\uff0c\u5979\u4ece\u5979\u7684\u630e\u5305\u91cc\u62ff\u51fa\u4e00\u53cc\u4e2d\u8ddf\u7684\u76ae\u978b\uff0c\u770b\u5979\u7684\u795e\u60c5\uff0c\u5728\u5f20\u606a\u3001\u8c22\u665a\u6674\u4e24\u4e2a\u964c\u751f\u4eba\u9762\u524d\u6362\u978b\u66f4\u662f\u5c34\u5c2c\uff0c\u4e0d\u8fc7\u8fd8\u7b97\u9547\u5b9a\uff0c\u6362\u597d\u978b\u8f6c\u5f20\u606a\u3001\u8c22\u665a\u6674\u62b1\u4ee5\u6de1\u6de1\u7684\u5fae\u7b11\u3002</p><p>\u3000\u3000\u540c\u4e00\u5c42\u51fa\u7535\u68af\uff0c\u90a3\u5973\u5b69\u5b50\u624d\u610f\u8bc6\u5230\u5f20\u606a\u4ed6\u4eec\u4e5f\u662f\u6765\u6d77\u88d5\u516c\u53f8\u7684\uff0c\u95ee\u4ed6\u4eec\uff1a\u201c\u4f60\u4eec\u4e5f\u662f\u9762\u8bd5\u7684\uff1f\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u6447\u4e86\u6447\u5934\uff0c\u90a3\u5973\u5b69\u5b50\u6709\u4e9b\u7126\u6025\u7684\u8bf4\uff1a\u201c\u6628\u5929\u624d\u901a\u77e5\u6211\u53c2\u52a0\u9762\u8bd5\uff0c\u4eca\u5929\u624b\u5934\u7684\u4e8b\u60c5\u521a\u505a\u5b8c\uff0c\u73b0\u5728\u5df2\u7ecf\u8fc7\u4e86\u9762\u8bd5\u65f6\u95f4\uff0c\u4f60\u4eec\u77e5\u9053\u4eba\u529b\u8d44\u6e90\u90e8\u5728\u54ea\u4e2a\u623f\u95f4\uff1f\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u7ed9\u90a3\u5973\u5b69\u5b50\u6307\u51fa\u4eba\u4e8b\u529e\u516c\u5ba4\u7684\u65b9\u4f4d\uff0c\u90a3\u5973\u5b69\u9053\u4e86\u58f0\u8c22\uff0c\u7126\u6025\u7684\u8d70\u8fc7\u53bb\uff0c\u770b\u5f97\u51fa\u5979\u5f88\u5728\u610f\u8fd9\u6b21\u673a\u4f1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7591\u60d1\u7684\u770b\u7740\u8c22\u665a\u6674\uff0c\u9762\u8bd5\u7684\u4e8b\u60c5\u96be\u9053\u662f\u6628\u5929\u624d\u5b9a\u4e0b\u6765\u7684\uff1f</p><p>\u3000\u3000\u8c22\u665a\u6674\u5012\u662f\u80fd\u770b\u51fa\u5f20\u606a\u773c\u91cc\u7684\u7591\u95ee\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6653\u5f97\u54ea\u4e2a\u73af\u8282\u51fa\u4e86\u95ee\u9898\uff0c\u53c2\u52a0\u9762\u8bd5\u7684\u4e09\u4e2a\u4eba\u5e94\u8be5\u65e9\u5c31\u6311\u9009\u597d\u4e86\u2026\u2026\u201d\u95ee\u8fc7\u6d77\u88d5\u516c\u53f8\u8fd9\u8fb9\u7684\u8d1f\u8d23\u4eba\uff0c\u624d\u77e5\u9053\u521a\u624d\u9047\u5230\u90a3\u4e2a\u5973\u5b69\u4e4b\u524d\u4e00\u76f4\u6ca1\u6709\u8054\u7cfb\u4e0a\uff0c\u6628\u5929\u624d\u901a\u77e5\u5230\uff0c\u53ea\u662f\u9762\u8bd5\u8fdf\u5230\u8ba9\u4eba\u611f\u89c9\u4e0d\u5927\u597d\u3002</p><p>\u3000\u3000\u665a\u6674\u62ac\u624b\u8ba9\u4ed6\u770b\u4e86\u8868\uff0c\u793a\u610f\u5979\u4eec\u4e5f\u4f1a\u6ca1\u610f\u8bc6\u5230\u4f1a\u8fdf\u5230\u3002</p><p>\u3000\u3000\u9762\u8bd5\u65f6\uff0c\u5f20\u606a\u867d\u7136\u656c\u966a\u672b\u5e2d\uff0c\u5374\u4e0d\u63d0\u95ee\uff0c\u4e5f\u4e0d\u53d1\u8868\u8bae\u8bba\uff0c\u8ddf\u7740\u6765\u73a9\u4f3c\u7684\uff0c\u4e0d\u60f3\u7ed9\u6d77\u88d5\u7684\u4eba\u4ee5\u55a7\u5bbe\u593a\u4e3b\u7684\u611f\u89c9\u3002\u4e0d\u8fc7\u8fdb\u884c\u82f1\u8bed\u538b\u529b\u9762\u8bd5\u65f6\uff0c\u6d77\u88d5\u516c\u53f8\u7684\u4eba\u5c31\u6709\u4e9b\u4e0d\u591f\u770b\uff0c\u5f20\u606a\u624d\u53c2\u4e0e\u63d0\u95ee\u3002</p><p>\u3000\u3000\u627e\u52a9\u7406\uff0c\u5dee\u4e0d\u591a\u79d8\u4e66\u7684\u89d2\u8272\uff0c\u56fd\u5185\u6240\u8c13\u7684\u4e13\u4e1a\u4eba\u58eb\u5e76\u4e0d\u80fd\u8ba9\u5f20\u606a\u5f88\u6ee1\u610f\uff0c\u6d77\u88d5\u8fd9\u8fb9\u6311\u9009\u51fa\u6765\u7684\u524d\u4e09\u4e2a\u53c2\u52a0\u9762\u8bd5\u7684\u4eba\uff0c\u6240\u8c13\u7684\u4e30\u5bcc\u7ecf\u9a8c\uff0c\u4e5f\u53ea\u662f\u5728\u5916\u8d44\u516c\u53f8\u53ae\u6df7\u8fc7\u51e0\u5e74\u7684\u8001\u6cb9\u5b50\u800c\u5df2\uff0c\u5979\u4eec\u5df2\u7ecf\u5b66\u4f1a\u62ff\u5047\u9762\u76ee\u5305\u88c5\u81ea\u5df1\uff0c\u6709\u4e00\u4e2a\u5973\u5b69\u5b50\u770b\u5230\u5979\u4ee5\u540e\u670d\u52a1\u7684BOSS\u7adf\u7136\u662f\u4e2a\u5973\u6027\uff0c\u8fd8\u9732\u51fa\u5f88\u72b9\u8c6b\u7684\u8868\u60c5\u3002\u5f20\u606a\u60f3\u8d77\u4e00\u4e2a\u7b11\u8bdd\uff0c\u5199\u7ed9\u8c22\u665a\u6674\uff1a\u201c\u627e\u5458\u5de5\u4e00\u822c\u90fd\u627e\u6709\u5de5\u4f5c\u7ecf\u9a8c\u7684\uff0c\u627e\u5973\u79d8\u4e66\u4e00\u822c\u90fd\u4e0d\u627e\u6709\u5de5\u4f5c\u7ecf\u9a8c\u7684\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e0d\u52a8\u58f0\u8272\u7684\u5c06\u5f20\u606a\u7ed9\u5979\u7684\u7eb8\u6761\u53e0\u597d\u653e\u81ea\u5df1\u53e3\u888b\u91cc\uff0c\u53c8\u62ff\u4ed6\u6ca1\u6709\u529e\u6cd5\u3002</p><p>\u3000\u3000\u5012\u662f\u8fdf\u5230\u7684\u90a3\u4e2a\u53eb\u5f20\u5ead\u7684\u5973\u5b69\u5b50\u7ed9\u4eba\u6734\u7d20\u7684\u611f\u89c9\uff0c\u8868\u73b0\u4e5f\u76f8\u5f53\u4e0d\u9519\uff0c\u5012\u4e0d\u662f\u8bf4\u65e0\u61c8\u53ef\u51fb\uff0c\u4f46\u4e5f\u8fd8\u8bf4\u5f97\u8fc7\u53bb\u3002\u5728\u9762\u8bd5\u8fc7\u7a0b\u4e2d\uff0c\u5f20\u606a\u63a5\u5230\u674e\u4e49\u6c5f\u7684\u7535\u8bdd\uff0c\u4ed6\u63a8\u95e8\u8d70\u51fa\u53bb\uff0c\u5230\u8fc7\u9053\u91cc\u63a5\u7535\u8bdd\uff0c\u4e0b\u53bb\u7684\u4e8b\u60c5\u5df2\u7ecf\u5b89\u6392\u597d\u4e86\uff0c\u660e\u5929\u5b89\u6392\u76f4\u5347\u98de\u673a\uff0c\u4e0e\u6797\u4e1a\u5385\u53ca\u56fd\u6709\u6797\u573a\u7ba1\u7406\u5c40\u7684\u9886\u5bfc\u4e00\u8d77\u4e0b\u53bb\uff0c\u4e0b\u9762\u60f3\u505a\u624b\u811a\u90fd\u4e0d\u53ef\u80fd\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u63a8\u95e8\u51fa\u6765\uff0c\u95ee\u4ed6\u5bf9\u56db\u4e2a\u9762\u8bd5\u8005\u7684\u5370\u8c61\uff0c\u5f20\u606a\u5bf9\u5979\uff1a\u201c\u8fd9\u4e2a\u5f20\u5ead\uff0c\u4f60\u8981\u89c9\u5f97\u4e0d\u6ee1\u610f\u5c31\u8ba9\u7ed9\u6211\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u7b11\u4e86\u7b11\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u6211\u6709\u8bf4\u4e0d\u6ee1\u610f\uff0c\u4f60\u504f\u504f\u5c06\u8bdd\u62a2\u8fc7\u53bb\u8bf4\u2026\u2026\u201d\u53c8\u5c06\u5f20\u606a\u521a\u624d\u9012\u7ed9\u5979\u7684\u7eb8\u6761\u585e\u5f20\u606a\u53e3\u888b\u91cc\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u773c\u775b\u91cc\u5012\u662f\u4e00\u4e9b\u7b11\u610f\uff0c\u201c\u54ea\u6765\u8fd9\u4e9b\u4e4c\u4e03\u516b\u7cdf\u7684\u4e1c\u897f\uff1f\u8fd9\u4e2a\u5973\u5b69\u770b\u8d77\u6765\u5012\u6ca1\u6709\u505a\u79d8\u4e66\u7684\u5de5\u4f5c\u7ecf\u9a8c\uff0c\u6562\u7559\u7ed9\u4f60\u8fd9\u4e2a\u5c0f\u8272\u72fc\u7528\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u644a\u644a\u624b\uff0c\u770b\u6765\u665a\u6674\u5bf9\u8fd9\u4e2a\u8fdf\u5230\u7684\u5973\u5b69\u5b50\u4e5f\u5341\u5206\u4e2d\u610f\uff0c\u4ed6\u662f\u627e\u4e2a\u79c1\u4eba\u52a9\u7406\uff0c\u4e0d\u8fc7\u4e0d\u6562\u627e\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u603b\u8981\u987e\u5fcc\u522b\u4eba\u7684\u60f3\u6cd5\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e0e\u5f20\u606a\u56de\u5230\u9762\u8bd5\u7684\u4f1a\u8bae\u5ba4\uff0c\u5979\u5bf9\u5f20\u5ead\u76f4\u63a5\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u8ba9\u6211\u4eec\u505a\u6210\u8fd9\u7b14\u4e70\u5356\uff0c\u6211\u5728\u8fd9\u91cc\u4e5f\u6709\u529e\u516c\u5ba4\uff0c\u4f46\u662f\u5de5\u4f5c\u5730\u70b9\u591a\u5728\u6d77\u5dde\uff0c\u4e00\u4e2a\u67083000\uff0c\u4f60\u4ec0\u4e48\u65f6\u95f4\u80fd\u5de5\u4f5c\uff1f\u201d3000\u5927\u6982\u662f\u52a9\u7406\u8fd9\u4e2a\u5de5\u4f5c\u5728\u7701\u5185\u660e\u9762\u4e0a\u7684\u6700\u9ad8\u5de5\u8d44\uff0c\u8c22\u665a\u6674\u4e0d\u7ed9\u5f20\u5ead\u8ba8\u4ef7\u8fd8\u4ef7\u7684\u673a\u4f1a\uff0c\u4e5f\u6ca1\u6709\u8ba9\u5979\u56de\u8fc7\u4e24\u4e2a\u793c\u62dc\u7b49\u901a\u77e5\uff0c\u76f4\u63a5\u95ee\u5979\u4ec0\u4e48\u65f6\u5019\u80fd\u8fc7\u6765\u5de5\u4f5c\u3002\u4e3a\u52a9\u7406\u7684\u4e8b\uff0c\u8c22\u665a\u6674\u4e5f\u5934\u75bc\u4e86\u5f88\u4e45\u3002</p><p>\u3000\u3000\u5f20\u5ead\u5f88\u9ad8\u5174\u7684\u8bf4\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u90fd\u53ef\u4ee5\uff1f\u201d</p><p>\u3000\u3000\u540e\u6765\u624d\u77e5\u9053\u5f20\u5ead\u8d74\u82f1\u8bfb\u4e66\u7684\u7b7e\u8bc1\u88ab\u62d2\uff0c\u5de5\u4f5c\u4e5f\u65e9\u8f9e\u6389\u597d\u4e45\uff0c\u53ea\u662f\u7ed9\u4eba\u5bb6\u505a\u7ffb\u8bd1\u6253\u96f6\u5de5\u8d5a\u751f\u6d3b\u8d39\uff0c\u6709\u65f6\u751a\u81f3\u8fd8\u63a5\u5bb6\u6559\u7684\u6d3b\u3002\u8c22\u665a\u6674\u5f88\u9ad8\u5174\u4e8b\u60c5\u5f88\u987a\u5229\u7684\u51b3\u5b9a\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u4ece\u73b0\u5728\u5c31\u5f00\u59cb\u5427\uff0c\u73b0\u5728\u79bb\u4e0b\u73ed\u65f6\u95f4\u8fd8\u6709\u4e09\u4e2a\u5c0f\u65f6\uff0c\u4f60\u5e2e\u6211\u5c06\u56fd\u5916\u6797\u7eb8\u4e00\u4f53\u5316\u7684\u8d44\u6599\u6536\u96c6\u4e00\u4e0b\u2026\u2026\u201d\u89c1\u5f20\u5ead\u5bf9\u6797\u7eb8\u4e00\u4f53\u5316\u6ca1\u4ec0\u4e48\u5177\u4f53\u7684\u6982\u5ff5\uff0c\u8ddf\u5979\u7565\u5fae\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u201c\u8fd9\u4e9b\u8d44\u6599\u6025\u7740\u7528\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8c22\u665a\u6674\u8fd8\u662f\u6682\u65f6\u8ba9\u5f20\u5ead\u5e2e\u4ed6\u7684\u5fd9\uff0c\u6797\u7eb8\u4e00\u4f53\u5316\u7684\u8d44\u6599\uff0c\u76ee\u524d\u53ea\u80fd\u68c0\u7d22\u5916\u6587\u8d44\u6599\uff0c\u8fd8\u771f\u7f3a\u4e2a\u4e13\u95e8\u7684\u4eba\u624b\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u8981\u4e0a\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\uff0c\u8fd9\u4e24\u5e74\u5c31\u4e0d\u80fd\u62ff\u94b1\u7ed9\u53f6\u5efa\u658c\uff0c\u5f20\u606a\u603b\u8981\u5f53\u9762\u8ddf\u53f6\u5efa\u658c\u89e3\u91ca\u53bb\uff0c\u6253\u7535\u8bdd\u8fc7\u53bb\uff0c\u6070\u5de7\u53f6\u5efa\u658c\u4e5f\u5728\u7701\u57ce\uff0c\u53f6\u5efa\u658c\u5728\u57ce\u5916\u7684\u53f6\u5bb6\u79c1\u5b85\u91cc\uff0c\u542c\u5230\u5f20\u606a\u627e\u4ed6\uff0c\u4fbf\u8ba9\u4ed6\u5230\u8001\u5b85\u53bb\uff0c\u53f6\u5bb6\u8001\u7237\u5b50\u53f6\u7956\u8303\u4e5f\u60f3\u7740\u4ed6\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u6d77\u88d5\u516c\u53f8\u62ff\u4e86\u4e00\u90e8\u8f66\uff0c\u8c22\u665a\u6674\u95ee\u4ed6\u665a\u4e0a\u8981\u4e0d\u8981\u5728\u65b0\u6885\u82d1\u7ed9\u4ed6\u5b89\u6392\u623f\u95f4\uff0c\u5f20\u606a\u6320\u4e86\u6320\u5934\uff0c\u8c01\u77e5\u9053\u8ddf\u53f6\u5efa\u658c\u8981\u8017\u5230\u51e0\u70b9\u949f\uff0c\u4fbf\u8bf4\u4e0d\u7528\u3002</p></article>"}}}]);
