(window.webpackJsonp=window.webpackJsonp||[]).push([[454],{476:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u56db\u8282 \u838e\u73de\u514b\u7684\u6545\u4e8b",content:"<article><h2>\u7b2c\u56db\u5341\u56db\u8282 \u838e\u73de\u514b\u7684\u6545\u4e8b</h2><p>\u201c\u4ec0\u4e48\u53ea\u6b64\u4e00\u6b21\uff1f\u201d</p><p>\u743c\u6069\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u73ca\u5609\u5df2\u7ecf\u9ed8\u8bf5\u51fa\u4e86\u5492\u8bed\uff0c\u900f\u660e\u7684\u8f6e\u5ed3\u5728\u7a7a\u6c14\u4e2d\u6084\u7136\u51fa\u73b0\uff0c\u5728\u534a\u79d2\u949f\u5185\u5b8c\u5168\u5177\u73b0\u4e3a\u4eba\u5f62\uff0c\u6b63\u662f\u838e\u73de\u514b\u3002\u548c\u5f80\u5e38\u4e0d\u540c\u7684\u662f\uff0c\u5979\u5e76\u975e\u4ee5\u6218\u6597\u59ff\u6001\u51fa\u73b0\uff0c\u800c\u662f\u4e00\u4e1d\u4e0d\u6302\uff0c\u8d64\u88f8\u7684\u8eaf\u4f53\u9a84\u50b2\u5730\u66b4\u9732\u5728\u7a7a\u6c14\u4e2d\uff0c\u8702\u8170\u9686\u81c0\uff0c\u6eda\u5706\u7ed3\u5b9e\u7684\u5927\u817f\u7d27\u7d27\u5e76\u62e2\uff0c\u80f8\u90e8\u50b2\u7136\u633a\u8d77\uff0c\u5145\u5206\u5c55\u793a\u51fa\u8eab\u4e3a\u9b45\u9b54\u7684\u65e0\u6bd4\u81ea\u4fe1\u3002</p><p>\u56e0\u4e3a\u4e0a\u6b21\u5728\u6b27\u675c\u6797\u7684\u6559\u8bad\uff0c\u743c\u6069\u628a\u8d1d\u88d8\u91cc\u5b9d\u77f3\u540a\u5760\u7ed9\u4e86\u73ca\u5609\uff0c\u76ee\u7684\u662f\u4e07\u4e00\u9047\u5230\u5371\u9669\uff0c\u80fd\u591f\u53ec\u5524\u51fa\u9b45\u9b54\u6765\u62a4\u536b\u3002\u53ea\u662f\u73ca\u5609\u5e76\u4e0d\u559c\u6b22\u628a\u838e\u73de\u514b\u5c01\u5728\u5b9d\u77f3\u91cc\uff0c\u5e73\u65f6\u603b\u662f\u8ba9\u5979\u7559\u5728\u5916\u9762\u3002\u6b64\u524d\u743c\u6069\u8ddf\u62c9\u52a0\u53bb\u5730\u4e0b\u5ba4\uff0c\u662f\u5429\u5490\u838e\u73de\u514b\u53bb\u966a\u7740\u73ca\u5609\u7684\uff0c\u6309\u9053\u7406\u8bf4\u9b45\u9b54\u4e0d\u53ef\u80fd\u6297\u547d\uff1b\u540e\u6765\u73ca\u5609\u51fa\u73b0\u7684\u65f6\u5019\uff0c\u838e\u73de\u514b\u5e76\u4e0d\u5728\u5979\u8eab\u8fb9\uff0c\u5f53\u65f6\u743c\u6069\u5c31\u6709\u70b9\u5947\u602a\uff0c\u5982\u4eca\u6765\u770b\u663e\u7136\u662f\u88ab\u5c01\u8fdb\u4e86\u5b9d\u77f3\u3002</p><p>\u66f4\u91cd\u8981\u7684\u662f\uff0c\u838e\u73de\u514b\u88ab\u5c01\u8fdb\u5b9d\u77f3\uff0c\u4fbf\u76f8\u5f53\u4e8e\u4e2d\u4e86\u201c\u6c38\u6052\u9759\u6ede\u201d\u6cd5\u672f\uff0c\u65e0\u8bba\u751f\u7406\u8fd8\u662f\u610f\u8bc6\u90fd\u5b8c\u5168\u9677\u5165\u505c\u987f\uff0c\u8fde\u601d\u8003\u90fd\u4e0d\u53ef\u80fd\uff0c\u66f4\u522b\u8bf4\u6362\u8863\u670d\u6216\u8005\u8131\u8863\u670d\u3002\u88ab\u5c01\u5165\u5b9d\u77f3\u524d\u662f\u4ec0\u4e48\u6837\uff0c\u90a3\u4e48\u88ab\u91ca\u653e\u51fa\u6765\u65f6\u5c31\u662f\u4ec0\u4e48\u6837\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u838e\u73de\u514b\u88ab\u5c01\u8fdb\u5b9d\u77f3\u65f6\uff0c\u5c31\u662f\u88f8\u4f53\u72b6\u6001\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u4f60\u2026\u2026\u201d</p><p>\u867d\u7136\u662f\u4ee5\u6027\u611f\u8bf1\u4eba\u4e3a\u672c\u804c\u7684\u9b45\u9b54\uff0c\u4f46\u838e\u73de\u514b\u5e76\u6ca1\u6709\u88f8\u5954\u7684\u7231\u597d\uff0c\u4e4b\u6240\u4ee5\u4f1a\u5982\u6b64\uff0c\u663e\u7136\u51fa\u81ea\u73ca\u5609\u6388\u610f\u3002\u73ca\u5609\u5e94\u8be5\u6ca1\u6709\u767e\u5408\u503e\u5411\uff0c\u4e4b\u6240\u4ee5\u8fd9\u4e48\u505a\uff0c\u663e\u7136\u662f\u65e9\u5c31\u6253\u7b97\u597d\u4e86\uff0c\u5982\u679c\u81ea\u5df1\u65e0\u6cd5\u201c\u641e\u5b9a\u201d\u743c\u6069\uff0c\u5c31\u53ec\u5524\u838e\u73de\u514b\u524d\u6765\u5e2e\u5fd9\u3002\u8fd9\u662f\u5f88\u7b80\u5355\u7684\u63a8\u7406\uff0c\u743c\u6069\u4e00\u60f3\u4fbf\u77e5\uff0c\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u4ed6\u624d\u4f1a\u611f\u5230\u60ca\u8bb6\u3002</p><p>\u65e0\u8bba\u4f5c\u4e3a\u59d0\u59d0\u4e5f\u7f62\uff0c\u4f5c\u4e3a\u60c5\u4eba\u4e5f\u7f62\uff0c\u73ca\u5609\u90fd\u51e0\u8fd1\u5b8c\u7f8e\uff0c\u5979\u7f8e\u4e3d\u800c\u4e0d\u5996\u8273\uff0c\u806a\u660e\u800c\u4e0d\u5f20\u626c\uff0c\u6e29\u67d4\u800c\u4e0d\u602f\u61e6\uff0c\u4ece\u5bb9\u800c\u4e0d\u51cc\u8feb\uff0c\u4ee4\u4eba\u65e0\u53ef\u6311\u5254\u3002\u4f46\u4ece\u53e6\u4e00\u65b9\u9762\u8bf4\uff0c\u5979\u662f\u4e2a\u5fc3\u9ad8\u6c14\u50b2\uff0c\u4e2a\u6027\u521a\u5f3a\u7684\u5973\u5b69\u5b50\uff0c\u6240\u4ee5\u5ac9\u5992\u5fc3\u91cd\uff0c\u72ec\u5360\u6b32\u5f3a\uff0c\u4e5f\u4fbf\u662f\u60c5\u7406\u4e4b\u4e2d\u3002\u4ece\u73ca\u5609\u7684\u7acb\u573a\u4e0a\u6765\u8bf4\uff0c\u6700\u7406\u60f3\u7684\u72b6\u6001\uff0c\u4fbf\u662f\u5176\u4ed6\u5973\u5b69\u5b50\u5168\u90fd\u7edf\u7edf\u6d88\u5931\u4e0d\u89c1\uff0c\u53ea\u6709\u5979\u548c\u743c\u6069\u5728\u4e00\u8d77\u5c31\u597d\u3002\u743c\u6069\u5e76\u4e0d\u8ba4\u4e3a\u8fd9\u79cd\u60f3\u6cd5\u4e0d\u6b63\u5e38\u2014\u2014\u4e8b\u5b9e\u4e0a\u662f\u6b63\u5e38\u5f97\u4e0d\u80fd\u518d\u6b63\u5e38\uff0c\u6240\u8c13\u7231\u60c5\uff0c\u672c\u5c31\u81ea\u79c1\uff0c\u4e16\u4e0a\u53c8\u6709\u54ea\u4e2a\u5973\u5b69\u5b50\u4f1a\u771f\u613f\u610f\u548c\u522b\u4eba\u5206\u4eab\u60c5\u4eba\u7684\uff1f\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u73ca\u5609\u6b64\u523b\u7684\u505a\u6cd5\uff0c\u5c31\u672a\u514d\u6709\u4e9b\u4ee4\u4eba\u8d39\u89e3\u4e86\u3002</p><p>\u4e3b\u52a8\u8ba9\u522b\u7684\u5973\u5b69\u5b50\u53c2\u52a0\u8fdb\u6765\uff0c\u5141\u8bb8\u5979\u548c\u81ea\u5df1\u5206\u4eab\u5f1f\u5f1f\uff0c\u54ea\u6015\u662f\u9884\u5148\u58f0\u660e\u201c\u53ea\u6b64\u4e00\u6b21\uff0c\u4e0b\u4e0d\u4e3a\u4f8b\u201d\uff0c\u5bf9\u4e8e\u73ca\u5609\u800c\u8a00\uff0c\u505a\u51fa\u8fd9\u79cd\u51b3\u5b9a\uff0c\u8fd8\u662f\u592a\u8270\u96be\u4e86\u5427\u3002\u5f53\u7136\uff0c\u838e\u73de\u514b\u548c\u73ca\u5609\u786e\u5b9e\u4e00\u76f4\u633a\u8c08\u5f97\u6765\uff0c\u76f8\u5904\u5f97\u4e0d\u9519\uff0c\u4f46\u8fd9\u4e5f\u4e0d\u80fd\u6210\u4e3a\u7406\u7531\u3002\u56e0\u4e3a\u5c31\u7b97\u662f\u8ba4\u8bc6\u6700\u4e45\u3001\u5173\u7cfb\u6700\u597d\u7684\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u59d0\u59b9\u4fe9\uff0c\u5728\u743c\u6069\u7684\u95ee\u9898\u4e0a\uff0c\u73ca\u5609\u5176\u5b9e\u90fd\u662f\u4e0d\u600e\u4e48\u613f\u610f\u8ba9\u6b65\u7684\uff0c\u6700\u591a\u4e5f\u5c31\u662f\u9ed8\u8bb8\u743c\u6069\u5077\u5403\uff0c\u5979\u7741\u4e00\u53ea\u773c\u95ed\u4e00\u53ea\u773c\uff0c\u4e0d\u4e88\u8ffd\u7a76\uff0c\u5c31\u5f53\u4e0d\u77e5\u9053\u3002\u4f46\u5982\u679c\u743c\u6069\u6562\u660e\u76ee\u5f20\u80c6\u5730\u5f53\u7740\u73ca\u5609\u7684\u9762\u63a8\u5012\u8299\u857e\u72c4\uff0c\u90a3\u4ed6\u5c31\u7b49\u7740\u88ab\u59d0\u59d0\u8ffd\u6740\u5427\u3002</p><p>\u6216\u8bb8\uff0c\u53ea\u6709\u50cf\u6885\u83f2\u65af\u548c\u51db\u90a3\u6837\u7684\u631a\u53cb\uff0c\u81ea\u5e7c\u76f8\u8bc6\uff0c\u4e00\u8d77\u957f\u5927\uff0c\u6709\u7740\u5171\u7ecf\u751f\u6b7b\u60a3\u96be\u7684\u6df1\u539a\u53cb\u8c0a\uff0c\u624d\u53ef\u80fd\u5728\u8fd9\u4e2a\u95ee\u9898\u4e0a\u52c9\u5f3a\u5bb9\u5fcd\u5f7c\u6b64\u5427\u3002\u96be\u9053\u8bf4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u73ca\u5609\u548c\u838e\u73de\u514b\u7684\u611f\u60c5\u5df2\u7ecf\u597d\u5230\u8fd9\u79cd\u5730\u6b65\u4e86\uff1f</p><p>\u6709\u90a3\u4e48\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u90fd\u8981\u6000\u7591\u838e\u73de\u514b\u662f\u5426\u5bf9\u73ca\u5609\u4e0b\u4e86\u9b45\u60d1\u672f\uff0c\u4f46\u4ed6\u968f\u5373\u5426\u5b9a\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002\u838e\u73de\u514b\u7edd\u6ca1\u8fd9\u4e2a\u80c6\u91cf\uff0c\u800c\u4ece\u73ca\u5609\u7684\u773c\u795e\u91cc\uff0c\u4e5f\u770b\u4e0d\u51fa\u4efb\u4f55\u88ab\u5fc3\u667a\u9b54\u6cd5\u5f71\u54cd\u7684\u8ff9\u8c61\uff0c\u53ea\u6709\u7f9e\u6da9\u3001\u60c5\u6b32\uff0c\u4ee5\u53ca\u6df7\u5408\u7740\u4e00\u4e1d\u4e0d\u7518\u5fc3\u7684\u5766\u7136\u3002\u800c\u4e14\u743c\u6069\u4e5f\u5f88\u6e05\u695a\uff0c\u73ca\u5609\u770b\u4f3c\u67d4\u5f31\uff0c\u5176\u5b9e\u5374\u662f\u548c\u6885\u83f2\u65af\u4e00\u6837\u5fc3\u667a\u6781\u575a\u5f3a\uff0c\u610f\u5fd7\u6781\u575a\u5b9a\u7684\u5973\u5b69\u5b50\uff0c\u5c31\u51ed\u838e\u73de\u514b\u90a3\u53d8\u6210\u9b45\u9b54\u540e\u624d\u5b66\u4f1a\u7684\u9b45\u60d1\u672f\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u5f71\u54cd\u5230\u5979\u3002</p><p>\u7b97\u4e86\uff0c\u60f3\u4e0d\u660e\u767d\uff0c\u7d22\u6027\u5c31\u4e0d\u53bb\u60f3\uff0c\u53cd\u6b63\u59d0\u59d0\u90fd\u4e3b\u52a8\u613f\u610f\u4e86\uff0c\u81ea\u5df1\u8fd8\u60f3\u8fd9\u4e48\u591a\u505a\u4ec0\u4e48\uff0c\u771f\u662f\u5eb8\u4eba\u81ea\u6270\u3002\u5c31\u5f53\u662f\u73ca\u5609\u4eca\u5929\u5fc3\u60c5\u597d\uff0c\u7a81\u7136\u5927\u65b9\u8d77\u6765\u4e86\u5427\u3002</p><p>\u838e\u73de\u514b\u663e\u7136\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u51fa\u73b0\u4e4b\u540e\u671d\u743c\u6069\u5a07\u5a9a\u5730\u7b11\u4e86\u7b11\uff0c\u722c\u5230\u73ca\u5609\u8eab\u65c1\u8dea\u4e0b\u3002\u201c\u5514\uff0c\u8fd9\u6837\u4e0d\u5bf9\u5462\uff0c\u59d0\u59d0\uff0c\u201d\u5979\u6307\u70b9\u7740\u73ca\u5609\uff0c\u201c\u5e94\u8be5\u8fd9\u6837\uff0c\u5bf9\uff0c\u7528\u624b\u63e1\u7740\u5b83\u2026\u2026\u8bb0\u5f97\u7528\u820c\u5c16\uff0c\u4ece\u6839\u90e8\u5f80\u4e0a\u2026\u2026\u201d</p><p>\u201c\u597d\u7d2f\u554a\u3002\u201d\u73ca\u5609\u62b1\u6028\u7740\u3002</p><p>\u201c\u56e0\u4e3a\u4f60\u7684\u59ff\u52bf\u4e0d\u6b63\u786e\uff0c\u800c\u4e14\u4e5f\u4e0d\u80fd\u4e00\u76f4\u4fdd\u6301\u540c\u4e00\u4e2a\u59ff\u52bf\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6211\u4e0a\u6b21\u5c31\u6559\u8fc7\u4f60\u7684\u5440\u3002\u201d</p><p>\u201c\u4f60\u6559\u5f97\u592a\u591a\uff0c\u54ea\u91cc\u5168\u8bb0\u5f97\u4f4f\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u800c\u4e14\u5c31\u7b97\u90fd\u8bb0\u5f97\uff0c\u4e00\u5b9e\u9645\u505a\u8d77\u6765\u5c31\u5fd8\u4e86\u3002\u201d</p><p>\u201c\u5f88\u6b63\u5e38\uff0c\u6240\u4ee5\u8981\u591a\u52a0\u7ec3\u4e60\u561b\u2026\u2026\u55ef\uff0c\u5f88\u5bf9\uff0c\u7528\u5634\u5507\u538b\u7d27\uff0c\u7537\u4eba\u90a3\u5730\u65b9\u6700\u654f\u611f\uff0c\u7136\u540e\u4e00\u70b9\u70b9\u5f80\u91cc\u541e\u2026\u2026\u201d</p><p>\u5728\u838e\u73de\u514b\u7ec6\u81f4\u5165\u5fae\u7684\u73b0\u573a\u6307\u70b9\u4e0b\uff0c\u73ca\u5609\u7684\u6280\u5de7\u8f83\u4e4b\u4ee5\u524d\u6709\u4e86\u660e\u663e\u7684\u8fdb\u6b65\uff0c\u4f46\u79bb\u771f\u6b63\u7684\u5408\u683c\u8fd8\u6709\u5f88\u957f\u4e00\u6bb5\u5dee\u8ddd\uff0c\u6bd5\u7adf\u5c06\u7406\u8bba\u8f6c\u5316\u4e3a\u5b9e\u9645\u662f\u9700\u8981\u65f6\u95f4\u548c\u7ecf\u9a8c\u7684\u3002\u73ca\u5609\u867d\u7136\u5728\u5f88\u591a\u65b9\u9762\u90fd\u4f18\u79c0\u5f97\u60ca\u4eba\uff0c\u4f46\u5728\u7528\u5507\u820c\u53d6\u60a6\u7537\u4eba\u7684\u6280\u5de7\u4e0a\uff0c\u8fd8\u771f\u8c08\u4e0d\u4e0a\u591a\u5c11\u5929\u8d4b\u3002\u800c\u4e14\u5979\u4e4b\u524d\u5df2\u7ecf\u201c\u54ac\u201d\u4e86\u534a\u664c\uff0c\u4f53\u529b\u660e\u663e\u8ddf\u4e0d\u4e0a\u4e86\u3002\u201c\u4e0d\u884c\uff0c\u5634\u4e00\u76f4\u5f20\u7740\uff0c\u9178\u5f97\u90fd\u5408\u4e0d\u4e0a\u4e86\uff0c\u201d\u5979\u65e0\u5948\u5730\u628a\u743c\u6069\u7684\u5bb6\u4f19\u5410\u51fa\u6765\uff0c\u201c\u574f\u86cb\uff01\u5230\u5e95\u8981\u6b3a\u8d1f\u59d0\u59d0\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u4eba\u5bb6\u90fd\u8981\u7d2f\u6b7b\u5566\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\u2026\u2026\u201d\u743c\u6069\u82e6\u7740\u8138\uff0c\u201c\u53c8\u4e0d\u662f\u6211\u60f3\u5c31\u80fd\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u54fc\uff01\u201d</p><p>\u73ca\u5609\u5658\u7740\u5c0f\u5634\uff0c\u9887\u4e0d\u60c5\u613f\u5730\u5411\u838e\u73de\u514b\u70b9\u4e86\u70b9\u5934\uff0c\u8ba9\u5f00\u4f4d\u7f6e\uff0c\u8dea\u5728\u65c1\u8fb9\u7684\u9b45\u9b54\u65e9\u5c31\u671f\u5f85\u5df2\u4e45\uff0c\u8fde\u5fd9\u722c\u4e0a\u6765\uff0c\u63a5\u66ff\u73ca\u5609\u7ee7\u7eed\u521a\u624d\u7684\u5de5\u4f5c\u3002\u5979\u523b\u610f\u628a\u52a8\u4f5c\u653e\u7f13\uff0c\u5e76\u4e14\u4e0d\u65ad\u53d8\u6362\u7740\u82b1\u6837\uff0c\u50cf\u662f\u5728\u73ca\u5609\u505a\u793a\u8303\uff0c\u73ca\u5609\u4fcf\u8138\u7eef\u7ea2\uff0c\u65e2\u7f9e\u53c8\u607c\uff0c\u5374\u8fd8\u662f\u8ba4\u8ba4\u771f\u771f\u5730\u5b66\u4e60\u7740\uff0c\u773c\u775b\u90fd\u4e0d\u7728\u4e00\u4e0b\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u4e2a\u52e4\u594b\u7684\u597d\u5b66\u751f\uff0c\u5c31\u5dee\u6ca1\u62ff\u4e2a\u672c\u5b50\u6765\u505a\u7b14\u8bb0\uff0c\u6a21\u6837\u8bf1\u4eba\u6781\u4e86\u3002</p><p>\u867d\u7136\u5df2\u7ecf\u201c\u53e3\u201d\u4e0b\u7559\u60c5\uff0c\u523b\u610f\u653e\u7f13\u4e86\u52a8\u4f5c\uff0c\u4f46\u838e\u73de\u514b\u7684\u7cbe\u6e5b\u6280\u672f\uff0c\u4f9d\u65e7\u4e0d\u662f\u73ca\u5609\u53ef\u4ee5\u6bd4\u62df\u7684\uff0c\u53ea\u8981\u53d1\u6325\u51fa\u516d\u4e03\u6210\u6765\uff0c\u5c31\u8db3\u591f\u8f7b\u8f7b\u677e\u677e\u5bf9\u4ed8\u743c\u6069\u4e86\u3002\u4e0d\u5230\u534a\u5c0f\u65f6\uff0c\u4ee5\u743c\u6069\u5728\u5c11\u5973\u5a07\u5ae9\u5589\u7ba1\u4e2d\u7684\u5267\u70c8\u55b7\u53d1\u4e3a\u6807\u5fd7\uff0c\u9b45\u9b54\u7684\u7b2c\u4e00\u5802\u4eb2\u8eab\u793a\u8303\u6559\u5b66\u8bfe\u7ed3\u675f\u4e86\u3002</p><p>\u201c\u771f\u5389\u5bb3\u5462\uff0c\u838e\u73de\u514b\u3002\u201d\u73ca\u5609\u4e0d\u65e0\u5ac9\u5992\u5730\u8bf4\u3002</p><p>\u9b45\u9b54\u7d27\u95ed\u7740\u5634\u5507\uff0c\u5589\u7ba1\u98a4\u52a8\uff0c\u5495\u565c\u5495\u565c\u5c06\u707c\u70ed\u7684\u6d53\u6d46\u5168\u90fd\u541e\u54bd\u4e0b\u53bb\uff0c\u7136\u540e\u624d\u80fd\u5f00\u53e3\u3002\u201c\u591a\u7ec3\u4e60\u5c31\u53ef\u4ee5\u554a\uff0c\u201d\u5979\u5598\u606f\u7740\u8bf4\uff0c\u201c\u6211\u4e00\u5f00\u59cb\u4e5f\u662f\u4ec0\u4e48\u90fd\u4e0d\u4f1a\uff0c\u88ab\u4eba\u903c\u7740\u5b66\uff0c\u505a\u5f97\u4e0d\u597d\u5c31\u8981\u6328\u6253\uff0c\u7ed3\u679c\u53ea\u7528\u4e86\u4e00\u661f\u671f\u5c31\u80fd\u5f88\u719f\u7ec3\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u8f7b\u8f7b\u5e94\u4e86\u4e00\u58f0\uff0c\u518d\u5ea6\u4fef\u4e0b\u8eab\u53bb\uff0c\u66ff\u743c\u6069\u505a\u4e8b\u540e\u6e05\u6d01\u3002\u5df2\u7ecf\u534a\u8f6f\u7684\u7537\u6027\u8c61\u5f81\uff0c\u6cbe\u6ee1\u4e86\u7565\u5e26\u82e6\u8165\u7684\u725b\u5976\u548c\u9b45\u9b54\u751c\u751c\u7684\u6d25\u6db2\uff0c\u73ca\u5609\u542e\u5728\u53e3\u4e2d\uff0c\u6709\u4e00\u79cd\u7279\u522b\u7684\u611f\u89e6\uff0c\u4eff\u4f5b\u6574\u4e2a\u4eba\u90fd\u6655\u6655\u5730\u98d8\u8d77\u6765\u3002</p><p>\u201c\u53ef\u4ee5\u4e86\u3002\u201d\u838e\u73de\u514b\u5728\u65c1\u63d0\u9192\u3002</p><p>\u611f\u53d7\u5230\u743c\u6069\u5df2\u7ecf\u5728\u81ea\u5df1\u7684\u5c0f\u5634\u91cc\u91cd\u632f\u96c4\u98ce\uff0c\u73ca\u5609\u5c06\u5b83\u5410\u51fa\uff0c\u7136\u540e\u4e0a\u524d\u8de8\u5750\u5728\u743c\u6069\u7684\u8170\u4e0a\u3002\u838e\u73de\u514b\u5728\u8eab\u540e\u6276\u7740\u73ca\u5609\uff0c\u5e2e\u5979\u5bf9\u51c6\u4f4d\u7f6e\uff0c\u4e00\u70b9\u70b9\u5730\u6c89\u8170\u5750\u5165\u3002\u8fd9\u79cd\u4f53\u4f4d\u5bf9\u5973\u6027\u7684\u8981\u6c42\u5f88\u9ad8\uff0c\u73ca\u5609\u4ece\u672a\u8bd5\u8fc7\uff0c\u5018\u82e5\u4e0d\u662f\u838e\u73de\u514b\u5e2e\u5fd9\u7684\u8bdd\u6839\u672c\u65e0\u6cd5\u5b8c\u6210\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u521a\u521a\u6062\u590d\u7684\u4e00\u70b9\u4f53\u529b\u4e5f\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u4e2d\u5b8c\u5168\u8017\u5c3d\uff0c\u53ea\u80fd\u4f0f\u5728\u743c\u6069\u80f8\u53e3\uff0c\u4f4e\u4f4e\u5598\u606f\u7740\uff0c\u4e1d\u6beb\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u201c\u592a\u5927\u4e86\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u4ef0\u8d77\u8138\uff0c\u54c0\u6028\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u611f\u89c9\u5728\u8eab\u4f53\u91cc\u597d\u6da8\u597d\u786c\uff0c\u5f04\u5f97\u59d0\u59d0\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\u3002\u201d</p><p>\u201c\u90a3\u59d0\u59d0\u4f11\u606f\u5427\uff0c\u8ba9\u6211\u6765\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff01\u8bf4\u597d\u8981\u59d0\u59d0\u670d\u4f8d\u4f60\u7684\u3002\u201d</p><p>\u73ca\u5609\u4e00\u53e3\u62d2\u7edd\uff0c\u5979\u4f11\u606f\u7247\u523b\uff0c\u547c\u5438\u6e10\u6e10\u5e73\u7f13\uff0c\u7136\u540e\u54ac\u7740\u7259\uff0c\u53cc\u624b\u6491\u5728\u743c\u6069\u80f8\u819b\u4e0a\uff0c\u52c9\u5f3a\u652f\u8d77\u4e0a\u8eab\uff0c\u539f\u672c\u60f3\u52a8\u4f5c\uff0c\u7136\u800c\u8bd5\u4e86\u51e0\u6b21\uff0c\u53cc\u817f\u9178\u8f6f\uff0c\u5b9e\u5728\u6ca1\u6709\u529b\u6c14\uff0c\u53ea\u5f97\u4fdd\u6301\u7740\u73b0\u5728\u7684\u59ff\u52bf\uff0c\u6309\u7167\u838e\u73de\u514b\u4f20\u6388\u7684\u65b9\u6cd5\uff0c\u987a\u7740\u547c\u5438\u7684\u8282\u594f\u7f13\u7f13\u6536\u7f29\u4f53\u5185\u82b1\u5f84\u3002\u9b45\u9b54\u4ece\u8eab\u540e\u62b1\u7740\u5979\uff0c\u5728\u8033\u8fb9\u8f7b\u58f0\u6307\u70b9\uff0c\u53cc\u624b\u5728\u5979\u8170\u81c0\u4e0a\u8f7b\u8f7b\u6309\u6469\uff0c\u5e2e\u52a9\u6062\u590d\u3002</p><p>\u623f\u95f4\u91cc\u5b89\u9759\u4e0b\u6765\uff0c\u5973\u5b50\u7684\u5598\u606f\u58f0\u663e\u5f97\u683c\u5916\u6e05\u6670\uff0c\u73ca\u5609\u7528\u529b\u6491\u4f4f\u743c\u6069\u80f8\u819b\uff0c\u6d01\u767d\u8d1d\u9f7f\u7d27\u7d27\u54ac\u7740\u4e0b\u5507\uff0c\u8138\u988a\u50cf\u706b\u70e7\u822c\u9161\u7ea2\uff0c\u5448\u73b0\u51fa\u4e00\u79cd\u9189\u4eba\u800c\u5996\u5f02\u7684\u7f8e\u611f\uff0c\u989d\u5934\u4e0a\u9999\u6c57\u70b9\u70b9\u6c81\u51fa\uff0c\u56e0\u4e3a\u5267\u70c8\u8fd0\u52a8\u800c\u5f04\u4e71\u7684\u53d1\u4e1d\u8d34\u5728\u8138\u988a\u4e0a\uff0c\u547c\u5438\u4e5f\u5df2\u7ecf\u5f00\u59cb\u7d0a\u4e71\uff0c\u65e0\u6cd5\u4fdd\u6301\u7a33\u5b9a\u8282\u594f\u3002\u743c\u6069\u539f\u672c\u4e00\u76f4\u95ed\u7740\u773c\u4eab\u53d7\uff0c\u5076\u7136\u7741\u5f00\u770b\u89c1\u73ca\u5609\u8138\u8272\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u77e5\u9053\u8fd9\u662f\u4f53\u529b\u4e25\u91cd\u900f\u652f\u7684\u8ff9\u8c61\uff0c\u4ed6\u8fde\u5fd9\u5c06\u5979\u6309\u4f4f\uff0c\u5750\u8d77\u8eab\u6765\uff0c\u201c\u505c\u4e0b\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u547d\u4ee4\uff0c\u201c\u4e0d\u51c6\u518d\u52a8\u4e86\uff0c\u4e56\u4e56\u542c\u8bdd\u3002\u201d</p><p>\u73ca\u5609\u539f\u672c\u5c31\u5df2\u7ecf\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u53ea\u662f\u5979\u751f\u6027\u597d\u5f3a\uff0c\u65e2\u7136\u8bf4\u4e86\u4eca\u5929\u4e00\u5207\u7531\u5979\u4e3b\u52a8\uff0c\u4f5c\u4e3a\u5411\u743c\u6069\u7684\u9053\u6b49\uff0c\u4fbf\u4e0d\u80af\u8f7b\u6613\u653e\u5f03\uff0c\u5b8c\u5168\u9760\u7740\u4e00\u80a1\u610f\u5fd7\u529b\u786c\u751f\u751f\u652f\u6301\u5230\u73b0\u5728\u3002\u88ab\u743c\u6069\u8fd9\u4e00\u6253\u65ad\uff0c\u4e00\u53e3\u6c14\u65ad\u4e86\uff0c\u987f\u65f6\u63a5\u4e0d\u4e0a\u6765\uff0c\u8fde\u8eab\u4f53\u4e5f\u6491\u4e0d\u4f4f\uff0c\u5a07\u541f\u4e00\u58f0\u8f6f\u8f6f\u762b\u5012\uff0c\u6b63\u597d\u88ab\u743c\u6069\u62b1\u5728\u6000\u4e2d\u3002\u201c\u771f\u662f\u7684\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8d23\u5907\uff0c\u201c\u5e72\u561b\u8fd9\u4e48\u52c9\u5f3a\u81ea\u5df1\u3002\u201d</p><p>\u201c\u59d0\u59d0\u2026\u2026\u59d0\u59d0\u7b54\u5e94\u4f60\u7684\u561b\u2026\u2026\u201d</p><p>\u201c\u2026\u2026\u7b28\u86cb\uff01\u201d</p><p>\u743c\u6069\u65e2\u662f\u751f\u6c14\u53c8\u662f\u611f\u52a8\uff0c\u4e00\u65f6\u95f4\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4ed6\u62ac\u624b\u629a\u6478\u7740\u73ca\u5609\u7684\u8138\u988a\uff0c\u5c06\u53d1\u4e1d\u62e2\u5230\u8033\u540e\uff0c\u7136\u540e\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u72e0\u72e0\u77aa\u4e86\u59d0\u59d0\u8eab\u540e\u7684\u9b45\u9b54\u4e00\u773c\u3002\u4ed6\u81ea\u5df1\u4e00\u76f4\u95ed\u773c\u4eab\u53d7\uff0c\u6ca1\u5bdf\u89c9\u73ca\u5609\u7684\u5f02\u6837\uff0c\u4f46\u838e\u73de\u514b\u7406\u5f53\u662f\u77e5\u9053\u7684\uff0c\u600e\u4e48\u4e5f\u4e0d\u63d0\u9192\u4e00\u58f0\u3002</p><p>\u770b\u5230\u743c\u6069\u7684\u607c\u6012\u76ee\u5149\uff0c\u838e\u73de\u514b\u4e0d\u6562\u8bf4\u8bdd\uff0c\u8d76\u5feb\u8d77\u8eab\u4e0b\u5e8a\uff0c\u4ece\u884c\u56ca\u91cc\u53d6\u51fa\u4e00\u4e2a\u5c0f\u73bb\u7483\u74f6\uff0c\u5012\u51fa\u4e24\u9897\u6d45\u7eff\u8272\u7684\u836f\u4e38\uff0c\u5148\u542b\u5165\u81ea\u5df1\u53e3\u4e2d\uff0c\u7136\u540e\u518d\u4e0a\u5e8a\u8dea\u5728\u73ca\u5609\u8eab\u65c1\uff0c\u53e3\u5bf9\u53e3\u5730\u5582\u7ed9\u5979\u3002\u8fd9\u662f\u5979\u95f2\u6765\u65e0\u4e8b\u65f6\u914d\u7684\u4e00\u79cd\u836f\uff0c\u6709\u632f\u594b\u63d0\u795e\uff0c\u5feb\u901f\u6062\u590d\u4f53\u529b\u7684\u4f5c\u7528\u3002\u73ca\u5609\u8ff7\u8ff7\u7cca\u7cca\u5730\u670d\u4e0b\u4e24\u9897\u836f\uff0c\u7247\u523b\u540e\u7cbe\u795e\u4fbf\u660e\u663e\u6062\u590d\u4e86\u8bb8\u591a\uff0c\u5979\u72b9\u81ea\u4e0d\u80af\u653e\u5f03\uff0c\u6b63\u8981\u518d\u7ee7\u7eed\u52aa\u529b\uff0c\u88ab\u743c\u6069\u7262\u7262\u6309\u4f4f\u4e86\u80a9\uff0c\u201c\u518d\u4e71\u6765\u6211\u771f\u8981\u751f\u6c14\u4e86\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8b66\u544a\uff0c\u201c\u4e56\u4e00\u70b9\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u73ca\u5609\u4e5f\u5b9e\u5728\u662f\u6ca1\u591a\u5c11\u529b\u6c14\u6323\u624e\uff0c\u53ea\u80fd\u4e56\u4e56\u542c\u4ece\u3002\u743c\u6069\u5c06\u5979\u62b1\u5728\u6000\u4e2d\uff0c\u601c\u60dc\u5730\u4eb2\u543b\u8138\u988a\uff0c\u8f7b\u8f7b\u54ac\u556e\u7740\u8033\u5782\uff0c\u8fd9\u91cc\u662f\u73ca\u5609\u7684\u654f\u611f\u70b9\uff0c\u5979\u7684\u8eab\u4f53\u4e00\u9635\u9635\u5730\u89e6\u7535\u822c\u98a4\u6296\u7740\uff0c\u8116\u9888\u4e0a\u7684\u5b9d\u77f3\u9879\u94fe\u4e5f\u968f\u4e4b\u8f7b\u8f7b\u6643\u52a8\uff0c\u53d1\u51fa\u6e05\u8106\u60a6\u8033\u7684\u649e\u51fb\u58f0\u3002\u743c\u6069\u770b\u89c1\uff0c\u67d0\u4e2a\u5728\u5fc3\u91cc\u76d8\u7b97\u5df2\u4e45\u7684\u90aa\u6076\u5ff5\u5934\u7a81\u7136\u6d8c\u8d77\uff0c\u4e00\u65f6\u95f4\u514b\u5236\u4e0d\u4f4f\uff0c\u4f38\u624b\u6458\u4e0b\u9879\u94fe\u7684\u642d\u6263\uff0c\u5c06\u5b83\u4ece\u73ca\u5609\u8116\u9888\u4e0a\u53d6\u4e86\u4e0b\u6765\uff0c\u7136\u540e\u671d\u838e\u73de\u514b\u4f7f\u4e86\u4e2a\u773c\u8272\u3002</p><p>\u9b45\u9b54\u5148\u662f\u9519\u6115\uff0c\u7ee7\u800c\u660e\u767d\u743c\u6069\u7684\u610f\u601d\uff0c\u5a07\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u4f38\u624b\u6276\u7740\u73ca\u5609\uff0c\u5c06\u5979\u62b1\u8d77\u3002\u73ca\u5609\u6c89\u9189\u5728\u60c5\u4eba\u7231\u629a\u4e2d\uff0c\u5bf9\u5373\u5c06\u53d1\u751f\u7684\u4e00\u5207\u5168\u65e0\u6240\u89c9\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u611f\u89c9\u5230\u743c\u6069\u4ece\u81ea\u5df1\u4f53\u5185\u9000\u51fa\uff0c\u8eab\u4f53\u987f\u65f6\u88ab\u5de8\u5927\u7684\u7a7a\u865a\u611f\u4fb5\u5360\uff0c\u65e2\u662f\u5947\u602a\uff0c\u53c8\u4e0d\u9ad8\u5174\uff0c\u6b63\u81ea\u4e0d\u89e3\uff0c\u5374\u88ab\u838e\u73de\u514b\u6446\u6210\u8dea\u4f0f\u5728\u5e8a\u4e0a\u7684\u59ff\u52bf\uff0c\u50cf\u53ea\u7f8e\u4e3d\u7684\u5c0f\u6bcd\u72ac\uff0c\u96ea\u5ae9\u7f8e\u81c0\u9ad8\u9ad8\u7fd8\u8d77\u3002\u9b45\u9b54\u7684\u67d4\u8f6f\u7389\u6307\u5728\u5979\u80a1\u95f4\u7f13\u7f13\u79fb\u52a8\u7740\uff0c\u89e6\u6478\u7740\u654f\u611f\u5730\u5e26\uff0c\u6311\u9017\u5979\u7684\u6b32\u671b\u3002</p><p>\u201c\u5c0f\u5f1f\u2026\u2026\u201d</p><p>\u73ca\u5609\u7684\u547c\u5524\u672a\u6b62\uff0c\u5634\u5507\u5df2\u7ecf\u88ab\u5c01\u4f4f\uff0c\u838e\u73de\u514b\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u722c\u5230\u5979\u9762\u524d\uff0c\u6367\u7740\u5979\u7684\u8138\u988a\u4eb2\u543b\u3002\u743c\u6069\u6b64\u65f6\u5219\u5230\u4e86\u73ca\u5609\u7684\u8eab\u540e\uff0c\u4ed6\u7684\u624b\u63a2\u5165\u5c11\u5973\u96ea\u767d\u7684\u817f\u80a1\u95f4\uff0c\u5728\u5979\u79c1\u5904\u8f7b\u8f7b\u629a\u6478\uff0c\u611f\u89c9\u82b1\u854a\u5904\u6cbe\u6ee1\u671d\u9732\uff0c\u6e29\u70ed\u7684\u871c\u6db2\u6b63\u7f13\u7f13\u6d41\u51fa\uff0c\u4eff\u4f5b\u6e05\u6cc9\u3002</p><p>\u201c\u59d0\u59d0\u6e7f\u900f\u4e86\u554a\u3002\u201d</p><p>\u4ed6\u4f4e\u58f0\u7b11\u7740\uff0c\u62ff\u8d77\u9879\u94fe\uff0c\u62c8\u7740\u7b2c\u4e00\u9897\u94bb\u77f3\uff0c\u5148\u653e\u82b1\u854a\u5904\u7528\u7231\u6db2\u6fe1\u6e7f\u4e86\uff0c\u7136\u540e\u6162\u6162\u63b0\u5f00\u59d0\u59d0\u7684\u96ea\u767d\u81c0\u4e18\uff0c\u5bfb\u627e\u5230\u4e86\u90a3\u6735\u7cbe\u5de7\u7684\u83ca\u82b1\u6240\u5728\uff0c\u624b\u6307\u4e00\u70b9\u70b9\u6084\u6084\u7528\u529b\uff0c\u5c06\u94bb\u77f3\u63a8\u8fdb\u3002</p><p>\u94bb\u77f3\u867d\u7136\u5df2\u7ecf\u5c3d\u53ef\u80fd\u6253\u78e8\u6210\u7403\u9762\uff0c\u4f46\u7ec8\u7a76\u4e0d\u53ef\u80fd\u5b8c\u5b8c\u5168\u5168\u7684\u5149\u6ed1\u5706\u6da6\uff0c\u4f9d\u65e7\u6709\u4e9b\u7ec6\u5fae\u68f1\u89d2\uff0c\u73ca\u5609\u540e\u5ead\u672a\u7ecf\u4eba\u4e8b\uff0c\u80a0\u9053\u5a07\u5ae9\u65e0\u6bd4\uff0c\u7ec6\u7a84\u5f97\u4e0d\u5bb9\u4e00\u6307\uff0c\u88ab\u8fd9\u6837\u51b0\u51b0\u51b7\u51b7\u7684\u4e1c\u897f\u7a81\u7136\u585e\u8fdb\u6765\uff0c\u65e2\u89c9\u5f97\u6da8\uff0c\u53c8\u6709\u4e9b\u8f7b\u5fae\u75bc\u75db\uff0c\u4e0d\u7531\u5f97\u7709\u5934\u8f7b\u8e59\uff0c\u4f4e\u541f\u51fa\u58f0\uff0c\u8138\u4e0a\u9732\u51fa\u75db\u695a\u4e4b\u8272\u3002\u5e78\u597d\u743c\u6069\u63a8\u8fdb\u7b2c\u4e00\u9897\uff0c\u4fbf\u505c\u4e86\u624b\uff0c\u838e\u73de\u514b\u8d81\u673a\u8d76\u5feb\u5728\u5979\u8138\u4e0a\u3001\u8033\u5782\u6e29\u67d4\u4eb2\u543b\uff0c\u65bd\u5c55\u79cd\u79cd\u8c03\u60c5\u624b\u6bb5\uff0c\u8ba9\u5979\u5c3d\u53ef\u80fd\u653e\u677e\u8eab\u4f53\u3002</p><p>\u8fc7\u4e86\u534a\u664c\uff0c\u73ca\u5609\u5598\u4e86\u53e3\u6c14\uff0c\u5979\u6b64\u65f6\u4e5f\u5df2\u7ecf\u6e05\u9192\u4e86\u70b9\uff0c\u77e5\u9053\u743c\u6069\u662f\u5728\u4ec0\u4e48\u4e3b\u610f\u3002\u867d\u7136\u6ca1\u6709\u8db3\u591f\u7684\u5fc3\u7406\u51c6\u5907\uff0c\u4f46\u505a\u4e86\u4ed6\u7684\u5973\u4eba\uff0c\u65e9\u77e5\u9053\u4f1a\u6709\u8fd9\u4e00\u5929\uff0c\u4e5f\u5e76\u4e0d\u5982\u4f55\u610f\u5916\u3002\u201c\u5c0f\u5f1f\uff0c\u201d\u5979\u4f4e\u58f0\u95ee\uff0c\u201c\u4f60\u771f\u7684\u60f3\u8981\u59d0\u59d0\u2026\u2026\u59d0\u59d0\u7684\u540e\u9762\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u2026\u2026\u90a3\u59d0\u59d0\u7ed9\u4f60\u5c31\u662f\uff0c\u201d\u73ca\u5609\u54ac\u7740\u7259\uff0c\u5fcd\u8010\u7740\u80a0\u9053\u4e2d\u4e00\u9635\u9635\u4f20\u6765\u7684\u51b0\u51b7\u548c\u4e1d\u4e1d\u6da8\u75db\uff0c\u201c\u53ea\u8981\u4f60\u559c\u6b22\uff0c\u59d0\u59d0\u4ec0\u4e48\u90fd\u7ed9\u4f60\u3002\u201d</p><p>\u743c\u6069\u4e0d\u56de\u7b54\uff0c\u800c\u662f\u7528\u624b\u6307\u6cbe\u4e0a\u5979\u7684\u871c\u6db2\uff0c\u6d82\u62b9\u5728\u7b2c\u4e8c\u9897\u5b9d\u77f3\u4e0a\uff0c\u53c8\u6162\u6162\u585e\u8fdb\u3002\u5c11\u5973\u94f6\u7259\u7d27\u54ac\uff0c\u54bd\u5589\u4e2d\u53d1\u51fa\u4e00\u9635\u9635\u706b\u70ed\u7684\u4f4e\u58f0\u5a07\u541f\uff0c\u9690\u542b\u75db\u695a\uff0c\u5374\u53c8\u5a9a\u60d1\u81f3\u6781\u3002\u743c\u6069\u505c\u4e86\u4e00\u505c\uff0c\u5f85\u5979\u6e10\u6e10\u9002\u5e94\uff0c\u518d\u5982\u6cd5\u70ae\u5236\uff0c\u6162\u6162\u53c8\u585e\u8fdb\u7b2c\u4e09\u9897\u3002</p><p>\u73ca\u5609\u6b7b\u6b7b\u54ac\u7740\u5634\u5507\uff0c\u6293\u7740\u5e8a\u5355\u7684\u53cc\u624b\u6307\u8282\u90fd\u56e0\u4e3a\u8fc7\u5ea6\u7528\u529b\u800c\u53d1\u767d\uff0c\u5168\u8eab\u5267\u70c8\u98a4\u6296\u7740\uff0c\u8fc7\u4e86\u597d\u534a\u5929\u624d\u957f\u957f\u900f\u51fa\u53e3\u6c14\uff0c\u201c\u597d\u70b9\u4e86\uff0c\u6ca1\u4e8b\u4e86\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u4f60\u2026\u2026\u7ee7\u7eed\u5427\u3002\u201d</p><p>\u743c\u6069\u585e\u8fdb\u4e86\u7b2c\u56db\u679a\u5b9d\u77f3\uff0c\u63a5\u7740\u662f\u7b2c\u4e94\u679a\uff0c\u73ca\u5609\u7aed\u5c3d\u5168\u529b\u627f\u53d7\u7740\uff0c\u7ec8\u4e8e\u5b9e\u5728\u5fcd\u8010\u4e0d\u4f4f\uff0c\u201c\u4e0d\uff0c\u4e0d\u8981\u4e86\uff0c\u201d\u5979\u5e26\u7740\u54ed\u8154\u8bf4\uff0c\u201c\u597d\u6da8\uff0c\u597d\u75bc\u2026\u2026\u53d7\u4e0d\u4e86\u4e86\uff0c\u5c0f\u5f1f\uff0c\u59d0\u59d0\u5b9e\u5728\u53d7\u4e0d\u4e86\u4e86\u3002\u201d</p><p>\u201c\u59d0\u59d0\u8fd9\u5c31\u53d7\u4e0d\u4e86\u4e86\uff1f\u8fd9\u9879\u94fe\u4e0a\u7684\u5404\u79cd\u9b54\u6cd5\u6548\u679c\u8fd8\u6ca1\u6709\u542f\u52a8\u5462\u3002\u201d</p><p>\u743c\u6069\u4f4e\u58f0\u8bf4\u7740\uff0c\u5374\u4f9d\u8a00\u505c\u4e0b\u3002\u73ca\u5609\u5168\u8eab\u98a4\u6817\u7740\u8dea\u5728\u5e8a\u4e0a\uff0c\u53cc\u81c2\u56e0\u4e3a\u5b9e\u5728\u65e0\u529b\u652f\u6491\uff0c\u6574\u4e2a\u4e0a\u534a\u8eab\u5df2\u7ecf\u8d34\u5728\u4e86\u5e8a\u5355\u4e0a\uff0c\u80f8\u53e3\u4e24\u5927\u56e2\u8f6f\u8089\u88ab\u6324\u538b\u5f97\u53d8\u5f62\uff0c\u76ae\u80a4\u8868\u9762\u95ea\u7740\u4eae\u4eae\u7684\u5149\u6cfd\uff0c\u6ee1\u8eab\u90fd\u662f\u6c57\u6c34\u3002\u201c\u62ff\u51fa\u6765\uff0c\u5c0f\u5f1f\uff0c\u6c42\u4f60\u4e86\uff0c\u201d\u5979\u5598\u606f\u7740\uff0c\u201c\u59d0\u59d0\u597d\u96be\u53d7\uff0c\u809a\u5b50\u91cc\u597d\u6da8\u2026\u2026\u201d</p><p>\u201c\u4e0d\u884c\u5462\uff0c\u73ca\u5609\u59d0\u59d0\uff0c\u201d\u838e\u73de\u514b\u63d2\u8bdd\uff0c\u201c\u5c31\u662f\u8981\u6162\u6162\u9002\u5e94\u624d\u884c\uff0c\u5426\u5219\u5c31\u6ca1\u6548\u679c\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u771f\u7684\u597d\u96be\u53d7\u2026\u2026\u201d</p><p>\u201c\u518d\u5fcd\u4e00\u4f1a\uff0c\u9002\u5e94\u4e86\u5c31\u4f1a\u597d\u4e9b\uff0c\u201d\u838e\u73de\u514b\u54c4\u7740\u5979\uff0c\u201c\u6ca1\u4e8b\u7684\uff0c\u59d0\u59d0\uff0c\u56e0\u4e3a\u662f\u7b2c\u4e00\u6b21\u6240\u4ee5\u4e0d\u4e60\u60ef\u3002\u4ed6\u90a3\u4e1c\u897f\u53ef\u6bd4\u8fd9\u51e0\u9897\u5b9d\u77f3\u5927\u591a\u4e86\uff0c\u5982\u679c\u8fd9\u90fd\u53d7\u4e0d\u4e86\uff0c\u90a3\u600e\u4e48\u80fd\u5bb9\u7eb3\u4ed6\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u771f\u7684\u597d\u6da8\u2026\u2026\u201d\u4eff\u4f5b\u5b69\u5b50\u6492\u5a07\u822c\uff0c\u73ca\u5609\u5e26\u7740\u54ed\u8154\u91cd\u590d\u7740\uff0c\u201c\u6211\u4e0d\u8981\u4e86\u2026\u2026\u9a97\u4eba\u7684\uff0c\u90a3\u4e48\u5927\u7684\u4e1c\u897f\uff0c\u600e\u4e48\u53ef\u80fd\u6709\u5973\u5b69\u5b50\u540e\u9762\u80fd\u8ba9\u4ed6\u8fdb\u53bb\u2026\u2026\u6211\u4e0d\u8981\u4e86\u2026\u2026\u201d</p><p>\u201c\u6709\u7684\u554a\uff0c\u827e\u5f25\u8587\u4e0d\u5c31\u662f\u5417\uff1f\u201d</p><p>\u4eff\u4f5b\u542c\u5230\u6709\u9b54\u529b\u7684\u5b57\u7b26\u822c\uff0c\u73ca\u5609\u7684\u54ed\u6ce3\u7a81\u7136\u5e73\u606f\u4e0b\u6765\uff0c\u53ea\u5269\u4e0b\u6781\u529b\u538b\u6291\u7684\u4f4e\u4f4e\u5598\u606f\uff0c\u201c\u827e\u5f25\u8587\u2026\u2026\u827e\u5f25\u8587\u771f\u7684\u80fd\u8ba9\u4ed6\u2026\u2026\u80fd\u7528\u540e\u9762\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u662f\u771f\u7684\uff0c\u800c\u4e14\u6bcf\u6b21\u90fd\u662f\u5462\u3002\u201d</p><p>\u73ca\u5609\u54ac\u7740\u5634\u5507\uff0c\u201c\u90a3\u2026\u2026\u90a3\u6211\u4e5f\u884c\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u53eb\u7740\u743c\u6069\uff0c\u201c\u518d\u7ee7\u7eed\u5427\u2026\u2026\u59d0\u59d0\u6ca1\u4e8b\u4e86\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u7740\uff0c\u6700\u540e\u4e00\u72e0\u5fc3\uff0c\u5c06\u7b2c\u516d\u679a\u6162\u6162\u9876\u8fdb\u3002\u5c11\u5973\u7684\u8eab\u4f53\u65e0\u6cd5\u6291\u5236\u5730\u98a4\u6296\u8d77\u6765\uff0c\u6700\u7ec8\u8fde\u8dea\u59ff\u90fd\u65e0\u6cd5\u4fdd\u6301\uff0c\u5168\u8eab\u762b\u8f6f\u5730\u8db4\u5728\u5e8a\u4e0a\uff0c\u4e24\u773c\u7ffb\u767d\u7adf\u7136\u6655\u5012\u8fc7\u53bb\u3002\u743c\u6069\u548c\u838e\u73de\u514b\u90fd\u662f\u4e00\u60ca\uff0c\u8fde\u5fd9\u62a2\u6551\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u73ca\u5609\u624d\u60a0\u60a0\u9192\u8f6c\u8fc7\u6765\u3002\u201c\u574f\u2026\u2026\u574f\u5bb6\u4f19\uff0c\u5dee\u70b9\u628a\u59d0\u59d0\u5f04\u6b7b\u4e86\uff0c\u201d\u5979\u65e0\u529b\u5730\u8bf4\uff0c\u201c\u73b0\u5728\u2026\u2026\u662f\u4e0d\u662f\u5168\u90fd\u8fdb\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u65e9\u7740\u5462\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u8fd9\u624d\u516d\u9897\uff0c\u521a\u5230\u4e00\u534a\u3002\u201d</p><p>\u201c\u624d\u516d\u9897\u5417\uff1f\u201d</p><p>\u73ca\u5609\u6781\u529b\u60f3\u8981\u652f\u6491\u8d77\u8eab\u4f53\uff0c\u5374\u662f\u534a\u70b9\u90fd\u7528\u4e0d\u51fa\u529b\u6c14\u3002\u743c\u6069\u4e0d\u6562\u518d\u5f04\uff0c\u793a\u610f\u838e\u73de\u514b\u62b1\u7740\u5979\uff0c\u6162\u6162\u5c06\u5b9d\u77f3\u4e00\u679a\u4e00\u679a\u5730\u4ece\u5979\u4f53\u5185\u62c9\u51fa\u3002\u5728\u6574\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u73ca\u5609\u4e00\u76f4\u54ac\u7d27\u7259\u5173\uff0c\u5c4f\u7d27\u547c\u5438\uff0c\u4e00\u58f0\u4e0d\u542d\u3002\u76f4\u5230\u5b9d\u77f3\u5168\u90e8\u9000\u51fa\uff0c\u5979\u624d\u5982\u91ca\u91cd\u8d1f\u5730\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u63a5\u7740\u6162\u6162\u8dea\u8d77\u8eab\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u7b49\u5f85\u7740\u3002\u7136\u800c\u8fc7\u4e86\u534a\u664c\uff0c\u743c\u6069\u4e0d\u4f46\u6ca1\u6709\u5982\u5979\u9884\u6599\u7684\u90a3\u6837\u6765\u4eab\u7528\u5979\u7684\u540e\u5ead\uff0c\u53cd\u800c\u5c06\u5979\u62b1\u5728\u6000\u4e2d\uff0c\u8eba\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u5c0f\u5f1f\uff0c\u4f60\u2026\u2026\u4e0d\u662f\u60f3\u8981\u59d0\u59d0\u540e\u9762\u2026\u2026\u201d</p><p>\u201c\u662f\u554a\uff0c\u4e0d\u8fc7\u6ca1\u8bf4\u4eca\u5929\u5c31\u8981\u561b\uff0c\u201d\u743c\u6069\u4eb2\u543b\u7740\u5979\u7684\u8138\u988a\uff0c\u201c\u6162\u6162\u6765\u3002\u201d</p><p>\u201c\u4ed6\u624d\u820d\u4e0d\u5f97\u5462\uff0c\u201d\u838e\u73de\u514b\u5728\u65c1\u8fb9\u8bf4\uff0c\u8bed\u6c14\u91cc\u9178\u6e9c\u6e9c\u7684\uff0c\u5145\u6ee1\u5ac9\u610f\uff0c\u201c\u5f53\u7136\u662f\u8981\u5148\u6162\u6162\u5f00\u53d1\uff0c\u76f4\u5230\u4f60\u80fd\u771f\u6b63\u9002\u5e94\u4e86\uff0c\u4ed6\u624d\u4f1a\u8981\u4f60\uff0c\u5426\u5219\u6015\u628a\u4f60\u5f04\u4f24\u5f04\u75bc\u3002\u201d</p><p>\u201c\u90a3\uff0c\u8981\u5f00\u53d1\u591a\u4e45\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e0d\u597d\u8bf4\uff0c\u4f46\u6700\u8d77\u7801\uff0c\u8981\u80fd\u591f\u5b8c\u5168\u5bb9\u7eb3\u8fd9\u4e32\u9879\u94fe\u5427\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6211\u4f30\u8ba1\u7684\u8bdd\uff0c\u5982\u679c\u6bcf\u5929\u90fd\u953b\u70bc\uff0c\u591a\u8f84\u4e24\u4e09\u4e2a\u6708\uff0c\u5c11\u8f84\u4e00\u4e2a\u6708\uff0c\u5e94\u8be5\u5c31\u5dee\u4e0d\u591a\u4e86\u5427\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u5417\uff1f\u201d\u73ca\u5609\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u76f4\u63a5\u5c31\u2026\u2026\u539f\u6765\u8fd8\u8fd9\u4e48\u590d\u6742\u3002\u201d</p><p>\u201c\u54ea\u6709\uff0c\u56e0\u4e3a\u4f60\u662f\u4ed6\u5fc3\u7231\u7684\u4eba\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u8fd9\u4e48\u601c\u60dc\u5728\u610f\uff0c\u6362\u4e86\u5176\u4ed6\u5973\u5b69\u5b50\uff0c\u624d\u6ca1\u8fd9\u79cd\u5f85\u9047\u5462\uff0c\u201d\u838e\u73de\u514b\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u5c31\u50cf\u4ed6\u5bf9\u6211\uff0c\u4ece\u6765\u90fd\u662f\u76f4\u63a5\u5c31\u8981\uff0c\u522b\u8bf4\u8fd8\u8010\u5fc3\u5f00\u53d1\uff0c\u5c31\u8fde\u57fa\u672c\u7684\u6da6\u6ed1\u90fd\u4e0d\u80af\u5e2e\u6211\u505a\u5462\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u73ca\u5609\u770b\u7740\u743c\u6069\uff0c\u79c0\u7709\u5fae\u8e59\uff0c\u201c\u5c0f\u5f1f\uff0c\u592a\u8fc7\u5206\u4e86\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u8fce\u7740\u59d0\u59d0\u7684\u76ee\u5149\uff0c\u743c\u6069\u53ea\u597d\u5c34\u5c2c\u5730\u5411\u9b45\u9b54\u9053\u6b49\uff0c\u540c\u65f6\u6697\u4e2d\u72e0\u72e0\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u76d8\u7b97\u7740\u600e\u4e48\u627e\u673a\u4f1a\u597d\u597d\u60e9\u7f5a\u5979\u3002\u838e\u73de\u514b\u5374\u76c8\u76c8\u5fae\u7b11\uff0c\u8fce\u7740\u4ed6\u7684\u76ee\u5149\u8fd8\u4fcf\u76ae\u5730\u5410\u4e86\u5410\u820c\u5934\uff0c\u795e\u60c5\u5f97\u610f\uff0c\u6d3b\u8131\u8131\u5c31\u662f\u4e2a\u6253\u5c0f\u62a5\u544a\u6210\u529f\u7684\u5b69\u5b50\u3002</p><p>\u201c\u4e0d\u8fc7\uff0c\u81f3\u5c11\u8fd8\u9700\u8981\u4e00\u4e2a\u6708\u5417\uff1f\u201d\u56de\u5fc6\u8d77\u521a\u624d\u7684\u7ecf\u5386\uff0c\u73ca\u5609\u4f9d\u65e7\u5fc3\u6709\u9980\u60b8\uff0c\u201c\u800c\u4e14\u4eca\u5929\u8fdb\u53bb\u7684\u624d\u662f\u4e00\u534a\uff0c\u6211\u5c31\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u8981\u6b7b\u6389\u4e86\u2026\u2026\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u59d0\u59d0\uff0c\u201d\u4e0d\u77e5\u4f55\u65f6\uff0c\u838e\u73de\u514b\u5df2\u7ecf\u5f88\u81ea\u7136\u5730\u628a\u73ca\u5609\u7684\u540d\u5b57\u7ed9\u7701\u7565\u4e86\uff0c\u201c\u5973\u4eba\u7684\u8eab\u4f53\uff0c\u6bd4\u4f60\u81ea\u5df1\u6240\u80fd\u60f3\u8c61\u5230\u7684\u66f4\u987d\u5f3a\u66f4\u575a\u97e7\u4e00\u767e\u500d\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u838e\u73de\u514b\u5fae\u5fae\u7b11\u7740\uff0c\u4e0d\u77e5\u4e3a\u4f55\uff0c\u65c1\u89c2\u7684\u743c\u6069\u7a81\u7136\u611f\u89c9\u8eab\u4e0a\u6709\u4e9b\u53d1\u51b7\uff0c\u201c\u59d0\u59d0\u4f60\u77e5\u9053\u5417\uff0c\u6211\u7b2c\u4e00\u6b21\u7684\u65f6\u5019\uff0c\u540e\u9762\u4e5f\u88ab\u4eba\u585e\u8fdb\u4e86\u4e00\u4e32\u9879\u94fe\uff0c\u6574\u6574\u5341\u516b\u9897\u73e0\u5b50\u3002\u5f53\u65f6\u6211\u4e5f\u4ee5\u4e3a\u81ea\u5df1\u4e0b\u4e00\u79d2\u949f\u5c31\u8981\u6b7b\u4e86\uff0c\u7ed3\u679c\u5462\uff0c\u7167\u6837\u6d3b\u5230\u4e86\u73b0\u5728\u3002\u201d</p><p>\u73ca\u5609\u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u201c\u5341\u516b\u9897\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5341\u516b\u9897\u73e0\u5b50\uff0c\u6bcf\u4e00\u9897\u90fd\u6bd4\u8fd9\u4e9b\u5b9d\u77f3\u81f3\u5c11\u5927\u4e00\u500d\uff0c\u201d\u838e\u73de\u514b\u8bed\u6c14\u5e73\u6de1\uff0c\u4eff\u4f5b\u8bf4\u5f97\u538b\u6839\u4e0d\u662f\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u201c\u90a3\u4e00\u5929\uff0c\u6b63\u597d\u662f\u6211\u5341\u5c81\u751f\u65e5\uff0c\u5077\u4e86\u4e00\u70b9\u94b1\uff0c\u7ed3\u679c\u88ab\u4ed6\u6293\u4f4f\u4e86\uff0c\u4ed6\u8bf4\u8981\u60e9\u7f5a\u6211\uff0c\u5c31\u628a\u6211\u5e26\u5230\u4e86\u5730\u4e0b\u5ba4\u3002\u6211\u88ab\u7ed1\u5728\u6728\u5934\u67b6\u5b50\u4e0a\uff0c\u7fd8\u7740\u5c41\u80a1\uff0c\u90a3\u4e2a\u5bb6\u4f19\u5c31\u7ad9\u5728\u540e\u9762\u62ff\u7740\u9879\u94fe\u5f80\u91cc\u585e\uff0c\u800c\u4e14\u6ca1\u6709\u534a\u70b9\u6da6\u6ed1\u54e6\uff0c\u201d\u5979\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u7b2c\u4e00\u9897\u73e0\u5b50\u585e\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u6211\u90a3\u91cc\u5c31\u88ab\u6495\u88c2\u4e86\uff0c\u80fd\u6e05\u695a\u611f\u89c9\u5230\u6d41\u8840\uff0c\u6cbf\u7740\u5927\u817f\u5f80\u4e0b\u6dcc\u3002\u4ed6\u6bcf\u585e\u4e00\u9897\uff0c\u6211\u5c31\u5728\u5fc3\u91cc\u6570\u4e00\u4e0b\uff0c\u6574\u6574\u6570\u4e86\u5341\u516b\u4e0b\uff0c\u4ed6\u585e\u8fdb\u4e86\u5341\u516b\u9897\u73e0\u5b50\uff0c\u4e00\u4e2a\u4e0d\u591a\uff0c\u4e00\u4e2a\u4e0d\u5c11\u3002\u201d</p><p>\u73ca\u5609\u9ed8\u7136\u4e0d\u8bed\uff0c\u5973\u5b50\u540e\u5ead\u521d\u6b21\u88ab\u5f00\u53d1\u7684\u8270\u96be\uff0c\u5979\u521a\u624d\u5df2\u7ecf\u4eb2\u8eab\u4f53\u9a8c\u8fc7\u3002\u5b9d\u77f3\u5165\u4f53\u7684\u65f6\u5019\uff0c\u5979\u7684\u7406\u667a\u90fd\u51e0\u4e4e\u8981\u5d29\u6e83\u6389\uff0c\u4ec5\u4ec5\u53ea\u585e\u5165\u516d\u679a\uff0c\u5979\u5c31\u627f\u53d7\u4e0d\u4f4f\u6655\u4e86\u8fc7\u53bb\u3002\u800c\u4e09\u500d\u7684\u6570\u91cf\uff0c\u6bcf\u4e2a\u73e0\u5b50\u8fd8\u66f4\u5927\u4e00\u500d\uff0c\u838e\u73de\u514b\u4e0d\u4f46\u5c3d\u6570\u627f\u53d7\u4e0b\u6765\uff0c\u751a\u81f3\u8fd8\u80fd\u6e05\u9192\u5730\u8ba1\u6570\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u80fd\u7528\u575a\u5f3a\u6765\u5f62\u5bb9\uff0c\u800c\u662f\u575a\u5fcd\uff0c\u751a\u81f3\u662f\u6b8b\u5fcd\u4e86\u3002</p><p>\u771f\u6b63\u7684\u6b8b\u5fcd\uff0c\u4ece\u6765\u5c31\u4e0d\u662f\u5bf9\u522b\u4eba\uff0c\u800c\u662f\u5bf9\u81ea\u5df1\u3002</p><p>\u201c\u4ed6\u628a\u73e0\u5b50\u5168\u585e\u8fdb\u6765\uff0c\u7136\u540e\u5c31\u5f00\u59cb\u7b11\uff0c\u7b11\u5f97\u7279\u522b\u5f00\u5fc3\u7279\u522b\u5f00\u5fc3\uff0c\u201d\u838e\u73de\u514b\u7ee7\u7eed\u8bf4\uff0c\u201c\u4ed6\u62cd\u6253\u7740\u6211\u7684\u5c41\u80a1\uff0c\u8bf4\u6211\u662f\u4e2a\u5929\u751f\u7684\u8361\u5987\u3002\u4ed6\u8bf4\u4ed6\u73a9\u8fc7\u65e0\u6570\u5973\u4eba\uff0c\u4ece\u6765\u6ca1\u6709\u54ea\u4e00\u4e2a\u50cf\u6211\u8fd9\u6837\uff0c\u5c41\u773c\u80fd\u628a\u6574\u6761\u9879\u94fe\u90fd\u541e\u8fdb\u53bb\u7684\u3002\u4ed6\u8bf4\u53ef\u89c1\u6211\u5929\u751f\u5c31\u5e94\u8be5\u88ab\u7537\u4eba\u5e72\u5c41\u80a1\uff0c\u800c\u4ed6\u5c31\u662f\u7b2c\u4e00\u4e2a\u3002\u4ed6\u8bf4\u4ed6\u4f1a\u4e00\u76f4\u5e72\u6211\uff0c\u5e72\u5230\u5929\u4eae\uff0c\u5982\u679c\u5230\u65f6\u5019\u6211\u8fd8\u6ca1\u6b7b\u7684\u8bdd\uff0c\u4ed6\u5c31\u9976\u8fc7\u6211\u3002\u4ed6\u8bf4\u7684\u8fd9\u4e9b\u8bdd\uff0c\u6bcf\u53e5\u8bdd\u6211\u90fd\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4e00\u4e2a\u5b57\u90fd\u6ca1\u5fd8\u3002\u201d</p><p>\u201c\u2026\u2026\u7136\u540e\u5462\uff1f\u201d\u743c\u6069\u52c9\u5f3a\u95ee\u3002</p><p>\u201c\u7136\u540e\u4ed6\u5c31\u5f00\u59cb\u5e72\u6211\uff0c\u5e72\u4e86\u6574\u6574\u4e00\u591c\uff0c\u800c\u4e14\u4ed6\u53ea\u5e72\u6211\u540e\u9762\u8fd9\u4e00\u4e2a\u5730\u65b9\uff0c\u201d\u867d\u7136\u8bf4\u7740\u8fd9\u4e48\u60b2\u60e8\u7684\u906d\u9047\uff0c\u838e\u73de\u514b\u7684\u8138\u4e0a\u5374\u4f9d\u65e7\u6302\u7740\u7b11\u610f\uff0c\u201c\u8bf4\u771f\u7684\uff0c\u4e3b\u4eba\uff0c\u90a3\u5bb6\u4f19\u53ef\u6bd4\u4f60\u8fd8\u5f3a\u70b9\u54e6\u3002\u90a3\u4e00\u591c\u6211\u4e00\u5171\u88ab\u4ed6\u5e72\u4e86\u516b\u6b21\uff0c\u6bcf\u6b21\u6211\u90fd\u4ee5\u4e3a\u81ea\u5df1\u80af\u5b9a\u4f1a\u6b7b\u6389\uff0c\u7ed3\u679c\u90fd\u6ca1\u6b7b\uff0c\u4e0d\u4f46\u6ca1\u6b7b\uff0c\u8fde\u6655\u8fc7\u53bb\u7684\u60c5\u5f62\u90fd\u4e00\u6b21\u4e5f\u6ca1\u6709\uff0c\u201d\u5979\u504f\u504f\u5934\uff0c\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u4e8b\u540e\u60f3\u8d77\u6765\uff0c\u6211\u81ea\u5df1\u90fd\u4e0d\u6562\u76f8\u4fe1\uff0c\u53ef\u80fd\u5c31\u50cf\u4ed6\u8bf4\u7684\uff0c\u6211\u5929\u751f\u5c31\u9002\u5408\u88ab\u7537\u4eba\u5e72\u5c41\u80a1\u5427\u3002\u201d</p><p>\u201c\u7b2c\u4e8c\u5929\u5929\u521a\u4eae\uff0c\u4ed6\u628a\u6211\u4e22\u5728\u4e00\u5ea7\u7834\u623f\u5b50\u91cc\uff0c\u81ea\u5df1\u8d70\u4e86\uff0c\u5012\u662f\u8fd8\u7ed9\u6211\u7559\u4e86\u4e2a\u7eaa\u5ff5\u7269\uff0c\u5c31\u662f\u90a3\u4e32\u73e0\u5b50\uff0c\u585e\u5728\u6211\u5c41\u80a1\u91cc\u3002\u6211\u82b1\u4e86\u56db\u4e2a\u5c0f\u65f6\uff0c\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u628a\u90a3\u4e32\u73e0\u5b50\u4e00\u9897\u9897\u62c9\u51fa\u6765\uff0c\u7136\u540e\u5e26\u5728\u8eab\u8fb9\uff0c\u4e00\u76f4\u7559\u7740\uff0c\u4e00\u76f4\u7559\u5230\u4e24\u5e74\u540e\u6211\u518d\u6b21\u627e\u5230\u4ed6\u3002\u201d</p><p>\u201c\u4f60\u627e\u5230\u4ed6\u4e86\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u627e\u5230\u4e86\uff0c\u90a3\u65f6\u5019\u6211\u5df2\u7ecf\u88ab\u745e\u5854\u6536\u517b\uff0c\u6210\u4e86\u94c1\u738b\u5ea7\u65b0\u8fd1\u5d1b\u8d77\u7684\u6740\u624b\u3002\u6211\u82b1\u4e86\u4e24\u4e2a\u6708\u65f6\u95f4\uff0c\u627e\u5230\u4e86\u90a3\u5bb6\u4f19\uff0c\u628a\u4ed6\u5e26\u5230\u5f53\u5e74\u90a3\u5ea7\u5730\u4e0b\u5ba4\u3002\u6211\u628a\u4ed6\u7ed1\u5728\u5f53\u5e74\u7ed1\u7740\u6211\u7684\u90a3\u4e2a\u6728\u5934\u67b6\u5b50\u4e0a\uff0c\u7136\u540e\u6211\u62ff\u51fa\u90a3\u4e32\u9879\u94fe\uff0c\u50cf\u4ed6\u5f53\u5e74\u5bf9\u6211\u505a\u7684\u90a3\u6837\uff0c\u4e00\u9897\u4e00\u9897\u5f80\u91cc\u585e\uff0c\u201d\u5979\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u771f\u662f\u6ca1\u7528\u7684\u7537\u4eba\u5462\uff0c\u770b\u8d77\u6765\u50cf\u718a\u4e00\u6837\u5f3a\u58ee\uff0c\u7ed3\u679c\u624d\u585e\u4e86\u4e03\u9897\uff0c\u4ed6\u5c31\u6655\u6b7b\u8fc7\u53bb\u3002\u6211\u7528\u51b7\u6c34\u628a\u4ed6\u6d47\u9192\uff0c\u7ee7\u7eed\u585e\uff0c\u4ed6\u53c8\u6655\u8fc7\u53bb\uff0c\u4e8e\u662f\u6211\u53c8\u628a\u4ed6\u6d47\u9192\uff0c\u4e00\u76f4\u5230\u628a\u6574\u6761\u9879\u94fe\u90fd\u585e\u8fdb\u53bb\u4e3a\u6b62\uff0c\u4e00\u5171\u5341\u516b\u9897\u73e0\u5b50\uff0c\u4e00\u9897\u4e0d\u591a\uff0c\u4e00\u9897\u4e0d\u5c11\u3002\u201d</p><p>\u201c\u6211\u585e\u7684\u65f6\u5019\uff0c\u4ed6\u65ad\u65ad\u7eed\u7eed\u5730\u8bf4\u8bdd\uff0c\u6c42\u6211\u9976\u8fc7\u4ed6\u4ec0\u4e48\u7684\uff0c\u6211\u6ca1\u6ce8\u610f\u542c\u3002\u7b49\u6240\u6709\u7684\u73e0\u5b50\u90fd\u585e\u8fdb\u53bb\uff0c\u6211\u544a\u8bc9\u4ed6\u8bf4\uff0c\u4ed6\u662f\u4e2a\u5929\u751f\u7684\u540c\u6027\u604b\u3002\u6211\u8bf4\u6211\u89c1\u8fc7\u65e0\u6570\u7537\u4eba\uff0c\u4ece\u6765\u6ca1\u6709\u54ea\u4e00\u4e2a\u50cf\u4ed6\u8fd9\u6837\uff0c\u5c41\u773c\u80fd\u628a\u6574\u6761\u9879\u94fe\u90fd\u541e\u8fdb\u53bb\u7684\u3002\u6211\u8bf4\u53ef\u89c1\u4ed6\u5929\u751f\u5c31\u5e94\u8be5\u88ab\u7537\u4eba\u5e72\u5c41\u80a1\u3002\u7136\u540e\u6211\u5c31\u627e\u4e86\u56db\u4e2a\u5f3a\u58ee\u7684\u7537\u4eba\uff0c\u4e00\u4eba\u7ed9\u4ed6\u4eec\u5341\u679a\u91d1\u5e01\uff0c\u8ba9\u4ed6\u4eec\u8f6e\u6d41\u6765\u5e72\u8fd9\u5bb6\u4f19\uff0c\u4e00\u76f4\u5e72\u5230\u5929\u4eae\u4e3a\u6b62\u3002\u6211\u544a\u8bc9\u90a3\u5bb6\u4f19\u8bf4\uff0c\u53ea\u8981\u4ed6\u80fd\u575a\u6301\u5230\u5929\u4eae\u8fd8\u4e0d\u6b7b\uff0c\u6211\u5c31\u9976\u8fc7\u4ed6\uff0c\u5c31\u50cf\u4ed6\u5f53\u5e74\u9976\u8fc7\u6211\u4e00\u6837\u3002\u201d</p><p>\u201c\u2026\u2026\u7ed3\u679c\u5462\uff1f\u201d</p><p>\u201c\u7ed3\u679c\u5417\uff1f\u4ed6\u6ca1\u80fd\u575a\u6301\u5230\u5929\u4eae\uff0c\u201d\u838e\u73de\u514b\u76c8\u76c8\u7b11\u7740\uff0c\u201c\u771f\u53ef\u60dc\u5462\uff0c\u6211\u8fd8\u7279\u5730\u4e3a\u4ed6\u8bf7\u4e86\u7267\u5e08\uff0c\u5b88\u5728\u65c1\u8fb9\u4e0d\u65ad\u5bf9\u4ed6\u653e\u6cbb\u7597\u672f\uff0c\u5e0c\u671b\u4ed6\u80fd\u6253\u7834\u6211\u7684\u8bb0\u5f55\u3002\u53ef\u60dc\u4ed6\u5b9e\u5728\u662f\u4e0d\u4e89\u6c14\u554a\uff0c\u773c\u770b\u79bb\u5929\u4eae\u53ea\u6709\u534a\u5c0f\u65f6\u4e86\uff0c\u4ed6\u81ea\u5df1\u54ac\u65ad\u820c\u5934\uff0c\u81ea\u6740\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u542c\u4e86\u838e\u73de\u514b\u7684\u6545\u4e8b\uff0c\u743c\u6069\u4e00\u65f6\u95f4\u90fd\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u597d\u3002\u6309\u9053\u7406\u8bf4\u5e94\u8be5\u8868\u793a\u540c\u60c5\uff0c\u7136\u800c\u5979\u5728\u8bb2\u8ff0\u7684\u65f6\u5019\uff0c\u4e00\u76f4\u5e26\u7740\u7b11\u610f\uff0c\u8ba9\u4eba\u6ca1\u6cd5\u8bf4\u51fa\u5b89\u6170\u7684\u8bed\u53e5\u3002\u4f46\u8981\u65e0\u52a8\u4e8e\u8877\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u5408\u60c5\u7406\u3002</p><p>\u5e78\u597d\uff0c\u838e\u73de\u514b\u663e\u7136\u5e76\u6ca1\u6709\u6307\u671b\u83b7\u5f97\u4ec0\u4e48\u5b89\u6170\u3002\u6216\u8005\u5bf9\u4e8e\u5979\u800c\u8a00\uff0c\u8be5\u7b97\u7684\u8d26\u5df2\u7ecf\u7b97\u6e05\uff0c\u8be5\u505a\u7684\u4e8b\u5df2\u7ecf\u505a\u5b8c\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5df2\u7ecf\u7ed3\u675f\uff0c\u6240\u4ee5\u624d\u80fd\u591f\u8fd9\u6837\u8c08\u7b11\u81ea\u82e5\u5730\u63d0\u53ca\u5427\u3002</p><p>\u201c\u54a6\uff0c\u201d\u5979\u65e0\u610f\u95f4\u77a5\u4e86\u743c\u6069\u80ef\u95f4\u4e00\u773c\uff0c\u201c\u600e\u4e48\u5b83\u53d8\u8f6f\u4e86\u3002\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u56de\u7b54\uff0c\u201c\u542c\u4e86\u4f60\u8fd9\u79cd\u6545\u4e8b\uff0c\u54ea\u4e2a\u7537\u4eba\u8fd8\u80fd\u786c\u633a\u7740\uff1f\u5c31\u7b97\u662f\u8fdc\u91ce\u5fd7\u8d35\u4e5f\u529e\u4e0d\u5230\u5427\u3002\u201d</p><p>\u201c\u8fdc\u91ce\u5fd7\u8d35\u662f\u8c01\uff1f\u201d</p><p>\u201c\u5443\uff0c\u662f\u6211\u7684\u4e00\u4e2a\u5d07\u62dc\u5076\u50cf\u3002\u201d</p><p>\u201c\u4e5f\u662f\u4e2a\u5927\u8272\u72fc\uff1f\u201d</p><p>\u201c\u55ef\u554a\u2026\u2026\u7b49\u7b49\uff0c\u201d\u743c\u6069\u6297\u8bae\uff0c\u201c\u4ec0\u4e48\u53eb\u505a\u2018\u4e5f\u2019\u662f\u4e2a\u5927\u8272\u72fc\uff1f\u201d</p><p>\u838e\u73de\u514b\u683c\u683c\u5a07\u7b11\u4e0d\u6b62\uff0c\u773c\u6ce2\u4fcf\u4e3d\u6d41\u8f6c\uff0c\u4eff\u4f5b\u52fe\u9b42\u593a\u9b44\u822c\uff0c\u5a9a\u6001\u65e0\u9650\u3002\u743c\u6069\u770b\u5f97\u76ee\u7729\u795e\u6447\uff0c\u539f\u672c\u5df2\u7ecf\u8f6f\u8db4\u8db4\u7684\u5bb6\u4f19\u4e0d\u77e5\u4e0d\u89c9\u95f4\u53c8\u6602\u9996\u633a\u80f8\u8d77\u6765\u3002\u73ca\u5609\u77a5\u4e86\u4e00\u773c\uff0c\u201c\u6211\u7d2f\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u88ab\u4f60\u6298\u817e\u5f97\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u4e86\uff0c\u56f0\u5f97\u5389\u5bb3\uff0c\u773c\u775b\u90fd\u5feb\u8981\u7741\u4e0d\u5f00\uff0c\u201d\u5979\u987a\u624b\u626f\u8fc7\u4e00\u4e2a\u6795\u5934\uff0c\u5c31\u52bf\u8eba\u4e0b\u6765\uff0c\u53c8\u62c9\u8fc7\u4e00\u6761\u6bdb\u6bef\u76d6\u5728\u8eab\u4e0a\uff0c\u201c\u4f60\u4eec\u8981\u7ee7\u7eed\u5c31\u7ee7\u7eed\uff0c\u52a8\u9759\u5c0f\u70b9\uff0c\u522b\u5435\u6211\u7761\u89c9\u3002\u201d</p><p>\u5979\u8bf4\u5b8c\u7ffb\u8fc7\u8eab\u53bb\uff0c\u7528\u80cc\u5bf9\u7740\u743c\u6069\uff0c\u4eff\u4f5b\u5f53\u771f\u7761\u53bb\u3002\u743c\u6069\u4e00\u5f00\u59cb\u8fd8\u6709\u4e9b\u7d27\u5f20\uff0c\u8fc7\u4e86\u7247\u523b\u89c1\u5979\u6ca1\u52a8\u9759\uff0c\u4fbf\u4f38\u624b\u5c06\u838e\u73de\u514b\u62c9\u4e86\u8fc7\u6765\uff0c\u4e00\u628a\u6309\u5012\uff0c\u6b63\u51c6\u5907\u633a\u67aa\u76f4\u5165\uff0c\u7a81\u7136\u65c1\u8fb9\u73ca\u5609\u53c8\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5c06\u8138\u8f6c\u8fc7\u6765\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u8fd8\u5fd8\u4e86\u63d0\u9192\u4f60\u4eec\uff0c\u522b\u73a9\u5f97\u592a\u75af\uff0c\u201d\u5979\u770b\u7740\u743c\u6069\u548c\u838e\u73de\u514b\uff0c\u5c3d\u91cf\u4fdd\u6301\u8bed\u6c14\u5e73\u9759\uff0c\u50cf\u662f\u5728\u6697\u793a\uff0c\u53c8\u50cf\u662f\u5728\u8b66\u544a\uff0c\u201c\u660e\u5929\u4e00\u5927\u65e9\u8fd8\u6709\u6b63\u4e8b\u8981\u505a\u5462\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u5782\u4e0b\u773c\u5e18\uff0c\u518d\u5ea6\u7ffb\u8fc7\u8eab\u53bb\uff0c\u7528\u6bdb\u6bef\u628a\u5934\u90fd\u8499\u4f4f\u4e86\u3002\u743c\u6069\u4f4e\u4e0b\u5934\uff0c\u6b63\u8fce\u4e0a\u838e\u73de\u514b\u542b\u7b11\u7684\u76ee\u5149\uff0c\u4e24\u4eba\u5bf9\u89c6\u7247\u523b\uff0c\u7a81\u7136\u90fd\u8f7b\u58f0\u7b11\u4e86\u8d77\u6765\u3002\u201c\u73ca\u5609\u59d0\u59d0\u771f\u7684\u975e\u5e38\u5728\u610f\u4f60\u5462\u3002\u201d\u9b45\u9b54\u6084\u58f0\u5728\u4ed6\u8033\u8fb9\u8bf4\u3002</p><p>\u4f60\u5176\u5b9e\u771f\u6b63\u60f3\u8bf4\u7684\u662f\u5979\u918b\u52b2\u771f\u5927\u5427\u2026\u2026</p><p>\u743c\u6069\u6709\u4e9b\u65e0\u5948\u5730\u8038\u8038\u80a9\uff0c\u629b\u5f00\u6240\u6709\u5ff5\u5934\uff0c\u5c06\u838e\u73de\u514b\u7684\u4e00\u6761\u817f\u62ac\u8d77\uff0c\u67b6\u5728\u80a9\u4e0a\uff0c\u7136\u540e\u51f6\u731b\u649e\u5165\u5979\u7684\u4f53\u5185\uff0c\u5fc3\u65e0\u65c1\u9a9b\u5730\u5f00\u59cb\u4eab\u53d7\u3002\u6b64\u524d\u548c\u73ca\u5609\u6b22\u597d\uff0c\u867d\u7136\u59d0\u59d0\u5df2\u7ecf\u7d2f\u5f97\u865a\u8131\uff0c\u4f46\u4ed6\u5176\u5b9e\u5374\u8fd8\u8fdc\u8fdc\u6ca1\u6709\u5f97\u5230\u6ee1\u8db3\uff0c\u53c8\u4e0d\u80fd\u53bb\u6b3a\u8d1f\u59d0\u59d0\uff0c\u81ea\u7136\u5c31\u8981\u5728\u9b45\u9b54\u8eab\u4e0a\u5168\u90e8\u53d1\u6cc4\u51fa\u6765\u4e86\u3002\u4f46\u6216\u8bb8\u662f\u56e0\u4e3a\u6015\u52a8\u9759\u592a\u5927\u60ca\u6270\u73ca\u5609\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u542c\u4e86\u838e\u73de\u514b\u7684\u6545\u4e8b\u540e\u5fc3\u6709\u9980\u60b8\uff0c\u4ed6\u4e0b\u610f\u8bc6\u5730\u6bd4\u4ee5\u524d\u6e29\u67d4\u4e86\u8bb8\u591a\u3002\u838e\u73de\u514b\u88ab\u4ed6\u538b\u5728\u8eab\u4e0b\uff0c\u66f4\u662f\u66f2\u610f\u9022\u8fce\uff0c\u67d4\u5a9a\u8ba8\u597d\uff0c\u5979\u59ff\u5bb9\u7f8e\u8c8c\u8fdc\u4e0d\u5982\u73ca\u5609\uff0c\u4f46\u80dc\u5728\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u52a0\u4e0a\u9b45\u9b54\u7684\u4f53\u8d28\u7279\u6b8a\uff0c\u4e91\u96e8\u95f4\u81ea\u6709\u4e00\u79cd\u98ce\u6d41\u5a49\u8f6c\uff0c\u4ee4\u4eba\u6c89\u9189\u5176\u4e2d\uff0c\u604b\u604b\u4e0d\u820d\u3002\u4e24\u4eba\u62b5\u6b7b\u7f20\u7ef5\uff0c\u76f4\u5230\u534a\u591c\u624d\u7b97\u5174\u5c3d\uff0c\u6c89\u6c89\u7761\u53bb\u3002</p></article>"}}}]);
