(window.webpackJsonp=window.webpackJsonp||[]).push([[459],{482:function(p){p.exports={chapterTitle:null,title:"\u7b2c324\u7ae0 \u690d\u7269\u56ed\u91cc\u7684\u5fe7\u601d",content:"<article><h2>\u7b2c324\u7ae0 \u690d\u7269\u56ed\u91cc\u7684\u5fe7\u601d</h2><p>\u3000\u3000\u5f20\u606a\u5f88\u5e73\u9759\u7684\u770b\u5f85\u9526\u57ce\u5c06\u4e0e\u6d77\u6cf0\u5408\u4f5c\u7684\u8fd9\u4ef6\u4e8b\uff0c\u867d\u7136\u8bf4\u5546\u573a\u5982\u6218\u573a\uff0c\u4f46\u662f\u5728\u5546\u573a\u4e0a\uff0c\u5374\u5f88\u96be\u5982\u610f\u7684\u5c06\u5bf9\u624b\u7684\u6240\u6709\u9000\u8def\u90fd\u5c01\u6b7b\uff0c\u9664\u975e\u83b7\u5f97\u7edd\u5bf9\u7684\u5f3a\u52bf\uff0c\u4e0d\u8fc7\u7b49\u5230\u90a3\u65f6\uff0c\u4ee5\u5f3a\u51cc\u5f31\u53c8\u4e0d\u662f\u5f20\u606a\u7684\u98ce\u683c\uff0c\u53ea\u5e0c\u671b\u8d75\u9526\u8363\u7236\u5b50\u4ee5\u540e\u80fd\u5b89\u5206\u5b88\u5df1\u3001\u77e5\u9053\u4e9b\u89c4\u77e9\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u53ef\u80fd\u6027\u5b9e\u5728\u4e0d\u9ad8\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f97\u77e5\u8c22\u5251\u5357\u5230\u9526\u6e56\u5e76\u6ca1\u6709\u591a\u8c08\u5e76\u8d2d\u6216\u64a4\u8d44\u7684\u4e8b\u60c5\uff0c\u53ea\u505c\u4e86\u7559\u4e00\u4f1a\u513f\u65f6\u95f4\u4eba\u5c31\u56de\u5bbe\u9986\u4e86\uff0c\u5f20\u606a\u7981\u4e0d\u4f4f\u60f3\uff1a\u8c22\u5251\u5357\u4e0e\u8d75\u9526\u8363\u7236\u5b50\u8017\u5728\u4e00\u8d77\u7684\u65f6\u95f4\u4f3c\u4e4e\u592a\u957f\u4e86\uff0c\u4ed6\u4eec\u5728\u5408\u8ba1\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\u4e0b\u5348\uff0c\u5f20\u606a\u4ece\u7535\u5b50\u5de5\u4e1a\u56ed\u56de\u6765\u7684\u8def\u4e0a\u518d\u6b21\u770b\u5230\u8d75\u9526\u8363\u7684\u7687\u51a0\uff0c\u4e24\u8f66\u76f8\u9519\u65f6\uff0c\u770b\u5230\u8c22\u5251\u5357\u4e5f\u5750\u5728\u91cc\u9762\uff0c\u5f80\u524d\u8d70\u9664\u4e86\u7535\u5b50\u5de5\u4e1a\u56ed\u3001\u519c\u7530\uff0c\u8fd8\u6709\u5c31\u662f\u5c0f\u6c5f\u5317\u5cb8\u7684\u51e0\u6392\u5782\u67f3\u3001\u767d\u6768\uff0c\u76f8\u4fe1\u8c22\u5251\u5357\u4e0d\u4f1a\u7279\u610f\u53bb\u770b\u521d\u6625\u6c5f\u6c34\u7684\u98ce\u5149\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u53bb\u7535\u5b50\u5de5\u4e1a\u56ed\u770b\u4ec0\u4e48\uff1f\u201d\u5468\u6e38\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\uff0c\u4ed6\u6700\u5148\u770b\u5230\u7687\u51a0\u8f66\u91cc\u7684\u8c22\u5251\u5357\u3001\u8d75\u9526\u8363\u4ed6\u4eec\uff0c\u4ed6\u5fc3\u91cc\u63a0\u8fc7\u4e00\u4e1d\u9634\u5f71\uff0c\u6bd5\u7adf\u6d77\u5dde\u8ddf\u7740\u7231\u8fbe\u7535\u5b50\u505a\u5f71\u789f\u673a\u7684\u5341\u4e00\u5bb6\u5382\uff0c\u60df\u6709\u9526\u57ce\u96c6\u56e2\u65d7\u4e0b\u7684\u90a3\u95f4\u7535\u5b50\u5382\u80fd\u5bf9\u7231\u8fbe\u7535\u5b50\u4ea7\u751f\u5a01\u80c1\uff0c\u800c\u6d77\u6cf0\u5728\u7701\u5185\u62e5\u6709\u96c4\u539a\u7684\u6e20\u9053\u8d44\u6e90\uff0c\u8c22\u5251\u5357\u4e0e\u8d75\u9526\u8363\u4e00\u8d77\u53bb\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u603b\u8ba9\u4eba\u6709\u4e0d\u8be6\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u603b\u4e0d\u80fd\u8ddf\u8c22\u5251\u5357\u88c5\u4f5c\u4e0d\u89c1\uff0c\u9694\u7740\u8f66\u7a97\u7b11\u4e86\u7b11\uff0c\u5374\u6ca1\u6709\u6253\u62db\u547c\u7684\u610f\u613f\uff0c\u60f3\u7740\u7ec8\u4e8e\u8ddf\u5bb6\u65cf\u8d70\u4e0a\u4e24\u6761\u622a\u7136\u4e0d\u540c\u7684\u9053\u8def\u4e86\u3002</p><p>\u3000\u3000\u201c\u8c22\u5bb6\u8001\u516d\u4f3c\u4e4e\u770b\u5230\u6211\u7684\u8f6f\u808b\u4e86\u2026\u2026\u201d\u5f20\u606a\u7ebf\u6761\u660e\u4fca\u7684\u8138\u5e9e\u6d6e\u73b0\u8f7b\u6d45\u7684\u7b11\u610f\uff0c\u6df1\u9083\u7684\u661f\u7738\u6ce8\u89c6\u7740\u8f66\u7a97\u5916\u660e\u51c0\u7684\u5929\u7a7a\uff0c\u4e00\u4efd\u7ecf\u6d4e\u91d1\u878d\u5468\u520a\u6401\u5728\u4ed6\u7684\u819d\u76d6\u4e0a\uff0c\u5468\u520a\u7ffb\u5728\u4e00\u7bc7\u5173\u4e8e\u5eb7\u4f73\u96c6\u56e2\u4e8e\u4e09\u516b\u5987\u5973\u8282\u8fd9\u5929\u5168\u94b1\u8c03\u4f4e\u65d7\u4e0b\u6240\u6709\u7cfb\u5217\u5f69\u7535\u552e\u4ef7\u7684\u62a5\u9053\uff0c\u8fd9\u662f\u56fd\u5185\u5f69\u7535\u53f2\u9996\u5ea6\u66b4\u53d1\u7684\u5168\u9762\u4ef7\u683c\u6218\uff0c\u8d77\u56e0\u662f\u4e24\u4e2a\u6708\u524d\u56db\u5ddd\u957f\u8679\u96c6\u56e2\u5bf9\u65d7\u4e0b\u5f69\u7535\u4ea7\u54c1\u5927\u89c4\u6a21\u7684\u964d\u4ef7\u3002\u8fd9\u662f\u56fd\u5185\u5f69\u7535\u4ea7\u4e1a\u53d1\u5c55\u5341\u4f59\u5e74\u4ee5\u6765\u9996\u5148\u66b4\u53d1\u7684\u5168\u884c\u4e1a\u4ef7\u683c\u6218\uff0c\u60c5\u51b5\u6bd4\u4e24\u5343\u5e74\u7a81\u7834\u4f4e\u9650\u4ef7\u7684\u4ef7\u683c\u6218\u7565\u597d\u4e00\u4e9b\uff0c\u53ea\u662f\u6253\u7834\u884c\u4e1a\u7684\u9ad8\u5229\u6da6\u73b0\u72b6\uff0c\u52a0\u901f\u884c\u4e1a\u6574\u5408\u7684\u8fdb\u7a0b\uff0c\u4f46\u662f\u6574\u4e2a\u884c\u4e1a\u8fd8\u4fdd\u6301\u7740\u4e00\u5b9a\u7684\u8d62\u5229\u80fd\u529b\uff0c\u4e00\u76f4\u5230\u4e24\u5343\u5e74\u66f4\u6b8b\u9177\u7684\u4ef7\u683c\u6218\u66b4\u53d1\u3002\u76f8\u4fe1\u73b0\u5728\u8fd8\u6ca1\u6709\u4eba\u610f\u8bc6\u5230\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u4ef7\u683c\u6218\u4f1a\u6765\u5f97\u66f4\u76f4\u63a5\u3001\u66f4\u6df7\u4e71\uff0c\u201c\u7fa4\u9cc4\u7ede\u6740\uff0c\u4e0d\u8fc7\u591a\u4e86\u4e00\u5934\u800c\u5df2\uff0c\u6ca1\u5fc5\u8981\u770b\u5f97\u591a\u4e25\u91cd\uff0c\u8981\u6765\u7684\uff0c\u603b\u5f52\u6765\u7684\uff0c\u4e0d\u662f\u9526\u57ce\u4e0e\u6d77\u6cf0\uff0c\u8fd8\u6709\u5176\u4ed6\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9526\u57ce\u4e0b\u9762\u7684\u90a3\u95f4\u5382\uff0c\u8001\u82cf\u8ba9\u4eba\u770b\u8fc7\uff0c\u867d\u7136\u5c0f\uff0c\u4f46\u662f\u8d1f\u8d23\u7684\u7ecf\u7406\u5f88\u6709\u624d\u5e72\uff0c\u662f\u505a\u7ba1\u7406\u7684\u6599\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d75\u9526\u8363\u8fd9\u4eba\u867d\u7136\u86ee\u6a2a\u65e0\u7406\uff0c\u8fd8\u662f\u505a\u6df7\u6df7\u51fa\u8eab\uff0c\u4e0d\u8fc7\u4e5f\u7684\u786e\u6709\u8fc7\u4eba\u4e4b\u5904\u2026\u2026\u201d\u5f20\u606a\u8f7b\u7b11\u9053\uff0c\u201c\u5584\u9022\u8fce\u5df4\u7ed3\u81ea\u4e0d\u7528\u8bf4\uff0c\u4e01\u7acb\u5c71\u5f53\u5e02\u59d4\u4e66\u8bb0\u65f6\uff0c\u8d75\u9526\u8363\u5728\u5e02\u91cc\u6298\u817e\u4e0d\u4e86\uff0c\u8fd8\u662f\u5f88\u8ba4\u771f\u7684\u505a\u8fc7\u5b9e\u4e1a\uff0c\u9526\u57ce\u4e0b\u9762\u7684\u6cb9\u8102\u516c\u53f8\u6548\u76ca\u5c31\u4e00\u76f4\u4e0d\u9519\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u665a\u6674\u3001\u5468\u6e38\u6253\u7b97\u5148\u53bb\u7535\u5b50\u5de5\u4e1a\u56ed\u8f6c\u4e86\u4e00\u5708\uff0c\u672c\u6253\u7b97\u518d\u53bb\u8c61\u5c71\u5357\u5d16\u4e0b\u7684\u690d\u7269\u56ed\u770b\u4e00\u773c\uff0c\u6ca1\u60f3\u5230\u5728\u8def\u4e0a\u53c8\u9047\u4e0a\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u5468\u6e38\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u8ddf\u8c22\u603b\u8fd8\u8981\u4e0d\u8981\u53bb\u690d\u7269\u56ed\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf4\u597d\u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u201d\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u201c\u8981\u662f\u522b\u4eba\u4e00\u6709\u52a8\u4f5c\uff0c\u6211\u4eec\u5c31\u614c\u4e86\u624b\u811a\uff0c\u8fd9\u65e5\u5b50\u5c31\u6ca1\u6cd5\u8fc7\u4e0b\u53bb\u4e86\uff0c\u8fd9\u8001\u677f\u505a\u5f97\u5c31\u592a\u6ca1\u6ecb\u5473\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u4f60\u4eec\u662f\u8001\u677f\uff0c\u6211\u8ba9\u5085\u4fca\u5148\u9001\u6211\u56de\u53bb\u2026\u2026\u201d\u5468\u6e38\u65e0\u5948\u7684\u82e6\u7b11\u7740\uff0c\u201c\u90a3\u4e24\u4e2a\u5728\u91d1\u4f70\u5229\u514b\u62c9\u514b\u7eb8\u4e1a\u96c6\u56e2\u5de5\u4f5c\u8fc7\u7684\u65c5\u7f8e\u7559\u5b66\u751f\uff0c\u6761\u4ef6\u90fd\u5df2\u7ecf\u8c08\u59a5\uff0c\u6700\u540e\u6572\u5b9a\u524d\uff0c\u662f\u4e0d\u662f\u5b89\u6392\u4ed6\u4eec\u6765\u4e00\u8d9f\u6d77\u5dde\u8ba9\u4f60\u89c1\u4e00\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u65e2\u7136\u80fd\u5728\u91d1\u4f70\u5229\u5750\u5230\u4e2d\u5c42\u7ba1\u7406\u7684\u4f4d\u5b50\u4e0a\uff0c\u90fd\u4f1a\u6709\u4e9b\u672c\u9886\uff0c\u4f60\u5148\u53bb\u4e0a\u6d77\u540c\u4ed6\u4eec\u89c1\u9762\uff0c\u5c06\u4f60\u7684\u5370\u8c61\u544a\u8bc9\u6211\uff0c\u6211\u518d\u51b3\u5b9a\u8981\u4e0d\u8981\u62bd\u7a7a\u53bb\u4e0a\u6d77\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u627e\u4e0d\u5230\u5408\u610f\u7684\u4eba\u624b\uff0c\u5c31\u592a\u8ba9\u4eba\u5934\u75bc\u4e86\u3002\u201d\u5468\u6e38\u6487\u5634\u7b11\u7740\u8bf4\uff0c\u201c\u516d\u5341\u4e07\u7684\u5e74\u85aa\uff0c\u5927\u6982\u53ef\u4ee5\u62ff\u5230\u65b0\u95fb\u5a92\u4f53\u4e0a\u597d\u597d\u7684\u7092\u4f5c\u4e00\u756a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u7b49\u524d\u671f\u7684\u5de5\u4f5c\u90fd\u5b8c\u6210\u4e4b\u540e\uff0c\u518d\u8003\u8651\u7092\u4f5c\u7684\u4e8b\u60c5\uff0c\u4f60\u5012\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u4f60\u7684\u8001\u672c\u884c\u554a\u3002\u54e6\uff0c\u8fd9\u6b21\u8ba9\u6768\u4e91\u5e2e\u4f60\u9876\u4e0b\u7231\u8fbe\u90a3\u91cc\u7684\u4e8b\u60c5\uff0c\u5218\u660e\u8f89\u4f1a\u4e0d\u4f1a\u6709\u60f3\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5fc3\u91cc\u591a\u5c11\u4f1a\u6709\u4e9b\u60f3\u6cd5\uff0c\u7ed9\u4ed6\u4e00\u6bb5\u65f6\u95f4\u4f1a\u8c03\u6574\u597d\u81ea\u5df1\u7684\u5fc3\u6001\u7684\uff0c\u6bd5\u7adf\u4f4d\u5b50\u53ea\u6709\u4e00\u4e2a\uff0c\u53ea\u80fd\u8ba9\u66f4\u5408\u9002\u7684\u4eba\u4e0a\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5728\u8c61\u5c71\u5357\u5d16\u4e0b\u641e\u6765\u4e8c\u767e\u4ea9\u5730\u5206\u7ed9\u5927\u5bb6\u5408\u529b\u5efa\u4e00\u4e2a\u5c0f\u89c4\u6a21\u7684\u690d\u7269\u56ed\uff0c\u6ce8\u518c\u4e86\u4e00\u5bb6\u516c\u53f8\u6765\u7ba1\u7406\u690d\u7269\u56ed\uff0c\u4ece\u56ed\u6797\u5c40\u4e0b\u5c5e\u7684\u4e8b\u4e1a\u5355\u4f4d\u8bf7\u4e86\u4e00\u4e2a\u5b66\u56ed\u6797\u7ba1\u7406\u7684\u4eba\u6765\u8d1f\u8d23\u6253\u7406\uff0c\u516c\u53f8\u8fd0\u8425\u7684\u7ecf\u8d39\u7531\u4f17\u4eba\u5206\u644a\uff0c\u5f53\u7136\u662f\u5f20\u606a\u51fa\u5927\u5934\uff0c\u5927\u5bb6\u5148\u7b79\u94b1\u5728\u8c61\u5c71\u5357\u5d16\u4e0b\u4fee\u4e86\u4e00\u6761\u76f4\u8fbe\u690d\u7269\u56ed\u7684\u6c34\u6ce5\u752c\u9053\uff0c\u4e0d\u9700\u8981\u4e00\u4e0b\u96e8\uff0c\u8f66\u8fdb\u53bb\u6e85\u4e00\u8f66\u7684\u6ce5\u70b9\u5b50\u3002</p><p>\u3000\u3000\u6cbf\u6728\u6805\u680f\u6d12\u4e0b\u8336\u6811\u7c7d\uff0c\u6625\u8282\u8fc7\u540e\u6d77\u5dde\u7684\u5929\u6c14\u5c31\u5927\u5e45\u56de\u5347\uff0c\u4e5f\u662f\u4eca\u5e74\u7684\u6625\u8282\u6bd4\u8f83\u665a\u7684\u7f18\u6545\uff0c\u5750\u5728\u8f66\u4e0a\u5c31\u53ef\u4ee5\u770b\u89c1\u8def\u65c1\u6728\u6805\u680f\u4e0b\u7684\u8336\u6811\u7c7d\u957f\u51fa\u65b0\u82bd\uff0c\u5f20\u606a\u5bf9\u665a\u6674\u8bf4\uff1a\u201c\u5230\u590f\u5929\uff0c\u8fd9\u91cc\u5c31\u53ef\u4ee5\u957f\u6210\u6811\u84e0\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u665a\u6674\u5728\u690d\u7269\u56ed\u524d\u4e0b\u4e86\u8f66\uff0c\u8ba9\u5085\u4fca\u5148\u9001\u5468\u6e38\u56de\u5e02\u91cc\uff0c\u516c\u53f8\u4e0b\u73ed\u524d\u6765\u63a5\u4ed6\u4eec\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u7684\u7ecf\u7406\u5e26\u7740\u4eba\u53bb\u82d7\u5703\u516c\u53f8\u770b\u82b1\u82d7\u51fa\u53bb\uff0c\u56ed\u5b50\u91cc\u6709\u4e9b\u5de5\u4eba\u5728\u690d\u571f\uff0c\u4e01\u69d0\u6625\u8282\u524d\u5c06\u4ed6\u5728\u5b89\u5fbd\u8001\u5bb6\u52a1\u519c\u7684\u7236\u6bcd\u63a5\u5230\u6d77\u5dde\uff0c\u5c31\u628a\u4ed6\u4eec\u5b89\u7f6e\u5728\u690d\u7269\u56ed\u91cc\u4f3a\u5019\u4ed6\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\u7684\u90a3\u7247\u7af9\u6797\uff0c\u5012\u662f\u975e\u5e38\u5408\u9002\u3002\u5f20\u606a\u4e0e\u665a\u6674\u8fc7\u53bb\u5148\u8ddf\u4e24\u4e2a\u8001\u4eba\u62db\u547c\u4e00\u58f0\u3002\u5f20\u606a\u5bf9\u7af9\u5b50\u7684\u4e86\u89e3\u5f88\u662f\u532e\u4e4f\uff0c\u800c\u4e14\u4e01\u69d0\u7236\u6bcd\u79fb\u690d\u90fd\u4e0d\u662f\u65b0\u82d7\uff0c\u636e\u8bf4\u51e0\u573a\u6625\u96e8\u8fc7\u540e\u80fd\u770b\u5230\u7af9\u7b0b\u8282\u8282\u957f\u8d77\uff0c\u53ef\u662f\u73b0\u5728\u5374\u6ca1\u4ec0\u4e48\u770b\u5934\uff0c\u4ee5\u4e3a\u5730\u8fd8\u8352\u7740\u5462\u3002</p><p>\u3000\u3000\u4ece\u8c61\u5c71\u5357\u5d16\u4e0b\u62ff\u5730\u6700\u521d\u7684\u610f\u613f\u662f\u7ed9\u5510\u5a67\u3001\u665a\u6674\u5efa\u653b\u7470\u56ed\uff0c\u73ab\u7470\u4e5f\u6709\u79cd\uff0c\u5927\u7ea6\u4e8c\u4e09\u5341\u4ea9\u7684\u56ed\u5b50\uff0c\u8fd9\u4e5f\u662f\u690d\u7269\u56ed\u6700\u65e9\u5efa\u7684\u56ed\u5b50\uff0c\u65b0\u82d7\u90fd\u5206\u79cd\u597d\uff0c\u53ef\u4ee5\u671f\u5f85\u82b1\u671f\u4e86\u3002</p><p>\u3000\u3000\u201c\u5c0f\u516d\u8fd9\u4e24\u5929\u90fd\u6ca1\u6709\u8ba4\u771f\u8c08\u64a4\u8d44\u7684\u4e8b\u60c5\u2026\u2026\u201d\u665a\u6674\u7ad9\u5728\u7530\u95f4\uff0c\u6709\u4e9b\u62c5\u5fe7\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\uff0c\u4f60\u62c5\u5fc3\u4f1a\u6709\u53d8\u6545\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u516d\u7684\u8111\u5b50\u5f88\u806a\u660e\uff0c\u4ed6\u8ddf\u8d75\u9526\u8363\u5728\u4e00\u8d77\u7684\u65f6\u95f4\u957f\u4e86\uff0c\u5f88\u591a\u4e8b\u60c5\uff0c\u53ea\u6015\u90fd\u4f1a\u8ba9\u4ed6\u60f3\u900f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u867d\u7136\u8eb2\u5728\u80cc\u540e\u9634\u4eba\u7684\u611f\u89c9\u5f88\u597d\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e00\u76f4\u8eb2\u5728\u80cc\u540e\u554a\uff0c\u8fd9\u591a\u5c11\u6709\u4e9b\u65e0\u8010\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u7b11\u7740\uff0c\u8ba4\u771f\u7684\u51dd\u89c6\u7740\u665a\u6674\u3002\u8fd9\u51e0\u5929\u5929\u6c14\u5f02\u5e38\u56de\u5347\uff0c\u4e2d\u5348\u7a7f\u6625\u88c5\u90fd\u4f1a\u89c9\u5f97\u70ed\uff0c\u665a\u6674\u5c06\u7edb\u7d27\u8272\u7684\u8584\u5462\u5b50\u6302\u5728\u624b\u81c2\u4e0a\uff0c\u53cc\u624b\u62b1\u80f8\u7ad9\u5728\u7530\u95f4\uff0c\u79c0\u4e3d\u7684\u957f\u53d1\u76d8\u4e86\u8d77\u6765\uff0c\u4e30\u8174\u767d\u7699\u7684\u8138\u5e9e\u67d4\u7f8e\uff0c\u7709\u773c\u5982\u6708\uff0c\u6563\u7740\u6210\u719f\u5987\u4eba\u7684\u98ce\u97f5\uff0c\u80f8\u812f\u9ad8\u9ad8\u7684\u633a\u8d77\uff0c\u8170\u80a2\u6bd4\u5c11\u5973\u7565\u9971\u6ee1\uff0c\u770b\u4e0a\u53bb\u5374\u6709\u4f3c\u4e4e\u7684\u8f6f\u5f39\u89e6\u611f\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u4ead\u4ead\u800c\u7acb\uff0c\u53cc\u817f\u4fee\u76f4\uff0c\u5927\u817f\u7d27\u7ef7\u7740\uff0c\u5b8c\u5168\u6ca1\u6709\u8fc7\u4e86\u4e09\u5341\u5c81\u7684\u5973\u4eba\u7684\u677e\u677e\u57ae\u57ae\u3002</p><p>\u3000\u3000\u201c\u53bb\u6c5f\u5824\u4e0a\u8d70\u8d70\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u665a\u6674\u76f4\u63a5\u7a7f\u8fc7\u7530\u5785\uff0c\u4ece\u5927\u95e8\u7ed5\u6709\u4e9b\u8fdc\uff0c\u5f20\u606a\u5148\u7ffb\u8fc7\u6728\u6805\u680f\uff0c\u665a\u6674\u5c06\u5916\u5957\u6401\u6728\u6805\u680f\u4e0a\uff0c\u5b66\u5f20\u606a\u90a3\u91cc\u722c\u4e0a\u7684\u6728\u6805\u680f\uff0c\u5f20\u606a\u4f38\u624b\u6402\u7740\u5979\u7684\u8170\uff0c\u8ba9\u5979\u4e00\u624b\u6491\u81ea\u5df1\u80a9\u8180\u4e0a\uff0c\u60f3\u8ba9\u5979\u8df3\u4e0b\u6765\uff0c\u6ca1\u60f3\u5230\u665a\u6674\u5c41\u80a1\u632a\u5750\u5230\u4ed6\u80a9\u8180\u4e0a\uff0c\u4ece\u4ed6\u6000\u91cc\u6ed1\u4e0b\u6765\u3002</p><p>\u3000\u3000\u7d27\u62e5\u7740\u665a\u6674\u4e30\u8174\u6210\u719f\u7684\u5a07\u8eaf\uff0c\u8eab\u4f53\u6d8c\u51fa\u4e9b\u611f\u89c9\uff0c\u4f38\u624b\u5728\u5979\u4e30\u633a\u81c0\u90e8\u6478\u4e86\u4e00\u628a\uff0c\u5fc3\u91cc\u611f\u6168\uff1a\u5973\u4eba\u8ddf\u9e26\u7247\u4f3c\u7684\uff0c\u4e00\u6cbe\u5c31\u5bb9\u6613\u4e0a\u763e\u3002\u4e4b\u524d\u8fd8\u6ca1\u4ec0\u4e48\uff0c\u81ea\u4ece\u4e0a\u56de\u5728\u7701\u57ce\u7684\u9752\u5c71\u516c\u5bd3\u4e0e\u665a\u6674\u53d1\u751f\u5173\u7cfb\uff0c\u5bf9\u5979\u7f8e\u5999\u8bf1\u4eba\u7684\u8eab\u4f53\u5c31\u6709\u4e9b\u6e34\u671b\uff0c\u4e0d\u518d\u662f\u90a3\u4e48\u5bb9\u6613\u904f\u5236\u4e86\uff0c\u504f\u504f\u5728\u6d77\u5dde\u7edd\u96be\u627e\u5230\u5077\u5403\u7684\u673a\u4f1a\uff0c\u5973\u4eba\u4e09\u5341\u5982\u72fc\u8c7a\uff0c\u4e0d\u6653\u5f97\u665a\u6674\u4f1a\u6709\u4ec0\u4e48\u611f\u89c9\u3002\u8fd9\u4e48\u60f3\u7740\uff0c\u624b\u53c8\u843d\u5230\u665a\u6674\u4e30\u6ee1\u7684\u8089\u81c0\u4e0a\uff0c\u5f39\u6027\u5341\u8db3\uff0c\u7a0d\u7528\u529b\u538b\u5c31\u611f\u89c9\u5230\u8089\u81c0\u5f80\u5916\u7ef7\uff0c\u90fd\u8bf4\u5973\u4eba\u8fc7\u4e86\u4e8c\u5341\u4e94\u5c81\u5c31\u4f1a\u51fa\u73b0\u7b2c\u4e00\u9053\u9c7c\u5c3e\u7eb9\uff0c\u5f20\u606a\u53ef\u4ee5\u8ba4\u771f\u5728\u665a\u6674\u7684\u8eab\u4f53\u4e0a\u4ed4\u7ec6\u627e\u4e86\u4e2a\u904d\uff0c\u90fd\u6ca1\u6709\u770b\u5230\u90a3\u4e2a\u5730\u65b9\u6709\u8981\u957f\u51fa\u7b2c\u4e00\u9053\u76b1\u7eb9\u7684\u8ff9\u8c61\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u8fd9\u8eab\u582a\u6bd4\u5c11\u5973\u7684\u808c\u80a4\u662f\u5982\u4f55\u4fdd\u517b\u7684\u3002</p><p>\u3000\u3000\u201c\u6709\u4e9b\u75d2\u2026\u2026\u201d\u665a\u6674\u5c06\u5f20\u606a\u7684\u602a\u624b\u62e8\u5f00\uff0c\u6015\u56ed\u5b50\u91cc\u7684\u5de5\u4eba\u77a7\u89c1\uff0c\u8fd8\u6709\u5c31\u662f\u5979\u7684\u81c0\u90e8\u4e00\u6328\u4e0a\u5f20\u606a\u7684\u624b\u5c31\u6709\u4e9b\u654f\u611f\uff0c\u4e0d\u5f97\u4e0d\u96c6\u4e2d\u6ce8\u610f\u529b\u60f3\u5176\u4ed6\u7684\u4e8b\u60c5\uff0c\u201c\u4f60\u771f\u4e0d\u6015\u4ed6\u4eec\u80cc\u540e\u91cc\u6363\u9b3c\uff1f\u201d</p></article>"}}}]);
