(window.webpackJsonp=window.webpackJsonp||[]).push([[459],{481:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e5d\u8282 \u89e6\u624b\u602a",content:"<article><h2>\u7b2c\u56db\u5341\u4e5d\u8282 \u89e6\u624b\u602a</h2><p>\u5728\u62c9\u52a0\u7387\u9886\u624b\u4e0b\u548c\u5b89\u535a\u7406\u5c55\u5f00\u6fc0\u6218\u65f6\uff0c\u743c\u6069\u6084\u6084\u9000\u5230\u4e86\u89d2\u843d\u7684\u9634\u5f71\u4e2d\uff0c\u4f4e\u58f0\u6307\u793a\u8299\u857e\u72c4\u4e3a\u4ed6\u65bd\u52a0\u201c\u7cbe\u51c6\u5c04\u51fb\u201d\uff0c\u63a5\u7740\u542f\u52a8\u4e86\u89e3\u79bb\u672f\u3002\u5728\u9884\u8a00\u672f\u7684\u5e2e\u52a9\u4e0b\uff0c\u4ed6\u989d\u5916\u82b1\u4e86\u6bd4\u5e73\u5e38\u591a\u4e24\u500d\u7684\u65f6\u95f4\uff0c\u6700\u7ec8\u6210\u529f\u5730\u5c06\u5b8c\u6210\u4e86\u76ee\u6807\u7684\u9501\u5b9a\u5de5\u4f5c\u3002\u800c\u5b89\u535a\u7406\u5bf9\u6b64\u4e00\u65e0\u6240\u77e5\uff0c\u5979\u751a\u81f3\u538b\u6839\u5c31\u6ca1\u6ce8\u610f\u5230\u743c\u6069\u8fd9\u4e2a\u4eba\u3002</p><p>\u7136\u540e\u743c\u6069\u51fa\u624b\u4e86\u3002</p><p>\u78a7\u7eff\u7684\u5c04\u7ebf\u81ea\u7d2b\u6c34\u6676\u6cd5\u6756\u9876\u7aef\u53d1\u51fa\uff0c\u4e0d\u504f\u4e0d\u501a\u6b63\u4e2d\u5b89\u535a\u7406\u7709\u5fc3\u3002\u6d77\u795e\u7684\u53cc\u773c\u731b\u7136\u7741\u5927\uff0c\u4eff\u4f5b\u4e0d\u6562\u7f6e\u4fe1\uff0c\u9ed1\u8272\u7a7a\u6d1e\u5728\u5979\u7684\u989d\u5934\u4e0a\u6084\u65e0\u58f0\u606f\u5730\u51fa\u73b0\uff0c\u8d77\u521d\u53ea\u6709\u6307\u5c16\u5927\u5c0f\uff0c\u4f46\u6025\u901f\u65cb\u8f6c\u6269\u5927\uff0c\u6df7\u6d4a\u7684\u6ce5\u6d46\u4ece\u9ed1\u6d1e\u4e2d\u6c69\u6c69\u6d8c\u51fa\uff0c\u4eff\u4f5b\u8840\u6db2\uff0c\u6d41\u6dcc\u5728\u5730\u3002\u77ed\u77ed\u51e0\u79d2\u949f\u91cc\uff0c\u5979\u7684\u6574\u4e2a\u8eab\u4f53\uff0c\u5305\u62ec\u5934\u9885\u3001\u624b\u81c2\u3001\u8eaf\u5e72\u3001\u817f\u811a\u2026\u2026\u8f6c\u773c\u95f4\u5168\u90fd\u201c\u878d\u5316\u201d\uff0c\u53d8\u6210\u4e86\u4e00\u5927\u6ee9\u6ce5\u6d46\uff0c\u6dcc\u5728\u5730\u677f\u4e0a\uff0c\u8fd8\u4e0d\u65ad\u5192\u7740\u6c14\u6ce1\uff0c\u4eff\u4f5b\u6cbc\u6cfd\u3002</p><p>\u201c\u6210\u529f\u4e86\uff1f\u201d</p><p>\u9634\u9b42\u57ce\u8bf8\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4e00\u65f6\u95f4\u90fd\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\uff0c\u4f46\u7b49\u4e86\u7247\u523b\u8fd8\u6ca1\u6709\u4efb\u4f55\u52a8\u9759\uff0c\u6e10\u6e10\u4fbf\u90fd\u5f00\u59cb\u5174\u594b\u6b22\u547c\u8d77\u6765\u3002\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u51e1\u4eba\u80fd\u591f\u51fb\u6740\u795e\u7947\uff0c\u8fd9\u90fd\u662f\u503c\u5f97\u65e0\u6bd4\u9a84\u50b2\u81ea\u8c6a\u7684\u4e8b\u60c5\uff0c\u5373\u4fbf\u662f\u5728\u6700\u8f89\u714c\u7684\u8010\u745f\u65f6\u4ee3\uff0c\u5927\u5965\u672f\u5e08\u4eec\u4e5f\u6ca1\u51e0\u4e2a\u80fd\u505a\u5230\u8fd9\u70b9\u3002\u7136\u800c\u743c\u6069\u5374\u53cc\u7709\u7d27\u9501\uff0c\u5fc3\u4e2d\u7684\u4e0d\u5b89\u611f\u8d8a\u6765\u8d8a\u91cd\u3002</p><p>\u548c\u5176\u4ed6\u4eba\u4e0d\u540c\uff0c\u743c\u6069\u53ef\u662f\u4eb2\u773c\u89c1\u8bc6\u8fc7\u795e\u7947\u5a01\u80fd\u7684\u3002\u5f53\u65e5\u5728\u963f\u683c\u62c9\u9686\uff0c\u9634\u9b42\u57ce\u548c\u585e\u5c14\u501f\u7740\u6b7b\u6708\u6cd5\u73e0\u5e03\u4e0b\u9677\u9631\uff0c\u5f15\u8bf1\u5deb\u5996\u4e4b\u795e\u7ef4\u6c99\u4f26\u7684\u5316\u8eab\u964d\u4e34\u7269\u8d28\u754c\uff0c\u743c\u6069\u5f53\u65f6\u4f5c\u4e3a\u8bf1\u9975\u5168\u7a0b\u65c1\u89c2\u4e86\u7ecf\u8fc7\u3002\u5728\u90a3\u4e00\u4ed7\u4e2d\uff0c\u7ef4\u6c99\u4f26\u9762\u5bf9\u62c9\u6c83\u514b\u3001\u8428\u624e\u65af\u5766\u548c\u5e03\u96f7\u7eb3\u65af\uff0c\u4ee5\u4e00\u654c\u4e09\uff0c\u72b9\u81ea\u4e0d\u843d\u4e0b\u98ce\uff08\u5f53\u7136\u4e0d\u6392\u9664\u62c9\u6c83\u514b\u7b49\u4eba\u793a\u5f31\u8bf1\u654c\u7684\u56e0\u7d20\uff09\uff0c\u6700\u540e\u662f\u6b27\u51ef\u5077\u88ad\uff0c\u7528\u5f11\u795e\u5315\u9996\u5c06\u4ed6\u9489\u4f4f\uff0c\u65b9\u624d\u6700\u7ec8\u6467\u6bc1\u3002</p><p>\u7ef4\u6c99\u4f26\u53ea\u662f\u5854\u6d1b\u65af\u7684\u795e\u4ec6\uff0c\u5728\u8bf8\u795e\u4e2d\u4f4d\u9636\u6700\u4f4e\uff0c\u901a\u5e38\u79f0\u4e4b\u4e3a\u201c\u534a\u795e\u201d\u3002\u800c\u5b89\u535a\u7406\u867d\u7136\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5389\u5bb3\u89d2\u8272\uff0c\u5728\u8bf8\u795e\u4e2d\u5927\u6982\u5904\u4e8e\u4e2d\u4e0b\u6e38\u7684\u4f4d\u7f6e\uff0c\u4f46\u6bd4\u8d77\u7ef4\u6c99\u4f26\u6765\u603b\u8be5\u5f3a\u4e0a\u5341\u4e03\u516b\u500d\u3002\u7ef4\u6c99\u4f26\u5f53\u65e5\u9762\u5bf9\u7684\u662f\u4e09\u4e2a\u5927\u5deb\u5e08\u52a0\u4e00\u4e2a\u66fe\u7ecf\u662f\u5947\u68b0\u5e08\u7684\u708e\u9b54\uff0c\u4ee5\u53ca\u4e00\u67c4\u4e13\u95e8\u7528\u6765\u5bf9\u4ed8\u795e\u7947\u7684\u795e\u5668\u5315\u9996\uff0c\u88ab\u6302\u6389\u53ef\u4ee5\u7406\u89e3\uff1b\u5b89\u535a\u7406\u5982\u4eca\u9762\u5bf9\u7684\u662f\u5341\u51e0\u4e2a\u5deb\u5e08\uff0c\u6570\u91cf\u4e0a\u56fa\u7136\u591a\u4e86\u51e0\u500d\uff0c\u8d28\u91cf\u4e0a\u5374\u662f\u5dee\u4e86\u4e00\u5927\u622a\uff0c\u5c40\u9762\u5e94\u8be5\u5b8c\u5168\u662f\u4e00\u8fb9\u5012\u624d\u5bf9\u3002\u7136\u800c\u4ece\u6218\u6597\u4e00\u5f00\u59cb\uff0c\u5b89\u535a\u7406\u5c31\u8868\u73b0\u5f97\u5f88\u5f31\uff0c\u5982\u4eca\u66f4\u662f\u88ab\u4e00\u51fb\u89e3\u79bb\u672f\u5c31\u5b8c\u5168\u6467\u6bc1\uff0c\u8fd9\u4e5f\u4f3c\u4e4e\u592a\u4e0d\u5408\u60c5\u7406\u4e86\u70b9\u5427\u3002</p><p>\u96be\u9053\u8bf4\uff0c\u773c\u524d\u7684\u5b89\u535a\u7406\u662f\u5723\u8005\u5f62\u6001\uff0c\u5e76\u975e\u5316\u8eab\uff1f</p><p>\u867d\u7136\u90fd\u662f\u795e\u7947\u5177\u73b0\u5728\u7269\u8d28\u754c\u7684\u65b9\u5f0f\uff0c\u4f46\u5316\u8eab\u548c\u5723\u8005\u662f\u6709\u7740\u663e\u8457\u5dee\u522b\u7684\uff0c\u524d\u8005\u662f\u795e\uff0c\u53ea\u662f\u53d7\u5230\u7269\u8d28\u754c\u4f4d\u9762\u89c4\u5219\u7684\u538b\u5236\uff0c\u529b\u91cf\u5927\u5e45\u5ea6\u524a\u5f31\u7f62\u4e86\uff1b\u540e\u8005\u4ece\u4e25\u683c\u610f\u4e49\u4e0a\u8bf4\u5176\u5b9e\u5df2\u7ecf\u4e0d\u662f\u795e\u4e86\uff0c\u53ea\u80fd\u7b97\u662f\u201c\u62e5\u6709\u5e9e\u5927\u795e\u529b\u7684\u51e1\u4eba\u201d\u3002\u795e\u7947\u96be\u5bf9\u4ed8\uff0c\u51e1\u4eba\u5c31\u5bb9\u6613\u641e\u5b9a\u591a\u4e86\uff0c\u5341\u516d\u5e74\u524d\u7684\u52a8\u8361\u65f6\u4ee3\uff0c\u8bf8\u795e\u4e4b\u6240\u4ee5\u6b7b\u6389\u4e00\u7247\uff0c\u539f\u56e0\u5c31\u5728\u4e8e\u4ed6\u4eec\u5f53\u65f6\u90fd\u662f\u5904\u4e8e\u5723\u8005\u5f62\u6001\u3002</p><p>\u8fd9\u4e48\u8bf4\u4f3c\u4e4e\u4e5f\u8bf4\u5f97\u901a\uff0c\u7136\u800c\u2026\u2026\u743c\u6069\u4ece\u6765\u4e0d\u8bb0\u5f97\u81ea\u5df1\u7684\u89e3\u79bb\u672f\u4f1a\u6709\u628a\u4eba\u5316\u4f5c\u6ce5\u6d46\u7684\u6548\u679c\u554a\u3002\u96be\u9053\u4e0d\u5c0f\u5fc3\u653e\u9519\u4e86\u6cd5\u672f\uff0c\u7528\u7684\u5176\u5b9e\u662f\u5316\u77f3\u4e3a\u6ce5\uff1f\u4f46\u5316\u77f3\u4e3a\u6ce5\u53ea\u5bf9\u77f3\u5934\u6709\u6548\uff0c\u5bf9\u4eba\u662f\u534a\u70b9\u6ca1\u7528\u7684\u3002</p><p>\u4ed6\u770b\u5411\u56db\u5468\uff0c\u53d1\u73b0\u540c\u4f34\u90fd\u5df2\u7ecf\u653e\u677e\u4e0b\u6765\uff0c\u552f\u6709\u62c9\u52a0\u8fd8\u76ef\u7740\u539f\u672c\u5b89\u535a\u7406\u6240\u7ad9\u7acb\u7684\u5730\u65b9\uff0c\u770b\u7740\u90a3\u6ee9\u4e0d\u65ad\u5192\u6ce1\u9f13\u6c14\u7684\u6ce5\u6d46\uff0c\u795e\u60c5\u4e25\u8083\u4e2d\u9690\u9690\u900f\u7740\u7d27\u5f20\u3002\u201c\u90fd\u522b\u5927\u610f\uff01\u201d\u4ed6\u63d0\u9192\uff0c\u201c\u4e8b\u60c5\u6ca1\u8fd9\u4e48\u5bb9\u6613\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4e00\u6761\u957f\u6ee1\u5012\u523a\u7684\u9ed1\u8272\u89e6\u624b\u81ea\u90a3\u6ee9\u6ce5\u6d46\u4e2d\u9661\u7136\u5f39\u8d77\uff0c\u5982\u87d2\u86c7\u6602\u9996\u822c\u8dc3\u5728\u534a\u7a7a\uff0c\u7167\u7740\u62c9\u52a0\u5f53\u5934\u62bd\u4e0b\u3002\u62c9\u52a0\u65e9\u6709\u51c6\u5907\uff0c\u95ea\u8eab\u4fbf\u907f\u4e86\u5f00\u53bb\uff0c\u7136\u800c\u8fd8\u4e0d\u7b49\u4ed6\u7ad9\u5b9a\uff0c\u53c8\u4e00\u6761\u89e6\u624b\u4ece\u6ce5\u6d46\u4e2d\u751f\u51fa\uff0c\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\u62e6\u8170\u6a2a\u626b\u3002\u62c9\u52a0\u8eb2\u95ea\u4e0d\u53ca\uff0c\u88ab\u89e6\u624b\u6b63\u6b63\u62bd\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u7a7a\u6c14\u6e05\u8106\u7206\u54cd\uff0c\u5deb\u5e08\u7684\u8eab\u5f71\u50cf\u6ce1\u6cab\u822c\u7834\u788e\u6d88\u901d\uff0c\u7d27\u63a5\u7740\uff0c\u4ed6\u5728\u4e8c\u5341\u5c3a\u5916\u7684\u5730\u65b9\u51fa\u73b0\u3002</p><p>\u201c\u4e0d\u662f\u4f20\u9001\uff0c\u5f88\u50cf\u62df\u50cf\u672f\uff0c\u4f46\u4e5f\u4e0d\u592a\u5bf9\u2026\u2026\u201d</p><p>\u743c\u6069\u731c\u6d4b\u7740\u62c9\u52a0\u8fd9\u4e00\u62db\u9003\u547d\u6280\u80fd\u662f\u4ec0\u4e48\uff0c\u4f46\u5f88\u5feb\u4ed6\u4fbf\u4e27\u5931\u4e86\u8fd9\u4efd\u95f2\u60c5\u9038\u81f4\uff0c\u66f4\u591a\u7684\u89e6\u624b\u4ece\u6ce5\u6d46\u4e2d\u751f\u51fa\uff0c\u63a5\u4e8c\u8fde\u4e09\uff0c\u50cf\u97ad\u96e8\u822c\u6b64\u8d77\u5f7c\u4f0f\u5730\u62bd\u6253\u7740\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u4eec\u3002\u5728\u573a\u7684\u4eba\u65e2\u7136\u6562\u8fdb\u6765\uff0c\u81ea\u7136\u90fd\u9884\u5148\u505a\u4e86\u5145\u8db3\u51c6\u5907\uff0c\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u81f3\u5c11\u53e0\u52a0\u4e86\u4e09\u9053\u9632\u5fa1\u6cd5\u672f\uff0c\u8fd9\u8fd8\u4e0d\u7b97\u4e0a\u5404\u79cd\u9b54\u6cd5\u88c5\u5907\u7684\u8f85\u52a9\u5f3a\u5316\uff0c\u4f46\u5374\u6ca1\u4eba\u6562\u786c\u63a5\u7740\u8fd9\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u89e6\u624b\u653b\u51fb\uff0c\u4e00\u4e2a\u4e2a\u4e1c\u8eb2\u897f\u95ea\uff0c\u88ab\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\u3002</p><p>\u201c\u6211\u9760\uff0c\u96be\u9053\u5b89\u535a\u91cc\u7684\u539f\u578b\u662f\u4e2a\u89e6\u624b\u602a\u4e48\uff1f\u201d</p><p>\u743c\u6069\u5fcd\u4e0d\u4f4f\u5492\u9a82\u8d77\u6765\uff0c\u4f5c\u4e3a\u4e00\u540d\u6709\u7406\u60f3\u6709\u9053\u5fb7\u7684\u9752\u5e74\uff0c\u4ed6\u5bf9\u89e6\u624b\u5411\u6765\u6df1\u6076\u75db\u7edd\uff0c\u8fde\u52a8\u753b\u90fd\u4e0d\u770b\u6b64\u79cd\u7c7b\u578b\u7684\uff0c\u773c\u524d\u7684\u8fd9\u5e45\u573a\u666f\uff0c\u5b9e\u5728\u4ee4\u4ed6\u65e0\u6cd5\u4e0d\u6d8c\u8d77\u67d0\u4e9b\u90aa\u6076\u8054\u60f3\u3002</p><p>\u4eff\u4f5b\u662f\u4e3a\u4e86\u5370\u8bc1\u4ed6\u7684\u60f3\u6cd5\uff0c\u4e00\u4e2a\u5706\u5706\u7684\u7269\u4f53\u4ece\u6ce5\u6d46\u4e2d\u6162\u6162\u201c\u62f1\u201d\u4e86\u8d77\u6765\uff0c\u5148\u662f\u53ea\u5982\u9505\u76d6\uff0c\u63a5\u7740\u4fbf\u6025\u5267\u81a8\u80c0\uff0c\u5f53\u5b83\u6700\u7ec8\u5b8c\u5168\u73b0\u51fa\u8eab\u5f62\u65f6\uff0c\u5df2\u7ecf\u63a5\u8fd1\u534a\u4e2a\u7bee\u7403\u573a\u5927\u5c0f\u3002\u90a3\u771f\u662f\u4e00\u53ea\u5e9e\u5927\u7684\u602a\u7269\uff0c\u7c89\u84dd\u8272\u7684\u76ae\u80a4\u4e0a\u5370\u7740\u9053\u9053\u6697\u9752\u82b1\u7eb9\uff0c\u56db\u53ea\u5c0f\u5c0f\u7684\u773c\u73e0\u4ece\u4e0a\u5230\u4e0b\u4e00\u5b57\u6392\u5f00\uff0c\u51e0\u5341\u6839\u9ed1\u8272\u89e6\u624b\u8054\u7ed3\u7740\u8eab\u4f53\uff0c\u56e0\u4e3a\u80a4\u8272\u7684\u5dee\u5f02\uff0c\u50cf\u662f\u786c\u51d1\u4e0a\u53bb\u7684\u4e00\u822c\uff0c\u663e\u5f97\u683c\u5916\u8be1\u5f02\u548c\u6ed1\u7a3d\u3002\u7136\u800c\u6b64\u65f6\u6b64\u523b\uff0c\u6ca1\u6709\u4eba\u8fd8\u80fd\u7b11\u5f97\u51fa\u6765\u3002</p><p>\u201c\u4e4c\u8d3c\uff1f\u201d</p><p>\u4e0d\u6b62\u4e00\u4e2a\u4eba\u8111\u4e2d\u95ea\u8fc7\u8fd9\u4e2a\u8bcd\uff0c\u4ece\u5916\u5f62\u4e0a\u770b\uff0c\u786e\u5b9e\u548c\u4e4c\u8d3c\u9887\u4e3a\u76f8\u4f3c\uff0c\u4f46\u666e\u901a\u7684\u4e4c\u8d3c\u663e\u7136\u4e0d\u4f1a\u957f\u56db\u4e2a\u773c\u775b\uff0c\u66f4\u4e0d\u4f1a\u6709\u51e0\u5341\u6761\u89e6\u624b\u3002\u7d27\u63a5\u7740\uff0c\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u4ece\u602a\u7269\u4f53\u5185\u53d1\u51fa\uff0c\u66f4\u52a0\u6e05\u695a\u5730\u8bf4\u660e\u4e86\u5b83\u7684\u8eab\u4efd\u3002</p><p>\u201c\u5351\u52a3\u7684\u51e1\u4eba\uff0c\u5c45\u7136\u903c\u5f97\u6211\u663e\u51fa\u8fd9\u6837\u4e11\u964b\u7684\u5f62\u6001\uff01\u201d\u5b89\u535a\u7406\u5486\u54ee\u7740\uff0c\u58f0\u82e5\u96f7\u9e23\uff0c\u201c\u4f60\u4eec\u901a\u901a\u90fd\u8981\u4e3a\u6b64\u4ed8\u51fa\u4ee3\u4ef7\uff01\u201d</p><p>\u62dc\u6258\uff0c\u96be\u4e0d\u6210\u4f60\u81ea\u4ee5\u4e3a\u53d8\u6210\u4eba\u5f62\u7684\u65f6\u5019\u5c31\u4e0d\u4e11\u964b\u4e86\u5417\uff1f</p><p>\u743c\u6069\u7684\u8bc4\u4ef7\u53ea\u662f\u653e\u5728\u5fc3\u91cc\uff0c\u5e76\u672a\u8bf4\u51fa\u53e3\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6ca1\u6709\u8fd9\u4e2a\u4f59\u6687\u3002\u4e00\u6839\u89e6\u624b\u5728\u7a7a\u4e2d\u7565\u7565\u76d8\u65cb\uff0c\u968f\u5373\u671d\u7740\u4ed6\u8fd9\u4e2a\u65b9\u5411\u76f4\u5c04\u8fc7\u6765\uff0c\u5e26\u8d77\u5f3a\u70c8\u7684\u7834\u7a7a\u58f0\u3002\u743c\u6069\u76ee\u5149\u4e00\u51dd\uff0c\u6307\u5c16\u5728\u7a7a\u6c14\u4e2d\u6025\u901f\u52fe\u52d2\u51fa\u900f\u660e\u7684\u7b26\u6587\uff0c\u5316\u4f5c\u4e00\u9762\u94c1\u84dd\u8272\u7684\u5de8\u5927\u5854\u76fe\u3002\u89e6\u624b\u523a\u4e2d\u5854\u76fe\uff0c\u5c06\u5b83\u51fb\u7834\u6210\u51e0\u5757\uff0c\u4f46\u529b\u9053\u5374\u4e5f\u56e0\u6b64\u524a\u5f31\u4e86\u5927\u534a\uff0c\u838e\u73de\u514b\u4e58\u673a\u524d\u51b2\uff0c\u624b\u4e2d\u77ed\u5251\u4e00\u6325\u800c\u4e0b\uff0c\u5c06\u89e6\u624b\u62e6\u8170\u5207\u65ad\u3002</p><p>\u201c\u554a\uff01\u201d</p><p>\u521a\u521a\u89e3\u51b3\u6389\u4e00\u53ea\u89e6\u624b\uff0c\u4fbf\u542c\u89c1\u8299\u857e\u72c4\u7684\u60ca\u53eb\u58f0\u4ece\u8eab\u540e\u54cd\u8d77\uff0c\u743c\u6069\u5306\u5fd9\u56de\u8eab\u770b\u53bb\uff0c\u53d1\u73b0\u53c8\u6709\u4e00\u6839\u89e6\u624b\u50cf\u86c7\u4e00\u6837\u8d34\u7740\u5730\u9762\u6084\u65e0\u58f0\u606f\u5730\u6e38\u52a8\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u4ece\u4fa7\u9762\u903c\u8fd1\u3002\u8299\u857e\u72c4\u9f13\u8db3\u52c7\u6c14\u4e3e\u8d77\u8c61\u7259\u6cd5\u6756\uff0c\u4e00\u4e32\u8d64\u7ea2\u98de\u5f39\u4ece\u4e2d\u53d1\u51fa\uff0c\u7cbe\u51c6\u5730\u7838\u5728\u89e6\u624b\u4e0a\uff0c\u4f46\u5374\u53ea\u662f\u8ba9\u5b83\u7f29\u4e86\u4e00\u7f29\uff0c\u968f\u5373\u4eff\u4f5b\u88ab\u6fc0\u6012\u822c\uff0c\u4ee5\u66f4\u5feb\u7684\u901f\u5ea6\u524d\u51b2\u8fc7\u6765\u3002</p><p>\u743c\u6069\u521a\u521a\u91ca\u653e\u4e86\u4e00\u4e2a\u6cd5\u672f\uff0c\u4e00\u65f6\u95f4\u6765\u4e0d\u53ca\u518d\u5ea6\u65bd\u6cd5\uff0c\u4ed6\u6478\u51fa\u4e00\u679a\u68cb\u5b50\uff0c\u6b63\u51c6\u5907\u7528\u5b83\u53bb\u6321\u4e0a\u4e00\u6321\uff0c\u9661\u7136\u8033\u8fb9\u4f20\u6765\u523a\u8033\u7684\u5c16\u5578\u58f0\uff0c\u50cf\u662f\u9f7f\u8f6e\u9ad8\u901f\u8fd0\u8f6c\u5207\u5f00\u7a7a\u6c14\uff0c\u4e00\u76f4\u6c89\u9ed8\u7684\u8299\u8389\u5a05\u53cc\u624b\u9ad8\u9ad8\u626c\u8d77\uff0c\u56db\u53ea\u7d2b\u9ed1\u8272\u900f\u660e\u5706\u789f\u4ece\u7ea4\u7ea4\u7389\u6307\u95f4\u98de\u65cb\u800c\u51fa\uff0c\u7eb5\u6a2a\u5207\u5272\uff0c\u5c06\u89e6\u624b\u62e6\u8170\u65a9\u65ad\u6210\u51e0\u622a\u3002</p><p>\u201c\u5f88\u5f3a\u554a\u3002\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u6697\u5fd6\u7740\uff0c\u8299\u8389\u5a05\u7684\u8868\u73b0\u9887\u4e3a\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\uff0c\u8fd9\u4e00\u624b\u653b\u51fb\u6cd5\u672f\u867d\u7136\u4e0d\u77e5\u9053\u540d\u79f0\uff0c\u4f46\u5355\u7eaf\u770b\u5916\u89c2\u7684\u8bdd\uff0c\u8db3\u53ef\u4ee5\u548c\u5deb\u5e08\u7684\u529b\u573a\u5251\u76f8\u63d0\u5e76\u8bba\u4e86\u3002\u838e\u5c14\u7684\u7267\u5e08\uff0c\u539f\u6765\u5982\u6b64\u64c5\u957f\u6218\u6597\u4e48\u3002</p><p>\u63a5\u8fde\u4e24\u6839\u89e6\u624b\u88ab\u5207\u65ad\uff0c\u663e\u7136\u8ba9\u5b89\u535a\u7406\u63d0\u9ad8\u4e86\u5bf9\u743c\u6069\u8fd9\u51e0\u4e2a\u4eba\u7684\u91cd\u89c6\uff0c\u66f4\u591a\u7684\u89e6\u624b\u547c\u5578\u7740\u98de\u5c04\u7ede\u7f20\u800c\u6765\u3002\u603b\u7b97\u743c\u6069\u4e00\u5f00\u59cb\u51c6\u5907\u5de5\u4f5c\u505a\u5f97\u8fd8\u7b97\u5145\u5206\uff0c\u63a5\u8fde\u7528\u5404\u79cd\u9632\u5fa1\u6cd5\u672f\uff0c\u5168\u90e8\u62b5\u6321\u4e86\u4e0b\u6765\uff0c\u4f46\u4e5f\u611f\u5230\u8d8a\u6765\u8d8a\u5403\u529b\u3002\u5b89\u535a\u91cc\u663e\u9732\u51fa\u89e6\u624b\u602a\u5f62\u6001\u4e4b\u540e\uff0c\u653b\u51fb\u529b\u9aa4\u7136\u589e\u5f3a\uff0c\u51e0\u5341\u6839\u89e6\u624b\u72c2\u98ce\u66b4\u96e8\u822c\u62bd\u6253\uff0c\u4e00\u5f00\u59cb\u8fd8\u53ea\u662f\u5355\u7eaf\u7684\u7269\u7406\u653b\u51fb\uff0c\u6e10\u6e10\u5730\u4fbf\u9644\u4e0a\u4e86\u5404\u79cd\u9b54\u6cd5\u6548\u679c\uff0c\u95ea\u7535\u548c\u706b\u7130\u5728\u89e6\u624b\u4e0a\u7f2d\u7ed5\uff0c\u5bd2\u51b0\u548c\u5f3a\u9178\u81ea\u89e6\u624b\u4e2d\u55b7\u51fa\uff0c\u6240\u6709\u7684\u5012\u523a\u7684\u5c16\u7aef\u90fd\u6cdb\u8d77\u4e86\u84dd\u5e7d\u5e7d\u7684\u5149\u6cfd\u3002\u800c\u9634\u9b42\u57ce\u8bf8\u4eba\u7684\u9632\u5fa1\u6cd5\u672f\u5374\u5728\u6025\u5267\u5730\u51cf\u5f31\u635f\u8017\uff0c\u6b64\u6d88\u5f7c\u957f\u4e4b\u4e0b\uff0c\u6218\u5c40\u5df2\u7ecf\u5f00\u59cb\u53d1\u751f\u4e25\u91cd\u4e0d\u4e50\u89c2\u7684\u503e\u659c\u3002</p><p>\u4e00\u540d\u9634\u9b42\u57ce\u5deb\u5e08\u63a5\u8fde\u88ab\u51fb\u4e2d\uff0c\u9b54\u6cd5\u9632\u5fa1\u6d88\u8017\u6b86\u5c3d\uff0c\u4ed6\u6b63\u5f85\u65bd\u6cd5\u8865\u5145\uff0c\u4e00\u6761\u89e6\u624b\u659c\u659c\u63a0\u6765\uff0c\u4ece\u4ed6\u7684\u53f3\u81c2\u4e0a\u94a9\u4e0b\u4e00\u5927\u5757\u76ae\u8089\u3002</p><p>\u201c\u554a\uff01\u201d</p><p>\u5deb\u5e08\u75db\u6781\u60e8\u547c\uff0c\u4f24\u53e3\u5904\u7684\u8840\u8089\u77ac\u95f4\u7126\u9ed1\u51dd\u7ed3\uff0c\u663e\u7136\u662f\u88ab\u9ad8\u6e29\u707c\u70e7\uff0c\u8868\u9762\u5374\u53c8\u8986\u76d6\u4e0a\u4e86\u4e00\u5c42\u6676\u83b9\u51b0\u971c\uff0c\u4ed6\u7684\u6574\u4e2a\u4eba\u5728\u534a\u79d2\u949f\u5185\u50cf\u9f13\u6c14\u4e00\u822c\u5b8c\u5168\u6d6e\u80bf\u8d77\u6765\uff0c\u4e94\u5b98\u626d\u66f2\uff0c\u9762\u76ee\u5168\u975e\uff0c\u5168\u8eab\u80c0\u5927\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u5730\u6b65\uff0c\u4ee5\u81f3\u4e8e\u5bbd\u677e\u7684\u957f\u888d\u90fd\u7ed9\u786c\u751f\u751f\u6491\u88c2\uff0c\u539f\u672c\u82cd\u767d\u7684\u76ae\u80a4\u6210\u4e86\u7d2b\u9ed1\u8272\uff0c\u56e0\u4e3a\u81a8\u80c0\u62c9\u4f38\u800c\u53d8\u5f97\u8584\u800c\u900f\u660e\uff0c\u80fd\u591f\u6e05\u6e05\u695a\u695a\u770b\u5230\u4f53\u8868\u4e0b\u7684\u8840\u7ba1\u7ecf\u8109\uff0c\u65e2\u6050\u6016\u53c8\u6076\u5fc3\u3002</p><p>\u4e00\u540d\u540c\u4f34\u5feb\u6b65\u4e0a\u524d\uff0c\u53cc\u624b\u865a\u5f20\uff0c\u53e3\u4e2d\u6025\u901f\u8bf5\u5492\uff0c\u4f3c\u4e4e\u60f3\u8981\u91ca\u653e\u4e00\u4e2a\u9a71\u6563\u6cd5\u672f\uff0c\u4f46\u5374\u88ab\u62c9\u52a0\u963b\u6b62\u4e86\u3002\u201c\u522b\u9760\u8fd1\u4ed6\uff01\u201d\u62c9\u52a0\u5927\u558a\u3002</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u5deb\u5e08\u5df2\u7ecf\u81a8\u80c0\u5230\u6781\u9650\u7684\u8eab\u4f53\u7ec8\u4e8e\u65e0\u6cd5\u627f\u53d7\u8fd9\u79cd\u81ea\u5185\u800c\u5916\u7684\u5e9e\u5927\u538b\u529b\uff0c\u7830\u5730\u4e00\u58f0\u7206\u88c2\u5f00\u6765\uff0c\u8165\u81ed\u7684\u8840\u8089\u548c\u7126\u9ed1\u7684\u788e\u9aa8\u88ab\u6c14\u6d6a\u51b2\u5411\u7a7a\u4e2d\uff0c\u7136\u540e\u7eb7\u7eb7\u843d\u4e0b\uff0c\u50cf\u662f\u4e0b\u4e86\u4e00\u573a\u66b4\u96e8\u3002\u62c9\u52a0\u6cd5\u6756\u987f\u5730\uff0c\u4e00\u5c42\u900f\u660e\u5149\u7f69\u81ea\u5730\u9762\u6d8c\u8d77\uff0c\u6025\u901f\u5347\u9ad8\uff0c\u5c06\u9634\u9b42\u57ce\u8bf8\u4eba\u7b3c\u7f69\u5728\u5185\u3002\u8840\u8089\u4e4b\u96e8\u843d\u5728\u5149\u7f69\u4e0a\uff0c\u53d1\u51fa\u55e4\u55e4\u55e4\u55e4\u7684\u5f3a\u9178\u8150\u8680\u58f0\uff0c\u6700\u7ec8\u672a\u80fd\u7a7f\u900f\u5149\u7f69\uff0c\u5b83\u4eec\u5feb\u901f\u5e72\u67af\u3001\u98ce\u5316\uff0c\u53d8\u6210\u4e00\u64ae\u64ae\u9ed1\u8272\u7c89\u672b\uff0c\u7136\u540e\u6084\u7136\u6563\u53bb\u3002</p><p>\u8f6c\u773c\u4e4b\u95f4\u53c8\u6298\u635f\u4e00\u540d\u90e8\u4e0b\uff0c\u62c9\u52a0\u7684\u8138\u8272\u96be\u770b\u5f97\u65e0\u4ee5\u590d\u52a0\uff0c\u4f46\u4ed6\u80fd\u591f\u88ab\u6d3e\u6765\u4e3b\u6301\u6b64\u4e8b\uff0c\u81ea\u7136\u4e5f\u4e0d\u662f\u6cdb\u6cdb\u4e4b\u8f88\u3002\u5fc3\u4e2d\u867d\u7136\u607c\u6012\u81f3\u6781\uff0c\u53cd\u800c\u8d8a\u53d1\u9547\u5b9a\u4e0b\u6765\u3002\u4ed6\u62ac\u8d77\u53f3\u624b\uff0c\u5c48\u8d77\u98df\u6307\uff0c\u8ba9\u6234\u7740\u7684\u84dd\u5b9d\u77f3\u6212\u6307\u5bf9\u51c6\u524d\u65b9\uff0c\u5ff5\u51fa\u53e3\u4ee4\uff0c\u4e03\u8272\u5149\u7ebf\u81ea\u6212\u6307\u4e0a\u6f2b\u5c04\u800c\u51fa\uff0c\u51dd\u6210\u4e00\u9762\u6d41\u5149\u6ea2\u5f69\u7684\u8679\u5149\u9ad8\u5899\uff0c\u5c06\u5b89\u535a\u7406\u548c\u9634\u9b42\u57ce\u8bf8\u4eba\u6682\u65f6\u9694\u5f00\u3002</p><p>\u51e0\u5341\u6761\u89e6\u624b\u75af\u72c2\u5730\u62bd\u6253\u649e\u51fb\u7740\u8679\u5149\u5899\uff0c\u60f3\u8981\u5c06\u5b83\u9707\u788e\uff0c\u4f46\u4e00\u65f6\u95f4\u5374\u65e0\u53ef\u5948\u4f55\u3002\u62c9\u52a0\u91ca\u653e\u51fa\u7684\u8fd9\u9053\u201c\u8679\u5149\u6cd5\u5899\u201d\u5e76\u4e0d\u662f\u5b8c\u5168\u7248\u672c\uff0c\u6301\u7eed\u65f6\u95f4\u975e\u5e38\u77ed\uff0c\u53ea\u6709\u5341\u4e8c\u79d2\u949f\u2014\u2014\u4f46\u5728\u8fd9\u5341\u4e8c\u79d2\u949f\u4e4b\u5185\uff0c\u5b83\u5c31\u50cf\u8679\u5149\u6cd5\u7403\u4e00\u6837\uff0c\u9664\u975e\u7528\u5927\u88c2\u89e3\u5f3a\u884c\u786c\u8f70\uff0c\u6216\u8005\u7528\u4e03\u79cd\u5bf9\u5e94\u7684\u6cd5\u672f\u9010\u4e2a\u6d88\u89e3\uff0c\u5426\u5219\u662f\u8fd1\u4e4e\u65e0\u654c\u7684\u3002\u63a5\u7740\u8fd9\u4e2a\u95f4\u9699\uff0c\u62c9\u52a0\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5927\u559d\u51fa\u58f0\uff1a\u201c\u53ec\u5524\u56db\u53f7\uff01\u201d</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u201c\u53ec\u5524\u56db\u53f7\u201d\uff0c\u8fd9\u663e\u7136\u662f\u67d0\u79cd\u6697\u8bed\uff0c\u6709\u4e09\u540d\u5deb\u5e08\u95fb\u8a00\u7acb\u523b\u7ad9\u4e86\u51fa\u6765\uff0c\u5217\u6210\u4e00\u4e2a\u4e0d\u5bf9\u79f0\u7684\u949d\u4e09\u89d2\u5f62\uff0c\u968f\u5373\u540c\u65f6\u65bd\u6cd5\u8bf5\u5492\u3002\u94f6\u7070\u3001\u6e56\u84dd\u548c\u8d64\u7ea2\u8272\u7684\u4e09\u79cd\u9b54\u6cd5\u7075\u7ebf\u5206\u522b\u4ece\u4ed6\u4eec\u7684\u811a\u4e0b\u5c04\u51fa\uff0c\u5728\u5730\u9762\u4e0a\u4ea4\u9519\u3001\u78b0\u649e\u3001\u5f39\u5c04\u3001\u7ea0\u7f20\uff0c\u6700\u7ec8\u7f16\u7ec7\u6210\u76f4\u5f84\u8d85\u8fc7\u4e09\u5341\u5c3a\u7684\u5de8\u5927\u5706\u5f62\u9b54\u6cd5\u9635\u3002\u5176\u4ed6\u5deb\u5e08\u6216\u8005\u8fdb\u5165\u9b54\u6cd5\u9635\uff0c\u5360\u636e\u4e00\u65b9\uff0c\u6216\u8005\u5728\u65c1\u8fb9\u5b88\u62a4\u7b49\u5f85\uff0c\u663e\u7136\u8bad\u7ec3\u6709\u7d20\u3002</p><p>\u9752\u84dd\u8272\u90aa\u708e\u5728\u9b54\u6cd5\u9635\u7684\u4e2d\u592e\u718a\u718a\u71c3\u70e7\uff0c\u626d\u66f2\u7684\u9ed1\u8272\u9aa8\u9abc\u4ece\u4e2d\u751f\u957f\u800c\u51fa\uff0c\u6784\u6210\u4e00\u6247\u76f4\u8fbe\u7a79\u9876\u7684\u5927\u95e8\u3002\u72f0\u72de\u7684\u4f4e\u543c\u58f0\u4ece\u95e8\u7684\u53e6\u4e00\u4fa7\u54cd\u8d77\uff0c\u4eff\u4f5b\u6765\u81ea\u65e0\u5c3d\u6df1\u6e0a\u7684\u6700\u5e95\u5c42\uff0c\u7d27\u63a5\u7740\uff0c\u4e00\u53ea\u5e9e\u5927\u7684\u602a\u7269\u4ece\u9aa8\u9abc\u4e4b\u95e8\u4e2d\u873f\u8712\u6e38\u51fa\uff0c\u5979\u7684\u4e0a\u534a\u8eab\u662f\u8d64\u88f8\u7684\u5996\u8273\u7f8e\u5973\uff0c\u4e0b\u534a\u8eab\u662f\u6c34\u6876\u7c97\u7ec6\u9ed1\u8272\u86c7\u5f62\uff0c\u91d1\u9ec4\u8272\u7684\u53cc\u773c\u6676\u83b9\u5254\u900f\uff0c\u4eff\u4f5b\u7409\u7483\uff0c\u4e73\u5c16\u6234\u7740\u4e24\u53ea\u91d1\u8272\u7ec6\u73af\uff0c\u516b\u53ea\u80f3\u818a\u4e0a\u6234\u7740\u51e0\u5341\u53ea\u5927\u5927\u5c0f\u5c0f\u7684\u624b\u956f\u548c\u81c2\u73af\uff0c\u53ee\u53ee\u5f53\u5f53\uff0c\u6e05\u8106\u4f5c\u54cd\uff0c\u638c\u4e2d\u5206\u522b\u63e1\u7740\u91cd\u5251\u3001\u5f2f\u5200\u3001\u77ed\u621f\u3001\u957f\u67aa\u7b49\u516b\u79cd\u6b66\u5668\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u7433\u7405\u6ee1\u76ee\u3002</p><p>\u201c\u516d\u81c2\u86c7\u9b54\u2026\u2026\u8fd8\u662f\u83c1\u82f1\u86c7\u9b54\u3002\u201d</p><p>\u5728\u4e0b\u5c42\u754c\u5f85\u8fc7\u534a\u5e74\uff0c\u4eb2\u8eab\u53c2\u52a0\u8fc7\u8840\u6218\uff0c\u743c\u6069\u5982\u4eca\u4e5f\u80fd\u7b97\u662f\u534a\u4e2a\u6076\u9b54\u5b66\u4e13\u5bb6\u4e86\u3002\u4ed6\u4e00\u773c\u4fbf\u8ba4\u51fa\u4e86\u9634\u9b42\u57ce\u5deb\u5e08\u4eec\u53ec\u5524\u51fa\u7684\u602a\u7269\uff1a\u516d\u81c2\u86c7\u9b54\uff0c\u5728\u6df1\u6e0a\u5f53\u4e2d\u5904\u4e8e\u7b2c\u4e8c\u4f4d\u9636\uff0c\u4ec5\u6b21\u4e8e\u6700\u9ad8\u7ea7\u7684\u5df4\u6d1b\u708e\u9b54\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u8fd9\u53ea\u86c7\u9b54\u8fd8\u5df2\u7ecf\u8fdb\u5316\u5230\u4e86\u201c\u83c1\u82f1\u201d\u9636\u6bb5\uff0c\u6240\u4ee5\u624d\u4f1a\u989d\u5916\u53c8\u591a\u957f\u51fa\u4e24\u53ea\u624b\u81c2\uff0c\u518d\u8fdb\u4e00\u6b65\u7684\u8bdd\u5979\u5c31\u80fd\u8131\u80ce\u6362\u9aa8\uff0c\u8715\u53d8\u4e3a\u708e\u9b54\u4e86\u3002</p><p>\u86c7\u9b54\u51fa\u73b0\u5728\u9b54\u6cd5\u9635\u4e2d\u5fc3\uff0c\u5979\u56db\u987e\u5f20\u671b\u7740\uff0c\u76ee\u9732\u51f6\u5149\uff0c\u4f46\u8eab\u4f53\u660e\u663e\u88ab\u67d0\u79cd\u529b\u91cf\u538b\u5236\u4f4f\u4e86\uff0c\u534a\u6b65\u65e0\u6cd5\u79fb\u52a8\u3002\u62c9\u52a0\u770b\u7740\u86c7\u9b54\uff0c\u505a\u4e86\u4e2a\u7b80\u5355\u7684\u624b\u52bf\uff0c\u7ef4\u6301\u9b54\u6cd5\u9635\u7684\u4e09\u540d\u5deb\u5e08\u540c\u65f6\u540e\u9000\u534a\u6b65\uff0c\u86c7\u9b54\u53ea\u89c9\u8eab\u4e0a\u538b\u529b\u4e00\u8f7b\uff0c\u65e0\u5f62\u7684\u675f\u7f1a\u5df2\u7ecf\u9aa4\u7136\u6d88\u5931\uff0c\u5979\u6b23\u559c\u4e4b\u4e0b\uff0c\u5c16\u5578\u4e00\u58f0\uff0c\u86c7\u8eab\u873f\u8712\u6e38\u52a8\u5982\u7535\uff0c\u516b\u53ea\u624b\u81c2\u9ad8\u4e3e\u5175\u5668\uff0c\u671d\u7740\u8ddd\u79bb\u6700\u8fd1\u7684\u4e00\u540d\u5deb\u5e08\u6251\u4e86\u8fc7\u6765\uff0c\u60f3\u8981\u5c06\u4ed6\u5241\u6210\u8089\u9171\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u62c9\u52a0\u4ece\u8170\u56ca\u4e2d\u6478\u51fa\u4e00\u652f\u9970\u6709\u87ba\u65cb\u7eb9\u8def\u7684\u767d\u8272\u53f7\u89d2\uff0c\u653e\u5230\u5507\u8fb9\u9f13\u52b2\u4e00\u5439\u3002</p><p>\u201c\u545c\u545c\u545c\u545c\uff01\u201d</p><p>\u53f7\u89d2\u58f0\u5e76\u975e\u60f3\u8c61\u4e2d\u7684\u96c4\u6d51\u6fc0\u6602\uff0c\u53cd\u800c\u662f\u4f4e\u6c89\u5f97\u4ee4\u4eba\u6ca1\u6765\u7531\u5730\u9635\u9635\u5fc3\u60b8\uff0c\u86c7\u9b54\u773c\u770b\u5c31\u8981\u5c06\u9762\u524d\u5deb\u5e08\u6210\u529f\u65a9\u6740\uff0c\u9aa4\u7136\u542c\u5230\u53f7\u89d2\uff0c\u6574\u4e2a\u8eab\u4f53\u90fd\u50cf\u89e6\u7535\u822c\u5267\u70c8\u98a4\u4e86\u98a4\uff0c\u5239\u90a3\u95f4\u987f\u4f4f\u4e86\uff0c\u7136\u540e\u4ee5\u4e00\u79cd\u50f5\u786c\u65e0\u6bd4\u7684\u52a8\u4f5c\u6162\u6162\u626d\u8fc7\u8170\u6765\uff0c\u9762\u671d\u7740\u8679\u5149\u5899\u7684\u65b9\u5411\uff0c\u91d1\u9ec4\u8272\u7684\u53cc\u773c\u53d8\u5f97\u8d64\u7ea2\u5982\u7099\uff0c\u6740\u6c14\u817e\u817e\uff0c\u5374\u900f\u7740\u660e\u663e\u7684\u5446\u6ede\uff0c\u5168\u7136\u4e0d\u4f3c\u6d3b\u7269\u3002</p><p>\u7830\u7830\u7830\u7830\u58f0\u63a5\u8fde\u54cd\u8d77\uff0c\u5728\u5b89\u535a\u7406\u8fde\u7eed\u7684\u6253\u51fb\u4e0b\uff0c\u8679\u5149\u5899\u7ec8\u4e8e\u627f\u53d7\u4e0d\u4f4f\uff0c\u5f00\u59cb\u51fa\u73b0\u88c2\u75d5\uff0c\u773c\u770b\u5c31\u8981\u5b8c\u5168\u6bc1\u574f\u3002\u62c9\u52a0\u8fde\u5fd9\u518d\u5ea6\u5439\u54cd\u53f7\u89d2\uff0c\u8fd9\u6b21\u97f3\u8c03\u53d8\u5f97\u5c16\u9510\u6025\u4fc3\u8d77\u6765\uff0c\u86c7\u9b54\u50cf\u662f\u542c\u5230\u547d\u4ee4\u822c\u5f00\u59cb\u5411\u524d\u6e38\u52a8\u3001\u52a0\u901f\uff0c\u671d\u7740\u8679\u5149\u5899\u51b2\u53bb\u3002</p><p>\u8fd8\u4e0d\u7b49\u5979\u51b2\u5230\u8fd1\u524d\uff0c\u8679\u5149\u5899\u5df2\u7ecf\u8f70\u7136\u5d29\u6e83\uff0c\u7eda\u70c2\u593a\u76ee\u7684\u4e03\u5f69\u5149\u534e\u4e2d\uff0c\u51e0\u5341\u6761\u89e6\u624b\u6c14\u52bf\u6c79\u6c79\u5730\u98de\u5c04\u51fa\u6765\uff0c\u6b63\u649e\u4e0a\u86c7\u9b54\u3002\u86c7\u9b54\u5c16\u53eb\u4e00\u58f0\uff0c\u516b\u53ea\u624b\u81c2\u5982\u98ce\u8f66\u822c\u6325\u52a8\uff0c\u5e26\u8d77\u4e00\u9635\u773c\u82b1\u7f2d\u4e71\u7684\u5149\u5f71\uff0c\u5237\u5237\u5237\u5237\uff0c\u8f6c\u773c\u95f4\u4fbf\u5c06\u6700\u524d\u9762\u7684\u4e94\u53ea\u89e6\u624b\u5c3d\u7686\u65a9\u65ad\u3002</p><p>\u5b89\u535a\u7406\u5389\u58f0\u60e8\u53eb\uff0c\u6307\u6325\u5176\u4ed6\u89e6\u624b\u56f4\u653b\u8fc7\u6765\uff0c\u86c7\u9b54\u5c06\u4e0b\u8eab\u76d8\u65cb\u53e0\u8d77\uff0c\u516b\u53ea\u624b\u81c2\u540c\u65f6\u6325\u821e\uff0c\u51db\u51db\u7684\u5200\u5149\u5251\u5f71\u7f16\u7ec7\u6210\u4e86\u4e00\u9053\u6b7b\u4ea1\u4e4b\u7f51\uff0c\u5c06\u524d\u540e\u5de6\u53f3\u56db\u9762\u516b\u65b9\u90fd\u5c01\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u6240\u6709\u80c6\u6562\u95ef\u5165\u7684\u89e6\u624b\u5168\u90fd\u88ab\u5207\u5272\u3001\u65a9\u65ad\uff0c\u72fc\u72c8\u8d25\u9000\u3002</p><p>\u8d81\u7740\u86c7\u9b54\u5438\u5f15\u4e86\u5b89\u535a\u7406\u5168\u90e8\u7684\u6ce8\u610f\uff0c\u5deb\u5e08\u4eec\u6beb\u4e0d\u4fdd\u7559\u5730\u5168\u529b\u653b\u51fb\uff0c\u5404\u79cd\u5e73\u65f6\u4ece\u4e0d\u8f7b\u7528\u7684\u5927\u5a01\u529b\u6cd5\u672f\u90fd\u88ab\u91ca\u653e\u4e86\u51fa\u6765\uff0c\u75be\u98ce\u66b4\u96e8\u822c\u671d\u7740\u5b89\u535a\u7406\u7838\u53bb\u3002\u4ed6\u4eec\u867d\u7136\u5728\u9634\u9b42\u57ce\u4e0d\u7b97\u662f\u4e00\u6d41\u4eba\u7269\uff0c\u5374\u4e5f\u90fd\u662f\u51dd\u6210\u4e86\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u5982\u6b64\u5bc6\u96c6\u7684\u8f70\u70b8\uff0c\u5c31\u7b97\u662f\u8428\u624e\u65af\u5766\u3001\u5e03\u96f7\u7eb3\u65af\u8fd9\u79cd\u771f\u6b63\u7684\u5927\u5deb\u5e08\u4e5f\u4e0d\u6562\u786c\u625b\u3002\u77ed\u77ed\u51e0\u79d2\u949f\u91cc\uff0c\u89e6\u624b\u602a\u539f\u672c\u5e73\u6ed1\u5706\u6eda\u7684\u8eaf\u4f53\u5c31\u88ab\u8f70\u6210\u4e86\u5343\u75ae\u767e\u5b54\uff0c\u56db\u53ea\u773c\u775b\u4e5f\u88ab\u6253\u778e\u4e86\u4e09\u53ea\uff0c\u9ed1\u8272\u7c98\u7a20\u6db2\u4f53\u4ece\u4f24\u53e3\u4e2d\u6d8c\u51fa\uff0c\u6d41\u6dcc\u5728\u5730\u9762\u4e0a\uff0c\u71c3\u8d77\u718a\u718a\u70c8\u7130\u3002</p><p>\u770b\u4f3c\u5f62\u52bf\u4e00\u7247\u5927\u597d\uff0c\u7136\u800c\u9634\u9b42\u57ce\u5deb\u5e08\u4eec\u7684\u5fc3\u4e2d\u5374\u662f\u8d8a\u6765\u8d8a\u7126\u6025\uff0c\u62c9\u52a0\u66f4\u662f\u53cc\u7709\u7d27\u9501\uff0c\u5fe7\u5f62\u4e8e\u8272\uff0c\u56e0\u4e3a\u5373\u4fbf\u5728\u8fd9\u6837\u7684\u653b\u51fb\u4e0b\uff0c\u5b89\u535a\u7406\u4e5f\u4f9d\u7136\u53ea\u4f24\u672a\u6b7b\u3002\u66f4\u8981\u547d\u7684\u662f\uff0c\u5b83\u8fd8\u5728\u5feb\u901f\u5730\u81ea\u6108\uff0c\u88ab\u529b\u573a\u5251\u6495\u5f00\u7684\u4f24\u53e3\uff0c\u51e0\u79d2\u949f\u540e\u4fbf\u6e10\u6e10\u5e73\u590d\uff0c\u88ab\u97f3\u6ce2\u77db\u8f70\u51fa\u7684\u6df1\u5751\uff0c\u534a\u5206\u949f\u540e\u5c31\u6d88\u5931\u4e0d\u89c1\uff0c\u5c31\u8fde\u90a3\u4e9b\u88ab\u5207\u65ad\u7684\u89e6\u624b\uff0c\u4e5f\u90fd\u4e00\u70b9\u70b9\u5730\u91cd\u65b0\u751f\u957f\u51fa\u6765\uff0c\u901f\u5ea6\u4e0d\u5feb\uff0c\u4f46\u4e5f\u7edd\u5bf9\u4e0d\u6162\u3002\u7167\u8fd9\u6837\u4e0b\u53bb\uff0c\u518d\u8fc7\u51e0\u5206\u949f\uff0c\u5979\u5c31\u4e00\u5207\u53c8\u6062\u590d\u5982\u521d\uff0c\u5deb\u5e08\u4eec\u521a\u624d\u7684\u52aa\u529b\u5c31\u5168\u767d\u8d39\u529b\u6c14\u4e86\u3002</p><p>\u5bf9\u4ed8\u8fd9\u79cd\u6709\u8d85\u5f3a\u81ea\u6108\u80fd\u529b\u7684\u602a\u7269\uff0c\u6700\u597d\u7684\u529e\u6cd5\u5c31\u662f\u96c6\u4e2d\u706b\u529b\u4e00\u51fb\u8f70\u6740\u2014\u2014\u53cd\u8fc7\u6765\u8bf4\uff0c\u5982\u679c\u5df2\u7ecf\u96c6\u4e2d\u706b\u529b\uff0c\u5374\u4f9d\u7136\u4e0d\u80fd\u5c06\u5b83\u5f7b\u5e95\u706d\u6740\uff0c\u90a3\u9ebb\u70e6\u5c31\u5927\u4e86\u3002\u5deb\u5e08\u539f\u672c\u5c31\u4e0d\u64c5\u957f\u6301\u4e45\u4f5c\u6218\uff0c\u8bb2\u7a76\u7684\u662f\u77ac\u95f4\u7206\u53d1\u529b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u6cd5\u672f\u662f\u6709\u9650\u7684\uff0c\u4e00\u65e6\u7528\u5b8c\uff0c\u5728\u91cd\u65b0\u51c6\u5907\u4e4b\u524d\u5c31\u53ea\u80fd\u5e72\u77aa\u773c\u3002\u6240\u4ee5\u5bf9\u9634\u9b42\u57ce\u4e00\u65b9\u6765\u8bf4\uff0c\u65f6\u95f4\u62d6\u5f97\u8d8a\u4e45\uff0c\u60c5\u5f62\u5c31\u8d8a\u4e0d\u5229\u3002</p><p>\u201c\u8fd9\u5bb6\u4f19\u2026\u2026\u8fd8\u771f\u662f\u8010\u6253\u554a\u3002\u201d</p><p>\u73ca\u5609\u79c0\u7709\u8f7b\u8e59\uff0c\u770b\u7740\u773c\u524d\u7684\u6218\u6597\u3002\u5728\u573a\u7684\u4eba\u4e2d\uff0c\u53ea\u6709\u5979\u662f\u57fa\u672c\u5168\u65e0\u81ea\u4fdd\u4e4b\u529b\uff0c\u6240\u4ee5\u4e00\u5f00\u59cb\u5c31\u88ab\u743c\u6069\u5217\u4e3a\u91cd\u70b9\u4fdd\u62a4\u5bf9\u8c61\uff0c\u4e0d\u4f46\u65e9\u65e9\u8eb2\u5230\u4e86\u5927\u6bbf\u7684\u6700\u5b89\u5168\u89d2\u843d\uff0c\u8eab\u8fb9\u8fd8\u6709\u4e24\u5c0a\u5353\u5c14\u6218\u58eb\u9b54\u50cf\u7d27\u7d27\u62a4\u536b\u7740\u3002\u5b89\u535a\u7406\u4f3c\u4e4e\u4e5f\u6ca1\u6ce8\u610f\u5230\uff0c\u4ece\u59cb\u81f3\u7ec8\u5c31\u6ca1\u653b\u51fb\u8fc7\u5979\uff0c\u8fd9\u8ba9\u73ca\u5609\u6709\u8db3\u591f\u7684\u95f2\u6687\u6765\u89c2\u5bdf\u6218\u5c40\u3002\u5979\u80fd\u6e05\u695a\u5730\u770b\u51fa\u5c40\u52bf\u5176\u5b9e\u4e0d\u5229\uff0c\u4f46\u5374\u5b8c\u5168\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u6b63\u81ea\u7740\u6025\uff0c\u7a81\u7136\u4e00\u4e2a\u9887\u4e3a\u8033\u719f\uff0c\u597d\u50cf\u524d\u4e0d\u4e45\u624d\u542c\u5230\u8fc7\u7684\u5973\u58f0\u5728\u8111\u4e2d\u76f4\u63a5\u54cd\u8d77\u3002</p><p>\u201c\u5f53\u7136\u8010\u6253\u4e86\uff0c\u5b89\u535a\u7406\u5728\u8bf8\u795e\u4e2d\u5c31\u662f\u4ee5\u751f\u547d\u529b\u987d\u5f3a\u8457\u79f0\u7684\uff0c\u9664\u4e86\u5bc6\u65af\u7279\u62c9\u5c31\u6570\u5979\u4e86\u201d\u90a3\u4e2a\u58f0\u97f3\u7684\u8bed\u6c14\u4e2d\u5e26\u7740\u8ba5\u8bee\uff0c\u201c\u7b2c\u4e00\u7eaa\u8bde\u751f\u7684\u795e\u660e\uff0c\u522b\u7684\u672c\u4e8b\u6ca1\u6709\uff0c\u5c31\u662f\u603b\u4e5f\u6b7b\u4e0d\u4e86\u3002\u201d</p></article>"}}}]);
