(window.webpackJsonp=window.webpackJsonp||[]).push([[46],{69:function(p){p.exports={chapterTitle:null,title:"\u7b2c46\u7ae0 \u8fd8\u662f\u9a97",content:"<article><h2>\u7b2c46\u7ae0 \u8fd8\u662f\u9a97</h2><p>\u3000\u3000\u539f\u5148\u6253\u7b97\u62db\u8058\u4e00\u540d\u5185\u52e4\u3001\u4e00\u540d\u51fa\u7eb3\u3001\u4e24\u540d\u4e1a\u52a1\uff0c\u62db\u8058\u5e7f\u544a\u4e0a\u4e5f\u662f\u8fd9\u4e48\u5199\u7684\uff0c\u4f46\u662f\u7559\u4e0b\u7684\u56db\u4e2a\u4eba\u90fd\u662f\u5e94\u8058\u4e1a\u52a1\u804c\u4f4d\u7684\u3002</p><p>\u3000\u3000\u56db\u4eba\u5f53\u4e2d\u6700\u5e74\u8f7b\u7684\u5218\u660e\u8f89\u662f\u534e\u4e1c\u7406\u5de5\u5927\u5b66\u5e02\u573a\u8425\u950093\u5c4a\u6bd5\u4e1a\u751f\uff0c\u4e4b\u524d\u5de5\u4f5c\u7684\u5355\u4f4d\u6709\u5927\u534a\u5e74\u6ca1\u6709\u53d1\u51fa\u5de5\u8d44\uff0c\u6ca1\u60f3\u7740\u6b7b\u5b88\u4e00\u68f5\u6811\u4e0a\u540a\uff0c\u53ea\u662f\u4e00\u76f4\u6ca1\u6709\u627e\u5230\u5408\u9002\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u5468\u4e00\u5e73\u662f\u6d77\u5dde\u5927\u5b66\u673a\u68b0\u7cfb\u4e5d\u4e00\u5c4a\u7684\u6bd5\u4e1a\u751f\uff0c\u8010\u4e0d\u4f4f\u6027\u5b50\u5728\u539f\u5148\u7684\u516c\u53f8\u505a\u6280\u672f\u5de5\u4f5c\uff0c\u770b\u5230\u6d77\u5dde\u65e5\u62a5\u4e0a\u8fd9\u5219\u5f85\u9047\u4f18\u6e25\u7684\u62db\u8058\u5e7f\u544a\uff0c\u4fbf\u8fc7\u6765\u8bd5\u4e00\u8bd5\u3002</p><p>\u3000\u3000\u4e5d\u96f6\u5e74\u4ece\u6d77\u5dde\u5546\u5b66\u9662\u6bd5\u4e1a\u7684\u6768\u4e91\uff0c\u662f\u56db\u4e2a\u4eba\u5f53\u4e2d\u8425\u9500\u7ecf\u9a8c\u6700\u4e30\u5bcc\u7684\uff0c\u4e5f\u56e0\u4e3a\u4ed6\u6bd4\u8f83\u4e30\u5bcc\u7684\u8425\u9500\u7ecf\u9a8c\uff0c\u9762\u8bd5\u65f6\u5f20\u606a\u4e0e\u4ed6\u591a\u804a\u4e86\u51e0\u53e5\u8bdd\uff0c\u4ed6\u89c9\u5f97\u53e5\u53e5\u8bf4\u5230\u81ea\u5df1\u5fc3\u574e\u91cc\u53bb\u4e86\uff0c\u4e5f\u662f\u4ed6\u529d\u5176\u4ed6\u4e09\u4eba\u6253\u6d88\u7591\u8651\u3002</p><p>\u3000\u3000\u848b\u8587\u662f\u56db\u4eba\u60df\u4e00\u7684\u5973\u6027\uff0c\u8eab\u6750\u6bd4\u8bb8\u601d\u8fd8\u8981\u5938\u5f20\uff0c\u773c\u7709\u95f4\u6709\u51e0\u7c92\u767d\u96c0\u6591\uff0c\u5012\u72b9\u663e\u51fa\u51e0\u5206\u4e30\u97f5\uff0c\u773c\u775b\u5f88\u8ff7\u4eba\uff0c\u624d\u4e09\u5341\u4e00\u5c81\uff0c\u5df2\u7ecf\u4ece\u539f\u6765\u7684\u5355\u4f4d\u529e\u4e86\u5185\u9000\uff0c\u8ba9\u8bb8\u601d\u5979\u4eec\u542c\u4e86\u7fa1\u6155\u4e0d\u5df2\u3002\u5f20\u606a\u5374\u89c1\u602a\u4e0d\u602a\uff0c\u53ea\u8981\u4e0a\u9762\u6709\u4eba\uff0c\u521a\u4ece\u5e7c\u513f\u56ed\u6bd5\u4e1a\u90fd\u80fd\u529e\u9000\u4f11\u62ff\u9000\u4f11\u5de5\u8d44\u3002\u540e\u9762\u624d\u77e5\u9053\u5979\u516c\u7239\u662f\u5e02\u536b\u751f\u5c40\u526f\u5c40\u957f\uff0c\u5979\u539f\u5148\u7684\u5de5\u4f5c\u5355\u4f4d\u662f\u57ce\u5357\u533a\u8d22\u653f\u5c40\uff0c\u4eca\u5e74\u4e0e\u4eba\u4e89\u7740\u4e0a\u6b63\u79d1\uff0c\u7ed9\u4eba\u9020\u8c23\u5979\u8ddf\u533a\u91cc\u9886\u5bfc\u4e71\u641e\u7537\u5973\u5173\u7cfb\uff0c\u5979\u4e08\u592b\u903c\u7740\u5979\u4ece\u8d22\u653f\u5c40\u5185\u9000\uff0c\u4e0d\u7136\u5c31\u79bb\u5a5a\u3002\u5f85\u9047\u591a\u5c11\uff0c\u5979\u5e76\u4e0d\u592a\u5173\u5fc3\uff0c\u53ea\u662f\u4e0d\u60f3\u5750\u5bb6\u91cc\u62ff\u9000\u4f11\u91d1\u7b49\u8001\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u56db\u4eba\u90fd\u53eb\u8fdb\u4f1a\u8bae\u5ba4\uff0c\u8ba9\u4ed6\u4eec\u56f4\u7740\u4f1a\u8bae\u684c\u5750\u4e0b\uff1a\u201c\u4eba\u624b\u5dee\u4e0d\u591a\u7b97\u662f\u62db\u9f50\u4e86\uff0c\u6ca1\u6709\u4e13\u95e8\u7684\u65f6\u95f4\u7ed9\u5927\u5bb6\u505a\u4e1a\u52a1\u4e0a\u7684\u57f9\u8bad\uff0c\u624b\u5934\u6709\u4e9b\u4e8b\u60c5\u9700\u8981\u9a6c\u4e0a\u53bb\u505a\u2026\u2026\u201d\u63a8\u7740\u8bb8\u601d\u7684\u80a9\u8180\uff0c\u8ba9\u5979\u5750\u5728\u81ea\u5df1\u8eab\u8fb9\uff0c\u8bf4\u9053\uff0c\u201c\u529e\u4e8b\u5904\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u7531\u8bb8\u601d\u8d1f\u8d23\uff0c\u6211\u5e73\u65f6\u8131\u4e0d\u5f00\u8eab\uff0c\u4f46\u662f\u4f60\u4eec\u6240\u6709\u7684\u8425\u9500\u4f1a\u8bae\uff0c\u6211\u90fd\u4f1a\u62bd\u65f6\u95f4\u51fa\u6765\u53c2\u52a0\uff0c\u76f4\u5230\u4f60\u4eec\u90fd\u80fd\u72ec\u7acb\u7684\u5f00\u5c55\u4e1a\u52a1\u4e3a\u6b62\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6602\u5934\u770b\u4e86\u770b\u5f20\u606a\uff0c\u89c1\u4ed6\u4e00\u8138\u7684\u81ea\u4fe1\uff0c\u4ece\u4ed6\u795e\u6001\u3001\u8a00\u884c\uff0c\u65e0\u6cd5\u76f8\u4fe1\u4ed6\u8fd8\u662f\u521a\u8bfb\u9ad8\u4e2d\u7684\u5c11\u5e74\uff0c\u4ece\u62db\u8058\u9762\u8bd5\u5230\u73b0\u5728\uff0c\u6768\u4e91\u3001\u848b\u8587\u4ed6\u4eec\u6839\u672c\u5c31\u6ca1\u6709\u6000\u7591\u8fc7\u4ed6\u7684\u5e74\u9f84\uff0c\u771f\u662f\u8ba9\u4eba\u65e0\u6cd5\u76f8\u4fe1\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8bb8\u601d\u6602\u5934\u770b\u7740\u81ea\u5df1\uff0c\u671d\u5979\u7b11\u4e86\u7b11\uff0c\u73b0\u5728\u4e5f\u4e0d\u662f\u9732\u602f\u7684\u65f6\u5019\uff0c\u5c06\u4f1a\u8bae\u5ba4\u89d2\u843d\u91cc\u7684\u79fb\u52a8\u767d\u677f\u62d6\u5230\u524d\u9762\u6765\uff0c\u8fd9\u5f20\u79fb\u52a8\u767d\u677f\u4e0e\u5f88\u591a\u529e\u516c\u7528\u54c1\u90fd\u662f\u4ee5\u8bd5\u7528\u7684\u540d\u4e49\u4ece\u4f9b\u8d27\u5546\u90a3\u91cc\u9a97\u6765\u7684\uff0c\u5c06\u7231\u7acb\u4fe1GH388\u7684\u56fe\u7247\u7528\u78c1\u6027\u8d34\u8d34\u5728\u767d\u677f\u4e0a\uff0c\u8fd9\u4e9b\u56fe\u7247\u662f\u5f20\u606a\u4ece\u901a\u8baf\u6742\u5fd7\u4e0a\u88c1\u4e0b\u6765\u7684\uff1a\u201c\u6211\u4eec\u5f88\u5feb\u5c31\u80fd\u62ff\u5230\u8fd9\u6b3e\u6570\u5b57\u624b\u673a\u7684\u4ee3\u7406\u6743\uff0c\u4f46\u662f\u6211\u4eec\u76ee\u524d\u5728\u6d77\u5dde\u8fd8\u6ca1\u6709\u5b8c\u6574\u7684\u5206\u9500\u6e20\u9053\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6123\u4e86\u6123\uff0c\u771f\u5b9e\u7684\u60c5\u51b5\u5e94\u8be5\u662f\u5efa\u7acb\u8d77\u5b8c\u6574\u7684\u5206\u9500\u6e20\u9053\u4ee5\u53ca\u83b7\u5f97\u4e00\u5b9a\u7684\u94fa\u8d27\u8d44\u91d1\u4e4b\u540e\u624d\u6709\u53ef\u80fd\u62ff\u5230\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u7684\u4ee3\u7406\u6743\uff0c\u5f20\u606a\u6839\u672c\u5c31\u6ca1\u6709\u6253\u7b97\u5c06\u5b9e\u60c5\u544a\u8bc9\u521a\u62db\u8fdb\u6765\u7684\u56db\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4f9d\u7740\u6905\u80cc\uff0c\u8eab\u5b50\u626d\u8fc7\u6765\u770b\u5934\u5f20\u606a\uff0c\u4e0d\u53bb\u60f3\u4ed6\u7684\u5e74\u9f84\uff0c\u4ed6\u7684\u8138\u90e8\u7ebf\u6761\u660e\u4fca\uff0c\u773c\u795e\u575a\u5b9a\u9510\u5229\uff0c\u975e\u5e38\u8010\u770b\uff0c\u8bb8\u601d\u8ba9\u5fc3\u91cc\u7a81\u7136\u5192\u51fa\u6765\u7684\u5ff5\u5934\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8be6\u7ec6\u4ecb\u7ecd\u6a21\u62df\u4fe1\u53f7\u901a\u8baf\u4e0e\u6570\u5b57\u901a\u8baf\u7684\u6280\u672f\u7279\u70b9\uff0c\u8fd9\u4e2a\u4e00\u65f6\u534a\u4f1a\u4e5f\u89e3\u91ca\u4e0d\u6e05\uff0c\u53ea\u8ddf\u4ed6\u4eec\u8bf4\uff1a\u201c\u8054\u901a\u516c\u53f8\u4e0e\u90ae\u7535\u90e8\u79fb\u8fd0\u901a\u4fe1\u5c40\u4eca\u5e74\u90fd\u5728\u5efa\u8bbe\u6570\u5b57\u901a\u8baf\u7f51\u7edc\uff0c\u73b0\u5728\u5927\u5bb6\u5e38\u770b\u5230\u7684\u2018\u5927\u54e5\u5927\u2019\u662f\u4e00\u79cd\u6a21\u62df\u4fe1\u53f7\u624b\u673a\uff0c\u5f88\u5feb\u5c31\u4f1a\u88ab\u6570\u5b57\u624b\u673a\u6240\u6dd8\u6c70\u3002\u5f53\u524d\uff0c\u6469\u6258\u7f57\u62c9\u90fd\u901a\u8fc7\u5404\u5730\u90ae\u5c40\u552e\u9500\u4ed6\u4eec\u7684\u901a\u8baf\u5668\u6750\uff0c\u5360\u636e\u5f53\u524d\u901a\u8baf\u5668\u6750\u5e02\u573a\u7edd\u5927\u591a\u90e8\u5206\u7684\u4efd\u989d\uff0c\u7231\u7acb\u4fe1\u7b49\u56fd\u9645\u5236\u9020\u5546\u8981\u8fdb\u5165\u56fd\u5185\uff0c\u65e0\u6cd5\u901a\u8fc7\u90ae\u653f\u7cfb\u7edf\u7684\u8d2d\u9500\u7f51\u7edc\u8ddf\u6469\u6258\u7f57\u4f4d\u7ade\u4e89\uff0c\u53ea\u6709\u91c7\u7528\u603b\u5206\u5f0f\u591a\u7ea7\u4ee3\u7406\u4f53\u7cfb\uff0c\u5229\u7528\u6e20\u9053\u5546\u5bf9\u5e02\u573a\u9500\u552e\u7f51\u70b9\u63a7\u5236\u7684\u4f18\u52bf\uff0c\u8fc5\u901f\u628a\u8d27\u94fa\u5230\u5168\u56fd\u4e3b\u8981\u5927\u4e2d\u57ce\u5e02\u3002\u7231\u7acb\u4fe1\u5728\u56fd\u5185\u7684\u5206\u516c\u53f8\uff0c\u5df2\u7ecf\u91c7\u53d6\u8fd9\u4e00\u505a\u6cd5\uff0c\u65e9\u5728\u5e74\u521d\u5c31\u786e\u5b9a\u4e86\u534e\u4e1c\u5730\u533a\u4ee3\u7406\u5546\u3002\u6211\u4eec\u5373\u5c06\u6210\u4e3a\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u5e02\u7684\u6e20\u9053\u4ee3\u7406\u5546\uff0c\u6240\u4ee5\uff0c\u9700\u8981\u5728\u5ea7\u7684\u56db\u4eba\u4e0e\u6211\u3001\u8bb8\u601d\uff0c\u5728\u6700\u77ed\u7684\u65f6\u95f4\u91cc\u4e00\u8d77\u5efa\u7acb\u8d77\u6d77\u5dde\u5e02\u7684\u5206\u9500\u7f51\u7edc\u3002\u201d</p><p>\u3000\u3000\u529e\u4e8b\u5904\u4e0d\u4ec5\u6ca1\u6709\u5206\u9500\u6e20\u9053\uff0c\u8fd8\u6ca1\u6709\u94fa\u8d27\u6240\u9700\u7684\u8d44\u91d1\uff0c\u73b0\u5728\u968f\u968f\u4fbf\u4fbf\u4e00\u90e8\u5927\u54e5\u5927\u5c31\u8981\u4e24\u4e09\u4e07\uff0c\u6570\u5b57\u624b\u673a\u4e5f\u4e0d\u4f1a\u4fbf\u5b9c\u591a\u5c11\uff0c\u5c31\u7b97\u80fd\u5728\u77ed\u65f6\u95f4\u5185\u5efa\u7acb\u8d77\u5206\u9500\u6e20\u9053\uff0c\u94fa\u8d27\u7684\u8d44\u91d1\u4e5f\u7edd\u5bf9\u4e0d\u662f\u5c0f\u6570\u76ee\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u4e00\u8138\u7684\u4ece\u5bb9\u81ea\u4fe1\uff0c\u8bb8\u601d\u4e5f\u8ba9\u4ed6\u7684\u6fc0\u60c5\u6240\u611f\u67d3\uff0c\u4f3c\u4e4e\u4e5f\u8ba4\u4e3a\u8fd9\u4e9b\u56f0\u96be\u4f1a\u8fce\u5203\u800c\u89e3\uff0c\u5f53\u7136\uff0c\u4ee5\u5f20\u606a\u5bb6\u5728\u6d77\u5dde\u7684\u57fa\u7840\uff0c\u53e6\u627e\u4e00\u5bb6\u6709\u8d44\u91d1\u5b9e\u529b\u7684\u5355\u4f4d\u5408\u4f5c\uff0c\u4e5f\u4e0d\u4f1a\u592a\u56f0\u96be\u3002</p><p>\u3000\u3000\u5f20\u606a\u4fa7\u5934\u89c1\u8bb8\u601d\u770b\u5411\u81ea\u5df1\u7684\u773c\u795e\u5f88\u67d4\u548c\uff0c\u95ee\u5979\uff1a\u201c\u6211\u8bf4\u9519\u4e86\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6447\u4e86\u6447\u5934\uff1a\u201c\u6211\u4eec\u5bf9\u6570\u5b57\u901a\u8baf\u4e00\u7a8d\u4e0d\u901a\uff0c\u662f\u4e0d\u662f\u8bf7\u516c\u53f8\u6d3e\u540d\u5de5\u7a0b\u5e08\u8fc7\u6765\uff0c\u7ed9\u6211\u4eec\u626b\u4e00\u4e0b\u76f2\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u5728\u5f20\u606a\u8fd9\u51e0\u5929\u7684\u704c\u8f93\u4e0b\uff0c\u5bf9\u6570\u5b57\u901a\u8baf\u5df2\u7ecf\u5efa\u7acb\u4e86\u76f8\u5bf9\u5b8c\u6574\u7684\u6982\u5ff5\uff0c\u4f46\u5979\u5bf9\u5f20\u606a\u6ca1\u6709\u592a\u5927\u7684\u4fe1\u5fc3\uff0c\u6240\u4ee5\u5bf9\u81ea\u5df1\u76ee\u524d\u6240\u4e86\u89e3\u5230\u7684\u77e5\u8bc6\u6ca1\u6709\u5e95\uff0c\u89c9\u5f97\u8bf7\u6d77\u88d5\u516c\u53f8\u7684\u5de5\u7a0b\u5e08\u8bb2\u89e3\u4e00\u4e0b\uff0c\u624d\u66f4\u52a0\u628a\u63e1\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u5df2\u7ecf\u8ddf\u665a\u6674\u59d0\u6253\u8fc7\u62db\u547c\u4e86\uff0c\u5de5\u7a0b\u5e08\u665a\u4e0a\u5c31\u80fd\u5230\uff0c\u53ea\u80fd\u5728\u6d77\u5dde\u7559\u4e24\u5929\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u542c\u5f20\u606a\u7684\u53e3\u6c14\uff0c\u5012\u662f\u65e9\u5c31\u786e\u5b9a\u5218\u660e\u8f89\u56db\u4e2a\u4eba\u4e00\u5b9a\u4e0d\u4f1a\u7ed9\u4e94\u5343\u5143\u7684\u5de5\u4f5c\u4fdd\u8bc1\u91d1\u5413\u9000\uff0c\u8fd9\u624d\u63d0\u524d\u8054\u7edc\u6d77\u88d5\u516c\u53f8\u90a3\u8fb9\u6d3e\u51fa\u57f9\u8bad\u5de5\u7a0b\u5e08\u3002\u60f3\u8d77\u4ed6\u6628\u5929\u8bf4\u201c\u6ca1\u6709\u60f3\u5230\u6709\u5931\u8d25\u7684\u53ef\u80fd\u201d\u8fd9\u53e5\u8bdd\u65f6\u90a3\u79cd\u81ea\u4fe1\u7684\u795e\u60c5\uff0c\u839e\u5c14\u4e00\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u514b\u5236\u7740\u4e0d\u53bb\u770b\u8bb8\u601d\u52fe\u9b42\u7684\u773c\u795e\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7559\u7ed9\u6211\u4eec\u5efa\u7acb\u5206\u9500\u7f51\u7edc\u7684\u65f6\u95f4\u5f88\u77ed\uff0c\u6211\u5e0c\u671b\u80fd\u8fde\u7eed\u4e09\u5929\u4ee5\u6700\u5c11\u7684\u8d44\u91d1\u62ff\u4e0b\u6d77\u5dde\u65e5\u62a5\u4e09\u7248\u7684\u534a\u7248\u7bc7\u5e45\u505a\u5e7f\u544a\uff0c\u901a\u8fc7\u5e7f\u544a\uff0c\u8ba9\u5e02\u573a\u7ec8\u7aef\u9500\u552e\u5546\u4e3b\u52a8\u6765\u627e\u6211\u4eec\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u62ff\u624b\u6307\u5934\u9876\u7740\u989d\u5934\uff0c\u5728\u8425\u9500\u65b9\u9762\u6709\u7740\u4e30\u5bcc\u7ecf\u9a8c\u7684\u6768\u4e91\u95ee\u9053\uff1a\u201c\u6570\u5b57\u624b\u673a\uff0c\u6211\u4eec\u51e0\u4e2a\u4eba\uff0c\u4e5f\u53ea\u6709\u5f88\u80a4\u6d45\u7684\u5370\u8c61\uff0c\u8981\u7ed9\u666e\u901a\u4eba\u7559\u4e0b\u6709\u5173\u6570\u5b57\u624b\u673a\u5f3a\u70c8\u7684\u5370\u8c61\uff0c\u5e76\u5438\u5f15\u4ed6\u4eec\u6210\u4e3a\u7231\u7acb\u4fe1\u7684\u7ec8\u7aef\u9500\u552e\u5546\uff0c\u4e09\u5929\u7684\u5e7f\u544a\u6295\u653e\uff0c\u53ea\u6015\u6548\u679c\u4e0d\u4f1a\u592a\u660e\u663e\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u8981\u5728\u5e7f\u544a\u5185\u5bb9\u52a8\u8111\u7b4b\uff01\u201d\u5f20\u606a\u679c\u65ad\u7684\u6253\u6d88\u6768\u4e91\u63d0\u51fa\u6765\u7684\u7591\u4e49\uff0c\u524d\u671f\u4ec0\u4e48\u8d44\u6e90\u90fd\u6ca1\u6709\uff0c\u518d\u6ca1\u6709\u4ec0\u4e48\u4fe1\u5fc3\u7684\u8bdd\uff0c\u4e8b\u60c5\u771f\u6709\u53ef\u80fd\u4f1a\u9ec4\uff0c\u201c\u73b0\u5728\u8981\u5148\u8c08\u4e0b\u6d77\u5dde\u65e5\u62a5\u793e\u5468\u4e09\u5230\u5468\u4e94\u7684\u4e09\u7248\u5e7f\u544a\u4f4d\u2026\u2026\u65f6\u95f4\u6709\u4e9b\u7d27\uff0c\u8c01\u6709\u628a\u63e1\u8c08\u4e0b\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u5411\u5218\u660e\u8f89\u56db\u4eba\u3002</p><p>\u3000\u3000\u8bf4\u4e0d\u5b9a\u6d77\u5dde\u65e5\u62a5\u4e0b\u5468\u7684\u7248\u9762\u5df2\u7ecf\u7ed9\u6392\u6ee1\u4e86\uff0c\u8981\u8fde\u7eed\u4e09\u5929\u6324\u51fa\u534a\u7248\u7684\u5e7f\u544a\u7bc7\u5e45\uff0c\u5c31\u7b97\u51fa\u5f88\u9ad8\u7684\u4ef7\uff0c\u4e5f\u5f88\u96be\u505a\u901a\u5de5\u4f5c\uff0c\u4f55\u51b5\u5f20\u606a\u5f00\u59cb\u5c31\u8bf4\u4e86\uff0c\u8981\u4ee5\u6700\u4fbf\u5b9c\u7684\u4ef7\u683c\u62ff\u4e0b\u5e7f\u544a\u4f4d\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5fc3\u91cc\u660e\u767d\uff0c\u624b\u91cc\u53ea\u662f\u5218\u660e\u8f89\u4ed6\u4eec\u56db\u4eba\u4eca\u5929\u4ea4\u4e0a\u6765\u7684\u4e24\u4e07\u5143\u5de5\u4f5c\u4fdd\u8bc1\u91d1\uff0c\u8981\u505a\u7684\u4e8b\u5374\u5f88\u591a\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u6211\u8bd5\u8bd5\u770b\uff1f\u201d\u848b\u8587\u8bf4\u9053\uff0c\u201c\u6211\u7231\u4eba\u5728\u5e02\u59d4\u5ba3\u4f20\u90e8\u5de5\u4f5c\uff0c\u53ef\u80fd\u627e\u5230\u65e5\u62a5\u793e\u7684\u719f\u4eba\u3002\u201d</p><p>\u3000\u3000\u5e02\u59d4\u5ba3\u4f20\u90e8\uff0c\u5f20\u606a\u53ea\u8ba4\u5f97\u90e8\u957f\u6768\u6587\u6e05\uff0c\u6768\u6587\u6e05\u4e94\u5341\u51fa\u5934\uff0c\u5c0f\u8001\u5934\u4e00\u4e2a\uff0c\u663e\u7136\u4e0d\u4f1a\u662f\u848b\u8587\u7684\u7231\u4eba\uff0c\u4e24\u4e2a\u526f\u90e8\u957f\u4f3c\u4e4e\u5e74\u7eaa\u4e5f\u4e0d\u5c11\uff0c\u656c\u8fc7\u9152\uff0c\u540d\u5b57\u5374\u6ca1\u6709\u8bb0\u4f4f\u3002\u4e0e\u848b\u8587\u521a\u521a\u63a5\u89e6\uff0c\u4e0d\u65b9\u4fbf\u95ee\u5f97\u592a\u4ed4\u7ec6\uff0c\u5fc3\u60f3\u5979\u4e5f\u662f\u6709\u76f8\u5f53\u7684\u628a\u63e1\u624d\u4f1a\u63d0\u51fa\u6765\uff0c\u4e0d\u7136\u53ea\u6709\u63d0\u524d\u62c9\u675c\u98de\u4e0b\u6c34\uff0c\u6216\u8005\u76f4\u63a5\u627e\u665a\u6674\u59d0\u5e2e\u5fd9\uff0c\u624d\u80fd\u89e3\u51b3\u8fd9\u4e2a\u96be\u9898\u3002</p></article>"}}}]);
