(window.webpackJsonp=window.webpackJsonp||[]).push([[461],{483:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e00\u8282 \u5947\u602a\u7ec4\u5408",content:"<article><h2>\u7b2c\u4e94\u5341\u4e00\u8282 \u5947\u602a\u7ec4\u5408</h2><p>\u6d88\u5931\u4e86\u3002</p><p>\u539f\u672c\u8086\u8650\u901e\u5a01\u7684\u89e6\u624b\u602a\uff0c\u5728\u75be\u98ce\u66b4\u96e8\u822c\u7684\u661f\u5149\u8f70\u51fb\u4e2d\u53ea\u575a\u6301\u4e86\u4e0d\u5230\u4e09\u5341\u79d2\u949f\uff0c\u5373\u4fbf\u7070\u98de\u70df\u706d\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u957f\u7b1b\u58f0\u60a0\u7136\u5a49\u8f6c\uff0c\u7f13\u7f13\u4f4e\u6c89\u4e0b\u53bb\uff0c\u6700\u7ec8\u53d8\u5f97\u6084\u4e0d\u53ef\u95fb\uff0c\u6f2b\u5929\u7684\u661f\u5149\u4e5f\u968f\u4e4b\u7184\u706d\uff0c\u6574\u5ea7\u5927\u6bbf\u9677\u5165\u9ed1\u6697\u3002\u53c8\u8fc7\u7247\u523b\uff0c\u4e00\u70b9\u5fae\u5149\u81ea\u5934\u9876\u7efd\u653e\u51fa\u6765\uff0c\u6495\u5f00\u6c89\u6c89\u591c\u5e55\uff0c\u5feb\u901f\u6269\u6563\u8513\u5ef6\uff0c\u5c06\u9ed1\u6697\u5b8c\u5168\u9a71\u9000\u3002</p><p>\u73ca\u5609\u7684\u8eab\u5f71\u518d\u5ea6\u5728\u534a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u65cb\u98ce\u67d4\u548c\u5730\u6258\u7740\uff0c\u8ba9\u5979\u7f13\u7f13\u843d\u4e0b\u5730\u6765\uff0c\u743c\u6069\u8d76\u5fd9\u4e0a\u524d\u6276\u4f4f\u3002\u4eff\u4f5b\u8017\u5c3d\u4e86\u5168\u90e8\u7684\u529b\u6c14\uff0c\u5c11\u5973\u7684\u8138\u8272\u82cd\u767d\u5982\u7eb8\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u4e24\u988a\u900f\u7740\u4e0d\u6b63\u5e38\u7684\u7ea2\u6655\uff0c\u547c\u51fa\u6765\u7684\u6c14\u90fd\u662f\u6eda\u70eb\u7684\uff0c\u989d\u5934\u4e0a\u70b9\u70b9\u7ec6\u6c57\u6c81\u51fa\uff0c\u50cf\u662f\u53d1\u70e7\u4e86\u4e00\u822c\u3002</p><p>\u201c\u59d0\u59d0\uff1f\u201d\u743c\u6069\u4f4e\u58f0\u53eb\u5979\uff0c\u73ca\u5609\u8ff7\u8ff7\u7cca\u7cca\u5730\u5514\u4e86\u58f0\uff0c\u773c\u76ae\u5fae\u52a8\u4f3c\u4e4e\u60f3\u8981\u7741\u5f00\uff0c\u6700\u7ec8\u5374\u6ca1\u80fd\u6210\u529f\u3002\u743c\u6069\u6709\u4e9b\u7126\u6025\uff0c\u6b63\u60f3\u8ba9\u8299\u8389\u5a05\u8fc7\u6765\u770b\u770b\uff0c\u662f\u5426\u80fd\u7528\u795e\u672f\u6cbb\u7597\uff0c\u4e00\u4e2a\u58f0\u97f3\u5728\u8033\u8fb9\u7a81\u7136\u54cd\u8d77\u3002</p><p>\u201c\u5979\u6ca1\u4e8b\u3002\u201d</p><p>\u743c\u6069\u8f6c\u8138\u671b\u53bb\uff0c\u770b\u89c1\u7070\u888d\u5973\u5b50\u6084\u65e0\u58f0\u606f\u5730\u51fa\u73b0\u5728\u8eab\u65c1\uff0c\u4ed6\u7684\u773c\u5149\u6bd4\u73ca\u5609\u654f\u9510\u5f97\u591a\uff0c\u4e00\u773c\u4fbf\u8fa8\u8ba4\u51fa\u662f\u4e2a\u5e7d\u7075\uff0c\u800c\u4e14\u4e5f\u542c\u51fa\u4e86\u5979\u7684\u58f0\u97f3\u3002\u201c\u5979\u6ca1\u4e8b\uff0c\u201d\u5965\u5609\u83b1\u65af\u53c8\u91cd\u590d\u4e86\u4e00\u904d\uff0c\u201c\u592a\u4e45\u6ca1\u8fd0\u52a8\uff0c\u8eab\u4f53\u4e0d\u9002\u5e94\u7f62\u4e86\uff0c\u4f11\u606f\u4f11\u606f\u5c31\u597d\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u867d\u7136\u4e0d\u660e\u8be6\u60c5\uff0c\u4f46\u743c\u6069\u731c\u4e5f\u731c\u5f97\u5230\uff0c\u73ca\u5609\u521a\u624d\u80fd\u591f\u53d1\u52a8\u5982\u6b64\u6c14\u52bf\u5e9e\u5927\u7684\u661f\u5760\u6cd5\u672f\uff0c\u4e0d\u53ef\u80fd\u662f\u5979\u672c\u8eab\u7684\u529b\u91cf\uff0c\u5341\u4e4b\u516b\u4e5d\u8981\u5f52\u529f\u5230\u8fd9\u5ea7\u661f\u9668\u57ce\u7684\u57ce\u4e3b\u5973\u58eb\u8eab\u4e0a\u3002\u65e2\u7136\u4eba\u5bb6\u5df2\u7ecf\u53d1\u8bdd\u4e86\uff0c\u6599\u60f3\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5927\u4e8b\u3002\u4ed6\u5c06\u73ca\u5609\u4ea4\u7ed9\u65c1\u8fb9\u7684\u8299\u857e\u72c4\uff0c\u6b63\u8981\u8be2\u95ee\u5965\u5609\u83b1\u65af\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u7a81\u7136\u542c\u89c1\u5176\u4ed6\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u4eec\u4e00\u9635\u60ca\u547c\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u743c\u6069\u4e0d\u89e3\u5730\u8f6c\u8fc7\u5934\uff0c\u987a\u7740\u4ed6\u4eec\u7684\u89c6\u7ebf\u770b\u53bb\u3002\u539f\u672c\u91d1\u78a7\u8f89\u714c\u7684\u5927\u6bbf\uff0c\u5148\u662f\u5b89\u535a\u7406\u548c\u5deb\u5e08\u4eec\u5267\u6218\uff0c\u63a5\u7740\u53c8\u88ab\u73ca\u5609\u53d1\u52a8\u7684\u661f\u5149\u4e71\u8f70\uff0c\u6700\u540e\u53c8\u88ab\u89e6\u624b\u602a\u6253\u5f00\u7a7a\u95f4\u88c2\u9699\uff0c\u5f15\u6765\u201c\u6e90\u6c34\u201d\u4fb5\u8680\uff0c\u6574\u4e2a\u5730\u9762\u3001\u5899\u58c1\u90fd\u5df2\u7ecf\u4e00\u584c\u7cca\u6d82\uff0c\u88ab\u7834\u574f\u5f97\u60e8\u4e0d\u5fcd\u7779\u3002\u5c31\u5728\u8fd9\u5806\u5e9f\u589f\u74e6\u783e\u4e2d\uff0c\u5343\u4e07\u70b9\u6df1\u84dd\u8272\u7684\u7ec6\u5c0f\u6c34\u73e0\u50cf\u6c14\u6ce1\u822c\u4ece\u5730\u9762\u5189\u5189\u5347\u8d77\uff0c\u81ea\u5185\u800c\u5916\u900f\u7740\u6beb\u5149\uff0c\u5b83\u4eec\u5728\u534a\u7a7a\u4e2d\u505c\u7559\u3001\u805a\u96c6\uff0c\u5f7c\u6b64\u5438\u5f15\uff0c\u4ee5\u975e\u5e38\u7f13\u6162\u7684\uff0c\u8089\u773c\u53ef\u4ee5\u6e05\u695a\u770b\u5230\u6bcf\u4e00\u6b65\u8fc7\u7a0b\u7684\u901f\u5ea6\uff0c\u878d\u5408\u6210\u4e3a\u4e00\u67c4\u900f\u660e\u7684\u4e09\u53c9\u621f\u3002</p><p>\u5b83\u9020\u578b\u53e4\u6734\uff0c\u5f0f\u6837\u7b80\u5355\u5230\u4e0d\u80fd\u518d\u7b80\u5355\uff0c\u770b\u8d77\u6765\u5e73\u5e73\u65e0\u5947\uff0c\u4f46\u5728\u5deb\u5e08\u7684\u5965\u672f\u89c6\u89c9\u4e2d\uff0c\u8fd9\u67c4\u4e09\u53c9\u621f\u4e0a\u900f\u51fa\u6765\u7684\u5149\u534e\u7480\u74a8\u5230\u4ee4\u4eba\u65e0\u6cd5\u76f4\u89c6\uff0c\u5355\u51ed\u90a3\u523a\u76ee\u7684\u9b54\u6cd5\u7075\u5149\uff0c\u5c31\u53ef\u4ee5\u6beb\u65e0\u7591\u95ee\u5730\u65ad\u5b9a\u5b83\u5c5e\u4e8e\u201c\u795e\u5668\u201d\u4e4b\u5217\u3002</p><p>\u201c\u6ce2\u2026\u2026\u6ce2\u6d9b\u621f\uff1f\u201d</p><p>\u62c9\u52a0\u6307\u7740\u60ac\u6d6e\u5728\u534a\u7a7a\u4e2d\u7684\u4e09\u53c9\u621f\uff0c\u4ed6\u7684\u58f0\u97f3\u53d1\u98a4\uff0c\u663e\u7136\u5fc3\u60c5\u6fc0\u52a8\u65e0\u6bd4\u3002\u5728\u573a\u7684\u4eba\u4e2d\uff0c\u9664\u6389\u5965\u5609\u83b1\u65af\u4e4b\u5916\uff0c\u8981\u8bba\u77e5\u8bc6\u6e0a\u535a\u89c1\u8bc6\u4e30\u5bcc\uff0c\u5927\u6982\u5c31\u8981\u6570\u4ed6\u4e86\u3002\u6ce2\u6d9b\u621f\uff0c\u76f8\u4f20\u662f\u5b89\u535a\u7406\u6301\u4ee5\u5de1\u6e38\u6c6a\u6d0b\uff0c\u9707\u6151\u6d77\u602a\u7684\u5b9d\u7269\uff0c\u62e5\u6709\u51e1\u4eba\u96be\u4ee5\u60f3\u8c61\u7684\u5a01\u80fd\u3002\u6b64\u524d\u5728\u6218\u6597\u4e2d\uff0c\u5b89\u535a\u7406\u4e00\u76f4\u6ca1\u6709\u62ff\u51fa\u8fd9\u4e1c\u897f\uff0c\u62c9\u52a0\u539f\u672c\u8fd8\u4ee5\u4e3a\u4f20\u95fb\u6709\u8bef\uff0c\u73b0\u5728\u770b\u6765\u5e76\u975e\u5982\u6b64\u3002</p><p>\u4ed6\u60c5\u4e0d\u81ea\u7981\u5730\u8d70\u4e0a\u524d\uff0c\u4f38\u624b\u60f3\u8981\u53bb\u62ff\uff0c\u4e09\u53c9\u621f\u4e0a\u5374\u8ff8\u51fa\u4e00\u56e2\u731b\u70c8\u5149\u8292\uff0c\u5c06\u4ed6\u9707\u5f97\u8e09\u8dc4\u540e\u9000\u51e0\u6b65\u3002\u4eff\u4f5b\u662f\u88ab\u60ca\u52a8\u4e00\u822c\uff0c\u4e09\u53c9\u621f\u5728\u7a7a\u6c14\u4e2d\u9ad8\u901f\u9707\u98a4\u8d77\u6765\uff0c\u53d1\u51fa\u6025\u4fc3\u7684\u55e1\u55e1\u58f0\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u84c4\u52bf\u5f85\u53d1\u7684\u9c7c\uff0c\u968f\u65f6\u51c6\u5907\u7834\u6c34\u8dc3\u51fa\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u743c\u6069\u8eab\u65c1\u7684\u5965\u5609\u83b1\u65af\u888d\u8896\u4e00\u626c\uff0c\u53f3\u624b\u5411\u524d\u5e73\u5e73\u4f38\u51fa\uff0c\u90a3\u672c\u5927\u4e66\u518d\u5ea6\u51fa\u73b0\uff0c\u60ac\u6d6e\u5728\u638c\u5fc3\u4e0a\u65b9\uff0c\u4e00\u9053\u7389\u767d\u8272\u5149\u675f\u81ea\u5c01\u9762\u4e0a\u7684\u6800\u5b50\u82b1\u56fe\u6848\u4e2d\u8ff8\u51fa\u3002\u4e09\u53c9\u621f\u88ab\u5149\u675f\u7167\u4e2d\uff0c\u5b83\u5728\u4e00\u77ac\u95f4\u88ab\u8fd8\u539f\u6210\u4e86\u7531\u5355\u7eaf\u7ebf\u6761\u6784\u6210\u7684\u6700\u7b80\u6d01\u8f6e\u5ed3\uff0c\u7136\u540e\u9010\u6e10\u53d8\u6de1\uff0c\u4e24\u79d2\u949f\u540e\u548c\u5149\u675f\u4e00\u8d77\u5b8c\u5168\u6d88\u5931\u4e86\u3002</p><p>\u201c\u55ef\uff0c\u770b\u6765\u8fd0\u6c14\u8fd8\u4e0d\u9519\u3002\u201d\u5965\u5609\u83b1\u65af\u81ea\u8a00\u81ea\u8bed\u7740\uff0c\u6536\u8d77\u4e66\uff0c\u7136\u540e\u62ac\u8d77\u5934\u6765\uff0c\u76ee\u5149\u7f13\u7f13\u4ece\u5728\u573a\u8bf8\u4eba\u7684\u8138\u4e0a\u626b\u8fc7\u3002\u5728\u770b\u5230\u838e\u73de\u514b\u7684\u65f6\u5019\uff0c\u5979\u6014\u4e86\u6014\uff0c\u4ed4\u7ec6\u6253\u91cf\u5979\u7247\u523b\uff0c\u53c8\u671d\u743c\u6069\u53cd\u590d\u770b\u4e86\u51e0\u773c\uff0c\u6447\u4e86\u6447\u5934\u3002</p><p>\u201c\u767d\u75f4\u3002\u201d\u5979\u8bc4\u4ef7\uff0c\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u56e0\u4e3a\u5c31\u5728\u8eab\u65c1\uff0c\u743c\u6069\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u4ed6\u504f\u4e86\u504f\u5934\uff0c\u81ea\u5632\u5730\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002</p><p>\u201c\u5927\u5965\u672f\u5e08\u9601\u4e0b\u2026\u2026\u201d</p><p>\u548c\u540c\u4f34\u4f4e\u58f0\u5546\u8bae\u7247\u523b\uff0c\u62c9\u52a0\u5fd0\u5fd1\u4e0d\u5b89\u5730\u8d70\u4e0a\u524d\uff0c\u6b63\u60f3\u8981\u8bf4\u8bdd\uff0c\u5374\u88ab\u5965\u5609\u83b1\u65af\u62ac\u624b\u5236\u6b62\u4e86\u3002\u201c\u51fa\u6765\u5427\u3002\u201d\u5979\u5bf9\u7740\u7a7a\u65e0\u4e00\u4eba\u7684\u5927\u6bbf\u95e8\u53e3\u8bf4\u3002</p><p>\u6240\u6709\u4eba\u90fd\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u9053\u5979\u5728\u5bf9\u8c01\u8bf4\u8bdd\uff0c\u4f46\u7b54\u6848\u5f88\u5feb\u5c31\u63ed\u6653\u4e86\u3002\u4f34\u968f\u7740\u4e00\u9635\u9707\u8033\u6b32\u804b\u7684\u54c8\u54c8\u5927\u7b11\u548c\u6c89\u91cd\u811a\u6b65\u58f0\uff0c\u56db\u4e2a\u4eba\u4ece\u95e8\u5916\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u9886\u5934\u7684\u662f\u4e00\u4f4d\u8eab\u6750\u9b41\u68a7\u7684\u4e2d\u5e74\u4eba\uff0c\u7537\u6027\uff0c\u5149\u5934\uff0c\u7edc\u816e\u80e1\u987b\uff0c\u5de6\u773c\u4e0a\u6234\u7740\u4e00\u53ea\u9ed1\u8272\u773c\u7f69\uff0c\u84dd\u767d\u8272\u957f\u888d\u4e0a\u753b\u7740\u95ea\u7535\u5f62\u72b6\u7684\u8840\u7ea2\u6761\u7eb9\u2014\u2014\u8fd9\u8eab\u6253\u626e\u743c\u6069\u770b\u7740\u6709\u4e9b\u773c\u719f\uff0c\u7136\u540e\u60f3\u4e86\u8d77\u6765\uff0c\u90a3\u6b21\u548c\u6885\u83f2\u65af\u4ece\u62c9\u6c83\u514b\u7684\u5deb\u5e08\u4e4b\u5893\u79bb\u5f00\uff0c\u56de\u9634\u9b42\u57ce\u7684\u8def\u4e0a\u7ecf\u8fc7\u5315\u9996\u6ee9\uff0c\u906d\u9047\u4e00\u7fa4\u8725\u8734\u4eba\uff0c\u5176\u4e2d\u6709\u4e2a\u5854\u6d1b\u65af\u7267\u5e08\u5c31\u662f\u8fd9\u526f\u6a21\u6837\u3002\u5728\u4e2d\u5e74\u4eba\u7684\u8eab\u540e\u7ad9\u7740\u4e24\u7537\u4e00\u5973\uff0c\u4e09\u4e2a\u4eba\u4e00\u5b57\u6392\u5f00\uff0c\u4e25\u9635\u4ee5\u5f85\u3002</p><p>\u5de6\u8fb9\u662f\u4e2a\u5de8\u4eba\uff0c\u4e0a\u534a\u8eab\u5b8c\u5168\u8d64\u88f8\uff0c\u4e0b\u534a\u8eab\u4ec5\u56f4\u7740\u4e00\u6761\u76ae\u88d9\uff0c\u51b0\u84dd\u8272\u7684\u76ae\u80a4\uff0c\u51b0\u84dd\u8272\u7684\u5934\u53d1\uff0c\u51b0\u84dd\u8272\u7684\u773c\u775b\uff0c\u5168\u8eab\u7b4b\u9aa8\u9686\u9686\uff0c\u808c\u8089\u5792\u8d77\uff0c\u7ed3\u5b9e\u5f97\u4eff\u4f5b\u94c1\u5757\uff0c\u4e00\u67c4\u6218\u65a7\u522b\u5728\u4ed6\u7684\u8170\u5e26\u4e0a\uff0c\u6676\u83b9\u900f\u660e\uff0c\u50cf\u662f\u51b0\u96d5\u7389\u7422\u800c\u6210\u3002\u8fd9\u663e\u7136\u662f\u4e2a\u971c\u5de8\u4eba\uff0c\u743c\u6069\u66fe\u7ecf\u542c\u6885\u83f2\u65af\u63d0\u8d77\u8fc7\u8fd9\u79cd\u751f\u6d3b\u5728\u5317\u5730\u7684\u51b0\u96ea\u4e4b\u6c11\uff0c\u636e\u8bf4\u662f\u5bd2\u51ac\u5973\u795e\u6b27\u5415\u5c14\u5728\u51e1\u95f4\u7684\u540e\u88d4\u2014\u2014\u8fd9\u79cd\u8bf4\u6cd5\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u4f46\u971c\u5de8\u4eba\u786e\u5b9e\u5168\u90fd\u5d07\u62dc\u6b27\u5415\u5c14\u3002\u5723\u6b66\u58eb\u6709\u4e00\u526f\u624b\u5957\uff0c\u5c31\u662f\u67d0\u4f4d\u971c\u5de8\u4eba\uff08\u6b27\u5415\u5c14\u7684\u9ad8\u9636\u7267\u5e08\uff09\u62ff\u81ea\u5df1\u7684\u76ae\u5236\u6210\u3002\u4e2d\u95f4\u662f\u4e2a\u719f\u4eba\uff0c\u5c31\u662f\u5728\u661f\u9668\u57ce\u5916\u88ab\u743c\u6069\u7b49\u4eba\u7fa4\u6bb4\uff0c\u6700\u540e\u53d8\u6210\u534a\u9ca8\u534a\u4eba\u602a\u7269\u9003\u8131\u7684\u90a3\u4f4d\u5b89\u535a\u7406\u7267\u5e08\uff0c\u4ed6\u5f53\u65f6\u88ab\u6253\u6210\u91cd\u4f24\uff0c\u80f3\u818a\u90fd\u88ab\u838e\u73de\u514b\u780d\u6389\u4e00\u53ea\uff0c\u5982\u4eca\u5374\u53c8\u91cd\u65b0\u957f\u4e86\u51fa\u6765\uff0c\u4ed6\u6068\u6068\u5730\u76ef\u7740\u743c\u6069\u7b49\u4eba\uff0c\u773c\u4e2d\u5c3d\u662f\u6012\u706b\uff0c\u50cf\u662f\u968f\u65f6\u4f1a\u6251\u4e0a\u6765\u4f3c\u7684\u3002\u7ad9\u5728\u53f3\u8fb9\u7684\u5219\u662f\u4f4d\u8eab\u6750\u5a07\u5c0f\u7684\u5973\u5b50\uff0c\u9762\u5bb9\u59e3\u597d\uff0c\u4e00\u5934\u5e72\u51c0\u5229\u843d\u7684\u706b\u7ea2\u77ed\u53d1\uff0c\u53cc\u773c\u6676\u83b9\u900f\u660e\u4eff\u4f5b\u7409\u7483\uff0c\u706b\u7ea2\u8272\u7d27\u8eab\u76ae\u8863\u7d27\u8d34\u5728\u5979\u8eab\u4e0a\uff0c\u73b2\u73d1\u6d6e\u51f8\uff0c\u66f2\u7ebf\u6bd5\u9732\uff0c\u642d\u5728\u8170\u95f4\u7684\u53cc\u624b\u4e0a\u6234\u7740\u4e00\u526f\u6697\u7ea2\u8272\u91d1\u5c5e\u722a\u5957\uff0c\u900f\u7740\u6bb7\u6bb7\u8840\u5149\u3002\u5979\u770b\u8d77\u6765\u662f\u4f4d\u7f8e\u8c8c\u7684\u4eba\u7c7b\u5c11\u5973\uff0c\u4f46\u4e24\u53ea\u5c16\u5c16\u7684\u732b\u8033\uff0c\u4ee5\u53ca\u81c0\u540e\u6447\u6643\u7684\u957f\u957f\u9ed1\u8272\u5c3e\u5df4\uff0c\u90fd\u6e05\u695a\u65e0\u7591\u5730\u8868\u660e\u4e86\u5979\u5e76\u975e\u5bfb\u5e38\u4eba\u7c7b\u3002</p><p>\u5979\u662f\u4e2a\u517d\u5316\u4eba\u3002</p><p>\u517d\u5316\u4eba\u4e0d\u540c\u4e8e\u201c\u517d\u4eba\u201d\uff0c\u8fd9\u662f\u4e24\u4e2a\u6781\u6613\u6df7\u6dc6\uff0c\u4f46\u5176\u5b9e\u6beb\u65e0\u5173\u8054\u7684\u6982\u5ff5\u3002\u517d\u4eba\u662f\u4e2a\u79cd\u65cf\uff0c\u56e0\u4e3a\u957f\u76f8\u4e11\u964b\uff08\u4ee5\u4eba\u7c7b\u7684\u5ba1\u7f8e\u89c2\u800c\u8a00\uff09\uff0c\u9752\u9762\u7360\u7259\u7eff\u76ae\u80a4\uff0c\u5f62\u5982\u72f0\u72de\u91ce\u517d\uff0c\u6240\u4ee5\u88ab\u79f0\u4e3a\u201c\u517d\u4eba\u201d\uff0c\u6709\u81ea\u5df1\u72ec\u6709\u7684\u795e\u660e\uff08\u73b0\u5728\u88ab\u7cbe\u7075\u795e\u548c\u7a46\u7f55\u745e\u5fb7\u8bf8\u795e\u8054\u624b\u6740\u5149\u4e86\uff09\uff1b\u800c\u517d\u5316\u4eba\uff0c\u5219\u662f\u5bf9\u90a3\u4e9b\u611f\u67d3\u4e86\u517d\u5316\u75c7\u7684\u4eba\u7684\u7edf\u79f0\uff0c\u6700\u5178\u578b\u7684\u5c31\u662f\u72fc\u4eba\uff0c\u4ed6\u4eec\u5176\u5b9e\u539f\u672c\u662f\u4eba\u7c7b\uff0c\u53ea\u662f\u611f\u67d3\u4e86\u75be\u75c5\uff08\u517d\u5316\u75c7\uff09\uff0c\u8eab\u4f53\u5f62\u6001\u4e43\u81f3\u5fc3\u7406\u90fd\u53d1\u751f\u53d8\u5316\uff0c\u8fd8\u4f1a\u53d7\u6708\u4eae\u7684\u5f71\u54cd\uff0c\u5b9a\u671f\u6216\u8005\u4e0d\u5b9a\u671f\u5730\u53d8\u8eab\u6210\u91ce\u517d\uff0c\u6b64\u65f6\u7ad9\u5728\u9762\u524d\u7684\uff0c\u663e\u7136\u5c31\u662f\u4e00\u4f4d\u732b\u5973\u3002\u517d\u5316\u4eba\u4e0d\u662f\u4e00\u79cd\u72ec\u7acb\u79cd\u65cf\uff0c\u81ea\u7136\u4e5f\u6ca1\u6709\u81ea\u5df1\u4e13\u5c5e\u7684\u795e\u7947\uff0c\u901a\u5e38\u6765\u8bf4\uff0c\u4fe1\u4ef0\u6708\u795e\u82cf\u4f26\u548c\u517d\u795e\u739b\u62c9\u7684\u6bd4\u8f83\u591a\uff0c\u524d\u8005\u662f\u5584\u826f\u517d\u5316\u4eba\u7684\u7688\u4f9d\uff0c\u540e\u8005\u662f\u90aa\u6076\u517d\u5316\u4eba\u7684\u5f15\u5bfc\u3002\u81f3\u4e8e\u8c7a\u72fc\u4eba\u3001\u8725\u8734\u4eba\u3001\u72d7\u5934\u4eba\u4e4b\u7c7b\uff0c\u65e2\u975e\u517d\u4eba\uff0c\u4e5f\u4e0d\u662f\u4eba\u7c7b\u611f\u67d3\u4e86\u517d\u5316\u75c7\uff0c\u53ea\u662f\u201c\u957f\u5f97\u65e2\u50cf\u4eba\u53c8\u50cf\u67d0\u79cd\u52a8\u7269\u201d\u7684\u751f\u7269\uff0c\u53c8\u662f\u53e6\u5916\u7684\u79cd\u65cf\u4e86\u3002</p><p>\u5854\u6d1b\u65af\u7267\u5e08\u3001\u971c\u5de8\u4eba\u3001\u5b89\u535a\u7406\u7267\u5e08\u3001\u732b\u5973\u2026\u2026\u8fd9\u79cd\u7ec4\u5408\u8fd8\u771f\u662f\u8be1\u5f02\u3002\u66f4\u8ba9\u743c\u6069\u60ca\u8bb6\uff0c\u6216\u8005\u8bf4\u60ca\u9a87\u7684\u662f\uff0c\u971c\u5de8\u4eba\u8170\u95f4\u7684\u6218\u65a7\u548c\u732b\u5973\u624b\u4e0a\u7684\u722a\u5957\uff0c\u5c45\u7136\u5168\u90fd\u662f\u6781\u5f3a\u529b\u7684\u5b9d\u7269\uff0c\u5355\u7eaf\u4ee5\u9b54\u6cd5\u7075\u5149\u7684\u5f3a\u5ea6\u6765\u5224\u65ad\uff0c\u5927\u7ea6\u548c\u5b89\u535a\u7406\u7684\u6ce2\u6d9b\u621f\u5c5e\u4e8e\u540c\u4e00\u4e2a\u5c42\u6b21\uff0c\u5373\u4fbf\u6709\u5dee\u8ddd\u4e5f\u4e0d\u4f1a\u5dee\u591a\u5c11\u2014\u2014\u6362\u8a00\u4e4b\uff0c\u4e5f\u90fd\u662f\u795e\u5668\u4e86\u3002\u5e78\u597d\u53e6\u5916\u4e24\u4e2a\u4eba\u90fd\u662f\u53cc\u624b\u7a7a\u7a7a\uff0c\u5426\u5219\u743c\u6069\u771f\u8981\u6000\u7591\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\uff0c\u795e\u5668\u6325\u6cea\u7529\u5356\u5927\u6279\u53d1\u4e86\u3002</p><p>\u62c9\u52a0\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u4ed6\u731c\u51fa\u4e86\u6765\u8005\u7684\u8eab\u4efd\uff0c\u201c\u2018\u98ce\u773c\u2019\u5361\u65af\u56fe\uff1f\u201d</p><p>\u201c\u539f\u6765\u6211\u8fd9\u6837\u6709\u540d\u5417\uff1f\u90a3\u53ef\u5f53\u771f\u662f\u53d7\u5ba0\u82e5\u60ca\u554a\uff0c\u201d\u9886\u5934\u7684\u4e2d\u5e74\u4eba\u4f3c\u4e4e\u975e\u5e38\u5f97\u610f\u5730\u7b11\u7740\uff0c\u9732\u51fa\u6ee1\u53e3\u6d01\u767d\u7259\u9f7f\uff0c\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u62c9\u52a0\u897f\u4e9a\u66fc\u7279\u65af\u5148\u751f\u4e86\uff0c\u5e78\u4f1a\u5e78\u4f1a\u3002\u6211\u662f\u5361\u65af\u56fe\uff0c\u8fd9\u4e09\u4f4d\u662f\u6211\u7684\u540c\u4f34\uff0c\u201d\u4ed6\u4f38\u624b\u5bf9\u7ad9\u5728\u8eab\u540e\u7684\u4e09\u4eba\u505a\u9010\u4e00\u4ecb\u7ecd\uff0c\u201c\u7a46\u4fee\xb7\u971c\u62f3\uff0c\u83ab\u9ed8\u65af\uff0c\u8fd9\u4f4d\u662f\u742a\u5a05\u5c0f\u59d0\u3002\u201d</p><p>\u62c9\u52a0\u9634\u6c89\u7740\u8138\uff0c\u76ee\u5149\u5728\u4e2d\u5e74\u4eba\u8eab\u540e\u7684\u4e09\u4e2a\u4eba\u8138\u4e0a\u8f6c\u4e86\u8f6c\uff0c\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u65e9\u8be5\u60f3\u5230\u624d\u5bf9\u2026\u2026\u98ce\u66b4\u4e4b\u795e\u7684\u2018\u6536\u5272\u2019\uff0c\u5df2\u7ecf\u5f00\u59cb\u4e86\u5417\uff1f\u201d</p><p>\u201c\u6536\u5272\u8fd9\u4e2a\u8bcd\u592a\u8840\u8165\u4e86\uff0c\u6211\u4e0d\u559c\u6b22\uff0c\u201d\u4e2d\u5e74\u4eba\u5f88\u8ba4\u771f\u5730\u56de\u7b54\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u4ecb\u610f\uff0c\u6211\u89c9\u5f97\u7528\u2018\u6536\u83b7\u2019\u66f4\u51c6\u786e\u4e9b\u3002\u770b\u6765\u4eca\u5929\u5c31\u662f\u4e2a\u6536\u83b7\u7684\u597d\u65e5\u5b50\uff0c\u4e00\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u4e00\u9897\u5b8c\u597d\u7684\u5bc6\u745f\u80fd\u6838\uff0c\u8fd8\u6709\u4e24\u4ef6\u795e\u5668\u2014\u2014\u554a\uff0c\u4e0d\u597d\u610f\u601d\uff0c\u5fd8\u4e86\u8bf4\u53e5\uff1a\u5404\u4f4d\uff0c\u4f60\u4eec\u7684\u4efb\u52a1\u5b8c\u6210\uff0c\u73b0\u5728\u53ef\u4ee5\u9000\u573a\u4e86\u3002\u201d\u4ed6\u6b6a\u4e86\u6b6a\u5634\u89d2\uff0c\u556a\u5730\u6253\u4e86\u4e2a\u54cd\u6307\u3002</p><p>\u201c\u6740\u5149\u4ed6\u4eec\uff01\u201d\u4ed6\u56a3\u5f20\u65e0\u6bd4\u5730\u53d1\u51fa\u547d\u4ee4\u3002</p><p>\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u971c\u5de8\u4eba\u53d1\u51fa\u4e00\u58f0\u7c97\u91ce\u7684\u543c\u53eb\uff0c\u62d4\u51fa\u4e86\u8170\u95f4\u6218\u65a7\u2014\u2014\u90a3\u539f\u672c\u5e94\u8be5\u662f\u4e00\u67c4\u9a91\u58eb\u65a7\uff0c\u9700\u8981\u53cc\u624b\u63e1\u6301\u624d\u80fd\u6325\u52a8\uff0c\u4f46\u5728\u5de8\u4eba\u7684\u624b\u4e2d\u5374\u6210\u4e86\u4e0d\u6298\u4e0d\u6263\u7684\u5355\u624b\u65a7\u3002\u4ed6\u5c06\u6218\u65a7\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u631f\u7740\u51b0\u5c51\u7684\u5bd2\u98ce\u4ece\u65a7\u5203\u4e2d\u547c\u5578\u5439\u51fa\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u5316\u8eab\u6210\u4e00\u5934\u51b0\u84dd\u8272\u731b\u517d\uff0c\u671d\u7740\u62c9\u52a0\u7b49\u4eba\u76f4\u51b2\u8fc7\u6765\u3002</p></article>"}}}]);
