(window.webpackJsonp=window.webpackJsonp||[]).push([[462],{484:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e8c\u8282 \u743c\u6069\u7684\u9690\u85cf\u6280\u80fd",content:"<article><h2>\u7b2c\u4e94\u5341\u4e8c\u8282 \u743c\u6069\u7684\u9690\u85cf\u6280\u80fd</h2><p>\u8fdb\u5165\u6d6e\u7a7a\u57ce\u7684\u65f6\u5019\uff0c\u62c9\u52a0\u5e26\u7740\u5341\u540d\u5c5e\u4e0b\uff0c\u5728\u548c\u5b89\u535a\u91cc\u7684\u6076\u6218\u4e2d\u6b7b\u6389\u56db\u540d\uff0c\u73b0\u5728\u8fde\u4ed6\u81ea\u5df1\u5728\u5185\u8fd8\u6709\u4e03\u4eba\u3002\u4e03\u540d\u9ad8\u9636\u5deb\u5e08\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\u65e0\u8bba\u653e\u5728\u5927\u9646\u7684\u54ea\u4e2a\u56fd\u5bb6\uff0c\u90fd\u7b97\u5f97\u4e0a\u662f\u4e00\u652f\u5f3a\u52b2\u6218\u529b\u3002\u7136\u800c\u8fd9\u4e2a\u971c\u5de8\u4eba\u89c6\u82e5\u65e0\u7269\uff0c\u5355\u67aa\u5339\u9a6c\u5c31\u6562\u6765\u51b2\u9635\uff0c\u8fd9\u4efd\u52c7\u6c14\u5b9e\u5728\u4ee4\u4eba\u94a6\u4f69\u3002</p><p>\u62c9\u52a0\u773c\u4e2d\u5389\u8292\u4e00\u95ea\uff0c\u6ca1\u6709\u60f3\u5230\u5bf9\u65b9\u8bf4\u7ffb\u8138\u5c31\u7ffb\u8138\uff0c\u4f46\u8fd9\u4e5f\u6b63\u4e2d\u4e0b\u6000\u3002\u4ed6\u5fc3\u91cc\u4e5f\u5f88\u6e05\u695a\uff0c\u4eca\u65e5\u4e4b\u5c40\uff0c\u52bf\u96be\u5584\u4e86\uff0c\u81ea\u5df1\u8fd9\u8fb9\u867d\u7136\u4eba\u591a\u52bf\u4f17\uff0c\u4f46\u521a\u521a\u548c\u5b89\u535a\u91cc\u6076\u6597\u4e86\u4e00\u573a\uff0c\u6cd5\u672f\u3001\u5b9d\u7269\u90fd\u6d88\u8017\u4e0d\u5c11\uff0c\u5e78\u597d\u64a4\u9000\u5f97\u53ca\u65f6\uff0c\u4fdd\u7559\u4e86\u90e8\u5206\u529b\u91cf\uff0c\u4f9d\u7136\u8fd8\u53ef\u4e00\u6218\u3002\u5230\u5e95\u8c01\u80dc\u8c01\u8d1f\uff0c\u9e7f\u6b7b\u8c01\u624b\uff0c\u5c1a\u672a\u53ef\u77e5\u5462\u3002</p><p>\u96c6\u4e2d\u706b\u529b\uff0c\u4e00\u51fb\u5e72\u6389\u4ed6\uff01</p><p>\u62c9\u52a0\u671d\u90e8\u4e0b\u505a\u4e86\u4e2a\u9690\u853d\u7684\u624b\u52bf\uff0c\u7136\u540e\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4e94\u9053\u6cd5\u672f\u88ab\u540c\u65f6\u91ca\u653e\u51fa\u6765\u3002\u7a7a\u6c14\u6025\u901f\u6d41\u52a8\uff0c\u5316\u4f5c\u4e24\u53ea\u900f\u660e\u5de8\u624b\uff0c\u4e00\u5de6\u4e00\u53f3\u5730\u6293\u5411\u971c\u5de8\u4eba\u7684\u53cc\u817f\uff0c\u963b\u788d\u4ed6\u7684\u524d\u51b2\u4e4b\u52bf\uff0c\u4e09\u56e2\u9634\u5f71\u9b3c\u9b45\u822c\u4ece\u5730\u9762\u5347\u8d77\uff0c\u5f20\u7259\u821e\u722a\u5730\u6251\u5411\u76ee\u6807\uff0c\u8bd5\u56fe\u8499\u853d\u5176\u8033\u76ee\uff0c\u9ebb\u75f9\u5176\u80a2\u4f53\uff0c\u6dc6\u4e71\u5176\u5fc3\u667a\u3002\u7d27\u63a5\u7740\u55e4\u55e4\u7834\u7a7a\u58f0\u63a5\u8fde\u54cd\u8d77\uff0c\u56db\u652f\u5bd2\u5149\u51db\u51db\u7684\u7cbe\u91d1\u77e2\u4ece\u4e0d\u540c\u89d2\u5ea6\u5c04\u8fc7\u6765\u3002</p><p>\u547c\uff01</p><p>\u731b\u70c8\u7684\u98d3\u98ce\u81ea\u971c\u5de8\u4eba\u4f53\u5185\u6d8c\u51fa\uff0c\u547c\u5578\u6210\u65cb\uff0c\u671d\u56db\u9762\u516b\u65b9\u6269\u5f20\u5f00\u53bb\uff0c\u5c06\u51fb\u4e2d\u4ed6\u7684\u6240\u6709\u6cd5\u672f\u5168\u90fd\u5f39\u5f00\u3002\u968f\u540e\u5c04\u6765\u7684\u7cbe\u91d1\u77e2\u88ab\u8fd9\u98d3\u98ce\u5439\u5f97\u4e1c\u5012\u897f\u6b6a\uff0c\u51c6\u5934\u5927\u5931\uff0c\u5176\u4e2d\u4e09\u652f\u76f4\u63a5\u5c31\u5c04\u7a7a\uff0c\u6700\u540e\u4e00\u652f\u867d\u7136\u5c04\u4e2d\u80f8\u53e3\uff0c\u5374\u5df2\u7ecf\u6ca1\u4ec0\u4e48\u529b\u9053\uff0c\u8fde\u76ae\u80a4\u90fd\u6ca1\u80fd\u7a7f\u900f\u3002\u98d3\u98ce\u6321\u5f00\u653b\u51fb\u540e\u5e76\u672a\u6d88\u5931\uff0c\u800c\u662f\u76d8\u65cb\u7f20\u7ed5\u7740\uff0c\u5c06\u971c\u5de8\u4eba\u539f\u672c\u5168\u65e0\u9632\u5fa1\u7684\u8eab\u8eaf\u4e25\u5bc6\u4fdd\u62a4\u8d77\u6765\u3002</p><p>\u201c\u66b4\u98ce\u969c\u58c1\uff1f\u201d</p><p>\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u98ce\u53ea\u662f\u7a7a\u6c14\u7684\u6d41\u52a8\uff0c\u5c31\u7b97\u662f\u518d\u731b\u70c8\u518d\u6709\u529b\u91cf\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u62e8\u5f00\u7bad\u77e2\uff0c\u4e0d\u53ef\u80fd\u5c06\u8fde\u65e0\u5f62\u7684\u9b54\u6cd5\u90fd\u53cd\u5f39\u5f00\uff0c\u80fd\u591f\u505a\u5230\u8fd9\u70b9\u7684\uff0c\u53ea\u6709\u201c\u66b4\u98ce\u969c\u58c1\u201d\u3002\u800c\u66b4\u98ce\u969c\u58c1\u5e76\u4e0d\u662f\u5bfb\u5e38\u6cd5\u672f\uff0c\u90a3\u662f\u98ce\u66b4\u4e4b\u795e\u8d50\u4e88\u4fe1\u5f92\u7684\u6700\u9ad8\u9636\u795e\u672f\u4e4b\u4e00\uff0c\u5728\u6574\u4e2a\u5854\u6d1b\u65af\u6559\u4f1a\u4e2d\uff0c\u80fd\u591f\u8fd0\u7528\u7684\u4eba\u4e5f\u5e94\u8be5\u662f\u5c48\u6307\u53ef\u6570\u3002</p><p>\u773c\u770b\u653b\u51fb\u5f92\u52b3\u65e0\u529f\uff0c\u62c9\u52a0\u5374\u4e5f\u4e1d\u6beb\u4e0d\u89c9\u610f\u5916\uff0c\u4ed6\u4f4e\u4f4e\u5ff5\u51fa\u5492\u8bed\uff0c\u542f\u52a8\u4e86\u4e00\u4e2a\u521a\u521a\u6697\u4e2d\u5b8c\u6210\u7684\u6cd5\u672f\u3002\u4e03\u8292\u661f\u9b54\u6cd5\u9635\u5728\u971c\u5de8\u4eba\u811a\u4e0b\u4e00\u95ea\u800c\u901d\uff0c\u968f\u5373\u4ed6\u7684\u5168\u8eab\u6bcf\u4e2a\u6bdb\u5b54\u4e2d\u90fd\u201c\u8f70\u201d\u5730\u55b7\u51fa\u78a7\u5e7d\u5e7d\u7684\u706b\u82d7\u6765\uff0c\u8f6c\u773c\u95f4\u6210\u4e86\u4e00\u652f\u5de8\u5927\u7684\u4eba\u5f62\u8721\u70db\u3002</p><p>\u201c\u54fc\u54fc\uff01\u201d\u62c9\u52a0\u51b7\u7b11\u8d77\u6765\uff0c\u8fd9\u662f\u201c\u70c8\u7130\u9001\u846c\u201d\uff0c\u8010\u745f\u65f6\u4ee3\u5927\u5965\u672f\u5e08\u6258\u7a46\u5c14\u7684\u5f97\u610f\u53d1\u660e\uff0c\u6070\u597d\u662f\u66b4\u98ce\u969c\u58c1\u5b8c\u5168\u8d77\u4e0d\u5230\u62b5\u6321\u4f5c\u7528\u7684\u5c11\u6570\u51e0\u79cd\u6cd5\u672f\u4e4b\u4e00\u3002\u800c\u62c9\u52a0\u53c8\u5728\u5176\u4e2d\u6dfb\u52a0\u65b0\u7684\u53d8\u5316\uff0c\u8ba9\u539f\u672c\u666e\u901a\u7684\u9b54\u6cd5\u706b\u7130\u9644\u5e26\u4e0a\u4e86\u4ea1\u7075\u672f\u7684\u6548\u679c\uff0c\u66f4\u9690\u853d\uff0c\u4e5f\u66f4\u6076\u6bd2\u3002\u971c\u5de8\u4eba\u4e2d\u4e86\u8fd9\u4e00\u51fb\uff0c\u5c31\u7b97\u4e0d\u88ab\u7acb\u523b\u6bd9\u6740\uff0c\u4e5f\u5e94\u8be5\u4f1a\u6682\u65f6\u4e27\u5931\u6218\u6597\u529b\uff0c\u4e0d\u8db3\u4e3a\u60e7\u4e86\u3002</p><p>\u971c\u5de8\u4eba\u4e00\u58f0\u6012\u543c\uff0c\u98d3\u98ce\u5939\u6742\u7740\u96ea\u82b1\u548c\u51b0\u68f1\u4ece\u624b\u4e2d\u6218\u65a7\u7684\u950b\u5203\u4e2d\u5439\u51fa\uff0c\u51b2\u5411\u81ea\u8eab\uff0c\u7728\u773c\u95f4\u5c45\u7136\u5c06\u706b\u7130\u786c\u751f\u751f\u201c\u51bb\u7ed3\u201d\u4f4f\u4e86\uff0c\u770b\u8d77\u6765\u4eff\u4f5b\u8eab\u4e0a\u6302\u7740\u4e00\u4e2a\u4e2a\u5c0f\u5c0f\u7684\u51b0\u96d5\uff0c\u8fd9\u60c5\u5f62\u786e\u5b9e\u662f\u8be1\u5f02\u65e0\u6bd4\uff0c\u4f46\u62c9\u52a0\u7684\u70c8\u7130\u9001\u846c\u672f\u4e5f\u56e0\u6b64\u800c\u53d8\u5f97\u5f7b\u5e95\u65e0\u6548\u4e86\u3002\u53c8\u6709\u4e24\u652f\u7cbe\u91d1\u77e2\u8fce\u9762\u5c04\u6765\uff0c\u971c\u5de8\u4eba\u5c06\u6218\u65a7\u5728\u80f8\u53e3\u4e00\u6a2a\uff0c\u94db\u94db\u4e24\u58f0\uff0c\u5bbd\u5927\u7684\u65a7\u9762\u4eff\u4f5b\u76fe\u724c\uff0c\u76f4\u63a5\u5c06\u7bad\u77e2\u6321\u5f00\u3002</p><p>\u201c\u53d7\u6b7b\u5427\uff01\u201d</p><p>\u53cc\u65b9\u7684\u8ddd\u79bb\u539f\u672c\u5c31\u4e0d\u8fdc\uff0c\u971c\u5de8\u4eba\u53c8\u8eab\u9ad8\u817f\u957f\uff0c\u6b65\u4f10\u6781\u5927\uff0c\u625b\u8fc7\u9634\u9b42\u57ce\u5deb\u5e08\u7684\u9996\u8f6e\u653b\u51fb\u540e\uff0c\u4ed6\u4fbf\u6210\u529f\u5730\u903c\u5230\u4e86\u8fd1\u524d\u3002\u6218\u65a7\u4e00\u4e3e\uff0c\u7167\u7740\u62c9\u52a0\u5f53\u5934\u5c31\u5288\u4e0b\u6765\u3002</p><p>\u62c9\u52a0\u8fd8\u4fdd\u7559\u4e86\u51e0\u4e2a\u9632\u5fa1\u6cd5\u672f\uff0c\u4f46\u4ed6\u53ef\u6ca1\u4fe1\u5fc3\u7528\u5b83\u4eec\u6765\u62b5\u6321\u795e\u5668\u7684\u653b\u51fb\uff0c\u8981\u77e5\u9053\u8fd9\u53ef\u662f\u4e00\u67c4\u8d27\u771f\u4ef7\u5b9e\u4e13\u95e8\u7528\u6765\u780d\u4eba\u7684\u6218\u65a7\uff0c\u4e0d\u662f\u50cf\u5b89\u535a\u7406\u7684\u6ce2\u6d9b\u621f\u90a3\u79cd\u6cd5\u5668\u3002\u4ed6\u654f\u6377\u5730\u5f80\u540e\u4e00\u8df3\uff0c\u8eb2\u5f00\u653b\u51fb\uff0c\u971c\u5de8\u4eba\u4eff\u4f5b\u7528\u529b\u8fc7\u731b\uff0c\u6536\u52bf\u4e0d\u4f4f\uff0c\u4e00\u65a7\u5934\u76f4\u63a5\u780d\u5728\u4e86\u5730\u9762\u4e0a\u3002</p><p>\u5237\uff01</p><p>\u6ca1\u6709\u9884\u60f3\u4e2d\u7684\u706b\u661f\u56db\u6e85\uff0c\u53cd\u800c\u662f\u900f\u660e\u51b0\u6c14\u81ea\u6218\u65a7\u7684\u950b\u5203\u4e2d\u6ea2\u51fa\uff0c\u56db\u9762\u6269\u6563\uff0c\u8f6c\u773c\u95f4\u8ba9\u65b9\u5706\u5341\u5c3a\u5185\u7684\u5730\u9762\u5168\u90fd\u8986\u76d6\u4e0a\u4e86\u4e00\u5c42\u6de1\u84dd\u8272\u5bd2\u51b0\u3002\u62c9\u52a0\u6ca1\u6599\u5230\u5bf9\u65b9\u6709\u8fd9\u4e00\u624b\uff0c\u4ed6\u6b63\u5728\u540e\u9000\uff0c\u4e00\u811a\u8e29\u5728\u5149\u6ed1\u7684\u51b0\u9762\u4e0a\uff0c\u987f\u65f6\u7acb\u8db3\u4e0d\u7a33\uff0c\u5dee\u70b9\u5c31\u6454\u4e86\u4e2a\u4ef0\u9762\u671d\u5929\uff0c\u5176\u4ed6\u5deb\u5e08\u4e5f\u7eb7\u7eb7\u540e\u64a4\uff0c\u60f3\u8981\u9003\u79bb\u8fd9\u7247\u51b0\u9762\uff0c\u971c\u5de8\u4eba\u5374\u5927\u6b65\u8ffd\u4e0a\u6765\uff0c\u5982\u5c65\u5e73\u5730\uff0c\u5168\u7136\u4e0d\u53d7\u534a\u70b9\u5f71\u54cd\u3002\u8d81\u7740\u5bf9\u624b\u624b\u5fd9\u811a\u4e71\u7684\u673a\u4f1a\uff0c\u4ed6\u4e00\u8bb0\u6218\u65a7\u6a2a\u626b\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u7834\u5f00\u91cd\u91cd\u9b54\u6cd5\u9632\u5fa1\uff0c\u5c06\u4e00\u540d\u5012\u9709\u5deb\u5e08\u7684\u8111\u888b\u524a\u6389\u4e86\u4e09\u5206\u4e4b\u4e00\uff0c\u8840\u6db2\u548c\u8111\u6d46\u5374\u534a\u70b9\u6ca1\u6709\u55b7\u51fa\u2014\u2014\u76f4\u63a5\u5c31\u88ab\u6218\u65a7\u4e0a\u7684\u5bd2\u6c14\u51dd\u7ed3\u6210\u51b0\u4e86\u3002</p><p>\u201c\u53ef\u6076\uff01\u201d</p><p>\u62c9\u52a0\u7ad9\u7a33\u8eab\u4f53\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u63a5\u8fde\u635f\u5175\u6298\u5c06\uff0c\u8ba9\u4ed6\u7684\u60c5\u7eea\u5df2\u7ecf\u6076\u52a3\u5230\u4e86\u6781\u81f4\u3002\u5728\u9634\u9b42\u57ce\u4e2d\uff0c\u4ed6\u5c5e\u4e8e\u5927\u5668\u665a\u6210\u7684\u90a3\u79cd\u7c7b\u578b\uff0c\u5f53\u5e74\u5728\u5b66\u6821\u8bfb\u4e66\u65f6\u6210\u7ee9\u5e73\u5e73\uff0c\u6bd5\u4e1a\u540e\u5728\u519b\u961f\u91cc\u6df7\u4e86\u4efd\u5dee\u4f7f\uff0c\u4e5f\u6ca1\u591a\u5c11\u6770\u51fa\u8868\u73b0\uff0c\u4e00\u76f4\u6ca1\u80fd\u5f97\u5230\u664b\u5347\u3002\u76f4\u5230\u56db\u5341\u5c81\u65f6\uff0c\u4ed6\u7a81\u7136\u8111\u7b4b\u5f00\u7a8d\u4e86\uff0c\u4e0d\u4f46\u5728\u5bb6\u65cf\u64c5\u957f\u7684\u5851\u80fd\u672f\u4e0a\u7a81\u98de\u731b\u8fdb\uff0c\u800c\u4e14\u663e\u9732\u51fa\u4e86\u60ca\u4eba\u7684\u9884\u8a00\u672f\u5929\u8d4b\uff0c\u6700\u7ec8\u88ab\u5e03\u96f7\u7eb3\u65af\u770b\u4e2d\uff0c\u6536\u4e3a\u5f1f\u5b50\u3002\u8fd9\u6b21\u641c\u5bfb\u6253\u635e\u661f\u9668\u57ce\u7684\u4efb\u52a1\uff0c\u539f\u672c\u5e03\u96f7\u7eb3\u65af\u5e76\u6ca1\u6709\u8003\u8651\u62c9\u52a0\uff0c\u4ed6\u4e00\u5f00\u59cb\u6253\u7b97\u59d4\u6d3e\u7684\u662f\u53e6\u4e00\u4f4d\u5deb\u5e08\uff0c\u662f\u62c9\u52a0\u4e3b\u52a8\u8bf7\u7f28\u624d\u4e89\u53d6\u5230\u4e86\u8fd9\u4e2a\u673a\u4f1a\u3002\u5bf9\u4e8e\u62c9\u52a0\u6765\u8bf4\uff0c\u6b64\u4e8b\u82e5\u6210\uff0c\u4e0d\u4f46\u610f\u5473\u7740\u5730\u4f4d\u548c\u58f0\u671b\u7684\u4e0a\u5347\uff0c\u66f4\u91cd\u8981\u7684\u662f\u4ed6\u6781\u6709\u53ef\u80fd\u56e0\u6b64\u5f97\u8499\u9ad8\u5c42\u7684\u8d4f\u8bc6\uff0c\u6210\u4e3a\u201c\u9634\u9b42\u201d\uff0c\u4ece\u6b64\u62e5\u6709\u8fd1\u4e4e\u4e0d\u8001\u4e0d\u6b7b\u7684\u751f\u547d\u3002\u8c01\u6599\u4e8b\u60c5\u51fa\u4e4e\u610f\u6599\u5730\u590d\u6742\uff0c\u5148\u662f\u5728\u6851\u6bd4\u4e9a\u8fd9\u91cc\u5927\u6d77\u635e\u9488\u4e86\u5927\u534a\u5e74\uff0c\u4e00\u65e0\u6240\u83b7\uff0c\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u4e86\u661f\u9668\u57ce\uff0c\u91cc\u9762\u5c45\u7136\u8eb2\u7740\u4e00\u4f4d\u795e\u7947\uff0c\u597d\u4e0d\u5bb9\u6613\u5e72\u6389\u4e86\u5b89\u535a\u7406\uff0c\u773c\u524d\u8fd9\u56db\u4e2a\u5bb6\u4f19\u53c8\u5192\u51fa\u6765\u3002\u8fd9\u6837\u4e0b\u53bb\u7684\u8bdd\uff0c\u5c31\u7b97\u81ea\u5df1\u4eca\u5929\u80fd\u591f\u8131\u8eab\uff0c\u53ea\u6015\u4e5f\u8981\u4e0a\u519b\u4e8b\u6cd5\u5ead\uff0c\u540e\u534a\u8f88\u5b50\u518d\u4e0d\u7528\u6307\u671b\u4e86\u3002</p><p>\u4e00\u53ea\u5730\u72f1\u4e09\u5934\u72ac\u88ab\u53ec\u5524\u51fa\u6765\uff0c\u6321\u4f4f\u4e86\u971c\u5de8\u4eba\uff0c\u66f4\u591a\u7684\u6cd5\u672f\u671d\u7740\u4ed6\u5bc6\u96c6\u8f70\u70b8\uff0c\u5176\u4e2d\u5c0f\u90e8\u5206\u6210\u529f\u7a7f\u900f\u4e86\u66b4\u98ce\u969c\u58c1\uff0c\u5bf9\u76ee\u6807\u9020\u6210\u4e86\u4e00\u5b9a\u4f24\u5bb3\uff0c\u4f46\u5e76\u4e0d\u4e25\u91cd\u3002\u971c\u5de8\u4eba\u672c\u5c31\u662f\u5de8\u4eba\u7684\u4e9a\u79cd\uff0c\u53c8\u5e38\u5e74\u751f\u6d3b\u5728\u51b0\u5929\u96ea\u5730\u7684\u4e25\u9177\u73af\u5883\u4e4b\u4e2d\uff0c\u4f53\u683c\u4e4b\u5f3a\u5065\u81ea\u975e\u5bfb\u5e38\uff0c\u80fd\u591f\u4e00\u51fb\u91cd\u4f24\u4eba\u7c7b\u7684\u5492\u672f\uff0c\u6253\u5728\u971c\u5de8\u4eba\u8eab\u4e0a\u53ef\u80fd\u5c31\u53ea\u662f\u75db\u4e00\u4e0b\u7f62\u4e86\uff0c\u8d77\u4e0d\u5230\u591a\u5927\u6548\u679c\u3002\u597d\u5728\u971c\u5de8\u4eba\u667a\u529b\u4e0d\u9ad8\uff0c\u653b\u51fb\u624b\u6bb5\u4e5f\u9887\u4e3a\u5355\u8c03\uff0c\u4e3b\u8981\u5c31\u662f\u76f4\u63a5\u7528\u6218\u65a7\u6325\u780d\uff0c\u795e\u672f\u90fd\u4e0d\u600e\u4e48\u7528\uff0c\u62c9\u52a0\u7b49\u4eba\u4e00\u5f00\u59cb\u662f\u88ab\u6253\u4e86\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u9002\u5e94\u4e86\u4e5f\u5c31\u4e0d\u96be\u5e94\u4ed8\uff0c\u4f46\u4e00\u65f6\u95f4\u4e5f\u5948\u4f55\u4e0d\u5f97\u5bf9\u65b9\uff0c\u5c40\u9762\u4fbf\u6682\u65f6\u50f5\u6301\u4e86\u8d77\u6765\u3002</p><p>\u4e0e\u6b64\u540c\u65f6\uff0c\u5927\u6bbf\u7684\u53e6\u5916\u4e00\u5904\uff0c\u4e5f\u6b63\u7206\u53d1\u7740\u6fc0\u6218\u3002</p><p>\u971c\u5de8\u4eba\u548c\u62c9\u52a0\u7b49\u4eba\u4ea4\u4e0a\u624b\u4e4b\u540e\uff0c\u90a3\u4f4d\u53eb\u505a\u742a\u5a05\u7684\u732b\u5973\u548c\u4f1a\u53d8\u9ca8\u5934\u602a\u7269\u7684\u5b89\u535a\u7406\u7267\u5e08\u4e5f\u5f00\u59cb\u884c\u52a8\uff0c\u4ed6\u4eec\u4e00\u5de6\u4e00\u53f3\uff0c\u540c\u65f6\u671d\u7740\u743c\u6069\u51b2\u8fc7\u6765\u3002\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u89e3\u81ea\u5df1\u4e3a\u4f55\u4f1a\u53d7\u5230\u5982\u6b64\u770b\u91cd\uff0c\u4f46\u663e\u7136\u95ee\u5bf9\u65b9\u4e5f\u4e0d\u4f1a\u6709\u56de\u7b54\u3002\u66f4\u4ee4\u4ed6\u65e2\u8bb6\u4e14\u6012\u7684\u662f\uff0c\u5965\u5609\u83b1\u65af\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u4f46\u4e0d\u5e2e\u5fd9\uff0c\u53cd\u800c\u8fdc\u8fdc\u9000\u5f00\u51e0\u6b65\uff0c\u4eff\u4f5b\u6253\u7b97\u7f6e\u8eab\u4e8b\u5916\u770b\u70ed\u95f9\u4e00\u822c\u3002\u552f\u4e00\u503c\u5f97\u5e86\u5e78\u7684\u662f\uff0c\u5bf9\u65b9\u90a3\u4e2a\u9886\u5934\u7684\u5149\u5934\u4e2d\u5e74\u4eba\u9009\u62e9\u4e86\u548c\u5965\u5609\u83b1\u65af\u540c\u6837\u7684\u6001\u5ea6\uff0c\u53ea\u662f\u6307\u6325\u624b\u4e0b\u4e0a\u573a\uff0c\u81ea\u5df1\u5374\u7559\u5728\u540e\u9762\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6682\u65f6\u4f3c\u4e4e\u6ca1\u6709\u53c2\u6218\u7684\u6253\u7b97\u3002</p><p>\u732b\u5973\u5954\u8dd1\u7740\uff0c\u5feb\u82e5\u95ea\u7535\uff0c\u8fc5\u82e5\u96f7\u9706\uff0c\u8fd1\u4e09\u5341\u5c3a\u7684\u8ddd\u79bb\u88ab\u77ac\u95f4\u8de8\u8d8a\uff0c\u4e00\u7728\u773c\u529f\u592b\u4fbf\u5df2\u8feb\u8fd1\u743c\u6069\u8ddf\u524d\uff0c\u5979\u731b\u7136\u505c\u987f\u3001\u5c48\u8eab\u3001\u5f13\u8170\uff0c\u63a5\u7740\u6574\u4e2a\u4eba\u5f53\u771f\u4fbf\u50cf\u4e00\u53ea\u7075\u732b\u822c\u9ad8\u9ad8\u5f39\u8d77\u6765\uff0c\u7409\u7483\u53cc\u773c\u4e2d\u91d1\u5149\u7efd\u653e\uff0c\u8138\u988a\u548c\u989d\u5934\u4e0a\u6d6e\u73b0\u51fa\u51e0\u9053\u8be1\u5f02\u7684\u6697\u9752\u82b1\u7eb9\uff0c\u4e24\u9897\u5c16\u5c16\u5229\u9f7f\u81ea\u5507\u89d2\u7a81\u51fa\uff0c\u8ba9\u5979\u539f\u672c\u67d4\u5a9a\u79c0\u4e3d\u7684\u9762\u5bb9\u5239\u90a3\u95f4\u7b3c\u7f69\u4e86\u4e00\u5c42\u72f0\u72de\u6740\u6c14\uff0c\u53cc\u624b\u4e92\u76f8\u4ea4\u51fb\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\uff0c\u8840\u7ea2\u8272\u7684\u722a\u5957\u4e0a\u987f\u65f6\u7206\u51fa\u4e24\u5927\u56e2\u70bd\u70c8\u706b\u7130\u3002\u732b\u5973\u63a5\u7740\u5389\u5578\u4e00\u58f0\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u5229\u7bad\u822c\u4fef\u51b2\u800c\u4e0b\uff0c\u53cc\u81c2\u76f4\u4f38\uff0c\u671d\u743c\u6069\u5f53\u5934\u6293\u4e0b\u3002</p><p>\u94ff\uff01</p><p>\u732b\u5973\u8fd9\u4e00\u51fb\u5feb\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u4ee5\u743c\u6069\u7684\u53cd\u5e94\u901f\u5ea6\uff0c\u5c31\u7b97\u662f\u65e9\u6709\u51c6\u5907\u4e5f\u5f88\u96be\u8eb2\u5f00\uff0c\u6240\u5e78\u4ed6\u8eab\u8fb9\u8fd8\u6709\u62a4\u536b\u3002\u838e\u73de\u514b\u53ca\u65f6\u4e0a\u524d\uff0c\u5de6\u624b\u6301\u5251\u67b6\u4f4f\u4e86\u70c8\u7130\u722a\u5957\uff0c\u53f3\u624b\u865a\u7a7a\u4e00\u6296\uff0c\u638c\u5fc3\u5df2\u7ecf\u63e1\u4f4f\u4e86\u7194\u5ca9\u97ad\uff0c\u7167\u7740\u732b\u5973\u7b14\u76f4\u62bd\u4e0b\u3002\u732b\u5973\u4eba\u5728\u534a\u7a7a\uff0c\u539f\u672c\u5e94\u8be5\u8d8b\u907f\u4e0d\u7075\u624d\u5bf9\uff0c\u4f46\u5979\u8eab\u624b\u5374\u654f\u6377\u5f97\u532a\u5937\u6240\u601d\uff0c\u5de6\u624b\u722a\u5957\u5728\u838e\u73de\u514b\u7684\u77ed\u5251\u4e0a\u4e00\u63a8\uff0c\u987a\u52bf\u501f\u529b\u5411\u53f3\u4fa7\u659c\u659c\u843d\u5730\uff0c\u6b63\u597d\u907f\u5f00\u7194\u5ca9\u97ad\u7684\u62bd\u51fb\u3002</p><p>\u838e\u73de\u514b\u7d27\u8ffd\u800c\u81f3\uff0c\u5de6\u624b\u77ed\u5251\u76f4\u523a\uff0c\u53f3\u624b\u957f\u97ad\u6a2a\u626b\uff0c\u5f00\u59cb\u4e86\u75be\u98ce\u66b4\u96e8\u822c\u7684\u653b\u52bf\u3002\u732b\u5973\u53e3\u4e2d\u4e0d\u65ad\u53d1\u51fa\u55b5\u545c\u55b5\u545c\u7684\u51c4\u5389\u5578\u58f0\uff0c\u6325\u821e\u722a\u5957\u53d1\u52a8\u53cd\u51fb\u3002\u8fd9\u4e24\u4eba\u90fd\u662f\u6807\u51c6\u7684\u9ad8\u654f\u6b66\u8005\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u4ee5\u5feb\u6253\u5feb\uff0c\u4e00\u6253\u8d77\u6765\u4fbf\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\uff0c\u53ea\u89c1\u4e24\u9053\u540c\u6837\u7a88\u7a95\u6027\u611f\u7684\u8eab\u5f71\uff0c\u6325\u52a8\u7740\u5371\u9669\u7684\u6740\u4eba\u5229\u5668\uff0c\u4ea4\u9519\u7f16\u7ec7\u6210\u6b7b\u4ea1\u7684\u6f29\u6da1\u3002\u732b\u5973\u663e\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u5979\u624b\u4e0a\u7684\u722a\u5957\u4e0d\u65f6\u7206\u51fa\u70c8\u7130\uff0c\u5176\u4e2d\u660e\u663e\u8574\u542b\u7740\u795e\u5723\u6c14\u606f\uff0c\u5bf9\u90aa\u9b54\u6709\u7740\u5f3a\u70c8\u7684\u538b\u5236\u4f5c\u7528\u3002\u5e78\u597d\u838e\u73de\u514b\u8fd9\u4e2a\u9b45\u9b54\u5e76\u4e0d\u7eaf\u7cb9\uff0c\u6240\u53d7\u5f71\u54cd\u4e0d\u5927\uff0c\u5426\u5219\u53ea\u6015\u65e9\u5c31\u843d\u8d25\u4e86\u3002</p><p>\u743c\u6069\u63e1\u7d27\u6cd5\u6756\uff0c\u6b63\u8981\u63f4\u6551\u838e\u73de\u514b\uff0c\u53e6\u4e00\u540d\u5bf9\u624b\u5df2\u7ecf\u903c\u8fd1\u3002\u5b89\u535a\u91cc\u7267\u5e08\u53cc\u624b\u7528\u529b\u5730\u6376\u6253\u8179\u90e8\uff0c\u731b\u7136\u5927\u5927\u5f20\u5f00\u5634\uff0c\u9732\u51fa\u4e24\u6392\u9ca8\u9c7c\u822c\u7684\u5229\u9f7f\uff0c\u201c\u5455\u201d\u5730\u4e00\u58f0\u5410\u51fa\u4e86\u4e00\u5927\u6ee9\u6c61\u6c34\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u4e24\u4e2a\u624b\u6301\u957f\u53c9\u7684\u5bc7\u6d9b\u9c7c\u4eba\u4ece\u4e2d\u8df3\u51fa\uff0c\u55f7\u55f7\u5730\u602a\u53eb\u7740\uff0c\u671d\u743c\u6069\u51b2\u6740\u8fc7\u6765\u3002</p><p>\u4e24\u540d\u5353\u5c14\u6218\u58eb\u9b54\u50cf\u5e76\u80a9\u4e0a\u524d\uff0c\u5237\u5237\u51e0\u5251\u4fbf\u5c06\u4e24\u4e2a\u5bc7\u6d9b\u9c7c\u4eba\u780d\u6210\u4e86\u56db\u622a\uff0c\u4f46\u7d27\u63a5\u7740\u53c8\u6709\u5176\u4ed6\u5947\u5f62\u602a\u72b6\u7684\u6d77\u602a\u4ece\u6c61\u6c34\u4e2d\u722c\u51fa\uff0c\u4e00\u6ce2\u4e00\u6ce2\u5730\u6d8c\u4e0a\u6765\u3002\u9b54\u50cf\u662f\u5ca9\u77f3\u8eaf\u4f53\uff0c\u575a\u56fa\u8010\u6253\uff0c\u5012\u662f\u4e0d\u60e7\u8fd9\u4e9b\u867e\u5175\u87f9\u5c06\uff0c\u6a2a\u780d\u7ad6\u6740\uff0c\u6240\u5411\u62ab\u9761\uff0c\u4f46\u654c\u4eba\u6570\u91cf\u5b9e\u5728\u662f\u592a\u591a\uff0c\u4eff\u4f5b\u65e0\u7a77\u65e0\u5c3d\u822c\uff0c\u524d\u8d74\u540e\u7ee7\uff0c\u608d\u4e0d\u754f\u6b7b\uff0c\u786c\u751f\u751f\u5c06\u9b54\u50cf\u7ed9\u56f4\u56f0\u4f4f\u4e86\u3002</p><p>\u5636\u5636\uff01\u5636\u5636\uff01</p><p>\u8d81\u7740\u9b54\u50cf\u548c\u6d77\u602a\u6df7\u6218\uff0c\u5b89\u535a\u91cc\u7267\u5e08\u5b8c\u6210\u4e86\u4e00\u4e2a\u65b0\u7684\u6cd5\u672f\uff0c\u9ecf\u7a20\u7684\u9ed1\u8272\u6db2\u4f53\u4ece\u4ed6\u53e3\u4e2d\u3001\u9f3b\u5b54\u4e2d\u3001\u8033\u6735\u548c\u773c\u7736\u91cc\u6d41\u6dcc\u51fa\u6765\uff0c\u4e00\u89c1\u98ce\u5c31\u51dd\u805a\uff0c\u5316\u4f5c\u5bd2\u5149\u95ea\u95ea\u7684\u9cde\u7532\u3002\u4ed6\u7684\u8eab\u4f53\u4eff\u4f5b\u8f6f\u6ce5\u822c\u62c9\u957f\u3001\u5ef6\u4f38\uff0c\u530d\u5310\u5728\u5730\uff0c\u6700\u7ec8\u53d8\u6210\u4e86\u4e00\u6761\u5de8\u5927\u7684\u87d2\u86c7\u3002\u4e09\u89d2\u5f62\u7684\u86c7\u5934\u4e0a\u957f\u7740\u9c9c\u7ea2\u9e21\u51a0\uff0c\u6697\u7d2b\u8272\u7684\u820c\u5934\u4e1d\u4e1d\u5410\u4fe1\uff0c\u53d1\u51fa\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u7684\u58f0\u97f3\uff0c\u6df1\u84dd\u8272\u9c7c\u9ccd\u4ece\u80cc\u4e0a\u751f\u51fa\uff0c\u81ea\u540e\u8111\u5ef6\u4f38\u5230\u5c3e\u690e\uff0c\u767d\u68ee\u68ee\u7684\u9aa8\u523a\u81ea\u8eab\u4f53\u4e24\u4fa7\u7684\u9cde\u7247\u4e0b\u5494\u5494\u5494\u5494\u5730\u5f39\u51fa\uff0c\u4eff\u4f5b\u5229\u5203\u3002\u8299\u857e\u72c4\u53d1\u5c04\u4e86\u9b54\u6cd5\u98de\u5f39\uff0c\u60f3\u8981\u6253\u65ad\u4ed6\u7684\u53d8\u5f62\uff0c\u4f46\u88ab\u9cde\u7532\u76f4\u63a5\u53cd\u5f39\u4e86\uff0c\u6ca1\u6709\u8d77\u5230\u534a\u70b9\u6548\u679c\u3002</p><p>\u5b89\u535a\u7406\u7267\u5e08\u53d8\u5f62\u5b8c\u6bd5\u540e\uff0c\u86c7\u773c\u6b7b\u6b7b\u76ef\u7740\u743c\u6069\uff0c\u968f\u5373\u5feb\u901f\u5411\u524d\u6e38\u52a8\u3002\u5b83\u6240\u8fc7\u4e4b\u5904\uff0c\u5730\u9762\u50cf\u662f\u88ab\u5f3a\u9178\u8150\u8680\u4e86\u4e00\u822c\u55e4\u55e4\u4f5c\u54cd\uff0c\u9752\u70df\u76f4\u5192\u3002\u743c\u6069\u4e0d\u6562\u6020\u6162\uff0c\u8fde\u5fd9\u6cd5\u6756\u9065\u9065\u6307\u5730\uff0c\u5de8\u5927\u7684\u77f3\u722a\u4ece\u5730\u4e0b\u500f\u5ffd\u63a2\u51fa\u3002</p><p>\u5e38\u8a00\u8bf4\u6253\u86c7\u6253\u4e03\u5bf8\uff0c\u6240\u8c13\u4e03\u5bf8\u662f\u6307\u86c7\u7684\u8179\u90e8\uff0c\u90a3\u91cc\u662f\u86c7\u5fc3\u810f\u7684\u4f4d\u7f6e\u6240\u5728\uff0c\u5fae\u5fae\u9686\u8d77\uff0c\u6bd4\u5176\u4ed6\u90e8\u4f4d\u7a0d\u7a0d\u5927\u4e00\u5708\uff0c\u6700\u662f\u8106\u5f31\uff0c\u4e00\u51fb\u4fbf\u6b7b\u3002\u743c\u6069\u5374\u4eff\u4f5b\u5b8c\u5168\u4e0d\u61c2\u8fd9\u4e2a\u9053\u7406\uff0c\u77f3\u722a\u53ea\u6293\u4f4f\u4e86\u86c7\u7684\u540e\u534a\u622a\u3002\u87d2\u86c7\u6323\u624e\u7740\uff0c\u5b83\u7684\u529b\u6c14\u5927\u5f97\u60ca\u4eba\uff0c\u575a\u786c\u7a0b\u5ea6\u582a\u6bd4\u7cbe\u91d1\u7684\u77f3\u722a\u5c45\u7136\u6293\u63e1\u4e0d\u4f4f\uff0c\u88ab\u5b83\u4e00\u70b9\u70b9\u6491\u4e86\u5f00\u6765\u3002</p><p>\u773c\u770b\u602a\u86c7\u5c31\u8981\u8131\u56f0\uff0c\u743c\u6069\u6b63\u5f85\u518d\u5ea6\u65bd\u6cd5\uff0c\u53ea\u542c\u5f97\u5c16\u9510\u547c\u5578\u58f0\u4ece\u8033\u65c1\u63a0\u8fc7\uff0c\u56db\u53ea\u7d2b\u9ed1\u8272\u5706\u76d8\u65cb\u8f6c\u98de\u821e\uff0c\u9ad8\u901f\u65a9\u6765\uff0c\u50cf\u9f7f\u8f6e\u822c\u5207\u5272\u5f00\u602a\u86c7\u7684\u575a\u786c\u9cde\u7532\uff0c\u6df1\u6df1\u5d4c\u5165\u3002\u602a\u86c7\u5267\u70c8\u5730\u6323\u624e\u7740\uff0c\u5f20\u53e3\u6012\u53f7\uff0c\u5168\u8eab\u50cf\u6c14\u56ca\u822c\u9aa4\u7136\u9f13\u80c0\u8d77\u6765\uff0c\u5c06\u9677\u5165\u8840\u8089\u7684\u5706\u76d8\u7830\u5730\u5c3d\u6570\u5f39\u5f00\uff0c\u77f3\u722a\u4e5f\u88ab\u5b8c\u5168\u6491\u5f00\uff0c\u8282\u8282\u65ad\u88c2\uff0c\u5f7b\u5e95\u6e83\u6563\u3002</p><p>\u602a\u86c7\u6323\u8131\u675f\u7f1a\uff0c\u6602\u9996\u5f20\u53e3\uff0c\u671d\u743c\u6069\u55b7\u51fa\u4e00\u9053\u707f\u70c2\u7535\u5149\u3002\u743c\u6069\u9669\u9669\u907f\u5f00\uff0c\u4f46\u66f4\u591a\u7684\u7535\u5149\u53c8\u5c04\u8fc7\u6765\u3002\u8fde\u5c04\u51e0\u6b21\uff0c\u743c\u6069\u7ec8\u4e8e\u8eb2\u95ea\u4e0d\u53ca\uff0c\u88ab\u55b7\u4e86\u4e2a\u6b63\u7740\uff0c\u5f3a\u5927\u7684\u7535\u6d41\u5728\u5168\u8eab\u6e38\u8d70\u4e71\u7a9c\uff0c\u4ed6\u8fde\u58f0\u97f3\u90fd\u6ca1\u53d1\u51fa\u5c31\u76f4\u633a\u633a\u5f80\u540e\u5012\u4e86\u4e0b\u53bb\u3002\u8299\u857e\u72c4\u60ca\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u4e0d\u987e\u4e00\u5207\u60f3\u8981\u4e0a\u524d\u53bb\u6276\u4ed6\uff0c\u5374\u88ab\u8299\u8389\u5a05\u6b7b\u6b7b\u62c9\u4f4f\u4e86\u3002</p><p>\u201c\u743c\u6069\uff01\u201d</p><p>\u5c11\u5973\u7684\u547c\u5524\u5e76\u6ca1\u6709\u4ee4\u743c\u6069\u590d\u82cf\uff0c\u53cd\u800c\u62db\u6765\u4e86\u5f3a\u654c\uff0c\u5b89\u535a\u91cc\u7267\u5e08\u53d8\u6210\u7684\u602a\u86c7\u4e00\u51fb\u6253\u5012\u4e86\u743c\u6069\uff0c\u521d\u65f6\u8fd8\u6000\u7591\u662f\u5426\u6709\u8bc8\uff0c\u4f46\u770b\u8299\u857e\u72c4\u7684\u6a21\u6837\uff0c\u987f\u65f6\u7591\u5fc3\u5c3d\u53bb\uff0c\u547c\u5730\u6e38\u4e0a\u524d\u6765\uff0c\u86c7\u53e3\u5f20\u5f00\u5230\u6700\u5927\uff0c\u4eff\u4f5b\u4e00\u4e2a\u9ed1\u6d1e\uff0c\u7167\u7740\u743c\u6069\u7684\u8111\u888b\u4fbf\u541e\u566c\u4e0b\u6765\u3002</p><p>\u773c\u770b\u5c31\u8981\u846c\u8eab\u86c7\u53e3\uff0c\u743c\u6069\u9661\u7136\u7741\u773c\uff0c\u5f20\u53e3\u5410\u51fa\u4e00\u56e2\u7edb\u7ea2\u8272\u553e\u6cab\u3002\u87d2\u86c7\u731d\u4e0d\u53ca\u9632\uff0c\u88ab\u55b7\u4e86\u4e2a\u6ee1\u5934\u6ee1\u8138\uff0c\u5b83\u7684\u4e09\u89d2\u5f62\u8111\u888b\u6643\u4e86\u6643\uff0c\u52a8\u4f5c\u5239\u90a3\u95f4\u53d8\u5f97\u50f5\u786c\u4e86\u8bb8\u591a\u3002\u743c\u6069\u4e58\u673a\u624b\u8098\u6491\u5730\u4e00\u4e2a\u7ffb\u8eab\u8dc3\u8d77\uff0c\u53cc\u624b\u63e1\u7740\u7d2b\u6c34\u6676\u6cd5\u6756\u9ad8\u4e3e\uff0c\u62ff\u5b83\u5f53\u68cd\u68d2\u4e00\u822c\u7167\u7740\u87d2\u86c7\u7684\u540e\u9888\u90e8\u4f4d\u731b\u529b\u7838\u4e0b\u3002</p><p>\u5494\u5693\uff01</p><p>\u4e00\u58f0\u9aa8\u9abc\u788e\u88c2\u7684\u8106\u54cd\uff0c\u7c97\u82e5\u624b\u81c2\u7684\u87d2\u86c7\u53d7\u6b64\u4e00\u51fb\uff0c\u8111\u888b\u767b\u65f6\u8f6f\u8f6f\u5782\u4e0b\uff0c\u6574\u4e2a\u8eab\u4f53\u50cf\u88ab\u62bd\u53bb\u4e86\u810a\u67f1\u4e00\u822c\uff0c\u762b\u75ea\u5728\u5730\u3002\u743c\u6069\u4f38\u51fa\u624b\uff0c\u634f\u4f4f\u86c7\u5934\u4e24\u4fa7\uff0c\u5c06\u5b83\u63d0\u4e86\u8d77\u6765\u3002</p><p>\u201c\u4e0d\u597d\u610f\u601d\uff0c\u670b\u53cb\uff0c\u6211\u5c0f\u65f6\u5019\u53ef\u662f\u5929\u5929\u6349\u86c7\u7684\u3002\u201d</p></article>"}}}]);
